let otherHadith_dataSet = [['الأدب المفرد 1191', 'عَنْ أَزْهَرَ بْنِ سَعِيدٍ قَالَ: سَمِعْتُ أَبَا أُمَامَةَ يَقُولُ: إِنَّ ‌الشَّيْطَانَ ‌يَأْتِي ‌إِلَى ‌فِرَاشِ أَحَدِكُمْ بَعْدَمَا يَفْرِشُهُ أَهْلُهُ وَيُهَيِّئُونَهُ، فَيُلْقِي عَلَيْهِ الْعُودَ، أَوِ الْحَجَرَ، أَوِ الشَّيْءَ، لِيُغْضِبَهُ عَلَى أَهْلِهِ، فَإِذَا وَجَدَ ذَلِكَ فَلَا يَغْضَبْ عَلَى أَهْلِهِ، قَالَ: لِأَنَّهُ مِنْ عَمَلِ الشَّيْطَانِ.', 'އަބޫ އުމާމާ ރިވާ ކުރެއްވިއެވެ: ހަމަކަށަވަރުން ތިޔަބައިމީހުން ކުރެ މީހެއްގެ އަންހެނުން އޭނާގެ އެނދުތަންމަތި ހަދާ ރީތި ކުރުމަށް ފަހު، ޝައިޠާނާ އައިސް އެ މީހާގެ އަންހެނުން ދެކެ އޭނާ ރުޅިއައުމަށްޓަކައި އެ ތަންމައްޗަށް ފިލާކޮޅު ނުވަތަ ހިލަކޮޅު އެއްލާލައެވެ. ވީމާ އެ ކަހަލަ ކަމެއް މީހާކަށް ފެނިއްޖެ ނަމަ، ފަހެ އެ މީހެއްގެ އަންހެނުން ދެކެ އޭނާ ރުޅި ނާންނާށެވެ. އެހެނީ ހަމަކަށަވަރުން އެއީ ޝައިޠާނާގެ ޢަމަލެކެވެ.', ''], ['منتقى ابن جارود 71', 'حَدَّثَنَا ابْنُ الْمُقْرِئِ، قَالَ: ثَنَا سُفْيَانُ، عَنْ يَحْيَى بْنِ سَعِيدٍ، عَنْ مُحَمَّدِ بْنِ إِبْرَاهِيمَ التَّيْمِيِّ، عَنْ عَلْقَمَةَ بْنِ وَقَّاصٍ، قَالَ: سَمِعْتُ عُمَرَ رَضِيَ اللهُ عَنْهُ عَلَى الْمِنْبَرِ وَهُوَ يُخْبِرُ ذَلِكَ عَنْ رَسُولِ اللهِ ﷺ: إِنَّ الْأَعْمَالَ بِالنِّيَّةِ، وَإِنَّ لِكُلِّ امْرِئٍ مَا نَوَى، فَمَنْ كَانَتْ هِجْرَتُهُ إِلَى اللهِ وَرَسُولِهِ فَهِجْرَتُهُ إِلَى مَا هَاجَرَ إِلَيْهِ، وَمَنْ كَانَتْ هِجْرَتُهُ إِلَى دُنْيَا يُصِيبُهَا أَوِ امْرَأَةٍ يَنْكِحُهَا، فَهِجْرَتُهُ إِلَى مَا هَاجَرَ إِلَيْهِ.', 'މިންބަރު މަތީގައި ހުންނަވައިގެން ޢުމަރު ބިން އަލްޚައްޠާބު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ނިޔަތުގެ މައްޗަށް މެނުވީ ޢަމަލުތައް ބިނާ ވެގެނެއް ނުވެއެވެ. އަދި ކޮންމެ މީހަކަށް ވެސް ވަނީ، އެ މީހަކު ނިޔަތް ގަތް ކަމެކެވެ. ވީމާ، މީހަކު ހިޖުރަ ކުރީ ﷲ އާއި އެ އިލާހުގެ ރަސޫލާއަށްޓަކައި ނަމަ، ފަހެ އެ މީހެއްގެ ހިޖުރަ ވަނީ، އޭނާ އެ ހިޖުރަ ކުރި ކަމަކަށެވެ. އަދި މީހަކު ހިޖުރަ ކުރީ ދުނިޔެ ހޯދުމަށް ނުވަތަ އަންހެނަކާ އިނުމަށް ކަމުގައި ވަނީ ނަމަ، ފަހެ އެ މީހެއްގެ ހިޖުރަ ވަނީ، އޭނާ އެ ހިޖުރަ ކުރި ކަމަކަށެވެ.“\n', 'b1'], ['صحيح ابن خزيمة 142', 'أَخْبَرَنَا أَبُو طَاهِرٍ، قَالَ: حَدَّثَنَا أَبُو بَكْرٍ، قَالَ: حَدَّثَنَا يَحْيَى بْنُ حَبِيبٍ الْحَارِثِيُّ، وَأَحْمَدُ بْنُ عَبْدَةَ الضَّبِّيُّ قَالَا: حَدَّثَنَا حَمَّادُ بْنُ زَيْدٍ، عَنْ يَحْيَى بْنِ سَعِيدٍ، عَنْ مُحَمَّدِ بْنِ إِبْرَاهِيمَ، عَنْ عَلْقَمَةَ بْنِ وَقَّاصٍ اللَّيْثِيِّ قَالَ: سَمِعْتُ عُمَرَ بْنَ الْخَطَّابِ يَقُولُ: سَمِعْتُ رَسُولَ اللهِ - ﷺ - يَقُولُ: إِنَّمَا الْأَعْمَالُ بِالنِّيَّةِ وَإِنَّمَا لِامْرِئٍ مَا نَوَى، فَمَنْ كَانَتْ هِجْرَتُهُ إِلَى اللهِ وَإِلَى رَسُولِهِ فَهِجْرَتُهُ إِلَى اللهِ وَإِلَى رَسُولِهِ، وَمَنْ كَانَتْ هِجْرَتُهُ إِلَى دُنْيَا يُصِيبُهَا أَوِ امْرَأَةٍ يَتَزَوَّجُهَا فَهِجْرَتُهُ إِلَى مَا هَاجَرَ إِلَيْهِ. لَمْ يَقُلْ أَحْمَدُ: وَإِنَّمَا لِامْرِئٍ مَا نَوَى.', 'ޢުމަރު ބިން އަލްޚައްޠާބު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ: ”ނިޔަތުގެ މައްޗަށް މެނުވީ ޢަމަލުތައް ބިނާ ވެގެނެއް ނުވެއެވެ. އަދި މީހަކަށް ވަނީ، އެ މީހަކު ނިޔަތް ގަތް ކަމެކެވެ. ވީމާ، މީހަކު ހިޖުރަ ކުރީ ﷲ އަށް އަދި އެ އިލާހުގެ ރަސޫލާއަށްޓަކައި ނަމަ، ފަހެ އޭނާގެ ހިޖުރަ ވަނީ ﷲ އަށް އަދި އެ އިލާހުގެ ރަސޫލާއަށެވެ. އަދި މީހަކު ހިޖުރަ ކުރީ ދުނިޔެ ހޯދުމަށް ނުވަތަ އަންހެނަކާ އިނުމަށް ކަމުގައި ވަނީ ނަމަ، ފަހެ އެ މީހެއްގެ ހިޖުރަ ވަނީ، އޭނާ އެ ހިޖުރަ ކުރި ކަމަކަށެވެ.“', 'b1'], ['صحيح ابن خزيمة 143', 'أَخْبَرَنَا أَبُو طَاهِرٍ، قَالَ: حَدَّثَنَا أَبُو بَكْرٍ، قَالَ: حَدَّثَنَا مُحَمَّدُ بْنُ الْوَلِيدِ، قَالَ: حَدَّثَنَا عَبْدُ الْوَهَّابِ - يَعْنِي ابْنَ عَبْدِ الْمَجِيدِ الثَّقَفِيَّ - قَالَ: سَمِعْتُ يَحْيَى بْنَ سَعِيدٍ يَقُولُ: أَخْبَرَنِي مُحَمَّدُ بْنُ إِبْرَاهِيمَ، أَنَّهُ سَمِعَ عَلْقَمَةَ بْنَ وَقَّاصٍ اللَّيْثِيَّ يَقُولُ: سَمِعْتُ عُمَرَ بْنَ الْخَطَّابِ يَقُولُ: سَمِعْتُ رَسُولَ اللهِ - ﷺ - يَقُولُ: الْأَعْمَالُ بِالنِّيَّةِ، وَإِنَّمَا لِامْرِئٍ مَا نَوَى.', 'ޢުމަރު ބިން އަލްޚައްޠާބު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ: ”ޢަމަލުތައް ބިނާ ވަނީ ނިޔަތުގެ މައްޗަށެވެ. އަދި މީހަކަށް ވަނީ، އެ މީހަކު ނިޔަތް ގަތް ކަމެކެވެ.“', 'b1'], ['صحيح ابن خزيمة 455', 'أَخْبَرَنَا أَبُو طَاهِرٍ، قَالَ: حَدَّثَنَا أَبُو بَكْرٍ، قَالَ: حَدَّثَنَا يَحْيَى بْنُ حَبِيبِ بْنِ عَرَبِيٍّ الْحَارِثِيُّ، وَأَحْمَدُ بْنُ عَبْدَةَ الضَّبِّيُّ قَالَا: حَدَّثَنَا حَمَّادُ بْنُ زَيْدٍ، عَنْ يَحْيَى بْنِ سَعِيدٍ، عَنْ مُحَمَّدِ بْنِ إِبْرَاهِيمَ، عَنْ عَلْقَمَةَ بْنِ وَقَّاصٍ اللَّيْثِيِّ قَالَ: سَمِعْتُ عُمَرَ بْنَ الْخَطَّابِ يَقُولُ: سَمِعْتُ رَسُولَ اللهِ - ﷺ - يَقُولُ: إِنَّمَا الْأَعْمَالُ بِالنِّيَّةِ.\nزَادَ يَحْيَى بْنُ حَبِيبٍ: " وَإِنَّمَا لِامْرِئٍ مَا نَوَى ".', 'ޢުމަރު ބިން އަލްޚައްޠާބު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ: ”ނިޔަތުގެ މައްޗަށް މެނުވީ ޢަމަލުތައް ބިނާ ވެގެނެއް ނުވެއެވެ. އަދި މީހަކަށް ވަނީ، އެ މީހަކު ނިޔަތް ގަތް ކަމެކެވެ.“', 'b1'], ['صحيح ابن حبان 388', 'أَخْبَرَنَا عَلِيُّ بْنُ مُحَمَّدٍ الْقَبَّانِيُّ حَدَّثَنَا عَبْدُ اللهِ بْنُ هَاشِمٍ الطُّوسِيُّ حَدَّثَنَا يَحْيَى بْنُ سَعِيدٍ الْقَطَّانُ عَنْ يَحْيَى بْنِ سَعِيدٍ الْأَنْصَارِيِّ عَنْ مُحَمَّدِ بْنِ إِبْرَاهِيمَ التَّيْمِيِّ عَنْ عَلْقَمَةَ بْنِ وَقَّاصٍ عَنْ عُمَرَ بْنِ الْخَطَّابِ - رَضِيَ اللهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللهِ - ﷺ -: الْأَعْمَالُ بِالنِّيَّاتِ، وَلِكُلِّ امْرِئٍ مَا نَوَى، فَمَنْ كَانَتْ هِجْرَتُهُ إِلَى اللهِ وَرَسُولِهِ، فَهِجْرَتُهُ إِلَى اللهِ وَرَسُولِهِ، وَمَنْ كَانَتْ هِجْرَتُهُ إِلَى دُنْيَا يُصِيبُهَا، أَوِ امْرَأَةٍ يَتَزَوَّجُهَا، فَهِجْرَتُهُ إِلَى مَا هَاجَرَ إِلَيْهِ ".', 'ޢުމަރު ބިން އަލްޚައްޠާބު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ޢަމަލުތައް ބިނާ ވަނީ ނިޔަތްތަކުގެ މައްޗަށެވެ. އަދި ކޮންމެ މީހަކަށް ވަނީ، އެ މީހަކު ނިޔަތް ގަތް ކަމެކެވެ. ވީމާ، މީހަކު ހިޖުރަ ކުރީ ﷲ އާއި އެ އިލާހުގެ ރަސޫލާއަށްޓަކައި ނަމަ، ފަހެ އޭނާގެ ހިޖުރަ ވަނީ ﷲ އާއި އެ އިލާހުގެ ރަސޫލާއަށެވެ. އަދި މީހަކު ހިޖުރަ ކުރީ ދުނިޔެ ހޯދުމަށް ނުވަތަ އަންހެނަކާ އިނުމަށް ކަމުގައި ވަނީ ނަމަ، ފަހެ އެ މީހެއްގެ ހިޖުރަ ވަނީ، އޭނާ އެ ހިޖުރަ ކުރި ކަމަކަށެވެ.“', 'b1'], ['صحيح ابن حبان 389', 'أَخْبَرَنَا عُمَرُ بْنُ سَعِيدِ بْنِ سِنَانٍ قَالَ: حَدَّثَنَا أَبِي قَالَ: حَدَّثَنَا عِيسَى بْنُ يُونُسَ عَنْ يَحْيَى بْنِ سَعِيدٍ الْأَنْصَارِيِّ عَنْ مُحَمَّدِ بْنِ إِبْرَاهِيمَ التَّيْمِيِّ عَنْ عَلْقَمَةَ بْنِ وَقَّاصٍ اللَّيْثِيِّ عَنْ عُمَرَ بْنِ الْخَطَّابِ، قَالَ: قَالَ رَسُولُ اللهِ - ﷺ -: الْأَعْمَالُ بِالنِّيَّةِ، وَلِكُلِّ امْرِئٍ مَا نَوَى، فَمَنْ كَانَتْ هِجْرَتُهُ إِلَى اللهِ وَرَسُولِهِ، فَهِجْرَتُهُ إِلَى اللهِ وَرَسُولِهِ، وَمَنْ كَانَتْ هِجْرَتُهُ لِدُنْيَا يُصِيبُهَا، أَوِ امْرَأَةٍ يَتَزَوَّجُهَا، فَهِجْرَتُهُ إِلَى مَا هَاجَرَ إِلَيْهِ ".', 'ޢުމަރު ބިން އަލްޚައްޠާބު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ޢަމަލުތައް ބިނާ ވަނީ ނިޔަތުގެ މައްޗަށެވެ. އަދި ކޮންމެ މީހަކަށް ވަނީ، އެ މީހަކު ނިޔަތް ގަތް ކަމެކެވެ. ވީމާ، މީހަކު ހިޖުރަ ކުރީ ﷲ އާއި އެ އިލާހުގެ ރަސޫލާއަށްޓަކައި ނަމަ، ފަހެ އޭނާގެ ހިޖުރަ ވަނީ ﷲ އާއި އެ އިލާހުގެ ރަސޫލާއަށެވެ. އަދި މީހަކު ހިޖުރަ ކުރީ ދުނިޔެ ހޯދުމަށް ނުވަތަ އަންހެނަކާ އިނުމަށް ކަމުގައި ވަނީ ނަމަ، ފަހެ އެ މީހެއްގެ ހިޖުރަ ވަނީ، އޭނާ އެ ހިޖުރަ ކުރި ކަމަކަށެވެ.“', 'b1'], ['صحيح ابن حبان 4868', 'أَخْبَرَنَا الْعَبَّاسُ بْنُ أَحْمَدَ بْنِ حَسَّانَ السَّامِيُّ بِالْبَصْرَةِ، حَدَّثَنَا الصَّلْتُ بْنُ مَسْعُودٍ الْجَحْدَرِيُّ حَدَّثَنَا عُمَرُ بْنُ عَلِيٍّ حَدَّثَنَا يَحْيَى بْنُ سَعِيدٍ عَنْ مُحَمَّدِ بْنِ إِبْرَاهِيمَ عَنْ عَلْقَمَةَ بْنِ وَقَّاصٍ عَنْ عُمَرَ بْنِ الْخَطَّابِ رَضِيَ اللهُ عَنْهُ، قَالَ: قَالَ رَسُولُ اللهِ ﷺ: الْأَعْمَالُ بِالنِّيَّاتِ وَلِكُلِّ امْرِئٍ مَا نَوَى، فَمَنْ كَانَتْ هِجْرَتُهُ إِلَى اللهِ وَرَسُولِهِ فَهِجْرَتُهُ إِلَى اللهِ وَرَسُولِهِ، وَمَنْ كَانَتْ هِجْرَتُهُ لِدُنْيَا يُصِيبُهَا، أَوِ امْرَأَةٍ يَتَزَوَّجُهَا، فَهِجْرَتُهُ إِلَى مَا هَاجَرَ إِلَيْهِ.', 'ޢުމަރު ބިން އަލްޚައްޠާބު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ޢަމަލުތައް ބިނާ ވަނީ ނިޔަތްތަކުގެ މައްޗަށެވެ. އަދި ކޮންމެ މީހަކަށް ވަނީ، އެ މީހަކު ނިޔަތް ގަތް ކަމެކެވެ. ވީމާ، މީހަކު ހިޖުރަ ކުރީ ﷲ އާއި އެ އިލާހުގެ ރަސޫލާއަށްޓަކައި ނަމަ، ފަހެ އޭނާގެ ހިޖުރަ ވަނީ ﷲ އާއި އެ އިލާހުގެ ރަސޫލާއަށެވެ. އަދި މީހަކު ހިޖުރަ ކުރީ ދުނިޔެ ހޯދުމަށް ނުވަތަ އަންހެނަކާ އިނުމަށް ކަމުގައި ވަނީ ނަމަ، ފަހެ އެ މީހެއްގެ ހިޖުރަ ވަނީ، އޭނާ އެ ހިޖުރަ ކުރި ކަމަކަށެވެ.“', 'b1'], ['سنن النسائي 75', 'أَخْبَرَنَا يَحْيَى بْنُ حَبِيبِ بْنِ عَرَبِيٍّ، عَنْ حَمَّادٍ - وَالْحَارِثُ بْنُ مِسْكِينٍ قِرَاءَةً عَلَيْهِ وَأَنَا أَسْمَعُ، عَنِ ابْنِ الْقَاسِمِ، حَدَّثَنِي مَالِكٌ. (ح) وَأَخْبَرَنَا سُلَيْمَانُ بْنُ مَنْصُورٍ قَالَ: أَنْبَأَنَا عَبْدُ اللهِ بْنُ الْمُبَارَكِ - وَاللَّفْظُ لَهُ - عَنْ يَحْيَى بْنِ سَعِيدٍ، عَنْ مُحَمَّدِ بْنِ إِبْرَاهِيمَ، عَنْ عَلْقَمَةَ بْنِ وَقَّاصٍ، عَنْ عُمَرَ بْنِ الْخَطَّابِ رَضِيَ اللهُ عَنْهُ قَالَ: قَالَ رَسُولُ اللهِ ﷺ: إِنَّمَا الْأَعْمَالُ بِالنِّيَّةِ، وَإِنَّمَا لِامْرِئٍ مَا نَوَى، فَمَنْ كَانَتْ هِجْرَتُهُ إِلَى اللهِ وَإِلَى رَسُولِهِ فَهِجْرَتُهُ إِلَى اللهِ وَإِلَى رَسُولِهِ، وَمَنْ كَانَتْ هِجْرَتُهُ إِلَى دُنْيَا يُصِيبُهَا أَوِ امْرَأَةٍ يَنْكِحُهَا فَهِجْرَتُهُ إِلَى مَا هَاجَرَ إِلَيْهِ.', 'ޢުމަރު ބިން އަލްޚައްޠާބު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ނިޔަތުގެ މައްޗަށް މެނުވީ ޢަމަލުތައް ބިނާ ވެގެނެއް ނުވެއެވެ. އަދި މީހަކަށް ވަނީ، ހަމައެކަނި އެ މީހަކު ނިޔަތް ގަތް ކަމެކެވެ. ވީމާ، މީހަކު ހިޖުރަ ކުރީ ﷲ އަށް އަދި އެ އިލާހުގެ ރަސޫލާއަށްޓަކައި ނަމަ، ފަހެ އޭނާގެ ހިޖުރަ ވަނީ ﷲ އަށް އަދި އެ އިލާހުގެ ރަސޫލާއަށެވެ. އަދި މީހަކު ހިޖުރަ ކުރީ ދުނިޔެ ހޯދުމަށް ނުވަތަ އަންހެނަކާ އިނުމަށް ކަމުގައި ވަނީ ނަމަ، ފަހެ އެ މީހެއްގެ ހިޖުރަ ވަނީ، އޭނާ އެ ހިޖުރަ ކުރި ކަމަކަށެވެ.“', 'b1'], ['سنن النسائي 3437', 'أَخْبَرَنَا عَمْرُو بْنُ مَنْصُورٍ قَالَ: حَدَّثَنَا عَبْدُ اللهِ بْنُ مَسْلَمَةَ قَالَ: حَدَّثَنَا مَالِكٌ، وَالْحَارِثُ بْنُ مِسْكِينٍ قِرَاءَةً عَلَيْهِ وَأَنَا أَسْمَعُ، عَنِ ابْنِ الْقَاسِمِ قَالَ: أَخْبَرَنِي مَالِكٌ، عَنْ يَحْيَى بْنِ سَعِيدٍ، عَنْ مُحَمَّدِ بْنِ إِبْرَاهِيمَ، عَنْ عَلْقَمَةَ بْنِ وَقَّاصٍ، عَنْ عُمَرَ بْنِ الْخَطَّابِ رَضِيَ اللهُ عَنْهُ، وَفِي حَدِيثِ الْحَارِثِ أَنَّهُ سَمِعَ عُمَرَ يَقُولُ: قَالَ رَسُولُ اللهِ ﷺ: إِنَّمَا الْأَعْمَالُ بِالنِّيَّةِ، وَإِنَّمَا لِامْرِئٍ مَا نَوَى، فَمَنْ كَانَتْ هِجْرَتُهُ إِلَى اللهِ وَرَسُولِهِ فَهِجْرَتُهُ إِلَى اللهِ وَرَسُولِهِ، وَمَنْ كَانَتْ هِجْرَتُهُ لِدُنْيَا يُصِيبُهَا أَوِ امْرَأَةٍ يَتَزَوَّجُهَا فَهِجْرَتُهُ إِلَى مَا هَاجَرَ إِلَيْهِ.', 'ޢުމަރު ބިން އަލްޚައްޠާބު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ނިޔަތުގެ މައްޗަށް މެނުވީ ޢަމަލުތައް ބިނާ ވެގެނެއް ނުވެއެވެ. އަދި މީހަކަށް ވަނީ، ހަމައެކަނި އެ މީހަކު ނިޔަތް ގަތް ކަމެކެވެ. ވީމާ، މީހަކު ހިޖުރަ ކުރީ ﷲ އާއި އެ އިލާހުގެ ރަސޫލާއަށްޓަކައި ނަމަ، ފަހެ އޭނާގެ ހިޖުރަ ވަނީ ﷲ އާއި އެ އިލާހުގެ ރަސޫލާއަށެވެ. އަދި މީހަކު ހިޖުރަ ކުރީ ދުނިޔެ ހޯދުމަށް ނުވަތަ އަންހެނަކާ އިނުމަށް ކަމުގައި ވަނީ ނަމަ، ފަހެ އެ މީހެއްގެ ހިޖުރަ ވަނީ، އޭނާ އެ ހިޖުރަ ކުރި ކަމަކަށެވެ.“', 'b1'], ['سنن النسائي 3803', 'أَخْبَرَنَا إِسْحَاقُ بْنُ إِبْرَاهِيمَ قَالَ: أَنْبَأَنَا سَلِيمُ بْنُ حَيَّانَ قَالَ: حَدَّثَنَا يَحْيَى بْنُ سَعِيدٍ، عَنْ مُحَمَّدِ بْنِ إِبْرَاهِيمَ، عَنْ عَلْقَمَةَ بْنِ وَقَّاصٍ، عَنْ عُمَرَ بْنِ الْخَطَّابِ، عَنِ النَّبِيِّ ﷺ قَالَ: إِنَّمَا الْأَعْمَالُ بِالنِّيَّةِ، وَإِنَّمَا لِامْرِئٍ مَا نَوَى، فَمَنْ كَانَتْ هِجْرَتُهُ إِلَى اللهِ وَرَسُولِهِ فَهِجْرَتُهُ إِلَى اللهِ وَرَسُولِهِ، وَمَنْ كَانَتْ هِجْرَتُهُ لِدُنْيَا يُصِيبُهَا أَوِ امْرَأَةٍ يَتَزَوَّجُهَا، فَهِجْرَتُهُ إِلَى مَا هَاجَرَ إِلَيْهِ.', 'ޢުމަރު ބިން އަލްޚައްޠާބު ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ނިޔަތުގެ މައްޗަށް މެނުވީ ޢަމަލުތައް ބިނާ ވެގެނެއް ނުވެއެވެ. އަދި މީހަކަށް ވަނީ، ހަމައެކަނި އެ މީހަކު ނިޔަތް ގަތް ކަމެކެވެ. ވީމާ، މީހަކު ހިޖުރަ ކުރީ ﷲ އާއި އެ އިލާހުގެ ރަސޫލާއަށްޓަކައި ނަމަ، ފަހެ އޭނާގެ ހިޖުރަ ވަނީ ﷲ އާއި އެ އިލާހުގެ ރަސޫލާއަށެވެ. އަދި މީހަކު ހިޖުރަ ކުރީ ދުނިޔެ ހޯދުމަށް ނުވަތަ އަންހެނަކާ އިނުމަށް ކަމުގައި ވަނީ ނަމަ، ފަހެ އެ މީހެއްގެ ހިޖުރަ ވަނީ، އޭނާ އެ ހިޖުރަ ކުރި ކަމަކަށެވެ.“', 'b1'], ['سنن أبي داود 2201', 'حَدَّثَنَا مُحَمَّدُ بْنُ كَثِيرٍ، أَنَا سُفْيَانُ، حَدَّثَنِي يَحْيَى بْنُ سَعِيدٍ، عَنْ مُحَمَّدِ بْنِ إِبْرَاهِيمَ التَّيْمِيِّ، عَنْ عَلْقَمَةَ بْنِ وَقَّاصٍ اللَّيْثِيِّ قَالَ: سَمِعْتُ عُمَرَ بْنَ الْخَطَّابِ يَقُولُ: قَالَ رَسُولُ اللهِ ﷺ: إِنَّمَا الْأَعْمَالُ بِالنِّيَّةِ، وَإِنَّمَا لِامْرِئٍ مَا نَوَى فَمَنْ كَانَتْ هِجْرَتُهُ إِلَى اللهِ وَرَسُولِهِ فَهِجْرَتُهُ إِلَى اللهِ وَرَسُولِهِ، وَمَنْ كَانَتْ هِجْرَتُهُ لِدُنْيَا يُصِيبُهَا، أَوِ امْرَأَةٍ يَتَزَوَّجُهَا فَهِجْرَتُهُ إِلَى مَا هَاجَرَ إِلَيْهِ.', 'ޢުމަރު ބިން އަލްޚައްޠާބު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ނިޔަތުގެ މައްޗަށް މެނުވީ ޢަމަލުތައް ބިނާ ވެގެނެއް ނުވެއެވެ. އަދި މީހަކަށް ވަނީ، ހަމައެކަނި އެ މީހަކު ނިޔަތް ގަތް ކަމެކެވެ. ވީމާ، މީހަކު ހިޖުރަ ކުރީ ﷲ އާއި އެ އިލާހުގެ ރަސޫލާއަށްޓަކައި ނަމަ، ފަހެ އޭނާގެ ހިޖުރަ ވަނީ ﷲ އާއި އެ އިލާހުގެ ރަސޫލާއަށެވެ. އަދި މީހަކު ހިޖުރަ ކުރީ ދުނިޔެ ހޯދުމަށް ނުވަތަ އަންހެނަކާ އިނުމަށް ކަމުގައި ވަނީ ނަމަ، ފަހެ އެ މީހެއްގެ ހިޖުރަ ވަނީ، އޭނާ އެ ހިޖުރަ ކުރި ކަމަކަށެވެ.“', 'b1'], ['سنن الترمذي 1647', 'حَدَّثَنَا مُحَمَّدُ بْنُ الْمُثَنَّى، قَالَ: حَدَّثَنَا عَبْدُ الْوَهَّابِ الثَّقَفِيُّ، عَنْ يَحْيَى بْنِ سَعِيدٍ، عَنْ مُحَمَّدِ بْنِ إِبْرَاهِيمَ، عَنْ عَلْقَمَةَ بْنِ وَقَّاصٍ اللَّيْثِيِّ، عَنْ عُمَرَ بْنِ الْخَطَّابِ قَالَ: قَالَ رَسُولُ اللهِ ﷺ: إِنَّمَا الْأَعْمَالُ بِالنِّيَّةِ، وَإِنَّمَا لِامْرِئٍ مَا نَوَى، فَمَنْ كَانَتْ هِجْرَتُهُ إِلَى اللهِ وَإِلَى رَسُولِهِ، فَهِجْرَتُهُ إِلَى اللهِ وَرَسُولِهِ، وَمَنْ كَانَتْ هِجْرَتُهُ إِلَى دُنْيَا يُصِيبُهَا أَوِ امْرَأَةٍ يَتَزَوَّجُهَا فَهِجْرَتُهُ إِلَى مَا هَاجَرَ إِلَيْهِ. \nهَذَا حَدِيثٌ حَسَنٌ صَحِيحٌ.', 'ޢުމަރު ބިން އަލްޚައްޠާބު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ނިޔަތުގެ މައްޗަށް މެނުވީ ޢަމަލުތައް ބިނާ ވެގެނެއް ނުވެއެވެ. އަދި މީހަކަށް ވަނީ، ހަމައެކަނި އެ މީހަކު ނިޔަތް ގަތް ކަމެކެވެ. ވީމާ، މީހަކު ހިޖުރަ ކުރީ ﷲ އާއި އެ އިލާހުގެ ރަސޫލާއަށްޓަކައި ނަމަ، ފަހެ އޭނާގެ ހިޖުރަ ވަނީ ﷲ އާއި އެ އިލާހުގެ ރަސޫލާއަށެވެ. އަދި މީހަކު ހިޖުރަ ކުރީ ދުނިޔެ ހޯދުމަށް ނުވަތަ އަންހެނަކާ އިނުމަށް ކަމުގައި ވަނީ ނަމަ، ފަހެ އެ މީހެއްގެ ހިޖުރަ ވަނީ، އޭނާ އެ ހިޖުރަ ކުރި ކަމަކަށެވެ.“', 'b1'], ['سنن ابن ماجه 4227', 'حَدَّثَنَا أَبُو بَكْرِ بْنُ أَبِي شَيْبَةَ، حَدَّثَنَا يَزِيدُ بْنُ هَارُونَ. (ح) وَحَدَّثَنَا مُحَمَّدُ بْنُ رُمْحٍ، أَخْبَرَنَا اللَّيْثُ بْنُ سَعْدٍ، قَالَا: أَخْبَرَنَا يَحْيَى بْنُ سَعِيدٍ، أَنَّ مُحَمَّدَ بْنَ إِبْرَاهِيمَ التَّيْمِيَّ أَخْبَرَهُ أَنَّهُ سَمِعَ عَلْقَمَةَ بْنَ وَقَّاصٍ، أَنَّهُ سَمِعَ عُمَرَ بْنَ الْخَطَّابِ وَهُوَ يَخْطُبُ النَّاسَ، فَقَالَ: سَمِعْتُ رَسُولَ اللهِ ﷺ يَقُولُ: إِنَّمَا الْأَعْمَالُ بِالنِّيَّاتِ، وَلِكُلِّ امْرِئٍ مَا نَوَى، فَمَنْ كَانَتْ هِجْرَتُهُ إِلَى اللهِ وَإِلَى رَسُولِهِ، فَهِجْرَتُهُ إِلَى اللهِ وَإِلَى رَسُولِهِ، وَمَنْ كَانَتْ هِجْرَتُهُ لِدُنْيَا يُصِيبُهَا أَوِ امْرَأَةٍ يَتَزَوَّجُهَا، فَهِجْرَتُهُ إِلَى مَا هَاجَرَ إِلَيْهِ.', 'ޢުމަރު ބިން އަލްޚައްޠާބު މީސްތަކުންނަށް ޚުތުބާ ދެއްވަމުން ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ: ”ނިޔަތްތަކުގެ މައްޗަށް މެނުވީ ޢަމަލުތައް ބިނާ ވެގެނެއް ނުވެއެވެ. އަދި ކޮންމެ މީހަކަށް ވަނީ، ހަމައެކަނި އެ މީހަކު ނިޔަތް ގަތް ކަމެކެވެ. ވީމާ، މީހަކު ހިޖުރަ ކުރީ ﷲ އަށް އަދި އެ އިލާހުގެ ރަސޫލާއަށްޓަކައި ނަމަ، ފަހެ އޭނާގެ ހިޖުރަ ވަނީ ﷲ އަށް އަދި އެ އިލާހުގެ ރަސޫލާއަށެވެ. އަދި މީހަކު ހިޖުރަ ކުރީ ދުނިޔެ ހޯދުމަށް ނުވަތަ އަންހެނަކާ އިނުމަށް ކަމުގައި ވަނީ ނަމަ، ފަހެ އެ މީހެއްގެ ހިޖުރަ ވަނީ، އޭނާ އެ ހިޖުރަ ކުރި ކަމަކަށެވެ.“', 'b1'], ['مسند أحمد 170', 'حَدَّثَنَا سُفْيَانُ، عَنْ يَحْيَى، عَنْ مُحَمَّدِ بْنِ إِبْرَاهِيمَ التَّيْمِيِّ، عَنْ عَلْقَمَةَ بْنِ وَقَّاصٍ قَالَ: سَمِعْتُ عُمَرَ يَقُولُ: سَمِعْتُ رَسُولَ اللهِ ﷺ يَقُولُ: إِنَّمَا الْأَعْمَالُ بِالنِّيَّةِ، وَلِكُلِّ امْرِئٍ مَا نَوَى، فَمَنْ كَانَتْ هِجْرَتُهُ إِلَى اللهِ عَزَّ وَجَلَّ فَهِجْرَتُهُ إِلَى مَا هَاجَرَ إِلَيْهِ، وَمَنْ كَانَتْ هِجْرَتُهُ لِدُنْيَا يُصِيبُهَا، أَوِ امْرَأَةٍ يَنْكِحُهَا، فَهِجْرَتُهُ إِلَى مَا هَاجَرَ إِلَيْهِ.', 'ޢުމަރު ބިން އަލްޚައްޠާބު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ: ”ނިޔަތުގެ މައްޗަށް މެނުވީ ޢަމަލުތައް ބިނާ ވެގެނެއް ނުވެއެވެ. އަދި ކޮންމެ މީހަކަށް ވަނީ، ހަމައެކަނި އެ މީހަކު ނިޔަތް ގަތް ކަމެކެވެ. ވީމާ، މީހަކު ހިޖުރަ ކުރީ ﷲ ޢައްޒަ ވަޖައްލާއި އެ އިލާހުގެ ރަސޫލާއަށްޓަކައި ނަމަ، ފަހެ އެ މީހެއްގެ ހިޖުރަ ވަނީ، އޭނާ އެ ހިޖުރަ ކުރި ކަމަކަށެވެ. އަދި މީހަކު ހިޖުރަ ކުރީ ދުނިޔެ ހޯދުމަށް ނުވަތަ އަންހެނަކާ އިނުމަށް ކަމުގައި ވަނީ ނަމަ، ފަހެ އެ މީހެއްގެ ހިޖުރަ ވަނީ، އޭނާ އެ ހިޖުރަ ކުރި ކަމަކަށެވެ.“', 'b1'], ['مسند أحمد 306', 'حَدَّثَنَا يَزِيدُ، أَخْبَرَنَا يَحْيَى بْنُ سَعِيدٍ أَنَّ مُحَمَّدَ بْنَ إِبْرَاهِيمَ أَخْبَرَهُ أَنَّهُ سَمِعَ عَلْقَمَةَ بْنَ وَقَّاصٍ اللَّيْثِيَّ يَقُولُ:\nإِنَّهُ سَمِعَ عُمَرَ بْنَ الْخَطَّابِ وَهُوَ يَخْطُبُ النَّاسَ وَهُوَ يَقُولُ: سَمِعْتُ رَسُولَ اللهِ ﷺ يَقُولُ: إِنَّمَا الْعَمَلُ بِالنِّيَّةِ، وَإِنَّمَا لِامْرِئٍ مَا نَوَى فَمَنْ كَانَتْ هِجْرَتُهُ إِلَى اللهِ وَإِلَى رَسُولِهِ، فَهِجْرَتُهُ إِلَى اللهِ وَإِلَى رَسُولِهِ، وَمَنْ كَانَتْ هِجْرَتُهُ لِدُنْيَا يُصِيبُهَا، أَوِ امْرَأَةٍ يَتَزَوَّجُهَا فَهِجْرَتُهُ إِلَى مَا هَاجَرَ إِلَيْهِ.', 'ޢުމަރު ބިން އަލްޚައްޠާބު މީސްތަކުންނަށް ޚުތުބާ ދެއްވަމުން ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ: ”ނިޔަތުގެ މައްޗަށް މެނުވީ ޢަމަލު ބިނާ ވެގެނެއް ނުވެއެވެ. އަދި މީހަކަށް ވަނީ، ހަމައެކަނި އެ މީހަކު ނިޔަތް ގަތް ކަމެކެވެ. ވީމާ، މީހަކު ހިޖުރަ ކުރީ ﷲ އަށް އަދި އެ އިލާހުގެ ރަސޫލާއަށްޓަކައި ނަމަ، ފަހެ އޭނާގެ ހިޖުރަ ވަނީ ﷲ އަށް އަދި އެ އިލާހުގެ ރަސޫލާއަށެވެ. އަދި މީހަކު ހިޖުރަ ކުރީ ދުނިޔެ ހޯދުމަށް ނުވަތަ އަންހެނަކާ އިނުމަށް ކަމުގައި ވަނީ ނަމަ، ފަހެ އެ މީހެއްގެ ހިޖުރަ ވަނީ، އޭނާ އެ ހިޖުރަ ކުރި ކަމަކަށެވެ.“', 'b1'], ['صحيح ابن خزيمة 308', 'أَخْبَرَنَا أَبُو طَاهِرٍ، قَالَ: حَدَّثَنَا أَبُو بَكْرٍ، قَالَ: حَدَّثَنَا مُحَمَّدُ بْنُ يَحْيَى، قَالَ: حَدَّثَنَا رَوْحُ بْنُ عُبَادَةَ، عَنْ حَنْظَلَةَ قَالَ: سَمِعْتُ عِكْرِمَةَ بْنَ خَالِدِ بْنِ الْعَاصِ يُحَدِّثُ طَاوُسًا:\nأَنَّ رَجُلًا قَالَ لِعَبْدِ اللهِ بْنِ عُمَرَ: أَلَا تَغْزُو؟ فَقَالَ عَبْدُ اللهِ بْنُ عُمَرَ: إِنِّي سَمِعْتُ رَسُولَ اللهِ - ﷺ - يَقُولُ: بُنِيَ الْإِسْلَامُ عَلَى خَمْسٍ: شَهَادَةِ أَنْ لَا إِلَهَ إِلَّا اللهُ، وَإِقَامِ الصَّلَاةِ، وَإِيتَاءِ الزَّكَاةِ، وَصِيَامِ رَمَضَانَ، وَحَجِّ الْبَيْتِ ".', 'އަބްދު ﷲ ބިން ޢުމަރަށް މީހަކު ދެންނެވިއެވެ: ތިބާ (ﷲ ގެ މަގުގައި ޖިހާދު ކުރުމަށް) ޣަޒްވާއަކަށް ނުނިކުންނަމުތޯއެވެ؟ އަބްދު ﷲ ބިން ޢުމަރު ވިދާޅުވިއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ: ”އިސްލާމް (ދީން) ބިނާވެގެން ވަނީ ފަސް ކަމެއްގެ މައްޗަށެވެ. އެއީ ﷲ ފިޔަވައި (އަޅުކަން ޙައްޤުވާ) އެހެން އެއްވެސް އިލާހަކު ނުވާކަމަށް ހެކިވުމާއި، ނަމާދު ގާއިމު ކުރުމާއި، (މުދަލު) ޒަކާތް ދިނުމާއި، ރަމަޟާން މަހުގެ ރޯދަ ހިފުމާއި، ގެފުޅަށް ގޮސް ޙައްޖުވުމެވެ.“', 'b8'], ['صحيح ابن خزيمة 309', 'أَخْبَرَنَا أَبُو طَاهِرٍ، قَالَ: حَدَّثَنَا أَبُو بَكْرٍ، قَالَ: حَدَّثَنَا أَحْمَدُ بْنُ مَنْصُورٍ الرَّمَادِيُّ، قَالَ: حَدَّثَنَا أَبُو النَّضْرِ، قَالَ: حَدَّثَنَا عَاصِمٌ - وَهُوَ ابْنُ مُحَمَّدِ بْنِ زَيْدِ بْنِ عَبْدِ اللهِ بْنِ عُمَرَ بْنِ الْخَطَّابِ -، عَنْ أَبِيهِ، عَنِ ابْنِ عُمَرَ، عَنِ النَّبِيِّ - ﷺ - قَالَ:\nبُنِيَ الْإِسْلَامُ عَلَى خَمْسٍ: شَهَادَةِ أَنْ لَا إِلَهَ إِلَّا اللهُ، وَأَنَّ مُحَمَّدًا رَسُولُ اللهِ، وَإِقَامِ الصَّلَاةِ، وَإِيتَاءِ الزَّكَاةِ، وَحَجِّ الْبَيْتِ، وَصَوْمِ رَمَضَانَ.', 'އިބްނު ޢުމަރު ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އިސްލާމް (ދީން) ބިނާވެގެން ވަނީ ފަސް ކަމެއްގެ މައްޗަށެވެ. އެއީ ﷲ ފިޔަވައި (އަޅުކަން ޙައްޤުވާ) އެހެން އެއްވެސް އިލާހަކު ނުވާކަމަށް ހެކިވުމާއި، އަދި މުޙައްމަދުގެފާނީ އެ އިލާހުގެ ރަސޫލާކަމަށް ހެކިވުމާއި، ނަމާދު ގާއިމު ކުރުމާއި، (މުދަލު) ޒަކާތް ދިނުމާއި، ގެފުޅަށް ގޮސް ޙައްޖުވުމާއި، ރަމަޟާން މަހުގެ ރޯދަ ހިފުމެވެ.“', 'b8'], ['صحيح ابن خزيمة 1880', 'حَدَّثَنَا سَلْمُ بْنُ جُنَادَةَ، قَالَ: حَدَّثَنَا وَكِيعٌ، عَنْ حَنْظَلَةَ الْجُمَحِيِّ، عَنْ عِكْرِمَةَ بْنِ خَالِدٍ الْمَخْزُومِيِّ، عَنِ ابْنِ عُمَرَ قَالَ: قَالَ رَسُولُ اللهِ - ﷺ -: بُنِيَ الْإِسْلَامُ عَلَى خَمْسٍ: شَهَادَةِ أَنْ لَا إِلَهَ إِلَّا اللهُ، وَإِقَامِ الصَّلَاةِ، وَإِيتَاءِ الزَّكَاةِ، وَحَجِّ الْبَيْتِ، وَصَوْمِ شَهْرِ رَمَضَانَ.', 'އިބްނު ޢުމަރު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އިސްލާމް (ދީން) ބިނާވެގެން ވަނީ ފަސް ކަމެއްގެ މައްޗަށެވެ. އެއީ ﷲ ފިޔަވައި (އަޅުކަން ޙައްޤުވާ) އެހެން އެއްވެސް އިލާހަކު ނުވާކަމަށް ހެކިވުމާއި، ނަމާދު ގާއިމު ކުރުމާއި، (މުދަލު) ޒަކާތް ދިނުމާއި، ގެފުޅަށް ގޮސް ޙައްޖުވުމާއި، ރަމަޟާން މަހުގެ ރޯދަ ހިފުމެވެ.“', 'b8'], ['صحيح ابن خزيمة 2505', 'حَدَّثَنَا أَبُو الْأَشْعَثِ أَحْمَدُ بْنُ الْمِقْدَامِ الْعِجْلِيُّ، قَالَ: حَدَّثَنَا بِشْرُ بْنُ الْمُفَضَّلِ، قَالَ: حَدَّثَنَا عَاصِمٌ، وَهُوَ ابْنُ مُحَمَّدِ بْنِ زَيْدِ بْنِ عَبْدِ اللهِ بْنِ عُمَرَ بْنِ الْخَطَّابِ، قَالَ: سَمِعْتُ أَبِي يُحَدِّثُ عَنِ ابْنِ عُمَرَ قَالَ: قَالَ رَسُولُ اللهِ - ﷺ -: إِنَّ الْإِسْلَامَ بُنِيَ عَلَى خَمْسٍ: شَهَادَةِ أَنْ لَا إِلَهَ إِلَّا اللهُ، وَإِقَامِ الصَّلَاةِ، وَإِيتَاءِ الزَّكَاةِ، وَحَجِّ الْبَيْتِ، وَصَوْمِ رَمَضَانَ.', 'އިބްނު ޢުމަރު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން އިސްލާމް (ދީން) ބިނާވެގެން ވަނީ ފަސް ކަމެއްގެ މައްޗަށެވެ. އެއީ ﷲ ފިޔަވައި (އަޅުކަން ޙައްޤުވާ) އެހެން އެއްވެސް އިލާހަކު ނުވާކަމަށް ހެކިވުމާއި، ނަމާދު ގާއިމު ކުރުމާއި، (މުދަލު) ޒަކާތް ދިނުމާއި، ގެފުޅަށް ގޮސް ޙައްޖުވުމާއި، ރަމަޟާން މަހުގެ ރޯދަ ހިފުމެވެ.“', 'b8'], ['صحيح ابن حبان 158', 'أَخْبَرَنَا عَبْدُ اللهِ بْنُ مُحَمَّدٍ الْأَزْدِيُّ، حَدَّثَنَا إِسْحَاقُ بْنُ إِبْرَاهِيمَ الْحَنْظَلِيُّ، أَخْبَرَنَا وَكِيعٌ، عَنْ حَنْظَلَةَ بْنِ أَبِي سُفْيَانَ، سَمِعْتُ عِكْرِمَةَ بْنَ خَالِدٍ يُحَدِّثُ طَاوُسًا، أَنَّ رَجُلًا قَالَ لِابْنِ عُمَرَ: أَلَا تَغْزُو؟ فَقَالَ عَبْدُ اللهِ بْنُ عُمَرَ: إِنِّي سَمِعْتُ رَسُولَ اللهِ ﷺ، يَقُولُ: بُنِيَ الْإِسْلَامُ عَلَى خَمْسٍ: شَهَادَةِ أَنْ لَا إِلَهَ إِلَّا اللهُ، وَإِقَامِ الصَّلَاةِ، وَإِيتَاءِ الزَّكَاةِ، وَصِيَامِ رَمَضَانَ، وَحَجِّ الْبَيْتِ. قَالَ أَبُو حَاتِمٍ: هَذَانِ خَبَرَانِ خَرَجَ خِطَابُهُمَا عَلَى حَسَبِ الْحَالِ، لِأَنَّهُ ﷺ ذَكَرَ الْإِيمَانَ، ثُمَّ عَدَّهُ أَرْبَعَ خِصَالٍ، ثُمَّ ذَكَرَ الْإِسْلَامَ وَعَدَّهُ خَمْسَ خِصَالٍ، وَهَذَا مَا نَقُولُ فِي كُتُبِنَا: بِأَنَّ الْعَرَبَ تَذْكُرُ الشَّيْءَ فِي لُغَتِهَا بِعَدَدٍ مَعْلُومٍ، وَلَا تُرِيدُ بِذِكْرِهَا ذَلِكَ الْعَدَدَ نَفْيًا عَمَّا وَرَاءَهُ، وَلَمْ يُرِدْ بِقَوْلِهِ ﷺ: إِنَّ الْإِيمَانَ لَا يَكُونُ إِلَّا مَا عُدَّ فِي خَبَرِ ابْنِ عَبَّاسٍ، لِأَنَّهُ ذَكَرَ ﷺ فِي غَيْرِ خَبَرٍ أَشْيَاءَ كَثِيرَةً مِنَ الْإِيمَانِ لَيْسَتْ فِي خَبَرِ ابْنِ عُمَرَ، وَلَا ابْنِ عَبَّاسٍ اللَّذَيْنِ ذَكَرْنَاهُمَا.', 'އަބްދު ﷲ ބިން ޢުމަރަށް މީހަކު ދެންނެވިއެވެ: ތިބާ (ﷲ ގެ މަގުގައި ޖިހާދު ކުރުމަށް) ޣަޒްވާއަކަށް ނުނިކުންނަމުތޯއެވެ؟ އަބްދު ﷲ ބިން ޢުމަރު ވިދާޅުވިއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ: ”އިސްލާމް (ދީން) ބިނާވެގެން ވަނީ ފަސް ކަމެއްގެ މައްޗަށެވެ. އެއީ ﷲ ފިޔަވައި (އަޅުކަން ޙައްޤުވާ) އެހެން އެއްވެސް އިލާހަކު ނުވާކަމަށް ހެކިވުމާއި، ނަމާދު ގާއިމު ކުރުމާއި، (މުދަލު) ޒަކާތް ދިނުމާއި، ރަމަޟާން މަހުގެ ރޯދަ ހިފުމާއި، ގެފުޅަށް ގޮސް ޙައްޖުވުމެވެ.“', 'b8'], ['صحيح ابن حبان 1446', 'أَخْبَرَنَا الْحَسَنُ بْنُ سُفْيَانَ، قَالَ: حَدَّثَنَا حَرْمَلَةُ بْنُ يَحْيَى، قَالَ: حَدَّثَنَا ابْنُ وَهْبٍ، قَالَ: حَدَّثَنَا حَنْظَلَةُ بْنُ أَبِي سُفْيَانَ، قَالَ: سَمِعْتُ عِكْرِمَةَ بْنَ خَالِدٍ الْمَخْزُومِيَّ، يُحَدِّثُ أَنَّ رَجُلًا قَالَ لِعَبْدِ اللهِ بْنِ عُمَرَ: أَلَا تَغْزُو؟ فَقَالَ: إِنِّي سَمِعْتُ رَسُولَ اللهِ ﷺ يَقُولُ: بُنِيَ الْإِسْلَامُ عَلَى خَمْسٍ: شَهَادَةِ أَنْ لَا إِلَهَ إِلَّا اللهُ، وَإِقَامِ الصَّلَاةِ، وَإِيتَاءِ الزَّكَاةِ، وَصَوْمِ رَمَضَانَ، وَحَجِّ الْبَيْتِ.', 'އަބްދު ﷲ ބިން ޢުމަރަށް މީހަކު ދެންނެވިއެވެ: ތިބާ (ﷲ ގެ މަގުގައި ޖިހާދު ކުރުމަށް) ޣަޒްވާއަކަށް ނުނިކުންނަމުތޯއެވެ؟ އަބްދު ﷲ ބިން ޢުމަރު ވިދާޅުވިއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ: ”އިސްލާމް (ދީން) ބިނާވެގެން ވަނީ ފަސް ކަމެއްގެ މައްޗަށެވެ. އެއީ ﷲ ފިޔަވައި (އަޅުކަން ޙައްޤުވާ) އެހެން އެއްވެސް އިލާހަކު ނުވާކަމަށް ހެކިވުމާއި، ނަމާދު ގާއިމު ކުރުމާއި، (މުދަލު) ޒަކާތް ދިނުމާއި، ރަމަޟާން މަހުގެ ރޯދަ ހިފުމާއި، ގެފުޅަށް ގޮސް ޙައްޖުވުމެވެ.“', 'b8'], ['سنن النسائي 5016', 'أَخْبَرَنَا مُحَمَّدُ بْنُ عَبْدِ اللهِ بْنِ عَمَّارٍ قَالَ: حَدَّثَنَا الْمُعَافَى - يَعْنِي: ابْنَ عِمْرَانَ، عَنْ حَنْظَلَةَ بْنِ أَبِي سُفْيَانَ، عَنْ عِكْرِمَةَ بْنِ خَالِدٍ، عَنِ ابْنِ عُمَرَ أَنَّ رَجُلًا قَالَ لَهُ: أَلَا تَغْزُو؟ قَالَ: سَمِعْتُ رَسُولَ اللهِ ﷺ يَقُولُ: بُنِيَ الْإِسْلَامُ عَلَى خَمْسٍ؛ شَهَادَةِ أَنْ لَا إِلَهَ إِلَّا اللهُ، وَإِقَامِ الصَّلَاةِ، وَإِيتَاءِ الزَّكَاةِ، وَالْحَجِّ، وَصِيَامِ رَمَضَانَ.\n', 'އިބްނު ޢުމަރަށް މީހަކު ދެންނެވިއެވެ: ތިބާ (ﷲ ގެ މަގުގައި ޖިހާދު ކުރުމަށް) ޣަޒްވާއަކަށް ނުނިކުންނަމުތޯއެވެ؟ އިބްނު ޢުމަރު ވިދާޅުވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ: ”އިސްލާމް (ދީން) ބިނާވެގެން ވަނީ ފަސް ކަމެއްގެ މައްޗަށެވެ. އެއީ ﷲ ފިޔަވައި (އަޅުކަން ޙައްޤުވާ) އެހެން އެއްވެސް އިލާހަކު ނުވާކަމަށް ހެކިވުމާއި، ނަމާދު ގާއިމު ކުރުމާއި، (މުދަލު) ޒަކާތް ދިނުމާއި، ޙައްޖުވުމާއި، ރަމަޟާން މަހުގެ ރޯދަ ހިފުމެވެ.“', 'b8'], ['سنن الترمذي 2609', 'حَدَّثَنَا ابْنُ أَبِي عُمَرَ، قَالَ: حَدَّثَنَا سُفْيَانُ بْنُ عُيَيْنَةَ، عَنْ سُعَيْرِ بْنِ الْخِمْسِ التَّمِيمِيِّ، عَنْ حَبِيبِ بْنِ أَبِي ثَابِتٍ، عَنِ ابْنِ عُمَرَ قَالَ: قَالَ رَسُولُ اللهِ ﷺ: بُنِيَ الْإِسْلَامُ عَلَى خَمْسٍ شَهَادَةِ أَنْ لَا إِلَهَ إِلَّا اللهُ، وَأَنَّ مُحَمَّدًا رَسُولُ اللهِ، وَإِقَامِ الصَّلَاةِ، وَإِيتَاءِ الزَّكَاةِ، وَصَوْمِ رَمَضَانَ، وَحَجِّ الْبَيْتِ. وَفِي الْبَابِ عَنْ جَرِيرِ بْنِ عَبْدِ اللهِ.\nهَذَا حَدِيثٌ حَسَنٌ صَحِيحٌ. وَقَدْ رُوِيَ مِنْ غَيْرِ وَجْهٍ عَنِ ابْنِ عُمَرَ، عَنِ النَّبِيِّ ﷺ نَحْوَ هَذَا.\nوَسُعَيْرُ بْنُ الْخِمْسِ ثِقَةٌ عِنْدَ أَهْلِ الْحَدِيثِ.', 'އިބްނު ޢުމަރު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އިސްލާމް (ދީން) ބިނާވެގެން ވަނީ ފަސް ކަމެއްގެ މައްޗަށެވެ. އެއީ ﷲ ފިޔަވައި (އަޅުކަން ޙައްޤުވާ) އެހެން އެއްވެސް އިލާހަކު ނުވާކަމަށް ހެކިވުމާއި، އަދި މުޙައްމަދުގެފާނީ އެ އިލާހުގެ ރަސޫލާކަމަށް ހެކިވުމާއި، ނަމާދު ގާއިމު ކުރުމާއި، (މުދަލު) ޒަކާތް ދިނުމާއި، ރަމަޟާން މަހުގެ ރޯދަ ހިފުމާއި، ގެފުޅަށް ގޮސް ޙައްޖުވުމެވެ.“', 'b8'], ['مسند أحمد 4890', 'حَدَّثَنَا وَكِيعٌ، عَنْ سُفْيَانَ، عَنْ مَنْصُورٍ، عَنْ سَالِمِ بْنِ أَبِي الْجَعْدِ، عَنْ يَزِيدَ بْنِ بِشْرٍ، عَنِ ابْنِ عُمَرَ قَالَ: بُنِيَ الْإِسْلَامُ عَلَى خَمْسٍ: " شَهَادَةِ أَنْ لَا إِلَهَ إِلَّا اللهُ، وَإِقَامِ الصَّلَاةِ، وَإِيتَاءِ الزَّكَاةِ، وَحَجِّ الْبَيْتِ، وَصَوْمِ رَمَضَانَ ". قَالَ: فَقَالَ لَهُ رَجُلٌ: وَالْجِهَادُ فِي سَبِيلِ اللهِ قَالَ ابْنُ عُمَرَ: الْجِهَادُ حَسَنٌ، هَكَذَا حَدَّثَنَا رَسُولُ اللهِ ﷺ.', 'އިބްނު ޢުމަރު ރިވާ ކުރެއްވިއެވެ: ”އިސްލާމް (ދީން) ބިނާވެގެން ވަނީ ފަސް ކަމެއްގެ މައްޗަށެވެ. އެއީ ﷲ ފިޔަވައި (އަޅުކަން ޙައްޤުވާ) އެހެން އެއްވެސް އިލާހަކު ނުވާކަމަށް ހެކިވުމާއި، ނަމާދު ގާއިމު ކުރުމާއި، (މުދަލު) ޒަކާތް ދިނުމާއި، ގެފުޅަށް ގޮސް ޙައްޖުވުމާއި، ރަމަޟާން މަހުގެ ރޯދަ ހިފުމެވެ.“ މީހަކު ޢިބްނު ޢުމަރަށް ދެންނެވިއެވެ: އަދި ﷲގެ މަގުގައި ޖިހާދު ކުރުން؟ އިބްނު ޢުމަރު ވިދާޅުވިއެވެ: ޖިހާދަކީ ރަނގަޅު ކަމެކެވެ. (އެހެނަސް) ރަސޫލު ﷲ ﷺ ގެ އަރިހުން ތިމަން އަޑު އެހީ މިފަދައިންނެވެ.', 'b8'], ['مسند أحمد 5776', 'حَدَّثَنَا أَبُو النَّضْرِ، حَدَّثَنَا أَبُو عَقِيلٍ، عَنْ بَرَكَةَ بْنِ يَعْلَى التَّيْمِيِّ: حَدَّثَنِي أَبُو سُوَيْدٍ الْعَبْدِيُّ قَالَ: أَتَيْنَا ابْنَ عُمَرَ فَجَلَسْنَا بِبَابِهِ لِيُؤْذَنَ لَنَا، قَالَ: فَأَبْطَأَ عَلَيْنَا الْإِذْنُ، قَالَ: فَقُمْتُ إِلَى جُحْرٍ فِي الْبَابِ فَجَعَلْتُ أَطَّلِعُ فِيهِ، فَفَطِنَ بِي، فَلَمَّا أَذِنَ لَنَا جَلَسْنَا فَقَالَ: أَيُّكُمُ اطَّلَعَ آنِفًا فِي دَارِي؟ قَالَ: قُلْتُ: أَنَا، قَالَ: بِأَيِّ شَيْءٍ اسْتَحْلَلْتَ أَنْ تَطَّلِعَ فِي دَارِي؟ قَالَ: قُلْتُ: أَبْطَأَ عَلَيْنَا الْإِذْنُ، فَنَظَرْتُ فَلَمْ أَتَعَمَّدْ ذَلِكَ، قَالَ: ثُمَّ سَأَلُوهُ عَنْ أَشْيَاءَ، فَقَالَ: سَمِعْتُ رَسُولَ اللهِ ﷺ يَقُولُ: بُنِيَ الْإِسْلَامُ عَلَى خَمْسٍ: شَهَادَةِ أَنْ لَا إِلَهَ إِلَّا اللهُ وَأَنَّ مُحَمَّدًا رَسُولُ اللهِ، وَإِقَامِ الصَّلَاةِ، وَإِيتَاءِ الزَّكَاةِ، وَحَجِّ الْبَيْتِ، وَصِيَامِ رَمَضَانَ، قُلْتُ: يَا أَبَا عَبْدِ الرَّحْمَنِ، مَا تَقُولُ فِي الْجِهَادِ؟ قَالَ: مَنْ جَاهَدَ فَإِنَّمَا يُجَاهِدُ لِنَفْسِهِ.', 'އިބްނު ޢުމަރު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ: ”އިސްލާމް (ދީން) ބިނާވެގެން ވަނީ ފަސް ކަމެއްގެ މައްޗަށެވެ. އެއީ ﷲ ފިޔަވައި (އަޅުކަން ޙައްޤުވާ) އެހެން އެއްވެސް އިލާހަކު ނުވާކަމަށް ހެކިވުމާއި، އަދި މުޙައްމަދުގެފާނީ އެ އިލާހުގެ އަޅާ އަދި ރަސޫލާކަމަށް ހެކިވުމާއި، ނަމާދު ގާއިމު ކުރުމާއި، (މުދަލު) ޒަކާތް ދިނުމާއި، ގެފުޅަށް ގޮސް ޙައްޖުވުމާއި، ރަމަޟާން މަހުގެ ރޯދަ ހިފުމެވެ.“ މީހަކު ޢިބްނު ޢުމަރަށް ދެންނެވިއެވެ: އޭ އަބޫ ޢަބްދުއްރަޙްމާނެވެ. ޖިހާދާމެދު ވިދާޅުވަނީ ކީކޭތޯއެވެ؟ އިބްނު ޢުމަރު ވިދާޅުވިއެވެ: ޖިހާދު ކުރި މީހާ (ދަންނާށެވެ) އެ ޖިހާދު ކުރަނީ ހަމަ އޭނާގެ އަމިއްލަ ނަފްސުގެ މަންފާއަށެވެ.', 'b8'], ['مسند أحمد 6123', 'حَدَّثَنَا هَاشِمٌ، حَدَّثَنَا عَاصِمٌ، عَنْ أَبِيهِ، عَنِ ابْنِ عُمَرَ، عَنِ النَّبِيِّ ﷺ قَالَ: بُنِيَ الْإِسْلَامُ عَلَى خَمْسٍ: شَهَادَةِ أَنْ لَا إِلَهَ إِلَّا اللهُ وَأَنَّ مُحَمَّدًا رَسُولُ اللهِ، وَإِقَامِ الصَّلَاةِ، وَإِيتَاءِ الزَّكَاةِ، وَحَجِّ الْبَيْتِ، وَصَوْمِ رَمَضَانَ.', 'އިބްނު ޢުމަރު ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އިސްލާމް (ދީން) ބިނާވެގެން ވަނީ ފަސް ކަމެއްގެ މައްޗަށެވެ. އެއީ ﷲ ފިޔަވައި (އަޅުކަން ޙައްޤުވާ) އެހެން އެއްވެސް އިލާހަކު ނުވާކަމަށް ހެކިވުމާއި، އަދި މުޙައްމަދުގެފާނީ އެ އިލާހުގެ އަޅާ އަދި ރަސޫލާކަމަށް ހެކިވުމާއި، ނަމާދު ގާއިމު ކުރުމާއި، (މުދަލު) ޒަކާތް ދިނުމާއި، ގެފުޅަށް ގޮސް ޙައްޖުވުމާއި، ރަމަޟާން މަހުގެ ރޯދަ ހިފުމެވެ.“', 'b8'], ['مسند أحمد 6412', 'حَدَّثَنَا ابْنُ نُمَيْرٍ، حَدَّثَنَا حَنْظَلَةُ، سَمِعْتُ عِكْرِمَةَ بْنَ خَالِدٍ يُحَدِّثُ طَاوُسًا قَالَ:\nإِنَّ رَجُلًا قَالَ لِعَبْدِ اللهِ بْنِ عُمَرَ: أَلَا تَغْزُو؟ قَالَ: إِنِّي سَمِعْتُ رَسُولَ اللهِ ﷺ يَقُولُ: إِنَّ الْإِسْلَامَ بُنِيَ عَلَى خَمْسٍ: شَهَادَةِ أَنْ لَا إِلَهَ إِلَّا اللهُ وَأَنَّ مُحَمَّدًا رَسُولُ اللهِ، وَإِقَامِ الصَّلَاةِ، وَإِيتَاءِ الزَّكَاةِ، وَصِيَامِ رَمَضَانَ، وَحَجِّ الْبَيْتِ.', 'ހަމަކަށަވަރުން މީހަކު އަބްދު ﷲ ބިން ޢުމަރަށް ދެންނެވިއެވެ: ތިބާ (ﷲ ގެ މަގުގައި ޖިހާދު ކުރުމަށް) ޣަޒްވާއަކަށް ނުނިކުންނަމުތޯއެވެ؟ އިބްނު ޢުމަރު ވިދާޅުވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ: ”ހަމަކަށަވަރުން އިސްލާމް (ދީން) ބިނާވެގެން ވަނީ ފަސް ކަމެއްގެ މައްޗަށެވެ. އެއީ ﷲ ފިޔަވައި (އަޅުކަން ޙައްޤުވާ) އެހެން އެއްވެސް އިލާހަކު ނުވާކަމަށް ހެކިވުމާއި، އަދި މުޙައްމަދުގެފާނީ އެ އިލާހުގެ ރަސޫލާކަމަށް ހެކިވުމާއި، ނަމާދު ގާއިމު ކުރުމާއި، (މުދަލު) ޒަކާތް ދިނުމާއި، ރަމަޟާން މަހުގެ ރޯދަ ހިފުމާއި، ގެފުޅަށް ގޮސް ޙައްޖުވުމެވެ.“', 'b8'], ['سنن الترمذي 2518', 'حَدَّثَنَا أَبُو مُوسَى الْأَنْصَارِيُّ، قَالَ: حَدَّثَنَا عَبْدُ اللهِ بْنُ إِدْرِيسَ، قَالَ: حَدَّثَنَا شُعْبَةُ، عَنْ بُرَيْدِ بْنِ أَبِي مَرْيَمَ، عَنْ أَبِي الْحَوْرَاءِ السَّعْدِيِّ قَالَ: قُلْتُ لِلْحَسَنِ بْنِ عَلِيٍّ: مَا حَفِظْتَ مِنْ رَسُولِ اللهِ ﷺ؟ قَالَ: حَفِظْتُ مِنْ رَسُولِ اللهِ ﷺ، دَعْ مَا يَرِيبُكَ إِلَى مَا لَا يَرِيبُكَ، فَإِنَّ الصِّدْقَ طُمَأْنِينَةٌ، وَإِنَّ الْكَذِبَ رِيبَةٌ وَفِي الْحَدِيثِ قِصَّةٌ. \nوَأَبُو الْحَوْرَاءِ السَّعْدِيُّ اسْمُهُ رَبِيعَةُ بْنُ شَيْبَانَ. \nوَهَذَا حَدِيثٌ صَحِيحٌ.', 'އަލްޙަސަން ބިން ޢަލީ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ އަރިހުން (މި ބަސްފުޅުތައް) ތިމަން ދަސް ކުރީމެވެ: ”ތިބާއަށް ޝައްކުވާ ކަންތައް ދޫކޮށްލާފައި، ޝައްކު ނުވާ ކަންތަކުގައި ހިފާށެވެ. އެހެނީ ހަމަކަށަވަރުން ތެދުވެރިކަމަކީ ހިތްހަމަޖެހުން ލިއްބައިދޭ ކަމެކެވެ . އަދި ހަމަކަށަވަރުން ދޮގުވެރިކަމަކީ ޝައްކު އުފެދޭ ކަމެކެވެ.“', ''], ['سنن الترمذي 2317', 'حَدَّثَنَا أَحْمَدُ بْنُ نَصْرٍ النَّيْسَابُورِيُّ وَغَيْرُ وَاحِدٍ، قَالُوا: حَدَّثَنَا أَبُو مُسْهِرٍ، عَنْ إِسْمَاعِيلَ بْنِ عَبْدِ اللهِ بْنِ سَمَاعَةَ، عَنِ الْأَوْزَاعِيِّ، عَنْ قُرَّةَ، عَنِ الزُّهْرِيِّ، عَنْ أَبِي سَلَمَةَ، عَنْ أَبِي هُرَيْرَةَ قَالَ: قَالَ رَسُولُ اللهِ ﷺ: مِنْ حُسْنِ إِسْلَامِ الْمَرْءِ تَرْكُهُ مَا لَا يَعْنِيهِ. \nهَذَا حَدِيثٌ غَرِيبٌ، لَا نَعْرِفُهُ مِنْ حَدِيثِ أَبِي سَلَمَةَ، عَنْ أَبِي هُرَيْرَةَ، عَنِ النَّبِيِّ ﷺ إِلَّا مِنْ هَذَا الْوَجْهِ.', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”މީހާގެ އިސްލާމްކަން ރަނގަޅުވެ ފުރިހަމަ ވުމުގެ ތެރޭގައި، އޭނާއާ ނުބެހޭ ކަންތައްތަކުގެ ތެރެއަށް ނުވަނުން ހިމެނެއެވެ.“', ''], ['سنن الترمذي 1987', 'حَدَّثَنَا مُحَمَّدُ بْنُ بَشَّارٍ، قَالَ: حَدَّثَنَا عَبْدُ الرَّحْمَنِ بْنُ مَهْدِيٍّ، قَالَ: حَدَّثَنَا سُفْيَانُ، عَنْ حَبِيبِ بْنِ أَبِي ثَابِتٍ، عَنْ مَيْمُونِ بْنِ أَبِي شَبِيبٍ، عَنْ أَبِي ذَرٍّ قَالَ: قَالَ لِي رَسُولُ اللهِ ﷺ: اتَّقِ اللهَ حَيْثُمَا كُنْتَ، وَأَتْبِعِ السَّيِّئَةَ الْحَسَنَةَ تَمْحُهَا، وَخَالِقِ النَّاسَ بِخُلُقٍ حَسَنٍ. \nوَفِي الْبَابِ عَنْ أَبِي هُرَيْرَةَ. \nهَذَا حَدِيثٌ حَسَنٌ صَحِيحٌ.', 'އަބޫ ޛައްރު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ތިމަންނައަށް ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާވީ ކޮންމެ ތާކުމެ ﷲ އަށް ތަޤްވާވެރި ވާށެވެ. އަދި (ފާފައެއް ކުރެވިއްޖެ ނަމަ، އެ) ފާފަޔަކާ ވިދިގެން ހެޔޮ ކަމެއް ކުރާށެވެ. (އެހެނީ) އެ ހެޔޮ ކަންތައް، އެ ފާފަ ފޮހެލާނެއެވެ. އަދި ރިވެތި އަޚްލާޤުން މީސްތަކުންނާ މުޢާމަލާތް ކުރާށެވެ.“', ''], ['سنن الترمذي 2516', 'حَدَّثَنَا أَحْمَدُ بْنُ مُحَمَّدِ بْنِ مُوسَى، قَالَ: أَخْبَرَنَا عَبْدُ اللهِ بْنُ الْمُبَارَكِ، قَالَ: أَخْبَرَنَا لَيْثُ بْنُ سَعْدٍ وَابْنُ لَهِيعَةَ، عَنْ قَيْسِ بْنِ الْحَجَّاجِ. (ح) وَحَدَّثَنَا عَبْدُ اللهِ بْنُ عَبْدِ الرَّحْمَنِ، قَالَ: أَخْبَرَنَا أَبُو الْوَلِيدِ، قَالَ: حَدَّثَنَا لَيْثُ بْنُ سَعْدٍ، قَالَ: حَدَّثَنِي قَيْسُ بْنُ الْحَجَّاجِ، الْمَعْنَى وَاحِدٌ عَنْ حَنَشٍ الصَّنْعَانِيِّ، عَنِ ابْنِ عَبَّاسٍ قَالَ: كُنْتُ خَلْفَ رَسُولِ اللهِ ﷺ يَوْمًا، فَقَالَ: يَا غُلَامُ، إِنِّي أُعَلِّمُكَ كَلِمَاتٍ؛ احْفَظِ اللهَ يَحْفَظْكَ، احْفَظِ اللهَ تَجِدْهُ تُجَاهَكَ، إِذَا سَأَلْتَ فَاسْأَلِ اللهَ، وَإِذَا اسْتَعَنْتَ فَاسْتَعِنْ بِاللهِ، وَاعْلَمْ أَنَّ الْأُمَّةَ لَوِ اجْتَمَعَتْ عَلَى أَنْ يَنْفَعُوكَ بِشَيْءٍ، لَمْ يَنْفَعُوكَ إِلَّا بِشَيْءٍ قَدْ كَتَبَهُ اللهُ لَكَ، وَلَوِ اجْتَمَعُوا عَلَى أَنْ يَضُرُّوكَ بِشَيْءٍ لَمْ يَضُرُّوكَ إِلَّا بِشَيْءٍ قَدْ كَتَبَهُ اللهُ عَلَيْكَ، رُفِعَتِ الْأَقْلَامُ وَجَفَّتِ الصُّحُفُ. \nهَذَا حَدِيثٌ حَسَنٌ صَحِيحٌ.', 'އިބިނު ޢައްބާސް ރިވާ ކުރެއްވިއެވެ: އެއް ދުވަހަކު ނަބިއްޔާ ﷺ ގެ ފަހަތްޕުޅުގައި ތިމަން ވަނިކޮށް، ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ ކޮއިފުޅާއެވެ. ހަމަކަށަވަރުން، ތިމަން ނަބިއްޔާ ތިބާއަށް ކަލިމަތަކެއް ދަސްކޮށް ދެއްވާނަމެވެ. ﷲ އާމެދު (އެބަހީ: އެ އިލާހުގެ އަމުރުފުޅުތަކާ މެދު) ތިބާ ރައްކާތެރި ވާށެވެ. އޭރުން އެ އިލާހު ތިބާ ރައްކާތެރި ކޮށްދެއްވާނެއެވެ. ﷲ އާމެދު ތިބާ ރައްކާތެރި ވާށެވެ. އޭރުން ތިބާގެ ކުރިމަތީގައި އެ އިލާހު ވޮޑިގެންވާނެއެވެ. (އެބަހީ: ތިބާ ﷲ އަށް ކުއްތަންވެ، އެ އިލާހުގެ އަމުރުފުޅުތަކަށް ރައްކާތެރި ވާނަމަ، ﷲ ވެސް ތިބާއާ ކުއްތަންވެވޮޑިގަންނަވާ، ތިބާ ރައްކާތެރިކޮށް، ތިބާގެ ދުޢާތައް އިޖާބަ ކުރައްވާނެއެވެ.) ތިބާ ކަމަކަށް އެދި ދަންނަވާ ނަމަ، ﷲ އަށް އެދި ދަންނަވާށެވެ. އަދި ތިބާ އެހީއަކަށް އެދޭ ނަމަ، ﷲގެ އެހީއަށް އެދޭށެވެ. އަދި ދަންނާށެވެ. ހަމަކަށަވަރުން މުޅި އުންމަތް، ތިބާއަށް އެއްވެސް މަންފާއެއް ކޮށްދިނުމަށް އެއްބައި ވިޔަސް، ﷲ ތިބާއަށް ޓަކައި ލިޔުއްވައި (މިންވަރު ކުރައްވައިފި) ކަމަކުން މެނުވީ އެބައިމީހުން ތިބާއަކަށް މަންފާއެއް ކޮށެއް ނުދެވޭނެއެވެ. އަދި ތިބާއަށް އެއްވެސް ގެއްލުމެއް ދިނުމަށް މުޅި އުންމަތް އެއްބައި ވިޔަސް، ﷲ ތިބާގެ މައްޗަށް ލިޔުއްވައި (މިންވަރު ކުރައްވައިފި) ކަމަކުން މެނުވީ އެބައިމީހުން ތިބާއަށް ގެއްލުމެއް ނުދެވޭނެއެވެ. ގަލަންތައް ވަނީ އުފުލިފައެވެ. އަދި ފަތްފުށްތައް ވަނީ ހިކިފައެވެ.“ (އެބަހީ: ލައުޙުލް މަޙްފޫޡުގައި ﷲ ކަނޑައެޅުއްވި ކަންތައް ލިޔެ ނިމި، އެ ގޮތަށް އެ ކަންތައް ހިނގާނެއެވެ.)', ''], ['سنن أبي داود 4607', 'حَدَّثَنَا أَحْمَدُ بْنُ حَنْبَلٍ، نَا الْوَلِيدُ بْنُ مُسْلِمٍ، نَا ثَوْرُ بْنُ يَزِيدَ حَدَّثَنِي خَالِدُ بْنُ مَعْدَانَ حَدَّثَنِي عَبْدُ الرَّحْمَنِ بْنُ عَمْرٍو السُّلَمِيُّ وَحُجْرُ بْنُ حُجْرٍ قَالَا: أَتَيْنَا الْعِرْبَاضَ بْنَ سَارِيَةَ وَهُوَ مِمَّنْ نَزَلَ فِيهِ { وَلا عَلَى الَّذِينَ إِذَا مَا أَتَوْكَ لِتَحْمِلَهُمْ قُلْتَ لا أَجِدُ مَا أَحْمِلُكُمْ عَلَيْهِ } فَسَلَّمْنَا، وَقُلْنَا: أَتَيْنَاكَ زَائِرِينَ وَعَائِدِينَ وَمُقْتَبِسِينَ، فَقَالَ الْعِرْبَاضُ: صَلَّى بِنَا رَسُولُ اللهِ ﷺ ذَاتَ يَوْمٍ ثُمَّ أَقْبَلَ عَلَيْنَا فَوَعَظَنَا مَوْعِظَةً بَلِيغَةً ذَرَفَتْ مِنْهَا الْعُيُونُ، وَوَجِلَتْ مِنْهَا الْقُلُوبُ، فَقَالَ قَائِلٌ: يَا رَسُولَ اللهِ، كَأَنَّ هَذِهِ مَوْعِظَةُ مُوَدِّعٍ، فَمَاذَا تَعْهَدُ إِلَيْنَا؟ فَقَالَ: أُوصِيكُمْ بِتَقْوَى اللهِ، وَالسَّمْعِ وَالطَّاعَةِ وَإِنْ عَبْدًا حَبَشِيًّا، فَإِنَّهُ مَنْ يَعِشْ مِنْكُمْ بَعْدِي فَسَيَرَى اخْتِلَافًا كَثِيرًا، فَعَلَيْكُمْ بِسُنَّتِي وَسُنَّةِ الْخُلَفَاءِ الرَّاشِدِينَ الْمَهْدِيِّينَ، تَمَسَّكُوا بِهَا وَعَضُّوا عَلَيْهَا بِالنَّوَاجِذِ، وَإِيَّاكُمْ وَمُحْدَثَاتِ الْأُمُورِ فَإِنَّ كُلَّ مُحْدَثَةٍ بِدْعَةٌ وَكُلَّ بِدْعَةٍ ضَلَالَةٌ. \n', 'އަލްޢިރްބާޟު ބިން ސާރިޔާ ރިވާ ކުރެއްވިއެވެ: އެއްދުވަހު ތިމަންމެންނައިގެން ރަސޫލު ﷲ ﷺ ނަމާދު ކުރެއްވިއެވެ. އެއަށްފަހު ތިމަންމެންނާ ކުރިމަތިލައްވައިގެން ވަރަށް އަސަރު ގަދަ ވަޢުޡެއް ދެއްވިއެވެ. އެ ވަޢުޡުގެ ސަބަބުން ހިތްތައް ތެޅިގަނެ، ލޯތަކުން ކަރުނަ އޮހޮރިގެން ދިޔައެވެ. ދެން މީހަކު ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. މިއީ ވަދާޢީ ވަޢުޡެއް ފަދައެވެ. ވީމާ ތިމަންމެންނަށް ޢަހުދުކުރުވަނީ ކ ކޮންކަމަކަށްތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާ ތިޔަބައިމީހުންނަށް ވަޞިއްޔަތް ކުރައްވަނީ، ﷲ އަށް ތަޤްވާވެރިވުމަށާއި، އަދި (ތިޔަބައިމީހުންގެ މައްޗަށް) ޙަބަޝީ އަޅަކު (ވެރިކަމަށް އައިކަމުގައި ވިޔަސް އެ ވެރިމީހާގެ) އަޑުއަހައި ކިޔަމަންތެރިވުމަށެވެ. ފަހެ ހަމަކަށަވަރުން، ތިމަން ނަބިއްޔާއަށް ފަހު، ތިޔަބައިމީހުންގެ ތެރެއިން ދިރިހުރި މީހަކަށް ގިނަގުނަވެގެންވާ އިޚްތިލާފުތަކެއް ފެންނާނެއެވެ. ވީމާ، ތިޔަބައިމީހުންގެ މައްޗަށް އޮތީ ތިމަން ނަބިއްޔާގެ ސުންނަތުގައި ހިފެހެއްޓުމެވެ. އަދި ތެދުމަގު ލިބިގެންވާ ޚަލީފާއިންގެ ސުންނަތުގައި ހިފެހެއްޓުމެވެ. ތިޔަބައިމީހުން އެ ސުންނަތުގައި ކޮލުދަތްތަކުން ވަރުގަދައަށް ދަތް އަޅާ ހިފަހައްޓާށެވެ. އަދި (ދީނުގައި) އަލަށް އުފެދިގެން އަންނަ ކަންކަމާ (ބިދުޢަތަކާ) މެދު ސަމާލުވެ ރައްކާތެރިވާށެވެ. އެހެނީ ހަމަކަށަވަރުން، އަލަށް އުފެދިގެން އަންނަ ކޮންމެ ކަމަކީ ބިދުޢައެކެވެ. އަދި ކޮންމެ ބިދުޢައަކީ މަގުފުރެދުމެކެވެ.“', ''], ['سنن الترمذي 2616', 'حَدَّثَنَا ابْنُ أَبِي عُمَرَ، قَالَ: حَدَّثَنَا عَبْدُ اللهِ بْنُ مُعَاذٍ الصَّنْعَانِيُّ، عَنْ مَعْمَرٍ، عَنْ عَاصِمِ بْنِ أَبِي النَّجُودِ، عَنْ أَبِي وَائِلٍ، عَنْ مُعَاذِ بْنِ جَبَلٍ قَالَ: كُنْتُ مَعَ النَّبِيِّ ﷺ فِي سَفَرٍ، فَأَصْبَحْتُ يَوْمًا قَرِيبًا مِنْهُ، وَنَحْنُ نَسِيرُ، فَقُلْتُ: يَا رَسُولَ اللهِ، أَخْبِرْنِي بِعَمَلٍ يُدْخِلُنِي الْجَنَّةَ وَيُبَاعِدُنِي عَنِ النَّارِ، قَالَ: لَقَدْ سَأَلْتَنِي عَنْ عَظِيمٍ، وَإِنَّهُ لَيَسِيرٌ عَلَى مَنْ يَسَّرَهُ اللهُ عَلَيْهِ، تَعْبُدُ اللهَ وَلَا تُشْرِكْ بِهِ شَيْئًا، وَتُقِيمُ الصَّلَاةَ، وَتُؤْتِي الزَّكَاةَ، وَتَصُومُ رَمَضَانَ، وَتَحُجُّ الْبَيْتَ، ثُمَّ قَالَ: أَلَا أَدُلُّكَ عَلَى أَبْوَابِ الْخَيْرِ؛ الصَّوْمُ جُنَّةٌ، وَالصَّدَقَةُ تُطْفِئُ الْخَطِيئَةَ كَمَا يُطْفِئُ الْمَاءُ النَّارَ، وَصَلَاةُ الرَّجُلِ مِنْ جَوْفِ اللَّيْلِ. قَالَ: ثُمَّ تَلَا: { تَتَجَافَى جُنُوبُهُمْ عَنِ الْمَضَاجِعِ } حَتَّى بَلَغَ: { يَعْمَلُونَ }، ثُمَّ قَالَ: أَلَا أُخْبِرُكَ بِرَأْسِ الْأَمْرِ كُلِّهِ وَعَمُودِهِ وَذُرْوَةِ سَنَامِهِ؟ قُلْتُ: بَلَى يَا رَسُولَ اللهِ. قَالَ: رَأْسُ الْأَمْرِ الْإِسْلَامُ، وَعَمُودُهُ الصَّلَاةُ، وَذُرْوَةُ سَنَامِهِ الْجِهَادُ ثُمَّ قَالَ: أَلَا أُخْبِرُكَ بِمِلَاكِ ذَلِكَ كُلِّهِ؟ قُلْتُ: بَلَى يَا نَبِيَّ اللهِ. فَأَخَذَ بِلِسَانِهِ. قَالَ: كُفَّ عَلَيْكَ هَذَا. فَقُلْتُ: يَا نَبِيَّ اللهِ، وَإِنَّا لَمُؤَاخَذُونَ بِمَا نَتَكَلَّمُ بِهِ، فَقَالَ: ثَكِلَتْكَ أُمُّكَ يَا مُعَاذُ، وَهَلْ يَكُبُّ النَّاسَ فِي النَّارِ عَلَى وُجُوهِهِمْ أَوْ عَلَى مَنَاخِرِهِمْ إِلَّا حَصَائِدُ أَلْسِنَتِهِمْ. \nهَذَا حَدِيثٌ حَسَنٌ صَحِيحٌ.', 'މުޢާޛު ބިން ޖަބަލު ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ އަރިހުގައި ދަތުރެއްގައި ތިމަން ވީމެވެ. ދަތުރު ކުރިއަށް ދަނިކޮށް ނަބިއްޔާއާ ތަންކޮޅެއް ގާތުގައި އެއް ދުވަހަކު ތިމަން ހުރީމެވެ. ތިމަން ދެންނެވީމެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ތިމަން ސުވަރުގެއަށް ވައްދައިދޭނެ، އަދި ނަރަކައިން ދުރުކުރެވޭނެ ޢަމަލެއްގެ ޚަބަރު ތިމަންނާއަށް ދެއްވާށެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން، ތިޔަ ސުވާލު ކުރެއްވީ ބޮޑުވެގެންވާ ކަމަކާ ބެހޭ ގޮތުންނެވެ. އަދި ހަމަކަށަވަރުން، ﷲ ފަސޭހަ ކޮށްދެއްވައިފި މީހަކަށް، އެ ކަމަކީ ނުހަނު ފަސޭހަ ކަމަކަށް ވެގެންދެއެވެ. ﷲ އާ އެއްވެސް ފަރާތެއް ޝަރީކު ކުރުމަކާ ނުލައި، ހަމައެކަނި އެ އިލާހަށް އަޅުކަން ކުރާށެވެ. އަދި ނަމާދު ގާއިމުކޮށް، (މުދަލު) ޒަކާތް ދީ، ރަމަޟާން މަހު ރޯދަ ހިފައި، ގެފުޅަށް ގޮސް ޙައްޖު ވާށެވެ.“ ދެން ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާ، ތިބާއަށް ހެޔޮކަމުގެ ދޮރުތައް ދައްކަވައި ދެއްވަންތޯއެވެ؟ ރޯދައަކީ އައްޑަނައެކެވެ. (އެބަހީ: ރައްކާތެރި ކަމެކެވެ.) އަދި ފެނުން އަލިފާން ނިވޭ ފަދައިން، ޞަދަޤާތުން ފާފަ ފޮހެވިގެންދެއެވެ. އަދި (ހަމަ އެފަދައިން) މީހަކު ރޭގަނޑުގެ މެންދަމު ކުރާ ނަމާދު ވެސްމެއެވެ.“ ދެން، ނަބިއްޔާ (މި އާޔަތް) ކިޔަވައި ވިދާޅުވިއެވެ: ❞އެބައިމީހުންގެ އަރިކަށިތައް ތަންމަތިތަކާ ފޭވެ (ރޭއަޅުކަން ކުރުމަށް ޓަކައި ނިދި ދޫކޮށްލާފައި ތެދުވެ)، އެބައިމީހުންގެ ރައްބަށް ބިރުވެތިކަމާއި އުންމީދާއެކު އެބައިމީހުން ދުޢާ ކުރެއެވެ. އަދި ތިމަން އިލާހު، އެބައިމީހުންނަށް ދެއްވި ތަކެތިން، އެބައިމީހުން ހޭދަ ކުރެއެވެ. ފަހެ އެބައިމީހުންނަށް ޓަކައި ފޮރުއްވާފައިވާ ލޮލުގެ ފިނިކަން (އެބަހީ: ސުވަރުގޭގެ ނިޢުމަތްތައް) އެއްވެސް ނަފްސަކަށް ނޭނގޭނެއެވެ. އެއީ އެބައިމީހުން ކޮށްއުޅުނު ޢަމަލުތަކުގެ ޖަޒާއެއްގެ ގޮތުންނެވެ.❝ ދެން ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”މުޅި މި ދީނުގެ އެންމެ މުހިންމު ކަމަކީ ކޮބައިކަމާއި، މި ދީނުގެ ތަނބާއި ކުރިބޯށްޓާ ބެހޭ ގޮތުން ތިބާއަށް ކިޔައި ދެއްވަންހެއްޔެވެ؟“ ތިމަން ދެންނެވީމެވެ: އާދޭހެވެ. އޭ ﷲގެ ރަސޫލާއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”މި ދީނުގެ އެންމެ މުހިންމު ކަމަކީ އިސްލާމްކަމެވެ. އަދި މި ދީނުގެ ތަނބަކީ ނަމާދެވެ. އަދި މި ދީނުގެ ކުރިބޯށްޓަކީ ޖިހާދެވެ.“ އެއަށްފަހު ޙަދީޘް ކުރެއްވިއެވެ: ”އެ ހުރިހާ ކަމަކަށް ބާރުވެރިކޮށްދޭނެ ކަމަކާ ބެހޭ ގޮތުން ތިބާއަށް ކިޔައި ދެއްވަންހެއްޔެވެ؟“ ތިމަން ދެންނެވީމެވެ: އާދޭހެވެ. އޭ ﷲގެ ރަސޫލާއެވެ. ދެން ނަބިއްޔާ ދޫފުޅުގައި ހިފަހައްޓަވާފައި ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ މީތި ހިފަހައްޓާށެވެ.“ ތިމަން ދެންނެވީމެވެ: އޭ ﷲގެ ނަބިއްޔާއެވެ. ދުލުން ދައްކާ ވާހަކަތަކުގެ ސަބަބުން ތިމަންމެން ހިއްޕެވޭނެހެއްޔެވެ؟ (އެބަހީ: ޢުޤޫބާތް ދެވޭނެހެއްޔެވެ؟) ފަހެ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ މުޢާޛެވެ. ތިބާގެ މަންމައަށް ތިބާ ގެއްލިދާށެވެ. (ނޯޓު: މިއީ ކަމެއްގެ ބޮޑުކަން ހާމަކުރަން ޢަރަބީންގެ ދޫމަތިން ބުނެ އުޅޭ އިބާރާތެކެވެ. ބަދު ދުޢާ ކުރުމުގެ މާނައިގައި ބުނާ ބަހެއް ނޫނެވެ.) ފަހެ މީހުންގެ ދޫތަކުން ހޯދައިގަތް ކަންތަކުން މެނުވީ، އެބައިމީހުން ނަރަކައިގެ ތެރެއަށް މޫނުވަތަށް —ނުވަތަ ނޭފަތް ވަތަށް— އެއްލާލެވޭނެހެއްޔެވެ؟“', ''], ['سنن الدارقطني 4396', 'حَدَّثَنَا الْقَاسِمُ بْنُ إِسْمَاعِيلَ، حَدَّثَنَا يَعْقُوبُ بْنُ إِبْرَاهِيمَ وَمُحَمَّدُ بْنُ حَسَّانَ الْأَزْرَقُ قَالَا: حَدَّثَنَا إِسْحَاقُ الْأَزْرَقُ، حَدَّثَنَا دَاوُدُ بْنُ أَبِي هِنْدٍ، عَنْ مَكْحُولٍ، عَنْ أَبِي ثَعْلَبَةَ الْخُشَنِيِّ قَالَ: قَالَ رَسُولُ اللهِ - ﷺ -: إِنَّ اللهَ تَعَالَى فَرَضَ فَرَائِضَ فَلَا تُضَيِّعُوهَا، وَحَرَّمَ حُرُمَاتٍ فَلَا تَنْتَهِكُوهَا، وَحَدَّ حُدُودًا فَلَا تَعْتَدُوهَا، وَسَكَتَ عَنْ أَشْيَاءَ مِنْ غَيْرِ نِسْيَانٍ فَلَا تَبْحَثُوا عَنْهَا. ', 'އަބޫ ޘަޢުލަބާ އަލްޚުޝަނީ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ﷲ ތަޢާލާ ވަނީ ފަރުޟު ކަންތައްތަކެއް ކަނޑައަޅުއްވާފައެވެ. ވީމާ ތިޔަބައިމީހުން އެ ކަންތައްތަކަށް (އިހުމާލުވެ) ދޫކޮށް ނުލާށެވެ. އަދި އެ އިލާހު ކަންތައްތަކެއް ޙަރާމް ކުރެއްވިއެވެ. ވީމާ ތިޔަބައިމީހުން އެ ކަންތައްތަކަށް އަރައިނުގަންނާށެވެ. އަދި އެ އިލާހު (އިންތަކަކާއި) ޙައްދުތަކެއް ކަނޑައެޅުއްވިއެވެ. ވީމާ ތިޔަބައިމީހުން އެ ޙައްދުތައް ފަހަނަޅައި ނުދާށެވެ. އަދި އެ އިލާހު (ބައެއް) ކަންތައްތަކާ މެދު ސުކޫތުވެވޮޑިގަތެވެ. (އެބަހީ: ވަކި ގޮތެއް ކަނޑަނާޅުއްވައި ދޫކޮށްލެއްވިއެވެ.) އެއީ ހަނދުމަނެތިވެވޮޑިގެނެއް ނޫނެވެ. ވީމާ ތިޔަބައިމީހުން އެ ކަންތައްތައް ހޯދަން ނޫޅޭށެވެ.“', ''], ['سنن ابن ماجه 4102', 'حَدَّثَنَا أَبُو عُبَيْدَةَ بْنُ أَبِي السَّفَرِ، حَدَّثَنَا شِهَابُ بْنُ عَبَّادٍ، حَدَّثَنَا خَالِدُ بْنُ عَمْرٍو الْقُرَشِيُّ، عَنْ سُفْيَانَ الثَّوْرِيِّ، عَنْ أَبِي حَازِمٍ، عَنْ سَهْلِ بْنِ سَعْدٍ السَّاعِدِيِّ قَالَ: أَتَى النَّبِيَّ ﷺ رَجُلٌ فَقَالَ: يَا رَسُولَ اللهِ، دُلَّنِي عَلَى عَمَلٍ إِذَا أَنَا عَمِلْتُهُ أَحَبَّنِي اللهُ، وَأَحَبَّنِي النَّاسُ، فَقَالَ رَسُولُ اللهِ ﷺ: ازْهَدْ فِي الدُّنْيَا يُحِبُّكَ اللهُ، وَازْهَدْ فِيمَا فِي أَيْدِي النَّاسِ يُحِبُّوكَ. ', 'ސަހްލު ބިން ޞަޢުދު އައްސާޢިދީ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ އަރިހަށް މީހަކު އައިސް ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. އެ ކަމެއް ކުރުމުން، ތިމަން ދެކެ ﷲ ލޯބިވެވޮޑިގަންނަވާނެ، އަދި މީސްތަކުން ވެސް ތިމަންދެކެ ލޯބިވާނެ ޢަމަލެއް ދައްކަވައި ދެއްވާށެވެ. ފަހެ ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ ދުނިޔޭގައި ޒުހުދުވެރިވާށެވެ. (އެބަހީ: ދުނިޔެއަށް އެދުންވެރި ނުވާށެވެ.) އޭރުން ﷲ ތިބާ ދެކެ ލޯބިވެވޮޑިގަންނަވާނެއެވެ. އަދި މީހުންގެ އަތުގައިވާ ތަކެއްޗާމެދު ޒުހުދުވެރިވާށެވެ. އޭރުން ތިބާ ދެކެ އެ މީހުން ލޯބިވާނެއެވެ.“', ''], ['سنن ابن ماجه 2341', 'حَدَّثَنَا مُحَمَّدُ بْنُ يَحْيَى، حَدَّثَنَا عَبْدُ الرَّزَّاقِ، أَخْبَرَنَا مَعْمَرٌ، عَنْ جَابِرٍ الْجُعْفِيِّ، عَنْ عِكْرِمَةَ، عَنِ ابْنِ عَبَّاسٍ، قَالَ: قَالَ رَسُولُ اللهِ ﷺ: لَا ضَرَرَ وَلَا ضِرَارَ. ', 'އިބްނު ޢައްބާސް ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ގެއްލުން ދިނުމެއް އޮވެގެނެއް ނުވާނެއެވެ. އަދި (މީހަކު ގެއްލުމެއް ދިނުމުން، ނުޙައްޤުން) ބަދަލުގައި ގެއްލުން ދިނުމެއް ވެސް އޮވެގެނެއް ނުވާނެއެވެ.“ (ނޯޓު: އަދި މީގެ ތެރޭގައި މި ކަންތައް ވެސް ހިމެނޭނެއެވެ: ގެއްލުން ދިނުމެއް ވެސް އަދި ގެއްލުން ލިބިގަތުމެއް ވެސް ނެތެވެ. އަދި ގަސްދެއް ނެތި އަދި ގަސްދަކާލައިގެން ވިޔަސް ގެއްލުން ދިނުމެއް ނެތެވެ. އަދި އަމިއްލަ ނަފްސަށް ވިޔަސް އަދި އެހެން މީހަކަށް ވިޔަސް ޤެއްލުން ދިނުމެއް ނެތެވެ.)', ''], ['سنن البيهقي 21201', '(أَخْبَرَنَا) أَبُو الْحَسَنِ: عَلِيُّ بْنُ أَحْمَدَ بْنِ عَبْدَانَ، أَنْبَأَ أَحْمَدُ بْنُ عُبَيْدٍ الصَّفَّارُ، ثَنَا جَعْفَرُ بْنُ مُحَمَّدٍ الْفِرْيَابِيُّ، ثَنَا الْحَسَنُ بْنُ سَهْلٍ، ثَنَا عَبْدُ اللهِ بْنُ إِدْرِيسَ، ثَنَا ابْنُ جُرَيْجٍ وَعُثْمَانُ بْنُ الْأَسْوَدِ عَنِ ابْنِ أَبِي مُلَيْكَةَ قَالَ: كُنْتُ قَاضِيًا لِابْنِ الزُّبَيْرِ عَلَى الطَّائِفِ فَذَكَرَ قِصَّةَ الْمَرْأَتَيْنِ قَالَ فَكَتَبْتُ إِلَى ابْنِ عَبَّاسٍ فَكَتَبَ ابْنُ عَبَّاسٍ رَضِيَ اللهُ عَنْهُمَا إِنَّ رَسُولَ اللهِ - ﷺ - قَالَ: لَوْ يُعْطَى النَّاسُ بِدَعْوَاهُمْ لَادَّعَى رِجَالٌ أَمْوَالَ قَوْمٍ وَدِمَاءَهُمْ وَلَكِنَّ الْبَيِّنَةَ عَلَى الْمُدَّعِي وَالْيَمِينَ عَلَى مَنْ أَنْكَرَ. وَذَكَرَ الْحَدِيثَ. ', 'އިބްނު ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމައެކަނި މީސްތަކުން ކޮށްލާ ދަޢުވާއަކުން، (ޝަރީޢަތުން) އެބައިމީހުންނަށް ކަންކަން ނިންމައިދެވޭ ނަމަ، (ﷲ އަށް ބިރުވެތި ނުވާ) މީހުން، އެހެން މީހުންގެ މުދަލާއި (މަރުގެ) ދިޔައަށް ދަޢުވާ ކުރީސްކަން ކަށަވަރެވެ. އެހެނެއްކަމަކު، ދަޢުވާ ކުރާ މީހާގެ މައްޗަށް ވަނީ (ދަޢުވާގެ ތެދުކަން ސާބިތު ކުރުމަށް) ހެކި ހުށަހެޅުމެވެ. އަދި (ދަޢުވާ ލިބި، އެއަށް) އިންކާރު ކުރާ މީހާގެ މައްޗަށް ވަނީ (އެ ދަޢުވާގެ ދޮގުކަން ބަޔާންކޮށް، ﷲ ގަންދީ) ހުވާ ކުރުމެވެ.“ (އެބަހީ: މީހެއްގެ މައްޗަށް ކުރެވޭ ދަޢުވާ ސާބިތު ކުރެވިއްޖައުމަށް ދާންދެން، އޭނާ ވާނީ އެ ކަމަކުން ބަރީއަ ވެގެންނެވެ.)', ''], ['سنن ابن ماجه 2043', 'حَدَّثَنَا إِبْرَاهِيمُ بْنُ مُحَمَّدِ بْنِ يُوسُفَ الْفِرْيَابِيُّ، حَدَّثَنَا أَيُّوبُ بْنُ سُوَيْدٍ، حَدَّثَنَا أَبُو بَكْرٍ الْهُذَلِيُّ، عَنْ شَهْرِ بْنِ حَوْشَبٍ، عَنْ أَبِي ذَرٍّ الْغِفَارِيِّ قَالَ: قَالَ رَسُولُ اللهِ ﷺ: إِنَّ اللهَ قَدْ تَجَاوَزَ عَنْ أُمَّتِي الْخَطَأَ وَالنِّسْيَانَ وَمَا اسْتُكْرِهُوا عَلَيْهِ. ', 'އަބޫ ޛައްރު އަލްޣިފާރީ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާގެ އުންމަތުގެ މީހުންގެ ކިބައިން (ގަސްދަކާ ނުލައި އޮޅުމަކުން) ކުރެވޭ ކުށްތަކާއި، ހަނދާން ނެތުމާ، އަދި ނުރުހޭ ޙާލު (މަޖުބޫރު ކުރުވައިގެން) ކުރެވޭ ކަންތައް، ﷲ ވަނީ ޢަފޫ ކޮށްދެއްވާފައެވެ.“', ''], ['سنن الترمذي 3540', ' حَدَّثَنَا عَبْدُ اللهِ بْنُ إِسْحَاقَ الْجَوْهَرِيُّ الْبَصْرِيُّ، قَالَ: حَدَّثَنَا أَبُو عَاصِمٍ، قَالَ: حَدَّثَنَا كَثِيرُ بْنُ فَائِدٍ، قَالَ: حَدَّثَنَا سَعِيدُ بْنُ عُبَيْدٍ قَالَ: سَمِعْتُ بَكْرَ بْنَ عَبْدِ اللهِ الْمُزَنِيَّ يَقُولُ: حَدَّثَنَا أَنَسُ بْنُ مَالِكٍ قَالَ: سَمِعْتُ رَسُولَ اللهِ ﷺ يَقُولُ: قَالَ اللهُ تَبَارَكَ وَتَعَالَى: يَا ابْنَ آدَمَ، إِنَّكَ مَا دَعَوْتَنِي وَرَجَوْتَنِي غَفَرْتُ لَكَ عَلَى مَا كَانَ فِيكَ وَلَا أُبَالِي، يَا ابْنَ آدَمَ لَوْ بَلَغَتْ ذُنُوبُكَ عَنَانَ السَّمَاءِ، ثُمَّ اسْتَغْفَرْتَنِي غَفَرْتُ لَكَ وَلَا أُبَالِي، يَا ابْنَ آدَمَ، إِنَّكَ لَوْ أَتَيْتَنِي بِقُرَابِ الْأَرْضِ خَطَايَا ثُمَّ لَقِيتَنِي لَا تُشْرِكُ بِي شَيْئًا لَأَتَيْتُكَ بِقُرَابِهَا مَغْفِرَةً. \nهَذَا حَدِيثٌ حَسَنٌ غَرِيبٌ لَا نَعْرِفُهُ إِلَّا مِنْ هَذَا الْوَجْهِ. ', 'އަނަސް ބިން މާލިކު ރިވާ ކުރެއްވިއެވެ: (ޤުދުސީ ޙަދީޘެއްގައި) ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ”ﷲ ތަބާރަކަ ވަތަޢާލާ ވަޙީ ކުރެއްވިއެވެ: އޭ އާދަމުގެ ދަރިއާއެވެ. ހަމަކަށަވަރުން ތިބާ، ތިމަން އިލާހަށް ދުޢާކޮށް ދަންނަވައި އަދި ތިމަން އިލާހުގެ ޙަޟްރަތުން އުންމީދު ކުރާހައި ހިނދަކު، ތިބާގެ ކިބައިން ވެވިފައިވާ ފާފަތައް ތިމަން އިލާހު ފުއްސަވައި ދެއްވާނަމެވެ. އަދި (އެ ފާފަތަކަށް) ތިމަން އިލާހު ނުބައްލަވާނަމެވެ. އޭ އާދަމުގެ ދަރިއާއެވެ. އުޑުގެ ވިލާތަކާ ހަމައަށް ތިބާގެ ފާފަތައް ފޯރިކަމުގައި ވިޔަސް، ފާފަ ފުއްސަވައި ދެއްވުމަށް އެދި ތިބާ ދަންނަވައިފި ނަމަ، ތިމަން އިލާހު ތިބާއަށް ފާފަ ފުއްސަވައި ދެއްވާނަމެވެ. އަދި (އެ ފާފަތަކަށް) ތިމަން އިލާހު ނުބައްލަވާނަމެވެ. އޭ އާދަމުގެ ދަރިއާއެވެ. ހަމަކަށަވަރުން ބިން ފުރޭހައި ވަރަށް ތިބާ ފާފަތަކައިގެން ތިމަން އިލާހާ ބައްދަލުވި ކަމުގައި ވިޔަސް، ތިބާ ތިމަން އިލާހާ އެއްވެސް ފަރާތެއް ޝަރީކުކޮށްފައި ނުވާ ނަމަ، ހަމަ އެއާ އެއްވަރަށް (އެބަހީ: ބިން ފުރޭހައި ވަރަށް) ތިމަން އިލާހު ތިބާއާ ފާފަ ފުއްސެވުމައިގެން ތިމަން އިލާހު ތިބާއާ ބައްދަލު ކުރައްވާނެކަން ކަށަވަރެވެ.“', ''], ['مسند أحمد 17186', 'حَدَّثَنَا أَبُو الْمُغِيرَةِ قَالَ: حَدَّثَنَا سُلَيْمَانُ بْنُ سُلَيْمٍ الْكِنَانِيُّ قَالَ: حَدَّثَنَا يَحْيَى بْنُ جَابِرٍ الطَّائِيُّ قَالَ: سَمِعْتُ الْمِقْدَامَ بْنَ مَعْدِي كَرِبَ الْكِنْدِيَّ قَالَ: سَمِعْتُ رَسُولَ اللهِ ﷺ يَقُولُ: مَا مَلَأَ ابْنُ آدَمَ وِعَاءً شَرٌّ مِنْ بَطْنٍ، حَسْبُ ابْنِ آدَمَ أُكُلَاتٌ يُقِمْنَ صُلْبَهُ، فَإِنْ كَانَ لَا مَحَالَةَ، فَثُلُثٌ طَعَامٌ، وَثُلُثٌ شَرَابٌ، وَثُلُثٌ لِنَفَسِهِ. ', 'އަލްމިޤްދާމު ބިން މަޢުދީ ކަރިބު އަލްކިންދީ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ”އާދަމުގެ ދަރިޔާ ބަނޑަށް ވުރެ ނުބައި ކަންވާރެއް ނުފުރައެވެ. އާދަމުގެ ދަރިޔާއަށް، އޭނާގެ ބުރަކަށި ސީދާ ތެދަށް ބަހައްޓައިދޭނެ މަދު ކާއެތިކޮޅަކުން ފުދޭނެއެވެ. ނަމަވެސް އެ ވަރުން ނުފުދޭ ނަމަ، ފަހެ (ބަނޑުގެ) ތިންބައި ކުޅަ އެއްބައި އޭނާގެ ކާއެއްޗެއްސަށް ބަހައްޓާށެވެ. އަދި ތިންބައި ކުޅަ އެއްބައި އޭނާގެ ބޯއެއްޗެއްސަށް ބަހައްޓާށެވެ. އަދި ތިންބައި ކުޅަ އެއްބައި އޭނާ ނޭވާލުމަށް (ހުސްކޮށް) ބަހައްޓާށެވެ.“', ''], ['مسند أحمد 205', 'أَخْبَرَنَا عَبْدُ اللهِ بْنُ يَزِيدَ قَالَ: ثَنَا حَيْوَةُ بْنُ شُرَيْحٍ، قَالَ: أَخْبَرَنِي بَكْرُ بْنُ عَمْرٍو أَنَّهُ سَمِعَ عَبْدَ اللهِ بْنَ هُبَيْرَةَ أَنَّهُ سَمِعَ أَبَا تَمِيمٍ الْجَيْشَانِيَّ يَقُولُ: إِنَّهُ سَمِعَ عُمَرَ بْنَ الْخَطَّابِ يَقُولُ: إِنَّهُ سَمِعَ رَسُولَ اللهِ ﷺ يَقُولُ: لَوْ أَنَّكُمْ تَوَكَّلُونَ عَلَى اللهِ حَقَّ تَوَكُّلِهِ لَرَزَقَكُمْ كَمَا يَرْزُقُ الطَّيْرَ تَغْدُو خِمَاصًا وَتَرُوحُ بِطَانًا. ', 'ޢުމަރު ބިން އަލްޚައްޠާބު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ”ﷲ އާ ވަކީލު ކުރަން ޖެހޭ ޙައްޤު ގޮތުގައި ތިޔަބައިމީހުން އެ އިލާހާ ވަކީލު ކުރާ ނަމަ، ﷲ ދޫނިތަކަށް ރިޒުޤު ދެއްވާ ފަދައިން އެ އިލާހު ތިޔަބައިމީހުންނަށް ވެސް ރިޒުޤު ދެއްވާނެކަން ކަށަވަރެވެ. ހެނދުނު ހެނދުނާ އެ ދޫނިތައް ނުކުމެގެން ދަނީ ހުސް ބަނޑާއެވެ. އަދި ހަވީރު އެނބުރި އަންނަނީ ބަނޑު ފުރައިގެންނެވެ.“', ''], ['مسند أحمد 17680', 'حَدَّثَنَا عَلِيُّ بْنُ عَيَّاشٍ، حَدَّثَنَا حَسَّانُ بْنُ نُوحٍ، عَنْ عَمْرِو بْنِ قَيْسٍ، عَنْ عَبْدِ اللهِ بْنِ بُسْرٍ، قَالَ: أَتَى النَّبِيَّ ﷺ أَعْرَابِيَّانِ، فَقَالَ أَحَدُهُمَا: مَنْ خَيْرُ الرِّجَالِ يَا مُحَمَّدُ؟ قَالَ النَّبِيُّ ﷺ: «مَنْ طَالَ عُمْرُهُ، وَحَسُنَ عَمَلُهُ» وَقَالَ الْآخَرُ: إِنَّ شَرَائِعَ الْإِسْلَامِ قَدْ كَثُرَتْ عَلَيْنَا، فَبَابٌ نَتَمَسَّكُ بِهِ جَامِعٌ؟ قَالَ: «لَا يَزَالُ لِسَانُكَ رَطْبًا مِنْ ذِكْرِ اللهِ [عَزَّ وَجَلَّ]». ', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ގަބޫލު ކުރެވިގެންވާ ފުރިހަމަ ޙައްޖަކަށް ސުވަރުގެ ފިޔަވައި އެހެން ޖަޒާއެއް ނުވެއެވެ. އަދި އެއް ޢުމްރާއަކަށް ފަހު އަނެއް ޢުމްރާ (ކުރުމަކީ) އޭގެ ދެމެދުގައި ހިނގައިދިޔަ ފާފަތަކަށްވާ ކައްފާރާއެކެވެ.“', ''], ['سنن الترمذي 889', 'حَدَّثَنَا مُحَمَّدُ بْنُ بَشَّارٍ، قَالَ: حَدَّثَنَا يَحْيَى بْنُ سَعِيدٍ، وَعَبْدُ الرَّحْمَنِ بْنُ مَهْدِيٍّ قَالَا: حَدَّثَنَا سُفْيَانُ، عَنْ بُكَيْرِ بْنِ عَطَاءٍ، عَنْ عَبْدِ الرَّحْمَنِ بْنِ يَعْمَرَ؛ أَنَّ نَاسًا مِنْ أَهْلِ نَجْدٍ أَتَوْا رَسُولَ اللهِ ﷺ وَهُوَ بِعَرَفَةَ، فَسَأَلُوهُ، فَأَمَرَ مُنَادِيًا فَنَادَى: الْحَجُّ عَرَفَةُ، مَنْ جَاءَ لَيْلَةَ جَمْعٍ قَبْلَ طُلُوعِ الْفَجْرِ فَقَدْ أَدْرَكَ الْحَجَّ، أَيَّامُ مِنًى ثَلَاثَةٌ، { فَمَنْ تَعَجَّلَ فِي يَوْمَيْنِ فَلا إِثْمَ عَلَيْهِ وَمَنْ تَأَخَّرَ فَلا إِثْمَ عَلَيْهِ }. \nقَالَ: وَزَادَ يَحْيَى: وَأَرْدَفَ رَجُلًا فَنَادَى. ', 'ޢަބްދުއްރަޙްމާން ބިން ޔަޢުމަރު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޢަރަފާތުގައި ހުންނަވަނިކޮށް، ނަޖުދު ކަރައިގެ ބަޔަކު އެ ނަބިއްޔާގެ އަރިހަށް އަތުވެއްޖެއެވެ. އަދި އެބައިމީހުން (ޙައްޖާ ބެހޭ ގޮތުން) އެ ނަބިއްޔާއާ ސުވާލު ކުރެއްވިއެވެ. ދެން (މި ފަދައިން) ގޮވައިލެއްވުމަށް ނަބިއްޔާ މީހަކަށް އަމުރު ކުރެއްވިއެވެ: ”ޙައްޖަކީ ޢަރަފާތެވެ. (އެބަހީ: ޢަރަފާތުގައި މަޑުނުކޮށް ޙައްޖު ޞައްޙައެއް ނުވާނެއެވެ.) (ޢީދު ދުވަހު) ފަތިސް ވުމުގެ ކުރިން ޖަމްޢުގެ ރޭގައި (އެބަހީ: މުޒްދަލިފާގެ ވިލޭރޭގައި ޢަރަފާތަށް) އަތުވެއްޖެ މީހާ، ފަހެ ހަމަކަށަވަރުން ޙައްޖު ހޯދައިފިއެވެ. މިނާގައި (މަޑުކުރުން ވަނީ އައްޔާމުއް ތަޝްރީޤުގެ) ތިން ދުވަހަށެވެ.“ (ދެން މި އާޔަތް ކިޔަވައި ވިދާޅުވިއެވެ:) ❞ފަހެ، ދެ ދުވަހުގެ ތެރޭގައި (މިނާގައި މަޑުކުރުން ނިންމައި އެނބުރި ދިޔުމަށް) އަވަސްވެގަތް މީހާގެ މައްޗަކަށް ފާފައެއް ނެތެވެ. އަދި (ތިން ދުވަހަށް) ލަސްކުރި މީހާގެ މައްޗަކަށް ވެސް ފާފައެއް ނެތެވެ.❝', ''], ['سنن النسائي 2622', 'أَخْبَرَنَا عَبْدَةُ بْنُ عَبْدِ اللهِ الصَّفَّارِ الْبَصْرِيُّ، قَالَ: حَدَّثَنَا سُوَيْدٌ - وَهُوَ ابْنُ عَمْرٍو الْكَلْبِيُّ -، عَنْ زُهَيْرٍ، قَالَ: حَدَّثَنَا سُهَيْلٌ، عَنْ سُمَيٍّ، عَنْ أَبِي صَالِحٍ، عَنْ أَبِي هُرَيْرَةَ قَالَ: قَالَ رَسُولُ اللهِ ﷺ: الْحَجَّةُ الْمَبْرُورَةُ لَيْسَ لَهَا جَزَاءٌ إِلَّا الْجَنَّةُ، وَالْعُمْرَةُ إِلَى الْعُمْرَةِ كَفَّارَةٌ لِمَا بَيْنَهُمَا. ', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ސސސ“', ''], ['سنن النسائي 2416', 'أَخْبَرَنَا أَبُو بَكْرِ بْنُ أَبِي النَّضْرِ قَالَ: حَدَّثَنِي أَبُو النَّضْرِ قَالَ: حَدَّثَنَا أَبُو إِسْحَاقَ الْأَشْجَعِيُّ - كُوفِيٌّ - عَنْ عَمْرِو بْنِ قَيْسٍ الْمُلَائِيِّ، عَنِ الْحُرِّ بْنِ الصَّيَّاحِ، عَنْ هُنَيْدَةَ بْنِ خَالِدٍ الْخُزَاعِيِّ، عَنْ حَفْصَةَ قَالَتْ: أَرْبَعٌ لَمْ يَكُنْ يَدَعُهُنَّ النَّبِيُّ ﷺ؛ صِيَامَ عَاشُورَاءَ، وَالْعَشْرَ، وَثَلَاثَةَ أَيَّامٍ مِنْ كُلِّ شَهْرٍ، وَرَكْعَتَيْنِ قَبْلَ الْغَدَاةِ.', 'ޙަފްޞާ ރިވާ ކުރެއްވިއެވެ: ހަތަރުކަމެއް ރަސޫލު ﷲ ﷺ ދޫނުކުރައްވައެވެ. އެއީ ޢާޝޫރާ ދުވަހު ރޯދަ ހިފުމާއި، ޒުލް ޙިއްޖާ މަހުގެ ދިހަ ދުވަހު ރޯދަ ހިފުމާއި، ކޮންމެ މަހެއްގެ ތިން ދުވަހު (ތޭރަ، ސާދަ، ފަނަރަވަނަ ދުވަހު) ރޯދަ ހިފުމާއި، އަދި ފަތިސް ނަމާދުގެ ކުރީގައި ދެ ރަކުޢަތް ކުރުމެވެ.“', ''], ['سنن الترمذي 2377', 'حَدَّثَنَا مُوسَى بْنُ عَبْدِ الرَّحْمَنِ الْكِنْدِيُّ، قَالَ: حَدَّثَنَا زَيْدُ بْنُ حُبَابٍ قَالَ: أَخْبَرَنِي الْمَسْعُودِيُّ، قَالَ: حَدَّثَنَا عَمْرُو بْنُ مُرَّةَ، عَنْ إِبْرَاهِيمَ، عَنْ عَلْقَمَةَ، عَنْ عَبْدِ اللهِ قَالَ: نَامَ رَسُولُ اللهِ ﷺ عَلَى حَصِيرٍ، فَقَامَ وَقَدْ أَثَّرَ فِي جَنْبِهِ، فَقُلْنَا: يَا رَسُولَ اللهِ لَوِ اتَّخَذْنَا لَكَ وِطَاءً فَقَالَ: مَا لِي وَلِلدُّنْيَا، مَا أَنَا فِي الدُّنْيَا إِلَّا كَرَاكِبٍ اسْتَظَلَّ تَحْتَ شَجَرَةٍ ثُمَّ رَاحَ وَتَرَكَهَا. \nوَفِي الْبَابِ عَنِ ابْنِ عُمَرَ، وَابْنِ عَبَّاسٍ. \nهَذَا حَدِيثٌ حَسَنٌ صَحِيحٌ. ', 'ޢަބްދު ﷲ ބިން މަސްޢޫދު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ކުނަލެއް މަތީ އަވަހާރަލެއްޕެވިއެވެ. ދެން ހޭފުޅުލެވުން އިރު ނަބިއްޔާގެ އަރިކަށިފުޅުގައި އޭގެ އަސަރު ހުއްޓެވެ. ތިމަންމެން ދެންނެވީމެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. އިބަ ނަބިއްޔާއަށް ޓަކައި (ރަނގަޅު) އެނދެއް ތިމަންމެން ތައްޔާރު ކުރެވިދާނެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާއާ ދުނިޔެއާ ހުރީ ކޮން ގުޅުމެއްތޯއެވެ؟ ދަތުރުވެރިއަކު ގަހެއްގެ ހިޔަލުގައި މަޑުކޮށްލުމުށް ފަހު، އެ ތަން ދޫކޮށްލައި ދިޔަފަދަ މީހަކަށް މެނުވީ ތިމަން ނަބިއްޔާ މި ދުނިޔެ މަތީ ނުވަމެވެ.“', ''], ['سنن الترمذي 241', 'حَدَّثَنَا عُقْبَةُ بْنُ مُكْرَمٍ وَنَصْرُ بْنُ عَلِيٍّ، قَالَا: حَدَّثَنَا سَلْمُ بْنُ قُتَيْبَةَ، عَنْ طُعْمَةَ بْنِ عَمْرٍو، عَنْ حَبِيبِ بْنِ أَبِي ثَابِتٍ، عَنْ أَنَسِ بْنِ مَالِكٍ، قَالَ: قَالَ رَسُولُ اللهِ ﷺ: مَنْ صَلَّى لِلهِ أَرْبَعِينَ يَوْمًا فِي جَمَاعَةٍ يُدْرِكُ التَّكْبِيرَةَ الْأُولَى كُتِبَتْ لَهُ بَرَاءَتَانِ: بَرَاءَةٌ مِنَ النَّارِ، وَبَرَاءَةٌ مِنَ النِّفَاقِ. \nوَقَدْ رُوِيَ هَذَا الْحَدِيثُ عَنْ أَنَسٍ مَوْقُوفًا، وَلَا أَعْلَمُ أَحَدًا رَفَعَهُ إِلَّا مَا رَوَى سَلْمُ بْنُ قُتَيْبَةَ، عَنْ طُعْمَةَ بْنِ عَمْرٍو، وَإِنَّمَا يُرْوَى هَذَا عَنْ حَبِيبِ بْنِ أَبِي حَبِيبٍ الْبَجَلِيِّ، عَنْ أَنَسِ بْنِ مَالِكٍ قَوْلَهُ. ', 'އަނަސް ބިން މާލިކު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ފުރަތަމަ ތަކްބީރު ލިބޭގޮތަށް ﷲ އަށްޓަކައި ސާޅީސް ދުވަސްވާންދެން ޖަމާޢަތުގައި ނަމާދުކޮށްފި މީހާ، އޭނާ ދެ ކަމަކުން ބަރީއަވާނޭ ކަމަށް ލިޔެވޭނެއެވެ. އެއީ ނަރަކައިން ބަރީއަވުމާއި، މުނާފިޤުކަމުން ބަރީއަވުމެވެ.“', ''], ['صحيح ابن خزيمة 1888', 'حَدَّثَنَا الرَّبِيعُ بْنُ سُلَيْمَانَ، قَالَ: حَدَّثَنَا ابْنُ وَهْبٍ، قَالَ: أَخْبَرَنِي سُلَيْمَانُ وَهُوَ ابْنُ بِلَالٍ، عَنْ كَثِيرِ بْنِ زَيْدٍ، عَنِ الْوَلِيدِ بْنِ رَبَاحٍ، عَنْ أَبِي هُرَيْرَةَ أَنَّ رَسُولَ اللهِ - ﷺ - رَقِيَ الْمِنْبَرَ، فَقَالَ: " آمِينَ، آمِينَ، آمِينَ ". فَقِيلَ لَهُ: يَا رَسُولَ اللهِ، مَا كُنْتَ تَصْنَعُ هَذَا؟ فَقَالَ: قَالَ لِي جِبْرِيلُ: أَرْغَمَ اللهُ أَنْفَ عَبْدٍ - أَوْ بَعُدَ - دَخَلَ رَمَضَانَ فَلَمْ يُغْفَرْ لَهُ، فَقُلْتُ: آمِينَ، ثُمَّ قَالَ: رَغِمَ أَنْفُ عَبْدٍ - أَوْ بَعُدَ - أَدْرَكَ وَالِدَيْهِ، أَوْ أَحَدَهُمَا لَمْ يُدْخِلْهُ الْجَنَّةَ، فَقُلْتُ: آمِينَ، ثُمَّ قَالَ: رَغِمَ أَنْفُ عَبْدٍ - أَوْ بَعُدَ - ذُكِرْتَ عِنْدَهُ فَلَمْ يُصَلِّ عَلَيْكَ، فَقُلْتُ: آمِينَ. ', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ މިންބަރު މައްޗަށް އަރައިވަޑައިގެން ވިދާޅިއެވެ: ”އާމީން، އާމީން، އާމީން.“ ނަބިއްޔާއަށް ދެންނެވުނެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ތިޔަފަދައިން ކަންތައް ކުރެއްވީ ކީއްވެގެންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ޖިބްރީލު ތިމަން ނަބިއްޔާއަށް ދެންނެވިއެވެ: ރަމަޟާން މަހާ ބައްދަލުވެ އެ އަޅެއްގެ ފާފަ ނުފުއްސެވޭ އަޅަކު ﷲ ނިކަމެތިކުރައްވާށިއެވެ. (ނުވަތަ އެ މީހަކަށް ހިތާމައާއި ގެއްލުން ހުއްޓެވެ.) ފަހެ ތިމަން ނަބިއްޔާ ވިދާޅުވީމެވެ: އާމީން. އެއަށްފަހު ޖިބްރީލު ދެންނެވިއެވެ: އެ އަޅެއްގެ މައިންބަފައިންނާ ބައްދަލުވެ، ނުވަތަ އެ ދެމީހުން ކުރެ އެކަކާ ބައްދަލުވެ، (މައިންބަފައިންނަށް ހެޔޮކޮށް ހިތުމުގެ) ސަބަބުން ސުވަރުގެއަށް ނުވައްދަވާ އަޅަކު ﷲ ނިކަމެތިކުރައްވާށިއެވެ. ފަހެ ތިމަން ނަބިއްޔާ ވިދާޅުވީމެވެ: އާމީން. އެއަށްފަހު ޖިބްރީލުގެ ދެންނެވިއެވެ: އެ އަޅެއްގެ ހާޒިރުގައި އިބަ ނަބިއްޔާގެ ނަންގަނެވޭ ހިނދު އިބަ ނަބިއްޔާގެ މައްޗަށް ޞަލަވާތް ނުކިޔާ އަޅަކު ﷲ ނިކަމެތިކުރައްވާށިއެވެ. ފަހެ ތިމަން ނަބިއްޔާ ވިދާޅުވީމެވެ: އާމީން.“', ''], ['سنن النسائي 2526', 'أَخْبَرَنَا عَبْدُ الْوَهَّابِ بْنُ عَبْدِ الْحَكَمِ، عَنْ حَجَّاجٍ، قَالَ ابْنُ جُرَيْجٍ: أَخْبَرَنِي عُثْمَانُ بْنُ أَبِي سُلَيْمَانَ، عَنْ عَلِيٍّ الْأَزْدِيِّ، عَنْ عُبَيْدِ بْنِ عُمَيْرٍ، عَنْ عَبْدِ اللهِ بْنِ حُبْشِيٍّ الْخَثْعَمِيِّ أَنَّ النَّبِيَّ ﷺ سُئِلَ: أَيُّ الْأَعْمَالِ أَفْضَلُ؟ قَالَ: " إِيمَانٌ لَا شَكَّ فِيهِ، وَجِهَادٌ لَا غُلُولَ فِيهِ، وَحَجَّةٌ مَبْرُورَةٌ ". قِيلَ: فَأَيُّ الصَّلَاةِ أَفْضَلُ؟ قَالَ: " طُولُ الْقُنُوتِ "، قِيلَ: فَأَيُّ الصَّدَقَةِ أَفْضَلُ؟ قَالَ: " جُهْدُ الْمُقِلِّ "، قِيلَ: فَأَيُّ الْهِجْرَةِ أَفْضَلُ؟ قَالَ: " مَنْ هَجَرَ مَا حَرَّمَ اللهُ عَزَّ وَجَلَّ "، قِيلَ: فَأَيُّ الْجِهَادِ أَفْضَلُ؟ قَالَ: " مَنْ جَاهَدَ الْمُشْرِكِينَ بِمَالِهِ وَنَفْسِهِ "، قِيلَ: فَأَيُّ الْقَتْلِ أَشْرَفُ؟ قَالَ: " مَنْ أُهَرِيقَ دَمُهُ وَعُقِرَ جَوَادُهُ. ', 'ޢަބްދު ﷲ ބިން ޙުބްޝީ އަލްޖަޘްޢަމީ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ އަށް ސުވާލު ކުރެއްވުނެވެ: އެންމެ ހެޔޮވެގެންވާ ޢަމަލަކީ ކޮބައިތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެއްވެސް ޝައްކުކަމެއް ނެތް އީމާންކަމެވެ. އަދި ޣަނީމާ މުދަލަށް ޚިޔާނާތްތެރިވުމެއް ނެތް ޖިހާދެވެ. އަދި ގަބޫލު ކުރެވިގެންވާ ފުރިހަމަ ޙެއްޖެވެ.“ ނަބިއްޔާއަށް ދެންނެވުނެވެ: ފަހެ އެންމެ ހެޔޮވެގެންވާ ނަމާދަކީ ކޮބައިތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ދާއިމީ ގޮތެއްގައި ކުރެވޭ ނަމާދެވެ.“ ނަބިއްޔާއަށް ދެންނެވުނެވެ: ފަހެ އެންމެ ހެޔޮވެގެންވާ ޞަދަޤާތަކީ ކޮބައިތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”މުދާ މަދުން ލިބިފައި ހުރި މީހާ (އޭނައަށް ވީވަރަކުން) ކުރާ ޞަދަޤާތެވެ.“ ނަބިއްޔާއަށް ދެންނެވުނެވެ: ފަހެ އެންމެ ހެޔޮވެގެންވާ ހިޖުރައަކީ ކޮބައިތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲ ޢައްޒަ ވަޖައްލަ ޙަރާމް ކުރެއްވި ކަންތައްތައް ދޫކޮށްލި މީހާއެވެ.“ ނަބިއްޔާއަށް ދެންނެވުނެވެ: ފަހެ އެންމެ ހެޔޮވެގެންވާ ޖިހާދަކީ ކޮބައިތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ މީހެއްގެ މުދަލާއި ފުރާނައިން މުޝްރިކުންނާ ދެކޮޅަށް ޖިހާދުކުރި މީހާއެވެ.“ ނަބިއްޔާއަށް ދެންނެވުނެވެ: ފަހެ އެންމެ ޝަރަފުވެރި ޤަތުލުވުމަކީ ކޮބައިތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ މީހެއްގެ ލޭ އޮހޮރެވި، އަދި އޭނާގެ އަސް މަރާލެވޭ ނުވަތަ ކޮށާލެވޭ މީހާއެވެ.“', ''], ['سنن النسائي 4165', 'أَخْبَرَنَا أَحْمَدُ بْنُ عَبْدِ اللهِ بْنِ الْحَكَمِ، قَالَ: حَدَّثَنَا مُحَمَّدُ بْنُ جَعْفَرٍ، قَالَ: حَدَّثَنَا شُعْبَةُ، عَنْ عَمْرِو بْنِ مُرَّةَ، عَنْ عَبْدِ اللهِ بْنِ الْحَارِثِ، عَنْ أَبِي كَثِيرٍ، عَنْ عَبْدِ اللهِ بْنِ عَمْرٍو، قَالَ: قَالَ رَجُلٌ: يَا رَسُولَ اللهِ، أَيُّ الْهِجْرَةِ أَفْضَلُ؟ قَالَ: أَنْ تَهْجُرَ مَا كَرِهَ رَبُّكَ عَزَّ وَجَلَّ. وَقَالَ رَسُولُ اللهِ ﷺ: الْهِجْرَةُ هِجْرَتَانِ: هِجْرَةُ الْحَاضِرِ، وَهِجْرَةُ الْبَادِي، فَأَمَّا الْبَادِي فَيُجِيبُ إِذَا دُعِيَ، وَيُطِيعُ إِذَا أُمِرَ، وَأَمَّا الْحَاضِرُ فَهُوَ أَعْظَمُهُمَا بَلِيَّةً، وَأَعْظَمُهُمَا أَجْرًا. ', 'ޢަބްދު ﷲ ބިން ޢަމްރު ރިވާ ކުރެއްވިއެވެ: މީހަކު ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. އެންމެ ހެޔޮވެގެންވާ ހިޖުރައަކީ ކޮބައިތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާގެ ރައްބު، ﷲ ޢައްޒަ ވަޖައްލަ ރުހިވޮޑިގެން ނުވާ ކަންތައްތައް ތިބާ ދޫކޮށްލުމެވެ.“ އަދި ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ދެ ވައްތަރެއްގެ ހިޖުރަވެއެވެ. ރަށްފުށުގެ އަހުލުވެރިޔާގެ ހިޖުރައާއި، ބާދިޔާގެ އަހުލުވެރިޔާގެ ހިޖުރައެވެ. ބާދިޔާގެ އަހުލުވެރިޔާގެ (ހިޖުރައިގައި)، ފަހެ އޭނާއަކަށް (ވަޠަން ދޫކޮށް ހިޖުރަ ކުރުމެއް ނެތެވެ. އެހެނެއްކަމަކު) އޮތީ (ޖިހާދަށް) އޭނާއަށް ގޮވާލެވޭ ހިނދު އިޖާބަ ދިނުމާއި، (ވެރިމީހާ) އޭނާ އަމުރެއް ކުރާ ހިނދު ތަބާވުމެވެ. އަދި ރަށްފުށުގެ އަހުލުވެރިޔާގެ (ހިޖުރައިގައި)، ފަހެ އޭނާއީ އެ ދެމީހުންކުރެ ބޮޑަށް އިމްތިޚާނު ކުރެވޭނޭ އަދި ބޮޑަށް ސަވާބު ލިބޭނޭ މީހާއެވެ.“', ''], ['سنن الترمذي 2910', 'حَدَّثَنَا مُحَمَّدُ بْنُ بَشَّارٍ، قَالَ: حَدَّثَنَا أَبُو بَكْرٍ الْحَنَفِيُّ، قَالَ: حَدَّثَنَا الضَّحَّاكُ بْنُ عُثْمَانَ، عَنْ أَيُّوبَ بْنِ مُوسَى قَالَ: سَمِعْتُ مُحَمَّدَ بْنَ كَعْبٍ الْقُرَظِيَّ يَقُولُ: سَمِعْتُ عَبْدَ اللهِ بْنَ مَسْعُودٍ يَقُولُ: قَالَ رَسُولُ اللهِ ﷺ: مَنْ قَرَأَ حَرْفًا مِنْ كِتَابِ اللهِ فَلَهُ بِهِ حَسَنَةٌ، وَالْحَسَنَةُ بِعَشْرِ أَمْثَالِهَا، لَا أَقُولُ (الم) حَرْفٌ، وَلَكِنْ أَلِفٌ حَرْفٌ، وَلَامٌ حَرْفٌ، وَمِيمٌ حَرْفٌ. \nوَيُرْوَى هَذَا الْحَدِيثُ مِنْ غَيْرِ هَذَا الْوَجْهِ عَنِ ابْنِ مَسْعُودٍ، رَوَاهُ أَبُو الْأَحْوَصِ، عَنِ ابْنِ مَسْعُودٍ، رَفَعَهُ بَعْضُهُمْ وَوَقَفَهُ بَعْضُهُمْ عَنِ ابْنِ مَسْعُودٍ. \nهَذَا حَدِيثٌ حَسَنٌ صَحِيحٌ غَرِيبٌ مِنْ هَذَا الْوَجْهِ، سَمِعْتُ قُتَيْبَةَ بْنَ سَعِيدٍ يَقُولُ: بَلَغَنِي أَنَّ مُحَمَّدَ بْنَ كَعْبٍ الْقُرَظِيَّ وُلِدَ فِي حَيَاةِ النَّبِيِّ ﷺ، وَمُحَمَّدُ بْنُ كَعْبٍ يُكْنَى أَبَا حَمْزَةَ. \n', 'ޢަބްދުﷲ ބިން މަސްޢޫދު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲގެ ފޮތުން (އެންމެ) ޙަރްފެއް ކިޔައިފި މީހަކަށް، އޭގެ ސަބަބުން އޭނާއަށް އެއް ހެޔޮކަން ލިބިގެންވެއެވެ. އަދި (ކޮންމެ) ހެޔޮ ކަމަކަށް އޭގެ ދިހަ ގުނަ ހެޔޮކަންތައް ލިބިގެންވެއެވެ. އަލިފް ލާމް މީމް، އެއީ އެންމެ ޙަރްފެއް ކަމުގައި ތިމަން ނަބިއްޔާ ޙަދީޘް ނުކުރައްވަމެވެ. އެހެނަސް އަލިފް، އެއީ ޙަރްފެކެވެ. އަދި ލާމު، އެއީ ޙަރްފެކެވެ. އަދި މީމު، އެއީ ޙަރްފެކެވެ.“ (ނޯޓް: ޙަރްފަކީ ކޮބައިކަމާމެދު ޢިލްމުވެރިން ވަނީ ޢިޚްތިލާފުވެފައިވެ. އެއްބައި ޢިލްމުވެރިން ގާތު އެއީ އަކުރެކެވެ. އަނެއްބައި ޢިލްމުވެރިން ގާތު އެއީ ބަހެކެވެ.)', ''], ['سنن أبي داود 3674', 'حَدَّثَنَا عُثْمَانُ بْنُ أَبِي شَيْبَةَ، قَالَ: نَا وَكِيعُ بْنُ الْجَرَّاحِ، عَنْ عَبْدِ الْعَزِيزِ بْنِ عُمَرَ، عَنْ أَبِي عَلْقَمَةَ مَوْلَاهُمْ وَعَبْدِ الرَّحْمَنِ بْنِ عَبْدِ اللهِ الْغَافِقِيِّ أَنَّهُمَا سَمِعَا ابْنَ عُمَرَ يَقُولُ: قَالَ رَسُولُ اللهِ ﷺ: لَعَنَ اللهُ الْخَمْرَ، وَشَارِبَهَا، وَسَاقِيَهَا، وَبَايِعَهَا، وَمُبْتَاعَهَا، وَعَاصِرَهَا، وَمُعْتَصِرَهَا، وَحَامِلَهَا، وَالْمَحْمُولَةَ إِلَيْهِ. ', 'އިބްނު ޢުމަރު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ރަލާއި، އެ ބޯ މީހާއާއި، އެ ބޯން ދޭ މީހާއާއި، އެ ވިއްކާ މީހާއާއި، އެ ގަންނަ މީހާއާއި، (އެ އުފެއްދުމަށް) އެ ފެލާ މިހާއާއި، އެ މީހަކަށް އެ ފެލެވޭ މީހާއާއި، އެ އުފުއްލާ މީހާއާއި، އެ އުފުލައިގެން ގެންގޮސް ދެވޭ މީހާ، މި އެންމެންނަށް ﷲ ވަނީ ލަޢުނަތް ލައްވާފައެވެ.“', ''], ['سنن ابن ماجه 3371', 'حَدَّثَنَا الْحُسَيْنُ بْنُ الْحَسَنِ الْمَرْوَزِيُّ، حَدَّثَنَا ابْنُ أَبِي عَدِيٍّ. (ح) وَحَدَّثَنَا إِبْرَاهِيمُ بْنُ سَعِيدٍ الْجَوْهَرِيُّ، حَدَّثَنَا عَبْدُ الْوَهَّابِ، جَمِيعًا عَنْ رَاشِدٍ أَبِي مُحَمَّدٍ الْحِمَّانِيِّ، عَنْ شَهْرِ بْنِ حَوْشَبٍ، عَنْ أُمِّ الدَّرْدَاءِ، عَنْ أَبِي الدَّرْدَاءِ قَالَ: أَوْصَانِي خَلِيلِي ﷺ: لَا تَشْرَبِ الْخَمْرَ، فَإِنَّهَا مِفْتَاحُ كُلِّ شَرٍّ. ', 'އަބުއްދަރްދާއު ރިވާ ކުރެއްވިއެވެ: ތިމަންނަގެ ގާތް މިތުރު (ނަބިއްޔާ) ﷺ ތިމަންނައަށް ވަޞިއްޔަތް ކުރެއްވިއެވެ: ”ރާ ނުބޯށެވެ. ހަމަކަށަވަރުން އެއީ ހުރިހާ ނުބައި ކަމެއްގެ ތަޅުދަނޑިއެވެ.“', ''], ['سنن النسائي 5666', 'أَخْبَرَنَا سُوَيْدٌ قَالَ: أَنْبَأَنَا عَبْدُ اللهِ، عَنْ مَعْمَرٍ، عَنِ الزُّهْرِيِّ، عَنْ أَبِي بَكْرِ بْنِ عَبْدِ الرَّحْمَنِ بْنِ الْحَارِثِ، عَنْ أَبِيهِ قَالَ: سَمِعْتُ عُثْمَانَ رَضِيَ اللهُ عَنْهُ يَقُولُ: اجْتَنِبُوا الْخَمْرَ؛ فَإِنَّهَا أُمُّ الْخَبَائِثِ، إِنَّهُ كَانَ رَجُلٌ مِمَّنْ خَلَا قَبْلَكُمْ تَعَبَّدَ، فَعَلِقَتْهُ امْرَأَةٌ غَوِيَّةٌ فَأَرْسَلَتْ إِلَيْهِ جَارِيَتَهَا، فَقَالَتْ لَهُ: إِنَّا نَدْعُوكَ لِلشَّهَادَةِ، فَانْطَلَقَ مَعَ جَارِيَتِهَا فَطَفِقَتْ كُلَّمَا دَخَلَ بَابًا أَغْلَقَتْهُ دُونَهُ، حَتَّى أَفْضَى إِلَى امْرَأَةٍ وَضِيئَةٍ، عِنْدَهَا غُلَامٌ، وَبَاطِيَةُ خَمْرٍ، فَقَالَتْ: إِنِّي وَاللهِ مَا دَعَوْتُكَ لِلشَّهَادَةِ، وَلَكِنْ دَعَوْتُكَ لِتَقَعَ عَلَيَّ، أَوْ تَشْرَبَ مِنْ هَذِهِ الْخَمْرَةِ كَأْسًا، أَوْ تَقْتُلَ هَذَا الْغُلَامَ. قَالَ: فَاسْقِينِي مِنْ هَذَا الْخَمْرِ كَأْسًا، فَسَقَتْهُ كَأْسًا، قَالَ: زِيدُونِي فَلَمْ يَرِمْ حَتَّى وَقَعَ عَلَيْهَا، وَقَتَلَ النَّفْسَ، فَاجْتَنِبُوا الْخَمْرَ؛ فَإِنَّهَا وَاللهِ لَا يَجْتَمِعُ الْإِيمَانُ وَإِدْمَانُ الْخَمْرِ إِلَّا لَيُوشِكُ أَنْ يُخْرِجَ أَحَدُهُمَا صَاحِبَهُ. ', 'ޢަބްދުއްރަޙްމާން ބިން އަލްޙާރިޘް ރިވާ ކުރެއްވިއެވެ: ޢުޘްމާން ބިން ޢައްފާނު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވި އަޑު ތިމަން އެހީމެވެ: ތިޔަބައިމީހުން ރަލާ ދުރުވެގަންނާށެވެ. އެހެނީ ހަމަކަށަވަރުން އެއީ ހުރިހާ ހަޑިމުޑުދާރު ނުބައި ކަމެއްގެ މަންމައެވެ. ހަމަކަށަވަރުން ތިޔަބައިމީހުންގެ ކުރީގެ (އުންމަތްތަކުގެ ތެރެއިން) މީހާކު އަޅުކަމައިގެން ހުސްވެއުޅުނެވެ. ދެން ފާޙިޝް އަންހެނަކު އޭނާއަށް ހިތާވެގަތެވެ. އަދި އެ އަންހެނާގެ އަޅު އަންހެން ކުއްޖަކު އެ މީހާ ގާތަށް ފޮނުވިއެވެ. އެ ކުއްޖާ އައިސް ދެންނެވިއެވެ: ތިމަންމެން ތިބާއަށް މި ގޮވާލަނީ ކަމަކަށް ހެކުކުރަންށެވެ. ދެން އެ ކުއްޖާއާއެކު އެ އަޅުވެރިޔާ ހިނގައްޖެއެވެ. (އެ އަންހެނާގެ ގޭގައި) އެ އަޅުވެރިޔާ ވަތް ކޮންމެ ދޮރަކަށް ފަހު، ފަހަތުން އެ އަޅު ކުއްޖާ އެ ދޮރެއް ތަޅުލިއެވެ. ދެން އެ އަޅުވެރިޔާ ވަރަށް ރީތި އަންހެނަކާ ހިސާބަށް ފޯރައިފިއެވެ. އެ އަންހެނާއާއެކު ޚާދިމު ޅަ ފިރިހެން ކުއްޖަކާއި ބޮޑު ރާ ތައްޓެއް ހުއްޓެވެ. ދެން އަޅުވެރިޔާއަށް އެ އަންހެނާ ބުނެފިއެވެ: ﷲ ގަންދީ ބުނަމެވެ. ތިބާއަށް މި ގޮވާލީކީ ކަމަކަށް ހެކުކުރާކަށެއް ނޫނެވެ. އެހެނެއްކަމަކު ތިބާއަށް މި ގޮވާލީ ތިމަންނައާއެކު ޒިނޭ ކުރުމަށެވެ. ނޫނީ މި ރާއެތިން ތައްޓެއް ބޯށެވެ. ނޫނީ މި ޚާދިމު ޅަ ފިރިހެން ކުއްޖާ މަރާލާށެވެ. އެ މީހާ (އެންމެ ލުއި ގޮތަކަށް ވިސްނާފައި) ދެންނެވިއެވެ: އެހެންކަމުގައި ވަނީ ނަމަ، މި ރާއެތިން ތިމަންނައަށް ތައްޓެއް ބޯން ދޭށެވެ. ދެން އެ އަންހެނާ އޭނާއަށް ތައްޓެއް ބޯންދިނެވެ. (އެއް ތައްޓަށް ފަހު ބޯ ހިތްވެގަނެވެން) އެ މީހާ ދެންނެވިއެވެ: ތިމަންނައަށް އިތުރަށް ދޭށެވެ. ދެން އެހެން ބޮމުން ބޮމުން ގޮސް (މަސްތުވެގެން) އެ އަންހެނާއާއެކު ޒިނޭކޮށް، އަދި އެ ޅަ ފިރިހެން ކުއްޖާ މަރާލުމަށް ދާންދެން ރާބުއިން ހުއްޓާނުލިއެވެ. ވީމާ ތިޔަބައިމީހުން ރަލާ ދުރުވެގަންނާށެވެ. އެހެނީ ހަމަކަށަވަރުން ﷲ ގަންދީ ބުނަމެވެ. އީމާންކަމާއި ރަލަށް ދެވިހިފުން އެއްކޮށެއް ނޫޅެވޭނެއެވެ. އެހެނަސް އޭގެ ތެރެއިން އެކަތި އަނެކަތި ބޭރުކުރާނެކަން ކަށަވަރެވެ.', ''], ['سنن الترمذي 614', 'حَدَّثَنَا عَبْدُ اللهِ بْنُ أَبِي زِيَادٍ الْقَطَوَانِيُّ الْكُوفِيُّ، قَالَ: حَدَّثَنَا عُبَيْدُ اللهِ بْنُ مُوسَى، قَالَ: حَدَّثَنَا غَالِبٌ أَبُو بِشْرٍ، عَنْ أَيُّوبَ بْنِ عَائِذٍ الطَّائِيِّ، عَنْ قَيْسِ بْنِ مُسْلِمٍ، عَنْ طَارِقِ بْنِ شِهَابٍ، عَنْ كَعْبِ بْنِ عُجْرَةَ قَالَ: قَالَ لِي رَسُولُ اللهِ ﷺ: أُعِيذُكَ بِاللهِ يَا كَعْبُ بْنَ عُجْرَةَ مِنْ أُمَرَاءَ يَكُونُونَ مِنْ بَعْدِي فَمَنْ غَشِيَ أَبْوَابَهُمْ فَصَدَّقَهُمْ فِي كَذِبِهِمْ وَأَعَانَهُمْ عَلَى ظُلْمِهِمْ فَلَيْسَ مِنِّي وَلَسْتُ مِنْهُ، وَلَا يَرِدُ عَلَيَّ الْحَوْضَ، وَمَنْ غَشِيَ أَبْوَابَهُمْ أَوْ لَمْ يَغْشَ وَلَمْ يُصَدِّقْهُمْ فِي كَذِبِهِمْ، وَلَمْ يُعِنْهُمْ عَلَى ظُلْمِهِمْ فَهُوَ مِنِّي، وَأَنَا مِنْهُ وَسَيَرِدُ عَلَيَّ الْحَوْضَ، يَا كَعْبُ بْنَ عُجْرَةَ! الصَّلَاةُ بُرْهَانٌ وَالصَّوْمُ جُنَّةٌ حَصِينَةٌ، وَالصَّدَقَةُ تُطْفِئُ الْخَطِيئَةَ كَمَا يُطْفِئُ الْمَاءُ النَّارَ، يَا كَعْبُ بْنَ عُجْرَةَ! إِنَّهُ لَا يَرْبُو لَحْمٌ نَبَتَ مِنْ سُحْتٍ إِلَّا كَانَتِ النَّارُ أَوْلَى بِهِ. \nهَذَا حَدِيثٌ حَسَنٌ غَرِيبٌ مِنْ هَذَا الْوَجْهِ، لَا نَعْرِفُهُ إِلَّا مِنْ حَدِيثِ عُبَيْدِ اللهِ بْنِ مُوسَى. \nوَأَيُّوبُ بْنُ عَائِذٍ يُضَعَّفُ وَيُقَالُ: كَانَ يَرَى رَأْيَ الْإِرْجَاءِ. \nوَسَأَلْتُ مُحَمَّدًا عَنْ هَذَا الْحَدِيثِ فَلَمْ يَعْرِفْهُ إِلَّا مِنْ حَدِيثِ عُبَيْدِ اللهِ بْنِ مُوسَى وَاسْتَغْرَبَهُ جِدًّا.\n', 'ކަޢުބް ބިން ޢުޖްރާ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ތިމަންނައަށް ޙަދީޘް ކުރެއްވިއެވެ: ”(ނުބައި) ވެރިންތަކެއްގެ ކިބައިން ﷲގެ ޙަޟްރަތުން ތިބާއަށް ރައްކާތެރިކޮށް ދެއްވުމަށް އެދި ތިމަން ނަބިއްޔާ ދަންނަވަމެވެ. އެ ވެރިންގެ ދޮރުތަކަށް އެއްވެ، އެ ބަޔެއްގެ ދޮގުތައް ތެދުކޮށް، އަދި އެ ބަޔެއްގެ އަނިޔާވެރިކަމުގައި އެހީތެރިވެއްޖެ މީހާ، ފަހެ އޭނާއީ ތިމަން ނަބިއްޔާގެ (މީސްތަކުންގެ) ތެރެއިންވާ މީހެއް ނޫނެވެ. އަދި ތިމަން ނަބިއްޔާއީ އޭނާގެ (މީސްތަކުންގެ) ތެރެއިންވާ މީހެއް ނޫނެވެ. އަދި (ތިމަން ނަބިއްޔާގެ) ޙައުޟުގައި އޭނާ ތިމަން ނަބިއްޔާއާ ބައްދަލެއް ނުވާނެއެވެ. އަދި އެ ވެރިންގެ ދޮރުތަކަށް އެއްވެ، ނޫނީ އެއްނުވި ކަމުގައި ވިޔަސް، އެ ބަޔެއްގެ ދޮގުތައް ތެދުނުކޮށް، އަދި އެ ބަޔެއްގެ އަނިޔާވެރިކަމުގައި އެހީތެރިނުވާ މީހާ، ފަހެ އޭނާއީ ތިމަން ނަބިއްޔާގެ (މީސްތަކުންގެ) ތެރެއިންވާ މީހެކެވެ. އަދި ތިމަން ނަބިއްޔާއީ އޭނާގެ (މީސްތަކުންގެ) ތެރެއިންވާ މީހަކީމެވެ. އަދި (ތިމަން ނަބިއްޔާގެ) ޙައުޟުގައި އޭނާ ތިމަން ނަބިއްޔާއާ ބައްދަލު ވާނެއެވެ. އޭ ކަޢުބު ބިން ޢުޖްރާއެވެ. ނަމާދަކީ (އީމާންކަމުގެ) ބުރުހާނެކެވެ. އަދި ރޯދައަކީ ވަރުގަދަ އައްޑަނައެކެވެ. (އެބަހީ: ރައްކާތެރި ކަމެކެވެ.) އަދި ފެނުން އަލިފާން ނިވޭ ފަދައިން، ޞަދަޤާތުން ފާފަ ފޮހެވިގެންދެއެވެ. އޭ ކަޢުބު ބިން ޢުޖްރާއެވެ. ހަމަކަށަވަރުން ޙަރާމް ތަކެތި ކައިގެން ހަށިގަނޑުގައި މަސްޖަހައިފި ނަމަ، އެ ހަށިގަނޑެއް އެންމެ އައުލާވާނީ ނަރަކައަށް ކަމުގައި މެނުވީ ވެގެން ނުދާނެއެވެ.“', ''], ['السنن الكبرى للنسائي 2873', 'أَخْبَرَنَا الرَّبِيعُ بْنُ سُلَيْمَانَ، قَالَ: حَدَّثَنَا يَحْيَى بْنُ حَسَّانَ، قَالَ: حَدَّثَنَا يَحْيَى بْنُ حَمْزَةَ، قَالَ: أَخْبَرَنِي يَحْيَى بْنُ الْحَارِثِ، عَنْ أَبِي أَسْمَاءَ الرَّحَبِيِّ، عَنْ ثَوْبَانَ، أَنَّ رَسُولَ اللهِ ﷺ قَالَ: صِيَامُ شَهْرِ رَمَضَانَ بِعَشَرَةِ أَشْهُرٍ، وَصِيَامُ سِتَّةِ أَيَّامٍ مِنْ شَوَّالٍ بِشَهْرَيْنِ، فَذَلِكَ صِيَامُ سَنَةٍ. \n', 'ޘައުބާން ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ރަމަޟާން މަހު ހިފާ ރޯދަ، (އެ ނޫން) ދިހަ މަސްދުވަހުގެ ރޯދަ ހިފުމާ އެއްފަދައެވެ. އަދި ރަމަޟާން މަހަށް ފަހު (ޝައްވާލު މަހުގައި) ހިފާ ހަ ރޯދަ، ދެ މަސްދުވަހުގެ ރޯދަ ހިފުމާ އެއްފަދައެވެ. ފަހެ އެފަދައިން މުޅި އަހަރުދުވަހު ރޯދަ ހިފުނު ފަދައެވެ.“', ''], ['سنن أبي داود 2437', 'حَدَّثَنَا مُسَدَّدٌ، نَا أَبُو عَوَانَةَ، عَنِ الْحُرِّ بْنِ الصَّبَّاحِ، عَنْ هُنَيْدَةَ بْنِ خَالِدٍ، عَنِ امْرَأَتِهِ، عَنْ بَعْضِ أَزْوَاجِ النَّبِيِّ عَلَيْهِ السَّلَامُ قَالَتْ: كَانَ رَسُولُ اللهِ ﷺ يَصُومُ تِسْعَ ذِي الْحِجَّةِ، وَيَوْمَ عَاشُورَاءَ، وَثَلَاثَةَ أَيَّامٍ مِنْ كُلِّ شَهْرٍ أَوَّلَ اثْنَيْنِ مِنَ الشَّهْرِ وَالْخَمِيسَ. ', 'ނަބިއްޔާ ޢަލައިހިއްސަލާމްގެ އަނބިކަނބަލުން އުންމު ސަލަމާ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޒުލް ޙިއްޖާ މަހުގެ ނުވަ ދުވަހާއި، ޢާޝޫރާ ދުވަހު ރޯދަ ހިއްޕަވާ އުޅުއްވިއެވެ. އަދި ކޮންމެ މަހެއްގެ ތިން ދުވަހުވެސް ރޯދަހިއްޕަވާ އުޅުއްވިއެވެ. އެ ގޮތުން މަހުގެ ފުރަތަމަ ހޯމަ ދުވަހާއި ބުރާސްފަތު ދުވަހެވެ.', ''], ['سنن الترمذي 2169', 'حَدَّثَنَا قُتَيْبَةُ، قَالَ: حَدَّثَنَا عَبْدُ الْعَزِيزِ بْنُ مُحَمَّدٍ، عَنْ عَمْرِو بْنِ أَبِي عَمْرٍو، عَنْ عَبْدِ اللهِ الْأَنْصَارِيِّ، عَنْ حُذَيْفَةَ بْنِ الْيَمَانِ، عَنِ النَّبِيِّ ﷺ قَالَ: وَالَّذِي نَفْسِي بِيَدِهِ لَتَأْمُرُنَّ بِالْمَعْرُوفِ وَلَتَنْهَوُنَّ عَنِ الْمُنْكَرِ أَوْ لَيُوشِكَنَّ اللهُ أَنْ يَبْعَثَ عَلَيْكُمْ عِقَابًا مِنْهُ، ثُمَّ تَدْعُونَهُ فَلَا يُسْتَجَابُ لَكُمْ. \nهَذَا حَدِيثٌ حَسَنٌ.', 'ޙުޛައިފާ ބިން އަލްޔަމާނު ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާގެ ނަފްސުފުޅު އެ ފަރާތެއްގެ އަތްޕުޅުގައިވާ ފަރާތް ގަންދީ ބުނަމެވެ. ތިޔަބައިމީހުން ހެޔޮކަންކަމަށް އަމުރުކުރަން ޖެހޭނެއެވެ. އަދި ނުބައިކަންކަން މަނާ ކުރަން ޖެހޭނެއެވެ. ނޫން ނަމަ، ﷲގެ ޙަޟްރަތުން އުޤޫބާތެއް ތިޔަބައިމީހުންގެ މައްޗަށް ފޮނުއްވާނެކަމީ ވަރަށް ގާތުގައިވާ ކަމެކެވެ. އެއަށްފަހު ތިޔަބައިމީހުން އެ އިލާހަށް ދުޢާ ކުރުމުން ތިޔަބައިމީހުންނަކަށް އިޖާބައެއް ނުދެއްވާނެއެވެ.“', ''], ['سنن أبي داود 3580', 'حَدَّثَنَا أَحْمَدُ بْنُ يُونُسَ، نَا ابْنُ أَبِي ذِئْبٍ، عَنِ الْحَارِثِ بْنِ عَبْدِ الرَّحْمَنِ، عَنْ أَبِي سَلَمَةَ، عَنْ عَبْدِ اللهِ بْنِ عَمْرٍو قَالَ: لَعَنَ رَسُولُ اللهِ ﷺ الرَّاشِيَ وَالْمُرْتَشِيَ. ', 'ޢަބްދު ﷲ ބިން ޢަމްރު ރިވާ ކުރެއްވިއެވެ: ރިޝްވަރު ދޭ މީހާއާއި ރިޝްވަތުގައި ހިފާ މީހާއަށް ރަސޫލު ﷲ ﷺ ލަޢުނެތް ލެއްވިއެވެ.', ''], ['منتقى ابن جارود 638', 'حَدَّثَنَا مُحَمَّدُ بْنُ يَحْيَى، قَالَ: ثَنَا أَبُو الْوَلِيدِ، قَالَ: ثَنَا أَبُو عَوَانَةَ، عَنْ عُمَرَ بْنِ أَبِي سَلَمَةَ، عَنْ أَبِيهِ، عَنْ أَبِي هُرَيْرَةَ رَضِيَ اللهُ عَنْهُ قَالَ: قَالَ رَسُولُ اللهِ ﷺ: لَعَنَ اللهُ الرَّاشِيَ وَالْمُرْتَشِيَ. ', 'އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ރިޝްވަރު ދޭ މީހާއާއި ރިޝްވަތުގައި ހިފާ މީހާއަށް ﷲ ލަޢުނެތް ލައްވާށިއެވެ.“', ''], ['سنن أبي داود 2419', 'حَدَّثَنَا الْحَسَنُ بْنُ عَلِيٍّ، نَا، وَهْبٌ، نَا مُوسَى بْنُ عُلَيٍّ. (ح) وَنَا عُثْمَانُ بْنُ أَبِي شَيْبَةَ، نَا، وَكِيعٌ، عَنْ مُوسَى بْنِ عُلَيٍّ، وَالْإِخْبَارُ فِي حَدِيثِ وَهْبٍ قَالَ: سَمِعْتُ أَبِي أَنَّهُ سَمِعَ عُقْبَةَ بْنَ عَامِرٍ قَالَ: قَالَ رَسُولُ اللهِ ﷺ: يَوْمُ عَرَفَةَ، وَيَوْمُ النَّحْرِ، وَأَيَّامُ التَّشْرِيقِ عِيدُنَا أَهْلَ الْإِسْلَامِ، وَهِيَ أَيَّامُ أَكْلٍ وَشُرْبٍ. ', 'ޢުޤްބާ ބިން ޢާމިރު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ޢަރަފާތް ދުވަހާއި، ނަޙްރުގެ (އެބަހީ: އުޟްޙިޔާ ކަތިލުމުގެ) ދުވަހާއި، އައްޔާމުއް ތަޝްރީޤުގެ ތިން ދުވަހަކީ އަހަރެމެން އިސްލާމުންގެ ޢީދެވެ. އަދި އެއީ ކެއުމާއި ބުއިމުގެ ދުވަސްތަކެކެވެ.“', ''], ['موطأ مالك 3338', 'مَالِكٌ، أَنَّهُ بَلَغَهُ أَنَّ رَسُولَ اللهِ ﷺ قَالَ: تَرَكْتُ فِيكُمْ أَمْرَيْنِ لَنْ تَضِلُّوا مَا تَمَسَّكْتُمْ بِهِمَا: كِتَابَ اللهِ وَسُنَّةَ نَبِيِّهِ. ', 'މާލިކު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާ، ތިޔަބައިމީހުންގެ ތެރެއަށް ދެ އެއްޗެއް ދޫކުރެއްވީމެވެ. އެ ދޭތީގައި ހިފަހައްޓައިގެން ތިބިހައި ހިނދަކު، ދުވަހަކުވެސް ތިޔަބައިމިހުން މަގު ފުރެދިގެން ނުދާނެއެވެ. އެއީ ﷲގެ ފޮތާއި، އެ އިލާހުގެ ނަބިއްޔާގެ ސުންނަތެވެ.“', ''], ['سنن أبي داود 3646', 'حَدَّثَنَا مُسَدَّدٌ وَأَبُو بَكْرِ بْنُ أَبِي شَيْبَةَ، قَالَا: نَا يَحْيَى، عَنْ عُبَيْدِ اللهِ بْنِ الْأَخْنَسِ، عَنِ الْوَلِيدِ بْنِ عَبْدِ اللهِ بْنِ أَبِي مُغِيثٍ، عَنْ يُوسُفَ بْنِ مَاهَكَ، عَنْ عَبْدِ اللهِ بْنِ عَمْرٍو قَالَ: كُنْتُ أَكْتُبُ كُلَّ شَيْءٍ أَسْمَعُهُ مِنْ رَسُولِ اللهِ ﷺ أُرِيدُ حِفْظَهُ، فَنَهَتْنِي قُرَيْشٌ، وَقَالُوا: أَتَكْتُبُ كُلَّ شَيْءٍ تَسْمَعُهُ، وَرَسُولُ اللهِ ﷺ بَشَرٌ يَتَكَلَّمُ فِي الْغَضَبِ وَالرِّضَا، فَأَمْسَكْتُ عَنِ الْكِتَابِ، فَذَكَرْتُ ذَلِكَ إِلَى رَسُولِ اللهِ ﷺ، فَأَوْمَأَ بِإِصْبَعِهِ إِلَى فِيهِ، فَقَالَ: اكْتُبْ فَوَالَّذِي نَفْسِي بِيَدِهِ مَا يَخْرُجُ مِنْهُ إِلَّا حَقٌّ. ', 'ޢަބްދު ﷲ ބިން ޢަމްރު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ އަރިހުން އިވޭ ކޮންމެ ބަސްފުޅެއް ތިމަން ލިޔެ އުޅުނީމެވެ. ތިމަން ބޭނުންވީ އެ ބަސްފުޅުތައް ދަސްކޮށް ރައްކާ ކުރުމަށެވެ. އެހެނަސް ޤުރައިޝު ވަންހައިގެ މުޝްރިކުން އެކަން ނުކުރުމަށް ތިމަންނައަށް އެންގިއެވެ. އަދި އެ މީހުން ދެންނެވިއެވެ: އިވޭ ކޮންމެ އެއްޗެއް ފަހެ ތިބާ ލިޔަނީހެއްޔެވެ؟ އެއީ ރަސޫލު ﷲ ﷺ އަކީ ހަމަ އިންސާނަކަށްވެފައި، ކޯފާއާއި ރުހުމުގައި ވާހަކަދައްކަވާ އިރުގައިވެސްހެއްޔެވެ؟ ދެން ތިމަން ލިޔުން ހުއްޓާލާފައި، އެ ވާހަކަ ރަސޫލު ﷲ ﷺ  ގެ އަރިހުގައި ދެންނެވީމެވެ. ފަހެ ނަބިއްޔާގެ އިނގިލިފުޅުން އެ ނަބިއްޔާގެ އަނގަފުޅަށް އިޝާރާތް ކުރައްވާފައި ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ ލިޔާށެވެ. އެހެނީ ތިމަން ނަބިއްޔާގެ ފުރާނަފުޅު އެ ފަރާތެއްގެ އަތްޕުޅުގައިވާ ފަރާތް ގަންދީ ބުނަމެވެ. މި އަނގަފުޅަކުން ޙައްޤު ތެދު މެނުވީ ނިކުމެގެން ނުދާނެއެވެ.“', ''], ['سنن ابن ماجه 12', 'حَدَّثَنَا أَبُو بَكْرِ بْنُ أَبِي شَيْبَةَ، حَدَّثَنَا زَيْدُ بْنُ الْحُبَابِ، عَنْ مُعَاوِيَةَ بْنِ صَالِحٍ، حَدَّثَنِي الْحَسَنُ بْنُ جَابِرٍ، عَنِ الْمِقْدَامِ بْنِ مَعْدِيكَرِبَ الْكِنْدِيِّ، أَنَّ رَسُولَ اللهِ ﷺ قَالَ: يُوشِكُ الرَّجُلُ مُتَّكِئًا عَلَى أَرِيكَتِهِ يُحَدَّثُ بِحَدِيثٍ مِنْ حَدِيثِي، فَيَقُولُ: بَيْنَنَا وَبَيْنَكُمْ كِتَابُ اللهِ عَزَّ وَجَلَّ، فَمَا وَجَدْنَا فِيهِ مِنْ حَلَالٍ اسْتَحْلَلْنَاهُ، وَمَا وَجَدْنَا فِيهِ مِنْ حَرَامٍ حَرَّمْنَاهُ، أَلَّا وَإِنَّ مَا حَرَّمَ رَسُولُ اللهِ ﷺ مِثْلُ مَا حَرَّمَ اللهُ.', 'އަލްމިޤްދާމު ބިން މަޢުދީ ކަރިބު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ސޮފާއެނދެއްގައި ލެނގިލައިގެން އިން މީހަކަށް، ތިމަން ނަބިއްޔާގެ ޙަދީޘްބަސްފުޅެއް ކިޔައިދެވޭ ހިނދު އޭނާ މިފަދައިން ބުނާނެ ދުވަސް ގާތްވެއްޖެއެވެ: ’ތިމަންމެންނާއި ތިޔަބައިމީހުންގެ ދެމެދު ވަނީ ﷲ ޢައްޒަ ވަޖައްލަގެ ފޮތެވެ. ވީމާ އޭގައި ޙަލާލު ކަމަށް ތިމަންމެންނަށް ފެނުނު އެއްޗެއް ޙަލާލު ކަމަށް ދެކޭނަމެވެ. އަދި އޭގައި ޙަރާމް ކަމަށް ތިމަންމެންނަށް ފެނުނު އެއްޗެއް ޙަރާމް ކަމަށް ދެކޭނަމެވެ.‘ ދަންނާށެވެ! ހަމަކަށަވަރުން ﷲގެ ރަސޫލާ ﷺ ޙަރާމް ކުރެއްވި އެއްޗެއް، ﷲ ޙަރާމް ކުރެއްވި އެއްޗަކާ ހަމަ އެއްފަދަވެގެންވެއެވެ.“', ''], ['سنن أبي داود 4604', 'حَدَّثَنَا عَبْدُ الْوَهَّابِ بْنُ نَجْدَةَ، نَا أَبُو عَمْرِو بْنُ كَثِيرِ بْنِ دِينَارٍ، عَنْ حَرِيزِ بْنِ عُثْمَانَ، عَنْ عَبْدِ الرَّحْمَنِ بْنِ أَبِي عَوْفٍ، عَنِ الْمِقْدَامِ بْنِ مَعْدِيكَرِبَ، عَنْ رَسُولِ اللهِ ﷺ أَنَّهُ قَالَ: أَلَا إِنِّي أُوتِيتُ الْكِتَابَ وَمِثْلَهُ مَعَهُ، أَلَا يُوشِكُ رَجُلٌ شَبْعَانُ عَلَى أَرِيكَتِهِ يَقُولُ عَلَيْكُمْ بِهَذَا الْقُرْآنِ، فَمَا وَجَدْتُمْ فِيهِ مِنْ حَلَالٍ فَأَحِلُّوهُ، وَمَا وَجَدْتُمْ فِيهِ مِنْ حَرَامٍ فَحَرِّمُوهُ، أَلَا لَا يَحِلُّ لَكُمُ الْحِمَارُ الْأَهْلِيُّ، وَلَا كُلُّ ذِي نَابٍ مِنَ السَّبُعِ، وَلَا لُقَطَةُ مُعَاهَدٍ إِلَّا أَنْ يَسْتَغْنِيَ عَنْهَا صَاحِبُهَا، وَمَنْ نَزَلَ بِقَوْمٍ فَعَلَيْهِمْ أَنْ يَقْرُوهُ، فَإِنْ لَمْ يَقْرُوهُ فَلَهُ أَنْ يُعْقِبَهُمْ بِمِثْلِ قِرَاهُ. ', 'އަލްމިޤްދާމު ބިން މަޢުދީ ކަރިބު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ދަންނާށެވެ! ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާއަށް ޤުރްއާނާއެކު ހަމަ އެފަދަ އެހެން އެއްޗެއް (އެބަހީ: ސުންނަތް) ދެވުނެވެ. ދަންނާށެވެ! ބަނޑުފުރައި ކައައިގެން ސޮފާއެނދެއްގައި ލެނގިލައިގެން އިން މީހަކު، މިފަދައިން ބުނާނެ ދުވަސް ގާތްވެއްޖެއެވެ: ތިޔަބައިމީހުންގެ މައްޗަށް އޮތީ (ހަމައެކަނި) މި ޤުރްއާނަށް ތަބާވުމެވެ. ވީމާ އޭގައި ޙަލާލު ކަމަށް ތިޔަބައިމީހުންނަށް ފެނުނު އެއްޗެއް ޙަލާލު ކަމަށް ދެކޭށެވެ. އަދި އޭގައި ޙަރާމް ކަމަށް ތިޔަބައިމީހުންނަށް ފެނުނު އެއްޗެއް ޙަރާމް ކަމަށް ދެކޭށެވެ.‘ ދަންނާށެވެ! ހިމާރު މަހާއި، ފޭރާދަތް ހުންނަ ޝިކާރަ ކުރާ ޖަނަވާރުގެ މަސް ކެއުން ތިޔަބައިމީހުންނަކަށް ހުއްދަވެގެނެއް ނުވެއެވެ. އަދި (ހަނގުރާމަ ނުކުރުމަށް) މުޢާހަދާއެއް ކުރެވުނު މީހެއްގެ އަތުން (ވެއްޓި) ގެއްލުނު އެއްޗެއް ހޮވުން ތިޔަބައިމީހުންނަކަށް ހުއްދަވެގެނެއް ނުވެއެވެ. އެއީ އޭގެ ވެރި ފަރާތް އޭތި ބޭނުން ނުވާ ޙާލު މެނުވީއެވެ. އަދި ބަޔެއްގެ ގާތުގައި މީހަކު މަޑުކޮށްފި ނަމަ، އެ ބަޔަކަށް އޮތީ އޭނާއަށް މެހެމާންދާރީ އަދާކުރުމެވެ. އަދި އަދާ ނުކޮށްފި ނަމަ، އޭނާއަށް އޭގެ ބަދަލު ލިބުން ހުއްޓެވެ.“', ''], ['سنن أبي داود 4605', 'حَدَّثَنَا أَحْمَدُ بْنُ مُحَمَّدِ بْنِ حَنْبَلٍ وَعَبْدُ اللهِ بْنُ مُحَمَّدٍ النُّفَيْلِيُّ قَالَا: نَا سُفْيَانُ، عَنْ أَبِي النَّضْرِ، عَنْ عُبَيْدِ اللهِ بْنِ أَبِي رَافِعٍ، عَنْ أَبِيهِ، عَنِ النَّبِيِّ ﷺ قَالَ: لَا أُلْفِيَنَّ أَحَدَكُمْ مُتَّكِئًا عَلَى أَرِيكَتِهِ يَأْتِيهِ الْأَمْرُ مِنْ أَمْرِي مِمَّا أَمَرْتُ بِهِ أَوْ نَهَيْتُ عَنْهُ، فَيَقُولُ: لَا نَدْرِي مَا وَجَدْنَا فِي كِتَابِ اللهِ اتَّبَعْنَاهُ. \n', 'އަބޫ ރާފިޢު ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ކުރުމަށް އަންގަވާފައިވާ ތިމަން ނަބިއްޔާގެ އަމުރެއް ނުވަތަ ނުކުރުމަށް އަންގަވާފައިވާ ނަހީއެއް، ތިޔަބައިމީހުންގެ ތެރެއިން މީހަކާ ހިސާބަށް ފޯރުމުން، ސޯފާއެނދެއްގައި ލެނގިލައިގެން އިނދެ މިފަދައިން ބުނަނިކޮށް ތިމަންނަބިއްޔާއަށް ފެނިގެން ނުވާނެއެވެ: ތިމަންމެންނަކަށް ނޭނގެއެވެ. ތިމަންމެންނަށް ﷲގެ ފޮތުގައި ފެނުނު އެއްޗެއް ނަމަ އެއަށް ދެން ތިމަންމެން ތަބާވާނީއެވެ.“', ''], ['سنن الترمذي 2518', 'حَدَّثَنَا أَبُو مُوسَى الْأَنْصَارِيُّ، قَالَ: حَدَّثَنَا عَبْدُ اللهِ بْنُ إِدْرِيسَ، قَالَ: حَدَّثَنَا شُعْبَةُ، عَنْ بُرَيْدِ بْنِ أَبِي مَرْيَمَ، عَنْ أَبِي الْحَوْرَاءِ السَّعْدِيِّ قَالَ: قُلْتُ لِلْحَسَنِ بْنِ عَلِيٍّ: مَا حَفِظْتَ مِنْ رَسُولِ اللهِ ﷺ؟ قَالَ: حَفِظْتُ مِنْ رَسُولِ اللهِ ﷺ، دَعْ مَا يَرِيبُكَ إِلَى مَا لَا يَرِيبُكَ، فَإِنَّ الصِّدْقَ طُمَأْنِينَةٌ، وَإِنَّ الْكَذِبَ رِيبَةٌ وَفِي الْحَدِيثِ قِصَّةٌ. \nوَأَبُو الْحَوْرَاءِ السَّعْدِيُّ اسْمُهُ رَبِيعَةُ بْنُ شَيْبَانَ. \nوَهَذَا حَدِيثٌ صَحِيحٌ.', 'އަލްޙަސަން ބިން ޢަލީ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ އަރިހުން (މި ބަސްފުޅުތައް) ތިމަން ދަސް ކުރީމެވެ: ”ތިބާއަށް ޝައްކުވާ ކަންތައް ދޫކޮށްލާފައި، ޝައްކު ނުވާ ކަންތަކުގައި ހިފާށެވެ. އެހެނީ ހަމަކަށަވަރުން ތެދުވެރިކަމަކީ ހިތްހަމަޖެހުން ލިއްބައިދޭ ކަމެކެވެ . އަދި ހަމަކަށަވަރުން ދޮގުވެރިކަމަކީ ޝައްކު އުފެދޭ ކަމެކެވެ.“', ''], ['سنن الترمذي 2317', 'حَدَّثَنَا أَحْمَدُ بْنُ نَصْرٍ النَّيْسَابُورِيُّ وَغَيْرُ وَاحِدٍ، قَالُوا: حَدَّثَنَا أَبُو مُسْهِرٍ، عَنْ إِسْمَاعِيلَ بْنِ عَبْدِ اللهِ بْنِ سَمَاعَةَ، عَنِ الْأَوْزَاعِيِّ، عَنْ قُرَّةَ، عَنِ الزُّهْرِيِّ، عَنْ أَبِي سَلَمَةَ، عَنْ أَبِي هُرَيْرَةَ قَالَ: قَالَ رَسُولُ اللهِ ﷺ: مِنْ حُسْنِ إِسْلَامِ الْمَرْءِ تَرْكُهُ مَا لَا يَعْنِيهِ. \nهَذَا حَدِيثٌ غَرِيبٌ، لَا نَعْرِفُهُ مِنْ حَدِيثِ أَبِي سَلَمَةَ، عَنْ أَبِي هُرَيْرَةَ، عَنِ النَّبِيِّ ﷺ إِلَّا مِنْ هَذَا الْوَجْهِ.', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”މީހާގެ އިސްލާމްކަން ރަނގަޅުވެ ފުރިހަމަ ވުމުގެ ތެރޭގައި، އޭނާއާ ނުބެހޭ ކަންތައްތަކުގެ ތެރެއަށް ނުވަނުން ހިމެނެއެވެ.“', ''], ['سنن الترمذي 1987', 'حَدَّثَنَا مُحَمَّدُ بْنُ بَشَّارٍ، قَالَ: حَدَّثَنَا عَبْدُ الرَّحْمَنِ بْنُ مَهْدِيٍّ، قَالَ: حَدَّثَنَا سُفْيَانُ، عَنْ حَبِيبِ بْنِ أَبِي ثَابِتٍ، عَنْ مَيْمُونِ بْنِ أَبِي شَبِيبٍ، عَنْ أَبِي ذَرٍّ قَالَ: قَالَ لِي رَسُولُ اللهِ ﷺ: اتَّقِ اللهَ حَيْثُمَا كُنْتَ، وَأَتْبِعِ السَّيِّئَةَ الْحَسَنَةَ تَمْحُهَا، وَخَالِقِ النَّاسَ بِخُلُقٍ حَسَنٍ. \nوَفِي الْبَابِ عَنْ أَبِي هُرَيْرَةَ. \nهَذَا حَدِيثٌ حَسَنٌ صَحِيحٌ.', 'އަބޫ ޛައްރު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ތިމަންނައަށް ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާވީ ކޮންމެ ތާކުމެ ﷲ އަށް ތަޤްވާވެރި ވާށެވެ. އަދި (ފާފައެއް ކުރެވިއްޖެ ނަމަ، އެ) ފާފަޔަކާ ވިދިގެން ހެޔޮ ކަމެއް ކުރާށެވެ. (އެހެނީ) އެ ހެޔޮ ކަންތައް، އެ ފާފަ ފޮހެލާނެއެވެ. އަދި ރިވެތި އަޚްލާޤުން މީސްތަކުންނާ މުޢާމަލާތް ކުރާށެވެ.“', ''], ['سنن الترمذي 2516', 'حَدَّثَنَا أَحْمَدُ بْنُ مُحَمَّدِ بْنِ مُوسَى، قَالَ: أَخْبَرَنَا عَبْدُ اللهِ بْنُ الْمُبَارَكِ، قَالَ: أَخْبَرَنَا لَيْثُ بْنُ سَعْدٍ وَابْنُ لَهِيعَةَ، عَنْ قَيْسِ بْنِ الْحَجَّاجِ. (ح) وَحَدَّثَنَا عَبْدُ اللهِ بْنُ عَبْدِ الرَّحْمَنِ، قَالَ: أَخْبَرَنَا أَبُو الْوَلِيدِ، قَالَ: حَدَّثَنَا لَيْثُ بْنُ سَعْدٍ، قَالَ: حَدَّثَنِي قَيْسُ بْنُ الْحَجَّاجِ، الْمَعْنَى وَاحِدٌ عَنْ حَنَشٍ الصَّنْعَانِيِّ، عَنِ ابْنِ عَبَّاسٍ قَالَ: كُنْتُ خَلْفَ رَسُولِ اللهِ ﷺ يَوْمًا، فَقَالَ: يَا غُلَامُ، إِنِّي أُعَلِّمُكَ كَلِمَاتٍ؛ احْفَظِ اللهَ يَحْفَظْكَ، احْفَظِ اللهَ تَجِدْهُ تُجَاهَكَ، إِذَا سَأَلْتَ فَاسْأَلِ اللهَ، وَإِذَا اسْتَعَنْتَ فَاسْتَعِنْ بِاللهِ، وَاعْلَمْ أَنَّ الْأُمَّةَ لَوِ اجْتَمَعَتْ عَلَى أَنْ يَنْفَعُوكَ بِشَيْءٍ، لَمْ يَنْفَعُوكَ إِلَّا بِشَيْءٍ قَدْ كَتَبَهُ اللهُ لَكَ، وَلَوِ اجْتَمَعُوا عَلَى أَنْ يَضُرُّوكَ بِشَيْءٍ لَمْ يَضُرُّوكَ إِلَّا بِشَيْءٍ قَدْ كَتَبَهُ اللهُ عَلَيْكَ، رُفِعَتِ الْأَقْلَامُ وَجَفَّتِ الصُّحُفُ. \nهَذَا حَدِيثٌ حَسَنٌ صَحِيحٌ.', 'އިބިނު ޢައްބާސް ރިވާ ކުރެއްވިއެވެ: އެއް ދުވަހަކު ނަބިއްޔާ ﷺ ގެ ފަހަތްޕުޅުގައި ތިމަން ވަނިކޮށް، ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ ކޮއިފުޅާއެވެ! ހަމަކަށަވަރުން، ތިމަން ނަބިއްޔާ ތިބާއަށް ކަލިމަތަކެއް ދަސްކޮށް ދެއްވާނަމެވެ. ﷲ އާމެދު (އެބަހީ: އެ އިލާހުގެ އަމުރުފުޅުތަކާ މެދު) ތިބާ ރައްކާތެރި ވާށެވެ. އޭރުން އެ އިލާހު ތިބާ ރައްކާތެރި ކޮށްދެއްވާނެއެވެ. ﷲ އާމެދު ތިބާ ރައްކާތެރި ވާށެވެ. އޭރުން ތިބާގެ ކުރިމަތީގައި އެ އިލާހު ވޮޑިގެންވާނެއެވެ. (އެބަހީ: ތިބާ ﷲ އަށް ކުއްތަންވެ، އެ އިލާހުގެ އަމުރުފުޅުތަކަށް ރައްކާތެރި ވާނަމަ، ﷲ ވެސް ތިބާއާ ކުއްތަންވެވޮޑިގަންނަވާ، ތިބާ ރައްކާތެރިކޮށް، ތިބާގެ ދުޢާތައް އިޖާބަ ކުރައްވާނެއެވެ.) ތިބާ ކަމަކަށް އެދި ދަންނަވާ ނަމަ، ﷲ އަށް އެދި ދަންނަވާށެވެ. އަދި ތިބާ އެހީއަކަށް އެދޭ ނަމަ، ﷲގެ އެހީއަށް އެދޭށެވެ. އަދި ދަންނާށެވެ! ހަމަކަށަވަރުން މުޅި އުންމަތް، ތިބާއަށް އެއްވެސް މަންފާއެއް ކޮށްދިނުމަށް އެއްބައި ވިޔަސް، ﷲ ތިބާއަށް ޓަކައި ލިޔުއްވައި (މިންވަރު ކުރައްވައިފި) ކަމަކުން މެނުވީ އެބައިމީހުން ތިބާއަކަށް މަންފާއެއް ކޮށެއް ނުދެވޭނެއެވެ. އަދި ތިބާއަށް އެއްވެސް ގެއްލުމެއް ދިނުމަށް މުޅި އުންމަތް އެއްބައި ވިޔަސް، ﷲ ތިބާގެ މައްޗަށް ލިޔުއްވައި (މިންވަރު ކުރައްވައިފި) ކަމަކުން މެނުވީ އެބައިމީހުން ތިބާއަށް ގެއްލުމެއް ނުދެވޭނެއެވެ. ގަލަންތައް ވަނީ އުފުލިފައެވެ. އަދި ފަތްފުށްތައް ވަނީ ހިކިފައެވެ.“ (އެބަހީ: ލައުޙުލް މަޙްފޫޡުގައި ﷲ ކަނޑައެޅުއްވި ކަންތައް ލިޔެ ނިމި، އެ ގޮތަށް އެ ކަންތައް ހިނގާނެއެވެ.)', ''], ['سنن أبي داود 4607', 'حَدَّثَنَا أَحْمَدُ بْنُ حَنْبَلٍ، نَا الْوَلِيدُ بْنُ مُسْلِمٍ، نَا ثَوْرُ بْنُ يَزِيدَ حَدَّثَنِي خَالِدُ بْنُ مَعْدَانَ حَدَّثَنِي عَبْدُ الرَّحْمَنِ بْنُ عَمْرٍو السُّلَمِيُّ وَحُجْرُ بْنُ حُجْرٍ قَالَا: أَتَيْنَا الْعِرْبَاضَ بْنَ سَارِيَةَ وَهُوَ مِمَّنْ نَزَلَ فِيهِ { وَلا عَلَى الَّذِينَ إِذَا مَا أَتَوْكَ لِتَحْمِلَهُمْ قُلْتَ لا أَجِدُ مَا أَحْمِلُكُمْ عَلَيْهِ } فَسَلَّمْنَا، وَقُلْنَا: أَتَيْنَاكَ زَائِرِينَ وَعَائِدِينَ وَمُقْتَبِسِينَ، فَقَالَ الْعِرْبَاضُ: صَلَّى بِنَا رَسُولُ اللهِ ﷺ ذَاتَ يَوْمٍ ثُمَّ أَقْبَلَ عَلَيْنَا فَوَعَظَنَا مَوْعِظَةً بَلِيغَةً ذَرَفَتْ مِنْهَا الْعُيُونُ، وَوَجِلَتْ مِنْهَا الْقُلُوبُ، فَقَالَ قَائِلٌ: يَا رَسُولَ اللهِ، كَأَنَّ هَذِهِ مَوْعِظَةُ مُوَدِّعٍ، فَمَاذَا تَعْهَدُ إِلَيْنَا؟ فَقَالَ: أُوصِيكُمْ بِتَقْوَى اللهِ، وَالسَّمْعِ وَالطَّاعَةِ وَإِنْ عَبْدًا حَبَشِيًّا، فَإِنَّهُ مَنْ يَعِشْ مِنْكُمْ بَعْدِي فَسَيَرَى اخْتِلَافًا كَثِيرًا، فَعَلَيْكُمْ بِسُنَّتِي وَسُنَّةِ الْخُلَفَاءِ الرَّاشِدِينَ الْمَهْدِيِّينَ، تَمَسَّكُوا بِهَا وَعَضُّوا عَلَيْهَا بِالنَّوَاجِذِ، وَإِيَّاكُمْ وَمُحْدَثَاتِ الْأُمُورِ فَإِنَّ كُلَّ مُحْدَثَةٍ بِدْعَةٌ وَكُلَّ بِدْعَةٍ ضَلَالَةٌ. \n', 'އަލްޢިރްބާޟު ބިން ސާރިޔާ ރިވާ ކުރެއްވިއެވެ: އެއްދުވަހު ތިމަންމެންނައިގެން ރަސޫލު ﷲ ﷺ ނަމާދު ކުރެއްވިއެވެ. އެއަށްފަހު ތިމަންމެންނާ ކުރިމަތިލައްވައިގެން ވަރަށް އަސަރު ގަދަ ވަޢުޡެއް ދެއްވިއެވެ. އެ ވަޢުޡުގެ ސަބަބުން ހިތްތައް ތެޅިގަނެ، ލޯތަކުން ކަރުނަ އޮހޮރިގެން ދިޔައެވެ. ދެން މީހަކު ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ! މިއީ ވަދާޢީ ވަޢުޡެއް ފަދައެވެ. ވީމާ ތިމަންމެންނަށް ޢަހުދުކުރުވަނީ ކ ކޮންކަމަކަށްތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާ ތިޔަބައިމީހުންނަށް ވަޞިއްޔަތް ކުރައްވަނީ، ﷲ އަށް ތަޤްވާވެރިވުމަށާއި، އަދި (ތިޔަބައިމީހުންގެ މައްޗަށް) ޙަބަޝީ އަޅަކު (ވެރިކަމަށް އައިކަމުގައި ވިޔަސް އެ ވެރިމީހާގެ) އަޑުއަހައި ކިޔަމަންތެރިވުމަށެވެ. ފަހެ ހަމަކަށަވަރުން، ތިމަން ނަބިއްޔާއަށް ފަހު، ތިޔަބައިމީހުންގެ ތެރެއިން ދިރިހުރި މީހަކަށް ގިނަގުނަވެގެންވާ އިޚްތިލާފުތަކެއް ފެންނާނެއެވެ. ވީމާ، ތިޔަބައިމީހުންގެ މައްޗަށް އޮތީ ތިމަން ނަބިއްޔާގެ ސުންނަތުގައި ހިފެހެއްޓުމެވެ. އަދި ތެދުމަގު ލިބިގެންވާ ޚަލީފާއިންގެ ސުންނަތުގައި ހިފެހެއްޓުމެވެ. ތިޔަބައިމީހުން އެ ސުންނަތުގައި ކޮލުދަތްތަކުން ވަރުގަދައަށް ދަތް އަޅާ ހިފަހައްޓާށެވެ. އަދި (ދީނުގައި) އަލަށް އުފެދިގެން އަންނަ ކަންކަމާ (ބިދުޢަތަކާ) މެދު ސަމާލުވެ ރައްކާތެރިވާށެވެ. އެހެނީ ހަމަކަށަވަރުން، އަލަށް އުފެދިގެން އަންނަ ކޮންމެ ކަމަކީ ބިދުޢައެކެވެ. އަދި ކޮންމެ ބިދުޢައަކީ މަގުފުރެދުމެކެވެ.“', ''], ['سنن الترمذي 2616', 'حَدَّثَنَا ابْنُ أَبِي عُمَرَ، قَالَ: حَدَّثَنَا عَبْدُ اللهِ بْنُ مُعَاذٍ الصَّنْعَانِيُّ، عَنْ مَعْمَرٍ، عَنْ عَاصِمِ بْنِ أَبِي النَّجُودِ، عَنْ أَبِي وَائِلٍ، عَنْ مُعَاذِ بْنِ جَبَلٍ قَالَ: كُنْتُ مَعَ النَّبِيِّ ﷺ فِي سَفَرٍ، فَأَصْبَحْتُ يَوْمًا قَرِيبًا مِنْهُ، وَنَحْنُ نَسِيرُ، فَقُلْتُ: يَا رَسُولَ اللهِ، أَخْبِرْنِي بِعَمَلٍ يُدْخِلُنِي الْجَنَّةَ وَيُبَاعِدُنِي عَنِ النَّارِ، قَالَ: لَقَدْ سَأَلْتَنِي عَنْ عَظِيمٍ، وَإِنَّهُ لَيَسِيرٌ عَلَى مَنْ يَسَّرَهُ اللهُ عَلَيْهِ، تَعْبُدُ اللهَ وَلَا تُشْرِكْ بِهِ شَيْئًا، وَتُقِيمُ الصَّلَاةَ، وَتُؤْتِي الزَّكَاةَ، وَتَصُومُ رَمَضَانَ، وَتَحُجُّ الْبَيْتَ، ثُمَّ قَالَ: أَلَا أَدُلُّكَ عَلَى أَبْوَابِ الْخَيْرِ؛ الصَّوْمُ جُنَّةٌ، وَالصَّدَقَةُ تُطْفِئُ الْخَطِيئَةَ كَمَا يُطْفِئُ الْمَاءُ النَّارَ، وَصَلَاةُ الرَّجُلِ مِنْ جَوْفِ اللَّيْلِ. قَالَ: ثُمَّ تَلَا: { تَتَجَافَى جُنُوبُهُمْ عَنِ الْمَضَاجِعِ } حَتَّى بَلَغَ: { يَعْمَلُونَ }، ثُمَّ قَالَ: أَلَا أُخْبِرُكَ بِرَأْسِ الْأَمْرِ كُلِّهِ وَعَمُودِهِ وَذُرْوَةِ سَنَامِهِ؟ قُلْتُ: بَلَى يَا رَسُولَ اللهِ. قَالَ: رَأْسُ الْأَمْرِ الْإِسْلَامُ، وَعَمُودُهُ الصَّلَاةُ، وَذُرْوَةُ سَنَامِهِ الْجِهَادُ ثُمَّ قَالَ: أَلَا أُخْبِرُكَ بِمِلَاكِ ذَلِكَ كُلِّهِ؟ قُلْتُ: بَلَى يَا نَبِيَّ اللهِ. فَأَخَذَ بِلِسَانِهِ. قَالَ: كُفَّ عَلَيْكَ هَذَا. فَقُلْتُ: يَا نَبِيَّ اللهِ، وَإِنَّا لَمُؤَاخَذُونَ بِمَا نَتَكَلَّمُ بِهِ، فَقَالَ: ثَكِلَتْكَ أُمُّكَ يَا مُعَاذُ، وَهَلْ يَكُبُّ النَّاسَ فِي النَّارِ عَلَى وُجُوهِهِمْ أَوْ عَلَى مَنَاخِرِهِمْ إِلَّا حَصَائِدُ أَلْسِنَتِهِمْ. \nهَذَا حَدِيثٌ حَسَنٌ صَحِيحٌ.', 'މުޢާޛު ބިން ޖަބަލު ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާﷺ އަރިހުގައި ދަތުރެއްގައި ތިމަން ވީމެވެ. ދަތުރު ކުރިއަށް ދަނިކޮށް ނަބިއްޔާއާ ތަންކޮޅެއް ގާތުގައި އެއް ދުވަހަކު ތިމަން ހުރީމެވެ. ތިމަން ދެންނެވީމެވެ: އޭ ﷲގެ ރަސޫލާއެވެ! ތިމަން ސުވަރުގެއަށް ވައްދައިދޭނެ، އަދި ނަރަކައިން ދުރުކުރެވޭނެ ޢަމަލެއްގެ ޚަބަރު ތިމަންނާއަށް ދެއްވާށެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން، ތިޔަ ސުވާލު ކުރެއްވީ ބޮޑުވެގެންވާ ކަމަކާ ބެހޭ ގޮތުންނެވެ. އަދި ހަމަކަށަވަރުން، ﷲ ފަސޭހަ ކޮށްދެއްވައިފި މީހަކަށް، އެ ކަމަކީ ނުހަނު ފަސޭހަ ކަމަކަށް ވެގެންދެއެވެ. ﷲ އާ އެއްވެސް ފަރާތެއް ޝަރީކު ކުރުމަކާ ނުލައި، ހަމައެކަނި އެ އިލާހަށް އަޅުކަން ކުރާށެވެ. އަދި ނަމާދު ގާއިމުކޮށް، (މުދަލު) ޒަކާތް ދީ، ރަމަޟާން މަހު ރޯދަ ހިފައި، ގެފުޅަށް ގޮސް ޙައްޖު ވާށެވެ.“ ދެން ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާ، ތިބާއަށް ހެޔޮކަމުގެ ދޮރުތައް ދައްކަވައި ދެއްވަންތޯއެވެ؟ ރޯދައަކީ އައްޑަނައެކެވެ. (އެބަހީ: ރައްކާތެރި ކަމެކެވެ.) އަދި ފެނުން އަލިފާން ނިވޭ ފަދައިން، ޞަދަޤާތުން ފާފަ ފޮހެވިގެންދެއެވެ. އަދި (ހަމަ އެފަދައިން) މީހަކު ރޭގަނޑުގެ މެންދަމު ކުރާ ނަމާދު ވެސްމެއެވެ.“ ދެން، ނަބިއްޔާ (މި އާޔަތް) ކިޔަވައި ވިދާޅުވިއެވެ: ❞އެބައިމީހުންގެ އަރިކަށިތައް ތަންމަތިތަކާ ފޭވެ (ރޭއަޅުކަން ކުރުމަށް ޓަކައި ނިދި ދޫކޮށްލާފައި ތެދުވެ)، އެބައިމީހުންގެ ރައްބަށް ބިރުވެތިކަމާއި އުންމީދާއެކު އެބައިމީހުން ދުޢާ ކުރެއެވެ. އަދި ތިމަން އިލާހު، އެބައިމީހުންނަށް ދެއްވި ތަކެތިން، އެބައިމީހުން ހޭދަ ކުރެއެވެ. ފަހެ އެބައިމީހުންނަށް ޓަކައި ފޮރުއްވާފައިވާ ލޮލުގެ ފިނިކަން (އެބަހީ: ސުވަރުގޭގެ ނިޢުމަތްތައް) އެއްވެސް ނަފްސަކަށް ނޭނގޭނެއެވެ. އެއީ އެބައިމީހުން ކޮށްއުޅުނު ޢަމަލުތަކުގެ ޖަޒާއެއްގެ ގޮތުންނެވެ.❝ ދެން ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”މުޅި މި ދީނުގެ އެންމެ މުހިންމު ކަމަކީ ކޮބައިކަމާއި، މި ދީނުގެ ތަނބާއި ކުރިބޯށްޓާ ބެހޭ ގޮތުން ތިބާއަށް ކިޔައި ދެއްވަންހެއްޔެވެ؟“ ތިމަން ދެންނެވީމެވެ: އާދޭހެވެ. އޭ ﷲގެ ރަސޫލާއެވެ! ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”މި ދީނުގެ އެންމެ މުހިންމު ކަމަކީ އިސްލާމްކަމެވެ. އަދި މި ދީނުގެ ތަނބަކީ ނަމާދެވެ. އަދި މި ދީނުގެ ކުރިބޯށްޓަކީ ޖިހާދެވެ.“ އެއަށް ފަހު ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ ހުރިހާ ކަމަކަށް ބާރުވެރިކޮށްދޭނެ ކަމަކާ ބެހޭ ގޮތުން ތިބާއަށް ކިޔައި ދެއްވަންހެއްޔެވެ؟“ ތިމަން ދެންނެވީމެވެ: އާދޭހެވެ. އޭ ﷲގެ ރަސޫލާއެވެ! ދެން ނަބިއްޔާ ދޫފުޅުގައި ހިފަހައްޓަވާފައި ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ މީތި ހިފަހައްޓާށެވެ.“ ތިމަން ދެންނެވީމެވެ: އޭ ﷲގެ ނަބިއްޔާއެވެ! ދުލުން ދައްކާ ވާހަކަތަކުގެ ސަބަބުން ތިމަންމެން ހިއްޕެވޭނެހެއްޔެވެ؟ (އެބަހީ: ޢުޤޫބާތް ދެވޭނެހެއްޔެވެ؟) ފަހެ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ މުޢާޛެވެ! ތިބާގެ މަންމައަށް ތިބާ ގެއްލިދާށެވެ. (ނޯޓު: މިއީ ކަމެއްގެ ބޮޑުކަން ހާމަކުރަން ޢަރަބީންގެ ދޫމަތިން ބުނެ އުޅޭ އިބާރާތެކެވެ. ބަދު ދުޢާ ކުރުމުގެ މާނައިގައި ބުނާ ބަހެއް ނޫނެވެ.) ފަހެ މީހުންގެ ދޫތަކުން ހޯދައިގަތް ކަންތަކުން މެނުވީ، އެބައިމީހުން ނަރަކައިގެ ތެރެއަށް މޫނުވަތަށް —ނުވަތަ ނޭފަތް ވަތަށް— އެއްލާލެވޭނެހެއްޔެވެ؟“', ''], ['سنن الدارقطني 4396', 'حَدَّثَنَا الْقَاسِمُ بْنُ إِسْمَاعِيلَ، حَدَّثَنَا يَعْقُوبُ بْنُ إِبْرَاهِيمَ وَمُحَمَّدُ بْنُ حَسَّانَ الْأَزْرَقُ قَالَا: حَدَّثَنَا إِسْحَاقُ الْأَزْرَقُ، حَدَّثَنَا دَاوُدُ بْنُ أَبِي هِنْدٍ، عَنْ مَكْحُولٍ، عَنْ أَبِي ثَعْلَبَةَ الْخُشَنِيِّ قَالَ: قَالَ رَسُولُ اللهِ - ﷺ -: " إِنَّ اللهَ تَعَالَى فَرَضَ فَرَائِضَ فَلَا تُضَيِّعُوهَا، وَحَرَّمَ حُرُمَاتٍ فَلَا تَنْتَهِكُوهَا، وَحَدَّ حُدُودًا فَلَا تَعْتَدُوهَا، وَسَكَتَ عَنْ أَشْيَاءَ مِنْ غَيْرِ نِسْيَانٍ فَلَا تَبْحَثُوا عَنْهَا. ', 'އަބޫ ޘަޢުލަބާ އަލްޚުޝަނީ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ﷲ ތަޢާލާ ވަނީ ފަރުޟު ކަންތައްތަކެއް ކަނޑައަޅުއްވާފައެވެ. ވީމާ ތިޔަބައިމީހުން އެ ކަންތައްތަކަށް (އިހުމާލުވެ) ދޫކޮށް ނުލާށެވެ. އަދި އެ އިލާހު ކަންތައްތަކެއް ޙަރާމް ކުރެއްވިއެވެ. ވީމާ ތިޔަބައިމީހުން އެ ކަންތައްތަކަށް އަރައިނުގަންނާށެވެ. އަދި އެ އިލާހު (އިންތަކަކާއި) ޙައްދުތަކެއް ކަނޑައެޅުއްވިއެވެ. ވީމާ ތިޔަބައިމީހުން އެ ޙައްދުތައް ފަހަނަޅައި ނުދާށެވެ. އަދި އެ އިލާހު (ބައެއް) ކަންތައްތަކާ މެދު ސުކޫތުވެވޮޑިގަތެވެ. (އެބަހީ: ވަކި ގޮތެއް ކަނޑަނާޅުއްވައި ދޫކޮށްލެއްވިއެވެ.) އެއީ ހަނދުމަނެތިވެވޮޑިގެނެއް ނޫނެވެ. ވީމާ ތިޔަބައިމީހުން އެ ކަންތައްތައް ހޯދަން ނޫޅޭށެވެ.“', ''], ['سنن ابن ماجه 4102', 'حَدَّثَنَا أَبُو عُبَيْدَةَ بْنُ أَبِي السَّفَرِ، حَدَّثَنَا شِهَابُ بْنُ عَبَّادٍ، حَدَّثَنَا خَالِدُ بْنُ عَمْرٍو الْقُرَشِيُّ، عَنْ سُفْيَانَ الثَّوْرِيِّ، عَنْ أَبِي حَازِمٍ، عَنْ سَهْلِ بْنِ سَعْدٍ السَّاعِدِيِّ قَالَ: أَتَى النَّبِيَّ ﷺ رَجُلٌ فَقَالَ: يَا رَسُولَ اللهِ، دُلَّنِي عَلَى عَمَلٍ إِذَا أَنَا عَمِلْتُهُ أَحَبَّنِي اللهُ، وَأَحَبَّنِي النَّاسُ، فَقَالَ رَسُولُ اللهِ ﷺ: ازْهَدْ فِي الدُّنْيَا يُحِبُّكَ اللهُ، وَازْهَدْ فِيمَا فِي أَيْدِي النَّاسِ يُحِبُّوكَ. ', 'ސަހްލު ބިން ޞަޢުދު އައްސާޢިދީ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ އަރިހަށް މީހަކު އައިސް ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ! އެ ކަމެއް ކުރުމުން، ތިމަން ދެކެ ﷲ ލޯބިވެވޮޑިގަންނަވާނެ، އަދި މީސްތަކުން ވެސް ތިމަންދެކެ ލޯބިވާނެ ޢަމަލެއް ދައްކަވައި ދެއްވާށެވެ. ފަހެ ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ ދުނިޔޭގައި ޒުހުދުވެރިވާށެވެ. (އެބަހީ: ދުނިޔެއަށް އެދުންވެރި ނުވާށެވެ.) އޭރުން ﷲ ތިބާ ދެކެ ލޯބިވެވޮޑިގަންނަވާނެއެވެ. އަދި މީހުންގެ އަތުގައިވާ ތަކެއްޗާމެދު ޒުހުދުވެރިވާށެވެ. އޭރުން ތިބާ ދެކެ އެ މީހުން ލޯބިވާނެއެވެ.“', ''], ['سنن ابن ماجه 2341', 'حَدَّثَنَا مُحَمَّدُ بْنُ يَحْيَى، حَدَّثَنَا عَبْدُ الرَّزَّاقِ، أَخْبَرَنَا مَعْمَرٌ، عَنْ جَابِرٍ الْجُعْفِيِّ، عَنْ عِكْرِمَةَ، عَنِ ابْنِ عَبَّاسٍ، قَالَ: قَالَ رَسُولُ اللهِ ﷺ: لَا ضَرَرَ وَلَا ضِرَارَ. ', 'އިބްނު ޢައްބާސް ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ގެއްލުން ދިނުމެއް އޮވެގެނެއް ނުވާނެއެވެ. އަދި (މީހަކު ގެއްލުމެއް ދިނުމުން، ނުޙައްޤުން) ބަދަލުގައި ގެއްލުން ދިނުމެއް ވެސް އޮވެގެނެއް ނުވާނެއެވެ.“ (ނޯޓު: އަދި މީގެ ތެރޭގައި މި ކަންތައް ވެސް ހިމެނޭނެއެވެ: ގެއްލުން ދިނުމެއް ވެސް އަދި ގެއްލުން ލިބިގަތުމެއް ވެސް ނެތެވެ. އަދި ގަސްދެއް ނެތި އަދި ގަސްދަކާލައިގެން ވިޔަސް ގެއްލުން ދިނުމެއް ނެތެވެ. އަދި އަމިއްލަ ނަފްސަށް ވިޔަސް އަދި އެހެން މީހަކަށް ވިޔަސް ޤެއްލުން ދިނުމެއް ނެތެވެ.)', ''], ['سنن البيهقي 21201', '(أَخْبَرَنَا) أَبُو الْحَسَنِ: عَلِيُّ بْنُ أَحْمَدَ بْنِ عَبْدَانَ، أَنْبَأَ أَحْمَدُ بْنُ عُبَيْدٍ الصَّفَّارُ، ثَنَا جَعْفَرُ بْنُ مُحَمَّدٍ الْفِرْيَابِيُّ، ثَنَا الْحَسَنُ بْنُ سَهْلٍ، ثَنَا عَبْدُ اللهِ بْنُ إِدْرِيسَ، ثَنَا ابْنُ جُرَيْجٍ وَعُثْمَانُ بْنُ الْأَسْوَدِ عَنِ ابْنِ أَبِي مُلَيْكَةَ قَالَ: كُنْتُ قَاضِيًا لِابْنِ الزُّبَيْرِ عَلَى الطَّائِفِ فَذَكَرَ قِصَّةَ الْمَرْأَتَيْنِ قَالَ فَكَتَبْتُ إِلَى ابْنِ عَبَّاسٍ فَكَتَبَ ابْنُ عَبَّاسٍ رَضِيَ اللهُ عَنْهُمَا إِنَّ رَسُولَ اللهِ - ﷺ - قَالَ: لَوْ يُعْطَى النَّاسُ بِدَعْوَاهُمْ لَادَّعَى رِجَالٌ أَمْوَالَ قَوْمٍ وَدِمَاءَهُمْ وَلَكِنَّ الْبَيِّنَةَ عَلَى الْمُدَّعِي وَالْيَمِينَ عَلَى مَنْ أَنْكَرَ. وَذَكَرَ الْحَدِيثَ. ', 'އިބްނު ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމައެކަނި މީސްތަކުން ކޮށްލާ ދަޢުވާއަކުން، (ޝަރީޢަތުން) އެބައިމީހުންނަށް ކަންކަން ނިންމައިދެވޭ ނަމަ، (ﷲ އަށް ބިރުވެތި ނުވާ) މީހުން، އެހެން މީހުންގެ މުދަލާއި (މަރުގެ) ދިޔައަށް ދަޢުވާ ކުރީސްކަން ކަށަވަރެވެ. އެހެނެއްކަމަކު، ދަޢުވާ ކުރާ މީހާގެ މައްޗަށް ވަނީ (ދަޢުވާގެ ތެދުކަން ސާބިތު ކުރުމަށް) ހެކި ހުށަހެޅުމެވެ. އަދި (ދަޢުވާ ލިބި، އެއަށް) އިންކާރު ކުރާ މީހާގެ މައްޗަށް ވަނީ (އެ ދަޢުވާގެ ދޮގުކަން ބަޔާންކޮށް، ﷲ ގަންދީ) ހުވާ ކުރުމެވެ.“ (އެބަހީ: މީހެއްގެ މައްޗަށް ކުރެވޭ ދަޢުވާ ސާބިތު ކުރެވިއްޖައުމަށް ދާންދެން، އޭނާ ވާނީ އެ ކަމަކުން ބަރީއަ ވެގެންނެވެ.)', ''], ['سنن ابن ماجه 2043', 'حَدَّثَنَا إِبْرَاهِيمُ بْنُ مُحَمَّدِ بْنِ يُوسُفَ الْفِرْيَابِيُّ، حَدَّثَنَا أَيُّوبُ بْنُ سُوَيْدٍ، حَدَّثَنَا أَبُو بَكْرٍ الْهُذَلِيُّ، عَنْ شَهْرِ بْنِ حَوْشَبٍ، عَنْ أَبِي ذَرٍّ الْغِفَارِيِّ قَالَ: قَالَ رَسُولُ اللهِ ﷺ: إِنَّ اللهَ قَدْ تَجَاوَزَ عَنْ أُمَّتِي الْخَطَأَ وَالنِّسْيَانَ وَمَا اسْتُكْرِهُوا عَلَيْهِ. ', 'އަބޫ ޛައްރު އަލްޣިފާރީ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާގެ އުންމަތުގެ މީހުންގެ ކިބައިން (ގަސްދަކާ ނުލައި އޮޅުމަކުން) ކުރެވޭ ކުށްތަކާއި، ހަނދާން ނެތުމާ، އަދި ނުރުހޭ ޙާލު (މަޖުބޫރު ކުރުވައިގެން) ކުރެވޭ ކަންތައް، ﷲ ވަނީ ޢަފޫ ކޮށްދެއްވާފައެވެ.“', ''], ['سنن الترمذي 3540', ' حَدَّثَنَا عَبْدُ اللهِ بْنُ إِسْحَاقَ الْجَوْهَرِيُّ الْبَصْرِيُّ، قَالَ: حَدَّثَنَا أَبُو عَاصِمٍ، قَالَ: حَدَّثَنَا كَثِيرُ بْنُ فَائِدٍ، قَالَ: حَدَّثَنَا سَعِيدُ بْنُ عُبَيْدٍ قَالَ: سَمِعْتُ بَكْرَ بْنَ عَبْدِ اللهِ الْمُزَنِيَّ يَقُولُ: حَدَّثَنَا أَنَسُ بْنُ مَالِكٍ قَالَ: سَمِعْتُ رَسُولَ اللهِ ﷺ يَقُولُ: قَالَ اللهُ تَبَارَكَ وَتَعَالَى: يَا ابْنَ آدَمَ، إِنَّكَ مَا دَعَوْتَنِي وَرَجَوْتَنِي غَفَرْتُ لَكَ عَلَى مَا كَانَ فِيكَ وَلَا أُبَالِي، يَا ابْنَ آدَمَ لَوْ بَلَغَتْ ذُنُوبُكَ عَنَانَ السَّمَاءِ، ثُمَّ اسْتَغْفَرْتَنِي غَفَرْتُ لَكَ وَلَا أُبَالِي، يَا ابْنَ آدَمَ، إِنَّكَ لَوْ أَتَيْتَنِي بِقُرَابِ الْأَرْضِ خَطَايَا ثُمَّ لَقِيتَنِي لَا تُشْرِكُ بِي شَيْئًا لَأَتَيْتُكَ بِقُرَابِهَا مَغْفِرَةً. \nهَذَا حَدِيثٌ حَسَنٌ غَرِيبٌ لَا نَعْرِفُهُ إِلَّا مِنْ هَذَا الْوَجْهِ. ', 'އަނަސް ބިން މާލިކު ރިވާ ކުރެއްވިއެވެ: (ޤުދުސީ ޙަދީޘެއްގައި) ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ: ”ﷲ ތަބާރަކަ ވަތަޢާލާ ވަޙީ ކުރެއްވިއެވެ: އޭ އާދަމުގެ ދަރިއާއެވެ! ހަމަކަށަވަރުން ތިބާ، ތިމަން އިލާހަށް ދުޢާކޮށް ދަންނަވައި އަދި ތިމަން އިލާހުގެ ޙަޟްރަތުން އުންމީދު ކުރާހައި ހިނދަކު، ތިބާގެ ކިބައިން ވެވިފައިވާ ފާފަތައް ތިމަން އިލާހު ފުއްސަވައި ދެއްވާނަމެވެ. އަދި (އެ ފާފަތަކަށް) ތިމަން އިލާހު ނުބައްލަވާނަމެވެ. އޭ އާދަމުގެ ދަރިއާއެވެ! އުޑުގެ ވިލާތަކާ ހަމައަށް ތިބާގެ ފާފަތައް ފޯރިކަމުގައި ވިޔަސް، ފާފަ ފުއްސަވައި ދެއްވުމަށް އެދި ތިބާ ދަންނަވައިފި ނަމަ، ތިމަން އިލާހު ތިބާއަށް ފާފަ ފުއްސަވައި ދެއްވާނަމެވެ. އަދި (އެ ފާފަތަކަށް) ތިމަން އިލާހު ނުބައްލަވާނަމެވެ. އޭ އާދަމުގެ ދަރިއާއެވެ! ހަމަކަށަވަރުން ބިން ފުރޭހައި ވަރަށް ތިބާ ފާފަތަކައިގެން ތިމަން އިލާހާ ބައްދަލުވި ކަމުގައި ވިޔަސް، ތިބާ ތިމަން އިލާހާ އެއްވެސް ފަރާތެއް ޝަރީކުކޮށްފައި ނުވާ ނަމަ، ހަމަ އެއާ އެއްވަރަށް (އެބަހީ: ބިން ފުރޭހައި ވަރަށް) ތިމަން އިލާހު ތިބާއާ ފާފަ ފުއްސެވުމައިގެން ތިމަން އިލާހު ތިބާއާ ބައްދަލު ކުރައްވާނެކަން ކަށަވަރެވެ.“', ''], ['مسند أحمد 17186', 'حَدَّثَنَا أَبُو الْمُغِيرَةِ قَالَ: حَدَّثَنَا سُلَيْمَانُ بْنُ سُلَيْمٍ الْكِنَانِيُّ قَالَ: حَدَّثَنَا يَحْيَى بْنُ جَابِرٍ الطَّائِيُّ قَالَ: سَمِعْتُ الْمِقْدَامَ بْنَ مَعْدِي كَرِبَ الْكِنْدِيَّ قَالَ: سَمِعْتُ رَسُولَ اللهِ ﷺ يَقُولُ: مَا مَلَأَ ابْنُ آدَمَ وِعَاءً شَرٌّ مِنْ بَطْنٍ، حَسْبُ ابْنِ آدَمَ أُكُلَاتٌ يُقِمْنَ صُلْبَهُ، فَإِنْ كَانَ لَا مَحَالَةَ، فَثُلُثٌ طَعَامٌ، وَثُلُثٌ شَرَابٌ، وَثُلُثٌ لِنَفَسِهِ. ', 'އަލްމިޤްދާމު ބިން މަޢުދީ ކަރިބު އަލްކިންދީ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ: ”އާދަމުގެ ދަރިޔާ ބަނޑަށް ވުރެ ނުބައި ކަންވާރެއް ނުފުރައެވެ. އާދަމުގެ ދަރިޔާއަށް، އޭނާގެ ބުރަކަށި ސީދާ ތެދަށް ބަހައްޓައިދޭނެ މަދު ކާއެތިކޮޅަކުން ފުދޭނެއެވެ. ނަމަވެސް އެ ވަރުން ނުފުދޭ ނަމަ، ފަހެ (ބަނޑުގެ) ތިންބައި ކުޅަ އެއްބައި އޭނާގެ ކާއެއްޗެއްސަށް ބަހައްޓާށެވެ. އަދި ތިންބައި ކުޅަ އެއްބައި އޭނާގެ ބޯއެއްޗެއްސަށް ބަހައްޓާށެވެ. އަދި ތިންބައި ކުޅަ އެއްބައި އޭނާ ނޭވާލުމަށް (ހުސްކޮށް) ބަހައްޓާށެވެ.“', ''], ['مسند أحمد 205', 'أَخْبَرَنَا عَبْدُ اللهِ بْنُ يَزِيدَ قَالَ: ثَنَا حَيْوَةُ بْنُ شُرَيْحٍ، قَالَ: أَخْبَرَنِي بَكْرُ بْنُ عَمْرٍو أَنَّهُ سَمِعَ عَبْدَ اللهِ بْنَ هُبَيْرَةَ أَنَّهُ سَمِعَ أَبَا تَمِيمٍ الْجَيْشَانِيَّ يَقُولُ: إِنَّهُ سَمِعَ عُمَرَ بْنَ الْخَطَّابِ يَقُولُ: إِنَّهُ سَمِعَ رَسُولَ اللهِ ﷺ يَقُولُ: لَوْ أَنَّكُمْ تَوَكَّلُونَ عَلَى اللهِ حَقَّ تَوَكُّلِهِ لَرَزَقَكُمْ كَمَا يَرْزُقُ الطَّيْرَ تَغْدُو خِمَاصًا وَتَرُوحُ بِطَانًا. ', 'ޢުމަރު ބިން އަލްޚައްޠާބު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ”ﷲ އާ ވަކީލު ކުރަން ޖެހޭ ޙައްޤު ގޮތުގައި ތިޔަބައިމީހުން އެ އިލާހާ ވަކީލު ކުރާ ނަމަ، ﷲ ދޫނިތަކަށް ރިޒުޤު ދެއްވާ ފަދައިން އެ އިލާހު ތިޔަބައިމީހުންނަށް ވެސް ރިޒުޤު ދެއްވާނެކަން ކަށަވަރެވެ. ހެނދުނު ހެނދުނާ އެ ދޫނިތައް ނުކުމެގެން ދަނީ ހުސް ބަނޑާއެވެ. އަދި ހަވީރު އެނބުރި އަންނަނީ ބަނޑު ފުރައިގެންނެވެ.“', ''], ['مسند أحمد 17680', 'حَدَّثَنَا عَلِيُّ بْنُ عَيَّاشٍ، حَدَّثَنَا حَسَّانُ بْنُ نُوحٍ، عَنْ عَمْرِو بْنِ قَيْسٍ، عَنْ عَبْدِ اللهِ بْنِ بُسْرٍ، قَالَ: أَتَى النَّبِيَّ ﷺ أَعْرَابِيَّانِ، فَقَالَ أَحَدُهُمَا: مَنْ خَيْرُ الرِّجَالِ يَا مُحَمَّدُ؟ قَالَ النَّبِيُّ ﷺ: «مَنْ طَالَ عُمْرُهُ، وَحَسُنَ عَمَلُهُ» وَقَالَ الْآخَرُ: إِنَّ شَرَائِعَ الْإِسْلَامِ قَدْ كَثُرَتْ عَلَيْنَا، فَبَابٌ نَتَمَسَّكُ بِهِ جَامِعٌ؟ قَالَ: «لَا يَزَالُ لِسَانُكَ رَطْبًا مِنْ ذِكْرِ اللهِ [عَزَّ وَجَلَّ]». ', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ގަބޫލު ކުރެވިގެންވާ ފުރިހަމަ ޙައްޖަކަށް ސުވަރުގެ ފިޔަވައި އެހެން ޖަޒާއެއް ނުވެއެވެ. އަދި އެއް ޢުމްރާއަކަށް ފަހު އަނެއް ޢުމްރާ (ކުރުމަކީ) އޭގެ ދެމެދުގައި ހިނގައިދިޔަ ފާފަތަކަށްވާ ކައްފާރާއެކެވެ.“', ''], ['سنن الترمذي 889', 'حَدَّثَنَا مُحَمَّدُ بْنُ بَشَّارٍ، قَالَ: حَدَّثَنَا يَحْيَى بْنُ سَعِيدٍ، وَعَبْدُ الرَّحْمَنِ بْنُ مَهْدِيٍّ قَالَا: حَدَّثَنَا سُفْيَانُ، عَنْ بُكَيْرِ بْنِ عَطَاءٍ، عَنْ عَبْدِ الرَّحْمَنِ بْنِ يَعْمَرَ؛ أَنَّ نَاسًا مِنْ أَهْلِ نَجْدٍ أَتَوْا رَسُولَ اللهِ ﷺ وَهُوَ بِعَرَفَةَ، فَسَأَلُوهُ، فَأَمَرَ مُنَادِيًا فَنَادَى: الْحَجُّ عَرَفَةُ، مَنْ جَاءَ لَيْلَةَ جَمْعٍ قَبْلَ طُلُوعِ الْفَجْرِ فَقَدْ أَدْرَكَ الْحَجَّ، أَيَّامُ مِنًى ثَلَاثَةٌ، { فَمَنْ تَعَجَّلَ فِي يَوْمَيْنِ فَلا إِثْمَ عَلَيْهِ وَمَنْ تَأَخَّرَ فَلا إِثْمَ عَلَيْهِ }. \nقَالَ: وَزَادَ يَحْيَى: وَأَرْدَفَ رَجُلًا فَنَادَى. ', 'ޢަބްދުއްރަޙްމާން ބިން ޔަޢުމަރު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޢަރަފާތުގައި ހުންނަވަނިކޮށް، ނަޖުދު ކަރައިގެ ބަޔަކު އެ ނަބިއްޔާގެ އަރިހަށް އަތުވެއްޖެއެވެ. އަދި އެބައިމީހުން (ޙައްޖާ ބެހޭ ގޮތުން) އެ ނަބިއްޔާއާ ސުވާލު ކުރެއްވިއެވެ. ދެން (މި ފަދައިން) ގޮވައިލެއްވުމަށް ނަބިއްޔާ މީހަކަށް އަމުރު ކުރެއްވިއެވެ: ”ޙައްޖަކީ ޢަރަފާތެވެ. (އެބަހީ: ޢަރަފާތުގައި މަޑުނުކޮށް ޙައްޖު ޞައްޙައެއް ނުވާނެއެވެ.) (ޢީދު ދުވަހު) ފަތިސް ވުމުގެ ކުރިން މުޒްދަލިފާގެ ވިލޭރޭގައި (ًޢަރަފާތަށް) އަތުވެއްޖެ މީހާ، ފަހެ ހަމަކަށަވަރުން ޙައްޖު ހޯދައިފިއެވެ. މިނާގައި (މަޑުކުރުން ވަނީ އައްޔާމުއް ތަޝްރީޤުގެ) ތިން ދުވަހަށެވެ.“ (ދެން މި އާޔަތް ކިޔަވައި ވިދާޅުވިއެވެ:) ❞ފަހެ، ދެ ދުވަހުގެ ތެރޭގައި (މިނާގައި މަޑުކުރުން ނިންމައި އެނބުރި ދިޔުމަށް) އަވަސްވެގަތް މީހާގެ މައްޗަކަށް ފާފައެއް ނެތެވެ. އަދި (ތިން ދުވަހަށް) ލަސްކުރި މީހާގެ މައްޗަކަށް ވެސް ފާފައެއް ނެތެވެ.❝', ''], ['سنن النسائي 2622', 'أَخْبَرَنَا عَبْدَةُ بْنُ عَبْدِ اللهِ الصَّفَّارِ الْبَصْرِيُّ، قَالَ: حَدَّثَنَا سُوَيْدٌ - وَهُوَ ابْنُ عَمْرٍو الْكَلْبِيُّ -، عَنْ زُهَيْرٍ، قَالَ: حَدَّثَنَا سُهَيْلٌ، عَنْ سُمَيٍّ، عَنْ أَبِي صَالِحٍ، عَنْ أَبِي هُرَيْرَةَ قَالَ: قَالَ رَسُولُ اللهِ ﷺ: الْحَجَّةُ الْمَبْرُورَةُ لَيْسَ لَهَا جَزَاءٌ إِلَّا الْجَنَّةُ، وَالْعُمْرَةُ إِلَى الْعُمْرَةِ كَفَّارَةٌ لِمَا بَيْنَهُمَا. ', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ސސސ“', ''], ['سنن النسائي 2416', 'أَخْبَرَنَا أَبُو بَكْرِ بْنُ أَبِي النَّضْرِ قَالَ: حَدَّثَنِي أَبُو النَّضْرِ قَالَ: حَدَّثَنَا أَبُو إِسْحَاقَ الْأَشْجَعِيُّ - كُوفِيٌّ - عَنْ عَمْرِو بْنِ قَيْسٍ الْمُلَائِيِّ، عَنِ الْحُرِّ بْنِ الصَّيَّاحِ، عَنْ هُنَيْدَةَ بْنِ خَالِدٍ الْخُزَاعِيِّ، عَنْ حَفْصَةَ قَالَتْ: أَرْبَعٌ لَمْ يَكُنْ يَدَعُهُنَّ النَّبِيُّ ﷺ؛ صِيَامَ عَاشُورَاءَ، وَالْعَشْرَ، وَثَلَاثَةَ أَيَّامٍ مِنْ كُلِّ شَهْرٍ، وَرَكْعَتَيْنِ قَبْلَ الْغَدَاةِ.', 'ޙަފްޞާ ރިވާ ކުރެއްވިއެވެ: ހަތަރުކަމެއް ރަސޫލު ﷲ ﷺ ދޫނުކުރައްވައެވެ. އެއީ ޢާޝޫރާ ދުވަހު ރޯދަ ހިފުމާއި، ޒުލް ޙިއްޖާ މަހުގެ ދިހަ ދުވަހު ރޯދަ ހިފުމާއި، ކޮންމެ މަހެއްގެ ތިން ދުވަހު (ތޭރަ، ސާދަ، ފަނަރަވަނަ ދުވަހު) ރޯދަ ހިފުމާއި، އަދި ފަތިސް ނަމާދުގެ ކުރީގައި ދެ ރަކުޢަތް ކުރުމެވެ.“', ''], ['سنن ابن ماجه 3375', 'حَدَّثَنَا أَبُو بَكْرِ بْنُ أَبِي شَيْبَةَ، وَمُحَمَّدُ بْنُ الصَّبَّاحِ، قَالَا: حَدَّثَنَا مُحَمَّدُ بْنُ سُلَيْمَانَ بْنِ الْأَصْبَهَانِيِّ، عَنْ سُهَيْلٍ، عَنْ أَبِيهِ، عَنْ أَبِي هُرَيْرَةَ قَالَ: قَالَ رَسُولُ اللهِ ﷺ: مُدْمِنُ الْخَمْرِ كَعَابِدِ وَثَنٍ. ', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ރާ ބުއިމަށް ދެވިހިފާފައިވާ މީހަކީ ބުދަށް އަޅުކަން ކުރާ މީހާ ފަދަ މީހެކެވެ.“', ''], ['سنن أبي داود 3681', 'حَدَّثَنَا قُتَيْبَةُ، نَا إِسْمَاعِيلُ يَعْنِي: ابْنَ جَعْفَرٍ، عَنْ دَاوُدَ بْنِ بَكْرِ بْنِ أَبِي الْفُرَاتِ، عَنْ مُحَمَّدِ بْنِ الْمُنْكَدِرِ، عَنْ جَابِرِ بْنِ عَبْدِ اللهِ قَالَ: قَالَ رَسُولُ اللهِ ﷺ: مَا أَسْكَرَ كَثِيرُهُ فَقَلِيلُهُ حَرَامٌ.', 'ޖާބިރު ބިން ޢަބްދު ﷲ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ އެއްޗެއްގެ ގިނަ މިންވަރަކުން މަސްތު ކުރުވާ އެއްޗެއް، ފަހެ އޭގެ މަދު މިންވަރުވެސް ޙަރާމްވެގެންވެއެވެ.“', ''], ['سنن البيهقي 9620', '(وَأَخْبَرَنَا) عَلِيُّ بْنُ أَحْمَدَ بْنِ عَبْدَانَ، أَنْبَأَ سُلَيْمَانُ بْنُ أَحْمَدَ بْنِ أَيُّوبَ، ثَنَا عَلِيُّ بْنُ عَبْدِ الْعَزِيزِ، ثَنَا أَبُو نُعَيْمٍ قَالَ: وَحَدَّثَنَا حَفْصٌ، ثَنَا قَبِيصَةُ قَالَ: وَحَدَّثَنَا يُوسُفُ الْقَاضِي، وَمُعَاذُ بْنُ الْمُثَنَّى قَالَا: ثَنَا ابْنُ كَثِيرٍ قَالُوا: ثَنَا سُفْيَانُ، عَنْ أَبِي الزُّبَيْرِ، عَنْ جَابِرٍ قَالَ: أَفَاضَ رَسُولُ اللهِ - ﷺ - وَعَلَيْهِ السَّكِينَةُ، وَأَمَرَهُمْ بِالسَّكِينَةِ، وَأَوْضَعَ فِي وَادِي مُحَسِّرٍ، وَأَمَرَهُمْ أَنْ يَرْمُوا الْجِمَارَ مِثْلَ حَصَى الْخَذْفِ، وَقَالَ: خُذُوا عَنِّي مَنَاسِكَكُمْ؛ لَعَلِّي لَا أَرَاكُمْ بَعْدَ عَامِي هَذَا. ', 'ޖާބިރު ބިން ޢަބްދު ﷲ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺމުޒްދަލިފާއިން ވަޑައިގަތީ މަޑުމޮޅިކަމާ އެކުގައެވެ. އަދި މީސްތަކުންނަށް އަމުރު ކުރެއްވީ މަޑުމޮޅިކަމާއެކު އުޅުމަށެވެ. ދެން މުޙައްސިރު ވާދީއަށް (ވަނުމަށް ފަހު) އެ ނަބިއްޔާގެ ޖަމަލުކޮޅު އަވަސް ކުރެއްވިއެވެ. އަދި މީސްތަކުންނަށް އަމުރު ކުރެއްވީ ޖަމަރާތަކަށް ކުދި ހިލަތަކުން އުކުމަށެވެ. އަދި ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ޙައްޖުގެ އަޅުކަން އަދާކުރަންވީ ގޮތްތަށް ތިމަން ނަބިއްޔާގެ ކިބައިން ތިޔަބައިމީހުންގެ ނަގާށެވެ. ތިމަން ނަބިއްޔާގެ މި އަހަރަށް ފަހު، ތިމަން ނަބިއްޔާ ތިޔަބައިމީހުން ނުފެނިދާނެއެވެ.“', ''], ['سنن الترمذي 2377', 'حَدَّثَنَا مُوسَى بْنُ عَبْدِ الرَّحْمَنِ الْكِنْدِيُّ، قَالَ: حَدَّثَنَا زَيْدُ بْنُ حُبَابٍ قَالَ: أَخْبَرَنِي الْمَسْعُودِيُّ، قَالَ: حَدَّثَنَا عَمْرُو بْنُ مُرَّةَ، عَنْ إِبْرَاهِيمَ، عَنْ عَلْقَمَةَ، عَنْ عَبْدِ اللهِ قَالَ: نَامَ رَسُولُ اللهِ ﷺ عَلَى حَصِيرٍ، فَقَامَ وَقَدْ أَثَّرَ فِي جَنْبِهِ، فَقُلْنَا: يَا رَسُولَ اللهِ لَوِ اتَّخَذْنَا لَكَ وِطَاءً فَقَالَ: مَا لِي وَلِلدُّنْيَا، مَا أَنَا فِي الدُّنْيَا إِلَّا كَرَاكِبٍ اسْتَظَلَّ تَحْتَ شَجَرَةٍ ثُمَّ رَاحَ وَتَرَكَهَا. \nوَفِي الْبَابِ عَنِ ابْنِ عُمَرَ، وَابْنِ عَبَّاسٍ. \nهَذَا حَدِيثٌ حَسَنٌ صَحِيحٌ. ', 'ޢަބްދު ﷲ ބިން މަސްޢޫދު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ކުނަލެއް މަތީ އަވަހާރަލެއްޕެވިއެވެ. ދެން ހޭފުޅުލެވުން އިރު ނަބިއްޔާގެ އަރިކަށިފުޅުގައި އޭގެ އަސަރު ހުއްޓެވެ. ތިމަންމެން ދެންނެވީމެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ! އިބަ ނަބިއްޔާއަށް ޓަކައި (ރަނގަޅު) އެނދެއް ތިމަންމެން ތައްޔާރު ކުރެވިދާނެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާއާ ދުނިޔެއާ ހުރީ ކޮން ގުޅުމެއްތޯއެވެ؟ ދަތުރުވެރިއަކު ގަހެއްގެ ހިޔަލުގައި މަޑުކޮށްލުމުށް ފަހު، އެ ތަން ދޫކޮށްލައި ދިޔަފަދަ މީހަކަށް މެނުވީ ތިމަން ނަބިއްޔާ މި ދުނިޔެ މަތީ ނުވަމެވެ.“', ''], ['سنن الترمذي 241', 'حَدَّثَنَا عُقْبَةُ بْنُ مُكْرَمٍ وَنَصْرُ بْنُ عَلِيٍّ، قَالَا: حَدَّثَنَا سَلْمُ بْنُ قُتَيْبَةَ، عَنْ طُعْمَةَ بْنِ عَمْرٍو، عَنْ حَبِيبِ بْنِ أَبِي ثَابِتٍ، عَنْ أَنَسِ بْنِ مَالِكٍ، قَالَ: قَالَ رَسُولُ اللهِ ﷺ: مَنْ صَلَّى لِلهِ أَرْبَعِينَ يَوْمًا فِي جَمَاعَةٍ يُدْرِكُ التَّكْبِيرَةَ الْأُولَى كُتِبَتْ لَهُ بَرَاءَتَانِ: بَرَاءَةٌ مِنَ النَّارِ، وَبَرَاءَةٌ مِنَ النِّفَاقِ. \nوَقَدْ رُوِيَ هَذَا الْحَدِيثُ عَنْ أَنَسٍ مَوْقُوفًا، وَلَا أَعْلَمُ أَحَدًا رَفَعَهُ إِلَّا مَا رَوَى سَلْمُ بْنُ قُتَيْبَةَ، عَنْ طُعْمَةَ بْنِ عَمْرٍو، وَإِنَّمَا يُرْوَى هَذَا عَنْ حَبِيبِ بْنِ أَبِي حَبِيبٍ الْبَجَلِيِّ، عَنْ أَنَسِ بْنِ مَالِكٍ قَوْلَهُ. ', 'އަނަސް ބިން މާލިކު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ފުރަތަމަ ތަކްބީރު ލިބޭގޮތަށް ﷲ އަށްޓަކައި ސާޅީސް ދުވަސްވާންދެން ޖަމާޢަތުގައި ނަމާދުކޮށްފި މީހާ، އޭނާ ދެ ކަމަކުން ބަރީއަވާނޭ ކަމަށް ލިޔެވޭނެއެވެ. އެއީ ނަރަކައިން ބަރީއަވުމާއި، މުނާފިޤުކަމުން ބަރީއަވުމެވެ.“', ''], ['صحيح ابن خزيمة 1888', 'حَدَّثَنَا الرَّبِيعُ بْنُ سُلَيْمَانَ، قَالَ: حَدَّثَنَا ابْنُ وَهْبٍ، قَالَ: أَخْبَرَنِي سُلَيْمَانُ وَهُوَ ابْنُ بِلَالٍ، عَنْ كَثِيرِ بْنِ زَيْدٍ، عَنِ الْوَلِيدِ بْنِ رَبَاحٍ، عَنْ أَبِي هُرَيْرَةَ أَنَّ رَسُولَ اللهِ - ﷺ - رَقِيَ الْمِنْبَرَ، فَقَالَ: " آمِينَ، آمِينَ، آمِينَ ". فَقِيلَ لَهُ: يَا رَسُولَ اللهِ، مَا كُنْتَ تَصْنَعُ هَذَا؟ فَقَالَ: قَالَ لِي جِبْرِيلُ: أَرْغَمَ اللهُ أَنْفَ عَبْدٍ - أَوْ بَعُدَ - دَخَلَ رَمَضَانَ فَلَمْ يُغْفَرْ لَهُ، فَقُلْتُ: آمِينَ، ثُمَّ قَالَ: رَغِمَ أَنْفُ عَبْدٍ - أَوْ بَعُدَ - أَدْرَكَ وَالِدَيْهِ، أَوْ أَحَدَهُمَا لَمْ يُدْخِلْهُ الْجَنَّةَ، فَقُلْتُ: آمِينَ، ثُمَّ قَالَ: رَغِمَ أَنْفُ عَبْدٍ - أَوْ بَعُدَ - ذُكِرْتَ عِنْدَهُ فَلَمْ يُصَلِّ عَلَيْكَ، فَقُلْتُ: آمِينَ. ', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ މިންބަރު މައްޗަށް އަރައިވަޑައިގެން ވިދާޅިއެވެ: ”އާމީން، އާމީން، އާމީން.“ ނަބިއްޔާއަށް ދެންނެވުނެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ! ތިޔަފަދައިން ކަންތައް ކުރެއްވީ ކީއްވެގެންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ޖިބްރީލުގެފާނު ތިމަން ނަބިއްޔާއަށް ދެންނެވިއެވެ: ރަމަޟާން މަހާ ބައްދަލުވެ އެ އަޅެއްގެ ފާފަ ނުފުއްސެވޭ އަޅަކު ﷲ ނިކަމެތިކުރައްވާށިއެވެ. (ނުވަތަ އެ މީހަކަށް ހިތާމައާއި ގެއްލުން ހުއްޓެވެ.) ފަހެ ތިމަން ނަބިއްޔާ ވިދާޅުވީމެވެ: އާމީން. އެއަށްފަހު ޖިބްރީލުގެފާނު ދެންނެވިއެވެ: އެ އަޅެއްގެ މައިންބަފައިންނާ ބައްދަލުވެ، ނުވަތަ އެ ދެމީހުން ކުރެ އެކަކާ ބައްދަލުވެ، (މައިންބަފައިންނަށް ހެޔޮކޮށް ހިތުމުގެ) ސަބަބުން ސުވަރުގެއަށް ނުވައްދަވާ އަޅަކު ﷲ ނިކަމެތިކުރައްވާށިއެވެ. ފަހެ ތިމަން ނަބިއްޔާ ވިދާޅުވީމެވެ: އާމީން. އެއަށްފަހު ޖިބްރީލުގެފާނު ދެންނެވިއެވެ: އެ އަޅެއްގެ ހާޒިރުގައި އިބަ ނަބިއްޔާގެ ނަންގަނެވޭ ހިނދު އިބަ ނަބިއްޔާގެ މައްޗަށް ޞަލަވާތް ނުކިޔާ އަޅަކު ﷲ ނިކަމެތިކުރައްވާށިއެވެ. ފަހެ ތިމަން ނަބިއްޔާ ވިދާޅުވީމެވެ: އާމީން.“', ''], ['سنن النسائي 2526', 'أَخْبَرَنَا عَبْدُ الْوَهَّابِ بْنُ عَبْدِ الْحَكَمِ، عَنْ حَجَّاجٍ، قَالَ ابْنُ جُرَيْجٍ: أَخْبَرَنِي عُثْمَانُ بْنُ أَبِي سُلَيْمَانَ، عَنْ عَلِيٍّ الْأَزْدِيِّ، عَنْ عُبَيْدِ بْنِ عُمَيْرٍ، عَنْ عَبْدِ اللهِ بْنِ حُبْشِيٍّ الْخَثْعَمِيِّ أَنَّ النَّبِيَّ ﷺ سُئِلَ: أَيُّ الْأَعْمَالِ أَفْضَلُ؟ قَالَ: " إِيمَانٌ لَا شَكَّ فِيهِ، وَجِهَادٌ لَا غُلُولَ فِيهِ، وَحَجَّةٌ مَبْرُورَةٌ ". قِيلَ: فَأَيُّ الصَّلَاةِ أَفْضَلُ؟ قَالَ: " طُولُ الْقُنُوتِ "، قِيلَ: فَأَيُّ الصَّدَقَةِ أَفْضَلُ؟ قَالَ: " جُهْدُ الْمُقِلِّ "، قِيلَ: فَأَيُّ الْهِجْرَةِ أَفْضَلُ؟ قَالَ: " مَنْ هَجَرَ مَا حَرَّمَ اللهُ عَزَّ وَجَلَّ "، قِيلَ: فَأَيُّ الْجِهَادِ أَفْضَلُ؟ قَالَ: " مَنْ جَاهَدَ الْمُشْرِكِينَ بِمَالِهِ وَنَفْسِهِ "، قِيلَ: فَأَيُّ الْقَتْلِ أَشْرَفُ؟ قَالَ: " مَنْ أُهَرِيقَ دَمُهُ وَعُقِرَ جَوَادُهُ. ', 'ޢަބްދު ﷲ ބިން ޙުބްޝީ އަލްޖަޘްޢަމީ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ އަށް ސުވާލު ކުރެވުނެވެ: އެންމެ ހެޔޮވެގެންވާ ޢަމަލަކީ ކޮބައިތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެއްވެސް ޝައްކުކަމެއް ނެތް އީމާންކަމެވެ. އަދި ޣަނީމާ މުދަލަށް ޚިޔާނާތްތެރިވުމެއް ނެތް ޖިހާދެވެ. އަދި ގަބޫލު ކުރެވިގެންވާ ފުރިހަމަ ޙެއްޖެވެ.“ ނަބިއްޔާއަށް ދެންނެވުނެވެ: ފަހެ އެންމެ ހެޔޮވެގެންވާ ނަމާދަކީ ކޮބައިތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ދާއިމީ ގޮތެއްގައި ކުރެވޭ ނަމާދެވެ.“ ނަބިއްޔާއަށް ދެންނެވުނެވެ: ފަހެ އެންމެ ހެޔޮވެގެންވާ ޞަދަޤާތަކީ ކޮބައިތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”މުދާ މަދުން ލިބިފައި ހުރި މީހާ (އޭނައަށް ވީވަރަކުން) ކުރާ ޞަދަޤާތެވެ.“ ނަބިއްޔާއަށް ދެންނެވުނެވެ: ފަހެ އެންމެ ހެޔޮވެގެންވާ ހިޖުރައަކީ ކޮބައިތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲ ޢައްޒަ ވަޖައްލަ ޙަރާމް ކުރެއްވި ކަންތައްތައް ދޫކޮށްލި މީހާއެވެ.“ ނަބިއްޔާއަށް ދެންނެވުނެވެ: ފަހެ އެންމެ ހެޔޮވެގެންވާ ޖިހާދަކީ ކޮބައިތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ މީހެއްގެ މުދަލާއި ފުރާނައިން މުޝްރިކުންނާ ދެކޮޅަށް ޖިހާދުކުރި މީހާއެވެ.“ ނަބިއްޔާއަށް ދެންނެވުނެވެ: ފަހެ އެންމެ ޝަރަފުވެރި ޤަތުލުވުމަކީ ކޮބައިތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ މީހެއްގެ ލޭ އޮހޮރެވި، އަދި އޭނާގެ އަސް މަރާލެވޭ ނުވަތަ ކޮށާލެވޭ މީހާއެވެ.“', ''], ['سنن النسائي 4165', 'أَخْبَرَنَا أَحْمَدُ بْنُ عَبْدِ اللهِ بْنِ الْحَكَمِ، قَالَ: حَدَّثَنَا مُحَمَّدُ بْنُ جَعْفَرٍ، قَالَ: حَدَّثَنَا شُعْبَةُ، عَنْ عَمْرِو بْنِ مُرَّةَ، عَنْ عَبْدِ اللهِ بْنِ الْحَارِثِ، عَنْ أَبِي كَثِيرٍ، عَنْ عَبْدِ اللهِ بْنِ عَمْرٍو، قَالَ: قَالَ رَجُلٌ: يَا رَسُولَ اللهِ، أَيُّ الْهِجْرَةِ أَفْضَلُ؟ قَالَ: أَنْ تَهْجُرَ مَا كَرِهَ رَبُّكَ عَزَّ وَجَلَّ. وَقَالَ رَسُولُ اللهِ ﷺ: الْهِجْرَةُ هِجْرَتَانِ: هِجْرَةُ الْحَاضِرِ، وَهِجْرَةُ الْبَادِي، فَأَمَّا الْبَادِي فَيُجِيبُ إِذَا دُعِيَ، وَيُطِيعُ إِذَا أُمِرَ، وَأَمَّا الْحَاضِرُ فَهُوَ أَعْظَمُهُمَا بَلِيَّةً، وَأَعْظَمُهُمَا أَجْرًا. ', 'ޢަބްދު ﷲ ބިން ޢަމްރު ރިވާ ކުރެއްވިއެވެ: މީހަކު ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ! އެންމެ ހެޔޮވެގެންވާ ހިޖުރައަކީ ކޮބައިތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާގެ ރައްބު، ﷲ ޢައްޒަ ވަޖައްލަ ރުހިވޮޑިގެން ނުވާ ކަންތައްތައް ތިބާ ދޫކޮށްލުމެވެ.“ އަދި ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ދެ ވައްތަރެއްގެ ހިޖުރަވެއެވެ. ރަށްފުށުގެ އަހުލުވެރިޔާގެ ހިޖުރައާއި، ބާދިޔާގެ އަހުލުވެރިޔާގެ ހިޖުރައެވެ. ބާދިޔާގެ އަހުލުވެރިޔާގެ (ހިޖުރައިގައި)، ފަހެ އޭނާއަކަށް (ވަޠަން ދޫކޮށް ހިޖުރަ ކުރުމެއް ނެތެވެ. އެހެނެއްކަމަކު) އޮތީ (ޖިހާދަށް) އޭނާއަށް ގޮވާލެވޭ ހިނދު އިޖާބަ ދިނުމާއި، (ވެރިމީހާ) އޭނާ އަމުރެއް ކުރާ ހިނދު ތަބާވުމެވެ. އަދި ރަށްފުށުގެ އަހުލުވެރިޔާގެ (ހިޖުރައިގައި)، ފަހެ އޭނާއީ އެ ދެމީހުންކުރެ ބޮޑަށް އިމްތިޚާނު ކުރެވޭނޭ އަދި ބޮޑަށް ސަވާބު ލިބޭނޭ މީހާއެވެ.“', ''], ['سنن الترمذي 2910', 'حَدَّثَنَا مُحَمَّدُ بْنُ بَشَّارٍ، قَالَ: حَدَّثَنَا أَبُو بَكْرٍ الْحَنَفِيُّ، قَالَ: حَدَّثَنَا الضَّحَّاكُ بْنُ عُثْمَانَ، عَنْ أَيُّوبَ بْنِ مُوسَى قَالَ: سَمِعْتُ مُحَمَّدَ بْنَ كَعْبٍ الْقُرَظِيَّ يَقُولُ: سَمِعْتُ عَبْدَ اللهِ بْنَ مَسْعُودٍ يَقُولُ: قَالَ رَسُولُ اللهِ ﷺ: مَنْ قَرَأَ حَرْفًا مِنْ كِتَابِ اللهِ فَلَهُ بِهِ حَسَنَةٌ، وَالْحَسَنَةُ بِعَشْرِ أَمْثَالِهَا، لَا أَقُولُ (الم) حَرْفٌ، وَلَكِنْ أَلِفٌ حَرْفٌ، وَلَامٌ حَرْفٌ، وَمِيمٌ حَرْفٌ. \nوَيُرْوَى هَذَا الْحَدِيثُ مِنْ غَيْرِ هَذَا الْوَجْهِ عَنِ ابْنِ مَسْعُودٍ، رَوَاهُ أَبُو الْأَحْوَصِ، عَنِ ابْنِ مَسْعُودٍ، رَفَعَهُ بَعْضُهُمْ وَوَقَفَهُ بَعْضُهُمْ عَنِ ابْنِ مَسْعُودٍ. \nهَذَا حَدِيثٌ حَسَنٌ صَحِيحٌ غَرِيبٌ مِنْ هَذَا الْوَجْهِ، سَمِعْتُ قُتَيْبَةَ بْنَ سَعِيدٍ يَقُولُ: بَلَغَنِي أَنَّ مُحَمَّدَ بْنَ كَعْبٍ الْقُرَظِيَّ وُلِدَ فِي حَيَاةِ النَّبِيِّ ﷺ، وَمُحَمَّدُ بْنُ كَعْبٍ يُكْنَى أَبَا حَمْزَةَ. \n', 'ޢަބްދުﷲ ބިން މަސްޢޫދު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲގެ ފޮތުން (އެންމެ) ޙަރްފެއް ކިޔައިފި މީހަކަށް، އޭގެ ސަބަބުން އޭނާއަށް އެއް ހެޔޮކަން ލިބިގެންވެއެވެ. އަދި ކޮންމެ ހެޔޮ ކަމަކަށް އޭގެ ދިހަ ގުނަ ހެޔޮކަން ލިބިގެންވެއެވެ. އަލިފް ލާމް މީމް، އެއީ އެންމެ ޙަރްފެއް ކަމުގައި ތިމަން ނަބިއްޔާ ޙަދީޘް ނުކުރައްވަމެވެ. އެހެނަސް އަލިފް، އެއީ ޙަރްފެކެވެ. އަދި ލާމު، އެއީ ޙަރްފެކެވެ. އަދި މީމު، އެއީ ޙަރްފެކެވެ.“ (ނޯޓް: ޙަރްފަކީ ކޮބައިކަމާމެދު ޢިލްމުވެރިން ވަނީ ޢިޚްތިލާފުވެފައިވެ. އެއްބައި ޢިލްމުވެރިން ގާތު އެއީ އަކުރެކެވެ. އަނެއްބައި ޢިލްމުވެރިން ގާތު އެއީ ބަހެކެވެ.)', ''], ['سنن أبي داود 3674', 'حَدَّثَنَا عُثْمَانُ بْنُ أَبِي شَيْبَةَ، قَالَ: نَا وَكِيعُ بْنُ الْجَرَّاحِ، عَنْ عَبْدِ الْعَزِيزِ بْنِ عُمَرَ، عَنْ أَبِي عَلْقَمَةَ مَوْلَاهُمْ وَعَبْدِ الرَّحْمَنِ بْنِ عَبْدِ اللهِ الْغَافِقِيِّ أَنَّهُمَا سَمِعَا ابْنَ عُمَرَ يَقُولُ: قَالَ رَسُولُ اللهِ ﷺ: لَعَنَ اللهُ الْخَمْرَ، وَشَارِبَهَا، وَسَاقِيَهَا، وَبَايِعَهَا، وَمُبْتَاعَهَا، وَعَاصِرَهَا، وَمُعْتَصِرَهَا، وَحَامِلَهَا، وَالْمَحْمُولَةَ إِلَيْهِ. ', 'އިބްނު ޢުމަރު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ރަލާއި، އެ ބޯ މީހާއާއި، އެ ބޯން ދޭ މީހާއާއި، އެ ވިއްކާ މީހާއާއި، އެ ގަންނަ މީހާއާއި، (އެ އުފެއްދުމަށް) އެ ފެލާ މިހާއާއި، އެ މީހަކަށް އެ ފެލެވޭ މީހާއާއި، އެ އުފުއްލާ މީހާއާއި، އެ އުފުލައިގެން ގެންގޮސް ދެވޭ މީހާ، މި އެންމެންނަށް ﷲ ވަނީ ލަޢުނަތް ލައްވާފައެވެ.“', ''], ['سنن ابن ماجه 3371', 'حَدَّثَنَا الْحُسَيْنُ بْنُ الْحَسَنِ الْمَرْوَزِيُّ، حَدَّثَنَا ابْنُ أَبِي عَدِيٍّ. (ح) وَحَدَّثَنَا إِبْرَاهِيمُ بْنُ سَعِيدٍ الْجَوْهَرِيُّ، حَدَّثَنَا عَبْدُ الْوَهَّابِ، جَمِيعًا عَنْ رَاشِدٍ أَبِي مُحَمَّدٍ الْحِمَّانِيِّ، عَنْ شَهْرِ بْنِ حَوْشَبٍ، عَنْ أُمِّ الدَّرْدَاءِ، عَنْ أَبِي الدَّرْدَاءِ قَالَ: أَوْصَانِي خَلِيلِي ﷺ: لَا تَشْرَبِ الْخَمْرَ، فَإِنَّهَا مِفْتَاحُ كُلِّ شَرٍّ. ', 'އަބުއްދަރްދާއު ރިވާ ކުރެއްވިއެވެ: ތިމަންނަގެ ގާތް މިތުރު (ނަބިއްޔާ) ﷺ ތިމަންނައަށް ވަޞިއްޔަތް ކުރެއްވިއެވެ: ”ރާ ނުބޯށެވެ. ހަމަކަށަވަރުން އެއީ ހުރިހާ ނުބައި ކަމެއްގެ ތަޅުދަނޑިއެވެ.“', ''], ['سنن النسائي 5666', 'أَخْبَرَنَا سُوَيْدٌ قَالَ: أَنْبَأَنَا عَبْدُ اللهِ، عَنْ مَعْمَرٍ، عَنِ الزُّهْرِيِّ، عَنْ أَبِي بَكْرِ بْنِ عَبْدِ الرَّحْمَنِ بْنِ الْحَارِثِ، عَنْ أَبِيهِ قَالَ: سَمِعْتُ عُثْمَانَ رَضِيَ اللهُ عَنْهُ يَقُولُ: اجْتَنِبُوا الْخَمْرَ؛ فَإِنَّهَا أُمُّ الْخَبَائِثِ، إِنَّهُ كَانَ رَجُلٌ مِمَّنْ خَلَا قَبْلَكُمْ تَعَبَّدَ، فَعَلِقَتْهُ امْرَأَةٌ غَوِيَّةٌ فَأَرْسَلَتْ إِلَيْهِ جَارِيَتَهَا، فَقَالَتْ لَهُ: إِنَّا نَدْعُوكَ لِلشَّهَادَةِ، فَانْطَلَقَ مَعَ جَارِيَتِهَا فَطَفِقَتْ كُلَّمَا دَخَلَ بَابًا أَغْلَقَتْهُ دُونَهُ، حَتَّى أَفْضَى إِلَى امْرَأَةٍ وَضِيئَةٍ، عِنْدَهَا غُلَامٌ، وَبَاطِيَةُ خَمْرٍ، فَقَالَتْ: إِنِّي وَاللهِ مَا دَعَوْتُكَ لِلشَّهَادَةِ، وَلَكِنْ دَعَوْتُكَ لِتَقَعَ عَلَيَّ، أَوْ تَشْرَبَ مِنْ هَذِهِ الْخَمْرَةِ كَأْسًا، أَوْ تَقْتُلَ هَذَا الْغُلَامَ. قَالَ: فَاسْقِينِي مِنْ هَذَا الْخَمْرِ كَأْسًا، فَسَقَتْهُ كَأْسًا، قَالَ: زِيدُونِي فَلَمْ يَرِمْ حَتَّى وَقَعَ عَلَيْهَا، وَقَتَلَ النَّفْسَ، فَاجْتَنِبُوا الْخَمْرَ؛ فَإِنَّهَا وَاللهِ لَا يَجْتَمِعُ الْإِيمَانُ وَإِدْمَانُ الْخَمْرِ إِلَّا لَيُوشِكُ أَنْ يُخْرِجَ أَحَدُهُمَا صَاحِبَهُ. ', 'ޢަބްދުއްރަޙްމާން ބިން އަލްޙާރިޘް ރިވާ ކުރެއްވިއެވެ: ޢުޘްމާން ބިން ޢައްފާނު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވި އަޑު ތިމަން އެހީމެވެ: ތިޔަބައިމީހުން ރަލާ ދުރުވެގަންނާށެވެ. އެހެނީ ހަމަކަށަވަރުން އެއީ ހުރިހާ ހަޑިމުޑުދާރު ނުބައި ކަމެއްގެ މަންމައެވެ. ހަމަކަށަވަރުން ތިޔަބައިމީހުންގެ ކުރީގެ (އުންމަތްތަކުގެ ތެރެއިން) މީހާކު އަޅުކަމައިގެން ހުސްވެއުޅުނެވެ. ދެން ފާޙިޝް އަންހެނަކު އޭނާއަށް ހިތާވެގަތެވެ. އަދި އެ އަންހެނާގެ އަޅު އަންހެން ކުއްޖަކު އެ މީހާ ގާތަށް ފޮނުވިއެވެ. އެ ކުއްޖާ އައިސް ދެންނެވިއެވެ: ތިމަންމެން ތިބާއަށް މި ގޮވާލަނީ ކަމަކަށް ހެކުކުރަންށެވެ. ދެން އެ ކުއްޖާއާއެކު އެ އަޅުވެރިޔާ ހިނގައްޖެއެވެ. (އެ އަންހެނާގެ ގޭގައި) އެ އަޅުވެރިޔާ ވަތް ކޮންމެ ދޮރަކަށް ފަހު، ފަހަތުން އެ އަޅު ކުއްޖާ އެ ދޮރެއް ތަޅުލިއެވެ. ދެން އެ އަޅުވެރިޔާ ވަރަށް ރީތި އަންހެނަކާ ހިސާބަށް ފޯރައިފިއެވެ. އެ އަންހެނާއާއެކު ޚާދިމު ޅަ ފިރިހެން ކުއްޖަކާއި ބޮޑު ރާ ތައްޓެއް ހުއްޓެވެ. ދެން އަޅުވެރިޔާއަށް އެ އަންހެނާ ބުނެފިއެވެ: ﷲ ގަންދީ ބުނަމެވެ. ތިބާއަށް މި ގޮވާލީކީ ކަމަކަށް ހެކުކުރާކަށެއް ނޫނެވެ. އެހެނެއްކަމަކު ތިބާއަށް މި ގޮވާލީ ތިމަންނައާއެކު ޒިނޭ ކުރުމަށެވެ. ނޫނީ މި ރާއެތިން ތައްޓެއް ބޯށެވެ. ނޫނީ މި ޚާދިމު ޅަ ފިރިހެން ކުއްޖާ މަރާލާށެވެ. އެ މީހާ (އެންމެ ލުއި ގޮތަކަށް ވިސްނާފައި) ދެންނެވިއެވެ: އެހެންކަމުގައި ވަނީ ނަމަ، މި ރާއެތިން ތިމަންނައަށް ތައްޓެއް ބޯން ދޭށެވެ. ދެން އެ އަންހެނާ އޭނާއަށް ތައްޓެއް ބޯންދިނެވެ. (އެއް ތައްޓަށް ފަހު ބޯ ހިތްވެގަނެވެން) އެ މީހާ ދެންނެވިއެވެ: ތިމަންނައަށް އިތުރަށް ދޭށެވެ. ދެން އެހެން ބޮމުން ބޮމުން ގޮސް (މަސްތުވެގެން) އެ އަންހެނާއާއެކު ޒިނޭކޮށް، އަދި އެ ޅަ ފިރިހެން ކުއްޖާ މަރާލުމަށް ދާންދެން ރާބުއިން ހުއްޓާނުލިއެވެ. ވީމާ ތިޔަބައިމީހުން ރަލާ ދުރުވެގަންނާށެވެ. އެހެނީ ހަމަކަށަވަރުން ﷲ ގަންދީ ބުނަމެވެ. އީމާންކަމާއި ރަލަށް ދެވިހިފުން އެއްކޮށެއް ނޫޅެވޭނެއެވެ. އެހެނަސް އޭގެ ތެރެއިން އެކަތި އަނެކަތި ބޭރުކުރާނެކަން ކަށަވަރެވެ.', ''], ['سنن الترمذي 614', 'حَدَّثَنَا عَبْدُ اللهِ بْنُ أَبِي زِيَادٍ الْقَطَوَانِيُّ الْكُوفِيُّ، قَالَ: حَدَّثَنَا عُبَيْدُ اللهِ بْنُ مُوسَى، قَالَ: حَدَّثَنَا غَالِبٌ أَبُو بِشْرٍ، عَنْ أَيُّوبَ بْنِ عَائِذٍ الطَّائِيِّ، عَنْ قَيْسِ بْنِ مُسْلِمٍ، عَنْ طَارِقِ بْنِ شِهَابٍ، عَنْ كَعْبِ بْنِ عُجْرَةَ قَالَ: قَالَ لِي رَسُولُ اللهِ ﷺ: أُعِيذُكَ بِاللهِ يَا كَعْبُ بْنَ عُجْرَةَ مِنْ أُمَرَاءَ يَكُونُونَ مِنْ بَعْدِي فَمَنْ غَشِيَ أَبْوَابَهُمْ فَصَدَّقَهُمْ فِي كَذِبِهِمْ وَأَعَانَهُمْ عَلَى ظُلْمِهِمْ فَلَيْسَ مِنِّي وَلَسْتُ مِنْهُ، وَلَا يَرِدُ عَلَيَّ الْحَوْضَ، وَمَنْ غَشِيَ أَبْوَابَهُمْ أَوْ لَمْ يَغْشَ وَلَمْ يُصَدِّقْهُمْ فِي كَذِبِهِمْ، وَلَمْ يُعِنْهُمْ عَلَى ظُلْمِهِمْ فَهُوَ مِنِّي، وَأَنَا مِنْهُ وَسَيَرِدُ عَلَيَّ الْحَوْضَ، يَا كَعْبُ بْنَ عُجْرَةَ! الصَّلَاةُ بُرْهَانٌ وَالصَّوْمُ جُنَّةٌ حَصِينَةٌ، وَالصَّدَقَةُ تُطْفِئُ الْخَطِيئَةَ كَمَا يُطْفِئُ الْمَاءُ النَّارَ، يَا كَعْبُ بْنَ عُجْرَةَ! إِنَّهُ لَا يَرْبُو لَحْمٌ نَبَتَ مِنْ سُحْتٍ إِلَّا كَانَتِ النَّارُ أَوْلَى بِهِ. \nهَذَا حَدِيثٌ حَسَنٌ غَرِيبٌ مِنْ هَذَا الْوَجْهِ، لَا نَعْرِفُهُ إِلَّا مِنْ حَدِيثِ عُبَيْدِ اللهِ بْنِ مُوسَى. \nوَأَيُّوبُ بْنُ عَائِذٍ يُضَعَّفُ وَيُقَالُ: كَانَ يَرَى رَأْيَ الْإِرْجَاءِ. \nوَسَأَلْتُ مُحَمَّدًا عَنْ هَذَا الْحَدِيثِ فَلَمْ يَعْرِفْهُ إِلَّا مِنْ حَدِيثِ عُبَيْدِ اللهِ بْنِ مُوسَى وَاسْتَغْرَبَهُ جِدًّا.\n', 'ކަޢުބް ބިން ޢުޖްރާ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ތިމަންނައަށް ޙަދީޘް ކުރެއްވިއެވެ: ”(ނުބައި) ވެރިންތަކެއްގެ ކިބައިން ﷲ ގެ ޙަޟްރަތުން ތިބާއަށް ރައްކާތެރިކޮށް ދެއްވުމަށް އެދި ތިމަން ނަބިއްޔާ ދަންނަވަމެވެ. އެ ވެރިންގެ ދޮރުތަކަށް އެއްވެ، އެ ބަޔެއްގެ ދޮގުތައް ތެދުކޮށް، އަދި އެ ބަޔެއްގެ އަނިޔާވެރިކަމުގައި އެހީތެރިވެއްޖެ މީހާ، ފަހެ އޭނާއީ ތިމަން ނަބިއްޔާގެ މީސްތަކުންގެ ތެރެއިންވާ މީހެއް ނޫނެވެ. އަދި ތިމަން ނަބިއްޔާއީ އޭނާގެ މީސްތަކުންގެ ތެރެއިންވާ މީހެއް ނޫނެވެ. އަދި (ތިމަން ނަބިއްޔާގެ) ޙައުޟުގައި އޭނާ ތިމަން ނަބިއްޔާއާ ބައްދަލެއް ނުވާނެއެވެ. އަދި އެ ވެރިންގެ ދޮރުތަކަށް އެއްވެ، ނޫނީ އެއްނުވި ކަމުގައި ވިޔަސް، އެ ބަޔެއްގެ ދޮގުތައް ތެދުނުކޮށް، އަދި އެ ބަޔެއްގެ އަނިޔާވެރިކަމުގައި އެހީތެރިނުވާ މީހާ، ފަހެ އޭނާއީ ތިމަން ނަބިއްޔާގެ މީސްތަކުންގެ ތެރެއިންވާ މީހެކެވެ. އަދި ތިމަން ނަބިއްޔާއީ އޭނާގެ މީސްތަކުންގެ ތެރެއިންވާ މީހަކީމެވެ. އަދި (ތިމަން ނަބިއްޔާގެ) ޙައުޟުގައި އޭނާ ތިމަން ނަބިއްޔާއާ ބައްދަލު ވާނެއެވެ. އޭ ކަޢުބު ބިން ޢުޖްރާއެވެ! ނަމާދަކީ (އީމާންކަމުގެ) ބުރުހާނެކެވެ. އަދި ރޯދައަކީ ވަރުގަދަ އައްޑަނައެކެވެ. (އެބަހީ: ރައްކާތެރި ކަމެކެވެ.) އަދި ފެނުން އަލިފާން ނިވޭ ފަދައިން، ޞަދަޤާތުން ފާފަ ފޮހެވިގެންދެއެވެ. އޭ ކަޢުބު ބިން ޢުޖްރާއެވެ! ހަމަކަށަވަރުން ޙަރާމް ތަކެތި ކައިގެން ހަށިގަނޑުގައި މަސްޖަހައިފި ނަމަ، އެ ހަށިގަނޑެއް އެންމެ އައުލާވާނީ ނަރަކައަށް ކަމުގައި މެނުވީ ވެގެން ނުދާނެއެވެ.“', ''], ['السنن الكبرى للنسائي 2873', 'أَخْبَرَنَا الرَّبِيعُ بْنُ سُلَيْمَانَ، قَالَ: حَدَّثَنَا يَحْيَى بْنُ حَسَّانَ، قَالَ: حَدَّثَنَا يَحْيَى بْنُ حَمْزَةَ، قَالَ: أَخْبَرَنِي يَحْيَى بْنُ الْحَارِثِ، عَنْ أَبِي أَسْمَاءَ الرَّحَبِيِّ، عَنْ ثَوْبَانَ، أَنَّ رَسُولَ اللهِ ﷺ قَالَ: صِيَامُ شَهْرِ رَمَضَانَ بِعَشَرَةِ أَشْهُرٍ، وَصِيَامُ سِتَّةِ أَيَّامٍ مِنْ شَوَّالٍ بِشَهْرَيْنِ، فَذَلِكَ صِيَامُ سَنَةٍ. \n', 'ޘައުބާން ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ރަމަޟާން މަހު ހިފާ ރޯދަ، (އެ ނޫން) ދިހަ މަސްދުވަހުގެ ރޯދަ ހިފުމާ އެއްފަދައެވެ. އަދި ރަމަޟާން މަހަށް ފަހު (ޝައްވާލު މަހުގައި) ހިފާ ހަ ރޯދަ، ދެ މަސްދުވަހުގެ ރޯދަ ހިފުމާ އެއްފަދައެވެ. ފަހެ އެފަދައިން މުޅި އަހަރުދުވަހު ރޯދަ ހިފުނު ފަދައެވެ.“', ''], ['سنن أبي داود 2437', 'حَدَّثَنَا مُسَدَّدٌ، نَا أَبُو عَوَانَةَ، عَنِ الْحُرِّ بْنِ الصَّبَّاحِ، عَنْ هُنَيْدَةَ بْنِ خَالِدٍ، عَنِ امْرَأَتِهِ، عَنْ بَعْضِ أَزْوَاجِ النَّبِيِّ عَلَيْهِ السَّلَامُ قَالَتْ: كَانَ رَسُولُ اللهِ ﷺ يَصُومُ تِسْعَ ذِي الْحِجَّةِ، وَيَوْمَ عَاشُورَاءَ، وَثَلَاثَةَ أَيَّامٍ مِنْ كُلِّ شَهْرٍ أَوَّلَ اثْنَيْنِ مِنَ الشَّهْرِ وَالْخَمِيسَ. ', 'ނަބިއްޔާ ޢަލައިހިއްސަލާމްގެ އަނބިކަނބަލުން އުންމު ސަލަމާ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޒުލް ޙިއްޖާ މަހުގެ ނުވަ ދުވަހާއި، ޢާޝޫރާ ދުވަހު ރޯދަ ހިއްޕަވާ އުޅުއްވިއެވެ. އަދި ކޮންމެ މަހެއްގެ ތިން ދުވަހުވެސް ރޯދަހިއްޕަވާ އުޅުއްވިއެވެ. އެ ގޮތުން މަހުގެ ފުރަތަމަ ހޯމަ ދުވަހާއި ބުރާސްފަތު ދުވަހެވެ.', ''], ['سنن الترمذي 2169', 'حَدَّثَنَا قُتَيْبَةُ، قَالَ: حَدَّثَنَا عَبْدُ الْعَزِيزِ بْنُ مُحَمَّدٍ، عَنْ عَمْرِو بْنِ أَبِي عَمْرٍو، عَنْ عَبْدِ اللهِ الْأَنْصَارِيِّ، عَنْ حُذَيْفَةَ بْنِ الْيَمَانِ، عَنِ النَّبِيِّ ﷺ قَالَ: وَالَّذِي نَفْسِي بِيَدِهِ لَتَأْمُرُنَّ بِالْمَعْرُوفِ وَلَتَنْهَوُنَّ عَنِ الْمُنْكَرِ أَوْ لَيُوشِكَنَّ اللهُ أَنْ يَبْعَثَ عَلَيْكُمْ عِقَابًا مِنْهُ، ثُمَّ تَدْعُونَهُ فَلَا يُسْتَجَابُ لَكُمْ. \nهَذَا حَدِيثٌ حَسَنٌ.', 'ޙުޛައިފާ ބިން އަލްޔަމާނު ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާގެ ނަފްސުފުޅު އެ ފަރާތެއްގެ އަތްޕުޅުގައިވާ ފަރާތް ގަންދީ ބުނަމެވެ. ތިޔަބައިމީހުން ހެޔޮކަންކަމަށް އަމުރުކުރަން ޖެހޭނެއެވެ. އަދި ނުބައިކަންކަން މަނާ ކުރަން ޖެހޭނެއެވެ. ނޫން ނަމަ، ﷲގެ ޙަޟްރަތުން އުޤޫބާތެއް ތިޔަބައިމީހުންގެ މައްޗަށް ފޮނުއްވާނެކަމީ ވަރަށް ގާތުގައިވާ ކަމެކެވެ. އެއަށްފަހު ތިޔަބައިމީހުން އެ އިލާހަށް ދުޢާ ކުރުމުން ތިޔަބައިމީހުންނަކަށް އިޖާބައެއް ނުދެއްވާނެއެވެ.“', ''], ['سنن أبي داود 3580', 'حَدَّثَنَا أَحْمَدُ بْنُ يُونُسَ، نَا ابْنُ أَبِي ذِئْبٍ، عَنِ الْحَارِثِ بْنِ عَبْدِ الرَّحْمَنِ، عَنْ أَبِي سَلَمَةَ، عَنْ عَبْدِ اللهِ بْنِ عَمْرٍو قَالَ: لَعَنَ رَسُولُ اللهِ ﷺ الرَّاشِيَ وَالْمُرْتَشِيَ. ', 'ޢަބްދު ﷲ ބިން ޢަމްރު ރިވާ ކުރެއްވިއެވެ: ރިޝްވަރު ދޭ މީހާއާއި ރިޝްވަތުގައި ހިފާ މީހާއަށް ރަސޫލު ﷲ ﷺ ލަޢުނެތް ލެއްވިއެވެ.', ''], ['منتقى ابن جارود 638', 'حَدَّثَنَا مُحَمَّدُ بْنُ يَحْيَى، قَالَ: ثَنَا أَبُو الْوَلِيدِ، قَالَ: ثَنَا أَبُو عَوَانَةَ، عَنْ عُمَرَ بْنِ أَبِي سَلَمَةَ، عَنْ أَبِيهِ، عَنْ أَبِي هُرَيْرَةَ رَضِيَ اللهُ عَنْهُ قَالَ: قَالَ رَسُولُ اللهِ ﷺ: لَعَنَ اللهُ الرَّاشِيَ وَالْمُرْتَشِيَ. ', 'އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ރިޝްވަރު ދޭ މީހާއާއި ރިޝްވަތުގައި ހިފާ މީހާއަށް ﷲ ލަޢުނެތް ލައްވާށިއެވެ.“', ''], ['سنن أبي داود 2419', 'حَدَّثَنَا الْحَسَنُ بْنُ عَلِيٍّ، نَا، وَهْبٌ، نَا مُوسَى بْنُ عُلَيٍّ. (ح) وَنَا عُثْمَانُ بْنُ أَبِي شَيْبَةَ، نَا، وَكِيعٌ، عَنْ مُوسَى بْنِ عُلَيٍّ، وَالْإِخْبَارُ فِي حَدِيثِ وَهْبٍ قَالَ: سَمِعْتُ أَبِي أَنَّهُ سَمِعَ عُقْبَةَ بْنَ عَامِرٍ قَالَ: قَالَ رَسُولُ اللهِ ﷺ: يَوْمُ عَرَفَةَ، وَيَوْمُ النَّحْرِ، وَأَيَّامُ التَّشْرِيقِ عِيدُنَا أَهْلَ الْإِسْلَامِ، وَهِيَ أَيَّامُ أَكْلٍ وَشُرْبٍ. ', 'ޢުޤްބާ ބިން ޢާމިރު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ޢަރަފާތް ދުވަހާއި، ނަޙްރުގެ (އެބަހީ: އުޟްޙިޔާ ކަތިލުމުގެ) ދުވަހާއި، އައްޔާމުއް ތަޝްރީޤުގެ ތިން ދުވަހަކީ އަހަރެމެން އިސްލާމުންގެ ޢީދެވެ. އަދި އެއީ ކެއުމާއި ބުއިމުގެ ދުވަސްތަކެކެވެ.“', ''], ['موطأ مالك 3338', 'مَالِكٌ، أَنَّهُ بَلَغَهُ أَنَّ رَسُولَ اللهِ ﷺ قَالَ: تَرَكْتُ فِيكُمْ أَمْرَيْنِ لَنْ تَضِلُّوا مَا تَمَسَّكْتُمْ بِهِمَا: كِتَابَ اللهِ وَسُنَّةَ نَبِيِّهِ. ', 'މާލިކު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާ، ތިޔަބައިމީހުންގެ ތެރެއަށް ދެ އެއްޗެއް ދޫކުރެއްވީމެވެ. އެ ދޭތީގައި ހިފަހައްޓައިގެން ތިބިހައި ހިނދަކު، ދުވަހަކުވެސް ތިޔަބައިމިހުން މަގު ފުރެދިގެން ނުދާނެއެވެ. އެއީ ﷲގެ ފޮތާއި، އެ އިލާހުގެ ނަބިއްޔާގެ ސުންނަތެވެ.“', ''], ['سنن أبي داود 3646', 'حَدَّثَنَا مُسَدَّدٌ وَأَبُو بَكْرِ بْنُ أَبِي شَيْبَةَ، قَالَا: نَا يَحْيَى، عَنْ عُبَيْدِ اللهِ بْنِ الْأَخْنَسِ، عَنِ الْوَلِيدِ بْنِ عَبْدِ اللهِ بْنِ أَبِي مُغِيثٍ، عَنْ يُوسُفَ بْنِ مَاهَكَ، عَنْ عَبْدِ اللهِ بْنِ عَمْرٍو قَالَ: كُنْتُ أَكْتُبُ كُلَّ شَيْءٍ أَسْمَعُهُ مِنْ رَسُولِ اللهِ ﷺ أُرِيدُ حِفْظَهُ، فَنَهَتْنِي قُرَيْشٌ، وَقَالُوا: أَتَكْتُبُ كُلَّ شَيْءٍ تَسْمَعُهُ، وَرَسُولُ اللهِ ﷺ بَشَرٌ يَتَكَلَّمُ فِي الْغَضَبِ وَالرِّضَا، فَأَمْسَكْتُ عَنِ الْكِتَابِ، فَذَكَرْتُ ذَلِكَ إِلَى رَسُولِ اللهِ ﷺ، فَأَوْمَأَ بِإِصْبَعِهِ إِلَى فِيهِ، فَقَالَ: اكْتُبْ فَوَالَّذِي نَفْسِي بِيَدِهِ مَا يَخْرُجُ مِنْهُ إِلَّا حَقٌّ. ', 'ޢަބްދު ﷲ ބިން ޢަމްރު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ އަރިހުން އިވޭ ކޮންމެ ބަސްފުޅެއް ތިމަން ލިޔެ އުޅުނީމެވެ. ތިމަން ބޭނުންވީ އެ ބަސްފުޅުތައް ދަސްކޮށް ރައްކާ ކުރުމަށެވެ. އެހެނަސް ޤުރައިޝު ވަންހައިގެ މުޝްރިކުން އެކަން ނުކުރުމަށް ތިމަންނައަށް އެންގިއެވެ. އަދި އެ މީހުން ދެންނެވިއެވެ: އިވޭ ކޮންމެ އެއްޗެއް ފަހެ ތިބާ ލިޔަނީހެއްޔެވެ؟ އެއީ ރަސޫލު ﷲ ﷺ އަކީ ހަމަ އިންސާނަކަށްވެފައި، ކޯފާއާއި ރުހުމުގައި ވާހަކަދައްކަވާ އިރުގައިވެސްހެއްޔެވެ؟ ދެން ތިމަން ލިޔުން ހުއްޓާލާފައި، އެ ވާހަކަ ރަސޫލު ﷲ ﷺ  ގެ އަރިހުގައި ދެންނެވީމެވެ. ފަހެ ނަބިއްޔާގެ އިނގިލިފުޅުން އެ ނަބިއްޔާގެ އަނގަފުޅަށް އިޝާރާތް ކުރައްވާފައި ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ ލިޔާށެވެ. އެހެނީ ތިމަން ނަބިއްޔާގެ ފުރާނަފުޅު އެ ފަރާތެއްގެ އަތްޕުޅުގައިވާ ފަރާތް ގަންދީ ބުނަމެވެ. މި އަނގަފުޅަކުން ޙައްޤު ތެދު މެނުވީ ނިކުމެގެން ނުދާނެއެވެ.“', ''], ['سنن ابن ماجه 12', 'حَدَّثَنَا أَبُو بَكْرِ بْنُ أَبِي شَيْبَةَ، حَدَّثَنَا زَيْدُ بْنُ الْحُبَابِ، عَنْ مُعَاوِيَةَ بْنِ صَالِحٍ، حَدَّثَنِي الْحَسَنُ بْنُ جَابِرٍ، عَنِ الْمِقْدَامِ بْنِ مَعْدِيكَرِبَ الْكِنْدِيِّ، أَنَّ رَسُولَ اللهِ ﷺ قَالَ: يُوشِكُ الرَّجُلُ مُتَّكِئًا عَلَى أَرِيكَتِهِ يُحَدَّثُ بِحَدِيثٍ مِنْ حَدِيثِي، فَيَقُولُ: بَيْنَنَا وَبَيْنَكُمْ كِتَابُ اللهِ عَزَّ وَجَلَّ، فَمَا وَجَدْنَا فِيهِ مِنْ حَلَالٍ اسْتَحْلَلْنَاهُ، وَمَا وَجَدْنَا فِيهِ مِنْ حَرَامٍ حَرَّمْنَاهُ، أَلَّا وَإِنَّ مَا حَرَّمَ رَسُولُ اللهِ ﷺ مِثْلُ مَا حَرَّمَ اللهُ.', 'އަލްމިޤްދާމު ބިން މަޢުދީ ކަރިބު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ސޮފާއެނދެއްގައި ލެނގިލައިގެން އިން މީހަކަށް، ތިމަން ނަބިއްޔާގެ ޙަދީޘްބަސްފުޅެއް ކިޔައިދެވޭ ހިނދު އޭނާ މިފަދައިން ބުނާނެ ދުވަސް ގާތްވެއްޖެއެވެ: ’ތިމަންމެންނާއި ތިޔަބައިމީހުންގެ ދެމެދު ވަނީ ﷲ ޢައްޒަ ވަޖައްލަގެ ފޮތެވެ. ވީމާ އޭގައި ޙަލާލު ކަމަށް ތިމަންމެންނަށް ފެނުނު އެއްޗެއް ޙަލާލު ކަމަށް ދެކޭނަމެވެ. އަދި އޭގައި ޙަރާމް ކަމަށް ތިމަންމެންނަށް ފެނުނު އެއްޗެއް ޙަރާމް ކަމަށް ދެކޭނަމެވެ.‘ ދަންނާށެވެ! ހަމަކަށަވަރުން ﷲ ގެ ރަސޫލާ ﷺ ޙަރާމް ކުރެއްވި އެއްޗެއް، ﷲ ޙަރާމް ކުރެއްވި އެއްޗަކާ ހަމަ އެއްފަދަވެގެންވެއެވެ.“', ''], ['سنن أبي داود 4604', 'حَدَّثَنَا عَبْدُ الْوَهَّابِ بْنُ نَجْدَةَ، نَا أَبُو عَمْرِو بْنُ كَثِيرِ بْنِ دِينَارٍ، عَنْ حَرِيزِ بْنِ عُثْمَانَ، عَنْ عَبْدِ الرَّحْمَنِ بْنِ أَبِي عَوْفٍ، عَنِ الْمِقْدَامِ بْنِ مَعْدِيكَرِبَ، عَنْ رَسُولِ اللهِ ﷺ أَنَّهُ قَالَ: أَلَا إِنِّي أُوتِيتُ الْكِتَابَ وَمِثْلَهُ مَعَهُ، أَلَا يُوشِكُ رَجُلٌ شَبْعَانُ عَلَى أَرِيكَتِهِ يَقُولُ عَلَيْكُمْ بِهَذَا الْقُرْآنِ، فَمَا وَجَدْتُمْ فِيهِ مِنْ حَلَالٍ فَأَحِلُّوهُ، وَمَا وَجَدْتُمْ فِيهِ مِنْ حَرَامٍ فَحَرِّمُوهُ، أَلَا لَا يَحِلُّ لَكُمُ الْحِمَارُ الْأَهْلِيُّ، وَلَا كُلُّ ذِي نَابٍ مِنَ السَّبُعِ، وَلَا لُقَطَةُ مُعَاهَدٍ إِلَّا أَنْ يَسْتَغْنِيَ عَنْهَا صَاحِبُهَا، وَمَنْ نَزَلَ بِقَوْمٍ فَعَلَيْهِمْ أَنْ يَقْرُوهُ، فَإِنْ لَمْ يَقْرُوهُ فَلَهُ أَنْ يُعْقِبَهُمْ بِمِثْلِ قِرَاهُ. ', 'އަލްމިޤްދާމު ބިން މަޢުދީ ކަރިބު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ދަންނާށެވެ! ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާއަށް ޤުރްއާނާއެކު ހަމަ އެފަދަ އެހެން އެއްޗެއް (އެބަހީ: ސުންނަތް) ދެވުނެވެ. ދަންނާށެވެ! ބަނޑުފުރައި ކައައިގެން ސޮފާއެނދެއްގައި ލެނގިލައިގެން އިން މީހަކު، މިފަދައިން ބުނާނެ ދުވަސް ގާތްވެއްޖެއެވެ: ތިޔަބައިމީހުންގެ މައްޗަށް އޮތީ (ހަމައެކަނި) މި ޤުރްއާނަށް ތަބާވުމެވެ. ވީމާ އޭގައި ޙަލާލު ކަމަށް ތިޔަބައިމީހުންނަށް ފެނުނު އެއްޗެއް ޙަލާލު ކަމަށް ދެކޭށެވެ. އަދި އޭގައި ޙަރާމް ކަމަށް ތިޔަބައިމީހުންނަށް ފެނުނު އެއްޗެއް ޙަރާމް ކަމަށް ދެކޭށެވެ.‘ ދަންނާށެވެ! ހިމާރު މަހާއި، ފޭރާދަތް ހުންނަ ޝިކާރަ ކުރާ ޖަނަވާރުގެ މަސް ކެއުން ތިޔަބައިމީހުންނަކަށް ހުއްދަވެގެނެއް ނުވެއެވެ. އަދި (ހަނގުރާމަ ނުކުރުމަށް) މުޢާހަދާއެއް ކުރެވުނު މީހެއްގެ އަތުން (ވެއްޓި) ގެއްލުނު އެއްޗެއް ހޮވުން ތިޔަބައިމީހުންނަކަށް ހުއްދަވެގެނެއް ނުވެއެވެ. އެއީ އޭގެ ވެރި ފަރާތް އޭތި ބޭނުން ނުވާ ޙާލު މެނުވީއެވެ. އަދި ބަޔެއްގެ ގާތުގައި މީހަކު މަޑުކޮށްފި ނަމަ، އެ ބަޔަކަށް އޮތީ އޭނާއަށް މެހެމާންދާރީ އަދާކުރުމެވެ. އަދި އަދާ ނުކޮށްފި ނަމަ، އޭނާއަށް އޭގެ ބަދަލު ލިބުން ހުއްޓެވެ.“', ''], ['سنن أبي داود 4605', 'حَدَّثَنَا أَحْمَدُ بْنُ مُحَمَّدِ بْنِ حَنْبَلٍ وَعَبْدُ اللهِ بْنُ مُحَمَّدٍ النُّفَيْلِيُّ قَالَا: نَا سُفْيَانُ، عَنْ أَبِي النَّضْرِ، عَنْ عُبَيْدِ اللهِ بْنِ أَبِي رَافِعٍ، عَنْ أَبِيهِ، عَنِ النَّبِيِّ ﷺ قَالَ: لَا أُلْفِيَنَّ أَحَدَكُمْ مُتَّكِئًا عَلَى أَرِيكَتِهِ يَأْتِيهِ الْأَمْرُ مِنْ أَمْرِي مِمَّا أَمَرْتُ بِهِ أَوْ نَهَيْتُ عَنْهُ، فَيَقُولُ: لَا نَدْرِي مَا وَجَدْنَا فِي كِتَابِ اللهِ اتَّبَعْنَاهُ. \n', 'އަބޫ ރާފިޢު ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ކުރުމަށް އަންގަވާފައިވާ ތިމަން ނަބިއްޔާގެ އަމުރެއް ނުވަތަ ނުކުރުމަށް އަންގަވާފައިވާ ނަހީއެއް، ތިޔަބައިމީހުންގެ ތެރެއިން މީހަކާ ހިސާބަށް ފޯރުމުން، ސޯފާއެނދެއްގައި ލެނގިލައިގެން އިނދެ މިފަދައިން ބުނަނިކޮށް ތިމަންނަބިއްޔާއަށް ފެނިގެން ނުވާނެއެވެ: ތިމަންމެންނަކަށް ނޭނގެއެވެ. ތިމަންމެންނަށް ﷲގެ ފޮތުގައި ފެނުނު އެއްޗެއް ނަމަ އެއަށް ދެން ތިމަންމެން ތަބާވާނީއެވެ.“', ''], ['صحيح ابن خزيمة 1986', 'حَدَّثَنَا الرَّبِيعُ بْنُ سُلَيْمَانَ الْمُرَادِيُّ، وَبَحْرُ بْنُ نَصْرٍ الْخَوْلَانِيُّ قَالَا: حَدَّثَنَا بِشْرُ بْنُ بَكْرٍ، قَالَ: حَدَّثَنَا ابْنُ جَابِرٍ، عَنْ سُلَيْمِ بْنِ عَامِرٍ أَبِي يَحْيَى الْكَلَاعِيِّ، قَالَ: حَدَّثَنِي أَبُو أُمَامَةَ الْبَاهِلِيُّ قَالَ: سَمِعْتُ رَسُولَ اللهِ - ﷺ - يَقُولُ: بَيْنَمَا أَنَا نَائِمٌ إِذْ أَتَانِي رَجُلَانِ، فَأَخَذَا بِضَبْعَيَّ، فَأَتَيَا بِي جَبَلًا وَعْرًا، فَقَالَا: اصْعَدْ، فَقُلْتُ: إِنِّي لَا أُطِيقُهُ، فَقَالَا: إِنَّا سَنُسَهِّلُهُ لَكَ، فَصَعِدْتُ حَتَّى إِذَا كُنْتُ فِي سَوَاءِ الْجَبَلِ إِذَا بِأَصْوَاتٍ شَدِيدَةٍ، قُلْتُ: مَا هَذِهِ الْأَصْوَاتُ؟ قَالُوا: هَذَا عُوَاءُ أَهْلِ النَّارِ، ثُمَّ انْطُلِقَ بِي، فَإِذَا أَنَا بِقَوْمٍ مُعَلَّقِينَ بِعَرَاقِيبِهِمْ، مُشَقَّقَةٌ أَشْدَاقُهُمْ، تَسِيلُ أَشْدَاقُهُمْ دَمًا. قَالَ: قُلْتُ: مَنْ هَؤُلَاءِ؟ قَالَ: هَؤُلَاءِ الَّذِينَ يُفْطِرُونَ قَبْلَ تَحِلَّةِ صَوْمِهِمْ، فَقَالَ: خَابَتِ الْيَهُودُ وَالنَّصَارَى. فَقَالَ سُلَيْمٌ: مَا أَدْرِي أَسَمِعَهُ أَبُو أُمَامَةَ مِنْ رَسُولِ اللهِ - ﷺ - أَمْ شَيْءٌ مِنْ رَأْيِهِ؟ ثُمَّ انْطَلَقَ، فَإِذَا بِقَوْمٍ أَشَدَّ شَيْءٍ انْتِفَاخًا وَأَنْتَنِهِ رِيحًا، وَأَسْوَئِهِ مَنْظَرًا، فَقُلْتُ: مَنْ هَؤُلَاءِ؟ فَقَالَ: هَؤُلَاءِ قَتْلَى الْكُفَّارِ، ثُمَّ انْطُلِقَ بِي، فَإِذَا بِقَوْمٍ أَشَدِّ [شَيْءٍ] انْتِفَاخًا، وَأَنْتَنِهِ رِيحًا، كَأَنَّ رِيحَهُمُ الْمَرَاحِيضُ، قُلْتُ: مَنْ هَؤُلَاءِ؟ قَالَ: هَؤُلَاءِ الزَّانُونَ وَالزَّوَانِي، ثُمَّ انْطُلِقَ بِي، فَإِذَا أَنَا بِنِسَاءٍ تَنْهَشُ ثُدِيَّهُنَّ الْحَيَّاتُ، قُلْتُ: مَا بَالُ هَؤُلَاءِ؟ قَالَ: هَؤُلَاءِ يَمْنَعْنَ أَوْلَادَهُنَّ أَلْبَانَهُنَّ، ثُمَّ انْطُلِقَ بِي، فَإِذَا أَنَا بِغِلْمَانٍ يَلْعَبُونَ بَيْنَ نَهَرَيْنِ، قُلْتُ: مَنْ هَؤُلَاءِ؟ قَالَ: هَؤُلَاءِ ذَرَارِيُّ الْمُؤْمِنِينَ، ثُمَّ شَرَفَ شَرَفًا، فَإِذَا أَنَا بِنَفَرٍ ثَلَاثَةٍ يَشْرَبُونَ مِنْ خَمْرٍ لَهُمْ، قُلْتُ: مَنْ هَؤُلَاءِ؟ قَالَ: هَؤُلَاءِ جَعْفَرٌ، وَزَيْدٌ، وَابْنُ رَوَاحَةَ، ثُمَّ شَرَفَنِي شَرَفًا آخَرَ، فَإِذَا أَنَا بِنَفَرٍ ثَلَاثَةٍ، قُلْتُ: مَنْ هَؤُلَاءِ؟ قَالَ: هَذَا إِبْرَاهِيمُ، وَمُوسَى، وَعِيسَى، وَهُمْ يَنْظُرُونِي. هَذَا حَدِيثُ الرَّبِيعِ ". ', 'އަބޫ އުމާމާ އަލްބާހިއްލީ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ: ”ތިމަން ނަބިއްޔާ އަވަހާރަފުޅުލައްޕަވަނިކޮށް، (ހުވަފެނެއްގައި) ދެ މީހަކު އަތުވެއްޖެއެވެ. ފަހެ އެ ދެ މީހުން ތިމަން ނަބިއްޔާގެ ދެ އަތްޕުޅުގައި ހިއްޕަވައިގެން، އަރަން އުނދަގޫ ފަރުބަދައެއް ކައިރިއަށް ހިނގައްޖެއެވެ. އެ ދެ މީހުން ދެންނެވިއެވެ: އަރާށެވެ. ވީމާ ތިމަން ނަބިއްޔާ ވިދާޅުވީމެވެ: އެކަމަށް ތިމަން ނަބިއްޔާ ކުޅަދާނަވެގެނެއް ނުވެއެވެ. އެ ދެމީހުން ދެންނެވިއެވެ: ހަމަކަށަވަރުން އިބަ ނަބިއްޔާއަށް ޓަކައި އެކަން ފަށޭހަކޮށްދެއްވާނަމެވެ. ދެން ތިމަން ނަބިއްޔާ އަރައިވަޑައިގަންނަވަން ފެއްޓެވީމެވެ. ފަހެ އެ ފަރުބަދައިގެ މެދާ ހިސާބަށް އަރާ ހަމަވުމުން، ވަރުގަދަ އަޑުފަށްގަނޑެއް އިވިވަޑައިގަތީމެވެ. ތިމަން ނަބިއްޔާ ވިދާޅުވީމެވެ: މިއީ ކޮން އަޑުފަށްގަނޑެއްތޯއެވެ؟ އެ މީހުން ދެންނެވިއެވެ: މިއީ ނަރަކައިގެ އަހުލުވެރިން ހޭރޭ އަޑުފަށްގަނޑެވެ. އެއަށްފަހު ތިމަން ނަބިއްޔާއައިގެން ކުރިއަށް ހިނގައިގަނެފިއެވެ. ދެން ތިމަން ނަބިއްޔާވީ އެ ބައެއްގެ ފުންނާބުތަކުން އެކޮޅުކޮޅަށް އެލުވިފައިވާ ބަޔެއްގެ ގާތުގައެވެ. އެބައިމީހުންގެ އަނގައިގެ މިޔަކަންތައް އިރިގެން ލޭ ފައިބަފައިބަ ހުއްޓެވެ. ތިމަން ނަބިއްޔާ ވިދާޅުވީމެވެ: މިއީ ކޮންބައެއްތޯއެވެ؟ އެ މީހުން ދެންނެވިއެވެ: މިއީ އެބައިމީހުންގެ ރޯދަ ވީއްލާން ހުއްދަ ނުވަނީސް، ރޯދަ ވީއްލާ އުޅުނު މީހުންނެވެ.', ''], ['سنن الترمذي 2079', 'حَدَّثَنَا رَجَاءُ بْنُ مُحَمَّدٍ الْعُذْرِيُّ الْبَصْرِيُّ، قَالَ: حَدَّثَنَا عَمْرُو بْنُ مُحَمَّدِ بْنِ أَبِي رَزِينٍ، قَالَ: حَدَّثَنَا شُعْبَةُ، عَنْ خَالِدٍ الْحَذَّاءِ، قَالَ: حَدَّثَنَا مَيْمُونٌ أَبُو عَبْدِ اللهِ قَالَ: سَمِعْتُ زَيْدَ بْنَ أَرْقَمَ قَالَ: أَمَرَنَا رَسُولُ اللهِ ﷺ أَنْ نَتَدَاوَى مِنْ ذَاتِ الْجَنْبِ بِالْقُسْطِ الْبَحْرِيِّ وَالزَّيْتِ.', 'ޒައިދު ބިން އަރްޤަމު ރިވާ ކުރެއްވިއެވެ: ޕްލިއުރިސީއަށް ފަރުވާ ކުރުމުގައި ކަނޑު ޤުސްޠާއި ތެޔޮ ބޭނުން ކުރަން ރަސޫލު ﷲ ﷺ ތިމަންމެންނަށް އެންގެވިއެވެ.', ''], ['مسند أحمد 12227', 'حَدَّثَنَا ابْنُ أَبِي عَدِيٍّ، عَنْ حُمَيْدٍ، عَنْ أَنَسٍ، أَنَّ النَّبِيَّ ﷺ قَالَ: خَيْرُ مَا تَدَاوَيْتُمْ بِهِ الْحِجَامَةُ، وَالْقُسْطُ الْبَحْرِيُّ، وَلَا تُعَذِّبُوا صِبْيَانَكُمْ بِالْغَمْزِ.', 'އަނަސް ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ފަރުވާ ކުރުމަށް ބޭނުން ކުރާ އެންމެ ރަނގަޅު އެއްޗަކީ ޙިޖާމާއާއި ކަނޑު ޤުސްތެވެ. އަދި ތިޔަބައިމިހުންގެ ކުދިންގެ ޓޮންސިލްތަކުގައި ފިއްތައިގެން އަނިޔާ ނުކުރާށެވެ.~~', ''], ['مسند أحمد 13081', 'حَدَّثَنَا يَحْيَى بْنُ سَعِيدٍ، عَنْ حُمَيْدٍ، قَالَ: سُئِلَ أَنَسُ بْنُ مَالِكٍ، عَنْ كَسْبِ الْحَجَّامِ، قَالَ: احْتَجَمَ رَسُولُ اللهِ ﷺ، حَجَمَهُ أَبُو طَيْبَةَ، فَأَمَرَ لَهُ بِصَاعَيْنِ مِنْ شَعِيرٍ، وَكَلَّمَ مَوَالِيَهُ أَنْ يُخَفِّفُوا عَنْهُ [مِنْ] ضَرِيبَتَهُ، وَقَالَ: أَمْثَلُ مَا تَدَاوَيْتُمْ بِهِ الْحِجَامَةُ، وَالْقُسْطُ الْبَحْرِيُّ.', 'ޙުމައިދު ރިވާ ކުރެއްވިއެވެ: ޙިޖާމާ ޖެހައިގެން ނަގާ އަގާ ބެހޭ ގޮތުން އަނަސްގެ ގާތު ސުވާލު ކުރެވުނުވެ. އެކަލޭގެފާނު ދެންނެވިއެވެ: އަބޫ ޠައިބާ، ރަސޫލު ﷲ ﷺ އަށް ހިޖާމާ ޖެއްސެވިއެވެ. އަދި އަބޫ ޠައިބާއަށް ދެ ޞާޢުގެ ކާތަކެތި ދޭން ނަބިއްޔާ އެންގެވިއެވެ. އަދި އަބޫ ޠައިބާގެ ސާހިބުން، އޭނާ (މަސައްކަތްކޮށްގެން ހޯއްދަން ޖައްސާ) ފައިސާ މަދު ކުރުމަށް ނަބިއްޔާ ވާހަކަފުޅު ދެއްކެވިއެވެ. އަދި ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ފަރުވާ ކުރުމަށް ބޭނުން ކުރާ އެންމެ ރަނގަޅު އެއްޗަކީ ޙިޖާމާއާއި ކަނޑު ޤުސްޠެވެ.“', ''], ['سنن أبي داود 3877', 'حَدَّثَنَا مُسَدَّدٌ، وَحَامِدُ بْنُ يَحْيَى قَالَا: نَا سُفْيَانُ، عَنِ الزُّهْرِيِّ، عَنْ عُبَيْدِ اللهِ بْنِ عَبْدِ اللهِ عَنْ أُمِّ قَيْسٍ بِنْتِ مِحْصَنٍ قَالَتْ: دَخَلْتُ عَلَى رَسُولِ اللهِ ﷺ بِابْنٍ لِي قَدْ أَعْلَقْتُ عَلَيْهِ مِنَ الْعُذْرَةِ فَقَالَ: عَلَامَ تَدْغَرْنَ أَوْلَادَكُنَّ بِهَذَا الْعِلَاقِ، عَلَيْكُنَّ بِهَذَا الْعُودِ الْهِنْدِيِّ، فَإِنَّ فِيهِ سَبْعَةَ أَشْفِيَةٍ مِنْهَا ذَاتُ الْجَنْبِ يُسْعَطُ مِنَ الْعُذْرَةِ، وَيُلَدُّ مِنْ ذَاتِ الْجَنْبِ قَالَ أَبُو دَاوُدَ: يَعْنِي بِالْعُودِ: الْقُسْطَ.', 'އުންމު ޤައިސް ބިންތި މިޙްޞަން ރިވާ ކުރެއްވިއެވެ: ތިމަން ކަމަނާގެ ފިރިހެން ކުޑަ ކުއްޖަކު ގޮވައިގެން، ރަސޫލު ﷲ ﷺ ގެ އަރިހަށް އަތުވީމެވެ. އެ ކުއްޖާއަށް ޓޮންސިލައިޓިސް ޖެހުމުގެ ސަބަބުން، އެ ކުއްޖާގެ ޓޮންސިލްތަކުގައި ތިމަން ކަމަނާ ފިއްތައިގެން ހުރީމެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަ ކަނބަލުންގެ ދަރިންގެ ޓޮންސިލްތަކުގައި ތި ގޮތަށް ފިއްތައިގެން ތިޔަ އަނިޔާ ކުރަނީ ކީއްވެތޯއެވެ؟ ތިޔަ ކަނބަލުންގެ މައްޗަށް އޮތީ މި އިންޑިޔާ ޤުސްޠު ބޭނުން ކުރުމެވެ. އެހެނީ ހަމަކަށަވަރުން އޭގައި ހަތް ޝިފާއެއް ލެއްވިގެންވެއެވެ. އޭގެ ތެރެއިން ޕްލިއުރިސީއަށް ޝިފާ ހިމެނެއެވެ. ޓޮންސިލައިޓިސަށް ފަރުވާ ކުރުމަށް އޭތި ނޭފަތަށް ދަމާލާނީއެވެ. އަދި ޕްލިއުރިސީއށް ފަރުވާ ކުރުމަށް އޭތި ކޮލުދަތްތައް ކައިރީގައި ހާކާލާނީއެވެ.“', ''], ['سنن ابن ماجه 3462', 'حَدَّثَنَا أَبُو بَكْرِ بْنُ أَبِي شَيْبَةَ، وَمُحَمَّدُ بْنُ الصَّبَّاحِ، قَالَا: حَدَّثَنَا سُفْيَانُ بْنُ عُيَيْنَةَ، عَنِ الزُّهْرِيِّ، عَنْ عُبَيْدِ اللهِ بْنِ عَبْدِ اللهِ، عَنْ أُمِّ قَيْسٍ بِنْتِ مِحْصَنٍ قَالَتْ: دَخَلْتُ بِابْنٍ لِي عَلَى النَّبِيِّ ﷺ، وَقَدْ أَعْلَقْتُ عَلَيْهِ مِنَ الْعُذْرَةِ، فَقَالَ: عَلَامَ تَدْغَرْنَ أَوْلَادَكُنَّ بِهَذَا الْعِلَاقِ؟ عَلَيْكُمْ بِهَذَا الْعُودِ الْهِنْدِيِّ، فَإِنَّ فِيهِ سَبْعَةَ أَشْفِيَةٍ: يُسْعَطُ بِهِ مِنَ الْعُذْرَةِ، وَيُلَدُّ بِهِ مِنْ ذَاتِ الْجَنْبِ.', 'އުންމު ޤައިސް ބިންތި މިޙްޞަން ރިވާ ކުރެއްވިއެވެ: ތިމަން ކަމަނާގެ ފިރިހެން ކުޑަ ކުއްޖަކު ގޮވައިގެން، ރަސޫލު ﷲ ﷺ ގެ އަރިހަށް އަތުވީމެވެ. އެ ކުއްޖާއަށް ޓޮންސިލައިޓިސް ޖެހުމުގެ ސަބަބުން، އެ ކުއްޖާގެ ޓޮންސިލްތަކުގައި ތިމަން ކަމަނާ ފިއްތައިގެން ހުރީމެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަ ކަނބަލުންގެ ދަރިންގެ ޓޮންސިލްތަކުގައި ތި ގޮތަށް ފިއްތައިގެން ތިޔަ އަނިޔާ ކުރަނީ ކީއްވެތޯއެވެ؟ ތިޔަ ކަނބަލުންގެ މައްޗަށް އޮތީ މި އިންޑިޔާ ޤުސްޠު ބޭނުން ކުރުމެވެ. އެހެނީ ހަމަކަށަވަރުން އޭގައި ހަތް ޝިފާއެއް ލެއްވިގެންވެއެވެ. އޭގެ ތެރެއިން ޕްލިއުރިސީއަށް ޝިފާ ހިމެނެއެވެ. ޓޮންސިލައިޓިސަށް ފަރުވާ ކުރުމަށް އޭތި ނޭފަތަށް ދަމާލާނީއެވެ. އަދި ޕްލިއުރިސީއށް ފަރުވާ ކުރުމަށް އޭތި ކޮލުދަތްތައް ކައިރީގައި ހާކާލާނީއެވެ.“', ''], ['سنن ابن ماجه 3462 (م)', 'حَدَّثَنَا أَحْمَدُ بْنُ عَمْرِو بْنِ السَّرْحِ الْمِصْرِيُّ، حَدَّثَنَا عَبْدُ اللهِ بْنُ وَهْبٍ، حَدَّثَنَا يُونُسُ، عَنِ ابْنِ شِهَابٍ، عَنْ عُبَيْدِ اللهِ، عَنْ أُمِّ قَيْسٍ بِنْتِ مِحْصَنٍ، عَنِ النَّبِيِّ ﷺ، بِنَحْوِهِ. قَالَ يُونُسُ: أَعْلَقْتُ يَعْنِي غَمَزْتُ', 'ކުރީގެ ޙަދީޘާ އެއްގޮތަކަށް ރިވާވެފައިވެއެވެ.', ''], ['سنن ابن ماجه 3468', 'حَدَّثَنَا أَبُو طَاهِرٍ أَحْمَدُ بْنُ عَمْرِو بْنِ السَّرْحِ الْمِصْرِيُّ، حَدَّثَنَا عَبْدُ اللهِ بْنُ وَهْبٍ، أَخْبَرَنَا يُونُسُ، وَابْنُ سَمْعَانَ، عَنِ ابْنِ شِهَابٍ، عَنْ عُبَيْدِ اللهِ بْنِ عَبْدِ اللهِ بْنِ عُتْبَةَ، عَنْ أُمِّ قَيْسٍ بِنْتِ مِحْصَنٍ قَالَتْ: قَالَ رَسُولُ اللهِ ﷺ: عَلَيْكُمْ بِالْعُودِ الْهِنْدِيِّ - يَعْنِي بِهِ الْكُسْتَ - فَإِنَّ فِيهِ سَبْعَةَ أَشْفِيَةٍ، مِنْهَا ذَاتُ الْجَنْبِ.', 'އުންމު ޤައިސް ބިންތި މިޙްޞަން ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ތިޔަބައިމީހުންގެ މައްޗަށް އޮތީ މި އިންޑިޔާ ޤުސްޠު ބޭނުން ކުރުމެވެ. އެހެނީ ހަމަކަށަވަރުން އޭގައި ހަތް ޝިފާވެއެވެ. އޭގެ ތެރެއިން ޕްލިއުރިސީއަށް ޝިފާ ހިމެނެއެވެ.', ''], ['مسند أحمد 27639', 'حَدَّثَنَا سُفْيَانُ، عَنِ الزُّهْرِيِّ، عَنْ عُبَيْدِ اللهِ، عَنْ أُمِّ قَيْسٍ بِنْتِ مِحْصَنٍ - أُخْتِ عُكَّاشَةَ بْنِ مِحْصَنٍ - قَالَتْ: دَخَلْتُ بِابْنٍ لِي عَلَى رَسُولِ اللهِ ﷺ لَمْ يَأْكُلِ الطَّعَامَ، فَبَالَ فَدَعَا بِمَاءٍ فَرَشَّهُ. وَدَخَلْتُ بِابْنٍ لِي قَدْ أَعْلَقْتُ عَنْهُ - وَقَالَ مَرَّةً: عَلَيْهِ - مِنَ الْعُذْرَةِ، فَقَالَ: عَلَامَ تَدْغَرْنَ أَوْلَادَكُنَّ بِهَذَا الْعِلَاقِ؟ عَلَيْكُمْ بِهَذَا الْقُسْطِ - وَقَالَ مَرَّةً سُفْيَانُ: الْعُودِ الْهِنْدِيِّ - فَإِنَّ فِيهِ سَبْعَةَ أَشْفِيَةٍ، مِنْهَا ذَاتُ الْجَنْبِ، يُسْعَطُ مِنَ الْعُذْرَةِ وَيُلَدُّ مِنْ ذَاتِ الْجَنْبِ.', 'އުންމު ޤައިސް ބިންތި މިޙްޞަން ރިވާ ކުރެއްވިއެވެ: ތިމަން ކަމަނާގެ ފިރިހެން ކުޑަ ކުއްޖަކު ގޮވައިގެން، ރަސޫލު ﷲ ﷺ ގެ އަރިހަށް އަތުވީމެވެ. އެ ކުއްޖާ އަދި އޭރު ކާކަށް ނުފަށައެވެ. ފަހެ (ނަބިއްޔާގެ އުނގުފުޅު މައްޗަށް) އެ ކުއްޖާ ކުޑަކަމުގޮސްފިއެވެ. ދެން ފެން ގެނައުމަށް އަންގަވާފައި ނަބިއްޔާ އެ ތަނުގައި ފެން ބުރުވާލެއްވިއެވެ. އެ ކަމަނާ (އިތުރަށް) ވިދާޅުވިއެވެ: ނަބިއްޔާގެ އަރިހަށް ދިޔައީ، ތިމަން ކަމަނާގެ ކުއްޖާއަށް ޓޮންސިލައިޓިސް ޖެހުމުގެ ސަބަބުން، އެ ކުއްޖާގެ ޓޮންސިލްތަކުގައި ތިމަން ކަމަނާ ފިއްތައިގެން ހުއްޓައެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަ ކަނބަލުންގެ ދަރިންގެ ޓޮންސިލްތަކުގައި ތި ގޮތަށް ފިއްތައިގެން ތިޔަ އަނިޔާ ކުރަނީ ކީއްވެތޯއެވެ؟ ތިޔަ ކަނބަލުންގެ މައްޗަށް އޮތީ މި އިންޑިޔާ ޤުސްޠު ބޭނުން ކުރުމެވެ. އެހެނީ ހަމަކަށަވަރުން އޭގައި ހަތް ޝިފާއެއް ލެއްވިގެންވެއެވެ. އޭގެ ތެރެއިން ޕްލިއުރިސީއަށް ޝިފާ ހިމެނެއެވެ. ޓޮންސިލައިޓިސަށް ފަރުވާ ކުރުމަށް އޭތި ނޭފަތަށް ދަމާލާނީއެވެ. އަދި ޕްލިއުރަސީއށް ފަރުވާ ކުރުމަށް އޭތި ކޮލުދަތްތައް ކައިރީގައި ހާކާލާނީއެވެ.“', ''], ['مسند أحمد 27642', 'حَدَّثَنَا عَبْدُ الرَّزَّاقِ قَالَ: حَدَّثَنَا مَعْمَرٌ، عَنِ الزُّهْرِيِّ، عَنْ عُبَيْدِ اللهِ بْنِ عَبْدِ اللهِ بْنِ عُتْبَةَ، عَنْ أُمِّ قَيْسٍ بِنْتِ مِحْصَنٍ الْأَسَدِيَّةِ - أُخْتِ عُكَّاشَةَ - قَالَتْ: جِئْتُ بِابْنٍ لِي قَدْ أَعْلَقْتُ عَنْهُ أَخَافُ أَنْ يَكُونَ بِهِ الْعُذْرَةُ، فَقَالَ النَّبِيُّ ﷺ: عَلَامَ تَدْغَرْنَ أَوْلَادَكُنَّ بِهَذِهِ الْعَلَائِقِ؟ عَلَيْكُنَّ بِهَذَا الْعُودِ الْهِنْدِيِّ - يَعْنِي الْكُسْتَ - فَإِنَّ فِيهِ سَبْعَةَ أَشْفِيَةٍ، مِنْهَا ذَاتُ الْجَنْبِ. ثُمَّ أَخَذَ النَّبِيُّ ﷺ صَبِيَّهَا فَوَضَعَهُ فِي حِجْرِهِ، فَبَالَ عَلَيْهِ، فَدَعَا بِمَاءٍ فَنَضَحَهُ. وَلَمْ يَكُنِ الصَّبِيُّ بَلَغَ أَنْ يَأْكُلَ الطَّعَامَ.', 'ޢުއްކާޝާގެ އުޚްތު، އުންމު ޤައިސް ބިންތި މިޙްޞަން އަލްއަސަދިއްޔާ ރިވާ ކުރެއްވިއެވެ: ތިމަން ކަމަނާގެ ފިރިހެން ކުޑަ ކުއްޖަކު ގޮވައިގެން، ރަސޫލު ﷲ ﷺ ގެ އަރިހަށް އަތުވީމެވެ. އެ ކުއްޖާއަށް ޓޮންސިލައިޓިސް ޖެހިއްޖެ ކަމުގެ ބިރަށްޓަކައި، އެ ކުއްޖާގެ ޓޮންސިލްތަކުގައި ތިމަން ކަމަނާ ފިއްތައިގެން ހުރީމެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަ ކަނބަލުންގެ ދަރިންގެ ޓޮންސިލްތަކުގައި ތި ގޮތަށް ފިއްތައިގެން ތިޔަ އަނިޔާ ކުރަނީ ކީއްވެތޯއެވެ؟ ތިޔަ ކަނބަލުންގެ މައްޗަށް އޮތީ މި އިންޑިޔާ ޤުސްޠު ބޭނުން ކުރުމެވެ. އެހެނީ ހަމަކަށަވަރުން އޭގައި ހަތް ޝިފާއެއް ލެއްވިގެންވެއެވެ. އޭގެ ތެރެއިން ޕްލިއުރިސީއަށް ޝިފާ ހިމެނެއެވެ.“ އެއަށްފަހު ނަބިއްޔާ އެ ކަމަނާގެ ކުއްޖާ ނަންގަވާފައި އުނގުފުޅުމަތީ ބެހެއްޓެވިއެވެ. އެ ކުއްޖާ ކުޑަކަމުގޮސްފިއެވެ. ނަބިއްޔާ ފެން ގެނައުމަށް އެންގެވުމަށް ފަހު، އެ ކުޑަކަމުދިއުން ޖެހުނު ތަނުގައި ފެން ބުރުވާލެއްވިއެވެ. އަދި ދޮވުމަކުން ނުދޮންނަވައެވެ. އޭރު އެ ކުއްޖާ ކާން ނުފަށައެވެ. (ޙަދީޘްގެ ރާވީއެއް ކަމަށްވާ) ޒުހްރީ ވިދާޅުވިއެވެ: ފަހެ ސުންނަތުގައި ވަނީ ކުޑަ ފިރިހެން ކުއްޖާގެ ކުޑަކަމުދިއުމަށް ފެން ބުރުވާލުމެވެ. އަދި ކުޑަ އަންހެން ކުއްޖާގެ ކުޑަކަމުދިއުން ދޮވުމެވެ. (އަދިވެސް) ޒުހްރީ ވިދާޅުވިއެވެ: ޓޮންސިލައިޓިސަށް ފަރުވާ ކުރުމަށް އޭތި ނޭފަތަށް ދަމާލާނީއެވެ. އަދި ޕްލިއުރިސީއށް ފަރުވާ ކުރުމަށް އޭތި ކޮލުދަތްތައް ކައިރީގައި ހާކާލާނީއެވެ.', ''], ['مسند أحمد 27645', 'حَدَّثَنَا عُثْمَانُ بْنُ عُمَرَ قَالَ: أَخْبَرَنَا يُونُسُ، عَنِ الزُّهْرِيِّ، عَنْ عُبَيْدِ اللهِ بْنِ عَبْدِ اللهِ بْنِ عُتْبَةَ، أَنَّ أُمَّ قَيْسٍ بِنْتَ مِحْصَنٍ إِحْدَى بَنِي أَسَدِ بْنِ خُزَيْمَةَ وَكَانَتْ مِنَ الْمُهَاجِرَاتِ الْأُوَلِ اللَّاتِي بَايَعْنَ رَسُولَ اللهِ ﷺ. قَالَ: فَأَخْبَرَتْنِي أَنَّهَا أَتَتْ رَسُولَ اللهِ ﷺ بِابْنٍ لَهَا لَمْ يَبْلُغْ أَنْ يَأْكُلَ الطَّعَامَ فَذَكَرَ الْحَدِيثَ. وَقَالَ: عَلَامَ تَدْغَرْنَ أَوْلَادَكُنَّ؟!.', 'އުންމު ޤައިސް ބިންތި މިޙްޞަން ރިވާ ކުރެއްވިއެވެ. އަދި އެ ކަމަނާއަކީ ފުރަތަމަ ފަހަރު ހިޖުރަ ކުރެއްވި، ރަސޫލު ﷲ ﷺ އަށް ބައިޢަތު ހިއްޕެވި އަންހެން ކަނބަލެކެވެ. އެ ކަމަނާގެ ފިރިހެން ކުޑަ ކުއްޖަކު ގޮވައިގެން، ރަސޫލު ﷲ ﷺ ގެ އަރިހަށް އަތުވިއެވެ. އޭރު އެ ކުއްޖާ ކާން ނުފަށައެވެ. އަދި ކުރީގެ ޙަދީޘާ އެއްގޮތަކަށް ރިވާވެފައިވެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަ ކަނބަލުންގެ ދަރިންގެ ޓޮންސިލްތަކުގައި ތި ގޮތަށް ފިއްތައިގެން ތިޔަ އަނިޔާ ކުރަނީ ކީއްވެތޯއެވެ؟“', ''], ['مسند أحمد 27646', 'حَدَّثَنَا مُحَمَّدُ بْنُ جَعْفَرٍ قَالَ: حَدَّثَنَا مَعْمَرٌ قَالَ: حَدَّثَنَا الزُّهْرِيُّ، عَنْ عُبَيْدِ اللهِ بْنِ عُتْبَةَ بْنِ مَسْعُودٍ عَنْ أُمِّ قَيْسٍ بِنْتِ مِحْصَنٍ أَنَّهَا جَاءَتْ بِابْنٍ لَهَا وَقَدْ أَعْلَقْتُ عَلَيْهِ مِنَ الْعُذْرَةِ، فَقَالَ لَهَا رَسُولُ اللهِ ﷺ: عَلَامَ تَدْغَرْنَ أَوْلَادَكُنَّ بِهَذِهِ الْعِلَقِ؟ عَلَيْكُمْ بِهَذَا الْعُودِ الْهِنْدِيِّ فَإِنَّ فِيهِ سَبْعَةَ أَشْفِيَةٍ مِنْهَا ذَاتُ الْجَنْبِ. ثُمَّ أَخَذَ الصَّبِيَّ فَبَالَ عَلَيْهِ، فَدَعَا بِمَاءٍ فَنَضَحَهُ. قَالَ ابْنُ شِهَابٍ: مَضَتِ السُّنَّةُ بِذَلِكَ.', 'އުންމު ޤައިސް ބިންތި މިޙްޞަން އަލްއަސަދިއްޔާ ރިވާ ކުރެއްވިއެވެ: ތިމަން ކަމަނާގެ ފިރިހެން ކުޑަ ކުއްޖަކު ގޮވައިގެން، ރަސޫލު ﷲ ﷺ ގެ އަރިހަށް އަތުވީމެވެ. އެ ކުއްޖާއަށް ޓޮންސިލައިޓިސް ޖެހުމުގެ ސަބަބުން، އެ ކުއްޖާގެ ޓޮންސިލްތަކުގައި ތިމަން ކަމަނާ ފިއްތައިގެން ހުރީމެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަ ކަނބަލުންގެ ދަރިންގެ ޓޮންސިލްތަކުގައި ތި ގޮތަށް ފިއްތައިގެން ތިޔަ އަނިޔާ ކުރަނީ ކީއްވެތޯއެވެ؟ ތިޔަ ކަނބަލުންގެ މައްޗަށް އޮތީ މި އިންޑިޔާ ޤުސްޠު ބޭނުން ކުރުމެވެ. އެހެނީ ހަމަކަށަވަރުން އޭގައި ހަތް ޝިފާއެއް ލެއްވިގެންވެއެވެ. އޭގެ ތެރެއިން ޕްލިއުރިސީއަށް ޝިފާ ހިމެނެއެވެ.“ އެއަށްފަހު ނަބިއްޔާ އެ ކަމަނާގެ ކުއްޖާ ނަންގަވާފައި (އުނގުފުޅުމަތީ ބެހެއްޓެވުމުން) އެ ކުއްޖާ ކުޑަކަމުގޮސްފިއެވެ. ނަބިއްޔާ ފެން ގެނައުމަށް އެންގެވުމަށް ފަހު، އެ ކުޑަކަމުދިއުން ޖެހުނު ތަނުގައި ފެން ބުރުވާލެއްވިއެވެ. (ޙަދީޘްގެ ރާވީއެއް ކަމަށްވާ) ޒުހްރީ ވިދާޅުވިއެވެ: ފަހެ ސުންނަތުގައި ވަނީ އެފަދައިން ކަން ކުރުމަށެވެ.', ''], ['مسند أحمد 8801', 'حَدَّثَنَا يَحْيَى، أَخْبَرَنَا ابْنُ لَهِيعَةَ، عَنْ أَبِي يُونُسَ، عَنْ أَبِي هُرَيْرَةَ، قَالَ: سَمِعْتُ النَّبِيَّ ﷺ يَقُولُ: أَهْلُ الْجَنَّةِ رَشْحُهُمُ الْمِسْكُ، وَوُقُودُهُمُ الْأَلُوَّةُ، قَالَ: قُلْتُ لِابْنِ لَهِيعَةَ: يَا أَبَا عَبْدِ الرَّحْمَنِ، مَا الْأَلُوَّةُ؟ قَالَ: الْعُودُ الْهِنْدِيُّ الْجَيِّدُ.', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ”ސުވަރުގޭގެ އަހުލުވެރިންގެ ދަލަކީ ގޮމަކަސްތޫރިއެވެ. އަދި އެބައިމީހުންގެ ދަރަކީ އަލުއްވަތެވެ.“ (ޙަދީޘްގެ ރާވީއެއް ކަމަށްވާ) ޔަޙްޔާ ވިދާޅުވިއެވެ: އިބްނު ލަހީޢާ ގާތު ތިމަން ދެންނެވީމެވެ: އޭ އަބޫ ޢަބްދުއްރަޙްމާނެވެ. އަލުއްވަތަކީ ކޮބައިތޯއެވެ؟ އެކަލޭގެފާނު ދެންނެވިއެވެ: އެއީ މޮޅު އިންޑިޔާ ޤުސްޠެވެ.', ''], ['مسند أحمد 19597', 'حَدَّثَنَا أَبُو دَاوُدَ، أَخْبَرَنَا شُعْبَةُ، عَنْ خَالِدٍ الْحَذَّاءِ قَالَ: سَمِعْتُ أَبَا عَبْدِ اللهِ مَيْمُونًا يُحَدِّثُ عَنْ زَيْدِ بْنِ أَرْقَمَ أَنَّ رَسُولَ اللهِ ﷺ أَمَرَهُمْ أَنْ يَتَدَاوَوْا مِنْ ذَاتِ الْجَنْبِ بِالْعُودِ الْهِنْدِيِّ وَالزَّيْتِ.', 'ޒައިދު ބިން އަރްޤަމު ރިވާ ކުރެއްވިއެވެ: ޕްލިއުރިސީއަށް ފަރުވާ ކުރުމުގައި އިންޑިއާ ޤުސްޠާއި ތެޔޮ ބޭނުން ކުރަން ރަސޫލު ﷲ ﷺ ތިމަންމެންނަށް އެންގެވިއެވެ.', ''], ['الأحاديث المختارة 1392', 'أَخْبَرَنَا أَسْعَدُ بْنُ سَعِيدِ بْنِ رَوْحٍ - بِأَصْبَهَانَ - أَنَّ فَاطِمَةَ بِنْتَ عَبْدِ اللهِ الْجُوزْدَانِيَّةَ أَخْبَرَتْهُمْ، أَنَا مُحَمَّدُ بْنُ عَبْدِ اللهِ بْنِ رِيذَةَ، أَنَا سُلَيْمَانُ بْنُ أَحْمَدَ الطَّبَرَانِيُّ، نَا عَبْدُ اللهِ بْنُ أَحْمَدَ بْنِ حَنْبَلٍ، نَا سُرَيْجُ بْنُ يُونُسَ، نَا الْمُطَّلِبُ بْنُ زِيَادٍ، عَنْ زِيَادِ بْنِ عِلَاقَةَ، عَنْ أُسَامَةَ بْنِ شَرِيكٍ، قَالَ: قَالَ النَّبِيُّ ﷺ: فِي الْحَبَّةِ السَّوْدَاءِ شِفَاءٌ مِنْ كُلِّ دَاءٍ إِلَّا السَّامَ. الْمُطَّلِبُ: وَثَّقَهُ أَحْمَدُ وَيَحْيَى.', 'އުސާމާ ބިން ޝަރީކު ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ކަޅުދިރީގައި މަރު ފިޔަވާ ކޮންމެ ބައްޔަކަށް ޝިފާވެއެވެ.“', ''], ['السنن الكبرى للنسائي 7534', 'أَخْبَرَنَا قُتَيْبَةُ بْنُ سَعِيدٍ، قَالَ: حَدَّثَنَا سُفْيَانُ، عَنِ الزُّهْرِيِّ، عَنْ أَبِي سَلَمَةَ، عَنْ أَبِي هُرَيْرَةَ، قَالَ: قَالَ رَسُولُ اللهِ ﷺ: عَلَيْكُمْ بِهَذِهِ الْحَبَّةِ السَّوْدَاءِ، فَإِنَّ فِيهَا شِفَاءً مِنْ كُلِّ دَاءٍ إِلَّا السَّامَ. وَالسَّامُ: الْمَوْتُ.', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ މައްޗަށް އޮތީ މި ކަޅުދިރި ބޭނުން ކުރުމެވެ. އެހެނީ ހަމަކަށަވަރުން އޭގައި ސާއްމު ފިޔަވާ ކޮންމެ ބައްޔަކަށް ޝިފާވެއެވެ.“ އަދި ސާއްމަކީ މަރެވެ.', ''], ['سنن الترمذي 2041', 'حَدَّثَنَا ابْنُ أَبِي عُمَرَ وَسَعِيدُ بْنُ عَبْدِ الرَّحْمَنِ الْمَخْزُومِيُّ، قَالَا: حَدَّثَنَا سُفْيَانُ، عَنِ الزُّهْرِيِّ، عَنْ أَبِي سَلَمَةَ، عَنْ أَبِي هُرَيْرَةَ أَنَّ النَّبِيَّ ﷺ قَالَ: عَلَيْكُمْ بِهَذِهِ الْحَبَّةِ السَّوْدَاءِ؛ فَإِنَّ فِيهَا شِفَاءً مِنْ كُلِّ دَاءٍ إِلَّا السَّامَ. وَالسَّامُ الْمَوْتُ وَفِي الْبَابِ عَنْ بُرَيْدَةَ، وَابْنِ عُمَرَ، وَعَائِشَةَ. وَهَذَا حَدِيثٌ حَسَنٌ صَحِيحٌ. وَالْحَبَّةُ السَّوْدَاءُ: هِيَ الشُّونِيزُ.', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ މައްޗަށް އޮތީ މި ކަޅުދިރި ބޭނުން ކުރުމެވެ. އެހެނީ ހަމަކަށަވަރުން އޭގައި ސާއްމު ފިޔަވާ ކޮންމެ ބައްޔަކަށް ޝިފާވެއެވެ.“ އަދި ސާއްމަކީ މަރެވެ.', ''], ['سنن ابن ماجه 3447', 'حَدَّثَنَا مُحَمَّدُ بْنُ رُمْحٍ، وَمُحَمَّدُ بْنُ الْحَارِثِ الْمِصْرِيَّانِ، قَالَا: حَدَّثَنَا اللَّيْثُ بْنُ سَعْدٍ، عَنْ عُقَيْلٍ، عَنِ ابْنِ شِهَابٍ، أَخْبَرَنِي أَبُو سَلَمَةَ بْنُ عَبْدِ الرَّحْمَنِ، وَسَعِيدُ بْنُ الْمُسَيِّبِ، أَنَّ أَبَا هُرَيْرَةَ أَخْبَرَهُمَا أَنَّهُ سَمِعَ رَسُولَ اللهِ ﷺ يَقُولُ: إِنَّ فِي الْحَبَّةِ السَّوْدَاءِ شِفَاءً مِنْ كُلِّ دَاءٍ إِلَّا السَّامَ. وَالسَّامُ: الْمَوْتُ، وَالْحَبَّةُ السَّوْدَاءُ: الشُّونِيزُ.', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ”ހަމަކަށަވަރުން ކަޅުދިރީގައި ސާއްމު ފިޔަވާ ކޮންމެ ބައްޔަކަށް ޝިފާވެއެވެ.“ (ޙަދީޘްގެ ރާވީއެއް ކަމަށްވާ އިބްނު ޝިހާބު ވިދާޅުވިއެވެ:) އަދި ސާއްމަކީ މަރެވެ. އަދި ކަޅުދިރިއަކީ ޝޫނީޒެވެ. (ނޯޓު: އޭރު އެ ޒަމާނުގައި ކަޅުދިރި މަޝްހޫރުވެފައިވަނީ އެ ނަމުންނެވެ.)', ''], ['سنن ابن ماجه 3448', 'حَدَّثَنَا أَبُو سَلَمَةَ يَحْيَى بْنُ خَلَفٍ، حَدَّثَنَا أَبُو عَاصِمٍ، عَنْ عُثْمَانَ بْنِ عَبْدِ الْمَلِكِ قَالَ: سَمِعْتُ سَالِمَ بْنَ عَبْدِ اللهِ يُحَدِّثُ عَنْ أَبِيهِ، أَنَّ رَسُولَ اللهِ ﷺ قَالَ: عَلَيْكُمْ بِهَذِهِ الْحَبَّةِ السَّوْدَاءِ، فَإِنَّ فِيهَا شِفَاءً مِنْ كُلِّ دَاءٍ إِلَّا السَّامَ.', 'ޢަބްދު ﷲ ބިން ޢުމަރު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ މައްޗަށް އޮތީ މި ކަޅުދިރި ބޭނުން ކުރުމެވެ. އެހެނީ ހަމަކަށަވަރުން އޭގައި މަރު ފިޔަވާ ކޮންމެ ބައްޔަކަށް ޝިފާވެއެވެ.“', ''], ['سنن ابن ماجه 3449', 'حَدَّثَنَا أَبُو بَكْرِ بْنُ أَبِي شَيْبَةَ، حَدَّثَنَا عُبَيْدُ اللهِ، أَخْبَرَنَا إِسْرَائِيلُ، عَنْ مَنْصُورٍ، عَنْ خَالِدِ بْنِ سَعْدٍ قَالَ: خَرَجْنَا وَمَعَنَا غَالِبُ بْنُ أَبْجَرَ، فَمَرِضَ فِي الطَّرِيقِ، فَقَدِمْنَا الْمَدِينَةَ وَهُوَ مَرِيضٌ، فَعَادَهُ ابْنُ أَبِي عَتِيقٍ، وَقَالَ لَنَا: عَلَيْكُمْ بِهَذِهِ الْحَبَّةِ السَّوْدَاءِ، فَخُذُوا مِنْهَا خَمْسًا أَوْ سَبْعًا فَاسْحَقُوهَا، ثُمَّ اقْطُرُوهَا فِي أَنْفِهِ بِقَطَرَاتِ زَيْتٍ، فِي هَذَا الْجَانِبِ وَفِي هَذَا الْجَانِبِ، فَإِنَّ عَائِشَةَ حَدَّثَتْهُمْ أَنَّهَا سَمِعَتْ رَسُولَ اللهِ ﷺ يَقُولُ: إِنَّ هَذِهِ الْحَبَّةَ السَّوْدَاءَ شِفَاءٌ مِنْ كُلِّ دَاءٍ إِلَّا أَنْ يَكُونَ السَّامُ. قُلْتُ: وَمَا السَّامُ؟ قَالَ: الْمَوْتُ.', 'ޚާލިދު ބިން ސަޢުދު ރިވާ ކުރެއްވިއެވެ: ތިމަންމެން (ދަތުރެއްގައި) ނުކުތީމެވެ. އަދި ދިޔަ މަގުމަތީގައި ތިމަންމެންނާއެކުގައި އުޅުނު ޣާލިބު ބިން އަބްޖަރު ބަލިވިއެވެ. ތިމަންމެން މަދީނާއަށް ދެވުނު އިރުވެސް ޣާލިބު އުޅުނީ ބަލި ޙާލުގައެވެ. ފަހެ އިބްނު އަބީ ޢަތީޤު އެކަލޭފެފާނަށް ޒިޔާރާތް ކުރައްވާފައި ތިމަންމެންނަށް ދެންނެވިއެވެ: ތިޔަބައިމީހުންގެ މައްޗަށް އޮތީ މި ކަޅުދިރި ބޭނުން ކުރުމެވެ. ފަހެ އޭގެން ފަހެއް ނުވަތަ ހަތް އޮށް ނަގާފައި މުގުރާށެވެ. އެއަށްފަހު ތެލާ އެއްކޮށްފައި އޭނާގެ ނޭފަތް ތެރޭ މި ދެ ދިމާލަށް ވެސް ތިކި ޖަހާށެވެ. އެހެނީ ހަމަކަށަވަރުން ޢާއިޝާ ތިމަންނައަށް ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷺ ޙަދީޘް ކުރައްވަނިކޮށް އެ ކަމަނާ އަޑު އެއްސެވިއެވެ: ”ހަމަކަށަވަރުން މި ކަޅުދިރިއަކީ ސާއްމު ފިޔަވާ ކޮންމެ ބައްޔަކަށްވާ ޝިފާއެކެވެ.“ ތިމަން ކަމަނާ ދެންނެވީމެވެ: ސާއްމަކީ ކޮބައިތޯއެވެ؟ ނަބިއްޔާ ޙަދިޘް ކުރެއްވިއެވެ: "މަރެވެ.“', ''], ['السنن الكبرى للبيهقي 19625', '(أَخْبَرَنَا) أَبُو مُحَمَّدٍ عَبْدُ اللهِ بْنُ يُوسُفَ الْأَصْبَهَانِيُّ، أَنْبَأَ أَبُو بَكْرٍ مُحَمَّدُ بْنُ الْحُسَيْنِ بْنِ الْحَسَنِ الْقَطَّانُ، ثَنَا أَبُو بَكْرٍ أَحْمَدُ بْنُ يُوسُفَ السُّلَمِيُّ، ثَنَا عَبْدُ الرَّزَّاقِ، أَنْبَأَ مَعْمَرُ بْنُ رَاشِدٍ، عَنِ الزُّهْرِيِّ، أَخْبَرَنِي أَبُو سَلَمَةَ بْنُ عَبْدِ الرَّحْمَنِ، عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللهُ عَنْهُ - قَالَ: سَمِعْتُ رَسُولَ اللهِ - ﷺ - يَقُولُ لِلشُّونِيزِ: عَلَيْكُمْ بِهَذِهِ الْحَبَّةِ السَّوْدَاءِ، فَإِنَّ فِيهَا شِفَاءً مِنْ كُلِّ شَيْءٍ أَوْ دَاءٍ إِلَّا السَّامَ. يُرِيدُ بِهِ الْمَوْتَ. رَوَاهُ مُسْلِمٌ فِي الصَّحِيحِ عَنْ عَبْدِ بْنِ حُمَيْدٍ، عَنْ عَبْدِ الرَّزَّاقِ، وَأَخْرَجَهُ الْبُخَارِيُّ مِنْ وَجْهٍ آخَرَ عَنِ الزُّهْرِيِّ.', 'އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ކަޅުދިރިއާ ބެހޭ ގޮތުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ”ތިޔަބައިމީހުންގެ މައްޗަށް އޮތީ މި ކަޅުދިރި ބޭނުން ކުރުމެވެ. އެހެނީ ހަމަކަށަވަރުން އޭގައި ސާއްމު ފިޔަވާ ކޮންމެ ބައްޔަކަށް ޝިފާވެއެވެ.“ ސާއްމު އެ މާނަ ކުރައްވަނީ މަރަށެވެ.', ''], ['مسند أحمد 7366', 'حَدَّثَنَا سُفْيَانُ، عَنِ الزُّهْرِيِّ، عَنْ أَبِي سَلَمَةَ (إِنْ شَاءَ اللهُ) عَنْ أَبِي هُرَيْرَةَ، عَنِ النَّبِيِّ ﷺ: عَلَيْكُمْ بِهَذِهِ الْحَبَّةِ السَّوْدَاءِ؛ فَإِنَّ فِيهَا شِفَاءً مِنْ كُلِّ دَاءٍ، إِلَّا السَّامَ. قَالَ سُفْيَانُ: السَّامُ: الْمَوْتُ، وَهِيَ الشُّونِيزُ.', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ”ތިޔަބައިމީހުންގެ މައްޗަށް އޮތީ މި ކަޅުދިރި ބޭނުން ކުރުމެވެ. އެހެނީ ހަމަކަށަވަރުން އޭގައި ސާއްމު ފިޔަވާ ކޮންމެ ބައްޔަކަށް ޝިފާވެއެވެ.“ ސުފްޔާނު ވިދާޅުވިއެވެ: ސާއްމަކީ މަރެވެ. އަދި (ކަޅުދިރިއަކީ) ޝޫނީޒޭ ކިޔުނު އެއްޗެވެ.', ''], ['مسند أحمد 7632', 'حَدَّثَنَا يَزِيدُ، وَيَعْلَى قَالَا: حَدَّثَنَا مُحَمَّدُ بْنُ عَمْرٍو، عَنْ أَبِي سَلَمَةَ، عَنْ أَبِي هُرَيْرَةَ أَنَّ رَسُولَ اللهِ ﷺ قَالَ: فِي هَذِهِ الْحَبَّةِ السَّوْدَاءِ شِفَاءٌ مِنْ كُلِّ دَاءٍ إِلَّا السَّامَ. قَالُوا: يَا رَسُولَ اللهِ، وَمَا السَّامُ؟ قَالَ: الْمَوْتُ.', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ”މި ކަޅުދިރީގައި ސާއްމު ފިޔަވާ ކޮންމެ ބައްޔަކަށް ޝިފާވެއެވެ.“ މީސްތަކުން ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. ސާއްމަކީ ކޮބައިތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”މަރެވެ.“', ''], ['مسند أحمد 7712', 'حَدَّثَنَا عَبْدُ الرَّزَّاقِ، أَخْبَرَنَا مَعْمَرٌ، عَنِ الزُّهْرِيِّ: أَخْبَرَنِي أَبُو سَلَمَةَ، عَنْ أَبِي هُرَيْرَةَ قَالَ: سَمِعْتُ رَسُولَ اللهِ ﷺ يَقُولُ لِلشُّونِيزِ: عَلَيْكُمْ بِهَذِهِ الْحَبَّةِ السَّوْدَاءِ فَإِنَّ فِيهَا شِفَاءً مِنْ كُلِّ شَيْءٍ إِلَّا السَّامَ، يُرِيدُ الْمَوْتَ.', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ކަޅުދިރިއާ ބެހޭ ގޮތުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ”ތިޔަބައިމީހުންގެ މައްޗަށް އޮތީ މި ކަޅުދިރި ބޭނުން ކުރުމެވެ. އެހެނީ ހަމަކަށަވަރުން އޭގައި ސާއްމު ފިޔަވާ ކޮންމެ ބައްޔަކަށް ޝިފާވެއެވެ.“ ސާއްމު އެ މާނަ ކުރައްވަނީ މަރަށެވެ.', ''], ['مسند أحمد 8590', 'حَدَّثَنَا عَفَّانُ، [قَالَ: حَدَّثَنَا] يَزِيدُ بْنُ زُرَيْعٍ، حَدَّثَنَا مَعْمَرٌ، عَنِ الزُّهْرِيِّ، عَنْ أَبِي سَلَمَةَ، عَنْ أَبِي هُرَيْرَةَ، قَالَ: قَالَ رَسُولُ اللهِ ﷺ: عَلَيْكُمْ بِهَذِهِ الْحَبَّةِ السَّوْدَاءِ، فَإِنَّ فِيهَا شِفَاءً مِنْ كُلِّ شَيْءٍ.', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ މައްޗަށް އޮތީ މި ކަޅުދިރި ބޭނުން ކުރުމެވެ. އެހެނީ ހަމަކަށަވަރުން އޭގައި ކޮންމެ ކަމަކަށް ޝިފާވެއެވެ.“', ''], ['مسند أحمد 9132', 'حَدَّثَنَا عَفَّانُ، حَدَّثَنَا عَبْدُ الرَّحْمَنِ بْنُ إِبْرَاهِيمَ الْقَاصُّ، قَالَ: حَدَّثَنَا الْعَلَاءُ بْنُ عَبْدِ الرَّحْمَنِ، عَنْ أَبِيهِ، عَنْ أَبِي هُرَيْرَةَ، عَنْ رَسُولِ اللهِ ﷺ قَالَ: مَا مِنْ دَاءٍ إِلَّا فِي الْحَبَّةِ السَّوْدَاءِ مِنْهُ شِفَاءٌ إِلَّا السَّامَ.', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ކަޅުދިރީގައި އެ ބައްޔަކަށް ޝިފާ ނުވާ އެއްވެސް ބައްޔެއް ނުވެއެވެ. އެއީ މަރު ފިޔަވައެވެ.“', ''], ['مسند أحمد 9553', '[وَبِإِسْنَادِهِ]، [عَنِ النَّبِيِّ] ﷺ قَالَ: الْحَبَّةُ السَّوْدَاءُ شِفَاءٌ مِنْ كُلِّ دَاءٍ إِلَّا السَّامَ. وَالسَّامُ الْمَوْتُ.', 'ކުރީގެ ޙަދީޘާ އެއްގޮތަކަށް ރިވާވެފައިވެއެވެ. ”ކަޅުދިރިއަކީ ސާއްމު ފިޔަވައި ކޮންމެ ބައްޔަކަށް ޝިފާއެކެވެ.“ އަދި ސާއްމަކީ މަރެވެ.', ''], ['مسند أحمد 9625', 'حَدَّثَنَا يَحْيَى [وَهُوَ] ابْنُ سَعِيدٍ، [قَالَ] حَدَّثَنَا مُحَمَّدُ بْنُ عَمْرٍو، [قَالَ حَدَّثَنَا] أَبُو سَلَمَةَ، عَنْ أَبِي هُرَيْرَةَ، قَالَ: قَالَ رَسُولُ اللهِ ﷺ: عَلَيْكُمْ بِهَذِهِ الْحَبَّةِ السَّوْدَاءِ فَإِنَّ فِيهَا شِفَاءً مِنْ كُلِّ دَاءٍ إِلَّا السَّامَ، قِيلَ: يَا رَسُولَ اللهِ، وَمَا السَّامُ؟ قَالَ: الْمَوْتُ.', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ މައްޗަށް އޮތީ މި ކަޅުދިރި ބޭނުން ކުރުމެވެ. އެހެނީ ހަމަކަށަވަރުން އޭގައި ސާއްމު ފިޔަވާ ކޮންމެ ބައްޔަކަށް ޝިފާވެއެވެ.“ ނަބިއްޔާއަށް ދެންނެވުނެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. ސާއްމަކީ ކޮބައިތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”މަރެވެ.“', ''], ['مسند أحمد 9626', 'حَدَّثَنَا يَزِيدُ بْنُ هَارُونَ، وَيَعْلَى، قَالَا: حَدَّثَنَا مُحَمَّدُ بْنُ عَمْرٍو مِثْلَهُ فِي الْحَبَّةِ السَّوْدَاءِ.', 'ކުރީގެ ޙަދީޘާ އެއްގޮތަކަށް ކަޅުދިރިއާ ބެހޭ ގޮތުން ރިވާވެފައިވެއެވެ.', ''], ['مسند أحمد 10132', 'حَدَّثَنَا مُحَمَّدُ بْنُ جَعْفَرٍ، قَالَ: حَدَّثَنَا شُعْبَةُ، عَنْ قَتَادَةَ، قَالَ: سَمِعْتُ هِلَالًا الْمُزَنِيَّ أَوِ الْمَازِنِيَّ يُحَدِّثُ عَنْ أَبِي هُرَيْرَةَ: عَنِ النَّبِيِّ ﷺ أَنَّهُ قَالَ: هَذِهِ الْحَبَّةُ السَّوْدَاءُ دَوَاءٌ قَالَ شُعْبَةُ أَوْ قَالَ: شِفَاءٌ مِنْ كُلِّ شَيْءٍ إِلَّا السَّامَ. قَالَ قَتَادَةُ: وَالسَّامُ الْمَوْتُ.', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”މި ކަޅުދިރއަކީ ސާއްމު ފިޔަވާ ކޮންމެ ކަމަކަށްވާ ޝިފާއެކެވެ.“ ޤަތާދާ ވިދާޅުވިއެވެ: ސާއްމަކީ މަރެވެ.', ''], ['مسند أحمد 10133', 'حَدَّثَنَا حَجَّاجٌ، قَالَ: سَمِعْتُ شُعْبَةَ يُحَدِّثُ عَنْ قَتَادَةَ، عَنْ هِلَالِ بْنِ يَزِيدَ أَنَّهُ سَمِعَ أَبَا هُرَيْرَةَ [يَقُولُ] عَنِ النَّبِيِّ ﷺ: إِنَّ هَذِهِ الْحَبَّةَ السَّوْدَاءَ شِفَاءٌ مِنْ كُلِّ دَاءٍ إِلَّا السَّامَ. قَالَ شُعْبَةُ: فَقُلْتُ لِقَتَادَةَ مَا السَّامُ؟ قَالَ: الْمَوْتُ.', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން މި ކަޅުދިރއަކީ ސާއްމު ފިޔަވާ ކޮންމެ ބައްޔަކަށްވާ ޝިފާއެކެވެ.“ ޝުޢުބާ ވިދާޅުވިއެވެ: ތިމަންނަ ޤަތާދާ ގާތު ދެންނެވީމެވެ: ސާއްމަކީ ކޮބައިތޯއެވެ؟ ޤަތާދާ ދެންނެވިއެވެ: މަރެވެ.', ''], ['مسند أحمد 10135', 'حَدَّثَنَا حُسَيْنٌ، قَالَ: حَدَّثَنَا شُعْبَةُ، قَالَ: أَنْبَأَنِي قَتَادَةُ، قَالَ: سَمِعْتُ هِلَالَ بْنَ يَزِيدَ مِنْ بَنِي مَازِنِ بْنِ شَيْبَانَ قَالَ: سَمِعْتُ أَبَا هُرَيْرَةَ يَقُولُ: عَنِ النَّبِيِّ ﷺ: إِنَّ هَذِهِ الْحَبَّةَ السَّوْدَاءَ شِفَاءٌ مِنْ كُلِّ شَيْءٍ لَيْسَ السَّامَ. وَقَالَ قَتَادَةُ: السَّامُ الْمَوْتُ.', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން މި ކަޅުދިރއަކީ ސާއްމު ފިޔަވާ ކޮންމެ ކަމަކަށްވާ ޝިފާއެކެވެ.“ ޤަތާދާ ވިދާޅުވިއެވެ: ސާއްމަކީ މަރެވެ.', ''], ['مسند أحمد 10371', 'حَدَّثَنَا عَبْدُ الرَّحْمَنِ، عَنْ زُهَيْرٍ، عَنِ الْعَلَاءِ، عَنْ أَبِيهِ، عَنْ أَبِي هُرَيْرَةَ، عَنِ النَّبِيِّ ﷺ قَالَ: مَا مِنْ دَاءٍ إِلَّا فِي الْحَبَّةِ السَّوْدَاءِ مِنْهُ شِفَاءٌ إِلَّا السَّامَ.', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ކަޅުދިރީގައި އެ ބައްޔަކަށް ޝިފާ ނުވާ އެއްވެސް ބައްޔެއް ނުވެއެވެ. އެއީ މަރު ފިޔަވައެވެ.“', ''], ['مسند أحمد 10641', 'حَدَّثَنَا يَزِيدُ، أَخْبَرَنَا مُحَمَّدٌ، عَنْ أَبِي سَلَمَةَ، عَنْ أَبِي هُرَيْرَةَ، قَالَ: قَالَ رَسُولُ اللهِ ﷺ: فِي هَذِهِ الْحَبَّةِ السَّوْدَاءِ شِفَاءٌ مِنْ كُلِّ دَاءٍ إِلَّا السَّامَ قَالُوا: يَا رَسُولَ اللهِ، وَمَا السَّامُ؟ قَالَ: الْمَوْتُ.', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”މި ކަޅުދިރީގައި ސާއްމު ފިޔަވާ ކޮންމެ ބައްޔަކަށް ޝިފާވެއެވެ.“ މީސްތަކުން ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. ސާއްމަކީ ކޮބައިތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”މަރެވެ.“', ''], ['مسند أحمد 10718', 'حَدَّثَنَا رَوْحٌ، حَدَّثَنَا مُحَمَّدُ بْنُ أَبِي حَفْصَةَ، قَالَ: حَدَّثَ ابْنُ شِهَابٍ، عَنْ سَعِيدِ بْنِ الْمُسَيِّبِ، عَنْ أَبِي هُرَيْرَةَ، قَالَ: قَالَ رَسُولُ اللهِ ﷺ: عَلَيْكُمْ بِهَذِهِ الْحَبَّةِ السَّوْدَاءِ فَإِنَّهَا شِفَاءٌ مِنْ كُلِّ شَيْءٍ إِلَّا مِنَ السَّامِ قَالَ: قَالَ ابْنُ شِهَابٍ الْمَوْتُ.', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ މައްޗަށް އޮތީ މި ކަޅުދިރި ބޭނުން ކުރުމެވެ. އެހެނީ ހަމަކަށަވަރުން އޭގައި ސާއްމު ފިޔަވާ ކޮންމެ ކަމަކަށް ޝިފާވެއެވެ.“ އިބްނު ޝިހާބު ވިދާޅުވިއެވެ: (ސާއްމަކީ) މަރެވެ.', ''], ['مسند أحمد 11045', 'حَدَّثَنَا هَاشِمٌ، حَدَّثَنَا شُعْبَةُ قَالَ قَتَادَةُ: أَنْبَأَنِي قَالَ: سَمِعْتُ هِلَالَ بْنَ زَيْدٍ - رَجُلًا مِنْ بَنِي مَازِنِ بْنِ شَيْبَانَ - قَالَ: سَمِعْتُ أَبَا هُرَيْرَةَ يَقُولُ عَنِ النَّبِيِّ ﷺ إِنَّ هَذِهِ الْحَبَّةَ السَّوْدَاءَ يَعْنِي الشُّونِيزَ شِفَاءٌ مِنْ كُلِّ شَيْءٍ لَيْسَ السَّامَ. قَالَ قَتَادَةُ: وَالسَّامُ الْمَوْتُ.', 'އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން މި ކަޅުދިރއަކީ (އެބަހީ: ޝޫނީޒަކީ) ސާއްމު ފިޔަވާ ކޮންމެ ކަމަކަށްވާ ޝިފާއެކެވެ.“ ޤަތާދާ ވިދާޅުވިއެވެ: ސާއްމަކީ މަރެވެ.', ''], ['مسند أحمد 23347', 'حَدَّثَنَا أَسْوَدُ بْنُ عَامِرٍ، حَدَّثَنَا زُهَيْرٌ، عَنْ وَاصِلِ بْنِ حَيَّانَ الْبَجَلِيِّ، حَدَّثَنِي عَبْدُ اللهِ بْنُ بُرَيْدَةَ، عَنْ أَبِيهِ، عَنِ النَّبِيِّ ﷺ قَالَ: الْكَمْأَةُ دَوَاءٌ لِلْعَيْنِ، وَإِنَّ الْعَجْوَةَ مِنْ فَاكِهَةِ الْجَنَّةِ، وَإِنَّ هَذِهِ الْحَبَّةَ السَّوْدَاءَ - قَالَ ابْنُ بُرَيْدَةَ: يَعْنِي: الشُّونِيزَ الَّذِي يَكُونُ فِي الْمِلْحِ - دَوَاءٌ مِنْ كُلِّ دَاءٍ إِلَّا الْمَوْتَ.', 'ބުރައިދާ ބިން އަލްޙުޞައިބު ރިވާ ކުރެއްވިއެވެ: ”ގުގުރިފިލާއަކީ އެސްފީނާއަށްވާ ފަރުވާއެކެވެ. އަދި ހަމަކަށަވަރުން ޢަޖުވާ ކަދުރަކީ ސުވަރުގޭގެ މޭވާއެކެވެ. އަދި ހަމަކަށަވަރުން މި ކަޅުދިރއަކީ މަރު ފިޔަވާ ކޮންމެ ކަމަކަށްވާ ޝިފާއެކެވެ.“ އިބްނު ބުރައިދާ ވިދާޅުވިއެވެ: އެބަހީ ޝޫނީޒެވެ. އެއީ ލޮނު ފެންފޮދަކާއެކު (މީހުން) އެއްކުރާ އެއްޗެވެ.', ''], ['مسند أحمد 23381', 'حَدَّثَنَا مُحَمَّدُ بْنُ عُبَيْدٍ، حَدَّثَنَا صَالِحٌ يَعْنِي: ابْنَ حَيَّانَ، عَنِ ابْنِ بُرَيْدَةَ، عَنْ أَبِيهِ أَنَّهُ كَانَ مَعَ رَسُولِ اللهِ ﷺ فِي اثْنَيْنِ وَأَرْبَعِينَ مِنْ أَصْحَابِهِ، وَالنَّبِيُّ ﷺ يُصَلِّي فِي الْمَقَامِ، وَهُمْ خَلْفَهُ جُلُوسٌ يَنْتَظِرُونَهُ، فَلَمَّا صَلَّى أَهْوَى، فِيمَا بَيْنَهُ، وَبَيْنَ الْكَعْبَةِ كَأَنَّهُ يُرِيدُ أَنْ يَأْخُذَ شَيْئًا، ثُمَّ انْصَرَفَ إِلَى أَصْحَابِهِ، فَثَارُوا، وَأَشَارَ إِلَيْهِمْ بِيَدِهِ أَنِ اجْلِسُوا، فَجَلَسُوا: فَقَالَ: رَأَيْتُمُونِي حِينَ فَرَغْتُ مِنْ صَلَاتِي أَهْوَيْتُ، فِيمَا بَيْنِي، وَبَيْنَ الْكَعْبَةِ كَأَنِّي أُرِيدُ أَنْ آخُذَ شَيْئًا؟ قَالُوا: نَعَمْ يَا رَسُولَ اللهِ، قَالَ: إِنَّ الْجَنَّةَ عُرِضَتْ عَلَيَّ، فَلَمْ أَرَ مِثْلَ مَا فِيهَا، وَإِنَّهَا مَرَّتْ بِي خَصْلَةٌ مِنْ عِنَبٍ، فَأَعْجَبَتْنِي، فَأَهْوَيْتُ إِلَيْهَا لِآخُذَهَا، فَسَبَقَتْنِي، وَلَوْ أَخَذْتُهَا لَغَرَسْتُهَا بَيْنَ ظَهْرَانِيكُمْ حَتَّى تَأْكُلُوا مِنْ فَاكِهَةِ الْجَنَّةِ، وَاعْلَمُوا أَنَّ الْكَمْأَةَ دَوَاءُ الْعَيْنِ، وَأَنَّ الْعَجْوَةَ مِنْ فَاكِهَةِ الْجَنَّةِ، وَأَنَّ هَذِهِ الْحَبَّةَ السَّوْدَاءَ الَّتِي تَكُونُ فِي الْمِلْحِ اعْلَمُوا أَنَّهَا دَوَاءٌ مِنْ كُلِّ دَاءٍ إِلَّا الْمَوْتَ.', 'ބުރައިދާ ބިން އަލްޙުޞައިބު ރިވާ ކުރެއްވިއެވެ: ސާޅީސްދެ ޞަޙާބީންނާއެކު ރަސޫލު ﷲ ﷺ ގެ އަރިހުގައި ބުރައިދާވެސް ހުއްޓެވެ. ނަބިއްޔާ ހުންނެވީ މަޤާމު އިބްރާހީމްގައި ނަމާދު ކުރައްވަންށެވެ. އަދި އެބައިމީހުން ނަބިއްޔާގެ ފަހަތްޕުޅުގައި އިށީނދެ ނަބިއްޔާއަށް އިންތިޒާރުކޮށްގެން ހުއްޓެވެ. ނަބިއްޔާ ނަމާދު ކުރެއްވުމުން، ނަބިއްޔާއާއި ކަޢުބާއާ ދެމެދުގައި ހުރި އެއްޗެއް ނަންގަވަން އުޅުއްވި ފަދަ ގޮތަކަށް އަތްޕުޅު ދިއްކޮށްލެއްވިއެވެ. ދެން ނަމާދު ނިންމަވައިގެން ނަބިއްޔާގެ ޞަޙާބީންނާ ދިމާލަށް ވަޑައިގަތުމުން އެބައިމީހުން ތެދުވިއެވެ. ނަބިއްޔާގެ އަތްޕުޅުން އެބައިމީހުން އިށީންނުނމަށް އިޝާރާތް ކުރެއްވުމުން އެބައިމީހުން އިށީންދެއްޖެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާގެ ނަމާދުން އަވަދިވުމުން، ތިމަން ނަބިއްޔާއާއި ކަޢުބާއާ ދެމެދުގައި ހުރި އެއްޗެއް ނަންގަވަން އުޅުއްވި ފަދަ ގޮތަކަށް އަތްޕުޅު ދިއްކޮށްލެއްވިތަން ތިޔަބައިމީހުން ދުށީމުތޯއެވެ؟“ އެބައިމީހުން ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. އާދޭހެވެ. ނަބިއްޔާ ޙާދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާއަށް ސުވަރުގެ ދެއްކެވުނެވެ. ފަހެ އޭގައި ހުރި އެއްޗަކާ އެއްފަދަ އެއްޗެއް ތިމަން ނަބިއްޔާ ދެކެވަޑައި ނުގަންނަވަމެވެ. އަދި ހަމަކަށަވަރުން މޭބިސްކަދުރު ކޯންޏެއް ތިމަން ނަބިއްޔާގެ ކުރިމަތިންލާފައި ދިޔައެވެ. އެ މޭބިސްކަދު ކޯނީ ތިމަން ނަބިއްޔާއަށް ކަމުދިޔައެވެ. ވީމާ އޭތި ނަންގަވަން އަތްޕުޅު ދިއްކޮށްލެއްވީމެވެ. އެހެނެއްކަމަކު ތިމަން ނަބިއްޔާގެ ގާތުންލާފައި އޭތި ހިނގައްޖެއެވެ. ތިމަން ނަބިއްޔާއަށް އޭތި ނެންގެވުނު ނަމަ، ތިޔަބައިމީހުން ސުވަރުގޭގެ މޭވާއިން ކެއުމަށްޓަކައި ތިޔަބައިމީހުންގެ ދެމެދުގައި އޭތި އިންދީމުހެވެ. އަދި ދަންނާށެވެ. ގުގުރިފިލާއަކީ އެސްފީނާއަށްވާ ފަރުވާއެކެވެ. އަދި ޢަޖުވާ ކަދުރަކީ ސުވަރުގޭގެ މޭވާއެކެވެ. އަދި (ލޮނު ފެންފޮދަކާއެކު މީހުން އެއްކުރާ) މި ކަޅުދިރއަކީ މަރު ފިޔަވާ ކޮންމެ ކަމަކަށްވާ ޝިފާއެއްކަން ދަންނާށެވެ.“', ''], ['مسند أحمد 23408', 'حَدَّثَنَا زَيْدٌ، حَدَّثَنِي حُسَيْنٌ، حَدَّثَنِي عَبْدُ اللهِ قَالَ: سَمِعْتُ أَبِي بُرَيْدَةَ يَقُولُ: سَمِعْتُ النَّبِيَّ ﷺ يَقُولُ: عَلَيْكُمْ بِهَذِهِ الْحَبَّةِ السَّوْدَاءِ (وَهِيَ الشُّونِيزُ) فَإِنَّ فِيهَا شِفَاءً.', 'އަބޫ ބުރައިދާ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ”ތިޔަބައިމީހުންގެ މައްޗަށް އޮތީ މި ކަޅުދިރި ބޭނުން ކުރުމެވެ. (އެއީ ޝޫނީޒެވެ.) އެހެނީ ހަމަކަށަވަރުން އޭގައި ޝިފާވެއެވެ.“', ''], ['مسند أحمد 25718', 'وَفِي الْحَبَّةِ السَّوْدَاءِ شِفَاءٌ مِنْ كُلِّ دَاءٍ إِلَّا السَّامَ. قَالُوا: يَا رَسُولَ اللهِ، وَمَا السَّامُ؟ قَالَ: الْمَوْتُ.', '( ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ:) ”އަދި ކަޅުދިރީގައި ސާއްމު ފިޔަވާ ކޮންމެ ބައްޔަކަށް ޝިފާވެއެވެ.“ މީސްތަކުން ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ސާއްމަކީ ކޮބައިތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”މަރެވެ.“', ''], ['سنن الترمذي 2336', 'حَدَّثَنَا أَحْمَدُ بْنُ مَنِيعٍ، قَالَ: حَدَّثَنَا الْحَسَنُ بْنُ سَوَّارٍ، قَالَ: حَدَّثَنَا لَيْثُ بْنُ سَعْدٍ، عَنْ مُعَاوِيَةَ بْنِ صَالِحٍ، أَنَّ عَبْدَ الرَّحْمَنِ بْنَ جُبَيْرِ بْنِ نُفَيْرٍ حَدَّثَهُ عَنْ أَبِيهِ، عَنْ كَعْبِ بْنِ عِيَاضٍ قَالَ: سَمِعْتُ النَّبِيَّ ﷺ يَقُولُ: إِنَّ لِكُلِّ أُمَّةٍ فِتْنَةً وَفِتْنَةُ أُمَّتِي الْمَالُ. هَذَا حَدِيثٌ حَسَنٌ صَحِيحٌ غَرِيبٌ، إِنَّمَا نَعْرِفُهُ مِنْ حَدِيثِ مُعَاوِيَةَ بْنِ صَالِحٍ.', 'ކަޢުބް ބިން ޢިޔާޟު ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ”ހަމަކަށަވަރުން ކޮންމެ އުންމަތްތަކަށް ފިތުނައެއް ވެއެވެ. އަދި ތިމަން ނަބިއްޔާގެ އުންމަތުގެ ފިތުނައަކީ މުދަލެވެ.“', ''], ['سنن الترمذي 2202', 'حَدَّثَنَا قُتَيْبَةُ، قَالَ: حَدَّثَنَا حَمَّادُ بْنُ زَيْدٍ، عَنْ أَيُّوبَ، عَنْ أَبِي قِلَابَةَ، عَنْ أَبِي أَسْمَاءَ، عَنْ ثَوْبَانَ، قَالَ: قَالَ رَسُولُ اللهِ ﷺ: إِذَا وُضِعَ السَّيْفُ فِي أُمَّتِي لَمْ يُرْفَعْ عَنْهَا إِلَى يَوْمِ الْقِيَامَةِ هَذَا حَدِيثٌ صَحِيحٌ..', 'ޘައުބާން ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާގެ އުންމަތުގައި ކަނޑި ދަމާލެވިއްޖެ ނަމަ، ޤިޔާމަތާ ހަމައަށްވެސް އެ ނުއުފުލޭނެއެވެ.“', ''], ['سنن الترمذي 3584', 'حَدَّثَنَا نَصْرُ بْنُ عَلِيٍّ الْجَهْضَمِيُّ، قَالَ: أَخْبَرَنِي أَبِي، عَنِ الْمُثَنَّى بْنِ سَعِيدٍ، عَنْ قَتَادَةَ، عَنْ أَنَسٍ قَالَ: كَانَ النَّبِيُّ ﷺ إِذَا غَزَا قَالَ: اللَّهُمَّ أَنْتَ عَضُدِي وَأَنْتَ نَصِيرِي وَبِكَ أُقَاتِلُ. هَذَا حَدِيثٌ حَسَنٌ غَرِيبٌ.', 'އަނަސް ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ހަނގުރާމަ ކުރައްވާ ނަމަ ޙަދީޘް ކުރައްވައެވެ: ”އޭ ﷲ އެވެ. އިބަ އިލާހީ މި އަޅާގެ ވާގިވެރިޔާއެވެ. އަދި އިބަ އިލާހީ މި އަޅާގެ މަދަދުވެރިޔާއެވެ. އަދި މި އަޅާ ހަނގުރާމަ ކުރަނީ ވެސް އިބަ އިލާހުގެ ވާގިފުޅުންނެވެ.“', ''], ['سنن الترمذي 3546', 'حَدَّثَنَا يَحْيَى بْنُ مُوسَى، وَزِيَادُ بْنُ أَيُّوبَ قَالَا: حَدَّثَنَا أَبُو عَامِرٍ الْعَقَدِيُّ، عَنْ سُلَيْمَانَ بْنِ بِلَالٍ، عَنْ عُمَارَةَ بْنِ غَزِيَّةَ، عَنْ عَبْدِ اللهِ بْنِ عَلِيِّ بْنِ حُسَيْنِ بْنِ عَلِيِّ بْنِ أَبِي طَالِبٍ، عَنْ أَبِيهِ، عَنْ حُسَيْنِ بْنِ عَلِيِّ بْنِ أَبِي طَالِبٍ، قَالَ: قَالَ رَسُولُ اللهِ ﷺ: الْبَخِيلُ الَّذِي مَنْ ذُكِرْتُ عِنْدَهُ، فَلَمْ يُصَلِّ عَلَيَّ.', 'ޙުސައިން ބިން ޢަލީ ބިން އަބޫ ޠާލިބު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ދަހިވެތި މީހަކީ، ތިމަން ނަބިއްޔާގެ ނަންފުޅު އެ މީހެއްގެ ގާތުގައި ބުނެވޭ ހިނދު، ތިމަން ނަބިއްޔާއަށް ޞަލަވާތް ނުކިޔަވާ މީހާއެވެ.“', ''], ['سنن النسائي 4456', 'أَخْبَرَنَا عَمْرُو بْنُ عَلِيٍّ، قَالَ: أَنْبَأَنَا وَهْبُ بْنُ جَرِيرٍ، قَالَ: حَدَّثَنِي أَبِي، عَنْ يُونُسَ، عَنِ الْحَسَنِ، عَنْ عَمْرِو بْنِ تَغْلِبَ، قَالَ: قَالَ رَسُولُ اللهِ ﷺ: إِنَّ مِنْ أَشْرَاطِ السَّاعَةِ أَنْ يَفْشُوَ الْمَالُ وَيَكْثُرَ، وَتَفْشُوَ التِّجَارَةُ، وَيَظْهَرَ الْعِلْمُ، وَيَبِيعَ الرَّجُلُ الْبَيْعَ، فَيَقُولَ: لَا، حَتَّى أَسْتَأْمِرَ تَاجِرَ بَنِي فُلَانٍ، وَيُلْتَمَسَ فِي الْحَيِّ الْعَظِيمِ الْكَاتِبُ فَلَا يُوجَدُ.', 'ޢަމްރު ބިން ތަޢްލިބު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ޤިޔާމަތުގެ ޢަލާމާތްތަކު ތެރޭގައި (މި ކަންކަން) ހިމެނެއެވެ: މުދާ ގިނަވެ އާންމުވުމާއި، ވިޔަފާރި އާންމުވުމާއި، ޢިލްމު ނެތިދިއުމާއި، އަދި މީހަކު އެއްޗެއް ވިއްކަން އުޅުމުން ދެންނެވޭނެއެވެ: ނޫނެކެވެ. މިވެނި ޤަބީލާއެއްގެ ވިޔަފާރިވެރިއެއް ގާތު އަހާ އޮޅުންފިލުވުމަށް ދާންދެން މަޑުކުރާށޭވެ. އަދި ބޮޑު އަވަށެއްގައި ލިޔާނެ މީހަކު ހޯދަން އުޅުމުން ފެނިގެން ނުދާނެއެވެ.“', ''], ['سنن أبي داود 1537', 'حَدَّثَنَا مُحَمَّدُ بْنُ الْمُثَنَّى، نَا مُعَاذُ بْنُ هِشَامٍ، حَدَّثَنِي أَبِي، عَنْ قَتَادَةَ، عَنْ أَبِي بُرْدَةَ بْنِ عَبْدِ اللهِ: أَنَّ أَبَاهُ حَدَّثَهُ، أَنَّ النَّبِيَّ ﷺ كَانَ إِذَا خَافَ قَوْمًا قَالَ: اللَّهُمَّ إِنَّا نَجْعَلُكَ فِي نُحُورِهِمْ، وَنَعُوذُ بِكَ مِنْ شُرُورِهِمْ.', 'ޢަބްދު ﷲ ބިން ޤައިސް ރިވާ ކުރެއްވިއެވެ: ބަޔަކު ދެކެ ނަބިއްޔާ ﷺ ބިރުފުޅުގަންނަ ނަމަ، (މިފަދައިން) ޙަދީޘް ކުރައްވައެވެ: ”އޭ ﷲ އެވެ. ހަމަކަށަވަރުން އަޅަމެން (ދިފާއު ކުރުމަށްޓަކައި) އެ މީހުންނާ (އެބަހީ: ދުޝްމަނުންނާ) ދިމާލަށް އިބަ އިލާހު ކުރިމަތި ކުރަމުއެވެ. އަދި އެބައިމީހުންގެ ނުބައި ކަމުން އަޅަމެން ރައްކާތެރި ކޮށްދެއްވުން އެދި އިބަ އިލާހުގެ ޙަޟްރަތުގައި ދަންނަވަމެވެ.“', ''], ['سنن أبي داود 4212', 'حَدَّثَنَا أَبُو تَوْبَةَ، نَا عُبَيْدُ اللهِ، عَنْ عَبْدِ الْكَرِيمِ الْجَزَرِيِّ، عَنْ سَعِيدِ بْنِ جُبَيْرٍ، عَنِ ابْنِ عَبَّاسٍ قَالَ: قَالَ رَسُولُ اللهِ ﷺ: يَكُونُ قَوْمٌ يَخْضِبُونَ فِي آخِرِ الزَّمَانِ بِالسَّوَادِ كَحَوَاصِلِ الْحَمَامِ، لَا يَرِيحُونَ رَائِحَةَ الْجَنَّةِ.', 'އިބްނު ޢައްބާސް ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ” ކޮތަރެއްގެ ދަށްބަނޑު ފަދައިން، ކަޅުކުލައިން (ނުރައިގައި) ކުލަޖައްސާ ބަޔަކު ފަހު ޒަމާނުގައި ވާނެއެވެ. އެބައިމީހުންނަށް ސުވަރުގޭގެ ވަސްވެސް ނުލިބޭނެއެވެ.“', ''], ['مصنف عبد الرزاق 5301', 'عَبْدُ الرَّزَّاقِ، عَنْ مَعْمَرٍ، عَنِ الزُّهْرِيِّ قَالَ: أَخْبَرَنِي مَنْ لَا أَتَّهِمُ، عَنْ أَصْحَابِ النَّبِيِّ ﷺ أَنَّهُمْ سَمِعُوا رَسُولَ اللهِ ﷺ فِي يَوْمِ جُمُعَةٍ مِنَ الْجُمَعِ، وَهُوَ عَلَى الْمِنْبَرِ يَقُولُ: يَا مَعْشَرَ الْمُسْلِمِينَ، إِنَّ هَذَا يَوْمٌ جَعَلَهُ اللهُ عِيدًا لِلْمُسْلِمِينَ فَاغْتَسِلُوا فِيهِ مِنَ الْمَاءِ، وَمَنْ كَانَ عِنْدَهُ طِيبٌ فَلَا يَضُرُّهُ أَنْ يَمَسَّ مِنْهُ، وَعَلَيْكُمْ بِهَذَا السِّوَاكِ.', 'ނަބިއްޔާގެ ޞަޙާބީންގެ އަރިހުން ރިވާ ކުރެވުނެވެ: ހުކުރު ދުވަހެއްގައި މިސްކިތުގެ މިންބަރު މަތީގައި ހުންނަވައިގެން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް އެބައިމީހުންނަށް އަޑުއިވުނެވެ: ”އޭ މުސްލިމުންގެ ޖަމާޢަތުގެ މީހުންނޭވެ. ހަމަކަށަވަރުން މި ދުވަހަކީ މުސްލިމުންނަށް ޓަކައި ﷲ ލެއްވި ޢީދު ދުވަހެވެ. ފަހެ މި ދުވަހުގައި ތިޔަބައިމީހުން ފެނުން ހިނައި ތާހިރުވެގަންނާށެވެ. އަދި އެ މީހެއްގެ އަތުގައި ހުވަނދު ހުރި މީހަކު ފަހެ އެ ލުމަކުން އޭނާއަށް ވާނޭ ގެއްލުމެއް ނެތެވެ. އަދި ތިޔަބައިމީހުންގެ މައްޗަށް އޮތީ މި ސިވާކުން ދަތް އުނގުޅުމެވެ.“', '']]
