let umdah_dataSet = [["#001","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","عَنْ عُمَرَ بْنِ الْخَطَّابِ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: سَمِعْتُ رَسُولَ اللَّهِ ﷺ يَقُولُ: «إنَّمَا الأَعْمَالُ بِالنِّيَّاتِ - وَفِي رِوَايَةٍ: بِالنِّيَّةِ - وَإِنَّمَا لِكُلِّ امْرِئٍ مَا نَوَى، فَمَنْ كَانَتْ هِجْرَتُهُ إلَى اللَّهِ وَرَسُولِهِ، فَهِجْرَتُهُ إلَى اللَّهِ وَرَسُولِهِ، وَمَنْ كَانَتْ هِجْرَتُهُ إلَى دُنْيَا يُصِيبُهَا أَوْ امْرَأَةٍ يَتَزَوَّجُهَا، فَهِجْرَتُهُ إلَى مَا هَاجَرَ إلَيْهِ»","ޢުމަރު ބިން އަލްޚައްޠާބު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ: ”ނިޔަތްތަކުގެ މައްޗަށް މެނުވީ ޢަމަލުތައް ބިނާ ވެގެން ނުވެއެވެ. އަދި ކޮންމެ މީހަކަށް ވެސް ވަނީ، ހަމައެކަނި އެ މީހަކު ނިޔަތް ގަތް ކަމެކެވެ. ވީމާ، މީހަކު ހިޖުރަ ކުރީ ﷲ އާއި އެ އިލާހުގެ ރަސޫލާއަށްޓަކައި ނަމަ، ފަހެ އޭނާގެ ހިޖުރަ ވަނީ ﷲ އާއި އެ އިލާހުގެ ރަސޫލާއަށެވެ. އަދި މީހަކު ހިޖުރަ ކުރީ ދުނިޔެ ހޯދުމަށް ނުވަތަ އަންހެނަކާ އިނުމަށް ކަމުގައި ވަނީ ނަމަ، ފަހެ އެ މީހެއްގެ ހިޖުރަ ވަނީ، އޭނާ އެ ހިޖުރަ ކުރި ކަމަކަށެވެ.“","البخاري 6689، ومسلم 1907"],["#002","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «لا يَقْبَلُ اللَّهُ صَلاةَ أَحَدِكُمْ إذَا أَحْدَثَ حَتَّى يَتَوَضَّأَ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ ތެރެއިން މީހަކު ހަދަސްވެރިވެއްޖެ ނަމަ، ވުޟޫކޮށްފުމަށް ދާންދެން ﷲ އޭނާގެ ނަމާދު ގަބޫލު ނުކުރައްވާނެއެވެ.“","البخاري 6954 واللفظ له، ومسلم 225"],["#003","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عَمْرِو بْنِ الْعَاصِ وَأَبِي هُرَيْرَةَ وَعَائِشَةَ رَضِيَ اللَّهُ عَنْهُم قَالُوا: قَالَ رَسُولُ اللَّهِ ﷺ: «وَيْلٌ لِلأَعْقَابِ مِنَ النَّارِ»","ޢަބްދު ﷲ ބިން ޢަމްރު ބިން އަލްޢާޞް، އަބޫ ހުރައިރާ އަދި ޢާއިޝާ رَضِيَ اللَّهُ عَنْهُمْ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ނަރަކައިން ފުންނާބުތަކަށް ހަލާކު ހުއްޓެވެ.“ (އެބަހީ: ވުޟޫ ކުރާއިރު ރަނގަޅަށް ނުދޮވުމުގެ ސަބަބުން، ފަރުވާކުޑަ ކުރާ މީހުންނަށް އަޒާބު ލިބޭނެކަމެވެ.)","البخاري 60، 165، ومسلم 241، 242، 240"],["#004","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ رَسُولَ اللَّهِ ﷺ قال: «إذَا تَوَضَّأَ أَحَدُكُمْ فَلْيَجْعَلْ فِي أَنْفِهِ مَاءً، ثُمَّ لِيَنْتَثِرْ، وَمَنْ اسْتَجْمَرَ فَلْيُوتِرْ، وَإِذَا اسْتَيْقَظَ أَحَدُكُمْ مِنْ نَوْمِهِ فَلْيَغْسِلْ يَدَيْهِ قَبْلَ أَنْ يُدْخِلَهُمَا فِي الإِنَاءِ ثَلاثاً، فَإِنَّ أَحَدَكُمْ لا يَدْرِي أَيْنَ بَاتَتْ يَدُهُ» \n<br><br>\nوَفِي لَفْظٍ لِمُسْلِمٍ: «فَلْيَسْتَنْشِقْ بِمِنْخَرَيْهِ مِنَ الْمَاءِ» \n<br><br>\nوَفِي لَفْظٍ: «مَنْ تَوَضَّأَ فَلْيَسْتَنْشِقْ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ ތެރެއިން މީހަކު ވުޟޫ ކުރާ ނަމަ، އޭނާގެ ނޭފަތަށް ފެންލާށެވެ. އެއަށްފަހު ނޭފަތުން ފެން ބޭރު ކުރާށެވެ. އަދި މީހަކު އިސްތިޖްމާރު ކުރާ ނަމަ، އޮނަހިރި އަދަދަކުން ފޮހޭށެވެ. (އެބަހީ: ތިނަކުން ފަށައިގެން އެއަށްވުރެ މަތީގެ އަދަދަކުންނެވެ.) އަދި ތިޔަބައިމީހުންގެ ތެރެއިން މީހަކު ނިދިން ހޭލައިފި ނަމަ، ފެން ކަންވާރަކަށް އޭނާ އަތްލުމުގެ ކުރިން، ތިން ފަހަރު ދެ އަތް ދޮންނާށެވެ. އެހެނީ ހަމަކަށަވަރުން ތިޔަބައިމީހުން ކުރެ މީހަކު ނިދާފަ އޮންނަ އިރު އޭނާގެ އަތް އޮތް ތަނެއް ނޭނގޭނެއެވެ.“ \n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ”ނޭފަތުގެ ދެ ހިނދުރިއަށް ފެން ވައްދާށެވެ.“ \n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”ވުޟޫ ކުރާ މީހަކު ނޭފަތަށް (ފެންލައި ދެމުމަށް ފަހު) ފެން ބޭރު ކުރާށެވެ.“","البخاري 162، ومسلم 237، 278\n<br><br>\nمسلم 237\n<br><br>\nالبخاري 161، ومسلم 237"],["#005","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «لا يَبُولَنَّ أَحَدُكُمْ فِي الْمَاءِ الدَّائِمِ الَّذِي لا يَجْرِي، ثُمَّ يَغْتَسِلُ مِنْهُ» \n<br><br>\nوَلِمُسْلِمٍ: «لا يَغْتَسِلُ أَحَدُكُمْ فِي الْمَاءِ الدَّائِمِ وَهُوَ جُنُبٌ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ ތެރެއިން މީހަކު، އެއް ތަނެއްގައި ގަރާރުވެފައި ހުންނަ ނުހިނގާ ފެންގަނޑަކަށް ކުޑަކަމުނުދާށެވެ. (އަދި އެއަށް ކުޑަކަމުދިއުމަށް ފަހު) ދެން އެއަށް (އެރިގެން) ފެން ނުވަރާށެވެ.“ \n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ”ތިޔަބައިމީހުންގެ ތެރެއިން މީހަކު ޖުނުބުވެރިކަން މަތީ ހުރެ، އެއް ތަނެއްގައި ގަރާރުވެފައި ހުންނަ ނުހިނގާ ފެންގަނޑަކަށް (އެރިގެން) ފެންނުވަރާށެވެ.“","البخاري 239 واللفظ له، ومسلم 282\n<br><br>\nمسلم 283"],["#006","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ -، أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «إذَا شَرِبَ الْكَلْبُ فِي إنَاءِ أَحَدِكُمْ فَلْيَغْسِلْهُ سَبْعاً» \n<br><br>\nوَلِمُسْلِمٍ: «أُولاهُنَّ بِالتُّرَابِ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ ތެރެއިން މީހެއްގެ ކަންވާރަކުން ކުއްތާއެއް ފެން ބޮއެފި ނަމަ، ފަހެ ހަތް ފަހަރު އެ ކަންވާރު ދޮންނާށެވެ.“ \n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ”އޭގެ ފުރަތަމަ ފަހަރު (ދޮންނާނީ) ވެލިންނެވެ.“","البخاري 172 واللفظ له، ومسلم 279\n<br><br>\nمسلم 279"],["#007","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","وَلَهُ فِي حَدِيثِ عَبْدِ اللَّهِ بْنِ مُغَفَّلٍ: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «إذَا وَلَغَ الْكَلْبُ فِي الإِناءِ فَاغْسِلُوهُ سَبْعاً وَعَفِّرُوهُ الثَّامِنَةَ بِالتُّرَابِ»","އަދި މުސްލިމުގެ ރިވާޔަތެއްގައި، ޢަބްދު ﷲ ބިން މުޣައްފަލު رَضِيَ اللَّهُ عَنْهُ ގެ ޙަދީޘްގައި ވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ކަންވާރެއްގައި ކުއްތާއެއް ދޫލައިފި ނަމަ، ހަތް ފަހަރު އެ ކަންވާރު ދޮންނާށެވެ. އަދި އަށްވަނަ ފަހަރު ވެލިން އުނގުޅާށެވެ.“\n","مسلم 280"],["#008","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","عَنْ حُمْرَانَ مَوْلَى عُثْمَانَ بْنِ عَفَّانَ رَضِيَ اللَّهُ عَنْهُمَا: «أَنَّهُ رَأَى عُثْمَانَ دَعَا بِوَضُوءٍ، فَأَفْرَغَ عَلَى يَدَيْهِ مِنْ إنَائِهِ، فَغَسَلَهُمَا ثَلاثَ مَرَّاتٍ، ثُمَّ أَدْخَلَ يَمِينَهُ فِي الْوَضُوءِ، ثُمَّ تَمَضْمَضَ وَاسْتَنْشَقَ وَاسْتَنْثَرَ، ثُمَّ غَسَلَ وَجْهَهُ ثَلاثاً، وَيَدَيْهِ إلَى الْمِرْفَقَيْنِ ثَلاثًا، ثُمَّ مَسَحَ بِرَأْسِهِ، ثُمَّ غَسَلَ كِلْتَا رِجْلَيْهِ ثَلاثًا، ثُمَّ قَالَ: رَأَيْتُ النَّبِيَّ ﷺ يَتَوَضَّأُ نَحْوَ وُضُوئِي هَذَا، وَقَالَ: «مَنْ تَوَضَّأَ نَحْوَ وُضُوئِي هَذَا، ثُمَّ صَلَّى رَكْعَتَيْنِ، لا يُحَدِّثُ فِيهِمَا نَفْسَهُ غُفِرَ لَهُ مَا تَقَدَّمَ مِنْ ذَنْبِهِ»","ޢުޘްމާނު ބިން ޢައްފާނު رَضِيَ اللَّهُ عَنْهُ މިނިވަން ކުރެއްވި އަޅެއް ކަމުގައިވާ ޙުމްރާނު ރިވާ ކުރެއްވިއެވެ: ޢުޘްމާނު (ވުޟޫ ކުރެއްވުމަށް ޓަކައި) ފެން ގެނައުމަށް އެންގެވިތަން ޙުމްރާނަށް ފެނުނެވެ. ދެން އެކަލޭގެފާނުގެ ކަންވާރުން އަތަށް ފެން އަޅުއްވައި، ތިން ފަހަރު ދެ އަތް ދޮންނެވިއެވެ. އެއަށްފަހު ފެން ކަންވާރުގެ ތެރެއަށް ކަނާތް ލައްވާ، އަނގައަށް ފެން ލައްވައި ކޯ ދޮންނެވިއެވެ. އަދި ނޭފަތަށް ފެން ލައްވައި ބޭރު ކުރެއްވިއެވެ. ދެން ތިން ފަހަރު މޫނު ދޮންނެވިއެވެ. އަދި އުޅަނބޮށްޓާ ހަމައަށް ތިން ފަހަރު ދެ އަތް ދޮންނެވިއެވެ. އަދި ބޮލުގައި ފެން ފުއްސެވުމަށް ފަހު، ތިން ފަހަރު ދެ ފައި ދޮންނެވިއެވެ. އެއަށްފަހު ވިދާޅުވިއެވެ: ތިމަންނަ މި ވުޟޫ ކުރެއްވި ގޮތަށް ނަބިއްޔާ ﷺ ވުޟޫ ކުރައްވަނިކޮށް ތިމަން ދުށީމެވެ. އަދި ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާ ވުޟޫ ކުރެއްވި ގޮތަށް މީހަކު ވުޟޫ ކުރުމަށް ފަހު، ދެ ރަކުޢަތުގެ ނަމާދެއްކޮށް، އެ ދެ ރަކުޢަތުގައި އޭނާގެ އަމިއްލަ ނަފުސާ ވާހަކަ ނުދައްކައިފި ނަމަ (އެބަހީ: ވަސްވާސްތަކަށް ސަމާލުކަން ނުދީ، ދުނިޔެވީ ކަންކަމާ މެދު ނުވިސްނާ، އިޚްލާޞްތެރިކަމާއެކު ނަމާދުކޮށްފި ނަމަ) ފަހެ އެ މީހަކަށް ކުރެވިފައިވާ ފާފަތައް، ﷲ އޭނާއަށްޓަކައި ފުއްސަވައި ދެއްވާނެއެވެ.“ ","البخاري 159، ومسلم 226"],["#009","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","عَنْ عَمْرِو بْنِ يَحْيَى الْمَازِنِيِّ عَنْ أَبِيهِ قَالَ: «شَهِدْتُ عَمْرَو بْنَ أَبِي حَسَنٍ سَأَلَ عَبْدَ اللَّهِ بْنَ زَيْدٍ عَنْ وُضُوءِ النَّبِيِّ ﷺ؟ فَدَعَا بِتَوْرٍ مِنْ مَاءٍ، فَتَوَضَّأَ لَهُمْ وُضُوءَ رَسُولِ اللَّهِ ﷺ فَأَكْفَأَ عَلَى يَدَيْهِ مِنْ التَّوْرِ، فَغَسَلَ يَدَيْهِ ثَلاثاً، ثُمَّ أَدْخَلَ يَدَهُ فِي التَّوْرِ، فَمَضْمَضَ وَاسْتَنْشَقَ وَاسْتَنْثَرَ ثَلاثاً بِثَلاثِ غَرْفَاتٍ، ثُمَّ أَدْخَلَ يَدَهُ فَغَسَلَ وَجْهَهُ ثَلاثاً، ثُمَّ أَدْخَلَ يَدَهُ فِي التَّوْرِ، فَغَسَلَهُمَا مَرَّتَيْنِ إلَى الْمِرْفَقَيْنِ ثُمَّ أَدْخَلَ يَدَهُ فِي التَّوْرِ، فَمَسَحَ رَأْسَهُ، فَأَقْبَلَ بِهِمَا وَأَدْبَرَ مَرَّةً وَاحِدَةً، ثُمَّ غَسَلَ رِجْلَيْهِ» \n<br><br>\nوَفِي رِوَايَةٍ: «بَدَأَ بِمُقَدَّمِ رَأْسِهِ، حَتَّى ذَهَبَ بِهِمَا إلَى قَفَاهُ، ثُمَّ رَدَّهُمَا حَتَّى رَجَعَ إلَى الْمَكَانِ الَّذِي بَدَأَ مِنْهُ» \n<br><br>\nوَفِي رِوَايَةٍ «أَتَانَا رَسُولُ اللَّهِ ﷺ فَأَخْرَجْنَا لَهُ مَاءً فِي تَوْرٍ مِنْ صُفْرٍ»","ޢަމްރު ބިން ޔަޙްޔާ އަލްމާޒިނީ، އެކަލޭގެފާނުގެ ބައްޕާފުޅު އަރިހުން ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ވުޟޫ ކުރައްވާ ގޮތާމެދު ޢަބްދު ﷲ ބިން ޒައިދުގެ އަރިހުގައި ޢަމްރު ބިން އަބުލް ޙަސަން ސުވާލު ކުރަނިކޮށް ތިމަން ދުށީމެވެ. ފަހެ ފެން ކަންވާރެއް ގެނައުމަށް ޢަބްދު ﷲ އަންގެވިއެވެ. އަދި އެބައިމީހުންނަށް (ދެއްކުމަށް ޓަކައި) ނަބިއްޔާ ﷺ ވުޟޫ ކުރެއްވި ގޮތަށް އެކަލޭގެފާނު ވުޟޫ ކުރެއްވިއެވެ. އެ ގޮތުން، ކަންވާރު އަރިކޮށްލައްވައިގެން އަތަށް ފެން އެޅުއްވުމަށް ފަހު، ތިން ފަހަރު ދެ އަތް ދޮންނެވިއެވެ. ދެން ކަންވާރުގެ ތެރެއަށް އަތް ލައްވަވާ، ތިން އަތްގޮށުން އަނގައަށް ފެން އަޅުއްވައި ތިން ފަހަރު ކޯ ދޮންނެވިއެވެ. އަދި ނޭފަތަށް ފެން ލައްވައި ބޭރު ކުރެއްވިއެވެ. ދެން އަނެއްކާ ކަންވާރުގެ ތެރެއަށް އަތް ލައްވަވާ ތިން ފަހަރު މޫނު ދޮންނެވިއެވެ. އެއަށްފަހު ކަންވާރުގެ ތެރެއަށް އަތް ލައްވަވާ އުޅަނބޮށްޓާ ހަމައަށް ދެ ފަހަރު އަތް ދޮންނެވިއެވެ. ދެން ކަންވާރުގެ ތެރެއަށް ދެ އަތް ލައްވަވާ، ބޮލުގެ ކުރިމަތިން ފަހަތަށާއި، ފަހަތުން ކުރިމައްޗަށް އެއްފަހަރު އަތުން ފެން ފުއްސެވިއެވެ. އެއަށްފަހު ދެ ފައިތިލަ ދޮންނެވިއެވެ. \n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: އެކަލޭގެފާނު ބޮލުގެ ކުރިމަތިން ފައްޓަވައިގެން ބޮލުގެ ފަހަތަށް ދާންދެން ފުއްސަވާފައި، ދެން އަނެއްކާ އަނބުރާ ފެށި ހިސާބަށް ދެ އަތް ގެންނެވިއެވެ. \n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ރަސޫލު ﷲ ﷺ ގެ އަރިހަށް ތިމަންމެން ދިޔައިމެވެ. އަދި ނަބިއްޔާއަށްޓަކައި ލޮއި ކަންވާރެއްގައި ފެން ގެނައީމެވެ.","البخاري 1، ومسلم 226\n<br><br>\nالبخاري 185، ومسلم 235\n<br><br>\nالبخاري 197"],["#010","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: «كَانَ رَسُولُ اللَّهِ ﷺ يُعْجِبُهُ التَّيَمُّنُ فِي تَنَعُّلِهِ، وَتَرَجُّلِهِ، وَطُهُورِهِ، وَفِي شَأْنِهِ كُلِّهِ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ފައިވާންޖޯޑުކޮޅު ތުރުކުރެއްވުމުގައްޔާއި، އިސްތަށިފުޅުގައި ފުނާ އެޅުއްވުމުގައްޔާއި، ތާހިރުވެވަޑައިގަތުމާ އަދި ހަމަ އެހެންމެ ހުރިހާ ކަމެއްގައި ވެސް ކަނާތް ފަރާތް އިސްކުރެއްވުމަށް ލޯބި ކުރެއްވިއެވެ.","البخاري 168، ومسلم 268"],["#011","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","عَنْ نُعَيْمٍ الْمُجْمِرِ عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - عَنْ النَّبِيِّ ﷺ أَنَّهُ قَالَ: «إنَّ أُمَّتِي يُدْعَوْنَ يَوْمَ القِيَامَةِ غُرّاً مُحَجَّلِينَ مِنْ آثَارِ الْوُضُوءِ» فَمَنْ اسْتَطَاعَ مِنْكُمْ أَنْ يُطِيلَ غُرَّتَهُ فَلْيَفْعَلْ. \n<br><br>\nوَفِي لَفْظٍ لِمُسْلِمٍ: «رَأَيْتُ أَبَا هُرَيْرَةَ يَتَوَضَّأُ، فَغَسَلَ وَجْهَهُ وَيَدَيْهِ حَتَّى كَادَ يَبْلُغُ الْمَنْكِبَيْنِ، ثُمَّ غَسَلَ رِجْلَيْهِ حَتَّى رَفَعَ إلَى السَّاقَيْنِ، ثُمَّ قَالَ: سَمِعْتُ رَسُولَ اللَّهِ ﷺ يَقُولُ: إنَّ أُمَّتِي يُدْعَوْنَ يَوْمَ الْقِيَامَةِ غُرَّاً مُحَجَّلِينَ مِنْ آثَارِ الْوُضُوءِ» فَمَنْ اسْتَطَاعَ مِنْكُمْ أَنْ يُطِيلَ غُرَّتَهُ وَتَحْجِيلَهُ فَلْيَفْعَلْ.","ނުޢައިމު އަލްމުޖްމިރު، އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ގެ އަރިހުން ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ޤިޔާމަތް ދުވަހު ތިމަން ނަބިއްޔާގެ އުންމަތަށް ގޮވާލެވޭނެއެވެ. އެބައިމީހުންގެ ނިތްކުރިތަކާއި ފައިތަކުގައި ވުޟޫގެ އަސަރުން ވިދާ ބަބުޅާ އަލިކަމެއް ވާނެއެވެ.“ (އަބޫ ހުރައިރާ ވިދާޅުވިއެވެ:) ވީމާ ތިޔަބައިމީހުންގެ ތެރެއިން އޭނާގެ އަލިކަން އިތުރު ކުރެވެން ހުރި މީހަކު، ފަހެ އެ ކަން ކުރާށެވެ. \n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: (ނުޢައިމު އަލްމުޖްމިރު ވިދާޅުވިއެވެ:) އަބޫ ހުރައިރާ ވުޟޫ ކުރައްވަނިކޮށް ތިމަން ދުށީމެވެ. އަބޫ ހުރައިރާގެ މޫނާއި ދެ އަތް ކޮނޑާ ހިސާބަށް ދަންދެން ދޮންނެވިއެވެ. އެއަށްފަހު ކަންކަށިމައްޗާ ހަމައަށް ދެ ފައި ދޮންނެވިއެވެ. ދެން އަބޫ ހުރައިރާ ވިދާޅުވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ: ”ހަމަކަށަވަރުން ޤިޔާމަތް ދުވަހު ތިމަން ނަބިއްޔާގެ އުންމަތަށް ގޮވާލެވޭނެއެވެ. އެބައިމީހުންގެ ނިތްކުރިތަކާއި ފައިތަކުގައި ވުޟޫގެ އަސަރުން ވިދާ ބަބުޅާ އަލިކަމެއް ވާނެއެވެ.“ (އަބޫ ހުރައިރާ ވިދާޅުވިއެވެ:) ވީމާ ތިޔަބައިމީހުންގެ ތެރެއިން އޭނާގެ ނިއްކުރީގެ އަލިކަން އިތުރު ކުރެވެން ހުރި މީހަކު، ފަހެ އެ ކަން ކުރާށެވެ.","البخاري 136\n<br><br>\nمسلم 236"],["#012","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","وَفِي لَفْظٍ لِمُسْلِمٍ: سَمِعْتُ خَلِيلِي ﷺ يَقُولُ: «تَبْلُغُ الْحِلْيَةُ مِنْ الْمُؤْمِنِ حَيْثُ يَبْلُغُ الْوُضُوءُ»","އަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: (އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ:) ތިމަންގެ މިތުރު (ނަބިއްޔާ) ﷺ ޙަދީޘް ކުރައްވަނިކޮށް އަޑު އެހީމެވެ: ”މުއުމިނާގެ ވުޟޫ ދިޔަ ހިސާބަކަށް އޭނާގެ ޒީނަތްތެރިކަން ފޯރާނެއެވެ.“","مسلم 250"],["#013","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","‌باب دخول الخلاء والاستطابة","ފާހާނައަށް ވަނުމައި ސާފުތާހިރުވުމުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ النَّبِيَّ ﷺ كَانَ إذَا دَخَلَ الْخَلاءَ قَالَ: «اللَّهُمَّ إنِّي أَعُوذُ بِكَ مِنْ الْخُبُثِ وَالْخَبَائِثِ»","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޤަޟާޙާޖާ ކުރާ ތަނަށް ވަދެވަޑައިގަންނަވާ ހިނދު ވިދާޅުވެއެވެ: ”ޔާ ﷲ އެވެ. ހަމަކަށަވަރުން ފިރިހެން ޝައިޠާނުންނާއި އަންހެން ޝައިޠާނުންގެ ކިބައިން މި އަޅާ ރައްކާތެރި ކޮށްދެއްވުން އެދި އިބަ އިލާހުގެ ޙަޟްރަތަށް ދަންނަވަމެވެ.“","البخاري 143، ومسلم 375"],["#014","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","‌باب دخول الخلاء والاستطابة","ފާހާނައަށް ވަނުމައި ސާފުތާހިރުވުމުގެ ބާބު","عَنْ أَبِي أَيُّوبَ الأَنْصَارِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «إذَا أَتَيْتُمْ الْغَائِطَ، فَلا تَسْتَقْبِلُوا الْقِبْلَةَ بِغَائِطٍ وَلا بَوْلٍ، وَلا تَسْتَدْبِرُوهَا، وَلَكِنْ شَرِّقُوا أَوْ غَرِّبُوا»\n<br><br>\nقَالَ أَبُو أَيُّوبَ: «فَقَدِمْنَا الشَّامَ، فَوَجَدْنَا مَرَاحِيضَ قَدْ بُنِيَتْ نَحْوَ الْكَعْبَةِ، فَنَنْحَرِفُ عَنْهَا، وَنَسْتَغْفِرُ اللَّهَ عَزَّ وَجَلَّ».","އަބޫ އައްޔޫބު އަލްއަންޞާރީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ޤަޟާޙާޖާ ކުރަން (ބޭރަށް) ހިނގައްޖެ ނަމަ، ބޮޑުކަމުދާންވެސް ކުޑަކަމުދާންވެސް ޤިބުލައާ ކުރިމަތި ނުލާށެވެ. އަދި ޤިބުލައަށް ފުރަގަސް ވެސް ނުދޭށެވެ. އެހެނެއްކަމަކު އިރުމައްޗަށް ނުވަތަ ހުޅަނގަށް އެނބުރޭށެވެ.“ \n<br><br>\nއަބޫ އައްޔޫބު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިއެވެ: ތިމަންމެން ޝާމަށް ދިޔައިމެވެ. ފަހެ (އެ ތަނުގައި) ކަޢުބާއާ ދިމާއަށް ފާހާނާތަކެއް ރާނާފައިވަނިކޮށް ތިމަންމެން ދުށީމެވެ. ދެން (އެ ފާހާނާތައް ބޭނުން ކުރި އިރު) ޤިބުލައާ ދިމާނުވާ ގޮތަށް ތިމަންމެން އެނބުރުނީމެވެ. އަދި (އެއަށްފަހު) ﷲ ޢައްޒަ ވަޖައްލަގެ ޙަޟްރަތުން ފާފަ ފުއްސެވުމަށް އެދި ދެންނެވީމެވެ.","البخاري 394، ومسلم 264"],["#015","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","‌باب دخول الخلاء والاستطابة","ފާހާނައަށް ވަނުމައި ސާފުތާހިރުވުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ بْنِ الْخَطَّابِ رَضِيَ اللَّهُ عَنْهُما قَالَ: «رَقِيْتُ يَوْماً عَلَى بَيْتِ حَفْصَةَ، فَرَأَيْتُ النَّبِيَّ ﷺ يَقْضِي حَاجَتَهُ مُسْتَقْبِلَ الشَّامَ، مُسْتَدْبِرَ الْكَعْبَةَ» \n<br><br>\nوَفِي رِوَايَةٍ «مُسْتَقْبِلاً بَيْتَ الْمَقْدِسِ»","ޢަބްދު ﷲ ބިން ޢުމަރު ބިން އަލްޚައްޠާބު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: އެއް ދުވަހަކު ޙަފްޞާގެ ގެކޮޅުގެ ފުރާޅު މައްޗަށް ތިމަން އެރީމެވެ. އެ ހިނދު ނަބިއްޔާ ﷺ ޝާމަށް ކުރިމަތިލައްވައި، އަދި ކަޢުބާއަށް ފުރަގަސްދެއްވާ ޙާލު ޤަޟާހާޖާ ކުރައްވަނިކޮށް ތިމަން ދުށީމެވެ. \n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ބައިތުލް މަޤްދިސަށް ކުރިމަތިލައްވައިގެން ހުންނެވިއެވެ.","البخاري 148، ومسلم 266"],["#016","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","‌باب دخول الخلاء والاستطابة","ފާހާނައަށް ވަނުމައި ސާފުތާހިރުވުމުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - أَنَّهُ قَالَ: «كَانَ رَسُولُ اللَّهِ ﷺ يَدْخُلُ الْخَلاءَ، فَأَحْمِلُ أَنَا وَغُلامٌ نَحْوِي إدَاوَةً مِنْ مَاءٍ وَعَنَزَةً، فَيَسْتَنْجِي بِالْمَاءِ»","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ (ގަޟާޙާޖާ ކުރެއްވުމަށް ޓަކައި) ވަދެވަޑައިގަންނަވާ ހިނދު، ތިމަންނައާއި ތިމަންގެ އުމުރުގެ އެހެން ކުއްޖަކު، ފެނުގެ ކަންވާރަކާއި، (މީހުންގެ ކިބައިން ނިވާވުމަށް ޓަކައި) އަސާކޮޅެއް (ނަބިއްޔާއަށް) ގެންގޮސްދީ އުޅުނީމެވެ. ދެން ނަބިއްޔާ ފެނުން އިސްތިންޖާ ކުރައްވައެވެ. (އެބަހީ: ދޮންނަވައެވެ.)","البخاري 152، ومسلم 271"],["#017","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","‌باب دخول الخلاء والاستطابة","ފާހާނައަށް ވަނުމައި ސާފުތާހިރުވުމުގެ ބާބު","عَنْ أَبِي قَتَادَةَ الْحَارِثِ بْنِ رِبْعِيٍّ الأَنْصَارِيِّ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ النَّبِيَّ ﷺ قَالَ: «لا يُمْسِكَنَّ أَحَدُكُمْ ذَكَرَهُ بِيَمِينِهِ وَهُوَ يَبُولُ وَلا يَتَمَسَّحْ مِنْ الْخَلاءِ بِيَمِينِهِ وَلا يَتَنَفَّسْ فِي الإِنَاءِ»","އަބޫ ޤަތާދާ އަލްޙާރިޘް ބިން ރިބްޢީ އަލްއަންޞާރީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ ތެރެއިން އެކަކުވެސް ކުޑަކަމުދާ ހިނދު، ކަނާތުން ލަދުވެތި ގުނަވަނުގައި ހިފަހައްޓައިގެނެއް ނުވާނެއެވެ. އަދި ބޮޑުކަމުދިޔުމަށް ފަހު، (ސާފު ކުރުމަށް ޓަކައި) ކަނާތުން ނުފޮހޭށެވެ. އަދި (އެއްޗެއް ބޯ ހިނދު) ކަންވާރުގެ ތެރެއަށް ނޭވާ ނުލާށެވެ.“","البخاري 153، ومسلم 367"],["#018","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","‌باب دخول الخلاء والاستطابة","ފާހާނައަށް ވަނުމައި ސާފުތާހިރުވުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: مَرَّ النَّبِيُّ ﷺ بِقَبْرَيْنِ، فَقَالَ: «إنَّهُمَا لَيُعَذَّبَانِ، وَمَا يُعَذَّبَانِ فِي كَبِيرٍ أَمَّا أَحَدُهُمَا: فَكَانَ لا يَسْتَتِرُ مِنْ الْبَوْلِ، وَأَمَّا الآخَرُ: فَكَانَ يَمْشِي بِالنَّمِيمَةِ فَأَخَذَ جَرِيدَةً رَطْبَةً، فَشَقَّهَا نِصْفَيْنِ، فَغَرَزَ فِي كُلِّ قَبْرٍ وَاحِدَةً، فَقَالُوا: يَا رَسُولَ اللَّهِ، لِمَ فَعَلْتَ هَذَا؟ قَالَ: لَعَلَّهُ يُخَفَّفُ عَنْهُمَا مَا لَمْ يَيْبَسَا»","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ދެ މަހާނަ ކައިރިން ލާފަ ނަބިއްޔާ ވަޑައިގަތެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން (އޭގައިވާ) ދެ މީހުންނަށް އަޒާބު ދެވެމުން އެބަދެއެވެ. އަދި އެ ދެ މީހުންނަށް އަޒާބު ދެވެނީކީ ބޮޑު ކަމަކާ ހެދިއެއް ނޫނެވެ. އެ ތަނުން އެކަކު ކުޑަކަމުދާ އިރު އޭގެން (ގަޔަށް ނުބުރާ ގޮތަށް) ރައްކާތެރިއެއް ނުވެއެވެ. އަދި އަނެކަކު ދެބައޮޑުވަމުން ހިނގައި އުޅުނެވެ.“ ދެން ނަބިއްޔާ ކަދުރު ރުކެއްގެ ރޯ ފަންތޮށިކޮޅެއް ނަންގަވާފައި ދެ ފަޅިއަށް ފަޅާލެއްވިއެވެ. އަދި އެއިން ފަޅިއެއް ކޮންމެ މަހާނައެއްގައި ހަރުކުރެއްވިއެވެ. ދެން މީސްތަކުން ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. ތިޔަފަދައިން ކަންތައް ކުރެއްވީ ކީއްވެގެންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެއީ އެ ދޭތި ނުހިކޭހާ ހިނދަކު، އެ ދެ މީހުންގެ ކިބައިން (އަޒާބު) ލުއި ކުރައްވާތޯއެވެ.“","البخاري 218 واللفظ له، ومسلم 292"],["#019","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","‌باب السواك","ސިވާކުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - عَنْ النَّبِيِّ ﷺ قَالَ: «لَوْلا أَنْ أَشُقَّ عَلَى أُمَّتِي لأَمَرْتُهُمْ بِالسِّوَاكِ عِنْدَ كُلِّ صَلاةٍ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާގެ އުންމަތުގެ މައްޗަށް ދަތި ކަމަކަށް ނުވާނަމަ، ކޮންމެ ނަމާދަކަށް ސިވާކު ބޭނުން ކުރަން އެބައިމީހުންނަށް އަމުރު ކުރެއްވީމުހެވެ.“","البخاري 887، ومسلم 252"],["#020","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","‌باب السواك","ސިވާކުގެ ބާބު","عَنْ حُذَيْفَةَ بْنِ الْيَمَانِ رَضِيَ اللَّهُ عَنْهُما قَالَ: «كَانَ رَسُولُ اللَّهِ ﷺ إذَا قَامَ مِنْ اللَّيْلِ يَشُوصُ فَاهُ بِالسِّوَاكِ»","ޙުޛައިފާ ބިން އަލްޔަމާނު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ރޭގަނޑު (ހޭފުޅުލައްވާ) ތެދުވެވަޑައިގަންނަވާ ނަމަ، ސިވާކުން އަނގަފުޅު ސާފު ކުރައްވައެވެ.","البخاري 245 واللفظ له، ومسلم 255"],["#021","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","‌باب السواك","ސިވާކުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: «دَخَلَ عَبْدُ الرَّحْمَنِ بْنُ أَبِي بَكْرٍ الصِّدِّيقِ - رَضِيَ اللَّهُ عَنْهُ - عَلَى النَّبِيِّ ﷺ وَأَنَا مُسْنِدَتُهُ إلَى صَدْرِي، وَمَعَ عَبْدِ الرَّحْمَنِ سِوَاكٌ رَطْبٌ يَسْتَنُّ بِهِ فَأَبَدَّهُ رَسُولُ اللَّهِ ﷺ بَصَرَهُ. فَأَخَذْتُ السِّوَاكَ فَقَضَمْتُهُ، فَطَيَّبْتُهُ، ثُمَّ دَفَعْتُهُ إلَى النَّبِيِّ ﷺ فَاسْتَنَّ بِهِ فَمَا رَأَيْتُ رَسُولَ اللَّهِ ﷺ اسْتَنَّ اسْتِنَانًا أَحْسَنَ مِنْهُ، فَمَا عَدَا أَنْ فَرَغَ رَسُولُ اللَّهِ ﷺ: رَفَعَ يَدَهُ - أَوْ إصْبَعَهُ - ثُمَّ قَالَ: فِي الرَّفِيقِ الأَعْلَى - ثَلاثاً - ثُمَّ قَضَى. وَكَانَتْ تَقُولُ: مَاتَ بَيْنَ حَاقِنَتِي وَذَاقِنَتِي» \n<br><br>\nوَفِي لَفْظٍ «فَرَأَيْتُهُ يَنْظُرُ إلَيْهِ، وَعَرَفْتُ: أَنَّهُ يُحِبُّ السِّوَاكَ فَقُلْتُ: آخُذُهُ لَكَ؟ فَأَشَارَ بِرَأْسِهِ: أَنْ نَعَمْ» هَذَا لَفْظُ الْبُخَارِيِّ وَلِمُسْلِمٍ نَحْوُهُ.","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ޢަބްދުއްރަޙްމާނު ބިން އަބޫ ބަކްރު އައްޞިއްދީޤު، ނަބިއްޔާ ﷺ ގެ އަރިހަށް ވަދުނެވެ. އެ ހިނދު ތިމަން ކަމަނާ އިންނެވީ ނަބިއްޔާ ތިމަން ކަމަނާގެ މޭގައި ޖައްސަވާލައިގެންނެވެ. އަދި ޢަބްދުއްރަޙްމާނުގެ އަތުގައި ރޯ ސިވާކެއް އޮވެފައި އޭގެން ދަތް އުނގުޅަން ހުއްޓެވެ. ދެން އެއާ ދިމާއަށް ރަސޫލު ﷲ ﷺ ބެއްލެވިއެވެ. ތިމަން ކަމަނާ އެ ސިވާކު ނަގައިފައި، އޭގެ ކޮޅު ދަތުން ހަފައި ބުރިކޮށް ސާފުކޮށްލުމަށް ފަހު، ނަބިއްޔާ ﷺ އަށް ދިނީމެވެ. ދެން ނަބިއްޔާ އޭގެން ދަތްޕުޅު އުނގުޅުއްވިއެވެ. ފަހެ އެއަށްވުރެ ރަނގަޅަށް ރަސޫލު ﷲ ﷺ ދަތްޕުޅު އުނގުޅެއްވި ފަހަރެއް ތިމަން ކަމަނާ ނުދެކެމެވެ. ދެން ނަބިއްޔާ ﷺ އެ ކަންތައްކޮށް ނިމިވަޑައިގަތުމަށް ފަހު، އަތްޕުޅު (ނުވަތަ އިނގިލިފުޅު) އުފުއްލަވާފައި ތިން ފަހަރު ޙަދީޘް ކުރެއްވިއެވެ: ”އެންމެ މަތިވެރި އެކުވެރިންނާ އެކުގައެވެ. (އެބަހީ: ނަބިއްޔުންނާއި ޞިއްދީޤުންނާއި ޝަހީދުންނާއި ޞާލިޙު ބޭކަލުންނާއެކުގައި މި އަޅާ ލައްވާށިއެވެ.) އެއަށްފަހު ނަބިއްޔާ އަވަހާރަވެ ވަޑައިގަތެވެ. އަދި ޢާއިޝާ ބުނެ އުޅުއްވިއެވެ: ނަބިއްޔާ އަވަހާރަވެ ވަޑައިގަތީ، ތިމަން ކަމަނާގެ ކޮނޑުކަށްޓާއި ލަގޮނޑިވަޅާ ދެމެދުގައި (ނަބިއްޔާގެ މޫނުފުޅު) ވަނިކޮށެވެ. \n<br><br> \nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ފަހެ ނަބިއްޔާ އެ ސިވާކާ ދިމާއަށް ބައްލަވަނިކޮށް ތިމަން ކަމަނާ ދުށީމެވެ. އަދި ނަބިއްޔާ ސިވާކުން ދަތް އުނގުޅުއްވަން ލޯބިފުޅު ކުރައްވާކަން ތިމަން ކަމަނާއަށް އެނގުނެވެ. ވީމާ ތިމަން ކަމަނާ ދެންނެވީމެވެ: އިބަ ނަބިއްޔާއަށް އޭތި ނަގައިދެންތޯއެވެ؟ ނަބިއްޔާގެ އިސްތަށިފުޅުން އިޝާރާތް ކުރެއްވިއެވެ: އެބަހީ އާދޭހެވެ. މިއީ ބުޚާރީގެ ރިވާޔަތެވެ. އަދި މިފަދަ ރިވާޔަތެއް މުސްލިމުގައި ވެސް ވެއެވެ.","البخاري 4438\n<br><br>\nالبخاري 4449، ومسلم 2443"],["#022","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","‌باب السواك","ސިވާކުގެ ބާބު","عَنْ أَبِي مُوسَى الأَشْعَرِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «أَتَيْتُ النَّبِيَّ ﷺ وَهُوَ يَسْتَاكُ بِسِوَاكٍ رَطْبٍ، قَالَ: وَطَرَفُ السِّوَاكِ عَلَى لِسَانِهِ، وَهُوَ يَقُولُ: أُعْ، أُعْ، وَالسِّوَاكُ فِي فِيهِ، كَأَنَّهُ يَتَهَوَّعُ»","އަބޫ މޫސާ އަލްޢަޝްޢަރީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ރޯ ސިވާކަކުން ދަތް އުނގުޅުއްވަނިކޮށް، ނަބިއްޔާގެ އަރިހަށް ތިމަން އަތުވީމެވެ. އެ ހިނދު ސިވާކުގެ ކޮޅު ވަނީ ނަބިއްޔާގެ ދޫފުޅު މަތީގައެވެ. ނަބިއްޔާގެ އަނގަފުޅުގެ ތެރޭގައި ސިވާކު ވަނިކޮށް، އުޢް އުޢް (މިފަދަ) އަޑުފުޅެއް ލައްވައެވެ. ނަބިއްޔާ ހޮޑުފުޅު ލައްވަން އުޅުއްވާ ފަދައެވެ.","البخاري 244 واللفظ له، ومسلم 254"],["#023","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب المسح على الخفين","ޚުއްފުގައި ފެން ފޮހުމުގެ ބާބު","عَنْ الْمُغِيرَةِ بْنِ شُعْبَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «كُنْتُ مَعَ النَّبِيِّ ﷺ فِي سَفَرٍ، فَأَهْوَيْتُ لأَنْزِعَ خُفَّيْهِ، فَقَالَ: دَعْهُمَا، فَإِنِّي أَدْخَلْتُهُمَا طَاهِرَتَيْنِ، فَمَسَحَ عَلَيْهِمَا»","އަލްމުޣީރާ ބިން ޝުޢުބާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ގެ އަރިހުގައި ތިމަން ދަތުރެއް ކުރީމެވެ. އަދި ނަބިއްޔާގެ (ދެ ފައިންޕުޅު ދޮވުމަށް) ޚުއްފު ބާލައިދޭން ތިމަން ދެ އަތް ދިއްކޮށްލީމެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ ދެ ޚުއްފު (ނުބާލާ) ދޫކޮށްލާށެވެ. ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާ އެ ޚުއްފަށް އަރައިވަޑައިގަތީ (ވުޟޫކޮށް) ތަހާރަތުގެ މަތީގައެވެ.“ ދެން ނަބިއްޔާ އެ ދެ ޚުއްފުގައި ފެން ފުއްސެވިއެވެ. (ނޯޓު: ޚުއްފަކީ ޖަނަވާރުގެ ހަމުން ހަދާފައިވާ އިސްޓާކީނެވެ.)","البخاري 206، ومسلم 247"],["#024","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب المسح على الخفين","ޚުއްފުގައި ފެން ފޮހުމުގެ ބާބު","عَنْ حُذَيْفَةَ بْنِ الْيَمَانِ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: «كُنْتُ مَعَ النَّبِيِّ ﷺ فَبَالَ، وَتَوَضَّأَ، وَمَسَحَ عَلَى خُفَّيْهِ»","ޙުޛައިފާ ބިން އަލްޔަމާނު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ގެ އަރިހުގައި ތިމަން (ދަތުރެއް) ކުރީމެވެ. ފަހެ ނަބިއްޔާ ކުޑަކަމުދިޔައެވެ. އަދި ވުޟޫކޮށް ދެ ޚުއްފުގައި ފެން ފުއްސެވިއެވެ.","مسلم 273"],["#025","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب في المذي وغيره","މަޒިއްޔާއި އެހެނިހެން ތަކެތީގެ ބާބު","عَنْ عَلِيِّ بْنِ أَبِي طَالِبٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «كُنْتُ رَجُلاً مَذَّاءً، فَاسْتَحْيَيْتُ أَنْ أَسْأَلَ رَسُولَ اللَّهِ ﷺ لِمَكَانِ ابْنَتِهِ مِنِّي، فَأَمَرْتُ الْمِقْدَادَ بْنَ الأَسْوَدِ فَسَأَلَهُ، فَقَالَ: يَغْسِلُ ذَكَرَهُ، وَيَتَوَضَّأُ» \n<br><br>\nوَلِلْبُخَارِيِّ «اغْسِلْ ذَكَرَكَ وَتَوَضَّأْ»\n<br><br>\nوَلِمُسْلِمٍ «تَوَضَّأْ وَانْضَحْ فَرْجَكَ»","ޢަލީ ބިން އަބީ ޠާލިބު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ތިމަންނަވީ ގިނަގިނައިން މަޒްޔު އަންނަ ފިރިހެނަކަށެވެ. އަދި ނަބިއްޔާ ﷺ ގެ ދަރިކަނބަލުންގެ މަގާމުގެ ސަބަބުން، (މި މަޒްޔުގެ ކަންތަކާ ބެހޭ ގޮތުން) ނަބިއްޔާ އަރިހުގައި ސުވާލު ކުރަން ތިމަން ލަދުވެތިވީމެވެ. ފަހެ (އެ ކަން ކުރަން) އަލްމިޤްދާދު ބިން އަލްއަސްވަދަށް އެންގުމުން އެކަލޭގެފާނު ނަބިއްޔާ އަރިހުގައި ސުވާލު ކުރެއްވިއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެކަލޭގެފާނުގެ ލަދުވެތި ގުނަވަން ދޮވެފައި ވުޟޫ ކުރާނީއެވެ.“ \n<br><br>\nއަދި ބުޚާރީގެ ރިވާޔަތެއްގައި ވެއެވެ: ”ތިބާގެ ލަދުވެތި ގުނަވަން ދޮވެފައި ވުޟޫ ކުރާށެވެ.“\n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ”ވުޟޫ ކުރާށެވެ. އަދި ތިބާގެ ލަދުވެތި ގުނަވަން ދޮންނާށެވެ.“","مسلم 303\n<br><br>\nالبخاري 269\n<br><br>\nمسلم 303"],["#026","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب في المذي وغيره","މަޒިއްޔާއި އެހެނިހެން ތަކެތީގެ ބާބު","عَنْ عَبَّادِ بْنِ تَمِيمٍ عَنْ عَبْدِ اللَّهِ بْنِ زَيْدٍ بْنِ عَاصِمٍ الْمَازِنِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «شُكِيَ إلَى النَّبِيِّ ﷺ الرَّجُلُ يُخَيَّلُ إلَيْهِ أَنَّهُ يَجِدُ الشَّيْءَ فِي الصَّلاةِ، فَقَالَ: لا يَنْصَرِفُ حَتَّى يَسْمَعَ صَوْتاً، أَوْ يَجِدَ رِيحاً»","ޢަބްދު ﷲ ބިން ޒައިދު ބިން ޢާޞިމް އަލްމާޒިނީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަމާދުގައި ހުއްޓައި (ވައި ބޭރުވާކަމަށް) މީހަކަށް ހީވެގެން ނަބިއްޔާގެ އަރިހުގައި ޝަކުވާ ދެންނެވުނެވެ. ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭނާއަށް އަޑެއް އިވިއްޖައުމަށް ދާންދެން ނުވަތަ ވަހެއް ދުވަންދެން ނަމާދު ދޫނުކުރާށެވެ.“","البخاري 137، ومسلم 361 واللفظ له"],["#027","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب في المذي وغيره","މަޒިއްޔާއި އެހެނިހެން ތަކެތީގެ ބާބު","عَنْ أُمِّ قَيْسِ بِنْتِ مِحْصَنٍ الأَسَدِيَّةِ «أَنَّهَا أَتَتْ بِابْنٍ لَهَا صَغِيرٍ، لَمْ يَأْكُلِ الطَّعَامَ إلَى رَسُولِ اللَّهِ ﷺ فَأَجْلَسَهُ فِي حِجْرِهِ، فَبَالَ عَلَى ثَوْبِهِ، فَدَعَا بِمَاءٍ فَنَضَحَهُ عَلَى ثَوْبِهِ، وَلَمْ يَغْسِلْهُ»","އުންމު ޤައިސް ބިންތި މިޙްޞަނު އަލްއަސަދިއްޔާ ރިވާ ކުރެއްވިއެވެ: ކާއެއްޗެހި ކާން ނުފަށާ އެ ކަމަނާގެ ކުޑަ ފިރިހެން ކުއްޖަކު ގޮވައިގެން، ރަސޫލު ﷲ ﷺ ގެ އަރިހަށް އެ ކަމަނާ ދިޔައެވެ. ދެން ރަސޫލު ﷲ ﷺ، އެކަލޭގެފާނުގެ އުނގުފުޅު މަތީގައި އެ ކުއްޖާ ބައިންދަވައިފިއެވެ. ފަހެ ނަބިއްޔާގެ ހެދުންކޮޅަށް އެ ކުއްޖާ ކުޑަކަމުގުގޮސްފިއެވެ. ދެން ފެން ގެނައުމަށް ނަބިއްޔާ އަންގަވައި ހެދުންކޮޅުގެ މައްޗަށް ފެން ބުރުއްސަވާލެއްވިއެވެ. އަދި އެ ހެދުންކޮޅު ނުދޮންނަވައެވެ.","البخاري 223، ومسلم 287"],["#028","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب في المذي وغيره","މަޒިއްޔާއި އެހެނިހެން ތަކެތީގެ ބާބު","وَعَنْ عَائِشَةَ أُمِّ الْمُؤْمِنِينَ رَضِيَ اللَّهُ عَنْهُا «أَنَّ النَّبِيَّ ﷺ أُتِيَ بِصَبِيٍّ، فَبَالَ عَلَى ثَوْبِهِ، فَدَعَا بِمَاءٍ، فَأَتْبَعَهُ إيَّاهُ» \n<br><br>\nوَلِمُسْلِمٍ: «فَأَتْبَعَهُ بَوْلَهُ، وَلَمْ يَغْسِلْهُ»","އަދި އުންމުލް މުއުމިނީން، ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ގެ އަރިހަށް ކުޑަ ކުއްޖަކު ގެނެވުނެވެ. ފަހެ އެ ކުއްޖާ ނަބިއްޔާގެ ހެދުންކޮޅަށް ކުޑަކަމުގޮސްފިއެވެ. ދެން ފެން ގެނައުމަށް ނަބިއްޔާ އެންގެވުމަށް ފަހު، އެ ކުޑަކަމުދިޔުން ޖެހުން ތަންތަނުގައި ފެން ބުރުއްސަވާލެއްވިއެވެ. \n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ދެން އެ ފެނުން އެ ކުޑަކަމުދިޔަ ތަނުގައި ބުރުއްސަވާލެއްވިއެވެ. އަދި އެ ހެދުންކޮޅު ނުދޮންނަވައެވެ.","البخاري 222\n<br><br>\nمسلم 286"],["#029","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب في المذي وغيره","މަޒިއްޔާއި އެހެނިހެން ތަކެތީގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «جَاءَ أَعْرَابِيٌّ، فَبَالَ فِي طَائِفَةِ الْمَسْجِدِ، فَزَجَرَهُ النَّاسُ، فَنَهَاهُمُ النَّبِيُّ ﷺ فَلَمَّا قَضَى بَوْلَهُ أَمَرَ النَّبِيُّ ﷺ بِذَنُوبٍ مِنْ مَاءٍ، فَأُهْرِيقَ عَلَيْهِ»","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ބަދަވީއަކު އައިސް މިސްކިތް ތެރޭ ދިމާއެއްގައި ކުޑަކަމުދިޔައެވެ. ފަހެ މީސްތަކުން އޭނާއަށް އެއްޗެހި ގޮވިއެވެ. އެކަމަކު ނަބިއްޔާ އެބައިމީހުންނަށް އެ ކަން ނަހީ ކުރެއްވިއެވެ. ދެން އެ ބަދަވީ ކުޑަކަމުގޮސް ނިމުމުން، ފެން ބާލިދީއެއް ގެނައުމަށް ނަބިއްޔާ ﷺ އެންގެވިއެވެ. އަދި އެ ތަނުގެ މައްޗަށް ފެން އޮއްސާލެވިގެން ދިޔައެވެ.","البخاري 221 واللفظ له، ومسلم 274"],["#030","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب في المذي وغيره","މަޒިއްޔާއި އެހެނިހެން ތަކެތީގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: سَمِعْتُ رَسُولَ اللَّهِ ﷺ يَقُولُ: «الْفِطْرَةُ خَمْسٌ: الْخِتَانُ، وَالاسْتِحْدَادُ، وَقَصُّ الشَّارِبِ، وَتَقْلِيمُ الأَظْفَارِ، وَنَتْفُ الإِبِطِ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ: ”ފަސް ކަމެއް ވަނީ ފިޠުރަތުގެ ތެރެއިންނެވެ. އެއީ ޚިތާނު ކުރުމާއި، ލަދުވެތި ގުނަވަން ކައިރިން އިސްތަށި ބޭލުމައި، މަތިމަސް ކުރުކުރުމާއި، ނިޔަފަތި ކެނޑުމާއި އަދި ކިހިލިފަތުން އިސްތަށި އުފުރުމެވެ.“","البخاري 5889، ومسلم 257"],["#031","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الجنابة","ޖުނުބުވެރިކަމުގެ ބާބު","عنْ أَبي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - «أَنَّ النَّبِيَّ ﷺ لَقِيَهُ في بعْضِ طُرُقِ المدينَةِ وهو جُنُبٌ، قالَ: فَانْخَنَسْتُ مِنْهُ، فَذَهَبْتُ فَاغْتَسَلْتُ ثُمَّ جِئْتُ، فقَالَ: أَيْنَ كُنْتَ ياأَبا هُرَيْرَةَ؟ قَالَ: كُنْتُ جُنُبَاً فَكَرِهْتُ أَنْ أُجالِسَكَ على غَيْرِ طَهارَةٍ، فَقَالَ: سُبْحانَ اللهِ، إِنَّ المُؤْمِنَ لايَنْجُسُ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: އަބޫ ހުރައިރާ ޖުނުބުވެރިކަމުގެ ޙާލުގައި ވަނިކޮށް، މަދީނާގެ މަގަކުން ނަބިއްޔާއާ ބައްދަލުވިއެވެ. އަބޫ ހުރައިރާ ވިދާޅުވިއެވެ: ދެން ނަބިއްޔާ ހުންނެވި ތަނުން ތިމަން ފަހަތަށް ޖެހި ފޮރުވިގެންފީމެވެ. ދެން ގޮސް ހިނައިގަތުމަށް ފަހު އެނބުރި އަނެއްކާ އަތުވެއްޖައީމެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ އަބޫ ހުރައިރާއެވެ. ތިބާ އުޅުނީ ކޮންތާކުތޯއެވެ؟“ ތިމަން ދެންނެވީމެވެ: ތިމަން އޭރު ހުރީ ޖުނުބުވެރިކަމުގެ ޙާލުގައެވެ. އަދި ތަހާރަތުގެ މަތީ ނޫން ގޮތަކަށް އިބަ ނަބިއްޔާގެ އަރިހުގައި އިށީންނެވުމަށް ތިމަން ބޭނުން ނުވީއެވެ. ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ސުބްޙާނަ ﷲ އެވެ. ހަމަކަށަވަރުން މުއުމިނާ ނަޖިހެއް ނަޖިސްވެގެނެއް ނުދެއެވެ.“","البخاري 285، ومسلم 371"],["#032","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الجنابة","ޖުނުބުވެރިކަމުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: «كَانَ النَّبِيُّ ﷺ إذَا اغْتَسَلَ مِنَ الْجَنَابَةِ غَسَلَ يَدَيْهِ، ثُمَّ تَوَضَّأَ وُضُوءَهُ لِلصَّلاةِ، ثُمَّ اغْتَسَلَ، ثُمَّ يُخَلِّلُ بِيَدَيْهِ شَعْرَهُ، حَتَّى إذَا ظَنَّ أَنَّهُ قَدْ أَرْوَى بَشَرَتَهُ، أَفَاضَ عَلَيْهِ الْمَاءَ ثَلاثَ مَرَّاتٍ، ثُمَّ غَسَلَ سَائِرَ جَسَدِهِ.","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލާ ﷺ ޖުނުބުވެރިކަމުން ހިނައިގަންނަވާ ހިނދު، (ފުރަތަމަ) ދެ އަތްތިލަ ދޮންނަވައެވެ. އެއަށްފަހު ނަމާދަށް ވުޟޫ ކުރައްވާ ފަދައިން ވުޟޫ ކުރައްވައެވެ. ދެން ހިނައިގަންނަވަން (ފައްޓަވައެވެ.) އަދި އިސްތަށިފުޅުގެ ތެރެއަށް ދެ އަތްޕުޅު ވައްދަވާ ހިނގަވާލައްވައެވެ. އެއީ (އިސްތަށިފުޅުގެ) ހަމުގައި ފެން ޖެއްސެވިއްޖެކަން އެނގިވަޑައިގަތުމަށް ދާންދެނެވެ. ދެން އިސްތަށިފުޅު މަތިން ތިން ފަހަރު ފެން އަޅުއްވައެވެ. އެއަށްފަހު މުޅި ހަށިކޮޅުގެ ދޮންނަވައެވެ.","البخاري 272 واللفظ له، ومسلم 316"],["#033","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الجنابة","ޖުނުބުވެރިކަމުގެ ބާބު","وَكَانَتْ تَقُولُ: كُنْتُ أَغْتَسِلُ أَنَا وَرَسُولُ اللَّهِ ﷺ مِنْ إنَاءٍ وَاحِدٍ، نَغْتَرِفُ مِنْهُ جَمِيعاً.","އަދި ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިއެވެ: ތިމަން ކަމަނައާއި ރަސޫލު ﷲ ﷺ އެއް ކަންވާރަކުން (ފެން ނަންގަވައިގެން) ހިނައިގަންނަވަމެވެ. ތިމަން ދެކަނބަލުން ވެސް އޭގެން އަތްގޮށިން ނަންގަވަމެވެ.","البخاري 273 واللفظ له، ومسلم 321"],["#034","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الجنابة","ޖުނުބުވެރިކަމުގެ ބާބު","عَنْ مَيْمُونَةَ بِنْتِ الْحَارِثِ رَضِيَ اللَّهُ عَنْهُا زَوْجِ النَّبِيِّ ﷺ أَنَّهَا قَالَتْ: «وَضَعْتُ لِرَسُولِ اللَّهِ ﷺ وَضُوءَ الْجَنَابَةِ، فَأَكْفَأَ بِيَمِينِهِ عَلَى يَسَارِهِ مَرَّتَيْنِ - أَوْ ثَلاثاً - ثُمَّ غَسَلَ فَرْجَهُ، ثُمَّ ضَرَبَ يَدَهُ بِالأَرْضِ، أَوْ الْحَائِطِ، مَرَّتَيْنِ - أَوْ ثَلاثاً - ثُمَّ تَمَضْمَضَ وَاسْتَنْشَقَ، وَغَسَلَ وَجْهَهُ وَذِرَاعَيْهِ، ثُمَّ أَفَاضَ عَلَى رَأْسِهِ الْمَاءَ، ثُمَّ غَسَلَ جَسَدَهُ، ثُمَّ تَنَحَّى، فَغَسَلَ رِجْلَيْهِ، فَأَتَيْتُهُ بِخِرْقَةٍ فَلَمْ يُرِدْهَا، فَجَعَلَ يَنْفُضُ الْمَاءَ بِيَدِهِ»","ނަބިއްޔާ ﷺ ގެ އަނބިކަނބަލުން، މައިމޫނާ ބިންތި އަލްޙާރިޘް رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޖުނުބުވެރިކަމުން ހިނައިގަތުމަށް ޓަކައި، އެ ކަމަނާ ފެން ގެނެސް ބެހެއްޓެވިއެވެ. ފަހެ ނަބިއްޔާ ކަނާތް އަތްޕުޅުން، ވާތް އަތްޕުޅަށް ދެ ފަހަރު ނުވަތަ ތިން ފަހަރު ފެން އޮއްސެވިއެވެ. ދެން ލަދުވެތި ގުނަވަން ދޮންނެވުމަށް ފަހު، ދެތިން ފަހަރު ބިމުގައި ނުވަތަ ފާރުގައި އަތް ޖައްސަވައި (އުނގުޅާލެއްވިއެވެ.) ދެން އަނގަފުޅަށް ފެން ލައްވައި ކޯފުޅު ދޮންނެވުމަށް ފަހު، ނޭފަތްޕުޅަށް ފެން ލެއްވިއެވެ. އަދި މޫނުފުޅާއި ދެ އަތްޕުޅު ދޮންނެވިއެވެ. ދެން އިސްތަށިފުޅަށް ފެން އޮއްސެވިއެވެ. އެއަށްފަހު މުޅި ހަށިކޮޅު ދޮންނެވިއެވެ. ދެން ހުންނެވި ތަނުން ދުރަށް ޖެހިވަޑައިގެންފައި ދެ ފައިންޕުޅު ދޮންނެވިއެވެ. ނަބިއްޔާ (ހަށިކޮޅު ހިއްކުމަށް ޓަކައި) ތިމަން ކަމަނާ ފޮތިކޮޅެއް ގެނެސް ދިނީމެވެ. އެކަމަކު ނަބިއްޔާ އެ ބޭނުންވެވަޑައެއް ނުގެންނެވިއެވެ. ދެން ނަބިއްޔާގެ އަތްޕުޅުން ފެންތައް ތަޅުވާލާ ފިލުވައިލެއްވިއެވެ.","البخاري 274 واللفظ له، ومسلم 317"],["#035","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الجنابة","ޖުނުބުވެރިކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ: أَنَّ عُمَرَ بْنَ الْخَطَّابِ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «يَا رَسُولَ اللَّهِ، أَيَرْقُدُ أَحَدُنَا وَهُوَ جُنُبٌ؟ قَالَ: نَعَمْ، إذَا تَوَضَّأَ أَحَدُكُمْ فَلْيَرْقُدْ»","ޢުމަރު ބިން އަލްޚައްޠާބު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. ތިމަންމެން ކުރެ މީހަކު ޖުނުބުވެރިވެފައިވަނިކޮށް ނިދިދާނެތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އާދޭހެވެ. ތިޔަބައިމީހުންގެ ތެރެއިން މީހަކު ވުޟޫކޮށްފިނަމަ ފަހެ ދެން ނިދާށެވެ.“","البخاري 287 واللفظ له، ومسلم 306"],["#036","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الجنابة","ޖުނުބުވެރިކަމުގެ ބާބު","عَنْ أُمِّ سَلَمَةَ رَضِيَ اللَّهُ عَنْهُا - زَوْجِ النَّبِيِّ ﷺ قَالَتْ: «جَاءَتْ أُمُّ سُلَيْمٍ امْرَأَةُ أَبِي طَلْحَةَ - إلَى رَسُولِ اللَّهِ ﷺ فَقَالَتْ: يَا رَسُولَ اللَّهِ، إنَّ اللَّهَ لا يَسْتَحْيِي مِنْ الْحَقِّ، فَهَلْ عَلَى الْمَرْأَةِ مِنْ غُسْلٍ إذَا هِيَ احْتَلَمَتْ؟ فَقَالَ رَسُولُ اللَّهِ ﷺ: نَعَمْ، إذَا رَأَتِ الْمَاءَ»","ނަބިއްޔާ ﷺ ގެ އަނބިކަނބަލުން، އުންމު ސަލަމާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ އަރިހަށް އަބޫ ޠަލްޙާގެ އަނބިކަނބަލުން އުންމު ސުލައިމު ވަޑައިގެން ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން ޙައްޤުގެ ކިބައިން ﷲ ލަދުވެތިވެ ވޮޑިނުގަންނަވައެވެ. އަންހެނަކު އިޙްތިލާމުވެއްޖެ ނަމަ ހިނައިގަންނަން ޖެހޭނެތޯއެވެ؟ ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އާދޭހެވެ. އެ ކަމަނާއަށް ފެން ފެނިއްޖެ ނަމައެވެ.“ (ނޯޓު: އިޙްތިލާމަކީ ނިދީގައި މަނި ބޭރުވުމެވެ.)","البخاري 282 واللفظ له، ومسلم 313"],["#037","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الجنابة","ޖުނުބުވެރިކަމުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: «كُنْت أَغْسِلُ الْجَنَابَةَ مِنْ ثَوْبِ رَسُولِ اللَّهِ ﷺ فَيَخْرُجُ إلَى الصَّلاةِ، وَإِنَّ بُقَعَ الْمَاءِ فِي ثَوْبِهِ» \n<br><br>\nوَفِي لَفْظٍ لِمُسْلِمٍ «لَقَدْ كُنْتُ أَفْرُكُهُ مِنْ ثَوْبِ رَسُولِ اللَّهِ ﷺ فَرْكاً، فَيُصَلِّي فِيهِ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ ހެދުންކޮޅުގައިވާ (އަދިވެސް ތެތްކޮށް ހުރި) މަނިތައް ތިމަން ކަމަނާ ދޮންނަވަމެވެ. ފަހެ ނަބިއްޔާގެ ހެދުންކޮޅުގައި ފެން ޖެހުނު ތަންތަން (ނުހިކި) ވަނިކޮށް ނަބިއްޔާ ނަމާދަށް ނުކުމެވަޑައިގަންނަވައެވެ.\n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތުގައި ވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ގެ ހެދުންކޮޅުން އެ (ހިކިފައި ހުރި މަނި) ތިމަން ކަމަނާ (ނިޔަފަތިން) ކަހާ ނައްޓަމެވެ. އެއަށްފަހު ނަބިއްޔާ އެ ހެދުންކޮޅުގައި ނަމާދު ކުރައްވައެވެ.","البخاري 229 واللفظ له، ومسلم 289\n<br><br>\nمسلم 288\n"],["#038","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الجنابة","ޖުނުބުވެރިކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - أَنَّ النَّبِيَّ ﷺ قَالَ: «إذَا جَلَسَ بَيْنَ شُعَبِهَا الأَرْبَعِ، ثُمَّ جَهَدَهَا، فَقَدْ وَجَبَ الْغُسْلُ»\n<br><br>\nوَفِي لَفْظٍ «وَإِنْ لَمْ يُنْزِلْ»","އަބޫ ހުރައރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އަންހެނެއްގެ ހަތަރެސްފައި ދެމެދުގައި މީހަކު އިށީންދެ، އޭނާގެ ލަދުވެތި ގުނަވަން އެ ކަނބުލޭގެ ފަރުޖު ތެރެއަށް ވައްދައިފި ނަމަ، ފަހެ ހަމަކަށަވަރުން ހިނައިގަތުން ވާޖިބުވާނެއެވެ.“ \n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”އަދި މަނި ބޭރު ނުވިކަމުގައި ވިޔަސް މެއެވެ.“","البخاري 291 واللفظ له، ومسلم 348\n<br><br>\nمسلم 348"],["#039","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الجنابة","ޖުނުބުވެރިކަމުގެ ބާބު","عَنْ أَبِي جَعْفَرٍ مُحَمَّدِ بْنِ عَلِيِّ بْنِ الْحُسَيْنِ بْنِ عَلِيِّ بْنِ أَبِي طَالِبٍ رَضِيَ اللَّهُ عَنْهُمْ «أَنَّهُ كَانَ هُوَ وَأَبُوهُ عِنْدَ جَابِرِ بْنِ عَبْدِ اللَّهِ، وَعِنْدَهُ قَوْمٌ، فَسَأَلُوهُ عَنْ الْغُسْلِ؟ فَقَالَ: صَاعٌ يَكْفِيكَ، فَقَالَ رَجُلٌ: مَا يَكْفِينِي، فَقَالَ جَابِرٌ: كَانَ يَكْفِي مَنْ هُوَ أَوْفَى مِنْك شَعَرَاً، وَخَيْراً مِنْكَ - يُرِيدُ رَسُولَ اللَّهِ ﷺ ثُمَّ أَمَّنَا فِي ثَوْبٍ» \n<br><br>\nوَفِي لَفْظٍ «كَانَ رَسُولُ اللَّهِ ﷺ يُفْرِغُ الْمَاءَ عَلَى رَأْسِهِ ثَلاثاً»","އަބޫ ޖަޢުފަރު މުޙައްމަދު ބިން ޢަލީ ބިން އަލްޙުސައިން ބިން ޢަލީ ބިން އަބީ ޠާލިބު رَضِيَ اللَّهُ عَنْهُمْ ރިވާ ކުރެއްވިއެވެ: އަބޫ ޖަޢުފަރާއި އެކަލޭގެފާނުގެ ބައްޕާފުޅު، މި ދެބަފައިކަލުން ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُ ގާތުގައި ހުންނެވިއެވެ. އަދި ޖާބިރުގެ އަރިހުގައި އެހެން ބަޔަކު ވެސް ހުއްޓެވެ. ފަހެ އެބައމީހުން ޖާބިރުގެ އަރިހުގައި ހިނައިގަތުމާއި ބެހޭ ގޮތުން ސުވާލު ކުރިއެވެ. ޖާބިރު ވިދާޅުވިއެވެ: ތިބާއަށް ޞާޢެއްގެ މިންވަރު ފުދޭނެއެވެ. (ނޯޓު: އެއް ޞާޢަކީ ގާތްގަނޑަކަށް ތިން ލީޓަރެވެ.) ދެން މީހަކު ދެންނެވިއެވެ: އެ ވަރަކުން ތިމަންނައަކަށް ނުފުދޭނެއެވެ. ޖާބިރު ވިދާޅުވިއެވެ: ތިބާއަށް ވުރެ އިސްތަށިކޮޅު ގިނަ އަދި ތިބާއަށް ވުރެ ހެޔޮވެގެންވާ މީހަކަށް އެ ވަރުން ފުދުނެވެ. – އެބަހީ ރަސޫލު ﷲ ﷺ އެވެ. –އެއަށްފަހު ޖާބިރު އެއް ހެދުންކޮޅެއް ތުރުކުރައްވައިގެން ހުރެ ތިމަންމެންނައިގެން އިމާމުވެ ނަމާދު ކުރެއްވިއެވެ.\n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ނަބިއްޔާ ﷺ (ހިނައިގަންނަވާ ހިނދު) އިސްތަށިފުޅުގައި ތިން ފަހަރު ފެން އޮއްސަވާލައްވައެވެ.","البخاري 252\n<br><br>\nالبخاري 255"],["#040","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب التيمم","ތަޔައްމަމުގެ ބާބު","عَنْ عِمْرَانَ بْنِ حُصَيْنٍ - رَضِيَ اللَّهُ عَنْهُ -: «أَنَّ رَسُولَ اللهِ ﷺ رَأَى رَجُلاً مُعْتَزلاً، لَمْ يُصَلِّ فِي الْقَوْمِ؟ فَقَالَ: يَا فُلانُ، مَا مَنَعَكَ أَنْ تُصَلِّيَ فِي الْقَوْمِ؟ فَقَالَ: يَا رَسُولَ اللَّهِ أَصَابَتْنِي جَنَابَةٌ، وَلا مَاءَ، فَقَالَ: عَلَيْك بِالصَّعِيدِ، فَإِنَّهُ يَكْفِيَكَ»","ޢިމްރާނު ބިން ޙުޞައިނު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: މީސްތަކުންނާއެކު ނަމާދު ނުކޮށް، އެކަހެރިވެގެން ހުރި މީހަކު ރަސޫލު ﷲ ﷺ ދެކެވަޑައިގަތެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ މިވެނި މީހާއެވެ. މީސްތަކުންނާއެކު ނަމާދު ކުރުމުން ތިބާ މަނާ ކުރުވީ ކޮން ކަމެއްތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. ތިމަންނަ ޖުނުބުވެރިވެފައި (ހިނައިގަންނާނެ) ފެނެއް ނެތެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާގެ މައްޗަށް އޮތީ ވެލިން (ތަޔައްމުމު ކުރުމެވެ.) އެހެނީ ހަމަކަށަވަރުން އެއިން ތިބާ (ހިނައިގަތުމަށް) ފުދޭނެއެވެ.“","البخاري 348"],["#041","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب التيمم","ތަޔައްމަމުގެ ބާބު","عَنْ عَمَّارِ بْنِ يَاسِرٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: «بَعَثَنِي النَّبِيُّ ﷺ فِي حَاجَةٍ، فَأَجْنَبْتُ، فَلَمْ أَجِدِ الْمَاءَ، فَتَمَرَّغْتُ فِي الصَّعِيدِ، كَمَا تَمَرَّغُ الدَّابَّةُ، ثُمَّ أَتَيْتُ النَّبِيَّ ﷺ فَذَكَرْتُ ذَلِكَ لَهُ، فَقَالَ: إنَّمَا يَكْفِيَكَ أَنْ تَقُولَ بِيَدَيْكَ هَكَذَا - ثُمَّ ضَرَبَ بِيَدَيْهِ الأَرْضَ ضَرْبَةً وَاحِدَةً، ثُمَّ مَسَحَ الشِّمَالَ عَلَى الْيَمِينِ، وَظَاهِرَ كَفَّيْهِ وَوَجْهَهُ»","ޢައްމާރު ބިން ޔާސިރު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ކަމަކައިގެން ރަސޫލު ﷲ ﷺ ތިމަން (ދަތުރެއްގައި) ފޮނުއްވިއެވެ. (ފަހެ ދަތުރު މަތީގައި) ތިމަން ޖުނުބުވެރިވެފައި ފެނެއް ނުހޯދުނެވެ. ދެން ޖަނަވާރަކު ވެލިތަކުމަތީ ފުރޮޅޭ ފަދައިން ތިމަން ވެލިތަކުމަތީ ފުރޮޅުނީމެވެ. އެއަށްފަހު ނަބިއްޔާ ﷺ ގެ އަރިހަށް އައިސް އެ ވާހަކަ ކިޔައިދިނީމެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ދެ އަތުން ހަމައެކަނި މިހެން ހަދާލިނަމަ ތިބާއަށް ފުދުނީހެވެ.“ ނަބިއްޔާ އެއް ފަހަރު ބިންމަތީގައި އަތްޕުޅުން ޖައްސަވާލިއެވެ. އެއަށްފަހު ވާތް އަތްޕުޅުން ކަނާތް އަތްޕުޅު މަތީ ފުއްސެވިއެވެ. އަދި އަތްތިލައިގެ (ބޭރުފުށާއި) މޫނުފުޅުގައި ފުއްސެވިއެވެ.","البخاري 347، ومسلم 338"],["#042","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب التيمم","ތަޔައްމަމުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رَضِيَ اللَّهُ عَنْهُما: أَنَّ النَّبِيَّ ﷺ قَالَ: «أُعْطِيتُ خَمْساً، لَمْ يُعْطَهُنَّ أَحَدٌ مِنْ الأَنْبِيَاءِ قَبْلِي: نُصِرْتُ بِالرُّعْبِ مَسِيرَةَ شَهْرٍ، وَجُعِلَتْ لِي الأَرْضُ مَسْجِدًا وَطَهُورًا، فَأَيُّمَا رَجُلٌ مِنْ أُمَّتِي أَدْرَكَتْهُ الصَّلاةُ فَلْيُصَلِّ، وَأُحِلَّتْ لِي الْمَغَانِمُ، وَلَمْ تَحِلَّ لأَحَدٍ قَبْلِي، وَأُعْطِيتُ الشَّفَاعَةَ، وَكَانَ النَّبِيُّ يُبْعَثُ إلَى قَوْمِهِ خَاصَّةً، وَبُعِثْتُ إلَى النَّاسِ عَامَّةً»","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާގެ ކުރިން އެއްވެސް ނަބިއްޔަކަށް ނުދެވޭ ފަސް ކަމެއް ތިމަން ނަބިއްޔާއަށް ދެއްވުނެވެ. (އެ ގޮތުން) މަސްދުވަހެއްގެ ރާސްތާގެ ދުރުމިނަށް (ތިމަން ނަބިއްޔާގެ އަދުއްވުންގެ ހިތްތަކަށް) ބިރުވެރިކަން ވެއްދުމުން ތިމަން ނަބިއްޔާއަށް ނަޞްރު ދެއްވުނެވެ. އަދި ތިމަން ނަބިއްޔާއަށް ޓަކައި ބިމަކީ މިސްކިތެއް (އެބަހީ: ނަމާދު ކުރެވޭ ތަނެއް) ކަމުގައި އަދި ތާހިރުވާން ބޭނުން ކުރެވޭ އެއްޗެއް ކަމުގައި ލެއްވިއެވެ. ތިމަން ނަބިއްޔާގެ އުންމަތުގެ ކޮންމެ މީހަކަށް ވެސް ނަމާދު ވަގުތު ވަނުމުން، ފަހެ އޭނާ ނަމާދު ކުރާށެވެ. އަދި ތިމަން ނަބިއްޔާއަށް ޣަނީމާ މުދާ ޙަލާލު ކުރެވުނެވެ. ތިމަން ނަބިއްޔާގެ ކުރިން އެއްވެސް މީހަކަށް އެ ހުއްދައެއް ނުކުރެވެއެވެ. އަދި ތިމަން ނަބިއްޔާއަށް ޝަފާޢާތް ދެއްވުނެވެ. އަދި (ކުރީގެ) ނަބިއްޔުން ފޮނުއްވުނީ އެ ނަބިއްޔެއްގެ ގައުމުގެ ބަޔަކު ގާތައް ޚާއްޞަކޮށެވެ. އެކަމަކު ތިމަން ނަބިއްޔާ ފޮނުއްވުނީ މުޅި އިންސާނިއްޔަތަށް އާންމުކޮށެވެ.","البخاري 335 واللفظ له، ومسلم 521"],["#043","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الحيض","ޙައިޟުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا «أَنَّ فَاطِمَةَ بِنْتَ أَبِي حُبَيْشٍ: سَأَلَتِ النَّبِيَّ ﷺ فَقَالَتْ: إنِّي أُسْتَحَاضُ فَلا أَطْهُرُ، أَفَأَدَعُ الصَّلاةَ؟ قَالَ: لا، إنَّ ذَلِكَ عِرْقٌ، وَلَكِنْ دَعِي الصَّلاةَ قَدْرَ الأَيَّامِ الَّتِي كُنْتِ تَحِيضِينَ فِيهَا، ثُمَّ اغْتَسِلِي وَصَلِّي» \n<br><br>\nوَفِي رِوَايَةٍ «وَلَيْسَتْ بِالْحَيْضَةِ، فَإِذَا أَقْبَلَتْ الْحَيْضَةُ: فَاتْرُكِي الصَّلاةَ فِيهَا، فَإِذَا ذَهَبَ قَدْرُهَا فَاغْسِلِي عَنْك الدَّمَ وَصَلِّي»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ އަރިހުގައި ފާޠިމާ ބިންތި އަބޫ ޙުބައިޝް ސުވާލު ކުރެއްވިއެވެ: ހަމަކަށަވަރުން ތިމަން ކަމަނާ އިޞްތިޙާލާވެ އެ ކަމުން ތާހިރެއް ނުވެއެވެ. (އެބަހީ: މެދުކެނޑުމެއް ނެތި ރަޙިމުގެ ތިރީބައިން ލޭފައިބަފައިބަ ހުރެއެވެ. މިއީ ޙައިޟުވުމާ ތަފާތު ކަމެކެވެ.) ވީމާ ތިމަން ކަމަނާ ނަމާދުކުރުން ދޫކޮށްލަންވީތޯއެވެ؟ ނަބިއްޔާ ﷺ ޙާދީޘް ކުރެއްވިއެވެ: ”ނޫނެކެވެ. ހަމަކަށަވަރުން ތިޔައީ ލޭ ނާރަކުން އަންނަ ލެއެކެވެ. (އެބަހީ: ޙައިޟުގެ ކަމެއް ނޫނެވެ.) އެހެނެއްކަމަކު ކަމަނާ (އާންމުކޮށް) ޙައިޟުވެ އުޅޭ މުއްދަތަށް ނަމާދުކުރުން ދޫކޮށްލާށެވެ. އެއަށްފަހު ހިނައިގަނެ ނަމާދު ކުރާށެވެ.“ \n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”އެއީ އަދި ޙައިޟެއް ނޫނެވެ. ވީމާ (އަސްލު) ޙައިޟުވާން ފެށުމުން ނަމާދުކުރުން ދޫކޮށްލާށެވެ. ދެން (އާންމުކޮށް) ޙައިޟުވެ އުޅޭ މުއްދަތު ނިމުމުން ކަމަނާގެ ކިބައިންވާ ލޭތައް ދޮވެ (ހިނައިގަންނާށެވެ.) އަދި ނަމާދު ކުރާށެވެ.“","البخاري 325\n<br><br>\nالبخاري 306، ومسلم 333"],["#044","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الحيض","ޙައިޟުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا «أَنَّ أُمَّ حَبِيبَةَ اُسْتُحِيضَتْ سَبْعَ سِنِينَ، فَسَأَلَتْ رَسُولَ اللَّهِ ﷺ عَنْ ذَلِكَ؟ فَأَمَرَهَا أَنْ تَغْتَسِلَ، قَالَتْ: فَكَانَتْ تَغْتَسِلُ لِكُلِّ صَلاةٍ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: އުންމު ޙަބީބާ ހަތް އަހަރު ވަންދެން އިސްތިޙާޟާވެ ވަޑައިގަތެވެ. އެ ކަމާ ބެހޭ ގޮތުން ރަސޫލު ﷲ ﷺ އަރިހުގައި އެ ކަމަނާ ސުވާލު ކުރެއްވިއެވެ. ނަބިއްޔާ އެންގެވީ (އާންމުކޮށް ޙައިޟުވެ އުޅޭ މުއްދަތު ނިމުމުން) އެ ކަމަނާ ހިނައިގަތުމަށެވެ. އަދި ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔައީ ލޭ ނާރަކުން އަންނަ ލެއެކެވެ.“ (އާޢިޝާ ވިދާޅުވިއެވެ:) ފަހެ ކޮންމެ ނަމާދަކަށް ވެސް އެ ކަމަނާ (އަމިއްލަފުޅަށް) ހިނައިގަންނަވައެވެ. (ނޯޓު: ކޮންމެ ފަހަރަކު ނަމާދަށް ހިނައިގަންނަން ނަބިއްޔާ އަމުރެއް ނުކުރައްވައެވެ. އެހެނަސް އަމުރު ކުރެއްވީ ޙައިޟުގެ މުއްދަތު ނިމުމުން އެއްފަހަރު ހިނައިގަނެގެން ދެން ނަމާދުކޮށް އުޅުމަށެވެ.)","البخاري 327 واللفظ له، ومسلم 334"],["#045","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الحيض","ޙައިޟުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: «كُنْتُ أَغْتَسِلُ أَنَا وَرَسُولُ اللَّهِ ﷺ مِنْ إنَاءٍ وَاحِدٍ، كِلانا جُنُبٌ.","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ތިމަން ކަމަނައާއި ރަސޫލު ﷲ ﷺ ވެސް ޖުނުބުވެރިވެފައި ވަނިކޮށް، އެއް ކަންވާރަކުން ތިމަން ދެބޭކަލުން ހިނައިގަންނަމެވެ.","البخاري 299 واللفظ له، ومسلم 321"],["#046","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الحيض","ޙައިޟުގެ ބާބު","وَكَانَ يَأْمُرُنِي فَأَتَّزِرُ، فَيُبَاشِرُنِي وَأَنَا حَائِضٌ.","އަދި ތިމަން ކަމަނާގެ އިޒާރު ބާރު ކުރަން ނަބިއްޔާ އަންގަވައެވެ. އަދި ތިމަން ކަމަނާ ޙައިޟުވެރިވެފައިވާ ޙާލު ތިމަން ކަމަނާ ގައިކޮޅުގައި ނަބިއްޔާ ބައްދަވާ ބީއްސަވާ ހައްދަވައެވެ.","البخاري 300 واللفظ له، ومسلم 293"],["#047","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الحيض","ޙައިޟުގެ ބާބު","وَكَانَ يُخْرِجُ رَأْسَهُ إلَيَّ، وَهُوَ مُعْتَكِفٌ، فَأَغْسِلُهُ وَأَنَا حَائِضٌ»","އަދި ނަބިއްޔާ (މިސްކިތުގައި) އިޢުތިކާފުގައި ވަނިކޮށް، ތިމަން ކަމަނާއަށް އިސްތަށިފުޅު ދިއްކުރައްވައެވެ. (އެ ކަމަނާ ހުންނެވީ ގެކޮޅުގައެވެ.) އަދި ތިމަން ކަމަނާ ޙައިޟުވެރިވެފައިވާ ވަނިކޮށް ނަބިއްޔާގެ އިސްތަށިފުޅު ދޮންނަމެވެ. (ނޯޓު: އޭރު ކޮޓަރިކޮޅައި މިސްކިތް ވަނީ ޖެހިގެންނެވެ.)","البخاري 301 واللفظ له، ومسلم 297"],["#048","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الحيض","ޙައިޟުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: «كَانَ رَسُولُ اللَّهِ ﷺ يَتَّكِئُ فِي حِجْرِي، فَيَقْرَأُ الْقُرْآنَ وَأَنَا حَائِضٌ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ތިމަން ކަމަނާ ޙައިޟުވެރިވެފައި ވަނިކޮށް، ރަސޫލު ﷲ ﷺ ތިމަން ކަމަނާގެ އުނގުމަތީ އިސްތަށިފުޅު އަޅުއްވައިގެން އޮވެ ޤުރްއާން ކިޔަވައި ވިދާޅުވެއެވެ.","البخاري 297، ومسلم 301 واللفظ له"],["#049","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الحيض","ޙައިޟުގެ ބާބު","عَنْ مُعَاذَةَ قَالَتْ: «سَأَلْتُ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا فَقَلتُ: مَا بَالُ الْحَائِضِ تَقْضِي الصَّوْمَ، وَلا تَقْضِي الصَّلاةَ؟ فَقَالَتْ: أَحَرُورِيَّةٌ أَنْتِ؟ فَقُلْتُ: لَسْتُ بِحَرُورِيَّةٍ، وَلَكِنِّي أَسْأَلُ. فَقَالَتْ: كَانَ يُصِيبُنَا ذَلِكَ، فَنُؤَمَّرُ بِقَضَاءِ الصَّوْمِ، وَلا نُؤَمَّرُ بِقَضَاءِ الصَّلاةِ»","މުޢާޛާ ރިވާ ކުރެއްވިއެވެ: ޢައިޝާ رَضِيَ اللَّهُ عَنْهَا ގާތު ތިމަން ކަމަނާ ސުވާލު ކުރީމެވެ: ޙައިޟުވެރިޔާ ރޯދަ ޤަޟާކޮށްފައި ނަމާދު ޤަޟާ ނުކުރަނީ ކީއްވެގެންތޯއެވެ؟ ޢާއިޝާ ދެންނެވިއެވެ: ކަމަނާ ތިޔައީ ޙަރޫރީއެއްތޯއެވެ؟ (އެބަހީ: ޚަވާރިޖުންގެ މީހެއްތޯއެވެ. މިފަދައިން އެ ކަމަނާ ވިދާޅުވީ، އެބައިމީހުން އަމިއްލައަށް ދީނުގެ ކަންކަމުގައި ބުއްދިއަށް ފެންނަ ގޮތަށް ކަންކަން ނިންމައި، ޙުކުމްތައް ބަދަލު ކުރާތީއެވެ. އެ ގޮތުން ޙައިޟުވެރިން ނަމާދު ވެސް ޤަޟާ ކުރަން ޖެހޭނެކަމަށް އޭގެ ބައެއް މީހުން ބުންޏެވެ.) ތިމަން ކަމަނާ ދެންނެވީމެވެ: ތިމަން ކަމަނާއީ ޙަރޫރީއެއް ނޫނެކީމެވެ. އެހެނެއްކަމަކު ތިމަން ކަމަނާ އަހާލީއެވެ. ޢާއިޝާ ދެންނެވިއެވެ: ތިމަން ކަނބަލުންނަށް އެ (ޙައިޟުވުން) މެދުވެރިވެއެވެ. އަދި ތިމަން ކަނބަލުން ރޯދަ ޤަޟާ ކުރަން އަމުރު ވެވިގެންވެއެވެ. އެކަމަކު ނަމާދު ޤަޟާ ކުރާކަށް އަމުރު ވެވިގެނެއް ނުވެއެވެ.","البخاري 321، ومسلم 335 واللفظ له"],["#050","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","عَنْ أَبِي عَمْرٍو الشَّيْبَانِيِّ وَاسْمُهُ سَعْدُ بْنُ إيَاسٍ - قَالَ: حَدَّثَنِي صَاحِبُ هَذِهِ الدَّارِ - وَأَشَارَ بِيَدِهِ إلَى دَارِ عَبْدِ اللَّهِ بْنِ مَسْعُودٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «سَأَلْتُ النَّبِيَّ ﷺ: أَيُّ الْعَمَلِ أَحَبُّ إلَى اللَّهِ؟ قَالَ: الصَّلاةُ عَلَى وَقْتِهَا. قُلْتُ: ثُمَّ أَيُّ؟ قَالَ: بِرُّ الْوَالِدَيْنِ، قُلْتُ: ثُمَّ أَيُّ؟ قَالَ: الْجِهَادُ فِي سَبِيلِ اللَّهِ، قَالَ: حَدَّثَنِي بِهِنَّ رَسُولُ اللَّهِ ﷺ وَلَوْ اسْتَزَدْتُهُ لَزَادَنِي»","އަބްދު ﷲ ބިން މަސްޢޫދު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ އަރިހު ތިމަން ސުވާލު ކުރީމެވެ: ﷲ ގެ ޙަޟްރަތުގައި އެންމެ ލޮބުވެތިކަން ބޮޑު ޢަމަލަކީ ކޮބައިތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ނަމާދު އޭގެ ވަގުތުގައި އަދާ ކުރުމެވެ.“ ތިމަން ދެންނެވީމެވެ: އެއަށްފަހު ކޮން ކަމެއްތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”މައިންބަފައިންނަށް ހެޔޮކޮށް ހިތުމެވެ.“ ތިމަން ދެންނެވީމެވެ: އެއަށްފަހު ކޮން ކަމެއްތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲ ގެ މަގުގައި ޖިހާދު ކުރުމެވެ.“ ޢަބްދު ﷲ ވިދާޅުވިއެވެ: ރަސޫލު ﷲ ﷺ ތިމަންނައަށް ޙާދީޘް ކުރެއްވީ މި ކަންކަމެވެ. އަދި ތިމަން އިތުރަށް އެދުނު ނަމަ، ނަބިއްޔާ އިތުރަށް ޙަދީޘް ކުރެއްވީސްކަން ކަށަވަރެވެ.","البخاري 527، ومسلم 139"],["#051","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: «لَقَدْ كَانَ رَسُولُ اللَّهِ ﷺ يُصَلِّي الْفَجْرَ، فَيَشْهَدُ مَعَهُ نِسَاءٌ مِنْ الْمُؤْمِنَاتِ، مُتَلَفِّعَاتٍ بِمُرُوطِهِنَّ ثُمَّ يَرْجِعْنَ إلَى بُيُوتِهِنَّ مَا يَعْرِفُهُنَّ أَحَدٌ، مِنْ الْغَلَسِ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ފަތިސް ނަމާދު ކުރައްވާ ހިނދު، މުއުމިނު އަންހެނުން އެ ނަމާދުގައި ބައިވެރިވެއެވެ. އެ ކަނބަލުން އިންނަވަނީ ހެދުން ފޮތިކޮޅުތައް ގައިގެ ވަށައި އޮޅާލައި ނިވައިވެގެންނެވެ. ދެން (ނަމާދަށް) ފަހު އެ ކަނބަލުން އެނބުރި ގެތަކަށް ދެއެވެ. އަދި އަނދިރިކަމުގެ ސަބަބުން އެއްވެސް މީހަކަށް އެ ކަނބަލުން ވަކި ކުރާކަށެއް ނޭންގެއެވެ.","البخاري 372، 578 واللفظ له، ومسلم 645"],["#052","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رَضِيَ اللَّهُ عَنْهُما قَالَ: «كَانَ ﷺ يُصَلِّي الظُّهْرَ بِالْهَاجِرَةِ، وَالْعَصْرَ وَالشَّمْسُ نَقِيَّةٌ وَالْمَغْرِبَ إذَا وَجَبَتْ، وَالْعِشَاءَ أَحْيَاناً وَأَحْيَاناً إذَا رَآهُمْ اجْتَمَعُوا عَجَّلَ. وَإِذَا رَآهُمْ أَبْطَئُوا أَخَّرَ، وَالصُّبْحُ كَانَ النَّبِيُّ ﷺ يُصَلِّيهَا بِغَلَسٍ»","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ މެންދުރު ނަމާދު ކުރައްވަނީ މެންދުރު ގަޑީގައެވެ. އަދި ޢަޞުރު ކުރައްވަނީ އިރުގައި އަދިވެސް އަލިކަމެއް ވަނިކޮށެވެ. އަދި މަޢްރިބު ކުރައްވަނީ އިރު އޮއްސުމަށް ފަހުގައެވެ. އަދި ޢިޝާ ކުރައްވަނީ އެކިއެކި ގަޑިތަކުގައެވެ. މީސްތަކުން (ޢިޝާ ނަމާދާށް) އެއްވެފައި ވަނިކޮށް ނަބިއްޔާއަށް ފެނިވަޑައިގަންނަވާ ނަމަ، ނަމާދު ފައްޓެވުން އަވަސް ކުރައްވައެވެ. އަދި މީސްތަކުން (ނަމާދަށް) އައުން ލަސްކޮށްފި ނަމަ، ނަބިއްޔާ ވެސް ނަމާދު ލަސް ކުރައްވައެވެ. އަދި އަނދިރިވެފައި ވަނިކޮށް ނަބިއްޔާ ﷺ ފަތިސް ނަމާދު ކުރައްވައެވެ.","البخاري 547 واللفظ له، ومسلم 647"],["#053","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","عَنْ أَبِي الْمِنْهَالِ سَيَّارِ بْنِ سَلامَةَ قَالَ: «دَخَلْتُ أَنَا وَأَبِي عَلَى أَبِي بَرْزَةَ الأَسْلَمِيِّ، فَقَالَ لَهُ أَبِي: كَيْفَ كَانَ النَّبِيُّ ﷺ يُصَلِّي الْمَكْتُوبَةَ؟ فَقَالَ: كَانَ يُصَلِّي الْهَجِيرَ - الَّتِي تَدْعُونَهَا الأُولَى - حِينَ تَدْحَضُ الشَّمْسُ، وَيُصَلِّي الْعَصْرَ، ثُمَّ يَرْجِعُ أَحَدُنَا إلَى رَحْلِهِ فِي أَقْصَى الْمَدِينَةِ وَالشَّمْسُ حَيَّةٌ. وَنَسِيتُ مَا قَالَ فِي الْمَغْرِبِ. وَكَانَ يُسْتَحَبُّ أَنْ يُؤَخِّرَ مِنْ الْعِشَاءِ الَّتِي تَدْعُونَهَا الْعَتَمَةَ. وَكَانَ يَكْرَهُ النَّوْمَ قَبْلَهَا، وَالْحَدِيثُ بَعْدَهَا. وَكَانَ يَنْفَتِلُ مِنْ صَلاةِ الْغَدَاةِ حِينَ يَعْرِفُ الرَّجُلَ جَلِيسَهُ. وَكَانَ يَقْرَأُ بِالسِّتِّينَ إلَى الْمِائَةِ»","އަބޫ މިންހާލު ސައްޔާރު ބިން ސަލާމާ ރިވާ ކުރެއްވިއެވެ: އަބޫ ބަރްޒާ އަލްއަސްލަމީގެ އަރިހަށް، ތިމަންނައާއި ތިމަންނަގެ ބައްޕަ ވަނީމެވެ. އަދި ތިމަންނަގެ ބައްޕަ އެކަލޭގެފާނު ގާތު ދެންނެވިއެވެ: ރަސޫލު ﷲ ﷺ ފަރުޟު ފަސް ނަމާދު ކުރައްވަނީ ކިހިނަކުންތޯއެވެ؟ އަބޫ ބަރްޒާ ދެންނެވިއެވެ: ތިޔަބައިމީހުން ފުރަތަމަ ނަމާދު ކަމަށް ބުނާ މެންދުރު ނަމާދު ކުރައްވަނީ އިރު ތިރިވުމުނުންނެވެ. އެއަށްފަހު ޢަޞުރު ނަމާދު ކުރައްވައެވެ. ދެން ތިމަންމެން ކުރެ މީހަކު، މަދީނާގެ އެންމެ ދުރު ހިސާބުގައިވާ އޭނާގެ ގެއަށް އަނބުރާ ދެވޭހައި ވަގުތު ވަންދެން އިރު ހުރެއެވެ. -(އަބޫ މިންހާލު ވިދާޅުވިއެވެ:) އަދި މަޢްރިބު ނަމާދައި މެދު އަބޫ ބަރްޒާ ވިދާޅުވި އެއްޗެއް ތިމަން ހަނދާން ނެތިއްޖެއެވެ.- (ދެން އަބޫ ބަރްޒާ ވިދާޅުވިއެވެ:) އަދި ތިޔަބައިމީހުން ޢަތަމާ އޭ ތި ކިޔާ ޢިޝާ ނަމާދު ލަސް ކުރެއްވުމަށް ނަބިއްޔާ ލޯބިފުޅު ކުރެއްވިއެވެ. އަދި އޭގެ ކުރިން އަވަހާރަފުޅު ލެއްޕެވުމަށާއި އޭގެ ފަހުން ވާހަކަދައްކައި ހެދުމަށް ނަބިއްޔާ ނުރުއްސަވައެވެ. އަދި ފަތިސް ނަމާދުންކޮށްގެން ނުކުމެވަޑައިގަންނަވަނީ، މީހަކަށް އޭނާއާ ޖެހިގެން އިށީންދެ އިން މީހާ ދެނެގަނެވޭހައި އިރުވެ (އަލިވުމުންނެވެ.) އަދި (އެ ނަމާދުގައި) ފަސްދޮޅަސް އާޔަތާއި ސަތޭކަ އާޔަތާއި ދެމެދަށް (ޤުރްއާން) ކިޔަވައި ވިދާޅުވެއެވެ.","البخاري 560 واللفظ له، ومسلم 646"],["#054","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","عَنْ عَلِيٍّ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ النَّبِيَّ ﷺ قَالَ يَوْمَ الْخَنْدَقِ: «مَلأَ اللَّهُ قُبُورَهُمْ وَبُيُوتَهُمْ نَارًا، كَمَا شَغَلُونَا عَنْ الصَّلاةِ الْوُسْطَى حَتَّى غَابَتْ الشَّمْسُ» \n<br><br>\nوَفِي لَفْظٍ لِمُسْلِمٍ «شَغَلُونَا عَنْ الصَّلاةِ الْوُسْطَى - صَلاةِ الْعَصْرِ - ثُمَّ صَلاهَا بَيْنَ الْمَغْرِبِ وَالْعِشَاءِ»","ޢަލީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޚަންދަޤު ހަނގުރާމައިގެ ދުވަހު ޙަދީޘް ކުރެއްވިއެވެ: ”އެ (މުޝްރިކުންގެ) ކަށްވަޅުތަކާއި ގެތަށް އަލިފާނުން ފުރުއްވާށިއެވެ. އެއީ އިރު އޮއްސުމަށް ދާންދެން، އެބައިމިހުން ތިމަންމެން ޢަޞުރު ނަމާދު އަދާ ކުރިޔަ ނުދީ (ހަނގުރާމައިގައި) މަޝްޣޫލު ކުރުވީތީއެވެ.“ \n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ”އެބައިމިހުން ތިމަންމެން މެދު ނަމާދު (އެބަހީ: ޢަޞުރު ނަމާދު) ކުރިޔަ ނުދީ (ހަނގުރާމައިގައި) މަޝްޣޫލު ކުރުވައިފިއެވެ.“ އެއަށްފަހު މަޢްރިބާއި ޢިޝާއާ ދެމެދު ނަބިއްޔާ އެ ޢަޞުރު ނަމާދު އަދާ ކުރެއްވިއެވެ.","البخاري 6396 واللفظ له، ومسلم 627\n<br><br>\nمسلم 627"],["#055","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","وَلَهُ عَنْ عَبْدِ اللَّهِ بْنِ مَسْعُودٍ قَالَ: «حَبَسَ الْمُشْرِكُونَ رَسُولَ اللَّهِ ﷺ عَنِ الْعَصْرِ، حَتَّى احْمَرَّتِ الشَّمْسُ أَوْ اصْفَرَّتْ، فَقَالَ رَسُولُ اللَّهِ ﷺ: شَغَلُونَا عَنْ الصَّلاةِ الْوُسْطَى - صَلاةِ الْعَصْرِ - مَلأَ اللَّهُ أَجْوَافَهُمْ وَقُبُورَهُمْ نَاراً، أَوْ حَشَا اللَّهُ أَجْوَافَهُمْ وَقُبُورَهُمْ نَاراً»","އަދިވެސް މުސްލިމުގެ ރިވާޔަތެއްގައި، ޢަބްދު ﷲ ބިން މަސްޢޫދު ރިވާ ކުރެއްވިއެވެ: އިރު ރަތްވެ ނުވަތަ ރީނދޫ ވެއްޖައުމަށް ދާންދެން، (ހަނގުރާމައިގައި) މުޝްރިކުން ރަސޫލު ﷲ ﷺ ޢަޞުރު ނަމާދު ކުރިޔަ ނުދެއްވާ ހިފެހެއްޓިއެވެ. ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އެބައިމިހުން ތިމަންމެން މެދު ނަމާދު (އެބަހީ: ޢަޞުރު ނަމާދު) އަދާ ކުރިޔަ ނުދީ (ހަނގުރާމައިގައި) މަޝްޣޫލު ކުރުވައިފިއެވެ. ﷲ އެބައިމީހުންގެ ބަނޑުތަކާއި ކަށްވަޅުތައް އަލިފާނުން ފުރުއްވާށިއެވެ.“","مسلم 628"],["#056","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: «أَعْتَمَ النَّبِيُّ ﷺ بِالْعِشَاءِ. فَخَرَجَ عُمَرُ، فَقَالَ: الصَّلاةُ، يَا رَسُولَ اللَّهِ. رَقَدَ النِّسَاءُ وَالصِّبْيَانُ. فَخَرَجَ وَرَأْسُهُ يَقْطُرُ يَقُولُ: لَوْلا أَنْ أَشُقَّ عَلَى أُمَّتِي - أَوْ عَلَى النَّاسِ - لأَمَرْتُهُمْ بِهَذِهِ الصَّلاةِ هَذِهِ السَّاعَةِ»","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: (އެއް ރެއަކު) ނަބިއްޔާ ﷺ ޢިޝާ ނަމާދު ފެއްޓެވުން ލަސް ކުރެއްވިއެވެ. ދެން ޢުމަރު ނުކުމެ ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. ނަމާދެވެ. އަންހެނުންނާއި ކުޑަކުދިން ނިދިޖެހިއްޖެއެވެ. ދެން ނަބިއްޔާ ﷺ ގެ އިސްތަށިފުޅުން ފެންތިކި ޖަހަނިކޮށް ނުކުމެވަޑައިގެން ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާގެ އުންމަތުގެ މީސްތަކުންގެ މައްޗަށް ދަތި ކަމަކަށް ނުވާނަމަ، މި ނަމާދު މި ގަޑީގައި ކުރުމަށް އެބައިމިހުންނަށް އަމުރު ކުރެއްވީސްކަން ކަށަވަރެވެ.“","البخاري 7239 واللفظ له، ومسلم 206"],["#057","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","عَنْ عائِشَةَ رَضِيَ اللَّهُ عَنْهُا: أَنَّ النَّبِيَّ ﷺ قَالَ: «إذَا أُقِيمَتْ الصَّلاةُ، وَحَضَرَ الْعَشَاءُ، فَابْدَءُوا بِالْعَشَاءِ»\n<br><br>\nوَعَنْ ابْنِ عُمَرَ نَحْوُهُ.","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ނާމާދަށް ޤަމަތް ދެވޭ ހިނދު، ރޭގަނޑުގެ ކެއުން ތައްޔާރުވެ ގެނެވިއްޖެ ނަމަ، ފުރަތަމަ ރޭގަނޑުގެ ކެއުމުން ތިޔަބައިމީހުން ފަށާށެވެ.“\n<br><br>\nއަދި އިބްނު ޢުމަރުގެ އަރިހުންވެސް ކުރީގެ ޙަދީޘާ އެއްގޮތަކަށް ރިވާވެފައިވެއެވެ.","البخاري 5465 واللفظ له، ومسلم 558\n<br><br>\nالبخاري 673، ومسلم 559\n"],["#058","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","وَلِمُسْلِمٍ عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: سَمِعْتُ رَسُولَ اللَّهِ ﷺ يقُولُ: «لا صَلاةَ بِحَضْرَةِ طَعَامٍ، وَلا وَهُوَ يُدَافِعُهُ الأَخْبَثَانِ»","އަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން ކަމަނާ އަޑު އެހީމެވެ: ”ކާތަކެތި ތައްޔާރުވެ ގެނެވިފައި ވަނިކޮށް ނަމާދު ކުރުމެއް ނުވެއެވެ. އަދި (ހަމަ އެފަދައިން) ކުޑަކަމުދިއުމާއި ބޮޑުކަމުދިއުން ހިފަހައްޓަނިކޮށް ވެސް (ނަމާދު ކުރުމެއް) ނުވެއެވެ.","مسلم 560"],["#059","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: «شَهِدَ عِنْدِي رِجَالٌ مَرْضِيُّونَ - وَأَرْضَاهُمْ عِنْدِي عُمَرُ - أَنَّ النَّبِيَّ ﷺ نَهَى عَنْ الصَّلاةِ بَعْدَ الصُّبْحِ حَتَّى تَطْلُعَ الشَّمْسُ، وَبَعْدَ الْعَصْرِ حَتَّى تَغْرُبَ»","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: (އެބައިމީހުންގެ ތެދުވެރިކަމާއި ދީންވެރިކަމާއި މެދު ތިމަން) ރުހޭ ބޭކަލުންތަކެއް ތިމަން ގާތު ހެކިވިއެވެ. އަދި އެބައިމީހުންގެ ތެރެއިން ތިމަންނަ އެންމެ ރުހޭ ބޭކަލަކީ ޢުމަރެވެ. ފަތިސް ނަމާދަށް ފަހު އިރު އަރައިފުމަށް ދާންދެން ނަމާދު ކުރެއްވުން ނަބިއްޔާ ﷺ ނަހީ ކުރެއްވިއެވެ. އަދި (ހަމަ އެފަދައިން) ޢަޞުރު ނަމާދަށް ފަހު އިރު އޮއްސުމަށް ދާންދެން ވެސް މެއެވެ.","البخاري 581 واللفظ له، ومسلم 826"],["#060","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ - رَضِيَ اللَّهُ عَنْهُ - عَنْ رَسُولِ اللَّهِ ﷺ أَنَّهُ قالَ: «لا صَلاةَ بَعْدَ الصُّبْحِ حَتَّى تَرْتَفِعَ الشَّمْسُ، وَلا صَلاةَ بَعْدَ الْعَصْرِ حَتَّى تَغِيبَ الشَّمْسُ»\n<br><br>\nوفي البابِ عنْ عليِّ بنِ أَبي طالبٍ، وعبدِ اللهِ بنِ مسعودٍ، وعبدِ اللهِ بنِ عمرَ بنِ الخطابِ، وعبدِ اللهِ بنِ عمرِو بنِ العاصِ، وأَبي هريرةَ، وسَمُرَةَ بنِ جُندُبِ، وسَلَمَةَ بنِ الأَكوَعِ، وزيدِ بنِ ثابتٍ ومعاذِ بنِ جبلٍ، ومعاذِ بنِ عفراء، وكعبِ بنِ مُرَّةَ، وأَبي أُمامةَ الباهليِّ، وعمرِو بنِ عبسةَ السُلَميِّ، وعائشةَ رَضِيَ اللَّهُ عَنْهُم، والصَّنابحيِّ، ولم يسمعْ منَ النبيِّ ﷺ.","އަބޫ ސަޢީދުލް ޚުދްރީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ފަތިސް ނަމާދަށް ފަހު އިރު އަރައިފުމަށް ދާންދެން ނަމާދު ކުރުމެއް ނުވެއެވެ. އަދި ޢަޞުރު ނަމާދަށް ފަހު އިރު އޮއްސުމަށް ދާންދެން ނަމާދު ކުރުމެއް ނުވެއެވެ.\n<br><br>\n(ފޮތުގެ މުޞައްނިފު ވިދާޅުވިއެވެ:) އަދި ޢަލީ ބިން އަބީ ޠާލިބާއި، ޢަބްދު ﷲ ބިން މަސްޢޫދާއި، ޢަބްދު ﷲ ބިން ޢުމަރު ބިން އަލްޚައްޠާބާއި، ޢަބްދު ﷲ ބިން ޢަމްރު ބިން އަލްޢާޞްއާއި، އަބޫ ހުރައިރާއާއި، ސަމުރާ ބިން ޖުންދުބާއި، ސަލަމާ ބިން އަލްއަކްވާއާއި، ޒައިދު ބިން ޘާބިތާއި، މުޢާޛު ބިން ޖަބަލާއި، މުޢާޛު ބިން އަފްރާއާއި، ކަޢުބް ބިން މުއްރާއާއި، އަބޫ އުމާމާ އަލްބާހިލީއާއި، ޢަމްރު ބިން ޢަބަސާ އައްސުލަމީއާއި، ޢާއިޝާ رَضِيَ اللَّهُ عَنْهُمْ ގެ ރިވާޔަތްތައް މި ކަމާ ގުޅޭ ގޮތުން އައިސްފައިވެއެވެ. އަދި އައްޞުނާބިޙީގެ ރިވާޔަތެއް ވެސް އައިސްފައި ވަނިކޮށް، ނަބިއްޔާގެ އަރިހުން އެކަލޭގެފާނު ސީދާ އަޑުއަހާފައެއް ނުވެއެވެ.","البخاري 586 واللفظ له، ومسلم 827"],["#061","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رَضِيَ اللَّهُ عَنْهُما «أَنَّ عُمَرَ بْنَ الْخَطَّابِ رَضِيَ اللَّهُ عَنْهُ جَاءَ يَوْمَ الْخَنْدَقِ بَعْدَ مَا غَرَبَتِ الشَّمْسُ فَجَعَلَ يَسُبُّ كُفَّارَ قُرَيْشٍ، وَقَالَ: يَا رَسُولَ اللَّهِ، مَا كِدْتُ أُصَلِّي الْعَصْرَ حَتَّى كَادَتِ الشَّمْسُ تَغْرُبُ. فَقَالَ النَّبِيُّ ﷺ: وَاَللَّهِ مَا صَلَّيْتُهَا. قَالَ: فَقُمْنَا إلَى بَطْحَانَ، فَتَوَضَّأَ لِلصَّلاةِ، وَتَوَضَّأْنَا لَهَا، فَصَلَّى الْعَصْرَ بَعْدَ مَا غَرَبَتْ الشَّمْسُ. ثُمَّ صَلَّى بَعْدَهَا الْمَغْرِبَ»","ޖާބިރު ބިން އަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ޚަންދަޤު ހަނގުރާމައިގެ ދުވަހު، އިރު އޮއްސުމަށް ފަހު ޢުމަރު ބިން އަލްޚައްޠާބު ވަޑައިގެން ޤުރައިޝުގެ ކާފަރުންނަށް ބަދުބަސް ވިދާޅުވަން ފެއްޓެވިއެވެ. އަދި ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. އިރު އޮއްސުމަށް ދާންދެން ވެސް ތިމަންނައަށް ޢަޞުރު ނަމާދެއް ނުކުރެވުނެވެ. ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲ ގަންދީ ބުނަމެވެ. ތިމަން ނަބިއްޔާއަށް ވެސް އެ ނަމާދެއް ނުކުރެވެއެވެ.“ އެއަށްފަހު ތިމަންމެން (މަދީނާގެ ވާދީއެއް ކަމަށްވާ) ބުޠުޙާނަށް ދިޔުމުން، ނަބިއްޔާ ނަމާދަށް ވުޟޫ ކުރެއްވިއެވެ. އަދި ތިމަންމެން ވެސް ނަމާދަށް ވުޟޫ ކުރީމެވެ. ދެން ނަބިއްޔާ ޢަޞުރު ނަމާދު ކުރެއްވީ އިރު އޮއްސުމަށް ފަހުގައެވެ. އެއަށްފަހު ނަބިއްޔާ މަޢްރިބު ނަމާދު ކުރެއްވިއެވެ.","البخاري 596 واللفظ له، ومسلم 631"],["#062","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب فضل الجماعة ووجوبها","ޖަމާޢަތުގައި ނަމާދު ކުރުމުގެ ފަޟްލުވެރިކަމާއި އޭގެ ވާޖިބުކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «صَلاةُ الْجَمَاعَةِ أَفْضَلُ مِنْ صَلاةِ الْفَذِّ بِسَبْعٍ وَعِشْرِينَ دَرَجَةً»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވި: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އެކަނި ހުރެ ކުރާ ނަމާދަށް ވުރެ ޖަމާޢަތުގައި ކުރާ ނަމާދު ހަތާވީސް ގުނައަށް ދަރަޖަ އިތުރުވެގެން ވެއެވެ.“","البخاري 645، ومسلم 650 واللفظ له"],["#063","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب فضل الجماعة ووجوبها","ޖަމާޢަތުގައި ނަމާދު ކުރުމުގެ ފަޟްލުވެރިކަމާއި އޭގެ ވާޖިބުކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «صَلاةُ الرَّجُلِ فِي جَمَاعَةٍ تُضَعَّفُ عَلَى صَلاتِهِ فِي بَيْتِهِ وَفِي سُوقِهِ خَمْساً وَعِشْرِينَ ضِعْفاً، وَذَلِكَ: أَنَّهُ إذَا تَوَضَّأَ، فَأَحْسَنَ الْوُضُوءَ. ثُمَّ خَرَجَ إلَى الْمَسْجِدِ لا يُخْرِجُهُ إلا الصَّلاةُ لَمْ يَخْطُ خَطْوَةً إلا رُفِعَتْ لَهُ بِهَا دَرَجَةٌ، وَحُطَّ عَنْهُ خَطِيئَةٌ. فَإِذَا صَلَّى لَمْ تَزَلْ الْمَلائِكَةُ تُصَلِّي عَلَيْهِ، مَا دَامَ فِي مُصَلاهُ: اللَّهُمَّ صَلِّ عَلَيْهِ، اللَّهُمَّ اغْفِرْ لَهُ، اللَّهُمَّ ارْحَمْهُ، وَلا يَزَالُ فِي صَلاةٍ مَا انْتَظَرَ الصَّلاةَ»","އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ޖަމާޢަތުގައި މީހަކު ކުރާ ނަމާދު، ގޭގައި ނުވަތަ ބާޒާރުގައި އެކަނި ހުރެ ކުރާ ނަމާދަށް ވުރެ ފަންސަވީސް ގުނަ އިތުރު ވެގެންވެއެވެ. އެހެނީ އޭނާ ވުޟޫ ކުރާ ހިނދު އެންމެ ފުރިހަމަ ގޮތުގައި ވުޟޫ ކުރުމަށް ފަހު، ނުކުމެ މިސްކިތަށް ދާއިރު، އޭނާ ނުކުންނަ ހަމައެކަނި ކަމަކީ ނަމާދު ކުރުން ކަމުގައިވާ ނަމަ، ފަހެ އޭނާ އަޅާ ކޮންމެ ފިޔަވަޅަކަށް، އޭގެ ސަބަބުން ދަރަޖައެއް އުފުއްލެވި އަދި ފާފައެއް ފޮހެވިގެންދާނެ ގޮތަށް މެނުވީ އޭނާ ފިޔަވަޅެއް ނާޅާނެއެވެ. އަދި އޭނާ ނަމާދު ކުރާ އިރު، އޭނާގެ ނަމާދު ކުރާ ތަނުގައި އޭނާ ހުރިހާ ހިނދަކު މަލާއިކަތުން އޭނާގެ މައްޗަށް ޞަލަވާތް ލައްވައި ދުޢާ ކުރައްވާނެއެވެ: ޔާ ﷲ އެވެ. އޭނާގެ މައްޗަށް ޞަލަވާތް ލައްވާށިވެ. ޔާ ﷲ އެވެ. އޭނާގެ ފާފަ ފުއްސަވާށިއެވެ. ޔާ ﷲ އެވެ. އޭނާއަށް ރަޙުމަތް ލައްވާށެވެ. އަދި އޭނާ ނަމާދަށް އިންތިޒާރު ކުރާހައި ހިނދަކު، އޭނަ ވާނީ ނަމާދުގެ މަތީގައެވެ.“","البخاري 647 واللفظ له، ومسلم 649"],["#064","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب فضل الجماعة ووجوبها","ޖަމާޢަތުގައި ނަމާދު ކުރުމުގެ ފަޟްލުވެރިކަމާއި އޭގެ ވާޖިބުކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «أَثْقَلُ الصَّلاةِ عَلَى الْمُنَافِقِينَ: صَلاةُ الْعِشَاءِ، وَصَلاةُ الْفَجْرِ. وَلَوْ يَعْلَمُونَ مَا فِيهَا لأَتَوْهُمَا وَلَوْ حَبْوًا. وَلَقَدْ هَمَمْتُ أَنْ آمُرَ بِالصَّلاةِ فَتُقَامَ، ثُمَّ آمُرَ رَجُلاً فَيُصَلِّيَ بِالنَّاسِ، ثُمَّ أَنْطَلِقَ مَعِي بِرِجَالٍ مَعَهُمْ حُزَمٌ مِنْ حَطَبٍ إلَى قَوْمٍ لا يَشْهَدُونَ الصَّلاةَ، فَأُحَرِّقَ عَلَيْهِمْ بُيُوتَهُمْ بِالنَّارِ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން މުނާފިޤުންގެ މައްޗަށް އެންމެ ބުރަ އުނދަގޫ ނަމާދަކީ ޢިޝާ ނަމާދާއި ފަތިސް ނަމާދެވެ. އަދި އެބައިމީހުންނަށް އެ ދެ ނަމާދުގައިވާ (ސަވާބު) އެނގުނު ނަމަ، ފިރުކިގަނެގެން ނަމަވެސް އެ ދެ ނަމާދަށް އެބައިމީހުން އައިހެވެ. އަދި ހަމަކަށަވަރުން (މިފަދައިން ކަންތައް ކުރައްވަން) ތިމަން ނަބިއްޔާގެ ހިތްޕުޅަށް އަރައިވަޑައިގަތެވެ. ނަމާދަށް ޤަމަތް ދިނުމަށް ތިމަން ނަބިއްޔާ އަންގަވާނަމެވެ. ދެން މީސްތަކުންނައިގެން ނަމާދު ކުރުމަށް މީހަކަށް އަންގަވާނަމެވެ. އެއަށްފަހު އަލިފާން ހުޅުކޮޅުތަކެއް ހިފައިގެން ތިބި ފިރިހެނުންތަކަކާއެކު ނުކުމެ ވަޑައިގެން، ނަމާދަށް ހާޒިރު ނުވާ މީހުންގެ ގާތަށް ގޮސް، އެބައިމީހުންގެ (ބޯތައް) މައްޗަށް އެބައިމީހުންގެ ގެތައް ރޯ ކުރައްވާނަމެވެ.“","البخاري 644، ومسلم 651 واللفظ له"],["#065","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب فضل الجماعة ووجوبها","ޖަމާޢަތުގައި ނަމާދު ކުރުމުގެ ފަޟްލުވެރިކަމާއި އޭގެ ވާޖިބުކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما عَنِ النَّبيِّ ﷺ قَالَ: «إذَا اسْتَأْذَنَتْ أَحَدَكُمْ امْرَأَتُهُ إلَى الْمَسْجِدِ فَلا يَمْنَعُهَا. قَالَ: فَقَالَ بِلالُ بْنُ عَبْدِ اللَّهِ: وَاَللَّهُ لَنَمْنَعَهُنَّ. قَالَ: فَأَقْبَلَ عَلَيْهِ عَبْدُ اللَّهِ، فَسَبَّهُ سَبّاً سَيِّئاً، مَا سَمِعْتُهُ سَبَّهُ مِثْلَهُ قَطُّ، وَقَالَ: أُخْبِرُكَ عَنْ رَسُولِ اللَّهِ ﷺ وَتَقُولُ: وَاَللَّهُ لَنَمْنَعَهُنَّ؟»\n<br><br>\n وَفِي لَفْظٍ «لا تَمْنَعُوا إمَاءَ اللَّهِ مَسَاجِدَ اللَّهِ»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ ތެރެއިން މީހެއްގެ އަންހެނުން، މިސްކިތަށް ދިޔުމަށް ތިޔަބައިމީހުން ގާތު ހުއްދައަށް އެދިއްޖެ ނަމަ، ފަހެ އެ ކަމަނާ (މިސްކިތަށް ދިޔުމުން) މަނާ ނުކުރާށެވެ.“ (ޙަދީޘްގެ ރާވީ ވިދާޅުވިއެވެ: ޢަބްދު ﷲ ބިން ޢުމަރުގެ ދަރިކަލުން) ބިލާލު ބިން ޢަބްދު ﷲ ދެންނެވިއެވެ: ﷲ ގަންދީ ބުނަމެވެ. ހަމަކަށަވަރުން އެ ކަނބަލުން (މިސްކިތަށް ދިޔުން) މަނާ ކުރާނަމެވެ. ފަހެ ޢަބްދު ﷲ ބިން ޢުމަރު އެކަލޭގެފާނަށް ކުރިމަތިލެއްވުމަށް ފަހު، މީގެ ކުރިން ދުވަހަކުވެސް އިބްނު ޢުމަރު ވިދާޅުނުވަނިކޮށް ތިމަން ނުދެކޭ ހަރުކަށި ބަސްތަކަކުން އެކަލޭގެފާނަށް ބަދުބަސް ވިދާޅުވިއެވެ. އަދި ދެންނެވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ އަރިހުން އިވުނު ވާހަކަފުޅެއް ތިބާއަށް ކިޔައިދިނީމާ، ތިޔަ ބުނަނީ: ﷲ ގަންދީ ބުނަމޭ، ހަމަކަށަވަރުން އެ ކަނބަލުން (މިސްކިތަށް ދިޔުމުން) މަނާ ކުރާނަމޭހެއްޔެވެ؟ \n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ”ﷲ ގެ އަންހެން އަޅުތަކުން އެ އިލާހުގެ މިސްކިތްތަކުން މަނާ ނުކުރާށެވެ.“","البخاري 5238، ومسلم 442\n<br><br>\nالبخاري 900، ومسلم 442"],["#066","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب فضل الجماعة ووجوبها","ޖަމާޢަތުގައި ނަމާދު ކުރުމުގެ ފަޟްލުވެރިކަމާއި އޭގެ ވާޖިބުކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما قَالَ: «صَلَّيْتُ مَعَ رَسُولِ اللَّهِ ﷺ رَكْعَتَيْنِ قَبْلَ الظُّهْرِ، وَرَكْعَتَيْنِ بَعْدَهَا، وَرَكْعَتَيْنِ بَعْدَ الْجُمُعَةِ، وَرَكْعَتَيْنِ بَعْدَ الْمَغْرِبِ، وَرَكْعَتَيْنِ بَعْدَ الْعِشَاءِ» \n<br><br>\nوَفِي لَفْظِ: «فَأَمَّا الْمَغْرِبُ وَالْعِشَاءُ وَالْجُمُعَةُ: فَفِي بَيْتِهِ» \n<br><br>\nوَفِي لَفْظٍ: أَنَّ ابْنَ عُمَرَ قَالَ: حَدَّثَتْنِي حَفْصَةُ: أَنَّ النَّبِيَّ ﷺ: «كَانَ يُصَلِّي سَجْدَتَيْنِ خَفِيفَتَيْنِ بَعْدَمَا يَطْلُعُ الْفَجْرُ. وَكَانَتْ سَاعَةً لا أَدْخُلُ عَلَى النَّبِيِّ ﷺ فِيهَا»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ އަރިހުގައި މެންދުރު ނަމާދުގެ ކުރިން ދެ ރަކުޢަތް ތިމަން ކުރަމެވެ. އަދި އޭގެ ފަހުން ދެ ރަކުޢަތް ކުރަމެވެ. އަދި ހުކުރު ނަމާދަށް ފަހު ދެ ރަކުޢަތް ކުރަމެވެ. އަދި މަޢްރިބު ނަމާދަށް ފަހު ދެ ރަކުޢަތް ކުރަމެވެ. އަދި ޢިޝާ ނަމާދަށް ފަހު ދެ ރަކުޢަތް ކުރަމެވެ. \n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ފަހެ މަޢްރިބާއި ޢިޝާއާއި ހުކުރު ނަމާދުގެ (ސުންނަތްތައް) ނަބިއްޔާ ކުރައްވަނީ ގެކޮޅުގައެވެ.\n<br><br>\nޙަފްޞާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ފަތިސްވުމުން ނަބިއްޔާ ﷺ ލުއި ދެ ރަކުޢަތެއް ކުރައްވައެވެ. އަދި އެއީ ނަބިއްޔާ ﷺގެ އަރިހަށް ތިމަން ކަމަނާ ނުވަންނަ ގަޑިއެކެވެ.\n","البخاري 1165 واللفظ له، ومسلم 729\n<br><br>\nالبخاري 1172، ومسلم 729\n<br><br>\nالبخاري 1173"],["#067","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب فضل الجماعة ووجوبها","ޖަމާޢަތުގައި ނަމާދު ކުރުމުގެ ފަޟްލުވެރިކަމާއި އޭގެ ވާޖިބުކަމުގެ ބާބު","عنْ عائِشَةَ رضِيَ اللهُ عنْها قالَتْ: «لَمْ يَكُنِ النَّبِيُّ ﷺ على شيءٍ منَ النَّوافِلِ تَعاهُدَاً منْهُ على ركْعَتَي الفَجْرِ» \n<br><br>\nوفي لفْظٍ لِمُسْلِمٍ: «رَكْعَتا الفَجْرِ خيرٌ منَ الدُّنيا وما فيها»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ފަތިސް ނަމާދުގެ އިހު ދެ ރަކުޢަތް ކުރުންމަތީ ދެމިހުންނެވި ވަރަކަށް، އެހެން އެއްވެސް ސުންނަތް ނަމާދެއް ކުރުން މަތީ ދެމި ނުހުންނަވައެވެ. \n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތުގައި ވެއެވެ: ”ފަތިސް ނަމާދުގެ އިހު ދެ ރަކުޢަތް މުޅި ދުނިޔެއާއި އޭގައި ވާހައި އެއްޗަކަށް ވުރެ ހެޔޮވެގެންވެއެވެ.“","البخاري 1169 واللفظ له، ومسلم 724\n<br><br>\n"],["#068","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الأذان والإقامة","ބަންގިއާއި ޤަމަތުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ رَضِيَ اللَّهُ عَنْهُ قَالَ: «أُمِرَ بِلالٌ أَنْ يَشْفَعَ الأَذَانَ، وَيُوتِرَ الإِقَامَةَ»","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ބަންގީގައި ކިޔައި ތަކެތި ދެ ފަހަރު ކިޔުމަށާއި، ޤަމަތުގައި ކިޔައި ތަކެތި އެއްފަހަރު ކިޔުމަށް ބިލާލަށް އެންގެވުނެވެ.","مسلم 725"],["#069","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الأذان والإقامة","ބަންގިއާއި ޤަމަތުގެ ބާބު","عَنْ أَبِي جُحَيْفَةَ وَهْبِ بْنِ عَبْدِ اللَّهِ السُّوَائِيِّ قَالَ: «أَتَيْتُ النَّبِيَّ ﷺ وَهُوَ فِي قُبَّةٍ لَهُ حَمْرَاءَ مِنْ أَدَمٍ - قَالَ: فَخَرَجَ بِلالٌ بِوَضُوءٍ، فَمِنْ نَاضِحٍ وَنَائِلٍ، قَالَ: فَخَرَجَ النَّبِيُّ ﷺ عَلَيْهِ حُلَّةٌ حَمْرَاءُ، كَأَنِّي أَنْظُرُ إلَى بَيَاضِ سَاقَيْهِ، قَالَ: فَتَوَضَّأَ وَأَذَّنَ بِلالٌ، قَالَ: فَجَعَلْتُ أَتَتَبَّعُ فَاهُ هَهُنَا وَهَهُنَا، يَقُولُ يَمِيناً وَشِمَالاً: حَيَّ عَلَى الصَّلاةِ ; حَيَّ عَلَى الْفَلاحِ ثُمَّ رُكِزَتْ لَهُ عَنَزَةٌ، فَتَقَدَّمَ وَصَلَّى الظُّهْرَ رَكْعَتَيْنِ، ثُمَّ نَزَلَ يُصَلِّي رَكْعَتَيْنِ حَتَّى رَجَعَ إلَى الْمَدِينَةِ»","އަބޫ ޖުޙައިފާ، ވަހްބު ބިން ޢަބްދު ﷲ އައްސުވާއީ ރިވާ ކުރެއްވިއެވެ: ޖަނަވާރު ހަމުން ހެދިފައިވާ ރަތްކުލައިގެ ނަބިއްޔާ ﷺ ގެ ފޭލިގެކޮޅެއްގައި އެކަލޭގެފާނު (މައްކާގައި) ވަނިކޮށް، ނަބިއްޔާގެ އަރިހަށް ތިމަން އަތުވީމެވެ. އެ ހިނދު ފެން ކަންވާރެއް ހިފައިގެން ބިލާލު ނުކުންނެވިއެވެ. ފަހެ (ނަބިއްޔާ ވުޟޫ ކުރެއްވުމުން، އެ ފެނުން ބާކީވި އެއްޗަކުން ބަރަކާތް ހޯދުން) ގައިގައި ފެން އުނގުޅާ ހެދި ބަޔަކާއި، (އެ ފުރަތަމަ ފެން އުނގުޅާ ހެދި) މީހުން އަތުން (ދެވަނަ ފަހަރަށް ބާކީވި ފެން) ލިބުނު ބަޔަކު ވިއެވެ. ދެން ނަބިއްޔާ ﷺ ރަތް ފޭރާންކޮޅެއް ތުރުކުރައްވައިގެން ނުކުމެވަޑައިގަތެވެ. ނަބިއްޔާގެ ކަށިމަތިފުޅުގެ ދޮންކަން ތިމަންނައަށް މިހާރުވެސް ފެންނަ ފަދައެވެ. ދެން ނަބިއްޔާ ވުޟޫ ކުރެއްވިއެވެ. އަދި ބިލާލު ބަންގި ގޮވިއެވެ. ފަހެ ބިލާލު ކަނާތަށާއި ވާތަށް އަނގަ އަނބުރަމުން 'ނަމާދަށް އަވަހަށް އަންނާށޭ، ކާމިޔާބަށް އަވަހަށް އަންނާށޭ' މިފަދައިން ކިޔުއްވިތަން ތިމަން ބަލަން ހުރީމެވެ. ދެން ނަބިއްޔާއަށް (ސުތުރާއެއްގެ ގޮތުގައި) ލޮންސިކޮޅެއް ބިންމަތީ ހަރު ކުރެވުނެވެ. އަދި މެންދުރު ނަމާދު (ޤަޞްރުކޮށް) ދެ ރަކުޢަތް ކުރެއްވިއެވެ. އެއަށްފަހު މަދީނާއަށް އެނބުރި ދެވެންދެން (ދަތުރު މަތީ މަޑު ކުރެއްވި ކޮންމެ ތާކު ނަމާދު ކުރެއްވީ) ދެ ރަކުޢަތަށް (ޤަޞްރުކޮށްގެންނެވެ.)","البخاري 187، ومسلم 503 واللفظ له"],["#070","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الأذان والإقامة","ބަންގިއާއި ޤަމަތުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما عَنْ رَسُولِ اللَّهِ - ﷺ أَنَّهُ قَالَ: «إنَّ بِلالاً يُؤَذِّنُ بِلَيْلٍ، فَكُلُوا وَاشْرَبُوا حَتَّى تَسْمَعُوا أَذَانَ ابْنِ أُمِّ مَكْتُومٍ»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ބިލާލު ރޭގަނޑު (ފަތިސް ނުވަނީސް) ބަންގި ގޮވައެވެ. ފަހެ އިބްނު ޢުންމު މަކްތޫމް ބަންގި ގޮވަންދެން ތިޔަބައިމީހުން ކާށެވެ.“","البخاري 617، ومسلم 1092 واللفظ له"],["#071","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الأذان والإقامة","ބަންގިއާއި ޤަމަތުގެ ބާބު","عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «إذَا سَمِعْتُمْ الْمُؤَذِّنَ فَقُولُوا مِثْلَ مَا يَقُولُ»","އަބޫ ސަޢީދުލް ޚުދްރީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންނަށް ބަންގި ގޮވާ މީހާ އިވިއްޖެ ނަމަ، އޭނާ ކިޔާ ފަދައިން ތިޔަބައިމީހުން ވެސް ކިޔާށެވެ.“","البخاري 611، ومسلم 383"],["#072","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب استقبال القبلة","ޤިބްލައަށް ކުރިމަތިލުމުގެ ބާބު","عَنْ ابْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما: «أَنَّ رَسُولَ اللَّهِ ﷺ كَانَ يُسَبِّحُ عَلَى ظَهْرِ رَاحِلَتِهِ، حَيْثُ كَانَ وَجْهُهُ، يُومِئُ بِرَأْسِهِ، وَكَانَ ابْنُ عُمَرَ يَفْعَلُهُ» \n<br><br>\nوَفِي رِوَايَةٍ: «كَانَ يُوتِرُ عَلَى بَعِيرِهِ» \n<br><br>\nوَلِمُسْلِمٍ: «غَيْرَ أَنَّهُ لا يُصَلِّي عَلَيْهَا الْمَكْتُوبَةَ» \n<br><br>\nوَلِلْبُخَارِيِّ: «إلاَّ الْفَرَائِضَ»\n","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ސަވާރީ މަތީ ހުންނަވައިގެން، އެ ސަވާރީ ކުރިމަތިލާ ދިމާއަކަށް ނަބިއްޔާ (ސުންނަތް) ނަމާދު ކުރައްވައެވެ. އަދި ނަބިއްޔާގެ އިސްތަށިފުޅުން އިޝާރަތް ކުރައްވާ (ރުކޫޢުކޮށް ސަޖިދަކޮށް) ހައްދަވައެވެ. އަދި އިބްނު ޢުމަރު ވެސް ހަމަ އެފަދައިން ކަންތައް ކުރެއްވިއެވެ.\n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ނަބިއްޔާގެ ޖަމަލުކޮޅު މަތީ ވިތުރި ކުރައްވައެވެ.\n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ފަރުޟު ނަމާދުތައް އޭގެ މަތީ ނުކުރައްވައެވެ. \n<br><br>\nއަދި ބުޚާރީގެ ރިވާޔަތެއްގައި ވެއެވެ: ފަރުޟު ނަމާދުގައި އެފަދައިން ކަންތައް ނުކުރައްވައެވެ.","البخاري 1105 واللفظ له، ومسلم 700\n<br><br>\nالبخاري 999، ومسلم 700\n<br><br>\nمسلم 700\n<br><br>\nالبخاري 1000"],["#073","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب استقبال القبلة","ޤިބްލައަށް ކުރިމަތިލުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما قَالَ: «بَيْنَمَا النَّاسُ بِقُبَاءَ فِي صَلاةِ الصُّبْحِ إذْ جَاءَهُمْ آتٍ، فَقَالَ: إنَّ النَّبِيَّ ﷺ قَدْ أُنْزِلَ عَلَيْهِ اللَّيْلَةَ قُرْآنٌ، وَقَدْ أُمِرَ أَنْ يَسْتَقْبِلَ الْقِبْلَةَ، فَاسْتَقْبِلُوهَا. وَكَانَتْ وُجُوهُهُمْ إلَى الشَّامِ، فَاسْتَدَارُوا إلَى الْكَعْبَةِ»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ. ޤުބާގައި މީސްތަކުން ފަތިސް ނަމާދު ކުރަނިކޮށް، މީހަކު އައިސް ދެންނެވިއެވެ: ހަމަކަށަވަރުން މިރޭ ނަބިއްޔާ ﷺ ގެ މައްޗަށް ޤުރްއާން ބާވައިލެއްވުނެވެ. އަދި ކަޢުބާއަށް ކުރިމަތިލެއްވުމަށް ނަބިއްޔާއަށް އަމުރު ކުރެއްވުނެވެ. ފަހެ ތިޔަބައިމިހުން ވެސް ކަޢުބާއަށް ކުރިމަތިލާށެވެ. (އޭގެ ކުރިން) އެބައިމީހުންގެ މޫނުތައް ވަނީ ޝާމާ ދިމާއަށެވެ. ދެން އެބައިމީހުން ކާއުބާއާ ދިމާއަށް އެނބުރުނެވެ.","البخاري 403 واللفظ له، ومسلم 526"],["#074","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب استقبال القبلة","ޤިބްލައަށް ކުރިމަތިލުމުގެ ބާބު","عَنْ أَنَسِ بْنِ سِيرِينَ قَالَ: «اسْتَقْبَلْنَا أَنَساً حِينَ قَدِمَ مِنْ الشَّامِ، فَلَقِينَاهُ بِعَيْنِ التَّمْرِ، فَرَأَيْتُهُ يُصَلِّي عَلَى حِمَارٍ، وَوَجْهُهُ مِنْ ذَا الْجَانِبِ - يَعْنِي عَنْ يَسَارِ الْقِبْلَةِ - فَقُلْتُ: رَأَيْتُكَ تُصَلِّي لِغَيْرِ الْقِبْلَةِ؟ فَقَالَ: لَوْلا أَنِّي رَأَيْتُ رَسُولَ اللَّهِ ﷺ يَفْعَلُهُ مَا فَعَلْتُهُ»","އަނަސް ބިން ސީރީން ރިވާ ކުރެއްވިއެވެ: އަނަސް ބިން މާލިކު ޝާމުން ވަޑައިގެންނެވި ހިނދު، އެކަލޭގެފާނާ ބައްދަލު ކުރަން ތިމަންމެން ނުކުތީމެވެ. އެކަލޭގެފާނާ ބައްދަލުވީ ޢައިނުއް ތަމްރު އޭ ކިޔުނު ތަނެއްގައެވެ. އެކަލޭގެފާނު ހިމާރެއް މަތީ ނަމާދު ކުރައްވަން ހުންނެވިއެވެ. އަދި އެކަލޭގެފާނުގެ މޫނުފުޅު ޤިބުލައިގެ ވާތަށް ހުންނަވަނިކޮށް ތިމަން ދުށީމެވެ. ތިމަން ދެންނެވީމެވެ: ޤިބުލަ ނޫން ދިމާއަކަށް ތިބާ ނަމާދު ކުރައްވަނިކޮށް ދުށީމެވެ. އެކަލޭގެފާނު ދެންނެވިއެވެ: ރަސޫލު ﷲ ﷺ އެ ފަދައިން ކަންކުރައްވަނިކޮށް ތިމަން ނުދެކޭ ނަމަ، ތިމަންނަ އެފަދައިން ކަންނުކުރެއްވީސްކަން ކަށަވަރެވެ.","البخاري 1100 واللفظ له، ومسلم 702"],["#075","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الصفوف","ސަފުހެދުމުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «سَوُّوا صُفُوفَكُمْ، فَإِنَّ تَسْوِيَةَ الصُّفُوفِ مِنْ تَمَامِ الصَّلاةِ»","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ ސަފުތައް ސީދާ ކުރާށެވެ. އެހެނީ ހަމަކަށަވަރުން ސަފުތައް ސީދާ ކުރުމަކީ ނަމާދުގެ ފުރިހަމަކަމުގެ ތެރެއިންވާ ކަމެކެވެ.“","البخاري 723، ومسلم 433 واللفظ له"],["#076","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الصفوف","ސަފުހެދުމުގެ ބާބު","عَنِ النُّعْمَانِ بْنِ بَشِيرٍ رَضِيَ اللَّهُ عَنْهُما، قَالَ: سَمِعْتُ رَسُولَ اللَّهِ ﷺ يَقُولُ: «لَتُسَوُّنَّ صُفُوفَكُمْ أَوْ لَيُخَالِفَنَّ اللَّهُ بَيْنَ وُجُوهِكُمْ» \n<br><br>\nوَلِمُسْلِمٍ: «كَانَ رَسُولُ اللَّهِ ﷺ يُسَوِّي صُفُوفَنَا، حَتَّى كَأَنَّمَا يُسَوِّي بِهَا الْقِدَاحَ، حَتَّى إذَا رَأَى أَنْ قَدْ عَقَلْنَا عَنْهُ، ثُمَّ خَرَجَ يَوْمًا فَقَامَ، حَتَّى إذَا كَادَ أَنْ يُكَبِّرَ، فَرَأَى رَجُلاً بَادِياً صَدْرُهُ، فَقَالَ: عِبَادَ اللَّهِ، لَتُسَوُّنَّ صُفُوفَكُمْ أَوْ لَيُخَالِفَنَّ اللَّهُ بَيْنَ وُجُوهِكُمْ»","އައްނުޢުމާން ބިން ބަޝީރު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ: ”ހަމަކަށަވަރުން ތިޔަބައިމީހުންގެ ސަފުތައް ތިޔަބައިމީހުން ސީދާ ކުރާނީއެވެ. ނޫން ނަމަ، ތިޔަބައިމީހުންގެ މެދުގައި ﷲ ބައިބައި ކުރައްވާނެއެވެ.“ \n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ދުނިދަނޑި ސީދާ ކުރާ ފަދައިން، ރަސޫލު ﷲ ﷺ ތިމަންމެންގެ ސަފުތައް ސީދާ ކުރައްވައެވެ. އެއީ ނަބިއްޔާގެ އަރިހުން އެ ގޮތަށް ކަންކުރަން ތިމަންމެންނަށް ދަސްވެއްޖެކަން ނަބިއްޔާއަށް ފެނިވަޑައިގަތުމަށް ދާންދެނެވެ. ފަހެ އެއް ދުވަހަކު ނަބިއްޔާ ނުކުމެވަޑައިގެން ނަމާދު ކުރެއްވުމަށް ތެދުވެ ތަކްބީރު ދެއްވަން އުޅުއްވި ހިނދު، މީހަކު އޭނާގެ މޭ ކުރިއަށް ނެރެގެން ހުއްޓާ ފެނިވަޑައިގަތެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲ ގެ އަޅުތަކުންނޭވެ. ހަމަކަށަވަރުން ތިޔަބައިމީހުންގެ ސަފުތައް ތިޔަބައިމީހުން ސީދާ ކުރާނީއެވެ. ނޫން ނަމަ، ތިޔަބައިމީހުންގެ މެދުގައި ﷲ ބައިބައި ކުރައްވާނެއެވެ.“","البخاري 717، ومسلم 436\n<br><br>\nمسلم 436"],["#077","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الصفوف","ސަފުހެދުމުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ -: «أَنَّ جَدَّتَهُ مُلَيْكَةَ دَعَتْ رَسُولَ اللَّهِ ﷺ لِطَعَامٍ صَنَعَتْهُ، فَأَكَلَ مِنْهُ، ثُمَّ قَالَ: قُومُوا فَلأُصَلِّيَ لَكُمْ؟ قَالَ أَنَسٌ: فَقُمْتُ إلَى حَصِيرٍ لَنَا قَدْ اسْوَدَّ مِنْ طُولِ مَا لُبِسَ، فَنَضَحْتُهُ بِمَاءٍ، فَقَامَ عَلَيْهِ رَسُولُ اللَّه ﷺ وَصَفَفْتُ أَنَا وَالْيَتِيمُ وَرَاءَهُ، وَالْعَجُوزُ مِنْ وَرَائِنَا. فَصَلَّى لَنَا رَكْعَتَيْنِ، ثُمَّ انْصَرَفَ» \n<br><br>\nوَلِمُسْلِمٍ «أَنَّ رَسُولَ اللَّهِ ﷺ صَلَّى بِهِ وَبِأُمِّهِ فَأَقَامَنِي عَنْ يَمِينِهِ، وَأَقَامَ الْمَرْأَةَ خَلْفَنَا»","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: އެކަލޭގެފާނުގެ މާމާފުޅު މުލައިކާ ޖާފަތެއް ތައްޔާރު ކުރެއްވުމަށްފަހު ރަސޫލު ﷲ ﷺ އަށް ދަޢުވަތު އެރުވިއެވެ. ނަބިއްޔާ އޭގެން ފަރީއްކުޅުވުމަށް ފަހު ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންނައިގެން ތިމަން ނަބިއްޔާ ނަމާދު ކުރެއްވުމަށް ތިޔަބައިމީހުން ތެދުވާށެވެ.“ އަނަސް ވިދާޅުވިއެވެ: ފަހެ ތިމަންމެން އަތުގައި ހުރި ކުނަލެއް ނަގަން ތިމަން ތެދުވީމެވެ. އޭތި ހުރެހުރެ ގިނަ ދުވަސްވުމުގެ ސަބަބުން ހުރީ ކަޅުވެފައެވެ. ދެން ތިމަން އޭގައި ފެންފޮދެއް ބުރުވާލީމެވެ. ދެން ރަސޫލު ﷲ ﷺ އޭގެ މަތީ ހުންނަވައިގެން ނަމާދު ކުރެއްވިއެވެ. ތިމަންނައާއި ޔަތީމު ކުއްޖަކު ނަބިއްޔާގެ ފަހަތްޕުޅުގައި ސަފު ހެދީމެވެ. އަދި އުމުރުން ދުވަސްވެފައި ހުންނެވި (މުލައިކާ) ތިމަންމެންގެ ފަހަތުގައި ސަފު ހެއްދެވިއެވެ. ދެން ތިމަންމެންނައިގެން ނަބިއްޔާ ދެ ރަކުޢަތް ކުރެއްވިއެވެ. އަދި އެއަށްފަހު ނުކުމެވަޑައިގަތެވެ.\n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: އަނަސްއާއި އެކަލޭގެފާނުގެ މަންމާފުޅައިގެން، ރަސޫލު ﷲ ﷺ ނަމާދު ކުރެއްވިއެވެ. (އަނަސް ވިދާޅުވިއެވެ:) ފަހެ ނަބިއްޔާގެ ކަނާތް ފަރާތުގައި ނަބިއްޔާ ތިމަން ބެހެއްޓެވިއެވެ. އަދި ތިމަންމެންގެ ފަހަތުގައި އެ ކަނބުލޭގެ ބެހެއްޓެވިއެވެ.","البخاري 380، ومسلم 658\n<br><br>\nمسلم 660"],["#078","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الصفوف","ސަފުހެދުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما: قَالَ: «بِتُّ عِنْدَ خَالَتِي مَيْمُونَةَ. فَقَامَ النَّبِيُّ ﷺ يُصَلِّي مِنْ اللَّيْلِ. فَقُمْتُ عَنْ يَسَارِهِ. فَأَخَذَ بِرَأْسِي فَأَقَامَنِي عَنْ يَمِينِهِ»","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ތިމަންނަގެ ބޮޑުދައިތާފުޅު މައިމޫނާގެ ގެކޮޅުގައި އެއް ރޭ މަޑު ކުރީމެވެ. ފަހެ ނަބިއްޔާ ﷺ ނަމާދު ކުރައްވަން ތެދުވެވަޑައިގަތެވެ. ދެން ތިމަން ވެސް ތެދުވެ، ނަބިއްޔާގެ ވާތް ފަރާތުގައި ހުރީމެވެ. އެހެނަސް ނަބިއްޔާ ތިމަންނަގެ ކަންފަތުގައި ހިއްޕަވައިގެން، ނަބިއްޔާގެ ކަނާތް ފަރާތަށް ތިމަން ގެންނެވިއެވެ.","البخاري 699 واللفظ له، ومسلم 763"],["#079","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الإمامة","އިމާމުކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - عَنْ النَّبِيِّ ﷺ قَالَ: «أَمَا يَخْشَى الَّذِي يَرْفَعُ رَأْسَهُ قَبْلَ الإِمَامِ أَنْ يُحَوِّلَ اللَّهُ رَأْسَهُ رَأْسَ حِمَارٍ، أَوْ يَجْعَلَ صُورَتَهُ صُورَةَ حِمَارٍ؟»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އިމާމުގެ ކުރިން ބޯ އުފުލާ މީހާ، އޭނާގެ ބޯ ހިމާރެއްގެ ބޮލަކަށް ﷲ ބަދަލު ކުރައްވައިފާނެކަމަށް ބިރު ނުގަންނަނީތޯއެވެ؟ ނޫނީ އޭނާގެ ސޫރާ ހިމާރެއްގެ ސޫރައަށް ބަދަލު ކުރައްވައިފާނެކަމަށް ބިރު ނުގަންނަނީތޯއެވެ؟“","البخاري 691، ومسلم 427"],["#080","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الإمامة","އިމާމުކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - عَنْ النَّبِيِّ ﷺ قَالَ: «إنَّمَا جُعِلَ الإِمَامُ لِيُؤْتَمَّ بِهِ. فَلا تَخْتَلِفُوا عَلَيْهِ. فَإِذَا كَبَّرَ فَكَبِّرُوا، وَإِذَا رَكَعَ فَارْكَعُوا. وَإِذَا قَالَ: سَمِعَ اللَّهُ لِمَنْ حَمِدَهُ، فَقُولُوا: رَبَّنَا وَلَكَ الْحَمْدُ. وَإِذَا سَجَدَ فَاسْجُدُوا. وَإِذَا صَلَّى جَالِساً فَصَلُّوا جُلُوساً أَجْمَعُونَ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އިމާމު މީހާ ލެއްވިގެން ވަނީ ހަމައެކަނި އޭނާއަށް ތަބާވެވުމަށެވެ. ފަހެ ތިޔަބައިމީހުން އޭނާއާ ޚިލާފު ނުވާށެވެ. އޭނާ ތަކްބީރު ދީފި ހިނދު، ތިޔަބައިމީހުން ވެސް ތަކްބީރު ދޭށެވެ. އަދި އޭނާ ރުކޫޢުކޮށްފި ހިނދު، ތިޔަބައިމީހުން ވެސް ރުކޫޢު ކުރާށެވެ. އަދި އޭނާ سَمِعَ اللَّهُ لِمَنْ حَمِدَهُ ކިޔައިފި ހިނދު، ތިޔަބައިމީހުން رَبَّنَا وَلَكَ الْحَمْدُ ކިޔާށެވެ. އަދި އޭނާ ސަޖިދަކޮށްފި ހިނދު، ތިޔަބައިމީހުން ވެސް ސަޖިދަ ކުރާށެވެ. އަދި އޭނާ އިށީނދެ އިނދެ ނަމާދުކޮށްފި ހިނދު، ތިޔަބައިމީހުން އެންމެން ވެސް އިށީނދެ އިނދެ ނަމާދު ކުރާށެވެ.“","البخاري 722، ومسلم 414 واللفظ له"],["#081","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الإمامة","އިމާމުކަމުގެ ބާބު","وَمَا فِي مَعْنَاهُ مِنْ حَدِيثِ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: «صَلَّى رَسُولُ اللَّهِ ﷺ فِي بَيْتِهِ وَهُوَ شَاكٍ، صَلَّى جَالِساً، وَصَلَّى وَرَاءَهُ قَوْمٌ قِيَاماً، فَأَشَارَ إلَيْهِمْ: أَنْ اجْلِسُوا لَمَّا انْصَرَفَ قَالَ إنَّمَا جُعِلَ الإِمَامُ لِيُؤْتَمَّ بِهِ، فَإِذَا رَكَعَ فَارْكَعُوا، وَإِذَا رَفَعَ فَارْفَعُوا، وَإِذَا قَالَ: سَمِعَ اللَّهُ لِمَنْ حَمِدَهُ فَقُولُوا: رَبَّنَا لَكَ الْحَمْدُ، وَإِذَا صَلَّى جَالِساً فَصَلُّوا جُلُوساً أَجْمَعُونَ»","އަދި ކުރީގެ ޙަދީޘާ އެއްފަދަ މާނައެއްގައި ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ އާލާސްކަންފުޅައިގެން އުޅުއްވި ހިނދު، އިށީނދެ އިނދެ ނަމާދު ކުރެއްވިއެވެ. ދެން ނަބިއްޔާގެ ފަހަތްޕުޅުގައި މީސްތަކުން ކޮއްޅަށް ހުރެ ނަމާދު ކުރިއެވެ. ފަހެ އެބައިމީހުން އިށީންނުމަށް ނަބިއްޔާ އިޝާރާތް ކުރެއްވިއެވެ. އަދި ނަމާދުން އަވަދިވެ ވަޑައިގެންނެވުމަށް ފަހު ޙަދީޘް ކުރެއްވިއެވެ: ”އިމާމު މީހާ ލެއްވިގެން ވަނީ ހަމައެކަނި އޭނާއަށް ތަބާވެވުމަށެވެ. ފަހެ އޭނާ ރުކޫޢުކޮށްފި ހިނދު، ތިޔަބައިމީހުން ވެސް ރުކޫޢު ކުރާށެވެ. އަދި އޭނާ ބޯ އުފުލައިފި ހިނދު، ތިޔަބައިމީހުން ވެސް ބޯ އުފުލާށެވެ. އަދި އޭނާ سَمِعَ اللَّهُ لِمَنْ حَمِدَهُ ކިޔައިފި ހިނދު، ތިޔަބައިމީހުން رَبَّنَا وَلَكَ الْحَمْدُ ކިޔާށެވެ. އަދި އޭނާ އިށީނދެ އިނދެ ނަމާދުކޮށްފި ނަމަ، ތިޔަބައިމީހުން އެންމެން ވެސް އިށީނދެ އިނދެ ނަމާދު ކުރާށެވެ.“","البخاري 688 واللفظ له، ومسلم 412"],["#082","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الإمامة","އިމާމުކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ يَزِيدَ الْخِطْمِيِّ الأَنْصَارِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: حَدَّثَنِي الْبَرَاءُ - وَهُوَ غَيْرُ كَذُوبٍ - قَالَ: «كَانَ رَسُولُ اللَّهِ ﷺ إذَا قَالَ: سَمِعَ اللَّهُ لِمَنْ حَمِدَهُ: لَمْ يَحْنِ أَحَدٌ مِنَّا ظَهْرَهُ حَتَّى يَقَعَ رَسُولُ اللَّهِ ﷺ سَاجِدًا، ثُمَّ نَقَعُ سُجُودًا بَعْدَهُ»","އަލްބަރާއު ބިން ޢާޒިބު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ سَمِعَ اللَّهُ لِمَنْ حَمِدَهُ ވިދާޅުވެއްޖެ ހިނދު، ނަބިއްޔާ ސަޖިދައަށް ދަންދެން ތިމަންމެން ކުރެ އެކަކުވެސް (ސަޖިދައަށް ދާން) އޭނާގެ ބުރަކަށި ލަނބައި ވެސް ނުލައެވެ. ދެން ނަބިއްޔާއަށް ފަހު ތިމަންމެން ސަޖިދައަށް ތިރިވަމެވެ.","البخاري 690، ومسلم 474"],["#083","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الإمامة","އިމާމުކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «إذَا أَمَّنَ الإِمَامُ فَأَمِّنُوا، فَإِنَّهُ مَنْ وَافَقَ تَأْمِينُهُ تَأْمِينَ الْمَلائِكَةِ: غُفِرَ لَهُ مَا تَقَدَّمَ مِنْ ذَنْبِهِ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އިމާމު މީހާ އާމީން ކިޔައިފި ހިނދު، ތިޔަބައިމީހުން ވެސް އާމީން ކިޔާށެވެ. އެހެނީ ހަމަކަށަވަރުން މީހެއްގެ އާމިން، މަލާއިކަތުންގެ އާމީންއާ ދިމާވެއްޖެ ނަމަ، އެ މީހެއްގެ އިސްވެ ގޮސްފައިވާ ފާފަތައް ފުއްސެވޭނެއެވެ.“","البخاري 780، ومسلم 410"],["#084","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الإمامة","އިމާމުކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «إذَا صَلَّى أَحَدُكُمْ لِلنَّاسِ فَلْيُخَفِّفْ فَإِنَّ فِيهِمْ الضَّعِيفَ وَالسَّقِيمَ وَذَا الْحَاجَةِ، وَإِذَا صَلَّى أَحَدُكُمْ لِنَفْسِهِ فَلْيُطَوِّلْ مَا شَاءَ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ކުރެ މީހަކު މީސްތަކުންނައިގެން ނަމާދު ކުރާ ނަމަ، ލުއިފަސޭހަ ކުރާށެވެ. އެހެނީ އެބައިމީހުންގެ ތެރޭގައި ބަލި މީހުންނާއި، ބަލިކަށި މީހުންނާއި، ހާޖަތް ޖެހިފައިވާ މީހުން ތިބޭނެކަށް ކަށަވަރެވެ. އަދި ތިޔަބައިމީހުން ކުރެ މީހާކު އޭނާގެ އަމިއްލަ ނަފްސަށް ނަމާދު ކުރާ ހިނދު، އޭނާ ބޭނުން ވަރަކަށް ނަމާދު ދިގު ކުރެވިދާނެއެވެ.“","البخاري 703، ومسلم 467"],["#085","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الإمامة","އިމާމުކަމުގެ ބާބު","وَمَا فِي مَعْنَاهُ مِنْ حَدِيثِ أَبِي مَسْعُودٍ الأَنْصَارِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «جَاءَ رَجُلٌ إلَى رَسُولِ اللَّهِ ﷺ فَقَالَ: إنِّي لأَتَأَخَّرُ عَنْ صَلاةِ الصُّبْحِ مِنْ أَجْلِ فُلانٍ، مِمَّا يُطِيلُ بِنَا، قَالَ: فَمَا رَأَيْتُ النَّبِيَّ ﷺ غَضِبَ فِي مَوْعِظَةٍ قَطُّ أَشَدَّ مِمَّا غَضِبَ يَوْمَئِذٍ، فَقَالَ: يَا أَيُّهَا النَّاسُ، إنَّ مِنْكُمْ مُنَفِّرِينَ، فَأَيُّكُمْ أَمَّ النَّاسَ فَلْيُوجِزْ، فَإِنَّ مِنْ وَرَائِهِ الْكَبِيرَ وَالضَّعِيفَ وَذَا الْحَاجَةِ»","އަދި ކުރީގެ ޙަދީޘާ އެއްފަދަ މާނައެއްގައި އަބޫ މަސްޢޫދު އަލްއަންޞާރީ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ އަރިހަށް މީހަކު އައިސް ދެންނެވިއެވެ: ހަމަކަށަވަރުން މިވެނި މީހަކު ތިމަންމެންނައިގެން ފަތިސް ނަމާދު ކުރާލެއް ދިގުކަމުން، އެ ނަމާދުން ތިމަން ފަސްޖެހެމެވެ. އަބޫ މަސްޢޫދު ވިދާޅުވިއެވެ: އެ ދުވަހު ނަބިއްޔާ ﷺ ކޯފާވެވަޑައިގަތް ވަރަށް ވުރެ ދުވަހަކުވެސް ވަޢުޡު ދިނުމުގައި ނަބިއްޔާ ކޯފާވެވަޑައިގަތް ތަނެއް ތިމަން ނުދެކެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ މީސްތަކުންނޭވެ. ތިޔަބައިމީހުންގެ ތެރޭގައި (އެހެން މީހުން) ފައްސާ ބަޔަކު އުޅޭކަން ކަށަވަރެވެ. ތިޔަބައިމީހުން ކުރެ މީހަކު މީސްތަކުންނައިގެން އިމާމުވެ ނަމާދު ކުރާ ނަމަ، ފަހެ ލުއިފަސޭހަ ކުރާށެވެ. އެހެނީ އެބައިމީހުންގެ ތެރޭގައި މުސްކުޅިންނާއި، ބަލިކަށި މީހުންނާއި، ހޖަތް ޖެހިފައިވާ މީހުން ތިބޭނެކަށް ކަށަވަރެވެ.“","البخاري 704، ومسلم 466 واللفظ له"],["#086","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «كَانَ رَسُولُ اللَّهِ ﷺ إذَا كَبَّرَ فِي الصَّلاةِ سَكَتَ هُنَيْهَةً قَبْلَ أَنْ يَقْرَأَ، فَقُلْتُ: يَا رَسُولَ اللَّهِ، بِأَبِي أَنْتَ وَأُمِّي، أَرَأَيْتَ سُكُوتَكَ بَيْنَ التَّكْبِيرِ وَالْقِرَاءَةِ: مَا تَقُولُ؟ قَالَ: أَقُولُ: اللَّهُمَّ بَاعِدْ بَيْنِي وَبَيْنَ خَطَايَايَ كَمَا بَاعَدْتَ بَيْنَ الْمَشْرِقِ وَالْمَغْرِبِ. اللَّهُمَّ نَقِّنِي مِنْ خَطَايَايَ كَمَا يُنَقَّى الثَّوْبُ الأَبْيَضُ مِنْ الدَّنَسِ. اللَّهُمَّ اغْسِلْنِي مِنْ خَطَايَايَ بِالْمَاءِ وَالثَّلْجِ وَالْبَرَدِ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ނާމާދުގެ (ފުރަތަމަ) ތަކްބީރު ދެއްވާ ހިނދު، (ޤުރްއާން) ކިޔަވައި ވިދާޅުވުމުގެ ކުރިން ހިނދުކޮޅަކަށް ސުކޫތުވެ ވަޑައިގަންނަވައެވެ. ތިމަން ދެންނެވީމެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. އިބަ ނަބިއްޔާއަށް ޓަކައި ތިމަންނަގެ ބައްޕައާއި މަންމަ ފިދާއެވެ. (ފުރަތަމަ) ތަކްބީރާއި (ޤުރްއާން) ކިޔަވައި ވިދާޅުވުމާ ދެމެދު އިބަ ނަބިއްޔާ ސުކޫތުވެ ވަޑައިގަންނަވާ ތަން ދެކެވަޑައިގަންނަވަމުތޯއެވެ؟ އިބަ ނަބިއްޔާ ވިދާޅުވަނީ ކީކޭތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާ ވިދާޅުވަނީ: ޔާ ﷲ އެވެ. އިބަ އިލާހު މަޝްރިޤާއި މަޣްރިބާ ދެމެދު ދުރުކުރެއްވި ފަދައިން، މި އަޅާއާއި މި އަޅާގެ ފާފަތަކާ ދެމެދު ދުރުކުރައްވާށިއެވެ. ޔާ ﷲ އެވެ. ހުދު ފޭރާމެއްގައި ޖެހިފައިވާ ހަޑިތައް ތާހިރު ކުރާ ފަދައިން، މި އަޅާގެ ފާފަތަކުން މި އަޅާ ތާހިރު ކުރައްވާށިއެވެ. ޔާ ﷲ އެވެ. ފެނާއި ސްނޯއާއި ގަނޑުފެނުން މި އަޅާގެ ކިބައިން މި އަޅާގެ ފާފަތައް ދޮންނަވައި (ފިލުއްވާށިއެވެ.)“","البخاري 744، ومسلم 598 واللفظ له"],["#087","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: «كَانَ رَسُولُ اللَّهِ ﷺ يَسْتَفْتِحُ الصَّلاةَ بِالتَّكْبِيرِ، وَالْقِرَاءَةَ بـ «الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ» وَكَانَ إذَا رَكَعَ لَمْ يُشْخِصْ رَأْسَهُ وَلَمْ يُصَوِّبْهُ وَلَكِنْ بَيْنَ ذَلِكَ، وَكَانَ إذَا رَفَعَ رَأْسَهُ مِنْ الرُّكُوعِ لَمْ يَسْجُدْ حَتَّى يَسْتَوِيَ قَائِماً، وَكَانَ إذَا رَفَعَ رَأْسَهُ مِنْ السَّجْدَةِ لَمْ يَسْجُدْ، حَتَّى يَسْتَوِيَ قَاعِداً، وَكَانَ يَقُولُ فِي كُلِّ رَكْعَتَيْنِ التَّحِيَّةَ، وَكَانَ يَفْرِشُ رِجْلَهُ الْيُسْرَى وَيَنْصِبُ رِجْلَهُ الْيُمْنَى، وَكَانَ يَنْهَى عَنْ عُقْبَةِ الشَّيْطَانِ، وَيَنْهَى أَنْ يَفْتَرِشَ الرَّجُلُ ذِرَاعَيْهِ افْتِرَاشَ السَّبُعِ، وَكَانَ يَخْتِمُ الصَّلاةَ بِالتَّسْلِيمِ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ނަމާދު ފައްޓަވަނީ ތަކްބީރުންނެވެ. އަދި (ޤުރްއާން) ކިޔަވައި ވިދާޅުވުން ފައްޓަވަނީ الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ ނުންނެވެ. އަދި ނަބިއްޔާ ރުކޫޢު ކުރައްވާ ހިނދު، ނަބިއްޔާގެ އިސްތަށިފުޅު މާ މައްޗަކަށް ނުއުފުއްލަވައެވެ. އަދި މާ ތިރިވެސް ނުކުރައްވައެވެ. އެހެނަސް ބަހައްޓަވަނީ އެ ދެމެދުގައެވެ. އަދި ރުކޫޢިން އިސްތަށިފުޅު އުފުއްލަވާ ހިނދު، ރީއްޗަށް ތެދުވެވަޑައިގަތުމަށް ދާންދެން ސަޖިދަ ނުކުރައްވައެވެ. އަދި ސަޖިދައިން އިސްތަށިފުޅު އުފުއްލަވާ ހިނދު، ރީއްޗަށް އިށީނދެވަޑަތުމަށް ދާންދެން (ދެވަނަ) ސަޖިދަ ނުކުރައްވައެވެ. އަދި ކޮންމެ ދެ ރަކުޢަތެއްގައި އައްތަޙިއްޔާތު ވިދާޅުވެއެވެ. އަދި ވާތް ފައިންޕުޅު އަރިއަކަށް ބާއްވަވާފައި، ކަނާތް ފައިންޕުޅު ކޮއްޅަށް ނަންގަވައެވެ. އަދި ޝައިޠާނާ އިށީންނަ ފަދައިން އިށީންނުން ނަބިއްޔާ ނަހީ ކުރެއްވިއެވެ. (އެބަހީ: ދެ ކަންކަށި އެކީގައި ކޮއްޅަށް ނަގާފައި، ހަށިގަނޑުގެ ދެ ފަރާތުގައި ދެ އަތްތިލަ ބިންމަތީ ބަހައްޓައިގެން އޭގައި ލެނގި ހުރެ، ފޫކޮޅު މަތީ އިށީންނުން މަނާ ކުރެއްވިއެވެ.) އަދި މަސްކާ ޖަނަވާރެއް ފަދައިން މީހަކު ދެ އަތްދަނޑި ބިންމަތީގައި ޖައްސައިގެން (އިށީނދެ) އިނުން ވެސް ނަހީ ކުރެއްވިއެވެ. އަދި ނަބިއްޔާ ނަމާދު ނިންމަވަނީ ސަލާމުންނެވެ.","مسلم 498"],["#088","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما: «أَنَّ النَّبِيَّ ﷺ كَانَ يَرْفَعُ يَدَيْهِ حَذْوَ مَنْكِبَيْهِ إذَا افْتَتَحَ الصَّلاةَ، وَإِذَا كَبَّرَ لِلرُّكُوعِ، وَإِذَا رَفَعَ رَأْسَهُ مِنْ الرُّكُوعِ رَفَعَهُمَا كَذَلِكَ، وَقَالَ: سَمِعَ اللَّهُ لِمَنْ حَمِدَهُ، رَبَّنَا وَلَكَ الْحَمْدُ. وَكَانَ لا يَفْعَلُ ذَلِكَ فِي السُّجُودِ»","ޢަބްދު ﷲ ބިން ޢުމަރު ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ނަމާދު ފެއްޓަވާ ހިނދު، ދެ ކޮނޑުފުޅާ ހިސާބަށް ދެ އަތްޕުޅު އުފުއްލަވައެވެ. އަދި ރުކޫޢަށް ދިޔުމަށް ތަކްބީރު ދެއްވާ ހިނދުގައިވެސް މެއެވެ. އަދި ރުކޫޢިން ތެދުވެވަޑައިގަންނަވާ ހިނދުވެސް ދެ އަތްޕުޅު އެފަދައިން އުފުއްލަވައެވެ. އަދި ވިދާޅުވެއެވެ: ”ﷲ އަށް ޙަމްދުކޮށްފި މީހަކަށް އެ އިލާހު އަޑުއައްސަވާވޮޑިގެންފިއެވެ. އޭ އަޅަމެންގެ ވެރި ރައްބެވެ. ފަހެ ޙަމްދު ލިބިގެންވަނީ އިބަ އިލާހަށެވެ.“ އެހެނަސް ސަޖިދަ ކުރައްވާ ހިނދު އެފަދައިން އަތްޕުޅު ނުއުފުލައްވައެވެ.","البخاري 735 واللفظ له، ومسلم 390"],["#089","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ ابْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «أُمِرْتُ أَنْ أَسْجُدَ عَلَى سَبْعَةِ أَعْظُمٍ: عَلَى الْجَبْهَةِ - وَأَشَارَ بِيَدِهِ إلَى أَنْفِهِ - وَالْيَدَيْنِ، وَالرُّكْبَتَيْنِ، وَأَطْرَافِ الْقَدَمَيْنِ»","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަތް ކަށްޓެއް މަތީގައި ސަޖިދަ ކުރައްވަން ތިމަން ނަބިއްޔާއަށް އަމުރުވެވިގެން ވެއެވެ. އެއީ ނިއްކުރި، (ދެން އަތްޕުޅުން ނޭފަތްޕުޅަށް އިޝާރާތް ކުރެއްވިއެވެ.) އަދި ދެ އަތާއި، ދެ ކަކުލާއި، ދެ ފައިތިލައިގެ އިނގިލިތަކެވެ.“","البخاري 812 واللفظ له، ومسلم 490"],["#090","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «كَانَ رَسُولُ اللَّهِ ﷺ إذَا قَامَ إلَى الصَّلاةِ يُكَبِّرُ حِينَ يَقُومُ، ثُمَّ يُكَبِّرُ حِينَ يَرْكَعُ، ثُمَّ يَقُولُ: سَمِعَ اللَّهُ لِمَنْ حَمِدَهُ، حِينَ يَرْفَعُ صُلْبَهُ مِنْ الرَّكْعَةِ، ثُمَّ يَقُولُ وَهُوَ قَائِمٌ: رَبَّنَا وَلَكَ الْحَمْدُ، ثُمَّ يُكَبِّرُ حِينَ يَهْوِي، ثُمَّ يُكَبِّرُ حِينَ يَرْفَعُ رَأْسَهُ، ثُمَّ يُكَبِّرُ حِينَ يَسْجُدُ، ثُمَّ يُكَبِّرُ حِينَ يَرْفَع رَأْسَهُ، ثُمَّ يَفْعَلُ ذَلِكَ فِي صَلاتِهِ كُلِّهَا، حَتَّى يَقْضِيَهَا، وَيُكَبِّرُ حِينَ يَقُومُ مِنْ الثِّنْتَيْنِ بَعْدَ الْجُلُوسِ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ނަމާދަށް ތެދުވެވަޑައިގަންނަވާ ހިނދު ތަކްބީރު ދެއްވައެވެ. ދެން ރުކޫޢު ކުރައްވާ ހިނދު ވެސް ތަކްބީރު ދެއްވައެވެ. އެއަށްފަހު ރުކޫޢިން ބުރަކަށިފުޅު އުފުއްލަވާ ހިނދު سَمِعَ اللَّهُ لِمَنْ حَمِدَهُ ވިދާޅުވެއެވެ. ދެން ކޮއްޅަށް ތެދުވެވަޑައިގެން رَبَّنَا وَلَكَ الْحَمْدُ ވިދާޅުވެއެވެ. އަދި ސަޖިދައަށް ތިރިވެވަޑައިގަންނަވަމުން ތަކްބީރު ދެއްވައެވެ. ދެން (ސަޖިދައިން) އިސްތަށިފުޅު އުފުއްލަވާ ހިނދު ތަކްބީރު ދެއްވައެވެ. އެއަށްފަހު (ދެވަނަ) ސަޖިދަ ކުރައްވާ ހިނދު ތަކްބީރު ދެއްވައެވެ. ދެން އަނެއްކާވެސް އިސްތަށިފުޅު އުފުއްލަވާ (ތެދުވެވަޑައިގަންނަވާ) ހިނދު ތަކްބީރު ދެއްވައެވެ. ދެން ނަބިއްޔާގެ މުޅި ނަމާދުން އަވަދިވެވަޑައިގަތުމަށް ދާންދެން ކަންކުރައްވަނީ އެފަދައިންނެވެ. އަދި ދެވަނަ ރަކުޢަތުން (އައްތަޙިޔާތަށް) އިށީނދެވަޑައިގަތުމަށް ފަހު، ތެދުވެވަޑައިގަންނަވާ ހިނދު ވެސް ތަކްބީރު ދެއްވައެވެ.","البخاري 789 واللفظ له، ومسلم 392"],["#091","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ مُطَرِّفِ بْنِ عَبْدِ اللَّهِ قَالَ: «صَلَّيْتُ أَنَا وَعِمْرَانُ بْنُ حُصَيْنٍ خَلْفَ عَلِيِّ بْنِ أَبِي طَالِبٍ. فَكَانَ إذَا سَجَدَ كَبَّرَ، وَإِذَا رَفَعَ رَأْسَهُ كَبَّرَ، وَإِذَا نَهَضَ مِنْ الرَّكْعَتَيْنِ كَبَّرَ، فَلَمَّا قَضَى الصَّلاةَ أَخَذَ بِيَدِي عِمْرَانُ بْنُ حُصَيْنٍ، وَقَالَ: قَدْ ذَكَّرَنِي هَذَا صَلاةَ مُحَمَّدٍ ﷺ أَوْ قَالَ: صَلَّى بِنَا صَلاةَ مُحَمَّدٍ ﷺ»","މުޠައްރިފު ބިން ޢަބްދު ﷲ ރިވާ ކުރެއްވިއެވެ: ޢަލީ ބިން އަބީ ޠާލިބު رَضِيَ اللَّهُ عَنْهُ ގެ ފަހަތްޕުޅުގައި، ތިމަންނައާއި ޢިމްރާން ބިން ޙުޞައިން ނަމާދު ކުރީމެވެ. ޢަލީގެފާނު ސަޖިދަ ކުރައްވާ ހިނދު ތަކްބީރު ދެއްވައެވެ. އަދި އިސްތަށިފުޅު އުފުއްލަވާ ހިނދު ތަކްބީރު ދެއްވައެވެ. އަދި ދެ ރަކުޢަތުން ތެދުވެވަޑައިގަންނަވާ ހިނދު ތަކްބީރު ދެއްވައެވެ. ދެން ނަމާދު ނިންމެވުމަށް ފަހު، ޢިމްރާން ބިން ޙުޞައިން ތިމަންނަގެ އަތުގައި ހިއްޕަވާފައި ވިދާޅުވިއެވެ: ހަމަކަށަވަރުން މުޙައްމަދު ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ މަތިން މި ކަލޭގެފާނު ތިމަންނައަށް ހަނދުމަކޮށްފިއެވެ. ނުވަތަ ވިދާޅުވިއެވެ: އެކަލޭގެފާނު ތިމަންމެންނައިގެން އެ ނަމާދު ކުރެއްވީ މުޙައްމަދު ﷺ ނަމާދު ކުރެއްވި ގޮތަށެވެ.","البخاري 786 واللفظ له، ومسلم 393"],["#092","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ الْبَرَاءِ بْنِ عَازِبٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: «رَمَقْتُ الصَّلاةَ مَعَ مُحَمَّدٍ ﷺ فَوَجَدْتُ قِيَامَهُ، فَرَكْعَتَهُ فَاعْتِدَالَهُ بَعْدَ رُكُوعِهِ، فَسَجْدَتَهُ، فَجِلْسَتَهُ بَيْنَ السَّجْدَتَيْنِ، فَسَجْدَتَهُ فَجِلْسَتَهُ مَا بَيْنَ التَّسْلِيمِ وَالانْصِرَافِ: قَرِيباً مِنْ السَّوَاءِ»\n<br><br>\nوَفِي رِوَايَةِ الْبُخَارِيِّ «مَا خَلا الْقِيَامَ وَالْقُعُودَ قَرِيباً مِنْ السَّوَاءِ»","އަލްބަރާއު ބިން ޢާޒިބު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: މުޙައްމަދު ﷺ ކުރެއްވި ނަމާދު ތިމަން ބެލީމެވެ. ފަހެ ތިމަންނައަށް ފެނުނީ، ނަބިއްޔާގެ ކޮއްޅަށް ތެދުވެވަޑައިގަތުމާއި، ރުކޫޢު ކުރެއްވުމާއި، ރުކޫޢާއި ސަޖިދައާ ދެމެދުގައި އިޢުތިދާލުގައި ހުންނެވުމާއި، ދެ ސަޖިދަ ދެމެދު އިށީނދެ އިންނެވުމާއި، ސަޖިދަ ކުރެއްވުމާއި، ސާލާމް ދެއްވުމާއި ތެދުވެވަޑައިގަތުމާ ދެމެދު އިށީނދެ އިންނެވި އިންނެވުން، މި ހުރިހާ ކަމެއްގައި (ހޭދަ ކުރެއްވި ވަގުތުކޮޅު) އެއްފަދަ ކަމަށެވެ.\n<br><br>\nއަދި ބުޚާރީގެ ރިވާޔަތުގައި ވެއެވެ: ކޮއްޅަށް ތެދުވެވަޑައިގަތުމާއި އިށީނދެވަޑައިގަތުން ފިޔަވައި ދެން ހުރި ކަންކަމުގައި (ހޭދަ ކުރެއްވި ވަގުތުކޮޅު) އެއްފަދައެވެ.","البخاري 792، ومسلم 471 واللفظ له"],["#093","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ ثَابِتٍ الْبُنَانِيِّ عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «إنِّي لا آلُو أَنْ أُصَلِّيَ بِكُمْ كَمَا كَانَ رَسُولُ اللَّهِ ﷺ يُصَلِّي بِنَا قَالَ ثَابِتٌ فَكَانَ أَنَسٌ يَصْنَعُ شَيْئاً لا أَرَاكُمْ تَصْنَعُونَهُ. كَانَ إذَا رَفَعَ رَأْسَهُ مِنْ الرُّكُوعِ: انْتَصَبَ قَائِماً، حَتَّى يَقُولَ الْقَائِلُ: قَدْ نَسِيَ، وَإِذَا رَفَعَ رَأْسَهُ مِنْ السَّجْدَةِ: مَكَثَ، حَتَّى يَقُولَ الْقَائِلُ: قَدْ نَسِيَ»","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ތިމަންމެންނައިގެން ނަމާދު ކުރެއްވި ފަދައިން، ތިމަންނަ ތިޔަބައިމިހުންނައިގެން ނަމާދު ކުރުމުގައި، އެއްވެސް ކަމެއް ތިމަންނަ އުންޏެއް ނުކުރާނަމެވެ. (ޙަދީޘްގެ ރާވީއެއް ކަމަށްވާ) ޘާބިތު ވިދާޅުވިއެވެ: ތިޔަބައިމީހުން (ނަމާދުގައި) ކުރަނިކޮށް ތިމަންނައަށް ނުފެންނަ ކަމެއް، އަނަސް ކުރެއްވިއެވެ. އެކަލޭގެފާނު ރުކޫޢިން އިސްތަށިފުޅު އުފުއްލަވާ ހިނދު ތެދުވެވަޑައިގެން މަޑުކޮށްލަވައެވެ. އެއި އެކަލޭގެފާނަށް ހަނދުމަފުޅު ނެތުނީއޭ މީހަކު ބުނެފާނެކަމަށް ދާންދެނެވެ. އަދި އެކަލޭގެފާނު ސަޖިދައިން އިސްތަށިފުޅު އުފުއްލަވާ ހިނދު އިށީންދެ ވަޑައިގެން މަޑުކޮށްލަވައެވެ. އެއި ވެސް އެކަލޭގެފާނަށް ހަނދުމަފުޅު ނެތުނީއޭ މީހަކު ބުނެފާނެކަމަށް ދާންދެނެވެ.","البخاري 721، ومسلم 472 واللفظ له"],["#094","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «مَا صَلَّيْتُ خَلْفَ إمَامٍ قَطُّ أَخَفَّ صَلاةً، وَلا أَتَمَّ صّلاةً مِنَ النَّبيِّ ﷺ»","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ އަށްވުރެ ލުއިކޮށް އަދި އެހާމެ ފުރިހަމަކޮށް ނަމާދު ކުރެއްވި އެއްވެސް އިމާމެއްގެ ފަހަތުގައި ތިމަންނަ ދުވަހަކުވެސް ނަމާދެއް ނުކުރަމެވެ.","البخاري 807 واللفظ له، ومسلم 469"],["#095","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ أَبِي قِلابَةَ عَبْدِ اللَّهِ بْنِ زَيْدٍ الْجَرْمِيِّ الْبَصْرِيِّ - قَالَ: «جَاءَنَا مَالِكُ بْنُ الْحُوَيْرِثِ فِي مَسْجِدِنَا هَذَا، فَقَالَ: إنِّي لأُصَلِّي بِكُمْ، وَمَا أُرِيدُ الصَّلاةَ، أُصَلِّي كَيْفَ رَأَيْتُ رَسُولَ اللَّهِ ﷺ يُصَلِّي، فَقُلْتُ لأَبِي قِلابَةَ: كَيْفَ كَانَ يُصَلِّي؟ فَقَالَ: مِثْلَ صَلاةِ شَيْخِنَا هَذَا، وَكَانَ يَجْلِسُ إذَا رَفَعَ رَأْسَهُ مِنْ السُّجُودِ قَبْلَ أَنْ يَنْهَضَ» أَرادَ بشيخِهمْ، أَبا يزيدَ، عَمرَو بنَ سَلَمَة الجَرْميَّ.","އަބޫ ޤިލާބާ، ޢަބްދު ﷲ ބިން ޒައިދު އަލްޖަރްމީ އަލްބަޞްރީ ރިވާ ކުރެއްވިއެވެ: ތިމަންމެންގެ މި މިސްކިތަށް މާލިކު ބިން އަލްޙުވައިރިޘް ވަޑައިގެން ދެންނެވިއެވެ: ހަމަކަށަވަރުން ތިޔަބައިމީހުންނައިގެން ތިމަންނަ ނަމާދު ކުރާނަމެވެ. އަދި ތިމަންނަގެ ބޭނުމަކީ ހަމަ ނަމާދުކޮށްލުމެއް ނޫނެވެ. (އެހެނެއްކަމަކު) ރަސޫލު ﷲ ﷺ ނަމާދު ކުރައްވަނިކޮށް ތިމަން ދެކުނު ފަދައިން ތިމަން ނަމާދު ކުރާނަމެވެ. (ޙަދީޘްގެ ރާވީއެއް ކަމަށްވާ އައްޔޫބު،) އަބޫ ޤިލާބާ ގާތު ދެންނެވިއެވެ: ނަބިއްޔާ ނަމާދު ކުރެއްވީ ކިހިނަކުންތޯއެވެ؟ އަބޫ ޤިލާބާ ދެންނެވިއެވެ: ތިމަންމެންގެ މި ޝައިޚު (ޢަމްރު ބިން ސަލަމާ) ނަމާދު ކުރެއްވި ފަދައިންނެވެ. އެކަލޭގެފާނު ސަޖިދައިން އިސްތަށިފުޅު އުފުއްލެވުމުން، ތެދުވެވަޑައިގަތުމުގެ ކުރިން (އިރުކޮޅަކު) އިށީނދެލައެވެ. އެކަލޭގެފާނު މި ޝައިޚޭ އެ ވިދާޅުވަނީ އަބޫ ޔަޒީދު، ޢަމްރު ބިން ސަލަމާ އަލްޖަރްމީއަށެވެ.","البخاري 677"],["#096","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ مَالِكٍ بْنِ بُحَيْنَةَ - رَضِيَ اللَّهُ عَنْهُ -: «أَنَّ النَّبِيَّ ﷺ كَانَ إذَا صَلَّى فَرَّجَ بَيْنَ يَدَيْهِ، حَتَّى يَبْدُوَ بَيَاضُ إبْطَيْهِ»","ޢަބްދު ﷲ ބިން މާލިކު ބިން ބުޙައިނާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ނަމާދު ކުރައްވާ ހިނދު، ނަބިއްޔާގެ ކިހިލިފަތްޕުޅުގެ ހުދުކަން ފެނުމަށް ދާންދެން (ސަޖިދައިގައި) ދެ އަތްޕުޅު ދުރު ކުރައްވައެވެ.","البخاري 390، ومسلم 495"],["#097","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ أَبِي مَسْلَمَةَ سَعِيدِ بْنِ يَزِيدَ قَالَ: «سَأَلْتُ أَنَسَ بْنَ مَالِكٍ: أَكَانَ النَّبِيُّ ﷺ يُصَلِّي فِي نَعْلَيْهِ؟ قَالَ: نَعَمْ»","އަބޫ މަސްލަމާ، ސަޢީދު ބިން ޔަޒީދު ރިވާ ކުރެއްވިއެވެ: އަނަސް ބިން މާލިކު އަރިހުގައި ތިމަން ސުވާލު ކުރީމެވެ: ނަބިއްޔާ ﷺ ފައިވާންޖޯޑުކޮޅުގައި ނަމާދު ކުރައްވާތޯއެވެ؟ އަނަސް ވިދާޅުވިއެވެ: އާދޭހެވެ.","البخاري 386، ومسلم 555"],["#098","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ أَبِي قَتَادَةَ الأَنْصَارِيِّ - رَضِيَ اللَّهُ عَنْهُ -: «أَنَّ رَسُولَ اللَّهِ ﷺ كَانَ يُصَلِّي وَهُوَ حَامِلٌ أُمَامَةَ بِنْتَ زَيْنَبَ بِنْتِ رَسُولِ اللَّهِ ﷺ","އަބޫ ޤަތާދާ އަލްއަންޞާރީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ހަމަކަށަވަރުން އުމާމާ ބިންތި ޒައިނަބު ބިންތި ރަސޫލު ﷲ ﷺ އުރާލައްވައިގެން ހުންނަވަނިކޮށް، ރަސޫލު ﷲ ﷺ ނަމާދު ކުރައްވައެވެ.","البخاري 516 واللفظ له، ومسلم 543"],["#099","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","وَلأَبِي الْعَاصِ بْنِ الرَّبِيعِ بْنِ عَبْدِ شَمْسٍ، فَإِذَا سَجَدَ وَضَعَهَا، وَإِذَا قَامَ حَمَلَهَا»","އަދި އަބުލް ޢާޞް ބިން އައްރަބީޢު ބިން ޢަބްދު ޝަމްސުގެ ރިވާޔަތުގައި ވެއެވެ: ފަހެ ނަބިއްޔާ ސަޖިދަ ކުރައްވާ ހިނދު، އެ ކަމަނާ ތިރި ކުރައްވައެވެ. އަދި ތެދުވެވަޑައިގަންނަވާ ހިނދު، އެ ކަމަނާ އުރާލައްވައެވެ.","البخاري 516 واللفظ له، ومسلم 543"],["#100","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - عَنْ النَّبِيِّ ﷺ قَالَ: «اعْتَدِلُوا فِي السُّجُودِ وَلا يَبْسُطْ أَحَدُكُمْ ذِرَاعَيْهِ انْبِسَاطَ الْكَلْبِ»","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ސަޖިދައިގައި ސީދާ ވާށެވެ. އަދި ކުއްތާ ބިންމަތީގައި ދެ އަތްދަނޑި ޖަހާ ފަދައިން ތިޔަބައިމީހުން ކުރެ މީހަކު އަތްދަނޑި ނުޖައްސާށެވެ.“","البخاري 822، ومسلم 493"],["#101","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب وجوب الطمأنينة في الركوع والسجود","ރުކޫޢާއި ސަޖިދައިގައި ހަމަޖެހިލުން ވާޖިބުކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ -: «أَنَّ النَّبِيَّ ﷺ دَخَلَ الْمَسْجِدَ، فَدَخَلَ رَجُلٌ فَصَلَّى، ثُمَّ جَاءَ فَسَلَّمَ عَلَى النَّبِيِّ ﷺ فَقَالَ: ارْجِعْ فَصَلِّ، فَإِنَّك لَمْ تُصَلِّ. فَرَجَعَ فَصَلَّى كَمَا صَلَّى، ثُمَّ جَاءَ فَسَلَّمَ عَلَى النَّبِيِّ ﷺ فَقَالَ: ارْجِعْ فَصَلِّ، فَإِنَّك لَمْ تُصَلِّ - ثَلاثاً - فَقَالَ: وَاَلَّذِي بَعَثَكَ بِالْحَقِّ لا أُحْسِنُ غَيْرَهُ، فَعَلِّمْنِي، فَقَالَ: إذَا قُمْتَ إلَى الصَّلاةِ فَكَبِّرْ، ثُمَّ اقْرَأْ مَا تَيَسَّرَ مِنْ الْقُرْآنِ، ثُمَّ ارْكَعْ حَتَّى تَطْمَئِنَّ رَاكِعاً، ثُمَّ ارْفَعْ حَتَّى تَعْتَدِلَ قَائِماً، ثُمَّ اُسْجُدْ حَتَّى تَطْمَئِنَّ سَاجِداً، ثُمَّ ارْفَعْ حَتَّى تَطْمَئِنَّ جَالِساً. وَافْعَلْ ذَلِكَ فِي صَلاتِكَ كُلِّهَا»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ މިސްކިތަށް ވަދެވަޑައިގަތެވެ. ދެން މީހަކު އައިސް މިސްކިތަށް ވަދެ ނަމާދު ކުރުމަށް ފަހު ނަބިއްޔާ ﷺގެ އަރިހަށް އައިސް ސަލާމް ދެންނެވިއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެނބުރި ގޮސް (އަލުން) ނަމާދު ކުރާށެވެ. އެހެނީ ހަމަކަށަވަރުން ތިބާ އަދި ނަމާދެއް ނުކުރެއެވެ.“ ދެން އެ މީހާ އެނބުރިގޮސް ކުރިއެކޭ އެއްފަދައިން ނަމާދު ކުރުމަށް ފަހު ނަބިއްޔާގެ އަރިހަށް އައިސް ސަލާން ދެންނެވިއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެނބުރި ގޮސް (އަލުން) ނަމާދު ކުރާށެވެ. އެހެނީ ހަމަކަށަވަރުން ތިބާ އަދި ނަމާދެއް ނުކުރެއެވެ.“ މި ހާދިސާ ތިން ފަހަރު ހިނގިއެވެ. ދެން އެ މީހާ ދެންނެވިއެވެ: އިބަ ނަބިއްޔާ ޙައްޤައިގެން ފޮނުއްވި ފަރާތް ގަންދީ ބުނަމެވެ. މިއަށްވުރެ ރަނގަޅަށް ނަމާދު ކުރަން ތިމަންނައަކަށް ނޭނގެއެވެ. ފަހެ ތިމަންނައަށް އުނގަންނަވައި ދެއްވާށެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ ނަމާދަށް ތެދުވާ ހިނދު ތަކްބީރު ދޭށެވެ. އެއަށްފަހު ތިބާއަށް ފަސޭވާ މިންވަރެއް ޤުރްއާނުން ކިޔަވާށެވެ. ދެން ތިބާ ހަމަޖެހިލެވެންދެން ރުކޫޢު ކުރާށެވެ. އެއަށްފަހު ބޯ އުފުލައި ތިބާ ސީދާވަންދެން (އިޢުތިދާލުގައި) ތެދުވާށެވެ. ދެން ތިބާ ހަމަޖެހިލެވެންދެން ސަޖިދަ ކުރާށެވެ. އެއަށްފަހު ބޯ އުފުލައި ތިބާ ހަމަޖެހިލެވެންދެން އިށީންނާށެވެ. އަދި ތިބާގެ މުޅި ނަމާދުގައި އެފަދައިން ކަންކުރާށެވެ.“","البخاري 793، ومسلم 397"],["#102","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب القراءة في الصلاة","ނަމާދުގައި (ޤުރްއާން) ކިޔެވުމުގެ ބާބު","عَنْ عُبَادَةَ بْنِ الصَّامِتِ - رَضِيَ اللَّهُ عَنْهُ - أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «لا صَلاةَ لِمَنْ لَمْ يَقْرَأْ بِفَاتِحَةِ الْكِتَابِ»","ޢުބާދާ ބިން އައްޞާމިތު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ފާތިޙަތުލް ކިތާބު (އެބަހީ: ސޫރަތުލް ފާތިޙާ) ނުކިޔައިފި މީހަކަށް ނަމާދެއް ނުވެއެވެ.“","البخاري 756، ومسلم 394"],["#103","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب القراءة في الصلاة","ނަމާދުގައި (ޤުރްއާން) ކިޔެވުމުގެ ބާބު","عَنْ أَبِي قَتَادَةَ الأَنْصَارِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «كَانَ رَسُولُ اللَّهِ ﷺ يَقْرَأُ فِي الرَّكْعَتَيْنِ الأُولَيَيْنِ مِنْ صَلاةِ الظُّهْرِ بِفَاتِحَةِ الْكِتَابِ وَسُورَتَيْنِ، يُطَوِّلُ فِي الأُولَى، وَيُقَصِّرُ فِي الثَّانِيَةِ، وَيُسْمِعُ الآيَةَ أَحْيَاناً، وَكَانَ يَقْرَأُ فِي الْعَصْرِ بِفَاتِحَةِ الْكِتَابِ وَسُورَتَيْنِ يُطَوِّلُ فِي الأُولَى، وَيُقَصِّرُ فِي الثَّانِيَةِ وَفِي الرَّكْعَتَيْنِ الأُخْرَيَيْنِ بِأُمِّ الْكِتَابِ. وَكَانَ يُطَوِّلُ فِي الرَّكْعَةِ الأُولَى مِنْ صَلاةِ الصُّبْحِ، وَيُقَصِّرُ فِي الثَّانِيَةِ»\n","އަބޫ ޤަތާދާ އަލްއަންޞާރީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: މެންދުރު ނަމާދުގެ ފުރަތަމަ ދެ ރަކުޢަތުގައި ރަސޫލު ﷲ ﷺ ފާތިޙަތުލް ކިތާބު (އެބަހީ: ސޫރަތުލް ފާތިޙާ) އާއި ދެ ސޫރަތް ކިޔަވައި ވިދާޅުވެއެވެ. ފުރަތަމަ ސޫރަތުގައި ކިޔެވުން ދިގު ކުރައްވައި، ދެވަނަ ސޫރަތުގައި ކުރު ކުރައްވައެވެ. އަދި ބައެއް ފަހަރު ކިޔަވައި ވިދާޅުވާ އާޔަތްތައް އިއްވަވައެވެ. (ނޯޓު: އެއީ އަޑުހަރުކޮށް ކިޔަވައި ވިދާޅުވުމަކާ ނުލައެވެ.) އަދި ޢަޞުރު ނަމާދުގައި ވެސް ފާތިޙަތުލް ކިތާބާއި ދެ ސޫރަތް ކިޔަވައި ވިދާޅުވެއެވެ. ފުރަތަމަ ސޫރަތުގައި ކިޔެވުން ދިގު ކުރައްވައި، ދެވަނަ ސޫރަތުގައި ކުރު ކުރައްވައެވެ. އަދި ފަހު ދެ ރަކުޢަތުގައި އުންމުލް ކިތާބު (އެބަހީ: ސޫރަތުލް ފާތިޙާ) ކިޔަވައި ވިދާޅުވެއެވެ. އަދި ފަތިސް ނަމާދުގެ ފުރަތަމަ ރަކުޢަތުގައި ކިޔެވުން ދިގު ކުރައްވައެވެ. އަދި ދެވަނަ ރަކުޢަތުގައި ކުރު ކުރައްވައެވެ.","البخاري 759 واللفظ له، ومسلم 451"],["#104","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب القراءة في الصلاة","ނަމާދުގައި (ޤުރްއާން) ކިޔެވުމުގެ ބާބު","عَنْ جُبَيْرِ بْنِ مُطْعِمٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «سَمِعْتُ النَّبِيَّ ﷺ يَقْرَأُ فِي الْمَغْرِبِ بِالطُّورِ»","ޖުބައިރު ބިން މުޠުޢިމު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ މަޢްރިބު ނަމާދުގައި ޠޫރު ސޫރަތް ކިޔަވަނިކޮށް ތިމަން އަޑު އެހީމެވެ.","البخاري 3050 واللفظ له، ومسلم 463"],["#105","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب القراءة في الصلاة","ނަމާދުގައި (ޤުރްއާން) ކިޔެވުމުގެ ބާބު","عَنْ الْبَرَاءِ بْنِ عَازِبٍ رَضِيَ اللَّهُ عَنْهُما: «أَنَّ النَّبِيَّ ﷺ كَانَ فِي سَفَرٍ، فَصَلَّى الْعِشَاءَ الآخِرَةَ، فَقَرَأَ فِي إحْدَى الرَّكْعَتَيْنِ بِالتِّينِ وَالزَّيْتُونِ فَمَا سَمِعْتُ أَحَداً أَحْسَنَ صَوْتاً أَوْ قِرَاءَةً مِنْهُ»","އަލްބަރާއު ބިން ޢާޒިބު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ދަތުރުފުޅެއްގެ މަތީގައި ވަނިކޮށް، ޢީޝާ ނަމާދު ކުރެއްވިއެވެ. އަދި (ފުރަތަމަ) ދެ ރަކުޢަތުން ކުރެ ރަކުޢަތެއްގައި ވައްތީނި ވައްޒައިތޫނި ސޫރަތް ކިޔަވައި ވިދާޅުވިއެވެ. ފަހެ ނަބިއްޔާއަށް ވުރެ ރިވެތި އަޑުފަޅަކުން ނުވަތަ ކިޔެވުމަކުން ކިޔަވައި ވިދާޅުވި އެކަކުވެސް ތިމަންނައެއް އަޑެއް ނާހަމެވެ.","البخاري 767، ومسلم 464 واللفظ له"],["#106","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب القراءة في الصلاة","ނަމާދުގައި (ޤުރްއާން) ކިޔެވުމުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا: «أَنَّ رَسُولَ اللَّهِ ﷺ بَعَثَ رَجُلاً عَلَى سَرِيَّةٍ فَكَانَ يَقْرَأُ لأَصْحَابِهِ فِي صَلاتِهِمْ، فَيَخْتِمُ بـ «قُلْ هُوَ اللَّهُ أَحَدٌ» فَلَمَّا رَجَعُوا ذَكَرُوا ذَلِكَ لِرَسُولِ اللهِ ﷺ فَقَالَ: سَلُوهُ لأَيِّ شَيْءٍ صَنَعَ ذَلِكَ؟ فَسَأَلُوهُ. فَقَالَ: لأَنَّهَا صِفَةُ الرَّحْمَنِ عَزَّ وَجَلَّ، فَأَنَا أُحِبُّ أَنْ أَقْرَأَ بِهَا. فَقَالَ رَسُولُ اللَّهِ ﷺ: أَخْبِرُوهُ: أَنَّ اللهَ تَعَالَى يُحِبُّهُ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ސަރިއްޔާއެއްގައި މީހަކު ފޮނުއްވިއެވެ. އެ މީހަކީ އޭނާއާއެކު އުޅުނު މީހުންނައިގެން (އިމާމުވެ) ނަމާދު ކުރާ މީހާއެވެ. އެ މީހާ (ދެވަނަ ރަކުޢަތުގައި) ކިޔެވުން ނިންމާލަނީ قُلْ هُوَ اللَّهُ أَحَدٌ ންނެވެ. ދެން އެ ސަރިއްޔާގެ މީހުން އެނބުރި އައުމުން އެކަމުގެ ވާހަކަ ރަސޫލު ﷲ ﷺ ގެ އަރިހުގައި ދެންނެވިއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ މީހާ ގާތު އަހާށެވެ. އެފަދައިން ކަން ކުރީ ކީއްވެގެންތޯއެވެ؟“ އެބައިމީހުން އޭނާ ގާތު ސުވާލު ކުރުމުން އޭނާ ދެންނެވިއެވެ: އެއީ އެ ސޫރަތުގައި އައްރަޙްމާން ޢައްޒަ ވަޖައްލަގެ ސިފަފުޅުތައް ވާތީއެވެ. ވީމާ އެ ސޫރަތް ކިޔަވަން ތިމަންނަ ލޯބި ކުރަމެވެ. ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ﷲ ތަޢާލާ އޭނާ ދެކެ ލޯބިވެވޮޑިގަންނަވާ ކަމުގެ ޚަބަރު އޭނާއަށް ދެށެވެ.“","البخاري 7375، ومسلم 813 واللفظ له"],["#107","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب القراءة في الصلاة","ނަމާދުގައި (ޤުރްއާން) ކިޔެވުމުގެ ބާބު","عَنْ جَابِرِ بنِ عبدِ اللهِ رَضِيَ اللَّهُ عَنْهُما: أَنَّ النَّبِيَّ ﷺ قَالَ لِمُعَاذٍ: «فَلَوْلا صَلَّيْتَ بِسَبِّحِ اسْمَ رَبِّكَ الأَعْلَى، وَالشَّمْسِ وَضُحَاهَا، وَاللَّيْلِ إذَا يَغْشَى؟ فَإِنَّهُ يُصَلِّي وَرَاءَكَ الْكَبِيرُ وَالضَّعِيفُ وَذُو الْحَاجَةِ»","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ މުޢާޛަށް ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ (އިމާމްވެ) ނަމާދު ކުރާ ހިނދު سَبِّحِ اسْمَ رَبِّكَ الأَعْلَى، وَالشَّمْسِ وَضُحَاهَا، وَاللَّيْلِ إذَا يَغْشَى މި ސޫރަތްތަށް ކިޔަވާ ނަމަ ކިހާ ރަނގަޅުހެއްޔެވެ. އެހެނީ ތިބާގެ ފަހަތުގައި މުސްކުޅިންނާއި ބަލިކަށި މީހުންނާއި ހާޖާތް ޖެހިފައިވާ މީހުން ތިބޭނެކަން ކަށަވަރެވެ.“","البخاري 705 واللفظ له، ومسلم 465"],["#108","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب ترك الجهر ببسم الله الرحمن الرحيم","އަޑުހަރުކޮށް ބިސްމި ނުކިޔުމުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ النَّبِيَّ ﷺ وَأَبَا بَكْرٍ وَعُمَرَ رَضِيَ اللَّهُ عَنْهُما: كَانُوا يَسْتَفْتِحُونَ الصَّلاةَ بِ «الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ» \n<br><br>\nوَفِي رِوَايَةٍ: «صَلَّيْتُ مَعَ أَبِي بَكْرٍ وَعُمَرَ وَعُثْمَانَ، فَلَمْ أَسْمَعْ أَحَدًا مِنْهُمْ يَقْرَأُ بِسْمِ اللهِ الرَّحْمَنِ الرَّحِيمِ»\n","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ އަދި އަބޫ ބަކްރާއި ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا، ނަމާދު ފައްޓަވަނީ الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ ންނެވެ. \n <br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: އަބޫ ބަކްރާއި ޢުމަރާއި ޢުޘްމާނުގެ ފަހަތްޕުޅުގައި ތިމަންނަ ނަމާދު ކުރީމެވެ. އަދި އެއިން އެއްވެސް ބޭކަލެއް بِسْمِ اللهِ الرَّحْمَنِ الرَّحِيمِ ކިޔަވައި ވިދާޅުވާ އަޑެއް ނާހާމެވެ.","البخاري 743\n <br><br>\nمسلم 399"],["#109","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب ترك الجهر ببسم الله الرحمن الرحيم","އަޑުހަރުކޮށް ބިސްމި ނުކިޔުމުގެ ބާބު","وَلِمُسْلِمٍ: «صَلَّيْتُ خَلْفَ النَّبِيِّ ﷺ وَأَبِي بَكْرٍ وَعُمَرَ وَعُثْمَانَ فَكَانُوا يَسْتَفْتِحُونَ بِ «الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ»، لا يَذْكُرُونَ بِسْمِ اللَّهِ الرَّحْمَنِ الرَّحِيمِ فِي أَوَّلِ قِرَاءَةٍ وَلا فِي آخِرِهَا»","އަދި އިމާމު މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ނަބިއްޔާ ﷺ އަދި އަބޫ ބަކްރާއި ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ގެ ފަހަތްޕުޅުގައި ތިމަން ނަމާދު ކުރީމެވެ. އެ ބޭކަލުން ނަމާދު ފައްޓަވަނީ الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ ންނެވެ. އެ ބޭކަލުންގެ ފުރަތަމަ ކިޔެވުމާއި ފަހު ކިޔެވުމުގައި ވެސް بِسْمِ اللهِ الرَّحْمَنِ الرَّحِيمِ ނުކިޔުއްވައެވެ.","مسلم 399"],["#110","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب سجود السهو","ކުށުސަޖިދައިގެ ބާބު","عَنْ مُحَمَّدِ بْنِ سِيرِينَ، عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «صَلَّى بِنَا رَسُولُ اللهِﷺ إحْدَى صَلاتَيْ الْعَشِيِّ - قَالَ ابْنُ سِيرِينَ: وَسَمَّاهَا أَبُو هُرَيْرَةَ. وَلَكِنْ نَسِيتُ أَنَا - قَالَ: فَصَلَّى بِنَا رَكْعَتَيْنِ، ثُمَّ سَلَّمَ. فَقَامَ إلَى خَشَبَةٍ مَعْرُوضَةٍ فِي الْمَسْجِدِ، فَاتَّكَأَ عَلَيْهَا كَأَنَّهُ غَضْبَانُ وَوَضَعَ يَدَهُ الْيُمْنَى عَلَى الْيُسْرَى، وَشَبَّكَ بَيْنَ أَصَابِعِهِ. وَخَرَجَتِ السَّرَعَانُ مِنْ أَبْوَابِ الْمَسْجِدِ فَقَالُوا: قَصُرَتِ الصَّلاةُ - وَفِي الْقَوْمِ أَبُو بَكْرٍ وَعُمَرُ - فَهَابَا أَنْ يُكَلِّمَاهُ. وَفِي الْقَوْمِ رَجُلٌ فِي يَدَيْهِ طُولٌ، يُقَالُ لَهُ: ذُو الْيَدَيْنِ فَقَالَ: يَا رَسُولَ اللَّهِ، أَنَسِيتَ، أَمْ قَصُرَتِ الصَّلاةُ؟ قَالَ: لَمْ أَنْسَ وَلَمْ تُقْصَرْ. فَقَالَ: أَكَمَا يَقُولُ ذُو الْيَدَيْنِ؟ فَقَالُوا: نَعَمْ. فَتَقَدَّمَ فَصَلَّى مَا تَرَكَ. ثُمَّ سَلَّمَ، ثُمَّ كَبَّرَ وَسَجَدَ مِثْلَ سُجُودِهِ أَوْ أَطْوَلَ. ثُمَّ رَفَعَ رَأْسَهُ فَكَبَّرَ، ثُمَّ كَبَّرَ وَسَجَدَ مِثْلَ سُجُودِهِ أَوْ أَطْوَلَ. ثُمَّ رَفَعَ رَأْسَهُ وَكَبَّرَ. فَرُبَّمَا سَأَلُوهُ: ثُمَّ سَلَّمَ؟ قَالَ: فَنُبِّئْتُ أَنَّ عِمْرَانَ بْنَ حُصَيْنٍ قَالَ: ثُمَّ سَلَّمَ» ","މުޙައްމަދު ބިން ސީރީން، އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ގެ އަރިހުން ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ތިމަންމެންނައިގެން ދުވާލުގެ ކޮންމެވެސް ނަމާދެއް ކުރެއްވިއެވެ. - އިބްނު ސީރީން ވިދާޅުވިއެވެ: އަދި އަބޫ ހުރައިރާ އެއީ ކޮން ނަމާދެއްކަން ވިދާޅުވިއެވެ. އެހެނަސް ތިމަންނައަށް ހަނދާން ނެތުނީއެވެ. - އަބޫ ހުރައިރާ ވިދާޅުވިއެވެ: ނަބިއްޔާ ތިމަންމެންނައިގެން ނަމާދުގައި ދެ ރަކުޢަތް ކުރެއްވުމަށް ފަހު ސަލާމް ދެއްވިއެވެ. ދެން މިސްކިތުގައި އޮތް ފިލާކޮޅެއް ކައިރިއަށް ކޮއްޅަށް ތެދުވެވަޑައިގެން އޭގައި ލެނގިލެއްވިއެވެ. ނަބިއްޔާ ކޯފާވެވަޑައިގެން ހުންނެވި ފަދައެވެ. އަދި ނަބިއްޔާގެ ކަނާތް އަތްޕުޅު، ވާތް އަތްޕުޅު މަތީގައި ބޭއްވުމަށް ފަހު، އެއް އަތްޕުޅުގެ އިނގިލިފުޅުތައް އަނެއް އަތްޕުޅަށް ވެއްދެވިއެވެ. ދެން އަވަސް އަރުވާލައިގެން އުޅުނު މީހުންތައް މިސްކިތުގެ ދޮރުކޮޅުތަކުން ނުކުމެއްޖެއެވެ. މީސްތަކުން ދެންނެވިއެވެ: ނަމާދު ކުރު ކުރެވުނީއެވެ. އަދި އެ ތަނުގައި ހުރި މީހުންގެ ތެރޭގައި އަބޫ ބަކްރާއި ޢުމަރު ހިމެނުނެވެ. އެހެނަސް ނަބިއްޔާއާ ވާހަކަ ދައްކަވަން އެ ދެ ބޭކަލުން ފަސްޖެހުނެވެ. އަދި އެ ތަނުގައި ހުރި މީހުންގެ ތެރޭގައި އަތްދިގު މީހަކު ހުއްޓެވެ. އޭނާއަށް ކިޔައި އުޅުނީ ޒުލް ޔަދައިނެވެ. އޭނާ ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. އިބަ ނަބިއްޔާއަށް ހަނދުމަފުޅު ނެތިވަޑައިގަތީތޯއެވެ؟ ނޫނީ ނަމާދު ކުރު ކުރެވުނީތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާ ހަނދުމަފުޅު ނެތިވަޑައި ނުގަންނަވަމެވެ. އަދި ނަމާދެއް ކުރެއްވެސް ނުކުރެވެއެވެ.“ (ދެން މީސްތަކުންނަށް) ޙަދީޘް ކުރެއްވިއެވެ: ”ޒުލް ޔަދައިން ދެންނެވި ގޮތަށް (ނަމާދު ކުރެވުނީ ކުރުކޮށް) ތޯއެވެ؟“ އެބައިމިހުން ދެންނެވިއެވެ: އާދޭހެވެ. ދެން ނަބިއްޔާ ކުރިއަށް ޖެހިވަޑައިގެން ނަމާދުގައި ދޫކޮށްލެވުނު ބައި އަލުން ކުރައްވައިގެން ސަލާމް ދެއްވިއެވެ. އަދި ތަކްބީރު ދެއްވުމަށް ފަހު، ނަބިއްޔާ އާންމުކޮށް ކުރައްވާ ވަރަށް ނުވަތަ އެއަށް ވުރެ ދިގުކޮށް ސަޖިދަ ކުރެއްވިއެވެ. ދެން އިސްތަށިފުޅު އުފުއްލަވައި ތަކްބީރު ދެއްވާފައި (ސަޖިދައިން ތެދުވެ އިށީނދެވަޑައިގަތެވެ.) އެއަށްފަހު އަނެއްކާވެސް ތަކްބީރު ދެއްވާފައި، ނަބިއްޔާ އާންމުކޮށް ކުރައްވާ ވަރަށް ނުވަތަ އެއަށް ވުރެ ދިގުކޮށް ސަޖިދަ ކުރެއްވިއެވެ. ދެން އިސްތަށިފުޅު އުފުއްލަވާފައި ތަކްބީރު ދެއްވާފައި (ސަޖިދައިން ތެދުވެ އިށީނދެވަޑައިގަތެވެ.) \n<br><br> \n(ޙަދީޘްގެ ރާވީއަކު ވިދާޅުވިއެވެ:) ފަހަރެއްގައި އިބްނު ސީރީނަށް ސުވާލު ކުރެވުނެވެ: އެއަށްފަހު ނަބިއްޔާ ސަލާމް ދެއްވިތޯއެވެ؟ އިބްނު ސީރީން ދެންނެވިއެވެ: ތިމަންނައަށް އެންގުނު ގޮތުގައ ޢިމްރާނު ބިން ޙުޞައިނު ވިދާޅުވިއެވެ: އެއަށްފަހު ނަބިއްޔާ ސަލާމް ދެއްވިއެވެ.","البخاري 482 واللفظ له، ومسلم 573"],["#111","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب سجود السهو","ކުށުސަޖިދައިގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ بُحَيْنَةَ - وَكَانَ مِنْ أَصْحَابِ النَّبِيِّ ﷺ «أَنَّ النَّبِيَّ ﷺ صَلَّى بِهِمْ الظُّهْرَ فَقَامَ فِي الرَّكْعَتَيْنِ الأُولَيَيْنِ، وَلَمْ يَجْلِسْ. فَقَامَ النَّاسُ مَعَهُ، حَتَّى إذَا قَضَى الصَّلاةَ، وَانْتَظَرَ النَّاسُ تَسْلِيمَهُ: كَبَّرَ وَهُوَ جَالِسٌ. فَسَجَدَ سَجْدَتَيْنِ قَبْلَ أَنْ يُسَلِّمَ ثُمَّ سَلَّمَ»","ޢަބްދު ﷲ ބިން ބުޙައިނާ ރިވާ ކުރެއްވިއެވެ – އަދި އެކަލޭގެފާނަކީ ނަބިއްޔާ ﷺ ގެ ޞަޙާބީއެކެވެ -: ތިމަންމެންނައިގެން ނަބިއްޔާ ﷺ މެނދުރު ނަމާދު ކުރެއްވިއެވެ. ފުރަތަމަ ދެ ރަކުޢަތަށް ފަހު (ހަނދުމަފުޅު ނެތިވަޑައިގެން ފުރަތަމަ ތަހިއްޔާތައް) އިށިނދެވަޑައި ނުގަނެ ނަބިއްޔާ ކޮއްޅަށް ތެދުވެވަޑައިގަތެވެ. އަދި މީސްތަކުންވެސް ނަބިއްޔާއާއެކު ކޮއްޅަށް ތެދުވިއެވެ. ދެން ނަމާދު ނިންމަވައި، ނަބިއްޔާ ސަލާމް ދެއްވަންދެން މީސްތަކުން އިންތިޒާރު ކުރަނިކޮށް، ފަހެ ނަބިއްޔާ އިށީންދެވަޑައިގެން އިންނަވައިގެން ތަކްބީރު ދެއްވިއެވެ. އަދި ސަލާމް ދެއްވުމުގެ ކުރިން ދެ ސަޖިދަ ކުރެއްވިއެވެ. އެއަށްފަހު ސަލާމް ދެއްވިއެވެ.","البخاري 829 واللفظ له، ومسلم 570"],["#112","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب المرور بين يدي المصلي","ނަމާދު ކުރާ މީހާގެ ކުރިމަތިން ހުރަސްކުރުމުގެ ބާބު","عَنْ أَبِي جُهَيْمِ بْنِ الْحَارِثِ بْنِ الصِّمَّةِ الأَنْصَارِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «لَوْ يَعْلَمُ الْمَارُّ بَيْنَ يَدَيْ الْمُصَلِّي مَاذَا عَلَيْهِ مِنْ الإِثْمِ؟ لَكَانَ أَنْ يَقِفَ أَرْبَعِينَ خَيْرًا لَهُ مِنْ أَنْ يَمُرَّ بَيْنَ يَدَيْهِ» قَالَ أَبُو النَّضْرِ: لا أَدْرِي: قَالَ أَرْبَعِينَ يَوْماً أَوْ شَهْراً أَوْ سَنَةً.","އަބީ ޖުހައިމު ބިން އަލްޙާރިޘް ބިން އައްޞިއްމާ އަލްއަންޞާރީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ނަމާދު ކުރާ މީހެއްގެ ކުރިމަތިން ހުރަސްކޮށްފަދާ މީހާ، އޭނާގެ މައްޗަށްވާ ފާފަ އެނގޭ ނަމަ، ފަހެ ކުރިމަތިން ހުރަސްކުރުމަށް ވުރެ، ސާޅީސް ވަންދެން (ހުރަސް ނުކޮށް) ކޮއްޅަށް ހުރުން ވެސް އޭނާއަށް މާ ހެޔޮވީހެވެ.“ (ޙަދީޘްގެ ރާވީއެއް ކަމަށްވާ) އަބޫ އައްނަޟްރު ވިދާޅުވިއެވެ: ނަބިއްޔާ ޙަދީޘް ކުރެއްވީ ސާޅީސް ދުވަސްކަމެއް، މަސްކަމެއް ނުވަތަ އަހަރުކަމެއް ތިމަންނައަކަށް ނޭނގެއެވެ.","البخاري 510، ومسلم 507"],["#113","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب المرور بين يدي المصلي","ނަމާދު ކުރާ މީހާގެ ކުރިމަތިން ހުރަސްކުރުމުގެ ބާބު","عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: سَمِعْتُ رَسُولَ اللَّهِ ﷺ يَقُولُ: «إذَا صَلَّى أَحَدُكُمْ إلَى شَيْءٍ يَسْتُرُهُ مِنْ النَّاسِ، فَأَرَادَ أَحَدٌ أَنْ يَجْتَازَ بَيْنَ يَدَيْهِ فَلْيَدْفَعْهُ. فَإِنْ أَبَى فَلْيُقَاتِلْهُ. فَإِنَّمَا هُوَ شَيْطَانٌ»","އަބޫ ސަޢީދުލް ޚުދްރީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ: ”ތިޔަބައިމީހުން ކުރެ މީހާކު، މީސްތަކުންނާ ވަކިކުރާ ސުތުރާއަކާ ކުރިމަތިލައިގެން ހުރެ ނަމާދު ކުރަނިކޮށް، އެހެން މީހަކު އައިސް އޭނާގެ ކުރިމަތިން ހުރަސްކުރަން އުޅޭ ނަމަ، ފަހެ އެ މީހަކު ހުއްޓުވައި ދުރު ކުރާށެވެ. އަދި އެ މީހަކު ހުރަސް ނުކޮށް ދޫކޮށްލުމަށް ދެކޮޅުހަދައިފި ނަމަ، ފަހެ އެ މީހަކާ ހަނގުރާމަ ކުރާށެވެ. އެހެނީ ހަމަކަށަވަރުން އެއީ ހަމަ ޝައިޠާނެކެވެ.“","البخاري 509 واللفظ له، ومسلم 505"],["#114","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب المرور بين يدي المصلي","ނަމާދު ކުރާ މީހާގެ ކުރިމަތިން ހުރަސްކުރުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: «أَقْبَلْتُ رَاكِباً عَلَى حِمَارٍ أَتَانٍ، وَأَنَا يَوْمَئِذٍ قَدْ نَاهَزْتُ الاحْتِلامَ، وَرَسُولُ اللَّهِ ﷺ يُصَلِّي بِالنَّاسِ بِمِنًى إلَى غَيْرِ جِدَارٍ. مَرَرْتُ بَيْنَ يَدَيْ بَعْضِ الصَّفِّ فَنَزَلْتُ، فَأَرْسَلْتُ الأَتَانَ تَرْتَعُ. وَدَخَلْتُ فِي الصَّفِّ، فَلَمْ يُنْكِرْ ذَلِكَ عَلَيَّ أَحَدٌ»","ޢަބްދު ﷲ ބިން ޢައްބާސް ރިވާ ކުރެއްވިއެވެ: އަންހެން ހިމާރެއްގެ މަތީ ތިމަންނަ (މިނާއަށް) އަތުވީމެވެ. އެއީ ތިމަންނަ ބާލިޢުވާން ކައިރިވެފައިވާ ދުވަސްވަރެކެވެ. އަދި ރަސޫލު ﷲ ﷺ މިނާގައި ފާރު ނޫން އެއްޗަކާ ކުރިމަތި ލައްވައިގެން، މީސްތަކުންނައިގެން ނަމާދު ކުރައްވަން ހުންނެވިއެވެ. ދެން ތިމަން ފައިބައިގެން އެ ހިމާރު ހުއިހައްޕަން ދޫކޮށްލުމަށް ފަހު، ތިމަން ސަފަށް ވަނީމެވެ. އަދި ތިމަންނަ (މައުމޫމުންގެ ސަފުތަކުގެ ކުރިމަތިން ހުރަސްކޮށްފައި ދިޔުމާ) ގުޅިގެން އެއްވެސް މީހަކު ތިމަންނައަކަށް އިންކާރެއް ނުކުރެއެވެ.","البخاري 493 واللفظ له، ومسلم 504"],["#115","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب المرور بين يدي المصلي","ނަމާދު ކުރާ މީހާގެ ކުރިމަތިން ހުރަސްކުރުމުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: «كُنْتُ أَنَامُ بَيْنَ يَدَيْ رَسُولِ اللَّهِ ﷺ وَرِجْلايَ فِي قِبْلَتِهِ - فَإِذَا سَجَدَ غَمَزَنِي، فَقَبَضْتُ رِجْلَيَّ. فَإِذَا قَامَ بَسَطْتُهُمَا. وَالْبُيُوتُ يَوْمَئِذٍ لَيْسَ فِيهَا مَصَابِيحُ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ ކުރިފުޅުމަތީގައި ތިމަން ކަމަނާ އަވަހާރަފުޅު ލައްޕަވަމެވެ. އަދި ތިމަން ކަމަނާގެ ދެ ފައި ވަނީ ނަބިއްޔާ ނަމާދު ކުރެއްވި ދިމާގައެވެ. ފަހެ ނަބިއްޔާ ސަޖިދަ ކުރައްވާ ހިނދު، ތިމަން ކަމަނާގެ (ފައިގައި) ނަބިއްޔާ ކޮށްޕަވާލައްވައެވެ. ދެން ތިމަން ކަމަނާގެ ދެ ފައި ކައިރިކޮށްލަމެވެ. އަދި ނަބިއްޔާ ކޮއްޅަށް ތެދުވެވަޑައިގެންނަވާ ހިނދު، ތިމަން ކަމަނާގެ ދެ ފައި އަނެއްކާވެސް ދަމާލަމެވެ. އަދި އެ ދުވަސްވަރު ގެތަކުގައި ބައްތިއެއް ނުހުރެއެވެ.","البخاري 382، ومسلم 512"],["#116","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب جامع","އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ أَبِي قَتَادَةَ بْنِ رِبْعِيٍّ الأَنْصَارِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ ﷺ: «إذَا دَخَلَ أَحَدُكُمْ الْمَسْجِدَ فَلا يَجْلِسْ حَتَّى يُصَلِّيَ رَكْعَتَيْنِ»","އަބޫ ޤަތާދާ ބިން ރިބްޢީ އަލްއަންޞާރީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ކުރެ މީހަކު މިސްކިތަށް ވަދެއްޖެ ނަމަ، ދެ ރަކުޢަތް ކުރުމުގެ ކުރިން ނީށިންނާށެވެ.“","البخاري 1163 واللفظ له، ومسلم 714"],["#117","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب جامع","އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ زَيْدِ بْنِ أَرْقَمَ قَالَ: «كُنَّا نَتَكَلَّمُ فِي الصَّلاةِ، يُكَلِّمُ الرَّجُلُ صَاحِبَهُ، وَهُوَ إلَى جَنْبِهِ فِي الصَّلاةِ، حَتَّى نَزَلَتْ «وَقُومُوا لِلَّهِ قَانِتِينَ» فَأُمِرْنَا بِالسُّكُوتِ وَنُهِينَا عَنْ الْكَلامِ»","ޒައިދު ބިން އަރްޤަމު ރިވާ ކުރެއްވިއެވެ: (ކުރީގައި) ތިމަންމެން ނަމާދުގައި ހުންނަ އިރު ވާހަކަ ދައްކާ ހަދަމެވެ. ނަމާދުގައި މީހަކާ ޖެހިގެން ހުރި އަނެއް މީހާއަށް ވާހަކަ ދައްކައެވެ. އެއީ ❞އަދި ތިޔަބައިމީހުން ﷲ އަށް ކިޔަމަންތެރިވެ މަޑުމައިތިރކަމާއެކު (ނަމާދުގައި) ގާއިމުވެ ތިބޭށެވެ.❝ (މި އާޔަތް) ބާވައިލެއްވުމަށް ދާންދެނެވެ. ދެން ތިމަންމެން ސުކޫތުވުމަށް އަމުރު ކުރެވުނެވެ. އަދި ވާހަކަ ދެއްކުން ތިމަންމެންނަށް ނަހީ ކުރެވުނެވެ.","البخاري 1200، ومسلم 539 واللفظ له"],["#118","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب جامع","އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ وَأَبِي هُرَيْرَةَ رَضِيَ اللَّهُ عَنْهُم عَنْ رَسُولِ اللَّهِ ﷺ أَنَّهُ قَالَ: «إذَا اشْتَدَّ الْحَرُّ فَأَبْرِدُوا بِالصَّلاةِ. فَإِنَّ شِدَّةَ الْحَرِّ مِنْ فَيْحِ جَهَنَّمَ»","ޢަބްދު ﷲ ބިން ޢުމަރު އަދި އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُمْ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ހަދޘް ކުރެއްވިއެވެ: ”ހޫނުކަން ގަދަވެއްޖެ ނަމަ، (ކޮންމެވެސް ވަރަކަށް ހޫނު މަޑުވެ) ފިނިވެލީމާ ތިޔަބައިމީހުން ނަމާދު ކުރާށެވެ. އެހެނީ ހަމަކަށަވަރުން ހޫނުގަދަވުމަކީ ނަރަކައިގެން ކެކިއަރައިގަތުމުން އަންނަ ކަމެކެވެ.“","البخاري 533، 534، ومسلم 615"],["#119","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب جامع","އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «مَنْ نَسِيَ صَلاةً فَلْيُصَلِّهَا إذَا ذَكَرهَا، وَلا كَفَّارَةَ لَهَا إلاَّ ذَلِكَ. أَقِمْ الصَّلاةَ لِذِكْرِي» \n<br><br>\nوَلِمُسْلِمٍ «مَنْ نَسِيَ صَلاةً، أَوْ نَامَ عَنْهَا. فَكَفَّارَتُهَا: أَنْ يُصَلِّيَهَا إذَا ذَكَرَهَا»\n","އަނަސް ބިން މާލިކު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”މީހަކަށް ނަމާދެއް ހަނދާން ނެތިއްޖެ ނަމަ، ފަހެ އޭނާއަށް ހަނދާންވުމުން އެ ނަމާދެއް ކުރާށެވެ. އަދި އެ ނޫން ކައްފާރާއެއް އެ ކަމަކަށް ނުވެއެވެ.“ (ދެން ﷲ ތަޢާލާގެ މި އާޔަތް ކިޔަވައި ވިދާޅުވިއެވެ:) ❞އަދި ތިމަން އިލާހު ހަނދުމަ ކުރުމަށްޓަކައި ނަމާދު ޤާއިމު ކުރާށެވެ.❝\n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: މީހަކަށް ނަމާދެއް ހަނދާން ނެތިއްޖެ ނަމަ، ނޫނީ ނުކޮށް ނިދިއްޖެ ނަމަ، ފަހެ އެ ކަމުގެ ކައްފާރާއަކީ ހަނދާންވުމުން އެ ނަމާދެއް ކުރުމެވެ.","البخاري 597 واللفظ له، ومسلم 784\n<br><br>\nمسلم 784"],["#120","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب جامع","އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ «أَنَّ مُعَاذَ بْنَ جَبَلٍ: كَانَ يُصَلِّي مَعَ رَسُولِ اللهِ ﷺ عِشَاءَ الآخِرَةِ. ثُمَّ يَرْجِعُ إلَى قَوْمِهِ، فَيُصَلِّي بِهِمْ تِلْكَ الصَّلاةَ» ","ޖާބިރު ބިން ޢަބްދު ﷲ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ އަރިހުގައި، މުޢާޛު ބިން ޖަބަލު ޢިޝާ ނަމާދު ކުރެއްވިއެވެ. އެއަށްފަހު މުޢާޛުގެ ގައުމުގެ މީހުން ގާތަށް އެކަލޭގެފާނު އެނބުރި ގޮސް، އެބައިމީހުންނައިގެން އެ ނަމާދު (އަލުން) ކުރައްވައެވެ.","البخاري 700، ومسلم 465 واللفظ له"],["#121","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب جامع","އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ قَالَ: «كُنَّا نُصَلِّي مَعَ رَسُولِ اللَّهِ ﷺ فِي شِدَّةِ الْحَرِّ. فَإِذَا لَمْ يَسْتَطِعْ أَحَدُنَا أَنْ يُمَكِّنَ جَبْهَتَهُ مِنَ الأَرْضِ: بَسَطَ ثَوْبَهُ فَسَجَدَ عَلَيْهِ»","އަނަސް ބިން މާލިކު ރިވާ ކުރެއްވިއެވެ: ހޫނުގަދަވެފައި ވަނިކޮށް ރަސޫލު ﷲ ﷺ ގެ އަރިހުގައި ތިމަންމެން ނަމާދު ކުރީމެވެ. ފަހެ ތިމަންމެން ކުރެ މީހަކަށް ބިންމަތީގައި ނިއްކުރި ޖައްސާލަން ނުކެރޭވަރު ވުމުން، (އެ ތަނުގައި) އެ މިހެއްގެ ހެދުން (ދާމާލާފައި ނުވަތަ) ފަތުރާލާފައި އޭގެ މަތީ ސަޖިދަ ކުރެއެވެ.","البخاري 1208 واللفظ له، ومسلم 620"],["#122","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب جامع","އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «لا يُصَلِّي أَحَدُكُمْ فِي الثَّوْبِ الْوَاحِدِ، لَيْسَ عَلَى عَاتِقِهِ مِنْهُ شَيْءٌ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ކުރެ އެކަކުވެސް އޭނާގެ ކޮނޑު ނިވާ ނުކުރާ، އެންމެ ފޭރާމެއް ލައިގެން ހުރެ ނަމާދު ނުކުރާށެވެ.“","البخاري 359، ومسلم 516"],["#123","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب جامع","އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رَضِيَ اللَّهُ عَنْهُما عَنِ النَّبِيِّ ﷺ أَنَّهُ قَالَ: «مَنْ أَكَلَ ثُوماً أَوْ بَصَلاً. فَلْيَعْتَزِلْنَا أَوْ لِيَعْتَزِلْ مَسْجِدَنَا، وَلْيَقْعُدْ فِي بَيْتِهِ. وَأُتِيَ بِقِدْرٍ فِيهِ خَضِرَاتٌ مِنْ بُقُولٍ. فَوَجَدَ لَهَا رِيحاً، فَسَأَلَ؟ فَأُخْبِرَ بِمَا فِيهَا مِنْ الْبُقُولِ. فَقَالَ: قَرِّبُوهَا إلَى بَعْضِ أَصْحَابِي. فَلَمَّا رَآهُ كَرِهَ أَكْلَهَا. قَالَ: كُلْ. فَإِنِّي أُنَاجِي مَنْ لا تُنَاجِي»","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ލޮނުމެދު ނުވަތަ ފިޔާ ކައިފި މީހަކު، ތިމަންމެންގެ މިސްކިތުން ދުރުވެގަންނާށެވެ. އަދި އެ މީހެއްގެ ގޭގައި މަޑުކުރާށެވެ.“ (އެއްފަހަރަކު) ތެއްޔެއްގައި ކައްކާފައި ހުރި ތަރުކާރިތަކެއް ނަބިއްޔާގެ އަރިހަށް ގެނެވުނެވެ. ނަބިއްޔާއަށް އެއިން ނުބައި ވަހެއް ދުވުމުން، އެއާ ބެހޭ ގޮތުން ސުވާލު ކުރެއްވިއެވެ. ދެން އޭގައި ހުރި ތަރުކާރިތައް ނަބިއްޔާއަށް ކިޔައިދެވުނެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާގެ ބައެއް ޞަޙާބީންގެ ގާތަށް އޭތި ކައިރި ކުރާށެވެ.“ ފަހެ ނަބިއްޔާއަށް އޭތި ފެނިވަޑައިގަތުމުން އެ ފަރީއްކުޅުވާން އެދިވަޑައި ނުގަތީއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާމެން ކާށެވެ. އެހެނީ ހަމަކަށަވަރުން ތިޔަބައިމީހުން ވާހަކަ ނުދައްކާ ފަރާތްތަކާ، ތިމަން ނަބިއްޔާ ވަކިން ވާހަކަފުޅު ދައްކަވަމެވެ.“","البخاري 855، ومسلم 564"],["#124","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب جامع","އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ جَابِرِ بنِ عبدِ اللهِ رَضِيَ اللَّهُ عَنْهُمَا أَنَّ النَّبِيَّ ﷺ قَالَ: «مَنْ أَكَلَ الثُّومَ وَالْبَصَلَ وَالْكُرَّاثَ فَلا يَقْرَبَنَّ مَسْجِدَنَا، فَإِنَّ الْمَلائِكَةَ تَتَأَذَّى مِمَّا يَتَأَذَّى مِنْهُ الإِنْسَانُ» \n<br><br>\nوفي روايةٍ «بني آدمَ»","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ލޮނުމެދު، ފިޔާ ނުވަތަ ލީކްސް ކައިފި މީހަކު، ތިމަންމެންގެ މިސްކިތާ ކައިރި ނުވާށެވެ. އެހެނީ ހަމަކަށަވަރުން އިންސާނާއަށް އުނދަގޫވާ ތަކެތިން މަލާއިކަތުންނަށް ވެސް އުނދަގޫވެއެވެ.“\n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”އާދަމުގެ ދަރިންނަށް (އުނދަގޫވާ ތަކެތިން މަލާއިކަތުންނަށް ވެސް އުނދަގޫވެއެވެ.)“","مسلم 464"],["#125","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب التشهد","ތަހިއްޔާތުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بنِ مَسْعُودٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «عَلَّمَنِي رَسُولُ اللَّهِ ﷺ التَّشَهُّدَ - كَفِّي بَيْنَ كَفَّيْهِ - كَمَا يُعَلِّمُنِي السُّورَةَ مِنْ الْقُرْآنِ: التَّحِيَّاتُ لِلَّهِ، وَالصَّلَوَاتُ وَالطَّيِّبَاتُ، السَّلامُ عَلَيْكَ أَيُّهَا النَّبِيُّ وَرَحْمَةُ اللَّهِ وَبَرَكَاتُهُ. السَّلامُ عَلَيْنَا وَعَلَى عِبَادِ اللَّهِ الصَّالِحِينَ. أَشْهَدُ أَنْ لا إلَهَ إلاَّ اللَّهُ وَأَشْهَدُ أَنَّ مُحَمَّداً عَبْدُهُ وَرَسُولُهُ»","ޢަބްދު ﷲ ބިން މަސްޢޫދު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ ދެ އަތްޕުޅު ތެރޭގައި ތިމަންނަގެ އަތް ވަނިކޮށް، ނަބިއްޔާ ތިމަންނައަށް (ފުރަތަމަ) ތަހިއްޔާތު އުނގަންނަވައި ދެއްވިއެވެ. އެއީ ހަމަ ޤުރްއާނުގެ ސޫރަތްތައް ތިމަންނައަށް އުނގަންވައިދެއްވި ފަދައިންނެވެ. (ފުރަތަމަ ތަހިއްޔާތުގައި ވިދާޅުވީ:) ”އެންމެހައި މަތިވެރިކަމަކާއި ޞަލަވާތާއި ހެޔޮ ކަމެއް ލިބިވޮޑިގެންވަނީ ﷲ އަށެވެ. އޭ ނަބިއްޔާއެވެ. އިބަ ނަބިއްޔާގެ މައްޗަށް ސަލާމާއި ﷲ ގެ ރަޙުމަތާއި ބަރަކާތް ލައްވާށިއެވެ. އަޅަމެންނަށާއި ﷲ ގެ ޞާލިޙު އަޅުތަކުންގެ މައްޗަށް ވެސް ސަލާމް ލައްވާށިއެވެ. ﷲ ފިޔަވައި (އަޅުކަން ޙައްޤުވާ) އެހެން އެއްވެސް އިލާހަކު ނުވާކަމަށާއި، މުޙައްމަދުގެފާނީ އެ އިލާހުގެ އަޅާ އަދި ރަސޫލާކަމަށް ތިމަން ހެކިވަމެވެ.“","البخاري 6265 واللفظ له، ومسلم 402"],["#126","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب التشهد","ތަހިއްޔާތުގެ ބާބު","وَفِي لَفْظٍ: «إذَا قَعَدَ أَحَدُكُمْ فِي الصَّلاةِ فَلْيَقُلْ: التَّحِيَّاتُ للهِ - وَذَكَرَهُ -\n<br><br>\nوَفِيهِ: فَإِنَّكُمْ إذَا فَعَلْتُمْ ذَلِكَ فَقَدْ سَلَّمْتُمْ عَلَى كُلِّ عَبْدٍ صَالِحٍ فِي السَّمَاءِ وَالأَرْضِ <br><br>\n- وَفِيهِ - فَلْيَتَخَيَّرْ مِنْ الْمَسْأَلَةِ مَا شَاءَ» ","އަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”ތިޔަބައިމީހުންގެ ތެރެއިން މީހަކު ނަމާދުގައި (އައްތަޙިޔާތަށް) އިށީނދިއްޖެ ނަމަ މިފަދައިން ކިޔާށެވެ: އެންމެހައި މަތިވެރިކަމެއް ލިބިވޮޑިގެންވަނީ ﷲ އަށެވެ.“ - ދެން ނަބިއްޔާ (އައްތަޙިއްޔާދުގެ ދުޢާ) ވިދާޅުވިއެވެ. -\n<br><br>\nއަދި ޙަދީޘް ކުރެއްވިއެވެ: ”އަދި ތިޔަބައިމީހުން އެހެން ހަދައިފި ނަމަ، ފަހެ ހަމަކަށަވަރުން އުޑާއި ބިމުގައިވާ ކޮންމެ ސާލިހު އަޅެއްގެ މައްޗަށް ތިޔަބައިމީހުން ސަލާމް ލެއްވުން އެދި ދިޢާ ކުރެވުނީއެވެ.“\n<br><br>\nދެންވެސް ޙަދީޘް ކުރެއްވިއެވެ: ”އެއަށްފަހު އެ މީހަކު ބޭނުން ކަމަކު އެދި ދުޢާ ކުރާށެވެ.“","البخاري 6328، ومسلم 402\n<br><br>\nالبخاري 1202 واللفظ له، ومسلم 402\n<br><br>\nالبخاري 6328، ومسلم 402 واللفظ له"],["#127","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب التشهد","ތަހިއްޔާތުގެ ބާބު","عَنْ عَبْدِ الرَّحْمَنِ بْنِ أَبِي لَيْلَى قَالَ: «لَقِيَنِي كَعْبُ بْنُ عُجْرَةَ فَقَالَ أَلا أُهْدِي لَكَ هَدِيَّةً؟ أَنَّ النَّبِيَّ ﷺ خَرَجَ عَلَيْنَا، فَقُلْنَا: يَا رَسُولَ اللَّهِ، قَدْ عَلَّمَنَا اللَّهُ كَيْفَ نُسَلِّمُ عَلَيْكَ: فَكَيْفَ نُصَلِّي عَلَيْكَ؟ فَقَالَ: قُولُوا: اللَّهُمَّ صَلِّ عَلَى مُحَمَّدٍ وَعَلَى آلِ مُحَمَّدٍ، كَمَا صَلَّيْتَ عَلَى إبْرَاهِيمَ إنَّكَ حَمِيدٌ مَجِيدٌ، وَبَارِكْ عَلَى مُحَمَّدٍ وَعَلَى آلِ مُحَمَّدٍ، كَمَا بَارَكْتَ عَلَى إبْرَاهِيمَ إنَّكَ حَمِيدٌ مَجِيدٌ»","ޢަބްދުއްރަޙްމާން ބިން އަބީ ލައިލާ ރިވާ ކުރެއްވިއެވެ: ކަޢުބް ބިން ޢުޖްރާ، ތިމަންނައާ ބައްދަލުވެގެން ދެންނެވިއެވެ: ތިބާއަށް ހަދީޔާއެއް ދެންތޯއެވެ؟ ނަބިއްޔާ ﷺ ތިމަންމެން ގާތަށް ނުކުމެވަޑައިގަތުމުން ތިމަންމެން ދެންނެވީމެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން އިބަ ނަބިއްޔާގެ މައްޗަށް ސަލާމް ލައްވާނެ ގޮތް ﷲ ވަނީ ތިމަންމެންނަށް އުނގަންނަވައި ދެއްވާފައެވެ. ފަހެ އިބަ ނަބިއްޔާގެ މައްޗަށް ޞަލަވާތް ލައްވާނީ ކިހިނަކުންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ކިޔާށެވެ: ޔާ ﷲ އެވެ. އިބަ އިލާހު އިބްރާހީމްގެފާނުގެ މައްޗަށް ޞަލަވާތް ލެއްވި ފަދައިން، މުޙައްމަދުގެފާނަށާއި މުހައްމަދުގެފާނުގެ އާއިލާގެ މައްޗަށް ވެސް ޞަލަވާތް ލައްވާށިއެވެ. ހަމަކަށަވަރުން އިބަ އިލާހި ޙަމްދާއި މަތިވެރިކަން ލިބިގެންވާ އިލާހެވެ.“","البخاري 6357، ومسلم 406"],["#128","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب التشهد","ތަހިއްޔާތުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «كَانَ رَسُولُ اللَّهِ ﷺ يَدْعُو: اللَّهُمَّ إنِّي أَعُوذُ بِكَ مِنْ عَذَابِ الْقَبْرِ، وَعَذَابِ النَّارِ، وَمِنْ فِتْنَةِ الْمَحْيَا وَالْمَمَاتِ، وَمِنْ فِتْنَةِ الْمَسِيحِ الدَّجَّالِ» \n <br><br>\nوَفِي لَفْظٍ لِمُسْلِمٍ: «إذَا تَشَهَّدَ أَحَدُكُمْ فَلْيَسْتَعِذْ بِاَللَّهِ مِنْ أَرْبَعٍ، يَقُولُ: اللَّهُمَّ إنِّي أَعُوذُ بِكَ مِنْ عَذَابِ جَهَنَّمَ»\n","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ (މިފަދައިން) ދުޢާ ކުރައްވައެވެ: ”ޔާ ﷲ އެވެ. ހަމަކަށަވަރުން ކަށްވަރުގެ އަޒާބާއި، ނަރަކައިގެ އަޒާބާއި، ދިރިއުޅުމާއި މަރުވުމުގެ ފިތުނައިންނާއި، އަލްމަސީހުއް ދައްޖާލުގެ ފިތުނައިން މި އަޅާ ރައްކާތެރިކޮށް ދެއްވުން އެދި އިބަ އިލާހުގެ ޙަޟްރަތުގައި ދަންނަވަމެވެ.“ \n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ”ތިޔަބައިމީހުން ކުރެ މީހަކު (ފުރަތަމަ) ތަހިއްޔާތު ކިޔައިފި ނަމަ، ފަހެ ހަތަރު ކަމަކުން ރައްކާތެރިކޮށް ދެއްވުން އެދި ﷲ ގެ ޙަޟްރަތުގައި ދަންނަވާށެވެ.“ (މިފަދައިން) ކިޔާށެވެ: ޔާ ﷲ އެވެ. ހަމަކަށަވަރުން ނަރަކައިގެ އަޒާބުން މި އަޅާ ރައްކާތެރިކޮށް ދެއްވުން އެދި އިބަ އިލާހުގެ ޙަޟްރަތުގައި ދަންނަވަމެވެ.“","البخاري 1377 واللفظ له، ومسلم 588\n<br><br>\nمسلم 588"],["#129","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب التشهد","ތަހިއްޔާތުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَمْرِو بْنِ الْعَاصِ عَنْ أَبِي بَكْرٍ الصِّدِّيقِ رَضِيَ اللَّهُ عَنْهُم أَنَّهُ قَالَ لِرَسُولِ اللَّهِ ﷺ: «عَلِّمْنِي دُعَاءً أَدْعُو بِهِ فِي صَلاتِي. قَالَ: قُلْ: اللَّهُمَّ إنِّي ظَلَمْتُ نَفْسِي ظُلْماً كَثِيرَاً. وَلا يَغْفِرُ الذُّنُوبَ إلاَّ أَنْتَ. فَاغْفِرْ لِي مَغْفِرَةً مِنْ عِنْدِكَ. وَارْحَمْنِي، إنَّكَ أَنْتَ الْغَفُورُ الرَّحِيمُ»","ޢަބްދު ﷲ ބިން ޢަމްރު ބިން އަލްޢާޞް، އަބޫ ބަކްރު އައްޞިއްދީޤު رَضِيَ اللَّهُ عَنْهُمْ ގެ އަރިހުން ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ އަރިހުގައި އަބޫ ބަކްރު ދެންނެވިއެވެ: ތިމަންނަގެ ނަމާދުގައި ކުރެވޭ ދުޢާއެއް އުނގަންނަވައި ދެއްވާށެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ކިޔާށެވެ: ޔާ ﷲ އެވެ. ހަމަކަށަވަރުން މި އަޅާގެ އަމިއްލަ ނަފްސަށް ގިނަގިނައިން އަނިޔާ ކުރެވިއްޖެކަން ކަށަވަރެވެ. އަދި އިބަ އިލާހު ފިޔަވައި ފާފަ ފުއްސަވާނެ ފަރާތަކު ނުވެއެވެ. ފަހެ އިބަ އިލާހުގެ ޙަޟްރަތުންވާ ފުއްސެވުމަކައިގެން މި އަޅާގެ ފާފަ ފުއްސަވާށިއެވެ. އަދި މި އަޅާއަށް ރަޙުމަތް ލައްވާށިއެވެ. ހަމަކަށާވަރުން އިބަ އިލާހީ ގިނަގިނައިން ފާފަ ފުއްސަވާ ރަޙީމްވަންތަ އިލާހެވެ.“","البخاري 834، ومسلم 2705"],["#130","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب التشهد","ތަހިއްޔާތުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: «مَا صَلَّى رَسُولُ اللَّهِ ﷺ بَعْدَ أَنْ نَزَلَتْ عَلَيْهِ «إذَا جَاءَ نَصْرُ اللَّهِ وَالْفَتْحُ» - إلاَّ يَقُولُ فِيهَا: سُبْحَانَكَ رَبَّنَا وَبِحَمْدِكَ، اللَّهُمَّ اغْفِرْ لِي» \n<br><br>\nوَفِي لَفْظٍ: «كَانَ رَسُولُ اللَّهِ ﷺ يُكْثِرُ أَنْ يَقُولَ فِي رُكُوعِهِ وَسُجُودِهِ: سُبْحَانَكَ اللَّهُمَّ رَبَّنَا وَبِحَمْدِكَ، اللَّهُمَّ اغْفِرْ لِي»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: إذَا جَاءَ نَصْرُ اللَّهِ وَالْفَتْحُ (މި ސޫރަތް) ބާވައިލެއްވި ފަހުން، ރަސޫލު ﷲ ﷺ ނަމާދު ކުރައްވާ ހިނދު (މިފަދައިން) ވިދާޅުނުވެ ނަމާދެއް ނުކުރައްވައެވެ: ”އިބަ އިލާހު ހުސްޠާހިރުވެ ވޮޑިގެންވެއެވެ. އޭ އަޅަމެންގެ ވެރި ރައްބެވެ. އިބަ އިލާހުގެ ޙަމްދުން (އިބަ އިލާހަށް ސަނާ ދަންނަވަމެވެ.) ޔާ ﷲ އެވެ. މި އަޅާގެ ފާފާ ފުއްސަވާށިއެވެ.“ \n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ރަސޫލު ﷲ ﷺ ރުކޫޢާއި ސަޖިދަ ކުރެއްވުމުގައި ވަރަށް ގިނައިން މިފަދައިން ވިދާޅުވެއެވެ: ”އިބަ އިލާހު ހުސްޠާހިރުވެ ވޮޑިގެންވެއެވެ. ޔާ ﷲ އެވެ. އޭ އަޅަމެންގެ ވެރި ރައްބެވެ. އިބަ އިލާހުގެ ޙަމްދުން (އިބަ އިލާހަށް ސަނާ ދަންނަވަމެވެ.) ޔާ ﷲ އެވެ. މި އަޅާގެ ފާފާ ފުއްސަވާށިއެވެ.“","البخاري 4967 واللفظ له، ومسلم 484\n<br><br>\nالبخاري 817، 4968، ومسلم 484"],["#131","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الوتر","ވިތުރީގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما قَالَ: «سَأَلَ رَجُلٌ النَّبِيَّ ﷺ وَهُوَ عَلَى الْمِنْبَرِ - مَا تَرَى فِي صَلاةِ اللَّيْلِ؟ قَالَ: مَثْنَى، مَثْنَى. فَإِذَا خَشِيَ أَحَدُكُمْ الصُّبْحَ صَلَّى وَاحِدَةً. فَأَوْتَرَتْ لَهُ مَا صَلَّى. وَإِنَّهُ كَانَ يَقُولُ: اجْعَلُوا آخِرَ صَلاتِكُمْ بِاللَّيْلِ وِتْراً»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ މިންބަރުގައި ހުންނަވަނިކޮށް، ނަބިއްޔާގެ އަރިހުގައި މީހަކު ސުވާލު ދެންނެވިއެވެ: ރޭއަޅުކަމާ މެދު އިބަ ނަބިއްޔާ ދެކިލައްވަނީ ކިހިނަކުންތޯއެވެ؟ (އެބަހީ: ކުރާނީ ކިހިނަކުންތޯއެވެ؟) ނަބިއްޔާ ޙަދިޘް ކުރެއްވިއެވެ: ”ދެ ރަކުޢަތުން ދެ ރަކުޢަތުންނެވެ. ދެން ތިޔަބައިމީހުން ކުރެ މީހަކަށް ފަތިސްވެދާނޭ ކަމުގެ ބިރު ހީވާނަމަ، އެއް ރަކުޢަތް (ކުރާށެވެ.) އޭރުން އޭނާ ކުރެވުނު ނަމާދުތަކަށް ވިތުރި ކުރެވުނީއެވެ.“ އަދި ހަމަކަށަވަރުން ނަބިއްޔާ ޙަދީޘް ކުރައްވައެވެ: ”ތިޔަބައިމީހުންގެ ރޭގަނޑުގެ ފަހު ނަމާދަކަށް ވިތުރި ހަދާށެވެ.“","البخاري 472 واللفظ له، ومسلم 739"],["#132","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الوتر","ވިތުރީގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: «مِنْ كُلِّ اللَّيْلِ أَوْتَرَ رَسُولُ اللَّهِ ﷺ: مِنْ أَوَّلِ اللَّيْلِ، وَأَوْسَطِهِ، وَآخِرِهِ. وَانْتَهَى وِتْرُهُ إلَى السَّحَرِ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ރޭގަނޑުގެ ހުރިހާ ބަޔެއްގައި ވެސް (އެކި ފަހަރު މަތިން) ރަސޫލު ﷲ ﷺ ވިތުރި ކުރެއްވައެވެ. (އެ ގޮތުން) ރޭގަނޑުގެ ފުރަތަމަ ކޮޅުގައާއި، މެދުތެރޭގައާއި، އަދި ފަހުކޮޅުގައި ވެސް މެއެވެ. އަދި ފަތިސްވުމުގެ ކުރިން ނަބިއްޔާގެ ވިތުރި ނިންމަވާލައްވައެވެ.","البخاري 996، ومسلم 745 واللفظ له"],["#133","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الوتر","ވިތުރީގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: «كَانَ رَسُولُ اللَّهِ ﷺ يُصَلِّي مِنْ اللَّيْلِ ثَلاثَ عَشْرَةَ رَكْعَةً يُوتِرُ مِنْ ذَلِكَ بِخَمْسٍ، لايَجْلِسُ فِي شَيْءٍ إلاَّ فِي آخِرِهَا»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ރޭއަޅުކަން ކުރައްވަނީ ތޭރަ ރަކުޢަތުންނެވެ. އޭގެ ތެރެއިން (އަށް ރަކުޢަތް ކުރައްވަނީ ދެ ރަކުޢަތުން ދެ ރަކުޢަތުންނެވެ.) ދެން ވިތުރީގެ ގޮތުގައި ފަސް ރަކުޢަތް ކުރައްވައެވެ. އެ ފަސް ރަކުޢަތުގެ ފަހުކޮޅުގައި މެނުވީ (ތަހިއްޔާތަށް) އިށީންދެވަޑައި ނުގަންނަވައެވެ.","مسلم 737"],["#134","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الذكر عقب الصلاة","ނަމާދަށް ފަހު ޒިކުރު ކިޔުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما: «أَنَّ رَفْعَ الصَّوْتِ بِالذِّكْرِ، حِينَ يَنْصَرِفُ النَّاسُ مِنْ الْمَكْتُوبَةِ كَانَ عَلَى عَهْدِ رَسُولِ اللَّهِ ﷺ» قَالَ ابْنُ عَبَّاسٍ: «كُنْتُ أَعْلَمُ إذَا انْصَرَفُوا بِذَلِكَ إذَا سَمِعْتُهُ». \n<br><br>\nوَفِي لَفْظٍ «مَا كُنَّا نَعْرِفُ انْقِضَاءَ صَلاةِ رَسُولِ اللَّهِ ﷺ بِالتَّكْبِيرِ».","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ ޒަމާނުގައި، މީސްތަކުން ފަރުޟު ނަމާދުތައް ނިންމުމަށް ފަހު އަޑުހަރުކޮށް ޒިކުރުތައް ކިޔުއްވައެވެ. އިބްނު ޢައްބާސް ވިދާޅުވިއެވެ: އެ ޒިކުރުތައް ކިޔާ އަޑު ތިމަންނައަށް އިވުމުން އެބައިމީހުން ނަމާދު ނިންމެވިކަން ތިމަންނައަށް އެނގިގެން ދެއެވެ.\n<br><br>\nއަދި އެހެން ރިވޔަތެއްގައި ވެއެވެ: ރަސޫލު ﷲ ﷺ (ޒިކުރުގައި) ތަކްބީރު ވިދާޅުވުމުން މެނުވީ ނަމާދު ނިންމެވިކަން ތިމަންމެންނަށް ނޭނގެއެވެ.","البخاري 841، ومسلم 583\n<br><br>\nمسلم 583"],["#135","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الذكر عقب الصلاة","ނަމާދަށް ފަހު ޒިކުރު ކިޔުމުގެ ބާބު","عَنْ وَرَّادٍ مَوْلَى الْمُغِيرَةِ بْنِ شُعْبَةَ قَالَ: أَمْلَى عَلَيَّ الْمُغِيرَةُ بْنُ شُعْبَةَ مِنْ كِتَابٍ إلَى مُعَاوِيَةَ: إنَّ النَّبِيَّ ﷺ كَانَ يَقُولُ فِي دُبُرِ كُلِّ صَلاةٍ مَكْتُوبَةٍ: «لا إلَهَ إلاَّ اللَّهُ وَحْدَهُ لا شَرِيكَ لَهُ، لَهُ الْمُلْكُ وَلَهُ الْحَمْدُ، وَهُوَ عَلَى كُلِّ شَيْءٍ قَدِيرٍ. اللَّهُمَّ لا مَانِعَ لِمَا أَعْطَيْتَ وَلا مُعْطِيَ لِمَا مَنَعْتَ، وَلا يَنْفَعُ ذَا الْجَدِّ مِنْكَ الْجَدُّ»\n<br><br>\nثُمَّ وَفَدْتُ بَعْدَ ذَلِكَ عَلَى مُعَاوِيَةَ فَسَمِعْتُهُ يَأْمُرُ النَّاسَ بِذَلِكَ. \n<br><br>\nوَفِي لَفْظٍ: كَانَ يَنْهَى عَنْ قِيلَ وَقَالَ، وَإِضَاعَةِ الْمَالِ، وَكَثْرَةِ السُّؤَالِ.\n<br><br>\nوَكَانَ يَنْهَى عَنْ عُقُوقِ الأُمَّهَاتِ، وَوَأْدِ الْبَنَاتِ، وَمَنْعٍ وَهَاتِ.","އަލްމުޣީރާ ބިން ޝުޢުބާ މިނިވަން ކުރެއްވި އަޅެއް ކަމުގައިވާ ވައްރާދު ރިވާ ކުރެއްވިއެވެ: މުޢާވިޔާއަށް ސިޓީއެއް ފޮނުއްވުމުގެ ތެރޭގައި، އަލްމުޣީރާ ބިން ޝުޢުބާ (ސިޓީގައި ލިޔަންވީ އެއްޗެއް) ކިޔައިދެއްވުމުން ތިމަން ލިޔުނީމެވެ: ހަމަކަށަވަރުން ކޮންމެ ފަރުޟު ނަމާދެއް ނިންމެވުމަށް ފަހު ނަބިއްޔާ ﷺ ވިދާޅުވެއެވެ: ”ﷲ ފިޔަވައި (އަޅުކަން ޙައްޤުވާ) އެހެން އެއްވެސް އިލާހަކު ނުވެއެވެ. އެ އިލާހު އެއްކައުވަންތަ ވޮޑިގެންވެއެވެ. އެ އިލާހަށް އެއްވެސް ޝަރީކަކު ނުވެއެވެ. އެންމެހައި މިލްކުވެރިކަމަކާއި ޙަމްދެއް ލިބިވޮޑިގެންވަނީ އެ އިލާހަށެވެ. އަދި އެ އިލާހީ ކޮންމެ ކަމަކަށް ކުޅަދުންވަންތަ އިލާހެވެ. ޔާ ﷲ އެވެ. އިބަ އިލާހު ދެއްވާ އެއްޗެއް މަނާ ކުރާނެ ފަރާތެއް ނުވެއެވެ. އަދި އިބަ އިލާހު މަނާ ކުރެއްވި އެއްޗެއް ދޭނެ ފަރާތެއް ނުވެއެވެ. އަދި މުދާވެރި ނަސީބުވެރިއަކަށް، އިބަ އިލާހާ ދެކޮޅަށް އޭނާގެ ނަސީބުވެރިކަމުން އެއްވެސް ފައިދާއެއް ނުކުރެވޭނެއެވެ.“\n<br><br>\nއެއަށްފަހު މުޢާވިޔާގެ ގާތަށް (އެ ސިޓީ ހިފައިގެން) ތިމަންނަ ދިޔައިމެވެ. އަދި އެ (ސިޓީގައިވާ) ގޮތަށް ކަންކުރެއްވުމަށް މީސްތަކުންނަށް މުޢާވިޔާ އެންގެވި އަޑު ތިމަން އެހީމެވެ. \n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: އެވެނި މީހަކު މިވެނި މީހަކު ބުންޏޭ ކިޔުމާއި، މުދާ ބޭކާރު ގޮތުގައި ހޭދަ ކުރުމާއި، ގިނަގިނައިން ސުވާލު ކުރުން ނަބިއްޔާ ނަހީ ކުރެއްވިއެވެ.\n<br><br>\nއަދި މައިމީހާއަށް ގޯސްކޮށް ހިތުމާއި، ޅަ އަންހެން ދަރިން (ދިރިހުއްޓައި) ވަޅުލުމާއި، (ދޭންވާ) އެއްޗެއް ނުދިނުމާއި އަދި (ލިބެން ނުވާ) އެއްޗެހި ދޭން ކިޔައިގަތުން ނަބިއްޔާ ނަހީ ކުރެއްވިއެވެ.","البخاري 844 واللفظ له، ومسلم 593\n<br><br>\nالبخاري 6615\n<br><br>\nالبخاري 6473 واللفظ له، ومسلم 593\n<br><br>\nالبخاري 7292 واللفظ له، ومسلم 593"],["#136","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الذكر عقب الصلاة","ނަމާދަށް ފަހު ޒިކުރު ކިޔުމުގެ ބާބު","عَنْ سُمَيٍّ مَوْلَى أَبِي بَكْرِ بْنِ عَبْدِ الرَّحْمَنِ بْنِ الْحَارِثِ بْنِ هِشَامٍ عَنْ أَبِي صَالِحٍ السَّمَّانِ عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ -: «أَنَّ فُقَرَاءَ الْمُسْلِمِينَ أَتَوْا رَسُولَ اللَّهِ ﷺ فَقَالُوا: يَا رَسُولَ اللَّهِ، قَدْ ذَهَبَ أَهْلُ الدُّثُورِ بِالدَّرَجَاتِ الْعُلَى وَالنَّعِيمِ الْمُقِيمِ. قَالَ: وَمَا ذَاكَ؟ قَالُوا: يُصَلُّونَ كَمَا نُصَلِّي، وَيَصُومُونَ كَمَا نَصُومُ، وَيَتَصَدَّقُونَ وَلا نَتَصَدَّقُ. وَيُعْتِقُونَ وَلا نُعْتِقُ. فَقَالَ رَسُولُ اللَّهِ ﷺ: أَفَلا أُعَلِّمُكُمْ شَيْئًا تُدْرِكُونَ بِهِ مَنْ سَبَقَكُمْ، وَتَسْبِقُونَ مَنْ بَعْدَكُمْ. وَلا يَكُونُ أَحَدٌ أَفْضَلَ مِنْكُمْ، إلاَّ مَنْ صَنَعَ مِثْلَ مَا صَنَعْتُمْ؟ قَالُوا: بَلَى، يَا رَسُولَ اللَّهِ. قَالَ: تُسَبِّحُونَ وَتُكَبِّرُونَ وَتَحْمَدُونَ دُبُرَ كُلِّ صَلاةٍ: ثَلاثاً وَثَلاثِينَ مَرَّةً. قَالَ أَبُو صَالِحٍ: فَرَجَعَ فُقَرَاءُ الْمُهَاجِرِينَ، فَقَالُوا: سَمِعَ إخْوَانُنَا أَهْلُ الأَمْوَالِ بِمَا فَعَلْنَا، فَفَعَلُوا مِثْلَهُ. فَقَالَ رَسُولُ اللَّهِ ﷺ: ذَلِكَ فَضْلُ اللَّهِ يُؤْتِيهِ مَنْ يَشَاءُ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: މުސްލިމުންގެ ތެރެއިންވާ ފަޤީރުން ރަސޫލު ﷲ ﷺ ގެ އަރިހަށް އައިސް ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން މުދަލުގެ އަހުލުވެރީން މަތީ ދަރަޖަތަކާއި ދާއިމީ ނިޢުމަތްތައް ހޯދައިގެން ހިނގައްޖެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެއީ ކިހިނަކުންތޯއެވެ؟“ އެބައިމީހުން ދެންނެވިއެވެ: ތިމަންމެން ނަމާދު ކުރާ ފަދައިން އެބައިމީހުން ވެސް ނަމާދު ކުރެއެވެ. އަދި ތިމަންމެން ރޯދަ ހިފާ ފަދައިން އެބައިމީހުން ވެސް ރޯދަ ހިފައެވެ. އެހެނަސް އެބައިމިހުން ޞަދަޤާތް ކުރެއެވެ. އަދި ތިމަންމެންނަކަށް ޞަދަޤާތެއް ނުދެވެއެވެ. އެބައިމީހުން އަޅުން މިނިވަން ކުރެއެވެ. އަދި ތިމަންމެންނަކަށް އަޅުންނެއް މިނިވަނެއް ނުކުރެވެއެވެ. ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންނަށް ވުރެ ކުރިއަރާފައި ތިބި ބަޔަކާ ތިޔަބައިމީހުން ހަމަ ކުރެވޭނެ، އަދި ތިޔަބައިމީހުންގެ ފަހުން އަންނާނެ ބަޔަކަށް ވުރެ ތިޔަބައިމީހުން ކުރިއަރރުވައިދޭނެ ކަމެއް، ތިޔަބައިމީހުންނަށް އުނގަންނަވައި ދެއްވަންތޯއެވެ؟“ އެބައިމިހުން ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. އާދޭހެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ކޮންމެ ނަމާދެއްގެ ފަހުން ތިރީސް ތިން ފަހަރު ތިޔަބައިމީހުން ތަޞްބީޙައާއި ތަކްބީރާއި ތަޙްމީދު ކިޔާށެވެ.“ (ރާވީއެއް ކަމަށްވާ) އަބޫ ޞާލިޙު ވިދާޅުވިއެވެ: ދެން އަނެއްކާވެސް މުހާޖިރުންގެ ތެރެއިންވާ ފަޤީރުން ނަބިއްޔާގެ އަރިހަށް އެނބުރި ގޮސް ދެންނެވިއެވެ: ތިމަންމެންގެ އަޚުން ކަމުގައިވާ މުދަލުގެ އަހުލުވެރިންނަށް، ތިމަންމެން ކުރި ކަންތައްތަކުގެ އަޑު އިވިގެން އެބައިމީހުންވެސް އެފަދައިން ކަންކުރެއެވެ. ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އެއީ ﷲ ގެ ފަޟްލުވަންތަކަމެވެ. އެ އިލާހު އިރާދަ ކުރައްވާ ބަޔަކަށް އެކަން ދެއްވައެވެ.“","البخاري 843، ومسلم 595 واللفظ له"],["#137","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الذكر عقب الصلاة","ނަމާދަށް ފަހު ޒިކުރު ކިޔުމުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا: «أَنَّ النَّبِيَّ ﷺ صَلَّى فِي خَمِيصَةٍ لَهَا أَعْلامٌ. فَنَظَرَ إلَى أَعْلامِهَا نَظْرَةً. فَلَمَّا انْصَرَفَ قَالَ: اذْهَبُوا بِخَمِيصَتِي هَذِهِ إلَى أَبِي جَهْمٍ، وَأْتُونِي بِأَنْبِجَانِيَّةِ أَبِي جَهْمٍ. فَإِنَّهَا أَلْهَتْنِي آنِفًا عَنْ صَلاتِي»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: (ކުލަކުލައިގެ) ގޮޅިޖެހި ޚަމީޞާ (އޭ ކިޔުނު ހެދުންކޮޅެއް) ތުރުކުރައްވައިގެން ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވަނިކޮށް، އޭގައި ހުރި ނިޝާންތަކަށް ބަލާލެއްވިއެވެ. ދެން ނަމާދުން އަވަދިވެވަޑައިގަތުމަށް ފަހު ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާގެ މި ޚަމީޞާ ހިފައިގެން އަބޫ ޖަހްމުގެ ގާތަށް ދާށެވެ. އަދި އަބޫ ޖަހްމުގެ އަންބިޖާނިއްޔާ (އޭ ކިޔުނު ބޯ، އެއްޗެއް ޖަހާފައި ނެތް ހެދުންކޮޅު) ތިމަން ނަބިއްޔާއަށް ގެންނާށެވެ. އެހެނީ ދެންމެ ކުރެއްވި ނަމާދުން މި ހެދުން ތިމަން ނަބިއްޔާގެ ސަމާލުކަން ގެއްލުވާލައިފިކަން ކަށަވަރެވެ.“","البخاري 373، ومسلم 556"],["#138","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الجمع بين الصلاتين في السفر","ދަތުރުމަތީގައި ދެ ނަމާދު އެއްކޮށް ޖަމްޢު ކުރުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: «كَانَ رَسُولُ اللَّهِ ﷺ يَجْمَعُ فِي السَّفَرِ بَيْنَ صَلاةِ الظُّهْرِ وَالْعَصْرِ، إذَا كَانَ عَلَى ظَهْرِ سَيْرٍ، وَيَجْمَعُ بَيْنَ الْمَغْرِبِ وَالْعِشَاءِ»","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ދަތުރުފުޅު މަތީގައި ހުންނަވާ އިރު، މެނދުރާއި ޢަޞުރު ނާމާދު އެއްކޮށް ޖަމްޢު ކުރައްވައެވެ. އަދި މަޣްރިބާއި ޢިޝާ ނަމާދު ވެސް އެއްކޮށް ޖަމްޢު ކުރައްވައެވެ.","البخاري 1107"],["#139","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب قصر الصلاة في السفر","ދަތުރު މަތީގައި ނަމާދު ކުރު ކުރުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما قَالَ: «صَحِبْتُ رَسُولَ اللَّهِ ﷺ فَكَانَ لا يَزِيدُ فِي السَّفَرِ عَلَى رَكْعَتَيْنِ، وَأَبَا بَكْرٍ وَعُمَرَ وَعُثْمَانَ كَذَلِكَ»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ އަރިހުގައި ތިމަން އުޅުނީމެވެ. އަދި ނަބިއްޔާ ދަތުރުފުޅުމަތީގައި (މެންދުރާއި ޢަޞުރާއި ޢިޝާ ނަމާދު ކުރައްވާ ހިނދު) ދެ ރަކުޢަތަށް ވުރެ ގިނައިން ނުކުރައްވައެވެ. އަދި އަބޫ ބަކްރާއި ޢުމަރާއި ޢުޘްމާނު ވެސް ކަންކުރެއްވީ ހަމަ އެފަދައިންނެވެ.","البخاري 1102 واللفظ له، ومسلم 689"],["#140","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الجمعة","ހުކުރު ކުރުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «مَنْ جَاءَ مِنْكُمْ الْجُمُعَةَ فَلْيَغْتَسِلْ»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ތިޔަބައިމީހުން ކުރެ މީހަކު ހުކުރު ދުވަހާ ބައްދަލުވެއްޖެ ނަމަ، ފަހެ އެ މީހާ ފެންވަރައި ހިނައިގަންނާށެވެ.","البخاري 894، ومسلم 844"],["#141","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الجمعة","ހުކުރު ކުރުމުގެ ބާބު","عَنْ جَابِرٍ- رَضِيَ اللَّهُ عَنْهُ - قَالَ: «كَانَ رَسُولُ اللَّهِ ﷺ يَخْطُبُ خُطْبَتَيْنِ وَهُوَ قَائِمٌ، يَفْصِلُ بَيْنَهُمَا بِجُلُوسٍ»","ޖާބިރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ކޮއްޅަށް ހުރެ ވަޑައިގެން ދެ ޚުޠުބާ ދެއްވައެވެ. އެ ދެ ޚުޠުބާ ދެމެދު ވަކި ކުރައްވަނީ އިށީނދެ ވަޑައިގަތުމަކުންނެވެ.","البخاري 920، 928، ومسلم 861، ولكن ليسا بهذا اللفظ، والأقرب لفظ النسائي 1416"],["#142","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الجمعة","ހުކުރު ކުރުމުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رَضِيَ اللَّهُ عَنْهُما قَالَ: «جَاءَ رَجُلٌ وَالنَّبِيُّ ﷺ يَخْطُبُ النَّاسَ يَوْمَ الْجُمُعَةِ. فَقَالَ: صَلَّيْتَ يَا فُلانُ؟ قَالَ: لا. قَالَ: قُمْ فَارْكَعْ رَكْعَتَيْنِ.»\n<br><br>\nوَفِي رِوَايَةٍ: فَصَلِّ رَكْعَتَيْنِ.","ޖާބިރު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ހުކުރު ދުވަހު ނަބިއްޔާ ﷺ މީސްތަކުންނަށް ޚުޠުބާ ދެއްވަނިކޮށް މީހަކު (މިސްކިތަށް) އަތުވިއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ މިވެނި މީހާއެވެ. ތިބާ ނަމާދުކޮށްފިންތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ: ނޫނެކެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތެދުވެ ދެ ރަކުޢަތް ކުރާށެވެ.“","البخاري 930 واللفظ له، ومسلم 875\n<br><br>\nالبخاري 931 واللفظ له، ومسلم 875"],["#143","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الجمعة","ހުކުރު ކުރުމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «إذَا قُلْتَ لِصَاحِبِكَ: أَنْصِتْ يَوْمَ الْجُمُعَةِ وَالإِمَامُ يَخْطُبُ - فَقَدْ لَغَوْتَ» ","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހުކުރު ދުވަހު އިމާމު ޚުތުބާ ދެއްވަނިކޮށް، ތިބާއާ ޖެހިގެން ހުރި މީހާ ގާތުގައި ހަނުހުންނަށް ބުނިކަމުގައި ވިޔަސް، ފަހެ ތިބާ ބޭކާރު ބާޠިލު ވާހަކަ ދެއްކި ކަމުގައި ވާނެއެވެ.“","البخاري 934، ومسلم 851 واللفظ له"],["#144","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الجمعة","ހުކުރު ކުރުމުގެ ބާބު","عَنْ سَهْلِ بْنِ سَعْدٍ السَّاعِدِيِّ قَالَ: «رَأَيْتُ رَسُولَ اللَّهِ قَامَ ﷺ فَكَبَّرَ وَكَبَّرَ النَّاسُ وَرَاءَهُ، وَهُوَ عَلَى الْمِنْبَرِ. ثُمَّ رَفَعَ فَنَزَلَ الْقَهْقَرَى، حَتَّى سَجَدَ فِي أَصْلِ الْمِنْبَرِ، ثُمَّ عَادَ حَتَّى فَرَغَ مِنْ آخِرِ صَلاتِهِ. ثُمَّ أَقْبَلَ عَلَى النَّاسِ، فَقَالَ: أَيُّهَا النَّاسُ، إنَّمَا صَنَعْتُ هَذَا لِتَأْتَمُّوا بِي، وَلِتَعْلَمُوا صَلاتِي»\n<br><br>\nوَفِي لَفْظٍ - صَلَّى عَلَيْهَا. ثُمَّ كَبَّرَ عَلَيْهَا. ثُمَّ رَكَعَ وَهُوَ عَلَيْهَا، فَنَزَلَ الْقَهْقَرَى.","ސަހްލު ބިން ސަޢުދު އައްސާޢިދީ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ތެދުވެވަޑައިގެން ތަކްބީރު ދެއްވާ ތަން ތިމަން ދުށީމެވެ. އަދި ނަބިއްޔާގެ ފަހަތްޕުޅުގައިވާ މީސްތަކުންވެސް ތަކްބީރު ދިނެވެ. އެ ވަގުތު ނަބިއްޔާ ހުންނެވީ މިންބަރު މަތީގައެވެ. ދެން ނަބިއްޔާ (ރުކޫޢިން) އިސްތަށިފުޅު އުފުއްލެވުމަށް ފަހު (ހުންނެވި ގޮތަށް ހުންނަވައިގެން) ފަހަތަށް ޖެހިވަޑައިގަތެވެ. އެއަށްފަހު މިންބަރުގެ ތިރީގައި (ބިންމަތީ) ސަޖިދަ ކުރެއްވިއެވެ. ދެން އަނެއްކާ ތެދުވެވަޑައިގެން ނަމާދު ނިމެންދެން ކަންތައް ކުރެއްވީ އެފަދައިންނެވެ. އެއަށްފަހު މީސްތަކުންނާ ކުރިމަތިލައްވައިގެން ހުންނަވައި ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ މީސްތަކުންނޭވެ. މިފަދައިން ތިމަން ނަބިއްޔާ ކަންތައް ކުރެއްވީ، ތިޔަބައިމީހުން ތިމަން ނަބިއްޔާއާ ތަބާވެ، ތިމަން ނަބިއްޔާ ނަމާދު ކުރައްވާ ގޮތް ތިޔަބައިމީހުން އުނގެނުމަށް ފިޔަވައި އެހެން ސަބަބަކަށް ނޫނެވެ.“\n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ނަބިއްޔާ ނަމާދު ކުރެއްވީ މިންބަރު މަތީގައެވެ. އަދި ތަކްބީރު ދެއްވީ އޭގެ މަތީގައެވެ. އަދި ރުކޫޢު ކުރެއްވީ އޭގެ މަތީގައެވެ. ދެން (ހުންނެވި ގޮތަށް ހުންނަވައިގެން) ފަހަތަށް ޖެހިވަޑައިގަތެވެ. ","البخاري 917، ومسلم 544 واللفظ له\n<br><br>\nالبخاري 917"],["#145","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الجمعة","ހުކުރު ކުރުމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «مَنْ اغْتَسَلَ يَوْمَ الْجُمُعَةِ، ثُمَّ رَاحَ في الساعَةِ الأُولى فَكَأَنَّمَا قَرَّبَ بَدَنَةً. وَمَنْ رَاحَ فِي السَّاعَةِ الثَّانِيَةِ فَكَأَنَّمَا قَرَّبَ بَقَرَةً. وَمَنْ رَاحَ فِي السَّاعَةِ الثَّالِثَةِ فَكَأَنَّمَا قَرَّبَ كَبْشًا أَقْرَنَ. وَمَنْ رَاحَ فِي السَّاعَةِ الرَّابِعَةِ فَكَأَنَّمَا قَرَّبَ دَجَاجَةً. وَمَنْ رَاحَ فِي السَّاعَةِ الْخَامِسَةِ فَكَأَنَّمَا قَرَّبَ بَيْضَةً. فَإِذَا خَرَجَ الإِمَامُ حَضَرَتِ الْمَلائِكَةُ يَسْمَعُونَ الذِّكْرَ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހުކުރު ދުވަހު ފެންވަރައި ހިނައިގަނެގެން ފުރަތަމަ ގަޑީގައި (މިސްކިތަށް) ގޮސްފި މީހަކު ޖަމަލެއް ޤުރުބާންކޮށްފި ފަދައެވެ. އަދި ދެވަނަ ގަޑީގައި ގޮސްފި މީހަކު ގެރިއެއް ޤުރުބާންކޮށްފި ފަދައެވެ. އަދި ތިންވަނަ ގަޑީގައި ގޮސްފި މީހަކު ދަޅުފެޅި ކަނބަޅިއެއް ޤުރުބާންކޮށްފި ފަދައެވެ. އަދި ހަތަރުވަނަ ގަޑީގައި ގޮސްފި މީހަކު ކުކުޅެއް ޤުރުބާންކޮށްފި ފަދައެވެ. އަދި ފަސްވަނަ ގަޑީގައި ގޮސްފި މީހަކު ކުކުޅު ބިހެއް ޤުރުބާންކޮށްފި ފަދައެވެ. ދެން އިމާމު މީހާ ނުކުންނަ ހިނދު، ޒިކުރު އަޑުއެހުމަށް މަލާއިކަތުން ހާޒިރުވެއެވެ.“","البخاري 881، ومسلم 850"],["#146","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الجمعة","ހުކުރު ކުރުމުގެ ބާބު","عَنْ سَلَمَةَ بْنِ الأَكْوَعِ - وَكَانَ مِنْ أَصْحَابِ الشَّجَرَةِ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «كُنَّا نُصَلِّي مَعَ رَسُولِ اللَّهِ ﷺ الْجُمُعَةَ، ثُمَّ نَنْصَرِفُ. وَلَيْسَ لِلْحِيطَانِ ظِلٌّ نَسْتَظِلُّ بِهِ» \n<br><br>\nوَفِي لَفْظٍ: «كُنَّا نُجَمِّعُ مَعَ رَسُولِ اللَّهِ ﷺ إذَا زَالَتْ الشَّمْسُ، ثُمَّ نَرْجِعُ فَنَتَتَبَّعُ الْفَيْءَ»","ސަލަމާ ބިން އަލްއަކްވަޢު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ – އަދި އެކަލޭގެފާނީ ގަސްދޮށުގައި ބައިޢަތުލް ރިޟްވާންގައި ބައިވެރިވި ޞަޙާބީއެކެވެ - : ރަސޫލު ﷲ ﷺ ގެ އަރިހުގައި ތިމަންމެން ހުކުރު ނަމާދު ކުރުމަށް ފަހު އެނބުރި (ގެއަށް) ދަމެވެ. އަދި އޭރު ތިމަންމެން ހިޔާވާނެ ހިޔަލެއްވެސް ފާރުތަކުން ނުލިބެއެވެ.\n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: އިރު މެދުޖެހުމަށް ފަހު، ރަސޫލު ﷲ ﷺ ގެ އަރިހުގައި ތިމަންމެން ހުކުރު ނަމާދު ކުރަމެވެ. އެއަށްފަހު ތިމަންމެން އެނބުރި (ގެއަށްދާ މަގުމަތީގައި) ހިޔާވެހިކަމުގެ (ތެރޭގައި ހިނގޭތޯ) މަސައްކަތް ކުރަމެވެ.","البخاري 4168 واللفظ له، ومسلم 860\n<br><br>\nمسلم 860"],["#147","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الجمعة","ހުކުރު ކުރުމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «كَانَ النَّبِيُّ ﷺ يَقْرَأُ فِي صَلاةِ الْفَجْرِ يَوْمَ الْجُمُعَةِ: الم تَنْزِيلُ السَّجْدَةَ وَ: هَلْ أَتَى عَلَى الإِنْسَانِ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ހުކުރު ދުވަހު، ފަތިސް ނަމާދުގެ (ދެ ރަކުޢަތުގައި) ނަބިއްޔާ ﷺ ކިޔަވައި ވިދާޅުވަނީ ސަޖިދަ ސޫރަތުގެ الم تَنْزِيلُ އާއި، (ޢާޝިޔާ ސޫރަތުގެ) هَلْ أَتَى عَلَى الإِنْسَانِ (މި ދެ ސޫރަތެވެ.)","البخاري 891، ومسلم 879 واللفظ له"],["#148","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صلاة العيدين","ދެ ޢީދު ދެ ނަމާދުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما قَالَ: «كَانَ النَّبِيُّ ﷺ وَأَبُو بَكْرٍ وَعُمَرُ يُصَلُّونَ الْعِيدَيْنِ قَبْلَ الْخُطْبَةِ»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ އާއި، އަބޫ ބަކްރާއި ޢުމަރު، ދެ ޢީދު ނަމާދު ކުރައްވަނީ ޚުތުބާ ދިނުމުގެ ކުރިންނެވެ.","البخاري 963، ومسلم 888"],["#149","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صلاة العيدين","ދެ ޢީދު ދެ ނަމާދުގެ ބާބު","عَنْ الْبَرَاءِ بْنِ عَازِبٍ رَضِيَ اللَّهُ عَنْهُ قَالَ: «خَطَبَنَا النَّبِيُّ ﷺ يَوْمَ الأَضْحَى بَعْدَ الصَّلاةِ، فَقَالَ: مَنْ صَلَّى صَلاتَنَا وَنَسَكَ نُسُكَنَا فَقَدْ أَصَابَ النُّسُكَ، وَمَنْ نَسَكَ قَبْلَ الصَّلاةِ فَلا نُسُكَ لَهُ. فَقَالَ أَبُو بُرْدَةَ بْنُ نِيَارٍ - خَالُ الْبَرَاءِ بْنِ عَازِبٍ - يَا رَسُولَ اللَّهِ، إنِّي نَسَكْتُ شَاتِي قَبْلَ الصَّلاةِ. وَعَرَفْتُ أَنَّ الْيَوْمَ يَوْمُ أَكْلٍ وَشُرْبٍ. وَأَحْبَبْتُ أَنْ تَكُونَ شَاتِي أَوَّلَ مَا يُذْبَحُ فِي بَيْتِي. فَذَبَحْتُ شَاتِي، وَتَغَذَّيْتُ قَبْلَ أَنْ آتِيَ الصَّلاةَ. فَقَالَ: شَاتُكَ شَاةُ لَحْمٍ. قَالَ: يَا رَسُولَ اللَّهِ، فَإِنَّ عِنْدَنَا عِنَاقاً هِيَ أَحَبُّ إلَيَّ مِنْ شَاتَيْنِ أَفَتُجْزِي عَنِّي؟ قَالَ: نَعَمْ، وَلَنْ تَجْزِيَ عَنْ أَحَدٍ بَعْدَكَ» ","އަލްބަރާއު ބިން ޢާޒިބު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ޢަޟްޙާ ޢީދު ދުވަހު، ނަމާދަށް ފަހު ނަބިއްޔާ ﷺ ތިމަންމެންނަށް ޚުޠުބާ ދެއްވަމުން ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަންމެންގެ ނަމާދާ އެއްގޮތަށް ނަމާދުކޮށް އަދި ތިމަންމެންގެ ޤުރުބާން ކުރެއްވުމާ އެއްގޮތަށް ޤުރުބާންކޮށްފި މީހާ، ފަހެ އެ މީހެއްގެ ޤުރުބާނީ އަދާކޮށްފިކަން ކަށަވަރެވެ. އަދި ނަމާދުގެ ކުރިން ޤުރުބާންކޮށްފި މީހަކަށް ޤުރުބާނީއެއް ނުވެއެވެ.“ ދެން އަލްބަރާއު ބިން ޢާޒިބުގެ ބޮޑުބޭބޭފުޅު، އަބޫ ބުރްދާ ބިން ނިޔާރު ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން ތިމަންނަގެ ކަންބަޅި ޤުރުބާން ކުރެވުނީ ނަމާދުގެ ކުރިންނެވެ. އަދި މި ދުވަހަކީ ކެއުމާއި ބުއިމުގެ ދުވަހެއްކަން ތިމަން ދެނެހުރީމެވެ. ފަހެ ތިމަންނަގެ ގޭގައި އެންމެ ފުރަތަމަ ކަތިލެވެނީ ތިމަންނަގެ ކަންބަޅިކަމުގައިވުމަށް ތިމަން ލޯބި ކުރީމެވެ. އަދި ތިމަންނަގެ ކަންބަޅި ކަތިލައި، ނަމާދަށް އައުމުގެ ކުރިން ތިމަންނަ ކެއީމެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާގެ ކަންބަޅިއަކީ މަސް ކެއުމަށްޓަކައި ކަތިލެވުނު ކަންބަޅިއެކެވެ.“ (އެބަހީ: ޤުރުބާނީއެއް ނޫނެކެވެ.) އަބޫ ބުރްދާ ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން ތިމަންމެންގެ އަތުގައި ޅަ އަންހެން ބަކަރިއެއް ވެއެވެ. ދެ ކަންބަޅިއަށް ވުރެ އޭތި ތިމަންނައަށް މާ ލޮބުވެތިކަން ބޮޑެވެ. ފަހެ ތިމަންނަގެ (ޤުރުބާނީ އަދާ ކުރުމުގެ ގޮތުގައި) އޭތި ފުދޭނެތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އާދޭހޭވެ. އަދި ތިބާގެ ފަހުން އަންނަ އެއްވެސް މީހަކަށް ނުފުދޭނެއެވެ.“ (އެބަހީ: ނަމާދުގެ ކުރިން ކަތިލައިގެން ބަދަލު ދިނުމަކީ އަބޫ ބުރްދާއަށް އެކަނި ދެއްވި ލުޔެކެވެ.)","البخاري 955 واللفظ له، ومسلم 1961"],["#150","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صلاة العيدين","ދެ ޢީދު ދެ ނަމާދުގެ ބާބު","عَنْ جُنْدُبِ بْنِ عَبْدِ اللَّهِ الْبَجَلِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «صَلَّى النَّبِيُّ ﷺ يَوْمَ النَّحْرِ. ثُمَّ خَطَبَ. ثُمَّ ذَبَحَ وَقَالَ: مَنْ ذَبَحَ قَبْلَ أَنْ يُصَلِّيَ فَلْيَذْبَحْ أُخْرَى مَكَانَهَا، وَمَنْ لَمْ يَذْبَحْ فَلْيَذْبَحْ بِاسْمِ اللَّهِ»","ޖުންދުބް ބިން ޢަބްދު ﷲ އަލްބަޖައްލީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ޢަޟްޙާ ޢީދު ދުވަހު ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވުމަށް ފަހު ޚުޠުބާ ދެއްވިއެވެ. ދެން ޢުޟްޙިޔާ ކަތިލައްވާފައި ޙަދީޘް ކުރެއްވިއެވެ: ”ނަމާދު ކުރުމުގެ ކުރިން (ޢުޟްޙިޔާ) ކަތިލައިފި މީހަކު، ފަހެ އޭގެ ބަދަލުގައި އެހެން ޤުރުބާނީއެއް އޭނާ ކަތިލާށެވެ. އަދި މިހާތަނަށް (ޢުޟްޙިޔާ) ކަތިނުލާ ހުރި މީހަކު ފަހެ ﷲގެ އިސްމުފުޅުން (މިހާރު) ކަތިލާށެވެ.“","البخاري 985 واللفظ له، ومسلم 1961"],["#151","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صلاة العيدين","ދެ ޢީދު ދެ ނަމާދުގެ ބާބު","عَنْ جَابِرٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «شَهِدْتُ مَعَ النَّبِيِّ ﷺ يَوْمَ الْعِيدِ. فَبَدَأَ بِالصَّلاةِ قَبْلَ الْخُطْبَةِ، بِلا أَذَانٍ وَلا إقَامَةٍ. ثُمَّ قَامَ مُتَوَكِّئًا عَلَى بِلالٍ، فَأَمَرَ بِتَقْوَى اللَّهِ تَعَالَى، وَحَثَّ عَلَى طَاعَتِهِ، وَوَعَظَ النَّاسَ وَذَكَّرَهُمْ، ثُمَّ مَضَى حَتَّى أَتَى النِّسَاءَ فَوَعَظَهُنَّ وَذَكَّرَهُنَّ، وَقَالَ: يَا مَعْشَرَ النِّسَاءِ، تَصَدَّقْنَ. فَإِنَّكُنَّ أَكْثَرُ حَطَبِ جَهَنَّمَ، فَقَامَتْ امْرَأَةٌ مِنْ سِطَةِ النِّسَاءِ، سَفْعَاءُ الْخَدَّيْنِ فَقَالَتْ: لِمَ يَا رَسُولَ اللَّهِ فَقَالَ: لأَنَّكُنَّ تُكْثِرْنَ الشَّكَاةَ، وَتَكْفُرْنَ الْعَشِيرَ. قَالَ: فَجَعَلْنَ يَتَصَدَّقْنَ مِنْ حُلِيِّهِنَّ يُلْقِينَ فِي ثَوْبِ بِلالٍ مِنْ أَقْرَاطِهِنَّ وَخَوَاتِيمِهِنَّ»","ޖާބިރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ގެ އަރިހު ޢީދު ދުވަހުގައި ތިމަން ބައިވެރިވީމެވެ. ޚުޠުބާގެ ކުރިން ނަބިއްޔާ ނަމާދު ފެއްޓެވިއެވެ. އެއީ ބަންގި ގޮވުމެއް އަދި ޤަމަތް ދިނުމަކާ ނުލައެވެ. އެއަށްފަހު ބިލާލު ގާތު ލެނގިލައްވައިގެން ހުންނަވައި، ﷲ ތަޢާލާއަށް ތަޤުވާވެރިވުމަށް އަމުރު ކުރައްވައި، އެ އިލާހަށް ކިޔަމަންތެރިވުމަށް ބާރު އެޅުއްވިއެވެ. އަދި މީސްތަކުންނަށް ވަޢުޡު ދެއްވައި ހަނދުމަކޮށް ދެއްވިއެވެ. އެއަށްފަހު އަންހެންވެރިންގެ ގާތަށް ވަޑައިގަންނަވައި، އެ ކަނބަލުންނަށް ވަޢުޡު ދެއްވައި ހަނދުމަކޮށް ދެއްވިއެވެ. އަދި ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ އަންހެން ކަނބަލުންނޭވެ. ތިޔަ ކަނބަލުން ޞަދަޤާތް ދޭށެވެ. އެހެނީ ހަމަކަށާވަރުން ތިޔަ ކަނބަލުންނީ ނަރަކައިގެ ދަރުގެ ބޮޑުބައެވެ.“ ދެން އަންހެނުންގެ ތެރޭގައި އިން، ދެ ކޮލުގައި ކަޅު ތަންކޮޅެއް ހުރި އަންހެނަކު ތެދުވެ ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. އެއީ ކީއްވެގެންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެއީ ތޔަ ކަނބަލުން ޝަކުވާ ގިނަ ކުރާތީއެވެ. އަދި ފިރިންނަށް ކާފަރުވާތީއެވެ.“ (އެބަހީ: ޝުކުރުވެރި ނުވާތީއެވެ.) ދެން އެ ކަނބަލުންގެ ހެދުންތަކު ތެރެއިން ކަންފަތު މުދިތަކާއި އަނގޮށްޓިތައް ނަގާފައި ޞަދަޤާތުގެ ގޮތުގައި ދިނެވެ.","البخاري 958، ومسلم 885 واللفظ له"],["#152","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صلاة العيدين","ދެ ޢީދު ދެ ނަމާދުގެ ބާބު","عَنْ أُمِّ عَطِيَّةَ - نُسَيْبَةَ الأَنْصَارِيَّةِ - قَالَتْ: «أَمَرَنَا رَسُولُ اللَّهِ ﷺ أَنْ نُخْرِجَ فِي الْعِيدَيْنِ الْعَوَاتِقَ وَذَوَاتِ الْخُدُورِ، وَأَمَرَ الْحُيَّضَ أَنْ يَعْتَزِلْنَ مُصَلَّى الْمُسْلِمِينَ» \n<br><br>\nوَفِي لَفْظٍ: «كُنَّا نُؤْمَرُ أَنْ نَخْرُجَ يَوْمَ الْعِيدِ، حَتَّى نُخْرِجَ الْبِكْرَ مِنْ خِدْرِهَا، حَتَّى تَخْرُجَ الْحُيَّضُ، فَيُكَبِّرْنَ بِتَكْبِيرِهِمْ وَيَدْعُونَ بِدُعَائِهِمْ، يَرْجُونَ بَرَكَةَ ذَلِكَ الْيَوْمِ وَطُهْرَتَهُ»","އުންމު ޢަޠިއްޔާ، ނުސައިބާ އަލްއަންޞާރިއްޔާ ރިވާ ކުރެއްވިއެވެ: (ބާލިޢުވެފައި ތިބި) ބޮޑެތި އަންހެނުންނާއި، (ޅަ) ބިކުރުވެރި އަންހެން ކުދިން ދެ ޢީދު (ދެ ނަމާދަށް) ނެރެން، ރަސޫލު ﷲ ﷺ ތިމަންމެންނަށް އަމުރު ކުރެއްވިއެވެ. އަދި މުސްލިމުން ނަމާދު ކުރާ ތަނުން ޙައިޟުވެރިން ދުރުވާން ނަބިއްޔާ އެންގެވިއެވެ. \n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ޢީދު ދުވަހު (ޢީދު ނަމާދަށް) ނުކުތުމަށް ތިމަން ކަނބަލުންނަށް އަމުރުވެވުނެވެ. ގޭގައި ފޮރުވިގެން އުޅޭ (ޅަ) ބިކުރުވެރިން އަދި އޭގެ އިތުރުން ޙައިޟުވެރިން ނުކުތުމަށް ދާންދެންވެސް މެއެވެ. އެއީ މީސްތަކުންގެ ތަކްބީރާއެކު އެ ކަނބަލުން ތަކްބީރު ކިޔައި، މީސްތަކުންގެ ދުޢާއާއެކު އެ ކަނބަލުން ދުޢާ ކުރުމަށެވެ. އަދި އެ ދުވަހުގެ ބަރަކާތާއި ފާފަ ފުއްސެވުން ހޯދައިގަތުމަށެވެ.","البخاري 973، ومسلم 890 واللفظ له\n<br><br>\nالبخاري 971 واللفظ له، ومسلم 890"],["#153","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب صلاة الكسوف","އިރުކޭތަ ނަމާދުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا: «أَنَّ الشَّمْسَ خَسَفَتْ عَلَى عَهْدِ رَسُولِ اللَّهِ ﷺ فَبَعَثَ مُنَادِياً يُنَادِي: الصَّلاةُ جَامِعَةٌ. فَاجْتَمَعُوا. وَتَقَدَّمَ، فَكَبَّرَ وَصَلَّى أَرْبَعَ رَكَعَاتٍ فِي رَكْعَتَيْنِ، وَأَرْبَعَ سَجَدَاتٍ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ ޒަމާނުގައި އިރުކޭތަ ހިފިއެވެ. ފަހެ މިފަދއިން ގޮވާލާ މީހަކު ނަބިއްޔާ ފޮނުއްވިއެވެ: ޖަމާޢަތުގައި ނަމާދު (ކުރެވެން އެބައުޅެއެވެ.) ދެން މީސްތަކުން ޖަމާޢަތުގައި އެއްވިއެވެ. ނަބިއްޔާ ކުރިއަށް ޖެހިވަޑައިގެން ތަކްބީރު ދެއްވިއެވެ. އަދި (އެ ނަމާދުގެ) ދެ ރަކުޢަތުގައި ހަތަރު ފަހަރު ރުކޫޢުކޮށް ހަތަރު ފަހަރު ސަޖިދަ ކުރެއްވިއެވެ.","البخاري 1066، ومسلم 901 واللفظ له"],["#154","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب صلاة الكسوف","އިރުކޭތަ ނަމާދުގެ ބާބު","عَنْ أَبِي مَسْعُودٍ - عُقْبَةَ بْنِ عَمْرٍو الأَنْصَارِيِّ الْبَدْرِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «إنَّ الشَّمْسَ وَالْقَمَرَ آيَتَانِ مِنْ آيَاتِ اللَّهِ، يُخَوِّفُ اللَّهُ بِهِمَا عِبَادَهُ، وَإِنَّهُمَا لا يَنْخَسِفَانِ لِمَوْتِ أَحَدٍ مِنْ النَّاسِ. فَإِذَا رَأَيْتُمْ مِنْهَا شَيْئًا فَصَلُّوا، وَادْعُوا حَتَّى يَنْكَشِفَ مَا بِكُمْ»","އަބޫ މަސްޢޫދު، ޢުޤްބާ ބިން ޢަމްރު އަލްއަންޞާރީ އަލްބަދްރީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން އިރާއި ހަނދަކީ ﷲގެ އާޔަތްތަކު ތެރެއިންވާ ދެ އާޔަތެވެ. ﷲ އެ ދޭތި (ކޭތަ ހިފުން މެދުވެރި ކުރައްވައި) އެ އިލާހުގެ އަޅުތަކުން ބިރުގަންނަވައެވެ. އަދި ހަމަކަށަވަރުން މީސްތަކުން ކުރެ އެއްވެސް މީހެއްގެ މަރުވުމާއި ދިރުމަކަށް އެ ދޭތި ކޭތަ ނުހިފައެވެ. ފަހެ ތިޔަބައިމީހުންނަށް އެ (ކޭތަ ހިފާ) ތަން ފެނިއްޖެ ނަމަ ނަމާދު ކުރާށެވެ. އަދި ތިޔަބައިމީހުންނަށް ޖެހިފައިވާ (ކޭތަ) ފިއްލަވަންދެން ﷲ އަށް ދުޢާ ކުރާށެވެ.“","البخاري 1041، ومسلم 911 واللفظ له"],["#155","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب صلاة الكسوف","އިރުކޭތަ ނަމާދުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا أَنَّهَا قَالَتْ: «خَسَفَتِ الشَّمْسُ عَلَى عَهْدِ رَسُولِ اللَّهِ ﷺ فَصَلَّى رَسُولُ اللَّهِ ﷺ بِالنَّاسِ. فَأَطَالَ الْقِيَامَ، ثُمَّ رَكَعَ، فَأَطَالَ الرُّكُوعَ، ثُمَّ قَامَ، فَأَطَالَ الْقِيَامَ - وَهُوَ دُونَ الْقِيَامِ الأَوَّلِ - ثُمَّ رَكَعَ، فَأَطَالَ الرُّكُوعَ - وَهُوَ دُونَ الرُّكُوعِ الأَوَّلِ - ثُمَّ سَجَدَ، فَأَطَالَ السُّجُودَ، ثُمَّ فَعَلَ فِي الرَّكْعَةِ الأُخْرَى مِثْلَ مَا فَعَلَ فِي الرَّكْعَةِ الأُولَى، ثُمَّ انْصَرَفَ، وَقَدْ تَجَلَّتْ الشَّمْسُ، فَخَطَبَ النَّاسَ، فَحَمِدَ اللَّهَ وَأَثْنَى عَلَيْهِ، ثُمَّ قَالَ: إنَّ الشَّمْسَ وَالْقَمَرَ آيَتَانِ مِنْ آيَاتِ اللَّهِ، لا يَخْسِفَانِ لِمَوْتِ أَحَدٍ وَلا لِحَيَاتِهِ، فَإِذَا رَأَيْتُمْ ذَلِكَ فَادْعُوا اللَّهَ وَكَبِّرُوا، وَصَلُّوا وَتَصَدَّقُوا، ثُمَّ قَالَ: يَا أُمَّةَ مُحَمَّدٍ، وَاَللَّهِ مَا مِنْ أَحَدٍ أَغْيَرُ مِنْ اللَّهِ أَنْ يَزْنِيَ عَبْدُهُ، أَوْ تَزْنِيَ أَمَتُهُ، يَا أُمَّةَ مُحَمَّدٍ، وَاَللَّهِ لَوْ تَعْلَمُونَ مَا أَعْلَمُ لَضَحِكْتُمْ قَلِيلاً وَلَبَكَيْتُمْ كَثِيراً»\n<br><br>\nوَفِي لَفْظٍ: «فَاسْتَكْمَلَ أَرْبَعَ رَكَعَاتٍ وَأَرْبَعَ سَجَدَاتٍ»\n","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ ޒަމާނުގައި އިރުކޭތަ ހިފިއެވެ. ދެން ރަސޫލު ﷲ ﷺ މީސްތަކުންނައިގެން (އިރުކޭތަ) ނަމާދު ކުރެއްވިއެވެ. އެ ގޮތުން ވަރަށް ގިނަ ވަގުތު ވަންދެން ނަބިއްޔާ ތެދުވެވަޑައިގެން ހުންނެވިއެވެ. އެއަށްފަހު ވަރަށް ގިނަ ވަގުތު ވަންދެން ރުކޫޢުގައި ހުންނެވިއެވެ. ދެން (އިޢުތިދާލުގައި) ފުރަތަމަ ފަހަރަށް ވުރެ ކުރުކޮށް ނަމަވެސް ގިނަ ވަގުތު ވަންދެން ތެދުވެވަޑައިގެން ހުންނެވިއެވެ. ދެން ފުރަތަމަ ރުކޫޢަށް ވުރެ ކުރުކޮށް ނަމަވެސް ގިނަ ވަގުތު ވަންދެން ރުކޫޢުގައި ހުންނެވިއެވެ. ދެން ވަރަށް ގިނަ ވަގުތު ވަންދެން (ދެ ފަހަރު) ސަޖިދައިގައި އިންނެވިއެވެ. އެއަށްފަހު ދެވަނަ ރަކުޢަތުގައި، ހަމަ ކުރީ ރަކުޢަތެކޭ އެފަދައިން ކަންކުރެއްވުމަށް ފަހު ނަމާދުން އަވަދިވެވަޑައިގަތެވެ. އޭރު (ކޭތަ ނިމިގެން) އިރުފެންނަށް ހުއްޓެވެ. ދެން ނަބިއްޔާ މީސްތަކުންނާ މުޚާޠަބު ކުރައްވައި، ﷲ އަށް ޙަމްދު ޘަނާ ދެންނެވުމަށް ފަހު ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން އިރާއި ހަނދަކީ ﷲގެ އާޔަތްތަކު ތެރެއިންވާ ދެ އާޔަތެވެ. އެއްވެސް މީހެއްގެ މަރުވުމާއި ދިރުމަކަށް އެ ދޭތި ކޭތަ ނުހިފައެވެ. އަދި ތިޔަބައިމީހުންނަށް އެ (ކޭތަ ހިފާ) ތަން ފެނިއްޖެ ނަމަ ﷲ އަށް ދުޢާކޮށް ތަކްބީރު ކިޔާށެވެ. އަދި ނަމާދުކޮށް ޞަދަޤާތް ދޭށެވެ.“ އެއަށްފަހު ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ މުޙައްމަދުގެފާނުގެ އުންމަތުގެ މީސްތަކުންނޭވެ. ﷲ ގަންދީ ބުނަމެވެ. އެ ފަރާތެއްގެ ފިރިހެން ނުވަތަ އަންހެން އަޅަކު ޒިނޭކޮށްފާނެ ކަމަށް ﷲ އަށްވުރެ ޣިރަތްތެރިވެ ވޮޑިގެންވާ ފަރާތަކު ނުވެއެވެ. އޭ މުޙައްމަދުގެފާނުގެ އުންމަތުގެ މީސްތަކުންނޭވެ. ﷲ ގަންދީ ބުނަމެވެ. ތިމަން ނަބިއްޔާއަށް އެނގިވަޑައިގަންނަވާ ކަންތައް ތިޔަބައިމީހުންނަށް ވެސް އެނގުނު ނަމަ، ތިޔަބައިމީހުން މަދުން ހީހަދައި، ގިނައިން ރޮއިހެދީސްކަން ކަށަވަރެވެ.“\n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ފަހެ (ނަމާދުގެ ދެ ރަކުޢަތުގައި) ހަތަރު ރުކޫޢު ކުރެއްވުމާއި، ހަތަރު ސަޖިދަ ނަބިއްޔާ ފުރިހަމަ ކުރެއްވިއެވެ.","البخاري 1044 واللفظ له، ومسلم 901\n<br><br>\nالبخاري 1046 واللفظ له، ومسلم 901"],["#156","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب صلاة الكسوف","އިރުކޭތަ ނަމާދުގެ ބާބު","عَنْ أَبِي مُوسَى الأَشْعَرِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «خَسَفَتِ الشَّمْسُ عَلَى زَمَانِ رَسُولِ اللَّهِ ﷺ. فَقَامَ فَزِعاً، وَيَخْشَى أَنْ تَكُونَ السَّاعَةُ، حَتَّى أَتَى الْمَسْجِدَ. فَقَامَ، فَصَلَّى بِأَطْوَلِ قِيَامٍ وَسُجُودٍ، مَا رَأَيْتُهُ يَفْعَلُهُ فِي صَلاتِهِ قَطُّ، ثُمَّ قَالَ: إنَّ هَذِهِ الآيَاتِ الَّتِي يُرْسِلُهَا اللَّهُ عَزَّ وَجَلَّ: لا تَكُونُ لِمَوْتِ أَحَدٍ وَلا لِحَيَاتِهِ. وَلَكِنَّ اللَّهَ يُرْسِلُهَا يُخَوِّفُ بِهَا عِبَادَهُ، فَإِذَا رَأَيْتُمْ مِنْهَا شَيْئاً فَافْزَعُوا إلَى ذِكْرِ اللَّهِ وَدُعَائِهِ وَاسْتِغْفَارِهِ»","އަބޫ މޫސާ އަލްއަޝްޢަރީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ ޒަމާނުގައި އިރުކޭތަ ހިފިއެވެ. ޤިޔާމަތްވީ ކަމަށް ނަބިއްޔާ ހީފުޅު ކުރައްވައިގެން ބިރުފުޅުން ތެދުވެވަޑައިގެން މިސްކިތަށް ވަޑައިގެންނެވިއެވެ. އަދި ވަރަށް ގިނަ ވަގުތު ވަންދެން ތެދުވެ ހުންނަވައިގެން އަދި ސަޖިދައަށް ހުންނަވައިގެން ނަމާދު ކުރެއްވިއެވެ. ދުވަހަކުވެސް ނަމާދުގައި ނަބިއްޔާ އެފަދައިން ކަންކުރެއްވި ތަނެއް ތިމަން ނުދެކެމެވެ. އެއަށްފަހު ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ހަމަކަށަވަރުން ﷲ ޢައްޒަ ވަޖައްލަ ފޮނުއްވާ މި (ކޭތަ ހިފުމުގެ) އާޔަތްތަކަކީ އެއްވެސް މީހެއްގެ މަރުވުމާއި ދިރުމަކާ ގުޅިގެންވާ ކަންތައްތަކެއް ނޫނެކެވެ. އެހެނެއްކަމަކު ﷲ އެ (ކޭތަ ހިފުމުގެ) އާޔަތްތައް ފޮނުއްވަނީ އެ އިލާހުގެ އަޅުތަކުން ބިރުގެންނެވުމަށެވެ. ވީމާ ތިޔަބައިމީހުންނަށް އެއިން ކަމެއް ފެނިއްޖެ ނަމަ، ފަހެ ﷲ ގެ ޒިކުރު ކުރުމަށާއި އެ އިލާހަށް ދުޢާ ދަންނަވައި އިސްތިޣްފާރު ކުރުމަށް އަވަސްވެގަންނާށެވެ.“\n","البخاري 1059، ومسلم 912 واللفظ له"],["#157","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب الاستسقاء","އިސްތިސްޤާ ނަމާދުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ زَيْدِ بْنِ عَاصِمٍ الْمَازِنِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «خَرَجَ النَّبِيُّ ﷺ يَسْتَسْقِي، فَتَوَجَّهَ إلَى الْقِبْلَةِ يَدْعُو، وَحَوَّلَ رِدَاءَهُ، ثُمَّ صَلَّى رَكْعَتَيْنِ، جَهَرَ فِيهِمَا بِالْقِرَاءَةِ»\n<br><br>\nوَفِي لَفْظٍ «إلَى الْمُصَلَّى».","ޢަބްދު ﷲ ބިން ޒައިދު ބިން ޢާޞިމް އަލްމާޒިނީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: އިސްތިސްޤާ ނަމާދު ކުރައްވަން ނަބިއްޔާ ﷺ ނުކުމެވަޑައިގަތެވެ. ދެން ޤިބްލައަށް ކުރިމަތިލައްވައިގެން ދުޢާ ކުރެއްވިއެވެ. އަދި ނަބިއްޔާގެ ރިދާކޮޅު އެތެރެފުށް ބޭރަށް ޖެއްސެވިއެވެ. އެއަށްފަހު ދެ ރަކުޢަތް ކުރައްވައި، އެ ދެ ރަކުޢަތުގައި އަޑުހަރުކޮށް ކިޔުއްވިއެވެ. \n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ނަމާދު ކުރާ ތަނަށް (ނަބިއްޔާ ނުކުމެވަޑައިގަތެވެ.)","البخاري 1024 واللفظ له، ومسلم 893\n<br><br>\nالبخاري 1012، ومسلم 894"],["#158","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب الاستسقاء","އިސްތިސްޤާ ނަމާދުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - «أَنَّ رَجُلاً دَخَلَ الْمَسْجِدَ يَوْمَ الْجُمُعَةِ مِنْ بَابٍ كَانَ نَحْوَ دَارِ الْقَضَاءِ، وَرَسُولُ اللَّهِ ﷺ قَائِمٌ يَخْطُبُ، فَاسْتَقْبَلَ رَسُولَ اللَّهِ ﷺ قَائِمًا، ثُمَّ قَالَ: يَا رَسُولَ اللَّهِ، هَلَكَتْ الأَمْوَالُ، وَانْقَطَعَتْ السُّبُلُ فَادْعُ اللَّهَ تَعَالَى يُغِيثُنَا، قَالَ: فَرَفَعَ رَسُولُ اللَّهِ ﷺ يَدَيْهِ ثُمَّ قَالَ: اللَّهُمَّ أَغِثْنَا، اللَّهُمَّ أَغِثْنَا، اللَّهُمَّ أَغِثْنَا. قَالَ أَنَسٌ: فَلا وَاَللَّهِ مَا نَرَى فِي السَّمَاءِ مِنْ سَحَابٍ وَلا قَزَعَةٍ، وَمَا بَيْنَنَا وَبَيْنَ سَلْعٍ مِنْ بَيْتٍ وَلا دَارٍ قَالَ: فَطَلَعَتْ مِنْ وَرَائِهِ سَحَابَةٌ مِثْلُ التُّرْسِ. فَلَمَّا تَوَسَّطَتْ السَّمَاءَ انْتَشَرَتْ ثُمَّ أَمْطَرَتْ، قَالَ: فَلا وَاَللَّهِ مَا رَأَيْنَا الشَّمْسَ سَبْتاً، قَالَ: ثُمَّ دَخَلَ رَجُلٌ مِنْ ذَلِكَ الْبَابِ فِي الْجُمُعَةِ الْمُقْبِلَةِ، وَرَسُولُ اللَّهِ ﷺ قَائِمٌ يَخْطُبُ النَّاسَ، فَاسْتَقْبَلَهُ قَائِماً، فَقَالَ: يَا رَسُولَ اللَّهِ، هَلَكَتْ الأَمْوَالُ، وَانْقَطَعَتْ السُّبُلُ، فَادْعُ اللَّهَ أَنْ يُمْسِكَهَا عَنَّا، قَالَ: فَرَفَعَ رَسُولُ اللَّهِ ﷺ يَدَيْهِ ثُمَّ قَالَ: اللَّهُمَّ حَوَالَيْنَا وَلا عَلَيْنَا، اللَّهُمَّ عَلَى الآكَامِ وَالظِّرَابِ وَبُطُونِ الأَوْدِيَةِ وَمَنَابِتِ الشَّجَرِ، قَالَ: فَأَقْلَعَتْ، وَخَرَجْنَا نَمْشِي فِي الشَّمْسِ. قَالَ شَرِيكٌ: فَسَأَلْتُ أَنَسَ بْنَ مَالِكٍ: أَهُوَ الرَّجُلُ الأَوَّلُ قَالَ: لا أَدْرِي.","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ހުކުރު ދުވަހުގައި، ދާރުލް ޤަޟާއޭ ކިޔުނު ތަނާ ދިމާއަށް ހުރި ދޮރުކޮޅަކުން މީހަކު މިސްކިތަށް ވަނެވެ. އެ ވަގުތު ރަސޫލު ﷲ ﷺ ހުންނެވީ ތެދުވެވަޑައިގެން މީސްތަކުންނަށް ޚުޠުބާ ދެއްވަންށެވެ. ރަސޫލު ﷲ ﷺ ގެ ކުރިފުޅު މަތީގައި ހުރެގެން އެ މީހާ ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. ގެރިބަކަރިޖަމަލުތައް މަރުވެއްޖެއެވެ. އަދި މަގުތަށް ބަންދުވެއްޖެއެވެ. ފަހެ ވާރޭވެއްސަވާތޯ ﷲ ތަޢާލާގެ ޙަޟްރަތުގައި ދުޢާ ދަންނަވާ ދެއްވާށެވެ. ދެން ރަސޫލު ﷲ ﷺ ގެ ދެ އަތްޕުޅު އުފުއްލަވައި ޙަދީޘް ކުރެއްވިއެވެ: ”ޔާ ﷲ އެވެ. އަޅަމެންނަށް ވާރޭ ވެއްސަވާދެއްވާށިއެވެ. ޔާ ﷲ އެވެ. އަޅަމެންނަށް ވާރޭ ވެއްސަވާދެއްވާށިއެވެ. ޔާ ﷲ އެވެ. އަޅަމެންނަށް ވާރޭ ވެއްސަވާދެއްވާށިއެވެ.“ އަނަސް ވިދާޅުވިއެވެ: ފަހެ ﷲ ގަންދީ ބުނަމެވެ. (އޭރު) އުޑުގައި ވިލާގަނޑެއް އަދި ވިލާގަނޑެއްގެ ތަންކޮޅެއް ވެސް ނެތެވެ. އަދި ތިމަންމެންނާއި ސިލާ ފަރުބަދައާ ދެމެދު އެއްވެސް ގެއެއް އަދި ގޯއްޗެއް ނެތެވެ. ފަހެ އެ ފަރުބަދައިގެ ފަހަތުން އައްޑަނައެއް ފަދައިން (ވަށްކޮށް ހުރި) ވިލާގަނޑެއް ފާޅުވިއެވެ. ދެން އޭތި އުޑުގެ މެދާ ހިސާބަށް އެރުމުން، ފެތުރިގަނެގެން އޭގެން ވާރޭ ވެހިގަނެއްޖެއެވެ. ﷲ ގަންދީ ބުނަމެވެ. ހަފްތާއެއް ވަންދެން ތިމަންމެންނަކަށް އިރެއް ނުފެނުނެވެ. ދެން ޖެހިގެން އައި ހުކުރު ދުވަހުގައި އެއްކަލަ ދޮރުކޮޅުން މީހަކު މިސްކިތަށް ވަނެވެ. އެ ވަގުތު ރަސޫލު ﷲ ﷺ ހުންނެވީ ތެދުވެވަޑައިގެން މީސްތަކުންނަށް ޚުޠުބާ ދެއްވަންށެވެ. އެ މީހާ ނަބިއްޔާގެ ކުރިފުޅު މަތީގައި ހުރެގެން ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. ގެރިބަކަރިޖަމަލުތައް މަރުވެއްޖެއެވެ. އަދި މަގުތަށް ބަންދުވެއްޖެއެވެ. ފަހެ ތިމަންމެންގެ މައްޗަށް ވާރޭ ވެހުން ހުއްޓަވާތޯ ﷲ ގެ ޙަޟްރަތުގައި ދުޢާ ދަންނަވާ ދެއްވާށެވެ. ދެން ރަސޫލު ﷲ ﷺ ގެ ދެ އަތްޕުޅު އުފުއްލަވައި ޙަދީޘް ކުރެއްވިއެވެ: ”ޔާ ﷲ އެވެ. އަޅަމެންގެ ވަށައިގެން (ވާރޭ ވެއްސަވާދެއްވާށިއެވެ.) އަޅަމެންގެ މައްޗަށް ނުވެއްސަވާށިއެވެ. ޔާ ﷲ އެވެ. އުސް ތަންތަނާއި، ކުދި ފަރުބަދަތަކާއި، ވާދީތަކާއި، ގަސްތައް ފަޅާ ތަންތަނަށް (ވާރޭ ވެއްސަދެއްވާށިއެވެ.)“ ފަހެ ވާރޭ ވެހުން ހުއްޓުނެވެ. އަދި އިރުގެ އަލީކަމުގައި ތިމަންމެން ނުކުމެ ހިނގީމުއެވެ. (ރާވީއެއް ކަމަށްވާ) ޝަރީކު ވިދާޅުވިއެވެ: ތިމަންނަ އަނަސް ބިން މާލިކު ގާތު ދެންނެވީމެވެ: އެއީ ހަމަ ފުރަތަމަ އައި މީހާތޯއެވެ؟ އަނަސް ދެންނެވިއެވެ: ތިމަންނައަކަށް ނޭނގެއެވެ.","البخاري 1014، ومسلم 897 واللفظ له"],["#159","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب صلاة الخوف","ޚައުފު ނަމާދުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ بْنِ الْخَطَّابِ رَضِيَ اللَّهُ عَنْهُما قَالَ: «صَلَّى بِنَا رَسُولُ اللَّهِ ﷺ صَلاةَ الْخَوْفِ فِي بَعْضِ أَيَّامِهِ، فَقَامَتْ طَائِفَةٌ مَعَهُ، وَطَائِفَةٌ بِإِزَاءِ الْعَدُوِّ، فَصَلَّى بِاَلَّذِينَ مَعَهُ رَكْعَةً، ثُمَّ ذَهَبُوا، وَجَاءَ الآخَرُونَ، فَصَلَّى بِهِمْ رَكْعَةً، وَقَضَتِ الطَّائِفَتَانِ رَكْعَةً، رَكْعَةً»","ޢަބްދު ﷲ ބިން ޢުމަރު ބިން އަލްޚައްޠާބު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ ދުވަސްވަރުގައި، ތިމަންމެންނައިގެން ނަބިއްޔާ ޚައުފު ނަމާދު ކުރެއްވިއެވެ. މީސްތަކުންގެ ތެރެއިން އެއް ބަޔަކު ނަބިއްޔާގެ އަރިހުގައި ނަމާދު ކުރި ހިނދު، އަނެއް ބަޔަކު އަދުއްވުންނާ ކުރިމަތިލައިގެން ތިއްބެވެ. ނަބިއްޔާގެ އަރިހުގައި ތިބި މީހުންނައިގެން އެއް ރަކުޢަތް ނަބިއްޔާ ކުރެއްވިއެވެ. އެއަށްފަހު އެބައިމީހުން ގޮސްފިއެވެ. ދެން (ކުރީގައި ނަމާދު ނުކޮށް ތިބި) މީހުން އައިސް، އެބައިމިހުންނައިގެން އަނެއް ރަކުޢަތް ނަބިއްޔާ ކުރެއްވިއެވެ. އަދި ދެބައިމީހުން ވެސް (އަމިއްލައަށް އިތުރު) އެއް ރަކުޢަތްކޮށް (ނަމާދު) ފުރިހަމަ ކުރިއެވެ.\n","البخاري 942، ومسلم 839 واللفظ له"],["#160","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب صلاة الخوف","ޚައުފު ނަމާދުގެ ބާބު","عَنْ يَزِيدَ بْنِ رُومَانَ عَنْ صَالِحِ بْنِ خَوَّاتِ بْنِ جُبَيْرٍ عَمَّنْ صَلَّى مَعَ رَسُولِ اللَّهِ ﷺ صَلاةَ ذَاتِ الرِّقَاعِ، صَلاةَ الْخَوْفِ: أَنَّ طَائِفَةً صُفَّتْ مَعَهُ، وَطَائِفَةً وِجَاهَ الْعَدُوِّ، فَصَلَّى بِاَلَّذِينَ مَعَهُ رَكْعَةً، ثُمَّ ثَبَتَ قَائِماً، وَأَتَمُّوا لأَنْفُسِهِمْ، ثُمَّ انْصَرَفُوا، فَصُفُّوا وِجَاهَ الْعَدُوِّ، وَجَاءَتْ الطَّائِفَةُ الأُخْرَى، فَصَلَّى بِهِمْ الرَّكْعَةَ الَّتِي بَقِيَتْ، ثُمَّ ثَبَتَ جَالِساً، وَأَتَمُّوا لأَنْفُسِهِمْ، ثُمَّ سَلَّمَ بِهِمْ»","ޔަޒީދު ބިން ރޫމާން، ޞާލިޙު ބިން ޚައްވާތު ބިން ޖުބައިރުގެ އަރިހުން، ޛާތުއް ރިޤާޢު ހަނގުރާމައިގައި ރަސޫލު ﷲ ﷺ ގެ އަރިހުގައި ޚައުފު ނަމާދު ކުރެއްވި ޞަޙާބީއެއްގެ އަރިހުން ރިވާ ކުރެއްވިއެވެ: އެއް ބަޔަކު ނަބިއްޔާގެ އަރިހުގައި ސަފު ހެދިއެވެ. އަދި އަނެއް ބަޔަކު ތިބީ އަދުއްވުންނާ ކުރިމަތިލައިގެންނެވެ. ފަހެ ނަބިއްޔާގެ އަރިހުގައި ތިބި ބަޔަކާއެކު އެއް ރަކުޢަތް ނަބިއްޔާ ކުރެއްވިއެވެ. ދެން ނަބިއްޔާ (ދެވަނަ ރަކުޢަތަށް) ތެދުވެވަޑައިގެން މަޑުކޮށްލަވައިގެން އިންނެވި ހިނދު، އެބައިމީހުން އަމިއްލައަށް (ދެވަނަ ރަކުޢަތްކޮށް) ނަމާދު ފުރިހަމަ ކުރިއެވެ. އެއަށްފަހު އެބައިމީހުން ނަމާދުން ވަކިވެ، ޢަދުއްވުންނާ ކުރިމަތިލައިގެން ސަފު ހެދިއެވެ. އަދި (ނަމާދު ނުކޮށް ތިބި) މީހުން އައިސް އެބައިމީހުންނައިގެން ބާކި ހުރި ރަކުޢަތް ނަބިއްޔާ ކުރެއްވިއެވެ. އެއަށްފަހު ނަބިއްޔާ އިށީނދެވަޑައިގެން މަޑުކޮށްލައްވައިގެން އިންނެވި ހިނދު، އެބައިމީހުން (އިތުރު ރަކުޢަތެއްކޮށް) ނަމާދު ފުރިހަމަ ކުރިއެވެ. ދެން ނަބިއްޔާ އެބައިމީހުންނައިގެން ސަލާމް ދެއްވިއެވެ.","البخاري 4129، ومسلم 832 واللفظ له"],["#161","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب صلاة الخوف","ޚައުފު ނަމާދުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ الأَنْصَارِيِّ رَضِيَ اللَّهُ عَنْهُما قَالَ: «شَهِدْتُ مَعَ رَسُولِ اللَّهِ ﷺ صَلاةَ الْخَوْفِ فَصَفَفْنَا صَفَّيْنِ خَلْفَ رَسُولِ اللَّهِ ﷺ وَالْعَدُوُّ بَيْنَنَا وَبَيْنَ الْقِبْلَةِ، وَكَبَّرَ النَّبِيُّ ﷺ وَكَبَّرْنَا جَمِيعاً، ثُمَّ رَكَعَ وَرَكَعْنَا جَمِيعاً، ثُمَّ رَفَعَ رَأْسَهُ مِنْ الرُّكُوعِ وَرَفَعْنَا جَمِيعاً، ثُمَّ انْحَدَرَ بِالسُّجُودِ وَالصَّفُّ الَّذِي يَلِيهِ، وَقَامَ الصَّفُّ الْمُؤَخَّرُ فِي نَحْرِ الْعَدُوِّ، فَلَمَّا قَضَى النَّبِيُّ ﷺ السُّجُودَ، وَقَامَ الصَّفُّ الَّذِي يَلِيهِ: انْحَدَرَ الصَّفُّ الْمُؤَخَّرُ بِالسُّجُودِ، وَقَامُوا، ثُمَّ تَقَدَّمَ الصَّفُّ الْمُؤَخَّرُ، وَتَأَخَّرَ الصَّفُّ الْمُقَدَّمُ، ثُمَّ رَكَعَ النَّبِيُّ ﷺ وَرَكَعْنَا جَمِيعاً، ثُمَّ رَفَعَ رَأْسَهُ مِنْ الرُّكُوعِ وَرَفَعْنَا جَمِيعاً، ثُمَّ انْحَدَرَ بِالسُّجُودِ، وَالصَّفُّ الَّذِي يَلِيهِ - الَّذِي كَانَ مُؤَخَّرًا فِي الرَّكْعَةِ الأُولَى - فَقَامَ الصَّفُّ الْمُؤَخَّرُ فِي نَحْرِ الْعَدُوِّ، فَلَمَّا قَضَى النَّبِيُّ ﷺ السُّجُودَ وَالصَّفُّ الَّذِي يَلِيهِ: انْحَدَرَ الصَّفُّ الْمُؤَخَّرُ بِالسُّجُودِ، فَسَجَدُوا ثُمَّ سَلَّمَ ﷺ وَسَلَّمْنَا جَمِيعاً، قَالَ جَابِرٌ: كَمَا يَصْنَعُ حَرَسُكُمْ هَؤُلاءِ بِأُمَرَائِهِمْ» وَذَكَرَهُ مُسْلِمٌ بِتَمَامِهِ.\n<br><br>\nوَذَكَرَ الْبُخَارِيُّ طَرَفاً مِنْهُ: «وَأَنَّهُ صَلَّى صَلاةَ الْخَوْفِ مَعَ النَّبِيِّ ﷺ فِي الْغَزْوَةِ السَّابِعَةِ، غَزْوَةِ ذَاتِ الرِّقَاعِ»\n","ޖާބިރު ބިން ޢަބްދު ﷲ އަލްއަންޞާރީ رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ. ރަސޫލު ﷲ ﷺ ގެ އަރިހުގައި ޚައުފު ނަމާދުގައި ތިމަން ބައިވެރިވީމެވެ. ފަހެ ރަސޫލު ﷲ ﷺ ގެ ފަހަތްޕުޅުގައި ދެ ސަފު ތިމަންމެން ހެދީމެވެ. އަދި އަދުއްވުން ތިބީ ތިމަންމެންނާއި ޤީބްލައާ ދެމެދުގައެވެ. (އެބަހީ: ތިމަންމެންގެ ކުރިމަތީގައިވާ ދިމާއަށެވެ.) ދެން ނަބިއްޔާ ތަކްބީރު ދެއްވުމުން، ތިމަންމެން އެންމެންވެސް ތަކްބީރު ދިނީމެވެ. އެއަށްފަހު ނަބިއްޔާ ރުކޫޢު ކުރުމުން، ތިމަންމެން އެންމެންވެސް ރުކޫޢު ކުރީމެވެ. ދެން ނަބިއްޔާ ރުކޫޢިން އިސްތަށިފުޅު އުފުއްލަވައި (ތެދުވެވަޑައިގަތެވެ.) އަދި ތިމަންމެން އެންމެންވެސް ބޯ އުފުލައި (ތެދުވީމެވެ). އެއަށްފަހު ނަބިއްޔާއާ ޖެހިގެން ތިބި ސަފައިގެން ސަޖިދައަށް ނަބިއްޔާ ތިރިވެލެއްވިއެވެ. އަދި އޭގެ ފަހަތުގައިވީ ސަފު އަދުއްވުންނާ ކުރިމަތިލައިގެން ތިއްބެވެ. ދެން ނަބިއްޔާ ﷺ ސަޖިދަކޮށް ނިންމަވައި، އަދި ނަބިއްޔާއާ ޖެހިގެން ތިބި ސަފު ތެދުވުމުން، އެ ހިސާބުން ފަހަތުގައި ތިބި ސަފު ސަޖިދައަށް ތިރިވިއެވެ. އެއަށްފަހު އެބައިމީހުން ތެދުވެ، ކުރީ ސަފަށް އެރިއެވެ. އަދި ފުރަތަމަ ސަފު ފަހަތަށް ޖެހުނެވެ. އެއަށްފަހު ނަބިއްޔާ ރުކޫޢު ކުރެއްވުމުން، ތިމަންމެން އެންމެވެސް ރުކޫޢު ކުރީމެވެ. ދެން ނަބިއްޔާ ރުކޫޢިން އިސްތަށިފުޅު އުފުއްލެވުމުން (ތެދުވެވަޑައި ގަތުމުން)، ތިމަންމެން އެންމެންވެސް ބޯ އުފުލައި (ތެދުވީމެވެ) އެއަށްފަހު ނަބިއްޔާ ސަޖިދައަށް ތިރިވެލެއްވިއެވެ. (މިހާރު) ނަބިއްޔާއާ ޖެހިގެން ތިބި (އެބަހީ: ކުރީ ރަކުޢަތުގައި ފަހަތުގައި ތިބި) ސަފުވެސް ސަޖިދައަށް ތިރިވިއެވެ. އަދި (މިހާރު) ފަހަތުގައިވި ސަފު އަދުއްވުންނާ ކުރިމަތިލައިގެން ތިއްބެވެ. ދެން ނަބިއްޔާ ﷺ ސަޖިދަކޮށް ނިންމަވައި، އަދި ނަބިއްޔާއާ ޖެހިގެން ތިބި ސަފު ވެސް ސަޖިދަކޮށް ނިމުމުން، އެ ހިސާބުން ފަހަތުގައި ތިބި ސަފު ވެސް ސަޖިދައަށް ތިރިވިއެވެ. އެއަށްފަހު ނަބިއްޔާ ﷺ ސަލާމް ދެއްވުމުން، ތިމަންމެން އެންމެންވެސް ސަލާމް ދިނީމެވެ. ޖާބިރު (އިތުރަށް) ވިދާޅުވިއެވެ: އެއީ ތިޔަބައިމީހުންގެ ތެރޭގައިވާ ފާރަވެރިން، ތިޔަބައިމިހުންގެ ވެރިންނައިގެން ކަންތައް ކުރާ ގޮތާއި އެއްފަދައިންނެވެ. (މުޢައްލިފު ވިދާޅުވިއެވެ:) އިމާމު މުސްލިމު މި ޙަދީޘް މިއިން ގޮތަށް ރިވާ ކުރެއްވިއެވެ. <br><br> އަދި އިމާމު ބުޚާރީ އޭގެން ބައެއް ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ގެ އަރިހުގައި ޖާބިރު ޚައުފު ނަމާދު ކުރިއެވެ. އެއީ ހަތް ވަނަ ޣަޒްވާކަމަށްވާ ޛާތުއް ރިޤާޢުގައެވެ.","مسلم 840\n<br><br>\nالبخاري 4125"],["#162","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «نَعَى النَّبِيُّ ﷺ النَّجَاشِيَّ فِي الْيَوْمِ الَّذِي مَاتَ فِيهِ، خَرَجَ بِهِمْ إلَى الْمُصَلَّى، فَصَفَّ بِهِمْ، وَكَبَّرَ أَرْبَعاً»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަޖާޝީ އަވަހާރަވި ދުވަހު، އެކަލޭގެފާނު އަވަހާރަވިކަމުގެ ޚަބަރު ނަބިއްޔާ ﷺ ދެއްވިއެވެ. އަދި މީސްތަކުންނައިގެން ނަމާދު ކުރާ ތަނަށް ނަބިއްޔާ ނުކުމެވަޑައިގެން ސަފު ހައްދެވުމަށް ފަހު، ހަތަރު ތަކްބީރު ދެއްވާ (ކަށުނަމާދު ކުރެއްވިއެވެ.)","البخاري 1245، ومسلم 951"],["#163","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ جَابِرِ - رَضِيَ اللَّهُ عَنْهُ -: «أَنَّ النَّبِيَّ ﷺ صَلَّى عَلَى النَّجَاشِيِّ، فَكُنْتُ فِي الصَّفِّ الثَّانِي، أَوْ الثَّالِثِ»","ޖާބިރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ނަޖާޝީގެ މައްޗަށް ކަށުނަމާދު ކުރެއްވިއެވެ. އަދި ތިމަންނަ ހުރީ ދެވަނަ ނުވަތަ ތިންވަނަ ސަފުގައެވެ.","البخاري 1317 واللفظ له، ومسلم 952"],["#164","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما: «أَنَّ النَّبِيَّ ﷺ صَلَّى عَلَى قَبْرٍ، بَعْدَ مَا دُفِنَ، فَكَبَّرَ عَلَيْهِ أَرْبَعاً»","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ކަށްވަޅެއްގައި (މީހަކު) ވަޅުލެވިފައިވީ ފަހުން، ނަބިއްޔާ ﷺ (އެ ތަނަށް ވަޑައިގެން) ހަތަރު ތަކްބީރު ދެއްވާ (އޭނާގެ) މައްޗަށް ކަށުނަމާދު ކުރައްވިއެވެ.","البخاري 1319، ومسلم 954 واللفظ له"],["#165","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا: «أَنَّ رَسُولَ اللَّهِ ﷺ كُفِّنَ فِي أَثْوَابٍ بِيضٍ يَمَانِيَةٍ، لَيْسَ فِيهَا قَمِيصٌ وَلا عِمَامَةٌ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ކަފުން ކުރެވުއްނީ ހުދު ޔަމަނީ (ތިން) ފޭރާންކޮޅަކުން (ހަށިކޮޅުގައި އޮޅާލައްވައިގެންނެވެ. އަދި ކަފުން ކުރެއްވުމުގައި ހަށިކޮޅުގެ ބައްޓަމަށް ފެތޭ) ގަމީސްކޮޅެއް އަދި (އިސްތަށިފުޅު މަތިން ތުރުކުރައްވާ) ފަގުޑިކޮޅެއް ވެސް ނުހިމެނެއެވެ.","البخاري 1264، ومسلم 941"],["#166","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ أُمِّ عَطِيَّةَ الأَنْصَارِيَّةِ قَالَتْ: «دَخَلَ عَلَيْنَا رَسُولُ اللَّهِ ﷺ حِينَ تُوُفِّيَتْ ابْنَتُهُ، فَقَالَ: اغْسِلْنَهَا ثَلاثاً، أَوْ خَمْساً، أَوْ أَكْثَرَ مِنْ ذَلِكَ - إنْ رَأَيْتُنَّ ذَلِكِ - بِمَاءٍ وَسِدْرٍ، وَاجْعَلْنَ فِي الأَخِيرَةِ كَافُوراً - أَوْ شَيْئاً مِنْ كَافُورٍ - فَإِذَا فَرَغْتُنَّ فَآذِنَّنِي» فَلَمَّا فَرَغْنَا آذَنَّاهُ. فَأَعْطَانَا حَقْوَهُ. وَقَالَ: أَشْعِرْنَهَا بِهِ - تَعْنِي إزَارَهُ.\n<br><br>\nوَفِي رِوَايَةٍ «أَوْ سَبْعاً»\n<br><br>\nوَقَالَ: «ابْدَأْنَ بِمَيَامِنِهَا وَمَوَاضِعِ الْوُضُوءِ مِنْهَا»\n<br><br>وَإِنَّ أُمَّ عَطِيَّةَ قَالَتْ: وَجَعَلْنَا رَأْسَهَا ثَلاثَةَ قُرُونٍ»\n","އުންމު ޢަޠިއްޔާ އަލްއަންޞާރިއްޔާ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ ދަރިކަނބަލުން (ޒައިނަބު) އަވަހާރަވުމުން، ތިމަންމެންގެ ގާތަށް ނަބިއްޔާ ވަޑައިގެން ޙަދީޘް ކުރެއްވިއެވެ: ”ތިން ފަހަރު، ނުވަތަ ފަސް ފަހަރު، ނުވަތަ ތިޔަ ކަނބަލުންނަށް ފެންނަ ނަމަ އެއަށްވުރެ ގިނަ ފަހަރު އެ ކަމަނާ ހިނަވާށެވެ. އަދި ފަހު ފަހަރު ފެންވަރުވާ އިރު ކާފޫރު ނުވަތަ އެފަދަ އެހެން އެއްޗެއް ބޭނުން ކުރާށެވެ. އަދި ތިޔަ ކަނބަލުން އެ ކަންކޮށް ނިމުމުން ތިމަން ނަބިއްޔާއަށް އަންގަވާށެވެ.“ ދެން ތިމަންމެން ނިމުމުން ނަބިއްޔާއަށް އެންގެވީމެވެ. ފަހެ ނަބިއްޔާގެ އިޒާރުކޮޅު ތިމަންމެންނަށް ދެއްވާފައި ޙަދީޘް ކުރެއްވިއެވެ: ”އެ ކަމަނާގެ ހަށިކޮޅާ ޖެހިގެން މީތި ބަހައްޓަވާށެވެ.“ (އެބަހީ: ނަބިއްޔާގެ އިޒާރުކޮޅު ބަހައްޓަވާށެވެ.) \n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”ނޫނީ ހަތް ފަހަރު (ހިނަވާށެވެ.)“\n<br><br>\nއަދި ޙަދީޘް ކުރެއްވިއެވެ: ”އެ ކަމަނާގެ ކަނާތުންނާއި، ވުޟޫ ކުރާ ތަންތަނުން (ދޮންނަވަން) ފައްޓަވާށެވެ.“\n<br><br>\nއަދި އުންމު ޢަޠިއްޔާ ވިދާޅުވިއެވެ: ތިމަންމެން އެކަމަނާގެ އިސްތަށިފުޅު ތިން ފަށަށް ގެތީމުއެވެ.","البخاري 1253، ومسلم 939\n<br><br>\nالبخاري 1259، ومسلم 939\n<br><br>\nالبخاري 1255، ومسلم 939\n<br><br>\nالبخاري 1259، ومسلم 939\n"],["#167","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: «بَيْنَمَا رَجُلٌ وَاقِفٌ بِعَرَفَةَ، إذْ وَقَعَ عَنْ رَاحِلَتِهِ، فَوَقَصَتْهُ - أَوْ قَالَ: فَأَوْقَصَتْهُ - فَقَالَ رَسُولُ اللَّهِ ﷺ: اغْسِلُوهُ بِمَاءٍ وَسِدْرٍ، وَكَفِّنُوهُ فِي ثَوْبَيْهِ. وَلا تُحَنِّطُوهُ، وَلا تُخَمِّرُوا رَأْسَهُ. فَإِنَّهُ يُبْعَثُ يَوْمَ الْقِيَامَةِ مُلَبِّياً»\n<br><br>\nوَفِي رِوَايَةٍ: «وَلا تُخَمِّرُوا وَجْهَهُ وَلا رَأْسَهُ»","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ޢަރަފާތުގައި މީހަކު މަޑުކުރަނިކޮށް، އޭނާގެ ސަވާރީ މަތިން ވެއްޓި ކަރު ބިނދައިގެން ގޮސް (އަވަހާރަވިއެވެ.) ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ފެނާއި ސިދުރުން އޭނާ ހިނަވާށެވެ. އަދި އޭނާ (ލައިގެން ހުރި) ހެދުމުގައި އޭނާ ކަފުން ކުރާށެވެ. އެހެނަސް އޭނާ ގައިގައި ހުވަނދު ނުލާށެވެ. އަދި އޭނާގެ ބޯ ނިވާ ނުކުރާށެވެ.\n<br><br>\nއެހެނީ ހަމަކަށަވަރުން ޤިޔާމަތް ދުވަހު އޭނާ ދިރުއްވޭނީ ތަލްބިޔާ ކިޔާ ޙާލުގައެވެ.“ އަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”އަދި އޭނާގެ މޫނުވެސް އަދި ބޯވެސް ނިވާ ނުކުރާށެވެ.“","البخاري 1265 واللفظ له، ومسلم 1206\n<br><br>\nمسلم 1206"],["#168","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ أُمِّ عَطِيَّةَ الأَنْصَارِيَّةِ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: «نُهِينَا عَنْ اتِّبَاعِ الْجَنَائِزِ وَلَمْ يُعْزَمْ عَلَيْنَا»","އުންމު ޢަޠިއްޔާ އަލްއަންޞާރިއްޔާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ތިމަން ކަނބަލުންނަށް ޖަނާޒާގެ ފަހަތުން ދިޔުން ނަހީކުރެއްވުނެވެ. އެހެނަސް ތިމަން ކަންބަލުންނަށް އެކަން ޙަރާމެއް ނުކުރައްވައެވެ.","البخاري 1278، ومسلم 938"],["#169","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - عَنْ النَّبِيِّ ﷺ قَالَ: «أَسْرِعُوا بِالْجِنَازَةِ فَإِنَّهَا إنْ تَكُ صَالِحَةً: فَخَيْرٌ تُقَدِّمُونَهَا إلَيْهِ. وَإِنْ تَكُ سِوَى ذَلِكَ: فَشَرٌّ: تَضَعُونَهُ عَنْ رِقَابِكُمْ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ޖާނާޒާ އަވަސް ކުރުވާށެވެ. އެހެނީ ހަމަކަށަވަރުން އެއީ ޞާލިޙު މީހެއްކަމުގައި ވާނަމަ، ފަހެ ތިޔަބައިމީހުން އޭނާ ތިޔަ އަވަސް ކުރުވަނީ ހެވަކަށެވެ. އަދި އެއީ އެ ނޫން ފަދަ މީހެއްކަމުގައި ވާނަމަ، ފަހެ ތިޔަބައިމީހުންގެ ކަނދުރާތަކުން ތިޔަ ބާލަނީ ނުބައި (މީހެކެވެ.)“","البخاري 1315 واللفظ له، ومسلم 944"],["#170","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ سَمُرَةَ بْنِ جُنْدُبٍ- رَضِيَ اللَّهُ عَنْهُ - قَالَ: «صَلَّيْت وَرَاءَ النَّبِيِّ ﷺ عَلَى امْرَأَةٍ مَاتَتْ فِي نِفَاسِهَا فَقَامَ فِي وَسَطِهَا»","ސަމުރާ ބިން ޖުންދުބް رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނިފާސްކަމުގެ ތެރޭގައި އަވަހާރަވި އަންހެނެއްގެ މައްޗަށް، ނަބިއްޔާ ﷺ ގެ ފަހަތްޕުޅުގައި ތިމަން ކަށުނަމާދު ކުރީމެވެ. ނަބިއްޔާ ތެދުވެވަޑައިގެން ހުންނެވީ އެ އަވަހާރަވި އަންހެނާގެ މެދަށްވާ ގޮތަށެވެ.","البخاري 332، 1331، 1332، ومسلم 964"],["#171","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ أَبِي مُوسَى عَبْدِ اللَّهِ بْنِ قَيْسٍ- رَضِيَ اللَّهُ عَنْهُ -: «أَنَّ رَسُولَ اللَّهِ ﷺ بَرِيءٌ مِنْ الصَّالِقَةِ وَالْحَالِقَةِ وَالشَّاقَّةِ»","އަބޫ މޫސާ، ޢަބްދު ﷲ ބިން ޤައިސް رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: (މުސީބާތެއް ޖެހުމުން) އަޑު އުފުލައި ހޭރިގަންނަ އަންހެނާއާއި، އިސްތަށި ބާލާ (ނުވަތަ އުފުރާ) އަންހެނާއާއި، ހެދުން އިރާލާ އަންހެނާގެ ކިބައިން ރަސޫލު ﷲ ﷺ ބަރީއަވެގެންވެއެވެ.","البخاري 1296، ومسلم 104"],["#172","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَ: «لَمَّا اشْتَكَى النَّبِيُّ ﷺ ذَكَرَ بَعْضُ نِسَائِهِ كَنِيسَةً رَأَيْنَهَا بِأَرْضِ الْحَبَشَةِ، يُقَالُ لَهَا: مَارِيَةُ - وَكَانَتْ أُمُّ سَلَمَةَ وَأُمُّ حَبِيبَةَ أَتَتَا أَرْضَ الْحَبَشَةِ - فَذَكَرَتَا مِنْ حُسْنِهَا وَتَصَاوِيرَ فِيهَا، فَرَفَعَ رَأْسَهُ ﷺ وَقَالَ: أُولَئِكَ إذَا مَاتَ فِيهِمْ الرَّجُلُ الصَّالِحُ بَنَوْا عَلَى قَبْرِهِ مَسْجِداً، ثُمَّ صَوَّرُوا فِيهِ تِلْكَ الصُّوَرَ أُولَئِكَ شِرَارُ الْخَلْقِ عِنْدَ اللَّهِ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ އާލާސްވެވަޑައިގެން އިންނެވި ހިނދު، ނަބިއްޔާގެ ބައެއް އަނބިކަނބަލުން ޚަބަޝާގައި ވަނިކޮށް އެ ކަންބަލުންނަށް ފެނުނު މާރިޔާއޭ ކިޔުނު ކަނީސާއެއްގެ ވާހަކަ ނަންގަތެވެ. - އުންމު ސަލަމާއާއި އުންމު ޙަބީބާ ވަނީ ޚަބަޝާއަށް ވަޑައިގެންފައެވެ. - އަދި އެ ކަނީސާގެ ރީތިކަމާއި އޭގައި ހުރި (މީހުންގެ) ސޫރަތަކުގެ ވާހަކަ ދެއްކެވިއެވެ. ފަހެ ނަބިއްޔާ ﷺ އިސްތަށިފުޅު އުފުއްލަވާފައި ޙަދީޘް ކުރެއްވިއެވެ: ”އެބައިމީހުންނީ، އެބައިމީހުންގެ ތެރެއިން ޞާލިޙު މީހަކު މަރުވެއްޖެ ނަމަ، އޭނާގެ މަހާނަ މަތީ މިސްކިތް ބިނާކޮށް، ތިޔަ ސޫރަތައް ހަދައި އުޅޭ މީހުންނެވެ. ﷲގެ ޙަޟްރަތުގައ އެބައިމީހުންނަކީ ޚަލްޤުތަކުންގެ ތެރެއިން އެންމެ ނުބައި މީހުންނެވެ.“","البخاري 1341 واللفظ له، ومسلم 528"],["#173","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: قَالَ رَسُولُ اللَّهِ ﷺ فِي مَرَضِهِ الَّذِي لَمْ يَقُمْ مِنْهُ: «لَعَنَ اللَّهُ الْيَهُودَ وَالنَّصَارَى اتَّخَذُوا قُبُورَ أَنْبِيَائِهِمْ مَسَاجِدَ» قَالَتْ: وَلَوْلا ذَلِكَ أُبْرِزَ قَبْرُهُ غَيْرَ أَنَّهُ خُشِيَ أَنْ يُتَّخَذَ مَسْجِداً.","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ފަސޭހަފުޅު ނުވި އާލާސްކަންފުޅުގައި ވަނިކޮށް ޙަދީޘް ކުރެއްވިއެވެ: ”ޔަހޫދީންނާއި ނަޞާރާއިންނަށް ﷲ ލަޢުނަތް ލައްވާށިއެވެ. އެބައިމީހުންގެ ނަބިއްޔުންގެ މަހާނަތައް މިސްކިތްތަކެއްގެ ގޮތުގައި އެބައިމީހުން ނެގިއެވެ.“ ޢާއިޝާ ވިދާޅުވިއެވެ: އެ ނޫން ނަމަ ނަބިއްޔާގެ މަހާނަފުޅު ވެސް ފާޅުވާ ގޮތަށް ހެދީހެވެ. އެހެނެއްކަމަކު މިސްކިތެއްގެ ގޮތުގައި އެ ތަން ނަގައިފާނެ ކަމުގެ ބިރު ހުއްޓެވެ.","البخاري 1330، ومسلم 529 واللفظ له"],["#174","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ مَسْعُودٍ - رَضِيَ اللَّهُ عَنْهُ - عَنْ النَّبِيِّ ﷺ أَنَّهُ قَالَ: «لَيْسَ مِنَّا مَنْ ضَرَبَ الْخُدُودَ، وَشَقَّ الْجُيُوبَ، وَدَعَا بِدَعْوَى الْجَاهِلِيَّةِ»","ޢަބްދު ﷲ ބިން މަސްޢޫދު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”(މުސީބާތެއް ޖެހުމުން އަމިއްލަ) ކޯތާފަތް މަތީ ޖަހާ ހަދާ މީހުންނާއި، ހެދުން އިރާލާ މީހުންނާއި، ޖާހިލިއްޔަތުގެ (ހޭރުންތަކާއި) ގޮވާލުންތަކުން ގޮވާ މީހުންނީ ތިމަންމެންގެ ތެރެއިންވާ ބަޔެއް ނޫނެވެ.“","البخاري 1294، ومسلم 103 واللفظ له"],["#175","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «مَنْ شَهِدَ الْجِنَازَةَ حَتَّى يُصَلِّيَ عَلَيْهَا فَلَهُ قِيرَاطٌ. وَمَنْ شَهِدَهَا حَتَّى تُدْفَنَ فَلَهُ قِيرَاطَانِ. قِيلَ: وَمَا الْقِيرَاطَانِ؟ قَالَ: مِثْلُ الْجَبَلَيْنِ الْعَظِيمَيْنِ»\n<br><br>\nوَلِمُسْلِمٍ: «أَصْغَرُهُمَا مِثْلُ أُحُدٍ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ޖަނާޒާއެއްގައި ނަމާދު ކުރުމަށް ދާންދެން އޭގައި ބައިވެރިވެއްޖެ މީހަކަށް ޤީރާޠެއްގެ ސަވާބު ލިބިގެންވެއެވެ. އަދި އެ (މައްޔިތާ) ވަޅުލުމަށް ދާންދެން ބައިވެރިވެއްޖެ މީހަކަށް ދެ ޤީރާޠް ލިބިގެންވެއެވެ.“ ނަބިއްޔާއަށް ދެންނެވުނެވެ: ދެ ޤީރާޠަކީ ކިހައިވަރެއްތޯއެވެ؟ ނަބިއްޔާ ޙާދީޘް ކުރެއްވިއެވެ: ”ބޮޑު ދެ ފަރުބަދައާ އެއްވަރުވެގެން ވެއެވެ.“ \n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ”އެ ދޭތީގެ އެންމެ ކުޑަ މިންވަރު ޢުހުދު ފަރުބަދައާ އެއްވަރުވެގެން ވެއެވެ.“","البخاري 1325، ومسلم 945 واللفظ له\n<br><br>\nمسلم 945"],["#176","كتاب الزكاة","ޒަކާތުގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ لِمُعَاذِ بْنِ جَبَلٍ - حِينَ بَعَثَهُ إلَى الْيَمَنِ -: «إنَّك سَتَأْتِي قَوْماً أَهْلَ كِتَابٍ. فَإِذَا جِئْتَهُمْ: فَادْعُهُمْ إلَى أَنْ يَشْهَدُوا أَنْ لا إلَهَ إلاَّ اللَّهُ، وَأَنَّ مُحَمَّداً رَسُولُ اللَّهِ. فَإِنْ هُمْ أَطَاعُوا لَكَ بِذَلِكَ فَأَخْبِرْهُمْ: أَنَّ اللَّهَ قَدْ فَرَضَ عَلَيْهِمْ خَمْسَ صَلَوَاتٍ فِي كُلِّ يَوْمٍ وَلَيْلَةٍ. فَإِنْ هُمْ أَطَاعُوا لَك بِذَلِكَ، فَأَخْبِرْهُمْ: أَنَّ اللَّهَ قَدْ فَرَضَ عَلَيْهِمْ صَدَقَةً، تُؤْخَذُ مِنْ أَغْنِيَائِهِمْ فَتُرَدُّ عَلَى فُقَرَائِهِمْ. فَإِنْ هُمْ أَطَاعُوا لَك بِذَلِكَ، فَإِيَّاكَ وَكَرَائِمَ أَمْوَالِهِمْ. وَاتَّقِ دَعْوَةَ الْمَظْلُومِ. فَإِنَّهُ لَيْسَ بَيْنَهَا وَبَيْنَ اللَّهِ حِجَابٌ»","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: މުޢާޛު ބިން ޖަބަލު ޔަމަނަށް ފޮނުއްވަމުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން އަހުލު ކިތާބީންގެ ބަޔަކު ގާތަށް ތިބާ އާދެވޭނެއެވެ. ފަހެ އެބައިމީހުންގެ ގާތަށް ދެވުމުން، ﷲ ފިޔަވައި (އަޅުކަން ޙައްޤުވާ) އެހެން އެއްވެސް އިލާހަކު ނުވާ ކަމަށާއި، މުޙައްމަދުގެފާނީ ﷲގެ ރަސޫލާ ކަމަށް ހެކިވުމަށް އެބައިމީހުންނަށް ގޮވާލާށެވެ. ދެން އެ ކަމުގައި ތިބާއަށް ތަބާވެއްޖެ ނަމަ، އެބައިމީހުންގެ މައްޗައް ކޮންމެ ރެއަކާއި ދުވާލު ފަސް ނަމާދެއް ﷲ ފަރުޟު ކުރެއްވިކަން އަންގާށެވެ. ދެން އެ ކަމުގައި ތިބާއަށް ތަބާވެއްޖެ ނަމަ، އެބައިމީހުންގެ މުއްސަންދިންގެ އަތުން ނެގޭ، އަދި އެބައިމީހުންގެ ފަޤީރުންނަށް ދެވޭ ޒަކާތެއް ﷲ އެބައިމީހުންގެ މައްޗަށް ފަރުޟު ކުރެއްވިކަން އަންގާށެވެ. ދެން އެ ކަމުގައި ތިބާއަށް ތަބާވެއްޖެއްޔާ، ނަމަނަމަ އެބައިމީހުންގެ އެންމެ މޮޅެތި މުދާތަކުން (ޒަކާތް) ނުނަގާށެވެ. އަދި އަނިޔާ ލިބިފައިވާ މީހާގެ (ބަދު) ދުޢާއިން ރައްކާތެރިވާށެވެ. އެހެނީ ހަމަކަށަވަރުން އެ ދުޢާއާއި ﷲއާ ދެމެދު އެއްވެސް ހުރަހެއް ނުވެއެވެ.“","البخاري 1496 واللفظ له، ومسلم 19"],["#177","كتاب الزكاة","ޒަކާތުގެ ފޮތް","","","عن أبي سعيد الخدري رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «لَيْسَ فِيمَا دُونَ خَمْسِ أَوَاقٍ صَدَقَةٌ. وَلا فِيمَا دُونَ خَمْسِ ذَوْدٍ صَدَقَةٌ. وَلا فِيمَا دُونَ خَمْسَةِ أَوْسُقٍ صَدَقَةٌ»","އަބޫ ސަޢީދުލް ޚުދްރީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”(މުދަލުގައި) ފަސް (ރިހި) އޫޤިޔާއަށް ވުރެ މަދުންވާ ނަމަ އޭގެ މައްޗަށް ޒަކާތެއް ނުވެއެވެ. އަދި (ޖަމަލު ގެންގުޅުމުގައި) ފަސް ޖަމަލަށް ވުރެ މަދުން ވާނަމަ އޭގެ މައްޗަށް ޒަކާތެއް ނުވެއެވެ. އަދި (ދަނޑުވެރިކަމުން އުފައްދާ ކާތަކެތީގައި) ފަސް ވަސަޤަށް ވުރެ މަދުން ވާނަމަ އޭގެ މައްޗަށް ޒަކާތެއް ނޯވެއެވެ.“ (ނޯޓު: މިއީ މުދަލުން ޒަކާތް ވާޖިބުވާ ނިޞާބުތަކެވެ. އޫޤިޔާއަކީ ސާޅީސް ދިރުހަމާ އެއްވަރުގެ މުދަލެވެ. އެބަހީ ދުއިސައްތަ ދިރުހަމު ނުވަތަ ފަސް ސަތޭކަ ނުވަދިހަ ފަސް ގްރާމްގެ ރިއްސަށްވުރެ މަދުންވާ މުދަލަށް ޒަކާތް ދައްކާކަށެއް ނުޖެހެއެވެ. އަދި ވަސަޤަކީ ފަސްދޮޅަސް ޞާޢެވެ. އެބަހީ ދަނޑުވެރިކަމުން އުފައްދާ ތަކެތިން ސަތޭކަ ޞާޢު ނުވަތަ އެއްހާސް ފަސް ސަތޭކަ ލީޓަރަށް ވުރެ މަދުންވާ ކާތަކެއްޗަށް ޒަކާތް ދައްކާކަށެއް ނުޖެހެއެވެ.)","البخاري 1405، ومسلم 979 واللفظ له"],["#178","كتاب الزكاة","ޒަކާތުގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ رَضِيَ اللَّهُ عَنْهُ أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «لَيْسَ عَلَى الْمُسْلِمِ فِي عَبْدِهِ وَلا فَرَسِهِ صَدَقَةٌ»\n<br><br>\nوَفِي لَفْظٍ: «إلاَّ زَكَاةَ الْفِطْرِ فِي الرَّقِيقِ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”މުސްލިމެއްގެ އަޅެއް ނުވަތަ އަހެއްގެ މައްޗަށް ދައްކަންޖެހޭ ޒަކާތެއް ނުވެއެވެ.“\n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”އެއީ އަޅުންގެ މަތިން ދައްކާ ފިޠުރު ޒަކާތް މެނުވީއެވެ.“","البخاري 1464، ومسلم 982 واللفظ له\n<br><br>\nأبو داود 1594"],["#179","كتاب الزكاة","ޒަކާތުގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «الْعَجْمَاءُ جُبَارٌ. وَالْبِئْرُ جُبَارٌ. وَالْمَعْدِنُ جُبَارٌ. وَفِي الرِّكَازِ الْخُمْسُ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”(ވެރިމީހާގެ އިހުމާލެއް ނެތި) ޖަނަވާރެއްގެ (ކިބައިން ގެއްލުމެއް ލިބިއްޖެ މީހަކަށް) ބަދަލުގައި އެއްޗެއް ދޭކަށެއް ނުޖެހެއެވެ. އަދި (އެހެން މީހެއްގެ ބިމެއްގައި ކޮނެފައިވާ) ވަޅަކަށް (ވެއްޓިއްޖެ މީހަކަށް) ބަދަލުގައި އެއްޗެއް ދޭކަށެއް ނުޖެހެއެވެ. އަދި މައުދަން ނެގުމުގެ (ތެރޭގައި ގެއްލުމެއް ލިބިއްޖެ މީހަކަށް) ބަދަލުގައި އެއްޗެއް ދޭކަށެއް ނުޖެހެއެވެ. އަދި ރިކާޒުގެ ފަސްބައި ކުޅަ އެއްބައި (މުދަލު ޒަކާތުގެ ގޮތުގައި) ދޭންޖެހޭނެއެވެ.“ (ނޯޓު: ރިކާޒަކީ ޖާހިލިއްޔަ ޒަމާނުގައި ވަޅުލެވިފައިވާ މުދަލެވެ.)","البخاري 1499، ومسلم 1710 واللفظ له"],["#180","كتاب الزكاة","ޒަކާތުގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «بَعَثَ رَسُولُ اللَّهِ ﷺ عُمَرَ - رَضِيَ اللَّهُ عَنْهُ - عَلَى الصَّدَقَةِ. فَقِيلَ: مَنَعَ ابْنُ جَمِيلٍ وَخَالِدُ بْنُ الْوَلِيدِ، وَالْعَبَّاسُ عَمُّ رَسُولِ اللَّهِ ﷺ. فَقَالَ رَسُولُ اللَّهِ ﷺ مَا يَنْقِمُ ابْنُ جَمِيلٍ، إلاَّ أَنْ كَانَ فَقِيراً: فَأَغْنَاهُ اللَّهُ؟ وَأَمَّا خَالِدٌ: فَإِنَّكُمْ تَظْلِمُونَ خَالِداً. وَقَدْ احْتَبَسَ أَدْرَاعَهُ وَأَعْتَادَهُ فِي سَبِيلِ اللَّهِ. وَأَمَّا الْعَبَّاسُ: فَهِيَ عَلَيَّ وَمِثْلُهَا. ثُمَّ قَالَ: يَا عُمَرُ، أَمَا شَعَرْتَ أَنَّ عَمَّ الرَّجُلِ صِنْوُ أَبِيهِ؟»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ޒަކާތް ބަލައިގަތުމަށް ޓަކައި ރަސޫލު ﷲ ﷺ ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ފޮނުއްވިއެވެ. ނަބިއްޔާއަށް ދެންނެވުނެވެ: އިބްނު ޖަމީލާއި، ޚާލިދު ބިން ވަލީދާއި، ރަސޫލު ﷲ ﷺ ގެ ބޮޑުބޭބޭފުޅު އަލްޢައްބާސް ޒަކާތް ދިނުމުން މަނާވެގަތެވެ. ރަސޫލު (ﷲ) ޙަދީޘް ކުރެއްވިއެވެ: ”އިބްނު ޖަމީލަށް އައިބު ކިޔޭ ހަމައެކަނި ކަމަކީ، އެކަލޭގެފާނު ފަޤީރު ޙާލުގައި ވަނިކޮށް، ﷲ އެކަލޭގެފާނު މުއްސަންދި ކުރެއްވިކަމެވެ. ދެން ޚާލިދުގެ (މައްސަލައިގައި)، ފަހެ ހަމަކަށަވަރުން ތިޔަބައިމީހުން ޚާލިދާމެދު (ބަސް ބުނީ) އިންސާފުވެރިކަމާ އެކީގައެއް ނޫނެވެ. އެހެނީ އެކަލޭގެފާނުގެ ހަނގުރާމައިގެ އާލާތްތަކާއި ތަކެތި ހިއްޕަވައިގެން ﷲ ގެ މަގުގައި ޖިހާދު ކުރެއްވިއެވެ. (އެބަހީ: އެއީ ވިޔަފާރިއަށް ގެންގުޅުއްވި ތަކެއްޗެއް ނޫނެވެ. ވީމާ އެއަށް ޒަކާތް ދައްކާކަށް ނުޖެހޭނެއެވެ.) ދެން އަލްޢައްބާސްގެ މައްސަލައިގައި)، ފަހެ އެކަލޭގެފާނުގެ އެ ޒަކާތާއި އޭގެ އިތުރު އަހަރެއްގެ ޒަކާތް( ދެއްކުން ތިމަން ނަބިއްޔާ ކަށަވަރުކޮށް ދެއްވާނަމެވެ.“ އެއަށްފަހު ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ ޢުމަރުގެފާނެވެ. މީހެއްގެ ބޮޑުބޭބެއަކީ އޭނާގެ ބައްޕަފަދަ މިހެއްކަން ނުދެނަހުރީމުތޯއެވެ؟“ (ނޯޓު: ނަބިއްޔާ އެހެން ޙަދީޘް ކުރެއްވީ ބޮޑުބޭބެފުޅުގެ މަގާމުގެ މަތިވެރިކަން ހާމަ ކުރައްވަންވެގެންނެވެ.)","البخاري 1468، ومسلم 983 واللفظ له"],["#181","كتاب الزكاة","ޒަކާތުގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ زَيْدِ بْنِ عَاصِمٍ قَالَ: «لَمَّا أَفَاءَ اللَّهُ عَلَى رَسُولِهِ يَوْمَ حُنَيْنٍ: قَسَمَ فِي النَّاسِ، وَفِي الْمُؤَلَّفَةِ قُلُوبُهُمْ وَلَمْ يُعْطِ الأَنْصَارَ شَيْئاً. فَكَأَنَّهُمْ وَجَدُوا فِي أَنْفُسِهِمْ، إذْ لَمْ يُصِبْهُمْ مَا أَصَابَ النَّاسَ. فَخَطَبَهُمْ، فَقَالَ: يَا مَعْشَرَ الأَنْصَارِ، أَلَمْ أَجِدْكُمْ ضُلاَّلاً فَهُدَاكُمْ اللَّهُ بِي؟ وَكُنْتُمْ مُتَفَرِّقِينَ فَأَلَّفَكُمْ اللَّهُ بِي؟ وَعَالَةً فَأَغْنَاكُمْ اللَّهُ بِي؟. كُلَّمَا قَالَ شَيْئاً، قَالُوا: اللَّهُ وَرَسُولُهُ أَمَنُّ. قَالَ: مَا يَمْنَعُكُمْ أَنْ تُجِيبُوا رَسُولَ اللَّهِ؟ قَالُوا: اللَّهُ وَرَسُولُهُ أَمَنُّ. قَالَ: لَوْ شِئْتُمْ لَقُلْتُمْ: جِئْتَنَا كَذَا وَكَذَا. أَلا تَرْضَوْنَ أَنْ يَذْهَبَ النَّاسُ بِالشَّاةِ وَالْبَعِيرِ، وَتَذْهَبُونَ بِرَسُولِ اللَّهِ إلَى رِحَالِكُمْ؟ لَوْلا الْهِجْرَةُ لَكُنْتُ امْرَأً مِنْ الأَنْصَارِ، وَلَوْ سَلَكَ النَّاسُ وَادِياً أَوْ شِعْباً لَسَلَكْتُ وَادِيَ الأَنْصَارِ وَشِعْبَهَا. الأَنْصَارُ شِعَارٌ، وَالنَّاسُ دِثَارٌ. إنَّكُمْ سَتَلْقَوْنَ بَعْدِي أَثَرَةً، فَاصْبِرُوا حَتَّى تَلْقَوْنِي عَلَى الْحَوْضِ»","ޢަބްދު ﷲ ބިން ޒައިދު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ޙުނައިނު ހަނގުރާމައިގެ ދުވަހު، ﷲ އެ އިލާހުގެ ރަސޫލާއަށް ޓަކައި ޣަނީމާ މުދާ ދެއްވި ހިނދު، ނަބިއްޔާ އެ މުދާ މީސްތަކުންގެ ތެރޭގައި ބެއްސެވިއެވެ. އެ ގޮތުން (އިސްލާމްކަމަށް ގާތްކުރުމާއި އީމާންކަން ވަރުގަދަ ކުރުމުގެ ގޮތުން) ހިތްތައް އެކުވެރި ކުރުމަށްޓަކައި ނަބިއްޔާ ބަޔަކަށް މުދާ ދެއްވިއެވެ. އެކަމަކު އަންޞާރުންނަށް އެއްވެސް އެއްޗެއް ނަބިއްޔާ ނުދެއްވައެވެ. ފަހެ މީސްތަކުންނަށް ލިބުނު އެއްޗެއް އަންޞާރުންނަށް ނުލިބުމުން، އެބައިމީހުންގެ ހިތްތަކުގައި (ހިތްހަމަ ނުޖެހުމެއް) ހުރިފަދައެވެ. ދެން ނަބިއްޔާ އެބައިމިހުންނާ މުޚާޠަބު ކުރައްވައި ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ އަންޞާރުންގެ ޖަމާޢަތުގެ މީހުންނޭވެ. ތިޔަބައިމީހުން މަގުފުރެދިފައި ވަނިކޮށް ތިމަން ނަބިއްޔާ ދެކެވަޑައި ނުގަންނަވަމުތޯއެވެ؟ ދެން ތިމަން ނަބިއްޔާ މެދުވެރިކޮށް ﷲ ތިޔަބައިމީހުންނަށް ހިދާޔަތު ދެއްކެވި ނޫންތޯއެވެ؟ އަދި ތިޔަބައިމީހުން ބައިބައިވެފައި ވަނިކޮށް، ތިމަން ނަބިއްޔާ މެދުވެރިކޮށް ﷲ ތިޔަބައިމީހުންގެ ހިތްތައް އެކުވެރި ކުރެއްވި ނޫންތޯއެވެ؟ އަދި ތިޔަބައިމީހުން ފަޤީރު ޙާލުގައި ވަނިކޮށް، ތިމަން ނަބިއްޔާ މެދުވެރިކޮށް ﷲ ތިޔަބައިމީހުން މުއްސަންދި ކުރެއްވި ނޫންވާތޯއެވެ؟“ ނަބިއްޔާ ބަސްފުޅެއް ވިދާޅުވި ކޮންމެ ފަހަރަކު، އެބައިމީހުން ދެންނެވިއެވެ: ﷲ އާއި އެ އިލާހުގެ ރަސޫލާ (ތިމަންމެންގެ މައްޗަށް) ދަރުމަވަންތަވެ ވޮޑިގެންފިއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲ ގެ ރަސޫލާއަށް އިޖާބަދިނުމުން ތިޔަބައިމިހުން މަނާ ކުރުވީ ކޮންކަމެއްތޯއެވެ؟“ އެބައިމީހުން ދެންނެވިއެވެ: ﷲ އާއި އެ އިލާހުގެ ރަސޫލާ (ތިމަންމެންގެ މައްޗަށް) ދަރުމަވަންތަވެ ވޮޑިގެންފިއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ތިޔަބައިމީހުން އެދުނު ނަމަ ބުނެވުނީހެވެ: އިބަ ނަބިއްޔާ ތިމަންމެންގެ ގާތަށް ވަޑައިގަތީ އެވެނި މިވެނި ޙާލެއްގައެވެ. ފަހެ މިސްތަކުން ކަނބަޅިތަކާއި ޖަމަލުތަކައިގެން އެނބުރިދާ ހިނދު، ﷲ ގެ ރަސޫލާއައިގެން ތިޔަބައިމިހުންގެ ގެތަކަށް ދެވޭކަމަށް ތިޔަބައިމީހުން ނުރުހެމުތޯއެވެ؟ އަދި ހިޖުރަ ކުރުން ނުވާ ނަމަ، އަންޞާރުންގެ ތެރެއިން މީހަކަށް ތިމަން ނަބިއްޔާ ވެވަޑައިގަތީމުހެވެ. އަދި ބޮޑު ނުވަތަ ކުޑަ ވާދީއެއްގެ ތެރެއިން މީސްތަކުން ހިނގައްޖެ ނަމަ، އަންޞާރުންގެ ބޮޑު ނުވަތަ ކުޑަ ވާދީގެ ތެރެއިންލާފައި ތިމަން ނަބިއްޔާ ވަޑައިގަތީމުހެވެ. އަންޞާރުންނަކީ ސީދާ ހަމުގެ މަތީގައި ލާ ހެދުމެވެ. އަދި އެހެން މީސްތަކުންނީ އެހެން ހެދުންތަކުގެ މަތީގައި ލާ ހެދުމެވެ. ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާއަށް ފަހުގައި، ތިޔަބައިމީހުންގެ މައްޗަށް އެހެން މިހުން އިސްކުރެވޭތަން ތިޔަބައިމީހުން ދެކޭނެއެވެ. ވީމާ (ޤިޔާމަތް ދުވަހު) ޙައުޟުގައި ތިމަން ނަބިއްޔާއާ ބައްދަލުވުމަށް ދާންދެން ކެތްތެރިވާށެވެ.“","البخاري 4330 واللفظ له، ومسلم 1061"],["#182","كتاب الزكاة","ޒަކާތުގެ ފޮތް","باب صدقة الفطر","ފިޠުރު ޒަކާތުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما قَالَ: «فَرَضَ رَسُولُ اللَّهِ ﷺ صَدَقَةَ الْفِطْرِ - أَوْ قَالَ رَمَضَانَ - عَلَى الذَّكَرِ وَالأُنْثَى وَالْحُرِّ وَالْمَمْلُوكِ: صَاعاً مِنْ تَمْرٍ، أَوْ صَاعاً مِنْ شَعِيرٍ. قَالَ: فَعَدَلَ النَّاسُ بِهِ نِصْفَ صَاعٍ مِنْ بُرٍّ، عَلَى الصَّغِيرِ وَالْكَبِيرِ»\n<br><br>\nوَفِي لَفْظٍ: «أَنْ تُؤَدَّى قَبْلَ خُرُوجِ النَّاسِ إلَى الصَّلاةِ»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ފިރިހެނުންނާއި އަންހެނުންނާއި، މިނިވަނުންނާއި އަޅުންނާއި، ކުޑަކުދިންނާއި ބޮޑެތިމީހުންގެ މައްޗަށް ފިޠުރު (ނުވަތަ ރަމަޟާނުގެ) ޒަކާތް ދެއްކުން ރަސޫލު ﷲ ﷺ ފަރުޟު ކުރެއްވިއެވެ. އެއީ ކަދުރު ނުވަތަ ހިމަގޮދަނުން ޞާޢެއްގެ މިންވަރެވެ. ދެން ގޮދަނުގެ ޒަކާތަކީ ޞާޢެއްގެ ދެބައިކުޅަ އެއްބައިކަމަށް މީސްތަކުން ހެއްދެވިއެވެ.\n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: މީސްތަކުން (ޢީދު) ނަމާދަށް ނުކުތުމުގެ ކުރިން ފިޠުރު ޒަކާތް ނެރެން (ފަރުޟު ކުރެވުނެވެ.)","البخاري 1151 واللفظ له، ومسلم 984\n<br><br>\nالبخاري 1503، ومسلم 986"],["#183","كتاب الزكاة","ޒަކާތުގެ ފޮތް","باب صدقة الفطر","ފިޠުރު ޒަކާތުގެ ބާބު","عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «كُنَّا نُعْطِيهَا فِي زَمَنِ ﷺ صَاعاً مِنْ طَعَامٍ، أَوْ صَاعاً مِنْ شَعِيرٍ، أَوْ صَاعاً مِنْ أَقِطٍ، أَوْ صَاعاً مِنْ زَبِيبٍ. فَلَمَّا جَاءَ مُعَاوِيَةُ، وَجَاءَتِ السَّمْرَاءُ، قَالَ: أَرَى مُدَّاً مِنْ هَذِهِ يَعْدِلُ مُدَّيْنِ.\n<br><br>\nقَالَ أَبُو سَعِيدٍ: أَمَّا أَنَا: فَلا أَزَالُ أُخْرِجُهُ كَمَا كُنْتُ أُخْرِجُهُ عَلَى عَهْدِ رَسُولِ اللَّهِ ﷺ»","އަބޫ ސަޢީދުލް ޚުދްރީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ގެ ޒަމާނުގައި، ކާބޯތަކެތިން ނުވަތަ ހިމަގޮދަނުން ނުވަތަ ޗީޒުން ނުވަތަ ހިކިމޭބިސްކަދުރުން ޞާޢެއްގެ މިންވަރަށް ފިޠުރު ޒަކާތް ތިމަންމެން ނެރުނީމެވެ. ދެން މުޢާވިޔާގެ (ޚަލީފާކަމުގެ ދުވަސްވަރުގައި) ގޮދަން އައުމާއެކީ އެކަލޭގެފާނު ވިދާޅުވިއެވެ: މީގެ މުއްދެއް ވަރު، (އެހެނިހެން ކާބޯތަކެތީގެ) ދެ މުއްދާ އެއްވަރުކަމަށް ތިމަން ދެކެމެވެ.\n<br><br>\nއަބޫ ސަޢީދު ވިދާޅުވިއެވެ: އެހެނެއްކަމަކު ތިމަންނަ (ދިރިހުރިހާ ހިނދަކު) މެދުކެނޑުމެއް ނެތި ފިޠުރު ޒަކާތް ނެރެނީ ރަސޫލު ﷲ ﷺ ގެ ޒަމާނުގައި ތިމަން ނެރުނު ގޮތަށެވެ.","البخاري 1508 واللفظ له، ومسلم 985\n<br><br>\nمسلم 985"],["#184","كتاب الصيام","ރޯދައިގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «لا تَقَدَّمُوا رَمَضَانَ بِصَوْمِ يَوْمٍ، أَوْ يَوْمَيْنِ إلاَّ رَجُلاً كَانَ يَصُومُ صَوْماً فَلْيَصُمْهُ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ރޯދަމަސް ފެށުމުގެ އެއް ދުވަސް ނުވަތަ ދެ ދުވަސް ކުރިން ރޯދަ ހިފަން (ނުފަށާށެވެ.) އެއީ (އާދައިގެ މަތިން އެ ދުވަސްތަކުގައި) ރޯދަ ހިފަމުން އައި މީހަކު މެނުވީއެވެ. ފަހެ އެ މީހާކު އެ ރޯދަ ހިފާށެވެ.“","البخاري 1914، ومسلم 1082 واللفظ له"],["#185","كتاب الصيام","ރޯދައިގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما قَالَ: سَمِعْتُ رَسُولَ اللَّهِ ﷺ يَقُولُ: «إذَا رَأَيْتُمُوهُ فَصُومُوا، وَإِذَا رَأَيْتُمُوهُ فَأَفْطِرُوا، فَإِنْ غُمَّ عَلَيْكُمْ فَاقْدُرُوا لَهُ»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ: ”ތިޔަބައިމީހުންނަށް (ރަމަޟާން މަހުގެ) ހަނދު ފެނުމުން ރޯދަ ހިފަން ފަށާށެވެ. ދެން (ޝައްވާލު މަހުގެ) ހަނދު ފެނުމުން ރޯދަ ހިފުން ދޫކޮށްލާށެވެ. އަދި ވިލާ ބޯވެގެން (ހަނދު ނުފެނިއްޖެ) ނަމަ ތިޔަބައިމީހުން އަދަދުކޮށް (ހަނދު ނުފެނުނު ކަމުގައި ކަނޑައަޅައި، ޝަޢުބާން މަހުގެ) ތިރީސް ދުވަސް ފުރިހަމަ ކުރާށެވެ.“","البخاري 1900، ومسلم 1080"],["#186","كتاب الصيام","ރޯދައިގެ ފޮތް","","","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ «تَسَحَّرُوا فَإِنَّ فِي السَّحُورِ بَرَكَةً»","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ހާރު ކާށެވެ. އެހެނީ ހަމަކަށަވަރުން ހާރު ކެއުމުގައި ބަރަކާތްލެއްވިގެން ވެއެވެ.“","البخاري 1923، ومسلم 1095"],["#187","كتاب الصيام","ރޯދައިގެ ފޮތް","","","عَنْ أَنَسِ بْنِ مَالِكٍ عَنْ زَيْدِ بْنِ ثَابِتٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: «تَسَحَّرْنَا مَعَ رَسُولِ اللَّهِ ﷺ. ثُمَّ قَامَ إلَى الصَّلاةِ. قَالَ أَنَسٌ: قُلْت لِزَيْدٍ: كَمْ كَانَ بَيْنَ الأَذَانِ وَالسَّحُورِ؟ قَالَ: قَدْرُ خَمْسِينَ آيَةً»","އަނަސް ބިން މާލިކު، ޒައިދު ބިން ޘާބިތު رَضِيَ اللَّهُ عَنْهُمَا ގެ އަރިހުން ރިވާ ކުރެއްވިއެވެ: ”ރަސޫލު ﷲ ﷺ ގެ އަރިހުގައި ތިމަންމެން ހާރު ކެއީމެވެ. އެއަށްފަހު ނަބިއްޔާ ނަމާދަށް ތެދުވެވަޑައިގަތެވެ. އަނަސް ވިދާޅުވިއެވެ: ތިމަން ޒައިދަށް ދެންނެވީމެވެ: ބަންގިއާއި ހާރު ކެއުމާ ދެމެދު ކިހާ ވަގުތެއް ވޭތުވިތޯއެވެ؟ ޒައިދު ދެންނެވިއެވެ: ފަންސާސް އާޔަތް ކިޔަވާހައި ވަގުތެވެ.","البخاري 1921 واللفظ له، ومسلم 1097"],["#188","كتاب الصيام","ރޯދައިގެ ފޮތް","","","عَنْ عَائِشَةَ وَأُمِّ سَلَمَةَ رَضِيَ اللَّهُ عَنْهُما: «أَنَّ رَسُولَ اللَّهِ ﷺ كَانَ يُدْرِكُهُ الْفَجْرُ وَهُوَ جُنُبٌ مِنْ أَهْلِهِ ثُمَّ يَغْتَسِلُ وَيَصُومُ»","ޢާއިޝާ އަދި އުންމު ސަލަމާ رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ އަނބިކަނބަލުންގެ އަރިހުން ޖުނުބުވެރިވެ ވަޑައިގެންވާ ޙާލުގައި ހުންނަވަނިކޮށް، ނަބިއްޔާއަށް ފަތިސް ވަގުތު ވަދެގެން އާދެއެވެ. ދެން ނަބިއްޔާ ހިނައިތާހިރުވެ ވަޑައިގެން ރޯދަ ހިއްޕަވައެވެ.","البخاري 1925، 1926 واللفظ له، ومسلم 1109"],["#189","كتاب الصيام","ރޯދައިގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ رَضِيَ اللَّهُ عَنْهُ عَنْ النَّبِيِّ ﷺ قَالَ: «مَنْ نَسِيَ وَهُوَ صَائِمٌ. فَأَكَلَ أَوْ شَرِبَ، فَلْيُتِمَّ صَوْمَهُ. فَإِنَّمَا أَطْعَمَهُ اللَّهُ وَسَقَاهُ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”މީހަކު ރޯދައަށް ހުއްޓައި ހަނދާން ނެތިގެން ކެވިއްޖެ ނުވަތަ ބޮވިއްޖެ ނަމަ، ފަހެ އެ މީހެއްގެ ރޯދަ ފުރިހަމަ ކުރާށެވެ. އެހެނީ ހަމަކަށަވަރުން އޭނާއަށް ކާންދެއްވައި ބޯންދެއްވީ ހަމަ ﷲ އެވެ.“","البخاري 1933، ومسلم 1155 واللفظ له"],["#190","كتاب الصيام","ރޯދައިގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «بَيْنَمَا نَحْنُ جُلُوسٌ عِنْدَ النَّبِيِّ ﷺ إذْ جَاءَهُ رَجُلٌ. فَقَالَ: يَا رَسُولَ اللَّهِ، هَلَكْتُ. قَالَ: مَا أَهْلَكَكَ؟ قَالَ: وَقَعْتُ عَلَى امْرَأَتِي، وَأَنَا صَائِمٌ - وَفِي رِوَايَةٍ: أَصَبْتُ أَهْلِي فِي رَمَضَانَ - فَقَالَ رَسُولُ اللَّهِ ﷺ: هَلْ تَجِدُ رَقَبَةً تُعْتِقُهَا؟ قَالَ: لا. قَالَ: فَهَلْ تَسْتَطِيعُ أَنْ تَصُومَ شَهْرَيْنِ مُتَتَابِعَيْنِ؟ قَالَ: لا. قَالَ: فَهَلْ تَجِدُ إطْعَامَ سِتِّينَ مِسْكِيناً؟ قَالَ: لا. قَالَ: فَمَكَثَ النَّبِيُّ ﷺ فَبَيْنَا نَحْنُ عَلَى ذَلِكَ أُتِيَ النَّبِيُّ ﷺ بِعَرَقٍ فِيهِ تَمْرٌ - وَالْعَرَقُ: الْمِكْتَلُ - قَالَ: أَيْنَ السَّائِلُ؟ قَالَ: أَنَا. قَالَ: خُذْ هَذَا، فَتَصَدَّقَ بِهِ. فَقَالَ الرَّجُلُ: عَلَى أَفْقَرَ مِنِّي: يَا رَسُولَ اللَّهِ؟ فَوَاَللَّهِ مَا بَيْنَ لابَتَيْهَا - يُرِيدُ الْحَرَّتَيْنِ - أَهْلُ بَيْتٍ أَفْقَرَ مِنْ أَهْلِ بَيْتِي. فَضَحِكَ رَسُولُ اللَّهِ - ﷺ حَتَّى بَدَتْ أَنْيَابُهُ. ثُمَّ قَالَ: أَطْعِمْهُ أَهْلَكَ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ގެ އަރިހުގައި ތިމަންމެން އިށީނދެ ތިއްބައި، މީހަކު އައިސް ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. ތިމަންނަ ހަލާކުވެއްޖެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ ހަލާކު ކުރުވީ ކޮންކަމެއްތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ: ތިމަންނަ ރޯދައަށް ހުއްޓައި، އަނބިމީހާއާއެކު އެއްދާން ކުރެވިއްޖެއެވެ. - އަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ރަމަޟާން މަހުގެ (ދުވާލު ގަޑީގައި) ތިމަންނަގެ އަނބިމީހާއާއެކު އެއްދާން ކުރެވިއްޖެއެވެ. - ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާއަށް މިނިވަން ކުރާނެ އަޅަކު ވޭތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ: ނޫނެކެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ޖެހިޖެހިގެން ދެ މަސްދުވަހު ރޯދަހިފުމުގެ ކުޅަދާނަކަން ލިބިގެން ވޭތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ: ނޫނެކެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ފަސްދޮޅަސް މިސްކީނުންނަށް ކާންދެވިދާނެތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ: ނޫނެކެވެ. ދެން ނަބިއްޔާ ﷺ މަޑުކޮށްލައްވައިގެން ހުންނެވިއެވެ. ފަހެ ތިމަންމެން އެ ގޮތުގެ މަތީގައި ވަނިކޮށް، ކަދުރު އަޅާފައި ހުރި ޢަރަޤެއް (އެބަހީ: މިނެކިރާ ކަންވާރެއް) ނަބިއްޔާގެ އަރިހަށް ގެނެވުނެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެއްކަލަ ސުވާލު ކުރި މީހާ ކޮބައިތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ: ތިމަންނަ (މިހުރީއެވެ.) ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”މި (ކަދުރުތައް) ނަގާފައި އޭގެން ޞަދަޤާތް ކުރާށެވެ.“ އޭނާ ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. (ޞަދަޤާތް ކުރަންވީ) ތިމަންނައަށް ވުރެ ފަޤީރު މީހުންނަށްތޯއެވެ؟ އެހެނީ ﷲ ގަންދީ ބުނަމެވެ. މަދީނާގެ ދެ ޙައްރާ ދެމެދުގައި ތިމަންނަގެ ގޭގެ އަހުލުވެރިންނަށް ވުރެ ފަޤީރު އެހެން ގެއެއްގެ ބައެއް ނުވެއެވެ. (ނޯޓު: ދެ ޙައްރާއަކީ މަދީނާގެ ހުޅަނގާއި އިރުމަތީގައިވާ ލާވާ ހިކިފައި ހުންނަ ދެ ބިންގަނޑެވެ.) ދެން ފޭރާދަތްކޮޅު ފެންނަ ވަރަށް ރަސޫލު ﷲ ﷺ ހިނިވަރުނަ ކުރެއްވިއެވެ. އަދި ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާގެ އަހުލުވެރިންނަށް އޭގެން ކާންދޭށެވެ.“","البخاري 1936 واللفظ له، ومسلم 111"],["#191","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا: «أَنَّ حَمْزَةَ بْنَ عَمْرٍو الأَسْلَمِيَّ قَالَ لِلنَّبِيِّ ﷺ: أَأَصُومُ فِي السَّفَرِ؟ - وَكَانَ كَثِيرَ الصِّيَامِ - فَقَالَ: إنْ شِئْتَ فَصُمْ وَإِنْ شِئْتَ فَأَفْطِرْ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ޙަމްޒާ ބިން ޢަމްރު އަލްއަސްލަމީ، ނަބިއްޔާ ﷺ ގެ އަރިހުގައި ދެންނެވިއެވެ: ދަތުރުމަތީގައި ތިމަންނަ ރޯދަ ހިފަންތޯއެވެ؟ - އަދި ޙަމްޒާއަކީ ވަރަށް ގިނައިން ރޯދަ ހިފާ އުޅުނު ބޭކަލެކެވެ. - ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ ބޭނުން ނަމަ ރޯދަ ހިފާށެވެ. އަދި ބޭނުން ނަމަ ރޯދަ ހިފުން ދޫކުރާށެވެ.“","البخاري 1934 واللفظ له، ومسلم 1121"],["#192","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «كُنَّا نُسَافِرُ مَعَ النَّبِيِّ ﷺ فَلَمْ يَعِبِ الصَّائِمُ عَلَى الْمُفْطِرِ. وَلا الْمُفْطِرُ عَلَى الصَّائِمِ»","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ގެ އަރިހުގައި ތިމަންމެން ދަތުރުކޮށް ހެދީމެވެ. ފަހެ ރޯދާ ހިފާ މީހަކު، ރޯދަ ދޫކޮށްލާ މީހަކަށް އައިބު ނުކިޔައެވެ. އަދި ރޯދަ ދޫކޮށްލި މީހަކު، ރޯދަ ހިފާ މީހަކަށް އައިބު ނުކިޔައެވެ.","البخاري 1947 واللفظ له، ومسلم 1118"],["#193","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ أَبِي الدَّرْدَاءِ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «خَرَجْنَا مَعَ رَسُولِ اللَّهِ ﷺ فِي شَهْرِ رَمَضَانَ. فِي حَرٍّ شَدِيدٍ، حَتَّى إنْ كَانَ أَحَدُنَا لَيَضَعُ يَدَهُ عَلَى رَأْسِهِ مِنْ شِدَّةِ الْحَرِّ. وَمَا فِينَا صَائِمٌ إلاَّ رَسُولُ اللَّهِ ﷺ وَعَبْدُ اللَّهِ بْنُ رَوَاحَةَ»","އަބުއް ދަރްދާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަމަޟާން މަހު ވަރަށް ގަދަ ހޫނުގައި، ރަސޫލު ﷲ ﷺ ގެ އަރިހު ތިމަންމެން (ދަތުރެއްގައި) ނުކުތީމެވެ. ހޫނުވެގެން ގޮސް ތިމަންމެން ކުރެ މީހަކު އޭނަގެ އަތް ބޯމަތީ އަޅައިގެން އުޅުނެވެ. އަދި ތިމަންމެންގެ ތެރޭގައި ރަސޫލު ﷲ ﷺ އާއި ޢަބްދު ﷲ ބިން ރަވާޙާ ފިޔަވައި ރޯދަވެރިއަކު ނެތެވެ.","البخاري 1945، ومسلم 1122 واللفظ له"],["#194","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رَضِيَ اللَّهُ عَنْهُما قَالَ: «كَانَ رَسُولُ اللَّهِ ﷺ فِي سَفَرٍ. فَرَأَى زِحَاماً وَرَجُلاً قَدْ ظُلِّلَ عَلَيْهِ، فَقَالَ: مَا هَذَا؟ قَالُوا: صَائِمٌ. قَالَ: لَيْسَ مِنْ الْبِرِّ الصِّيَامُ فِي السَّفَرِ»\n<br><br>\nوَفِي لَفْظٍ لِمُسْلِمٍ: «عَلَيْكُمْ بِرُخْصَةِ اللَّهِ الَّتِي رَخَّصَ لَكُمْ»","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ދަތުރުފުޅެއް މަތީގައި ވަނިކޮށް، މީސްތަކުން ބޮނޑިވެގެން ތިބި ތަނާއި، މީހަކަށް ހިޔާއެޅިފައި ވަނިކޮށް ނަބިއްޔާ ދެކެވަޑައިގަތެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”މިއީ ކޮންކަމެއްތޯއެވެ؟“ އެބައިމީހުން ދެންނެވިއެވެ: އެއީ ރޯދަވެރިއެކެވެ. ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ދަތުރު މަތީގައި ރޯދައަށް ހުރުމަކީ ހެޔޮކަމުގެ ތެރެއިންވާ ކަމެއް ނޫނެވެ.“ (އެބަހީ: ދަތުރު ކުރާއިރު، މިހާ ގަދަ ހޫނާއި ދަތިކަމާއި އުނދަގޫކަން ތަހައްމަލު ކުރަންޖެހޭ ޙާލުވެސް، ރޯދަ ހިފުމަކީ ކުއްތަންވުމުގެ ތެރެއިންވާ ކަމެއް ނޫނެވެ.) \n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތުގައި ވެއެވެ: ”ތިޔަބައިމީހުންގެ މައްޗަށް އޮތީ، ތިޔަބައިމީހުންނަށް ޓަކައި ﷲ ފަސޭހަ ކޮށްދެއްވި ލުއިފަސޭހަކަމުގައި ހިފުމެވެ.“ (އެބަހީ: ދަތުރު މަތީގައި ރޯދަ ދޫކޮށްލުމެވެ.)","البخاري 1946 واللفظ له، ومسلم 1115\n<br><br>\nمسلم 1115"],["#195","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «كُنَّا مَعَ النَّبِيِّ ﷺ فِي السَّفَرِ فَمِنَّا الصَّائِمُ، وَمِنَّا الْمُفْطِرُ قَالَ: فَنَزَلْنَا مَنْزِلاً فِي يَوْمٍ حَارٍّ، وَأَكْثَرُنَا ظِلاً: صَاحِبُ الْكِسَاءِ. وَمِنَّا مَنْ يَتَّقِي الشَّمْسَ بِيَدِهِ. قَالَ: فَسَقَطَ الصُّوَّامُ، وَقَامَ الْمُفْطِرُونَ فَضَرَبُوا الأَبْنِيَةَ. وَسَقَوْا الرِّكَابَ، فَقَالَ رَسُولُ اللَّهِ ﷺ: ذَهَبَ الْمُفْطِرُونَ الْيَوْمَ بِالأَجْرِ»","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ގެ އަރިހު ތިމަންމެން ދަތުރެއް ކުރީމެވެ. އަދި ތިމަންމެންގެ ތެރޭގައި ރޯދަވެރިންނާއި ރޯދަ ދޫކުރި މީހުން ވިއެވެ. ފަހެ ހޫނުގަދަ ދުވަހެއްގައި ތިމަންމެން ތަނެއްގައި މަޑު ކުރީމެވެ. ތިމަންމެންގެ ތެރެއިން އެންމެ ބޮޑަށް ހިޔާވަހިކަން ލިބުނީ ފޭރާން (ބޭނުންކޮށްގެން ހިޔާވަހިކަން ހޯދައިގަތް) ބަޔެކެވެ. އަދި އިރުގެ އަވިން ސަލާމަތް ވުމަށް އެ މީހެއްގެ އަމިއްލަ އަތުން ހިޔާވަހިކަން ހޯދި ބަޔަކުވެސް ތިމަންމެންގެ ތެރޭގައި ވިއެވެ. ފަހެ ރޯދަވެރިން (ވަރުބަލިވެގެން) ވެއްޓި އޮށޯތެވެ. އަދި ރޯދަ ދޫކުރި މީހުން ތެދުވެ ފޭލިގެތައް ޖަހައި، ސަވާރީތަކަށް ފެން ދިނެވެ. ދެން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”މިއަދު (ހުރިހާ) އަޖުރެއް ހޯދައިގެން ދިޔައީ ރޯދަ ދޫކޮށްލި މީހުންނެވެ.“","البخاري 2890، ومسلم 1119 واللفظ له"],["#196","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: «كَانَ يَكُونُ عَلَيَّ الصَّوْمُ مِنْ رَمَضَانَ، فَمَا أَسْتَطِيعُ أَنْ أَقْضِيَ إلاَّ فِي شَعْبَانَ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ރަމަޟާން މަހުގައި ނުހިފި ހުރި ރޯދަތަކެއް ތިމަން ކަމަނާގެ މައްޗަށް ވިއެވެ. ފަހެ ޝަޢުބާން މަހުގައި މެނުވީ އެ ރޯދަތައް ތިމަން ކަމަނާއަށް ޤަޟާއެއް ނުކުރެވުނެވެ.“","البخاري 1950، ومسلم 1146"],["#197","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «مَنْ مَاتَ وَعَلَيْهِ صِيَامٌ صَامَ عَنْهُ وَلِيُّهُ»\n<br><br>\nوَأَخْرَجَهُ أَبُو دَاوُد وَقَالَ «هَذَا فِي النَّذْرِ، وَهُوَ قَوْلُ أَحْمَدَ بْنِ حَنْبَلٍ».","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ޙަދީޘް ކުރެއްވިއެވެ: ”ނުހިފި ހުރި ރޯދަތަކެއް މީހެއްގެ މައްޗަށް ވަނިކޮށް އޭނާ ނިޔާވެއްޖެ ނަމަ، ފަހެ އޭނާގެ ވާރުތަވެރިން އެ މީހެއްގެ މަތިން ރޯދަ ހިފާށެވެ.“ \n<br><br>\nއަދި އަބޫ ދާވޫދު މި ޙަދީޘް ރިވާ ކުރެއްވުމަށް ފަހު ވިދާޅުވިއެވެ: މިއީ ނަދުރުގައިވާ (ޚާއްޞަ ޙުކުމެކެވެ.) އަދި އެއީ އަޙްމަދު ބިން ޙަންބަލްގެ ބަސްވެސް މެއެވެ.","البخاري 1952، ومسلم 1147\n<br><br>\nأبو داود 2400"],["#198","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: «جَاءَ رَجُلٌ إلَى النَّبِيِّ ﷺ فَقَالَ: يَا رَسُولَ اللَّهِ، إنَّ أُمِّي مَاتَتْ وَعَلَيْهَا صَوْمُ شَهْرٍ. أَفَأَقْضِيهِ عَنْهَا؟ فَقَالَ: لَوْ كَانَ عَلَى أُمِّكَ دَيْنٌ أَكُنْتَ قَاضِيَهُ عَنْهَا؟ قَالَ: نَعَمْ. قَالَ: فَدَيْنُ اللَّهِ أَحَقُّ أَنْ يُقْضَى»\n<br><br>\nوَفِي رِوَايَةٍ: «جَاءَتِ امْرَأَةٌ إلَى رَسُولِ اللَّهِ ﷺ فَقَالَتْ: يَا رَسُولَ اللَّهِ، إنَّ أُمِّي مَاتَتْ وَعَلَيْهَا صَوْمُ نَذْرٍ. أَفَأَصُومُ عَنْهَا؟ فَقَالَ: أَرَأَيْتِ لَوْ كَانَ عَلَى أُمِّكِ دَيْنٌ فَقَضَيْتِيهِ، أَكَانَ ذَلِكَ يُؤَدِّي عَنْهَا؟ فَقَالَتْ: نَعَمْ. قَالَ: فَصُومِي عَنْ أُمِّكِ»","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ އަރިހަށް މީހަކު އައިސް ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން ތިމަންނަގެ މަންމަގެ މައްޗަށް މަސްދުވަހެއްގެ ރޯދަ ހިފުން ވަނިކޮށް، އެ ކަމަނާ ނިޔާވެއްޖެއެވެ. ފަހެ އެ ކަމާނާގެ މަތިނތިމަންނަ ރޯދަ ޤަޟާކުރަންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާގެ މަންމަގެ މައްޗަށް ދަރަންޏެއް ވާނަމަ، އެ ކަމަނާގެ މަތިން ތިބާ ދަރަނި އަދާ ކުރާނަންތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ: އާދޭހެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ފަހެ ﷲ ގެ ދަރަނި އަދާކުރުން މާ ޙައްޤުކަން ބޮޑެވެ.“ \n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ރަސޫލު ﷲ ﷺ ގެ އަރިހަށް އަންހެނަކު އައިސް ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ: ހަމަކަށަވަރުން ތިމަންނަގެ މަންމަ ނަދުރު ރޯދަތަކެއް ހިފަން ޖެހިފައިވަނިކޮށް، އެ ކަމަނާ ނިޔާވެއްޖެއެވެ. ފަހެ އެ ކަމާނާގެ މަތިން ތިމަންނަ ރޯދަ ހިފަންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާގެ މަންމަގެ މައްޗަށް ދަރަންޏެއް ވަނިކޮށް، ތިބާ އެ ދަރަނި އަދާ ކޮށްފިކަމުގައި ވާނަމަ، ފަހެ އެކަމުން މަންމަގެ ދަރަނި އަދާވާނެކަމަށް ތިބާ ދެކެމުތޯއެވެ؟“ އެ ކަމަނާ ދެންނެވިއެވެ: އާދޭހެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ފަހެ ތިބާގެ މަންމަގެ މަތިން ރޯދަ ހިފާށެވެ.“","البخاري 1593، ومسلم 1148 واللفظ له\n<br><br>\nمسلم 1148"],["#199","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ سَهْلِ بْنِ سَعْدٍ السَّاعِدِيِّ - رَضِيَ اللَّهُ عَنْهُ - أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «لا يَزَالُ النَّاسُ بِخَيْرٍ مَا عَجَّلُوا الْفِطْرَ»","ސަހުލު ބިން ސަޢުދު އައްސާޢިދީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”މީސްތަކުން ރޯދަ ވީއްލުން އަވަސް ކުރާހައި ހިނދަކު، ހެޔޮކަމުގެ މަތީގައި އެބައިމީހުން މެދުނުކެނޑި ދެމިއޮންނާނެއެވެ.“","البخاري 1957، ومسلم 1098"],["#200","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ عُمَرَ بْنِ الْخَطَّابِ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «إذَا أَقْبَلَ اللَّيْلُ مِنْ هَهُنَا. وَأَدْبَرَ النَّهَارُ مِنْ هَهُنَا: فَقَدْ أَفْطَرَ الصَّائِمُ»","ޢުމަރު ބިން އަލްޙައްޠާބު ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ރޭގަނޑު މި ފަރާތުން (އިރުމަތިން) ވަދެ، އަދި ދުވާލު މި ފަރާތުން (ހުޅަނގުން) އޮއްސިއްޖެ ހިނދު، ފަހެ ރޯދަވެރިޔާ ރޯދަ ވީއްލާށެވެ.“","البخاري 1954 واللفظ له، ومسلم 1100"],["#201","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما قَالَ: «نَهَى رَسُولُ اللَّهِ ﷺ عَنْ الْوِصَالِ. قَالُوا: إنَّكَ تُوَاصِلُ. قَالَ: إنِّي لَسْتُ كَهَيْئَتِكُمْ، إنِّي أُطْعَمَ وَأُسْقَى»\n<br><br>\nوَرَوَاهُ أَبُو هُرَيْرَةَ وَعَائِشَةُ وَأَنَسُ بْنُ مَالِكٍ.","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ވިޞާލު ކުރުން (އެބަހީ: އެއް ރޯދަ އަނެއް ރޯދައާ ދެމެދު ކެއުމެއް ބުއިމެއް ނެތި ގުޅުވުން) ރަސޫލު ﷲ ﷺ ނަހީ ކުރެއްވިއެވެ. މީސްތަކުން ދެންނެވިއެވެ: ހަމަކަށަވަރުން އިބަ ނަބިއްޔާ ވިޞާލު ކުރައްވައެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާއީ ތިޔަބައިމީހުން ފަދަ ބޭކަލެއް ނޫނެވެ. ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާއަށް (ﷲ ގެ ޙަޟްރަތުން) ކާންދެއްވައި ބޯންދެއްވެއެވެ.“\n<br><br>\nއަދި އަބޫ ހުރައިރާއާއި ޢާއިޝާއާއި އަނަސް ބިން މާލިކު ވެސް މި ޙަދީޘް ރިވާ ކުރެއްވިއެވެ.","البخاري 1972، ومسلم 1102\n<br><br>\nالبخاري 1965، 1964، 1961، ومسلم 1103، 1105، 1104"],["#202","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","وَلِمُسْلِمٍ عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ - رَضِيَ اللَّهُ عَنْهُ -: «فَأَيُّكُمْ أَرَادَ أَنْ يُوَاصِلَ فَلْيُوَاصِلْ إلَى السَّحَرِ»","އަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: އަބޫ ސަޢީދުލް ޚުދްރީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ ތެރެއިން މީހަކު ވިޞާލު ކުރަން ބޭނުން ނަމަ، ފަހެ ހާރު ކެއުމާ ހަމައަށް ކުރާށެވެ.“","البخاري 1963"],["#203","كتاب الصيام","ރޯދައިގެ ފޮތް","باب أفضل الصيام وغيره","އެންމެ ހެޔޮވެގެންވާ ރޯދައާއި އެހެނިހެން ކަންކަތައްތަކުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَمْرِو بْنِ الْعَاصِ رَضِيَ اللَّهُ عَنْهُما قَالَ: «أُخْبِرَ رَسُولُ اللَّهِ ﷺ أَنِّي أَقُولُ: وَاَللَّهِ لأَصُومَنَّ النَّهَارَ، وَلأَقُومَنَّ اللَّيْلَ مَا عِشْتُ. فَقَالَ رَسُولُ اللَّهِ ﷺ: أَنْتَ الَّذِي قُلْتَ ذَلِكَ؟ فَقُلْتُ لَهُ: قَدْ قُلْتُهُ، بِأَبِي أَنْتَ وَأُمِّي. فَقَالَ: فَإِنَّكَ لا تَسْتَطِيعُ ذَلِكَ. فَصُمْ وَأَفْطِرْ، وَقُمْ وَنَمْ. وَصُمْ مِنْ الشَّهْرِ ثَلاثَةَ أَيَّامٍ فَإِنَّ الْحَسَنَةَ بِعَشْرِ أَمْثَالِهَا. وَذَلِكَ مِثْلُ صِيَامِ الدَّهْرِ. قُلْتُ: فَإِنِّي أُطِيقُ أَفْضَلَ مِنْ ذَلِكَ. قَالَ: فَصُمْ يَوْماً وَأَفْطِرْ يَوْمَيْنِ. قُلْتُ: أُطِيقُ أَفْضَلَ مِنْ ذَلِكَ. قَالَ: فَصُمْ يَوْماً وَأَفْطِرْ يَوْماً. فَذَلِكَ مِثْلُ صِيَامِ دَاوُد. وَهُوَ أَفْضَلُ الصِّيَامِ. فَقُلْتُ: إنِّي أُطِيقُ أَفْضَلَ مِنْ ذَلِكَ. قَالَ: لا أَفْضَلَ مِنْ ذَلِكَ»\n<br><br>\nوَفِي رِوَايَةٍ: «لا صَوْمَ فَوْقَ صَوْمِ أَخِي دَاوُد - شَطْرَ الدَّهْرِ - صُمْ يَوْماً وَأَفْطِرْ يَوْماً»\n","ޢަބްދު ﷲ ބިން ޢަމްރު ބިން އަލްޢާޞް رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ހަމަކަށަވަރުން މިފަދައިން ތިމަންނަ ދެންނެވިކަމަށް ރަސޫލު ﷲ ﷺ އަށް ޚަބަރު ލިބިވަޑައިގަތެވެ: ﷲ ގަންދީ ބުނަމެވެ. ހަމަކަށަވަރުން ތިމަންނަ ދިރިހުރިހާ ހިނދަކު، ދުވާލު ރޯދަ ހިފައި، ރޭގަނޑު ރޭއަޅުކަން ކުރާނަމެވެ. ފަހެ ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އެފަދައިން ދެންނެވީ ތިބާތޯއެވެ؟“ ނަބިއްޔާއަށް ތިމަން ދެންނެވީމެވެ: ތިމަންނަގެ ބައްޕައާއި މަންމަ އިބަ ނަބިއްޔާއަށް ފިދާއެވެ. ހަމަކަށަވަރުން އެފަދައިން ތިމަން ބުނީމެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ތިބާއަކަށް ތިކަމެއް ކުޅަދާނައެއް ނުވާނެއެވެ. ފަހެ ތިބާ ރޯދަ ހިފާށެވެ. އަދި ރޯދަ ނުހިފާ ދޫވެސް ކުރާށެވެ. އަދި ރޭއަޅުކަން ކުރާށެވެ. އަދި ނިދާށެވެ. އަދި މަހެއްގައި ތިން ދުވަހު ރޯދަ ހިފާށެވެ. އެހެނީ ހަމަކަށަވަރުން ކޮންމެ ހެޔޮކަމަކަށް އެއާ އެއްފަދަ ދިހަ ހެޔޮކަމެއް ލިބިގެންވެއެވެ. އެ ގޮތަށް މުޅި އަހަރުދުވަސް ރޯދަ ހިފުމާ އެއްފަދަ ވާނެއެވެ.“ ތިމަން ދެންނެވީމެވެ: ހަމަކަށަވަރުން ތިއަށްވުރެ ރަނގަޅަށް (ރޯދަ ހިފުމަށް) ތިމަންނަ ކުޅަދާނަވެގެންވެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ފަހެ އެއް ދުވަހު ރޯދަ ހިފާފައި އަނެއް ދެ ދުވަހު ރޯދަ ނުހިފާ ދޫކުރާށެވެ.“ ތިމަން ދެންނެވީމެވެ: ތިއަށްވުރެ ރަނގަޅަށް (ރޯދަ ހިފުމަށް) ތިމަންނަ ކުޅަދާނަވެގެންވެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ފަހެ އެއް ދުވަހު ރޯދަ ހިފާފައި އަނެއް ދުވަހު ރޯދަ ނުހިފާ ދޫކުރާށެވެ. އެ ގޮތަށް ދާވޫދުގެފާނުގެ ރޯދައާ އެއްފަދަ ވާނެއެވެ. އަދި އެންމެ ހެޔޮވެގެންވާ ރޯދައަކީ އެއީއެވެ.“ ތިމަން ދެންނެވީމެވެ: ހަމަކަށަވަރުން ތިއަށްވުރެ ރަނގަޅަށް (ރޯދަ ހިފުމަށް) ތިމަންނަ ކުޅަދާނަވެގެން ވެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެއަށްވުރެ ރަނގަޅު (މޮޅުއިތުރު) ގޮތެއް ނުވެއެވެ.“\n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”ތިމަން ނަބިއްޔާގެ އަޚާ، ދާވޫދުގެފާނުގެ ރޯދައަށް ވުރެ މަތިވެރި (ސުންނަތް) ރޯދައެއް ނުވެއެވެ. އެއި އަހަރުގެ ދެބައިކުޅަ އެއްބައި (ރޯދަ ހިފުމެވެ.) ތިބާ އެއް ދުވަހު ރޯދަ ހިފާފައި އަނެއް ދުވަހު ރޯދަ ނުހިފާ ދޫކުރާށެވެ.“","البخاري 1976 واللفظ له، ومسلم 1159\n<br><br>\nالبخاري 1980 واللفظ له، ومسلم 1159"],["#204","كتاب الصيام","ރޯދައިގެ ފޮތް","باب أفضل الصيام وغيره","އެންމެ ހެޔޮވެގެންވާ ރޯދައާއި އެހެނިހެން ކަންކަތައްތަކުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَمْرِو بْنِ الْعَاصِ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «إنَّ أَحَبَّ الصِّيَامِ إلَى اللَّهِ صِيَامُ دَاوُد. وَأَحَبَّ الصَّلاةِ إلَى اللَّهِ صَلاةُ دَاوُد. كَانَ يَنَامُ نِصْفَ اللَّيْلِ، وَيَقُومُ ثُلُثَهُ. وَيَنَامُ سُدُسَهُ. وَكَانَ يَصُومُ يَوْماً وَيُفْطِرُ يَوْماً»","ޢަބްދު ﷲ ބިން ޢަމްރު ބިން އަލްޢާޞް رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ﷲ ގެ ޙަޟްރަތުގައި އެންމެ ލޮބުވެތިކަން ބޮޑު (ސުންނަތް) ރޯދައަކީ ދާވޫދޫގެފާނުގެ ރޯދައެވެ. އަދި ﷲ ގެ ޙަޟްރަތުގައި އެންމެ ލޮބުވެތިކަން ބޮޑު (ސުންނަތް) ނަމާދަކީ ދާވޫދޫގެފާނުގެ ނަމާދެވެ. ރޭގަނޑުގެ ދެބައި ކުޅަ އެއްބައިގައި ދާވޫދުގެފާނު އަވަހާރަފުޅު ލައްޕަވައެވެ. އަދި އެ ރޭގެ ތިންބައި ކުޅަ އެއްބައިގައި ރޭއަޅުކަން ކުރައްވައެވެ. ދެން އެ ރޭގެ ހަބައިކުޅަ އެއްބައިގައި އަނެއްކާވެސް އަވަހާރަފުޅު ލައްޕަވައެވެ. އަދި އެއް ދުވަހު ރޯދަ ހިއްޕަވާ އަނެއް ދުވަހު ރޯދަ ނުހިއްޕަވާ ދޫކުރައްވައެވެ.“","البخاري 1131، ومسلم 1159 واللفظ له"],["#205","كتاب الصيام","ރޯދައިގެ ފޮތް","باب أفضل الصيام وغيره","އެންމެ ހެޔޮވެގެންވާ ރޯދައާއި އެހެނިހެން ކަންކަތައްތަކުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «أَوْصَانِي خَلِيلِي ﷺ بِثَلاثٍ صِيَامِ ثَلاثَةِ أَيَّامٍ مِنْ كُلِّ شَهْرٍ، وَرَكْعَتَيْ الضُّحَى، وَأَنْ أُوتِرَ قَبْلَ أَنْ أَنَامَ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ތިމަންނަގެ ގާތް މިތުރު (ނަބިއްޔާ) ﷺ ތިމަންނައަށް ތިން ކަމަކަށް ވަޞިއްޔަތް ކުރެއްވީއެވެ. އެއީ ކޮންމެ މަހެއްގެ ތިން ދުވަހު ރޯދަ ހިފުމަށާއި، ޟުޙާ ނަމާދުގެ ދެ ރަކުޢަތް ކުރުމަށާއި، ތިމަންނަ ނިދުމުގެ ކުރިން ވިތުރި ކުރުމަށެވެ.","البخاري 1981 واللفظ له، ومسلم 721"],["#206","كتاب الصيام","ރޯދައިގެ ފޮތް","باب أفضل الصيام وغيره","އެންމެ ހެޔޮވެގެންވާ ރޯދައާއި އެހެނިހެން ކަންކަތައްތަކުގެ ބާބު","عَنْ مُحَمَّدِ بْنِ عَبَّادِ بْنِ جَعْفَرٍ قَالَ: «سَأَلْتُ جَابِرَ بْنَ عَبْدِ اللَّهِ أَنَهَى النَّبِيُّ ﷺ عَنْ صَوْمِ يَوْمِ الْجُمُعَةِ؟ قَالَ: نَعَمْ»\n<br><br>\nوَزَادَ مُسْلِمٌ «وَرَبِّ الْكَعْبَةِ»","މުޙައްމަދު ބިން ޢައްބާދު ބިން ޖަޢުފަރު ރިވާ ކުރެއްވިއެވެ: ޖާބިރު ބިން ޢަބްދު ﷲ ގެ އަރިހުގައި ތިމަން ސުވާލު ކުރީމެވެ: ހުކުރު ދުވަހު ރޯދަ ހިފުން ނަބިއްޔާ ﷺ ނަހީ ކުރެއްވިތޯއެވެ؟ ޢަބްދު ﷲ ދެންނެވިއެވެ: އާދޭހެވެ.\n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތުގައި (އެކަލޭގެފާނު އިތުރަށް ދެންނެވިކަމަށް) ރިވާވެގެން ވެއެވެ: ކަޢުބާގެ ވެރި ރައްބު ގަންދީ ބުނަމެވެ.","البخاري 1984 واللفظ له، ومسلم 1143\n<br><br>\nومسلم 1143"],["#207","كتاب الصيام","ރޯދައިގެ ފޮތް","باب أفضل الصيام وغيره","އެންމެ ހެޔޮވެގެންވާ ރޯދައާއި އެހެނިހެން ކަންކަތައްތަކުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: سَمِعْتُ رَسُولَ اللَّهِ ﷺ يَقُولُ: «لا يَصُومَنَّ أَحَدُكُمْ يَوْمَ الْجُمُعَةِ، إلاَّ أَنْ يَصُومَ يَوْماً قَبْلَهُ، أَوْ يَوْماً بَعْدَهُ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ: ”ތިޔަބައިމީހުން ކުރެ އެކަކުވެސް ހުކުރު ދުވަހު ރޯދަ ހިފައިގެން ނުވާނެއެވެ. އެއީ އޭގެ ކުރިއަށް ނުވަތަ ފަހަތަށް ދުވަހެއް ރޯދަ ހިފާނަމަ މެނުވީއެވެ.“","البخاري 1985، ومسلم 1144"],["#208","كتاب الصيام","ރޯދައިގެ ފޮތް","باب أفضل الصيام وغيره","އެންމެ ހެޔޮވެގެންވާ ރޯދައާއި އެހެނިހެން ކަންކަތައްތަކުގެ ބާބު","عَنْ أَبِي عُبَيْدٍ مَوْلَى ابْنِ أَزْهَرَ وَاسْمُهُ سَعْدُ بْنُ عُبَيْدٍ - قَالَ: «شَهِدْت الْعِيدَ مَعَ عُمَرَ بْنِ الْخَطَّابِ - رَضِيَ اللَّهُ عَنْهُ - فَقَالَ: هَذَانِ يَوْمَانِ نَهَى رَسُولُ اللَّهِ ﷺ عَنْ صِيَامِهِمَا: يَوْمُ فِطْرِكُمْ مِنْ صِيَامِكُمْ، وَالْيَوْمُ الآخَرُ: تَأْكُلُونَ فِيهِ مِنْ نُسُكِكُمْ»","އިބްނު އަޒްހަރު މިނިވަން ކުރެއްވި އަޅެއް ކަމުގައި އަބޫ ޢުބައިދު ރިވާ ކުރެއްވިއެވެ. އަދި އެކަލޭގެފާނުގެ ނަމަކީ ސަޢުދު ބިން ޢުބައިދެވެ. ޢުމަރު ބިން އަލްޚާއްޠާބު رَضِيَ اللَّهُ عَنْهُ ގެ އަރިހު ޢީދުގައި ތިމަން ބައިވެރިވީމެވެ. ޢުމަރު ވިދާޅުވިއެވެ: މި ދެ ދުވަހުގައި ރޯދަ ހިފުން ރަސޫލު ﷲ ﷺ ނަހީ ކުރެއްވިއެވެ. (އެއް ދުވަހަކީ) ތިޔަބައިމީހުން (ރަމަޟާން މަހުގެ) ރޯދަހިފުން ނިންމާލައި ދުވަހެވެ. އަދި (އަނެއް ދުވަހަކީ) ތިޔަބައިމީހުންގެ ޢުޟްޙިޔާތަކުން ތިޔަބައިމީހުން ކާ ދުވަހެވެ.","البخاري 1990 واللفظ له، ومسلم 1137"],["#209","كتاب الصيام","ރޯދައިގެ ފޮތް","باب أفضل الصيام وغيره","އެންމެ ހެޔޮވެގެންވާ ރޯދައާއި އެހެނިހެން ކަންކަތައްތަކުގެ ބާބު","عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «نَهَى رَسُولُ اللَّهِ ﷺ عَنْ صَوْمِ يَوْمَيْنِ: الْفِطْرِ وَالنَّحْرِ. وَعَنْ الصَّمَّاءِ، وَأَنْ يَحْتَبِيَ الرَّجُلُ فِي الثَّوْبِ الْوَاحِدِ، وَعَنْ الصَّلاةِ بَعْدَ الصُّبْحِ وَالْعَصْرِ» أَخْرَجَهُ مُسْلِمٌ بِتَمَامِهِ.\n<br><br>\nوَأَخْرَجَ الْبُخَارِيُّ الصَّوْمَ فَقَطْ.","އަބޫ ސަޢީދުލް ޚުދްރީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ދެ ދުވަހެއްގައި ރޯދަ ހިފުން ރަސޫލު ﷲ ﷺ ނަހީ ކުރެއްވިއެވެ. އެއި ފިޠުރު ޢީދު ދުވަހާއި، ޢަޟްޙާ ޢީދު ދުވަހެވެ. އަދި ޞައްމާއު (އެބަހީ: ދެ އަތް ނުނެރެވޭ ގޮތަށް ބަންދުކޮށް، ފޭރާމަކުން މުޅި ހަށިގަނޑުގައި އޮޅުން) ވެސް ނަހީ ކުރެއްވިއެވެ. އަދި އެއް ފޭރާން ލައިގެން ހުއްޓައި ދެ ފައިގެ ކަށިމަތި ކޮޅަށް ޖަހައިގެން އުޑަފައިންއިނުން ނަހީ ކުރެއްވިއެވެ. އަދި ފަތިސް ނަމާދާއި ޢަޞުރު ނަމާދަށް ފަހު ނަމާދު ކުރުން ނަހީ ކުރެއްވިއެވެ. (މުއައްލިފު ވިދާޅުވިއެވެ:) އިމާމު މުސްލިމް މި ޙަދީޘް މި އިން ގޮތަށް ރިވާ ކުރެއްވީއެވެ.\n<br><br>\nއަދި ބުޚާރީ ރިވާ ކުރެއްވި ޙަދީޘްގައި ވަނީ ހަމައެކަނި ރޯދަ ހިފުމުގެ ވާހަކައެވެ.\n","مسلم 827، أخرجه مقتصرا على الصوم فقط\n<br><br>\nالبخاري 1991، 1992، أجرجه بتمامه، وليس العكس"],["#210","كتاب الصيام","ރޯދައިގެ ފޮތް","باب أفضل الصيام وغيره","އެންމެ ހެޔޮވެގެންވާ ރޯދައާއި އެހެނިހެން ކަންކަތައްތަކުގެ ބާބު","عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «مَنْ صَامَ يَوْماً فِي سَبِيلِ اللَّهِ بَعَّدَ اللَّهُ وَجْهَهُ عَنْ النَّارِ سَبْعِينَ خَرِيفاً»","އަބޫ ސަޢީދުލް ޚުދްރީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲގެ މަގުގައި އެންމެ ދުވަހަކު ރޯދަ ހިފައިފި މީހާ، ފަހެ ﷲ އޭނާގެ މޫނު ހަތްދިހަ އަހަރުގެ ރާސްތާއަށް ނަރަކައިން ދުރު ކުރައްވާނެއެވެ.“","البخاري 2840 واللفظ له، ومسلم 1153"],["#211","كتاب الصيام","ރޯދައިގެ ފޮތް","باب ليلة القدر","ލައިލަތުލް ޤަދްރީގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما: «أَنَّ رِجَالاً مِنْ أَصْحَابِ النَّبِيِّ ﷺ أُرُوا لَيْلَةَ الْقَدْرِ فِي الْمَنَامِ فِي السَّبْعِ الأَوَاخِرِ. فَقَالَ النَّبِيُّ ﷺ: أَرَى رُؤْيَاكُمْ قَدْ تَوَاطَأَتْ فِي السَّبْعِ الأَوَاخِرِ. فَمَنْ كَانَ مُتَحَرِّيهَا فَلْيَتَحَرَّهَا فِي السَّبْعِ الأَوَاخِرِ»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ގެ ޞަޙާބީންގެ ތެރެއިން ފިރިހެނުންގެ ބަޔަކަށް، ހުވަފެނުގައި ލައިލަތުލް ޤަދްރި ވަނީ ފަހު ހަތް ރޭގައި ކަމަށް ދެއްކެވުނެވެ. ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ތިޔަބައިމީހުންގެ ހުވަފެންތައް ފަހު ހަތް ރެއަށް އިއްތިފާގުވާތަން ތިމަން ނަބިއްޔާ ދެކެވަޑައިގަންނަމެވެ. ފަހެ އެ ރޭ ހޯދަން މަސައްކަތް ކުރާ މީހާ، ފަހު ހަތް ރޭގައި ހޯދުމަށް ގަދައަޅައި މަސައްކަތް ކުރާށެވެ.“","البخاري 2015، ومسلم 1165"],["#212","كتاب الصيام","ރޯދައިގެ ފޮތް","باب ليلة القدر","ލައިލަތުލް ޤަދްރީގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «تَحَرَّوْا لَيْلَةَ الْقَدْرِ فِي الْوِتْرِ مِنْ الْعَشْرِ الأَوَاخِرِ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ފަހު ދިހައިގެ އޮނަހިރި ރޭތަކުގައި ލައިލަތުލް ޤަދްރި ހޯދުމަށް ތިޔަބައިމީހުން ގަދައަޅައި މަސައްކަތް ކުރާށެވެ.“","البخاري 2017"],["#213","كتاب الصيام","ރޯދައިގެ ފޮތް","باب ليلة القدر","ލައިލަތުލް ޤަދްރީގެ ބާބު","عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ - رَضِيَ اللَّهُ عَنْهُ -: «أَنَّ رَسُولَ اللَّهِ ﷺ كَانَ يَعْتَكِفُ فِي الْعَشْرِ الأَوْسَطِ مِنْ رَمَضَانَ. فَاعْتَكَفَ عَاماً، حَتَّى إذَا كَانَتْ لَيْلَةُ إحْدَى وَعِشْرِينَ - وَهِيَ اللَّيْلَةُ الَّتِي يَخْرُجُ مِنْ صَبِيحَتِهَا مِنْ اعْتِكَافِهِ - قَالَ: مَنْ اعْتَكَفَ مَعِي فَلْيَعْتَكِفْ الْعَشْرَ الأَوَاخِرَ فَقَدْ أُرِيتُ هَذِهِ اللَّيْلَةَ. ثُمَّ أُنْسِيتُهَا، وَقَدْ رَأَيْتُنِي أَسْجُدُ فِي مَاءٍ وَطِينٍ مِنْ صَبِيحَتِهَا. فَالْتَمِسُوهَا فِي الْعَشْرِ الأَوَاخِرِ. وَالْتَمِسُوهَا فِي كُلِّ وِتْرٍ. فَمَطَرَتِ السَّمَاءُ تِلْكَ اللَّيْلَةِ. وَكَانَ الْمَسْجِدُ عَلَى عَرِيشٍ. فَوَكَفَ الْمَسْجِدُ، فَأَبْصَرَتْ عَيْنَايَ رَسُولَ اللَّهِ ﷺ وَعَلَى جَبْهَتِهِ أَثَرُ الْمَاءِ وَالطِّينِ مِنْ صُبْحِ إحْدَى وَعِشْرِينَ»","އަބޫ ސަޢީދުލް ޚުދްރީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: (ކުރީގައި) ރަސޫލު ﷲ ﷺ އިޢުތިކާފުއަށް އިންނަވަނީ ރަމަޟާން މަހުގެ މެދު ދިހައިގައެވެ. ފަހެ އެއް އަހަރަކު އެކާވީސް ވަނަ ރޭވަންދެން ނަބިއްޔާ އިޢުތިކާފަށް އިންނެވިއެވެ. އަދި އެ ރެއަކީ (ކުރީގައި އާދައިގެ މަތިން) އޭގެ ފަތިހު ނަބިއްޔާ އިޢުތިކާފު (ނިންމަވައި) ނުކުމެވަޑައިގަންނަވާ ރެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާއާއެކު (މެދު ދިހައިގައި) އިޢުތިކާފަށް އިން ބަޔަކު، ފަހު ދިހައިގައި ވެސް އިޢުތިކާފަށް އިންނާށެވެ. އެހެނީ ހަމަކަށަވަރުން މިރޭ (އެބަހީ: ލައިލަތުލް ޤަދްރި ވިލޭރޭ) ތިމަން ނަބިއްޔާއަށް ދެއްކެވުނެވެ. އެއަށްފަހު ތިމަން ނަބިއްޔާއަށް އެ ރޭ ހަނދުމަފުޅު ނެތިކުރެއްވުނެވެ. އަދި (ހުވަފެންފުޅެއްގައި) އެ ރޭގެ ފަތިހު ތިމަން ނަބިއްޔާ ފެނާއި މަށީގައި ސަޖިދަ ކުރައްވާ ތަން ދެކެވަޑައިގަތީމެވެ. ވީމާ ފަހު ދިހައިގައި އެ ރޭ ހޯދުމަށް ތިޔަބައިމީހުން މަސައްކަތް ކުރާށެވެ. އަދި (އޭގެން ވެސް) ކޮންމެ އޮނަހިރި ރެއެއްގައި އެ ރޭ ހޯދަން މަސައްކަތް ކުރާށެވެ.“ ދެން އެ ރޭ އުޑުން ވާރޭ ވެހުނެވެ. (އެ ދުވަސްވަރު) މިސްކިތް ވަނީ ކަދުރު ރުކުގެ ފަނުން ހިޔާ ކުރެވިފައެވެ. ވީމާ މިސްކިތުގެ ފުރާޅުން ފެން ފޭބިއެވެ. އަދި ތިމަންނަގެ ދެ ލޮލުން ރަސޫލު ﷲ ﷺ ދުށީމެވެ. ނަބިއްޔާގެ ނިއްކުރިފުޅުގައި ވަނީ އެކާވީސްވަނަ ރޭގެ ފަތިހުގެ ފެނާއި މަށީގެ އަސަރެވެ. ","البخاري 2027 واللفظ له، ومسلم 1167"],["#214","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الاعتكاف","އިޢުތިކާފުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا: «أَنَّ رَسُولَ اللَّهِ ﷺ كَانَ يَعْتَكِفُ فِي الْعَشْرِ الأَوَاخِرِ مِنْ رَمَضَانَ، حَتَّى تَوَفَّاهُ اللَّهُ عَزَّ وَجَلَّ. ثُمَّ اعْتَكَفَ أَزْوَاجُهُ بَعْدَهُ»\n<br><br>\nوَفِي لَفْظٍ «كَانَ رَسُولُ اللَّهِ ﷺ يَعْتَكِفُ فِي كُلِّ رَمَضَانَ. فَإِذَا صَلَّى الْغَدَاةَ جَاءَ مَكَانَهُ الَّذِي اعْتَكَفَ فِيهِ»\n","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ ފުރާނަފުޅު ﷲ ޢައްޒަ ވަޖައްލަ ހިއްޕަވައިފުމަށް ދާންދެން، (ކޮންމެ) ރަމަޟާން މަހެއްގެ ފަހު ދިހައިގައި ނަބިއްޔާ އިޢުތިކާފަށް އިންނަވައެވެ. ދެން ނަބިއްޔާ އަވަހާރަވުމަށް ފަހުގައި ވެސް އެކަލޭގެފާނުގެ އަނބިކަނބަލުން އިޢުތިކާފަށް އިންނަވައެވެ.\n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ކޮންމެ ރަމަޟާން މަހަކު ރަސޫލު ﷲ ﷺ އިޢުތިކާފަށް އިންނަވައެވެ. އަދި ނަބިއްޔާ ފަތިސް ނަމާދު ކުރެއްވުމަށް ފަހު އިޢުތިކާފަށް އިންނެވި ތަނަށް އެނބުރި ވަޑައިގަންނަވައެވެ.","البخاري 2026، ومسلم 1172\n<br><br>\nالبخاري 2041"],["#215","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الاعتكاف","އިޢުތިކާފުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا: «أَنَّهَا كَانَتْ تُرَجِّلُ النَّبِيَّ ﷺ وَهِيَ حَائِضٌ، وَهُوَ مُعْتَكِفٌ فِي الْمَسْجِدِ. وَهِيَ فِي حُجْرَتِهَا: يُنَاوِلُهَا رَأْسَهُ»\n<br><br>\nوَفِي رِوَايَةٍ: «وَكَانَ لا يَدْخُلُ الْبَيْتَ إلاَّ لِحَاجَةِ الإِنْسَانِ»\n<br><br>\nوَفِي رِوَايَةٍ أَنَّ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ «إنْ كُنْتُ لأَدْخُلُ الْبَيْتَ لِلْحَاجَةِ وَالْمَرِيضُ فِيهِ. فَمَا أَسْأَلُ عَنْهُ إلاَّ وَأَنَا مَارَّةٌ».","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ މިސްކިތުގައި އިޢުތިކާފަށް އިންނަވަނިކޮށް، އެ ކަމަނާ ޙައިޟުވެފައިވާ ޙާލުގައި އެކަލޭގެފާނުގެ އިސްތަށިފުޅުގައި ފުނާ އަޅުއްވައެވެ. (އެ ގޮތުން އާޢިޝާ) އެ ކަމަނާގެ ކޮޓަރިކޮޅުގައި އިންނަވަނިކޮށް، ނަބިއްޔާގެ އިސްތަށިފުޅު އެ ކަމަނާއަށް ދިއްކޮށްލައްވައެވެ. (ނޯޓު: އޭރު މިސްކިތާއި ކޮޓަރިކޮޅު ވަނީ ޖެހިޖެހިގެން ދޮރުކޮޅަކުން ގުޅިފައެވެ.)\n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: އަދި އިންސާނީ ހާޖަތެއް ފުއްދުމަށް ޓަކައި މެނުވީ ނަބިއްޔާ ގެކޮޅަށް ވަދެވަޑައި ނުގަންނަވައެވެ.\n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިއެވެ: ތިމަން ކަމަނާ (އިޢުތިކާފަށް އިންނަވަނިކޮށް) ހާޖަތެއް ޖެހިގެން ގެކޮޅަށް ވަންނަ ހިނދު، އެ ތަނުގައި ބަލިމީހަކު ވާނަމަ، ތިމަން ކަމަނާ އެ ދާ މަގުމަތީ ހިނގަހިނގައި ހުއްޓައި މެނުވީ އޭނާގެ ޙާލާއި ބެހޭ ގޮތުން ނާހަމެވެ. (އެބަހީ: މިސްކިތަށް ދިޔުމުގައި ނުވަތަ ގެއަށް އެނބުރި އައުމުގައި އެއްވެސް ތަނަކު ނުހުއްޓަވައެވެ.)","البخاري 2046 واللفظ له، ومسلم 297\n<br><br>\nمسلم 297\n<br><br>\nمسلم 297"],["#216","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الاعتكاف","އިޢުތިކާފުގެ ބާބު","عَنْ عُمَرَ بْنِ الْخَطَّابِ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قُلْتُ: «يَا رَسُولَ اللَّهِ، إنِّي كُنْتُ نَذَرْتُ فِي الْجَاهِلِيَّةِ أَنْ أَعْتَكِفَ لَيْلَةً» - وَفِي رِوَايَةٍ: «يَوْماً - فِي الْمَسْجِدِ الْحَرَامِ. قَالَ فَأَوْفِ بِنَذْرِكَ»\n<br><br>وَلَمْ يَذْكُرْ بَعْضُ الرُّوَاةِ يَوْماً ولا لَيْلَةً.","ޢުމަރު ބިން އަލްޚައްޠާބު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ތިމަން ދެންނެވީމެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން މަސްޖިދުލް ޙަރާމުގައި އެއް ރޭ (އެހެން ރިވާޔަތެއްގައި އެއް ދުވަސް) އިޢުތިކާފަށް އިނުމަށް، ޖާހިލިއްޔަ ޒަމާނުގައި ތިމަންނަ ނަދުރެއް ބުނެފައިވާނެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ފަހެ ތިބާގެ ނަދުރު ފުއްދާށެވެ.“\n<br><br>\nއަދި ބައެއް ރާވީން އެއްރޭ ނުވަތަ ދުވަހުގެ ވާހަކައެއް ޒިކުރެއް ނުކުރައްވައެވެ.","البخاري 2032، ومسلم 1656"],["#217","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الاعتكاف","އިޢުތިކާފުގެ ބާބު","عَنْ صَفِيَّةَ بِنْتِ حُيَيٍّ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: «كَانَ النَّبِيُّ ﷺ مُعْتَكِفًا. فَأَتَيْتُهُ أَزُورُهُ لَيْلاً. فَحَدَّثْتُهُ، ثُمَّ قُمْتُ لأَنْقَلِبَ، فَقَامَ مَعِي لِيَقْلِبَنِي - وَكَانَ مَسْكَنُهَا فِي دَارِ أُسَامَةَ بْنِ زَيْدٍ - فَمَرَّ رَجُلانِ مِنْ الأَنْصَارِ فَلَمَّا رَأَيَا رَسُولَ اللَّهِ ﷺ أَسْرَعَا. فَقَالَ النَّبِيُّ ﷺ: عَلَى رِسْلِكُمَا. إنَّهَا صَفِيَّةُ بِنْتُ حُيَيٍّ. فَقَالا: سُبْحَانَ اللَّهِ يَا رَسُولَ اللَّهِ. فَقَالَ: إنَّ الشَّيْطَانَ يَجْرِي مِنْ ابْنِ آدَمَ مَجْرَى الدَّمِ. وَإِنِّي خَشِيتُ أَنْ يَقْذِفَ فِي قُلُوبِكُمَا شَرّاً- أَوْ قَالَ شَيْئاً»\n<br><br>\nوَفِي رِوَايَةٍ «أَنَّهَا جَاءَتْ تَزُورُهُ فِي اعْتِكَافِهِ فِي الْمَسْجِدِ فِي الْعَشْرِ الأَوَاخِرِ مِنْ رَمَضَانَ. فَتَحَدَّثَتْ عِنْدَهُ سَاعَةً. ثُمَّ قَامَتْ تَنْقَلِبُ. فَقَامَ النَّبِيُّ ﷺ مَعَهَا يَقْلِبُهَا، حَتَّى إذَا بَلَغَتْ بَابَ الْمَسْجِدِ عِنْدَ بَابِ أُمِّ سَلَمَةَ» ثُمَّ ذَكَرَهُ بِمَعْنَاهُ.","ޞަފިއްޔާ ބިންތި ޙުޔައްޔު رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ އިޢުތިކާފަށް އިންނަވަނިކޮށް، އެކަލޭގެފާނަށް ޒިޔާރަތް ކުރެއްވުމަށް އެއް ރެއަކު ތިމަން ކަމަނާ ދިޔައީމެވެ. ދެން ނަބިއްޔާއާ ވާހަކަ ދެއްކުމަށްފަހު، އެނބުރި ދިޔުމަށް ތިމަން ކަމަނާ ތެދުވީމެވެ. ފަހެ ތިމަން ކަމަނާ ގެއަށް ލައިދެއްވުމަށް ނަބިއްޔާ ވެސް ތެދުވެވަޑައިގަތެވެ. (އެ ކަމަނާގެ ގެކޮޅު ވަނީ މިހާރު އުސާމާ ބިން ޒައިދުގެ ގެކޮޅު ހުންނަ ތަނުގައެވެ.) ފަހެ އަންޞާރުންގެ ދެ ބޭކަލުން ހިނގާފައި ދަނިކޮށް، ރަސޫލު ﷲ ﷺ ފެނުމުން އެ ދެ ބޭކަލުން ގެ ހިނގުން އަވަސްކޮށްލެއްވިއެވެ. ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަ ދެ ބޭކަލުން ހަމަޖެހިލައިގެން ހިނގާށެވެ. އެހެނީ ހަމަކަށަވަރުން މިއީ ޞަފިއްޔާ ބިންތި ޙުޔައްޔެވެ.“ އެ ދެ ބޭކަލުން ދެންނެވިއެވެ: سُبْحَانَ اللَّهِ އެވެ. އޭ ﷲ ގެ ރަސޫލާއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން އާދަމުގެ ދަރިޔާގެ ގައިގާ ލޭ ދައުރުވާ ފަދައިން ޝައިތާނާ ދައުރުވެއެވެ. އަދި ހަމަކަށަވަރުން ޝައިޠާނާ ތިޔަ ދެބޭކަލުންގެ ހިތްތަކަށް ނުބައި އެއްޗެއް އަރުވައިފާނެކަމަށް ތިމަން ނަބިއްޔާ ބިރުފުޅުގަތީއެވެ.“\n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ރަމަޟާން މަހުގެ ފަހު ދިހައިގައި ނަބިއްޔާ މިސްކިތުގައި އިޢުތިކާފަށް އިންނަވަނިކޮށް، އެކަލޭގެފާނަށް ޒިޔާރަތް ކުރެއްވުމަށް އެ ކަމަނާ ވަޑައިގަތެވެ. އަދި ގަޑިއެއްގެ އިރު އެ ކަމަނާ ނަބިއްޔާއާ ވާހަކަ ދެއްކެވުމަށްފަހު، އެނބުރި ދިޔުމަށް ތެދުވެވަޑައިގަތެވެ. ފަހެ އެ ކަމަނާ ގެއަށް ލައިދެއްވުމަށް ނަބިއްޔާ ﷺ ވެސް ތެދުވެވަޑައިގެން، މިސްކިތުގައިވާ އުންމު ސަލަމާގެ ދޮރުކޮޅާ ހިސާބަށް އެ ކަމަނާ ގެންދެވިއެވެ. (އެއަށްފަހު ކުރީގެ ރިވާޔަތާ އެއްފަދައިން ޙަދީޘް ގެންނެވިއެވެ.)","البخاري 3281، ومسلم 2175\n<br><br>\nالبخاري 2035 واللفظ له، ومسلم 2175"],["#218","كتاب الحج","ޙައްޖުގެ ފޮތް","باب المواقيت","މީޤާތުތަކުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رضي الله عنهما: (أَنَّ رَسُولَ اللهِ - ﷺ - وَقَّتَ لأَهْلِ الْمَدِينَةِ: ذَا الْحُلَيْفَةِ. وَلأَهْلِ الشَّامِ: الْجُحْفَةَ. وَلأَهْلِ نَجْدٍ: قَرْنَ الْمَنَازِلِ. وَلأَهْلِ الْيَمَنِ: يَلَمْلَمَ. هُنَّ لَهُمْ وَلِمَنْ أَتَى عَلَيْهِنَّ مِنْ غَيْرِ أَهْلِهِنَّ، مِمَّنْ أَرَادَ الْحَجَّ أَوْ الْعُمْرَةَ. وَمَنْ كَانَ دُونَ ذَلِكَ: فَمِنْ حَيْثُ أَنْشَأَ، حَتَّى أَهْلُ مَكَّةَ مِنْ مَكَّةَ) .","ޢަބްދުﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ މީޤާތު ގޮތުގައި ލެއްވީ، މަދީނާގެ އަހުލުވެރިންނަށް ޛުލްޙުލައިފާއެވެ. އަދި ޝާމުގެ އަހުލުވެރިންނަށް ޖުޙްފާއެވެ. އަދި ނަޖުދުގެ އަހުލުވެރިންނަށް ޤަރްނުލް މަނާޒިލެވެ. އަދި ޔަމަނުގެ އަހުލުވެރިންނަށް ޔަލަމްލަމެވެ. އަދި ނަބިއްޔާ ޙަދީޘްކުރެއްވިއެވެ. ”އެ މީޤާތުތައް ވަނީ ޙައްޖު ނުވަތަ ޢުމްރާ ކުރަން އެދޭ، އެ ތަންތަނުގެ އަހުލުވެރިންނަށާއި، އަދި (އޭގެ އިތުރުން) އެ ތަންތަނުގެ އަހުލުވެރިން ކަމުގައި ނުވާ އެކަމަކު އެ ތަންތަނުންލާފައި އަންނަ އެހެން މީހުންނަށެވެ. އަދި އެ (މީޤާތުތަކަށް ވުރެ) އެތެރޭގައި އުޅޭ މީހުން (އިޙްރާމް ބަންނާނީ) ހަމަ އެ ބަޔަކު އުޅޭ ހިސާބުން ފެށިގެންނެވެ. އެ ގޮތުން މައްކާގެ އަހުލުވެރިން (އިޙްރާމް ބަންނާނީ) މައްކާއިންނެވެ.“ ","البخاري 1524 واللفظ له، ومسلم 1181"],["#219","كتاب الحج","ޙައްޖުގެ ފޮތް","باب المواقيت","މީޤާތުތަކުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رضي الله عنهما أَنَّ رَسُولَ اللَّهِ - ﷺ - قَالَ: (يُهِلُّ أَهْلُ الْمَدِينَةِ مِنْ ذِي الْحُلَيْفَةِ، وَأَهْلُ الشَّامِ مِنْ الْجُحْفَةِ، وَأَهْلُ نَجْدٍ مِنْ قَرْنٍ) . قَالَ: وَبَلَغَنِي أَنَّ رَسُولَ اللَّهِ - ﷺ - قَالَ: (وَيُهِلُّ أَهْلُ الْيَمَنِ مِنْ يَلَمْلَمَ) .","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”މަދީނާގެ އަހުލުވެރިން އިޙްރާމް ބަންނާނީ ޛުލްޙުލައިފާ އިންނެވެ. އަދި ޝާމުގެ އަހުލުވެރިން ޖުޙްފާ އިންނެވެ. އަދި ނަޖުދުގެ އަހުލުވެރިން ޤަރްނުލް (މަނާޒިލުންނެވެ.).“ ޢަބްދު ﷲ ވިދާޅުވިއެވެ. \"އަދި ރަސޫލު ﷺ ޙަދީޘް ކުރެއްވި ކަމަށް ތިމަންނައަށް ފޯރާފައިވެއެވެ: ”އަދި ޔަމަނުގެ އަހުލުވެރިން އިހުރާމް ބަންނާނީ ޔަލަމްލަމް އިންނެވެ.~\n","البخاري 1525 واللفظ له، ومسلم 1182"],["#220","كتاب الحج","ޙައްޖުގެ ފޮތް","باب ما يلبس المحرم من الثياب","އިޙްރާމް ބަންނަ މީހާ އަޅާ ހެދުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رضي الله عنهما: (أَنَّ رَجُلاً قَالَ يَا رَسُولَ اللَّهِ، مَا يَلْبَسُ الْمُحْرِمُ مِنْ الثِّيَابِ؟ قَالَ رَسُولُ اللَّهِ - ﷺ -: لايَلْبَسُ الْقَمِيصَ، وَلا الْعَمَائِمَ، وَلا السَّرَاوِيلاتِ، وَلا الْبَرَانِسَ، وَلا الْخِفَافَ، إلاَّ أَحَدٌ لا يَجِدُ نَعْلَيْنِ فَلْيَلْبَسْ خُفَّيْنِ وَلْيَقْطَعْهُمَا أَسْفَلَ مِنْ الْكَعْبَيْنِ، وَلا يَلْبَسْ مِنْ الثِّيَابِ شَيْئاً مَسَّهُ زَعْفَرَانٌ أَوْ وَرْسٌ) . \n<br><br>\nوَلِلْبُخَارِيِّ: (وَلا تَنْتَقِبِ الْمَرْأَةُ. وَلا تَلْبَسِ الْقُفَّازَيْنِ) .","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: މީހަކު ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. އިޙްރާމް ބަންނަ މީހާ އަޅާނީ ކޮން ހެދުމެއްތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭނާ ގަމީސް ނުލާށެވެ. އަދި ފަގުޑި ނާޅާށެވެ. އަދި ހަރުވާޅު ނުލާށެވެ. އަދި ބޮލާއި އެއްކޮށް ނިވާ ކުރެވޭ ގޮތަށް ހުންނަ ހެދުން ނުލާށެވެ. އަދި ޚުއްފަށް ނާރާށެވެ. އެއީ އަރާނެ ފައިވާނެއް ނުލިބުނު މީހާ މެނުވީއެވެ. ފަހެ އޭނާ ޚުއްފަށް އެރިދާނެއެވެ. އެކަމަކު ދެ ތަނބިކަށްޓަށް ވުރެ ތިރިން (އެބަހީ: ދެ ތަނބިކަށި ފެންނަ ގޮތަށް) އެ ޚުއްފު ބުރިކޮށްލާށެވެ. އަދި ސެފްރޮން ނުވަތަ ވަރްސު (ފަދަ ހުވަނދު) ޖެހިފައިވާ ހެދުން ނުލާށެވެ.“\n<br><br>\nއަދި ބުޚާރީގެ ރިވާޔަތެއްގައި ވެއެވެ: ”އަދި (އިޙްރާމް ބަންނަނީ އަންހެނެއް ކަމުގައި ވާނަމަ) ނިޤާބް (އެބަހީ: މޫނު ބުރުގާ) ނާޅާށެވެ. އަދި އެ ކަނބުލޭގެ އަންގި ނުލައްވާށެވެ.“","البخاري 1542 واللفظ له، ومسلم 1177\n<br><br>\nالبخاري 1838"],["#221","كتاب الحج","ޙައްޖުގެ ފޮތް","باب ما يلبس المحرم من الثياب","އިޙްރާމް ބަންދެގެން ހުރި މީހާ އަޅާ ހެދުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رضي الله عنهما قَالَ: (سَمِعْتُ رَسُولَ اللَّهِ - ﷺ - يَخْطُبُ بِعَرَفَاتٍ: مَنْ لَمْ يَجِدْ نَعْلَيْنِ فَلْيَلْبَسِ الْخُفَّيْنِ، وَمَنْ لَمْ يَجِدْ إزَاراً فَلْيَلْبَسْ السَّرَاوِيلَ لِلْمُحْرِمِ) .","ޢަބްދު ﷲ ބިން ޢައްބާސް ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޢަރަފާތުގައި ޚުތުބާ ދެއްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ: ”އިހްރާމް ބަންނަ މީހަކަށް އަރާނެ ފައިވާނެއް ނުލިބުނު މީހާ ފަހެ ޚުއްފަށް އަރާށެވެ. އަދި ލާނެ އިޒާރެއް ނުލިބުނު މީހާ ފަހެ ހަރުވާޅު ލާށެވެ.“","البخاري 1841 واللفظ له، ومسلم 1178"],["#222","كتاب الحج","ޙައްޖުގެ ފޮތް","باب ما يلبس المحرم من الثياب","އިޙްރާމް ބަންދެގެން ހުރި މީހާ އަޅާ ހެދުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رضي الله عنهما: (أَنَّ تَلْبِيَةَ رَسُولِ اللَّهِ - ﷺ -: لَبَّيْكَ اللَّهُمَّ لَبَّيْكَ، لَبَّيْكَ لا شَرِيكَ لَكَ لَبَّيْكَ، إنَّ الْحَمْدَ وَالنِّعْمَةَ لَكَ وَالْمُلْكَ، لا شَرِيكَ لَكَ).\n<br><br>\nقَالَ: وَكَانَ عَبْدُ اللَّهِ بْنُ عُمَرَ يَزِيدُ فِيهَا «لَبَّيْكَ لَبَّيْكَ، وَسَعْدَيْكَ، وَالْخَيْرُ بِيَدَيْكَ، وَالرَّغْبَاءُ إلَيْكَ وَالْعَمَلُ» .","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ ތަލްބިޔާއަކީ: ” لَبَّيْكَ اللَّهُمَّ لَبَّيْكَ، لَبَّيْكَ لا شَرِيكَ لَكَ لَبَّيْكَ. إنَّ الْحَمْدَ وَالنِّعْمَةَ لَكَ وَالْمُلْكَ، لا شَرِيكَ لَكَ“ އެވެ. (މާނައީ: އިބަ އިލާހަށް މި އަޅާ އިޖާބަ ދެމެވެ. ޔާ ﷲ އެވެ. އިބަ އިލާހަށް މި އަޅާ އިޖާބަ ދެމެވެ. ހަމަކަށަވަރުން އެންމެހައި ޙަމްދަކާއި ނިޢުމަތަކާއި ވެރިކަމެއް ވަނީ އިބަ އިލާހަށެވެ. އިބަ އިލާހަށް އެއްވެސް ޝަރީކަކު ނުވެއެވެ.)\n<br><br>\nއަދި އިބްނު ޢުމަރު ތަލްބިޔާއަށް (މިފަދައިން ވިދާޅުވެ) އިތުރު ކުރައްވައެވެ: لَبَّيْكَ لَبَّيْكَ وَسَعْدَيْكَ، وَالْخَيْرُ بِيَدَيْكَ، لبيك، وَالرَّغْبَاءُ إلَيْكَ وَالْعَمَلُ. (މާނައީ: އިބަ އިލާހަށް މި އަޅާ އިޖާބަ ދެމެވެ. އިބަ އިލާހަށް މި އަޅާ އިޖާބަ ދެމެވެ. އަދި އިބަ އިލާހުގެ އެހީތެރިކަމަށް އެދެމެވެ. އަދި އެންމެހައި ހެވެއް ވަނީ އިބަ އިލާހުގެ ދެ އަތްޕުޅުގައެވެ. އިބަ އިލާހަށް މި އަޅާ އިޖާބަ ދެމެވެ. އަދި އެދޭ ކަންކަމުގައި ދެންނެވުން ވަނީ އިބަ އިލާހަށެވެ. އަދި ޢަމަލުތައް ވަނީ ވެސް އިބަ އިލާހަށެވެ.)\n","البخاري 1549، ومسلم 1184 واللفظ له"],["#223","كتاب الحج","ޙައްޖުގެ ފޮތް","باب ما يلبس المحرم من الثياب","އިޙްރާމް ބަންދެގެން ހުރި މީހާ އަޅާ ހެދުމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رضي الله عنه - قَالَ: قَالَ رَسُولُ اللَّهِ - ﷺ -: (لا يَحِلُّ لامْرَأَةٍ تُؤْمِنُ بِاَللَّهِ وَالْيَوْمِ الآخِرِ أَنْ تُسَافِرَ مَسِيرَةَ يَوْمٍ وَلَيْلَةٍ إلاَّ وَمَعَهَا حُرْمَةٌ) .\n<br><br>\nوَفِي لَفْظِ الْبُخَارِيِّ: (لا تُسَافِرُ مَسِيرَةَ يَوْمٍ إلاَّ مَعَ ذِي مَحْرَمٍ) ","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲ އާއި އާޚިރަތް ދުވަހަށް އީމާންވާ އަންހެނަކަށް، އެއް ރެއާއި ދުވާލުގެ ދުރުމިނަށް މަޙްރަމަކާ ނުލާ ދަތުރު ކުރުން ހުއްދަވެގެން ނުވެއެވެ.“\n<br><br>\nއަދި ބުޚާރީގެ ރިވާޔަތެއްގައި ވެއެވެ: ”އެންމެ ދުވަހެއްވެސް އަދި ރެއެއްވެސް މަޙްރަމަކާ ލައިގެން މެނުވީ އެ ކަނބުލޭގެ ދަތުރުކޮށްގެން ނުވާނެއެވެ.“\n","البخاري 1088 واللفظ له، ومسلم 1339\n<br><br>\nمسلم 1339"],["#224","كتاب الحج","ޙައްޖުގެ ފޮތް","باب الفدية\n","ފިދުޔައިގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ مَعْقِلٍ قَالَ: (جَلَسْتُ إلَى كَعْبِ بْنِ عُجْرَةَ. فَسَأَلَتْهُ عَنِ الْفِدْيَةِ؟ فَقَالَ: نَزَلَتْ فِي خَاصَّةً. وَهِيَ لَكُمْ عَامَّةً. حُمِلْتُ إلَى رَسُولِ اللَّهِ - ﷺ - وَالْقَمْلُ يَتَنَاثَرُ عَلَى وَجْهِي. فَقَالَ: مَا كُنْتُ أُرَى الْوَجَعَ بَلَغَ بِكَ مَا أَرَى - أَوْ مَا كُنْتُ أُرَى الْجَهْدَ بَلَغَ بِكَ مَا أَرَى - أَتَجِدُ شَاةً؟ فَقُلْتُ: لا. فَقَالَ: صُمْ ثَلاثَةَ أَيَّامٍ، أَوْ أَطْعِمْ سِتَّةَ مَسَاكِينَ، لِكُلِّ مِسْكِينٍ نِصْفَ صَاعٍ) .\n<br><br>\nوَفِي رِوَايَةٍ: (فَأَمَرَهُ رَسُولُ اللَّهِ - ﷺ - أَنْ يُطْعِمَ فَرَقاً بَيْنَ سِتَّةٍ، أَوْ يُهْدِيَ شَاةً، أَوْ يَصُومَ ثَلاثَةَ أَيَّامٍ) .","ޢަބްދު ﷲ ބިން މަޢުޤަލް ރިވާ ކުރެއްވިއެވެ: ކަޢުބް ބިން ޢުޖްރާގެ އަރިހު ތިމަން އިށީނދެ، ފިދުޔައާ ބެހޭ ގޮތުން ސުވާލު ކުރީމެވެ. ކަޢުބް ދެންނެވިއެވެ: އެކަމާ ގުޅޭ (އާޔަތް) ބާވައިލެއްވުނީ ތިމަންނައާ މެދުގައި ޚާއްޞަކޮށެވެ. އަދި ތިޔަބައިމީހުންނަށް އެ އާޔަތް ވަނީ އާންމުކޮށެވެ. (ޙައްޖުގައި) ނަބިއްޔާގެ އަރިހަށް ތިމަން އުފުލައިގެން ގެންދެވުނެވެ. އޭރު ތިމަންނަގެ މޫނު މައްޗަށް އުކުނު ފައިބަފައިބަ ހުއްޓެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާއަށް ޖެހިފައިވާ ވަރަށް އުނދަގޫ ޖެހޭނެ ކަމަކަށް ތިމަން ނަބިއްޔާ ހީފުޅު ނުކުރައްވަމެވެ. ތިބާއަށް ކަންބަޅިއެއް ހޯދިދާނެތޯއެވެ؟“ (އެބަހީ: ބޯ ބޭލުމުގެ ފިދުޔައަށްޓަކައި ކަތިލާނެ ކަނބަޅިއެއް ލިބިދާނެތޯއެވެ.) ތިމަން ދެންނެވީމެވެ: ނޫނެކެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ފަހެ ތިން ދުވަސް ވަންދެން ރޯދަ ހިފާށެވެ. ނޫނީ ހަ މިސްކީނުންނަށް ކާންދޭށެވެ. ކޮންމެ މިސްކީނަކަށް ޞާޢެއްގެ ދެބައިކުޅަ އެއްބައެވެ.“\n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”ފަހެ ރަސޫލު ﷲ ﷺ ކަޢުބަށް އަމުރު ކުރެއްވީ، ހަ މިސްކީނުންނަށް ފަރަޤެއްގެ މިންވަރު (އެބަހީ: ޞާޢެއްގެ ތިންބައި ކުޅަ އެއްބައި) ކާންދެއްވުމަށެވެ. ނޫނީ ކަންބަޅިއެއް ހަދްޔުގެ ގޮތުގައި (ކަތިލުމަށެވެ.) ނޫނީ ތިން ދުވަސް ވަންދެއް ރޯދަ ހިފުމަށެވެ.","البخاري 1816 واللفظ له، ومسلم 1201\n<br><br>\nالبخاري 1817واللفظ له، ومسلم 1201"],["#225","كتاب الحج","ޙައްޖުގެ ފޮތް","باب حرمة مكة","މައްކާގެ ޙުރުމަތްތެރިކަމުގެ ބާބު","عَنْ أَبِي شُرَيْحٍ - خُوَيْلِدِ بْنِ عَمْرٍو - الْخُزَاعِيِّ الْعَدَوِيِّ رضي الله عنه: أَنَّهُ قَالَ لِعَمْرِو بْنِ سَعِيدِ بْنِ الْعَاصِ - وَهُوَ يَبْعَثُ الْبُعُوثَ إلَى مَكَّةَ - ائْذَنْ لِي أَيُّهَا الأَمِيرُ أَنْ أُحَدِّثَكَ قَوْلاً قَامَ بِهِ رَسُولُ اللَّهِ - ﷺ - الْغَدَ مِنْ يَوْمِ الْفَتْحِ. فَسَمِعَتْهُ أُذُنَايَ، وَوَعَاهُ قَلْبِي، وَأَبْصَرَتْهُ عَيْنَايَ، حِينَ تَكَلَّمَ بِهِ: (أَنَّهُ حَمِدَ اللَّهَ وَأَثْنَى عَلَيْهِ. ثُمَّ قَالَ: إنَّ مَكَّةَ حَرَّمَهَا اللَّهُ تَعَالَى، وَلَمْ يُحَرِّمْهَا النَّاسُ. فَلا يَحِلُّ لامْرِئٍ يُؤْمِنُ بِاَللَّهِ وَالْيَوْمِ الآخِرِ: أَنْ يَسْفِكَ بِهَا دَماً، وَلا يَعْضِدَ بِهَا شَجَرَةً. فَإِنْ أَحَدٌ تَرَخَّصَ بِقِتَالِ رَسُولِ اللَّهِ - ﷺ - فَقُولُوا: إنَّ اللَّهَ قَدْ أَذِنَ لِرَسُولِهِ، وَلَمْ يَأْذَنْ لَكُمْ. وَإِنَّمَا أُذِنَ لِي سَاعَةً مِنْ نَهَارٍ. وَقَدْ عَادَتْ حُرْمَتُهَا الْيَوْمَ كَحُرْمَتِهَا بِالأَمْسِ. فَلْيُبَلِّغْ الشَّاهِدُ الْغَائِبَ. فَقِيلَ لأَبِي شُرَيْحٍ: مَا قَالَ لَكَ؟ قَالَ: أَنَا أَعْلَمُ بِذَلِكَ مِنْكَ يَا أَبَا شُرَيْحٍ، إنَّ الْحَرَمَ لا يُعِيذُ عَاصِياً، وَلا فَارَّاً بِدَمٍ وَلا فَارَّاً بِخَرْبَةٍ) .","އަބޫ ޝުރައިޙު، ޚުވައިލިދު ބިން ޢަމްރު އަލްޚުޒާޢީ އަލްޢަދަވީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ޢަމްރު ބިން ސަޢީދު ބިން އަލްޢާޞް، ލަޝްކަރުތައް މައްކާއަށް ފޮނުވި އިރު ތިމަން ދެންނެވީމެވެ: އޭ އަމީރެވެ. ވާހަކަ ދެއްކުމަށް ތިމަންނައަށް ހުއްދަ ދެއްވާށެވެ. އެއީ ނަބިއްޔާ ﷺ މައްކާ ފަތަޙަ ކުރެއްވި ދުވަހާ ޖެހިގެން އައި ދުވަހު ޙަދީޘް ކުރެއްވި ވާހަކަފުޅެކެވެ. އެ ދެއްކެވި ވާހަކަފުޅު ތިމަންނަގެ ދެ ކަންފަތުން އަޑުއަހައި، ތިމަންނަގެ ހިތުން ރަނގަޅަށް ދެނެގަނެ، އަދި ތިމަންނަގެ ދެ ލޮލުން ނަބިއްޔާ ވާހަކަފުޅު ދައްކަވަނިކޮށް ދުށީމެވެ. ﷲއަށް ޙަމްދު ސަނާ ދެންނެވުމަށް ފަހު ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން މައްކާ ޙުރުމަތްތެރި ކުރެއްވީ ﷲ ތަޢާލާއެވެ. އެވެ. އަދި އެ ތަން ޙުރުމަތްތެރި ކުރީކީ މީސްތަކުންނެއް ނޫނެވެ. ވީމާ ﷲއާއި އާޚިރަތް ދުވަހަށް އީމާންވާ މީހަކަށް އެ ތަނުގައި ލޭ އޮހޮރުވުން ހުއްދަވެގެނެއް ނުވެއެވެ. އަދި އެ ތަނުގައި ގަސް ކެނޑުން ވެސް ހުއްދައެއް ނުވެއެވެ. އަދި ރަސޫލު ﷲ ﷺ އެ ތަނުގައި ހަނގުރާމަ ކުރެއްވީތީވެ އެކަން ކުރުން ހުއްދަވާނެ ކަމަށް މީހަކު ބުނާ ނަމަ، ފަހެ ތިޔަބައިމީހުން ބުނާށެވެ: ހަމަކަށަވަރުން ﷲ ވަނީ އެ އިލާހުގެ ރަސޫލާއަށް ހުއްދަ ދެއްވާފައެވެ. އަދި އެ އިލާހު ތިޔަބައިމީހުންނަކަށް ހުއްދައެއް ނުދެއްވައެވެ. އަދި އެ އިލާހުގެ ރަސޫލާއަށް ހުއްދަ ދެއްވީ ވެސް ހަމައެކަނި ދުވާލުގެ ވަގުތުކޮޅަކަށެވެ. އެއަށްފަހު ހަމަކަށަވަރުން އޭގެ ކުރީ ކުރީ ދުވަހެކޭ އެއްފަދައިން އެ ތަނުގެ ޙުރުމަތްތެރިކަން މިއަދު އިއާދަވެއްޖެއެވެ. ފަހެ ހާޒިރުގައި ހުރި މީހާ، ހާޒިރުގައި ނެތް މީހާއަށް (މި ބަސްފުޅު) ފޯރުކޮށްދޭށެވެ.“ އަބޫ ޝޫރައިޙަށް ދެންނެވުނެވެ: ޢަމްރު ތިބާއަށް ދެންނެވީ ކީކޭތޯއެވެ؟ އަބޫ ޝުރައިޙު ދެންނެވިއެވެ: ޢަމްރު ދެންނެވީ: އޭ އަބޫ ޝުރައިޙެވެ. ތިބާއަށް ވުރެ ބޮޑަށް ތިމަންނަ ތިކަންތައް ދެނަހުރީމެވެ. އޭ އަބޫ ޝުރައިޙެވެ. ހަމަކަށަވަރުން އުރެދޭ މީހަކަށް، ނުވަތަ ލޭއޮހޮރުވައިގެން ފިލާ މީހަކަށް، ނުވަތަ ޖިޒްޔަ ނުދޭންވެގެން ފިލާ މީހަކަށް (މައްކާގެ) ޙަރަމުގެ ޙިމާޔަތެއް ނުވެއެވެ.","البخاري 1832، ومسلم 1354\n"],["#226","كتاب الحج","ޙައްޖުގެ ފޮތް","باب حرمة مكة","މައްކާގެ ޙުރުމަތްތެރިކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رضي الله عنهما قَالَ: قَالَ رَسُولُ اللَّهِ - ﷺ - يَوْمَ فَتْحِ مَكَّةَ - (لا هِجْرَةَ بَعْدَ الفَتْحِ، وَلَكِنْ جِهَادٌ وَنِيَّةٌ. وَإِذَا اُسْتُنْفِرْتُمْ فَانْفِرُوا.\n<br><br>\nوَقَالَ: يَوْمَ فَتْحِ مَكَّةَ: إنَّ هَذَا الْبَلَدَ حَرَّمَهُ اللَّهُ يَوْمَ خَلَقَ اللَّهُ السَّمَوَاتِ وَالأَرْضَ. فَهُوَ حَرَامٌ بِحُرْمَةِ اللَّهِ إلَى يَوْمِ الْقِيَامَةِ. وَإِنَّهُ لَمْ يَحِلَّ الْقِتَالُ فِيهِ لأَحَدٍ قَبْلِي، وَلَمْ يَحِلَّ لِي إلاَّ سَاعَةً مِنْ نَهَارٍ فَهُوَ حَرَامٌ بِحُرْمَةِ اللَّهِ إلَى يَوْمِ الْقِيَامَةِ. لا يُعْضَدُ شَوْكُهُ، وَلا يُنَفَّرُ صَيْدُهُ، وَلا يَلْتَقِطُ لُقْطَتَهُ إلاَّ مَنْ عَرَّفَهَا. وَلا يُخْتَلَى خَلاهُ. فَقَالَ الْعَبَّاسُ: يَا رَسُولَ اللَّهِ، إلاَّ الإِذْخِرَ. فَإِنَّهُ لِقَيْنِهِمْ وَبُيُوتِهِمْ. فَقَالَ: إلاَّ الإِذْخِرَ) .","ޢަބްދު ﷲ ޞިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ މައްކާ ފަތަޙަ ކުރެއްވި ދުވަހު ޙަދީޘް ކުރެއްވިއެވެ: ފަތަޙަ ކުރެއްވުމަށް ފަހު (މައްކާއިން) ހިޖުރަ ކުރުމެއް ނުވެއެވެ. އެހެނެއްކަމަކު އޮތީ ޖިހާދު ކުރުމާއި ނިޔަތްގަތުމެވެ. އެ ގޮތުން ތިޔަބައިމީހުން (ޖިހާދަށް) ނުކުތުމަށް ގޮވާލެވޭ ހިނދު، ފަހެ ތިޔަބައިމީހުން ނުކުންނާށެވެ.\n<br><br>\nއަދިވެސް މައްކާ ފަތަޙަ ކުރެއްވި ދުވަހު ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ހަމަކަށަވަރުން ﷲ އުޑުތަކާއި ބިން ޚަލްޤު ކުރެއްވި ދުވަހު، މި ރަށް (އެބަހީ: މައްކާ) ޙުރުމަތްތެރި ކުރެއްވިއެވެ. ފަހެ ﷲ ގެ ޙުރުމަތްތެރި ކުރެއްވުމައިގެން ޤިޔާމަތަށް ދާންދެން މި ބިން ޙަރަމެއް ކަމުގައި ވެއެވެ. އަދި ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާގެ ކުރިން އެކަކަށް ވެސް މައްކާގައި ހަނގުރާމަ ކުރުން ހުއްދަ ކުރެވިފައެއް ނުވެއެވެ. ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާއަށް (މައްކާގައި ހަނގުރާމަ ކުރެއްވުން) ހުއްދަ ކުރެއްވުނީ ހަމައެކަނި ދުވާލުގެ ވަގުތުކޮޅަކަށެވެ. އެ މި ވަގުތުކޮޅެވެ. ފަހެ (އަނެއްކާވެސް) ﷲ ގެ ޙުރުމަތްތެރި ކުރެއްވުމައިގެން ޤިޔާމަތަށް ދާންދެން މި ބިން ޙަރަމެއް ކަމުގައި ވެއެވެ. މައްކާގެ ކަށިގަސްތައް އުފުރުމެއް ނުވެއެވެ. އަދި (އާންމުކޮށް އެހެން ތަންތަނުގައި) ޝިކާރަ ކުރެވޭ ޖަނަވާރުތައް އެ ތަނުގެ ފަހާ ދުވުމެއްވެސް ނުވެއެވެ. އަދި އެ ތަނުގައި ވެއްޓިގެން (ގެއްލޭ) އެއްޗެއް، އޭގެ ވެރިމީހާ ހޯދުމުގެ ގޮތުން އިޢުލާން ކުރުމަށް މެނުވީ ހޮވައިގެން ނުވެއެވެ. އަދި މައްކާގެ ވިނަގަސްތައް ކެނޑުމެއް ނުވެއެވެ.“ ދެން އަލްޢައްބާސް ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. އިޛްޚިރު (އެބަހީ: ގަނދަކޯޅި) ފިޔަވައެވެ. އެހެނީ ހަމަކަށަވަރުން ތިމަންމެންގެ ދަގަނޑު މަސައްކަތް ކުރާ ތަންތަނާއި ގެތަކުގައި (މީރުވަސް ދުއްވަން) އޭތި ބަހައްޓަންށެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އިޛްޚިރު ފިޔަވައެވެ.“","البخاري 1834 واللفظ له، ومسلم 1353"],["#227","كتاب الحج","ޙައްޖުގެ ފޮތް","باب ما يجوز قتله","މެރުން ހުއްދަ ތަކެތީގެ ބާބު","عَنْ عَائِشَةَ رضي الله عنها: أَنَّ رَسُولَ اللَّهِ - ﷺ - قَالَ: (خَمْسٌ مِنْ الدَّوَابِّ كُلُّهُنَّ فَاسِقٌ، يُقْتَلْنَ فِي الْحَرَمِ: الْغُرَابُ، وَالْحِدَأَةُ، وَالْعَقْرَبُ، وَالْفَأْرَةُ، وَالْكَلْبُ الْعَقُورُ) .\n<br><br>\nوَلِمُسْلِمٍ: (يُقْتَلُ خَمْسٌ فَوَاسِقُ فِي الْحِلِّ وَالْحَرَمِ)","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ޖަނަވާރުންގެ ތެރޭގައި ފަހެއްޗެއް ވެއެވެ. އެއިން ކޮންމެ އެއްޗަކީ ފަސާދަކޮށް (ގެއްލުން ދެނިވި) ތަކެއްޗެވެ. އަދި އެއިން ކޮންމެ އެއްޗެއް ޙަރަމުގައި ވެސް މެރުން ހުއްޓެވެ. (އެއީ:) ކާޅާއި، އެހަނޑައާއި، ނަގުލަންދާއްޓާއި، މީދާއާއި، (އަނިޔާދީ ޤަތުލުކޮށް ޝިކާރަ ކުރާ) ކުއްތާ (ނުވަތަ ސިންގާ، މިނިކާވަގު، ވަލު ކުއްތާ، ލެޕަޑު ފަދަ މަސްކާ ޖަނަވާރެވެ.)\n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ”ފަސާދަ ކުރާ ފަސް (ޖަނަވާރެއް) ޙަރަމުގައާއި އެ ނޫން ތަންނަނުގައި ވެސް މެރުން ހުއްޓެވެ.\n","البخاري 1829 واللفظ له، ومسلم 1198\n<br><br>\nمسلم 1198"],["#228","كتاب الحج","ޙައްޖުގެ ފޮތް","باب دخول مكة وغيره","މައްްކާއަށް ވަނުމާއި، އެ ނޫން ވެސް ކަންކަމުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رضي الله عنه -: (أَنَّ رَسُولَ اللَّهِ - ﷺ - دَخَلَ مَكَّةَ عَامَ الْفَتْحِ، وَعَلَى رَأْسِهِ الْمِغْفَرُ، فَلَمَّا نَزَعَهُ جَاءَهُ رَجُلٌ فَقَالَ: ابْنُ خَطَلٍ مُتَعَلِّقٌ بِأَسْتَارِ الْكَعْبَةِ فَقَالَ: اُقْتُلُوهُ) .","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: މައްކާ ފަތަޙަ ކުރެއްވި އަހަރު، ރަސޫލު ﷲ ﷺ މައްކާއަށް ވަދެވަޑައިގަތީ އިސްތަށިފުޅުގައި  ދަގަނޑު ތާކިހާއެއް ތުރުކުރައްވައިގެންނެވެ. ދެން ނަބިއްޔާ އެ ބޭލުމުން، މީހަކު އައިސް ދެންނެވިއެވެ: ކަޢުބާގެ އުރަކޮޅުގައި އިބްނު ޚަޠަލު އެލިގަނެގެން (ހިމާޔަތަށް އެދި) އެބައޮތެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭނާ ޤަތުލު ކުރާށެވެ.“ (ނޯޓު: އިބްނު ޚަޠަލަކީ، ކުރިން އިސްލާމްވެގެން ހިޖުރަ ކުރި މީހެކެވެ. އެއް ދުވަހަކު އިބްނު ޚަޠަލު ޒަކާތް އެއްކުރުމަށް ރަސޫލާ ފޮނުއްވެވިއެވެ. އަދި އޭނާއާއެކު ޚާދިމު މީހެއް ވެސް އަންޞާރުންގެ ޚުޒާޢާ ވަންހައިގެ ތެރެއިން ފޮނުއްވިއެވެ. އެ ގޮތުން ތަނެއްގައި މަޑު ކުރުމަށް ފަހު، ކާން ތައްޔާރު ކުރަން އެ ޚާދީމު މީހާ ގާތު އިބްނު ޚަޠަލު އެނގުމަށް ފަހު، ދުވާލުގެ ދެބައިކުޅަ އެއްބައި ނިދައިފިއެވެ. ދެން ހޭލެވުނު އިރު، ކާން ނަހަދާ އެ ޚާދިމު މީހާ ހުރީ ނިދާފައެވެ. ދެން ރުޅިއައިސްގެން އޭނާ ގައިގައި ތެޅިތެޅީނުންގޮސް މަރާލައިފިއެވެ. އޭނާ ހިތަށް އަރައިފިއެވެ. ތިމަންނަ މުޙައްމަދުގެ އަރިހަށް ގޮސްފިނަމަ، ބަދަލުގެ ގޮތުގައި ތިމަންނަ މަރާނެއެވެ. ދެން އޭނާ މުރުތައްދުވެ، ޒަކާތުގެ މުދާކޮޅު އަތުލައިގެން މައްކާއަށް ފިލާ ރައްކާވެއްޖެއެވެ. އަދި މުޝްރިކުންނާއެކު އެބައިމީހުންގެ ދީން މަތީ އުޅުނެވެ. އަދި މީގެ އިތުރަށް، އޭނާ ބުނެގެން ނަބިއްޔާއަށް ހަޖޫ ޖަހާއި ފުރައްސާރަކޮށް ލަވަ ކިޔާހަދައި ދެ އަންހެން އަޅުން އޭނާ ގެންގުޅުނެވެ. ދެން މައްކާ ފަތަޙަ ކުރެއްވި ދުވަހު، މުސްލިމުން އަންނަކަން އެނގިގެން، އިބްނު ޚަޠަލު ދަގަނޑު ދަހަނާ އަޅައިގެން، ހަތިޔާރު ހިފައިގެން އަސްމަތީ ނުކުތެވެ. އެކަމަކު މުސްލިމުންގެ ލަޝްކަރު ފެނިގެން ބިރުންގޮސް އެނބުރި ކަޢުބާގެ އުރަކޮޅުތަކުތެރެއަށް ވަނެވެ.)","البخاري 1846، ومسلم 1357 واللفظ له"],["#229","كتاب الحج","ޙައްޖުގެ ފޮތް","باب دخول مكة وغيره","މައްްކާއަށް ވަނުމާއި، އެ ނޫން ވެސް ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رضي الله عنهما: (أَنَّ رَسُولَ اللَّهِ - ﷺ - دَخَلَ مَكَّةَ مِنْ كَدَاءٍ، مِنْ الثَّنِيَّةِ الْعُلْيَا الَّتِي بِالْبَطْحَاءِ، وَخَرَجَ مِنْ الثَّنِيَّةِ السُّفْلَى) .","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ މައްކާއަށް ވަދެވަޑައިގެންނެވީ ބަޠުޙާގައިވާ ކަދާއޭ ކިޔުނު، ޘަނިއްޔަތުލް ޢުލްޔާގެ އުސް މަގުންނެވެ. (މިހާރު މައްކާގެ ގަބުރުސްތާނުގެ މެދުންލާފައި މި މަގު ހިނގައެވެ.) އަދި ނަބިއްޔާ ނުކުމެވަޑައިގަތީ (ކުދާއޭ ކިޔުނު) ޘަނިއްޔަތުއް ސުފްލާއޭ ކިޔުނު އުސް މަގުންނެވެ.","البخاري 1576 واللفظ له، ومسلم 1257"],["#230","كتاب الحج","ޙައްޖުގެ ފޮތް","باب دخول مكة وغيره","މައްްކާއަށް ވަނުމާއި، އެ ނޫން ވެސް ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رضي الله عنهما قَالَ: (دَخَلَ رَسُولُ اللَّهِ - ﷺ - الْبَيْتَ، وَأُسَامَةُ بْنُ زَيْدٍ وَبِلالٌ وَعُثْمَانُ بْنُ طَلْحَةَ، فَأَغْلَقُوا عَلَيْهِمْ الْبَابَ فَلَمَّا فَتَحُوا: كُنْتُ أَوَّلَ مَنْ وَلَجَ. فَلَقِيتُ بِلالاً، فَسَأَلَتْهُ: هَلْ صَلَّى فِيهِ رَسُولُ اللَّهِ - ﷺ -؟ قَالَ: نَعَمْ، بَيْنَ الْعَمُودَيْنِ الْيَمَانِيَيْنِ) .","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ އާއި، އުސާމާ ބިން ޒައިދާއި، ބިލާލާއި، ޢުޘްމާން ބިން ޠަލްޙާ ގެފުޅަށް ވަދެވަޑައިގަތެވެ. އަދި އެ ބޭކަލުންގެ ފަހަތުން ދޮރުކޮޅު ލައްޕައިފިއެވެ. ފަހެ އެބޭކަލުން ދޮރުކޮޅު ހުޅުވާލެއްވުމުން، ދެން އެންމެ ފުރަތަމަ ވަނީ ތިމަންނައެވެ. އަދި ބިލާލާ ބައްދަލުވުމުން ތިމަން ސުވާލު ކުރީމެވެ: ގެފުޅު ތެރޭގައި ރަސޫލު ﷲ ﷺ ނަމާދު ކުރެއްވިތޯއެވެ؟ ބިލާލު ދެންނެވިއެވެ: އާދޭހެވެ. ކުރެއްވީ ޔަމާނީ ދެ ތަނބު ދެމެދުގައެވެ. (އެބަހީ: ޙަޖަރުލް އަސްވަދުގެ ތަނބާއި ރުކުނުލް ޔަމާނީގެ ތަނބެވެ.)","البخاري 1598 واللفظ له، ومسلم 1329"],["#231","كتاب الحج","ޙައްޖުގެ ފޮތް","باب دخول مكة وغيره","މައްްކާއަށް ވަނުމާއި، އެ ނޫން ވެސް ކަންކަމުގެ ބާބު","عَنْ عُمَرَ - رضي الله عنه - (أَنَّهُ جَاءَ إلَى الْحَجَرِ الأَسْوَدِ، فَقَبَّلَهُ. وَقَالَ: إنِّي لأَعْلَمُ أَنَّك حَجَرٌ، لا تَضُرُّ وَلا تَنْفَعُ، وَلَوْلا أَنِّي رَأَيْتُ النَّبِيَّ - ﷺ - يُقَبِّلُكَ مَا قَبَّلْتُكَ) .","ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: އެކަލޭގެފާނު ޙަޖަރުލް އަސްވަދަށް އައިސް ބޮސްދެއްވިއެވެ. އަދި ދެންނެވިއެވެ: ހަމަކަށަވަރުން ތިޔައީ ގެއްލުމެއް ވެސް އަދި ފައިދާއެއް ވެސް ނުދެވޭ ހިލައެއްކަން ތިމަން ދެނަހުރީމެވެ. އަދި ނަބިއްޔާ ﷺ ތިޔަށް ބޮސްދެއްވިތަން ތިމަންނައަށް ފެނިފައި ނުވާ ނަމަ، ތިޔަކަށް ބޮހެއް ނުދިނީމުހެވެ.","البخاري 1597 واللفظ له، ومسلم 1270"],["#232","كتاب الحج","ޙައްޖުގެ ފޮތް","باب دخول مكة وغيره","މައްްކާއަށް ވަނުމާއި، އެ ނޫން ވެސް ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رضي الله عنهما قَالَ: (لَمَّا قَدِمَ رَسُولُ اللَّهِ - ﷺ - وَأَصْحَابُهُ مَكَّةَ. فَقَالَ الْمُشْرِكُونَ: إنَّهُ يَقْدَمُ عَلَيْكُمْ قَوْمٌ وَهَنَتْهُمْ حُمَّى يَثْرِبَ. فَأَمَرَهُمْ النَّبِيُّ - ﷺ - أَنْ يَرْمُلُوا الأَشْوَاطَ الثَّلاثَةَ، وَأَنْ يَمْشُوا مَا بَيْنَ الرُّكْنَيْنِ، وَلَمْ يَمْنَعْهُمْ أَنْ يَرْمُلُوا الأَشْوَاطَ كُلَّهَا: إلاَّ الإِبْقَاءُ عَلَيْهِمْ) .","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ އާއި އެކަލޭގެފާނުގެ ޞަޙާބީން މައްކާއަށް ވަޑައިގަތެވެ. ފަހެ މުޝްރިކުން ބުންޏެވެ: ހަމަކަށަވަރުން ޔަޘްރިބު (އެބަހީ: މަދީނާގެ) ހުމުގެ ސަބަބުން ބަލިކަށިވެފައިވާ ބަޔަކު ތިޔަބައިމީހުންގެ ގާތަށް އެބަ އާދެއެވެ. ދެން ނަބިއްޔާ ﷺ ޞަޙާބީންގެ (ފިރިހެނުންގެ) ގާތު (ޠަވާފުގެ) ފުރަތަމަ ތިން ބުރުގައި ބާރަށް ހިނގަން  އެންގެވިއެވެ. އަދި (އެ ކަމުގެ ތެރޭގައި، ޙަޖަރުލް އަސްވަދާއި ރުކުނުލް ޔަމާނީގެ) ދެ ރުކުން ދެމެދު (އާދައިގެ މަތިން) ހިނގާފައި ދިޔުމަށް އެންގެވިއެވެ. އަދި މުޅި (ހަތް) ބުރުގައި ބާރަށް ހިނގާފައި (ޠަވާފު ކުރަން) ނޭނގެވީ އެބައިމީހުންނަށް ރަޙުމް ކުރެއްވުމުގެ ސަބަބުންނެވެ. ","البخاري 1602 واللفظ له، ومسلم 1266"],["#233","كتاب الحج","ޙައްޖުގެ ފޮތް","باب دخول مكة وغيره","މައްްކާއަށް ވަނުމާއި، އެ ނޫން ވެސް ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رضي الله عنهما قَالَ: (رَأَيْتُ رَسُولَ اللَّهِ - ﷺ - حِينَ يَقْدَمُ مَكَّةَ إذَا اسْتَلَمَ الرُّكْنَ الأَسْوَدَ - أَوَّلَ مَا يَطُوفُ - يَخُبُّ ثَلاثَةَ أَشْوَاطٍ) .","ޢަބްދު ﷲ ބިން ޢަމްރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ މައްކާއަށް ވަދެވަޑައިގެންނެވި ހިނދު ތިމަން ދުށީމެވެ. ނަބިއްޔާ ތަވާފު ކުރައްވަން ފެއްޓެވީ ޙަޖަރުލް އަސްވަދުގައި ބީއްސަވައިގެންނެވެ. އަދި (ޠަވާފުގެ) ފުރަތަމަ ތިން ބުރުގައި ނަބިއްޔާ ބާރަށް ހިނގައިވަޑައިގަތެވެ.","البخاري 1603، ومسلم 1261"],["#234","كتاب الحج","ޙައްޖުގެ ފޮތް","باب دخول مكة وغيره","މައްްކާއަށް ވަނުމާއި، އެ ނޫން ވެސް ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رضي الله عنهما قَالَ: (طَافَ النَّبِيُّ - ﷺ - فِي حَجَّةِ الْوَدَاعِ عَلَى بَعِيرٍ، يَسْتَلِمُ الرُّكْنَ بِمِحْجَنٍ) .","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ޙައްޖަތުލް ވަދާޢުގައި ނަބިއްޔާ ﷺ ޠަވާފު ކުރެއްވީ ޖަމަލުކޮޅު މަތީގައެވެ. އަދި ޙަޖަރުލް އަސްވަދުގައި ނަބިއްޔާ ބީއްސެވީ އަސާކޮޅަކުންނެވެ.","البخاري 1607 واللفظ له، ومسلم 1272"],["#235","كتاب الحج","ޙައްޖުގެ ފޮތް","باب دخول مكة وغيره","މައްްކާއަށް ވަނުމާއި، އެ ނޫން ވެސް ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رضي الله عنهما قَالَ: (لَمْ أَرَ النَّبيَّ - ﷺ - يَسْتَلِمُ مِنْ الْبَيْتِ إلاَّ الرُّكْنَيْنِ الْيَمَانِيَيْنِ) .","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ޔަމާނީ ދެ ތަނބުގައި (އެބަހީ: ޙަޖަރުލް އަސްވަދާއި ރުކުނުލް ޔަމާނީ) ފިޔަވައި ގެފުޅުގެ އެހެން ތަނެއްގައި ނަބިއްޔާ ﷺ  ބީއްސެވި ތަނެއް ތިމަން ނުދެކެމެވެ.","البخاري 1609 واللفظ له، ومسلم 1267"],["#236","كتاب الحج","ޙައްޖުގެ ފޮތް","باب التمتع","ތަމައްތުޢުގެ ބާބު","عَنْ أَبِي جَمْرَةَ نَصْرِ بْنِ عِمْرَانَ الضُّبَعِيِّ، قَالَ: (سَأَلْتُ ابْنَ عَبَّاسٍ عَنْ الْمُتْعَةِ؟ فَأَمَرَنِي بِهَا، وَسَأَلَتْهُ عَنْ الْهَدْيِ؟ فَقَالَ: فِيهِ جَزُورٌ، أَوْ بَقَرَةٌ، أَوْ شَاةٌ، أَوْ شِرْكٌ فِي دَمٍ قَالَ: وَكَانَ نَاسٌ كَرِهُوهَا، فَنِمْتُ. فَرَأَيْتُ فِي الْمَنَامِ: كَأَنَّ إنْسَاناً يُنَادِي: حَجٌّ مَبْرُورٌ، وَمُتْعَةٌ مُتَقَبَّلَةٌ. فَأَتَيْتُ ابْنَ عَبَّاسٍ فَحَدَّثَتْهُ. فَقَالَ: اللَّهُ أَكْبَرُ سُنَّةُ أَبِي الْقَاسِمِ - ﷺ -) .","އަބޫ ޖަމަރާ، ނަޞްރު ބިން ޢިމްރާން އައްޟުބަޢީ ރިވާ ކުރެއްވިއެވެ: (ތިމަންނަ ތަމައްތުޢު ކުރުމުން ބަޔަކު މީހުން އެކަން ނުކުރުމަށް ތިމަން ގާތު ދެންނެވިއެވެ. ދެން) އިބްނު ޢައްބާސް ގާތު ތަމަތުޢު ކުރުމާމެދު ތިމަން ސުވާލު ކުރީމެވެ. ފަހެ ތަމައްތުޢު ކުރުމަށް އިބްނު ޢައްބާސް ތިމަންނައަށް އެންގެވިއެވެ. ދެން ހަދްޔު ކަތިލުމާމެދު ތިމަން ސުވާލު ކުރީމެވެ. އިބްނު ޢައްބާސް ދެންނެވިއެވެ: އޭގައި ކަތިލަން ޖެހޭނީ ޖަމަލެއް، ނުވަތަ ގެރިއެއް، ނުވަތަ ކަންބަޅިއެކެވެ. ނުވަތަ އެހެން ބަޔަކާއެކު ބައިވެރިވާ ގޮތަށް ހަދްޔު ކަތިލެވިދާނެއެވެ. އަބޫ ޖަމަރާ ދެންނެވިއެވެ: ތަމައްތުޢު ކުރުމާމެދު ބަޔަކު މީހުން ނުރުހޭ ފަދައެވެ. ދެން ތިމަންނަ ނިދައިފީމެވެ. ފަހެ ނިދީގައި އިންސާނަކު (މިފަދައިން) ގޮވާލަނިކޮށް ތިމަންނައަށް ފެނުނެވެ: މަބްރޫރު ޙައްޖެކެވެ. އަދި ގަބޫލު ވެވިގެންވާ ތަމައްތުޢެކެވެ. ދެން އިބްނު ޢައްބާސް رَضِيَ اللَّهُ عَنْهُ އަރިހަށް އައިސް އެ ވާހަކަ ދެންނެވީމެވެ. އިބްނު ޢައްބާސް ދެންނެވިއެވެ: ﷲ އަކްބަރެވެ. އަބުލް ޤާސިމް (އެބަހީ: ރަސޫލު ﷲ) ﷺ ގެ ސުންނަތެވެ.","البخاري 1688 واللفظ له، ومسلم 1242"],["#237","كتاب الحج","ޙައްޖުގެ ފޮތް","باب التمتع","ތަމައްތުޢުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رضي الله عنهما قَالَ: (تَمَتَّعَ رَسُولُ اللَّهِ - ﷺ - فِي حَجَّةِ الْوَدَاعِ بِالْعُمْرَةِ إلَى الْحَجِّ وَأَهْدَى. فَسَاقَ مَعَهُ الْهَدْيَ مِنْ ذِي الْحُلَيْفَةِ. وَبَدَأَ رَسُولُ اللَّهِ - ﷺ - وَأَهَلَّ بِالْعُمْرَةِ، ثُمَّ أَهَلَّ بِالْحَجِّ، فَتَمَتَّعَ النَّاسُ مَعَ رَسُولِ اللَّهِ - ﷺ - فَأَهَلَّ بِالْعُمْرَةِ إلَى الْحَجِّ، فَكَانَ مِنْ النَّاسِ مَنْ أَهْدَى، فَسَاقَ الْهَدْيَ مِنْ الْحُلَيْفَةِ. وَمِنْهُمْ مَنْ لَمْ يُهْدِ فَلَمَّا قَدِمَ رَسُولُ اللَّهِ - ﷺ - قَالَ لِلنَّاسِ: مَنْ كَانَ مِنْكُمْ أَهْدَى، فَإِنَّهُ لا يَحِلُّ مِنْ شَيْءٍ حَرُمَ مِنْهُ حَتَّى يَقْضِيَ حَجَّهُ. وَمَنْ لَمْ يَكُنْ أَهْدَى فَلْيَطُفْ بِالْبَيْتِ وَبِالصَّفَا وَالْمَرْوَةِ، وَلْيُقَصِّرْ وَلْيَحْلِلْ، ثُمَّ لِيُهِلَّ بِالْحَجِّ وَلْيُهْدِ، فَمَنْ لَمْ يَجِدْ هَدْياً فَلْيَصُمْ ثَلاثَةَ أَيَّامٍ فِي الْحَجِّ وَسَبْعَةً إذَا رَجَعَ إلَى أَهْلِهِ فَطَافَ رَسُولُ اللَّهِ - ﷺ - حِينَ قَدِمَ مَكَّةَ. وَاسْتَلَمَ الرُّكْنَ أَوَّلَ شَيْءٍ، ثُمَّ خَبَّ ثَلاثَةَ أَطْوَافٍ مِنْ السَّبْعِ، وَمَشَى أَرْبَعَةً، وَرَكَعَ حِينَ قَضَى طَوَافَهُ بِالْبَيْتِ عِنْدَ الْمَقَامِ رَكْعَتَيْنِ، ثُمَّ انْصَرَفَ فَأَتَى الصَّفَا، وَطَافَ بِالصَّفَا وَالْمَرْوَةِ سَبْعَةَ أَطْوَافٍ، ثُمَّ لَمْ يَحْلِلْ مِنْ شَيْءٍ حَرُمَ مِنْهُ حَتَّى قَضَى حَجَّهُ، وَنَحَرَ هَدْيَهُ يَوْمَ النَّحْرِ. وَأَفَاضَ فَطَافَ بِالْبَيْتِ، ثُمَّ حَلَّ مِنْ كُلِّ شَيْءٍ حَرُمَ مِنْهُ، وَفَعَلَ مِثْلَ مَا فَعَلَ رَسُولُ اللَّهِ - ﷺ -: مَنْ أَهْدَى وَسَاقَ الْهَدْيَ مِنْ النَّاسِ) .","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ޙައްޖަތުލް ވަދާޢުގައި، ތަމައްތުޢުކޮށް ޢުމްރާއަށް ފަހު ޙައްޖު ކުރެއްވުމަށް ރަސޫލު ﷲ ﷺ (އަމުރު ކުރެއްވިއެވެ.) (ނަބިއްޔާ ޙައްޖު ކުރެއްވީ ޤީރާންކޮށެވެ.) އަދި ހަދްޔު ކަތިލައްވަން (މަދީނާގެ މީޤާތުކަމަށްވާ) ޛުލް ޙުލައިފާއިން ނަބިއްޔާއާއެކު ހަދްޔު ގެންނެވިއެވެ. އަދި ނަބިއްޔާ އިޙްރާން ބަނެ ޢުމްރާއާއި ޙައްޖަށް ތަލްބިޔާ ވިދާޅުވިއެވެ. (އެބަހީ: ލައްބައިކަ ބިޢުމްރަތިން ވަޙައްޖިން، މިފަދައިން ވިދާޅުވިއެވެ.) އަދި ނަބިއްޔާގެ އަރިހުގައި ތިއްބެވި މީސްތަކުން (އާޚިރުގައި ޙައްޖު ކުރީ) ތަމައްތުޢުކޮށެވެ. (އެބައިމީހުން ކުރެ ގިނަ މީހުން ކުރިން ފެއްޓީ އިފްރާދުކޮށެވެ.) އެ ގޮތުން މީސްތަކުންގެ ތެރޭގައި ހަދްޔު ކަތިލަން ޛުލް ޙުލައިފާއިން ހަދްޔު ގެނައި ބަޔަކު ތިއްބެވެ. އަދި ހަދްޔު ނުގެންނަ ބަޔަކުވެސް ތިއްބެވެ. ދެން ނަބިއްޔާ ﷺ މައްކާއަށް ވަޑައިގަތް ހިނދު މީސްތަކުންނަށް ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ ތެރެއިން ހަދްޔު ގެނައި މީހަކު، ފަހެ ހަމަކަށަވަރުން އޭނާގެ ޙައްޖު އަދާކޮށް ނިމެންދެން (މުޙްރިމަކަށް) ޙަރާމްވާ އެއްވެސް ކަމަކުން އިޙްރާމް ނުކަނޑާ ހުންނާށެވެ. އަދި ތިޔަބައިމީހުންގެ ތެރެއިން ހަދްޔު ނުގެންނަ މީހަކު، ފަހެ ގެފުޅު ވަށައި ޠަވާފުކޮށް، ޞަފާ މަރްވާ ދެމެދު ސަޢުޔުކޮށް، ބޯކޮށައި އިޙްރާމް ކަނޑާށެވެ. ދެން އެ މީހަކު އަނެއްކާ ޙައްޖަށް އިޙްރާމް ބަނެ ތަލްބިޔާ ކިޔާށެވެ. (އެބަހީ: ލައްބައިކަ ޙައްޖަން ކިޔާށެވެ.) އެއަށްފަހު ހަދްޔު ކަތިލާށެވެ. އަދި ހަދްޔު ކަތިލުމަށް ޖަނަވާރެއް ނުލިބުނު މީހާ، ފަހެ ޙައްޖުގައި ތިން ދުވަހު ރޯދަ ހިފާށެވެ. އަދި އެނބުރި އާއިލާ ކައިރިއަށް ދެވުމުން (އެބަހީ: ގައުމަށް ދެވުމުން) ހަތް ދުވަހު ރޯދާ ހިފާށެވެ.“ ފަހެ ރަސޫލު ﷲ ﷺ މައްކާއަށް ވަދެވަޑައިގަތް ހިނދު ޠަވާފު ކުރެއްވިއެވެ. އެ ގޮތުން ފުރަތަމަ ޙަޖަރުލް އަސްވަދުގައި ބީއްސެވިއެވެ. އަދި ޠަވާފުގެ ހަތް ބުރުން ކުރެ ފުރަތަމަ ތިން ބުރުގައި ބާރަށް ހިނގައިވަޑައިގަތެވެ. އަދި އަނެއް ހަތަރު ބުރުގައި (އާދައިގެ މަތިން) ހިނގައިވަޑައިގަތެވެ. ދެން ޠަވާފުން އަވަދިވުމުން، މަޤާމު އިބްރާހީމްގެ (ފަހަތުގައި) ދެ ރަކުޢަތް ކުރެއްވިއެވެ. ދެން ސަލާމް ދެއްވުމަށް ފަހު ސަފާއަށް ވަޑައިގަތެވެ. އަދި ޞަފާ މަރްވާ ދެމެދު ހަތް ބުރު ޖެއްސެވިއެވެ. އަދި ޙައްޖު އަދާކޮށް ނިމެންދެން (މުޙްރިމަކަށް) ޙަރާމްވާ އެއްވެސް ކަމަކުން އިޙްރާމް ނުކަނޑުވައެވެ. ދެން ނަޙްރުގެ ދުވަހު (އެބަހީ: އަޟްޙާ ޢީދު ދުވަހު) ނަބިއްޔާގެ ހަދްޔު ކަތިލެއްވިއެވެ. އެއަށްފަހު (ގެފުޅަށް) ވަޑައިގެން ޠަވާފުލް އިފާޟާ ކުރެއްވިއެވެ. ދެން އިޙްރާމް ކެނޑުއްވިއެވެ. އަދި މީސްތަކުންގެ ތެރެއިން ހަދްޔު ގެނައި ބަޔަކު ވެސް ކަންތައް ކުރީ، ރަސޫލު ﷲ ﷺ ކަންތައް ކުރެއްވި ގޮތަށެވެ.","البخاري 1691، ومسلم 1227 واللفظ له"],["#238","كتاب الحج","ޙައްޖުގެ ފޮތް","باب التمتع","ތަމައްތުޢުގެ ބާބު","عَنْ حَفْصَةَ زَوْجِ النَّبِيِّ - ﷺ - أَنَّهَا قَالَتْ: (يَا رَسُولَ اللَّهِ، مَا شَأْنُ النَّاسِ حَلُّوا مِنْ الْعُمْرَةِ وَلَمْ تَحِلَّ أَنْتَ مِنْ عُمْرَتِكَ فَقَالَ: إنِّي لَبَّدْتُ رَأْسِي، وَقَلَّدْتُ هَدْيِي، فَلا أَحِلُّ حَتَّى أَنْحَرَ) .","ނަބިއްޔާގެ އަނބިކަނބަލުން ޙަފްޞާ ރިވާ ކުރެއްވިއެވެ: އެ ކަމަނާ ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. އިބަ ނަބިއްޔާ ޢުމްރާއިން އިޙްރާމް ނުކަނޑުއްވާ ޙާލު، މީސްތަކުން ޢުމްރާއިން އިޙްރާމް ކެނޑީ ކޮންކަމަކާތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާގެ އިސްތަށިފުޅު ތަލްބީދު ކުރެއްވީމެވެ. (އެބަހީ: އެއްޗެއް ބޭނުންކޮށްގެން އިސްތަށިތައް ތަތްކޮށް ހަރުކުރެއްވީމެވެ. މިއީ ބޯހޭވިޔަ އުކުނު އެޅުނަ ނުދިނުމަށްޓަކައި ދިގު ދަތުރުތަކުގައި މީހުން ކޮށްއުޅޭ ކަމެކެވެ.) އަދި ތިމަން ނަބިއްޔާގެ ހަދްޔުގެ ކަރުގައި ޤަލާއިދު ފަށް އަޅުއްވައިފީމެވެ. (މިއީ ހަދްޔުކަން އަނގައިދޭ ފާހަގައެކެވެ.) ފަހެ ތިމަން ނަބިއްޔާ ހަދްޔު ކަތިލައިފުމަށް ދާންދެން އިޙްރާމް ނުކަނޑުއްވާނަމެވެ. (ނޯޓު: ހަދްޔު ނުގެންނަ މީހުންނަށް ނަބިއްޔާ އެންގެވީ ތަމައްތުޢު ކުރެއްވުމަށެވެ. އެ ގޮތުން އެބައިމީހުން އިޙްރާމް ކެނޑުމަށްފަހު ދެވަނަ ފަހަރަށް އިޙްރާމް ބަނެވެ. އަދި ނަބިއްޔާއާއި ހަދްޔު ގެންނެވި އެހެން މީހުން އިޙްރާމް ނުކަނޑާ ހުންނެވިއެވެ.)","البخاري 1566 واللفظ له، ومسلم 1229"],["#239","كتاب الحج","ޙައްޖުގެ ފޮތް","باب التمتع","ތަމައްތުޢުގެ ބާބު","عَنْ عِمْرَانَ بْنِ حُصَيْنٍ قَالَ: (أُنْزِلَتْ آيَةُ الْمُتْعَةِ فِي كِتَابِ اللَّهِ تَعَالَى. فَفَعَلْنَاهَا مَعَ رَسُولِ اللَّهِ - ﷺ - وَلَمْ يَنْزِلْ قُرْآنٌ يُحَرِّمُهَا، وَلَمْ يَنْهَ عَنْهَا حَتَّى مَاتَ. قَالَ رَجُلٌ بِرَأْيِهِ مَا شَاءَ) قَالَ الْبُخَارِيُّ \" يُقَالُ: «إنَّهُ عُمَرُ» .\n<br><br>\nوَلِمُسْلِمٍ (نَزَلَتْ آيَةُ الْمُتْعَةِ - يَعْنِي مُتْعَةَ الْحَجِّ - وَأَمَرَنَا بِهَا رَسُولُ اللَّهِ - ﷺ - ثُمَّ لَمْ تَنْزِلْ آيَةٌ تَنْسَخُ آيَةَ مُتْعَةِ الْحَجِّ وَلَمْ يَنْهَ عَنْهَا رَسُولُ اللَّهِ - ﷺ - حَتَّى مَاتَ)\n<br><br>\nوَلَهُمَا بِمَعْنَاهُ.","ޢިމްރާން ބިން ޙުޞައިން رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ﷲ ގެ ފޮތުގައި ތަމައްތުޢު ކުރުމުގެ އާޔަތް ބާވައިލެއްވުނެވެ. ފަހެ ރަސޫލު ﷲ ﷺ ގެ ހުންނަވަނިކޮށް ތިމަންމެން އެކަން ކުރީމެވެ. އަދި އެކަން މަނާ ކުރާ (އާޔަތެއް) ޤުރްއާނުން ބާވައިނުލައްވައެވެ. އަދި ނަބިއްޔާ އަވަހާރަފުޅުވުމަށް ދާންދެން ވެސް އެކަން ނަހީ ނުކުރައްވައެވެ. އަދި (ނަހީ ކުރެއްވި) ބޭކަލަކު، ފަހެ އެ ބޭކަލަކަށް ފެނުނު ރައުޔެއް ދެންނެވީއެވެ. އިމާމު ބުޚާރީ ވިދާޅުވިއެވެ: އެ ފަދައިން ދެންނެވި ބޭކަލަކީ ޢުމަރު ކަމަށް ބުނެވުނެވެ.\n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: (ޙައްޖުގައި) ތަމައްތުޢު ކުރުމުގެ އާޔަތް ބާވައިލެއްވުނެވެ. އަދި ރަސޫލު ﷲ ﷺ އެކަން ކުރަން އަމުރު ކުރެއްވިއެވެ. އެއަށްފަހު ޙައްޖުގައި ތަމައްތުޢު ކުރުން މަންސޫޚަ ކުރުވާ އެހެން އާޔަތެއް ބާވައިނުލައްވައެވެ. އަދި ނަބިއްޔާ އަވަހާރަފުޅުވުމަށް ދާންދެން ވެސް އެކަން ނަހީ ނުކުރައްވައެވެ.\n<br><br>\nމުސައްނިފު ވިދާޅުވިއެވެ: އަދި ބުޚާރީއާއި މުސްލިމްގައި މި މާނައިގައި އެހެން ރިވާޔަތްތައް އައިސްފައިވެއެވެ.","البخاري 4518\n<br><br>\nمسلم 1226\n<br><br>\nالبخاري 1571، ومسلم 1226"],["#240","كتاب الحج","ޙައްޖުގެ ފޮތް","باب الهدي","ހަދްޔުގެ ބާބު","عَنْ عَائِشَةَ رضي الله عنها قَالَتْ: (فَتَلْتُ قَلائِدَ هَدْيِ رَسُولِ اللَّهِ - ﷺ - ثُمَّ أَشْعَرْتُهَا وَقَلَّدَهَا - أَوْ قَلَّدْتُهَا - ثُمَّ بَعَثَ بِهَا إلَى الْبَيْتِ. وَأَقَامَ بِالْمَدِينَةِ، فَمَا حَرُمَ عَلَيْهِ شَيْءٌ كَانَ لَهُ حِلاًّ) .","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާގެ ހަދްޔުތަކުގައި އަޅުއްވަން ތިމަން ކަމަނާ ޤަލާއިދުގެ ހެއްދެވީމެވެ. ދެން ނަބިއްޔާ އެ (ޖަމަލު) ހަދްޔުތަކުގެ ގޮންގަނޑުތައް (ކަނާތް ފަޅިން) ކަނޑުއްވާ ލޭ ފޭބިޔަ ދިނެވެ. އަދި (ކަންބަޅި ބަކަރި) ހަދްޔުތަކުގެ ކަރުގައި ޤަލާއިދު ފަށް އެޅުއްވިއެވެ. (މިއީ ހަދްޔުކަން ފާހަގަ ކުރަން ކުރާ ކަމެކެވެ.) ދެން (އަބޫ ބަކުރާއެކު) ގެފުޅަށް ހަދްޔުތައް ފޮނުއްވުމަށް ފަހު، މަދީނާގައި ނަބިއްޔާ ހުންނެވި އިރު، (މުޙްރިމަކަށް) ޙަރާމްވާ އެއްވެސް ކަމެއް ނަބިއްޔާއަށް ޙަރާމެއް ނުވެއެވެ. (އެއީ ހަދްޔުތައް ކަތިލެވުމަށް ދާންދެން ވެސް މެއެވެ.) (ނޯޓު: މިއީ ނަބިއްޔާ ޙައްޖު ކުރެއްވި ފަހަރެއް ނޫނެވެ. އެހެނަސް މިއީ ޙިޖުރައިގެ ނުވަވަނަ އަހަރު މީސްތަކުންނައިގެން އަބޫ ބަކުރު ޙިޖުރަ ކުރެއްވި ފަހަރެވެ.)","البخاري 1699 واللفظ له، ومسلم 1321"],["#241","كتاب الحج","ޙައްޖުގެ ފޮތް","باب الهدي","ހަދްޔުގެ ބާބު","عَنْ عَائِشَةَ رضي الله عنها قَالَتْ: (أَهْدَى رَسُولُ اللَّهِ - ﷺ - مَرَّةً غَنَماً) .","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ އެއްފަހަރު ކަންބަޅިއެއް ހަދްޔުގެ ގޮތުގައި ފޮނުއްވިއެވެ. (އަދި އޭގެ ކަރުގައި ޤަލާއިދު ފަށް އެޅުއްވިއެވެ.)","البخاري 1701 واللفظ له، ومسلم 1321"],["#242","كتاب الحج","ޙައްޖުގެ ފޮތް","باب الهدي","ހަދްޔުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رضي الله عنه -: (أَنَّ نَبِيَّ اللَّهِ - ﷺ - رَأَى رَجُلاً يَسُوقُ بَدَنَةً، فَقَالَ: ارْكَبْهَا. قَالَ: إنَّهَا بَدَنَةٌ. قَالَ ارْكَبْهَا. فَرَأَيْتُهُ رَاكِبَهَا، يُسَايِرُ النَّبِيَّ - ﷺ -) .\n<br><br>\nوَفِي لَفْظٍ قَالَ فِي الثَّانِيَةِ، أَوْ الثَّالِثَةِ: (ارْكَبْهَا. وَيْلَكَ، أَوْ وَيْحَكَ) .","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: (ހަދްޔުގެ) ޖަމަލެއް މީހަކު ހިނގުވާފައި ގެންދަނިކޮށް ނަބިއްޔާ  ﷺ އަށް ފެނިވަޑައިގަތުމުން ޙަދީޘް ކުރެއްވިއެވެ: ”އެއަށް ސަވާރުވާށެވެ.“ އޭނާ ދެންނެވިއެވެ: ހަމަކަށަވަރުން އެއީ (ހަދްޔުގެ) ޖަމަލެކެވެ. ނަބިއްޔާ އަނެއްކާވެސް ޙަދީޘް ކުރެއްވިއެވެ: ”އެއަށް ސަވާރުވާށެވެ.“ ދެން ނަބިއްޔާ އެނގެވި ގޮތަށް އެ މީހަކު އެއަށް ސަވާރުވިތަން ތިމަން ދުށީމެވެ.\n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ދެވަނަ ނުވަތަ ތިން ވަނަ ފަހަރު (އެއަށް ސަވާރުވާން އަންގަން ޖެހުމުން) ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެއަށް ސަވާރުވާށެވެ. ތިބާއަށް ހަލާކު ހުއްޓެވެ.“ (ނޯޓު: މިއީ ޢަރަބީންގެ ދޫމަތިން ބުނެ އުޅޭ އިބާރާތެކެވެ. ބަދު ދުޢާ ކުރުމުގެ މާނައިގައި ބުނާ ބަހެއް ނޫނެވެ. އެ ގޮތުން ނަބިއްޔާއަށް ވަގުތުން ތަބާނުވީމާ އޭނާ އަދަބުވެރި ކުރުވުމުގެ ގޮތުން ވިދާޅުވި ބަސްފުޅެކެވެ. އެހެނީ ޖާހިލިއްޔަ ޒަމާނުގައި މީހުން ގަބޫލު ކުރީ ހަދްޔުތަކަށް ސަވާރުވެ ނޫޅެވޭނެ ކަމަށެވެ.)","البخاري 1706، ومسلم 1322\n<br><br>\nالبخاري 1689، 2755، ومسلم 1322"],["#243","كتاب الحج","ޙައްޖުގެ ފޮތް","باب الهدي","ހަދްޔުގެ ބާބު","عَنْ عَلِيِّ بْنِ أَبِي طَالِبٍ - رضي الله عنه - قَالَ: (أَمَرَنِي رَسُولُ اللَّهِ - ﷺ - أَنْ أَقُومَ عَلَى بُدْنِهِ، وَأَنْ أَتَصَدَّقَ بِلَحْمِهَا وَجُلُودِهَا وَأَجِلَّتِهَا، وَأَنْ لا أُعْطِيَ الْجَزَّارَ مِنْهَا شَيْئًا) . وَقَالَ: نَحْنُ نُعْطِيهِ مِنْ عِنْدِنَا.","ޢަލީ ބިން އަބީ ޠާލިބު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ގެ (ހަދްޔު) ޖަމަލުތައް ބަލަހައްޓަން ތިމަންނައަށް އެނގެވިއެވެ. އަދި އެ ތަކެތީގެ މަހާއި، ހަމާއި އަދި ބުރަކަށީގައި އަޅުވާ ފޮތިގަނޑު ވެސް ޞަދަޤާތް ކުރުމަށް ތިމަންނައަށް އެނގެވިއެވެ. އަދި (ކަތިލުމަށް ދެވޭ ބަދަލެއްގެ ގޮތުން) ކަތިލާމީހާއަށް އެއްވެސް އެއްޗެއް އެއިން ނުދިނުމަށް އެނގެވިއެވެ. އަދި ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ކަތިލާމީހާއަށް (އަގު) ދޭނީ ތިމަންމެންގެ އަމިއްލަ މުދަލުންނެވެ.“","البخاري 1707، ومسلم 1317 واللفظ له"],["#244","كتاب الحج","ޙައްޖުގެ ފޮތް","باب الهدي","ހަދްޔުގެ ބާބު","عَنْ زِيَادِ بْنِ جُبَيْرٍ قَالَ: (رَأَيْتُ ابْنَ عُمَرَ أَتَى عَلَى رَجُلٍ قَدْ أَنَاخَ بَدَنَتَهُ، فَنَحَرَهَا. فَقَالَ ابْعَثْهَا قِيَاماً مُقَيَّدَةً سُنَّةَ مُحَمَّدٍ - ﷺ -) .","ޒިޔާދު ބިން ޖުބައިރު ރިވާ ކުރެއްވިއެވެ: މީހެއްގެ (ހަދްޔު) ޖަމަލު ކަތިލުމަށްޓަކައި އެ ޖަމަލު އަރިއަކަށް ބާއްވައިގެން އެ މީހަކު އިށީންދެ އިންދާ، އިބްނު ޢުމަރު އަތުވިތަން ދުށީމެވެ. އިބްނު ޢުމަރު ދެންނެވިއެވެ: އެ ޖަމަލު ކޮއްޅަށް ނަގާފައި (އޭގެ ކުރިމަތީ ވާތުފައި) އައްސައިގެން (ތިބާ ކޮއްޅަށް ހުރެ އޭތި ނަޙުރުކޮށް) ކަތިލާށެވެ. މުޙައްމަދު ﷺ ގެ ސުންނަތަކީ އެއީއެވެ.","البخاري 1713 واللفظ له، ومسلم 1320"],["#245","كتاب الحج","ޙައްޖުގެ ފޮތް","باب الغسل للمحرم","މުޙްރިމަކު ފެންވެރުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ حُنَيْنٍ: (أَنَّ عَبْدَ اللَّهِ بْنَ عَبَّاسٍ وَالْمِسْوَرَ بْنَ مَخْرَمَةَ اخْتَلَفَا بِالأَبْوَاءِ. فَقَالَ ابْنُ عَبَّاسٍ: يَغْسِلُ الْمُحْرِمُ رَأْسَهُ. وَقَالَ الْمِسْوَرُ: لا يَغْسِلُ رَأْسَهُ. قَالَ: فَأَرْسَلَنِي ابْنُ عَبَّاسٍ إلَى أَبِي أَيُّوبَ الْأَنْصَارِيِّ - رضي الله عنه -. فَوَجَدْتُهُ يَغْتَسِلُ بَيْنَ الْقَرْنَيْنِ، وَهُوَ يُسْتَرُ بِثَوْبٍ. فَسَلَّمْت عَلَيْهِ. فَقَالَ: مَنْ هَذَا؟ فَقُلْت: أَنَا عَبْدُ اللَّهِ بْنُ حُنَيْنٍ، أَرْسَلَنِي إلَيْكَ ابْنُ عَبَّاسٍ، يَسْأَلُكَ: كَيْفَ كَانَ رَسُولُ اللَّهِ - ﷺ - يَغْسِلُ رَأْسَهُ وَهُوَ مُحْرِمٌ؟ فَوَضَعَ أَبُو أَيُّوبَ يَدَهُ عَلَى الثَّوْبِ، فَطَأْطَأَهُ، حَتَّى بَدَا لِي رَأْسُهُ. ثُمَّ قَالَ لإِنْسَانٍ يَصُبُّ عَلَيْهِ الْمَاءَ: اُصْبُبْ، فَصَبَّ عَلَى رَأْسِهِ. ثُمَّ حَرَّكَ رَأْسَهُ بِيَدَيْهِ، فَأَقْبَلَ بِهِمَا وَأَدْبَرَ. ثُمَّ قَالَ: هَكَذَا رَأَيْتُهُ - ﷺ - يَغْتَسِلُ) .\n<br><br>\nوَفِي رِوَايَةٍ \" فَقَالَ الْمِسْوَرُ لابْنِ عَبَّاسٍ: لا أُمَارِيكَ أَبَداً \".","ޢަބްދު ﷲ ބިން ޙުނައިނު ރިވާ ކުރެއްވިއެވެ: (މައްކާއާއި މަދީނާ ދެމެދުވާ ތަނެއް ކަމުގައިވާ) އަލްއަބްވާގައި (މަޑުކުރައްވައިގެން އިންނަވަނިކޮށް) ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُ އާއި އަލްމިސްވަރު ބިން މަޚްރަމާ رَضِيَ اللَّهُ عَنْهُ އާ ދެބޭކަލުން (ކަމަކާމެދު) އިޚްތިލާފުވިއެވެ. އިބްނު ޢައްބާސް ދެންނެވިއެވެ: މުޙްރިމަކަށް އޭނާގެ ބޯ ދޮވިދާނެއެވެ. އަދި އަލްމިސްވަރު ދެންނެވިއެވެ: މުޙްރިމަކަށް އޭނާގެ ބޮލެއް ނުދޮވޭނެއެވެ. ދެން ފަހެ އިބްނު ޢައްބާސް، އަބޫ އައްޔޫބް އަލްއަންޞާރީ رَضِيَ اللَّهُ عَنْهُ ގެ އަރިހަށް ތިމަން ފޮނުއްވިއެވެ. އެކަލޭގެފާނާ ދިމާވި ހިނދު އިންނެވީ، ވަޅެއްގެ ދެ މުޑި ދެމެދު ފޭރާމެއްގެ ފަހަތުގައި ނިވާވެ ފެންވަރާށެވެ. ދެން ތިމަންނަ ސަލާމް ދެންނެވުމުން އެކަލޭގެފާނު ވިދާޅުވިއެވެ: ތިއީ ކާކުތޯއެވެ؟ ތިމަން ދެންނެވީމެވެ: ތިމަންނައީ ޢަބްދު ﷲ ބިން ޙުނައިނެވެ. ތިބާގެ ގާތަށް ތިމަން ފޮނުއްވީ ޢިބްނު ޢައްބާސެވެ. ރަސޫލު ﷲ މުޙްރިމަކަށް ވެވަޑައިގެން ހުންނަވިކޮށް އިސްތަށިފުޅު ދޮންނެވީ ކިހިނަކުންތޯ އިބްނު ޢައްބާސް އައްސަވައެވެ. ދެން އަބޫ އައްޔޫބު އެ ފޭރާން މަތީ އަތް އަޅުއްވާފައި، އެކަލޭގެފާނުގެ ބޯ ތިމަންނައަށް ދެއްކޭވަރަށް އެ ފަރުދާ ތިރިކުރެއްވިއެވެ. އަދި އެކަލޭގެފާނުގެ ބޮލަށް ފެން އަޅުއްވަން މީހަކު ގާތު ދެންނެވުމުން، އެކަން ކުރެވުނެވެ. ދެން އެކަލޭގެފާނުގެ ދެ އަތުން ބޮލުގެ ކުރިމަތިން ފަހަތަށާއި، ފަހަތުން ކުރިމައްޗަށް އަތް އުނގުޅާލެއްވިއެވެ. އެއަށްފަހު ދެންނެވިއެވެ: ތިމަންނައަށް ފެނުނީ ނަބިއްޔާ ﷺ މިފަދައިން ކަން ކުރައްވަނިކޮށެވެ.\n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ދެން އިބްނު ޢައްބާސް ގާތު އަލްމިސްވަރު ދެންނެވިއެވެ: މި ކަމަށް ފަހު ދުވަހަކުވެސް ތިބާއާ ބަހުސެއް ނުކުރާނަމެވެ.","البخاري 1840، ومسلم 1205\n<br><br>\nمسلم 1205"],["#246","كتاب الحج","ޙައްޖުގެ ފޮތް","باب فسخ الحج إلى العمرة","ޙައްޖު ފަސްޚުކޮށް ޢުމްރާއަކަށް ބަދަލު ކުރުމުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رضي الله عنهما قَالَ (أَهَلَّ النَّبِيُّ - ﷺ - وَأَصْحَابُهُ بِالْحَجِّ وَلَيْسَ مَعَ أَحَدٍ مِنْهُمْ هَدْيٌ غَيْرَ النَّبِيِّ - ﷺ - وَطَلْحَةَ، وَقَدِمَ عَلِيُّ - رضي الله عنه - مِنْ الْيَمَنِ. فَقَالَ: أَهْلَلْتُ بِمَا أَهَلَّ بِهِ النَّبِيُّ - ﷺ - فَأَمَرِ النَّبِيُّ - ﷺ - أَصْحَابَهُ: أَنْ يَجْعَلُوهَا عُمْرَةً، فَيَطُوفُوا ثُمَّ يُقَصِّرُوا وَيَحِلُّوا، إلاَّ مَنْ كَانَ مَعَهُ الْهَدْيُ فَقَالُوا: نَنْطَلِقُ إلَى «مِنىً» وَذَكَرُ أَحَدِنَا يَقْطُرُ؟ فَبَلَغَ ذَلِكَ النَّبِيَّ - ﷺ - فَقَالَ: لَوْ اسْتَقْبَلْتُ مِنْ أَمْرِي مَا اسْتَدْبَرْتُ مَا أَهْدَيْتُ، وَلَوْلا أَنَّ مَعِي الْهَدْيَ لأَحْلَلْتُ. وَحَاضَتْ عَائِشَةُ. فَنَسَكَتِ الْمَنَاسِكَ كُلَّهَا، غَيْرَ أَنَّهَا لَمْ تَطُفْ بِالْبَيْتِ. فَلَمَّا طَهُرَتْ وَطَافَتْ بِالْبَيْتِ قَالَتْ: يَا رَسُولَ اللَّهِ، يَنْطَلِقُونَ بِحَجٍّ وَعُمْرَةٍ، وَأَنْطَلِقُ بِحَجٍّ فَأَمَرَ عَبْدَ الرَّحْمَنِ بْنَ أَبِي بَكْرٍ: أَنْ يَخْرُجَ مَعَهَا إلَى التَّنْعِيمِ فَاعْتَمَرَتْ بَعْدَ الْحَجِّ) .","ޖާބިރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: (ޛުލް ޙުލައިފާގައި) ނަބިއްޔާ ﷺ އާއި އެކަލޭގެފާނުގެ ޞަޙާބީން އިޙްރާމް ބަންނަވާ ޙައްޖަށް ތަލްބިޔާ ވިދާޅުވިއެވެ. (އެބަހީ: ލައްބައިކަ ޙައްޖަން ވިދާޅުވިއެވެ.) އަދި ނަބިއްޔާ ﷺ އާއި ޠަލްޙާ ފިޔަވައި އެއިން އެއްވެސް ބޭކަލަކު (ރަށުން މައްކާއާ ހަމައަށް) ހަދްޔު ނުގެންނަވައެވެ. އަދި ޢަލީ ޔަމަނުން (މައްކާއަށް ހަދްޔުއާއެކު) ވަޑައިގަންނަވައިގެން ދެންނެވިއެވެ: ތިމަންނަ އިޙްރާމް ބެންނެވީ، ނަބިއްޔާ ﷺ އިޙްރާމް ބެންނެވި ކަމަކަށެވެ. ދެން ނަބިއްޔާގެ ޞަޙާބީންގެ ޙައްޖު ޢުމްރާއަކަށް ބަދަލު ކުރަން ނަބިއްޔާ އެންގެވިއެވެ. އަދި އެބައިމީހުން ޠަވާފު ކުރުމަށާއި، އެއަށްފަހު ބޯކޮށައި އިޙްރާމް ކެނޑުމަށް އެނގެވިއެވެ. އެއީ އެ މީހަކާއެކު ހަދްޔު ގެނައި މީހަކު މެނުވީއެވެ. މީސްތަކުން (އެބައިމީހުންގެ މެދުގައި) ދެންނެވިއެވެ: ތިމަންމެންގެ ލަދުވެތި ގުނަވަނުން ތިކިޖަހާ ޙާލު (އެބަހީ: އަންހެނުންނާއެކު ޖިމާޢުވުމަށް ފަހު) މިނާއަށް ދާނީ (ފަހެ ކިހިނަކުންހެއްޔެވެ؟) (ނޯޓު: ޢުމްރާއަށް ފަހު އިޙްރާމް ކެނޑުމުން ޖިމާޢުވުމާއި އެހެނިހެން ކަންކަން ހުއްދަވާނެއެވެ. ދެން ޙައްޖު މަހުގެ އަށްވަނަ ދުވަހު ޙައްޖަށް އިޙްރާމް ބަނދެގެން ދާނީ މިނާއަށެވެ. އެ ގޮތުން ޞަޙާބީން އެފަދައިން ދެންނެވީ، މި ދެ ކަންކަން ދެމެދު މުއްދަތު ކުރުކަމުން އެކަމާމެދު އަޖައިބުވެގެންނެވެ. އަދި ޖާހިލިއްޔަ ޒަމާނުގައި މީހުން ގަބޫލު ކުރި ގޮތުގައި ޙައްޖު މަހުގައި ޢުމްރާ ކުރުމަކީ ބޮޑުވެގެންވާ ފާފައެކެވެ.) ދެން ނަބިއްޔާގެ އަރިހަށް އެ ވާހަކަ ފޯރުމުން ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާއަށް މީހާރު އެނގިވަޑައިގަންނަވާ ކަންތައް، ކުރިން އެނގިވަޑައިގެންނެވި ނަމަ، ހަދްޔު ނުގެންނެވީހެވެ. އަދި ތިމަން ނަބިއްޔާއާއެކު ހަދްޔު ނުވާނަމަ ތިމަން ނަބިއްޔާވެސް އިޙްރާމް ކެނޑުއްވީސްކަން ކަށަވަރެވެ.“ އަދި ޢާއިޝާ ޙައޟުވެ ވަޑައިގެންނެވިއެވެ. ވީމާ ގެފުޅު ވަށާ ޠަވާފު ކުރުން ފިޔަވައި ދެން ހުރި ހުރިހާ އަޅުކަމެއް އެ ކަމަނާ އަދާ ކުރެއްވިއެވެ. އަދި އެ ކަމަނާ ޙައިޟުން ތާހިރުވެ ވަޑައިގަތުމުން ގެފުޅު ވަށާ ޠަވާފު ވެސް ކުރެއްވިއެވެ. އެ ކަމަނާ ވިދާޅުވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. މީސްތަކުން އެ ދަނީ ޙައްޖާއި ޢުމްރާ އަދާކޮށްގެންނެވެ. އެކަމަކު ތިމަން ކަމަނާ މި ވަޑައިގަންނަވަން ޖެހެނީ (ހަމައެކަނި) ޙައްޖު އަދާކުރުމުންނެވެ. ދެން އަބްދުއްރަޙްމާން ބިން އަބޫ ބަކުރު ގާތު ނަބިއްޔާ އެންގެވީ، އެ ކަމަނާ ގޮވައިގެން (މައްކާގެ ރަށްބޭރުގައިވާ) ތަންޢީމަށް ވަޑައިގަންނަވަންށެވެ. (އެއީ މައްކާގައި ހުރި މީހަކަށް ޢުމްރާ ކުރެވޭނީ މައްކާއިން ބޭރަށް ނުކުމެ އިޙްރާމް ބަންދެގެންނެވެ.) ދެން ފަހެ އެ ކަމަނާގެ ޙައްޖަށް ފަހު ޢުމްރާ ވެސް އަދާ ކުރެއްވިއެވެ.","البخاري 1651 واللفظ له، ومسلم 1216"],["#247","كتاب الحج","ޙައްޖުގެ ފޮތް","باب فسخ الحج إلى العمرة","ޙައްޖު ފަސްޚުކޮށް ޢުމްރާއަކަށް ބަދަލު ކުރުމުގެ ބާބު","عَنْ جَابِرٍ - رضي الله عنه - قَالَ: (قَدِمْنَا مَعَ رَسُولِ اللَّهِ - ﷺ - وَنَحْنُ نَقُولُ: لَبَّيْكَ بِالْحَجِّ. فَأَمَرَنَا رَسُولُ اللَّهِ - ﷺ - فَجَعَلْنَاهَا عُمْرَةً) .","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ގެ އަރިހުގައި ތިމަންމެން (މައްކާއަށް ޙައްޖަށް) ވަޑައިގަތީ ’ލައްބައިކަ ބިލްޙައްޖި‘ މިފަދައިން ވިދާޅުވެގެންނެވެ. (އެބަހީ: ޙައްޖަށް އިޙްރާމް ބަނދެގެންނެވެ.) ދެން ރަސޫލު ﷲ ﷺ އެންގެވުމުން ތިމަންމެންގެ ޙައްޖު ޢުމްރާއަކަށް ބަދަލުކުރީމެވެ.","البخاري 1570، ومسلم 1216"],["#248","كتاب الحج","ޙައްޖުގެ ފޮތް","باب فسخ الحج إلى العمرة","ޙައްޖު ފަސްޚުކޮށް ޢުމްރާއަކަށް ބަދަލު ކުރުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رضي الله عنهما قَالَ: (قَدِمَ رَسُولُ اللَّهِ - ﷺ - وَأَصْحَابُهُ صَبِيحَةَ رَابِعَةٍ. فَأَمَرَهُمْ أَنْ يَجْعَلُوهَا عُمْرَةً. فَقَالُوا: يَا رَسُولَ اللَّهِ، أَيُّ الْحِلِّ؟ قَالَ: الْحِلُّ كُلُّهُ) .","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ޛުލް ޙިއްޖާ މަހުގެ ހަތަރުވަނަ ދުވަހުގެ ހެނދު، ރަސޫލު ﷲ ﷺ އާއި އެކަލޭގެފާނުގެ ޞަޙާބީން (މައްކާއަށް) ވަޑައިގަތީ ޙައްޖަށް އިޙްރާމް ބަނދެގެންނެވެ. ދެން އެބައިމީހުން (އިޙްރާމް ކަނޑާ) ޙައްޖު ޢުމްރާއަކަށް ބަދަލު ކުރަން ނަބިއްޔާ އެންގެވިއެވެ. (އެއާމެދު ކަންބޮޑުވެގެން) އެބައިމީހުން ދެންނެވިއެވެ. އޭ ﷲ ގެ ރަސޫލާއެވެ. ކޮން އިޙްރާމް ކެނޑުމެއްތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ހުރިހާ ކަމަކުންވެސް އިޙްރާމް ކަނޑާށެވެ.“","البخاري 1564، ومسلم 1240"],["#249","كتاب الحج","ޙައްޖުގެ ފޮތް","باب فسخ الحج إلى العمرة","ޙައްޖު ފަސްޚުކޮށް ޢުމްރާއަކަށް ބަދަލު ކުރުމުގެ ބާބު","عَنْ عُرْوَةَ بْنِ الزُّبَيْرِ قَالَ: (سُئِلَ أُسَامَةُ بْنُ زَيْدٍ - وَأَنَا جَالِسٌ - كَيْفَ كَانَ رَسُولُ اللَّهِ - ﷺ - يَسِيرُ حِينَ دَفَعَ؟ قَالَ: كَانَ يَسِيرُ الْعَنَقَ. فَإِذَا وَجَدَ فَجْوَةً نَصَّ) .","ޢުރްވާ ބިން ޒުބައިރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ތިމަން އިށީނދެ ވަނިކޮށް އުސާމާ ބިން ޒައިދު ގާތު އެއްސެވުނެވެ: ޙައްޖަތުލް ވަދާޢުގައި ރަސޫލު ﷲ ﷺ (ޢަރަފާތުން މުޒްދަލިފާއަށް ޖަމަލުކޮޅުގައި) ވަޑައިގަތީ ކިހިނަކުންތޯއެވެ؟ އުސާމާ ދެންނެވިއެވެ: ނަބިއްޔާ ވަޑައިގަތީ މެދުމިނެއްގެ ދުވެއްޔެއްގައެވެ. ދެން ޖާގައެއް ފެނިވަޑައިގަންނަވާ ނަމަ ދުވެލި ބާރުކުރައްވައެވެ.","البخاري 1666 واللفظ له، ومسلم 1286"],["#250","كتاب الحج","ޙައްޖުގެ ފޮތް","باب فسخ الحج إلى العمرة","ޙައްޖު ފަސްޚުކޮށް ޢުމްރާއަކަށް ބަދަލު ކުރުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رضي الله عنهما: (أَنَّ رَسُولَ اللَّهِ - ﷺ - وَقَفَ فِي حَجَّةِ الْوَدَاعِ. فَجَعَلُوا يَسْأَلُونَهُ. فَقَالَ: رَجُلٌ لَمْ أَشْعُرْ، فَحَلَقْتُ قَبْلَ أَنْ أَذْبَحَ؟ قَالَ. اذْبَحْ وَلا حَرَجَ. وَجَاءَ آخَرُ، فَقَالَ: لَمْ أَشْعُرْ، فَنَحَرْتُ قَبْلَ أَنْ أَرْمِيَ؟ قَالَ: ارْمِ وَلا حَرَجَ. فَمَا سُئِلَ يَوْمَئِذٍ عَنْ شَيْءٍ قُدِّمَ وَلا أُخِّرَ إلاَّ قَالَ: افْعَلْ وَلا حَرَجَ) .","ޢަބްދު ﷲ ބިން ޢަމްރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙައްޖަތުލް ވަދާޢުގައި މީނާގައި މަޑު ކުރެއްވިއެވެ. ފަހެ މީސްތަކުން އައިސް ނަބިއްޔާ އަރިހު ސުވާލު ކުރަމުން ދިޔައެވެ. އެ ގޮތުން މީހަކު ދެންނެވިއެވެ: ތިމަންނައަށް ނޭނގި ހުއްޓާ، ހަދްޔު ކަތިލުމުގެ ކުރިން ތިމަންނައަށް ބޯ ބޭލިއްޖެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”(މިހާރު ގޮސް ހަދްޔު) ކަތިލާށެވެ. މައްސަލައެއް ނެތެވެ.“ ދެން އެހެން މީހަކު އައިސް ދެންނެވިއެވެ: ތިމަންނައަށް ނޭނގި ހުއްޓާ، ޖަމްރާއަށް ހިލަ އުކުމުގެ ކުރިން ތިމަންނައަށް ޖަމަލު ކަތިލެވިއްޖެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”(މިހާރު ގޮސް ޖަމްރާއަށް) ހިލަ އުކާށެވެ. މައްސަލައެއް ނެތެވެ.“ ފަހެ (މީހަކު ޙައްޖުގެ) ކަމެއް އިސްކޮށް ނުވަތަ ފަސްކޮށް ކުރެވިގެން އެ ދުވަހު ނަބިއްޔާ ﷺ ގެ އަރިހު ސުވާލު ކުރެވިއްޖެ ނަމަ، މިފަދައިން މެނުވީ ޖަވާބު ނުދެއްވައެވެ: ކުރާށެވެ. މައްސަލައެއް ނެތެވެ.","البخاري 1736 واللفظ له، ومسلم 1306"],["#251","كتاب الحج","ޙައްޖުގެ ފޮތް","باب فسخ الحج إلى العمرة","ޙައްޖު ފަސްޚުކޮށް ޢުމްރާއަކަށް ބަދަލު ކުރުމުގެ ބާބު","عنْ عَبْدِ الرَّحْمَنِ بْنِ يَزِيدَ النَّخَعِيِّ: (أَنَّهُ حَجَّ مَعَ ابْنِ مَسْعُودٍ. فَرَآهُ رَمَى الْجَمْرَةَ الْكُبْرَى بِسَبْعِ حَصَيَاتٍ فَجَعَلَ الْبَيْتَ عَنْ يَسَارِهِ، وَمِنًى عَنْ يَمِينِهِ. ثُمَّ قَالَ: هَذَا مَقَامُ الَّذِي أُنْزِلَتْ عَلَيْهِ سُورَةُ الْبَقَرَةِ - ﷺ -) .","ޢަބްދުއްރަޙްމާން ބިން ޔަޒީދު އައްނަޚަޢީ ރިވާ ކުރެއްވިއެވެ: އެކަލޭގެފާނު އިބްނު މަސްޢޫދު رَضِيَ اللَّهُ عَنْهُ އާއެކު ޙައްޖު ކުރެއްވިއެވެ. އަދި އިބްނި މަސްޢޫދު ބޮޑު ޖަމަރާއަށް ހަތް ހިލަކޮޅު އުއްކެވިތަން އެކަލޭގެފާނަށް ފެނުނެވެ. ފަހެ އިބްނު މަސްޢޫދުގެ ވާތް ފަރާތުގައި ވަނީ ގެފުޅެވެ. އަދި ކަނާތް ފަރާތުގައި ވަނީ މިނާއެވެ. ދެން އިބްނު މަސްޢޫދު ވިދާޅުވިއެވެ: މިއީ ނަބިއްޔާ ﷺ ގެ މައްޗަށް ސޫރަތުލް ބަޤަރާ ބާވައިލެއްވުނު ތަނެވެ.","البخاري 1749 واللفظ له، ومسلم 1296"],["#252","كتاب الحج","ޙައްޖުގެ ފޮތް","باب فسخ الحج إلى العمرة","ޙައްޖު ފަސްޚުކޮށް ޢުމްރާއަކަށް ބަދަލު ކުރުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رضي الله عنهما: أَنَّ رَسُولَ اللَّهِ - ﷺ - قَالَ: (اللَّهُمَّ ارْحَمْ الْمُحَلِّقِينَ. قَالُوا: وَالْمُقَصِّرِينَ يَا رَسُولَ اللَّهِ. قَالَ: اللَّهُمَّ ارْحَمْ الْمُحَلِّقِينَ. قَالُوا وَالْمُقَصِّرِينَ يَا رَسُولَ اللَّهِ. قَالَ: وَالْمُقَصِّرِينَ) .","ޢަބްދު ﷲ ބިން ޢަމްރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ޔާ ﷲ އެވެ. ބޯ ބާލާ މީހުންނަށް ރަޙްމަތް ލައްވާށިއެވެ.“ މީސްތަކުން ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. ބޯ ކޮށާ މީހުންނޭ ވެސް (ވިދާޅުވާށެވެ.) ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ޔާ ﷲ އެވެ. ބޯ ބާލާ މީހުންނަށް ރަޙްމަތް ލައްވާށިއެވެ.“ މީސްތަކުން ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. ބޯ ކޮށާ މީހުންނޭ ވެސް (ވިދާޅުވާށެވެ.) ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ޔާ ﷲ އެވެ. ބޯ ބާލާ މީހުންނަށް ރަޙްމަތް ލައްވާށިއެވެ.“ މީސްތަކުން ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. ބޯ ކޮށާ މީހުންނޭ ވެސް (ވިދާޅުވާށެވެ.) ނަބިއްޔާ ދެން ޙަދީޘް ކުރެއްވިއެވެ: ”އަދި ބޯ ކޮށާ މީހުންވެސް މެއެވެ.“","البخاري 1727، ومسلم 1301 واللفظ له"],["#253","كتاب الحج","ޙައްޖުގެ ފޮތް","باب فسخ الحج إلى العمرة","ޙައްޖު ފަސްޚުކޮށް ޢުމްރާއަކަށް ބަދަލު ކުރުމުގެ ބާބު","عَنْ عَائِشَةَ رضي الله عنها قَالَتْ: (حَجَجْنَا مَعَ النَّبِيِّ - ﷺ - فَأَفَضْنَا يَوْمَ النَّحْرِ. فَحَاضَتْ صَفِيَّةُ. فَأَرَادَ النَّبِيُّ - ﷺ - مِنْهَا مَا يُرِيدُ الرَّجُلُ مِنْ أَهْلِهِ. فَقُلْتُ: يَا رَسُولَ اللَّهِ، إنَّهَا حَائِضٌ. قَالَ: أَحَابِسَتُنَا هِيَ؟ قَالُوا: يَا رَسُولَ اللَّهِ، إنَّهَا قَدْ أَفَاضَتْ يَوْمَ النَّحْرِ قَالَ: اُخْرُجُوا) .\n<br><br>\nوَفِي لَفْظٍ: قَالَ النَّبِيُّ - ﷺ -: (عَقْرَى، حَلْقَى. أَطَافَتْ يَوْمَ النَّحْرِ؟ قِيلَ: نَعَمْ. قَالَ: فَانْفِرِي) .","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ގެ އަރިހުގައި ތިމަންމެން ޙައްޖު ކުރެއްވީމެވެ. ފަހެ ނަޙްރުގެ ދުވަހު (އެބަހީ: އަޟްޙާ ޢީދު ދުވަހު) ތިމަންމެން ޠަވާފުލް އިފާޟާ ކުރައްވާ (ޙައްޖުވެ ނިމުނީމެވެ.) ފަހެ ޞަފިއްޔާ ޙައިޟުވެވަޑައިގެންނެވިއެވެ. އަދި (ނަބިއްޔާއަށް އެކަން އެނގިވަޑައި ނުގަންނަވާ ޙާލު) މީހަކު، އެ މީހެއްގެ އަނބިމީހާ ގާތުން އެދޭ ކަންތައް (އެބަހީ: ޖިމާޢުވުން) އެ ކަމަނާގެ ކިބައިން ނަބިއްޔާ އެދިވަޑައިގެންނެވިއެވެ. (ނަބިއްޔާ ހީފުޅު ކުރެއްވީ އެ ކަމަނާ ޠަވާފުލް އިފާޟާ ކުރައްވާ ޙައްޖުވެ ނިމުނީކަމަށެވެ.) ތިމަން ކަމަނާ ދެންނެވީމެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. ހަމަކަށަވަރު އެ ކަމަނާ ޙައިޟުވެވަޑައިގެންފިއެވެ. (ދެން ނަބިއްޔާ ހީފުޅު ކުރެއްވީ ޙައިޟުގެ ސަބަބުން އެ ކަމަނާ އަދި ޠަވާފުލް އިފާޟާ ނުކުރައްވާ ކަމަށެވެ.) ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ””އެ ކަމަނާ (ޙައިޟުން ތާހިރުވެ، ޠަވާފުލް އިފާޟާކޮށް ނިމެންދެން) ތިމަންމެން (މަދީނާއަށް އެނބުރި ދިޔަނުދީ، މައްކާގައި) މަޑުކުރަން ޖެހެނީތޯއެވެ؟“ (ނަބިއްޔާގެ އަނބިކަނބަލުން) ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން ނަޙްރުގެ ދުވަހުގައި އެ ކަމަނާ ޠަވާފުލް އިފާޟާކޮށް ނިންމައިފިއެވެ. (އެބަހީ: ޙައިޟުވެވަޑައިގަތީ އޭގެ ފަހުންނެވެ.) ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ””އެހެންވިއްޔާ ތިޔަބައިމީހުން (މަދީނާއަށް ފުރަން) ނުކުންނަވާށެވެ.“        \n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ””ޢަޤްރަން ޚަލްޤަންއެވެ. (ނޯޓު: މިއީ ކަމަކާމެދު އަޖައިބުވުމުން ޢަރަބީންގެ ދޫމަތިން ބުނެ އުޅޭ އިބާރާތެކެވެ. ބަދު ދުޢާ ކުރުމުގެ މާނައިގައި ބުނާ ބަހެއް ނޫނެވެ. ޢަޤްރަކީ ޒަހަމްވުން ނުވަތަ ދަރިމައި ނުވުމެވެ. އަދި ޙަލްޤަކީ  ބޯބޭލުން ނުވަތަ ކަރުގައި ރިއްސުމެވެ.) ނަޙްރު ގެ ދުވަހުގައި އެ ކަމަނާ ޠަވާފު ކުރެއްވިތޯޢެވެ؟“ (ނަބިއްޔާގެ އަނބިކަނބަލުން) ދެންނެވިއެވެ:  އާދޭހެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ””އެހެންވިއްޔާ (މަދީނާއަށް ފުރަން) ކަމަނާ ނުކުންނަވާށެވެ.“","البخاري 1733 واللفظ له، ومسلم 1211\n<br><br>\nالبخاري 1771 واللفظ له، ومسلم 1211"],["#254","كتاب الحج","ޙައްޖުގެ ފޮތް","باب فسخ الحج إلى العمرة","ޙައްޖު ފަސްޚުކޮށް ޢުމްރާއަކަށް ބަދަލު ކުރުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رضي الله عنهما قَالَ: (أُمِرَ النَّاسُ أَنْ يَكُونَ آخِرُ عَهْدِهِمْ بِالْبَيْتِ، إلاَّ أَنَّهُ خُفِّفَ عَنْ الْمَرْأَةِ الْحَائِضِ) .\n","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: މީސްތަކުން (މައްކާ ދޫކޮށް ދިޔުމުގެ ކުރިން)، އެބައިމީހުންގެ ޙައްޖުގައި ކުރާ އެންމެ ފަހު ކަމަކަށް ގެފުޅުގައި (ޠަވާފުލް ވަދާޢު ކުރައްވަން) އެބައިމީހުންނަށް އެންގެވުނެވެ. އެހެނަސް ޙައިޟުވެފައިވާ އަންހެނަކަށް ލުއިކުރުމުގެ ގޮތުން އެ ކަނބުލޭގެ މައްޗަށް އެކަންތައް ކުރުމެއް ނުވެއެވެ.","البخاري 1755، ومسلم 1211 واللفظ له"],["#255","كتاب الحج","ޙައްޖުގެ ފޮތް","باب فسخ الحج إلى العمرة","ޙައްޖު ފަސްޚުކޮށް ޢުމްރާއަކަށް ބަދަލު ކުރުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رضي الله عنهما قَالَ: (اسْتَأْذَنَ الْعَبَّاسُ بْنُ عَبْدِ الْمُطَّلِبِ رَسُولَ اللَّهِ - ﷺ -: أَنْ يَبِيتَ بِمَكَّةَ لَيَالِيَ مِنىً، مِنْ أَجْلِ سِقَايَتِهِ فَأَذِنَ لَهُ) .","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: އަލްޢައްބާސް ބިން ޢަބްދުލް މުއްޠަލިބު (ޙައްޖުވެރިންނަށް) ފެން ދިނުމަށްޓަކައި، މިނާގެ ރޭތަކުގައި އެކަލޭގެފާނު މައްކާގައި މަޑުކުރެއްވުމަށް، ރަސޫލު ﷲ ﷺ ގެ އަރިހުން ހުއްދައަށް އެދިވަޑައިގަތެވެ. އަދި ނަބިއްޔާ އެކަލޭގެފާނަށް ހުއްދަ ދެއްވިއެވެ.","البخاري 1643 واللفظ له، ومسلم 1315"],["#256","كتاب الحج","ޙައްޖުގެ ފޮތް","باب فسخ الحج إلى العمرة","ޙައްޖު ފަސްޚުކޮށް ޢުމްރާއަކަށް ބަދަލު ކުރުމުގެ ބާބު","وَعَنْهُ - أَيْ عَنْ ابْنِ عُمَرَ - قَالَ: (جَمَعَ النَّبِيُّ - ﷺ - بَيْنَ الْمَغْرِبِ وَالْعِشَاءِ «بِجَمْعٍ»، لِكُلِّ وَاحِدَةٍ مِنْهُمَا إقَامَةٌ. وَلَمْ يُسَبِّحْ بَيْنَهُمَا، وَلا عَلَى إثْرِ وَاحِدَةٍ مِنْهُمَا) .","އަދިވެސް ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: މުޒްދަލިފާގައި ނަބިއްޔާ ﷺ މަޢްރިބާއި ޢިޝާ ނަމާދު ކުރެއްވީ ޖަމްޢުކޮށެވެ. އެއިން ކޮންމެ ނަމާދެއްގައިވެސް ޤަމަތް ދެއްވިއެވެ. އަދި އެ ދެ ނަމާދު ދެމެދުގައި ސުންނަތް ނަމާދެއް ނުކުރައްވައެވެ. އަދި އެ ދެ ނަމާދަށް ފަހު ވެސް ސުންނަތް ނަމާދެއް ނުކުރައްވައެވެ.","البخاري 1673، ومسلم 1288"],["#257","كتاب الحج","ޙައްޖުގެ ފޮތް","باب المحرم يأكل من صيد الحلال","ޙަލާލު ޝިކާރައަކުން މުޙްރިމަކު ކެއުމުގެ ބާބު","عَنْ أَبِي قَتَادَةَ الأَنْصَارِيِّ: (أَنَّ رَسُولَ اللَّهِ - ﷺ - خَرَجَ حَاجَّاً. فَخَرَجُوا مَعَهُ. فَصَرَفَ طَائِفَةً مِنْهُمْ - فِيهِمْ أَبُو قَتَادَةَ - وَقَالَ: خُذُوا سَاحِلَ الْبَحْرِ، حَتَّى نَلْتَقِيَ. فَأَخَذُوا سَاحِلَ الْبَحْرِ فَلَمَّا انْصَرَفُوا أَحْرَمُوا كُلُّهُمْ، إلاَّ أَبَا قَتَادَةَ، فَلَمْ يُحْرِمْ. فَبَيْنَمَا هُمْ يَسِيرُونَ إذْ رَأَوْا حُمُرَ وَحْشٍ. فَحَمَلَ أَبُو قَتَادَةَ عَلَى الْحُمُرِ. فَعَقَرَ مِنْهَا أَتَانَاً. فَنَزَلْنَا فَأَكَلْنَا مِنْ لَحْمِهَا. ثُمَّ قُلْنَا: أَنَأْكُلُ لَحْمَ صَيْدٍ، وَنَحْنُ مُحْرِمُونَ؟ فَحَمَلْنَا مَا بَقِيَ مِنْ لَحْمِهَا فَأَدْرَكْنَا رَسُولَ اللَّهِ - ﷺ -. فَسَأَلْنَاهُ عَنْ ذَلِكَ؟ فَقَالَ: مِنْكُمْ أَحَدٌ أَمَرَهُ أَنْ يَحْمِلَ عَلَيْهَا، أَوْ أَشَارَ إلَيْهَا؟ قَالُوا: لا. قَالَ: فَكُلُوا مَا بَقِيَ مِنْ لَحْمِهَا) .\n<br><br>\nوَفِي رِوَايَةٍ: (قَالَ: هَلْ مَعَكُمْ مِنْهُ شَيْءٌ؟ فَقُلْت: نَعَمْ. فَنَاوَلْتُهُ الْعَضُدَ، فَأَكَلَ مِنْهَا) .","އަބޫ ޤަތާދާ އަލްއަންޞާރީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ (ގެފުޅަށް ޒިޔާރާތް ކުރެއްވުމަށް) ޢުމްރާއަށް ނުކުމެވަޑައިގެންނެވިއެވެ. އަދި މީސްތަކުންވެސް ނަބިއްޔާގެ އަރިހު ނުކުތެވެ. (ނޯޓު: މިއީ ޞުލްޙުލް ޙުދައިބިއްޔާގެ ފަހަރެވެ. އަދި މައްކާގެ މުޝްރިކުން ހަނގުރާމައަށް ނުކުމެފާނެކަމުގެ ބިރު އޮތެވެ.) އެ ގޮތުން އެބައިމީހުންގެ ތެރެއިން އެއްބަކު ވަކިކޮށް އެހެން މަގަކުން ފޮނުއްވިއެވެ. މިބައިމީހުންގެ ތެރޭގައި އަބޫ ޤަތާދާ ހިމެނުނެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަންމެން ބައްދަލުވަންދެން، ކަނޑުގެ އައްސޭރިފަށުންލާފައި ތިޔަބައިމީހުން ދަތުރު ކުރާށެވެ.“ ދެން އެބައިމީހުން އައްސޭރިފަށުންލާފައި ހިނގައްޖެއެވެ. އެ ގޮތުން މިބައިމީހުން ނުކުންނެވި އިރު، އަބޫ ޠަލްޙާ ފިޔަވައި އެބައިމީހުން އެންމެން އިޙްރާމް ބަނދެފިއެވެ. އެކަލޭގެފާނު އިޙްރާމް ނުބަންނަވައެވެ. ދެން އެބައިމީހުން އެހެން ދަނިކޮށް، ވަލުހިމާރެއް (އެބަހީ: އޮނެޖާއެއް) އެބައިމީހުންނަށް ފެނިއްޖެއެވެ. ފަހެ އަބޫ ޤަޠާދާ ހިމާރަކަށް ސަވާރުވެގެން އެތަނުން އަންހެން ވަލުހިމާރަކަށް ހަމަލާދީ ޝިކާރަކޮށްފިއެވެ. ދެން ތިމަންމެން އެ ތަނުގައި މަޑުކޮށް އެ ވަލުހިމާރުގެ މަސް ކެއީމެވެ. އެއަށްފަހު ތިމަންމެން ދެންނެވީމެވެ: ތިމަންމެން އިޙްރާމް ބަނދެގެން ހުރެ، ޝިކާރަކުޅަ މަސް ކެވޭނެހެއްޔެވެ؟ (އެހެނީ: މުޙްރިމުންނަށް ޝިކާރަ ކުރުން ޙަރާމްވެގެން ވެއެވެ.) ދެން އޭގެ މަހުގެ ބާކީ އޮތް ބައި ހިފައިގެން ގޮސް، ރަސޫލު ﷲ ﷺ އާ އަރާހަމަ ކުރީމެވެ. އަދި އެކަމާ ބެހޭ ގޮތުން ސުވާލު ކުރީމެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ކުރެ އެކަކުވެސް އަބޫ ޤަތާދާ ގާތު އެ ޝިކާރަ ކުރަން އެންގެވިންތޯއެވެ؟ ނޫނީ އެއަށް އިޝާރާތް ކުރެއްވިންތޯއެވެ؟ އެބައިމީހުން ދެންނެވިއެވެ: ނޫނެކެވެ. ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އެހެންވިއްޔާ އެ މަހުގެ ބާކީ އޮތް އެއްޗަކުން ކާށެވެ.“\n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”އެ މަހުގެ އެއްވެސް އެތިކޮޅެއް ތިޔަބައިމީހުންނާއެކު ވޭތޯއެވެ؟“ ތިމަން ދެންނެވީމެވެ: އާދޭހެވެ. ދެން ތިމަންމެން އޭގެ ފައި ދެއްވުމުން ނަބިއްޔާ ފަރީއްކުޅުވިއެވެ.","البخاري 1824، ومسلم 1196\n<br><br>\nالبخاري 2570 واللفظ له، ومسلم 1196"],["#258","كتاب الحج","ޙައްޖުގެ ފޮތް","باب المحرم يأكل من صيد الحلال","ޙަލާލު ޝިކާރައަކުން މުޙްރިމަކު ކެއުމުގެ ބާބު","عَنْ الصَّعْبِ بْنِ جَثَّامَةَ اللَّيْثِيِّ - رضي الله عنه - (أَنَّهُ أَهْدَى إلَى النَّبِيِّ - ﷺ - حِمَاراً وَحْشِيَّاً، وَهُوَ بِالأَبْوَاءِ أَوْ بِوَدَّانَ - فَرَدَّهُ عَلَيْهِ. فَلَمَّا رَأَى مَا فِي وَجْهِي، قَالَ: إنَّا لَمْ نَرُدَّهُ عَلَيْكَ إلاَّ أَنَّا حُرُمٌ) .\n<br><br>\nوَفِي لَفْظٍ لِمُسْلِمٍ «رِجْلَ حِمَارٍ» وَفِي لَفْظٍ «شِقَّ حِمَارٍ» وَفِي لَفْظٍ «عَجُزَ حِمَارٍ» .","އައްޞަޢުބް ބިން ޖައްޘާމާ އައްލައިޘީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: އެކަލޭގެފާނު، ވަލުހިމާރު (އެބަހީ: އޮނެޖާއެއްގެ) މަސްކޮޅެއް ނަބިއްޔާ ﷺ އަށް ހަދިޔާ ކުރެއްވިއެވެ. އޭރު ނަބިއްޔާ ވަނީ (މައްކާއާއި މަދީނާއާ ދެމެދުގައިވާ) އަބްވާ ނުވަތަ ވައްދާނުގައެވެ. ފަހެ އައްޞަޢުބުގެ ހަދިޔާ ނަބިއްޔާ އަނބުރާ ރައްދު ކުރެއްވިއެވެ. ދެން އައްޞަޢުބުގެ މޫނުމަތިން (ދެރަވެފައި ހުރިކަން) ފެނިވަޑައިގަތުމުން ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަންމެން އިޙްރާމް ބަނދެގެން ހުރިކަމަށްޓަކައި ނޫނީވިއްޔާ، އެ ހަދިޔާ އަނބުރާ ތިބާއަށް ރައްދު ނުކުރެއްވީހެވެ.“\n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތުގައި ވަނީ: ވަލުހިމާރެއްގެ ފަޔެކެވެ. އަދި އެހެން ރިވާޔަތެއްގައި ވަނީ: ވަލުހިމާރެއްގެ ބައެކެވެ. އަދި އެހެން ރިވާޔަތެއްގައި ވަނީ: ވަލުހިމާރެއްގެ ފަހަތު ފަޔެވެ.\n<br><br>\n(ނޯޓު: ނަބިއްޔާ މި ހަދިޔާ ގަބޫލު ނުކުރެއްވި ސަބަބަކީ، އައްޞަޢުބް ޝީކާރަ ކުރީ ހަމަ ސީދާ ނަބިއްޔާއަށް އެ މަސް ދޭންވެގެން ކަމުގައި ވާތީއެވެ. އަދި މުޙްރިމަކު އަމިއްލައަށް ޝިކާރަ ކުރުމާއި، ޝިކާރަ ކުރުމުގައި އެހީވުމާއި އިޝާރާތް ކުރުން ޙަރާމްވުމުގެ އިތުރަށް، ސީދާ މުޙްރިމަކަށްޓަކައި ނުވަތަ މުޙްރިމަކަށް ދޭންވެގެން ވެސް ޝިކާރަ ކުރުން ޙަރާމްވެގެން ވެއެވެ. އަދި އަބޫ ޤަތާދާ ޝިކާރަ ކުރެއްވީ މުޙްރިމަކަށް ނޫނެވެ. އެހެނަސް އެކަލޭގެފާނު މުޙްރިމަކަށް ނުވާ ޙާލު ޝިކާރަ ކުރެއްވީ އަމިއްލަ ނަފްސަށްޓަކާއެވެ. އެއަށްފަހު ކެއުމުގައި މުޙްރިމުންނަށް މަސް ހަދިޔާ ކުރެއްވީއެވެ. ހުރިހާވެސް ތަފާތަކީ ނިޔަތެވެ.)\n<br><br>\n(ނޯޓު: ޙިމާރުލް ވަޙްޝަކީ އޮނެޖާއެވެ. އޭގެ މަސް ކެއުން ހުއްދައެވެ. އާދައިގެ ހިމާރާ މުޅިން ތަފާތު ޖަނަވާރެކެވެ. އޭގެ މަސް ކެއުން ޙަރާމެވެ. ޙިމާރުލް ވަޙްޝު، އާދައިގެ ހިމާރާ ވައްތަރު ގޮތެއް ހުރެއެވެ. އެހެނަސް ކުލައާއި ސައިޒު ކުޑަކޮށް ތަފާތެވެ. އަދި ޒިބްރާ ނުވަތަ ކަފިހިއަހަކީ ޙިމާރުލް ވަޙްޝުގެ ބާވަތެކެވެ. އަދި އިންސާނުން ގެންގުޅޭ އާދައިގެ ހިމާރު، ފިލައިގެން ވަލުތެރޭގައި އުޅެ އޭގެ އޮޅުކަން ފިލައިދިޔަސް އެއީ ޙިމާރުލް ވަޙްޝުކަމުގައެވެ ނުވާނެއެވެ. އަދި އޭގެ މަސް ކެއުންވެސް ހުއްދައެއް ނުވާނެއެވެ. )","البخاري 1825 واللفظ له، ومسلم 1193\n<br><br>\nمسلم 1193"],["#259","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رضي الله عنهما عَنْ رَسُولِ اللَّهِ - ﷺ - أَنَّهُ قَالَ: (إذَا تَبَايَعَ الرَّجُلانِ، فَكُلُّ وَاحِدٍ مِنْهُمَا بِالْخِيَارِ مَا لَمْ يَتَفَرَّقَا وَكَانَا جَمِيعاً، أَوْ يُخَيِّرُ أَحَدُهُمَا الآخَرَ. فَتَبَايَعَا عَلَى ذَلِكَ. فَقَدْ وَجَبَ الْبَيْعُ) . وَمَا فِي مَعْنَاهُ مِنْ حَدِيثِ","މާނައީ: ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ދެ މީހަކު ގަނެވިއްކާނަމަ، އެ ދެމީހުން ވަކިނުވާހައި ހިނދަކު، އެއިން ކޮންމެ މީހަކު ވެސް ވަނީ (އެ ވިޔަފާރީގެ މުއާމަލާތް ދެމެހެއްޓުން، ނުވަތަ އެ މުއާމަލާތް ބާޠިލު ކުރުމަށްފަހު އެ ތަކެތި ރުޖޫއަ ކުރުމުގެ) އިޚްތިޔާރު ލިބިގެންނެވެ. އެއީ އެ ދެބައިމީހުން އެއްކޮށް ހުންނަހައި ހިނދަކުއެވެ. ނޫނީ އެއިން އެކަކު (އޭނާގެ އިޚްތިޔާރު ކުރުމުގެ ޙައްޤު ދޫކޮށް)، (ހަމައެކަނި) އަނެކަކަށް އިޚްތިޔާރު ކުރުމުގެ ޙައްޤު ދީފި ނަމައެވެ. އަދި (އެ ގޮތަށް) އަނެކަކަށް އިޚްތިޔާރު ކުރުމުގެ ޙައްޤު ދިނުމަށް ފަހު، ދެ މީހުން ވެސް އެ ގޮތުގެ މަތިން ވިޔަފާރި ކޮށްފިނަމަ، ފަހެ އެ މުޢާމަލާތް ލާޒިމްވެގެން ދާނެއެވެ. އަދި އެ ދެމީހުން ވިޔަފާރިއެއް ކުރުމަށް ފަހު، އެއިން އެކަކުވެސް އެ މުއާމަލާތް ބާޠިލުނުކޮށް ދެން ވަކިވެއްޖެ ނަމަ، ފަހެ އެ މުއާމަލާތް ލާޒިމްވެގެން ދާނެއެވެ.","البخاري 2112، ومسلم 1531"],["#260","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","","","عَنْ حَكِيمِ بْنِ حِزَامٍ رضي الله عنه قَالَ: قَالَ رَسُولُ اللَّهِ - ﷺ -: (الْبَيِّعَانِ بِالْخِيَارِ مَا لَمْ يَتَفَرَّقَا - أَوْ قَالَ: حَتَّى يَتَفَرَّقَا - فَإِنْ صَدَقَا وَبَيَّنَا بُورِكَ لَهُمَا فِي بَيْعِهِمَا. وَإِنْ كَتَمَا وَكَذَبَا مُحِقَتْ بَرَكَةُ بَيْعِهِمَا) .","ޙަކީމް ބިން ޙިޒާމް رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ގަނެވިއްކާ ދެ މީހުން ވަކިވުމަށް ދާންދެން، އެއިން ކޮންމެ މީހަކު ވެސް ވަނީ (އެ ވިޔަފާރީގެ މުއާމަލާތް ދެމެހެއްޓުން، ނުވަތަ އެ މުއާމަލާތް ބާޠިލު ކުރުމަށްފަހު އެ ތަކެތި ރުޖޫއަ ކުރުމުގެ) އިޚްތިޔާރު ލިބިގެންނެވެ. ދެން އެ ދެމީހުން ތެދުވެރިވެ، (ރަނގަޅަށް ކަން އޮތް) ބަޔާންކޮށްދީފި ނަމަ، އެ ދެމީހުންގެ ވިޔަފާރީގައި ބަރަކާތް ލައްވާނެއެވެ. އަދި އެ ދެބައިމީހުން (ކަން އޮތް ގޮތް) ވަންހަނާކޮށް، ދޮގުހަދައިފިނަމަ، އެ ދެމީހުންގެ ވިޔަފާރީގެ ބަރަކާތް ނެތިގެންދާނެއެވެ.“","البخاري 2079 واللفظ له، ومسلم 1532"],["#261","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب ما نهي عنه من البيوع","ގަނެވިއްކުމުގައި މަނާ ކުރެވިގެންވާ ކަންކަމުގެ ބާބު","عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ - رضي الله عنه -: (أَنَّ رَسُولَ اللَّهِ - ﷺ - نَهَى عَنْ الْمُنَابَذَةِ - وَهِيَ طَرْحُ الرَّجُلِ ثَوْبَهُ بِالْبَيْعِ إلَى الرَّجُلِ قَبْلَ أَنْ يُقَلِّبَهُ، أَوْ يَنْظُرَ إلَيْهِ - وَنَهَى عَنْ الْمُلامَسَةِ. وَالْمُلامَسَةُ: لَمْسُ الثَّوْبِ وَلا يُنْظَرُ إلَيْهِ) .","އަބޫ ސަޢީދުލް ޚުދްރީ رَضِيَ اللَّهُ عَنْهُ ރިވާކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ މުނާބަޛާގެ މުއާމަލާތް ކުރުން މަނާ ކުރެއްވިއެވެ. މިއީ މީހާކު އޭނާގެ ހެދުމެއް އެހެން މީހަކަށް އުކައިގެން، އެ މީހަކު ލައްވާ އެ ގަންނަން ޖެއްސުމެވެ. އެއީ އަނެކާ އެ ހެދުން ހުޅުވައި ބަލާ ނެހެދެނީހެވެ. އަދި ނަބިއްޔާ މުލާމަސާގެ މުއާމަލާތް ކުރުންވެސް މަނާ ކުރެއްވިއެވެ. އަދި މުލާމަސާއަކީ މީހަކު ހެދުމެއް ނުބަލައި (ހަމައެކަނި އޭގައި) އަތްލުމުން (އެ ގަންނަން ޖެއްސުމެވެ.)","البخاري 2144 واللفظ له، ومسلم 1512"],["#262","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب ما نهي عنه من البيوع","ގަނެވިއްކުމުގައި މަނާ ކުރެވިގެންވާ ކަންކަމުގެ ބާބު"," عَنْ أَبِي هُرَيْرَةَ - رضي الله عنه -: أَنَّ رَسُولَ اللَّهِ - ﷺ - قَالَ: (لا تَلَقَّوْا الرُّكْبَانَ، وَلا يَبِعْ بَعْضُكُمْ عَلَى بَيْعِ بَعْضٍ. وَلا تَنَاجَشُوا. وَلا يَبِعْ حَاضِرٌ لِبَادٍ. وَلا تُصَرُّوا الْغَنَمَ. وَمَنْ ابْتَاعَهَا فَهُوَ بِخَيْرِ النَّظَرَيْنِ، بَعْدَ أَنْ يَحْلُبَهَا. وَإِنْ رَضِيَهَا أَمْسَكَهَا، وَإِنْ سَخِطَهَا رَدَّهَا وَصَاعاً مِنْ تَمْرٍ) .\n<br><br>\nوَفِي لَفْظٍ: (هُوَ بِالْخِيَارِ ثَلاثَاً) .","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”(ބާޒާރަށް މުދާ ގެނައުމުގެ ކުރިން، ކުރީބައިގައި އެ އުފުލާ މީހުންގެ ކިބައިން ވިޔަފާރި ކުރުމަށް) ތިޔަބައިމީހުން ވިޔަފާރި ޣާފިލާތައް ދުއްވާ މީހުންނާ ބައްދަލު ނުކުރާށެވެ. އަދި އެއްބަޔަކު އަނެއްބަޔެއްގެ ވިޔަފާރީގެ މައްޗަށް ވިޔަފާރި ނުކުރާށެވެ. އަދި އެއްބަޔަކު އަނެއްބަޔަކަށް (ލަނޑުދިނުމުގެ ގޮތުން ރާވައިގެން، އެއްޗެއް ގަތުމުގެ ގަސްދަކާ ނުލައި އޭގެ) އަގު ބޮޑުކޮށް ނުހަދާށެވެ. އަދި ރަށުގައި އުޅޭ މީހަކު، (ރަށްފުށު) ބާދިޔާގައި އުޅޭ މީހަކަށް ޓަކައި (ވަކީލެއްގެ ގޮތުން) އެއްޗެހި ނުވިއްކާށެވެ. (އެހެނީ: އޭޖެންޓަކަށް ވެގެން ހުރެ ވިއްކައިފި ނަމަ، އެއަށް އިތުރު ފައިދާލާފަ މީސްތަކުންނަށް އަގު ބޮޑުކުރާނެއެވެ. އޭގެ ބަދަލުގައި ރަށްފުށު މީހާ ބާޒާރަށް ގޮސް އަމިއްލައަށް ވިއްކައިފިނަމަ އެންމެންނަށް ތަކެތި އަގުހެޔޮވާނެއެވެ.) އަދި ޖަމަލާއި ކަނބަޅީގެ (ކިރު ގިނަކަން މީސްތަކުންނަށް ދެއްކުމަށް ޓަކައި، އެ ތަކެތީގެ) ކިރު ނުފެލާ (ގިނަ ދުވަސް ވަންދެން) ނުބަހަައްޓާށެވެ. އަދި އެ ގޮތަށް ހުރި އެއްޗެއް މީހަކު ގަނެ ކިރު ފެލައިފި ނަމަ، ފަހެ އޭނާއަށް ދެ އިޚްތިޔާރެއް ލިބިގެންވެއެވެ. އެއަށް ރުހޭ ނަމަ އޭނާގެ އަތުގައި އޭތި ބަހައްޓަންވީއެވެ. އަދި އެއަށް ނުރުހޭ ނަމަ، ކަދުރުގެ ޞާޢަކާއެކު އޭތި އަނބުރާ ރައްދު ކުރަންވީއެވެ.“\n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”ފަހެ އޭނާއަށް އިޚްތިޔާރު ލިބިގެންވެއެވެ.“ (މިފަދައިން) ތިން ފަހަރު ޙަދީޘް ކުރެއްވިއެވެ.","البخاري 2150 واللفظ له، ومسلم 1515\n<br><br>\nالبخاري 2148 واللفظ له، ومسلم 1524\n"],["#263","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب ما نهي عنه من البيوع","ގަނެވިއްކުމުގައި މަނާ ކުރެވިގެންވާ ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رضي الله عنهما (أَنَّ رَسُولَ اللَّهِ - ﷺ - نَهَى عَنْ بَيْعِ حَبَلِ الْحَبَلَةِ. وَكَانَ بَيْعاً يَتَبَايَعُهُ أَهْلُ الْجَاهِلِيَّةِ، وَكَانَ الرَّجُلُ يَبْتَاعُ الْجَزُورَ إلَى أَنْ تُنْتَجَ النَّاقَةُ. ثُمَّ تُنْتَجَ الَّتِي فِي بَطْنِهَا.)\n<br><br>\nقِيلَ: إنَّهُ كَانَ يَبِيعُ الشَّارِفَ - وَهِيَ الْكَبِيرَةُ الْمُسِنَّةُ - بِنِتَاجِ الْجَنِينِ الَّذِي فِي بَطْنِ نَاقَتِهِ .","އަދި ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ޖަމަލެއްގެ ބަނޑުގައިވާ ދަރީގެ ދަރި ވިހުމާ ހަމައަށް، އެއްޗެއް ވިއްކައިގެން އޭގެ އަގު ދެއްކުން ފަސް ކުރުން (ނުވަތަ މާނައީ: ޖަމަލެއްގެ ބަނޑުގައިވާ ދަރީގެ ދަރި ވިއްކުން) ރަސޫލު ﷲ ﷺ މަނާ ކުރެއްވިއެވެ. އެއީ ޖާހިލިއްޔަ (ޒަމާނުގައި) މީހުން ކޮށްއުޅުނު ވިޔަފާރިއެކެވެ. ޖަމަލެއްގެ ބަނޑުގައިވާ ދަރިއެއް ވިހައިގެން، ދެން އެ ދަރި (ބޮޑުވުމަށް ފަހު އެއަށް އަނެއްކާ) އިތުރު ދަރިއެއް ލިބެންދެން (ފައިސާ ދެއްކުން ފަސް ކުރާ ގޮތަށް، އެހެން) ޖަމަލެއް (ނޫނީ ޖަމަލު މަސް) މީހުން ގަނެ ހަދާ އުޅުނެވެ.\n<br><br>\nއަދި ބުނެވޭ ގޮތުގައި: ބޮޑު މުސްކުޅި އަންހެން ޖަމަލެއް ވިއްކައި، އޭގެ އަގަކީ އެހެން ޖަމަލެއްގެ ބަނޑުގައިވާ ދަރީގެ ދަރި ކަމަށް ހެދުމެވެ. (ނޯޓު: މި ވިޔަފާރިތައް މަނާ ކުރެވުނީ ގޮތާއި މުއްދަތު ނޭންގޭނެތީވެއެވެ.)","البخاري 2143 واللفظ له، ومسلم 1514\n"],["#264","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب ما نهي عنه من البيوع","ގަނެވިއްކުމުގައި މަނާ ކުރެވިގެންވާ ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رضي الله عنهما: (أَنَّ رَسُولَ اللَّهِ - ﷺ - نَهَى عَنْ بَيْعِ الثَّمَرَةِ حَتَّى يَبْدُوَ صَلاحُهَا. نَهَى الْبَائِعَ وَالْمُشْتَرِيَ) .","އަދިވެސް ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: (ގަހުގައި އަޅާ) މޭވާ ރަނގަޅުކަން ފާޅުވުމުގެ ކުރިން އެ ތަކެތި ވިއްކުން ރަސޫލު ﷲ ﷺ މަނާ ކުރެއްވިއެވެ. ވިއްކާ މީހާއާއި ގަންނަ މީހާއަށް ވެސް މަނާ ކުރެއްވިއެވެ.","البخاري 2194، ومسلم 1535، 1534، وأبو داود 3367 واللفظ له"],["#265","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب ما نهي عنه من البيوع","ގަނެވިއްކުމުގައި މަނާ ކުރެވިގެންވާ ކަންކަމުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رضي الله عنه -: (أَنَّ رَسُولَ اللَّهِ - ﷺ - نَهَى عَنْ بَيْعِ الثِّمَارِ حَتَّى تُزْهِيَ قِيلَ: وَمَا تُزْهِي؟ قَالَ: حَتَّى تَحْمَرَّ. قَالَ: أَرَأَيْتَ إنْ مَنَعَ اللَّهُ الثَّمَرَةَ، بِمَ يَسْتَحِلُّ أَحَدُكُمْ مَالَ أَخِيهِ؟) .\n","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: (ގަހުގައި އަޅާ) މޭވާ ފައްކާވުމުގެ ކުރިން އެ ތަކެތި ވިއްކުން ރަސޫލު ﷲ ﷺ މަނާ ކުރެއްވިއެވެ. މީހަކު ދެންނެވިއެވެ: ދޮންވުމަކީ ކޮބައިތޯއެވެ؟ ވިދާޅުވިއެވެ: އެ ތަކެތި ދޮންވުމެވެ. އަދިވެސް ވިދާޅުވިއެވެ: ތިބާ ދެކެމުހެއްޔެވެ؟ ﷲ އެ މޭވާތައް ހަލާކު ކުރައްވައިފި ނަމަ، ތިޔަބައިމީހުންގެ ތެރެއިން މީހަކު (ކުރީބައިގައި ލާރި ނެގުމަށް ފަހު، ގަތް މީހާއަށް ބަދަލު ދިނުމަކާ ނުލައި) އޭނާގެ އަޚާގެ މުދާ ޙަލާލު ވާނީ ކިހިނަކުންހެއްޔެވެ؟","البخاري 2198 واللفظ له، ومسلم 1555"],["#266","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب ما نهي عنه من البيوع","ގަނެވިއްކުމުގައި މަނާ ކުރެވިގެންވާ ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رضي الله عنهما قَالَ: (نَهَى رَسُولُ اللَّهِ - ﷺ - أَنْ تُتَلَقَّى الرُّكْبَانُ، وَأَنْ يَبِيعَ حَاضِرٌ لِبَادٍ، قَالَ: فَقُلْتُ لابْنِ عَبَّاسٍ: مَا قَوْلُهُ حَاضِرٌ لِبَادٍ؟ قَالَ: لا يَكُونُ لَهُ سِمْسَاراً) .","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: (ބާޒާރަށް މުދާ ގެނައުމުގެ ކުރިން، ކުރީބައިގައި އެ އުފުލާ މީހުންގެ ކިބައިން ވިޔަފާރި ކުރުމަށް) އެ ވިޔަފާރި ޣާފިލާތައް ދުއްވާ މީހުންނާ ބައްދަލު ކުރުން ރަސޫލު ﷲ ﷺ މަނާ ކުރެއްވިއެވެ. އަދި ރަށުގައި އުޅޭ މީހަކު، (ރަށްފުށު) ބާދިޔާގައި އުޅޭ މީހަކަށް ޓަކައި އެއްޗެހި ވިއްކުން ނަބިއްޔާ މަނާ ކުރެއްވިއެވެ. (ޙަދީޘްގެ ރާވީއެއް ކަމަށްވާ ޠާވޫސް،) އިބްނު ޢައްބާސް އަރިހު ދެންނެވިއެވެ: ރަށުގައި އުޅޭ މީހަކު، ބާދިޔާގައި އުޅޭ މީހަކަށް (ނުވިއްކާށޭ) ވިދާޅުވުމުގެ މާނައަކީ ކޮބައިތޯއެވެ؟ އިބްނު ޢައްބާސް ވިދާޅުވިއެވެ: އެ ބާދިޔާގައި އުޅޭ މީހާއަށް، ރަށުގައި އުޅޭ މީހާ (އަގަށް) ވަކީލެއް ކަމުގައި ނުވާށެވެ.","البخاري 2274، ومسلم 1521 واللفظ له"],["#267","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب ما نهي عنه من البيوع","ގަނެވިއްކުމުގައި މަނާ ކުރެވިގެންވާ ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رضي الله عنهما قَالَ: (نَهَى رَسُولُ اللَّهِ - ﷺ - عَنْ الْمُزَابَنَةِ: أَنْ يَبِيعَ ثَمَرَ حَائِطِهِ، إنْ كَانَ نَخْلاً: بِتَمْرٍ كَيْلاً. وَإِنْ كَانَ كَرْماً: أَنْ يَبِيعَهُ بِزَبِيبٍ كَيْلاً، أَوْ كَانَ زَرْعاً: أَنْ يَبِيعَهُ بِكَيْلِ طَعَامٍ. نَهَى عَنْ ذَلِكَ كُلِّهِ) .","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ މުޒާބަނާގެ މުއާމަލާތް ކުރުން މަނާ ކުރެއްވިއެވެ. އެއީ މީހަކު އޭނާގެ ދަނޑުގައިވާ މޭވާ، އެ މޭވާއަކީ ރުކުގައި (އަޅާފައިވާ) ރޯކަދުރު ނަމަ،  އެ ރޯކަދުރުގެ ބަދަލަކަށް ހިކި ކަދުރު ހަމަ އެ މިންވަރަށް ވިއްކުމެވެ. އަދި އެއީ (ގަހުގައި އަޅާފައިވާ) މޭބިސްކަދުރު ނަމަ، އެ މޭބިސްކަދުރުގެ ބަދަލަކަށް ހިކި މޭބިސްކަދުރު ހަމަ އެ މިންވަރަށް ވިއްކުމެވެ. އަދި (ކުރާގަނޑުގައި އަޅާފައިވާ) ގޮވާން ކަމުގައި ވާނަމަ، އޭގެ ބަދަލަކަށް (ކަނޑާފައިވާ) ގޮވާން ހަމަ އެ މިންވަރަށް ވިއްކުމެވެ. އެއިން ކޮންމެ ކަމެއް ނަބިއްޔާ މަނާ ކުރެއްވިއެވެ. (ނޯޓު: މިއީ ގޮތް ނޭނގުމާއި މިންވަރު ނޭނގުމުގެ ސަބަބުން މަނާ ކުރެވިފައިވާ ވިޔަފާރިއެކެވެ. ނަމަވެސް ޢަރާޔާގެ މުއާމަލާތަކީ މީގެ އިސްތިސްނާއެކެވެ. އެއީ ފަސްދޮޅަސް ޞާޢަށް ވުރެ ގިނަ ނުވާ ގޮތަށް، ރުކުގައި އަޅާފައިވާ ރޯކަދުރުގެ ލާފާ ކުރެވޭ މިންވަރެއްގެ ބަދަލުގައި، އެއާ އެއްވަރަށް ހިކިކަދުރު ދިނުމެވެ. މިއީ ފައިސާ ނެތިގެން، ކެއުމަށް ޓަކައި ރޯކަދުރަށް ބޭނުން ޖެހިފައިވާތީ މީސްތަކުންނަށް ލުއި ކުރުމުގެ ގޮތުން ދެވުނު ހުއްދައެކެވެ.)","البخاري 2205 واللفظ له، ومسلم 1542"],["#268","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب ما نهي عنه من البيوع","ގަނެވިއްކުމުގައި މަނާ ކުރެވިގެންވާ ކަންކަމުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رضي الله عنهما قَالَ: (نَهَى النَّبِيُّ - ﷺ - عَنْ الْمُخَابَرَةِ وَالْمُحَاقَلَةِ، وَعَنْ الْمُزَابَنَةِ وَعَنْ بَيْعِ الثَّمَرَةِ حَتَّى يَبْدُوَ صَلاحُهَا، وَأَنْ لا تُبَاعَ إلاَّ بِالدِّينَارِ وَالدِّرْهَمِ، إلاَّ الْعَرَايَا)","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ މުޚާބަރާއާއި، މުޙާޤަލާއާއި، މުޒާބަނާގެ މުއާމަލާތްތައް ކުރުން މަނާ ކުރެއްވިއެވެ. (މުޚާބަރާއަކީ ދަނޑުވެރިކަން ކުރުމަށް ބިން ދޫކުރުމުގެ ބަދަލަކަށް، އަގުގެ ގޮތުގައި އޭގައި ހައްދާ ތަކެތީގެ ބައެއް ނެގުމެވެ. އަދި މުޙާޤަލާއަކީ ކުރާގަނޑުގައި އަޅާފައިވާ ގޮވާމުގެ ބަދަލަކަށް، އަގުގެ ގޮތުގައި ކަނޑާފައިވާ ގޮވާން ދިނުމެވެ. އަދި މުޒާބަނާއަކީ ރުކުގައި އަޅާފައިވާ ރޯކަދުރުގެ ބަދަލަކަށް، އަގުގެ ގޮތުގައި ހިކި ކަދުރު ދިނުން، ނުވަތަ ގަހުގައި އަޅާފައިވާ މޭބިސްކަދުރުގެ ބަދަލަކަށް، ހިކި މޭބިސްކަދުރު ދިނުމެވެ.) އަދި (ގަހުގައި އަޅާ) މޭވާ ރަނގަޅުކަން ފާޅުވުމުގެ ކުރިން އެ ތަކެތި ވިއްކުން ނަބިއްޔާ މަނާ ކުރެއްވިއެވެ. އަދި ދީނާރާއި ދިރުހަމަށް (އެބަހީ: ފައިސާއަށް) ފިޔަވައި މޭވާ ވިއްކުން މަނާ ކުރެއްވިއެވެ. އެއީ ޢަރާޔާގެ މުއާމަލާތުގައި މެނުވީއެވެ. (އެބަހީ: ފަސްދޮޅަސް ޞާޢަށް ވުރެ ގިނަ ނުވާ ގޮތަށް، ރުކުގައި އަޅާފައިވާ ރޯކަދުރުގެ ލާފާ ކުރެވޭ މިންވަރެއްގެ ބަދަލުގައި، އެއާ އެއްވަރަށް ހިކިކަދުރު ދިނުމެވެ.)","البخاري 2381، ومسلم 1536"],["#269","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب ما نهي عنه من البيوع","ގަނެވިއްކުމުގައި މަނާ ކުރެވިގެންވާ ކަންކަމުގެ ބާބު","عَنْ أَبِي مَسْعُودٍ الأَنْصَارِيِّ - رضي الله عنه - (أَنَّ رَسُولَ اللَّهِ - ﷺ - نَهَى عَنْ ثَمَنِ الْكَلْبِ، وَمَهْرِ الْبَغِيِّ، وَحُلْوَانِ الْكَاهِنِ) .","އަބޫ މަސްޢޫދު އަލްއަންޞާރީ ﷲ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ކުއްތާ (ގަނެވިއްކުމުގެ) އަގާއި، ހަށިވިއްކާ މީހާގެ އަގާއި، ކާހިނުގެ އަގު، ރަސޫލު ﷲ ﷺ މަނާ ކުރެއްވިއެވެ.","البخاري 2237، ومسلم 1567"],["#270","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب ما نهي عنه من البيوع","ގަނެވިއްކުމުގައި މަނާ ކުރެވިގެންވާ ކަންކަމުގެ ބާބު","عَنْ رَافِعِ بْنِ خَدِيجٍ - رضي الله عنه - أَنْ رَسُولَ اللَّهِ - ﷺ - قَالَ: (ثَمَنُ الْكَلْبِ خَبِيثٌ. وَمَهْرُ الْبَغِيِّ خَبِيثٌ، وَكَسْبُ الْحَجَّامِ خَبِيثٌ) .","ރާފިޢު ބިން ޚަދީޖު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ކުއްތާ (ގަނެވިއްކުމުގެ) އަގަކީ ހަޑި މުޑުދާރު އެއްޗެކެވެ. އަދި ހަށިވިއްކާ މީހާގެ އަގަކީ ހަޑި މުޑުދާރު އެއްޗެކެވެ. އަދި ޙިޖާމާ ޖަހާ މީހާ ނަގާ އަގަކީ ހަޑި މުޑުދާރު އެއްޗެކެވެ.","مسلم 1568"],["#271","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب العرايا وغير ذلك","ޢަރާޔާއާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ زَيْدِ بْنِ ثَابِتٍ - رضي الله عنه - (أَنَّ رَسُولَ اللَّهِ - ﷺ - رَخَّصَ لِصَاحِبِ الْعَرِيَّةِ: أَنْ يَبِيعَهَا بِخَرْصِهَا)\n<br><br>\nوَلِمُسْلِمٍ: (بِخَرْصِهَا تَمْراً، يَأْكُلُونَهَا رُطَباً) .","ޒައިދު ބިން ޘާބިތު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ (މީސްތަކުންނަށް) ލުއި ކުރެއްވުމުގެ ގޮތުން، އެ މީހެއްގެ ރުއްތަކުގައި ރޯކަދުރު އަޅާފައި ހުރި މީހަކަށް، އެ ރޯކަދުރުގެ ބަދަލުގައި (ހިކިކަދުރު) ހަމަ އެ މިންވަރަށް ވިއްކުން ނަބިއްޔާ ހުއްދަ ކުރެއްވިއެވެ.\n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: (ލަފާކޮށްފައި) އެ ރޯކަދުރުގެ ބަދަލުގައި ހަމަ އެ މިންވަރަށް ހިކިކަދުރު ވިއްކާ ގޮތަށް، އަދި (ގަންނަ މީހާ) ރޯކަދުރު ކާ ގޮތަށް (މުއާމަލާތް ކުރުމުގެ ހުއްދަ ދެއްވިއެވެ.) (މިއީ ރިބާގެ މުއާމަލާތްތަކުގައި ވާ އިސްތިސްނާއެކެވެ. ފަސްދޮޅަސް ޞާޢަށް ވުރެ ގިނަ ނުވާ ގޮތަށް މި ގޮތަށް ދެ ވައްތަރެއްގެ ކަދުރު ބަދަލު ކުރުން ހުއްދަވެގެން ވެއެވެ.)","البخاري 2188، ومسلم 1539\n<br><br>\nمسلم 1539"],["#272","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب العرايا وغير ذلك","ޢަރާޔާއާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رضي الله عنه -: (أَنَّ رَسُولَ اللَّهِ - ﷺ - رَخَّصَ فِي بَيْعِ الْعَرَايَا فِي خَمْسَةِ أَوْسُقٍ أَوْ دُونَ خَمْسَةِ أَوْسُقٍ) .","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷲ ﷺ (މީސްތަކުންނަށް) ލުއި ކުރެއްވުމުގެ ގޮތުން، (ލަފާކޮށްފައި) ރުއްތަކުގައި އަޅާފައި ރޯކަދުރުގެ ބަދަލަކަށް، އަގުގެ ގޮތުން (ހިކިކަދުރު) ހަމަ އެ މިންވަރަށް ވިއްކުން ނަބިއްޔާ ހުއްދަ ކުރެއްވިއެވެ. އެއީ ފަސް ވަސްޤު (ނުވަތަ ފަސްދޮޅަސް ޞާޢުގެ) މިންވަރަށެވެ. ނޫނީ ފަސް ވަސްޤަށް ވުރެ މަދުވާ މިންވަރަށެވެ.","البخاري 2190 واللفظ له، ومسلم 1541"],["#273","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب العرايا وغير ذلك","ޢަރާޔާއާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رضي الله عنهما أَنَّ رَسُولَ اللَّهِ - ﷺ قَالَ: (مَنْ بَاعَ نَخْلاً قَدْ أُبِّرَتْ فَثَمَرُهَا لِلْبَائِعِ، إلاَّ أَنْ يَشْتَرِطَ الْمُبْتَاعُ) .\n<br><br>\nوَلِمُسْلِمٍ (وَمَنْ ابْتَاعَ عَبْداً فَمَالُهُ لِلَّذِي بَاعَهُ إلاَّ أَنْ يُشْتَرَطَ الْمُبْتَاعُ) .","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ކަދުރު ރުކެއް ޖޯޑު (އެބަހީ: ޕޮލިނޭޓް) ކުރެވުމަށް ފަހު މީހަކު އެ ވިއްކައިފި ނަމަ، ފަހެ އޭގެ މޭވާ ލިބިގެން ވަނީ ވިއްކި މީހާއަށެވެ. އެއީ (އެ ނޫން ގޮތަކަށް) ގަންނަ މީހާ ޝަރުތު ކުރާ ނަމަ މެނުވީއެވެ.“\n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ”މީހަކު އަޅެއް ވިއްކައިފި ނަމަ، ފަހެ އެ އަޅެއްގެ މުދާ ލިބިގެން ވަނީ އޭނާ ވިއްކި މީހާއަށެވެ. އެއީ (އެ ނޫން ގޮތަކަށް) ގަންނަ މީހާ  ޝަރުތު ކުރާ ނަމަ މެނުވީއެވެ.“","البخاري 2716، ومسلم 1543\n<br><br>\nمسلم 1543"],["#274","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب العرايا وغير ذلك","ޢަރާޔާއާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رضي الله عنهما أَنَّ رَسُولَ اللَّهِ - ﷺ - قَالَ: (مَنْ ابْتَاعَ طَعَامًا فَلا يَبِعْهُ حَتَّى يَسْتَوْفِيَهُ)\n<br><br>\nوَفِي لَفْظٍ: (حَتَّى يَقْبِضَهُ) .\n<br><br>\nوَعَنْ ابْنِ عَبَّاسٍ مِثْلُهُ.","އަދިވެސް ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: މީހަކު ގޮދަން (ނުވަތަ ކާތަކެތި) ގަނެފި ނަމަ، އޭތި އެއްކޮށް ލިބެންދެން (އެހެން މީހަކަށް) ވިއްކައިގެން ނުވާނެއެވެ.\n<br><br>\nއަދި އެހެން ރިވާތެއްގައި ވެއެވެ: އޭތި އަތަށް ނަގަންދެން (އެހެން މީހަކަށް ވިއްކައިގެން ނުވާނެއެވެ.)\n<br><br>\nއަދި އިބްނު ޢައްބާސްގެ އަރިހުން ވެސް މިފަދައިން ރިވާވެފައި ވެއެވެ.","البخاري 2126، ومسلم 1526\n<br><br>\nالبخاري 2133، ومسلم 1526\n<br><br>\nالبخاري 2132، ومسلم 1525"],["#275","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب العرايا وغير ذلك","ޢަރާޔާއާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رضي الله عنهما أَنَّهُ سَمِعَ رَسُولَ اللَّهِ - ﷺ - يَقُولُ عَامَ الْفَتْحِ: (إنَّ اللَّهَ وَرَسُولَهُ حَرَّمَ بَيْعَ الْخَمْرِ وَالْمَيْتَةِ وَالْخِنْزِيرِ وَالأَصْنَامِ. فَقِيلَ: يَا رَسُولَ اللَّهِ، أَرَأَيْتَ شُحُومَ الْمَيْتَةِ؟ فَإِنَّهُ يُطْلَى بِهَا السُّفُنُ، وَيُدْهَنُ بِهَا الْجُلُودُ. وَيَسْتَصْبِحُ بِهَا النَّاسُ. فَقَالَ: لا، هُوَ حَرَامٌ. ثُمَّ قَالَ رَسُولُ اللَّهِ - ﷺ - عِنْدَ ذَلِكَ: قَاتَلَ اللَّهُ الْيَهُودَ. إنَّ اللَّهَ لَمَّا حَرَّمَ عَلَيْهِمْ شُحُومَهَا، جَمَلُوهُ ثُمَّ بَاعُوهُ فَأَكَلُوا ثَمَنَهُ) .","ޖާބިރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: މައްކާ ފަތަޙަ ކުރެއްވި އަހަރު، ރަސޫލު ﷲ ﷺ އެ ބިމުގައި ހުންނަވައިގެން ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑު އެހީމެވެ : ”ހަމަކަށަވަރުން ﷲ އާއި އެ އިލާހުގެ ރަސޫލާ ވަނީ ރަލާއި، މުޅަ ތަކެއްޗާއި، އޫރު މަހާއި، ބުދުތަކާ މި ތަކެތީގެ ވިޔަފާރި ކުރުން ޙަރާމް ކުރައްވާފައެވެ.“ ދެން ނަބިއްޔާއަށް ދެންނެވުނެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. މުޅަ ތަކެތީގެ ސަރުބީއާމެދު ދެކިލައްވަނީ ކިހިނަކުންތޯއެވެ؟ އެހެނީ ހަމަކަށަވަރުން އޮޑިދޯނިފަހަރުގައި ( ތެޔޮ ހާކައިގެން ފެން ވަނަ ނުދިނުމަށް) ކަޑިތާރު ލާން، އެ މުޅަ ތަކެތީގެ ސަރުބީގެ ތެޔޮ ބޭނުން ކުރެވެއެވެ. އަދި ދިބާޣުކުޅަހަންތަކުގައި ތެޔޮ ހޭކުމަށް ވެސް އޭގެ ބޭނުން ކުރެއެވެ. އަދި ވޮށް ދިއްލައި އަލި ކުރުމަށް ވެސް މީސްތަކުން އޭގެ ބޭނުން ކުރެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ނޫނެކެވެ. އެ ހަރާމްވެގެން ވެއެވެ.“ ދެން އެއަށްފަހު ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲ ޔަހޫދީން ޤަތުލު ކުރައްވާށިއެވެ. (އެބަހީ: އެބައިމީހުންނަށް ލަޢުނަތް ލައްވާށިއެވެ.) ހަމަކަށަވަރުން ﷲ އެބައިމީހުންނަށް ސަރުބީ ޙަރާމް ކުރެއްވުމުން، އެބައިމީހުން އެ ސަރުބީ ވިރުވައި ތެލަށް ހަދައިގެން، ދެން އެ ވިއްކައިގެން އޭގެ އަގު ކައި އުޅުނެވެ.“ (އެބަހީ: އޭގެން ލިބުނު ފައިސާ ބޭނުން ކުރިއެވެ.)        ","البخاري 2236، ومسلم 1581"],["#276","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب السلم","ސަލަމް (ކުރިއާލާ އަގު ދެއްކުމުގެ) ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رضي الله عنهما قَالَ: (قَدِمَ رَسُولُ اللَّهِ - ﷺ - الْمَدِينَةَ، وَهُمْ يُسْلِفُونَ فِي الثِّمَارِ: السَّنَةَ وَالسَّنَتَيْنِ وَالثَّلاثَ. فَقَالَ: مَنْ أَسْلَفَ فِي شَيْءٍ فَلْيُسْلِفْ فِي كَيْلٍ مَعْلُومٍ، وَوَزْنٍ مَعْلُومٍ، إلَى أَجَلٍ مَعْلُومٍ) .","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ މަދީނާއަށް ވަޑައިގެންނެވި ހިނދު، މީސްތަކުން ކުރިއާލާ އަހަރެއް، ދެ އަހަރު، އަދި ތިން އަހަރު މޭވާއަށް އަގު ދައްކާ ހަދާ އުޅުނެވެ. ދެން ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”މީހަކު އެއްޗަކަށް ކުރިއާލާ އަގު ދައްކާ ނަމަ، ފަހެ ކަނޑައެޅިގެންވާ ވަކި މިންވަރަކަށް، އަދި ވަކި ވަޒަނަކަށް، އަދި ވަކި މުއްދަކާ ހަމައަށް ދައްކާށެވެ.“","البخاري 2239، 2240، ومسلم 1604"],["#277","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الشروط في البيع","ވިޔަފާރީގައި ޝަރުތު ކުރުމުގެ ބާބު","عَنْ عَائِشَةَ رضي الله عنها قَالَتْ: (جَاءَتْنِي بَرِيرَةُ: فَقَالَتْ: كَاتَبْتُ أَهْلِي عَلَى تِسْعِ أَوَاقٍ، فِي كُلِّ عَامٍ أُوقِيَّةٌ. فَأَعِينِينِي. فَقُلْتُ: إنْ أَحَبَّ أَهْلُكِ أَنْ أَعُدَّهَا لَهُمْ، وَوَلاؤُكِ لِي فَعَلْتُ. فَذَهَبَتْ بَرِيرَةُ إلَى أَهْلِهَا، فَقَالَتْ: لَهُمْ. فَأَبَوْا عَلَيْهَا. فَجَاءَتْ مِنْ عِنْدِهِمْ وَرَسُولُ اللَّهِ - ﷺ - جَالِسٌ. فَقَالَتْ: إنِّي عَرَضْتُ ذَلِكَ عَلَى أَهْلِي، فَأَبَوْا إلاَّ أَنْ يَكُونَ لَهُمُ الْوَلاءُ. فَأَخْبَرَتْ عَائِشَةُ النَّبِيَّ - ﷺ -. فَقَالَ: خُذِيهَا وَاشْتَرِطِي لَهُمُ الْوَلاءَ. فَإِنَّمَا الْوَلاءُ لِمَنْ أَعْتَقَ. فَفَعَلَتْ عَائِشَةُ. ثُمَّ قَامَ رَسُولُ اللَّهِ - ﷺ - فِي النَّاسِ، فَحَمِدَ اللَّهَ وَأَثْنَى عَلَيْهِ. ثُمَّ قَالَ: أَمَّا بَعْدُ. فَمَا بَالُ رِجَالٍ يَشْتَرِطُونَ شُرُوطاً لَيْسَتْ فِي كِتَابِ اللَّهِ؟ كُلُّ شَرْطٍ لَيْسَ فِي كِتَابِ اللَّهِ فَهُوَ بَاطِلٌ وَإِنْ كَانَ مِائَةَ شَرْطٍ. قَضَاءُ اللَّهِ أَحَقُّ. وَشَرْطُ اللَّهِ أَوْثَقُ. وَإِنَّمَا الْوَلاءُ لِمَنْ أَعْتَقَ) .","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: (އޭރު އަޅު އަންހެނެއް ކަމުގައިވާ) ބަރީރާ ތިމަން ކަމަނާގެ ގާތަށް އައިސް ދެންނެވިއެވެ: ތިމަންނަގެ ސާހިބާއިންނަށް ކޮންމެ  އަހަރަކު (ރަނުގެ) އޫޤިޔާއެއް ދީ، (ޖުމްލަ) ނުވަ އޫޤިޔާ ހަމަކޮށްގެން ތިމަން މިނިވަންވާ ގޮތަށް އެބައިމީހުންނާ އެއްބަސްވުމެއް ހަދައިފީމެވެ. ވީމާ ކަމަނާ ތިމަންނައަށް އެހީވެދެއްވާށެވެ. ޢާއިޝާ ވިދާޅުވިއެވެ: ތިބާގެ ސާހިބައިން ރުހޭ ނަމަ، ތިބާގެ ވަލާވެރިކަން ތިމަން ކަމަނާއަށް ލިބޭ ގޮތުގެ މަތިން، ތިމަން ކަމަނާ (އެއް ފަހަރާ) އެ އަދަދު ދައްކާ ޚަލާޞްކޮށްދީފާނަމެވެ. (ނޯޓު: ވަލާވެރިކަމަކީ މީހަކު އަޅެއް މިނިވަން ކުރުމުގެ ސަބަބުން، އެ މީހަކަށް އެ އަޅެއްގެ ކިބައިން ވާރުތަވުމުގެ ޙައްޤު ލިބުމެވެ.) ދެން ބަރީރާ އޭނާގެ ސާހިބައިން ގާތަށް ދިޔުމުން، އެބައިމީހުން އެ ކަމަށް އެއްބަހެއް ނުވިއެވެ. އެއަށްފަހު ބަރީރާ އެނބުރި ޢާއިޝާގެ އަރިހަށް އަތުވިއެވެ. އެ ހިނދު ރަސޫލު ﷲ ﷺ އެތާ އިށީނދެވަޑައިގެން އިންނެވިއެވެ. ދެން ބަރީރާ ދެންނެވިއެވެ: އެބައިމީހުންނަށް އެ ކަން ހުށާހެޅީމެވެ. ނަމަވެސް އެއްބަހެއް ނުވިއެވެ. އެއީ އެބައިމީހުންނަށް ވަލާވެރިކަން ލިބޭ ނަމަ މެނުވީއެވެ. ފަހެ ނަބިއްޔާ ﷺ އަށް (އެ ވާހަކަ) އިވިވަޑައިގަތެވެ. އަދި ޢާއިޝާ، ނަބިއްޔާ ﷺ އަށް (ކަންތައް ވީ ގޮތުގެ) ޚަބަރު ކިޔައިދެއްވިއެވެ. ދެން ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ކަމަނާ ބަރީރާ (ގަންނަވާ) ނަންގަވާށެވެ. އަދި (ކަމަނާއަށް ވަލާވެރިކަން ލިބޭގޮތަށް) އެބައިމީހުންނަށް ޝަރުތު ކުރާށެވެ. އެހެނީ ވަލާވެރިކަން ލިބުން ވަނީ ހަމައެކަނި މިނިވަން ކުރާ މީހާއަށެވެ.“ ފަހެ ޢާއިޝާ އެ ފަދައިން ކަންތައް ކުރެއްވިއެވެ. ދެން ނަބިއްޔާ މީސްތަކުންގެ މެދުގައި ތެދުވެވަޑައިގެން، ﷲ އަށް ޙަމްދު ސަނާ ދަންނަވާފައި ޙަދީޘް ކުރެއްވިއެވެ: ”އެއަށްފަހު ދަންނާށެވެ. ﷲ ގެ ފޮތުގައި ނުވާ ޝަރުތުތަކެއް ބަޔަކު މީހުން ކަނޑައަޅަނީ ކިހިނެއްވެގެންހެއްޔެވެ؟ ﷲ ގެ ފޮތުގައި ނުވާ ކޮންމެ ޝަރުތަކީ ބާޠިލު އެއްޗެކެވެ. އެއީ ސަތޭކަ ޝަރުތު ކަމުގައިވިޔަސް ވެސްމެއެވެ. ﷲ ގެ ނިޔާ މާ ޙައްޤުވެގެން ވެއެވެ. އަދި ﷲ ގެ ޝަރުތު މާ ވަރުގަދަވެގެން ވެއެވެ. އަދި  ވަލާވެރިކަން ލިބުން ވަނީ ހަމައެކަނި މިނިވަން ކުރާ މީހާއަށެވެ.“","البخاري 2168 واللفظ له، ومسلم 1504"],["#278","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الشروط في البيع","ވިޔަފާރީގައި ޝަރުތު ކުރުމުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رضي الله عنهما (أَنَّهُ كَانَ يَسِيرُ عَلَى جَمَلٍ فَأَعْيَا، فَأَرَادَ أَنْ يُسَيِّبَهُ. فَلَحِقَنِي النَّبِيُّ - ﷺ - فَدَعَا لِي، وَضَرَبَهُ. فَسَارَ سَيْراً لَمْ يَسِرْ مِثْلَهُ. ثُمَّ قَالَ: بِعْنِيهِ بِوُقِيَّةٍ. قُلْتُ: لا. ثُمَّ قَالَ: بِعْنِيهِ. فَبِعْتُهُ بِأُوقِيَّةٍ. وَاسْتَثْنَيْتُ حِمْلانَهُ إلَى أَهْلِي. فَلَمَّا بَلَغْتُ: أَتَيْتُهُ بِالْجَمَلِ. فَنَقَدَنِي ثَمَنَهُ. ثُمَّ رَجَعْتُ. فَأَرْسَلَ فِي إثْرِي. فَقَالَ: أَتُرَانِي مَاكَسْتُكَ لآخُذَ جَمَلَكَ؟ خُذْ جَمَلَكَ وَدَرَاهِمَكَ. فَهُوَ لَكَ)","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ހިނގުން ބަލިކަށިވެފައިވާ ޖަމަލެއްގެ މަތީގައި ޖާބިރު ދަތުރު ކުރިއެވެ. ޖާބިރު ބޭނުންވީ އެ ޖަމަލު (ލަސްކަމުން ސަހަރާ ތެރެއަށް) ދޫކޮށްލާށެވެ. ޖާބިރު ވިދާޅުވިއެވެ: (އެ ދަތުރު މަތީގައި) ނަބިއްޔާ ﷺ ތިމަން ގާތަށް ވަޑައިގެން ގޮވާލެއްވިއެވެ. އަދި އެ ޖަމަލުގައި ޖައްސަވާލެއްވިއެވެ. އެއަށްފަހު އެ ޖަމަލު ކުރިން ދުވަހަކުވެސް ނުދާހާ ބާރުމިނަކަށް ހިނގަައިގަތެވެ. ދެން ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”(ރަނުގެ) އޫޤިޔާއަކަށް އެ ޖަމަލު ތިމަން ނަބިއްޔާއަށް ވިއްކާށެވެ.“ ތިމަން ދެންނެވީމެވެ: ނޫނެކެވެ. (ނޯތު: އެއީ ޖާބިރުގެ ހުރި ހަމައެކަނި ޖަމަލެވެ. އަދި ޖާބިރު ވަނީ ޝަހީދުވި ބައްޕާފުޅުގެ ޔަތީމު ކުޑަކުޑަ އަންހެން ދަރިންތަކެއް ދަތި ޙާލުގައި ބަލަން ޖެހިފައެވެ.) ދެންވެސް ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ ޖަމަލު ތިމަން ނަބިއްޔާއަށް ވިއްކާށެވެ.“ ދެން އެ ޖަމަލު މަތީގައި (ފުރަތަމަ) ތިމަން ގެއަށް ފޯރަންދެން ދަތުރު ކުރާ ގޮތަށް، އޫޤިޔާއަކަށް ނަބިއްޔާއަށް އެ ޖަމަލު ވިއްކީމެވެ. (ގެއާ ހިސާބަށް) ދެވުމުން، ޖަމަލު ހިފައިގެން ނަބިއްޔާގެ އަރިހަށް ތިމަން ދިޔައިމެވެ. އަދި އޭގެ އަގު ނަބިއްޔާ ތިމަންނައަށް ދެއްވިއެވެ. އެއަށްފަހު ތިމަން އެނބުރި ދާން ހިނގައިގަތީމެވެ.  ނަބިއްޔާ ތިމަން ފަހަތުން މީހަކު ފޮނުއްވައި (ތިމަން އަނބުރާ ގެނެއްސިއެވެ.)  ދެން ޙަދީޘް ކުރެއްވިއެވެ. ”ތިބާއަށް އަގު ކުޑަކޮށް ތިމަން ނަބިއްޔާ ވިދާޅުވީ ތިބާގެ ޖަމަލު އަތުލެއްވުމަށް ކަމަށް ހީކުރީތޯއެވެ؟ ތިބާގެ ޖަމަލާއި އަދި ދިރުހަމްތައް ވެސް ނަގާށެވެ. އެއީ ތިބާއަށްވާ ތަކެއްޗެވެ.“","البخاري 2718، ومسلم 1221 واللفظ له"],["#279","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الشروط في البيع","ވިޔަފާރީގައި ޝަރުތު ކުރުމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رضي الله عنه - قَالَ: (نَهَى رَسُولُ اللَّهِ - ﷺ أَنْ يَبِيعَ حَاضِرٌ لِبَادٍ. وَلا تَنَاجَشُوا وَلا يَبِعِ الرَّجُلُ عَلَى بَيْعِ أَخِيهِ. وَلا يَخْطُبْ عَلَى خِطْبَتِهِ. وَلا تَسْأَلِ الْمَرْأَةُ طَلاقَ أُخْتِهَا لِتَكْفِئَ مَا فِي صَحْفَتِهَا)","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަށުގައި އުޅޭ މީހަކު، (ރަށްފުށު) ބާދިޔާގައި އުޅޭ މީހަކަށް ޓަކައި (ވަކީލެއްގެ ގޮތުން) އެއްޗެހި ވިއްކުން ރަސޫލު ﷲ ﷺ މަނާ ކުރެއްވިއެވެ. އަދި އެއްބަޔަކު އަނެއްބަޔަކަށް (ލަނޑުދިނުމުގެ ގޮތުން ރާވައިގެން، އެއްޗެއް ގަތުމުގެ ގަސްދަކާ ނުލައި އޭގެ) އަގު ބޮޑުކޮށް ނަހަދާން ނަބިއްޔާ އެންގެވިއެވެ. އަދި މީހަކު، އޭނާގެ އަޚާގެ ވިޔަފާރީގެ މައްޗަށް ވިޔަފާރި ނުކުރާން އެންގެވިއެވެ. އަދި މީހަކު، އޭނާގެ އަޚާގެ ޚިޠުބާގެ މައްޗަށް ޚިތުބާ ނުވާން އެންގެވިއެވެ. އަދި އަންހެނަކު، އޭނާގެ އުޚްތާއަށް ލިބިފައިވާ (ކައިވެނީގެ ހެޔޮކަން) އަތުލައިގަތުމުގެ ގޮތުން، އެ އުޚްތާ ވަރިކޮށްދިނުން އެދި ނާހާން ނަބިއްޔާ އެންގެވިއެވެ.","البخاري 2140 واللفظ له، ومسلم 413"],["#280","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الربا والصرف","ރިބާއާއި މާރު ކުރުމުގެ ބާބު","عَنْ عُمَرَ بْنِ الْخَطَّابِ - رضي الله عنه - قَالَ: قَالَ رَسُولُ اللَّهِ - ﷺ -: (الذَّهَبُ بِالْوَرِقِ رِبًا، إلاَّ هَاءَ وَهَاءَ، وَالْبُرُّ بِالْبُرِّ رِباً، إلاَّ هَاءَ وَهَاءَ. وَالشَّعِيرُ بِالشَّعِيرِ رِباً، إلاَّ هَاءَ وَهَاءَ) .","ޢުމަރު ބިން އަލްޚާއްޠާބު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ރަނަށް ރަން ބަދަލު ކުރުމަކީ ރިބާއެވެ. އެއީ އަތުން އަތަށް (އެއް ވަގުތެއްގައި، އެއްވަރަކަށް) ބަދަލު ކުރެވޭނަމަ މެނުވީއެވެ. އަދި ރިއްސަށް ރިހި ބަދަލު ކުރުމަކީ ރިބާއެވެ. އެއީ އަތުން އަތަށް ބަދަލު ކުރެވޭނަމަ މެނުވީއެވެ. އަދި ގޮދަނަށް ގޮދަން ބަދަލު ކުރުމަކީ ރިބާއެވެ. އެއީ އަތުން އަތަށް އެއްވަރަކަށް ބަދަލު ކުރެވޭނަމަ މެނުވީއެވެ. އަދި ހިމަގޮދަނަށް ހިމަގޮދަން ބަދަލު ކުރުމަކީ ރިބާއެވެ. އެއީ އަތުން އަތަށް ބަދަލު ކުރެވޭނަމަ މެނުވީއެވެ.“\n\n(ނޯޓު: ބަހަވީ ގޮތުން ރިބާއަކީ އިތުރުވުމެވެ. ޝަރުޢީ ގޮތުން ރިބާއަކީ އެކަކު އަނެކަކަށް މުދާ ބަދަލު ކުރުމުގައި، ވަކި މުދާތަކެއްގައި އުނިއިތުރުކަން ގެނައުމުން، ނުވަތަ ވަކި މުދާތަކެއް ދިނުމުގައި ލަސް ކުރުމުން ޙަރާމްވެގެންދާ މުއާމަލާތެކެވެ. މިއީހަލާކު ކުރަނިވި ބޮޑުވެގެންވާ ފާފައެކެވެ. ރިބާގައި އުނިއިތުރުކަން ގެނައުމަށް ކިޔަނީ ފަޟްލެވެ. އަދި ލަސް ކުރުމަށް ކިޔަނީ ނަސީއާއެވެ. ރިބާ ހިނގަނީ ދަރަންޏާއި ވިޔަފާރީ، މި ދެ ކަމުގައެވެ. \n\nރިބާގެ ފުރަތަމަ ވައްތަރު ކަމުގައިވާ ދަރަނީގެ ރިބާ (އެބަހީ: ދައިނުގެ ރިބާ) އަކީ، ދަރަންޏަށް ނަގާ މުދަލެއް ނުވަތަ އެހެން ވެސް އެއްޗެއްގެ އިތުރަށް، އަނބުރާ ދޭންޖެހޭ ކޮންމެ މަންފާއެކެވެ. އެ މަންފާއަކީ އެއްޗެއް ނުވަތަ ކަމެއް ކަމުގައި ވިޔަސް މެއެވެ. އެ ގޮތުން ދަރަންޏާ ގުޅިގެން އަންނަ އިންޓަރެސްޓާއި، ފީއާއި، ޖޫރިމަނާ އަދި ފައިދާ މީގެ ތެރެއަށް ވަންނާނެއެވެ. އަދި ދަރަންޏަށް ނަގާ މުދަލެއްގެ އިތުރަށް ކަމެއް ކޮށްދިނުމާއި، ކަމެއްގައި އެހީވުމާއި، މެދުވެރިއަކަށް ވުމާއި، ނުފޫޒު ފޯރުވައި ދިނުމާއި، ޚިދުމަތެއް ދިނުމާ އަދި މަސައްކަތު ބޮންޑެއް އަދާ ކުރަން ޖެހުން ޝަރުތު ކުރުން ވެސް މީގެ ތެރެއަށް ވަންނާނެއެވެ. އެހެނަސް ކުރީބައިގައި ޝަރުތު ކުރުމަކާ ނުލާ، ނަގާ މީހާ އަމިއްލައަށް ދަރަނީގެ އިތުރަށް އަނބުރާ އެއްޗެއް ދޭނަމަ ރިބާއަކަށް ނުވާނެއެވެ. \n \nރިބާގެ ދެވަނަ ވައްތަރު ކަމުގައިވާ ވިޔަފާރީގެ ރިބާ (އެބަހީ: ބުޔޫޢުގެ ރިބާ) ބެހިގެން ދަނީ ތިން ބަޔަކަށެވެ. ފުރަތަމަ ބަޔަކީ، އެއް ސަބަބެއް އެކުލެވޭ އެއް ޖިންސެއްގެ ރިބާ ހިނގައިފާނެ މުދާ ބަދަލު ކުރުމެވެ. މީގައި މިންވަރު ތަފާތުވުން އަދި ފަހުން ދެއްކުންވެސް ޙަރާމްވެގެން ވެއެވެ. ދެވަނަ ބަޔަކީ، އެއް ސަބަބެއް އެކުލެވޭ ތަފާތު ދެ ޖިންސެއްގެ ރިބާ ހިނގައިފާނެ މުދާ ބަދަލު ކުރުމެވެ. މީގައި މިންވަރު ތަފާތުވުން ހުއްދަވެފައި، ފަހުން ދެއްކުން ޙަރާމްވެގެން ވެއެވެ. ތިންވަނަ ބަޔަކީ، ތަފާތު ދެ ސަބަބެއް އެކުލެވޭ ތަފާތު ދެ ޖިންސެއްގެ ރިބާ ހިނގައިފާނެ މުދާ ބަދަލު ކުރުމެވެ. މީގައި މިންވަރު ތަފާތުވުން ވެސް އަދި ފަހުން ދެއްކުންވެސް ހުއްދަވެގެން ވެއެވެ. \n\nވިޔަފާރި މުދަލުގައި ރިބަވީ ސަބަބަށް (އެބަހީ: ޢިއްލަތަށް) ބަލާއިރު، ރިބާ ހިނގައިފާނެ މުދާތަކާއި ރިބާ ނުހިނގާނެ މުދާތަކެއް ވެއެވެ. ރިބާ ހިނގައިފާނެ (އެބަހީ: ރިބަވީ) މުދަލަކަށް ވާން ދެ ސަބަބެއް އޮވެއެވެ. އެއް ސަބަބަކީ ފައިސާއަކަށް ވުމެވެ. އަދި އަނެއް ސަބަބަކީ އާންމުކޮށް ކައިއުޅޭ ތަކެތީގެ ތެރެއިން ގިނަ ދުވަހަށް ރައްކާ ކުރެވޭ ތަކެއްޗަށް ވުމެވެ. މިނޫން މުދަލެއްގައި ރިބާ ނުހިނގާނެއެވެ. އެ ގޮތުން ބިމާއި، ގެއާއި، ކާރާއި، ޖަނަވާރު ފަދަ ތަކެތީގައި ރިބާ ނުހިނގާނެއެވެ. \n\nވިޔަފާރީގައި ފައިސާގެ މިސާލަކީ ރަނާއި ރިއްސެވެ. އަދި މީގެ ތެރޭގައި ޑޮލަރާއި ރުފިޔާ ފަދަ ކޮންމެ ގައުމެއްގެ ޚާއްޞަ ފައިސާވެސް ހިމެނޭނެއެވެ. މިއިން ކޮންމެ އެއްޗަކީ މުދަލުގެ ވަކި ޖިންސެކެވެ. ދެން އާންމުކޮށް ކައިއުޅޭ ތަކެތީގެ ތެރެއިން ގިނަ ދުވަހަށް ރައްކާ ކުރެވޭ ތަކެތީގެ މިސާލަކީ ގޮދަނާއި، ހިމަގޮދަނާއި، ކަދުރާއި، ލޮނެވެ. މީގެ ތެރޭގައި ހަނޑުލާއި، ހަކުރާއި، ފުށްވެސް ހިމެނޭނެއެވެ. މިއިން ކޮންމެ އެއްޗަކީ ވެސް މުދަލުގެ ވަކި ޖިންސެކެވެ. \n\nވިޔަފާރީގައި މުދާ ބަދަލު ކުރާއިރު މިންވަރު ތަފާތުވުމުގެ ތެރޭގައި ބަރުދަނާއި، އަދަދާއި، ފެންވަރު ފަދަ ކަންކަން ތަފާތުވުން ހިމެނެއެވެ. އަދި އެއް ޖިންސެއްގެ ރިބަވީ މުދާ ބަދަލު ކުރާއިރު މިންވަރު ތަފާތުވުމުގެ މިސާލަކި، ސައްވީސް ކެރެޓު ރަނަށް އަށާރަ ރަން، ނުވަތަ އާ ރިއްސަށް ބާ ރިހި، ނުވަތަ އެއް ޑޮލަރަށް ދެ ޑޮލަރު، ނުވަތަ މޭބިސްކަދުރަށް ހިކިމޭބިސްކަދުރު، ނުވަތަ މޮޅު ކަދުރަށް ދެރަ ކަދުރު، ނުވަތަ މިންވަރު އެނގޭ ގޮދަނަށް މިންވަރު ނޭނގޭ ގޮދަން، ނުވަތަ އަޖްވާ ކަދުރަށް ސުއްކަރީ ކަދުރު، ނުވަތަ ދެ އަޖްވާ ކަދުރަށް އެއް އަޖްވާ ކަދުރު ބަދަލު ކުރުމެވެ. މިއީ ފަޟްލުގެ ރިބާއެވެ. \n\nވިޔަފާރީގައި މިންވަރު ނޭނގޭ އެއްޗަކަށް މިންވަރު އެނގޭ އެއްޗެއް ބަދަލު ކުރުމަކީ ޙަރާމް ކަމެކެވެ. އެއީ ޢަރާޔާގެ މުއާމަލާތުގައި މެނުވީއެވެ. އެއީ ފަސްދޮޅަސް ޞާޢަށް ވުރެ ގިނަ ނުވާ ގޮތުގެ މަތިން، ރުކުގައި އަޅާފައިވާ ރޯކަދުރުގެ ލަފާ ކުރެވޭ މިންވަރެއްގެ ބަދަލުގައި، އެއާ އެއްވަރަށް ހިކިކަދުރު ދިނުމެވެ. މިއީ ފައިސާ ނެތިގެން، ކެއުމަށް ޓަކައި ރޯކަދުރަށް ބޭނުން ޖެހިފައިވާތީ މީސްތަކުންނަށް ލުއި ކުރުމުގެ ގޮތުން ދެވުނު ހުއްދައެކެވެ. މިއީ ރިބާގެ މުއާމަލާތް ތަކުގައި ދެވިފައިވާ އިސްތިސްނާއެކެވެ.\n\nވިޔަފާރީގައި މުދާ ބަދަލު ކުރާއިރު ފަހުން ދެއްކުމަކީ، އެއް މުދާ ދިނުމުން، ފަހުން އަނެއް މުދާ ނުވަތަ އެ މުދަލުގެ ބަޔެއްވެސް ބަދަލުގައި ލިބުމެވެ. އެބަހީ ވަގުތުންވަގުތަށް އެކީއެކައްޗަކަށް ބަދަލު ނުވާ ދެ މުދަލެކެވެ. މީގެ މިސާލަކީ ރުފިޔާ ދިނުމުން، އެއް ގަޑިއިރު ފަހުން ޑޮލަރު ބަދަލުގައި ލިބުމެވެ. މިއީ ނަސީއާގެ ރިބާއެވެ. \n\nވިޔަފާރީގައި އެއް ސަބަބެއް އެކުލެވޭ ތަފާތު ދެ ޖިންސެއްގެ ރިބާ ހިނގައިފާނެ މުދާ ބަދަލު ކުރާއިރު، ފަހުން ދެއްކުން ޙަރާމްވެފައި، މިންވަރު ތަފާތުވުން ހުއްދަވެގެން ވެއެވެ. މީގެ މިސާލަކީ ފަނަރަ ރުފިޔާ ދިނުމުން، ލަސްވުމެއް ނެތި އެއް ޑޮލަރު ބަދަލުގައި ލިބުމެވެ. ނުވަތަ އެއް ގްރާމް ރަން ދިނުމުން، ވަގުތުންވަގުތަށް ފަސްދޮޅަސް ގްރާމް ރިހި ބަދަލުގައި ލިބުމެވެ. މިތާ ބަދަލު ކުރާ ތަކެތީގެ މިންވަރު ތަފާތު ކަމުގައި ވިޔަސް، އެއް ވަގުތެއްގައި ދޭތި ބަދަލު ކުރެވޭތީ މުއާމަލާތް ހުއްދަވެގެން ދެއެވެ. \n\nވިޔަފާރީގައި ތަފާތު ދެ ސަބަބެއް އެކުލެވޭ ތަފާތު ދެ ޖިންސެއްގެ ރިބާ ހިނގައިފާނެ މުދާ ބަދަލު ކުރާއިރު، ފަހުން ދެއްކުމާ އަދި ހަމައެހެންމެ މިންވަރު ތަފާތުވުން ވެސް ހުއްދަވާނެއެވެ. އެބަހީ، އާންމުކޮށް ކައިއުޅޭ ތަކެތީގެ ތެރެއިން ގިނަ ދުވަހަށް ރައްކާ ކުރެވޭ އެއްޗަކަށް ފައިސާ ބަދަލު ކުރުމެވެ. މީގެ މިސާލަކީ ފަހުން ދައްކާ ގޮތަށް، އެއް ރުފިޔާއަށް ތިން ކަދުރު ބަދަލު ކުރުމެވެ. މީގައި އަދަދު ތަފާތު ވިޔަސް އަދި ވަގުތުންވަގުތަށް ބަދަލު ނުކުރިޔަސް މައްސަލައެއް ނެތެވެ. \n\n…\n\nރިބާ ޝަރުތު ކުރެވޭ ކޮންމެ އެއްބަސްވުމެއް ހެދުމަކި ޙަރާމް ކަމެކެވެ. އަދި އެއީ ބާޠިލު އެއްޗެކެވެ. ދޭމީހާއަށް ވެސް ނަގާމީހާއަށް ވެސް އެ އަރައިގަނެވެނީ ބޮޑުވެގެންވާ ފާފައަކަށެވެ. އަދި ރިބާ ޗާޖު ކުރެވުމުގެ ކުރިން، މުއްދަތުގެ ތެރޭ ދައްކާ ޚަލާޞް ކުރެވުނަސް އެވާނީ ހަދަން ހުއްދަ ނޫން ޙަރާމް އެއްބަސްވުމަކަށެވެ. ރިބާގެ މުއާމަލާތެއްގައި މީހަކު ބައިވެރިވެއްޖެ ނަމަ، ތައުބާވާން ޖެހޭނެއެވެ. އެއީ އޭގެ ފަހުން ރިބާގެ މުއާމަލާތެއްގައި ބައިވެރި ނުވާނެ ގޮތުގެ މަތިންނެވެ. އަދި ތައުބާގެ އިތުރަށް ކުރަން ޖެހޭނެ ކަންކަން ވެއެވެ. އެ ގޮތުން ރިބާގެ ގޮތުގައި އިތުރަށް އަރާ މިންވަރު ދޭން ޖެހޭ ޖެހުން ބާޠިލު ކުރަން ޖެހޭނެއެވެ. އެ ދެވިގެން ނުވާނެއެވެ. ދެވުނު ނަމަ، އަނބުރާ ރައްދު ކުރެވެން ޖެހޭނެއެވެ. ބާޠިލު ކުރެވެން ނުވަތަ ރައްދު ކުރެވެން ނެތް ނަމަ، ރިބާގެ ގޮތުގައި އަރާ އިތުރު މިންވަރު ޞަދަޤާތުގެ ގޮތުގައި ދީ ހުސް ކުރަން ޖެހޭނެއެވެ. މިއީ ހެޔޮ ޢަމަލެއް ކުރުމުގެ ގޮތުން ދޭ ޞަދަޤާތެއް ނޫނެވެ. އެހެނީ އެހެން ހެދޭނީ ޙަލާލު މުދަލުންނެވެ. އެހެނެއްކަމަކު މިއީ ޙަރާމް ރިބާ ނައްތާލުމަށް ޓަކައި ކުރާ ކަމެކެވެ. އެއްގޮތަކަށް ވެސް މި ކަންތައްތައް ކުރެވެން ނެތް ނަމަ، ތައުބާއިން އެކަނި ފުދޭނެއެވެ. \n\nވިޔަފާރި ކުރުމަށް ބަޔަކަށް ކެޕިޓަލްގެ ގޮތުގައި ފައިސާ ދޭ ނަމަ، އެ ވިޔަފާރީގެ ފައިދާ ނުވަތަ ގެއްލުން ހާސިލު ވުމުގެ ކުރިން، އެ ދިން ކެޕިޓަލް ނުވަތަ ޑިޕޮސިޓްގެ ނިސްބަތަށް ބިނާކޮށް، ކަނޑައެޅިގެން ވަކި މިންވަރެއް ނުވަތަ އިންސައްތައެއް ފައިދާގެ ގޮތުގައި ދޭން ކުރީބައިގައި އެއްބަސްވުމަކީ ޙަރާމް ކަމެކެވެ. މީގެ މިސާލަކީ ބޭންކެއްގައި ފައިސާ ބެހެއްޓުމުން، ވިޔަފާރީގެ ފައިދާ ހާސިލު ވުމުގެ ކުރިން، އެ ބެހެއްޓި ފައިސާގެ މިންވަރަށް ބިނާކޮށް، ކޮންމެ އަހަރަކު ވަކި ނިސްބަތެއް ނުވަތަ އަދަދެއް އެ ބެހެއްޓި މީހާއަށް ދޭން އެއްބަސްވުމެވެ. ބޭންކުން މީހުންނަށް ދެނީ ލޯނެވެ. އަދި މީހުން ބޭންކަށް ދެނީ ޑިޕޮސިޓެވެ. ލޯނެއް ނޫނެވެ. އެ ޑިޕޮސިޓުގައި، ނުކުރެވޭ ވިޔަފާރިއަކުން ލިބިގެންދާނެ ފައިދާއެއް ގެއްލުމެއް ނޭނގެނީސް ވަކި މިންވަރެއް އިންޓަރެސްޓުގެ ގޮތުގައި ކުރީބައިގައި ދޭން އެއްބަސްވާތީ، މި ޖަހާލަތުގެ ސަބަބުން މުއާމަލާތް ރިބާއަށްވެ ޙަރާމް ވެގެންދެއެވެ.\n\nނަމަވެސް ފައިދާއާއި ގެއްލުން ދެކޮޅަށް ހިއްސާވާ ގޮތަށް، އެ ވިޔަފާރީގެ ނަތީޖާ ހަޤީޤަތުގައިވެސް ހާސިލުވުމަށް ފަހު ބަހާ ނަމަ، ލިބޭ ފައިދާއާއި ގެއްލުމުގެ ނިސްބަތަށް ބިނާކޮށް އިންސައްތައެއް ދޭން އެއްބަސްވުމަކީ ހުއްދަ ކަމެކެވެ. މިއީ އިސްލާމީ ބޭންކުތަކުގައި ހަދާ ގޮތެވެ. \n\nއަދި ބަޔަކަށް ފައިސާ ދިނުމުން، އޭގެން ރިބާގެ މުއާމަލާތް ކުރާނެކަން އެނގޭ ނަމަ، އެ ބަޔަކަށް ފައިސާ ދިނުން ޙަރާމް ވާނެއެވެ. މީގެ މިސާލަކީ ބޭންކެއްގައި ފައިސާ ބެހެއްޓުމުން، އެ ފައިސާ އެހެން މީހަކަށް ރިބަވީ ލޯނެއްގެ ގޮތުގައި ދޫކުރުމެވެ. އަދި ރިބަވީ ލޯނެއް ނެގި މީހަކަށް، އެ ލޯނު ދައްކާ ޚަލާޞް ކުރުމަށް މާލީ އެހީ ދިނުމަކީ ވެސް ޙަރާމް ކަމެކެވެ. އެއީ ދެން އެކަން ނުކުރާ ގޮތަށް ތައުބާ ވާނަމަ މެނުވީއެވެ.)","البخاري 2134 واللفظ له، ومسلم 1586"],["#281","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الربا والصرف","ރިބާއާއި މާރު ކުރުމުގެ ބާބު","عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ - رضي الله عنه - أَنَّ رَسُولَ اللَّهِ - ﷺ - قَالَ: (لا تَبِيعُوا الذَّهَبَ بِالذَّهَبِ إلاَّ مِثْلاً بِمِثْلٍ. وَلا تُشِفُّوا بَعْضَهَا عَلَى بَعْضٍ. وَلا تَبِيعُوا الْوَرِقَ بِالْوَرِقِ إلاَّ مِثْلاًّ بِمِثْلٍ. وَلا تُشِفُّوا بَعْضَهَا عَلَى بَعْضٍ. وَلا تَبِيعُوا مِنْهَا غَائِباً بِنَاجِزٍ) .\n<br><br>\nوَفِي لَفْظٍ (إلاَّ يَداً بِيَدٍ) .\n<br><br>\nوَفِي لَفْظٍ (إلاَّ وَزْناً بِوَزْنٍ، مِثْلاً بِمِثْلٍ سَوَاءً بِسَوَاءٍ) .","އަބޫ ސަޢީދުލް ޚުދްރީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ރަނަށް ރަން ނުވިއްކާށެވެ. އެއީ އެއްވަރަކަށް ވާނަމަ މެނުވީއެވެ. އަދި އެއިން މިންވަރެއް، އުނިއިތުރު މިންވަރަކަށް ބަދަލު ނުކުރާށެވެ. އަދި ތިޔަބައިމީހުން ރިއްސަށް ރިހި ނުވިއްކާށެވެ. އެއީ އެއްވަރަކަށް ވާނަމަ މެނުވީއެވެ. އަދި އެއިން މިންވަރެއް، އުނިއިތުރު މިންވަރަކަށް ބަދަލު ނުކުރާށެވެ. އަދި އެއިން އެއްޗެއް، ފަހުން އަގު ދޭ ގޮތަށް ނުވިއްކާށެވެ.“\n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”އަތުން އަތަށް (ވަގުތުން ވަގުތައް) ވިއްކާ ނަމަ މެނުވީއެވެ.“\n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”އެއް ބަރުދަނަކަށް، އެއްވަރަކަށް، އެކައްޗަކަށް ވިއްކާ ނަމަ މެނުވީއެވެ.“","البخاري 2177، ومسلم 1584\n<br><br>\nمسلم 1584\n<br><br>\nمسلم 1584"],["#282","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الربا والصرف","ރިބާއާއި މާރު ކުރުމުގެ ބާބު","عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ - رضي الله عنه - قَالَ: (جَاءَ بِلالٌ إلَى رَسُولِ اللَّهِ - ﷺ - بِتَمْرٍ بَرْنِيِّ فَقَالَ لَهُ النَّبِيُّ - ﷺ -: مِنْ أَيْنَ لَكَ هَذَا؟ قَالَ بِلالٌ: كَانَ عِنْدَنَا تَمْرٌ رَدِيءٌ، فَبِعْتُ مِنْهُ صَاعَيْنِ بِصَاعٍ لِيَطْعَمَ النَّبِيُّ - ﷺ -. فَقَالَ النَّبِيُّ - ﷺ - عِنْدَ ذَلِكَ: أَوَّهْ، أَوَّهْ، عَيْنُ الرِّبَا، عَيْنُ الرِّبَا، لا تَفْعَلْ. وَلَكِنْ إذَا أَرَدْتَ أَنْ تَشْتَرِيَ فَبِعْ التَّمْرَ بِبَيْعٍ آخَرَ. ثُمَّ اشْتَرِ بِهِ) .","އަބޫ ސަޢީދުލް ޚުދްރީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: (މޮޅު) ބަރްނީ ކަދުރު ހިފައިގެން، ނަބިއްޔާ ﷺ ގެ އަރިހަށް ބިލާލު އަތުވިއެވެ. ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ މި ގެނައި ކޮންތާކުންތޯއެވެ؟“ ބިލާލު ދެންނެވިއެވެ: ތިމަންނަ އަތުގައި ދެރަ ކަދުރު އޮތެވެ. ދެން އެ ދެރަ ކަދުރުގެ ދެ ޞާޢަށް، (މި މޮޅު ކަދުރުގެ) އެއް ޞާޢު ތިމަން ވިއްކީމެވެ. އެއީ ނަބިއްޔާ ﷺ ފަރީކުޅުވުމަށެވެ. ދެން ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ހުރި ހިތާމައެއްގެ ބޮޑުކަމާއެވެ. ހުރި ހިތާމައެއްގެ ބޮޑުކަމާއެވެ. ތިއީ ހަމަ ސީދާ ރިބާއެވެ. ތިފަދައިން ކަންތައް ނުކުރާށެވެ. އެހެނެއްކަމަކު ތިބާ ގަންނަން ބޭނުން ނަމަ، ކަދުރު (ފުރަތަމަ ފައިސާއަށް) ވިއްކުމަށް ފަހު ދެން އޭގެން ގަންނާށެވެ.","البخاري 2312، ومسلم 1594"],["#283","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الربا والصرف","ރިބާއާއި މާރު ކުރުމުގެ ބާބު","عَنْ أَبِي الْمِنْهَالِ قَالَ: (سَأَلْتُ الْبَرَاءَ بْنَ عَازِبٍ وَزَيْدَ بْنَ أَرْقَمَ، عَنْ الصَّرْفِ؟ فَكُلُّ وَاحِدٍ يَقُولُ: هَذَا خَيْرٌ مِنِّي. وَكِلاهُمَا يَقُولُ: نَهَى رَسُولُ اللَّهِ - ﷺ - عَنْ بَيْعِ الذَّهَبِ بِالْوَرِقِ دَيْناً) .","އަބޫ މިންހާލު ރިވާ ކުރެއްވިއެވެ: އަލްބަރާއު ބިން ޢާޒިބާއި، ޒައިދު ބިން އަރްޤަމު رَضِيَ اللَّهُ عَنْهُمَا ގާތު މާރު ކުރުމާ ބެހޭ ގޮތުން ތިމަން އެހީމެވެ. އަދި އެއިން ކޮންމެ ބޭކަލެއްވެސް ވިދާޅުވީ ’ތިމަންނައަށް ވުރެ (އަނެކާއާ ސުވާލު ކުރުން) ހެޔޮވެގެން ވެއެވެ.‘ އަދި އެ ދެބޭކަލުން ވެސް ވިދާޅުވިއެވެ: ފަހުން ދައްކާ ގޮތަށް، ރަނަށް ރިހި ބަދަކު ކުރުން ރަސޫލު ﷲ ﷺ މަނާ ކުރެއްވިއެވެ.","البخاري 2180، 2181 واللفظ له، ومسلم 1589"],["#284","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الربا والصرف","ރިބާއާއި މާރު ކުރުމުގެ ބާބު","عَنْ أَبِي بَكْرَةَ - رضي الله عنه - قَالَ: (نَهَى رَسُولُ اللَّهِ - ﷺ - عَنْ الْفِضَّةِ بِالْفِضَّةِ، وَالذَّهَبِ بِالذَّهَبِ، إلاَّ سَوَاءً بِسَوَاءٍ، وَأَمَرَنَا: أَنْ نَشْتَرِيَ الْفِضَّةَ بِالذَّهَبِ، كَيْفَ شِئْنَا. وَنَشْتَرِيَ الذَّهَبَ بِالْفِضَّةِ كَيْفَ شِئْنَا. قَالَ: فَسَأَلَهُ رَجُلٌ فَقَالَ: يَدًا بِيَدٍ؟ فَقَالَ: هَكَذَا سَمِعْتُ) .","އަބޫ ބަކްރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރިއްސަށް ރިހި، އަދި ރަނަށް ރަން ބަދަލު ކުރުން ނަބިއްޔާ ﷺ މަނާ ކުރެއްވިއެވެ. އެއީ އެއްވަރަކަށް ވާނަމަ މެނުވީއެވެ. އަދި ރަނަށް ރިހި ބޭނުން ގޮތަކަށް ގަންނަން ތިމަންމެންނަށް އެންގެވިއެވެ. އަދި ރިއްސަށް ރަން ބޭނުން ގޮތަކަށް ގަންނަން ތިމަންމެންނަށް އެންގެވިއެވެ. މީހަކު، އަބޫ ބަކްރާ ގާތު އެއްސެވިއެވެ: އަތުން އަތަށްތޯއެވެ؟ އަބޫ ބަކްރާ ދެންނެވިއެވެ: ތިމަން އަޑު އެހީ (ނަބިއްޔާ) މި ފަދައިން ޙަދީޘް ކުރައްވަނިކޮށެވެ.","البخاري 2182، ومسلم 1590 واللفظ له"],["#285","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ عَائِشَةَ رضي الله عنها (أَنَّ رَسُولَ اللَّهِ - ﷺ - اشْتَرَى مِنْ يَهُودِيٍّ طَعَاماً، وَرَهَنَهُ دِرْعاً مِنْ حَدِيدٍ) .","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ (ފަހުން ދައްކަވާ ގޮތަށް) ޔަހޫދީއެއްގެ އަތުން ގޮދަން ބައްލަވައިގަތެވެ. އަދި ނަބިއްޔާ ދަގަނޑު ދަހަނާކޮޅެއް ރަހުނެއްގެ ގޮތުގައި އެ މީހާ އަތުގައި ބެހެއްޓެވިއެވެ.","البخاري 2068، ومسلم 1603 واللفظ له"],["#286","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رضي الله عنه - أَنَّ رَسُولَ اللَّهِ - ﷺ - قَالَ: (مَطْلُ الْغَنِيِّ ظُلْمٌ. فَإِذَا أُتْبِعَ أَحَدُكُمْ عَلَى مَلِيءٍ فَلْيَتْبَعْ) .","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”(ދަރަނި އަދާ ކުރުމަށް ކުޅަދާނަވެގެންވާ) ފުދުންތެރި މީހަކު (އެ އަދާނުކޮށް) ލަސް ކުރުމަކީ އަނިޔާއެކެވެ. އަދި ތިޔަބައިމީހުން ކުރެ މީހަކު ދަރަންޏެއް ދޫކޮށްފަައިވާ ވާނަމަ، އެ ދަރަނި އަދާ ކުރަން ޖެހޭ ފަރާތަށް އެހެން ފުދުންތެރި މީހަކު ދަރާފައިވާ ނަމަ، އެ (ތިން ވަނަ ފުދުންތެރި) މީހާގެ އަތުން އެ ދަރަނި ނަގާ ގޮތަށް ތިބާ ގާތު އެދިއްޖެއްޔާ އެކަމަށް ރުހޭށެވެ.“","البخاري 2287، ومسلم 1564"],["#287","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رضي الله عنه - قَالَ: قَالَ رَسُولُ اللَّهِ - ﷺ - أَوْ قَالَ: سَمِعْتُ النَّبِيَّ - ﷺ - يَقُولُ -: (مَنْ أَدْرَكَ مَالَهُ بِعَيْنِهِ عِنْدَ رَجُلٍ - أَوْ إنْسَانٍ - قَدْ أَفْلَسَ فَهُوَ أَحَقُّ بِهِ مِنْ غَيْرِهِ) .","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”(މީހަކު، އެހެން މީހަކަށް މުދަލެއް ބަހައްޓަން ދީފައި، ނުވަތަ އެހެން މީހަކަށް މުދަލެއް ވިއްކާފައި އޭގެ އަގު ލިބުމުގެ ކުރިން، އެ ނެގި މީހަކީ ބަނގުރޫޓު ވެފައިވާ މީހެއްކަން އެނގިއްޖެއްޔާ) އެ ބަނގުރޫޓު ވެފައިވާ މީހާގެ އަތުގައި، އޭނާގެ މުދާ (ބަދަލެއް ނައިސް) އިންގޮތަށް ފެނިއްޖެ ނަމަ، ފަހެ އެހެން ބަޔަކަށް ވުރެ އެ މުދާ ލިބުން ޙައްޤުވަނީ އެ (ދިން) މީހާއަށެވެެ.“","البخاري 2402، ومسلم 1559"],["#288","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رضي الله عنهما قَالَ (جَعَلَ - وَفِي لَفْظٍ: (قَضَى النَّبِيُّ - ﷺ - بِالشُّفْعَةِ فِي كُلِّ مَا لَمْ يُقْسَمْ. فَإِذَا وَقَعَتِ الْحُدُودُ، وَصُرِّفَتِ الطُّرُقُ: فَلا شُفْعَةَ) .","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ކޮންމެ (ބައެއްގެ މެދުގައި) ގޯތިގެދޮރެއް ބަހައި ނިންމުމުގެ ކުރިން، އޭގެން ހިއްސާވެރިއަކު އޭނާގެ ބައި ވިއްކާ ނަމަ، އެހެން ފަރާތަކަށް ވުރެ ކުރިން ދެން ތިބި ހިއްސާވެރިންނަށް އެ ހިއްސާ ގަތުމުގެ ޙައްޤު، ރަސޫލު ﷲ ﷺ ދެއްވިއެވެ. ނަމަވެސް އެ ގޯތިގެދޮރު ބަހައި، ހައްދުތައް ވަކިކުރެވި، މަގުތައް ކަނޑައެޅުމަށް ފަހު ދެން އެ ޙައްޤެއް ނޯންނާނެއެވެ.","البخاري 2213، ومسلم 1647"],["#289","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رضي الله عنهما قَالَ: (قَدْ أَصَابَ عُمَرُ أَرْضاً بِخَيْبَرَ. فَأَتَى النَّبِيَّ - ﷺ - يَسْتَأْمِرُهُ فِيهَا. فَقَالَ: يَا رَسُولَ اللَّهِ، إنِّي أَصَبْتُ أَرْضاً بِخَيْبَرَ، لَمْ أُصِبْ مَالاً قَطُّ هُوَ أَنْفَسُ عِنْدِي مِنْهُ، فَمَا تَأْمُرُنِي بِهِ؟ فَقَالَ: إنْ شِئْتَ حَبَسْتَ أَصْلَهَا، وَتَصَدَّقْتَ بِهَا. قَالَ: فَتَصَدَّقَ بِهَا. غَيْرَ أَنَّهُ لا يُبَاعُ أَصْلُهَا، وَلا يُوهَبُ، وَلا يُورَثُ. قَالَ: فَتَصَدَّقَ عُمَرُ فِي الْفُقَرَاءِ، وَفِي الْقُرْبَى، وَفِي الرِّقَابِ، وَفِي سَبِيلِ اللَّهِ، وَابْنِ السَّبِيلِ، وَالضَّيْفِ. لا جُنَاحَ عَلَى مَنْ وَلِيَهَا: أَنْ يَأْكُلَ مِنْهَا بِالْمَعْرُوفِ، أَوْ يُطْعِمَ صَدِيقاً، غَيْرَ مُتَمَوِّلٍ فِيهِ) .\n<br><br>\nوَفِي لَفْظٍ: (غَيْرَ مُتَأَثِّلٍ) .","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ޚައިބަރުގައި ޢުމަރުގެފާނަށް ބިމެއް ލިބިވަޑައިގަތެވެ. ދެން އެއާމެދު ކަން ކުރަންވީ ގޮތް އައްސަވަން ރަސޫލު ﷲ ﷺ ގެ އަރިހަށް ވަޑައިގެން ދެންނެވިއެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން ޚައިބަރުން ބިމެއް ތިމަންނައަށް ލިބިއްޖެއެވެ. އެއަށްވުރެ ލޮބުވެތިކަން ބޮޑު މުދަލެއް ތިމަންނައަށް މީގެ ކުރިން ލިބިފައެއް ނުވެއެވެ. ފަހެ އެއާމެދު އިބަ ނަބިއްޔާ ތިމަންނައަށް އަމުރު ކުރައްވަނީ ކިހިނަކުންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: \"\"ތިބާ ބޭނުން ނަމަ، އެ ބިމުގެ އަސްލު ތިބާ އަތުގައި ބަހައްޓަވާފައި، އޭގެން (ލިބޭ ތަކެތީން) ޞަދަޤާތް ކުރާށެވެ.~~ (އެބަހީ: ވަޤްފު ކުރާށެވެ.) ދެން ޢުމަރުގެފާނު އެ ބިން (ވަޤްފު ކުރެއްވިއެވެ.) އެއީ އެ ބިމުގެ އަސްލު ނުވިއްކާ، ވާރުތަ ނުވާ އަދި ހިބައަށް ނުދެވޭ ގޮތަށެވެ. އެ ގޮތުން ފަޤީރުންނާއި، ގާތް ތިމާގެ މީހުންނާއި، އަޅުން މިނިވަން ކުރުމަށާއި، ﷲ ގެ މަގުގައި ޖިހާދު ކުރުމަށާއި، ޙާލުގައި ޖެހިފައިވާ ދަތުރުވެރިންނަށާއި، މެހެމާނުންނަށް އެ ތަން ވަޤްފު ކުރެއްވިއެވެ. އަދި އެ ގޯތި ބެލުމަށް ހަވާލު ކުރެވުނު ބަޔަކު އޭގެން ހެޔޮ ގޮތުގައި ކެއުމަށާއި، ރަހުމަތްތެރިއަކަށް ކާންދިނުމުގައި މައްސަލައެއް ނެތެވެ. އެއީ އޭގެން އޭނާ މުދާ ގިނަ ކުރުމުގެ ބޭނުމުގައި ނުނަގާ ގޮތުގެ މަތިންނެވެ. \n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: އެއީ އޭގެން އޭނާއަށް މުދާ މިލްކު ނުވާ ގޮތުގެ މަތިންނެވެ.","البخاري 2737، ومسلم 1632 واللفظ له\n<br><br>\nمسلم 1632"],["#290","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ عُمَرَ- رضي الله عنه - قَالَ: (حَمَلْتُ عَلَى فَرَسٍ فِي سَبِيلِ اللَّهِ، فَأَضَاعَهُ الَّذِي كَانَ عِنْدَهُ، فَأَرَدْتُ أَنْ أَشْتَرِيَهُ، فَظَنَنْتُ أَنَّهُ يَبِيعُهُ بِرُخْصٍ. فَسَأَلْتُ النَّبِيَّ - ﷺ -؟ فَقَالَ: لا تَشْتَرِهِ. وَلا تَعُدْ فِي صَدَقَتِكَ، وَإِنْ أَعْطَاكَهُ بِدِرْهَمٍ. فَإِنَّ الْعَائِدَ فِي هِبَتِهِ كَالْعَائِدِ فِي قَيْئِهِ) .\n","ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ޖިހާދު ކުރުމަށް ތިމަން އަހެއް ޞަދަޤާތް ކުރީމެވެ. ފަހެ އެ އަސް ގެންގުޅުނު މީހާ އެއަށް ފަރުވާ ކުޑަކުރިއެވެ. ވީމާ އެ އަސް އަނބުރާ ގަންނަން ތިމަން ބޭނުން ވިއެވެ. އަދި އެ މީހާ އަގު ހެޔޮކޮށް އެ ވިއްކާނެ ކަމަށް ތިމަން ހީކުރީމެވެ. ދެން (އެކަމާ މެދު) ނަބިއްޔާ ﷺ އަރިހު ތިމަން ސުވާލު ކުރީމެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ ނުގަންނާށެވެ. އަދި ތިބާ ދިން ޞަދަޤާތް އަނބުރާ ނުނަގާށެވެ. އެއީ އެންމެ ދިރުހަމަކަށް އޭނާ އޭތި ވިއްކި ކަމުގައި ވިޔަސް މެއެވެ. އެހެނީ ހަމަކަށަވަރުން މީހަކު އެއްޗެއް ހިބަކޮށް ދީފައި އަނބުރާ ނެގުމަކީ، މީހަކު ހޮޑުލާފައި އެ ހޮޑު އަނބުރާ ނަގާ ކެއުން ފަދަ ކަމެކެވެ.“","البخاري 1490 واللفظ له، ومسلم 1620"],["#291","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު"," وعن ابن عباس رضي الله عنهما: أن رسول الله صلي الله عليه وسلم قال: \"العائد في هبته، كالعائد فيى قيئه\" .\n<br><br>\nوفي لفظ: \"فإن الذى يعود في صدقته كالكلب [يقئ ثم] يعود في قيئه\" .","އިބްނު ޢައްބާސް رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”މީހަކު އެއްޗެއް ހިބަކޮށް ދީފައި އަނބުރާ ނެގުމަކީ، މީހަކު ހޮޑުލާފައި އެ ހޮޑު އަނބުރާ ނަގާ ކެއުން ފަދަ ކަމެކެވެ.“ \n<br><br>\n”އަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: މީހަކު ޞަދަޤާތެއް ދީފައި އަނބުރާ ނެގުމަކީ، ކުއްތާއެއް ހޮޑުލާފައި އެ ހޮޑު އަނބުރާ ކެއުން ފަދަ ކަމެކެވެ.“","البخاري 2621، ومسلم 1622\n<br><br>\nالبخاري 2623، ومسلم 1620 واللفظ له"],["#292","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنِ النُّعْمَانِ بْنِ بَشِيرٍ رضي الله عنهما قَالَ: (تَصَدَّقَ عَلَيَّ أَبِي بِبَعْضِ مَالِهِ. فَقَالَتْ أُمِّي عَمْرَةُ بِنْتُ رَوَاحَةَ: لا أَرْضَى حَتَّى تُشْهِدَ رَسُولَ اللَّهِ - ﷺ -. فَانْطَلَقَ أَبِي إلَى رَسُولِ اللَّهِ - ﷺ - لِيُشْهِدَ عَلَى صَدَقَتِي فَقَالَ لَهُ رَسُولُ اللَّهِ - ﷺ -: أَفَعَلْتَ هَذَا بِوَلَدِكَ كُلِّهِمْ؟ قَالَ: لا. قَالَ: اتَّقُوا اللَّهَ وَاعْدِلُوا فِي أَوْلادِكُمْ، فَرَجَعَ أَبِي، فَرَدَّ تِلْكَ الصَّدَقَةَ) .\n<br><br>\nوَفِي لَفْظٍ: (فَلا تُشْهِدْنِي إذاً. فَإِنِّي لا أَشْهَدُ عَلَى جَوْرٍ)\n<br><br>\nوَفِي لَفْظٍ: (فَأَشْهِدْ عَلَى هَذَا غَيْرِي) .","ނުޢުމާން ބިން ބަޝީރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ތިމަންނަގެ ބައްޕަ އެކަލޭގެފާނުގެ މުދަލުގެ ބަޔަކުން، ތިމަންނައަށް ޞަދަޤާތް ކުރެއްވިއެވެ. ފަހެ ތިމަންނަގެ މަންމަ ޢަމްރާ ބިންތި ރަވާޙާ ދެންނެވިއެވެ: (ތިކަމުގެ މައްޗަށް) ރަސޫލު ﷲ ﷺ ހެކި ނުކުރައްވަނީސް ތިމަން ކަމަނާ ނުރުހޭނަމެވެ. ދެން ތިމަންނައަށް ދެއްވި ޞަދަޤާތުގެ މައްޗަށް ހެކި ކުރެއްވުމަށް ޓަކައި، ނަބިއްޔާގެ އަރިހަށް ތިމަންނަގެ ބައްޕަ ނުކުންނެވިއެވެ. ފަހެ ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާގެ ހުރިހާ ދަރިންނަށް ތިފަދައިން ކަންތައް ކުރެއްވިންތޯއެވެ؟“ ބައްޕަ ދެންނެވިއެވެ: ނޫނެކެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲ އަށް ބިރުވެތިވާށެވެ. އަދި ތިބާގެ ދަރިންގެ މެދުގައި އަދުލުވެރިވާށެވެ.“ ދެން ތިމަންނަގެ ބައްޕަ އެނބުރި ގެއަށް ދުރުވެ، އެ ޞަދަޤާތް އަނބުރާ ނެންގެވިއެވެ. \n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”އެހެން ކަމުގައި ވަނީ ނަމަ، ތިމަން ނަބިއްޔާ ހެކި ނުކުރާށެވެ. އެހެނީ ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާ ބޭއިންސާފަކަށް ހެކިވެރިއެއް ނުވާނަމެވެ.“ \n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”ތިކަމުގެ މައްޗަށް ތިމަން ނަބިއްޔާ ނޫން މީހަކު ހެކި ކުރާށެވެ.“","البخاري 2587، ومسلم 1623 واللفظ له\n<br><br>\nمسلم 1623\n<br><br>\nمسلم 1623"],["#293","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رضي الله عنهما (أَنَّ النَّبِيَّ - ﷺ - عَامَلَ أَهْلَ خَيْبَرَ بِشَطْرِ مَا يَخْرُجُ مِنْهَا مِنْ تَمْرٍ أَوْ زَرْعٍ) .","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ޚައިބަރު (ފަތަހަ ކުރެވި އެ ބިންތައް މުސްލިމުންނަށް ލިބުމަށް ފަހު، އެ ތަނުގެ ޔަހޫދީ) އަހުލުވެރިން، އެބައިމީހުން ދަނޑުވެރިކަން ކޮށްގެން ނެރޭ ކަދުރު ނުވަތަ ގޮވާމުގެ ދެބައިކުޅަ އެއްބައި (ޚަރާޖުގެ ގޮތުގައި) ދޭ ގޮތަށް، ނަބިއްޔާ ﷺ އެބައިމީހުންނަށް އެ ދަނޑުބިންތަކުގައި މަސައްކަތްކޮށް އުޅުމުގެ ހުއްދަ ދެއްވިއެވެ.","البخاري 2329، ومسلم 1551"],["#294","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ رَافِعِ بْنِ خَدِيجٍ قَالَ: (كُنَّا أَكْثَرَ الأنْصَارِ حَقْلاً. وَكُنَّا نُكْرِي الأرْضَ، عَلَى أَنَّ لَنَا هَذِهِ، وَلَهُمْ هَذِهِ فَرُبَّمَا أَخْرَجَتْ هَذِهِ، وَلَمْ تُخْرِجْ هَذِهِ فَنَهَانَا عَنْ ذَلِكَ. فَأَمَّا بِالْوَرِقِ: فَلَمْ يَنْهَنَا) .","ރާފިޢު ބިން ޚަދީޖު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ތިމަންމެންވީ އަންޞާރުންގެ ތެރެއިން އެންމެ ދަނޑުބިން ގިނަ މީހުންނަށެވެ. އަދި ތިމަންމެން ދަނޑުބިން ކުއްޔަށް ދޫކޮށް އުޅުނީމެވެ. އެއީ މި ގޮތުގެ މަތިންނެވެ: ބިމުގެ އެއް ބައިން ހެދޭ ތަކެތި ވާނީ ތިމަންމެންނަށެވެ. އަދި އަނެއް ބައިން ހެދޭ ތަކެތި ވާނީ އެ ކުއްޔަށް ދޫކުރި މީހުންނަށެވެ. ދެން ފަހަރެއްގައި އެއް ބައިން ތަކެތި ހެދިދާނެއެވެ. އަދި އަނެއް ބައިން އެއްޗެއް ނެހެދިދާނެއެވެ. ވީމާ އެކަން ކުރުން ނަބިއްޔާ ތިމަންމެންނަށް މަނާ ކުރެއްވިއެވެ. އެހެނެއްކަމަކު (ކުލީގެ އަގު) ރިހިން ދެއްކުން މަނަލެއް ނުކުރައްވައެވެ.","البخاري 2327، ومسلم 1547 واللفظ له"],["#295","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","وَلِمُسْلِمٍ عَنْ حَنْظَلَةَ بْنِ قَيْسٍ قَالَ: (سَأَلْتُ رَافِعَ بْنَ خَدِيجٍ عَنْ كِرَاءِ الأَرْضِ بِالذَّهَبِ وَالْوَرِقِ؟ فَقَالَ: لا بَأْسَ بِهِ. إنَّمَا كَانَ النَّاسُ يُؤَاجِرُونَ عَلَى عَهْدِ رَسُولِ اللَّهِ - ﷺ - بِمَا عَلَى الْمَاذِيَانَاتِ، وَأَقْبَالِ الْجَدَاوِلِ، وَأَشْيَاءَ مِنْ الزَّرْعِ فَيَهْلِكُ هَذَا، وَيَسْلَمُ هَذَا وَلَمْ يَكُنْ لِلنَّاسِ كِرَاءٌ إلاَّ هَذَا. وَلِذَلِكَ زَجَرَ عَنْهُ. فَأَمَّا شَيْءٌ مَعْلُومٌ مَضْمُونٌ: فَلا بَأْسَ بِهِ) .","އަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ޙަންޠަލާ ބިން ޤައިސް ރިވާ ކުރެއްވިއެވެ: ރަނާއި ރިހި ދޭގޮތަށް ބިން ކުއްޔަށް ދޫކުރުމާމެދު ރާފިޢު ބިން ޚަދީޖު ގާތު ތިމަން އެހީމެވެ. ރާފިޢު ވިދާޅުވިއެވެ: އެ ކަމުގައި މައްސަލައެއް ނެތެވެ. ރަސޫލު ﷲ ﷺ ގެ ޒަމާނުގައި މީސްތަކުން (ބިން) ކުއްޔަށް ދޫކޮށް އުޅުނީ، ހަމައެކަނި ބޮޑެތި ކޯރުތައް ކައިރީގައި ހެދޭ ތަކެއްޗާއި، ކުދި ކޯރުތައް ފެށޭ ހިސާބުގައި ހެދޭ ތަކެއްޗާއި، ދަނޑުބިންތަކުގެ ވަކިވަކި ބައިބައިތަކުގައި ހެދޭ ތަކެއްޗެވެ. ދެން ފަހަރެއްގައި އެއް ބައިން ހެދޭ ތަކެތި ހަލާކުވެދާނެއެވެ. އަދި އަނެއް ބައިން ހެދޭ ތަކެތި ސަލާމަތުން ހުރެދާނެއެވެ. އޭރު މި ނޫން ގޮތަކަށް މީސްތަކުން ބިން ކުއްޔަށް ދޫކޮށެއް ނޫޅެއެވެ. އެހެން ކަމުން އެ ގޮތަށް ކަންކުރުން ނަބިއްޔާ މަނާ ކުރެއްވިއެވެ. އެހެނެއްކަމަކު (ކުލީގެ އަގަކީ) ކަނޑައެޅިގެން އެނގޭ ޔަގީން އެއްޗެއް ނަމަ، މައްސަލައެއް ނެތެވެ.","مسلم 1547"],["#296","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رضي الله عنهما قَالَ: (قَضَى رَسُولُ اللَّهِ - ﷺ - بِالْعُمْرَى لِمَنْ وُهِبَتْ لَهُ) .\n<br><br>\nوَفِي لَفْظٍ: (مَنْ أُعْمِرَ عُمْرَى لَهُ وَلِعَقِبِهِ. فَإِنَّهَا لِلَّذِي أُعْطِيَهَا. لا تَرْجِعُ إلَى الَّذِي أَعْطَاهَا ; لأَنَّهُ أَعْطَى عَطَاءً وَقَعَتْ فِيهِ الْمَوَارِيثُ) . وَقَالَ جَابِرٌ: (إنَّمَا الْعُمْرَى الَّتِي أَجَازَهَا رَسُولُ اللَّهِ - ﷺ -، أَنْ يَقُولَ: هِيَ لَكَ وَلِعَقِبِكَ، فَأَمَّا إذَا قَالَ: هِيَ لَك مَا عِشْتَ: فَإِنَّهَا تَرْجِعُ إلَى صَاحِبِهَا) .\n<br><br>\nوَفِي لَفْظٍ لِمُسْلِمٍ: (أَمْسِكُوا عَلَيْكُمْ أَمْوَالَكُمْ وَلا تُفْسِدُوهَا، فَإِنَّهُ مَنْ أَعْمَرَ عُمْرَى فَهِيَ لِلَّذِي أُعْمِرَهَا: حَيّاً، وَمَيِّتاً، وَلِعَقِبِهِ) .","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: އުމުރު ދުވަހަށް (އެބަހީ: މަރުވަންދެން) އެއްޗެއް ހިބަކޮށް ދީފި ނަމަ، އެ އެއްޗެއް (އެއްކޮށް) ލިބިގެން ވަނީ އެ ދެވުނު މީހާއަށް ކަމަށް ނަބިއްޔާ ﷺ ނިޔާ ކުރެއްވިއެވެ. (އެބަހީ: އެ ދިން މީހާއަށް އަނބުރައެއް ނުލިބޭނެއެވެ.)\n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: އުމުރު ދުވަހަށް އެއްޗެއް ހިބަކޮށް ލިބިއްޖެ މީހަކަށް، ފަހެ އެ އެއްޗެއް ވަނީ އޭނާއަށާއި އޭނާގެ ދަރިފަސްކޮޅަށެވެ. އެހެނީ ހަމަކަށަވަރުން އެ އެއްޗެއް ވަނީ އެ ލިބުނު ފަރާތަކަށެވެ. އެ ދިން ފަރާތަކަށް އަނބުރައެއް ނުލިބޭނެއެވެ. ސަބަބަކީ އެ އެއްޗަކީ ވާރުތައަށް ވަންނާނެ އެއްޗެކެވެ.\n<br><br>\nއަދި ޖާބިރު ވިދާޅުވިއެވެ: ހިބައިގެ ގޮތުގައި އުމުރު ދުވަހަށް ދެވޭ ދިނުން ނަބިއްޔާ ﷺ ހުއްދަ ކުރެއްވި ހަމައެކަނި ގޮތަކީ، މިއީ ތިބާއަށާއި ތިބާގެ ދަރިފަސްކޮޅަށް ދެވޭ އެއްޗެކޭ، މިފަދައިން ބުނުމެވެ. (އެބަހީ: މިލްކު ވާނެއެވެ.) އެހެނެއްކަމަކު، މިއީ ތިބާ ދިރިހުރިހާ ދުވަހެއް ވަންދެން (ތިބާ ގެންގުޅެން) ދޭ އެއްޗެކޭ، މިފަދައިން ބުނެފިނަމަ، ފަހެ ހަމަކަށަވަރުން އޭތި ދިން މީހަކަށް އަނބުރާ ލިބޭނެއެވެ. (އެހެނީ: އެ ދެނީ މިލްކުވާ ގޮތަކަށެއް ނޫނެވެ.)\n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ތިޔަބައިމީހުންގެ މުދާތަކުގައި ހިފާށެވެ. އަދި އެ ތަކެތި ނަގައިނުލާށެވެ. އެހެނީ ހަމަކަށަވަރުން އުމުރު ދުވަހަށް ހިބައިގެ ގޮތުގައި މީހަކަށް އެއްޗެއް ދެވިއްޖެ ނަމަ، ފަހެ އެއީ ދިރިހުރުމައި މަރުވުމުގައިވެސް އެ ލިބުމު މީހާއާއި، އޭނާގެ ދަރިފަސްކޮޅަށް ލިބިގެންވާ އެއްޗެކެވެ.","البخاري 2625 واللفظ له، ومسلم 1625\n<br><br>\nمسلم 1625\n<br><br>\nمسلم 1625"],["#297","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ رضي الله عنه أَنَّ رَسُولَ اللَّهِ - ﷺ - قَالَ: (لا يَمْنَعَنَّ جَارٌ جَارَهُ: أَنْ يَغْرِزَ خَشَبَةً فِي جِدَارِهِ ثُمَّ يَقُولُ أَبُو هُرَيْرَةَ: مَا لِي أَرَاكُمْ عَنْهَا مُعْرِضِينَ؟ وَاَللَّهِ لأَرْمِيَنَّ بِها بَيْنَ أَكْتَافِكُمْ) .","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އެއް އަވައްޓެރިޔަކު، އޭނާގެ ފާރުގައި އަނެއް އަވައްޓެރިޔާ ލަކުޑިބުރި ޖެހުން މަނާ ނުކުރާށެވެ.“ (އެބަހީ: ބޭނުމެއް ޖެހިގެން، މާ ބޮޑު ގެއްލުމެއް ނުވާ ގޮތަށެވެ.) އެއަށްފަހު އަބޫ ހުރައިރާ ވިދާޅުވިއެވެ: ތިޔަބައިމީހުން އެކަމަށް ދެކޮޅުވެރިވާތަން ތިމަންނައަށް ފެންނަނީ ކީއްވެހެއްޔެވެ؟ ﷲ ގަންދީ ބުނަމެވެ. ހަމަކަށަވަރުން (ތިޔަބައިމީހުން ނުރުހުނަސް) ތިޔަބައިމީހުންގެ މެދުގައި މި (ޙުކުމް އިއްވާނަމެވެ.)","البخاري 2463، ومسلم 1609"],["#298","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ عَائِشَةَ رضي الله عنها: أَنَّ رَسُولَ اللَّهِ - ﷺ - قَالَ: (مَنْ ظَلَمَ قِيدَ شِبْرٍ مِنْ الأَرْضِ: طُوِّقَهُ مِنْ سَبْعِ أَرَضِينَ) .","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ބިމެއްގެ ކައިވަތެއްގެ މިންވަރު ވެސް ނުޙައްޤުން މީހަކު އަތުލައިފި ނަމަ، (ޤިޔާމަތް ދުވަހު) އެ މީހެއްގެ ކަރުގައި ހަތް ބިން އެޅުވޭނެއެވެ.“","البخاري 2453، ومسلم 1612"],["#299","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب اللقطة","ގެއްލިފައި ވަނިކޮށް ފެންނަ ތަކެތީގެ ބާބު","عَنْ زَيْدِ بْنِ خَالِدٍ الْجُهَنِيِّ - رضي الله عنه - قَالَ: (سُئِلَ رَسُولُ اللَّهِ - ﷺ - عَنْ لُقَطَةِ الذَّهَبِ، أَوْ الْوَرِقِ؟ فَقَالَ: اعْرِفْ وِكَاءَهَا وَعِفَاصَهَا، ثُمَّ عَرِّفْهَا سَنَةً فَإِنْ لَمْ تُعْرَفْ فَاسْتَنْفِقْهَا وَلْتَكُنْ وَدِيعَةً عِنْدَكَ فَإِنْ جَاءَ طَالِبُهَا يَوْماً مِنْ الدَّهْرِ: فَأَدِّهَا إلَيْهِ، وَسَأَلَهُ عَنْ ضَالَّةِ الإِبِلِ؟ فَقَالَ: مَا لَك وَلَهَا؟ دَعْهَا فَإِنَّ مَعَهَا حِذَاءَهَا وَسِقَاءَهَا، تَرِدُ الْمَاءَ وَتَأْكُلُ الشَّجَرَ، حَتَّى يَجِدَهَا رَبُّهَا. وَسَأَلَهُ عَنْ الشَّاةِ؟ فَقَالَ: خُذْهَا فَإِنَّمَا هِيَ لَكَ، أَوْ لأَخِيك، أَوْ لِلذِّئْبِ) .","ޒައިދު ބިން ޚާލިދު އަލްޖުހަނީ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ގެއްލިފައި ވަނިކޮށް ފެންނަ ރަނާއި ރިއްސާމެދު ރަސޫލު ﷲ ﷺ އަރިހުގައި ސުވާލު ކުރެއްވުނެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ (ފައިސާ) އަޅާފައިވާ އެއްޗަކާއި، އެ ބަނދު ކުރަން އައްސާފައިވާ ވާކޮޅު (ރަނގަޅަށް) ދެނެގަންނާށެވެ. އެއަށްފަހު އެއް އަހަރުދުވަސް ވަންދެން (އޭގެ ވެރިޔާ ހޯދަން) އިއުލާން ކުރާށެވެ. ދެން އޭތި ބަލައިގަންނަ މީހަކު ނުވެއްޖެ ނަމަ، އެ ހޭދަ ކުރާށެވެ. އެއީ ތިބާގެ އަތުގައި ބަހައްޓާފައިވާ (އަނބުރާ ދޭން ޖެހޭ އެއްޗެއް) ގޮތުގައެވެ.“ ދެން ދުވަހަކުވެސް އޭގެ ވެރިޔާ އޭތި ހޯދަން ތިބާ ގާތަށް އައިސްފި ނަމަ، (އެ މިންވަރު) އަނބުރާ ހަވާލު ކުރާށެވެ. ދެން ގެއްލިފައިވަނިކޮށް (ފެންނަ) ޖަމަލާމެދު ސުވާލު ކުރެއްވުނެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެއާއި ތިބާއާ ކޮންކަމެއްހެއްޔެވެ؟ އޭތި ދޫކޮށްލާށެވެ. އެހެނީ ހަމަކަށަވަރުން އެއާއެކު އޭގެ ފަޔާއި ފެން ކަންވާރު ވެއެވެ. އޭގެ ވެރިޔާ އޭތި ފެންނަން ދެން އޭތި ފެން ބޮއެފައި ގަސްތަކުން ކައިހަދާނެއެވެ.“ ދެން (ގެއްލިފައި ވަނިކޮށް ފެންނަ) ކަންބަޅިއާމެދު ސުވާލު ކުރެއްވުނެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭތި ނަގާށެވެ. އެހެނީ ހަމަކަށަވަރުން އެ ވަނީ ތިބާއަށެވެ. ނޫނީ ތިބާގެ އަޚަކަށެވެ. ނޫނީ ވަލުކުއްތާއަށެވެ.","البخاري 91، ومسلم 1722 واللفظ له"],["#300","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الوصايا","ވަޞިއްޔަތުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رضي الله عنهما أَنَّ رَسُولَ اللَّهِ - ﷺ - قَالَ: (مَا حَقُّ امْرِئٍ مُسْلِمٍ، لَهُ شَيْءٌ يُوصِي فِيهِ، يَبِيتُ لَيْلَتَيْنِ إلاَّ وَوَصِيَّتُهُ مَكْتُوبَةٌ عِنْدَهُ) .\n<br><br>\nزَادَ مُسْلِمٌ: قَالَ ابْنُ عُمَرَ: (مَا مَرَّتْ عَلَيَّ لَيْلَةٌ مُنْذُ سَمِعْتُ رَسُولَ اللَّهِ - ﷺ - يَقُولُ ذَلِكَ، إلاَّ وَعِنْدِي وَصِيَّتِي) .","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ވަޞިއްޔަތެއް ކުރާނެ އެއްވެސް އެއްޗެއް އޮތް މުސްލިމު މީހަކު، އެ ވަޞިއްޔަތް އޭނާގެ އަތުގައި ލިޔެފައިވާ ޙާލު މެނުވީ އެންމެ ރެއެއް ނުވަތަ ދެރޭވެސް އޭނާ ވޭތުކުރުން ޙައްޤުވެގެން ނުވެއެވެ.“\n<br><br>\nއަދި މުސްލިމުގެ ރިވާޔަތުގައި ވެއެވެ: އިބްނު ޢުމަރު ވިދާޅުވިއެވެ: ފަހެ ﷲ ގަންދީ ބުނަމެވެ. ރަސޫލު ﷲ ﷺ އެފަދައިން ޙަދީޘް ކުރެއްވި އަޑު އެހުމަށް ފަހު، ތިމަންނަގެ ވަޞިއްޔަތް ތިމަންނަގެ އަތުގައި ނެތީސް އެންމެ ރެއެއް ވޭތުވެ ނުދެއެވެ.","البخاري 2738، ومسلم 1627\n<br><br>\nمسلم 1627"],["#301","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الوصايا","ވަޞިއްޔަތުގެ ބާބު","عَنْ سَعْدِ بْنِ أَبِي وَقَّاصٍ - رضي الله عنه - قَالَ: (جَاءَني رَسُولُ اللَّهِ - ﷺ - يَعُودُنِي عَامَ حَجَّةِ الْوَدَاعِ مِنْ وَجَعٍ اشْتَدَّ بِي فَقُلْتُ: يَا رَسُولَ اللَّهِ، قَدْ بَلَغَ بِي مِنْ الْوَجَعِ مَا تَرَى، وَأَنَا ذُو مَالٍ، وَلا يَرِثُنِي إلاَّ ابْنَةٌ أَفَأَتَصَدَّقُ بِثُلُثَيْ مَالِي؟ قَالَ: لا، قُلْتُ: فَالشَّطْرُ يَا رَسُولَ اللَّهِ؟ قَالَ: لا، قُلْت: فَالثُّلُثُ قَالَ: الثُّلُثُ، وَالثُّلُثُ كَثِيرٌ، إِنَّكَ أَنْ تَذَرَ ورَثَتَكَ أَغنياءَ خيرٌ منْ أَنْ تَذَرَهمْ عالَةً يَتَكَفَّفونَ النَّاسَ، وإنَّكَ لَنْ تُنْفِقَ نَفَقَةً تَبْتَغِي بِهَا وَجْهَ اللَّهِ إلاَّ أُجِرْتَ بِهَا، حَتَّى مَا تَجْعَلُ فِي فِي امْرَأَتِكَ. قَالَ: قُلْتُ: يَا رَسُولَ اللَّهِ أُخَلَّفُ بَعْدَ أَصْحَابِي؟ قَالَ: إنَّكَ لَنْ تُخَلَّفَ فَتَعْمَلَ عَمَلاً تَبْتَغِي بِهِ وَجْهَ اللَّهِ إلاَّ ازْدَدْت بِهِ دَرَجَةً وَرِفْعَةً، وَلَعَلَّكَ أَنْ تُخَلَّفَ حَتَّى يَنْتَفِعَ بِكَ أَقْوَامٌ، وَيُضَرُّ بِكَ آخَرُونَ. اللَّهُمَّ أَمْضِ لأَصْحَابِي هِجْرَتَهُمْ، وَلا تَرُدَّهُمْ عَلَى أَعْقَابِهِمْ، لَكِنِ الْبَائِسُ سَعْدُ بْنُ خَوْلَةَ يَرْثِي لَهُ رَسُولُ اللَّهِ - ﷺ - أَنْ مَاتَ بِمَكَّةَ) ","ސަޢުދު ބިން އަބީ ވައްޤާޞް رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ޙައްޖަތުލް ވަދާޢުގެ އަހަރުގައި ތިމަންނަ ވަރަށް ބޮޑަށް އާލާސްކަމުގައި އިނުމުން، ރަސޫލު ﷲ ﷺ ތިމަންނައަށް ޒިޔާރާތް ކުރެއްވިއެވެ. ތިމަން ދެންނެވީމެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. އިބަ ނަބިއްޔާއަށް ފެނިވަޑައިގަންނަވާ ފަދައިން މި އާލާސްކަމުގައި (ތިމަންނަގެ ޙާލު ވަރަށް ދެރަވެއްޖެއެވެ.) އަދި ތިމަންނައީ މުދާވެރިއަކަށްވެފައި، އަންހެން ދަރިއަކު މެނުވީ ތިމަންނަގެ ފަރާތުން ވާރުތަ ނުކުރާނެއެވެ. ވީމާ ތިމަންނަގެ މުދަލުގެ ތިންބައި ކުޅަ އެއްބައިން ޞަދަޤާތް ކުރަންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ނޫނެކެވެ.“ ތިމަން ދެންނެވީމެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. ދެބައި ކުޅަ އެއްބައި ކުރަންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ނޫނެކެވެ.“ ތިމަން ދެންނެވީމެވެ: ތިންބައި ކުޅަ އެއްބައި ކުރަންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިންބައި ކުޅަ އެއްބައި (ކުރެވިދާނެއެވެ.) އަދި ތިންބައި ކުޅަ އެއްބަޔަކީ ވެސް ގިނައެވެ. ހަމަކަށަވަރުން ތިބާގެ ވާރުތަވެރިން ފަޤީރުކަންމަތީ އެހެން މީހުންގެ ކިބައިން ސަލާންޖަހަން ދޫކޮށްލުމަށް ވުރެ، ތަނަވަސްކަންމަތީ ދޫކުރުން މާ ހެޔޮވެގެންވެއެވެ. އަދި ހަމަކަށަވަރުން ﷲގެ ވަޖްހުފުޅު އަދައިގެން ތިބާ ހޭދަ ކުރާ އެއްވެސް އެއްޗަކަށް ހެޔޮ ޖަޒާ ލިބިގެން މެނުވީ ނުދާނެއެވެ. އެއީ ތިބާގެ އަނބިމީހާގެ އަނގައަށް ލަވާ ކާއެތިކޮޅަށް ދާންދެން ވެސްމެއެވެ.“ ތިމަން ދެންނެވީމެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ތިމަންނައާއެކު އުޅުނު މީހުން (މަދީނާއަށް އެނބުރި ދާއިރު، ތިމަންނަގެ އާލާސްކަމުގެ ސަބަބުން، އެ ބިމަކުން ހިޖުރަ މި ކުރި މައްކާގައި) ފަހަތުގައި ތިމަން މަޑުކުރަން ޖެހެނީތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ ފަހަތުގައި މަޑުކުރަން (ޖެހުނުކަމުގައި ވިޔަސް) ހެޔޮ ޢަމަލެއްކޮށްފި ކަމުގައި ވާނަމަ، އެކަމުގެ ސަބަބުން ތިބާގެ ދަރަޖައާއި މަތިވެރިކަން އިތުރުވެގެން މެނުވީ ނުދާނެއެވެ. އަދި ފަހަރެއްގައި ތިބާގެ ސަބަބުން ބަޔަކަށް ފައިދާ ކުރެވިއްޖައުމަށް ދާންދެން، އަދި އަނެއް ބަޔަކަށް ގެއްލުން ލިބިއްޖައުމަށް ދާންދެން، ތިބާ ދިރިދަންމަވަފާނެއެވެ. ޔާ ﷲ އެވެ. ތިމަން ނަބިއްޔާގެ ޞަޙާބީންގެ ހިޖުރަ ފުރިހަމަ ކުރައްވާށިއެވެ. (އެބަހީ: އެކަމުގައި ސާބިތު ކުރައްވާށިއެވެ.) އަދި އެބައިމީހުން (ކުރި ހިޖުރަ ދޫކޮށް) އެނބުރި ރައްދު ނުކުރައްވާށިއެވެ. އެހެނަސް ސަޢުދު ބިން ޚައުލާއަށް ވީގޮތް ހާދަ ދެރައެވެ.“ އެކަލޭގެފާނު މައްކާގައި އަވަހާރަވީތީ ރަސޫލު ﷲ ﷺ ސަޢުދު ބިން ޚައުލާއަށް ޓަކައި ދެރަފުޅުވިއެވެ","البخاري 1295 واللفظ له، ومسلم 1628"],["#302","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الوصايا","ވަޞިއްޔަތުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رضي الله عنهما قَالَ: (لَوْ أَنَّ النَّاسَ غَضُّوا مِنْ الثُّلُثِ إلَى الرُّبْعِ؟ فَإِنَّ رَسُولَ اللَّهِ - ﷺ - قَالَ: الثُّلُثُ، وَالثُّلُثُ كَثِيرٌ) .","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: (މީސްތަކުން އެބައިމީހުންގެ މުދާ ޞަދަޤާތް ކުރުމަށް ވަޞިއްޔަތް ކުރާއިރު، އެ މުދަލުގެ) ތިންބައި ކުޅަ އެއްބައިން ހަތަރުބައި ކުޅަ އެއްބަޔަށް މަދު ކުރާނަމަ ކިހާ ރަނގަޅުހެއްޔެވެ؟ އެހެނީ ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިންބައި ކުޅަ އެއްބައި (ޞަދަޤާތުގައި ދިނުމަށް ވަޞިއްޔަތް ކުރެވިދާނެއެވެ.) އަދި ތިންބައި ކުޅަ އެއްބައިވެސް ގިނައެވެ.“","البخاري 2743، ومسلم 1629 واللفظ له"],["#303","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الفرائض","ތަރިކަ މުދަލުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رضي الله عنهما عَنْ النَّبِيِّ - ﷺ - قَالَ: (أَلْحِقُوا الْفَرَائِضَ بِأَهْلِهَا، فَمَا بَقِيَ فَهُوَ لأَوْلَى رَجُلٍ ذَكَرٍ) .\n<br><br>\nوَفِي رِوَايَةٍ: (اقْسِمُوا الْمَالَ بَيْنَ أَهْلِ الْفَرَائِضِ عَلَى كِتَابِ اللَّهِ فَمَا تَرَكَتْ: فَلأَوْلَى رَجُلٍ ذَكَرٍ) .","އިބްނު ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތަރިކަ މުދަލުން ފަރުޟު ކަނޑައެޅިފައިވާ ބައިތައް، އޭގެ ޙައްޤުވެރިންނަށް ބަހާށެވެ. އެއަށްފަހު ބާކީ ހުރި އެއްޗެއް ލިބުން ވަނީ، ޢާއިލީ ގޮތުން (މައްޔިތާއާ) އެންމެ ގާތް ފިރިހެނަކަށެވެ.“\n<br><br>\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”ﷲ ގެ ފޮތުގައިވާ ގޮތުގެ މަތިން، ތަރިކަ މުދަލުން ފަރުޟު ކަނޑައެޅިފައިވާ ބައިތައް އޭގެ ޙައްޤުވެރިންގެ މެދުގައި ބަހާށެވެ. ދެން އޭގެން ބާކީ ހުރި އެއްޗެއް ލިބުން ވަނީ، ޢާއިލީ ގޮތުން (މައްޔިތާއާ) އެންމެ ގާތް ފިރިހެނަކަށެވެ.“\n","البخاري 6732، ومسلم 1615\n<br><br>\nمسلم 1615"],["#304","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الفرائض","ތަރިކަ މުދަލުގެ ބާބު","عَنْ أُسَامَةَ بْنِ زَيْدٍ رضي الله عنهما قَالَ: (قُلْتُ يَا رَسُولَ اللَّهِ، أَتَنْزِلُ غَداً فِي دَارِكَ بِمَكَّةَ؟ قَالَ: وَهَلْ تَرَكَ لَنَا عَقِيلٌ مِنْ رِبَاعٍ؟\n<br><br>\nثُمَّ قَالَ: لا يَرِثُ الْكَافِرُ الْمُسْلِمَ وَلا الْمُسْلِمُ الْكَافِرَ) .","އުސާމާ ބިން ޒައިދު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: (މައްކާއަށް ވަޑައިގަންނަވަމުން) ރަސޫލު ﷲ ﷺ ގެ އަރިހުގައި ތިމަން ދެންނެވީމެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. މައްކާގައިވާ އިބަ ނަބިއްޔާ (ކުރިން އުޅުއްވި އަބޫ ޠާލިބުގެ) ގެކޮޅުގައި މާދަމާ މަޑު ކުރައްވާނަމުތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ޢުޤައިލު (ބިން އަބީ ޠާލިބު) ތިމަންމެންނަށް އެއްވެސް ގެއެއް ގޯއްޗެއް ދޫކުރިހެއްޔެވެ؟“\n<br><br>\nއެއަށްފަހު ޙަދީޘް ކުރެއްވިއެވެ: ”މުސްލިމަކު ކާފަރެއްގެ ކިބައިން ވާރުތަ ނުކުރާނެއެވެ. އަދި ކާފަރަކު މުސްލިމެއްގެ ކިބައިން ވާރުތަ ނުކުރާނެއެވެ.“ (ނޯޓު: އަބޫ ޠާލިބު ނިޔާވީ ކާފަރެއްގެ ގޮތުގައެވެ. އަދި ނަބިއްޔާ އެކަލޭގެފާނު އުފަންވެވަޑައިގަތް ގެކޮޅު ދޫކޮށްލައްވާފައި މަދީނާއަށް ހިޖުރަ ކުރެއްވިއެވެ. އަބޫ ޠާލިބުގެ ހަތަރު ފިރިހެން ދަރިން ތިއްބެވެ. އޭގެ ތެރެއިން ޖަޢުފަރާއި ޢަލީ އިސްލާމްވުމުން، އެ ދެ ބޭކަލުންނަކަށް އަބޫ ޠާލިބުގެ ފަރާތުން ވާރުތައެއް ނުވެއެވެ. އެއީ ދީން ތަފާތުވުމުގެ ސަބަބުން ވާރުތަ ނުވާތީއެވެ. ދެން ވާރުތަވާން އޮތީ ކާފަރުކަންމަތީ ތިބި ދެ ދަރިން ކަމުގައިވާ ޠާލިބާއި ޢުޤައިލަށެވެ. އެކަމަކު ޠާލިބު ނިޔާވިއެވެ. ދެން މުޅި ހުރިހައި ގެއަކާއި ގޯއްޗެއް ޢުޤައިލަށް ވާރުތަވުމުން، އެ ތަކެތި ވިއްކާލިއެވެ. ފަހުން ޢުޤައިލު އިސްލާމްވިއެވެ.)","البخاري 1588، 3058، ومسلم 1351 واللفظ له\n<br><br>\nالبخاري 6764 واللفظ له، ومسلم 1614"],["#305","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الفرائض","ތަރިކަ މުދަލުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رضي الله عنهما (أَنَّ النَّبِيَّ - ﷺ - نَهَى عَنْ بَيْعِ الْوَلاءِ وَعَنْ هِبَتِهِ) .","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ވަލާވެރިކަން ގަނެވިއްކުމާއި ހިބަކޮށްދިނުން ނަބިއްޔާ މަނާ ކުރެއްވިއެވެ. (ނޯޓު: ވަލާއަކީ މީހަކު އަޅެއް މިނިވަން ކުރުމުން، އެ މިނިވަން ކުރާ މީހައަށް އެ އަޅެއްގެ ފަރާތުން ވާރުތަވުމުގެ ޙައްޤު ލިބިގެންދިއުމެވެ.)","البخاري 2535، ومسلم 1506"],["#306","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الفرائض","ތަރިކަ މުދަލުގެ ބާބު","عَنْ عَائِشَةَ رضي الله عنها قَالَتْ: (كَانَتْ فِي بَرِيرَةَ ثَلاثُ سُنَنٍ: خُيِّرَتْ عَلَى زَوْجِهَا حِينَ عَتَقَتْ، وَأُهْدِيَ لَهَا لَحْمٌ، فَدَخَلَ عَلَيَّ رَسُولُ اللَّهِ - ﷺ - وَالْبُرْمَةُ عَلَى النَّارِ، فَدَعَا بِطَعَامٍ فَأُتِيَ بِخُبْزٍ وَأُدْمٍ مِنْ أُدْمِ الْبَيْتِ فَقَالَ: أَلَمْ أَرَ الْبُرْمَةَ عَلَى النَّارِ فِيهَا لَحْمٌ؟ قَالُوا: بَلَى، يَا رَسُولَ اللَّهِ. ذَلِكَ لَحْمٌ تُصُدِّقَ بِهِ عَلَى بَرِيرَةَ فَكَرِهْنَا أَنْ نُطْعِمَكَ مِنْهُ فَقَالَ: هُوَ عَلَيْهَا صَدَقَةٌ، وَهُوَ مِنْهَا لَنَا هَدِيَّةٌ، وَقَالَ النَّبِيُّ - ﷺ - فِيهَا: إنَّمَا الْوَلاءُ لِمَنْ أَعْتَقَ) .","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: (އެކަމަނާ މިނިވަން ކުރެއްވި އަންހެން އަޅެއް ކަމުގައިވާ) ބަރީރާގެ (ސަބަބުން) ތިން ސުންނަތެއް (އެބަހީ: ޝަރުޢީ ޙުކުމެއް) އައިސްފައިވެއެވެ. (ފުރަތަމަ ކަމަކީ) ބަރީރާ މިނިވަންވުމުން، އޭނާގެ ފިރިމީހާއާއެކު ކައިވެނީގައި ދެމިތިބުމަށް ނުވަތަ ވަކިވަށް އިޚްތިޔާރު (ނަބިއްޔާ) ދެއްވިއެވެ. (ނޯޓު: ފިރިމީހާއަކީ މިނިވަން ނުވާ އަޅެކެވެ.) (ބަރީރާއާ ގުޅޭގޮތުން އައިސްފައިވާ ދެވަނަ ކަމަކީ) ބަރީރާއަށް މަސްކޮޅެއް ހަދީޔާގެ ގޮތުގައި ލިބުނެވެ. ރަސޫލު ﷲ ﷺ ތިމަން ކަމަނާގެ ގެކޮޅަށް ވަދެވަޑައިގަތް ހިނދު، މަސްތެލި ހުރީ ކައްކަން އުނދާފައެވެ. ދެން ނަބިއްޔާއަށް ކާން ގެންނަވަން އެންގެވިއެވެ. ފަހެ ރޮށިކޮޅަކާއި ގޭގައި (އެއާއެކު އާންމުކޮށް ކާން ގެންގުޅޭ ދިޔަފަދަ އެއްޗެއް) ނަބިއްޔާގެ އަރިހަށް ގެނެވުނެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތެއްޔެއްގައި މަސް އުނދާފައިވަނިކޮށް ތިމަން ނަބިއްޔާ ފެނިވަޑައި ނުގަންނަވަމުތޯއެވެ؟“ ނަބިއްޔާއަށް ދެންނެވުނެވެ: އޭ ﷲ ގެ ރަސޫލާއެވެ. އާދޭހެވެ. އެހެނަސް އެއީ ބަރީރާއަށް ޞަދަޤާތުގެ ގޮތުގައި ދެވުނު މަސްކޮޅެކެވެ. ވީމާ އޭގެން އިބަ ނަބިއްޔާއަށް ފަރީއްކުޅުވާން އަރުވަން ތިމަންމެން ނުރުހުނީއެވެ. (ނޯޓު: ޞަދަޤާތުގެ ގޮތުގައި ދެވޭ ތަކެތި ނަބިއްޔާ ފަރީކުޅުވުން ހުއްދަވެގެން ނުވެއެވެ.) ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެއީ ބަރީރާއަށް ވާ ޞަދަޤާތެކެވެ. އަދި އެއީ އޭނާގެ ފަރާތުން ތިމަންމެންނަށް ވާ ހަދިޔާއެކެވެ.“ (ބަރީރާއާ ގުޅޭގޮތުން އައިސްފައިވާ ތިންވަނަ ކަމަކީ) ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ވަލާވެރިކަން ލިބުން ވަނީ ހަމައެކަނި މިނިވަން ކުރާ މީހާއަށެވެ.“","البخاري 5097، ومسلم 1504 واللفظ له"]]
