const umdah_dataSet=[["#001","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","عَنْ عُمَرَ بْنِ الْخَطَّابِ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: سَمِعْتُ رَسُولَ اللَّهِ ﷺ يَقُولُ: «إنَّمَا الأَعْمَالُ بِالنِّيَّاتِ - وَفِي رِوَايَةٍ: بِالنِّيَّةِ - وَإِنَّمَا لِكُلِّ امْرِئٍ مَا نَوَى، فَمَنْ كَانَتْ هِجْرَتُهُ إلَى اللَّهِ وَرَسُولِهِ، فَهِجْرَتُهُ إلَى اللَّهِ وَرَسُولِهِ، وَمَنْ كَانَتْ هِجْرَتُهُ إلَى دُنْيَا يُصِيبُهَا أَوْ امْرَأَةٍ يَتَزَوَّجُهَا، فَهِجْرَتُهُ إلَى مَا هَاجَرَ إلَيْهِ»","ޢުމަރު ބިން އަލްޚައްޠާބު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ”ނިޔަތްތަކުގެ މައްޗަށް މެނުވީ އަމަލުތައް ބިނާ ވެގެން ނުވެއެވެ. އަދި ކޮންމެ މީހަކަށް ވެސް ވަނީ، ހަމައެކަނި އެ މީހަކު ނިޔަތް ގަތް ކަމެކެވެ. ވީމާ، މީހަކު ހިޖުރަ ކުރީ ﷲ އާއި އެ އިލާހުގެ ރަސޫލާއަށްޓަކައި ނަމަ، ފަހެ އޭނާގެ ހިޖުރަ ވަނީ ﷲ އާއި އެ އިލާހުގެ ރަސޫލާއަށެވެ. އަދި މީހަކު ހިޖުރަ ކުރީ ދުނިޔެ ހޯދުމަށް ނުވަތަ އަންހެނަކާ އިނުމަށް ކަމުގައި ވަނީ ނަމަ، ފަހެ އެ މީހެއްގެ ހިޖުރަ ވަނީ، އޭނާ އެ ހިޖުރަ ކުރި ކަމަކަށެވެ.“","[البخاري 6689، ومسلم 1907]"],["#002","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «لا يَقْبَلُ اللَّهُ صَلاةَ أَحَدِكُمْ إذَا أَحْدَثَ حَتَّى يَتَوَضَّأَ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ ތެރެއިން މީހަކު ހަދަސްވެރިވެއްޖެ ނަމަ، ވުޟޫކޮށްފުމަށް ދާންދެން ﷲ އޭނާގެ ނަމާދު ގަބޫލު ނުކުރައްވާނެއެވެ.“","[البخاري 6954 واللفظ له، ومسلم 225]"],["#003","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عَمْرِو بْنِ الْعَاصِ وَأَبِي هُرَيْرَةَ وَعَائِشَةَ رَضِيَ اللَّهُ عَنْهُم قَالُوا: قَالَ رَسُولُ اللَّهِ ﷺ: «وَيْلٌ لِلأَعْقَابِ مِنَ النَّارِ»","ޢަބްދު ﷲ ބިން ޢަމްރު ބިން އަލްޢާޞް، އަބޫ ހުރައިރާ އަދި ޢާއިޝާ رَضِيَ اللَّهُ عَنْهُمْ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ނަރަކައިން ފުންނާބުތަކަށް ހަލާކު ހުއްޓެވެ.“ (އެބަހީ: ވުޟޫ ކުރާއިރު ރަނގަޅަށް ނުދޮވުމުގެ ސަބަބުން، ފަރުވާކުޑަ ކުރާ މީހުންނަށް އަޒާބު ލިބޭނެކަމެވެ.)","[البخاري 60، 165، ومسلم 241، 242، 240]"],["#004","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ رَسُولَ اللَّهِ ﷺ قال: «إذَا تَوَضَّأَ أَحَدُكُمْ فَلْيَجْعَلْ فِي أَنْفِهِ مَاءً، ثُمَّ لِيَنْتَثِرْ، وَمَنْ اسْتَجْمَرَ فَلْيُوتِرْ، وَإِذَا اسْتَيْقَظَ أَحَدُكُمْ مِنْ نَوْمِهِ فَلْيَغْسِلْ يَدَيْهِ قَبْلَ أَنْ يُدْخِلَهُمَا فِي الإِنَاءِ ثَلاثاً، فَإِنَّ أَحَدَكُمْ لا يَدْرِي أَيْنَ بَاتَتْ يَدُهُ» \n\nوَفِي لَفْظٍ لِمُسْلِمٍ: «فَلْيَسْتَنْشِقْ بِمِنْخَرَيْهِ مِنَ الْمَاءِ» \n\nوَفِي لَفْظٍ: «مَنْ تَوَضَّأَ فَلْيَسْتَنْشِقْ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ ތެރެއިން މީހަކު ވުޟޫ ކުރާ ނަމަ، އޭނާގެ ނޭފަތަށް ފެންލާށެވެ. އެއަށްފަހު ނޭފަތުން ފެން ބޭރު ކުރާށެވެ. އަދި މީހަކު އިސްތިޖްމާރު ކުރާ ނަމަ، އޮނަހިރި އަދަދަކުން ފޮހޭށެވެ. (އެބަހީ: ފާހަނަކުރުމަށް ފަހު، ތިނަކުން ފަށައިގެން އެއަށްވުރެ މަތީގެ އޮނަހިރި އަދަދަކުން ހިލަ ނުވަތަ ޓިޝޫ ފަދަ އެއްޗެއް ބޭނުންކޮށްގެން ނަޖިސް ފިލުވައިލުމެވެ.) އަދި ތިޔަބައިމީހުންގެ ތެރެއިން މީހަކު ނިދިން ހޭލައިފި ނަމަ، ފެން ކަންވާރަކަށް އޭނާ އަތްލުމުގެ ކުރިން، ތިން ފަހަރު ދެ އަތް ދޮންނާށެވެ. އެހެނީ ހަމަކަށަވަރުން ތިޔަބައިމީހުން ކުރެ މީހަކު ނިދާފަ އޮންނަ އިރު އޭނާގެ އަތް އޮތް ތަނެއް ނޭނގޭނެއެވެ.“ \n\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ”ނޭފަތުގެ ދެ ހިނދުރިއަށް ފެން ވައްދާށެވެ.“ \n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”ވުޟޫ ކުރާ މީހަކު ނޭފަތަށް (ފެންލައި ދެމުމަށް ފަހު) ފެން ބޭރު ކުރާށެވެ.“","[البخاري 162، ومسلم 237، 278\n\nمسلم 237\n\nالبخاري 161، ومسلم 237]"],["#005","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «لا يَبُولَنَّ أَحَدُكُمْ فِي الْمَاءِ الدَّائِمِ الَّذِي لا يَجْرِي، ثُمَّ يَغْتَسِلُ مِنْهُ» \n\nوَلِمُسْلِمٍ: «لا يَغْتَسِلُ أَحَدُكُمْ فِي الْمَاءِ الدَّائِمِ وَهُوَ جُنُبٌ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ ތެރެއިން މީހަކު، އެއް ތަނެއްގައި ގަރާރުވެފައި ހުންނަ ނުހިނގާ ފެންގަނޑަކަށް ކުޑަކަމުނުދާށެވެ. (އަދި އެއަށް ކުޑަކަމުދިއުމަށް ފަހު) ދެން އެއަށް (އެރިގެން) ފެން ނުވަރާށެވެ.“ \n\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ”ތިޔަބައިމީހުންގެ ތެރެއިން މީހަކު ޖުނުބުވެރިކަން މަތީ ހުރެ، އެއް ތަނެއްގައި ގަރާރުވެފައި ހުންނަ ނުހިނގާ ފެންގަނޑަކަށް (އެރިގެން) ފެންނުވަރާށެވެ.“","[البخاري 239 واللفظ له، ومسلم 282\n\nمسلم 283]"],["#006","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ -، أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «إذَا شَرِبَ الْكَلْبُ فِي إنَاءِ أَحَدِكُمْ فَلْيَغْسِلْهُ سَبْعاً» \n\nوَلِمُسْلِمٍ: «أُولاهُنَّ بِالتُّرَابِ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ ތެރެއިން މީހެއްގެ ކަންވާރަކުން ކުއްތާއެއް ފެން ބޮއެފި ނަމަ، ފަހެ ހަތް ފަހަރު އެ ކަންވާރު ދޮންނާށެވެ.“ \n\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ”އޭގެ ފުރަތަމަ ފަހަރު (ދޮންނާނީ) ވެލިންނެވެ.“","[البخاري 172 واللفظ له، ومسلم 279\n\nمسلم 279]"],["#007","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","وَلَهُ فِي حَدِيثِ عَبْدِ اللَّهِ بْنِ مُغَفَّلٍ: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «إذَا وَلَغَ الْكَلْبُ فِي الإِناءِ فَاغْسِلُوهُ سَبْعاً وَعَفِّرُوهُ الثَّامِنَةَ بِالتُّرَابِ»","އަދި މުސްލިމުގެ ރިވާޔަތެއްގައި، ޢަބްދު ﷲ ބިން މުޣައްފަލު رَضِيَ اللَّهُ عَنْهُގެ ޙަދީޘްގައި ވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ކަންވާރެއްގައި ކުއްތާއެއް ދޫލައިފި ނަމަ، ހަތް ފަހަރު އެ ކަންވާރު ދޮންނާށެވެ. އަދި އަށްވަނަ ފަހަރު ވެލިން އުނގުޅާށެވެ.“","[مسلم 280]"],["#008","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","عَنْ حُمْرَانَ مَوْلَى عُثْمَانَ بْنِ عَفَّانَ رَضِيَ اللَّهُ عَنْهُمَا: «أَنَّهُ رَأَى عُثْمَانَ دَعَا بِوَضُوءٍ، فَأَفْرَغَ عَلَى يَدَيْهِ مِنْ إنَائِهِ، فَغَسَلَهُمَا ثَلاثَ مَرَّاتٍ، ثُمَّ أَدْخَلَ يَمِينَهُ فِي الْوَضُوءِ، ثُمَّ تَمَضْمَضَ وَاسْتَنْشَقَ وَاسْتَنْثَرَ، ثُمَّ غَسَلَ وَجْهَهُ ثَلاثاً، وَيَدَيْهِ إلَى الْمِرْفَقَيْنِ ثَلاثًا، ثُمَّ مَسَحَ بِرَأْسِهِ، ثُمَّ غَسَلَ كِلْتَا رِجْلَيْهِ ثَلاثًا، ثُمَّ قَالَ: رَأَيْتُ النَّبِيَّ ﷺ يَتَوَضَّأُ نَحْوَ وُضُوئِي هَذَا، وَقَالَ: «مَنْ تَوَضَّأَ نَحْوَ وُضُوئِي هَذَا، ثُمَّ صَلَّى رَكْعَتَيْنِ، لا يُحَدِّثُ فِيهِمَا نَفْسَهُ غُفِرَ لَهُ مَا تَقَدَّمَ مِنْ ذَنْبِهِ»","ޢުޘްމާނު ބިން ޢައްފާނު މިނިވަން ކުރެއްވި އަޅެއް ކަމުގައިވާ ޙުމްރާނު رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ޢުޘްމާނު (ވުޟޫ ކުރެއްވުމަށް ޓަކައި) ފެން ގެނައުމަށް އެންގެވިތަން ޙުމްރާނަށް ފެނުނެވެ. ދެން އެކަލޭގެފާނުގެ ކަންވާރުން އަތަށް ފެން އަޅުއްވައި، ތިން ފަހަރު ދެ އަތް ދޮންނެވިއެވެ. އެއަށްފަހު ފެން ކަންވާރުގެ ތެރެއަށް ކަނާތް ލައްވާ، އަނގައަށް ފެން ލައްވައި ކޯ ދޮންނެވިއެވެ. އަދި ނޭފަތަށް ފެން ލައްވައި ބޭރު ކުރެއްވިއެވެ. ދެން ތިން ފަހަރު މޫނު ދޮންނެވިއެވެ. އަދި އުޅަނބޮށްޓާ ހަމައަށް ތިން ފަހަރު ދެ އަތް ދޮންނެވިއެވެ. އަދި ބޮލުގައި ފެން ފުއްސެވުމަށް ފަހު، ތިން ފަހަރު ދެ ފައި ދޮންނެވިއެވެ. އެއަށްފަހު ވިދާޅުވިއެވެ: ތިމަންނަ މި ވުޟޫ ކުރެއްވި ގޮތަށް ނަބިއްޔާ ﷺ ވުޟޫ ކުރައްވަނިކޮށް ތިމަން ދުށީމެވެ. އަދި ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާ ވުޟޫ ކުރެއްވި ގޮތަށް މީހަކު ވުޟޫ ކުރުމަށް ފަހު، ދެ ރަކުއަތުގެ ނަމާދެއްކޮށް، އެ ދެ ރަކުއަތުގައި އޭނާގެ އަމިއްލަ ނަފުސާ ވާހަކަ ނުދައްކައިފި ނަމަ (އެބަހީ: ވަސްވާސްތަކަށް ސަމާލުކަން ނުދީ، ދުނިޔެވީ ކަންކަމާ މެދު ނުވިސްނާ، އިޚްލާސްތެރިކަމާއެކު ނަމާދުކޮށްފި ނަމަ) ފަހެ އެ މީހަކަށް ކުރެވިފައިވާ ފާފަތައް، ﷲ އޭނާއަށްޓަކައި ފުއްސަވައި ދެއްވާނެއެވެ.“","[البخاري 159، ومسلم 226]"],["#009","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","عَنْ عَمْرِو بْنِ يَحْيَى الْمَازِنِيِّ عَنْ أَبِيهِ قَالَ: «شَهِدْتُ عَمْرَو بْنَ أَبِي حَسَنٍ سَأَلَ عَبْدَ اللَّهِ بْنَ زَيْدٍ عَنْ وُضُوءِ النَّبِيِّ ﷺ؟ فَدَعَا بِتَوْرٍ مِنْ مَاءٍ، فَتَوَضَّأَ لَهُمْ وُضُوءَ رَسُولِ اللَّهِ ﷺ فَأَكْفَأَ عَلَى يَدَيْهِ مِنْ التَّوْرِ، فَغَسَلَ يَدَيْهِ ثَلاثاً، ثُمَّ أَدْخَلَ يَدَهُ فِي التَّوْرِ، فَمَضْمَضَ وَاسْتَنْشَقَ وَاسْتَنْثَرَ ثَلاثاً بِثَلاثِ غَرْفَاتٍ، ثُمَّ أَدْخَلَ يَدَهُ فَغَسَلَ وَجْهَهُ ثَلاثاً، ثُمَّ أَدْخَلَ يَدَهُ فِي التَّوْرِ، فَغَسَلَهُمَا مَرَّتَيْنِ إلَى الْمِرْفَقَيْنِ ثُمَّ أَدْخَلَ يَدَهُ فِي التَّوْرِ، فَمَسَحَ رَأْسَهُ، فَأَقْبَلَ بِهِمَا وَأَدْبَرَ مَرَّةً وَاحِدَةً، ثُمَّ غَسَلَ رِجْلَيْهِ» \n\nوَفِي رِوَايَةٍ: «بَدَأَ بِمُقَدَّمِ رَأْسِهِ، حَتَّى ذَهَبَ بِهِمَا إلَى قَفَاهُ، ثُمَّ رَدَّهُمَا حَتَّى رَجَعَ إلَى الْمَكَانِ الَّذِي بَدَأَ مِنْهُ» \n\nوَفِي رِوَايَةٍ «أَتَانَا رَسُولُ اللَّهِ ﷺ فَأَخْرَجْنَا لَهُ مَاءً فِي تَوْرٍ مِنْ صُفْرٍ»","ޢަމްރު ބިން ޔަޙްޔާ އަލްމާޒިނީ، އެކަލޭގެފާނުގެ ބައްޕާފުޅު އަރިހުން ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺ ވުޟޫ ކުރައްވާ ގޮތާމެދު ޢަބްދު ﷲ ބިން ޒައިދުގެ އަރިހުގައި ޢަމްރު ބިން އަބުލް ޙަސަން ސުވާލު ކުރަނިކޮށް ތިމަން ދުށީމެވެ. ފަހެ ފެން ކަންވާރެއް ގެނައުމަށް ޢަބްދު ﷲ އެންގެވިއެވެ. އަދި އެބައިމީހުންނަށް (ދެއްކުމަށް ޓަކައި) ނަބިއްޔާ ﷺ ވުޟޫ ކުރެއްވި ގޮތަށް އެކަލޭގެފާނު ވުޟޫ ކުރެއްވިއެވެ. އެ ގޮތުން، ކަންވާރު އަރިކޮށްލައްވައިގެން އަތަށް ފެން އެޅުއްވުމަށް ފަހު، ތިން ފަހަރު ދެ އަތް ދޮންނެވިއެވެ. ދެން ކަންވާރުގެ ތެރެއަށް އަތް ލައްވަވާ، ތިން އަތްގޮށުން އަނގައަށް ފެން އަޅުއްވައި ތިން ފަހަރު ކޯ ދޮންނެވިއެވެ. އަދި ނޭފަތަށް ފެން ލައްވައި ބޭރު ކުރެއްވިއެވެ. ދެން އަނެއްކާ ކަންވާރުގެ ތެރެއަށް އަތް ލައްވަވާ ތިން ފަހަރު މޫނު ދޮންނެވިއެވެ. އެއަށްފަހު ކަންވާރުގެ ތެރެއަށް އަތް ލައްވަވާ އުޅަނބޮށްޓާ ހަމައަށް ދެ ފަހަރު އަތް ދޮންނެވިއެވެ. ދެން ކަންވާރުގެ ތެރެއަށް ދެ އަތް ލައްވަވާ، ބޮލުގެ ކުރިމަތިން ފަހަތަށާއި، ފަހަތުން ކުރިމައްޗަށް އެއްފަހަރު އަތުން ފެން ފުއްސެވިއެވެ. އެއަށްފަހު ދެ ފައިތިލަ ދޮންނެވިއެވެ. \n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: އެކަލޭގެފާނު ބޮލުގެ ކުރިމަތިން ފައްޓަވައިގެން ބޮލުގެ ފަހަތަށް ދާންދެން ފުއްސަވާފައި، ދެން އަނެއްކާ އަނބުރާ ފެށި ހިސާބަށް ދެ އަތް ގެންނެވިއެވެ. \n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ރަސޫލު ﷲ ﷺގެ އަރިހަށް ތިމަންމެން ދިޔައިމެވެ. އަދި ނަބިއްޔާއަށްޓަކައި ލޮއި ކަންވާރެއްގައި ފެން ގެނައީމެވެ.","[البخاري 1، ومسلم 226\n\nالبخاري 185، ومسلم 235\n\nالبخاري 197]"],["#010","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا قَالَتْ: «كَانَ رَسُولُ اللَّهِ ﷺ يُعْجِبُهُ التَّيَمُّنُ فِي تَنَعُّلِهِ، وَتَرَجُّلِهِ، وَطُهُورِهِ، وَفِي شَأْنِهِ كُلِّهِ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ފައިވާންޖޯޑުކޮޅު ތުރުކުރެއްވުމުގައްޔާއި، އިސްތަށިފުޅުގައި ފުނާ އެޅުއްވުމުގައްޔާއި، ތާހިރުވެވަޑައިގަތުމާ އަދި ހަމަ އެހެންމެ ހުރިހާ ކަމެއްގައި ވެސް ކަނާތް ފަރާތް އިސްކުރެއްވުމަށް ލޯބި ކުރެއްވިއެވެ.","[البخاري 168، ومسلم 268]"],["#011","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","عَنْ نُعَيْمٍ الْمُجْمِرِ عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - عَنْ النَّبِيِّ ﷺ أَنَّهُ قَالَ: «إنَّ أُمَّتِي يُدْعَوْنَ يَوْمَ القِيَامَةِ غُرّاً مُحَجَّلِينَ مِنْ آثَارِ الْوُضُوءِ» فَمَنْ اسْتَطَاعَ مِنْكُمْ أَنْ يُطِيلَ غُرَّتَهُ فَلْيَفْعَلْ. \n\nوَفِي لَفْظٍ لِمُسْلِمٍ: «رَأَيْتُ أَبَا هُرَيْرَةَ يَتَوَضَّأُ، فَغَسَلَ وَجْهَهُ وَيَدَيْهِ حَتَّى كَادَ يَبْلُغُ الْمَنْكِبَيْنِ، ثُمَّ غَسَلَ رِجْلَيْهِ حَتَّى رَفَعَ إلَى السَّاقَيْنِ، ثُمَّ قَالَ: سَمِعْتُ رَسُولَ اللَّهِ ﷺ يَقُولُ: إنَّ أُمَّتِي يُدْعَوْنَ يَوْمَ الْقِيَامَةِ غُرَّاً مُحَجَّلِينَ مِنْ آثَارِ الْوُضُوءِ» فَمَنْ اسْتَطَاعَ مِنْكُمْ أَنْ يُطِيلَ غُرَّتَهُ وَتَحْجِيلَهُ فَلْيَفْعَلْ.","ނުޢައިމު އަލްމުޖްމިރު، އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާ ކުރެއްވިއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ގިޔާމަތް ދުވަހު ތިމަން ނަބިއްޔާގެ އުންމަތަށް ގޮވާލެވޭނެއެވެ. އެބައިމީހުންގެ ނިތްކުރިތަކާއި ފައިތަކުގައި ވުޟޫގެ އަސަރުން ވިދާ ބަބުޅާ އަލިކަމެއް ވާނެއެވެ.“ (އަބޫ ހުރައިރާ ވިދާޅުވިއެވެ:) ވީމާ ތިޔަބައިމީހުންގެ ތެރެއިން އޭނާގެ އަލިކަން އިތުރު ކުރެވެން ހުރި މީހަކު، ފަހެ އެ ކަން ކުރާށެވެ. \n\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: (ނުޢައިމު އަލްމުޖްމިރު ވިދާޅުވިއެވެ:) އަބޫ ހުރައިރާ ވުޟޫ ކުރައްވަނިކޮށް ތިމަން ދުށީމެވެ. އަބޫ ހުރައިރާގެ މޫނާއި ދެ އަތް ކޮނޑާ ހިސާބަށް ދަންދެން ދޮންނެވިއެވެ. އެއަށްފަހު ކަންކަށިމައްޗާ ހަމައަށް ދެ ފައި ދޮންނެވިއެވެ. ދެން އަބޫ ހުރައިރާ ވިދާޅުވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ”ހަމަކަށަވަރުން ގިޔާމަތް ދުވަހު ތިމަން ނަބިއްޔާގެ އުންމަތަށް ގޮވާލެވޭނެއެވެ. އެބައިމީހުންގެ ނިތްކުރިތަކާއި ފައިތަކުގައި ވުޟޫގެ އަސަރުން ވިދާ ބަބުޅާ އަލިކަމެއް ވާނެއެވެ.“ (އަބޫ ހުރައިރާ ވިދާޅުވިއެވެ:) ވީމާ ތިޔަބައިމީހުންގެ ތެރެއިން އޭނާގެ ނިއްކުރީގެ އަލިކަން އިތުރު ކުރެވެން ހުރި މީހަކު، ފަހެ އެ ކަން ކުރާށެވެ.","[البخاري 136\n\nمسلم 236]"],["#012","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","","","وَفِي لَفْظٍ لِمُسْلِمٍ: سَمِعْتُ خَلِيلِي ﷺ يَقُولُ: «تَبْلُغُ الْحِلْيَةُ مِنْ الْمُؤْمِنِ حَيْثُ يَبْلُغُ الْوُضُوءُ»","އަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: (އަބޫ ހުރައިރާ ރިވާ ކުރެއްވިއެވެ:) ތިމަންގެ މިތުރު (ނަބިއްޔާ) ﷺ ޙަދީޘް ކުރައްވަނިކޮށް އަޑުއެހީމެވެ: ”މުއުމިނާގެ ވުޟޫ ދިޔަ ހިސާބަކަށް އޭނާގެ ޒީނަތްތެރިކަން ފޯރާނެއެވެ.“","[مسلم 250]"],["#013","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","‌باب دخول الخلاء والاستطابة","ފާހާނައަށް ވަނުމައި ސާފުތާހިރުވުމުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ النَّبِيَّ ﷺ كَانَ إذَا دَخَلَ الْخَلاءَ قَالَ: «اللَّهُمَّ إنِّي أَعُوذُ بِكَ مِنْ الْخُبُثِ وَالْخَبَائِثِ»","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺ ގަޟާހާޖާ ކުރާ ތަނަށް ވަދެވަޑައިގަންނަވާ ހިނދު ވިދާޅުވެއެވެ: ”ޔާ ﷲ! ހަމަކަށަވަރުން ފިރިހެން ޝައިތާނުންނާއި، އަންހެން ޝައިތާނުންގެ ކިބައިން މި އަޅާ ރައްކާތެރި ކޮށްދެއްވުން އެދި އިބަ އިލާހަށް ދަންނަވަމެވެ.“","[البخاري 143، ومسلم 375]"],["#014","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","‌باب دخول الخلاء والاستطابة","ފާހާނައަށް ވަނުމައި ސާފުތާހިރުވުމުގެ ބާބު","عَنْ أَبِي أَيُّوبَ الأَنْصَارِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «إذَا أَتَيْتُمْ الْغَائِطَ، فَلا تَسْتَقْبِلُوا الْقِبْلَةَ بِغَائِطٍ وَلا بَوْلٍ، وَلا تَسْتَدْبِرُوهَا، وَلَكِنْ شَرِّقُوا أَوْ غَرِّبُوا»\n\nقَالَ أَبُو أَيُّوبَ: «فَقَدِمْنَا الشَّامَ، فَوَجَدْنَا مَرَاحِيضَ قَدْ بُنِيَتْ نَحْوَ الْكَعْبَةِ، فَنَنْحَرِفُ عَنْهَا، وَنَسْتَغْفِرُ اللَّهَ عَزَّ وَجَلَّ».","އަބޫ އައްޔޫބު އަލްއަންޞާރީ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ގަޟާހާޖާ ކުރަން (ބޭރަށް ނުކުމެއްޖެ ނަމަ)، ބޮޑުކަމުދިޔުމަށް ވެސް އަދި ކުޑަކަމުދިޔުމަށް ވެސް ގިބުލައާ ކުރިމަތި ނުލާށެވެ. އަދި ގިބުލައަށް ފުރަގަސް ވެސް ނުދޭށެވެ. އެހެނެއްކަމަކު އިރުމައްޗަށް ނުވަތަ ހުޅަނގަށް އެނބުރޭށެވެ.“ (ނޯޓު: މިއީ ހާމަ ތަނެއްގައި ގަޟާހާޖާކުރާ ނަމައެވެ. ބިނާއެއްގެ އެތެރޭގައި ކުރާ ނަމަ މައްސަލައެއް ނޯންނާނެއެވެ.)\n\nއަބޫ އައްޔޫބު ވިދާޅުވިއެވެ: ތިމަންމެން ޝާމް ކަރައަށް ދިޔައިމެވެ. ފަހެ (އެ ތަނުގައި) ކައުބާއާ ދިމާއަށް ފާހާނާތަކެއް ރާނާފައިވަނިކޮށް ތިމަންމެން ދުށީމެވެ. ދެން (އެ ފާހާނާތައް ބޭނުންކުރި އިރު) ގިބުލައާ ދިމާނުވާ ގޮތަށް ތިމަންމެން އެނބުރުނީމެވެ. އަދި (އެއަށްފަހު) ﷲ ޢައްޒަ ވަޖައްލައަށް އިސްތިޢްފާރު ކުރީމެވެ.","[البخاري 394، ومسلم 264]"],["#015","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","‌باب دخول الخلاء والاستطابة","ފާހާނައަށް ވަނުމައި ސާފުތާހިރުވުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ بْنِ الْخَطَّابِ رَضِيَ اللَّهُ عَنْهُما قَالَ: «رَقِيْتُ يَوْماً عَلَى بَيْتِ حَفْصَةَ، فَرَأَيْتُ النَّبِيَّ ﷺ يَقْضِي حَاجَتَهُ مُسْتَقْبِلَ الشَّامَ، مُسْتَدْبِرَ الْكَعْبَةَ» \n\nوَفِي رِوَايَةٍ «مُسْتَقْبِلاً بَيْتَ الْمَقْدِسِ»","ޢަބްދު ﷲ ބިން ޢުމަރު ބިން އަލްޚައްޠާބު رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އެއް ދުވަހަކު ޙަފްޞާގެ ގެކޮޅުގެ ފުރާޅު މައްޗަށް ތިމަން އެރީމެވެ. އެ ހިނދު ނަބިއްޔާ ﷺ ޝާމް ކަރައަށް ކުރިމަތިލައްވައިގެން، ކައުބާއަށް ފުރަގަސްދެއްވާ ހާލު ގަޟާހާޖާ ކުރައްވަނިކޮށް ތިމަން ދުށީމެވެ. \n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ބައިތުލް މަގްދިސަށް ކުރިމަތިލައްވައިގެން ހުންނެވިއެވެ.","[البخاري 148، ومسلم 266]"],["#016","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","‌باب دخول الخلاء والاستطابة","ފާހާނައަށް ވަނުމައި ސާފުތާހިރުވުމުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - أَنَّهُ قَالَ: «كَانَ رَسُولُ اللَّهِ ﷺ يَدْخُلُ الْخَلاءَ، فَأَحْمِلُ أَنَا وَغُلامٌ نَحْوِي إدَاوَةً مِنْ مَاءٍ وَعَنَزَةً، فَيَسْتَنْجِي بِالْمَاءِ»","ހަމަކަށަވަރުން އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ (ގަޟާޙާޖާ ކުރެއްވުމަށް ޓަކައި) ވަދެވަޑައިގަންނަވާ ހިނދު، ތިމަންނައާއި ތިމަންގެ އުމުރުގެ އެހެން ކުއްޖަކު، ފެނުގެ ކަންވާރަކާއި، (މީހުންގެ ކިބައިން ނިވާވުމަށް ޓަކައި) އަސާކޮޅެއް (ނަބިއްޔާއަށް) ގެންގޮސްދީ އުޅުނީމެވެ. ދެން ނަބިއްޔާ ފެނުން އިސްތިންޖާ ކުރައްވައެވެ. (އެބަހީ: ދޮންނަވައެވެ.)","[البخاري 152، ومسلم 271]"],["#017","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","‌باب دخول الخلاء والاستطابة","ފާހާނައަށް ވަނުމައި ސާފުތާހިރުވުމުގެ ބާބު","عَنْ أَبِي قَتَادَةَ الْحَارِثِ بْنِ رِبْعِيٍّ الأَنْصَارِيِّ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ النَّبِيَّ ﷺ قَالَ: «لا يُمْسِكَنَّ أَحَدُكُمْ ذَكَرَهُ بِيَمِينِهِ وَهُوَ يَبُولُ وَلا يَتَمَسَّحْ مِنْ الْخَلاءِ بِيَمِينِهِ وَلا يَتَنَفَّسْ فِي الإِنَاءِ»","އަބޫ ޤަތާދާ އަލްޙާރިޘް ބިން ރިބްޢީ އަލްއަންޞާރީ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ ތެރެއިން އެކަކުވެސް ކުޑަކަމުދާ ހިނދު، ކަނާތުން ލަދުވެތި ގުނަވަނުގައި ހިފަހައްޓައިގެނެއް ނުވާނެއެވެ. އަދި ބޮޑުކަމުދިޔުމަށް ފަހު، (ސާފު ކުރުމަށް ޓަކައި) ކަނާތުން ނުފޮހޭށެވެ. އަދި (އެއްޗެއް ބޯ ހިނދު) ކަންވާރުގެ ތެރެއަށް ނޭވާ ނުލާށެވެ.“","[البخاري 153، ومسلم 367]"],["#018","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","‌باب دخول الخلاء والاستطابة","ފާހާނައަށް ވަނުމައި ސާފުތާހިރުވުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: مَرَّ النَّبِيُّ ﷺ بِقَبْرَيْنِ، فَقَالَ: «إنَّهُمَا لَيُعَذَّبَانِ، وَمَا يُعَذَّبَانِ فِي كَبِيرٍ أَمَّا أَحَدُهُمَا: فَكَانَ لا يَسْتَتِرُ مِنْ الْبَوْلِ، وَأَمَّا الآخَرُ: فَكَانَ يَمْشِي بِالنَّمِيمَةِ فَأَخَذَ جَرِيدَةً رَطْبَةً، فَشَقَّهَا نِصْفَيْنِ، فَغَرَزَ فِي كُلِّ قَبْرٍ وَاحِدَةً، فَقَالُوا: يَا رَسُولَ اللَّهِ، لِمَ فَعَلْتَ هَذَا؟ قَالَ: لَعَلَّهُ يُخَفَّفُ عَنْهُمَا مَا لَمْ يَيْبَسَا»","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ދެ މަހާނަ ކައިރިން ލާފަ ނަބިއްޔާ ވަޑައިގަތެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން (އޭގައިވާ) ދެ މީހުންނަށް އަޒާބު ދެވެމުން އެބަދެއެވެ. އަދި އެ ދެ މީހުންނަށް އަޒާބު ދެވެނީކީ ބޮޑު ކަމަކާ ހެދިއެއް ނޫނެވެ. އެ ތަނުން އެކަކު ކުޑަކަމުދާ އިރު އޭގެން (ގަޔަށް ނުބުރާ ގޮތަށް) ރައްކާތެރިއެއް ނުވެއެވެ. އަދި އަނެކަކު ދެބައޮޑުވަމުން ހިނގައި އުޅުނެވެ.“ ދެން ނަބިއްޔާ ކަދުރު ރުކެއްގެ ރޯ ފަންތޮށިކޮޅެއް ނަންގަވާފައި ދެ ފަޅިއަށް ފަޅާލެއްވިއެވެ. އަދި އެއިން ފަޅިއެއް ކޮންމެ މަހާނައެއްގައި ހަރުކުރެއްވިއެވެ. ދެން މީސްތަކުން ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ތިޔަފަދައިން ކަންތައް ކުރެއްވީ ކީއްވެގެންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެއީ އެ ދޭތި ނުހިކޭހާ ހިނދަކު، އެ ދެ މީހުންގެ ކިބައިން (އަޒާބު) ލުއި ކުރައްވާތޯއެވެ.“","[البخاري 218 واللفظ له، ومسلم 292]"],["#019","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","‌باب السواك","ސިވާކުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - عَنْ النَّبِيِّ ﷺ قَالَ: «لَوْلا أَنْ أَشُقَّ عَلَى أُمَّتِي لأَمَرْتُهُمْ بِالسِّوَاكِ عِنْدَ كُلِّ صَلاةٍ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާގެ އުންމަތުގެ މައްޗަށް ދަތި ކަމަކަށް ނުވާނަމަ، ކޮންމެ ނަމާދަކަށް ސިވާކު ބޭނުން ކުރަން އެބައިމީހުންނަށް އަމުރު ކުރެއްވީމުހެވެ.“","[البخاري 887، ومسلم 252]"],["#020","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","‌باب السواك","ސިވާކުގެ ބާބު","عَنْ حُذَيْفَةَ بْنِ الْيَمَانِ رَضِيَ اللَّهُ عَنْهُما قَالَ: «كَانَ رَسُولُ اللَّهِ ﷺ إذَا قَامَ مِنْ اللَّيْلِ يَشُوصُ فَاهُ بِالسِّوَاكِ»","ޙުޛައިފާ ބިން އަލްޔަމާނު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ރޭގަނޑު (ހޭފުޅުލައްވާ) ތެދުވެވަޑައިގަންނަވާ ނަމަ، ސިވާކު ދަނޑިކޮޅަކުން އަނގަފުޅު ސާފު ކުރައްވައެވެ.","[البخاري 245 واللفظ له، ومسلم 255]"],["#021","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","‌باب السواك","ސިވާކުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا قَالَتْ: «دَخَلَ عَبْدُ الرَّحْمَنِ بْنُ أَبِي بَكْرٍ الصِّدِّيقِ - رَضِيَ اللَّهُ عَنْهُ - عَلَى النَّبِيِّ ﷺ وَأَنَا مُسْنِدَتُهُ إلَى صَدْرِي، وَمَعَ عَبْدِ الرَّحْمَنِ سِوَاكٌ رَطْبٌ يَسْتَنُّ بِهِ فَأَبَدَّهُ رَسُولُ اللَّهِ ﷺ بَصَرَهُ. فَأَخَذْتُ السِّوَاكَ فَقَضَمْتُهُ، فَطَيَّبْتُهُ، ثُمَّ دَفَعْتُهُ إلَى النَّبِيِّ ﷺ فَاسْتَنَّ بِهِ فَمَا رَأَيْتُ رَسُولَ اللَّهِ ﷺ اسْتَنَّ اسْتِنَانًا أَحْسَنَ مِنْهُ، فَمَا عَدَا أَنْ فَرَغَ رَسُولُ اللَّهِ ﷺ: رَفَعَ يَدَهُ - أَوْ إصْبَعَهُ - ثُمَّ قَالَ: فِي الرَّفِيقِ الأَعْلَى - ثَلاثاً - ثُمَّ قَضَى. وَكَانَتْ تَقُولُ: مَاتَ بَيْنَ حَاقِنَتِي وَذَاقِنَتِي» \n\nوَفِي لَفْظٍ «فَرَأَيْتُهُ يَنْظُرُ إلَيْهِ، وَعَرَفْتُ: أَنَّهُ يُحِبُّ السِّوَاكَ فَقُلْتُ: آخُذُهُ لَكَ؟ فَأَشَارَ بِرَأْسِهِ: أَنْ نَعَمْ» هَذَا لَفْظُ الْبُخَارِيِّ وَلِمُسْلِمٍ نَحْوُهُ.","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ޢަބްދުއްރަޙްމާނު ބިން އަބޫ ބަކުރު އައްސިއްދީގު، ނަބިއްޔާ ﷺގެ އަރިހަށް ވަނެވެ. އެ ހިނދު ތިމަން ކަމަނާ އިންނެވީ ނަބިއްޔާ ތިމަން ކަމަނާގެ މޭގައި ޖައްސަވާލައިގެންނެވެ. އަދި ޢަބްދުއްރަޙްމާނު ހުންނެވީ އަތުގައި އޮތް ރޯ ސިވާކު ދަނޑިކޮޅަކުން ދަތް އުނގުޅުއްވަންށެވެ. އެ ހިނދު އެއާ ދިމާއަށް ރަސޫލު ﷲ ﷺ ބައްލަވާލެއްވިއެވެ. ދެން ތިމަން ކަމަނާ އެ ސިވާކު ނަންގަވާފައި، އޭގެ ކޮޅު ތިމަން ކަމަނާގެ ދަތްޕުޅުން ހަފައި ބުރިކޮށް ސާފުކޮށްލުމަށް ފަހު، ނަބިއްޔާ ﷺ އަށް ދެއްވީމެވެ. ދެން ނަބިއްޔާ އޭގެން ދަތްޕުޅު އުނގުޅުއްވިއެވެ. ފަހެ އެއަށްވުރެ ރަނގަޅަށް ރަސޫލު ﷲ ﷺ ދަތްޕުޅު އުނގުޅުއްވި ފަހަރެއް ތިމަން ކަމަނާ ނުދެކެމެވެ. ނަބިއްޔާ ﷺ އެ ކަންތައް ކުރައްވާ ނުންމެވުމަށް ފަހު، އަތްޕުޅު (ނުވަތަ އިނގިލިފުޅު) އުފުއްލަވާފައި ތިން ފަހަރު ޙަދީޘް ކުރެއްވިއެވެ: ”އެންމެ މަތިވެރި އެކުވެރިންނާ އެކުގައެވެ.“ (އެބަހީ: ނަބިއްޔުންނާއި ސިއްދީގުންނާއި ޝަހީދުންނާއި ސާލިހު ބޭކަލުންނާއެކުގައި މި އަޅާ ލައްވާށިއެވެ.) އެއަށްފަހު ނަބިއްޔާ އަވަހާރަވިއެވެ. އަދި ޢާއިޝާ ވިދާޅުވެ އުޅުއްވިއެވެ: ނަބިއްޔާ އަވަހާރަވީ، ތިމަން ކަމަނާގެ ކޮނޑުކަށްޓާއި ލަގޮނޑިވަޅާ ދެމެދުގައި (ނަބިއްޔާގެ މޫނުފުޅު) ވަނިކޮށެވެ. \n \nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ފަހެ ނަބިއްޔާ އެ ސިވާކު ދަނޑިކޮޅާ ދިމާއަށް ބައްލަވަނިކޮށް ތިމަން ކަމަނާ ދުށީމެވެ. އަދި ނަބިއްޔާ ސިވާކުން ދަތްޕުޅު އުނގުޅައްވަން ލޯބި ކުރައްވާކަން ތިމަން ކަމަނާއަށް އެނގުނެވެ. ވީމާ ތިމަން ކަމަނާ ދެންނެވީމެވެ: އިބަ ނަބިއްޔާއަށް އޭތި ނަގައިދެންތޯއެވެ؟ ނަބިއްޔާގެ އިސްތަށިފުޅުން އިޝާރާތް ކުރެއްވިއެވެ. އެބަހީ އާދޭހެވެ. މިއީ ބުޚާރީގެ ރިވާޔަތެވެ. އަދި މިފަދަ ރިވާޔަތެއް މުސްލިމުގައި ވެސް ވެއެވެ.","[البخاري 4438\n\nالبخاري 4449، ومسلم 2443]"],["#022","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","‌باب السواك","ސިވާކުގެ ބާބު","عَنْ أَبِي مُوسَى الأَشْعَرِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «أَتَيْتُ النَّبِيَّ ﷺ وَهُوَ يَسْتَاكُ بِسِوَاكٍ رَطْبٍ، قَالَ: وَطَرَفُ السِّوَاكِ عَلَى لِسَانِهِ، وَهُوَ يَقُولُ: أُعْ، أُعْ، وَالسِّوَاكُ فِي فِيهِ، كَأَنَّهُ يَتَهَوَّعُ»","އަބޫ މޫސާ އަލްޢަޝްޢަރީ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ ރޯ ސިވާކު ދަނޑިކޮޅަކުން ދަތްޕުޅު އުނގުޅުއްވަނިކޮށް، ނަބިއްޔާގެ އަރިހަށް ތިމަން އައީމެވެ. ނަބިއްޔާގެ އަނގަފުޅު ތެރޭގައި ސިވާކު އޮތް އިރު، ހޮޑުލައްވަން އުޅުއްވާފަދަ ގޮތަކަށް ’އުޢް އުޢް‘ ފަދަ އަޑެއް ލައްވަވައެވެ.","[البخاري 244 واللفظ له، ومسلم 254]"],["#023","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب المسح على الخفين","ޚުއްފުގައި ފެން ފޮހުމުގެ ބާބު","عَنْ الْمُغِيرَةِ بْنِ شُعْبَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «كُنْتُ مَعَ النَّبِيِّ ﷺ فِي سَفَرٍ، فَأَهْوَيْتُ لأَنْزِعَ خُفَّيْهِ، فَقَالَ: دَعْهُمَا، فَإِنِّي أَدْخَلْتُهُمَا طَاهِرَتَيْنِ، فَمَسَحَ عَلَيْهِمَا»","އަލްމުޣީރާ ބިން ޝުޢުބާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺގެ އަރިހުގައި ތިމަން ދަތުރެއް ކުރީމެވެ. އަދި ނަބިއްޔާގެ (ދެ ފައިންޕުޅު ދޮވުމަށް) ޚުއްފު ބާލައިދޭން ތިމަން ދެ އަތް ދިއްކޮށްލީމެވެ. އެ ހިނދު ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ ދެ ޚުއްފު (ނުބާލާ) ދޫކޮށްލާށެވެ. ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާ އެ ޚުއްފަށް އަރައިވަޑައިގަތީ (ވުޟޫކޮށް) ތަހާރަތުގެ މަތީގައެވެ.“ ދެން ނަބިއްޔާ އެ ދެ ޚުއްފުގައި ފެން ފުއްސެވިއެވެ. (ނޯޓު: ޚުއްފަކީ ޖަނަވާރުގެ ހަމުން ހަދާފައިވާ އިސްޓާކީނެވެ.)","[البخاري 206، ومسلم 247]"],["#024","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب المسح على الخفين","ޚުއްފުގައި ފެން ފޮހުމުގެ ބާބު","عَنْ حُذَيْفَةَ بْنِ الْيَمَانِ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: «كُنْتُ مَعَ النَّبِيِّ ﷺ فَبَالَ، وَتَوَضَّأَ، وَمَسَحَ عَلَى خُفَّيْهِ»","ޙުޛައިފާ ބިން އަލްޔަމާނު رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺގެ އަރިހުގައި ތިމަން (ދަތުރެއްގައި) ވީމެވެ. ފަހެ ނަބިއްޔާ ކުޑަގިފިލި ކުރެއްވުމަށް ފަހު، ވުޟޫ ކުރައްވާފައި ދެ ޚުއްފުގައި ފެން ފުއްސެވިއެވެ.","[مسلم 273]"],["#025","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب في المذي وغيره","މަޒިއްޔާއި އެހެނިހެން ތަކެތީގެ ބާބު","عَنْ عَلِيِّ بْنِ أَبِي طَالِبٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «كُنْتُ رَجُلاً مَذَّاءً، فَاسْتَحْيَيْتُ أَنْ أَسْأَلَ رَسُولَ اللَّهِ ﷺ لِمَكَانِ ابْنَتِهِ مِنِّي، فَأَمَرْتُ الْمِقْدَادَ بْنَ الأَسْوَدِ فَسَأَلَهُ، فَقَالَ: يَغْسِلُ ذَكَرَهُ، وَيَتَوَضَّأُ» \n\nوَلِلْبُخَارِيِّ «اغْسِلْ ذَكَرَكَ وَتَوَضَّأْ»\n\nوَلِمُسْلِمٍ «تَوَضَّأْ وَانْضَحْ فَرْجَكَ»","ޢަލީ ބިން އަބީ ޠާލިބު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ތިމަންނަވީ ގިނަގިނައިން މަޒްޔު އަންނަ ފިރިހެނަކަށެވެ. އަދި ނަބިއްޔާ ﷺގެ ދަރިކަނބަލުންގެ މަގާމުގެ ސަބަބުން، (މި މަޒްޔުގެ ކަންތަކާ ބެހޭ ގޮތުން) ނަބިއްޔާ އަރިހުގައި ސުވާލު ކުރަން ތިމަން ލަދުވެތިވީމެވެ. ފަހެ (އެ ސުވާލުކުރުމަށް) އަލްމިގްދާދު ބިން އަލްއަސްވަދަށް އެންގުމުން، އެކަލޭގެފާނު ނަބިއްޔާ އަރިހުގައި ސުވާލު ކުރެއްވިއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެކަލޭގެފާނުގެ ލަދުވެތި ގުނަވަން ދޮވެފައި ވުޟޫ ކުރާނީއެވެ.“\n\nއަދި ބުޚާރީގެ ރިވާޔަތެއްގައި ވެއެވެ: ”ތިބާގެ ލަދުވެތި ގުނަވަން ދޮވެފައި ވުޟޫ ކުރާށެވެ.“\n\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ”ވުޟޫ ކުރާށެވެ. އަދި ތިބާގެ ލަދުވެތި ގުނަވަން ދޮންނާށެވެ.“","[مسلم 303\n\nالبخاري 269\n\nمسلم 303]"],["#026","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب في المذي وغيره","މަޒިއްޔާއި އެހެނިހެން ތަކެތީގެ ބާބު","عَنْ عَبَّادِ بْنِ تَمِيمٍ عَنْ عَبْدِ اللَّهِ بْنِ زَيْدٍ بْنِ عَاصِمٍ الْمَازِنِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «شُكِيَ إلَى النَّبِيِّ ﷺ الرَّجُلُ يُخَيَّلُ إلَيْهِ أَنَّهُ يَجِدُ الشَّيْءَ فِي الصَّلاةِ، فَقَالَ: لا يَنْصَرِفُ حَتَّى يَسْمَعَ صَوْتاً، أَوْ يَجِدَ رِيحاً»","ޢައްބާދު ބިން ތަމީމު، ޢަބްދު ﷲ ބިން ޒައިދު ބިން ޢާޞިމް އަލްމާޒިނީ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާ ކުރެއްވިއެވެ: މީހަކަށް ނަމާދުގައި ހުއްޓައި (ވައި ބޭރުވާކަމަށް) ހީވެގެން، (އެކަމާ ގުޅޭ ގޮތުން) ނަބިއްޔާގެ އަރިހުގައި ޝަކުވާ ދެންނެވުނެވެ. ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ މީހަކަށް އަޑެއް އިވިއްޖައުމަށް ދާންދެން ނުވަތަ ވަހެއް ފާހަގަވަންދެން ނަމާދު ދޫކޮށް ނުލާށެވެ.“","[البخاري 137، ومسلم 361 واللفظ له]"],["#027","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب في المذي وغيره","މަޒިއްޔާއި އެހެނިހެން ތަކެތީގެ ބާބު","عَنْ أُمِّ قَيْسِ بِنْتِ مِحْصَنٍ الأَسَدِيَّةِ «أَنَّهَا أَتَتْ بِابْنٍ لَهَا صَغِيرٍ، لَمْ يَأْكُلِ الطَّعَامَ إلَى رَسُولِ اللَّهِ ﷺ فَأَجْلَسَهُ فِي حِجْرِهِ، فَبَالَ عَلَى ثَوْبِهِ، فَدَعَا بِمَاءٍ فَنَضَحَهُ عَلَى ثَوْبِهِ، وَلَمْ يَغْسِلْهُ»","އުންމު ގައިސް ބިންތި މިޙްޞަނު އަލްއަސަދިއްޔާގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ކާންދޭން ނުފަށާ އެ ކަމަނާގެ ތުއްތު ފިރިހެން ކުއްޖަކު ގޮވައިގެން، ރަސޫލު ﷲ ﷺގެ އަރިހަށް އެ ކަމަނާ ދިޔައެވެ. ދެން ރަސޫލު ﷲ ﷺ، އެކަލޭގެފާނުގެ އުނގުފުޅު މަތީގައި އެ ކުއްޖާ ބޭންދެވިއެވެ. ފަހެ ނަބިއްޔާގެ ހެދުންކޮޅަށް އެ ކުއްޖާ ކުޑަކަމުދިޔައެވެ. ދެން ފެން ގެނައުމަށް ނަބިއްޔާ އަންގަވައި ހެދުންކޮޅުގެ މައްޗަށް ފެން ބުރުއްސަވާލެއްވިއެވެ. އަދި އެ ހެދުންކޮޅު ނުދޮންނަވައެވެ.","[البخاري 223، ومسلم 287]"],["#028","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب في المذي وغيره","މަޒިއްޔާއި އެހެނިހެން ތަކެތީގެ ބާބު","وَعَنْ عَائِشَةَ أُمِّ الْمُؤْمِنِينَ رَضِيَ اللَّهُ عَنْهَا «أَنَّ النَّبِيَّ ﷺ أُتِيَ بِصَبِيٍّ، فَبَالَ عَلَى ثَوْبِهِ، فَدَعَا بِمَاءٍ، فَأَتْبَعَهُ إيَّاهُ» \n\nوَلِمُسْلِمٍ: «فَأَتْبَعَهُ بَوْلَهُ، وَلَمْ يَغْسِلْهُ»","އަދި އުންމުލް މުއުމިނީން، ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺގެ އަރިހަށް ކުޑަ ކުއްޖަކު ގެނެވުނެވެ. ފަހެ އެ ކުއްޖާ ނަބިއްޔާގެ ހެދުންކޮޅަށް ކުޑަކަމުދިޔައެވެ. ދެން ފެން ގެނައުމަށް ނަބިއްޔާ އެންގެވުމަށް ފަހު، އެ ކުޑަކަމުދާ ތަކެތި ޖެހުނު ތަންތަނަށް ފެން ބުރުއްސަވާލެއްވިއެވެ. \n\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ދެން އެ ކުޑަކަމުދާ ތަކެތި ޖެހުނު ތަންތަނަށް ފެން ބުރުއްސަވާލެއްވިއެވެ. އަދި އެ ހެދުންކޮޅު ނުދޮންނަވައެވެ.","[البخاري 222\n\nمسلم 286]"],["#029","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب في المذي وغيره","މަޒިއްޔާއި އެހެނިހެން ތަކެތީގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «جَاءَ أَعْرَابِيٌّ، فَبَالَ فِي طَائِفَةِ الْمَسْجِدِ، فَزَجَرَهُ النَّاسُ، فَنَهَاهُمُ النَّبِيُّ ﷺ فَلَمَّا قَضَى بَوْلَهُ أَمَرَ النَّبِيُّ ﷺ بِذَنُوبٍ مِنْ مَاءٍ، فَأُهْرِيقَ عَلَيْهِ»","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ބަދަވީއަކު އައިސް މިސްކިތް ތެރޭ ދިމާއެއްގައި ކުޑަކަމުދިޔައެވެ. ފަހެ މީސްތަކުން އޭނާއަށް އެއްޗެހި ގޮވިއެވެ. ނަމަވެސް ނަބިއްޔާ ﷺ އެބައިމީހުންނަށް އެ ކަން ނަހީ ކުރެއްވިއެވެ. ދެން އެ ބަދަވީ ކުޑަކަމުގޮސް ނިމުމުން، ފެން ބާލިދީއެއް ގެނައުމަށް ނަބިއްޔާ ﷺ އެންގެވިއެވެ. އަދި އެ ތަނަށް ފެން އޮއްސާލެވިގެން ދިޔައެވެ.","[البخاري 221 واللفظ له، ومسلم 274]"],["#030","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب في المذي وغيره","މަޒިއްޔާއި އެހެނިހެން ތަކެތީގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: سَمِعْتُ رَسُولَ اللَّهِ ﷺ يَقُولُ: «الْفِطْرَةُ خَمْسٌ: الْخِتَانُ، وَالاسْتِحْدَادُ، وَقَصُّ الشَّارِبِ، وَتَقْلِيمُ الأَظْفَارِ، وَنَتْفُ الإِبِطِ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ”ފަސް ކަމެއް ވަނީ ފިތުރަތުގެ ތެރެއިންނެވެ. އެއީ ޚިތާނު ކުރުމާއި، ލަދުވެތި ގުނަވަން ކައިރިން އިސްތަށި ބޭލުމާއި، މަތިމަސް ކުރުކުރުމާއި، ނިޔަފަތި ކެނޑުމާއި، ކިހިލިފަތްދަށުން އިސްތަށި އުފުރުމެވެ.“","[البخاري 5889، ومسلم 257]"],["#031","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الجنابة","ޖުނުބުވެރިކަމުގެ ބާބު","عنْ أَبي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - «أَنَّ النَّبِيَّ ﷺ لَقِيَهُ في بعْضِ طُرُقِ المدينَةِ وهو جُنُبٌ، قالَ: فَانْخَنَسْتُ مِنْهُ، فَذَهَبْتُ فَاغْتَسَلْتُ ثُمَّ جِئْتُ، فقَالَ: أَيْنَ كُنْتَ ياأَبا هُرَيْرَةَ؟ قَالَ: كُنْتُ جُنُبَاً فَكَرِهْتُ أَنْ أُجالِسَكَ على غَيْرِ طَهارَةٍ، فَقَالَ: سُبْحانَ اللهِ، إِنَّ المُؤْمِنَ لا يَنْجُسُ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން އެކަލޭގެފާނު ޖުނުބުވެރިކަމުގެ ހާލުގައި ވަނިކޮށް، މަދީނާގެ މަގަކުން ނަބިއްޔާ ﷺ އާ ބައްދަލުވިއެވެ. އަބޫ ހުރައިރާ ވިދާޅުވިއެވެ: ދެން ނަބިއްޔާ ހުންނެވި ތަނުން ތިމަން ފަހަތަށް ޖެހި ފޮރުވިގެންފީމެވެ. އަދި ގޮސް ހިނައިގަތުމަށް ފަހު އެނބުރި އަނެއްކާ އަތުވެއްޖައީމެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ އަބޫ ހުރައިރާއެވެ. ތިބާ އުޅުނީ ކޮންތާކުތޯއެވެ؟“ ތިމަން ދެންނެވީމެވެ: ތިމަން އޭރު ހުރީ ޖުނުބުވެރިކަމުގެ ހާލުގައެވެ. އަދި ތަހާރަތުގެ މަތީ ނޫން ގޮތަކަށް އިބަ ނަބިއްޔާގެ އަރިހުގައި އިށީނުމަށް ތިމަން ބޭނުން ނުވީއެވެ. ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ސުބްޙާނަ ﷲ އެވެ. ހަމަކަށަވަރުން (އެއްވެސް ހާލެއްގައި) މުއުމިނަކު ނަޖިހެއް ނުވާނެއެވެ.“","[البخاري 285، ومسلم 371]"],["#032","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الجنابة","ޖުނުބުވެރިކަމުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا قَالَتْ: «كَانَ النَّبِيُّ ﷺ إذَا اغْتَسَلَ مِنَ الْجَنَابَةِ غَسَلَ يَدَيْهِ، ثُمَّ تَوَضَّأَ وُضُوءَهُ لِلصَّلاةِ، ثُمَّ اغْتَسَلَ، ثُمَّ يُخَلِّلُ بِيَدَيْهِ شَعْرَهُ، حَتَّى إذَا ظَنَّ أَنَّهُ قَدْ أَرْوَى بَشَرَتَهُ، أَفَاضَ عَلَيْهِ الْمَاءَ ثَلاثَ مَرَّاتٍ، ثُمَّ غَسَلَ سَائِرَ جَسَدِهِ.","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލާ ﷺ ޖުނުބުވެރިކަމުން ހިނައިގަންނަވާ ހިނދު، (ފުރަތަމަ) ދެ އަތްތިލަ ދޮންނަވައެވެ. އެއަށްފަހު ނަމާދަށް ވުޟޫ ކުރައްވާ ފަދައިން ވުޟޫ ކުރައްވައެވެ. ދެން ނާނޭފުޅު ފިއްލަވައެވެ. އަދި އިސްތަށިފުޅުގެ ތެރެއަށް ދެ އަތްޕުޅު ލައްވަވާ ހިނގަވާލައްވައެވެ. އެއީ (އިސްތަށިފުޅުގެ) ހަމުގައި ފެން ޖެއްސެވިއްޖެކަން އެނގިވަޑައިގަތުމަށް ދާންދެނެވެ. ދެން އިސްތަށިފުޅު މަތިން ތިން ފަހަރު ފެން އަޅުއްވައެވެ. އެއަށްފަހު މުޅި ހަށިކޮޅު ދޮންނަވައެވެ.","[البخاري 272 واللفظ له، ومسلم 316]"],["#033","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الجنابة","ޖުނުބުވެރިކަމުގެ ބާބު","وَكَانَتْ تَقُولُ: كُنْتُ أَغْتَسِلُ أَنَا وَرَسُولُ اللَّهِ ﷺ مِنْ إنَاءٍ وَاحِدٍ، نَغْتَرِفُ مِنْهُ جَمِيعاً.","އަދި ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވެއެވެ: ތިމަން ކަމަނާއާއި ރަސޫލު ﷲ ﷺ އެއް ކަންވާރަކުން (ފެން ނަންގަވައިގެން) ހިނައިގަންނަވަމެވެ. ތިމަން ދެކަނބަލުން ވެސް އޭގެން ފެން އަތްގޮށި އެކުއެކީގައި ނަންގަވަމެވެ.","[البخاري 273 واللفظ له، ومسلم 321]"],["#034","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الجنابة","ޖުނުބުވެރިކަމުގެ ބާބު","عَنْ مَيْمُونَةَ بِنْتِ الْحَارِثِ رَضِيَ اللَّهُ عَنْهَا زَوْجِ النَّبِيِّ ﷺ أَنَّهَا قَالَتْ: «وَضَعْتُ لِرَسُولِ اللَّهِ ﷺ وَضُوءَ الْجَنَابَةِ، فَأَكْفَأَ بِيَمِينِهِ عَلَى يَسَارِهِ مَرَّتَيْنِ - أَوْ ثَلاثاً - ثُمَّ غَسَلَ فَرْجَهُ، ثُمَّ ضَرَبَ يَدَهُ بِالأَرْضِ، أَوْ الْحَائِطِ، مَرَّتَيْنِ - أَوْ ثَلاثاً - ثُمَّ تَمَضْمَضَ وَاسْتَنْشَقَ، وَغَسَلَ وَجْهَهُ وَذِرَاعَيْهِ، ثُمَّ أَفَاضَ عَلَى رَأْسِهِ الْمَاءَ، ثُمَّ غَسَلَ جَسَدَهُ، ثُمَّ تَنَحَّى، فَغَسَلَ رِجْلَيْهِ، فَأَتَيْتُهُ بِخِرْقَةٍ فَلَمْ يُرِدْهَا، فَجَعَلَ يَنْفُضُ الْمَاءَ بِيَدِهِ»","ހަމަކަށަވަރުން ނަބިއްޔާ ﷺގެ އަނބިކަނބަލުން، މައިމޫނާ ބިންތި އަލްޙާރިޘް رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޖުނުބުވެރިކަމުން ހިނައިގެންނެވުމަށް ޓަކައި، ތިމަން ކަމަނާ ފެން ބެހެއްޓެވީމެވެ. ފަހެ ނަބިއްޔާ ކަނާތްޕުޅުން، ވާތްޕުޅަށް ދެ ފަހަރު ނުވަތަ ތިން ފަހަރު ފެން އޮއްސެވިއެވެ. ދެން ލަދުވެތި ގުނަވަން ދޮންނެވުމަށް ފަހު، ދެ ފަހަރު ނުވަތަ ތިން ފަހަރު ބިމުގައި ނުވަތަ ފާރުގައި އަތް ޖައްސަވައި (އުނގުޅާލެއްވިއެވެ.) ދެން އަނގަފުޅަށް ފެން ލައްވައި ކޯފުޅު ދޮންނެވުމަށް ފަހު، ނޭފަތްޕުޅަށް ފެން ލެއްވިއެވެ. އަދި މޫނުފުޅާއި ދެ އަތްޕުޅު ދޮންނެވިއެވެ. ދެން އިސްތަށިފުޅަށް ފެން އޮއްސެވިއެވެ. އެއަށްފަހު މުޅި ހަށިކޮޅު ދޮންނެވިއެވެ. ދެން ހުންނެވި ތަނުން ދުރަށް ޖެހިވަޑައިގެންފައި ދެ ފައިންޕުޅު ދޮންނެވިއެވެ. ނަބިއްޔާ (ހަށިކޮޅު ހިއްކުމަށް ޓަކައި) ތިމަން ކަމަނާ ފޮތިކޮޅެއް ގެނެސް ދެއްވީމެވެ. އެކަމަކު ނަބިއްޔާ އެ ބޭނުންވެވަޑައެއް ނުގެންނެވިއެވެ. ދެން ނަބިއްޔާގެ އަތްޕުޅުން ފެންތައް ތަޅުވާލައްވާ ފިލުވައިލެއްވިއެވެ.","[البخاري 274 واللفظ له، ومسلم 317]"],["#035","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الجنابة","ޖުނުބުވެރިކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ: أَنَّ عُمَرَ بْنَ الْخَطَّابِ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «يَا رَسُولَ اللَّهِ، أَيَرْقُدُ أَحَدُنَا وَهُوَ جُنُبٌ؟ قَالَ: نَعَمْ، إذَا تَوَضَّأَ أَحَدُكُمْ فَلْيَرْقُدْ»","ޢަބްދު ﷲ ބިން ޢުމަރުގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ޢުމަރު ބިން އަލްޚައްޠާބު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ތިމަންމެން ކުރެ މީހަކު ޖުނުބުވެރި ހާލުގައި ނިދިދާނެތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އާދޭހެވެ. ތިޔަބައިމީހުން ކުރެ މީހަކު ވުޟޫކޮށްފިނަމަ ދެން ނިދާށެވެ.“","[البخاري 287 واللفظ له، ومسلم 306]"],["#036","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الجنابة","ޖުނުބުވެރިކަމުގެ ބާބު","عَنْ أُمِّ سَلَمَةَ رَضِيَ اللَّهُ عَنْهَا - زَوْجِ النَّبِيِّ ﷺ قَالَتْ: «جَاءَتْ أُمُّ سُلَيْمٍ امْرَأَةُ أَبِي طَلْحَةَ - إلَى رَسُولِ اللَّهِ ﷺ فَقَالَتْ: يَا رَسُولَ اللَّهِ، إنَّ اللَّهَ لا يَسْتَحْيِي مِنْ الْحَقِّ، فَهَلْ عَلَى الْمَرْأَةِ مِنْ غُسْلٍ إذَا هِيَ احْتَلَمَتْ؟ فَقَالَ رَسُولُ اللَّهِ ﷺ: نَعَمْ، إذَا رَأَتِ الْمَاءَ»","ނަބިއްޔާ ﷺގެ އަނބިކަނބަލުން، އުންމު ސަލަމާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺގެ އަރިހަށް އަބޫ ޠަލްޙާގެ އަނބިކަނބަލުން އުންމު ސުލައިމު ވަޑައިގެން ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން ހައްގުގެ ކިބައިން ﷲ ލަދުވެތިވެ ވޮޑިނުގަންނަވައެވެ. އަންހެނަކު އިޙްތިލާމުވެއްޖެ ނަމަ ހިނައިގަންނަން ޖެހޭނެތޯއެވެ؟ ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އާދޭހެވެ. އެ ކަނބުލޭގެއަށް ފެން ފެނިއްޖެ ނަމައެވެ.“ (ނޯޓު: އިޙްތިލާމަކީ ނިދީގައި މަނި ބޭރުވުމެވެ.)","[البخاري 282 واللفظ له، ومسلم 313]"],["#037","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الجنابة","ޖުނުބުވެރިކަމުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا قَالَتْ: «كُنْت أَغْسِلُ الْجَنَابَةَ مِنْ ثَوْبِ رَسُولِ اللَّهِ ﷺ فَيَخْرُجُ إلَى الصَّلاةِ، وَإِنَّ بُقَعَ الْمَاءِ فِي ثَوْبِهِ» \n\nوَفِي لَفْظٍ لِمُسْلِمٍ «لَقَدْ كُنْتُ أَفْرُكُهُ مِنْ ثَوْبِ رَسُولِ اللَّهِ ﷺ فَرْكاً، فَيُصَلِّي فِيهِ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺގެ ހެދުންކޮޅުގައ ޖެހިފައިވާ (ތެތްކޮށް ހުރި) މަނި ތިމަން ކަމަނާ ދޮންނަވަމެވެ. ފަހެ ނަބިއްޔާގެ ހެދުންކޮޅުގައި ފެން ޖެހުނު ތަންތަން (ނުހިކި) ވަނިކޮށް ނަބިއްޔާ ނަމާދަށް ނުކުމެވަޑައިގަންނަވައެވެ.\n\nއަދި މުސްލިމުގެ ރިވާޔަތުގައި ވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺގެ ހެދުންކޮޅުން އެ (ހިކިފައި ހުރި މަނި) ތިމަން ކަމަނާ (ނިޔަފަތިން) ކަހާ ނައްޓަވަމެވެ. އެއަށްފަހު ނަބިއްޔާ އެ ހެދުންކޮޅުގައި ނަމާދު ކުރައްވައެވެ.","[البخاري 229 واللفظ له، ومسلم 289\n\nمسلم 288\n]"],["#038","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الجنابة","ޖުނުބުވެރިކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - أَنَّ النَّبِيَّ ﷺ قَالَ: «إذَا جَلَسَ بَيْنَ شُعَبِهَا الأَرْبَعِ، ثُمَّ جَهَدَهَا، فَقَدْ وَجَبَ الْغُسْلُ»\n\nوَفِي لَفْظٍ «وَإِنْ لَمْ يُنْزِلْ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އަންހެނެއްގެ ހަތަރެސްފައި ދެމެދުގައި މީހަކު އިށީންދެ، އޭނާގެ ލަދުވެތި ގުނަވަން އެ ކަނބުލޭގެ ފަރުޖު ތެރެއަށް ވައްދައިފި ނަމަ، ފަހެ ހަމަކަށަވަރުން ހިނައިގަތުން ވާޖިބުވާނެއެވެ.“ \n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”އަދި މަނި ބޭރު ނުވިކަމުގައި ވިޔަސް މެއެވެ.“","[البخاري 291 واللفظ له، ومسلم 348\n\nمسلم 348]"],["#039","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الجنابة","ޖުނުބުވެރިކަމުގެ ބާބު","عَنْ أَبِي جَعْفَرٍ مُحَمَّدِ بْنِ عَلِيِّ بْنِ الْحُسَيْنِ بْنِ عَلِيِّ بْنِ أَبِي طَالِبٍ رَضِيَ اللَّهُ عَنْهُمْ «أَنَّهُ كَانَ هُوَ وَأَبُوهُ عِنْدَ جَابِرِ بْنِ عَبْدِ اللَّهِ، وَعِنْدَهُ قَوْمٌ، فَسَأَلُوهُ عَنْ الْغُسْلِ؟ فَقَالَ: صَاعٌ يَكْفِيكَ، فَقَالَ رَجُلٌ: مَا يَكْفِينِي، فَقَالَ جَابِرٌ: كَانَ يَكْفِي مَنْ هُوَ أَوْفَى مِنْك شَعَرَاً، وَخَيْراً مِنْكَ - يُرِيدُ رَسُولَ اللَّهِ ﷺ ثُمَّ أَمَّنَا فِي ثَوْبٍ» \n\nوَفِي لَفْظٍ «كَانَ رَسُولُ اللَّهِ ﷺ يُفْرِغُ الْمَاءَ عَلَى رَأْسِهِ ثَلاثاً»","އަބޫ ޖަޢުފަރު މުޙައްމަދު ބިން ޢަލީ ބިން އަލްޙުސައިން ބިން ޢަލީ ބިން އަބީ ޠާލިބު رَضِيَ اللَّهُ عَنْهُمْގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން އަބޫ ޖަޢުފަރާއި އެކަލޭގެފާނުގެ ބައްޕާފުޅާ ދެބަފައިކަލުން ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُ ގާތުގައި ތިއްބެވިއެވެ. އަދި ޖާބިރުގެ އަރިހުގައި އެހެން ބަޔަކު ވެސް ތިއްބެވެ. ފަހެ އެބައިމީހުން ޖާބިރުގެ އަރިހުގައި ހިނައިގަތުމާ ބެހޭ ގޮތުން ސުވާލު ކުރިއެވެ. ޖާބިރު ވިދާޅުވިއެވެ: ތިބާއަށް ޞާޢެއްގެ މިންވަރު ފުދޭނެއެވެ. (ނޯޓު: އެއް ޞާޢަކީ ގާތްގަނޑަކަށް ތިން ލީޓަރެވެ.) ދެން މީހަކު ދެންނެވިއެވެ: އެ ވަރަކުން ތިމަންނައަކަށް ނުފުދޭނެއެވެ. ޖާބިރު ވިދާޅުވިއެވެ: ތިބާއަށް ވުރެ އިސްތަށިކޮޅު ގިނަ އަދި ތިބާއަށް ވުރެ ހެޔޮވެގެންވާ ބޭކަލަކަށް އެ ވަރުން ފުދުނެވެ. – އެބަހީ ރަސޫލު ﷲ ﷺ އެވެ. – އެއަށްފަހު ޖާބިރު އެއް ހެދުންކޮޅެއް ތުރުކުރައްވައިގެން ހުރެ ތިމަންމެންނައިގެން އިމާމުވެ ނަމާދު ކުރެއްވިއެވެ.\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ނަބިއްޔާ ﷺ (ހިނައިގަންނަވާ ހިނދު) އިސްތަށިފުޅަށް ތިން ފަހަރު ފެން އޮއްސަވާލައްވައެވެ.","[البخاري 252\n\nالبخاري 255]"],["#040","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب التيمم","ތަޔައްމަމުގެ ބާބު","عَنْ عِمْرَانَ بْنِ حُصَيْنٍ - رَضِيَ اللَّهُ عَنْهُ -: «أَنَّ رَسُولَ اللهِ ﷺ رَأَى رَجُلاً مُعْتَزلاً، لَمْ يُصَلِّ فِي الْقَوْمِ؟ فَقَالَ: يَا فُلانُ، مَا مَنَعَكَ أَنْ تُصَلِّيَ فِي الْقَوْمِ؟ فَقَالَ: يَا رَسُولَ اللَّهِ أَصَابَتْنِي جَنَابَةٌ، وَلا مَاءَ، فَقَالَ: عَلَيْك بِالصَّعِيدِ، فَإِنَّهُ يَكْفِيَكَ»","ޢިމްރާނު ބިން ޙުޞައިން رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން މީސްތަކުންނާއެކު ނަމާދު ނުކޮށް، އެކަހެރިވެގެން ހުރި މީހަކު ރަސޫލު ﷲ ﷺ ދެކެވަޑައިގަތެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ މިވެނި މީހާއެވެ. މީސްތަކުންނާއެކު ނަމާދު ކުރުމުން ތިބާ މަނާ ކުރުވީ ކޮން ކަމެއްތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ތިމަން މި ހުރީ ޖުނުބުވެރިވެފައެވެ. އަދި (ހިނައިގަންނާނެ) ފެނެއް ނެތެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާގެ މައްޗަށް އޮތީ ވެލިން (ތަޔައްމުމު ކުރުމެވެ.) އެހެނީ ހަމަކަށަވަރުން އެއިން ތިބާ (ހިނައިގަތުމަށް) ފުދޭނެއެވެ.“","[البخاري 348]"],["#041","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب التيمم","ތަޔައްމަމުގެ ބާބު","عَنْ عَمَّارِ بْنِ يَاسِرٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: «بَعَثَنِي النَّبِيُّ ﷺ فِي حَاجَةٍ، فَأَجْنَبْتُ، فَلَمْ أَجِدِ الْمَاءَ، فَتَمَرَّغْتُ فِي الصَّعِيدِ، كَمَا تَمَرَّغُ الدَّابَّةُ، ثُمَّ أَتَيْتُ النَّبِيَّ ﷺ فَذَكَرْتُ ذَلِكَ لَهُ، فَقَالَ: إنَّمَا يَكْفِيَكَ أَنْ تَقُولَ بِيَدَيْكَ هَكَذَا - ثُمَّ ضَرَبَ بِيَدَيْهِ الأَرْضَ ضَرْبَةً وَاحِدَةً، ثُمَّ مَسَحَ الشِّمَالَ عَلَى الْيَمِينِ، وَظَاهِرَ كَفَّيْهِ وَوَجْهَهُ»","ޢައްމާރު ބިން ޔާސިރު رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ތިމަން (ދަތުރެއްގައި) ކަމަކު ފޮނުއްވިއެވެ. (ފަހެ ދަތުރު މަތީގައި) ތިމަން ޖުނުބުވެރިވީމެވެ. އަދި ފެން ލިބޭ ގޮތެއް ނުވިއެވެ. ދެން ޖަނަވާރަކު ވެލިތަކުމަތީ ފުރޮޅޭ ފަދައިން ތިމަން ވެލިތަކުމަތީ ފުރޮޅުނީމެވެ. އެއަށްފަހު ނަބިއްޔާ ﷺގެ އަރިހަށް އައިސް އެ ވާހަކަ ދެންނެވީމެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ދެ އަތުން ހަމައެކަނި މިހެން ހަދާލިނަމަ ތިބާއަށް ފުދުނީހެވެ.“ ދެން ނަބިއްޔާ އެއް ފަހަރު ބިންމަތީގައި އަތްޕުޅުން ޖެއްސެވިއެވެ. އެއަށްފަހު ވާތްޕުޅުން ކަނާތްޕުޅު ފުއްސެވިއެވެ. އަދި ދެ އަތްތިލައިގެ (ބޭރުފުށާއި) މޫނުފުޅުގައި ފުއްސެވިއެވެ.","[البخاري 347، ومسلم 338]"],["#042","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب التيمم","ތަޔައްމަމުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رَضِيَ اللَّهُ عَنْهُما: أَنَّ النَّبِيَّ ﷺ قَالَ: «أُعْطِيتُ خَمْساً، لَمْ يُعْطَهُنَّ أَحَدٌ مِنْ الأَنْبِيَاءِ قَبْلِي: نُصِرْتُ بِالرُّعْبِ مَسِيرَةَ شَهْرٍ، وَجُعِلَتْ لِي الأَرْضُ مَسْجِدًا وَطَهُورًا، فَأَيُّمَا رَجُلٌ مِنْ أُمَّتِي أَدْرَكَتْهُ الصَّلاةُ فَلْيُصَلِّ، وَأُحِلَّتْ لِي الْمَغَانِمُ، وَلَمْ تَحِلَّ لأَحَدٍ قَبْلِي، وَأُعْطِيتُ الشَّفَاعَةَ، وَكَانَ النَّبِيُّ يُبْعَثُ إلَى قَوْمِهِ خَاصَّةً، وَبُعِثْتُ إلَى النَّاسِ عَامَّةً»","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާގެ ކުރިން އެއްވެސް ނަބިއްޔަކަށް ނުދެވޭ ފަސް ކަމެއް ތިމަން ނަބިއްޔާއަށް ދެއްވުނެވެ. (އެ ގޮތުން) މަސްދުވަހެއްގެ ރާސްތާގެ ދުރުމިނަށް (ތިމަން ނަބިއްޔާގެ އަދުއްވުންގެ ހިތްތަކަށް) ބިރުވެރިކަން ވެއްދެވިގެން ތިމަން ނަބިއްޔާއަށް ނަސްރު ދެއްވުނެވެ. އަދި ތިމަން ނަބިއްޔާއަށް ޓަކައި ބިމަކީ މިސްކިތެއް ކަމުގައި (އެބަހީ: ނަމާދު ކުރެވޭ ތަނެއް ކަމުގައި) އަދި ތާހިރުވާން ބޭނުން ކުރެވޭ އެއްޗެއް ކަމުގައި ލެއްވިއެވެ. ތިމަން ނަބިއްޔާގެ އުންމަތުގެ ކޮންމެ މީހަކަށް ވެސް ނަމާދު ވަގުތު ވަނުމުން، ފަހެ އޭނާ ނަމާދު ކުރާށެވެ. އަދި ތިމަން ނަބިއްޔާއަށް ޣަނީމާ މުދާ ހަލާލު ކުރެވުނެވެ. ތިމަން ނަބިއްޔާގެ ކުރިން އެއްވެސް މީހަކަށް އެ ހުއްދައެއް ނުކުރެވެއެވެ. އަދި ތިމަން ނަބިއްޔާއަށް ޝަފާއަތް ދެއްވުނެވެ. އަދި (ކުރީގެ) ނަބިއްޔުން ފޮނުއްވުނީ އެ ނަބިއްޔެއްގެ ގައުމުގެ ގާތަށް ޚާއްސަކޮށެވެ. އެކަމަކު ތިމަން ނަބިއްޔާ ފޮނުއްވުނީ މުޅި އިންސާނިއްޔަތަށް އާންމުކޮށެވެ.","[البخاري 335 واللفظ له، ومسلم 521]"],["#043","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الحيض","ހައިޟުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا «أَنَّ فَاطِمَةَ بِنْتَ أَبِي حُبَيْشٍ: سَأَلَتِ النَّبِيَّ ﷺ فَقَالَتْ: إنِّي أُسْتَحَاضُ فَلا أَطْهُرُ، أَفَأَدَعُ الصَّلاةَ؟ قَالَ: لا، إنَّ ذَلِكَ عِرْقٌ، وَلَكِنْ دَعِي الصَّلاةَ قَدْرَ الأَيَّامِ الَّتِي كُنْتِ تَحِيضِينَ فِيهَا، ثُمَّ اغْتَسِلِي وَصَلِّي» \n\nوَفِي رِوَايَةٍ «وَلَيْسَتْ بِالْحَيْضَةِ، فَإِذَا أَقْبَلَتْ الْحَيْضَةُ: فَاتْرُكِي الصَّلاةَ فِيهَا، فَإِذَا ذَهَبَ قَدْرُهَا فَاغْسِلِي عَنْك الدَّمَ وَصَلِّي»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺ އަރިހުގައި ފާޠިމާ ބިންތި އަބޫ ޙުބައިޝް ސުވާލު ކުރެއްވިއެވެ: ހަމަކަށަވަރުން ތިމަން ކަމަނާ އިޞްތިޙާޟާވެ އެ ކަމުން ތާހިރެއް ނުވެއެވެ. (އެބަހީ: މެދުކެނޑުމެއް ނެތި ރަޙިމުގެ ތިރީބައިން ލޭފައިބަފައިބަ ހުރެއެވެ. މިއީ ހައިޟުވުމާ ތަފާތު ކަމެކެވެ.) ވީމާ ތިމަން ކަމަނާ ނަމާދުކުރުން ދޫކޮށްލަންވީތޯއެވެ؟ ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ނޫނެކެވެ. ހަމަކަށަވަރުން ތިޔައީ ނާރަކުން އަންނަ ލެއެކެވެ. (އެބަހީ: ހައިޟުގެ ކަމެއް ނޫނެވެ.) އެހެނެއްކަމަކު ކަނބުލޭގެ (އާންމުކޮށް) ހައިޟުވެ އުޅޭ މުއްދަތަށް ނަމާދުކުރުން ދޫކޮށްލާށެވެ. އެއަށްފަހު ހިނައިގަނެ ނަމާދު ކުރާށެވެ.“ \n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”އެއީ އަދި ހައިޟެއް ނޫނެވެ. ވީމާ (އަސްލު) ހައިޟުވާން ފެށުމުން ނަމާދުކުރުން ދޫކޮށްލާށެވެ. ދެން (އާންމުކޮށް) ހައިޟުވެ އުޅޭ މުއްދަތު ނިމުމުން ކަނބުލޭގެ ކިބައިގައިވާ ލޭތައް ދޮވެ (ހިނައިގަންނާށެވެ.) އަދި ނަމާދު ކުރާށެވެ.“","[البخاري 325\n\nالبخاري 306، ومسلم 333]"],["#044","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الحيض","ހައިޟުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا «أَنَّ أُمَّ حَبِيبَةَ اُسْتُحِيضَتْ سَبْعَ سِنِينَ، فَسَأَلَتْ رَسُولَ اللَّهِ ﷺ عَنْ ذَلِكَ؟ فَأَمَرَهَا أَنْ تَغْتَسِلَ، قَالَتْ: فَكَانَتْ تَغْتَسِلُ لِكُلِّ صَلاةٍ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން އުންމު ޙަބީބާ ހަތް އަހަރު ވަންދެން އިސްތިޙާޟާވެ ވަޑައިގަތެވެ. އެ ކަމާ ބެހޭ ގޮތުން ރަސޫލު ﷲ ﷺ އަރިހުގައި އެ ކަމަނާ ސުވާލު ކުރެއްވިއެވެ. ނަބިއްޔާ އެންގެވީ (އާންމުކޮށް ހައިޟުވެ އުޅޭ މުއްދަތު ނިމުމުން) އެ ކަމަނާ ހިނައިގަތުމަށެވެ. އަދި ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔައީ ނާރަކުން އަންނަ ލެއެކެވެ.“ (އާއިޝާ ވިދާޅުވިއެވެ:) ފަހެ ކޮންމެ ނަމާދަކަށް ވެސް އެ ކަމަނާ (އަމިއްލަފުޅަށް) ހިނައިގަންނަވައެވެ. (ނޯޓު: ކޮންމެ ފަހަރަކު ނަމާދަށް ހިނައިގަންނަން ނަބިއްޔާ އަމުރެއް ނުކުރައްވައެވެ. އެހެނަސް އަމުރު ކުރެއްވީ ހައިޟުގެ މުއްދަތު ނިމުމުން އެއްފަހަރު ހިނައިގަނެގެން ދެން ނަމާދުކޮށް އުޅުމަށެވެ.)","[البخاري 327 واللفظ له، ومسلم 334]"],["#045","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الحيض","ހައިޟުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا قَالَتْ: «كُنْتُ أَغْتَسِلُ أَنَا وَرَسُولُ اللَّهِ ﷺ مِنْ إنَاءٍ وَاحِدٍ، كِلانا جُنُبٌ.","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ތިމަން ކަމަނާއާއި ރަސޫލު ﷲ ﷺ ޖުނުބުވެރިކަމުގެ ހާލުގައި ވަނިކޮށް، އެއް ކަންވާރަކުން ތިމަން ދެކަނބަލުން ހިނައިގަންނަވަމެވެ.","[البخاري 299 واللفظ له، ومسلم 321]"],["#046","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الحيض","ހައިޟުގެ ބާބު","وَكَانَ يَأْمُرُنِي فَأَتَّزِرُ، فَيُبَاشِرُنِي وَأَنَا حَائِضٌ.","އަދި ތިމަން ކަމަނާގެ ކަމަރުކޮޅު ބާރުކޮށްލައްވަން ނަބިއްޔާ އަންގަވައެވެ. އަދި ތިމަން ކަމަނާ ހައިޟުވެފައިވާ ހާލު ތިމަން ކަމަނާގެ ހަށިކޮޅުގައި ނަބިއްޔާ ބައްދަވާ ބީއްސަވާ ހައްދަވައެވެ.","[البخاري 300 واللفظ له، ومسلم 293]"],["#047","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الحيض","ހައިޟުގެ ބާބު","وَكَانَ يُخْرِجُ رَأْسَهُ إلَيَّ، وَهُوَ مُعْتَكِفٌ، فَأَغْسِلُهُ وَأَنَا حَائِضٌ»","އަދި ނަބިއްޔާ (މިސްކިތުގައި) އިއުތިކާފުގައި ހުންނަވަމުން، ތިމަން ކަމަނާއަށް އިސްތަށިފުޅު ދިއްކުރައްވައެވެ. (އެ ކަމަނާ ހުންނެވީ ގެކޮޅުގައެވެ.) އަދި ތިމަން ކަމަނާ ހައިޟުވެފައިވާ ހާލު ނަބިއްޔާގެ އިސްތަށިފުޅު ދޮންނަވަމެވެ. (ނޯޓު: އޭރު ކޮޓަރިކޮޅު ހުންނަނީ މިސްކިތާ އިންވެގެންނެވެ.)","[البخاري 301 واللفظ له، ومسلم 297]"],["#048","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الحيض","ހައިޟުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا قَالَتْ: «كَانَ رَسُولُ اللَّهِ ﷺ يَتَّكِئُ فِي حِجْرِي، فَيَقْرَأُ الْقُرْآنَ وَأَنَا حَائِضٌ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ތިމަން ކަމަނާ ހައިޟުވެފައިވާ ހާލު، ތިމަން ކަމަނާގެ އުނގުފުޅުމަތީ ރަސޫލު ﷲ ﷺ އިސްތަށިފުޅު އަޅުއްވައިގެން އޮންނަވާ، ޤުރްއާން ކިޔަވައި ވިދާޅުވެއެވެ.","[البخاري 297، ومسلم 301 واللفظ له]"],["#049","‌كتاب الطهارة","ތަހާރާގެ ފޮތް","باب الحيض","ހައިޟުގެ ބާބު","عَنْ مُعَاذَةَ قَالَتْ: «سَأَلْتُ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا فَقُلتُ: مَا بَالُ الْحَائِضِ تَقْضِي الصَّوْمَ، وَلا تَقْضِي الصَّلاةَ؟ فَقَالَتْ: أَحَرُورِيَّةٌ أَنْتِ؟ فَقُلْتُ: لَسْتُ بِحَرُورِيَّةٍ، وَلَكِنِّي أَسْأَلُ. فَقَالَتْ: كَانَ يُصِيبُنَا ذَلِكَ، فَنُؤَمَّرُ بِقَضَاءِ الصَّوْمِ، وَلا نُؤَمَّرُ بِقَضَاءِ الصَّلاةِ»","މުޢާޛާ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا އަރިހު ތިމަން ސުވާލު ކުރީމެވެ: ހައިޟުވެރިޔާ ރޯދަ ގަޟާކޮށްފައި ނަމާދު ގަޟާ ނުކުރަނީ ކީއްވެގެންތޯއެވެ؟ ޢާއިޝާ ވިދާޅުވިއެވެ: ކަނބުލޭގެ ތިޔައީ ޙަރޫރީއެއްތޯއެވެ؟ (އެބަހީ: ޚަވާރިޖުންގެ މީހެއްތޯއެވެ. މިފަދައިން އެ ކަމަނާ ވިދާޅުވީ، އެބައިމީހުން އަމިއްލައަށް ދީނުގެ ކަންކަމުގައި ބުއްދިއަށް ފެންނަ ގޮތަށް ކަންކަން ނިންމައި، ހުކުމްތައް ބަދަލު ކުރާތީއެވެ. އެ ގޮތުން ހައިޟުވެރިން ނަމާދު ވެސް ގަޟާކުރަން ޖެހޭނެކަމަށް އޭގެ ބައެއް މީހުން ބުންޏެވެ.) ތިމަން ދެންނެވީމެވެ: ތިމަންނާއީ ޙަރޫރީއެއް ނޫނެވެ. އެހެނެއްކަމަކު ތިމަން އަހާލީއެވެ. ޢާއިޝާ ވިދާޅުވިއެވެ: ތިމަން ކަނބަލުންނަށް އެ (ހައިޟުވުން) މެދުވެރިވެއެވެ. އަދި ތިމަން ކަނބަލުން ރޯދަ ގަޟާ ކުރަން އަމުރު ވެވިގެންވެއެވެ. އެކަމަކު ނަމާދު ގަޟާ ކުރާކަށް އަމުރު ވެވިގެނެއް ނުވެއެވެ.","[البخاري 321، ومسلم 335 واللفظ له]"],["#050","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","عَنْ أَبِي عَمْرٍو الشَّيْبَانِيِّ وَاسْمُهُ سَعْدُ بْنُ إيَاسٍ - قَالَ: حَدَّثَنِي صَاحِبُ هَذِهِ الدَّارِ - وَأَشَارَ بِيَدِهِ إلَى دَارِ عَبْدِ اللَّهِ بْنِ مَسْعُودٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «سَأَلْتُ النَّبِيَّ ﷺ: أَيُّ الْعَمَلِ أَحَبُّ إلَى اللَّهِ؟ قَالَ: الصَّلاةُ عَلَى وَقْتِهَا. قُلْتُ: ثُمَّ أَيُّ؟ قَالَ: بِرُّ الْوَالِدَيْنِ، قُلْتُ: ثُمَّ أَيُّ؟ قَالَ: الْجِهَادُ فِي سَبِيلِ اللَّهِ، قَالَ: حَدَّثَنِي بِهِنَّ رَسُولُ اللَّهِ ﷺ وَلَوْ اسْتَزَدْتُهُ لَزَادَنِي»","އެކަލޭގެފާނުގެ ނަންފުޅަކީ ސަޢުދު ބިން އިޔާސްކަމުގައިވާ، އަބޫ ޢަމްރު ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އެކަލޭގެފާނުގެ އަތްޕުޅުން ޢަބްދު ﷲ ބިން މަސްޢޫދު رَضِيَ اللَّهُ عَنْهُގެ ގެކޮޅަށް އިޝާރާތް ކުރައްވާފައި ވިދާޅުވިއެވެ: މި ގޭގެ ވެރިޔާ ތިމަންނައަށް ވިދާޅުވިއެވެ: ރަސޫލު ﷲ ﷺ އަރިހު ތިމަން ސުވާލު ކުރީމެވެ: ﷲގެ ހަޒްރަތުގައި އެންމެ ލޮބުވެތިކަން ބޮޑު އަމަލަކީ ކޮބައިތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ނަމާދު އޭގެ ވަގުތުގައި އަދާ ކުރުމެވެ.“ ތިމަން ދެންނެވީމެވެ: އެއަށްފަހު ކޮން ކަމެއްތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”މައިންބަފައިންނަށް ހެޔޮކޮށް ހިތުމެވެ.“ ތިމަން ދެންނެވީމެވެ: އެއަށްފަހު ކޮން ކަމެއްތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲގެ މަގުގައި ޖިހާދު ކުރުމެވެ.“ ޢަބްދު ﷲ ވިދާޅުވިއެވެ: ރަސޫލު ﷲ ﷺ ތިމަންނައަށް ޙަދީޘް ކުރެއްވީ މި ކަންކަމެވެ. އަދި ތިމަން އިތުރަށް އެދުނު ނަމަ، ނަބިއްޔާ އިތުރަށް ޙަދީޘް ކުރެއްވީސްކަން ކަށަވަރެވެ.","[البخاري 527، ومسلم 139]"],["#051","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا قَالَتْ: «لَقَدْ كَانَ رَسُولُ اللَّهِ ﷺ يُصَلِّي الْفَجْرَ، فَيَشْهَدُ مَعَهُ نِسَاءٌ مِنْ الْمُؤْمِنَاتِ، مُتَلَفِّعَاتٍ بِمُرُوطِهِنَّ ثُمَّ يَرْجِعْنَ إلَى بُيُوتِهِنَّ مَا يَعْرِفُهُنَّ أَحَدٌ، مِنْ الْغَلَسِ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ފަތިސް ނަމާދު ކުރައްވާ ހިނދު، މުއުމިނު އަންހެން ކަނބަލުން އެ ނަމާދުގައި ބައިވެރިވެއެވެ. އެ ކަނބަލުން ތިބެނީ ހެދުން ފޮތިކޮޅުތައް ގައިގެ ވަށައި އޮޅާލައި ނިވާވެގެންނެވެ. ދެން (ނަމާދަށް) ފަހު އެ ކަނބަލުން އެނބުރި ގެތަކަށް ދެއެވެ. އަދި (އެ ވަގުތު) އަނދިރިކަމުން ގޮސް އެއްވެސް މީހަކަށް އެ ކަނބަލުން ވަކި ކުރާކަށް ނޭނގެއެވެ.","[البخاري 372، 578 واللفظ له، ومسلم 645]"],["#052","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رَضِيَ اللَّهُ عَنْهُما قَالَ: «كَانَ ﷺ يُصَلِّي الظُّهْرَ بِالْهَاجِرَةِ، وَالْعَصْرَ وَالشَّمْسُ نَقِيَّةٌ وَالْمَغْرِبَ إذَا وَجَبَتْ، وَالْعِشَاءَ أَحْيَاناً وَأَحْيَاناً إذَا رَآهُمْ اجْتَمَعُوا عَجَّلَ. وَإِذَا رَآهُمْ أَبْطَئُوا أَخَّرَ، وَالصُّبْحُ كَانَ النَّبِيُّ ﷺ يُصَلِّيهَا بِغَلَسٍ»","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ މެންދުރު ނަމާދު ކުރައްވަނީ މެންދުރު ގަޑީގައެވެ. އަދި އަސުރު ކުރައްވަނީ އިރުގެ އަލިކަން އަދިވެސް ފާޅުކޮށް ހުންނަ އިރުގައެވެ. އަދި މަޣްރިބު ކުރައްވަނީ އިރު އޮއްސުމަށް ފަހުގައެވެ. އަދި އިޝާ ކުރައްވަނީ އެކި ފަހަރު އެކި ގަޑިތަކުގައެވެ. މީސްތަކުން (އިޝާ ނަމާދަށް) އެއްވެފައި ވަނިކޮށް ނަބިއްޔާއަށް ފެނިވަޑައިގަންނަވާ ނަމަ، ނަމާދު ފެއްޓެވުން އަވަސް ކުރައްވައެވެ. އަދި މީސްތަކުން (ނަމާދަށް) އައުން ލަސްކޮށްފި ނަމަ، ނަބިއްޔާ ވެސް ނަމާދު ލަސް ކުރައްވައެވެ. އަދި އަނދިރިވެފައި ވަނިކޮށް ނަބިއްޔާ ﷺ ފަތިސް ނަމާދު ކުރައްވައެވެ.","[البخاري 547 واللفظ له، ومسلم 647]"],["#053","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","عَنْ أَبِي الْمِنْهَالِ سَيَّارِ بْنِ سَلامَةَ قَالَ: «دَخَلْتُ أَنَا وَأَبِي عَلَى أَبِي بَرْزَةَ الأَسْلَمِيِّ، فَقَالَ لَهُ أَبِي: كَيْفَ كَانَ النَّبِيُّ ﷺ يُصَلِّي الْمَكْتُوبَةَ؟ فَقَالَ: كَانَ يُصَلِّي الْهَجِيرَ - الَّتِي تَدْعُونَهَا الأُولَى - حِينَ تَدْحَضُ الشَّمْسُ، وَيُصَلِّي الْعَصْرَ، ثُمَّ يَرْجِعُ أَحَدُنَا إلَى رَحْلِهِ فِي أَقْصَى الْمَدِينَةِ وَالشَّمْسُ حَيَّةٌ. وَنَسِيتُ مَا قَالَ فِي الْمَغْرِبِ. وَكَانَ يُسْتَحَبُّ أَنْ يُؤَخِّرَ مِنْ الْعِشَاءِ الَّتِي تَدْعُونَهَا الْعَتَمَةَ. وَكَانَ يَكْرَهُ النَّوْمَ قَبْلَهَا، وَالْحَدِيثُ بَعْدَهَا. وَكَانَ يَنْفَتِلُ مِنْ صَلاةِ الْغَدَاةِ حِينَ يَعْرِفُ الرَّجُلَ جَلِيسَهُ. وَكَانَ يَقْرَأُ بِالسِّتِّينَ إلَى الْمِائَةِ»","އަބޫ މިންހާލު ސައްޔާރު ބިން ސަލާމާ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އަބޫ ބަރްޒާ އަލްއަސްލަމީގެ އަރިހަށް، ތިމަންނައާއި ތިމަންނަގެ ބައްޕަ ވަނީމެވެ. އަދި ތިމަންނަގެ ބައްޕަ އެކަލޭގެފާނު އަރިހު ދެންނެވިއެވެ: ރަސޫލު ﷲ ﷺ ފަރުޟު ފަސް ނަމާދު ކުރައްވަނީ ކިހިނެއްތޯއެވެ؟ އަބޫ ބަރްޒާ ވިދާޅުވިއެވެ: ތިޔަބައިމީހުން ފުރަތަމަ ނަމާދު ކަމަށް ބުނާ މެންދުރު ނަމާދު ކުރައްވަނީ އިރު ތިރިވުމުންނެވެ. އެއަށްފަހު އަސުރު ނަމާދު ކުރައްވައެވެ. ދެން ތިމަންމެން ކުރެ މީހަކު، މަދީނާގެ އެންމެ ދުރު ހިސާބުގައިވާ އޭނާގެ ގެއަށް އަނބުރާ ދެވޭހައި ވަގުތު ވަންދެން އިރު ހުރެއެވެ. -(އަބޫ މިންހާލު ވިދާޅުވިއެވެ:) އަދި މަޣްރިބު ނަމާދައި މެދު އަބޫ ބަރްޒާ ވިދާޅުވި އެއްޗެއް ތިމަން ހަނދާން ނެތިއްޖެއެވެ.- (ދެން އަބޫ ބަރްޒާ ވިދާޅުވިއެވެ:) އަދި ތިޔަބައިމީހުން ޢަތަމާ އޭ ތި ކިޔާ އިޝާ ނަމާދު ލަސް ކުރެއްވުމަށް ނަބިއްޔާ ލޯބި ކުރެއްވިއެވެ. އަދި އޭގެ ކުރިން އަވަހާރަފުޅު ލެއްޕެވުމަށާއި އޭގެ ފަހުން ވާހަކަދައްކަވައި ހެއްދުމަށް ނަބިއްޔާ ނުރުއްސަވައެވެ. އަދި ފަތިސް ނަމާދުކޮށްގެން ނުކުމެވަޑައިގަންނަވަނީ، މީހަކަށް އޭނާއާ ޖެހިގެން އިށީންދެ އިން މީހާ ދެނެގަނެވޭހައި އިރުވެ (އަލިވުމުންނެވެ.) އަދި (އެ ނަމާދުގައި) ފަސްދޮޅަސް އާޔަތާއި ސަތޭކަ އާޔަތާއި ދެމެދު (ޤުރްއާން) ކިޔަވައި ވިދާޅުވެއެވެ.","[البخاري 560 واللفظ له، ومسلم 646]"],["#054","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","عَنْ عَلِيٍّ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ النَّبِيَّ ﷺ قَالَ يَوْمَ الْخَنْدَقِ: «مَلأَ اللَّهُ قُبُورَهُمْ وَبُيُوتَهُمْ نَارًا، كَمَا شَغَلُونَا عَنْ الصَّلاةِ الْوُسْطَى حَتَّى غَابَتْ الشَّمْسُ» \n\nوَفِي لَفْظٍ لِمُسْلِمٍ «شَغَلُونَا عَنْ الصَّلاةِ الْوُسْطَى - صَلاةِ الْعَصْرِ - ثُمَّ صَلاهَا بَيْنَ الْمَغْرِبِ وَالْعِشَاءِ»","ޢަލީ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺ ޚަންދަޤު ހަނގުރާމައިގެ ދުވަހު ޙަދީޘް ކުރެއްވިއެވެ: ”مَلأَ اللَّهُ قُبُورَهُمْ وَبُيُوتَهُمْ نَارًا (އެ (މުޝްރިކުންގެ) ކަށްވަޅުތަކާއި ގެތައް އަލިފާނުން ފުރުއްވާށިއެވެ.) އެއީ އިރު އޮއްސުމަށް ދާންދެން، އެބައިމިހުން ތިމަންމެން އަސުރު ނަމާދު އަދާ ކުރިޔަ ނުދީ (ހަނގުރާމައިގައި) މަޝްޣޫލު ކުރުވީތީއެވެ.“ \n\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ”އެބައިމިހުން ތިމަންމެން މެދު ނަމާދު (އެބަހީ: އަސުރު ނަމާދު) ކުރިޔަ ނުދީ (ހަނގުރާމައިގައި) މަޝްޣޫލު ކުރުވައިފިއެވެ.“ އެއަށްފަހު މަޣްރިބާއި އިޝާއާ ދެމެދު ނަބިއްޔާ އެ އަސުރު ނަމާދު އަދާ ކުރެއްވިއެވެ.","[البخاري 6396 واللفظ له، ومسلم 627\n\nمسلم 627]"],["#055","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","وَلَهُ عَنْ عَبْدِ اللَّهِ بْنِ مَسْعُودٍ قَالَ: «حَبَسَ الْمُشْرِكُونَ رَسُولَ اللَّهِ ﷺ عَنِ الْعَصْرِ، حَتَّى احْمَرَّتِ الشَّمْسُ أَوْ اصْفَرَّتْ، فَقَالَ رَسُولُ اللَّهِ ﷺ: شَغَلُونَا عَنْ الصَّلاةِ الْوُسْطَى - صَلاةِ الْعَصْرِ - مَلأَ اللَّهُ أَجْوَافَهُمْ وَقُبُورَهُمْ نَاراً، أَوْ حَشَا اللَّهُ أَجْوَافَهُمْ وَقُبُورَهُمْ نَاراً»","އަދިވެސް މުސްލިމުގެ ރިވާޔަތެއްގައި، ޢަބްދު ﷲ ބިން މަސްޢޫދު ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އިރު ރަތްވެ ނުވަތަ ރީނދޫ ވެއްޖައުމަށް ދާންދެން، (ހަނގުރާމައިގައި) މުޝްރިކުން ރަސޫލު ﷲ ﷺ އަސުރު ނަމާދު ކުރިޔަ ނުދެއްވާ ހިފެހެއްޓިއެވެ. ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އެބައިމިހުން ތިމަންމެން މެދު ނަމާދު (އެބަހީ: އަސުރު ނަމާދު) އަދާ ކުރިޔަ ނުދީ (ހަނގުރާމައިގައި) މަޝްޣޫލު ކުރުވައިފިއެވެ. مَلأَ اللَّهُ أَجْوَافَهُمْ وَقُبُورَهُمْ نَاراً. (ނުވަތަ ޙަދީޘް ކުރެއްވިއެވެ:) حَشَا اللَّهُ أَجْوَافَهُمْ وَقُبُورَهُمْ نَاراًﷲ (އެބައިމީހުންގެ ބަނޑުތަކާއި ކަށްވަޅުތައް އަލިފާނުން ފުރުއްވާށިއެވެ.)“","[مسلم 628]"],["#056","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: «أَعْتَمَ النَّبِيُّ ﷺ بِالْعِشَاءِ. فَخَرَجَ عُمَرُ، فَقَالَ: الصَّلاةُ، يَا رَسُولَ اللَّهِ. رَقَدَ النِّسَاءُ وَالصِّبْيَانُ. فَخَرَجَ وَرَأْسُهُ يَقْطُرُ يَقُولُ: لَوْلا أَنْ أَشُقَّ عَلَى أُمَّتِي - أَوْ عَلَى النَّاسِ - لأَمَرْتُهُمْ بِهَذِهِ الصَّلاةِ هَذِهِ السَّاعَةِ»","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: (އެއް ރެއަކު) ނަބިއްޔާ ﷺ އިޝާ ނަމާދު ފެއްޓެވުން ލަސް ކުރެއްވިއެވެ. ދެން ޢުމަރު ނުކުމެ ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ނަމާދެވެ. އަންހެނުންނާއި ކުޑަކުދިން ނިދިޖެހިއްޖެއެވެ. ދެން ނަބިއްޔާ ﷺގެ އިސްތަށިފުޅުން ފެންތިކި ޖަހަނިކޮށް ނުކުމެވަޑައިގެން ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާގެ އުންމަތުގެ މީސްތަކުންގެ މައްޗަށް ދަތި ކަމަކަށް ނުވާނަމަ، މި ނަމާދު މި ގަޑީގައި ކުރުމަށް އެބައިމިހުންނަށް އަމުރު ކުރެއްވީސްކަން ކަށަވަރެވެ.“","[البخاري 7239 واللفظ له، ومسلم 206]"],["#057","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","عَنْ عائِشَةَ رَضِيَ اللَّهُ عَنْهَا: أَنَّ النَّبِيَّ ﷺ قَالَ: «إذَا أُقِيمَتْ الصَّلاةُ، وَحَضَرَ الْعَشَاءُ، فَابْدَءُوا بِالْعَشَاءِ»\n\nوَعَنْ ابْنِ عُمَرَ نَحْوُهُ.","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ނަމާދަށް ގަމަތް ދެވޭ ހިނދު، ރޭގަނޑުގެ ކެއުން ތައްޔާރުވެ ގެނެވިއްޖެ ނަމަ، ފުރަތަމަ ރޭގަނޑުގެ ކެއުމުން ތިޔަބައިމީހުން ފަށާށެވެ.“\n\nއަދި އިބްނު ޢުމަރުގެ އަރިހުންވެސް މި ފަދައިން ރިވާވެފައިވެއެވެ.","[البخاري 5465 واللفظ له، ومسلم 558\n\nالبخاري 673، ومسلم 559]"],["#058","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","وَلِمُسْلِمٍ عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا قَالَتْ: سَمِعْتُ رَسُولَ اللَّهِ ﷺ يقُولُ: «لا صَلاةَ بِحَضْرَةِ طَعَامٍ، وَلا وَهُوَ يُدَافِعُهُ الأَخْبَثَانِ»","އަދި މުސްލިމުގެ ރިވާޔަތެއްގައި، ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން ކަމަނާ އަޑު އެއްސެވީމެވެ: ”ކާތަކެތި ތައްޔާރުވެ ގެނެވިފައި ވަނިކޮށް ނަމާދު ކުރުމެއް ނުވެއެވެ. އަދި (ހަމަ އެފަދައިން) ކުޑަކަމުދިއުމާއި ބޮޑުކަމުދިއުން ހިފަހައްޓާ ހާލު ވެސް (ނަމާދު ކުރުމެއް) ނުވެއެވެ.","[مسلم 560]"],["#059","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: «شَهِدَ عِنْدِي رِجَالٌ مَرْضِيُّونَ - وَأَرْضَاهُمْ عِنْدِي عُمَرُ - أَنَّ النَّبِيَّ ﷺ نَهَى عَنْ الصَّلاةِ بَعْدَ الصُّبْحِ حَتَّى تَطْلُعَ الشَّمْسُ، وَبَعْدَ الْعَصْرِ حَتَّى تَغْرُبَ»","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: (އެބައިމީހުންގެ ތެދުވެރިކަމާއި ދީންވެރިކަމާއި މެދު ތިމަން) ރުހޭ ބޭކަލުންތަކެއް ތިމަން ގާތު މިފަދައިން ހެކިވިއެވެ. -އަދި އެބައިމީހުންގެ ތެރެއިން ތިމަންނަ އެންމެ ރުހޭ ބޭކަލަކީ ޢުމަރެވެ.- ފަތިސް ނަމާދަށް ފަހު އިރު އަރައިފުމަށް ދާންދެން ނަމާދު ކުރެއްވުން ނަބިއްޔާ ﷺ ނަހީ ކުރެއްވިއެވެ. އަދި (ހަމަ އެފަދައިން) އަސުރު ނަމާދަށް ފަހު އިރު އޮއްސުމަށް ދާންދެން ވެސް މެއެވެ.","[البخاري 581 واللفظ له، ومسلم 826]"],["#060","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ - رَضِيَ اللَّهُ عَنْهُ - عَنْ رَسُولِ اللَّهِ ﷺ أَنَّهُ قالَ: «لا صَلاةَ بَعْدَ الصُّبْحِ حَتَّى تَرْتَفِعَ الشَّمْسُ، وَلا صَلاةَ بَعْدَ الْعَصْرِ حَتَّى تَغِيبَ الشَّمْسُ»\n\nوفي البابِ عنْ عليِّ بنِ أَبي طالبٍ، وعبدِ اللهِ بنِ مسعودٍ، وعبدِ اللهِ بنِ عمرَ بنِ الخطابِ، وعبدِ اللهِ بنِ عمرِو بنِ العاصِ، وأَبي هريرةَ، وسَمُرَةَ بنِ جُندُبِ، وسَلَمَةَ بنِ الأَكوَعِ، وزيدِ بنِ ثابتٍ ومعاذِ بنِ جبلٍ، ومعاذِ بنِ عفراء، وكعبِ بنِ مُرَّةَ، وأَبي أُمامةَ الباهليِّ، وعمرِو بنِ عبسةَ السُلَميِّ، وعائشةَ رَضِيَ اللَّهُ عَنْهُم، والصَّنابحيِّ، ولم يسمعْ منَ النبيِّ ﷺ.","އަބޫ ސަޢީދު އަލްޚުދްރީ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ފަތިސް ނަމާދަށް ފަހު އިރު އަރައިފުމަށް ދާންދެން ނަމާދު ކުރުމެއް ނުވެއެވެ. އަދި އަސުރު ނަމާދަށް ފަހު އިރު އޮއްސުމަށް ދާންދެން ނަމާދު ކުރުމެއް ނުވެއެވެ.\n\n(ފޮތުގެ މުއައްލިފު ވިދާޅުވިއެވެ:) އަދި ޢަލީ ބިން އަބީ ޠާލިބާއި، ޢަބްދު ﷲ ބިން މަސްޢޫދާއި، ޢަބްދު ﷲ ބިން ޢުމަރު ބިން އަލްޚައްޠާބާއި، ޢަބްދު ﷲ ބިން ޢަމްރު ބިން އަލްޢާޞްއާއި، އަބޫ ހުރައިރާއާއި، ސަމުރާ ބިން ޖުންދުބާއި، ސަލަމާ ބިން އަލްއަކްވަޢުއާއި، ޒައިދު ބިން ޘާބިތާއި، މުޢާޛު ބިން ޖަބަލާއި، މުޢާޛު ބިން އަފްރާޢުއާއި، ކައުބް ބިން މުއްރާއާއި، އަބޫ އުމާމާ އަލްބާހިލީއާއި، ޢަމްރު ބިން ޢަބަސާ އައްސުލަމީއާއި، ޢާއިޝާ رَضِيَ اللَّهُ عَنْهُمْގެ އަރިހުން ރިވާޔަތްތައް މި ކަމާ ގުޅޭ ގޮތުން އައިސްފައިވެއެވެ. އަދި އައްޞަނާބިޙީގެ އަރިހުން ރިވާޔަތެއް ވެސް އައިސްފައި ވަނިކޮށް، ނަބިއްޔާގެ އަރިހުން އެކަލޭގެފާނު (ސީދާ) އަޑުއަހާފައެއް ނުވެއެވެ.","[البخاري 586 واللفظ له، ومسلم 827]"],["#061","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب المواقيت","ނަމާދު ވަގުތުތަކުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رَضِيَ اللَّهُ عَنْهُما «أَنَّ عُمَرَ بْنَ الْخَطَّابِ رَضِيَ اللَّهُ عَنْهُ جَاءَ يَوْمَ الْخَنْدَقِ بَعْدَ مَا غَرَبَتِ الشَّمْسُ فَجَعَلَ يَسُبُّ كُفَّارَ قُرَيْشٍ، وَقَالَ: يَا رَسُولَ اللَّهِ، مَا كِدْتُ أُصَلِّي الْعَصْرَ حَتَّى كَادَتِ الشَّمْسُ تَغْرُبُ. فَقَالَ النَّبِيُّ ﷺ: وَاَللَّهِ مَا صَلَّيْتُهَا. قَالَ: فَقُمْنَا إلَى بَطْحَانَ، فَتَوَضَّأَ لِلصَّلاةِ، وَتَوَضَّأْنَا لَهَا، فَصَلَّى الْعَصْرَ بَعْدَ مَا غَرَبَتْ الشَّمْسُ. ثُمَّ صَلَّى بَعْدَهَا الْمَغْرِبَ»","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ޚަންދަގު ހަނގުރާމައިގެ ދުވަހު، އިރު އޮއްސުމަށް ފަހު ޢުމަރު ބިން އަލްޚައްޠާބު ވަޑައިގެން ގުރައިޝުންގެ ކާފަރުންނަށް ބަދުބަސް ވިދާޅުވަން ފެއްޓެވިއެވެ. އަދި ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. އިރު އޮއްސުމަށް ދާންދެން ވެސް ތިމަންނައަށް އަސުރު ނަމާދެއް ނުކުރެވުނެވެ. ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲ ގަންދީ ބުނަމެވެ. ތިމަން ނަބިއްޔާއަށް ވެސް އެ ނަމާދެއް ނުކުރެވެއެވެ.“ އެއަށްފަހު ތިމަންމެން (މަދީނާގެ ވާދީއެއް ކަމަށްވާ) ބަޠުޙާނަށް ދިޔުމުން، ނަބިއްޔާ ނަމާދަށް ވުޟޫ ކުރެއްވިއެވެ. އަދި ތިމަންމެން ވެސް ނަމާދަށް ވުޟޫ ކުރީމެވެ. ދެން ނަބިއްޔާ އަސުރު ނަމާދު ކުރެއްވީ އިރު އޮއްސުމަށް ފަހުގައެވެ. އެއަށްފަހު ނަބިއްޔާ މަޣްރިބު ނަމާދު ކުރެއްވިއެވެ.","[البخاري 596 واللفظ له، ومسلم 631]"],["#062","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب فضل الجماعة ووجوبها","ޖަމާޢަތުގައި ނަމާދު ކުރުމުގެ ފަޟްލުވެރިކަމާއި އޭގެ ވާޖިބުކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «صَلاةُ الْجَمَاعَةِ أَفْضَلُ مِنْ صَلاةِ الْفَذِّ بِسَبْعٍ وَعِشْرِينَ دَرَجَةً»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އެކަނި ހުރެ ކުރާ ނަމާދަށް ވުރެ ޖަމާޢަތުގައި ކުރާ ނަމާދު ހަތާވީސް ގުނައަށް ދަރަޖަ އިތުރުވެގެން ވެއެވެ.“","[البخاري 645، ومسلم 650 واللفظ له]"],["#063","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب فضل الجماعة ووجوبها","ޖަމާޢަތުގައި ނަމާދު ކުރުމުގެ ފަޟްލުވެރިކަމާއި އޭގެ ވާޖިބުކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «صَلاةُ الرَّجُلِ فِي جَمَاعَةٍ تُضَعَّفُ عَلَى صَلاتِهِ فِي بَيْتِهِ وَفِي سُوقِهِ خَمْساً وَعِشْرِينَ ضِعْفاً، وَذَلِكَ: أَنَّهُ إذَا تَوَضَّأَ، فَأَحْسَنَ الْوُضُوءَ. ثُمَّ خَرَجَ إلَى الْمَسْجِدِ لا يُخْرِجُهُ إلا الصَّلاةُ لَمْ يَخْطُ خَطْوَةً إلا رُفِعَتْ لَهُ بِهَا دَرَجَةٌ، وَحُطَّ عَنْهُ خَطِيئَةٌ. فَإِذَا صَلَّى لَمْ تَزَلْ الْمَلائِكَةُ تُصَلِّي عَلَيْهِ، مَا دَامَ فِي مُصَلاهُ: اللَّهُمَّ صَلِّ عَلَيْهِ، اللَّهُمَّ اغْفِرْ لَهُ، اللَّهُمَّ ارْحَمْهُ، وَلا يَزَالُ فِي صَلاةٍ مَا انْتَظَرَ الصَّلاةَ»","އަބޫ ހުރައިރާ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ޖަމާޢަތުގައި މީހަކު ކުރާ ނަމާދު، ގޭގައި ނުވަތަ ބާޒާރުގައި އެކަނި ހުރެ ކުރާ ނަމާދަށް ވުރެ ފަންސަވީސް ގުނަ އިތުރު ވެގެންވެއެވެ. އެހެނީ އޭނާ ވުޟޫ ކުރާ ހިނދު އެންމެ ފުރިހަމަ ގޮތުގައި ވުޟޫ ކުރުމަށް ފަހު، ނުކުމެ މިސްކިތަށް ދާއިރު، އޭނާ ނުކުންނަ ހަމައެކަނި މަގުސަދަކީ ނަމާދު ކުރުން ކަމުގައި ވާނަމަ، ފަހެ އޭނާ އަޅާ ކޮންމެ ފިޔަވަޅަކަށް، އޭގެ ސަބަބުން ދަރަޖައެއް އުފުލެވި އަދި ފާފައެއް ފޮހެވިގެން މެނުވީ ފިޔަވަޅެއް ނާޅާނެއެވެ. އަދި އޭނާ ނަމާދު ކުރާ އިރު، އޭނާގެ އެ ނަމާދު ކުރާ ތަނުގައި ހުރި ހައި ހިނދަކު މަލާއިކަތުން އޭނާއަށް ސަލަވާތް ލައްވައި (މިފަދައިން) ދުއާ ކުރައްވާނެއެވެ: اللَّهُمَّ صَلِّ عَلَيْهِ، اللَّهُمَّ اغْفِرْ لَهُ، اللَّهُمَّ ارْحَمْهُ (ޔާ ﷲ! އޭނާގެ މައްޗަށް ސަލަވާތް ލައްވާށިއެވެ. ޔާ ﷲ! އޭނާގެ ފާފަ ފުއްސަވާށިއެވެ. ޔާ ﷲ! އޭނާއަށް ރަހުމަތް ލައްވާށިއެވެ.) އަދި އޭނާ ނަމާދަށް އިންތިޒާރު ކުރާހައި ހިނދަކު، އޭނަ އެ ވަނީ ނަމާދުގެ މަތީގައެވެ.“","[البخاري 647 واللفظ له، ومسلم 649]"],["#064","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب فضل الجماعة ووجوبها","ޖަމާޢަތުގައި ނަމާދު ކުރުމުގެ ފަޟްލުވެރިކަމާއި އޭގެ ވާޖިބުކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «أَثْقَلُ الصَّلاةِ عَلَى الْمُنَافِقِينَ: صَلاةُ الْعِشَاءِ، وَصَلاةُ الْفَجْرِ. وَلَوْ يَعْلَمُونَ مَا فِيهَا لأَتَوْهُمَا وَلَوْ حَبْوًا. وَلَقَدْ هَمَمْتُ أَنْ آمُرَ بِالصَّلاةِ فَتُقَامَ، ثُمَّ آمُرَ رَجُلاً فَيُصَلِّيَ بِالنَّاسِ، ثُمَّ أَنْطَلِقَ مَعِي بِرِجَالٍ مَعَهُمْ حُزَمٌ مِنْ حَطَبٍ إلَى قَوْمٍ لا يَشْهَدُونَ الصَّلاةَ، فَأُحَرِّقَ عَلَيْهِمْ بُيُوتَهُمْ بِالنَّارِ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން މުނާފިގުންގެ މައްޗަށް އެންމެ ބުރަ އުނދަގޫ ނަމާދަކީ އިޝާ ނަމާދާއި ފަތިސް ނަމާދެވެ. އަދި އެބައިމީހުންނަށް އެ ދެ ނަމާދުގައިވާ (ސަވާބު) އެނގުނު ނަމަ، ފިރުކިގަނެގެން ނަމަވެސް އެ ދެ ނަމާދަށް އެބައިމީހުން އައީހެވެ. އަދި ހަމަކަށަވަރުން (މިފަދައިން ކަންތައް ކުރައްވަން) ތިމަން ނަބިއްޔާގެ ހިތްޕުޅަށް އަރައިވަޑައިގަތެވެ: ނަމާދަށް ގަމަތް ދިނުމަށް ތިމަން ނަބިއްޔާ އަންގަވާނަމެވެ. ދެން މީސްތަކުންނައިގެން ނަމާދު ކުރުމަށް މީހަކަށް އަންގަވާނަމެވެ. އެއަށްފަހު އަލިފާން ހުޅުކޮޅުތަކެއް ހިފައިގެން ތިބި ފިރިހެނުންތަކަކާއެކު ނުކުމެ ވަޑައިގެން، ނަމާދަށް ހާޒިރު ނުވާ މީހުންގެ ގާތަށް ގޮސް، އެބައިމީހުންގެ (ބޯތަކު) މައްޗަށް އެބައިމީހުންގެ ގެތައް ރޯ ކުރައްވާނަމެވެ.“","[البخاري 644، ومسلم 651 واللفظ له]"],["#065","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب فضل الجماعة ووجوبها","ޖަމާޢަތުގައި ނަމާދު ކުރުމުގެ ފަޟްލުވެރިކަމާއި އޭގެ ވާޖިބުކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما عَنِ النَّبيِّ ﷺ قَالَ: «إذَا اسْتَأْذَنَتْ أَحَدَكُمْ امْرَأَتُهُ إلَى الْمَسْجِدِ فَلا يَمْنَعُهَا. قَالَ: فَقَالَ بِلالُ بْنُ عَبْدِ اللَّهِ: وَاَللَّهُ لَنَمْنَعَهُنَّ. قَالَ: فَأَقْبَلَ عَلَيْهِ عَبْدُ اللَّهِ، فَسَبَّهُ سَبّاً سَيِّئاً، مَا سَمِعْتُهُ سَبَّهُ مِثْلَهُ قَطُّ، وَقَالَ: أُخْبِرُكَ عَنْ رَسُولِ اللَّهِ ﷺ وَتَقُولُ: وَاَللَّهُ لَنَمْنَعَهُنَّ؟»\n\nوَفِي لَفْظٍ «لا تَمْنَعُوا إمَاءَ اللَّهِ مَسَاجِدَ اللَّهِ»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ ތެރެއިން މީހެއްގެ އަނބިމީހާ، އެ މީހަކު ގާތު މިސްކިތަށް ދިޔުމަށް ހުއްދައަށް އެދިއްޖެ ނަމަ، ފަހެ އެ ކަނބުލޭގެ (މިސްކިތަށް ދިޔުމުން) މަނާ ނުކުރާށެވެ.“ (ޙަދީޘްގެ ރާވީ ވިދާޅުވިއެވެ: ޢަބްދު ﷲ ބިން ޢުމަރުގެ ދަރިކަލުން) ބިލާލު ބިން ޢަބްދު ﷲ ދެންނެވިއެވެ: ﷲ ގަންދީ ބުނަމެވެ. ހަމަކަށަވަރުން އެ ކަނބަލުން (މިސްކިތަށް ދިޔުން) މަނާ ކުރާނަމެވެ. ފަހެ ޢަބްދު ﷲ ބިން ޢުމަރު އެކަލޭގެފާނާ ކުރިމަތިލެއްވުމަށް ފަހު، މީގެ ކުރިން ދުވަހަކުވެސް އިބްނު ޢުމަރު ވިދާޅުވަނިކޮށް ތިމަން އަޑުނާހާ ހަރުކަށި ބަސްތަކަކުން އެކަލޭގެފާނަށް ބަދުބަސް ވިދާޅުވިއެވެ. އެއަށްފަހު ޢިބްނު ޢުމަރު ވިދާޅުވިއެވެ: ރަސޫލު ﷲ ﷺގެ އަރިހުން އިވުނު ވާހަކަފުޅެއް ތިބާއަށް ކިޔައިދިނުމުން، ތިޔަ ބުނަނީ: ﷲ ގަންދީ ބުނަމޭ، ހަމަކަށަވަރުން އެ ކަނބަލުން (މިސްކިތަށް ދިޔުމުން) މަނާ ކުރާނަމޭހެއްޔެވެ؟\n\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ”ތިޔަބައިމީހުން ﷲގެ އަންހެން އަޅުތަކުން އެ އިލާހުގެ މިސްކިތްތަކުން މަނާ ނުކުރާށެވެ.“","[البخاري 5238، ومسلم 442\n\nالبخاري 900، ومسلم 442]"],["#066","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب فضل الجماعة ووجوبها","ޖަމާޢަތުގައި ނަމާދު ކުރުމުގެ ފަޟްލުވެރިކަމާއި އޭގެ ވާޖިބުކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما قَالَ: «صَلَّيْتُ مَعَ رَسُولِ اللَّهِ ﷺ رَكْعَتَيْنِ قَبْلَ الظُّهْرِ، وَرَكْعَتَيْنِ بَعْدَهَا، وَرَكْعَتَيْنِ بَعْدَ الْجُمُعَةِ، وَرَكْعَتَيْنِ بَعْدَ الْمَغْرِبِ، وَرَكْعَتَيْنِ بَعْدَ الْعِشَاءِ» \n\nوَفِي لَفْظِ: «فَأَمَّا الْمَغْرِبُ وَالْعِشَاءُ وَالْجُمُعَةُ: فَفِي بَيْتِهِ» \n\nوَفِي لَفْظٍ: أَنَّ ابْنَ عُمَرَ قَالَ: حَدَّثَتْنِي حَفْصَةُ: أَنَّ النَّبِيَّ ﷺ: «كَانَ يُصَلِّي سَجْدَتَيْنِ خَفِيفَتَيْنِ بَعْدَمَا يَطْلُعُ الْفَجْرُ. وَكَانَتْ سَاعَةً لا أَدْخُلُ عَلَى النَّبِيِّ ﷺ فِيهَا»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺގެ އަރިހުގައި މެންދުރު ނަމާދުގެ ކުރިން ދެ ރަކުއަތް ތިމަން ކުރަމެވެ. އަދި މެންދުރު ނަމާދުގެ ފަހުން ވެސް ދެ ރަކުއަތް ކުރަމެވެ. އަދި ހުކުރު ނަމާދަށް ފަހު ދެ ރަކުއަތް ކުރަމެވެ. އަދި މަޣްރިބު ނަމާދަށް ފަހު ދެ ރަކުއަތް ކުރަމެވެ. އަދި އިޝާ ނަމާދަށް ފަހު ދެ ރަކުއަތް ކުރަމެވެ. \n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ފަހެ މަޣްރިބާއި އިޝާއާއި ހުކުރު ނަމާދުގެ (ސުންނަތް ނަމާދުތައް) ނަބިއްޔާ ކުރައްވަނީ ގެކޮޅުގައެވެ.\n\nއަދިވެސް އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ޙަފްޞާ رَضِيَ اللَّهُ عَنْهَا ރިވާ ކުރެއްވިއެވެ: ފަތިސްވުމުން ނަބިއްޔާ ﷺ ލުއި ދެ ރަކުއަތެއް ކުރައްވައެވެ. އަދި އެއީ ނަބިއްޔާ ﷺގެ އަރިހަށް ތިމަން ކަމަނާ ނުވަންނަ ގަޑިއެކެވެ.","[البخاري 1165 واللفظ له، ومسلم 729\n\nالبخاري 1172، ومسلم 729\n\nالبخاري 1173]"],["#067","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب فضل الجماعة ووجوبها","ޖަމާޢަތުގައި ނަމާދު ކުރުމުގެ ފަޟްލުވެރިކަމާއި އޭގެ ވާޖިބުކަމުގެ ބާބު","عنْ عائِشَةَ رضِيَ اللهُ عنْها قالَتْ: «لَمْ يَكُنِ النَّبِيُّ ﷺ على شيءٍ منَ النَّوافِلِ تَعاهُدَاً منْهُ على ركْعَتَي الفَجْرِ» \n\nوفي لفْظٍ لِمُسْلِمٍ: «رَكْعَتا الفَجْرِ خيرٌ منَ الدُّنيا وما فيها»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ ފަތިސް ނަމާދުގެ އިހު ދެ ރަކުއަތް ކުރެއްވުން މަތީ ދެމިހުންނެވި ވަރަކަށް، އެހެން އެއްވެސް ސުންނަތް ނަމާދެއް ކުރެއްވުން މަތީ ދެމި ނުހުންނަވައެވެ. \n\nއަދި މުސްލިމުގެ ރިވާޔަތުގައި ވެއެވެ: (ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ:) ”ފަތިސް ނަމާދުގެ އިހު ދެ ރަކުއަތް މުޅި ދުނިޔެއާއި އޭގައި ވާހައި އެއްޗަކަށް ވުރެ ހެޔޮވެގެންވެއެވެ.“","[البخاري 1169 واللفظ له، ومسلم 724\n\nمسلم 725]"],["#068","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الأذان والإقامة","ބަންގިއާއި ގަމަތުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ رَضِيَ اللَّهُ عَنْهُ قَالَ: «أُمِرَ بِلالٌ أَنْ يَشْفَعَ الأَذَانَ، وَيُوتِرَ الإِقَامَةَ»","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ބަންގީގައި ކިޔާ ތަކެތި ދެ ފަހަރު ކިޔުމަށާއި، ގަމަތުގައި ކިޔާ ތަކެތި އެއްފަހަރު ކިޔުމަށް ބިލާލަށް އެންގެވުނެވެ.","[البخاري 603، 605، 606، 607، ومسلم 378 واللفظ له]"],["#069","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الأذان والإقامة","ބަންގިއާއި ގަމަތުގެ ބާބު","عَنْ أَبِي جُحَيْفَةَ وَهْبِ بْنِ عَبْدِ اللَّهِ السُّوَائِيِّ قَالَ: «أَتَيْتُ النَّبِيَّ ﷺ وَهُوَ فِي قُبَّةٍ لَهُ حَمْرَاءَ مِنْ أَدَمٍ - قَالَ: فَخَرَجَ بِلالٌ بِوَضُوءٍ، فَمِنْ نَاضِحٍ وَنَائِلٍ، قَالَ: فَخَرَجَ النَّبِيُّ ﷺ عَلَيْهِ حُلَّةٌ حَمْرَاءُ، كَأَنِّي أَنْظُرُ إلَى بَيَاضِ سَاقَيْهِ، قَالَ: فَتَوَضَّأَ وَأَذَّنَ بِلالٌ، قَالَ: فَجَعَلْتُ أَتَتَبَّعُ فَاهُ هَهُنَا وَهَهُنَا، يَقُولُ يَمِيناً وَشِمَالاً: حَيَّ عَلَى الصَّلاةِ ; حَيَّ عَلَى الْفَلاحِ ثُمَّ رُكِزَتْ لَهُ عَنَزَةٌ، فَتَقَدَّمَ وَصَلَّى الظُّهْرَ رَكْعَتَيْنِ، ثُمَّ نَزَلَ يُصَلِّي رَكْعَتَيْنِ حَتَّى رَجَعَ إلَى الْمَدِينَةِ»","އަބޫ ޖުޙައިފާ، ވަހްބު ބިން ޢަބްދު ﷲ އައްސުވާއީ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ (މައްކާގައި) އެކަލޭގެފާނުގެ ޖަނަވާރު ހަމުން ހެދިފައިވާ ރަތްކުލައިގެ ފޭލިގެކޮޅެއްގައި ވަނިކޮށް، އެކަލޭގެފާނުގެ އަރިހަށް ތިމަން އައީމެވެ. އެ ހިނދު ފެން ކަންވާރެއް ހިއްޕަވައިގެން ބިލާލު ނުކުންނެވިއެވެ. ފަހެ (ނަބިއްޔާ ވުޟޫ ކުރެއްވުމުން، އެ ފެނުން ބާކީވި އެއްޗަކުން ބަރަކާތް ހޯދުމަށް) ގައިގައި ފެން އުނގުޅާ ހެދި ބަޔަކާއި، (އެ ފުރަތަމަ ފެން އުނގުޅާ ހެދި) މީހުންގެ އަތުން (ދެވަނަ ފަހަރަށް ބާކީވި ފެން) ލިބުނު ބަޔަކު ވެސް ވިއެވެ. ދެން ނަބިއްޔާ ﷺ ރަތް ފޭރާންކޮޅެއް ތުރުކުރައްވައިގެން ނުކުމެވަޑައިގަތެވެ. ނަބިއްޔާގެ ކަށިމަތިފުޅުގެ ދޮންކަން ތިމަންނައަށް މިހާރުވެސް ފެންނަ ފަދައެވެ. ދެން ނަބިއްޔާ ވުޟޫ ކުރެއްވިއެވެ. އަދި ބިލާލު ބަންގި ދެއްވިއެވެ. ފަހެ ބިލާލު ކަނާތަށާއި ވާތަށް އަނގަފުޅު އަނބުރައްވަމުން ’ނަމާދަށް އަވަހަށް އަންނާށޭ، ކާމިޔާބަށް އަވަހަށް އަންނާށޭ‘ މިފަދައިން ކިޔުއްވިތަން ތިމަން ބަލަން ހުރީމެވެ. ދެން ނަބިއްޔާއަށް (ސުތުރާއެއްގެ ގޮތުގައި) ލޮންސިކޮޅެއް ބިންމަތީ ހަރު ކުރެވުނެވެ. އަދި މެންދުރު ނަމާދު (ގަސްރުކޮށް) ދެ ރަކުއަތް ގޮތުގައި ކުރެއްވިއެވެ. އެއަށްފަހު މަދީނާއަށް އެނބުރި ވަޑައިގެންނެވެން ދެން (ދަތުރު މަތީ މަޑު ކުރެއްވި ކޮންމެ ތަނަކު ނަމާދު ކުރެއްވީ) ދެ ރަކުއަތަށް (ގަސްރު ކުރައްވައިގެންނެވެ.)","[البخاري 187، ومسلم 503 واللفظ له]"],["#070","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الأذان والإقامة","ބަންގިއާއި ގަމަތުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما عَنْ رَسُولِ اللَّهِ - ﷺ أَنَّهُ قَالَ: «إنَّ بِلالاً يُؤَذِّنُ بِلَيْلٍ، فَكُلُوا وَاشْرَبُوا حَتَّى تَسْمَعُوا أَذَانَ ابْنِ أُمِّ مَكْتُومٍ»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ބިލާލު ރޭގަނޑު (ފަތިސް ނުވަނީސް) ބަންގި ގޮވައެވެ. ފަހެ އިބްނު އުންމު މަކްތޫމް ބަންގި ގޮވަންދެން ތިޔަބައިމީހުން ކައިބޮއެ ހަދާށެވެ.“","[البخاري 617، ومسلم 1092 واللفظ له]"],["#071","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الأذان والإقامة","ބަންގިއާއި ގަމަތުގެ ބާބު","عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «إذَا سَمِعْتُمْ الْمُؤَذِّنَ فَقُولُوا مِثْلَ مَا يَقُولُ»","އަބޫ ސަޢީދު އަލްޚުދްރީ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންނަށް ބަންގި (ދޭމީހާގެ) އަޑު އިވިއްޖެނަމަ، ފަހެ (ބަންގިދޭމީހާ) ކިޔާ ފަދައިން (އަޑުދަށުން) ކިޔާށެވެ.“","[البخاري 611، ومسلم 383]"],["#072","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب استقبال القبلة","ގިބުލައަށް ކުރިމަތިލުމުގެ ބާބު","عَنْ ابْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما: «أَنَّ رَسُولَ اللَّهِ ﷺ كَانَ يُسَبِّحُ عَلَى ظَهْرِ رَاحِلَتِهِ، حَيْثُ كَانَ وَجْهُهُ، يُومِئُ بِرَأْسِهِ، وَكَانَ ابْنُ عُمَرَ يَفْعَلُهُ» \n\nوَفِي رِوَايَةٍ: «كَانَ يُوتِرُ عَلَى بَعِيرِهِ» \n\nوَلِمُسْلِمٍ: «غَيْرَ أَنَّهُ لا يُصَلِّي عَلَيْهَا الْمَكْتُوبَةَ» \n\nوَلِلْبُخَارِيِّ: «إلاَّ الْفَرَائِضَ»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺ ސަވާރީކޮޅުގެ މަތީގައި އިންނަވައިގެން، ކޮންމެ ދިމާއަކަށް އޭގެ މިސްރާބު އޮތްކަމުގައި ވިޔަސް، ސުންނަތް ނަމާދުކުރައްވައެވެ. އަދި ނަބިއްޔާގެ އިސްތަށިފުޅުން އިޝާރަތް ކުރައްވާ (ރުކޫކޮށް ސަޖިދަކޮށް) ހައްދަވައެވެ. އަދި އިބްނު ޢުމަރު ވެސް ހަމަ އެފަދައިން ކަންތައް ކުރެއްވިއެވެ.\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ނަބިއްޔާގެ ޖަމަލުކޮޅު މަތީ ވިތުރި ކުރައްވައެވެ.\n\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ފަރުޟު ނަމާދުތައް އޭގެ މަތީ ނުކުރައްވައެވެ. \n\nއަދި ބުޚާރީގެ ރިވާޔަތެއްގައި ވެއެވެ: ފަރުޟު ނަމާދުތަކުގައި އެފަދައިން ނުކުރައްވައެވެ.","[البخاري 1105 واللفظ له، ومسلم 700\n\nالبخاري 999، ومسلم 700\n\nمسلم 700\n\nالبخاري 1000]"],["#073","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب استقبال القبلة","ގިބުލައަށް ކުރިމަތިލުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما قَالَ: «بَيْنَمَا النَّاسُ بِقُبَاءَ فِي صَلاةِ الصُّبْحِ إذْ جَاءَهُمْ آتٍ، فَقَالَ: إنَّ النَّبِيَّ ﷺ قَدْ أُنْزِلَ عَلَيْهِ اللَّيْلَةَ قُرْآنٌ، وَقَدْ أُمِرَ أَنْ يَسْتَقْبِلَ الْقِبْلَةَ، فَاسْتَقْبِلُوهَا. وَكَانَتْ وُجُوهُهُمْ إلَى الشَّامِ، فَاسْتَدَارُوا إلَى الْكَعْبَةِ»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: މީސްތަކުން ގުބާގައި ފަތިސް ނަމާދު ކުރަނިކޮށް، މީހަކު އައިސް ދެންނެވިއެވެ: ހަމަކަށަވަރުން މިރޭ ނަބިއްޔާ ﷺގެ މައްޗަށް ޤުރްއާނުގެ (އާޔަތްތަކެއް) ބާވައިލެއްވިޖެއެވެ. އަދި (ނަމާދުގައި) ކައުބާއަށް ކުރިމަތިލެއްވުމަށް ނަބިއްޔާއަށް އަމުރު ކުރެއްވިއްޖެއެވެ. ފަހެ ތިޔަބައިމިހުން ވެސް ކައުބާއަށް ކުރިމަތިލާށެވެ. (އެ ހިނދު) އެބައިމީހުން ކުރިމަތިލައިގެން ތިބީ ޝާމްއާ ދިމާއަށެވެ. (އެބަހީ: މަސްޖިދުލް އަގްސާއަށެވެ.) ދެން އެބައިމީހުން (އެ ހުރި ގޮތަށް ނަމާދުގައި) ކައުބާއާ ދިމާއަށް އެނބުރުނެވެ.","[البخاري 403 واللفظ له، ومسلم 526]"],["#074","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب استقبال القبلة","ގިބުލައަށް ކުރިމަތިލުމުގެ ބާބު","عَنْ أَنَسِ بْنِ سِيرِينَ قَالَ: «اسْتَقْبَلْنَا أَنَساً حِينَ قَدِمَ مِنْ الشَّامِ، فَلَقِينَاهُ بِعَيْنِ التَّمْرِ، فَرَأَيْتُهُ يُصَلِّي عَلَى حِمَارٍ، وَوَجْهُهُ مِنْ ذَا الْجَانِبِ - يَعْنِي عَنْ يَسَارِ الْقِبْلَةِ - فَقُلْتُ: رَأَيْتُكَ تُصَلِّي لِغَيْرِ الْقِبْلَةِ؟ فَقَالَ: لَوْلا أَنِّي رَأَيْتُ رَسُولَ اللَّهِ ﷺ يَفْعَلُهُ مَا فَعَلْتُهُ»","އަނަސް ބިން ސީރީން ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އަނަސް ބިން މާލިކު ޝާމުން ވަޑައިގެންނެވި ހިނދު، އެކަލޭގެފާނާ ބައްދަލު ކުރަން ތިމަންމެން ނުކުތީމެވެ. އެކަލޭގެފާނާ ބައްދަލުވީ ޢައިނުއް ތަމްރު އޭ ކިޔުނު ތަނެއްގައެވެ. އެކަލޭގެފާނު ހިމާރެއް މަތީ (ސުންނަތް) ނަމާދު ކުރައްވަން ހުންނެވިއެވެ. އަދި އެކަލޭގެފާނުގެ މޫނުފުޅު ގިބުލައިގެ ވާތަށް ހުންނަވަނިކޮށް ތިމަން ދުށީމެވެ. ތިމަން ދެންނެވީމެވެ: ގިބުލަ ނޫން ދިމާއަކަށް ތިބާ ނަމާދު ކުރައްވަނިކޮށް ދުށީމެވެ. އެކަލޭގެފާނު ދެންނެވިއެވެ: ރަސޫލު ﷲ ﷺ އެފަދައިން ކަންތައް ކުރައްވަނިކޮށް ތިމަން ނުދެކޭ ނަމަ، ތިމަންނަ އެފަދައިން ކަންތައް ނުކުރެއްވީސްކަން ކަށަވަރެވެ.","[البخاري 1100 واللفظ له، ومسلم 702]"],["#075","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الصفوف","ސަފުހެދުމުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «سَوُّوا صُفُوفَكُمْ، فَإِنَّ تَسْوِيَةَ الصُّفُوفِ مِنْ تَمَامِ الصَّلاةِ»","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ ސަފުތައް ސީދާ ކުރާށެވެ. އެހެނީ ހަމަކަށަވަރުން ސަފުތައް ސީދާ ކުރުމަކީ ނަމާދުގެ ފުރިހަމަކަމުގެ ތެރެއިންވާ ކަމެކެވެ.“","[البخاري 723، ومسلم 433 واللفظ له]"],["#076","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الصفوف","ސަފުހެދުމުގެ ބާބު","عَنِ النُّعْمَانِ بْنِ بَشِيرٍ رَضِيَ اللَّهُ عَنْهُما، قَالَ: سَمِعْتُ رَسُولَ اللَّهِ ﷺ يَقُولُ: «لَتُسَوُّنَّ صُفُوفَكُمْ أَوْ لَيُخَالِفَنَّ اللَّهُ بَيْنَ وُجُوهِكُمْ» \n\nوَلِمُسْلِمٍ: «كَانَ رَسُولُ اللَّهِ ﷺ يُسَوِّي صُفُوفَنَا، حَتَّى كَأَنَّمَا يُسَوِّي بِهَا الْقِدَاحَ، حَتَّى إذَا رَأَى أَنْ قَدْ عَقَلْنَا عَنْهُ، ثُمَّ خَرَجَ يَوْمًا فَقَامَ، حَتَّى إذَا كَادَ أَنْ يُكَبِّرَ، فَرَأَى رَجُلاً بَادِياً صَدْرُهُ، فَقَالَ: عِبَادَ اللَّهِ، لَتُسَوُّنَّ صُفُوفَكُمْ أَوْ لَيُخَالِفَنَّ اللَّهُ بَيْنَ وُجُوهِكُمْ»","އައްނުޢުމާން ބިން ބަޝީރު رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ”ތިޔަބައިމީހުންގެ ސަފުތައް ސީދާ ކުރާހުށިކަމެވެ. ނޫން ނަމަ، ތިޔަބައިމީހުންގެ މެދުގައި ﷲ ބައިބައި ކުރައްވަފާނެކަން ކަށަވަރެވެ.“\n\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ތީރުތަކެއް ސީދާ ކުރާ ފަދައިން، ރަސޫލު ﷲ ﷺ ތިމަންމެންގެ ސަފުތައް ސީދާ ކުރައްވައެވެ. އެއީ ނަބިއްޔާގެ އަރިހުން އެ ގޮތަށް ކަންކުރަން ތިމަންމެންނަށް ދަސްވެއްޖެކަން ނަބިއްޔާއަށް ފެނިވަޑައިގަތުމަށް ދާންދެނެވެ. ފަހެ އެއް ދުވަހަކު ނަބިއްޔާ ނުކުމެވަޑައިގެން ނަމާދު ކުރެއްވުމަށް ތެދުވެވަޑައިގެން ތަކްބީރު ދެއްވަން އުޅުއްވި ހިނދު، މީހަކު އޭނާގެ މޭ ކުރިއަށް (ސަފުން ބޭރަށް) ނެރެގެން ހުއްޓާ ފެނިވަޑައިގަތެވެ. ދެން ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲގެ އަޅުތަކުންނޭވެ. ތިޔަބައިމީހުންގެ ސަފުތައް ސީދާ ކުރާހުށިކަމެވެ. ނޫން ނަމަ، ތިޔަބައިމީހުންގެ މެދުގައި ﷲ ބައިބައި ކުރައްވަފާނެކަން ކަށަވަރެވެ.“","[البخاري 717، ومسلم 436\n\nمسلم 436]"],["#077","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الصفوف","ސަފުހެދުމުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ -: «أَنَّ جَدَّتَهُ مُلَيْكَةَ دَعَتْ رَسُولَ اللَّهِ ﷺ لِطَعَامٍ صَنَعَتْهُ، فَأَكَلَ مِنْهُ، ثُمَّ قَالَ: قُومُوا فَلأُصَلِّيَ لَكُمْ؟ قَالَ أَنَسٌ: فَقُمْتُ إلَى حَصِيرٍ لَنَا قَدْ اسْوَدَّ مِنْ طُولِ مَا لُبِسَ، فَنَضَحْتُهُ بِمَاءٍ، فَقَامَ عَلَيْهِ رَسُولُ اللَّه ﷺ وَصَفَفْتُ أَنَا وَالْيَتِيمُ وَرَاءَهُ، وَالْعَجُوزُ مِنْ وَرَائِنَا. فَصَلَّى لَنَا رَكْعَتَيْنِ، ثُمَّ انْصَرَفَ» \n\nوَلِمُسْلِمٍ «أَنَّ رَسُولَ اللَّهِ ﷺ صَلَّى بِهِ وَبِأُمِّهِ فَأَقَامَنِي عَنْ يَمِينِهِ، وَأَقَامَ الْمَرْأَةَ خَلْفَنَا»","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން އެކަލޭގެފާނުގެ މާމާފުޅު މުލައިކާ ފަރިއްކޮޅެއް ތައްޔާރު ކުރެއްވުމަށްފަހު ރަސޫލު ﷲ ﷺ އަށް ދައުވަތު އެރުވިއެވެ. އޭގެން ނަބިއްޔާ ފަރީއްކުޅުވުމަށް ފަހު ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންނައިގެން ތިމަން ނަބިއްޔާ ނަމާދު ކުރެއްވުމަށް ތިޔަބައިމީހުން ތެދުވާށެވެ.“ އަނަސް ވިދާޅުވިއެވެ: ފަހެ ތިމަންމެން އަތުގައި ހުރި ކުނަލެއް ނަގަން ތިމަން ތެދުވީމެވެ. އޭތި ގިނަ ދުވަސްވުމުގެ ސަބަބުން ހުރީ ކަޅުވެފައެވެ. ދެން (ސާފުކުރަން) ތިމަން އެޔަށް ފެންފޮދެއް ބުރުވާލީމެވެ. އަދި ރަސޫލު ﷲ ﷺ އޭގެ މަތީ ހުންނަވައިގެން ނަމާދު ކުރެއްވިއެވެ. ތިމަންނައާއި ޔަތީމު ކުއްޖަކު ނަބިއްޔާގެ ފަހަތްޕުޅުގައި ސަފު ހެދީމެވެ. އަދި އުމުރުން ދުވަސްވެފައި ހުންނެވި މާމާފުޅު ތިމަންމެންގެ ފަހަތުގައި ސަފު ހެއްދެވިއެވެ. ދެން ތިމަންމެންނައިގެން ނަބިއްޔާ ދެ ރަކުއަތް ކުރެއްވިއެވެ. އަދި އެއަށްފަހު ނުކުމެވަޑައިގަތެވެ.\n\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: އަނަސްއާއި އެކަލޭގެފާނުގެ މަންމާފުޅައިގެން، ރަސޫލު ﷲ ﷺ ނަމާދު ކުރެއްވިއެވެ. (އަނަސް ވިދާޅުވިއެވެ:) ފަހެ ނަބިއްޔާގެ ކަނާތް ފަރާތުގައި ތިމަން ބެހެއްޓެވިއެވެ. އަދި ތިމަންމެންގެ ފަހަތުގައި އެ ކަނބުލޭގެ ބެހެއްޓެވިއެވެ.","[البخاري 380، ومسلم 658\n\nمسلم 660]"],["#078","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الصفوف","ސަފުހެދުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما: قَالَ: «بِتُّ عِنْدَ خَالَتِي مَيْمُونَةَ. فَقَامَ النَّبِيُّ ﷺ يُصَلِّي مِنْ اللَّيْلِ. فَقُمْتُ عَنْ يَسَارِهِ. فَأَخَذَ بِرَأْسِي فَأَقَامَنِي عَنْ يَمِينِهِ»","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ތިމަންނަގެ ބޮޑުދައިތާފުޅު (ނަބިއްޔާގެ އަނބިކަނބަލުން) މައިމޫނާގެ ގެކޮޅުގައި އެއް ރޭ ތިމަން މަޑު ކުރީމެވެ. ފަހެ ނަބިއްޔާ ﷺ (ދަމު) ނަމާދު ކުރައްވަން ތެދުވެވަޑައިގަތެވެ. ދެން ތިމަން ވެސް ތެދުވެ، ނަބިއްޔާގެ ވާތް ފަރާތުގައި ހުރީމެވެ. އެހެނަސް ތިމަންނަގެ ބޮލުގައި ހިއްޕަވައިގެން، ނަބިއްޔާގެ ކަނާތް ފަރާތަށް ތިމަން ގެންނެވިއެވެ.","[البخاري 699 واللفظ له، ومسلم 763]"],["#079","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الإمامة","އިމާމުކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - عَنْ النَّبِيِّ ﷺ قَالَ: «أَمَا يَخْشَى الَّذِي يَرْفَعُ رَأْسَهُ قَبْلَ الإِمَامِ أَنْ يُحَوِّلَ اللَّهُ رَأْسَهُ رَأْسَ حِمَارٍ، أَوْ يَجْعَلَ صُورَتَهُ صُورَةَ حِمَارٍ؟»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އިމާމަށް ވުރެ ކުރިން ބޯ އުފުލާ މީހާ، އޭނާގެ ބޯ ހިމާރެއްގެ ބޮލަކަށް ﷲ ބަދަލު ކުރައްވައިފާނެކަމަށް ބިރު ނުގަންނަނީހެއްޔެވެ؟ ނޫނީ އޭނާގެ ސޫރާ ހިމާރެއްގެ ސޫރައަށް ބަދަލު ކުރައްވައިފާނެކަމަށް ބިރު ނުގަންނަނީހެއްޔެވެ؟“","[البخاري 691، ومسلم 427]"],["#080","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الإمامة","އިމާމުކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - عَنْ النَّبِيِّ ﷺ قَالَ: «إنَّمَا جُعِلَ الإِمَامُ لِيُؤْتَمَّ بِهِ. فَلا تَخْتَلِفُوا عَلَيْهِ. فَإِذَا كَبَّرَ فَكَبِّرُوا، وَإِذَا رَكَعَ فَارْكَعُوا. وَإِذَا قَالَ: سَمِعَ اللَّهُ لِمَنْ حَمِدَهُ، فَقُولُوا: رَبَّنَا وَلَكَ الْحَمْدُ. وَإِذَا سَجَدَ فَاسْجُدُوا. وَإِذَا صَلَّى جَالِساً فَصَلُّوا جُلُوساً أَجْمَعُونَ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އިމާމު މީހާ ލެއްވިގެން ވަނީ ހަމައެކަނި އޭނާއަށް ތަބާވެވުމަށެވެ. ފަހެ ތިޔަބައިމީހުން އޭނާއާ ޚިލާފު ނުވާށެވެ. އޭނާ ތަކްބީރު ދިނުމުން، ތިޔަބައިމީހުން ވެސް ތަކްބީރު ދޭށެވެ. އަދި އޭނާ ރުކޫކުރުމުން، ތިޔަބައިމީހުން ވެސް ރުކޫ ކުރާށެވެ. އަދި އޭނާ سَمِعَ اللَّهُ لِمَنْ حَمِدَهُ ކިޔުމުން، ތިޔަބައިމީހުން رَبَّنَا وَلَكَ الْحَمْدُ ކިޔާށެވެ. އަދި އޭނާ ސަޖިދަ ކުރުމުން، ތިޔަބައިމީހުން ވެސް ސަޖިދަ ކުރާށެވެ. އަދި އޭނާ އިށީނދެ އިނދެ ނަމާދުކޮށްފި ނަމަ، ތިޔަބައިމީހުން އެންމެން ވެސް އިށީނދެ ތިބެ ނަމާދު ކުރާށެވެ.“","[البخاري 722، ومسلم 414 واللفظ له]"],["#081","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الإمامة","އިމާމުކަމުގެ ބާބު","وَمَا فِي مَعْنَاهُ مِنْ حَدِيثِ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا قَالَتْ: «صَلَّى رَسُولُ اللَّهِ ﷺ فِي بَيْتِهِ وَهُوَ شَاكٍ، صَلَّى جَالِساً، وَصَلَّى وَرَاءَهُ قَوْمٌ قِيَاماً، فَأَشَارَ إلَيْهِمْ: أَنْ اجْلِسُوا لَمَّا انْصَرَفَ قَالَ إنَّمَا جُعِلَ الإِمَامُ لِيُؤْتَمَّ بِهِ، فَإِذَا رَكَعَ فَارْكَعُوا، وَإِذَا رَفَعَ فَارْفَعُوا، وَإِذَا قَالَ: سَمِعَ اللَّهُ لِمَنْ حَمِدَهُ فَقُولُوا: رَبَّنَا لَكَ الْحَمْدُ، وَإِذَا صَلَّى جَالِساً فَصَلُّوا جُلُوساً أَجْمَعُونَ»","އަދި ކުރީގެ ޙަދީޘާ އެއްފަދަ މާނައެއްގައި ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ އާލާސްކަންފުޅާ އުޅުއްވަމުން، ގެކޮޅުގައި އިށީނދެވަޑައިގެން އިންނަވާ ނަމާދު ކުރައްވައެވެ. އަދި ނަބިއްޔާގެ ފަހަތްޕުޅުގައި މީސްތަކުން ކޮޅަށް ތިބެ ނަމާދު ކުރިއެވެ. ފަހެ އެބައިމީހުން އިށީނުމަށް ނަބިއްޔާ އިޝާރާތް ކުރެއްވިއެވެ. އަދި ނަމާދުން އަވަދިވެ ވަޑައިގެންނެވުމަށް ފަހު ޙަދީޘް ކުރެއްވިއެވެ: ”އިމާމު މީހާ ލެއްވިގެން ވަނީ ހަމައެކަނި އޭނާއަށް ތަބާވެވުމަށެވެ. ފަހެ އޭނާ ރުކޫ ކުރުމުން، ތިޔަބައިމީހުން ވެސް ރުކޫ ކުރާށެވެ. އަދި އޭނާ ތެދުވުމުން، ތިޔަބައިމީހުން ވެސް ތެދުވާށެވެ. އަދި އޭނާ سَمِعَ اللَّهُ لِمَنْ حَمِدَهُ ކިޔުމުން، ތިޔަބައިމީހުން رَبَّنَا وَلَكَ الْحَمْدُ ކިޔާށެވެ. އަދި އޭނާ އިށީނދެ އިނދެ ނަމާދުކޮށްފި ނަމަ، ތިޔަބައިމީހުން އެންމެން ވެސް އިށީނދެ ތިބެ ނަމާދު ކުރާށެވެ.“","[البخاري 688 واللفظ له، ومسلم 412]"],["#082","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الإمامة","އިމާމުކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ يَزِيدَ الْخِطْمِيِّ الأَنْصَارِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: حَدَّثَنِي الْبَرَاءُ - وَهُوَ غَيْرُ كَذُوبٍ - قَالَ: «كَانَ رَسُولُ اللَّهِ ﷺ إذَا قَالَ: سَمِعَ اللَّهُ لِمَنْ حَمِدَهُ: لَمْ يَحْنِ أَحَدٌ مِنَّا ظَهْرَهُ حَتَّى يَقَعَ رَسُولُ اللَّهِ ﷺ سَاجِدًا، ثُمَّ نَقَعُ سُجُودًا بَعْدَهُ»","ޢަބްދު ﷲ ބިން ޔަޒީދު އަލްޚަޠްމީ އަލްއަންޞާރީ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އެކަލޭގެފާނަކީ ދޮގުވެރިއެއް ކަމުގައި ނުވާ ބަރާއު ބިން ޢާޒިބު ތިމަންނައަށް ވިދާޅުވިއެވެ: ރަސޫލު ﷲ ﷺ سَمِعَ اللَّهُ لِمَنْ حَمِدَهُ ވިދާޅުވުމުން، އެކަލޭގެފާނު ސަޖިދައަށް ވަޑައިގަންނަވަން ދެން ތިމަންމެން ކުރެ އެކަކުވެސް (ސަޖިދައަށް ދާން) ބުރަކަށި ލަނބައި ވެސް ނުލަމެވެ. ދެން ނަބިއްޔާއަށް ފަހު ތިމަންމެން ސަޖިދައަށް ތިރިވަމެވެ.","[البخاري 690، ومسلم 474]"],["#083","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الإمامة","އިމާމުކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «إذَا أَمَّنَ الإِمَامُ فَأَمِّنُوا، فَإِنَّهُ مَنْ وَافَقَ تَأْمِينُهُ تَأْمِينَ الْمَلائِكَةِ: غُفِرَ لَهُ مَا تَقَدَّمَ مِنْ ذَنْبِهِ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އިމާމު މީހާ އާމީން ކިޔާ ހިނދު، ތިޔަބައިމީހުން ވެސް އާމީން ކިޔާށެވެ. އެހެނީ ހަމަކަށަވަރުން މީހެއްގެ އާމިން، މަލާއިކަތުންގެ އާމީންއާ ދިމާވެއްޖެ ނަމަ، އެ މީހެއްގެ އިސްވެ ގޮސްފައިވާ ފާފަތައް ފުއްސެވޭ ހުއްޓެވެ.“","[البخاري 780، ومسلم 410]"],["#084","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الإمامة","އިމާމުކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «إذَا صَلَّى أَحَدُكُمْ لِلنَّاسِ فَلْيُخَفِّفْ فَإِنَّ فِيهِمْ الضَّعِيفَ وَالسَّقِيمَ وَذَا الْحَاجَةِ، وَإِذَا صَلَّى أَحَدُكُمْ لِنَفْسِهِ فَلْيُطَوِّلْ مَا شَاءَ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ކުރެ މީހަކު މީސްތަކުންނައިގެން ނަމާދު ކުރާ ނަމަ، ފަހެ ލުއިފަސޭހަ ކުރާށެވެ. އެހެނީ އެބައިމީހުންގެ ތެރޭގައި ބަލި މީހުންނާއި، ބަލިކަށި މީހުންނާއި، ހާޖަތް ޖެހިފައިވާ މީހުން ތިބޭނެކަން ކަށަވަރެވެ. އަދި ތިޔަބައިމީހުން ކުރެ މީހަކު އޭނާގެ އަމިއްލަ ނަފްސައިގެން (އެކަނި ހުރެ) ނަމާދު ކުރާ ނަމަ، ފަހެ އޭނާ ބޭނުން ވަރަކަށް ނަމާދު ދިގު ކުރާށެވެ.“","[البخاري 703، ومسلم 467]"],["#085","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الإمامة","އިމާމުކަމުގެ ބާބު","وَمَا فِي مَعْنَاهُ مِنْ حَدِيثِ أَبِي مَسْعُودٍ الأَنْصَارِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «جَاءَ رَجُلٌ إلَى رَسُولِ اللَّهِ ﷺ فَقَالَ: إنِّي لأَتَأَخَّرُ عَنْ صَلاةِ الصُّبْحِ مِنْ أَجْلِ فُلانٍ، مِمَّا يُطِيلُ بِنَا، قَالَ: فَمَا رَأَيْتُ النَّبِيَّ ﷺ غَضِبَ فِي مَوْعِظَةٍ قَطُّ أَشَدَّ مِمَّا غَضِبَ يَوْمَئِذٍ، فَقَالَ: يَا أَيُّهَا النَّاسُ، إنَّ مِنْكُمْ مُنَفِّرِينَ، فَأَيُّكُمْ أَمَّ النَّاسَ فَلْيُوجِزْ، فَإِنَّ مِنْ وَرَائِهِ الْكَبِيرَ وَالضَّعِيفَ وَذَا الْحَاجَةِ»","އަދި ކުރީގެ ޙަދީޘާ އެއްފަދަ މާނައެއްގައި އަބޫ މަސްޢޫދު އަލްއަންޞާރީ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺގެ އަރިހަށް މީހަކު އައިސް ދެންނެވިއެވެ: ހަމަކަށަވަރުން މިވެނި މީހަކު ތިމަންމެންނައިގެން ފަތިސް ނަމާދު ކުރާލެއް ދިގުކަމުން، އެ ނަމާދުން ތިމަން ފަސްޖެހެމެވެ. އަބޫ މަސްޢޫދު ވިދާޅުވިއެވެ: އެ ދުވަހު ނަބިއްޔާ ﷺ ކޯފާވި ވަރަށް ވުރެ ދުވަހަކުވެސް ވައުޒު ދިނުމުގައި އެކަލޭގެފާނު ކޯފާވި ތަނެއް ތިމަން ނުދެކެމެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ މީސްތަކުންނޭވެ. ހަމަކަށަވަރުން ތިޔަބައިމީހުންގެ ތެރޭގައި (އެހެން މީހުން) ފައްސާ ދުރުކުރާ ބަޔަކު އުޅެއެވެ. ތިޔަބައިމީހުން ކުރެ މީހަކު މީސްތަކުންނައިގެން އިމާމުވެ ނަމާދު ކުރާ ނަމަ، ފަހެ ލުއިފަސޭހަ ކުރާށެވެ. އެހެނީ އެބައިމީހުންގެ ތެރޭގައި މުސްކުޅިންނާއި، ބަލިކަށި މީހުންނާއި، ހާޖަތް ޖެހިފައިވާ މީހުން ތިބޭނެކަން ކަށަވަރެވެ.“","[البخاري 704، ومسلم 466 واللفظ له]"],["#086","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «كَانَ رَسُولُ اللَّهِ ﷺ إذَا كَبَّرَ فِي الصَّلاةِ سَكَتَ هُنَيْهَةً قَبْلَ أَنْ يَقْرَأَ، فَقُلْتُ: يَا رَسُولَ اللَّهِ، بِأَبِي أَنْتَ وَأُمِّي، أَرَأَيْتَ سُكُوتَكَ بَيْنَ التَّكْبِيرِ وَالْقِرَاءَةِ: مَا تَقُولُ؟ قَالَ: أَقُولُ: اللَّهُمَّ بَاعِدْ بَيْنِي وَبَيْنَ خَطَايَايَ كَمَا بَاعَدْتَ بَيْنَ الْمَشْرِقِ وَالْمَغْرِبِ. اللَّهُمَّ نَقِّنِي مِنْ خَطَايَايَ كَمَا يُنَقَّى الثَّوْبُ الأَبْيَضُ مِنْ الدَّنَسِ. اللَّهُمَّ اغْسِلْنِي مِنْ خَطَايَايَ بِالْمَاءِ وَالثَّلْجِ وَالْبَرَدِ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ނަމާދުގެ (ފުރަތަމަ) ތަކްބީރު ދެއްވުމަށް ފަހު، (ޤުރްއާން) ކިޔަވައި ވިދާޅުވުމުގެ ކުރިން ހިނދުކޮޅަކަށް ކިޔަވައި ވިދާޅުވުމުގެ ކުރިން ހިނދުކޮޅަކަށް ހަމަހިމޭންވެ ވަޑައިގަންނަވައެވެ. ތިމަން ދެންނެވީމެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. އިބަ ނަބިއްޔާއަށް ޓަކައި ތިމަންނަގެ ބައްޕައާއި މަންމަ ފިދާ ކުރުއްވާށިއެވެ. (ފުރަތަމަ) ތަކްބީރާއި (ޤުރްއާން) ކިޔަވައި ވިދާޅުވުމާ ދެމެދު އިބަ ނަބިއްޔާ ހަމަހިމޭންވެ ވަޑައިގަންނަވާ ތަން ދެކެވަޑައިގަންނަވަމުތޯއެވެ؟ އިބަ ނަބިއްޔާ ވިދާޅުވަނީ ކީކޭތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާ ވިދާޅުވަނީ: اللَّهُمَّ بَاعِدْ بَيْنِي وَبَيْنَ خَطَايَايَ كَمَا بَاعَدْتَ بَيْنَ الْمَشْرِقِ وَالْمَغْرِبِ. اللَّهُمَّ نَقِّنِي مِنْ خَطَايَايَ كَمَا يُنَقَّى الثَّوْبُ الأَبْيَضُ مِنْ الدَّنَسِ. اللَّهُمَّ اغْسِلْنِي مِنْ خَطَايَايَ بِالْمَاءِ وَالثَّلْجِ وَالْبَرَدِ (ޔާ ﷲ! އިބަ އިލާހު މަޝްރިގާއި މަޣްރިބާ ދެމެދު ދުރުކުރެއްވި ފަދައިން، މި އަޅާއާއި މި އަޅާގެ ފާފަތަކާ ދެމެދު ދުރުކުރައްވާށިއެވެ. ޔާ ﷲ! ހުދު ފޭރާމެއްގައި ޖެހިފައިވާ ހަޑިތައް އެ ފޭރާމުން ފިލުވިގެންދާ ފަދައިން، މި އަޅާގެ ފާފަތަކުން މި އަޅާ ތާހިރު ކުރައްވާށިއެވެ. ޔާ ﷲ! ފެނާއި ސްނޯއާއި ގަނޑުފެނުން މި އަޅާގެ ފާފަތައް ދޮންނަވައި ފިއްލައްވާށިއެވެ.)“","[البخاري 744، ومسلم 598 واللفظ له]"],["#087","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا قَالَتْ: «كَانَ رَسُولُ اللَّهِ ﷺ يَسْتَفْتِحُ الصَّلاةَ بِالتَّكْبِيرِ، وَالْقِرَاءَةَ بـ «الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ» وَكَانَ إذَا رَكَعَ لَمْ يُشْخِصْ رَأْسَهُ وَلَمْ يُصَوِّبْهُ وَلَكِنْ بَيْنَ ذَلِكَ، وَكَانَ إذَا رَفَعَ رَأْسَهُ مِنْ الرُّكُوعِ لَمْ يَسْجُدْ حَتَّى يَسْتَوِيَ قَائِماً، وَكَانَ إذَا رَفَعَ رَأْسَهُ مِنْ السَّجْدَةِ لَمْ يَسْجُدْ، حَتَّى يَسْتَوِيَ قَاعِداً، وَكَانَ يَقُولُ فِي كُلِّ رَكْعَتَيْنِ التَّحِيَّةَ، وَكَانَ يَفْرِشُ رِجْلَهُ الْيُسْرَى وَيَنْصِبُ رِجْلَهُ الْيُمْنَى، وَكَانَ يَنْهَى عَنْ عُقْبَةِ الشَّيْطَانِ، وَيَنْهَى أَنْ يَفْتَرِشَ الرَّجُلُ ذِرَاعَيْهِ افْتِرَاشَ السَّبُعِ، وَكَانَ يَخْتِمُ الصَّلاةَ بِالتَّسْلِيمِ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ނަމާދު ފައްޓަވަނީ ތަކްބީރުންނެވެ. އަދި (ޤުރްއާން) ކިޔަވައި ވިދާޅުވާން ފައްޓަވަނީ الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِين އިންނެވެ. އަދި ނަބިއްޔާ ރުކޫ ކުރައްވާ އިރު، ނަބިއްޔާގެ އިސްތަށިފުޅު މާ މައްޗަކަށް ނުއުފުއްލަވައެވެ. އަދި މާ ތިރިވެސް ނުކުރައްވައެވެ. އެހެނަސް ބަހައްޓަވަނީ އެ ދެމެދުގައެވެ. އަދި ރުކޫއިން އިސްތަށިފުޅު އުފުއްލެވުމަށް ފަހު، އެކަލޭގެފާނުގެ ހަށިކޮޅު ރަނގަޅަށް ތެދުކޮށްލެއްވުމަށް ފަހުގައި މެނުވީ ސަޖިދަ ނުކުރައްވައެވެ. އަދި ސަޖިދައިން އިސްތަށިފުޅު އުފުއްލެވުމަށް ފަހު، ރީއްޗަށް އިށީނދެވަޑައިގަތުމަށް ފަހުގައި މެނުވީ (ދެވަނަ) ސަޖިދަ ނުކުރައްވައެވެ. އަދި ކޮންމެ ދެ ރަކުއަތެއްގައި އައްތަހިއްޔާތު ވިދާޅުވެއެވެ. އަދި ވާތް ފައިންޕުޅު (އޭގެ މަތީ އިށީނދެވަޑައިގަނެވޭ ގޮތަށް) އަރިއަކަށް ބާއްވާފައި، ކަނާތް ފައިންޕުޅުގެ (ފުންނާބު މައްޗަށް ނަންގަވާފައި، ފައިތިލާގެ އިނގިލިތައް ބިންމަތީގައި ޖެހޭގޮތަށް) ބަހައްޓަވައެވެ. އަދި ޝައިތާނާ އިށީންނަ ފަދައިން އިށީނުން ނަބިއްޔާ ނަހީ ކުރެއްވިއެވެ. (އެބަހީ: ދެއަރިއަށް ބިންމަތީގައި ދެއަތް ވިއްދައިގެން، ދެކަށިމަތި އެކީގައި މައްޗަށް ނަގައިގެން އިނުން މަނާ ކުރެއްވިއެވެ.) އަދި ޝިކާރަ ކުރާ ޖަނަވާރެއް ފަދައިން މީހަކު ދެ އަތްދަނޑި ބިންމަތީގައި ޖަހައިގެން (ސަޖިދައަށް) އޮތުން ވެސް ނަހީ ކުރެއްވިއެވެ. އަދި ނަބިއްޔާ ނަމާދު ނިންމަވަނީ ސަލާމުންނެވެ.","[مسلم 498]"],["#088","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما: «أَنَّ النَّبِيَّ ﷺ كَانَ يَرْفَعُ يَدَيْهِ حَذْوَ مَنْكِبَيْهِ إذَا افْتَتَحَ الصَّلاةَ، وَإِذَا كَبَّرَ لِلرُّكُوعِ، وَإِذَا رَفَعَ رَأْسَهُ مِنْ الرُّكُوعِ رَفَعَهُمَا كَذَلِكَ، وَقَالَ: سَمِعَ اللَّهُ لِمَنْ حَمِدَهُ، رَبَّنَا وَلَكَ الْحَمْدُ. وَكَانَ لا يَفْعَلُ ذَلِكَ فِي السُّجُودِ»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ ނަމާދު ފައްޓަވާ ހިނދު، ދެ ކޮނޑުފުޅާ ހިސާބަށް ދެ އަތްޕުޅު އުފުއްލަވައެވެ. އަދި ރުކޫއަށް ދިޔުމަށް ތަކްބީރު ދެއްވާ ހިނދުގައިވެސް މެއެވެ. އަދި ރުކޫއިން ތެދުވެވަޑައިގަންނަވާ ހިނދުވެސް ދެ އަތްޕުޅު އެފަދައިން އުފުއްލަވައެވެ. އަދި ވިދާޅުވެއެވެ: سَمِعَ اللَّهُ لِمَنْ حَمِدَهُ، رَبَّنَا وَلَكَ الْحَمْدُ (ﷲ އަށް ހަމްދުކޮށްފި މީހަކަށް އެ އިލާހު އަޑުއައްސަވާ ވޮޑިގެންފިއެވެ. އަޅަމެންގެ ވެރި ރައްބެވެ. ފަހެ އެންމެހާ ހަމްދެއް ލިބިވޮޑިގެންވަނީ އިބަ އިލާހަށެވެ.) އެހެނަސް ސަޖިދަ ކުރައްވާ ހިނދު އެފަދައިން (އަތްޕުޅު އުފުއްލަވާ) ނަހަދައެވެ.","[البخاري 735 واللفظ له، ومسلم 390]"],["#089","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ ابْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «أُمِرْتُ أَنْ أَسْجُدَ عَلَى سَبْعَةِ أَعْظُمٍ: عَلَى الْجَبْهَةِ - وَأَشَارَ بِيَدِهِ إلَى أَنْفِهِ - وَالْيَدَيْنِ، وَالرُّكْبَتَيْنِ، وَأَطْرَافِ الْقَدَمَيْنِ»","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަތް ގުނަވަނެއްގެ މަތީގައި ސަޖިދަ ކުރައްވަން ތިމަން ނަބިއްޔާއަށް އަމުރުވެވިގެންވެއެވެ. އެއީ ނިއްކުރި، (އަދި ނަބިއްޔާ އަތްޕުޅުން ނޭފަތްޕުޅަށް އިޝާރާތް ކުރެއްވިއެވެ.) އަދި ދެ އަތާއި، ދެ ކަކުލާއި، ދެ ފައިތިލައިގެ އިނގިލިތަކެވެ.“","[البخاري 812 واللفظ له، ومسلم 490]"],["#090","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «كَانَ رَسُولُ اللَّهِ ﷺ إذَا قَامَ إلَى الصَّلاةِ يُكَبِّرُ حِينَ يَقُومُ، ثُمَّ يُكَبِّرُ حِينَ يَرْكَعُ، ثُمَّ يَقُولُ: سَمِعَ اللَّهُ لِمَنْ حَمِدَهُ، حِينَ يَرْفَعُ صُلْبَهُ مِنْ الرَّكْعَةِ، ثُمَّ يَقُولُ وَهُوَ قَائِمٌ: رَبَّنَا وَلَكَ الْحَمْدُ، ثُمَّ يُكَبِّرُ حِينَ يَهْوِي، ثُمَّ يُكَبِّرُ حِينَ يَرْفَعُ رَأْسَهُ، ثُمَّ يُكَبِّرُ حِينَ يَسْجُدُ، ثُمَّ يُكَبِّرُ حِينَ يَرْفَع رَأْسَهُ، ثُمَّ يَفْعَلُ ذَلِكَ فِي صَلاتِهِ كُلِّهَا، حَتَّى يَقْضِيَهَا، وَيُكَبِّرُ حِينَ يَقُومُ مِنْ الثِّنْتَيْنِ بَعْدَ الْجُلُوسِ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ނަމާދަށް ތެދުވެ ވަޑައިގަންނަވާ ހިނދު ތަކްބީރު ދެއްވައެވެ. އަދި ރުކޫ ކުރައްވާ ހިނދު ވެސް ތަކްބީރު ދެއްވައެވެ. އަދި ރުކޫއިން ބުރަކަށިފުޅު އުފުއްލަވާ ހިނދު سَمِعَ اللَّهُ لِمَنْ حَمِدَهُ ވިދާޅުވެއެވެ. އަދި ކޮއްޅަށް ތެދުވެވަޑައިގެން رَبَّنَا وَلَكَ الْحَمْدُ ވިދާޅުވެއެވެ. އަދި ސަޖިދައަށް ތިރިވެ ވަޑައިގަންނަވަމުން ތަކްބީރު ދެއްވައެވެ. އަދި (ސަޖިދައިން) އިސްތަށިފުޅު އުފުއްލަވާ ހިނދު ތަކްބީރު ދެއްވައެވެ. އަދި (ދެވަނަ) ސަޖިދަ ކުރައްވާ ހިނދު ތަކްބީރު ދެއްވައެވެ. އަދި އަނެއްކާވެސް އިސްތަށިފުޅު އުފުއްލަވާ (ތެދުވެވަޑައިގަންނަވާ) ހިނދު ތަކްބީރު ދެއްވައެވެ. ދެން ނަބިއްޔާގެ މުޅި ނަމާދުން އަވަދިވެ ވަޑައިގަތުމަށް ދާންދެން ކަންކުރައްވަނީ އެފަދައިންނެވެ. އަދި ދެވަނަ ރަކުއަތުން (އައްތަހިޔާތަށް) އިށީނދެ ވަޑައިގަތުމަށް ފަހު، ތެދުވެ ވަޑައިގަންނަވާ ހިނދު ވެސް ތަކްބީރު ދެއްވައެވެ.","[البخاري 789 واللفظ له، ومسلم 392]"],["#091","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ مُطَرِّفِ بْنِ عَبْدِ اللَّهِ قَالَ: «صَلَّيْتُ أَنَا وَعِمْرَانُ بْنُ حُصَيْنٍ خَلْفَ عَلِيِّ بْنِ أَبِي طَالِبٍ. فَكَانَ إذَا سَجَدَ كَبَّرَ، وَإِذَا رَفَعَ رَأْسَهُ كَبَّرَ، وَإِذَا نَهَضَ مِنْ الرَّكْعَتَيْنِ كَبَّرَ، فَلَمَّا قَضَى الصَّلاةَ أَخَذَ بِيَدِي عِمْرَانُ بْنُ حُصَيْنٍ، وَقَالَ: قَدْ ذَكَّرَنِي هَذَا صَلاةَ مُحَمَّدٍ ﷺ أَوْ قَالَ: صَلَّى بِنَا صَلاةَ مُحَمَّدٍ ﷺ»","މުޠައްރިފު ބިން ޢަބްދު ﷲ ރިވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ޢަލީ ބިން އަބީ ޠާލިބު رَضِيَ اللَّهُ عَنْهُގެ ފަހަތްޕުޅުގައި، ތިމަންނައާއި ޢިމްރާން ބިން ޙުޞައިން ނަމާދު ކުރީމެވެ. ޢަލީގެފާނު ސަޖިދަ ކުރައްވާ ހިނދު ތަކްބީރު ދެއްވައެވެ. އަދި އިސްތަށިފުޅު އުފުއްލަވާ ހިނދު ތަކްބީރު ދެއްވައެވެ. އަދި ދެ ރަކުއަތުން ތެދުވެ ވަޑައިގަންނަވާ ހިނދު ތަކްބީރު ދެއްވައެވެ. ދެން ނަމާދު ނިންމެވުމަށް ފަހު، ޢިމްރާން ބިން ޙުޞައިން ތިމަންނަގެ އަތުގައި ހިއްޕަވާފައި ވިދާޅުވިއެވެ: ހަމަކަށަވަރުން މުޙައްމަދު ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ މަތިން މި ކަލޭގެފާނު ތިމަންނައަށް ހަނދުމަކޮށްދީފިއެވެ. ނުވަތަ ވިދާޅުވިއެވެ: އެކަލޭގެފާނު ތިމަންމެންނައިގެން އެ ނަމާދު ކުރެއްވީ މުޙައްމަދު ﷺ ނަމާދު ކުރެއްވި ގޮތަށެވެ.","[البخاري 786 واللفظ له، ومسلم 393]"],["#092","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ الْبَرَاءِ بْنِ عَازِبٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: «رَمَقْتُ الصَّلاةَ مَعَ مُحَمَّدٍ ﷺ فَوَجَدْتُ قِيَامَهُ، فَرَكْعَتَهُ فَاعْتِدَالَهُ بَعْدَ رُكُوعِهِ، فَسَجْدَتَهُ، فَجِلْسَتَهُ بَيْنَ السَّجْدَتَيْنِ، فَسَجْدَتَهُ فَجِلْسَتَهُ مَا بَيْنَ التَّسْلِيمِ وَالانْصِرَافِ: قَرِيباً مِنْ السَّوَاءِ»\n\nوَفِي رِوَايَةِ الْبُخَارِيِّ «مَا خَلا الْقِيَامَ وَالْقُعُودَ قَرِيباً مِنْ السَّوَاءِ»","އަލްބަރާއު ބިން ޢާޒިބް رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: މުޙައްމަދު ﷺ ކުރެއްވި ނަމާދު ތިމަން ބެލީމެވެ. ފަހެ ނަބިއްޔާގެ ކޮއްޅަށް ތެދުވެ ވަޑައިގަތުމާއި، ރުކޫ ކުރެއްވުމާއި، ރުކޫއާއި ސަޖިދައާ ދެމެދުގައި އިއުތިދާލުގައި ހުންނެވުމާއި، ސަޖިދަ ކުރެއްވުމާއި، ދެ ސަޖިދަ ދެމެދު އިށީނދެ އިންނެވުމާއި، (އަނެއްކާވެސް) ސަޖިދަ ކުރެއްވުމާއި، ސާލާމް ދެއްވުމާއި ތެދުވެ ވަޑައިގަތުމާ ދެމެދު އިށީނދެ އިންނެވި އިންނެވުން، މި ހުރިހާ ކަމެއްގައި (ނަބިއްޔާ ހޭދަ ކުރެއްވި ވަގުތުކޮޅު) ދާދި އެއްވަރު ކަމަށް ތިމަންނައަށް ފެނުނެވެ.\n\nއަދި ބުޚާރީގެ ރިވާޔަތުގައި ވެއެވެ: ކޮއްޅަށް ތެދުވެ ވަޑައިގަތުމާއި، އިށީނދެ ވަޑައިގަތުން ފިޔަވާ، ދެން ހުރި ކަންކަމުގައި (ހޭދަ ކުރެއްވި ވަގުތުކޮޅު) ދާދި އެއްވަރެވެ.","[البخاري 792، ومسلم 471 واللفظ له]"],["#093","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ ثَابِتٍ الْبُنَانِيِّ عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «إنِّي لا آلُو أَنْ أُصَلِّيَ بِكُمْ كَمَا كَانَ رَسُولُ اللَّهِ ﷺ يُصَلِّي بِنَا - قَالَ ثَابِتٌ:- فَكَانَ أَنَسٌ يَصْنَعُ شَيْئاً لا أَرَاكُمْ تَصْنَعُونَهُ. كَانَ إذَا رَفَعَ رَأْسَهُ مِنْ الرُّكُوعِ: انْتَصَبَ قَائِماً، حَتَّى يَقُولَ الْقَائِلُ: قَدْ نَسِيَ، وَإِذَا رَفَعَ رَأْسَهُ مِنْ السَّجْدَةِ: مَكَثَ، حَتَّى يَقُولَ الْقَائِلُ: قَدْ نَسِيَ»","އަދި ޘާބިތު އަލްބުނާނީ، އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާ ކުރެއްވިއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ތިމަންމެންނައިގެން ނަމާދު ކުރެއްވި ފަދައިން، ތިމަންނަ ތިޔަބައިމިހުންނައިގެން ނަމާދު ކުރުމުގައި، އެއްވެސް ކަމެއް ތިމަންނަ އުންޏެއް ނުކުރާނަމެވެ. (ޙަދީޘްގެ ރާވީއެއް ކަމަށްވާ) ޘާބިތު ވިދާޅުވިއެވެ: ތިޔަބައިމީހުން (ނަމާދުގައި) ކުރަނިކޮށް ތިމަންނައަށް ނުފެންނަ ކަމެއް، އަނަސް ކުރެއްވިއެވެ. އެކަލޭގެފާނު ރުކޫއިން އިސްތަށިފުޅު އުފުއްލަވާ ހިނދު، ތެދުވެ ވަޑައިގެން މަޑުކޮށްލަވައެވެ. އެއީ އެކަލޭގެފާނަށް ހަނދުމަފުޅު ނެތުނީއޭ މީހަކު ބުނެފާނެކަމަށް ދާންދެނެވެ. އަދި އެކަލޭގެފާނު ސަޖިދައިން އިސްތަށިފުޅު އުފުއްލަވާ ހިނދު، އިށީންދެ ވަޑައިގެން މަޑުކޮށްލަވައެވެ. އެއީ ވެސް އެކަލޭގެފާނަށް ހަނދުމަފުޅު ނެތުނީއޭ މީހަކު ބުނެފާނެކަމަށް ދާންދެނެވެ.","[البخاري 721، ومسلم 472 واللفظ له]"],["#094","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «مَا صَلَّيْتُ خَلْفَ إمَامٍ قَطُّ أَخَفَّ صَلاةً، وَلا أَتَمَّ صّلاةً مِنَ النَّبيِّ ﷺ»","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ އަށްވުރެ ލުއިކޮށް އަދި އެހާމެ ފުރިހަމަކޮށް ނަމާދު ކުރެއްވި އެއްވެސް އިމާމެއްގެ ފަހަތުގައި ތިމަންނަ ދުވަހަކުވެސް ނަމާދެއް ނުކުރަމެވެ.","[البخاري 807 واللفظ له، ومسلم 469]"],["#095","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ أَبِي قِلابَةَ عَبْدِ اللَّهِ بْنِ زَيْدٍ الْجَرْمِيِّ الْبَصْرِيِّ - قَالَ: «جَاءَنَا مَالِكُ بْنُ الْحُوَيْرِثِ فِي مَسْجِدِنَا هَذَا، فَقَالَ: إنِّي لأُصَلِّي بِكُمْ، وَمَا أُرِيدُ الصَّلاةَ، أُصَلِّي كَيْفَ رَأَيْتُ رَسُولَ اللَّهِ ﷺ يُصَلِّي، فَقُلْتُ لأَبِي قِلابَةَ: كَيْفَ كَانَ يُصَلِّي؟ فَقَالَ: مِثْلَ صَلاةِ شَيْخِنَا هَذَا، وَكَانَ يَجْلِسُ إذَا رَفَعَ رَأْسَهُ مِنْ السُّجُودِ قَبْلَ أَنْ يَنْهَضَ» أَرادَ بشيخِهمْ، أَبا يزيدَ، عَمرَو بنَ سَلَمَة الجَرْميَّ.","އަބޫ ގިލާބާ، ޢަބްދު ﷲ ބިން ޒައިދު އަލްޖަރްމީ އަލްބަޞްރީ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ތިމަންމެންގެ މި މިސްކިތަށް މާލިކު ބިން އަލްޙުވައިރިޘް ވަޑައިގެން ދެންނެވިއެވެ: ހަމަކަށަވަރުން ތިޔަބައިމީހުންނައިގެން ތިމަންނަ ނަމާދު ކުރާނަމެވެ. އަދި ތިމަންނަގެ ބޭނުމަކީ ހަމަ ނަމާދުކޮށްލުމެއް ނޫނެވެ. (އެހެނެއްކަމަކު) ރަސޫލު ﷲ ﷺ ނަމާދު ކުރައްވަނިކޮށް ތިމަން ދެކުނު ފަދައިން ތިމަން ނަމާދު ކުރާނަމެވެ. (ޙަދީޘްގެ ރާވީއެއް ކަމަށްވާ އައްޔޫބު،) އަބޫ ގިލާބާ ގާތު ދެންނެވިއެވެ: ނަބިއްޔާ ނަމާދު ކުރެއްވީ ކިހިނަކުންތޯއެވެ؟ އަބޫ ގިލާބާ ވިދާޅުވިއެވެ: އެއީ ހަމަ ތިމަންމެންގެ މި ޝައިޚު (ޢަމްރު ބިން ސަލަމާ) ނަމާދު ކުރެއްވި ފަދައިންނެވެ. އަދި އެކަލޭގެފާނު ސަޖިދައިން އިސްތަށިފުޅު އުފުއްލެމަށް ފަހު، ތެދުވެ ވަޑައިގަތުމުގެ ކުރިން (އިރުކޮޅަކު) އިށީނދެލައެވެ. އެކަލޭގެފާނު މި ޝައިޚޭ އެ ވިދާޅުވަނީ އަބޫ ޔަޒީދު، ޢަމްރު ބިން ސަލަމާ އަލްޖަރްމީއަށެވެ.","[البخاري 677]"],["#096","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ مَالِكٍ بْنِ بُحَيْنَةَ - رَضِيَ اللَّهُ عَنْهُ -: «أَنَّ النَّبِيَّ ﷺ كَانَ إذَا صَلَّى فَرَّجَ بَيْنَ يَدَيْهِ، حَتَّى يَبْدُوَ بَيَاضُ إبْطَيْهِ»","ޢަބްދު ﷲ ބިން މާލިކު ބިން ބުޙައިނާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺ ނަމާދު ކުރައްވާ ހިނދު، ނަބިއްޔާގެ ކިހިލިފަތްޕުޅުގެ ހުދުކަން ފެނުމަށް ދާންދެން (ސަޖިދައިގައި) ދެ އަތްޕުޅު ދުރު ކުރައްވައެވެ.","[البخاري 390، ومسلم 495]"],["#097","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ أَبِي مَسْلَمَةَ سَعِيدِ بْنِ يَزِيدَ قَالَ: «سَأَلْتُ أَنَسَ بْنَ مَالِكٍ: أَكَانَ النَّبِيُّ ﷺ يُصَلِّي فِي نَعْلَيْهِ؟ قَالَ: نَعَمْ»","އަބޫ މަސްލަމާ ސަޢީދު ބިން ޔަޒީދު ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އަނަސް ބިން މާލިކު އަރިހުގައި ތިމަން ސުވާލު ކުރީމެވެ: ނަބިއްޔާ ﷺ ފައިވާންޖޯޑުކޮޅުގައި ނަމާދު ކުރައްވާތޯއެވެ؟ އަނަސް ވިދާޅުވިއެވެ: އާދޭހެވެ.","[البخاري 386، ومسلم 555]"],["#098","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ أَبِي قَتَادَةَ الأَنْصَارِيِّ - رَضِيَ اللَّهُ عَنْهُ -: «أَنَّ رَسُولَ اللَّهِ ﷺ كَانَ يُصَلِّي وَهُوَ حَامِلٌ أُمَامَةَ بِنْتَ زَيْنَبَ بِنْتِ رَسُولِ اللَّهِ ﷺ","އަބޫ ގަތާދާ އަލްއަންޞާރީ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން އުމާމާ ބިންތި ޒައިނަބު ބިންތި ރަސޫލު ﷲ ﷺ އުރާލައްވައިގެން ހުންނަވަނިކޮށް، ރަސޫލު ﷲ ﷺ ނަމާދު ކުރައްވައެވެ.","[البخاري 516 واللفظ له، ومسلم 543]"],["#099","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","وَلأَبِي الْعَاصِ بْنِ الرَّبِيعِ بْنِ عَبْدِ شَمْسٍ، فَإِذَا سَجَدَ وَضَعَهَا، وَإِذَا قَامَ حَمَلَهَا»","އަދި އަބުލް ޢާޞް ބިން އައްރަބީޢު ބިން ޢަބްދު ޝަމްސުގެ އަރިހުން ރިވާވެގެންވެއެވެ: ފަހެ ނަބިއްޔާ (ނަމާދުގައި) ސަޖިދަ ކުރައްވާ ހިނދު، އެ ކަމަނާ ތިރި ކުރައްވައެވެ. އަދި ތެދުވެ ވަޑައިގަންނަވާ ހިނދު، އެ ކަމަނާ އުރާލައްވައެވެ.","[البخاري 516 واللفظ له، ومسلم 543]"],["#100","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صفة صلاة النبي ﷺ","ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވި ގޮތުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - عَنْ النَّبِيِّ ﷺ قَالَ: «اعْتَدِلُوا فِي السُّجُودِ وَلا يَبْسُطْ أَحَدُكُمْ ذِرَاعَيْهِ انْبِسَاطَ الْكَلْبِ»","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ސަޖިދައިގައި ސީދާ ވާށެވެ. އަދި ކުއްތާ ބިންމަތީގައި ދެ އަތްދަނޑި ޖަހާ ފަދައިން، ތިޔަބައިމީހުން ކުރެ މީހަކު އަތްދަނޑި ނުޖައްސާށެވެ.“","[البخاري 822، ومسلم 493]"],["#101","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب وجوب الطمأنينة في الركوع والسجود","ރުކޫއާއި ސަޖިދައިގައި ހަމަޖެހިލުން ވާޖިބުކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ -: «أَنَّ النَّبِيَّ ﷺ دَخَلَ الْمَسْجِدَ، فَدَخَلَ رَجُلٌ فَصَلَّى، ثُمَّ جَاءَ فَسَلَّمَ عَلَى النَّبِيِّ ﷺ فَقَالَ: ارْجِعْ فَصَلِّ، فَإِنَّك لَمْ تُصَلِّ. فَرَجَعَ فَصَلَّى كَمَا صَلَّى، ثُمَّ جَاءَ فَسَلَّمَ عَلَى النَّبِيِّ ﷺ فَقَالَ: ارْجِعْ فَصَلِّ، فَإِنَّك لَمْ تُصَلِّ - ثَلاثاً - فَقَالَ: وَاَلَّذِي بَعَثَكَ بِالْحَقِّ لا أُحْسِنُ غَيْرَهُ، فَعَلِّمْنِي، فَقَالَ: إذَا قُمْتَ إلَى الصَّلاةِ فَكَبِّرْ، ثُمَّ اقْرَأْ مَا تَيَسَّرَ مِنْ الْقُرْآنِ، ثُمَّ ارْكَعْ حَتَّى تَطْمَئِنَّ رَاكِعاً، ثُمَّ ارْفَعْ حَتَّى تَعْتَدِلَ قَائِماً، ثُمَّ اُسْجُدْ حَتَّى تَطْمَئِنَّ سَاجِداً، ثُمَّ ارْفَعْ حَتَّى تَطْمَئِنَّ جَالِساً. وَافْعَلْ ذَلِكَ فِي صَلاتِكَ كُلِّهَا»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺ މިސްކިތަށް ވަދެވަޑައިގަތެވެ. ދެން މީހަކު އައިސް މިސްކިތަށް ވަދެ ނަމާދު ކުރުމަށް ފަހު، ނަބިއްޔާ ﷺގެ އަރިހަށް އައިސް ސަލާމް ދެންނެވިއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެނބުރި ގޮސް (އަލުން) ނަމާދު ކުރާށެވެ. އެހެނީ ހަމަކަށަވަރުން ތިބާ އަދި ނަމާދެއް ނުކުރެއެވެ.“ ދެން އެ މީހާ އެނބުރި ގޮސް ކުރިއެކޭ އެއްފަދައިން ނަމާދު ކުރުމަށް ފަހު، ނަބިއްޔާގެ އަރިހަށް އައިސް ސަލާމް ދެންނެވިއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެނބުރި ގޮސް (އަލުން) ނަމާދު ކުރާށެވެ. އެހެނީ ހަމަކަށަވަރުން ތިބާ އަދި ނަމާދެއް ނުކުރެއެވެ.“ މި ހާދިސާ ތިން ފަހަރު ހިނގިއެވެ. ދެން އެ މީހާ ދެންނެވިއެވެ: އިބަ ނަބިއްޔާ ހައްގައިގެން ފޮނުއްވި ފަރާތް ގަންދީ ބުނަމެވެ. މިއަށްވުރެ ރަނގަޅަށް ނަމާދު ކުރަން ތިމަންނައަކަށް ނޭނގެއެވެ. ފަހެ ތިމަންނައަށް އުނގަންނަވައި ދެއްވާށެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ ނަމާދަށް ތެދުވާ ހިނދު ތަކްބީރު ދޭށެވެ. އެއަށްފަހު ތިބާއަށް ފަސޭވާ މިންވަރެއް ޤުރްއާނުން ކިޔަވާށެވެ. ދެން ތިބާ ހަމަޖެހިލެވެންދެން ރުކޫ ކުރާށެވެ. އެއަށްފަހު ބޯ އުފުލައި ތިބާ ސީދާވަންދެން (އިއުތިދާލުގައި) ތެދުވާށެވެ. ދެން ތިބާ ހަމަޖެހިލެވެންދެން ސަޖިދަ ކުރާށެވެ. އެއަށްފަހު ބޯ އުފުލައި ތިބާ ހަމަޖެހިލެވެންދެން އިށީންނާށެވެ. އަދި ތިބާގެ މުޅި ނަމާދުގައި އެފަދައިން ކަންކުރާށެވެ.“","[البخاري 793، ومسلم 397]"],["#102","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب القراءة في الصلاة","ނަމާދުގައި (ޤުރްއާން) ކިޔެވުމުގެ ބާބު","عَنْ عُبَادَةَ بْنِ الصَّامِتِ - رَضِيَ اللَّهُ عَنْهُ - أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «لا صَلاةَ لِمَنْ لَمْ يَقْرَأْ بِفَاتِحَةِ الْكِتَابِ»","ޢުބާދާ ބިން އައްޞާމިތު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ފާތިޙަތުލް ކިތާބު (އެބަހީ: ސޫރަތުލް ފާތިޙާ) ނުކިޔައިފި މީހަކަށް ނަމާދެއް ނުވެއެވެ.“","[البخاري 756، ومسلم 394]"],["#103","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب القراءة في الصلاة","ނަމާދުގައި (ޤުރްއާން) ކިޔެވުމުގެ ބާބު","عَنْ أَبِي قَتَادَةَ الأَنْصَارِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «كَانَ رَسُولُ اللَّهِ ﷺ يَقْرَأُ فِي الرَّكْعَتَيْنِ الأُولَيَيْنِ مِنْ صَلاةِ الظُّهْرِ بِفَاتِحَةِ الْكِتَابِ وَسُورَتَيْنِ، يُطَوِّلُ فِي الأُولَى، وَيُقَصِّرُ فِي الثَّانِيَةِ، وَيُسْمِعُ الآيَةَ أَحْيَاناً، وَكَانَ يَقْرَأُ فِي الْعَصْرِ بِفَاتِحَةِ الْكِتَابِ وَسُورَتَيْنِ يُطَوِّلُ فِي الأُولَى، وَيُقَصِّرُ فِي الثَّانِيَةِ وَفِي الرَّكْعَتَيْنِ الأُخْرَيَيْنِ بِأُمِّ الْكِتَابِ. وَكَانَ يُطَوِّلُ فِي الرَّكْعَةِ الأُولَى مِنْ صَلاةِ الصُّبْحِ، وَيُقَصِّرُ فِي الثَّانِيَةِ»","އަބޫ ގަތާދާ އަލްއަންޞާރީ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: މެންދުރު ނަމާދުގެ ފުރަތަމަ ދެ ރަކުއަތުގައި ރަސޫލު ﷲ ﷺ ފާތިޙަތުލް ކިތާބު (އެބަހީ: ސޫރަތުލް ފާތިޙާ) އާއި (އެހެން) ދެ ސޫރަތެއް ކިޔަވައި ވިދާޅުވެއެވެ. ފުރަތަމަ ރަކުއަތުގައި ކިޔެވުން ދިގު ކުރައްވައި، ދެވަނަ ރަކުއަތުގައި ކުރު ކުރައްވައެވެ. އަދި ބައެއް ފަހަރު ކިޔަވައި ވިދާޅުވާ އާޔަތްތައް އިއްވަވައެވެ. (އެބަހީ: އަޑުހަރުކޮށް ކިޔަވައި ވިދާޅުވުމަކާ ނުލައެވެ.) އަދި އަސުރު ނަމާދުގައި ވެސް ފާތިޙަތުލް ކިތާބާއި (އެހެން) ދެ ސޫރަތެއް ކިޔަވައި ވިދާޅުވެއެވެ. ފުރަތަމަ ރަކުއަތުގައި ކިޔެވުން ދިގު ކުރައްވައި، ދެވަނަ ރަކުއަތުގައި ކުރު ކުރައްވައެވެ. އަދި ފަހު ދެ ރަކުއަތުގައި އުންމުލް ކިތާބު (އެބަހީ: ސޫރަތުލް ފާތިޙާ) ކިޔަވައި ވިދާޅުވެއެވެ. އަދި ފަތިސް ނަމާދުގެ ފުރަތަމަ ރަކުއަތުގައި ވެސް ކިޔެވުން ދިގު ކުރައްވައި، ދެވަނަ ރަކުއަތުގައި ކުރު ކުރައްވައެވެ.","[البخاري 759 واللفظ له، ومسلم 451]"],["#104","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب القراءة في الصلاة","ނަމާދުގައި (ޤުރްއާން) ކިޔެވުމުގެ ބާބު","عَنْ جُبَيْرِ بْنِ مُطْعِمٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «سَمِعْتُ النَّبِيَّ ﷺ يَقْرَأُ فِي الْمَغْرِبِ بِالطُّورِ»","ޖުބައިރު ބިން މުޠުޢިމު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ މަޣްރިބު ނަމާދުގައި ޠޫރު ސޫރަތް ކިޔަވަނިކޮށް ތިމަން އަޑުއެހީމެވެ.","[البخاري 3050 واللفظ له، ومسلم 463]"],["#105","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب القراءة في الصلاة","ނަމާދުގައި (ޤުރްއާން) ކިޔެވުމުގެ ބާބު","عَنْ الْبَرَاءِ بْنِ عَازِبٍ رَضِيَ اللَّهُ عَنْهُما: «أَنَّ النَّبِيَّ ﷺ كَانَ فِي سَفَرٍ، فَصَلَّى الْعِشَاءَ الآخِرَةَ، فَقَرَأَ فِي إحْدَى الرَّكْعَتَيْنِ بِالتِّينِ وَالزَّيْتُونِ فَمَا سَمِعْتُ أَحَداً أَحْسَنَ صَوْتاً أَوْ قِرَاءَةً مِنْهُ»","އަލްބަރާއު ބިން ޢާޒިބު رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺ ދަތުރުފުޅެއްގެ މަތީގައި ވަނިކޮށް، އިޝާ ނަމާދު ކުރެއްވިއެވެ. އަދި (ފުރަތަމަ) ދެ ރަކުއަތުން ކުރެ ރަކުއަތެއްގައި ވައްތީނި ވައްޒައިތޫނި ސޫރަތް ކިޔަވައި ވިދާޅުވިއެވެ. އަދި ނަބިއްޔާއަށް ވުރެ ރިވެތި އަޑުފަޅަކުން ނުވަތަ ކިޔެވުމަކުން ކިޔަވައި ވިދާޅުވި އެކަކުވެސް ތިމަންނައެއް އަޑެއް ނާހަމެވެ.","[البخاري 767، ومسلم 464 واللفظ له]"],["#106","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب القراءة في الصلاة","ނަމާދުގައި (ޤުރްއާން) ކިޔެވުމުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا: «أَنَّ رَسُولَ اللَّهِ ﷺ بَعَثَ رَجُلاً عَلَى سَرِيَّةٍ فَكَانَ يَقْرَأُ لأَصْحَابِهِ فِي صَلاتِهِمْ، فَيَخْتِمُ بـ «قُلْ هُوَ اللَّهُ أَحَدٌ» فَلَمَّا رَجَعُوا ذَكَرُوا ذَلِكَ لِرَسُولِ اللهِ ﷺ فَقَالَ: سَلُوهُ لأَيِّ شَيْءٍ صَنَعَ ذَلِكَ؟ فَسَأَلُوهُ. فَقَالَ: لأَنَّهَا صِفَةُ الرَّحْمَنِ عَزَّ وَجَلَّ، فَأَنَا أُحِبُّ أَنْ أَقْرَأَ بِهَا. فَقَالَ رَسُولُ اللَّهِ ﷺ: أَخْبِرُوهُ: أَنَّ اللهَ تَعَالَى يُحِبُّهُ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ލަޝްކަރުގެ ބަޔަކާ ހަވާލުގައި މީހަކު ފޮނުއްވިއެވެ. އޭނާ އެ މީހުންނައިގެން (އިމާމުވެ) ނަމާދުކޮށް އުޅުނެވެ. އަދި އެ މީހާ (ފުރަތަމަ ދެ ރަކުއަތުގައި) ކިޔެވުން ނިންމާލަނީ قُلْ هُوَ اللَّهُ أَحَد އިންނެވެ. ދެން އެބައިމީހުން އެނބުރި އައުމުން، އެކަމުގެ ވާހަކަ ރަސޫލު ﷲ ﷺގެ އަރިހުގައި އެބައިމީހުން ދެންނެވިއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެފަދައިން ކަން ކުރީ ކީއްވެގެންތޯ އެ މީހާ ގާތު އަހާށެވެ.“ އެބައިމީހުން އޭނާ ގާތު ސުވާލު ކުރުމުން އެ މީހާ ދެންނެވިއެވެ: އެއީ އެ ސޫރަތުގައި އައްރަޙްމާން ޢައްޒަ ވަޖައްލަގެ ސިފަފުޅުތައް ވާތީއެވެ. އެހެންކަމުން އެ ސޫރަތް ކިޔަވަން ތިމަންނަ ލޯބި ކުރަމެވެ. ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ﷲ ތަޢާލާ އެ މީހާދެކެ ލޯބިވެ ވޮޑިގަންނަވާ ކަމުގެ ޚަބަރު އޭނާއަށް ދޭށެވެ.“","[البخاري 7375، ومسلم 813 واللفظ له]"],["#107","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب القراءة في الصلاة","ނަމާދުގައި (ޤުރްއާން) ކިޔެވުމުގެ ބާބު","عَنْ جَابِرِ بنِ عبدِ اللهِ رَضِيَ اللَّهُ عَنْهُما: أَنَّ النَّبِيَّ ﷺ قَالَ لِمُعَاذٍ: «فَلَوْلا صَلَّيْتَ بِسَبِّحِ اسْمَ رَبِّكَ الأَعْلَى، وَالشَّمْسِ وَضُحَاهَا، وَاللَّيْلِ إذَا يَغْشَى؟ فَإِنَّهُ يُصَلِّي وَرَاءَكَ الْكَبِيرُ وَالضَّعِيفُ وَذُو الْحَاجَةِ»","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺ މުޢާޛަށް ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ (މީސްތަކުންނައިގެން އިމާމުވެ) ނަމާދު ކުރާ ހިނދު سَبِّحِ اسْمَ رَبِّكَ الأَعْلَى، وَالشَّمْسِ وَضُحَاهَا، وَاللَّيْلِ إذَا يَغْشَى މި ސޫރަތްތައް ކިޔަވާ ނަމަ ކިހާ ރަނގަޅުހެއްޔެވެ. އެހެނީ ތިބާގެ ފަހަތުގައި މުސްކުޅިންނާއި ބަލިކަށި މީހުންނާއި ހާޖާތްތައް ޖެހިފައިވާ މީހުން ތިބޭނެކަން ކަށަވަރެވެ.“","[البخاري 705 واللفظ له، ومسلم 465]"],["#108","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب ترك الجهر ببسم الله الرحمن الرحيم","އަޑުހަރުކޮށް ބިސްމި ނުކިޔުމުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ النَّبِيَّ ﷺ وَأَبَا بَكْرٍ وَعُمَرَ رَضِيَ اللَّهُ عَنْهُما: كَانُوا يَسْتَفْتِحُونَ الصَّلاةَ بِ «الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ» \n\nوَفِي رِوَايَةٍ: «صَلَّيْتُ مَعَ أَبِي بَكْرٍ وَعُمَرَ وَعُثْمَانَ، فَلَمْ أَسْمَعْ أَحَدًا مِنْهُمْ يَقْرَأُ بِسْمِ اللهِ الرَّحْمَنِ الرَّحِيمِ»","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺ އަދި އަބޫ ބަކްރާއި ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا، ނަމާދު(ގައި އަޑުހަރުކޮށް ޤުރްއާން ކިޔަވަން) ފައްޓަވަނީ الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِين އިންނެވެ.\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: އަބޫ ބަކްރާއި ޢުމަރާއި ޢުޘްމާނުގެ ފަހަތްޕުޅުގައި ތިމަންނަ ނަމާދު ކުރީމެވެ. އަދި އެއިން އެއްވެސް ބޭކަލެއް بِسْمِ اللهِ الرَّحْمَنِ الرَّحِيمِ (އަޑުހަރުކޮށް) ކިޔަވައި ވިދާޅުވާ އަޑެއް ނާހާމެވެ.","[البخاري 743\nمسلم 399]"],["#109","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب ترك الجهر ببسم الله الرحمن الرحيم","އަޑުހަރުކޮށް ބިސްމި ނުކިޔުމުގެ ބާބު","وَلِمُسْلِمٍ: «صَلَّيْتُ خَلْفَ النَّبِيِّ ﷺ وَأَبِي بَكْرٍ وَعُمَرَ وَعُثْمَانَ فَكَانُوا يَسْتَفْتِحُونَ بِ «الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ»، لا يَذْكُرُونَ بِسْمِ اللَّهِ الرَّحْمَنِ الرَّحِيمِ فِي أَوَّلِ قِرَاءَةٍ وَلا فِي آخِرِهَا»","އަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ނަބިއްޔާ ﷺ އަދި އަބޫ ބަކްރާއި ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَاގެ ފަހަތްޕުޅުގައި ތިމަން ނަމާދު ކުރީމެވެ. އެ ބޭކަލުން ނަމާދު(ގައި އަޑުހަރުކޮށް ޤުރްއާން ކިޔަވަން) ފައްޓަވަނީ الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِين އިންނެވެ. އެ ބޭކަލުންގެ ފުރަތަމަ ކިޔެވުމާއި ފަހު ކިޔެވުމުގައި ވެސް بِسْمِ اللهِ الرَّحْمَنِ الرَّحِيمِ (އަޑުހަރުކޮށް) ނުކިޔުއްވައެވެ.","[مسلم 399]"],["#110","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب سجود السهو","ކުށުސަޖިދައިގެ ބާބު","عَنْ مُحَمَّدِ بْنِ سِيرِينَ، عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «صَلَّى بِنَا رَسُولُ اللهِﷺ إحْدَى صَلاتَيْ الْعَشِيِّ - قَالَ ابْنُ سِيرِينَ: وَسَمَّاهَا أَبُو هُرَيْرَةَ. وَلَكِنْ نَسِيتُ أَنَا - قَالَ: فَصَلَّى بِنَا رَكْعَتَيْنِ، ثُمَّ سَلَّمَ. فَقَامَ إلَى خَشَبَةٍ مَعْرُوضَةٍ فِي الْمَسْجِدِ، فَاتَّكَأَ عَلَيْهَا كَأَنَّهُ غَضْبَانُ وَوَضَعَ يَدَهُ الْيُمْنَى عَلَى الْيُسْرَى، وَشَبَّكَ بَيْنَ أَصَابِعِهِ. وَخَرَجَتِ السَّرَعَانُ مِنْ أَبْوَابِ الْمَسْجِدِ فَقَالُوا: قَصُرَتِ الصَّلاةُ - وَفِي الْقَوْمِ أَبُو بَكْرٍ وَعُمَرُ - فَهَابَا أَنْ يُكَلِّمَاهُ. وَفِي الْقَوْمِ رَجُلٌ فِي يَدَيْهِ طُولٌ، يُقَالُ لَهُ: ذُو الْيَدَيْنِ فَقَالَ: يَا رَسُولَ اللَّهِ، أَنَسِيتَ، أَمْ قَصُرَتِ الصَّلاةُ؟ قَالَ: لَمْ أَنْسَ وَلَمْ تُقْصَرْ. فَقَالَ: أَكَمَا يَقُولُ ذُو الْيَدَيْنِ؟ فَقَالُوا: نَعَمْ. فَتَقَدَّمَ فَصَلَّى مَا تَرَكَ. ثُمَّ سَلَّمَ، ثُمَّ كَبَّرَ وَسَجَدَ مِثْلَ سُجُودِهِ أَوْ أَطْوَلَ. ثُمَّ رَفَعَ رَأْسَهُ فَكَبَّرَ، ثُمَّ كَبَّرَ وَسَجَدَ مِثْلَ سُجُودِهِ أَوْ أَطْوَلَ. ثُمَّ رَفَعَ رَأْسَهُ وَكَبَّرَ. فَرُبَّمَا سَأَلُوهُ: ثُمَّ سَلَّمَ؟ قَالَ: فَنُبِّئْتُ أَنَّ عِمْرَانَ بْنَ حُصَيْنٍ قَالَ: ثُمَّ سَلَّمَ»","މުޙައްމަދު ބިން ސީރީން، އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ތިމަންމެންނައިގެން ދުވާލުގެ ނަމާދެއް ކުރެއްވިއެވެ. -އިބްނު ސީރީން ވިދާޅުވިއެވެ: އަދި އަބޫ ހުރައިރާ އެއީ ކޮން ނަމާދެއްކަން ވިދާޅުވިއެވެ. އެހެނަސް ތިމަންނަ ހަނދާން ނެތުނީއެވެ- އަބޫ ހުރައިރާ ވިދާޅުވިއެވެ: ނަބިއްޔާ ތިމަންމެންނައިގެން ނަމާދުގައި ދެ ރަކުއަތް ކުރެއްވުމަށް ފަހު ސަލާމް ދެއްވިއެވެ. ދެން ތެދުވެވަޑައިގެން ގޮސް މިސްކިތުގައި އޮތް ފިލާކޮޅެއްގައި ލެނގިލެއްވައިގެން އިންނެވިއެވެ. ނަބިއްޔާ ކޯފާވެވަޑައިގެން ހުންނެވި ފަދައެވެ. އަދި ނަބިއްޔާގެ ކަނާތް އަތްޕުޅު، ވާތް އަތްޕުޅު މަތީގައި ބާއްވައިގެން، އެއް އަތްޕުޅުގެ އިނގިލިފުޅުތައް އަނެއް އަތްޕުޅަށް ވައްދަވައިގެން ހުންނެވިއެވެ. ދެން އަވަހަށް ނުކުންނަ މީހުން މިސްކިތުގެ ދޮރުކޮޅުތަކުން ނުކުމެ ބުނެފިއެވެ: ނަމާދު ކުރު ކުރެވުނީއެވެ. އަދި (އެ ތަނުގައި) ހުރި މީހުންގެ ތެރޭގައި އަބޫ ބަކްރާއި ޢުމަރު ހިމެނުނެވެ. އެހެނަސް ނަބިއްޔާއާ ވާހަކަ ދައްކަވަން އެ ދެ ބޭކަލުން ފަސްޖެހުނެވެ. އަދި އެ މީހުންގެ ތެރޭގައި ޛުލް ޔަދައިނޭ ކިޔާ އަތްދިގު މީހަކު ހުއްޓެވެ. އޭނާ ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. އިބަ ނަބިއްޔާއަށް ހަނދުމަފުޅު ނެތިވަޑައިގަތީތޯއެވެ؟ ނޫނީ ނަމާދު ކުރު ކުރެވުނީތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާ ހަނދުމަފުޅު ނެތިވަޑައި ނުގަންނަވަމެވެ. އަދި ނަމާދެއް ކުރެއްވެސް ނުކުރެވެއެވެ.“ (ދެން މީސްތަކުންނަށް) ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ޛުލް ޔަދައިން އެ ބުނި ގޮތަށް (ނަމާދު ކުރެއްވުނީ ކުރުކޮށްތޯއެވެ؟)“ އެބައިމިހުން ދެންނެވިއެވެ: އާދޭހެވެ. ދެން ނަބިއްޔާ ކުރިއަށް ޖެހިވަޑައިގެން ނަމާދުގައި ދޫކޮށްލެވުނު ބައި އަލުން ކުރައްވައިގެން ސަލާމް ދެއްވިއެވެ. އަދި ތަކްބީރު ދެއްވުމަށް ފަހު، ނަބިއްޔާ އާންމުކޮށް ކުރައްވާ ވަރަށް ނުވަތަ އެއަށް ވުރެ ދިގުކޮށް ސަޖިދަ ކުރެއްވިއެވެ. ދެން އިސްތަށިފުޅު އުފުއްލަވައި ތަކްބީރު ދެއްވާފައި (ސަޖިދައިން ތެދުވެ އިށީނދެވަޑައިގަތެވެ.) އެއަށްފަހު އަނެއްކާވެސް ތަކްބީރު ދެއްވާފައި، ނަބިއްޔާ އާންމުކޮށް ކުރައްވާ ވަރަށް ނުވަތަ އެއަށް ވުރެ ދިގުކޮށް ސަޖިދަ ކުރެއްވިއެވެ. ދެން އިސްތަށިފުޅު އުފުއްލަވާފައި ތަކްބީރު ދެއްވާފައި (ސަޖިދައިން ތެދުވެ އިށީނދެވަޑައިގަތެވެ.) \n \n(ޙަދީޘްގެ ރާވީއަކު ވިދާޅުވިއެވެ:) ފަހަރެއްގައި އިބްނު ސީރީނަށް ސުވާލު ކުރެވުނެވެ: އެއަށްފަހު ނަބިއްޔާ ސަލާމް ދެއްވިތޯއެވެ؟ އިބްނު ސީރީން ވިދާޅުވިއެވެ: ތިމަންނައަށް އެނގުނު ގޮތުގައ ޢިމްރާނު ބިން ޙުޞައިން ވިދާޅުވިއެވެ: އެއަށްފަހު ނަބިއްޔާ ސަލާމް ދެއްވިއެވެ.","[البخاري 482 واللفظ له، ومسلم 573]"],["#111","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب سجود السهو","ކުށުސަޖިދައިގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ بُحَيْنَةَ - وَكَانَ مِنْ أَصْحَابِ النَّبِيِّ ﷺ «أَنَّ النَّبِيَّ ﷺ صَلَّى بِهِمْ الظُّهْرَ فَقَامَ فِي الرَّكْعَتَيْنِ الأُولَيَيْنِ، وَلَمْ يَجْلِسْ. فَقَامَ النَّاسُ مَعَهُ، حَتَّى إذَا قَضَى الصَّلاةَ، وَانْتَظَرَ النَّاسُ تَسْلِيمَهُ: كَبَّرَ وَهُوَ جَالِسٌ. فَسَجَدَ سَجْدَتَيْنِ قَبْلَ أَنْ يُسَلِّمَ ثُمَّ سَلَّمَ»","ނަބިއްޔާ ﷺގެ ސަހާބީއެއް ކަމުގައިވާ ޢަބްދު ﷲ ބިން ބުޙައިނާގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ތިމަންމެންނައިގެން ނަބިއްޔާ ﷺ މެނދުރު ނަމާދު ކުރެއްވިއެވެ. ފުރަތަމަ ދެ ރަކުއަތަށް ފަހު އެކަލޭގެފާނު (ހަނދުމަފުޅު ނެތިވަޑައިގެން ފުރަތަމަ އައްތަހިއްޔާތައް) އިށިނދެވަޑައި ނުގަނެ، ކޮއްޅަށް ތެދުވެވަޑައިގަތެވެ. އަދި މީސްތަކުން ވެސް އެކަލޭގެފާނާއެކު ކޮއްޅަށް ތެދުވިއެވެ. ދެން ނަމާދު ނިންމަވައި، ނަބިއްޔާ ސަލާމް ދެއްވަންދެން މީސްތަކުން އިންތިޒާރު ކުރަނިކޮށް، އެކަލޭގެފާނު އިށީންދެވަޑައިގެން އިންނަވަނިކޮށް ތަކްބީރު ދެއްވައިފިއެވެ. އަދި ސަލާމް ދެއްވުމުގެ ކުރިން (ކުށު) ދެ ސަޖިދަޖެއްސެވިއެވެ. އެއަށްފަހު ސަލާމް ދެއްވިއެވެ.","[البخاري 829 واللفظ له، ومسلم 570]"],["#112","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب المرور بين يدي المصلي","ނަމާދު ކުރާ މީހާގެ ކުރިމަތިން ހުރަސްކުރުމުގެ ބާބު","عَنْ أَبِي جُهَيْمِ بْنِ الْحَارِثِ بْنِ الصِّمَّةِ الأَنْصَارِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «لَوْ يَعْلَمُ الْمَارُّ بَيْنَ يَدَيْ الْمُصَلِّي مَاذَا عَلَيْهِ مِنْ الإِثْمِ؟ لَكَانَ أَنْ يَقِفَ أَرْبَعِينَ خَيْرًا لَهُ مِنْ أَنْ يَمُرَّ بَيْنَ يَدَيْهِ» قَالَ أَبُو النَّضْرِ: لا أَدْرِي: قَالَ أَرْبَعِينَ يَوْماً أَوْ شَهْراً أَوْ سَنَةً.","އަބީ ޖުހައިމު ބިން އަލްޙާރިޘް ބިން އައްޞިއްމާ އަލްއަންޞާރީ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ނަމާދު ކުރާ މީހެއްގެ ކުރިމަތިން ހުރަސްކޮށްފަދާ މީހާ، އޭނާގެ މައްޗަށްވާ ފާފަ އެނގޭ ނަމަ، ފަހެ ހަމަކަށަވަރުން ކުރިމަތިން ހުރަސްކުރުމަށް ވުރެ، ސާޅީސް ވަންދެން (ހުރަސް ނުކޮށް) ކޮއްޅަށް ހުރުން ވެސް އޭނާއަށް މާ ހެޔޮވީހެވެ.“ (ޙަދީޘްގެ ރާވީއެއް ކަމަށްވާ) އަބޫ އައްނަޟްރު ވިދާޅުވިއެވެ: ނަބިއްޔާ ޙަދީޘް ކުރެއްވީ ސާޅީސް ދުވަސްކަމެއް، މަސްކަމެއް ނުވަތަ އަހަރުކަމެއް ތިމަންނައަކަށް ނޭނގެއެވެ.","[البخاري 510، ومسلم 507]"],["#113","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب المرور بين يدي المصلي","ނަމާދު ކުރާ މީހާގެ ކުރިމަތިން ހުރަސްކުރުމުގެ ބާބު","عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: سَمِعْتُ رَسُولَ اللَّهِ ﷺ يَقُولُ: «إذَا صَلَّى أَحَدُكُمْ إلَى شَيْءٍ يَسْتُرُهُ مِنْ النَّاسِ، فَأَرَادَ أَحَدٌ أَنْ يَجْتَازَ بَيْنَ يَدَيْهِ فَلْيَدْفَعْهُ. فَإِنْ أَبَى فَلْيُقَاتِلْهُ. فَإِنَّمَا هُوَ شَيْطَانٌ»","އަބޫ ސަޢީދު އަލްޚުދްރީ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ”ތިޔަބައިމީހުން ކުރެ މީހާކު، މީސްތަކުންނާ ވަކިކުރާ ސުތުރާއަކާ ކުރިމަތިލައިގެން ހުރެ ނަމާދު ކުރަނިކޮށް، އެހެން މީހަކު އައިސް އޭނާގެ ކުރިމަތިން ހުރަސްކުރަން އުޅޭ ނަމަ، ފަހެ އެ މީހަކު ހުއްޓުވައި ދުރު ކުރާށެވެ. އަދި އެ މީހަކު (ހުރަސް ނުކޮށް) ދޫކޮށްލުމަށް ދެކޮޅުހަދައިފި ނަމަ، ފަހެ (އެއަށް ވުރެ ވަރުގަދައަށް އޭނާ ހުއްޓުވައި ދުރުކުރާށެވެ.) އެހެނީ އެއީ ހަމަ ޝައިތާނެއް ކަމުގައި މެނުވީ ނުވެއެވެ.“","[البخاري 509 واللفظ له، ومسلم 505]"],["#114","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب المرور بين يدي المصلي","ނަމާދު ކުރާ މީހާގެ ކުރިމަތިން ހުރަސްކުރުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: «أَقْبَلْتُ رَاكِباً عَلَى حِمَارٍ أَتَانٍ، وَأَنَا يَوْمَئِذٍ قَدْ نَاهَزْتُ الاحْتِلامَ، وَرَسُولُ اللَّهِ ﷺ يُصَلِّي بِالنَّاسِ بِمِنًى إلَى غَيْرِ جِدَارٍ. مَرَرْتُ بَيْنَ يَدَيْ بَعْضِ الصَّفِّ فَنَزَلْتُ، فَأَرْسَلْتُ الأَتَانَ تَرْتَعُ. وَدَخَلْتُ فِي الصَّفِّ، فَلَمْ يُنْكِرْ ذَلِكَ عَلَيَّ أَحَدٌ»","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އަންހެން ހިމާރެއްގެ މަތީ ތިމަންނަ (މިނާއަށް) އައީމެވެ. އެއީ ތިމަންނަ ބާލިޣުވާން ކައިރިވެފައިވާ ދުވަސްވަރެކެވެ. އަދި ރަސޫލު ﷲ ﷺ މިނާގައި ފާރު ނޫން އެއްޗަކާ ކުރިމަތި ލައްވައިގެން، މީސްތަކުންނައިގެން ނަމާދު ކުރައްވަން ހުންނެވިއެވެ. ދެން ސަފުގެ ބައެއް (މީހުންގެ) ކުރިމަތިންލާފައި ތިމަން ގޮސް، ހިމާރުން ފައިބައި އެ ހުއިހައްޕަން ދޫކޮށްލުމަށް ފަހު (ނަމާދު ކުރަން) ތިމަން ވެސް ސަފަށް ވަނީމެވެ. އަދި އެކަމާ ގުޅިގެން އެއްވެސް މީހަކު ތިމަންނައަކަށް އިންކާރެއް ނުކުރެއެވެ.","[البخاري 493 واللفظ له، ومسلم 504]"],["#115","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب المرور بين يدي المصلي","ނަމާދު ކުރާ މީހާގެ ކުރިމަތިން ހުރަސްކުރުމުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا قَالَتْ: «كُنْتُ أَنَامُ بَيْنَ يَدَيْ رَسُولِ اللَّهِ ﷺ وَرِجْلايَ فِي قِبْلَتِهِ - فَإِذَا سَجَدَ غَمَزَنِي، فَقَبَضْتُ رِجْلَيَّ. فَإِذَا قَامَ بَسَطْتُهُمَا. وَالْبُيُوتُ يَوْمَئِذٍ لَيْسَ فِيهَا مَصَابِيحُ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺގެ ކުރިފުޅުމަތީގައި ތިމަން ކަމަނާ އަވަހާރަފުޅު ލައްޕަވަމެވެ. އަދި ތިމަން ކަމަނާގެ ދެ ފައި ވަނީ ނަބިއްޔާ ނަމާދު ކުރެއްވި ދިމާގައެވެ. ފަހެ ނަބިއްޔާ ސަޖިދަ ކުރައްވާ ހިނދު، ތިމަން ކަމަނާގެ (ފައިގައި) ނަބިއްޔާ ކޮއްޕަވާލައްވައެވެ. ދެން ތިމަން ކަމަނާގެ ދެ ފައި ކައިރިކޮށްލަމެވެ. އަދި ނަބިއްޔާ ކޮއްޅަށް ތެދުވެ ވަޑައިގެންނަވާ ހިނދު، ތިމަން ކަމަނާގެ ދެ ފައި އަނެއްކާވެސް ދަމާލަމެވެ. އަދި އެ ދުވަސްވަރު ގެތަކުގައި ބައްތިއެއް ނުހުރެއެވެ.","[البخاري 382، ومسلم 512]"],["#116","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب جامع","(އެކި ކަންކަމާ ބެހޭ) ޖާމިއު ބާބު","عَنْ أَبِي قَتَادَةَ بْنِ رِبْعِيٍّ الأَنْصَارِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ ﷺ: «إذَا دَخَلَ أَحَدُكُمْ الْمَسْجِدَ فَلا يَجْلِسْ حَتَّى يُصَلِّيَ رَكْعَتَيْنِ»","އަބޫ ގަތާދާ ބިން ރިބްޢީ އަލްއަންޞާރީ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ކުރެ މީހަކު މިސްކިތަށް ވަދެއްޖެ ނަމަ، ދެ ރަކުއަތް ކުރުމުގެ ކުރިން ނީށިންނާށެވެ.“","[البخاري 1163 واللفظ له، ومسلم 714]"],["#117","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب جامع","(އެކި ކަންކަމާ ބެހޭ) ޖާމިއު ބާބު","عَنْ زَيْدِ بْنِ أَرْقَمَ قَالَ: «كُنَّا نَتَكَلَّمُ فِي الصَّلاةِ، يُكَلِّمُ الرَّجُلُ صَاحِبَهُ، وَهُوَ إلَى جَنْبِهِ فِي الصَّلاةِ، حَتَّى نَزَلَتْ «وَقُومُوا لِلَّهِ قَانِتِينَ» فَأُمِرْنَا بِالسُّكُوتِ وَنُهِينَا عَنْ الْكَلامِ»","ޒައިދު ބިން އަރްގަމް ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: (ކުރީގައި) ތިމަންމެން ނަމާދުގައި ހުންނަ އިރު ވާހަކަ ދައްކާ ހަދަމެވެ. ނަމާދުގައި މީހަކާ ޖެހިގެން ހުރި އަނެއް މީހާއަށް ވާހަކަ ދައްކައެވެ. އެއީ ’އަދި ތިޔަބައިމީހުން ﷲ އަށް ކިޔަމަންތެރިވެ މަޑުމައިތިރކަމާއެކު (ނަމާދުގައި) ގާއިމުވެ ތިބޭށޭ‘ (މި އާޔަތް) ބާވައިލެއްވުމަށް ދާންދެނެވެ. ދެން ތިމަންމެން ސުކޫތުވުމަށް އަމުރު ކުރެވުނެވެ. އަދި ވާހަކަ ދެއްކުން ތިމަންމެންނަށް މަނާ ކުރެވުނެވެ.","[البخاري 1200، ومسلم 539 واللفظ له]"],["#118","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب جامع","(އެކި ކަންކަމާ ބެހޭ) ޖާމިއު ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ وَأَبِي هُرَيْرَةَ رَضِيَ اللَّهُ عَنْهُم عَنْ رَسُولِ اللَّهِ ﷺ أَنَّهُ قَالَ: «إذَا اشْتَدَّ الْحَرُّ فَأَبْرِدُوا بِالصَّلاةِ. فَإِنَّ شِدَّةَ الْحَرِّ مِنْ فَيْحِ جَهَنَّمَ»","ޢަބްދު ﷲ ބިން ޢުމަރު އަދި އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُمْގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”(އިރުގެ) ހޫނުކަން ގަދަވެއްޖެ ނަމަ، (ކޮންމެވެސް ވަރަކަށް ހޫނު މަޑުވެ) ފިނިވެލީމާ ތިޔަބައިމީހުން ނަމާދު ކުރާށެވެ. އެހެނީ ހަމަކަށަވަރުން ހޫނުގަދަވުމަކީ ނަރަކައިގެ ކެކިއަރައިގަތުމުން އަންނަ ކަމެކެވެ.“","[البخاري 533، 534، ومسلم 615]"],["#119","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب جامع","(އެކި ކަންކަމާ ބެހޭ) ޖާމިއު ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «مَنْ نَسِيَ صَلاةً فَلْيُصَلِّهَا إذَا ذَكَرهَا، وَلا كَفَّارَةَ لَهَا إلاَّ ذَلِكَ. أَقِمْ الصَّلاةَ لِذِكْرِي» \n\nوَلِمُسْلِمٍ «مَنْ نَسِيَ صَلاةً، أَوْ نَامَ عَنْهَا. فَكَفَّارَتُهَا: أَنْ يُصَلِّيَهَا إذَا ذَكَرَهَا»","އަނަސް ބިން މާލިކު ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”މީހަކަށް ނަމާދެއް ހަނދާން ނެތިއްޖެ ނަމަ، ފަހެ އޭނާއަށް ހަނދާންވުމުން އެ ނަމާދެއް ކުރާށެވެ. އަދި އެ ނޫން (ބަދަލެއް) ކައްފާރާއެއް އެ ކަމަކަށް ނުވެއެވެ.“ (ދެން ﷲ ތަޢާލާގެ މި އާޔަތް ކިޔަވައި ވިދާޅުވިއެވެ:) ’އަދި ތިމަން އިލާހު ހަނދުމަ ކުރުމަށްޓަކައި ނަމާދު ގާއިމު ކުރާށެވެ.‘\n\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: މީހަކަށް ނަމާދެއް ހަނދާން ނެތިއްޖެ ނަމަ، ނޫނީ ނުކޮށް ނިދިއްޖެ ނަމަ، ފަހެ އެ ކަމުގެ ކައްފާރާއަކީ ހަނދާންވުމުން އެ ނަމާދެއް ކުރުމެވެ.","[البخاري 597 واللفظ له، ومسلم 784\n\nمسلم 784]"],["#120","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب جامع","(އެކި ކަންކަމާ ބެހޭ) ޖާމިއު ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ «أَنَّ مُعَاذَ بْنَ جَبَلٍ: كَانَ يُصَلِّي مَعَ رَسُولِ اللهِ ﷺ عِشَاءَ الآخِرَةِ. ثُمَّ يَرْجِعُ إلَى قَوْمِهِ، فَيُصَلِّي بِهِمْ تِلْكَ الصَّلاةَ»","ޖާބިރު ބިން ޢަބްދު ﷲ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން މުޢާޛު ބިން ޖަބަލު (އެންމެ ފަހުން ކުރާ ނަމާދު ކަމުގައިވާ) އިޝާ ނަމާދު ރަސޫލު ﷲ ﷺގެ އަރިހުގައި ކުރައްވައެވެ. އެއަށްފަހު މުޢާޛުގެ ގައުމުގެ މީހުން ގާތަށް އެކަލޭގެފާނު އެނބުރި ގޮސް، އެބައިމީހުންނައިގެން މުޢާޛު އިމާމުވެ، އެ ނަމާދު (އަލުން) ކުރައްވައެވެ.","[البخاري 700، ومسلم 465 واللفظ له]"],["#121","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب جامع","(އެކި ކަންކަމާ ބެހޭ) ޖާމިއު ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ قَالَ: «كُنَّا نُصَلِّي مَعَ رَسُولِ اللَّهِ ﷺ فِي شِدَّةِ الْحَرِّ. فَإِذَا لَمْ يَسْتَطِعْ أَحَدُنَا أَنْ يُمَكِّنَ جَبْهَتَهُ مِنَ الأَرْضِ: بَسَطَ ثَوْبَهُ فَسَجَدَ عَلَيْهِ»","އަނަސް ބިން މާލިކު ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ހޫނުގަދަވެފައި ވަނިކޮށް ރަސޫލު ﷲ ﷺގެ އަރިހުގައި ތިމަންމެން (މެންދުރު) ނަމާދު ކުރަމެވެ. ފަހެ ތިމަންމެން ކުރެ މީހަކަށް ބިންމަތީގައި ނިއްކުރި ޖައްސާލަން ނުކެރޭވަރު ވުމުން، (އެ ތަނުގައި) އެ މިހެއްގެ ހެދުން (ދާމާލާފައި ނުވަތަ) ފަތުރާލާފައި އޭގެ މަތީ ސަޖިދަ ކުރެއެވެ.","[البخاري 1208 واللفظ له، ومسلم 620]"],["#122","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب جامع","(އެކި ކަންކަމާ ބެހޭ) ޖާމިއު ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «لا يُصَلِّي أَحَدُكُمْ فِي الثَّوْبِ الْوَاحِدِ، لَيْسَ عَلَى عَاتِقِهِ مِنْهُ شَيْءٌ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ކުރެ އެކަކުވެސް އޭނާގެ ކޮނޑު ނިވާ ނުކުރާ، އެންމެ ފޭރާމެއް ލައިގެން ހުރެ ނަމާދު ނުކުރާށެވެ.“","[البخاري 359، ومسلم 516]"],["#123","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب جامع","(އެކި ކަންކަމާ ބެހޭ) ޖާމިއު ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رَضِيَ اللَّهُ عَنْهُما عَنِ النَّبِيِّ ﷺ أَنَّهُ قَالَ: «مَنْ أَكَلَ ثُوماً أَوْ بَصَلاً. فَلْيَعْتَزِلْنَا أَوْ لِيَعْتَزِلْ مَسْجِدَنَا، وَلْيَقْعُدْ فِي بَيْتِهِ. وَأُتِيَ بِقِدْرٍ فِيهِ خَضِرَاتٌ مِنْ بُقُولٍ. فَوَجَدَ لَهَا رِيحاً، فَسَأَلَ؟ فَأُخْبِرَ بِمَا فِيهَا مِنْ الْبُقُولِ. فَقَالَ: قَرِّبُوهَا إلَى بَعْضِ أَصْحَابِي. فَلَمَّا رَآهُ كَرِهَ أَكْلَهَا. قَالَ: كُلْ. فَإِنِّي أُنَاجِي مَنْ لا تُنَاجِي»","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ލޮނުމެދު ނުވަތަ ފިޔާ ކައިފި މީހަކު، ތިމަންމެންނާ ދުރުވެގަންނާށެވެ. ނުވަތަ ތިމަންމެންގެ މިސްކިތުން ދުރުވެގަންނާށެވެ. އަދި އެ މީހެއްގެ ގޭގައި މަޑުކުރާށެވެ.“ (އެއްފަހަރަކު) ތެއްޔެއްގައި ކައްކާފައި ހުރި ތަރުކާރިތަކެއް ނަބިއްޔާގެ އަރިހަށް ގެނެވުނެވެ. ނަބިއްޔާއަށް އެއިން ވަހެއް ފާހަގަކުރެއްވުމުން، އެއާ ބެހޭ ގޮތުން ސުވާލު ކުރެއްވިއެވެ. ދެން އޭގައި ހުރި ތަރުކާރިތައް ނަބިއްޔާއަށް ކިޔައިދެވުނެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާގެ ސަހާބީގެ ގާތަށް އޭތި ކައިރި ކުރާށެވެ.“ ނަބިއްޔާއަށް އެ ފަރީއްކުޅުވާން އެދިވަޑައި ނުގަތްކަން އެ ސަހާބީއަށް ފެނުމުން، ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ ކާށެވެ. އެހެނީ ހަމަކަށަވަރުން ތިބާ ވާހަކަ ނުދައްކާ ފަރާތްތަކާ، ތިމަން ނަބިއްޔާ ވަކިން ވާހަކަފުޅު ދައްކަވަމެވެ.“","[البخاري 855، ومسلم 564]"],["#124","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب جامع","(އެކި ކަންކަމާ ބެހޭ) ޖާމިއު ބާބު","عَنْ جَابِرِ بنِ عبدِ اللهِ رَضِيَ اللَّهُ عَنْهُمَا أَنَّ النَّبِيَّ ﷺ قَالَ: «مَنْ أَكَلَ الثُّومَ وَالْبَصَلَ وَالْكُرَّاثَ فَلا يَقْرَبَنَّ مَسْجِدَنَا، فَإِنَّ الْمَلائِكَةَ تَتَأَذَّى مِمَّا يَتَأَذَّى مِنْهُ الإِنْسَانُ» \n\nوفي روايةٍ «بني آدمَ»","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ލޮނުމެދު، ފިޔާ ނުވަތަ ލީކްސް ކައިފި މީހަކު، ތިމަންމެންގެ މިސްކިތާ ކައިރި ނުވާށެވެ. އެހެނީ ހަމަކަށަވަރުން އިންސާނާއަށް އުނދަގޫވާ ތަކެތިން މަލާއިކަތުންނަށް ވެސް އުނދަގޫވެއެވެ.“\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”އާދަމުގެ ދަރިންނަށް (އުނދަގޫވާ ތަކެތިން މަލާއިކަތުންނަށް ވެސް އުނދަގޫވެއެވެ.)“","[مسلم 464]"],["#125","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب التشهد","ތަހިއްޔާތުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بنِ مَسْعُودٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «عَلَّمَنِي رَسُولُ اللَّهِ ﷺ التَّشَهُّدَ - كَفِّي بَيْنَ كَفَّيْهِ - كَمَا يُعَلِّمُنِي السُّورَةَ مِنْ الْقُرْآنِ: التَّحِيَّاتُ لِلَّهِ، وَالصَّلَوَاتُ وَالطَّيِّبَاتُ، السَّلامُ عَلَيْكَ أَيُّهَا النَّبِيُّ وَرَحْمَةُ اللَّهِ وَبَرَكَاتُهُ. السَّلامُ عَلَيْنَا وَعَلَى عِبَادِ اللَّهِ الصَّالِحِينَ. أَشْهَدُ أَنْ لا إلَهَ إلاَّ اللَّهُ وَأَشْهَدُ أَنَّ مُحَمَّداً عَبْدُهُ وَرَسُولُهُ»","ޢަބްދު ﷲ ބިން މަސްޢޫދު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺގެ ދެއަތްޕުޅު ތެރޭގައި ތިމަންނަގެ އަތް ވަނިކޮށް، ނަބިއްޔާ ތިމަންނައަށް (ފުރަތަމަ) ތަހިއްޔާތު އުނގަންނަވައި ދެއްވިއެވެ. އެއީ ހަމަ ޤުރްއާނުގެ ސޫރަތްތައް ތިމަންނައަށް އުނގަންވައިދެއްވި ފަދައިންނެވެ. (ފުރަތަމަ ތަހިއްޔާތުގައި ވިދާޅުވީ:) ”التَّحِيَّاتُ لِلَّهِ، وَالصَّلَوَاتُ وَالطَّيِّبَاتُ، السَّلامُ عَلَيْكَ أَيُّهَا النَّبِيُّ وَرَحْمَةُ اللَّهِ وَبَرَكَاتُهُ. السَّلامُ عَلَيْنَا وَعَلَى عِبَادِ اللَّهِ الصَّالِحِينَ. أَشْهَدُ أَنْ لا إلَهَ إلاَّ اللَّهُ وَأَشْهَدُ أَنَّ مُحَمَّداً عَبْدُهُ وَرَسُولُهُ (އެންމެހައި މަތިވެރިކަމަކާއި، ސަލަވާތާއި، ހެޔޮ ކަމެއް ލިބިވޮޑިގެންވަނީ ﷲ އަށެވެ. އޭ ނަބިއްޔާއެވެ. އިބަ ނަބިއްޔާގެ މައްޗަށް ސަލާމާއި ﷲގެ ރަހުމަތާއި ބަރަކާތް ލައްވާށިއެވެ. އަޅަމެންނަށާއި ﷲގެ ސާލިހު އަޅުތަކުންގެ މައްޗަށް ވެސް ސަލާމް ލައްވާށިއެވެ. ﷲ ފިޔަވައި (އަޅުކަން ކުރުން ހައްގު) އެހެން އެއްވެސް އިލާހަކު ނުވާކަމަށާއި، މުޙައްމަދުގެފާނީ އެ އިލާހުގެ އަޅާ އަދި ރަސޫލާކަމަށް އަޅު ހެކިވަމެވެ.)“","[البخاري 6265 واللفظ له، ومسلم 402]"],["#126","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب التشهد","ތަހިއްޔާތުގެ ބާބު","وَفِي لَفْظٍ: «إذَا قَعَدَ أَحَدُكُمْ فِي الصَّلاةِ فَلْيَقُلْ: التَّحِيَّاتُ للهِ - وَذَكَرَهُ -\n\nوَفِيهِ: فَإِنَّكُمْ إذَا فَعَلْتُمْ ذَلِكَ فَقَدْ سَلَّمْتُمْ عَلَى كُلِّ عَبْدٍ صَالِحٍ فِي السَّمَاءِ وَالأَرْضِ \n- وَفِيهِ - فَلْيَتَخَيَّرْ مِنْ الْمَسْأَلَةِ مَا شَاءَ»","އަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”ތިޔަބައިމީހުންގެ ތެރެއިން މީހަކު ނަމާދުގައި (އައްތަހިޔާތަށް) އިށީނދެއްޖެ ނަމަ މިފަދައިން ކިޔާށެވެ: التَّحِيَّاتُ للهِ (އެންމެހައި މަތިވެރިކަމެއް ލިބިވޮޑިގެންވަނީ ﷲ އަށެވެ.)“ - ދެން ނަބިއްޔާ (އައްތަހިއްޔާތުގެ ދުއާ) ޒިކުރު ކުރެއްވިއެވެ. -\n\nއަދި އެ ޙަދީޘްގައި ވެއެވެ: ”އަދި ތިޔަބައިމީހުން އެހެން ހަދައިފި ނަމަ، ފަހެ ހަމަކަށަވަރުން އުޑާއި ބިމުގައިވާ ކޮންމެ ސާލިހު އަޅެއްގެ މައްޗަށް ތިޔަބައިމީހުން ސަލާމް ލެއްވުން އެދި ދުއާ ކުރެވުނީއެވެ.“\n\nއަދިވެސް އެ ޙަދީޘްގައި ވެއެވެ: ”އެއަށްފަހު އެ މީހަކު ބޭނުން ކަމަކަށް އެދި ދުއާ ކުރާށެވެ.“","[البخاري 6328، ومسلم 402\n\nالبخاري 1202 واللفظ له، ومسلم 402\n\nالبخاري 6328، ومسلم 402 واللفظ له]"],["#127","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب التشهد","ތަހިއްޔާތުގެ ބާބު","عَنْ عَبْدِ الرَّحْمَنِ بْنِ أَبِي لَيْلَى قَالَ: «لَقِيَنِي كَعْبُ بْنُ عُجْرَةَ فَقَالَ أَلا أُهْدِي لَكَ هَدِيَّةً؟ أَنَّ النَّبِيَّ ﷺ خَرَجَ عَلَيْنَا، فَقُلْنَا: يَا رَسُولَ اللَّهِ، قَدْ عَلَّمَنَا اللَّهُ كَيْفَ نُسَلِّمُ عَلَيْكَ: فَكَيْفَ نُصَلِّي عَلَيْكَ؟ فَقَالَ: قُولُوا: اللَّهُمَّ صَلِّ عَلَى مُحَمَّدٍ وَعَلَى آلِ مُحَمَّدٍ، كَمَا صَلَّيْتَ عَلَى إبْرَاهِيمَ إنَّكَ حَمِيدٌ مَجِيدٌ، وَبَارِكْ عَلَى مُحَمَّدٍ وَعَلَى آلِ مُحَمَّدٍ، كَمَا بَارَكْتَ عَلَى إبْرَاهِيمَ إنَّكَ حَمِيدٌ مَجِيدٌ»","ޢަބްދުއްރަޙްމާން ބިން އަބީ ލައިލާ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ކައުބް ބިން ޢުޖްރާ، ތިމަންނައާ ބައްދަލުވެގެން ދެންނެވިއެވެ: ތިބާއަށް ހަދީޔާއެއް ދެންތޯއެވެ؟ ހަމަކަށަވަރުން ނަބިއްޔާ ﷺ ތިމަންމެން ގާތަށް ނުކުމެވަޑައިގަތުމުން ތިމަންމެން ދެންނެވީމެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން އިބަ ނަބިއްޔާގެ މައްޗަށް ސަލާމް ލައްވާނެ ގޮތް ﷲ ވަނީ ތިމަންމެންނަށް އުނގަންނަވައި ދެއްވާފައެވެ. ފަހެ އިބަ ނަބިއްޔާގެ މައްޗަށް ސަލަވާތް ލައްވާނީ ކިހިނަކުންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ކިޔާށެވެ: اللَّهُمَّ صَلِّ عَلَى مُحَمَّدٍ وَعَلَى آلِ مُحَمَّدٍ، كَمَا صَلَّيْتَ عَلَى إبْرَاهِيمَ إنَّكَ حَمِيدٌ مَجِيدٌ، وَبَارِكْ عَلَى مُحَمَّدٍ وَعَلَى آلِ مُحَمَّدٍ، كَمَا بَارَكْتَ عَلَى إبْرَاهِيمَ إنَّكَ حَمِيدٌ مَجِيدٌ (ޔާ ﷲ! އިބަ އިލާހު އިބްރާހީމްގެފާނުގެ މައްޗަށް ސަލަވާތް ލެއްވި ފަދައިން، މުޙައްމަދުގެފާނަށާއި މުހައްމަދުގެފާނުގެ އާއިލާގެ މައްޗަށް ވެސް ސަލަވާތް ލައްވާށިއެވެ. ހަމަކަށަވަރުން އިބަ އިލާހި ހަމްދާއި މަތިވެރިކަން ލިބިގެންވާ އިލާހެވެ.)“","[البخاري 6357، ومسلم 406]"],["#128","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب التشهد","ތަހިއްޔާތުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «كَانَ رَسُولُ اللَّهِ ﷺ يَدْعُو: اللَّهُمَّ إنِّي أَعُوذُ بِكَ مِنْ عَذَابِ الْقَبْرِ، وَعَذَابِ النَّارِ، وَمِنْ فِتْنَةِ الْمَحْيَا وَالْمَمَاتِ، وَمِنْ فِتْنَةِ الْمَسِيحِ الدَّجَّالِ» \n \nوَفِي لَفْظٍ لِمُسْلِمٍ: «إذَا تَشَهَّدَ أَحَدُكُمْ فَلْيَسْتَعِذْ بِاَللَّهِ مِنْ أَرْبَعٍ، يَقُولُ: اللَّهُمَّ إنِّي أَعُوذُ بِكَ مِنْ عَذَابِ جَهَنَّمَ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ (މިފަދައިން) ދުއާ ކުރައްވައެވެ: ”اللَّهُمَّ إنِّي أَعُوذُ بِكَ مِنْ عَذَابِ الْقَبْرِ، وَعَذَابِ النَّارِ، وَمِنْ فِتْنَةِ الْمَحْيَا وَالْمَمَاتِ، وَمِنْ فِتْنَةِ الْمَسِيحِ الدَّجَّالِ (ޔާ ﷲ! ހަމަކަށަވަރުން ކަށްވަޅުގެ އަޒާބާއި، ނަރަކައިގެ އަޒާބާއި، ދިރިއުޅުމާއި މަރުވުމުގެ ފިތުނައިންނާއި، އަލްމަސީހުއް ދައްޖާލުގެ ފިތުނައިން، މި އަޅާ ރައްކާތެރި ކޮށްދެއްވުން އެދި އިބަ އިލާހަށް ދަންނަވަމެވެ.)“\n\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ”ތިޔަބައިމީހުން ކުރެ މީހަކު (ފުރަތަމަ) ތަހިއްޔާތު ކިޔައިފި ނަމަ، ފަހެ ހަތަރު ކަމަކުން ރައްކާތެރި ކޮށްދެއްވުން އެދި ﷲ އަށް ދަންނަވާށެވެ. (މިފަދައިން) ކިޔާށެވެ: اللَّهُمَّ إنِّي أَعُوذُ بِكَ مِنْ عَذَابِ جَهَنَّمَ (ޔާ ﷲ! ހަމަކަށަވަރުން މި އަޅާ ނަރަކައިގެ އަޒާބުން ރައްކާތެރި ކޮށްދެއްވުން އެދި އިބަ އިލާހަށް ދަންނަވަމެވެ.)“","[البخاري 1377 واللفظ له، ومسلم 588\n\nمسلم 588]"],["#129","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب التشهد","ތަހިއްޔާތުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَمْرِو بْنِ الْعَاصِ عَنْ أَبِي بَكْرٍ الصِّدِّيقِ رَضِيَ اللَّهُ عَنْهُم أَنَّهُ قَالَ لِرَسُولِ اللَّهِ ﷺ: «عَلِّمْنِي دُعَاءً أَدْعُو بِهِ فِي صَلاتِي. قَالَ: قُلْ: اللَّهُمَّ إنِّي ظَلَمْتُ نَفْسِي ظُلْماً كَثِيرَاً. وَلا يَغْفِرُ الذُّنُوبَ إلاَّ أَنْتَ. فَاغْفِرْ لِي مَغْفِرَةً مِنْ عِنْدِكَ. وَارْحَمْنِي، إنَّكَ أَنْتَ الْغَفُورُ الرَّحِيمُ»","ޢަބްދު ﷲ ބިން ޢަމްރު ބިން އަލްޢާޞް، އަބޫ ބަކްރު އައްސިއްދީގު رَضِيَ اللَّهُ عَنْهُمْގެ އަރިހުން ރިވާ ކުރެއްވިއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺގެ އަރިހުގައި އަބޫ ބަކްރު ދެންނެވިއެވެ: ތިމަންނަގެ ނަމާދުގައި ކުރެވޭނެ ދުއާއެއް އުނގަންނަވައި ދެއްވާށެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ކިޔާށެވެ: اللَّهُمَّ إنِّي ظَلَمْتُ نَفْسِي ظُلْماً كَثِيرَاً. وَلا يَغْفِرُ الذُّنُوبَ إلاَّ أَنْتَ. فَاغْفِرْ لِي مَغْفِرَةً مِنْ عِنْدِكَ. وَارْحَمْنِي، إنَّكَ أَنْتَ الْغَفُورُ الرَّحِيمُ (ޔާ ﷲ! މި އަޅާގެ އަމިއްލަ ނަފްސަށް ގިނަގިނައިން އަނިޔާ ކުރެވިއްޖެކަން ކަށަވަރެވެ. އަދި އިބަ އިލާހު ފިޔަވައި ފާފަ ފުއްސަވާނެ ފަރާތަކު ނުވެއެވެ. ފަހެ އިބަ އިލާހުގެ ހަޒްރަތުންވާ ފުއްސެވުމަކައިގެން މި އަޅާގެ ފާފަ ފުއްސަވާށިއެވެ. އަދި މި އަޅާއަށް ރަހުމަތް ލައްވާށިއެވެ. ހަމަކަށާވަރުން އިބަ އިލާހީ، ގިނަގިނައިން ފާފަ ފުއްސަވާ ރަޙީމްވަންތަ އިލާހެވެ.)“","[البخاري 834، ومسلم 2705]"],["#130","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب التشهد","ތަހިއްޔާތުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا قَالَتْ: «مَا صَلَّى رَسُولُ اللَّهِ ﷺ بَعْدَ أَنْ نَزَلَتْ عَلَيْهِ «إذَا جَاءَ نَصْرُ اللَّهِ وَالْفَتْحُ» - إلاَّ يَقُولُ فِيهَا: سُبْحَانَكَ رَبَّنَا وَبِحَمْدِكَ، اللَّهُمَّ اغْفِرْ لِي» \n\nوَفِي لَفْظٍ: «كَانَ رَسُولُ اللَّهِ ﷺ يُكْثِرُ أَنْ يَقُولَ فِي رُكُوعِهِ وَسُجُودِهِ: سُبْحَانَكَ اللَّهُمَّ رَبَّنَا وَبِحَمْدِكَ، اللَّهُمَّ اغْفِرْ لِي»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: إذَا جَاءَ نَصْرُ اللَّهِ وَالْفَتْحُ (މި ސޫރަތް) ބާވައިލެއްވުނު ފަހުން، ރަސޫލު ﷲ ﷺ ނަމާދު ކުރައްވާ ހިނދު (މިފަދައިން) ވިދާޅުނުވެ ނަމާދެއް ނުކުރައްވައެވެ: ”سُبْحَانَكَ رَبَّنَا وَبِحَمْدِكَ، اللَّهُمَّ اغْفِرْ لِي (އިބަ އިލާހު ހުސްތާހިރުވެ ވޮޑިގެންވެއެވެ. އަޅަމެންގެ ވެރި ރައްބެވެ. އިބަ އިލާހުގެ ހަމްދުން (އިބަ އިލާހަށް ސަނާ ދަންނަވަމެވެ.) ޔާ ﷲ! މި އަޅާގެ ފާފާ ފުއްސަވާށިއެވެ.)“ \n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ރަސޫލު ﷲ ﷺ ރުކޫއާއި ސަޖިދަ ކުރެއްވުމުގައި ވަރަށް ގިނައިން މިފަދައިން ވިދާޅުވެއެވެ: ”سُبْحَانَكَ اللَّهُمَّ رَبَّنَا وَبِحَمْدِكَ، اللَّهُمَّ اغْفِرْ لِي (އިބަ އިލާހު ހުސްތާހިރުވެ ވޮޑިގެންވެއެވެ. ޔާ ﷲ! އަޅަމެންގެ ވެރި ރައްބެވެ. އިބަ އިލާހުގެ ހަމްދުން (އިބަ އިލާހަށް ސަނާ ދަންނަވަމެވެ.) ޔާ ﷲ! މި އަޅާގެ ފާފާ ފުއްސަވާށިއެވެ.)“","[البخاري 4967 واللفظ له، ومسلم 484\n\nالبخاري 817، 4968، ومسلم 484]"],["#131","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الوتر","ވިތުރީގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما قَالَ: «سَأَلَ رَجُلٌ النَّبِيَّ ﷺ وَهُوَ عَلَى الْمِنْبَرِ - مَا تَرَى فِي صَلاةِ اللَّيْلِ؟ قَالَ: مَثْنَى، مَثْنَى. فَإِذَا خَشِيَ أَحَدُكُمْ الصُّبْحَ صَلَّى وَاحِدَةً. فَأَوْتَرَتْ لَهُ مَا صَلَّى. وَإِنَّهُ كَانَ يَقُولُ: اجْعَلُوا آخِرَ صَلاتِكُمْ بِاللَّيْلِ وِتْراً»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ މިންބަރުގައި ހުންނަވަނިކޮށް، ނަބިއްޔާގެ އަރިހުގައި މީހަކު ސުވާލު ދެންނެވިއެވެ: ރޭއަޅުކަމާ މެދު އިބަ ނަބިއްޔާ ދެކިލައްވަނީ ކިހިނަކުންތޯއެވެ؟ (އެބަހީ: ކުރާނީ ކިހިނަކުންތޯއެވެ؟) ނަބިއްޔާ ޙަދިޘް ކުރެއްވިއެވެ: ”ދެ ރަކުއަތުން ދެ ރަކުއަތުންނެވެ. ދެން ތިޔަބައިމީހުން ކުރެ މީހަކަށް ފަތިސްވެދާނޭ ކަމުގެ ބިރު ހީވާނަމަ، އެއް ރަކުއަތް (ކުރާށެވެ.) އޭރުން އޭނާ ކުރެވުނު ނަމާދުތަކަށް ވިތުރި ކުރެވުނީއެވެ.“ އަދި ހަމަކަށަވަރުން ނަބިއްޔާ ޙަދީޘް ކުރައްވައެވެ: ”ތިޔަބައިމީހުންގެ ރޭގަނޑުގެ ފަހު ނަމާދަކަށް ވިތުރި ހަދާށެވެ.“","[البخاري 472 واللفظ له، ومسلم 739]"],["#132","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الوتر","ވިތުރީގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا قَالَتْ: «مِنْ كُلِّ اللَّيْلِ أَوْتَرَ رَسُولُ اللَّهِ ﷺ: مِنْ أَوَّلِ اللَّيْلِ، وَأَوْسَطِهِ، وَآخِرِهِ. وَانْتَهَى وِتْرُهُ إلَى السَّحَرِ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރޭގަނޑުގެ ހުރިހާ ބައެއްގައި ވެސް (އެކި ފަހަރު މަތިން) ރަސޫލު ﷲ ﷺ ވިތުރި ކުރެއްވައެވެ. (އެ ގޮތުން) ރޭގަނޑުގެ ފުރަތަމަ ބައިގައާއި، މެދުތެރޭގައާއި، އަދި ފަހުބައިގައި ވެސް މެއެވެ. އަދި ފަތިސްވުމުގެ ކުރިން ނަބިއްޔާގެ ވިތުރިކޮށް ނިންމަވާލައްވައެވެ.","[البخاري 996، ومسلم 745 واللفظ له]"],["#133","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الوتر","ވިތުރީގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا قَالَتْ: «كَانَ رَسُولُ اللَّهِ ﷺ يُصَلِّي مِنْ اللَّيْلِ ثَلاثَ عَشْرَةَ رَكْعَةً يُوتِرُ مِنْ ذَلِكَ بِخَمْسٍ، لايَجْلِسُ فِي شَيْءٍ إلاَّ فِي آخِرِهَا»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ރޭއަޅުކަން ކުރައްވަނީ ތޭރަ ރަކުއަތުންނެވެ. އޭގެ ތެރެއިން (އަށް ރަކުއަތް ކުރައްވަނީ ދެ ރަކުއަތުން ދެ ރަކުއަތުންނެވެ.) ދެން ވިތުރީގެ ގޮތުގައި ފަސް ރަކުއަތް ކުރައްވައެވެ. އެ ފަސް ރަކުއަތުގެ ފަހު ރަކުއަތުގައި މެނުވީ (ތަހިއްޔާތަށް) އިށީންދެވަޑައި ނުގަންނަވައެވެ.","[مسلم 737]"],["#134","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الذكر عقب الصلاة","ނަމާދަށް ފަހު ޒިކުރު ކިޔުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما: «أَنَّ رَفْعَ الصَّوْتِ بِالذِّكْرِ، حِينَ يَنْصَرِفُ النَّاسُ مِنْ الْمَكْتُوبَةِ كَانَ عَلَى عَهْدِ رَسُولِ اللَّهِ ﷺ» قَالَ ابْنُ عَبَّاسٍ: «كُنْتُ أَعْلَمُ إذَا انْصَرَفُوا بِذَلِكَ إذَا سَمِعْتُهُ». \n\nوَفِي لَفْظٍ «مَا كُنَّا نَعْرِفُ انْقِضَاءَ صَلاةِ رَسُولِ اللَّهِ ﷺ بِالتَّكْبِيرِ».","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺގެ ޒަމާނުގައި، މީސްތަކުން ފަރުޟު ނަމާދުތައް ނިންމުމަށް ފަހު އަޑުހަރުކޮށް ޒިކުރުތައް ކިޔުއްވައެވެ. އިބްނު ޢައްބާސް ވިދާޅުވިއެވެ: އެ ޒިކުރުތައް ކިޔާ އަޑު ތިމަންނައަށް އިވުމުން، އެބައިމީހުން ނަމާދު ނިންމިކަން ތިމަންނައަށް އެނގިގެން ދެއެވެ.\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ރަސޫލު ﷲ ﷺ (ޒިކުރުގައި) ތަކްބީރު ވިދާޅުވުމުން މެނުވީ ނަމާދު ނިންމެވިކަން ތިމަންމެންނަށް ނޭނގެއެވެ.","[البخاري 841، ومسلم 583\n\nمسلم 583]"],["#135","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الذكر عقب الصلاة","ނަމާދަށް ފަހު ޒިކުރު ކިޔުމުގެ ބާބު","عَنْ وَرَّادٍ مَوْلَى الْمُغِيرَةِ بْنِ شُعْبَةَ قَالَ: أَمْلَى عَلَيَّ الْمُغِيرَةُ بْنُ شُعْبَةَ مِنْ كِتَابٍ إلَى مُعَاوِيَةَ: إنَّ النَّبِيَّ ﷺ كَانَ يَقُولُ فِي دُبُرِ كُلِّ صَلاةٍ مَكْتُوبَةٍ: «لا إلَهَ إلاَّ اللَّهُ وَحْدَهُ لا شَرِيكَ لَهُ، لَهُ الْمُلْكُ وَلَهُ الْحَمْدُ، وَهُوَ عَلَى كُلِّ شَيْءٍ قَدِيرٍ. اللَّهُمَّ لا مَانِعَ لِمَا أَعْطَيْتَ وَلا مُعْطِيَ لِمَا مَنَعْتَ، وَلا يَنْفَعُ ذَا الْجَدِّ مِنْكَ الْجَدُّ»\n\nثُمَّ وَفَدْتُ بَعْدَ ذَلِكَ عَلَى مُعَاوِيَةَ فَسَمِعْتُهُ يَأْمُرُ النَّاسَ بِذَلِكَ. \n\nوَفِي لَفْظٍ: كَانَ يَنْهَى عَنْ قِيلَ وَقَالَ، وَإِضَاعَةِ الْمَالِ، وَكَثْرَةِ السُّؤَالِ.\n\nوَكَانَ يَنْهَى عَنْ عُقُوقِ الأُمَّهَاتِ، وَوَأْدِ الْبَنَاتِ، وَمَنْعٍ وَهَاتِ.","އަލްމުޣީރާ ބިން ޝުޢުބާ މިނިވަން ކުރެއްވި އަޅެއް ކަމުގައިވާ ވައްރާދު ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: މުޢާވިޔާއަށް ސިޓީއެއް ފޮނުއްވުމުގެ ތެރޭގައި، އަލްމުޣީރާ ބިން ޝުޢުބާ (ސިޓީގައި ލިޔަންވީ އެއްޗެއް) ކިޔައިދެއްވުމުން ތިމަން ލިޔުނީމެވެ: ހަމަކަށަވަރުން ކޮންމެ ފަރުޟު ނަމާދެއް ނިންމެވުމަށް ފަހު ނަބިއްޔާ ﷺ ވިދާޅުވެއެވެ: ”ﷲ ފިޔަވައި (އަޅުކަންކުރުން ހައްގުވާ) އެހެން އެއްވެސް އިލާހަކު ނުވެއެވެ. އެ އިލާހު އެއްކައުވަންތަވެ ވޮޑިގެންވެއެވެ. އެ އިލާހަށް އެއްވެސް ބައިވެރިއަކު ނުވެއެވެ. އެންމެހައި މިލްކުވެރިކަމަކާއި ހަމްދެއް ލިބިވޮޑިގެންވަނީ އެ އިލާހަށެވެ. އަދި އެ އިލާހީ ކޮންމެ ކަމަކަށް ކުޅަދުންވަންތަ އިލާހެވެ. ޔާ ﷲ! އިބަ އިލާހު ދެއްވާ އެއްޗެއް މަނާ ކުރާނެ ފަރާތެއް ނުވެއެވެ. އަދި އިބަ އިލާހު މަނާ ކުރެއްވި އެއްޗެއް ދޭނެ ފަރާތެއް ނުވެއެވެ. އަދި މުދާވެރި ނަސީބުވެރިއަކަށް، އިބަ އިލާހާ ދެކޮޅަށް އޭނާގެ ނަސީބުވެރިކަމުން އެއްވެސް ފައިދާއެއް ނުކުރެވޭނެއެވެ.“\n\nއެއަށްފަހު މުޢާވިޔާގެ ގާތަށް (އެ ސިޓީ ހިފައިގެން) ތިމަންނަ ދިޔައިމެވެ. އަދި އެ (ސިޓީގައިވާ) ގޮތަށް ކަންކުރެއްވުމަށް މީސްތަކުންނަށް މުޢާވިޔާ އެންގެވި އަޑު ތިމަން އެހީމެވެ. \n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: އެވެނި މީހަކު މިވެނި މީހަކު ބުންޏޭ ކިޔުމާއި، މުދާ ބޭކާރު ގޮތުގައި ހޭދަ ކުރުމާއި، މާ ގިނައިން ސުވާލު ކުރުން ނަބިއްޔާ ނަހީ ކުރެއްވިއެވެ.\n\nއަދި މައިމީހާއަށް ގޯސްކޮށް ހިތުމާއި، ޅަ އަންހެން ދަރިން (ދިރިހުއްޓައި) ވަޅުލުމާއި، (ދޭންވާ) އެއްޗެއް ނުދިނުމާ އަދި (ލިބެން ނުވާ) އެއްޗެހި ދޭން ކިޔައިގަތުން ވެސް ނަބިއްޔާ ނަހީ ކުރެއްވިއެވެ.","[البخاري 844 واللفظ له، ومسلم 593\n\nالبخاري 6615\n\nالبخاري 6473 واللفظ له، ومسلم 593\n\nالبخاري 7292 واللفظ له، ومسلم 593]"],["#136","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الذكر عقب الصلاة","ނަމާދަށް ފަހު ޒިކުރު ކިޔުމުގެ ބާބު","عَنْ سُمَيٍّ مَوْلَى أَبِي بَكْرِ بْنِ عَبْدِ الرَّحْمَنِ بْنِ الْحَارِثِ بْنِ هِشَامٍ عَنْ أَبِي صَالِحٍ السَّمَّانِ عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ -: «أَنَّ فُقَرَاءَ الْمُسْلِمِينَ أَتَوْا رَسُولَ اللَّهِ ﷺ فَقَالُوا: يَا رَسُولَ اللَّهِ، قَدْ ذَهَبَ أَهْلُ الدُّثُورِ بِالدَّرَجَاتِ الْعُلَى وَالنَّعِيمِ الْمُقِيمِ. قَالَ: وَمَا ذَاكَ؟ قَالُوا: يُصَلُّونَ كَمَا نُصَلِّي، وَيَصُومُونَ كَمَا نَصُومُ، وَيَتَصَدَّقُونَ وَلا نَتَصَدَّقُ. وَيُعْتِقُونَ وَلا نُعْتِقُ. فَقَالَ رَسُولُ اللَّهِ ﷺ: أَفَلا أُعَلِّمُكُمْ شَيْئًا تُدْرِكُونَ بِهِ مَنْ سَبَقَكُمْ، وَتَسْبِقُونَ مَنْ بَعْدَكُمْ. وَلا يَكُونُ أَحَدٌ أَفْضَلَ مِنْكُمْ، إلاَّ مَنْ صَنَعَ مِثْلَ مَا صَنَعْتُمْ؟ قَالُوا: بَلَى، يَا رَسُولَ اللَّهِ. قَالَ: تُسَبِّحُونَ وَتُكَبِّرُونَ وَتَحْمَدُونَ دُبُرَ كُلِّ صَلاةٍ: ثَلاثاً وَثَلاثِينَ مَرَّةً. قَالَ أَبُو صَالِحٍ: فَرَجَعَ فُقَرَاءُ الْمُهَاجِرِينَ، فَقَالُوا: سَمِعَ إخْوَانُنَا أَهْلُ الأَمْوَالِ بِمَا فَعَلْنَا، فَفَعَلُوا مِثْلَهُ. فَقَالَ رَسُولُ اللَّهِ ﷺ: ذَلِكَ فَضْلُ اللَّهِ يُؤْتِيهِ مَنْ يَشَاءُ»","އަބޫ ބަކްރު ބިން ޢަބްދުއްރަޙްމާން ބިން އަލްޙާރިޘް ބިން ހިޝާމް މިނިވަން ކުރެއްވި އަޅެއް ކަމުގައިވާ، ސުމައްޔާގެ އަރިހުން، އަބޫ ސާލިހު އައްސައްމާންގެ އަރިހުން، އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: ހަމަކަށަވަރުން މުސްލިމުންގެ ތެރެއިންވާ ފަގީރުން ރަސޫލު ﷲ ﷺގެ އަރިހަށް އައިސް ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން މުދަލުގެ އަހުލުވެރީން މަތީ ދަރަޖަތަކާއި ދެމިހުންނަނިވި ނިއުމަތްތައް ހޯދައިގެން ހިނގައްޖެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެއީ ކިހިނަކުންތޯއެވެ؟“ އެބައިމީހުން ދެންނެވިއެވެ: ތިމަންމެން ނަމާދު ކުރާ ފަދައިން އެބައިމީހުން ވެސް ނަމާދު ކުރެއެވެ. އަދި ތިމަންމެން ރޯދަ ހިފާ ފަދައިން އެބައިމީހުން ވެސް ރޯދަ ހިފައެވެ. އެހެނަސް އެބައިމިހުން ސަދަގާތް ކުރާއިރު، ތިމަންމެންނަކަށް ސަދަގާތެއް ނުކުރެވެއެވެ. އަދި އެބައިމީހުން އަޅުން މިނިވަން ކުރާއިރު، ތިމަންމެންނަކަށް އަޅުންނެއް މިނިވަނެއް ނުކުރެވެއެވެ. ދެން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންނަށް ވުރެ ކުރިއަރާފައި ތިބި ބަޔަކާ ތިޔަބައިމީހުން އަރާ ހަމަކުރެވޭނެ، އަދި ތިޔަބައިމީހުންގެ ފަހުން އަންނާނެ ބަޔަކަށް ވުރެ ތިޔަބައިމީހުން ކުރިއަރރުވައިދޭނެ ކަމެއް، ތިޔަބައިމީހުންނަށް އުނގަންނަވައި ދެއްވަންތޯއެވެ؟“ އެބައިމިހުން ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. އާދޭހެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ކޮންމެ ނަމާދެއްގެ ފަހުން ތިރީސް ތިން ފަހަރު ތިޔަބައިމީހުން ތަސްބީހައާއި ތަކްބީރާއި ތަހްމީދު ކިޔާށެވެ.“ (އެބަހީ: سُبْحَانَ اللَّهِ، اللَّهُ أَكْبَرُ، اَلْحَمْدُ لِلَّهِ މި ޒިކުރުތައް ކިޔާށެވެ.) (ޙަދީޘްގެ ރާވީއެއް ކަމަށްވާ) އަބޫ ސާލިހު ވިދާޅުވިއެވެ: ދެން އަނެއްކާވެސް މުހާޖިރުންގެ ތެރެއިންވާ ފަގީރުން ނަބިއްޔާގެ އަރިހަށް އެނބުރި ގޮސް ދެންނެވިއެވެ: ތިމަންމެންގެ އަޚުން ކަމުގައިވާ މުދަލުގެ އަހުލުވެރިންނަށް، ތިމަންމެން ކުރި ކަންތައްތަކުގެ އަޑު އިވިގެން އެބައިމީހުންވެސް އެފަދައިން ކަންކުރަން (ފަށައިފިއެވެ.) ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އެއީ ﷲގެ ފަޟްލުވަންތަކަމެވެ. އެ އިލާހު އިރާދަ ކުރައްވާ ބަޔަކަށް އެކަން ދެއްވައެވެ.“","[البخاري 843، ومسلم 595 واللفظ له]"],["#137","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الذكر عقب الصلاة","ނަމާދަށް ފަހު ޒިކުރު ކިޔުމުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا: «أَنَّ النَّبِيَّ ﷺ صَلَّى فِي خَمِيصَةٍ لَهَا أَعْلامٌ. فَنَظَرَ إلَى أَعْلامِهَا نَظْرَةً. فَلَمَّا انْصَرَفَ قَالَ: اذْهَبُوا بِخَمِيصَتِي هَذِهِ إلَى أَبِي جَهْمٍ، وَأْتُونِي بِأَنْبِجَانِيَّةِ أَبِي جَهْمٍ. فَإِنَّهَا أَلْهَتْنِي آنِفًا عَنْ صَلاتِي»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން (ކުލަކުލައިގެ) ގޮޅިޖެހި ޚަމީޞާ (އޭ ކިޔުނު ހެދުންކޮޅެއް) ތުރުކުރައްވައިގެން ނަބިއްޔާ ﷺ ނަމާދު ކުރައްވަނިކޮށް، އޭގައި ކުރަހާފައި ހުރި ނިޝާންތަކަށް ބެއްލެވުމަކުން ބައްލަވައިލެއްވުނެވެ. ދެން ނަމާދުން އަވަދިވެވަޑައިގަތުމުން ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާގެ މި ޚަމީޞާ ހިފައިގެން އަބޫ ޖަހްމުގެ ގާތަށް ދާށެވެ. އަދި އަބޫ ޖަހްމުގެ އަންބިޖާނިއްޔާ (އޭ ކިޔުނު ބޯ، ކުރެހުމެއް ނެތް ހެދުންކޮޅު) ތިމަން ނަބިއްޔާއަށް ގެންނާށެވެ. އެހެނީ ހަމަކަށަވަރުން މި ހެދުން، ދެންމެ ކުރެއްވި ނަމާދުން ތިމަން ނަބިއްޔާގެ ސަމާލުކަން ގެއްލުވާލައިފިއެވެ.“","[البخاري 373، ومسلم 556]"],["#138","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الجمع بين الصلاتين في السفر","ދަތުރުމަތީގައި ދެ ނަމާދު އެއްކޮށް ޖަމްއު ކުރުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: «كَانَ رَسُولُ اللَّهِ ﷺ يَجْمَعُ فِي السَّفَرِ بَيْنَ صَلاةِ الظُّهْرِ وَالْعَصْرِ، إذَا كَانَ عَلَى ظَهْرِ سَيْرٍ، وَيَجْمَعُ بَيْنَ الْمَغْرِبِ وَالْعِشَاءِ»","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ދަތުރުފުޅުތަކުގައި ރަސޫލު ﷲ ﷺ މެނދުރާއި އަސުރު ނާމާދު އެއްކޮށް ޖަމްއު ކުރައްވައެވެ. އެއީ ދަތުރުފުޅު މަތީގައި ހުންނަވާ ހާލުގައެވެ. އަދި މަޣްރިބާއި އިޝާ ނަމާދު ވެސް އެއްކޮށް ޖަމްއު ކުރައްވައެވެ.","[البخاري 1107]"],["#139","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب قصر الصلاة في السفر","ދަތުރު މަތީގައި ނަމާދު ކުރު ކުރުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما قَالَ: «صَحِبْتُ رَسُولَ اللَّهِ ﷺ فَكَانَ لا يَزِيدُ فِي السَّفَرِ عَلَى رَكْعَتَيْنِ، وَأَبَا بَكْرٍ وَعُمَرَ وَعُثْمَانَ كَذَلِكَ»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺގެ އަރިހުގައި ތިމަން އުޅުނީމެވެ. އަދި ނަބިއްޔާ ދަތުރުފުޅުމަތީގައި (މެންދުރާއި އަސުރާއި އިޝާ ނަމާދު ކުރައްވާ އިރު) ދެ ރަކުއަތަށް ވުރެ އިތުރަށް ނުކުރައްވައެވެ. އަދި އަބޫ ބަކްރާއި ޢުމަރާއި ޢުޘްމާން ވެސް (ކަންކުރެއްވީ) ހަމަ އެފަދައިންނެވެ.","[البخاري 1102 واللفظ له، ومسلم 689]"],["#140","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الجمعة","ހުކުރު ކުރުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «مَنْ جَاءَ مِنْكُمْ الْجُمُعَةَ فَلْيَغْتَسِلْ»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ކުރެ މީހަކު ހުކުރަށް އަންނަ ނަމަ، ފަހެ އެ މީހާ ފެންވަރައި ހިނައިގަންނާށެވެ.“","[البخاري 894، ومسلم 844]"],["#141","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الجمعة","ހުކުރު ކުރުމުގެ ބާބު","عَنْ جَابِرٍ رَضِيَ اللَّهُ عَنْهُ قَالَ: «كَانَ رَسُولُ اللَّهِ ﷺ يَخْطُبُ خُطْبَتَيْنِ وَهُوَ قَائِمٌ، يَفْصِلُ بَيْنَهُمَا بِجُلُوسٍ»","ޖާބިރު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ކޮއްޅަށް ހުރެވަޑައިގެން ދެ ޚުތުބާ ދެއްވައެވެ. އެ ދެ ޚުތުބާ ދެމެދު ވަކި ކުރައްވަނީ އިށީނދެ ވަޑައިގަތުމަކުންނެވެ.","[البخاري 920، 928، ومسلم 861، ولكن ليسا بهذا اللفظ، والأقرب لفظ النسائي 1416]"],["#142","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الجمعة","ހުކުރު ކުރުމުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رَضِيَ اللَّهُ عَنْهُما قَالَ: «جَاءَ رَجُلٌ وَالنَّبِيُّ ﷺ يَخْطُبُ النَّاسَ يَوْمَ الْجُمُعَةِ. فَقَالَ: صَلَّيْتَ يَا فُلانُ؟ قَالَ: لا. قَالَ: قُمْ فَارْكَعْ رَكْعَتَيْنِ.»\n\nوَفِي رِوَايَةٍ: فَصَلِّ رَكْعَتَيْنِ.","ޖާބިރު رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ހުކުރު ދުވަހު ނަބިއްޔާ ﷺ މީސްތަކުންނަށް ޚުތުބާ ދެއްވަނިކޮށް މީހަކު (މިސްކިތަށް) އައެވެ. ފަހެ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ މިވެނި މީހާއެވެ. ތިބާ ނަމާދުކުރިންހެއްޔެވެ؟“ އޭނާ ދެންނެވިއެވެ: ނޫނެކެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތެދުވެ ދެ ރަކުއަތް ކުރާށެވެ.“","[البخاري 930 واللفظ له، ومسلم 875\n\nالبخاري 931 واللفظ له، ومسلم 875]"],["#143","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الجمعة","ހުކުރު ކުރުމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «إذَا قُلْتَ لِصَاحِبِكَ: أَنْصِتْ يَوْمَ الْجُمُعَةِ وَالإِمَامُ يَخْطُبُ - فَقَدْ لَغَوْتَ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހުކުރު ދުވަހު އިމާމު ޚުތުބާ ދެއްވަނިކޮށް، ތިބާއާ ޖެހިގެން ހުރި މީހާ ގާތުގައި ހަނުހުންނަން ބުނިކަމުގައި ވިޔަސް، ފަހެ ހަމަކަށަވަރުން ތިބާ ބޭކާރު ބާތިލު ވާހަކަ ދެއްކި ކަމުގައި ވާނެއެވެ.“","[البخاري 934، ومسلم 851 واللفظ له]"],["#144","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الجمعة","ހުކުރު ކުރުމުގެ ބާބު","عَنْ سَهْلِ بْنِ سَعْدٍ السَّاعِدِيِّ قَالَ: «رَأَيْتُ رَسُولَ اللَّهِ قَامَ ﷺ فَكَبَّرَ وَكَبَّرَ النَّاسُ وَرَاءَهُ، وَهُوَ عَلَى الْمِنْبَرِ. ثُمَّ رَفَعَ فَنَزَلَ الْقَهْقَرَى، حَتَّى سَجَدَ فِي أَصْلِ الْمِنْبَرِ، ثُمَّ عَادَ حَتَّى فَرَغَ مِنْ آخِرِ صَلاتِهِ. ثُمَّ أَقْبَلَ عَلَى النَّاسِ، فَقَالَ: أَيُّهَا النَّاسُ، إنَّمَا صَنَعْتُ هَذَا لِتَأْتَمُّوا بِي، وَلِتَعْلَمُوا صَلاتِي»\n\nوَفِي لَفْظٍ - صَلَّى عَلَيْهَا. ثُمَّ كَبَّرَ عَلَيْهَا. ثُمَّ رَكَعَ وَهُوَ عَلَيْهَا، فَنَزَلَ الْقَهْقَرَى.","ސަހްލު ބިން ސަޢުދު އައްސާޢިދީ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ މިންބަރު މަތީގައި ތެދުވެވަޑައިގެން (ނަމާދު ފެއްޓެވުމަށް) ތަކްބީރު ދެއްވަނިކޮށް ތިމަން ދުށީމެވެ. އަދި ނަބިއްޔާގެ ފަހަތްޕުޅުގައިވާ މީސްތަކުންވެސް ތަކްބީރު ދިނެވެ. ދެން (ނަމާދުގައި ނަބިއްޔާ ރުކޫއިން) އިސްތަށިފުޅު އުފުއްލެވުމަށް ފަހު (ގިބްލައަށް ކުރިމަތިފުޅު ލައްވައިގެން ހުންނަވައި، މިންބަރުން) ފަހަތަށް ޖެހިވަޑައިގެން ގޮސް، މިންބަރުގެ ތިރީގައި (ބިންމަތީ) ސަޖިދަ ކުރެއްވިއެވެ. ދެން (ދެވަނަ ސަޖިއަށް ފަހު މިންބަރު މައްޗަށް) އަނެއްކާ ތެދުވެވަޑައިގެން ނަމާދު ނިމެންދެން ކަންތައް ކުރެއްވީ އެފަދައިންނެވެ. އެއަށްފަހު މީސްތަކުންނާ ކުރިމަތިލައްވައިގެން ހުންނަވައި ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ މީސްތަކުންނޭވެ. މިފަދައިން ތިމަން ނަބިއްޔާ ކަންތައް ކުރެއްވި ހަމައެކަނި ސަބަބަކީ، ތިޔަބައިމީހުން ތިމަން ނަބިއްޔާއަށް ތަބާވެ، އަދި ތިމަން ނަބިއްޔާގެ ނަމާދު ތިޔަބައިމީހުން އުނގެނުމަށްޓަކައެވެ.“\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ނަބިއްޔާ ނަމާދު ކުރެއްވީ އެ މިންބަރު މަތީގައެވެ. ދެން ތަކްބީރު ދެއްވީ އޭގެ މަތީގައެވެ. ދެން ރުކޫ ކުރެއްވީ އޭގެ މަތީގައެވެ. ދެން (އޭގެން) ފައިބައިވަޑައިގެން ފަހަތަށް ޖެހިވަޑައިގަތެވެ.","[البخاري 917، ومسلم 544 واللفظ له\n\nالبخاري 917]"],["#145","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الجمعة","ހުކުރު ކުރުމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «مَنْ اغْتَسَلَ يَوْمَ الْجُمُعَةِ، ثُمَّ رَاحَ في الساعَةِ الأُولى فَكَأَنَّمَا قَرَّبَ بَدَنَةً. وَمَنْ رَاحَ فِي السَّاعَةِ الثَّانِيَةِ فَكَأَنَّمَا قَرَّبَ بَقَرَةً. وَمَنْ رَاحَ فِي السَّاعَةِ الثَّالِثَةِ فَكَأَنَّمَا قَرَّبَ كَبْشًا أَقْرَنَ. وَمَنْ رَاحَ فِي السَّاعَةِ الرَّابِعَةِ فَكَأَنَّمَا قَرَّبَ دَجَاجَةً. وَمَنْ رَاحَ فِي السَّاعَةِ الْخَامِسَةِ فَكَأَنَّمَا قَرَّبَ بَيْضَةً. فَإِذَا خَرَجَ الإِمَامُ حَضَرَتِ الْمَلائِكَةُ يَسْمَعُونَ الذِّكْرَ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހުކުރު ދުވަހު ފެންވަރައި ހިނައިގަނެގެން، ފުރަތަމަ ގަޑީގައި (މިސްކިތަށް) ގޮސްފި މީހަކު ޖަމަލެއް ގުރުބާންކޮށްފި ފަދައެވެ. އަދި ދެވަނަ ގަޑީގައި ގޮސްފި މީހަކު ގެރިއެއް ގުރުބާންކޮށްފި ފަދައެވެ. އަދި ތިންވަނަ ގަޑީގައި ގޮސްފި މީހަކު ދަޅުފެޅި ކަނބަޅިއެއް ގުރުބާންކޮށްފި ފަދައެވެ. އަދި ހަތަރުވަނަ ގަޑީގައި ގޮސްފި މީހަކު ކުކުޅެއް ގުރުބާންކޮށްފި ފަދައެވެ. އަދި ފަސްވަނަ ގަޑީގައި ގޮސްފި މީހަކު ކުކުޅު ބިހެއް ގުރުބާންކޮށްފި ފަދައެވެ. ދެން ފަހެ އިމާމު މީހާ (ޚުތުބާ ދިނުމަށް) ނުކުންނަ ހިނދު، ޒިކުރު އަޑުއެހުމަށް މަލާއިކަތުން ހާޒިރުވެއެވެ.“","[البخاري 881، ومسلم 850]"],["#146","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الجمعة","ހުކުރު ކުރުމުގެ ބާބު","عَنْ سَلَمَةَ بْنِ الأَكْوَعِ - وَكَانَ مِنْ أَصْحَابِ الشَّجَرَةِ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «كُنَّا نُصَلِّي مَعَ رَسُولِ اللَّهِ ﷺ الْجُمُعَةَ، ثُمَّ نَنْصَرِفُ. وَلَيْسَ لِلْحِيطَانِ ظِلٌّ نَسْتَظِلُّ بِهِ» \n\nوَفِي لَفْظٍ: «كُنَّا نُجَمِّعُ مَعَ رَسُولِ اللَّهِ ﷺ إذَا زَالَتْ الشَّمْسُ، ثُمَّ نَرْجِعُ فَنَتَتَبَّعُ الْفَيْءَ»","ސަލަމާ ބިން އަލްއަކްވަޢު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ. –އަދި އެކަލޭގެފާނީ ގަސްދޮށުގައި ބައިއަތުލް ރިޟްވާންގައި ބައިވެރިވި ސަހާބީއެކެވެ-: ރަސޫލު ﷲ ﷺގެ އަރިހުގައި ތިމަންމެން ހުކުރު ނަމާދު ކުރުމަށް ފަހު އެނބުރި (ގެއަށް) ދަމެވެ. އަދި އޭރު ތިމަންމެން ހިޔާވާނެ ހިޔަލެއްވެސް ފާރުތަކުން ނުލިބެއެވެ.\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: އިރު މެދުޖެހުމަށް ފަހު، ރަސޫލު ﷲ ﷺގެ އަރިހުގައި ތިމަންމެން ހުކުރު ނަމާދު ކުރަމެވެ. އެއަށްފަހު ތިމަންމެން އެނބުރި (ގެއަށްދާ މަގުމަތީގައި މެނދުރުގެ ހޫނު އަވިން) ހިޔާވަހިކަން ލިބޭތޯ ބަލަމެވެ.","[البخاري 4168 واللفظ له، ومسلم 860\n\nمسلم 860]"],["#147","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب الجمعة","ހުކުރު ކުރުމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «كَانَ النَّبِيُّ ﷺ يَقْرَأُ فِي صَلاةِ الْفَجْرِ يَوْمَ الْجُمُعَةِ: الم تَنْزِيلُ السَّجْدَةَ وَ: هَلْ أَتَى عَلَى الإِنْسَانِ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ހުކުރު ދުވަހު، ފަތިސް ނަމާދުގެ (ދެ ރަކުއަތުގައި) ނަބިއްޔާ ﷺ ކިޔަވައި ވިދާޅުވަނީ ސަޖިދަ ސޫރަތުގެ الم تَنْزِيلُ އާއި، (އަލްއިންސާން ސޫރަތުގެ) هَلْ أَتَى عَلَى الإِنْسَانِ (މި ދެ ސޫރަތެވެ.)","[البخاري 891، ومسلم 879 واللفظ له]"],["#148","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صلاة العيدين","ދެ އީދު ދެ ނަމާދުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما قَالَ: «كَانَ النَّبِيُّ ﷺ وَأَبُو بَكْرٍ وَعُمَرُ يُصَلُّونَ الْعِيدَيْنِ قَبْلَ الْخُطْبَةِ»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ އާއި، އަބޫ ބަކުރާއި ޢުމަރު، ދެ އީދު ނަމާދު ކުރައްވަނީ ޚުތުބާ ދިނުމުގެ ކުރިންނެވެ.","[البخاري 963، ومسلم 888]"],["#149","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صلاة العيدين","ދެ އީދު ދެ ނަމާދުގެ ބާބު","عَنْ الْبَرَاءِ بْنِ عَازِبٍ رَضِيَ اللَّهُ عَنْهُ قَالَ: «خَطَبَنَا النَّبِيُّ ﷺ يَوْمَ الأَضْحَى بَعْدَ الصَّلاةِ، فَقَالَ: مَنْ صَلَّى صَلاتَنَا وَنَسَكَ نُسُكَنَا فَقَدْ أَصَابَ النُّسُكَ، وَمَنْ نَسَكَ قَبْلَ الصَّلاةِ فَلا نُسُكَ لَهُ. فَقَالَ أَبُو بُرْدَةَ بْنُ نِيَارٍ - خَالُ الْبَرَاءِ بْنِ عَازِبٍ - يَا رَسُولَ اللَّهِ، إنِّي نَسَكْتُ شَاتِي قَبْلَ الصَّلاةِ. وَعَرَفْتُ أَنَّ الْيَوْمَ يَوْمُ أَكْلٍ وَشُرْبٍ. وَأَحْبَبْتُ أَنْ تَكُونَ شَاتِي أَوَّلَ مَا يُذْبَحُ فِي بَيْتِي. فَذَبَحْتُ شَاتِي، وَتَغَذَّيْتُ قَبْلَ أَنْ آتِيَ الصَّلاةَ. فَقَالَ: شَاتُكَ شَاةُ لَحْمٍ. قَالَ: يَا رَسُولَ اللَّهِ، فَإِنَّ عِنْدَنَا عِنَاقاً هِيَ أَحَبُّ إلَيَّ مِنْ شَاتَيْنِ أَفَتُجْزِي عَنِّي؟ قَالَ: نَعَمْ، وَلَنْ تَجْزِيَ عَنْ أَحَدٍ بَعْدَكَ»","އަލްބަރާއު ބިން ޢާޒިބު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އަޟްހާ އީދު ދުވަހު، ނަމާދަށް ފަހު ނަބިއްޔާ ﷺ ތިމަންމެންނަށް ޚުތުބާ ދެއްވަމުން ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަންމެންގެ ނަމާދާ އެއްގޮތަށް ނަމާދުކޮށް އަދި ތިމަންމެންގެ ގުރުބާން ކުރެއްވުމާ އެއްގޮތަށް ގުރުބާންކޮށްފި މީހާ، ފަހެ އެ މީހެއްގެ ގުރުބާނީ އަދާކޮށްފިކަން ކަށަވަރެވެ. އަދި ނަމާދުގެ ކުރިން ގުރުބާންކޮށްފި މީހަކަށް ގުރުބާނީއެއް ނުވެއެވެ.“ ދެން އަލްބަރާއު ބިން ޢާޒިބުގެ ބޮޑުބޭބޭފުޅު، އަބޫ ބުރްދާ ބިން ނިޔާރު ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން ތިމަންނަގެ ކަންބަޅި ގުރުބާން ކުރެވުނީ ނަމާދުގެ ކުރިންނެވެ. އަދި މި ދުވަހަކީ ކެއުމާއި ބުއިމުގެ ދުވަހެއްކަން ތިމަން ދެނެހުރީމެވެ. ފަހެ ތިމަންނަގެ ގޭގައި އެންމެ ފުރަތަމަ ކަތިލެވެނީ ތިމަންނަގެ ކަންބަޅި ކަމުގައިވުމަށް ތިމަން ލޯބި ކުރީމެވެ. އަދި ތިމަންނަގެ ކަންބަޅި ކަތިލައި، ނަމާދަށް އައުމުގެ ކުރިން ތިމަންނަ ކެއީމެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާގެ ކަންބަޅިއަކީ މަސް ކެއުމަށްޓަކައި ކަތިލެވުނު ކަންބަޅިއެކެވެ.“ (އެބަހީ: އުޟްހިޔާގެ ގުރުބާނީއެއް ނޫނެކެވެ.) އަބޫ ބުރްދާ ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން ތިމަންމެންގެ އަތުގައި ޅަ އަންހެން ބަކަރިއެއް ވެއެވެ. ދެ ކަންބަޅިއަށް ވުރެ އޭތި ތިމަންނައަށް މާ ލޮބުވެތިކަން ބޮޑެވެ. ފަހެ ތިމަންނަގެ (ގުރުބާނީ އަދާ ކުރުމުގެ ގޮތުގައި) އޭތި ފުދޭނެތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އާދޭހެވެ. އަދި ތިބާގެ ފަހުން އަންނަ އެއްވެސް މީހަކަށް ނުފުދޭނެއެވެ.“ (އެބަހީ: ނަމާދުގެ ކުރިން ކަތިލައިގެން ބަދަލު ދިނުމަކީ އަބޫ ބުރްދާއަށް އެކަނި ދެއްވި ލުޔެކެވެ.)","[البخاري 955 واللفظ له، ومسلم 1961]"],["#150","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صلاة العيدين","ދެ އީދު ދެ ނަމާދުގެ ބާބު","عَنْ جُنْدُبِ بْنِ عَبْدِ اللَّهِ الْبَجَلِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «صَلَّى النَّبِيُّ ﷺ يَوْمَ النَّحْرِ. ثُمَّ خَطَبَ. ثُمَّ ذَبَحَ وَقَالَ: مَنْ ذَبَحَ قَبْلَ أَنْ يُصَلِّيَ فَلْيَذْبَحْ أُخْرَى مَكَانَهَا، وَمَنْ لَمْ يَذْبَحْ فَلْيَذْبَحْ بِاسْمِ اللَّهِ»","ޖުންދުބް ބިން ޢަބްދު ﷲ އަލްބަޖައްލީ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އަޟްހާ އީދު ދުވަހު ނަބިއްޔާ ﷺ ނަމާދު ކުރެއްވުމަށް ފަހު ޚުތުބާ ދެއްވިއެވެ. ދެން އުޟްހިޔާ ކަތިލައްވާފައި ޙަދީޘް ކުރެއްވިއެވެ: ”ނަމާދު ކުރުމުގެ ކުރިން (އުޟްހިޔާ) ކަތިލައިފި މީހަކު، ފަހެ އޭގެ ބަދަލުގައި އެހެން ގުރުބާނީއެއް އޭނާ ކަތިލާށެވެ. އަދި މިހާތަނަށް (އުޟްހިޔާ) ކަތިނުލާ ހުރި މީހަކު ފަހެ ﷲގެ އިސްމުފުޅުން (މިހާރު) ކަތިލާށެވެ.“","[البخاري 985 واللفظ له، ومسلم 1961]"],["#151","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صلاة العيدين","ދެ އީދު ދެ ނަމާދުގެ ބާބު","عَنْ جَابِرٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «شَهِدْتُ مَعَ النَّبِيِّ ﷺ يَوْمَ الْعِيدِ. فَبَدَأَ بِالصَّلاةِ قَبْلَ الْخُطْبَةِ، بِلا أَذَانٍ وَلا إقَامَةٍ. ثُمَّ قَامَ مُتَوَكِّئًا عَلَى بِلالٍ، فَأَمَرَ بِتَقْوَى اللَّهِ تَعَالَى، وَحَثَّ عَلَى طَاعَتِهِ، وَوَعَظَ النَّاسَ وَذَكَّرَهُمْ، ثُمَّ مَضَى حَتَّى أَتَى النِّسَاءَ فَوَعَظَهُنَّ وَذَكَّرَهُنَّ، وَقَالَ: يَا مَعْشَرَ النِّسَاءِ، تَصَدَّقْنَ. فَإِنَّكُنَّ أَكْثَرُ حَطَبِ جَهَنَّمَ، فَقَامَتْ امْرَأَةٌ مِنْ سِطَةِ النِّسَاءِ، سَفْعَاءُ الْخَدَّيْنِ فَقَالَتْ: لِمَ يَا رَسُولَ اللَّهِ فَقَالَ: لأَنَّكُنَّ تُكْثِرْنَ الشَّكَاةَ، وَتَكْفُرْنَ الْعَشِيرَ. قَالَ: فَجَعَلْنَ يَتَصَدَّقْنَ مِنْ حُلِيِّهِنَّ يُلْقِينَ فِي ثَوْبِ بِلالٍ مِنْ أَقْرَاطِهِنَّ وَخَوَاتِيمِهِنَّ»","ޖާބިރު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺގެ އަރިހު އީދު ދުވަހުގައި ތިމަން ބައިވެރިވީމެވެ. ޚުތުބާގެ ކުރިން ނަބިއްޔާ ނަމާދު ފެއްޓެވިއެވެ. އެއީ ބަންގި ގޮވުމެއް އަދި ގަމަތް ދިނުމަކާ ނުލައެވެ. އެއަށްފަހު ބިލާލު ގާތު ލެނގިލައްވައިގެން ހުންނަވައި، ﷲ ތަޢާލާއަށް ތަގުވާވެރިވުމަށް އަމުރު ކުރައްވައި، އެ އިލާހަށް ކިޔަމަންތެރިވުމަށް ބާރު އެޅުއްވިއެވެ. އަދި މީސްތަކުންނަށް ވައުޒު ދެއްވައި ހަނދުމަކޮށް ދެއްވިއެވެ. އެއަށްފަހު އަންހެންވެރިންގެ ގާތަށް ވަޑައިގަންނަވައި، އެ ކަނބަލުންނަށް ވައުޒު ދެއްވައި ހަނދުމަކޮށް ދެއްވިއެވެ. އަދި ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ އަންހެން ކަނބަލުންނޭވެ. ތިޔަ ކަނބަލުން ސަދަގާތް ދޭށެވެ. އެހެނީ ހަމަކަށާވަރުން ތިޔަ ކަނބަލުންނީ ނަރަކައިގެ ދަރުގެ ބޮޑުބައެވެ.“ ދެން އަންހެނުންގެ ތެރޭގައި އިން، ދެ ކޮލުގައި ކަޅު ތަންކޮޅެއް ހުރި އަންހެނަކު ތެދުވެ ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. އެއީ ކީއްވެގެންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެއީ ތިޔަ ކަނބަލުން ޝަކުވާ ގިނަ ކުރާތީއެވެ. އަދި ފިރިންނަށް ކާފަރުވާތީއެވެ.“ (އެބަހީ: ޝުކުރުވެރި ނުވާތީއެވެ.) ދެން އެ ކަނބަލުންގެ ހެދުންތަކު ތެރެއިން ކަންފަތު މުދިތަކާއި އަނގޮށްޓިތައް ނަގާފައި ސަދަގާތުގެ ގޮތުގައި ދިނެވެ.","[البخاري 958، ومسلم 885 واللفظ له]"],["#152","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","‌باب صلاة العيدين","ދެ އީދު ދެ ނަމާދުގެ ބާބު","عَنْ أُمِّ عَطِيَّةَ - نُسَيْبَةَ الأَنْصَارِيَّةِ - قَالَتْ: «أَمَرَنَا رَسُولُ اللَّهِ ﷺ أَنْ نُخْرِجَ فِي الْعِيدَيْنِ الْعَوَاتِقَ وَذَوَاتِ الْخُدُورِ، وَأَمَرَ الْحُيَّضَ أَنْ يَعْتَزِلْنَ مُصَلَّى الْمُسْلِمِينَ» \n\nوَفِي لَفْظٍ: «كُنَّا نُؤْمَرُ أَنْ نَخْرُجَ يَوْمَ الْعِيدِ، حَتَّى نُخْرِجَ الْبِكْرَ مِنْ خِدْرِهَا، حَتَّى تَخْرُجَ الْحُيَّضُ، فَيُكَبِّرْنَ بِتَكْبِيرِهِمْ وَيَدْعُونَ بِدُعَائِهِمْ، يَرْجُونَ بَرَكَةَ ذَلِكَ الْيَوْمِ وَطُهْرَتَهُ»","އުންމު ޢަޠިއްޔާ، ނުސައިބާ އަލްއަންޞާރިއްޔާ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: (ބާލިޣުވެފައި ތިބި) ބޮޑެތި އަންހެނުންނާއި، (ޅަ) ބިކުރުވެރި އަންހެން ކުދިން ދެ އީދު (ދެ ނަމާދަށް) ނެރެން، ރަސޫލު ﷲ ﷺ ތިމަންމެންނަށް އަމުރު ކުރެއްވިއެވެ. އަދި ހައިޟުވެރިން (އީދަށް ގޮސް)، މުސްލިމުން ނަމާދު ކުރާ ތަނުން ދުރުވާން ނަބިއްޔާ އެންގެވިއެވެ. \n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: އީދު ދުވަހު (އީދު ނަމާދަށް) ނުކުތުމަށް ތިމަން ކަނބަލުންނަށް އަމުރުވެވިގެން ވިއެވެ. ގޭގައި ފޮރުވިގެން އުޅޭ (ޅަ) ބިކުރުވެރިން އަދި އޭގެ އިތުރުން ހައިޟުވެރިން ނުކުތުމަށް ދާންދެންވެސް މެއެވެ. އެއީ މީސްތަކުންގެ ތަކްބީރާއެކު އެ ކަނބަލުން ތަކްބީރު ކިޔައި، މީސްތަކުންގެ ދުއާއާއެކު އެ ކަނބަލުން ދުއާ ކުރުމަށެވެ. އަދި އެ ދުވަހުގެ ބަރަކާތާއި ފާފަ ފުއްސެވުން ހޯދައިގަތުމަށެވެ.","[البخاري 973، ومسلم 890 واللفظ له\n\nالبخاري 971 واللفظ له، ومسلم 890]"],["#153","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب صلاة الكسوف","އިރުކޭތަ ނަމާދުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا: «أَنَّ الشَّمْسَ خَسَفَتْ عَلَى عَهْدِ رَسُولِ اللَّهِ ﷺ فَبَعَثَ مُنَادِياً يُنَادِي: الصَّلاةُ جَامِعَةٌ. فَاجْتَمَعُوا. وَتَقَدَّمَ، فَكَبَّرَ وَصَلَّى أَرْبَعَ رَكَعَاتٍ فِي رَكْعَتَيْنِ، وَأَرْبَعَ سَجَدَاتٍ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺގެ ޒަމާނުގައި އިރުކޭތަ ހިފިއެވެ. ފަހެ ’ޖަމާއަތުގައި ނަމާދު (ކުރެވެން އެބައުޅެއޭ)‘ (މިފަދއިން) ގޮވާލާ މީހަކު ނަބިއްޔާ ފޮނުއްވިއެވެ. ދެން މީސްތަކުން ޖަމާއަތަށް އެއްވިއެވެ. ނަބިއްޔާ ކުރިއަށް ޖެހިވަޑައިގެން ތަކްބީރު ދެއްވިއެވެ. އަދި (އެ ނަމާދުގެ) ދެ ރަކުއަތުގައި ހަތަރު ފަހަރު ރުކޫކޮށް، ހަތަރު ފަހަރު ސަޖިދަ ކުރެއްވިއެވެ.","[البخاري 1066، ومسلم 901 واللفظ له]"],["#154","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب صلاة الكسوف","އިރުކޭތަ ނަމާދުގެ ބާބު","عَنْ أَبِي مَسْعُودٍ - عُقْبَةَ بْنِ عَمْرٍو الأَنْصَارِيِّ الْبَدْرِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «إنَّ الشَّمْسَ وَالْقَمَرَ آيَتَانِ مِنْ آيَاتِ اللَّهِ، يُخَوِّفُ اللَّهُ بِهِمَا عِبَادَهُ، وَإِنَّهُمَا لا يَنْخَسِفَانِ لِمَوْتِ أَحَدٍ مِنْ النَّاسِ. فَإِذَا رَأَيْتُمْ مِنْهَا شَيْئًا فَصَلُّوا، وَادْعُوا حَتَّى يَنْكَشِفَ مَا بِكُمْ»","އަބޫ މަސްޢޫދު، ޢުގްބާ ބިން ޢަމްރު އަލްއަންޞާރީ އަލްބަދްރީ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން އިރާއި ހަނދަކީ ﷲގެ އާޔަތްތަކުގެ ތެރެއިންވާ ދެ އާޔަތެވެ. ﷲ އެ ދޭތި (ކޭތަ ހިފުން މެދުވެރި ކުރައްވައި) އެ އިލާހުގެ އަޅުތަކުން ބިރުގަންނަވައެވެ. އަދި ހަމަކަށަވަރުން މީސްތަކުން ކުރެ އެއްވެސް މީހެއްގެ މަރުވުމާއި ދިރުމަކަށް އެ ދޭތި ކޭތަ ނުހިފައެވެ. ފަހެ ތިޔަބައިމީހުންނަށް އެ (ކޭތަ ހިފާ) ތަން ފެނިއްޖެ ނަމަ، ފަހެ ނަމާދު ކުރާށެވެ. އަދި ތިޔަބައިމީހުންނަށް ޖެހިފައިވާ (ކޭތަ) ފިއްލަވަންދެން ﷲ އަށް ދުއާ ކުރާށެވެ.“","[البخاري 1041، ومسلم 911 واللفظ له]"],["#155","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب صلاة الكسوف","އިރުކޭތަ ނަމާދުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا أَنَّهَا قَالَتْ: «خَسَفَتِ الشَّمْسُ عَلَى عَهْدِ رَسُولِ اللَّهِ ﷺ فَصَلَّى رَسُولُ اللَّهِ ﷺ بِالنَّاسِ. فَأَطَالَ الْقِيَامَ، ثُمَّ رَكَعَ، فَأَطَالَ الرُّكُوعَ، ثُمَّ قَامَ، فَأَطَالَ الْقِيَامَ - وَهُوَ دُونَ الْقِيَامِ الأَوَّلِ - ثُمَّ رَكَعَ، فَأَطَالَ الرُّكُوعَ - وَهُوَ دُونَ الرُّكُوعِ الأَوَّلِ - ثُمَّ سَجَدَ، فَأَطَالَ السُّجُودَ، ثُمَّ فَعَلَ فِي الرَّكْعَةِ الأُخْرَى مِثْلَ مَا فَعَلَ فِي الرَّكْعَةِ الأُولَى، ثُمَّ انْصَرَفَ، وَقَدْ تَجَلَّتْ الشَّمْسُ، فَخَطَبَ النَّاسَ، فَحَمِدَ اللَّهَ وَأَثْنَى عَلَيْهِ، ثُمَّ قَالَ: إنَّ الشَّمْسَ وَالْقَمَرَ آيَتَانِ مِنْ آيَاتِ اللَّهِ، لا يَخْسِفَانِ لِمَوْتِ أَحَدٍ وَلا لِحَيَاتِهِ، فَإِذَا رَأَيْتُمْ ذَلِكَ فَادْعُوا اللَّهَ وَكَبِّرُوا، وَصَلُّوا وَتَصَدَّقُوا، ثُمَّ قَالَ: يَا أُمَّةَ مُحَمَّدٍ، وَاَللَّهِ مَا مِنْ أَحَدٍ أَغْيَرُ مِنْ اللَّهِ أَنْ يَزْنِيَ عَبْدُهُ، أَوْ تَزْنِيَ أَمَتُهُ، يَا أُمَّةَ مُحَمَّدٍ، وَاَللَّهِ لَوْ تَعْلَمُونَ مَا أَعْلَمُ لَضَحِكْتُمْ قَلِيلاً وَلَبَكَيْتُمْ كَثِيراً»\n\nوَفِي لَفْظٍ: «فَاسْتَكْمَلَ أَرْبَعَ رَكَعَاتٍ وَأَرْبَعَ سَجَدَاتٍ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺގެ ޒަމާނުގައި އިރުކޭތަ ހިފިއެވެ. ދެން ރަސޫލު ﷲ ﷺ މީސްތަކުންނައިގެން (އިރުކޭތަ) ނަމާދު ކުރެއްވިއެވެ. އެ ގޮތުން ވަރަށް ގިނަ ވަގުތު ވަންދެން ނަބިއްޔާ ތެދުވެވަޑައިގެން ހުންނެވިއެވެ. އެއަށްފަހު ވަރަށް ގިނަ ވަގުތު ވަންދެން ރުކޫގައި ހުންނެވިއެވެ. ދެން ގިނަ ވަގުތު ވަންދެން (އިއުތިދާލުގައި) ތެދުވެވަޑައިގެން ހުންނެވިއެވެ. އެކަމަކު އެއީ ފުރަތަމަ ތެދުވެވަޑައިގެން ހުންނެވުމަށް ވުރެ ކުރުކޮށެވެ. ދެން ގިނަ ވަގުތު ވަންދެން ރުކޫގައި ހުންނެވިއެވެ. އެކަމަކު އެއީ ފުރަތަމަ ރުކޫއަށް ވުރެ ކުރުކޮށެވެ. ނަމަވެސް  ދެން ވަރަށް ގިނަ ވަގުތު ވަންދެން (ދެ ފަހަރު) ސަޖިދައިގައި އިންނެވިއެވެ. އެއަށްފަހު ދެވަނަ ރަކުއަތުގައި، ހަމަ ކުރީ ރަކުއަތެކޭ އެފަދައިން ކަންކުރެއްވުމަށް ފަހު، ނަމާދުން އަވަދިވެވަޑައިގަތެވެ. އޭރު (ކޭތަ ނިމިގެން) އިރު ފެންނަށް ހުއްޓެވެ. ދެން ނަބިއްޔާ މީސްތަކުންނާ މުޚާތަބު ކުރައްވައި، ﷲ އަށް ހަމްދު ސަނާ ދެންނެވުމަށް ފަހު ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން އިރާއި ހަނދަކީ ﷲގެ އާޔަތްތަކު ތެރެއިންވާ ދެ އާޔަތެވެ. އެއްވެސް މީހެއްގެ މަރުވުމާއި ދިރުމަކަށް އެ ދޭތި ކޭތަ ނުހިފައެވެ. އަދި ތިޔަބައިމީހުންނަށް އެ (ކޭތަ ހިފާ) ތަން ފެނިއްޖެ ނަމަ، ފަހެ ﷲ އަށް ދުއާކޮށް ތަކްބީރު ކިޔާށެވެ. އަދި ނަމާދުކޮށް ސަދަގާތް ދޭށެވެ.“ އެއަށްފަހު ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ މުޙައްމަދުގެފާނުގެ އުންމަތުގެ މީސްތަކުންނޭވެ. ﷲ ގަންދީ ބުނަމެވެ. އެ ފަރާތެއްގެ ފިރިހެން ނުވަތަ އަންހެން އަޅަކު ޒިނޭކޮށްފާނެ ކަމަށް، ﷲ އަށްވުރެ ޣީރަތްތެރިވެ ވޮޑިގެންވާ ފަރާތަކު ނުވެއެވެ. އޭ މުޙައްމަދުގެފާނުގެ އުންމަތުގެ މީސްތަކުންނޭވެ. ﷲ ގަންދީ ބުނަމެވެ. ތިމަން ނަބިއްޔާއަށް އެނގިވަޑައިގަންނަވާ ކަންތައް ތިޔަބައިމީހުންނަށް ވެސް އެނގުނު ނަމަ، ތިޔަބައިމީހުން މަދުން ހީހަދައި، ގިނައިން ރޮއިހެދީސްކަން ކަށަވަރެވެ.“\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ފަހެ (ނަމާދުގެ ދެ ރަކުއަތުގައި) ހަތަރު ރުކޫ ކުރެއްވުމާއި، ހަތަރު ސަޖިދަ ނަބިއްޔާ ފުރިހަމަ ކުރެއްވިއެވެ.","[البخاري 1044 واللفظ له، ومسلم 901\n\nالبخاري 1046 واللفظ له، ومسلم 901]"],["#156","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب صلاة الكسوف","އިރުކޭތަ ނަމާދުގެ ބާބު","عَنْ أَبِي مُوسَى الأَشْعَرِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «خَسَفَتِ الشَّمْسُ عَلَى زَمَانِ رَسُولِ اللَّهِ ﷺ. فَقَامَ فَزِعاً، وَيَخْشَى أَنْ تَكُونَ السَّاعَةُ، حَتَّى أَتَى الْمَسْجِدَ. فَقَامَ، فَصَلَّى بِأَطْوَلِ قِيَامٍ وَسُجُودٍ، مَا رَأَيْتُهُ يَفْعَلُهُ فِي صَلاتِهِ قَطُّ، ثُمَّ قَالَ: إنَّ هَذِهِ الآيَاتِ الَّتِي يُرْسِلُهَا اللَّهُ عَزَّ وَجَلَّ: لا تَكُونُ لِمَوْتِ أَحَدٍ وَلا لِحَيَاتِهِ. وَلَكِنَّ اللَّهَ يُرْسِلُهَا يُخَوِّفُ بِهَا عِبَادَهُ، فَإِذَا رَأَيْتُمْ مِنْهَا شَيْئاً فَافْزَعُوا إلَى ذِكْرِ اللَّهِ وَدُعَائِهِ وَاسْتِغْفَارِهِ»","އަބޫ މޫސާ އަލްއަޝްޢަރީ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺގެ ޒަމާނުގައި އިރުކޭތަ ހިފިއެވެ. ގިޔާމަތްވީ ކަމަށް ނަބިއްޔާ ހީފުޅު ކުރައްވައިގެން، ބިރުފުޅުން ތެދުވެވަޑައިގެން މިސްކިތަށް ވަޑައިގެންނެވިއެވެ. އަދި ވަރަށް ގިނަ ވަގުތު ވަންދެން ތެދުވެ ހުންނަވައިގެން އަދި ސަޖިދައަށް ހުންނަވައިގެން ނަމާދު ކުރެއްވިއެވެ. ދުވަހަކުވެސް ނަމާދުގައި ނަބިއްޔާ އެފަދައިން ކަންކުރެއްވި ތަނެއް ތިމަން ނުދެކެމެވެ. އެއަށްފަހު ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ﷲ ޢައްޒަ ވަޖައްލަ ފޮނުއްވާ މި (ކޭތަ ހިފުމުގެ) އާޔަތްތަކަކީ، އެއްވެސް މީހެއްގެ މަރުވުމާއި ދިރުމަކާ ގުޅިގެންވާ ކަންތައްތަކެއް ނޫނެކެވެ. އެހެނެއްކަމަކު ﷲ އެ (ކޭތަ ހިފުމުގެ) އާޔަތްތައް ފޮނުއްވަނީ، އެ އިލާހުގެ އަޅުތަކުން ބިރުގެންނެވުމަށެވެ. ވީމާ ތިޔަބައިމީހުންނަށް އެއިން ކަމެއް ފެނިއްޖެ ނަމަ، ފަހެ ﷲގެ ޒިކުރު ކުރުމަށާއި އެ އިލާހަށް ދުއާ ދަންނަވައި އިސްތިޣްފާރު ކުރުމަށް އަވަސްވެގަންނާށެވެ.“","[البخاري 1059، ومسلم 912 واللفظ له]"],["#157","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب الاستسقاء","އިސްތިސްޤާ ނަމާދުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ زَيْدِ بْنِ عَاصِمٍ الْمَازِنِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «خَرَجَ النَّبِيُّ ﷺ يَسْتَسْقِي، فَتَوَجَّهَ إلَى الْقِبْلَةِ يَدْعُو، وَحَوَّلَ رِدَاءَهُ، ثُمَّ صَلَّى رَكْعَتَيْنِ، جَهَرَ فِيهِمَا بِالْقِرَاءَةِ»\n\nوَفِي لَفْظٍ «إلَى الْمُصَلَّى».","ޢަބްދު ﷲ ބިން ޒައިދު ބިން ޢާޞިމް އަލްމާޒިނީ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އިސްތިސްގާ ނަމާދު ކުރައްވަން ނަބިއްޔާ ﷺ ނުކުމެވަޑައިގަތެވެ. ދެން ގިބުލައަށް ކުރިމަތިލައްވައިގެން ހުންނަވާ ދުއާ ކުރެއްވިއެވެ. އަދި ނަބިއްޔާގެ ރިދާކޮޅު އެތެރެފުށް ބޭރަށް ޖެއްސެވިއެވެ. އެއަށްފަހު ދެ ރަކުއަތް ކުރައްވިއެވެ. އެ ދެ ރަކުއަތުގައި (ޤުރްއާން) ކިޔަވައިވިދާޅުވީ އަޑުހަރުކޮށެވެ.\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ނަމާދު ކުރަން ހުސް ބިމަކަށް (ނަބިއްޔާ ނުކުމެވަޑައިގަތެވެ.)","[البخاري 1024 واللفظ له، ومسلم 893\n\nالبخاري 1012، ومسلم 894]"],["#158","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب الاستسقاء","އިސްތިސްޤާ ނަމާދުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - «أَنَّ رَجُلاً دَخَلَ الْمَسْجِدَ يَوْمَ الْجُمُعَةِ مِنْ بَابٍ كَانَ نَحْوَ دَارِ الْقَضَاءِ، وَرَسُولُ اللَّهِ ﷺ قَائِمٌ يَخْطُبُ، فَاسْتَقْبَلَ رَسُولَ اللَّهِ ﷺ قَائِمًا، ثُمَّ قَالَ: يَا رَسُولَ اللَّهِ، هَلَكَتْ الأَمْوَالُ، وَانْقَطَعَتْ السُّبُلُ فَادْعُ اللَّهَ تَعَالَى يُغِيثُنَا، قَالَ: فَرَفَعَ رَسُولُ اللَّهِ ﷺ يَدَيْهِ ثُمَّ قَالَ: اللَّهُمَّ أَغِثْنَا، اللَّهُمَّ أَغِثْنَا، اللَّهُمَّ أَغِثْنَا. قَالَ أَنَسٌ: فَلا وَاَللَّهِ مَا نَرَى فِي السَّمَاءِ مِنْ سَحَابٍ وَلا قَزَعَةٍ، وَمَا بَيْنَنَا وَبَيْنَ سَلْعٍ مِنْ بَيْتٍ وَلا دَارٍ قَالَ: فَطَلَعَتْ مِنْ وَرَائِهِ سَحَابَةٌ مِثْلُ التُّرْسِ. فَلَمَّا تَوَسَّطَتْ السَّمَاءَ انْتَشَرَتْ ثُمَّ أَمْطَرَتْ، قَالَ: فَلا وَاَللَّهِ مَا رَأَيْنَا الشَّمْسَ سَبْتاً، قَالَ: ثُمَّ دَخَلَ رَجُلٌ مِنْ ذَلِكَ الْبَابِ فِي الْجُمُعَةِ الْمُقْبِلَةِ، وَرَسُولُ اللَّهِ ﷺ قَائِمٌ يَخْطُبُ النَّاسَ، فَاسْتَقْبَلَهُ قَائِماً، فَقَالَ: يَا رَسُولَ اللَّهِ، هَلَكَتْ الأَمْوَالُ، وَانْقَطَعَتْ السُّبُلُ، فَادْعُ اللَّهَ أَنْ يُمْسِكَهَا عَنَّا، قَالَ: فَرَفَعَ رَسُولُ اللَّهِ ﷺ يَدَيْهِ ثُمَّ قَالَ: اللَّهُمَّ حَوَالَيْنَا وَلا عَلَيْنَا، اللَّهُمَّ عَلَى الآكَامِ وَالظِّرَابِ وَبُطُونِ الأَوْدِيَةِ وَمَنَابِتِ الشَّجَرِ، قَالَ: فَأَقْلَعَتْ، وَخَرَجْنَا نَمْشِي فِي الشَّمْسِ. قَالَ شَرِيكٌ: فَسَأَلْتُ أَنَسَ بْنَ مَالِكٍ: أَهُوَ الرَّجُلُ الأَوَّلُ قَالَ: لا أَدْرِي.","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ހުކުރު ދުވަހުގައި، ދާރުލް ގަޟާއޭ ކިޔުނު ތަނަކާ ދިމާލަށް ހުރި ދޮރަކުން މީހަކު މިސްކިތަށް ވަނެވެ. އެ ވަގުތު ރަސޫލު ﷲ ﷺ ހުންނެވީ ތެދުވެވަޑައިގެން މީސްތަކުންނަށް ޚުތުބާ ދެއްވަންށެވެ. ރަސޫލު ﷲ ﷺގެ ކުރިފުޅު މަތީގައި ހުރެގެން އެ މީހާ ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ގެރިބަކަރިޖަމަލުތައް މަރުވެއްޖެއެވެ. އަދި މަގުތަށް ބަންދުވެއްޖެއެވެ. ފަހެ ވާރޭވެއްސަވާތޯ ﷲ ތަޢާލާއަށް ދުއާ ދަންނަވާ ދެއްވާށެވެ. ދެން ރަސޫލު ﷲ ﷺގެ ދެ އަތްޕުޅު އުފުއްލަވާފައި ދެންނެވިއެވެ: ”اللَّهُمَّ أَغِثْنَا، اللَّهُمَّ أَغِثْنَا، اللَّهُمَّ أَغِثْنَا (ޔާ ﷲ! އަޅަމެންނަށް ވާރޭ ވެއްސަވާދެއްވާށިއެވެ. ޔާ ﷲ! އަޅަމެންނަށް ވާރޭ ވެއްސަވާދެއްވާށިއެވެ. ޔާ ﷲ! އަޅަމެންނަށް ވާރޭ ވެއްސަވާދެއްވާށިއެވެ.)“ އަނަސް ވިދާޅުވިއެވެ: ފަހެ ﷲ ގަންދީ ބުނަމެވެ. (އޭރު) އުޑުގައި ވިލާގަނޑެއް ނުވަތަ ވިލާގަނޑެއްގެ ތަންކޮޅެއް ވެސް ނެތެވެ. އަދި ތިމަންމެންނާއި ސިލާ ފަރުބަދައާ ދެމެދު އެއްވެސް ގެއެއް އަދި ގޯއްޗެއް ނެތެވެ. ފަހެ އެ ފަރުބަދައިގެ ފަހަތުން އައްޑަނައެއް ފަދައިން (ވަށްކޮށް ހުރި) ވިލާގަނޑެއް ފާޅުވިއެވެ. ދެން އޭތި އުޑުގެ މެދާ ހިސާބަށް އެރުމުން، ފެތުރިގަނެގެން އޭގެން ވާރޭ ވެހިގަނެއްޖެއެވެ. ﷲ ގަންދީ ބުނަމެވެ. ހަފުތާއެއް ވަންދެން ތިމަންމެންނަކަށް އިރެއް ނުފެނުނެވެ. ދެން ޖެހިގެން އައި ހުކުރު ދުވަހުގައި އެއްކަލަ ދޮރުން މީހަކު މިސްކިތަށް ވަނެވެ. އެ ވަގުތު ރަސޫލު ﷲ ﷺ ހުންނެވީ ތެދުވެވަޑައިގެން މީސްތަކުންނަށް ޚުތުބާ ދެއްވަންށެވެ. އެ މީހާ ނަބިއްޔާގެ ކުރިފުޅު މަތީގައި ހުރެގެން ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ގެރިބަކަރިޖަމަލުތައް މަރުވެއްޖެއެވެ. އަދި މަގުތަށް ބަންދުވެއްޖެއެވެ. ފަހެ ތިމަންމެންގެ މައްޗަށް ވާރޭ ވެހުން ހުއްޓަވާތޯ ﷲ އަށް ދުއާ ދަންނަވާ ދެއްވާށެވެ. ދެން ރަސޫލު ﷲ ﷺގެ ދެ އަތްޕުޅު އުފުއްލަވާފައި ދެންނެވިއެވެ: ”اللَّهُمَّ حَوَالَيْنَا وَلا عَلَيْنَا، اللَّهُمَّ عَلَى الآكَامِ وَالظِّرَابِ وَبُطُونِ الأَوْدِيَةِ وَمَنَابِتِ الشَّجَرِ (ޔާ ﷲ! އަޅަމެންގެ ވަށައިގެން (ވާރޭ ވެއްސަވާދެއްވާށިއެވެ.) އަޅަމެންގެ މައްޗަށް ނުވެއްސަވާށިއެވެ. ޔާ ﷲ! އުސް ތަންތަނާއި، ކުދި ފަރުބަދަތަކާއި، ވާދީތަކާއި، ގަސްތައް ފަޅާ ތަންތަނަށެވެ.)“ ދެން ފަހެ ވާރޭ ވެހުން ހުއްޓުނެވެ. އަދި އިރުގެ އަލީގައި ތިމަންމެން ނުކުމެ ހިނގީމުއެވެ. (ރާވީއެއް ކަމަށްވާ) ޝަރީކު ވިދާޅުވިއެވެ: ތިމަންނަ އަނަސް ބިން މާލިކު ގާތު ދެންނެވީމެވެ: އެއީ ހަމަ ފުރަތަމަ އައި މީހާތޯއެވެ؟ އަނަސް ދެންނެވިއެވެ: ތިމަންނައަކަށް ނޭނގެއެވެ.","[البخاري 1014، ومسلم 897 واللفظ له]"],["#159","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب صلاة الخوف","ޚައުފު ނަމާދުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ بْنِ الْخَطَّابِ رَضِيَ اللَّهُ عَنْهُما قَالَ: «صَلَّى بِنَا رَسُولُ اللَّهِ ﷺ صَلاةَ الْخَوْفِ فِي بَعْضِ أَيَّامِهِ، فَقَامَتْ طَائِفَةٌ مَعَهُ، وَطَائِفَةٌ بِإِزَاءِ الْعَدُوِّ، فَصَلَّى بِاَلَّذِينَ مَعَهُ رَكْعَةً، ثُمَّ ذَهَبُوا، وَجَاءَ الآخَرُونَ، فَصَلَّى بِهِمْ رَكْعَةً، وَقَضَتِ الطَّائِفَتَانِ رَكْعَةً، رَكْعَةً»","ޢަބްދު ﷲ ބިން ޢުމަރު ބިން އަލްޚައްޠާބު رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺގެ (ހަނގުރާމައިގެ) ދުވަހެއްގައި، ތިމަންމެންނައިގެން ނަބިއްޔާ ޚައުފު ނަމާދު ކުރެއްވިއެވެ. (ނޯޓު: އެއީ ހަނގުރާމަ ފަދަ ބިރުވެރިކަމުގެ ހާލަތްތަކުގައި ނަމާދު ކުރަން އޮންނަ ގޮތެވެ.) މީސްތަކުންގެ ތެރެއިން އެއް ބަޔަކު ނަބިއްޔާގެ އަރިހުގައި ނަމާދު ކުރި ހިނދު، އަނެއް ބަޔަކު އަދުއްވުންނާ ކުރިމަތިލައިގެން ތިއްބެވެ. ނަބިއްޔާގެ އަރިހުގައި ތިބި މީހުންނައިގެން އެއް ރަކުއަތް ނަބިއްޔާ ކުރެއްވިއެވެ. އެއަށްފަހު އެބައިމީހުން ގޮސްފިއެވެ. ދެން (ކުރީގައި ނަމާދު ނުކޮށް ތިބި) މީހުން އައިސް، އެބައިމިހުންނައިގެން އަނެއް ރަކުއަތް ނަބިއްޔާ ކުރެއްވިއެވެ. އަދި ދެބައިމީހުން ވެސް (އަމިއްލައަށް އިތުރު) އެއް ރަކުއަތްކޮށް (ނަމާދު) ފުރިހަމަ ކުރިއެވެ.","[البخاري 942، ومسلم 839 واللفظ له]"],["#160","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب صلاة الخوف","ޚައުފު ނަމާދުގެ ބާބު","عَنْ يَزِيدَ بْنِ رُومَانَ عَنْ صَالِحِ بْنِ خَوَّاتِ بْنِ جُبَيْرٍ عَمَّنْ صَلَّى مَعَ رَسُولِ اللَّهِ ﷺ صَلاةَ ذَاتِ الرِّقَاعِ، صَلاةَ الْخَوْفِ: أَنَّ طَائِفَةً صُفَّتْ مَعَهُ، وَطَائِفَةً وِجَاهَ الْعَدُوِّ، فَصَلَّى بِاَلَّذِينَ مَعَهُ رَكْعَةً، ثُمَّ ثَبَتَ قَائِماً، وَأَتَمُّوا لأَنْفُسِهِمْ، ثُمَّ انْصَرَفُوا، فَصُفُّوا وِجَاهَ الْعَدُوِّ، وَجَاءَتْ الطَّائِفَةُ الأُخْرَى، فَصَلَّى بِهِمْ الرَّكْعَةَ الَّتِي بَقِيَتْ، ثُمَّ ثَبَتَ جَالِساً، وَأَتَمُّوا لأَنْفُسِهِمْ، ثُمَّ سَلَّمَ بِهِمْ»","ޔަޒީދު ބިން ރޫމާންގެ އަރިހުން، ސާލިހު ބިން ޚައްވާތު ބިން ޖުބައިރުގެ އަރިހުން، ޛާތުއް ރިގާޢު ހަނގުރާމައިގައި ރަސޫލު ﷲ ﷺގެ އަރިހުގައި ޚައުފު ނަމާދު ކުރެއްވި ސަހާބީއެއްގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން (ނަމާދުގައި) އެއް ބަޔަކު ނަބިއްޔާގެ އަރިހުގައި ސަފު ހެދިއެވެ. އަދި އަނެއް ބަޔަކު ތިބީ އަދުއްވުންނާ ކުރިމަތިލައިގެންނެވެ. ފަހެ ނަބިއްޔާގެ އަރިހުގައި ތިބި ބަޔަކާއެކު އެއް ރަކުއަތް ނަބިއްޔާ ކުރެއްވިއެވެ. ދެން ނަބިއްޔާ (ދެވަނަ ރަކުއަތަށް) ތެދުވެވަޑައިގެން މަޑުކޮށްލަވައިގެން އިންނެވި ހިނދު، އެބައިމީހުން އަމިއްލައަށް (ދެވަނަ ރަކުއަތްކޮށް) ނަމާދު ފުރިހަމަ ކުރިއެވެ. އެއަށްފަހު އެބައިމީހުން ނަމާދުން ވަކިވެ، އަދުއްވުންނާ ކުރިމަތިލައިގެން ސަފު ހެދިއެވެ. އަދި (ނަމާދު ނުކޮށް ތިބި) މީހުން އައިސް އެބައިމީހުންނައިގެން ބާކި އޮތް ރަކުއަތް ނަބިއްޔާ ކުރެއްވިއެވެ. އެއަށްފަހު ނަބިއްޔާ އިށީނދެވަޑައިގެން މަޑުކޮށްލައްވައިގެން އިންނެވި ހިނދު، އެބައިމީހުން (އިތުރު ރަކުއަތެއްކޮށް) ނަމާދު ފުރިހަމަ ކުރިއެވެ. ދެން ނަބިއްޔާ އެބައިމީހުންނައިގެން ސަލާމް ދެއްވިއެވެ.","[البخاري 4129، ومسلم 832 واللفظ له]"],["#161","‌كتاب الصلاة","ނަމާދުގެ ފޮތް","باب صلاة الخوف","ޚައުފު ނަމާދުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ الأَنْصَارِيِّ رَضِيَ اللَّهُ عَنْهُما قَالَ: «شَهِدْتُ مَعَ رَسُولِ اللَّهِ ﷺ صَلاةَ الْخَوْفِ فَصَفَفْنَا صَفَّيْنِ خَلْفَ رَسُولِ اللَّهِ ﷺ وَالْعَدُوُّ بَيْنَنَا وَبَيْنَ الْقِبْلَةِ، وَكَبَّرَ النَّبِيُّ ﷺ وَكَبَّرْنَا جَمِيعاً، ثُمَّ رَكَعَ وَرَكَعْنَا جَمِيعاً، ثُمَّ رَفَعَ رَأْسَهُ مِنْ الرُّكُوعِ وَرَفَعْنَا جَمِيعاً، ثُمَّ انْحَدَرَ بِالسُّجُودِ وَالصَّفُّ الَّذِي يَلِيهِ، وَقَامَ الصَّفُّ الْمُؤَخَّرُ فِي نَحْرِ الْعَدُوِّ، فَلَمَّا قَضَى النَّبِيُّ ﷺ السُّجُودَ، وَقَامَ الصَّفُّ الَّذِي يَلِيهِ: انْحَدَرَ الصَّفُّ الْمُؤَخَّرُ بِالسُّجُودِ، وَقَامُوا، ثُمَّ تَقَدَّمَ الصَّفُّ الْمُؤَخَّرُ، وَتَأَخَّرَ الصَّفُّ الْمُقَدَّمُ، ثُمَّ رَكَعَ النَّبِيُّ ﷺ وَرَكَعْنَا جَمِيعاً، ثُمَّ رَفَعَ رَأْسَهُ مِنْ الرُّكُوعِ وَرَفَعْنَا جَمِيعاً، ثُمَّ انْحَدَرَ بِالسُّجُودِ، وَالصَّفُّ الَّذِي يَلِيهِ - الَّذِي كَانَ مُؤَخَّرًا فِي الرَّكْعَةِ الأُولَى - فَقَامَ الصَّفُّ الْمُؤَخَّرُ فِي نَحْرِ الْعَدُوِّ، فَلَمَّا قَضَى النَّبِيُّ ﷺ السُّجُودَ وَالصَّفُّ الَّذِي يَلِيهِ: انْحَدَرَ الصَّفُّ الْمُؤَخَّرُ بِالسُّجُودِ، فَسَجَدُوا ثُمَّ سَلَّمَ ﷺ وَسَلَّمْنَا جَمِيعاً، قَالَ جَابِرٌ: كَمَا يَصْنَعُ حَرَسُكُمْ هَؤُلاءِ بِأُمَرَائِهِمْ» وَذَكَرَهُ مُسْلِمٌ بِتَمَامِهِ.\n\nوَذَكَرَ الْبُخَارِيُّ طَرَفاً مِنْهُ: «وَأَنَّهُ صَلَّى صَلاةَ الْخَوْفِ مَعَ النَّبِيِّ ﷺ فِي الْغَزْوَةِ السَّابِعَةِ، غَزْوَةِ ذَاتِ الرِّقَاعِ»","ޖާބިރު ބިން ޢަބްދު ﷲ އަލްއަންޞާރީ رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺގެ އަރިހުގައި ޚައުފު ނަމާދުގައި ތިމަން ބައިވެރިވީމެވެ. ފަހެ ރަސޫލު ﷲ ﷺގެ ފަހަތްޕުޅުގައި ދެ ސަފު ތިމަންމެން ހެދީމެވެ. އަދި އަދުއްވުން ތިބީ ތިމަންމެންނާއި ގިބްލައާ ދެމެދުގައެވެ. (އެބަހީ: ތިމަންމެންގެ ކުރިމަތީގައިވާ ދިމާއަށެވެ.) ދެން ނަބިއްޔާ ތަކްބީރު ދެއްވުމުން، ތިމަންމެން އެންމެންވެސް ތަކްބީރު ދިނީމެވެ. އެއަށްފަހު ނަބިއްޔާ ރުކޫ ކުރުމުން، ތިމަންމެން އެންމެންވެސް ރުކޫ ކުރީމެވެ. ދެން ނަބިއްޔާ ރުކޫއިން އިސްތަށިފުޅު އުފުއްލަވައި (ތެދުވެވަޑައިގަތެވެ.) އަދި ތިމަންމެން އެންމެންވެސް ބޯ އުފުލައި (ތެދުވީމެވެ.) އެއަށްފަހު ނަބިއްޔާއާ ޖެހިގެން ތިބި ސަފައިގެން ސަޖިދައަށް ނަބިއްޔާ ތިރިވެލެއްވިއެވެ. އަދި އޭގެ ފަހަތުގައިވީ ސަފު އަދުއްވުންނާ ކުރިމަތިލައިގެން ތިއްބެވެ. ދެން ނަބިއްޔާ ﷺ ސަޖިދަކޮށް ނިންމަވައި، އަދި ނަބިއްޔާއާ ޖެހިގެން ތިބި ސަފު ތެދުވުމުން، އެ ހިސާބުން ފަހަތުގައި ތިބި ސަފު ސަޖިދައަށް ތިރިވިއެވެ. އެއަށްފަހު އެބައިމީހުން ތެދުވެ، ކުރީ ސަފަށް އެރިއެވެ. އަދި ފުރަތަމަ ސަފު ފަހަތަށް ޖެހުނެވެ. އެއަށްފަހު ނަބިއްޔާ ރުކޫ ކުރެއްވުމުން، ތިމަންމެން އެންމެވެސް ރުކޫ ކުރީމެވެ. ދެން ނަބިއްޔާ ރުކޫއިން އިސްތަށިފުޅު އުފުއްލަވައި (ތެދުވެވަޑައިގަތުމުން)، ތިމަންމެން އެންމެންވެސް ބޯ އުފުލައި (ތެދުވީމެވެ.) އެއަށްފަހު ނަބިއްޔާ ސަޖިދައަށް ތިރިވެލެއްވިއެވެ. (ދެން) ނަބިއްޔާއާ ޖެހިގެން ތިބި (އެބަހީ: ކުރީ ރަކުއަތުގައި ފަހަތުގައި ތިބި) ސަފުވެސް ސަޖިދައަށް ތިރިވިއެވެ. އަދި ފަހަތުގައިވި ސަފު އަދުއްވުންނާ ކުރިމަތިލައިގެން ތިއްބެވެ. ދެން ނަބިއްޔާ ﷺ ސަޖިދަކޮށް ނިންމަވައި، އަދި ނަބިއްޔާއާ ޖެހިގެން ތިބި ސަފު ވެސް ސަޖިދަކޮށް ނިމުމުން، އެ ހިސާބުން ފަހަތުގައި ތިބި ސަފު ވެސް ސަޖިދައަށް ތިރިވިއެވެ. އެއަށްފަހު ނަބިއްޔާ ﷺ ސަލާމް ދެއްވުމުން، ތިމަންމެން އެންމެންވެސް ސަލާމް ދިނީމެވެ. ޖާބިރު (އިތުރަށް) ވިދާޅުވިއެވެ: އެއީ ތިޔަބައިމީހުންގެ ތެރޭގައިވާ ފާރަވެރިން، ތިޔަބައިމިހުންގެ ވެރިންނައިގެން ކަންތައް ކުރާ ގޮތާއި އެއްފަދައިންނެވެ. (މުއައްލިފު ވިދާޅުވިއެވެ:) މުސްލިމު މި ޙަދީޘް މިއިން ގޮތަށް ރިވާ ކުރެއްވިއެވެ.\n\nއަދި ބުޚާރީ އޭގެން ތަންކޮޅެއް ރިވާ ކުރެއްވިއެވެ: ނަބިއްޔާ ﷺގެ އަރިހުގައި ޖާބިރު ޚައުފު ނަމާދު ކުރިއެވެ. އެއީ ހަތް ވަނަ ޣަޒްވާކަމަށްވާ، ޛާތުއް ރިގާޢުގައެވެ.","[مسلم 840\n\nالبخاري 4125]"],["#162","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «نَعَى النَّبِيُّ ﷺ النَّجَاشِيَّ فِي الْيَوْمِ الَّذِي مَاتَ فِيهِ، خَرَجَ بِهِمْ إلَى الْمُصَلَّى، فَصَفَّ بِهِمْ، وَكَبَّرَ أَرْبَعاً»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަޖާޝީ އަވަހާރަވި ދުވަހު، އެކަލޭގެފާނު އަވަހާރަވިކަމުގެ ޚަބަރު ދެއްވީ ނަބިއްޔާ ﷺ އެވެ. އަދި މީސްތަކުންނައިގެން ނަމާދު ކުރާ ހުސް ބިމަކަށް ނަބިއްޔާ ނުކުމެވަޑައިގެން ސަފު ހައްދެވުމަށް ފަހު، ހަތަރު ތަކްބީރު ދެއްވާ (ކަށުނަމާދު ކުރެއްވިއެވެ.)","[البخاري 1245، ومسلم 951]"],["#163","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ جَابِرِ - رَضِيَ اللَّهُ عَنْهُ -: «أَنَّ النَّبِيَّ ﷺ صَلَّى عَلَى النَّجَاشِيِّ، فَكُنْتُ فِي الصَّفِّ الثَّانِي، أَوْ الثَّالِثِ»","ޖާބިރު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺ ނަޖާޝީގެ މައްޗަށް ކަށުނަމާދު ކުރެއްވިއެވެ. އަދި ތިމަންނަ ހުރީ ދެވަނަ ނުވަތަ ތިންވަނަ ސަފުގައެވެ.","[البخاري 1317 واللفظ له، ومسلم 952]"],["#164","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما: «أَنَّ النَّبِيَّ ﷺ صَلَّى عَلَى قَبْرٍ، بَعْدَ مَا دُفِنَ، فَكَبَّرَ عَلَيْهِ أَرْبَعاً»","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ކަށްވަޅުގައި (މީހަކު) ވަޅުލެވުނު ފަހުން، ނަބިއްޔާ ﷺ (އެ ތަނަށް ވަޑައިގެން) ހަތަރު ތަކްބީރު ދެއްވާ (އޭނާގެ) މައްޗަށް ކަށުނަމާދު ކުރައްވިއެވެ.","[البخاري 1319، ومسلم 954 واللفظ له]"],["#165","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا: «أَنَّ رَسُولَ اللَّهِ ﷺ كُفِّنَ فِي أَثْوَابٍ بِيضٍ يَمَانِيَةٍ، لَيْسَ فِيهَا قَمِيصٌ وَلا عِمَامَةٌ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ކަފުން ކުރެއްވުނީ ހުދު ޔަމަނީ (ތިން) ފޭރާންކޮޅަކުން (ހަށިކޮޅުގައި އޮޅާލައްވައިގެންނެވެ.) (އަދި އެ ކަފުން ކުރެއްވުމުގައި) ގަމީސްކޮޅެއް އަދި ފަގުޑިކޮޅެއް ވެސް ނުހިމެނެއެވެ.","[البخاري 1264، ومسلم 941]"],["#166","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ أُمِّ عَطِيَّةَ الأَنْصَارِيَّةِ قَالَتْ: «دَخَلَ عَلَيْنَا رَسُولُ اللَّهِ ﷺ حِينَ تُوُفِّيَتْ ابْنَتُهُ، فَقَالَ: اغْسِلْنَهَا ثَلاثاً، أَوْ خَمْساً، أَوْ أَكْثَرَ مِنْ ذَلِكَ - إنْ رَأَيْتُنَّ ذَلِكِ - بِمَاءٍ وَسِدْرٍ، وَاجْعَلْنَ فِي الأَخِيرَةِ كَافُوراً - أَوْ شَيْئاً مِنْ كَافُورٍ - فَإِذَا فَرَغْتُنَّ فَآذِنَّنِي» فَلَمَّا فَرَغْنَا آذَنَّاهُ. فَأَعْطَانَا حَقْوَهُ. وَقَالَ: أَشْعِرْنَهَا بِهِ - تَعْنِي إزَارَهُ.\n\nوَفِي رِوَايَةٍ «أَوْ سَبْعاً»\n\nوَقَالَ: «ابْدَأْنَ بِمَيَامِنِهَا وَمَوَاضِعِ الْوُضُوءِ مِنْهَا»\nوَإِنَّ أُمَّ عَطِيَّةَ قَالَتْ: وَجَعَلْنَا رَأْسَهَا ثَلاثَةَ قُرُونٍ»","އުންމު ޢަޠިއްޔާ އަލްއަންޞާރިއްޔާ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺގެ ދަރިކަނބަލުން (ޒައިނަބު) އަވަހާރަވުމުން، ތިމަންމެންގެ ގާތަށް ނަބިއްޔާ ވަޑައިގެން ޙަދީޘް ކުރެއްވިއެވެ: ”ތިން ފަހަރު، ނުވަތަ ފަސް ފަހަރު، ނުވަތަ ތިޔަ ކަނބަލުންނަށް ފެންނަ ނަމަ އެއަށްވުރެ ގިނަ ފަހަރު އެ ކަމަނާ ހިނަވާށެވެ. އަދި ފަހު ފަހަރު ފެންވަރުވާ އިރު، ކާފޫރު ނުވަތަ އެފަދަ އެހެން އެއްޗެއް ބޭނުން ކުރާށެވެ. އަދި ތިޔަ ކަނބަލުން އެ ކަންތައްކޮށް ނިމުމުން ތިމަން ނަބިއްޔާއަށް އަންގަވާށެވެ.“ ދެން ތިމަންމެން (އެ ކަންތައްކޮށް) ނިމުމުން ނަބިއްޔާއަށް (އެ ޚަބަރު) އަންގަވައިފީމެވެ. ފަހެ ނަބިއްޔާގެ އިޒާރުކޮޅު ތިމަންމެންނަށް ދެއްވާފައި ޙަދީޘް ކުރެއްވިއެވެ: ”އެ ކަމަނާގެ ހަށިކޮޅާ ޖެހިގެން މި (އިޒާރުކޮޅު) ބަހައްޓާށެވެ.“\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”ނޫނީ ހަތް ފަހަރު (ހިނަވާށެވެ.)“\n\nއަދި ޙަދީޘް ކުރެއްވިއެވެ: ”އެ ކަމަނާގެ ކަނާތުންނާއި، ވުޟޫ ކުރާ ތަންތަނުން (ދޮންނަވަން) ފައްޓަވާށެވެ.“\n\nއަދި އުންމު ޢަޠިއްޔާ ވިދާޅުވިއެވެ: ތިމަންމެން އެ ކަމަނާގެ އިސްތަށިފުޅު ތިން ފަށަށް ގެތީމުއެވެ.","[البخاري 1253، ومسلم 939\n\nالبخاري 1259، ومسلم 939\n\nالبخاري 1255، ومسلم 939\n\nالبخاري 1259، ومسلم 939\r\n]"],["#167","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: «بَيْنَمَا رَجُلٌ وَاقِفٌ بِعَرَفَةَ، إذْ وَقَعَ عَنْ رَاحِلَتِهِ، فَوَقَصَتْهُ - أَوْ قَالَ: فَأَوْقَصَتْهُ - فَقَالَ رَسُولُ اللَّهِ ﷺ: اغْسِلُوهُ بِمَاءٍ وَسِدْرٍ، وَكَفِّنُوهُ فِي ثَوْبَيْهِ. وَلا تُحَنِّطُوهُ، وَلا تُخَمِّرُوا رَأْسَهُ. فَإِنَّهُ يُبْعَثُ يَوْمَ الْقِيَامَةِ مُلَبِّياً»\n\nوَفِي رِوَايَةٍ: «وَلا تُخَمِّرُوا وَجْهَهُ وَلا رَأْسَهُ»","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އަރަފާތުގައި މީހަކު މަޑުކުރަނިކޮށް، އޭނާގެ ސަވާރީ މަތިން ވެއްޓި ކަރު ބިނދައިގެން ގޮސް (ނިޔާވިއެވެ.) ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ފެނާއި ސިދުރުން އޭނާ ހިނަވާށެވެ. އަދި އޭނާ (ލައިގެން ހުރި) ހެދުމުގައި އޭނާ ކަފުން ކުރާށެވެ. އެހެނަސް އޭނާ ގައިގައި ހުވަނދު ނުލާށެވެ. އަދި އޭނާގެ ބޯ ނިވާ ނުކުރާށެވެ. އެހެނީ ހަމަކަށަވަރުން ގިޔާމަތް ދުވަހު އޭނާ ދިރުއްވޭނީ ތަލްބިޔާ ކިޔާ ހާލުގައެވެ.“ \n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”އަދި އޭނާގެ މޫނުވެސް އަދި ބޯވެސް ނިވާ ނުކުރާށެވެ.“","[البخاري 1265 واللفظ له، ومسلم 1206\n\nمسلم 1206]"],["#168","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ أُمِّ عَطِيَّةَ الأَنْصَارِيَّةِ رَضِيَ اللَّهُ عَنْهَا قَالَتْ: «نُهِينَا عَنْ اتِّبَاعِ الْجَنَائِزِ وَلَمْ يُعْزَمْ عَلَيْنَا»","އުންމު ޢަޠިއްޔާ އަލްއަންޞާރިއްޔާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ޖަނާޒާގެ ފަހަތުން ދިޔުން، ތިމަން ކަނބަލުންނަށް ނަހީކުރެއްވުނެވެ. އެހެނަސް ތިމަން ކަނބަލުންނަށް އެކަން ހަރާމެއް ނުކުރައްވައެވެ.","[البخاري 1278، ومسلم 938]"],["#169","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - عَنْ النَّبِيِّ ﷺ قَالَ: «أَسْرِعُوا بِالْجِنَازَةِ فَإِنَّهَا إنْ تَكُ صَالِحَةً: فَخَيْرٌ تُقَدِّمُونَهَا إلَيْهِ. وَإِنْ تَكُ سِوَى ذَلِكَ: فَشَرٌّ: تَضَعُونَهُ عَنْ رِقَابِكُمْ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ޖާނާޒާ އަވަސް ކުރުވާށެވެ. އެހެނީ ހަމަކަށަވަރުން އެއީ ސާލިހު މީހެއްކަމުގައި ވާނަމަ، ފަހެ އޭނާ ތިޔަ އަވަސް ކުރުވަނީ ހެވަކަށެވެ. އަދި އެއީ އެ ނޫން މީހެއްކަމުގައި ވާނަމަ، ފަހެ އެ ނުބައި ތިޔަބައިމީހުންގެ ކަނދުރާތަކުން ބޭލުނީއެވެ.“","[البخاري 1315 واللفظ له، ومسلم 944]"],["#170","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ سَمُرَةَ بْنِ جُنْدُبٍ- رَضِيَ اللَّهُ عَنْهُ - قَالَ: «صَلَّيْت وَرَاءَ النَّبِيِّ ﷺ عَلَى امْرَأَةٍ مَاتَتْ فِي نِفَاسِهَا فَقَامَ فِي وَسَطِهَا»","ސަމުރާ ބިން ޖުންދުބް رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނިފާސްކަމުގެ ތެރޭގައި އަވަހާރަވި އަންހެނެއްގެ މައްޗަށް، ނަބިއްޔާ ﷺގެ ފަހަތްޕުޅުގައި ތިމަން ކަށުނަމާދު ކުރީމެވެ. ނަބިއްޔާ ތެދުވެވަޑައިގެން ހުންނެވީ އެ އަވަހާރަވި އަންހެނާގެ މެދަށްވާ ގޮތަށެވެ.","[البخاري 332، 1331، 1332، ومسلم 964]"],["#171","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ أَبِي مُوسَى عَبْدِ اللَّهِ بْنِ قَيْسٍ- رَضِيَ اللَّهُ عَنْهُ -: «أَنَّ رَسُولَ اللَّهِ ﷺ بَرِيءٌ مِنْ الصَّالِقَةِ وَالْحَالِقَةِ وَالشَّاقَّةِ»","އަބޫ މޫސާ، ޢަބްދު ﷲ ބިން ގައިސް رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން (މުސީބާތެއް ޖެހުމުން) އަޑު އުފުލައި ހޭރިގަންނަ އަންހެނާއާއި، އިސްތަށި ބާލާ (ނުވަތަ އުފުރާ) އަންހެނާއާއި، ހެދުން އިރާލާ އަންހެނާގެ ކިބައިން ރަސޫލު ﷲ ﷺ ބަރީއަވެގެންވެއެވެ.","[البخاري 1296، ومسلم 104]"],["#172","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا قَالَ: «لَمَّا اشْتَكَى النَّبِيُّ ﷺ ذَكَرَ بَعْضُ نِسَائِهِ كَنِيسَةً رَأَيْنَهَا بِأَرْضِ الْحَبَشَةِ، يُقَالُ لَهَا: مَارِيَةُ - وَكَانَتْ أُمُّ سَلَمَةَ وَأُمُّ حَبِيبَةَ أَتَتَا أَرْضَ الْحَبَشَةِ - فَذَكَرَتَا مِنْ حُسْنِهَا وَتَصَاوِيرَ فِيهَا، فَرَفَعَ رَأْسَهُ ﷺ وَقَالَ: أُولَئِكَ إذَا مَاتَ فِيهِمْ الرَّجُلُ الصَّالِحُ بَنَوْا عَلَى قَبْرِهِ مَسْجِداً، ثُمَّ صَوَّرُوا فِيهِ تِلْكَ الصُّوَرَ أُولَئِكَ شِرَارُ الْخَلْقِ عِنْدَ اللَّهِ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ އާލާސްވެވަޑައިގެން އިންނެވި ހިނދު، ނަބިއްޔާގެ ބައެއް އަނބިކަނބަލުން ޙަބަޝާގައި ވަނިކޮށް އެ ކަނބަލުންނަށް ފެނުނު މާރިޔާއޭ ކިޔުނު ކަނީސާއެއްގެ ވާހަކަ ނަންގަތެވެ. - އުންމު ސަލަމާއާއި އުންމު ޙަބީބާ ވަނީ ޙަބަޝާއަށް ވަޑައިގެންފައެވެ. - އަދި އެ ކަނީސާގެ ރީތިކަމާއި އޭގައި ހުރި (މީހުންގެ) ސޫރަތަކުގެ ވާހަކަ ދެއްކެވިއެވެ. ފަހެ ނަބިއްޔާ ﷺ އިސްތަށިފުޅު އުފުއްލަވާފައި ޙަދީޘް ކުރެއްވިއެވެ: ”އެބައިމީހުންނީ، އެބައިމީހުންގެ ތެރެއިން ސާލިހު މީހަކު މަރުވެއްޖެ ނަމަ، އޭނާގެ މަހާނަ މަތީ މިސްކިތް ބިނާކޮށް، ތިޔަ ސޫރަތައް ހަދައި އުޅޭ މީހުންނެވެ. ﷲގެ ހަޒްރަތުގައި އެބައިމީހުންނަކީ ޚަލްގުތަކުންގެ ތެރެއިން އެންމެ ނުބައި ޚަލްގުތަކެވެ.“","[البخاري 1341 واللفظ له، ومسلم 528]"],["#173","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا قَالَتْ: قَالَ رَسُولُ اللَّهِ ﷺ فِي مَرَضِهِ الَّذِي لَمْ يَقُمْ مِنْهُ: «لَعَنَ اللَّهُ الْيَهُودَ وَالنَّصَارَى اتَّخَذُوا قُبُورَ أَنْبِيَائِهِمْ مَسَاجِدَ» قَالَتْ: وَلَوْلا ذَلِكَ أُبْرِزَ قَبْرُهُ غَيْرَ أَنَّهُ خُشِيَ أَنْ يُتَّخَذَ مَسْجِداً.","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ފަސޭހަފުޅު ނުވި އާލާސްކަންފުޅުގައި ވަނިކޮށް ޙަދީޘް ކުރެއްވިއެވެ: ”لَعَنَ اللَّهُ الْيَهُودَ وَالنَّصَارَى (ޔަހޫދީންނާއި ނަސާރާއިންނަށް ﷲ ލައުނަތް ލައްވާށިއެވެ.) އެބައިމީހުންގެ ނަބިއްޔުންގެ މަހާނަފުޅުތައް މިސްކިތްތަކެއްގެ ގޮތުގައި އެބައިމީހުން ނެގިއެވެ.“ ޢާއިޝާ ވިދާޅުވިއެވެ: އެހެން ނޫން ނަމަ، ނަބިއްޔާގެ މަހާނަފުޅު ވެސް (ބިނާކުރުމުގައި) އުފުލައި ނެގީހެވެ. އެހެނެއްކަމަކު، ހަމަކަށަވަރުން މިސްކިތެއްގެ ގޮތުގައި އެ ތަން ނެގިގެންދާނެ ކަމުގެ ބިރު ހުއްޓެވެ.","[البخاري 1330، ومسلم 529 واللفظ له]"],["#174","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ مَسْعُودٍ - رَضِيَ اللَّهُ عَنْهُ - عَنْ النَّبِيِّ ﷺ أَنَّهُ قَالَ: «لَيْسَ مِنَّا مَنْ ضَرَبَ الْخُدُودَ، وَشَقَّ الْجُيُوبَ، وَدَعَا بِدَعْوَى الْجَاهِلِيَّةِ»","ޢަބްދު ﷲ ބިން މަސްޢޫދު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”(މުސީބާތެއް ޖެހުމުން އަމިއްލަ) ކޯތާފަތް މަތީ ޖަހާ ހަދާ މީހުންނާއި، ހެދުން އިރާލާ މީހުންނާއި، ޖާހިލިއްޔަތުގެ (ހޭރުންތަކާއި) ގޮވާލުންތަކުން ގޮވާ މީހުންނަކީ، ތިމަންމެންގެ ތެރެއިންވާ ބަޔެއް ނޫނެވެ.“","[البخاري 1294، ومسلم 103 واللفظ له]"],["#175","كتاب الجنائز","ޖަނާޒާގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «مَنْ شَهِدَ الْجِنَازَةَ حَتَّى يُصَلِّيَ عَلَيْهَا فَلَهُ قِيرَاطٌ. وَمَنْ شَهِدَهَا حَتَّى تُدْفَنَ فَلَهُ قِيرَاطَانِ. قِيلَ: وَمَا الْقِيرَاطَانِ؟ قَالَ: مِثْلُ الْجَبَلَيْنِ الْعَظِيمَيْنِ»\n\nوَلِمُسْلِمٍ: «أَصْغَرُهُمَا مِثْلُ أُحُدٍ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ޖަނާޒާއެއްގައި ނަމާދު ކުރުމަށް ދާންދެން އޭގައި ބައިވެރިވެއްޖެ މީހަކަށް، ގީރާޠެއް (ގެ ސަވާބު) ލިބިގެންވެއެވެ. އަދި އެ (މައްޔިތާ) ވަޅުލުމަށް ދާންދެން ބައިވެރިވެއްޖެ މީހަކަށް ދެ ގީރާޠް ލިބިގެންވެއެވެ.“ ނަބިއްޔާއަށް ދެންނެވުނެވެ: ދެ ގީރާޠަކީ ކިހާވަރެއްތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ބޮޑު ދެ ފަރުބަދައާ އެއްވަރުވެގެން ވެއެވެ.“ \n\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ”އެ ދޭތީގެ އެންމެ ކުޑަ މިންވަރު، އުހުދު ފަރުބަދައާ އެއްވަރުވެގެން ވެއެވެ.“","[البخاري 1325، ومسلم 945 واللفظ له\n\nمسلم 945]"],["#176","كتاب الزكاة","ޒަކާތުގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ لِمُعَاذِ بْنِ جَبَلٍ - حِينَ بَعَثَهُ إلَى الْيَمَنِ -: «إنَّك سَتَأْتِي قَوْماً أَهْلَ كِتَابٍ. فَإِذَا جِئْتَهُمْ: فَادْعُهُمْ إلَى أَنْ يَشْهَدُوا أَنْ لا إلَهَ إلاَّ اللَّهُ، وَأَنَّ مُحَمَّداً رَسُولُ اللَّهِ. فَإِنْ هُمْ أَطَاعُوا لَكَ بِذَلِكَ فَأَخْبِرْهُمْ: أَنَّ اللَّهَ قَدْ فَرَضَ عَلَيْهِمْ خَمْسَ صَلَوَاتٍ فِي كُلِّ يَوْمٍ وَلَيْلَةٍ. فَإِنْ هُمْ أَطَاعُوا لَك بِذَلِكَ، فَأَخْبِرْهُمْ: أَنَّ اللَّهَ قَدْ فَرَضَ عَلَيْهِمْ صَدَقَةً، تُؤْخَذُ مِنْ أَغْنِيَائِهِمْ فَتُرَدُّ عَلَى فُقَرَائِهِمْ. فَإِنْ هُمْ أَطَاعُوا لَك بِذَلِكَ، فَإِيَّاكَ وَكَرَائِمَ أَمْوَالِهِمْ. وَاتَّقِ دَعْوَةَ الْمَظْلُومِ. فَإِنَّهُ لَيْسَ بَيْنَهَا وَبَيْنَ اللَّهِ حِجَابٌ»","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: މުޢާޛު ބިން ޖަބަލު ޔަމަނަށް ފޮނުއްވަމުން، ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން އަހުލު ކިތާބީންގެ ބަޔަކު ގާތަށް ތިބާ ދެވޭނެއެވެ. ފަހެ އެބައިމީހުންގެ ގާތަށް ދެވުމުން، ﷲ ފިޔަވައި (އަޅުކަންކުރުން ހައްގުވާ) އެހެން އެއްވެސް އިލާހަކު ނުވާ ކަމަށާއި، މުޙައްމަދުގެފާނީ ﷲގެ ރަސޫލާ ކަމަށް ހެކިވުމަށް އެބައިމީހުންނަށް ގޮވާލާށެވެ. ދެން އެ ކަމުގައި ތިބާއަށް ތަބާވެއްޖެ ނަމަ، އެބައިމީހުންގެ މައްޗައް ކޮންމެ ރެއަކާއި ދުވާލު ފަސް ނަމާދެއް ﷲ ފަރުޟު ކުރެއްވިކަން އަންގާށެވެ. ދެން އެ ކަމުގައި ތިބާއަށް ތަބާވެއްޖެ ނަމަ، އެބައިމީހުންގެ މުއްސަންދިންގެ އަތުން ނެގޭ، އަދި އެބައިމީހުންގެ ފަގީރުންނަށް ދެވޭ ޒަކާތެއް ﷲ އެބައިމީހުންގެ މައްޗަށް ފަރުޟު ކުރެއްވިކަން އަންގާށެވެ. ދެން އެ ކަމުގައި ތިބާއަށް ތަބާވެއްޖެއްޔާ، ނަމަނަމަ އެބައިމީހުންގެ އެންމެ މޮޅެތި މުދާތަކުން (ޒަކާތް) ނުނަގާށެވެ. އަދި އަނިޔާ ލިބިފައިވާ މީހާގެ (ބަދު) ދުއާއިން ރައްކާތެރިވާށެވެ. އެހެނީ ހަމަކަށަވަރުން އެ ދުއާއާއި ﷲއާ ދެމެދު އެއްވެސް ހުރަހެއް ނުވެއެވެ.“","[البخاري 1496 واللفظ له، ومسلم 19]"],["#177","كتاب الزكاة","ޒަކާތުގެ ފޮތް","","","عن أبي سعيد الخدري رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «لَيْسَ فِيمَا دُونَ خَمْسِ أَوَاقٍ صَدَقَةٌ. وَلا فِيمَا دُونَ خَمْسِ ذَوْدٍ صَدَقَةٌ. وَلا فِيمَا دُونَ خَمْسَةِ أَوْسُقٍ صَدَقَةٌ»","އަބޫ ސަޢީދު އަލްޚުދްރީ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”(މުދަލުގައި) ފަސް (ރިހި) އޫގިޔާއަށް ވުރެ މަދުންވާ ނަމަ، އޭގެ މައްޗަށް ދައްކަންޖެހޭ ޒަކާތެއް ނުވެއެވެ. އަދި (ޖަމަލު ގެންގުޅުމުގައި) ފަސް ޖަމަލަށް ވުރެ މަދުން ވާނަމަ، އޭގެ މައްޗަށް ދައްކަންޖެހޭ ޒަކާތެއް ނުވެއެވެ. އަދި (ދަނޑުވެރިކަމުން އުފައްދާ ކާތަކެތީގައި) ފަސް ވަސަގަށް ވުރެ މަދުން ވާނަމަ، އޭގެ މައްޗަށް ދައްކަންޖެހޭ ޒަކާތެއް ނުވެއެވެ.“\n\n(ނޯޓު: މިއީ މުދަލުން ޒަކާތް ވާޖިބުވާ ނިސާބުތަކެވެ. އޫގިޔާއަކީ ސާޅީސް ދިރުހަމާ އެއްވަރުގެ މުދަލެވެ. އެބަހީ ދުއިސައްތަ ދިރުހަމް، ނުވަތަ ފަސް ސަތޭކަ ނުވަދިހަ ފަސް ގްރާމްގެ ރިއްސަށްވުރެ މަދުންވާ މުދަލުގައި، ޒަކާތް ދައްކާކަށެއް ނުޖެހެއެވެ. އަދި ވަސަގަކީ ފަސްދޮޅަސް ޞާޢެވެ. އެބަހީ ދަނޑުވެރިކަމުން އުފައްދާ ތަކެތިން ސަތޭކަ ޞާޢު، ނުވަތަ އެއްހާސް ފަސް ސަތޭކަ ލީޓަރަށް ވުރެ މަދުންވާ ދަނޑުވެރިކަމުގެ ކާތަކެތީގައި، ޒަކާތް ދައްކާކަށެއް ނުޖެހެއެވެ.)","[البخاري 1405، ومسلم 979 واللفظ له]"],["#178","كتاب الزكاة","ޒަކާތުގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ رَضِيَ اللَّهُ عَنْهُ أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «لَيْسَ عَلَى الْمُسْلِمِ فِي عَبْدِهِ وَلا فَرَسِهِ صَدَقَةٌ»\n\nوَفِي لَفْظٍ: «إلاَّ زَكَاةَ الْفِطْرِ فِي الرَّقِيقِ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”މުސްލިމެއްގެ އަޅެއް ނުވަތަ އަހެއްގެ މައްޗަށް ދައްކަންޖެހޭ ޒަކާތެއް ނުވެއެވެ.“\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”އެއީ އަޅުންގެ މަތިން ދައްކާ ފިތުރު ޒަކާތް މެނުވީއެވެ.“","[البخاري 1464، ومسلم 982 واللفظ له\n\nأبو داود 1594]"],["#179","كتاب الزكاة","ޒަކާތުގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «الْعَجْمَاءُ جُبَارٌ. وَالْبِئْرُ جُبَارٌ. وَالْمَعْدِنُ جُبَارٌ. وَفِي الرِّكَازِ الْخُمْسُ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”(ވެރިމީހާގެ އިހުމާލެއް ނެތި) ޖަނަވާރެއްގެ (ކިބައިން ގެއްލުމެއް ލިބިއްޖެ މީހަކަށް) ބަދަލުގައި އެއްޗެއް ދޭކަށެއް ނުޖެހެއެވެ. އަދި (އެހެން މީހެއްގެ ބިމެއްގައި ކޮނެފައިވާ) ވަޅަކަށް (ވެއްޓިއްޖެ މީހަކަށް) ބަދަލުގައި އެއްޗެއް ދޭކަށެއް ނުޖެހެއެވެ. އަދި މައުދަން ނެގުމުގެ (ތެރޭގައި ގެއްލުމެއް ލިބިއްޖެ މީހަކަށް) ބަދަލުގައި އެއްޗެއް ދޭކަށެއް ނުޖެހެއެވެ. އަދި ރިކާޒުގެ ފަސްބައި ކުޅަ އެއްބައި (މުދަލު ޒަކާތުގެ ގޮތުގައި) ދޭންޖެހޭނެއެވެ.“\n\n(ނޯޓު: ރިކާޒަކީ ޖާހިލިއްޔަ ޒަމާނުގައި ވަޅުލެވިފައިވާ މުދަލެވެ.)","[البخاري 1499، ومسلم 1710 واللفظ له]"],["#180","كتاب الزكاة","ޒަކާތުގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «بَعَثَ رَسُولُ اللَّهِ ﷺ عُمَرَ - رَضِيَ اللَّهُ عَنْهُ - عَلَى الصَّدَقَةِ. فَقِيلَ: مَنَعَ ابْنُ جَمِيلٍ وَخَالِدُ بْنُ الْوَلِيدِ، وَالْعَبَّاسُ عَمُّ رَسُولِ اللَّهِ ﷺ. فَقَالَ رَسُولُ اللَّهِ ﷺ مَا يَنْقِمُ ابْنُ جَمِيلٍ، إلاَّ أَنْ كَانَ فَقِيراً: فَأَغْنَاهُ اللَّهُ؟ وَأَمَّا خَالِدٌ: فَإِنَّكُمْ تَظْلِمُونَ خَالِداً. وَقَدْ احْتَبَسَ أَدْرَاعَهُ وَأَعْتَادَهُ فِي سَبِيلِ اللَّهِ. وَأَمَّا الْعَبَّاسُ: فَهِيَ عَلَيَّ وَمِثْلُهَا. ثُمَّ قَالَ: يَا عُمَرُ، أَمَا شَعَرْتَ أَنَّ عَمَّ الرَّجُلِ صِنْوُ أَبِيهِ؟»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ޒަކާތް ބަލައިގަތުމަށް ޓަކައި ރަސޫލު ﷲ ﷺ، ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ފޮނުއްވިއެވެ. ނަބިއްޔާއަށް ދެންނެވުނެވެ: އިބްނު ޖަމީލާއި، ޚާލިދު ބިން ވަލީދާއި، ރަސޫލު ﷲ ﷺގެ ބޮޑުބޭބޭފުޅު އަލްޢައްބާސް ޒަކާތް ދިނުމުން މަނާވެގަތެވެ. ރަސޫލު (ﷲ) ޙަދީޘް ކުރެއްވިއެވެ: ”އިބްނު ޖަމީލަށް އައިބު ކިޔޭ ހަމައެކަނި ކަމަކީ، އެކަލޭގެފާނު ފަގީރު ހާލުގައި ވަނިކޮށް، ﷲ އެކަލޭގެފާނު މުއްސަންދި ކުރެއްވިކަމެވެ. ދެން ޚާލިދު (ގެ މައްސަލައިގައި)، ފަހެ ހަމަކަށަވަރުން ތިޔަބައިމީހުން ޚާލިދާމެދު (ބަސް ބުނީ) އިންސާފުވެރިކަމާ އެކީގައެއް ނޫނެވެ. އެހެނީ އެކަލޭގެފާނުގެ ހަނގުރާމައިގެ އާލާތްތަކާއި ތަކެތި ހިއްޕަވައިގެން ﷲގެ މަގުގައި ޖިހާދު ކުރެއްވިއެވެ. (އެބަހީ: އެއީ ވިޔަފާރިއަށް ގެންގުޅުއްވި ތަކެއްޗެއް ނޫނެވެ. ވީމާ އެއަށް ޒަކާތް ދައްކާކަށް ނުޖެހޭނެއެވެ.) ދެން އަލްޢައްބާސް (ގެ މައްސަލައިގައި)، ފަހެ އެކަލޭގެފާނުގެ އެ ޒަކާތާއި އޭގެ އިތުރު އަހަރެއްގެ ޒަކާތް ދެއްކުން ތިމަން ނަބިއްޔާ ކަށަވަރުކޮށް ދެއްވާނަމެވެ.“ އެއަށްފަހު ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ ޢުމަރުގެފާނެވެ. މީހެއްގެ ބޮޑުބޭބެއަކީ އޭނާގެ ބައްޕަފަދަ މިހެއްކަން ނުދެނަހުރީމުތޯއެވެ؟“ (ނޯޓު: ނަބިއްޔާ އެހެން ޙަދީޘް ކުރެއްވީ ބޮޑުބޭބެފުޅުގެ މަގާމުގެ މަތިވެރިކަން ހާމަ ކުރެއްވުމުގެ ގޮތުންނެވެ.)","[البخاري 1468، ومسلم 983 واللفظ له]"],["#181","كتاب الزكاة","ޒަކާތުގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ زَيْدِ بْنِ عَاصِمٍ قَالَ: «لَمَّا أَفَاءَ اللَّهُ عَلَى رَسُولِهِ يَوْمَ حُنَيْنٍ: قَسَمَ فِي النَّاسِ، وَفِي الْمُؤَلَّفَةِ قُلُوبُهُمْ وَلَمْ يُعْطِ الأَنْصَارَ شَيْئاً. فَكَأَنَّهُمْ وَجَدُوا فِي أَنْفُسِهِمْ، إذْ لَمْ يُصِبْهُمْ مَا أَصَابَ النَّاسَ. فَخَطَبَهُمْ، فَقَالَ: يَا مَعْشَرَ الأَنْصَارِ، أَلَمْ أَجِدْكُمْ ضُلاَّلاً فَهُدَاكُمْ اللَّهُ بِي؟ وَكُنْتُمْ مُتَفَرِّقِينَ فَأَلَّفَكُمْ اللَّهُ بِي؟ وَعَالَةً فَأَغْنَاكُمْ اللَّهُ بِي؟. كُلَّمَا قَالَ شَيْئاً، قَالُوا: اللَّهُ وَرَسُولُهُ أَمَنُّ. قَالَ: مَا يَمْنَعُكُمْ أَنْ تُجِيبُوا رَسُولَ اللَّهِ؟ قَالُوا: اللَّهُ وَرَسُولُهُ أَمَنُّ. قَالَ: لَوْ شِئْتُمْ لَقُلْتُمْ: جِئْتَنَا كَذَا وَكَذَا. أَلا تَرْضَوْنَ أَنْ يَذْهَبَ النَّاسُ بِالشَّاةِ وَالْبَعِيرِ، وَتَذْهَبُونَ بِرَسُولِ اللَّهِ إلَى رِحَالِكُمْ؟ لَوْلا الْهِجْرَةُ لَكُنْتُ امْرَأً مِنْ الأَنْصَارِ، وَلَوْ سَلَكَ النَّاسُ وَادِياً أَوْ شِعْباً لَسَلَكْتُ وَادِيَ الأَنْصَارِ وَشِعْبَهَا. الأَنْصَارُ شِعَارٌ، وَالنَّاسُ دِثَارٌ. إنَّكُمْ سَتَلْقَوْنَ بَعْدِي أَثَرَةً، فَاصْبِرُوا حَتَّى تَلْقَوْنِي عَلَى الْحَوْضِ»","ޢަބްދު ﷲ ބިން ޒައިދު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ޙުނައިނު ހަނގުރާމައިގެ ދުވަހު، ﷲ އެ އިލާހުގެ ރަސޫލާއަށް ޓަކައި ފައިއު މުދާ ދެއްވި ހިނދު، ނަބިއްޔާ އެ މުދާތައް މީސްތަކުންގެ ތެރޭގައި ބެއްސެވިއެވެ. (ނޯޓު: ފައިއު މުދަލަކީ، ކާފަރުން ހަނގުރާމަކުރުން ދޫކޮށް އެ ހުރިތަނަކުން ދިޔުމުން ނުވަތަ ފިލުމުން، އެބައިމީހުންގެ ފަހަތުގައި ދޫކޮށްފަދާ މުދަލެވެ.) އެ ގޮތުން (އިސްލާމްކަމަށް ގާތްކުރުމާއި އީމާންކަން ވަރުގަދަ ކުރުމުގެ ގޮތުން) ހިތްތައް އެކުވެރި ކުރުމަށްޓަކައި ނަބިއްޔާ ބަޔަކަށް މުދާ ދެއްވިއެވެ. އެކަމަކު އަންސާރުންނަށް އެއްވެސް އެއްޗެއް ނަބިއްޔާ ނުދެއްވައެވެ. ފަހެ މީސްތަކުންނަށް ލިބުނު އެއްޗަކުން އަންސާރުންނަށް ނުލިބުމުން، އެބައިމީހުންގެ ހިތްތަކުގައި (ހިތްހަމަ ނުޖެހުމެއް) ހުރިފަދައެވެ. ދެން ނަބިއްޔާ އެބައިމިހުންނާ މުޚާތަބު ކުރައްވައި ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ އަންސާރުންގެ ޖަމާއަތުގެ މީހުންނޭވެ. ތިމަން ނަބިއްޔާ ތިޔަބައިމީހުން ދެކެވަޑައިގަތީ، ތިޔަބައިމީހުން މަގުފުރެދިފައިވަނިކޮށް ނޫންހެއްޔެވެ؟ ދެން ތިމަން ނަބިއްޔާގެ ސަބަބުން، ﷲ ތިޔަބައިމީހުންނަށް ހިދާޔަތު ދެއްކެވިއެވެ. އަދި ތިޔަބައިމީހުން ބައިބައިވެފައިވަނިކޮށް، ތިމަން ނަބިއްޔާގެ ސަބަބުން ﷲ ތިޔަބައިމީހުންގެ ހިތްތައް އެކުވެރި ކުރެއްވި ނޫންހެއްޔެވެ؟ އަދި ތިޔަބައިމީހުން ފަގީރު ހާލުގައި ވަނިކޮށް، ތިމަން ނަބިއްޔާގެ ސަބަބުން ﷲ ތިޔަބައިމީހުން މުއްސަންދި ކުރެއްވި ނޫންހެއްޔެވެ؟“ ނަބިއްޔާ ބަސްފުޅެއް ވިދާޅުވި ކޮންމެ ފަހަރަކު، އެބައިމީހުން ދެންނެވިއެވެ: ﷲ އާއި އެ އިލާހުގެ ރަސޫލާ (ތިމަންމެންނަށް) ނިއުމަތް ދެއްވައިފިއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲގެ ރަސޫލާއަށް އިޖާބަދިނުމުން ތިޔަބައިމިހުން މަނާ ކުރަނީ ކޮންކަމެއްތޯއެވެ؟“ އެބައިމީހުން ދެންނެވިއެވެ: ﷲ އާއި އެ އިލާހުގެ ރަސޫލާ (ތިމަންމެންނަށް) ނިއުމަތް ދެއްވައިފިއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ތިޔަބައިމީހުން އެދުނު ނަމަ ބުނެވުނީހެވެ: އިބަ ނަބިއްޔާ ތިމަންމެންގެ ގާތަށް ވަޑައިގަތީ އެވެނި މިވެނި ހާލެއްގައެވެ. ފަހެ މީސްތަކުން ކަނބަޅިތަކާއި ޖަމަލުތަކައިގެން އެނބުރިދާ ހިނދު، ﷲގެ ރަސޫލާއައިގެން ތިޔަބައިމިހުންގެ ގެތަކަށް ދެވޭކަމަށް ތިޔަބައިމީހުން ނުރުހެމުތޯއެވެ؟ އަދި ހިޖުރަ ކުރުން ނުވާ ނަމަ، އަންސާރުންގެ ތެރެއިން މީހަކަށް ތިމަން ނަބިއްޔާ ވެވަޑައިގަތީމުހެވެ. އަދި ބޮޑު ނުވަތަ ކުޑަ ވާދީއެއްގެ ތެރެއިން މީސްތަކުން ހިނގައްޖެ ނަމަ، އަންސާރުންގެ ބޮޑު ނުވަތަ ކުޑަ ވާދީގެ ތެރެއިންލާފައި ތިމަން ނަބިއްޔާ ވަޑައިގަތީމުހެވެ. އަންސާރުންނަކީ ސީދާ ހަމުގެ މަތީގައި ލާ ހެދުމެވެ. އަދި އެހެން މީސްތަކުންނަކީ އެހެން ހެދުންތަކުގެ މަތީގައި ލާ ހެދުމެވެ. ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާއަށް ފަހުގައި، ތިޔަބައިމީހުންގެ މައްޗަށް އެހެން މިހުން އިސްކުރެވޭތަން ތިޔަބައިމީހުން ދެކޭނެއެވެ. ވީމާ (ގިޔާމަތް ދުވަހު) ޙައުޟުގައި ތިމަން ނަބިއްޔާއާ ބައްދަލުވުމަށް ދާންދެން ކެތްތެރިވާށެވެ.“","[البخاري 4330 واللفظ له، ومسلم 1061]"],["#182","كتاب الزكاة","ޒަކާތުގެ ފޮތް","باب صدقة الفطر","ފިތުރު ޒަކާތުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما قَالَ: «فَرَضَ رَسُولُ اللَّهِ ﷺ صَدَقَةَ الْفِطْرِ - أَوْ قَالَ رَمَضَانَ - عَلَى الذَّكَرِ وَالأُنْثَى وَالْحُرِّ وَالْمَمْلُوكِ: صَاعاً مِنْ تَمْرٍ، أَوْ صَاعاً مِنْ شَعِيرٍ. قَالَ: فَعَدَلَ النَّاسُ بِهِ نِصْفَ صَاعٍ مِنْ بُرٍّ، عَلَى الصَّغِيرِ وَالْكَبِيرِ»\n\nوَفِي لَفْظٍ: «أَنْ تُؤَدَّى قَبْلَ خُرُوجِ النَّاسِ إلَى الصَّلاةِ»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ފިރިހެނުންނާއި އަންހެނުންނާއި، މިނިވަނުންނާއި އަޅުންގެ މައްޗަށް، ފިތުރު (ނުވަތަ ރަމަޟާނުގެ) ޒަކާތް ދެއްކުން ރަސޫލު ﷲ ﷺ ފަރުޟު ކުރެއްވިއެވެ. އެއީ ކަދުރު ނުވަތަ ހިމަގޮދަނުން ޞާޢެއްގެ މިންވަރެވެ. ދެން ގޮދަނުގެ ޒަކާތަކީ ޞާޢެއްގެ ދެބައިކުޅަ އެއްބައިކަމަށް މީސްތަކުން ކަނޑައެޅުއްވިއެވެ. އެއީ ކުޑަކުދިންނާއި ބޮޑެތިމީހުންގެ މައްޗަށްވެސް މެއެވެ.\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: މީސްތަކުން (އީދު) ނަމާދަށް ނުކުތުމުގެ ކުރިން ފިތުރު ޒަކާތް ދެއްކުން (ފަރުޟު ކުރެވުނެވެ.)","[البخاري 1151 واللفظ له، ومسلم 984\n\nالبخاري 1503، ومسلم 986]"],["#183","كتاب الزكاة","ޒަކާތުގެ ފޮތް","باب صدقة الفطر","ފިތުރު ޒަކާތުގެ ބާބު","عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «كُنَّا نُعْطِيهَا فِي زَمَنِ ﷺ صَاعاً مِنْ طَعَامٍ، أَوْ صَاعاً مِنْ شَعِيرٍ، أَوْ صَاعاً مِنْ أَقِطٍ، أَوْ صَاعاً مِنْ زَبِيبٍ. فَلَمَّا جَاءَ مُعَاوِيَةُ، وَجَاءَتِ السَّمْرَاءُ، قَالَ: أَرَى مُدَّاً مِنْ هَذِهِ يَعْدِلُ مُدَّيْنِ.\n\nقَالَ أَبُو سَعِيدٍ: أَمَّا أَنَا: فَلا أَزَالُ أُخْرِجُهُ كَمَا كُنْتُ أُخْرِجُهُ عَلَى عَهْدِ رَسُولِ اللَّهِ ﷺ»","އަބޫ ސަޢީދު އަލްޚުދްރީ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺގެ ޒަމާނުގައި، ކާބޯތަކެތިން، ނުވަތަ ހިމަގޮދަނުން، ނުވަތަ ޗީޒުން، ނުވަތަ ހިކިމޭބިސްކަދުރުން ޞާޢެއްގެ މިންވަރު ފިތުރު ޒަކާތުގެ ގޮތުގައި ތިމަންމެން ނެރުނީމެވެ. ދެން މުޢާވިޔާގެ (ޚަލީފާކަމުގެ ދުވަސްވަރުގައި) ގޮދަން އައުމާއެކީ އެކަލޭގެފާނު ވިދާޅުވިއެވެ: މީގެ މުއްދެއް ވަރު، (އެހެނިހެން ކާބޯތަކެތީގެ) ދެ މުއްދާ އެއްވަރުކަމަށް ތިމަން ދެކެމެވެ.\n\nއަބޫ ސަޢީދު ވިދާޅުވިއެވެ: އެހެނެއްކަމަކު ތިމަންނަ (ދިރިހުރިހާ ހިނދަކު) މެދުކެނޑުމެއް ނެތި ފިތުރު ޒަކާތް ނެރެނީ ރަސޫލު ﷲ ﷺގެ ޒަމާނުގައި ތިމަން ނެރުނު ގޮތަށެވެ.","[البخاري 1508 واللفظ له، ومسلم 985\n\nمسلم 985]"],["#184","كتاب الصيام","ރޯދައިގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «لا تَقَدَّمُوا رَمَضَانَ بِصَوْمِ يَوْمٍ، أَوْ يَوْمَيْنِ إلاَّ رَجُلاً كَانَ يَصُومُ صَوْماً فَلْيَصُمْهُ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ރަމަޟާން މަސް ފެށުމުގެ އެއް ދުވަސް ނުވަތަ ދެ ދުވަސް ކުރިން ރޯދަ ހިފަން (ނުފަށާށެވެ.) އެއީ (އާދައިގެ މަތިން އެ ދުވަސްތަކުގައި) ރޯދަ ހިފަމުން އައި މީހަކު މެނުވީއެވެ. ފަހެ އެ މީހާކު އެ ރޯދަ ހިފާށެވެ.“","[البخاري 1914، ومسلم 1082 واللفظ له]"],["#185","كتاب الصيام","ރޯދައިގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما قَالَ: سَمِعْتُ رَسُولَ اللَّهِ ﷺ يَقُولُ: «إذَا رَأَيْتُمُوهُ فَصُومُوا، وَإِذَا رَأَيْتُمُوهُ فَأَفْطِرُوا، فَإِنْ غُمَّ عَلَيْكُمْ فَاقْدُرُوا لَهُ»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ”ތިޔަބައިމީހުންނަށް (ރަމަޟާން މަހުގެ) ހަނދު ފެނުމުން ރޯދަ ހިފަން ފަށާށެވެ. ދެން (ޝައްވާލު މަހުގެ) ހަނދު ފެނުމުން ރޯދަ ހިފުން ދޫކޮށްލާށެވެ. އަދި ވިލާ ބޯވެގެން (ހަނދު ނުފެނިއްޖެ) ނަމަ ތިޔަބައިމީހުން އަދަދުކޮށް (ޝައުބާން މަހުގެ) ތިރީސް ދުވަސް ފުރިހަމަ ކުރާށެވެ.“","[البخاري 1900، ومسلم 1080]"],["#186","كتاب الصيام","ރޯދައިގެ ފޮތް","","","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ «تَسَحَّرُوا فَإِنَّ فِي السَّحُورِ بَرَكَةً»","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ހާރު ކާށެވެ. އެހެނީ ހަމަކަށަވަރުން ހާރު ކެއުމުގައި ބަރަކާތްލެއްވިގެން ވެއެވެ.“","[البخاري 1923، ومسلم 1095]"],["#187","كتاب الصيام","ރޯދައިގެ ފޮތް","","","عَنْ أَنَسِ بْنِ مَالِكٍ عَنْ زَيْدِ بْنِ ثَابِتٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: «تَسَحَّرْنَا مَعَ رَسُولِ اللَّهِ ﷺ. ثُمَّ قَامَ إلَى الصَّلاةِ. قَالَ أَنَسٌ: قُلْت لِزَيْدٍ: كَمْ كَانَ بَيْنَ الأَذَانِ وَالسَّحُورِ؟ قَالَ: قَدْرُ خَمْسِينَ آيَةً»","އަނަސް ބިން މާލިކު، ޒައިދު ބިން ޘާބިތު رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺގެ އަރިހުގައި ތިމަންމެން ހާރު ކެއީމެވެ. އެއަށްފަހު ނަބިއްޔާ ނަމާދަށް ތެދުވެވަޑައިގަތެވެ. އަނަސް ވިދާޅުވިއެވެ: ތިމަން ޒައިދަށް ދެންނެވީމެވެ: ބަންގިއާއި ހާރު ކެއުމާ ދެމެދު ކިހާ ވަގުތެއް ވޭތުވިތޯއެވެ؟ ޒައިދު ވިދާޅުވިއެވެ: ފަންސާސް އާޔަތް ކިޔަވާހައި ވަގުތެވެ.","[البخاري 1921 واللفظ له، ومسلم 1097]"],["#188","كتاب الصيام","ރޯދައިގެ ފޮތް","","","عَنْ عَائِشَةَ وَأُمِّ سَلَمَةَ رَضِيَ اللَّهُ عَنْهُما: «أَنَّ رَسُولَ اللَّهِ ﷺ كَانَ يُدْرِكُهُ الْفَجْرُ وَهُوَ جُنُبٌ مِنْ أَهْلِهِ ثُمَّ يَغْتَسِلُ وَيَصُومُ»","ޢާއިޝާ އަދި އުންމު ސަލަމާ رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺގެ އަނބިކަނބަލުންގެ އަރިހުން، އެކަލޭގެފާނު ޖުނުބުވެރިވެ ވަޑައިގެންވާ ހާލުގައި ހުންނަވަނިކޮށް، އެކަލޭގެފާނަށް ފަތިސް ވަގުތު ވަދެގެން އާދެއެވެ. ދެން އެކަލޭގެފާނު ހިނައިތާހިރުވެ ވަޑައިގެން ރޯދަ ހިއްޕަވައެވެ.","[البخاري 1925، 1926 واللفظ له، ومسلم 1109]"],["#189","كتاب الصيام","ރޯދައިގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ رَضِيَ اللَّهُ عَنْهُ عَنْ النَّبِيِّ ﷺ قَالَ: «مَنْ نَسِيَ وَهُوَ صَائِمٌ. فَأَكَلَ أَوْ شَرِبَ، فَلْيُتِمَّ صَوْمَهُ. فَإِنَّمَا أَطْعَمَهُ اللَّهُ وَسَقَاهُ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”މީހަކު ރޯދައަށް ހުއްޓައި ހަނދާން ނެތިގެން ކެވިއްޖެ ނުވަތަ ބޮވިއްޖެ ނަމަ، ފަހެ އެ މީހެއްގެ ރޯދަ ފުރިހަމަ ކުރާށެވެ. އެހެނީ ހަމަކަށަވަރުން އޭނާއަށް ކާންދެއްވައި ބޯންދެއްވީ ﷲ ކަމުގައި މެނުވީ ނުވެއެވެ.“","[البخاري 1933، ومسلم 1155 واللفظ له]"],["#190","كتاب الصيام","ރޯދައިގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «بَيْنَمَا نَحْنُ جُلُوسٌ عِنْدَ النَّبِيِّ ﷺ إذْ جَاءَهُ رَجُلٌ. فَقَالَ: يَا رَسُولَ اللَّهِ، هَلَكْتُ. قَالَ: مَا أَهْلَكَكَ؟ قَالَ: وَقَعْتُ عَلَى امْرَأَتِي، وَأَنَا صَائِمٌ - وَفِي رِوَايَةٍ: أَصَبْتُ أَهْلِي فِي رَمَضَانَ - فَقَالَ رَسُولُ اللَّهِ ﷺ: هَلْ تَجِدُ رَقَبَةً تُعْتِقُهَا؟ قَالَ: لا. قَالَ: فَهَلْ تَسْتَطِيعُ أَنْ تَصُومَ شَهْرَيْنِ مُتَتَابِعَيْنِ؟ قَالَ: لا. قَالَ: فَهَلْ تَجِدُ إطْعَامَ سِتِّينَ مِسْكِيناً؟ قَالَ: لا. قَالَ: فَمَكَثَ النَّبِيُّ ﷺ فَبَيْنَا نَحْنُ عَلَى ذَلِكَ أُتِيَ النَّبِيُّ ﷺ بِعَرَقٍ فِيهِ تَمْرٌ - وَالْعَرَقُ: الْمِكْتَلُ - قَالَ: أَيْنَ السَّائِلُ؟ قَالَ: أَنَا. قَالَ: خُذْ هَذَا، فَتَصَدَّقَ بِهِ. فَقَالَ الرَّجُلُ: عَلَى أَفْقَرَ مِنِّي: يَا رَسُولَ اللَّهِ؟ فَوَاَللَّهِ مَا بَيْنَ لابَتَيْهَا - يُرِيدُ الْحَرَّتَيْنِ - أَهْلُ بَيْتٍ أَفْقَرَ مِنْ أَهْلِ بَيْتِي. فَضَحِكَ رَسُولُ اللَّهِ - ﷺ حَتَّى بَدَتْ أَنْيَابُهُ. ثُمَّ قَالَ: أَطْعِمْهُ أَهْلَكَ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺގެ އަރިހުގައި ތިމަންމެން އިށީނދެ ތިއްބައި، މީހަކު އައިސް ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ތިމަންނަ ހަލާކުވެއްޖެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ ހަލާކު ކުރުވީ ކޮންކަމެއްތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ: ތިމަންނަ ރޯދައަށް ހުއްޓައި، އަނބިމީހާއާއެކު އެއްދާން ކުރެވިއްޖެއެވެ. -އަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ރަމަޟާން މަހުގެ (ދުވާލު ގަޑީގައި) ތިމަންނަގެ އަނބިމީހާއާއެކު އެއްދާން ކުރެވިއްޖެއެވެ- ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާއަށް މިނިވަން ކުރާނެ އަޅަކު ވޭތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ: ނޫނެކެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ވިދިވިދިގެން ދެ މަސްދުވަހު ރޯދަހިފުމުގެ ކުޅަދާނަކަން ލިބިގެން ވޭތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ: ނޫނެކެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ފަސްދޮޅަސް މިސްކީނުންނަށް ކާންދެވިދާނެތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ: ނޫނެކެވެ. ދެން ނަބިއްޔާ ﷺ މަޑުކޮށްލައްވައިގެން ހުންނެވިއެވެ. ދެން ތިމަންމެން އެ ގޮތުގެ މަތީގައި ވަނިކޮށް، ކަދުރު އަޅާފައި ހުރި ޢަރަގެއް (އެބަހީ: މިނެކިރާ ކަންވާރެއް) ނަބިއްޔާގެ އަރިހަށް ގެނެވުނެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެއްކަލަ ސުވާލު ކުރި މީހާ ކޮބައިތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ: ތިމަންނަ (މިހުރީއެވެ.) ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”މި (ކަދުރުތައް) ނަގާފައި އޭގެން ސަދަގާތް ކުރާށެވެ.“ އޭނާ ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. (ސަދަގާތް ކުރަންވީ) ތިމަންނައަށް ވުރެ ފަގީރު މީހުންނަށްތޯއެވެ؟ އެހެނީ ﷲ ގަންދީ ބުނަމެވެ. މަދީނާގެ ދެ ޙައްރާ ދެމެދުގައި ތިމަންނަގެ ގޭގެ އަހުލުވެރިންނަށް ވުރެ ފަގީރު އެހެން ގެއެއްގެ ބައެއް ނުވެއެވެ. ދެން ފޭރާދަތްކޮޅު ފެންނަ ވަރަށް ރަސޫލު ﷲ ﷺ ހިނިވަރުނަ ކުރެއްވިއެވެ. އަދި ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާގެ އަހުލުވެރިންނަށް އޭގެން ކާންދޭށެވެ.“\n\n(ނޯޓު: ދެ ޙައްރާއަކީ މަދީނާގެ ހުޅަނގާއި އިރުމަތީގައިވާ ލާވާ ހިކިފައި ހުންނަ ދެ ބިންގަނޑެވެ.)","[البخاري 1936 واللفظ له، ومسلم 111]"],["#191","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا: «أَنَّ حَمْزَةَ بْنَ عَمْرٍو الأَسْلَمِيَّ قَالَ لِلنَّبِيِّ ﷺ: أَأَصُومُ فِي السَّفَرِ؟ - وَكَانَ كَثِيرَ الصِّيَامِ - فَقَالَ: إنْ شِئْتَ فَصُمْ وَإِنْ شِئْتَ فَأَفْطِرْ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ޙަމްޒާ ބިން ޢަމްރު އަލްއަސްލަމީ، ނަބިއްޔާ ﷺގެ އަރިހުގައި ދެންނެވިއެވެ: ދަތުރުމަތީގައި ތިމަންނަ ރޯދަ ހިފަންތޯއެވެ؟ -އަދި ޙަމްޒާއަކީ ވަރަށް ގިނައިން ރޯދަ ހިފާ އުޅުއްވި ބޭކަލެކެވެ- ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ ބޭނުން ނަމަ ރޯދަ ހިފާށެވެ. އަދި ބޭނުން ނަމަ ރޯދަ ހިފުން ދޫކުރާށެވެ.“","[البخاري 1934 واللفظ له، ومسلم 1121]"],["#192","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «كُنَّا نُسَافِرُ مَعَ النَّبِيِّ ﷺ فَلَمْ يَعِبِ الصَّائِمُ عَلَى الْمُفْطِرِ. وَلا الْمُفْطِرُ عَلَى الصَّائِمِ»","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺގެ އަރިހުގައި ތިމަންމެން ދަތުރުކޮށް ހެދީމެވެ. ފަހެ ރޯދާ ހިފާ މީހަކު، ރޯދަ ދޫކޮށްލާ މީހަކަށް އައިބު ކިޔައި ނަހަދައެވެ. އަދި ރޯދަ ދޫކޮށްލި މީހަކު، ރޯދަ ހިފާ މީހަކަށް އައިބު ކިޔައި ނަހަދައެވެ.","[البخاري 1947 واللفظ له، ومسلم 1118]"],["#193","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ أَبِي الدَّرْدَاءِ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «خَرَجْنَا مَعَ رَسُولِ اللَّهِ ﷺ فِي شَهْرِ رَمَضَانَ. فِي حَرٍّ شَدِيدٍ، حَتَّى إنْ كَانَ أَحَدُنَا لَيَضَعُ يَدَهُ عَلَى رَأْسِهِ مِنْ شِدَّةِ الْحَرِّ. وَمَا فِينَا صَائِمٌ إلاَّ رَسُولُ اللَّهِ ﷺ وَعَبْدُ اللَّهِ بْنُ رَوَاحَةَ»","އަބުއް ދަރްދާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަމަޟާން މަހު ވަރަށް ގަދަ ހޫނުގައި، ރަސޫލު ﷲ ﷺގެ އަރިހު ތިމަންމެން (ދަތުރެއްގައި) ނުކުތީމެވެ. ހޫނުވެގެން ގޮސް ތިމަންމެން ކުރެ މީހަކު އޭނަގެ އަތް ބޯމަތީ އަޅައިގެން އުޅުނެވެ. އަދި ތިމަންމެންގެ ތެރޭގައި ރަސޫލު ﷲ ﷺ އާއި ޢަބްދު ﷲ ބިން ރަވާޙާ ފިޔަވައި އެހެން ރޯދަވެރިއަކު ނެތެވެ.","[البخاري 1945، ومسلم 1122 واللفظ له]"],["#194","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رَضِيَ اللَّهُ عَنْهُما قَالَ: «كَانَ رَسُولُ اللَّهِ ﷺ فِي سَفَرٍ. فَرَأَى زِحَاماً وَرَجُلاً قَدْ ظُلِّلَ عَلَيْهِ، فَقَالَ: مَا هَذَا؟ قَالُوا: صَائِمٌ. قَالَ: لَيْسَ مِنْ الْبِرِّ الصِّيَامُ فِي السَّفَرِ»\n\nوَفِي لَفْظٍ لِمُسْلِمٍ: «عَلَيْكُمْ بِرُخْصَةِ اللَّهِ الَّتِي رَخَّصَ لَكُمْ»","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ދަތުރުފުޅެއް މަތީގައި ވަނިކޮށް، މީސްތަކުން ބޮނޑިވެގެން ތިބެ، މީހަކަށް ހިޔާއެޅިފައިވާ ތަން ނަބިއްޔާ ދެކެވަޑައިގަތެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”މިއީ ކޮންކަމެއްތޯއެވެ؟“ އެބައިމީހުން ދެންނެވިއެވެ: އެއީ ރޯދަވެރިއެކެވެ. ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ދަތުރު މަތީގައި ރޯދައަށް ހުރުމަކީ ހެޔޮކަމުގެ ތެރެއިންވާ ކަމެއް ނޫނެވެ.“ (އެބަހީ: މިހާވަރުން ރޯދަ ހިފުމަކީ ކުއްތަންވުމުގެ ތެރެއިންވާ ކަމެއް ނޫނެވެ.) \n\nއަދި މުސްލިމުގެ ރިވާޔަތުގައި ވެއެވެ: ”ތިޔަބައިމީހުންގެ މައްޗަށް އޮތީ، ތިޔަބައިމީހުންނަށް ޓަކައި ﷲ ފަސޭހަ ކޮށްދެއްވި ލުއިފަސޭހަކަމުގައި ހިފުމެވެ.“ (އެބަހީ: ދަތުރު މަތީގައި ރޯދަ ދޫކޮށްލުމެވެ.)","[البخاري 1946 واللفظ له، ومسلم 1115\n\nمسلم 1115]"],["#195","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «كُنَّا مَعَ النَّبِيِّ ﷺ فِي السَّفَرِ فَمِنَّا الصَّائِمُ، وَمِنَّا الْمُفْطِرُ قَالَ: فَنَزَلْنَا مَنْزِلاً فِي يَوْمٍ حَارٍّ، وَأَكْثَرُنَا ظِلاً: صَاحِبُ الْكِسَاءِ. وَمِنَّا مَنْ يَتَّقِي الشَّمْسَ بِيَدِهِ. قَالَ: فَسَقَطَ الصُّوَّامُ، وَقَامَ الْمُفْطِرُونَ فَضَرَبُوا الأَبْنِيَةَ. وَسَقَوْا الرِّكَابَ، فَقَالَ رَسُولُ اللَّهِ ﷺ: ذَهَبَ الْمُفْطِرُونَ الْيَوْمَ بِالأَجْرِ»","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺގެ އަރިހު ތިމަންމެން ދަތުރެއް ކުރީމެވެ. އަދި ތިމަންމެންގެ ތެރޭގައި ރޯދަވެރިންނާއި ރޯދަ ދޫކުރި މީހުން ތިއްބެވެ. ފަހެ ހޫނުގަދަ ދުވަހެއްގައި ތިމަންމެން ތަނެއްގައި މަޑު ކުރީމެވެ. ތިމަންމެންގެ ތެރެއިން އެންމެ ބޮޑަށް ހިޔާވަހިކަން ލިބުނީ ފޭރާން (ބޭނުންކޮށްގެން ހިޔާވަހިކަން ހޯދައިގަތް) ބަޔެކެވެ. އަދި އިރުގެ އަވިން ސަލާމަތް ވުމަށް އެ މީހެއްގެ އަމިއްލަ އަތުން ހިޔާވަހިކަން ހޯދި ބަޔަކުވެސް ތިމަންމެންގެ ތެރޭގައި ވިއެވެ. ފަހެ ރޯދަވެރިން (ވަރުބަލިވެގެން) ވެއްޓި އޮށޯވެއްޖެއެވެ. އަދި ރޯދަ ދޫކުރި މީހުން ތެދުވެ ގޮސް ފޭލިގެތައް ޖަހައި، ސަވާރީތަކަށް ފެން ދީ ހެދިއެވެ. ދެން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”މިއަދު އަޖުރު ހޯދައިގެން ދިޔައީ ރޯދަ ދޫކޮށްލި މީހުންނެވެ.“","[البخاري 2890، ومسلم 1119 واللفظ له]"],["#196","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا قَالَتْ: «كَانَ يَكُونُ عَلَيَّ الصَّوْمُ مِنْ رَمَضَانَ، فَمَا أَسْتَطِيعُ أَنْ أَقْضِيَ إلاَّ فِي شَعْبَانَ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަމަޟާން މަހުގައި ނުހިފި ހުރި ރޯދަތަކެއް ތިމަން ކަމަނާގެ މައްޗަށް ވިއެވެ. ފަހެ ޝައުބާން މަސް އައުމަށް ފަހުގައި މެނުވީ އެ ރޯދަތައް ގަޟާކުރަން ތިމަން ކަމަނާއަށް ކުޅަދާނަވެވަޑައި ނުގެންނެވިއެވެ.","[البخاري 1950، ومسلم 1146]"],["#197","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «مَنْ مَاتَ وَعَلَيْهِ صِيَامٌ صَامَ عَنْهُ وَلِيُّهُ»\n\nوَأَخْرَجَهُ أَبُو دَاوُد وَقَالَ «هَذَا فِي النَّذْرِ، وَهُوَ قَوْلُ أَحْمَدَ بْنِ حَنْبَلٍ».","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ޙަދީޘް ކުރެއްވިއެވެ: ”ނުހިފި ހުރި ރޯދަތަކެއް މީހެއްގެ މައްޗަށް ވަނިކޮށް އޭނާ ނިޔާވެއްޖެ ނަމަ، ފަހެ އޭނާގެ ވާރުތަވެރިން އެ މީހެއްގެ މަތިން އެ ރޯދަތައް ހިފާށެވެ.“ \n\nއަދި އަބޫ ދާވޫދު މި ޙަދީޘް ނެރުއްވުމަށް ފަހު ވިދާޅުވިއެވެ: މިއީ ނަދުރުގައިވާ (ޚާއްސަ ހުކުމެކެވެ.) އަދި މިއީ އަޙްމަދު ބިން ޙަންބަލް (ދެކިލައްވާ) ބަސްފުޅުވެސް މެއެވެ.","[البخاري 1952، ومسلم 1147\n\nأبو داود 2400]"],["#198","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُما قَالَ: «جَاءَ رَجُلٌ إلَى النَّبِيِّ ﷺ فَقَالَ: يَا رَسُولَ اللَّهِ، إنَّ أُمِّي مَاتَتْ وَعَلَيْهَا صَوْمُ شَهْرٍ. أَفَأَقْضِيهِ عَنْهَا؟ فَقَالَ: لَوْ كَانَ عَلَى أُمِّكَ دَيْنٌ أَكُنْتَ قَاضِيَهُ عَنْهَا؟ قَالَ: نَعَمْ. قَالَ: فَدَيْنُ اللَّهِ أَحَقُّ أَنْ يُقْضَى»\n\nوَفِي رِوَايَةٍ: «جَاءَتِ امْرَأَةٌ إلَى رَسُولِ اللَّهِ ﷺ فَقَالَتْ: يَا رَسُولَ اللَّهِ، إنَّ أُمِّي مَاتَتْ وَعَلَيْهَا صَوْمُ نَذْرٍ. أَفَأَصُومُ عَنْهَا؟ فَقَالَ: أَرَأَيْتِ لَوْ كَانَ عَلَى أُمِّكِ دَيْنٌ فَقَضَيْتِيهِ، أَكَانَ ذَلِكَ يُؤَدِّي عَنْهَا؟ فَقَالَتْ: نَعَمْ. قَالَ: فَصُومِي عَنْ أُمِّكِ»","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺގެ އަރިހަށް މީހަކު އައިސް ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން ތިމަންނަގެ މަންމަގެ މައްޗަށް މަސްދުވަހެއްގެ ރޯދަ ހިފުން ވަނިކޮށް، އެ ކަމަނާ ނިޔާވެއްޖެއެވެ. ފަހެ މަންމަގެ މަތިން ތިމަންނަ ރޯދަ ގަޟާކުރަންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާގެ މަންމަގެ މައްޗަށް ދަރަންޏެއް ވާނަމަ، އެ ކަމަނާގެ ފަރާތުން އެ ދަރަނި ތިބާ އަދާ ކުރާނަންތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ: އާދޭހެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ފަހެ ﷲ އަށްވާ ދަރަނި އަދާކުރުން މާ ހައްގުކަން ބޮޑެވެ.“ \n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ރަސޫލު ﷲ ﷺގެ އަރިހަށް އަންހެނަކު އައިސް ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ: ހަމަކަށަވަރުން ތިމަންނަގެ މަންމަ ނަދުރު ރޯދަތަކެއް ހިފަން ޖެހިފައިވަނިކޮށް، އެ ކަމަނާ ނިޔާވެއްޖެއެވެ. ފަހެ މަންމަގެ މަތިން ތިމަންނަ ރޯދަ ހިފަންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާގެ މަންމަގެ މައްޗަށް ދަރަންޏެއް ވަނިކޮށް، ތިބާ އެ ދަރަނި އަދާ ކޮށްފިކަމުގައި ވާނަމަ، ފަހެ އެކަމުން މަންމަގެ ދަރަނި އަދާވާނެކަމަށް ތިބާ ދެކެމުތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ: އާދޭހެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ފަހެ ތިބާގެ މަންމަގެ މަތިން ރޯދަ ހިފާށެވެ.“","[البخاري 1593، ومسلم 1148 واللفظ له\n\nمسلم 1148]"],["#199","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ سَهْلِ بْنِ سَعْدٍ السَّاعِدِيِّ - رَضِيَ اللَّهُ عَنْهُ - أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «لا يَزَالُ النَّاسُ بِخَيْرٍ مَا عَجَّلُوا الْفِطْرَ»","ސަހްލު ބިން ސަޢުދު އައްސާޢިދީ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”މީސްތަކުން ރޯދަ ވީއްލުން އަވަސް ކުރާހާ ހިނދަކު، ހެޔޮކަމުގެ މަތީގައި އެބައިމީހުން މެދުނުކެނޑި ދެމިތިބޭނެއެވެ.“","[البخاري 1957، ومسلم 1098]"],["#200","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ عُمَرَ بْنِ الْخَطَّابِ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «إذَا أَقْبَلَ اللَّيْلُ مِنْ هَهُنَا. وَأَدْبَرَ النَّهَارُ مِنْ هَهُنَا: فَقَدْ أَفْطَرَ الصَّائِمُ»","ޢުމަރު ބިން އަލްޚައްޠާބު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ރޭމި ފަރާތުން (އެބަހީ: އިރުމަތިން) ގަނޑު ވަދެ، އަދި މި ފަރާތުން (އެބަހީ: ހުޅަނގުން) ދުވާލު ގެއްލި (އިރު އޮއްސިއްޖެ) ހިނދު، ފަހެ ރޯދަވެރިޔާ (ރޯދަ ވީއްލާށެވެ.)“","[البخاري 1954 واللفظ له، ومسلم 1100]"],["#201","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما قَالَ: «نَهَى رَسُولُ اللَّهِ ﷺ عَنْ الْوِصَالِ. قَالُوا: إنَّكَ تُوَاصِلُ. قَالَ: إنِّي لَسْتُ كَهَيْئَتِكُمْ، إنِّي أُطْعَمَ وَأُسْقَى»\n\nوَرَوَاهُ أَبُو هُرَيْرَةَ وَعَائِشَةُ وَأَنَسُ بْنُ مَالِكٍ.","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ވިޞާލު ކުރުން ނަހީ ކުރެއްވިއެވެ. (ނޯޓު: ވިޞާލު ކުރުމަކީ، އެއް ރޯދަ އަނެއް ރޯދައާ ދެމެދު ކެއުމެއް ބުއިމެއް ނެތި ގުޅުވުމެވެ.) މީސްތަކުން ދެންނެވިއެވެ: ހަމަކަށަވަރުން އިބަ ނަބިއްޔާ ވިޞާލު ކުރައްވަމުއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާއީ ތިޔަބައިމީހުން ފަދަ ބޭކަލެއް ނޫނެވެ. ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާއަށް (ﷲގެ ހަޒްރަތުން) ކާންދެއްވި ބޯންދެއްވިގެން ވެއެވެ.“\n\nއަދި އަބޫ ހުރައިރާއާއި، ޢާއިޝާއާއި، އަނަސް ބިން މާލިކު ވެސް މި ޙަދީޘް ރިވާ ކުރެއްވިއެވެ.","[البخاري 1972، ومسلم 1102\n\nالبخاري 1965، 1964، 1961، ومسلم 1103، 1105، 1104]"],["#202","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الصوم في السفر وغيره","ދަތުރުމަތީގައި ރޯދަ ހިފުމާއި އެހެނިހެން ކަންތައްތަކުގެ ބާބު","وَلِمُسْلِمٍ عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ - رَضِيَ اللَّهُ عَنْهُ -: «فَأَيُّكُمْ أَرَادَ أَنْ يُوَاصِلَ فَلْيُوَاصِلْ إلَى السَّحَرِ»","އަދި މުސްލިމުގެ ރިވާޔަތެއްގައި، އަބޫ ސަޢީދު އަލްޚުދްރީ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ”ތިޔަބައިމީހުންގެ ތެރެއިން މީހަކު ވިޞާލު ކުރަން ބޭނުން ނަމަ، ފަހެ ހާރު ކެއުމާ ހަމައަށް ވިޞާލު ކުރާށެވެ.“","[البخاري 1963]"],["#203","كتاب الصيام","ރޯދައިގެ ފޮތް","باب أفضل الصيام وغيره","އެންމެ ހެޔޮވެގެންވާ ރޯދައާއި އެހެނިހެން ކަންކަތައްތަކުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَمْرِو بْنِ الْعَاصِ رَضِيَ اللَّهُ عَنْهُما قَالَ: «أُخْبِرَ رَسُولُ اللَّهِ ﷺ أَنِّي أَقُولُ: وَاَللَّهِ لأَصُومَنَّ النَّهَارَ، وَلأَقُومَنَّ اللَّيْلَ مَا عِشْتُ. فَقَالَ رَسُولُ اللَّهِ ﷺ: أَنْتَ الَّذِي قُلْتَ ذَلِكَ؟ فَقُلْتُ لَهُ: قَدْ قُلْتُهُ، بِأَبِي أَنْتَ وَأُمِّي. فَقَالَ: فَإِنَّكَ لا تَسْتَطِيعُ ذَلِكَ. فَصُمْ وَأَفْطِرْ، وَقُمْ وَنَمْ. وَصُمْ مِنْ الشَّهْرِ ثَلاثَةَ أَيَّامٍ فَإِنَّ الْحَسَنَةَ بِعَشْرِ أَمْثَالِهَا. وَذَلِكَ مِثْلُ صِيَامِ الدَّهْرِ. قُلْتُ: فَإِنِّي أُطِيقُ أَفْضَلَ مِنْ ذَلِكَ. قَالَ: فَصُمْ يَوْماً وَأَفْطِرْ يَوْمَيْنِ. قُلْتُ: أُطِيقُ أَفْضَلَ مِنْ ذَلِكَ. قَالَ: فَصُمْ يَوْماً وَأَفْطِرْ يَوْماً. فَذَلِكَ مِثْلُ صِيَامِ دَاوُد. وَهُوَ أَفْضَلُ الصِّيَامِ. فَقُلْتُ: إنِّي أُطِيقُ أَفْضَلَ مِنْ ذَلِكَ. قَالَ: لا أَفْضَلَ مِنْ ذَلِكَ»\n\nوَفِي رِوَايَةٍ: «لا صَوْمَ فَوْقَ صَوْمِ أَخِي دَاوُد - شَطْرَ الدَّهْرِ - صُمْ يَوْماً وَأَفْطِرْ يَوْماً»","ޢަބްދު ﷲ ބިން ޢަމްރު ބިން އަލްޢާޞް رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން މިފަދައިން ތިމަންނަ ދެންނެވިކަމަށް ރަސޫލު ﷲ ﷺ އަށް ޚަބަރު ލިބިވަޑައިގަތެވެ: ﷲ ގަންދީ ބުނަމެވެ. ހަމަކަށަވަރުން ތިމަންނަ ދިރިހުރިހާ ހިނދަކު، ދުވާލު ރޯދަ ހިފައި، ރޭގަނޑު ރޭއަޅުކަން ކުރާނަމެވެ. ފަހެ ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އެފަދައިން ދެންނެވީ ތިބާތޯއެވެ؟“ ނަބިއްޔާއަށް ތިމަން ދެންނެވީމެވެ: ތިމަންނަގެ ބައްޕައާއި މަންމަ އިބަ ނަބިއްޔާއަށް ފިދާއެވެ. ހަމަކަށަވަރުން އެފަދައިން ތިމަން ބުނީމެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ތިބާއަކަށް ތިކަމެއް ކުޅަދާނައެއް ނުވާނެއެވެ. ފަހެ ތިބާ ރޯދަ ހިފާށެވެ. އަދި ރޯދަ ނުހިފާ ދޫވެސް ކުރާށެވެ. އަދި ރޭއަޅުކަން ކުރާށެވެ. އަދި ނިދާށެވެ. އަދި މަހެއްގައި ތިން ދުވަހު ރޯދަ ހިފާށެވެ. އެހެނީ ހަމަކަށަވަރުން ކޮންމެ ހެޔޮކަމަކަށް އެއާ އެއްފަދަ ދިހަ ހެޔޮކަމެއް ލިބިގެންވެއެވެ. އެ ގޮތަށް މުޅި އަހަރުދުވަސް ރޯދަ ހިފުމާ އެއްފަދަ ވާނެއެވެ.“ ތިމަން ދެންނެވީމެވެ: ހަމަކަށަވަރުން ތިއަށްވުރެ ރަނގަޅަށް (ރޯދަ ހިފުމަށް) ތިމަންނަ ކުޅަދާނަވެގެންވެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ފަހެ އެއް ދުވަހު ރޯދަ ހިފާފައި، އަނެއް ދެ ދުވަހު ރޯދަ ނުހިފާ ދޫކުރާށެވެ.“ ތިމަން ދެންނެވީމެވެ: ތިއަށްވުރެ ރަނގަޅަށް (ރޯދަ ހިފުމަށް) ތިމަންނަ ކުޅަދާނަވެގެންވެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ފަހެ އެއް ދުވަހު ރޯދަ ހިފާފައި، އަނެއް ދުވަހު ރޯދަ ނުހިފާ ދޫކުރާށެވެ. އެއީ ދާވޫދުގެފާނުގެ ރޯދައާ އެއްފަދައެވެ. އަދި އެންމެ ހެޔޮވެގެންވާ (ސުންނަތް) ރޯދައަކީ އެއީއެވެ.“ ތިމަން ދެންނެވީމެވެ: ހަމަކަށަވަރުން ތިއަށްވުރެ ރަނގަޅަށް (ރޯދަ ހިފުމަށް) ތިމަންނަ ކުޅަދާނަވެގެން ވެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެއަށްވުރެ ރަނގަޅު (މޮޅުއިތުރު) ގޮތެއް ނުވެއެވެ.“\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”ތިމަން ނަބިއްޔާގެ އަޚާ، ދާވޫދުގެފާނުގެ ރޯދައަށް ވުރެ މަތިވެރި (ސުންނަތް) ރޯދައެއް ނުވެއެވެ. އެއީ އަހަރުގެ ދެބައިކުޅަ އެއްބައި (ރޯދަ ހިފުމެވެ.) ތިބާ އެއް ދުވަހު ރޯދަ ހިފާފައި، އަނެއް ދުވަހު ރޯދަ ނުހިފާ ދޫކުރާށެވެ.“","[البخاري 1976 واللفظ له، ومسلم 1159\n\nالبخاري 1980 واللفظ له، ومسلم 1159]"],["#204","كتاب الصيام","ރޯދައިގެ ފޮތް","باب أفضل الصيام وغيره","އެންމެ ހެޔޮވެގެންވާ ރޯދައާއި އެހެނިހެން ކަންކަތައްތަކުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَمْرِو بْنِ الْعَاصِ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «إنَّ أَحَبَّ الصِّيَامِ إلَى اللَّهِ صِيَامُ دَاوُد. وَأَحَبَّ الصَّلاةِ إلَى اللَّهِ صَلاةُ دَاوُد. كَانَ يَنَامُ نِصْفَ اللَّيْلِ، وَيَقُومُ ثُلُثَهُ. وَيَنَامُ سُدُسَهُ. وَكَانَ يَصُومُ يَوْماً وَيُفْطِرُ يَوْماً»","ޢަބްދު ﷲ ބިން ޢަމްރު ބިން އަލްޢާޞް رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ﷲގެ ހަޒްރަތުގައި އެންމެ ލޮބުވެތިކަން ބޮޑު (ސުންނަތް) ރޯދައަކީ ދާވޫދޫގެފާނުގެ ރޯދައެވެ. އަދި ﷲގެ ހަޒްރަތުގައި އެންމެ ލޮބުވެތިކަން ބޮޑު (ސުންނަތް) ނަމާދަކީ ދާވޫދޫގެފާނުގެ ނަމާދެވެ. ރޭގަނޑުގެ ދެބައި ކުޅަ އެއްބައިގައި ދާވޫދުގެފާނު އަވަހާރަފުޅު ލައްޕަވައެވެ. އަދި އެ ރޭގެ ތިންބައި ކުޅަ އެއްބައިގައި ރޭއަޅުކަން ކުރައްވައެވެ. ދެން އެ ރޭގެ ހަބައިކުޅަ އެއްބައިގައި އަނެއްކާވެސް އަވަހާރަފުޅު ލައްޕަވައެވެ. އަދި އެއް ދުވަހު ރޯދަ ހިއްޕަވާ އަނެއް ދުވަހު ރޯދަ ނުހިއްޕަވާ ދޫކުރައްވައެވެ.“","[البخاري 1131، ومسلم 1159 واللفظ له]"],["#205","كتاب الصيام","ރޯދައިގެ ފޮތް","باب أفضل الصيام وغيره","އެންމެ ހެޔޮވެގެންވާ ރޯދައާއި އެހެނިހެން ކަންކަތައްތަކުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «أَوْصَانِي خَلِيلِي ﷺ بِثَلاثٍ صِيَامِ ثَلاثَةِ أَيَّامٍ مِنْ كُلِّ شَهْرٍ، وَرَكْعَتَيْ الضُّحَى، وَأَنْ أُوتِرَ قَبْلَ أَنْ أَنَامَ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ތިމަންނަގެ ގާތް މިތުރު (ނަބިއްޔާ) ﷺ ތިމަންނައަށް ތިން ކަމަކަށް ވަސިއްޔަތް ކުރެއްވީއެވެ. އެއީ ކޮންމެ މަހެއްގެ ތިން ދުވަހު ރޯދަ ހިފުމަށާއި، ޟުޙާ ނަމާދުގެ ދެ ރަކުއަތް ކުރުމަށާއި، ތިމަންނަ ނިދުމުގެ ކުރިން ވިތުރި ކުރުމަށެވެ.","[البخاري 1981 واللفظ له، ومسلم 721]"],["#206","كتاب الصيام","ރޯދައިގެ ފޮތް","باب أفضل الصيام وغيره","އެންމެ ހެޔޮވެގެންވާ ރޯދައާއި އެހެނިހެން ކަންކަތައްތަކުގެ ބާބު","عَنْ مُحَمَّدِ بْنِ عَبَّادِ بْنِ جَعْفَرٍ قَالَ: «سَأَلْتُ جَابِرَ بْنَ عَبْدِ اللَّهِ أَنَهَى النَّبِيُّ ﷺ عَنْ صَوْمِ يَوْمِ الْجُمُعَةِ؟ قَالَ: نَعَمْ»\n\nوَزَادَ مُسْلِمٌ «وَرَبِّ الْكَعْبَةِ»","މުޙައްމަދު ބިން ޢައްބާދު ބިން ޖަޢުފަރު ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ޖާބިރު ބިން ޢަބްދު ﷲގެ އަރިހުގައި ތިމަން ސުވާލު ކުރީމެވެ: ހުކުރު ދުވަހު ރޯދަ ހިފުން ނަބިއްޔާ ﷺ ނަހީ ކުރެއްވިތޯއެވެ؟ ޢަބްދު ﷲ ދެންނެވިއެވެ: އާދޭހެވެ.\n\nއަދި މުސްލިމުގެ ރިވާޔަތުގައި (އެކަލޭގެފާނު އިތުރަށް ދެންނެވިކަމަށް) ރިވާވެގެން ވެއެވެ: ކައުބާގެ ވެރި ރައްބު ގަންދީ ބުނަމެވެ.","[البخاري 1984 واللفظ له، ومسلم 1143\n\nومسلم 1143]"],["#207","كتاب الصيام","ރޯދައިގެ ފޮތް","باب أفضل الصيام وغيره","އެންމެ ހެޔޮވެގެންވާ ރޯދައާއި އެހެނިހެން ކަންކަތައްތަކުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: سَمِعْتُ رَسُولَ اللَّهِ ﷺ يَقُولُ: «لا يَصُومَنَّ أَحَدُكُمْ يَوْمَ الْجُمُعَةِ، إلاَّ أَنْ يَصُومَ يَوْماً قَبْلَهُ، أَوْ يَوْماً بَعْدَهُ»","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ”ތިޔަބައިމީހުން ކުރެ އެކަކުވެސް ހުކުރު ދުވަހު ރޯދަ ހިފައިގެން ނުވާނެއެވެ. އެއީ އޭގެ ކުރިއަށް ނުވަތަ ފަހަތަށް ދުވަހެއް ރޯދަ ހިފާނަމަ މެނުވީއެވެ.“","[البخاري 1985، ومسلم 1144]"],["#208","كتاب الصيام","ރޯދައިގެ ފޮތް","باب أفضل الصيام وغيره","އެންމެ ހެޔޮވެގެންވާ ރޯދައާއި އެހެނިހެން ކަންކަތައްތަކުގެ ބާބު","عَنْ أَبِي عُبَيْدٍ مَوْلَى ابْنِ أَزْهَرَ وَاسْمُهُ سَعْدُ بْنُ عُبَيْدٍ - قَالَ: «شَهِدْت الْعِيدَ مَعَ عُمَرَ بْنِ الْخَطَّابِ - رَضِيَ اللَّهُ عَنْهُ - فَقَالَ: هَذَانِ يَوْمَانِ نَهَى رَسُولُ اللَّهِ ﷺ عَنْ صِيَامِهِمَا: يَوْمُ فِطْرِكُمْ مِنْ صِيَامِكُمْ، وَالْيَوْمُ الآخَرُ: تَأْكُلُونَ فِيهِ مِنْ نُسُكِكُمْ»","އިބްނު އަޒްހަރު މިނިވަން ކުރެއްވި އަޅެއް ކަމުގައިވާ އަބޫ ޢުބައިދު ރިވާ ކުރެއްވިއެވެ. އަދި އެކަލޭގެފާނުގެ ނަމަކީ ސަޢުދު ބިން ޢުބައިދެވެ. އެކަލޭގެފާނު ވިދާޅުވިއެވެ: ޢުމަރު ބިން އަލްޚާއްޠާބު رَضِيَ اللَّهُ عَنْهُގެ އަރިހު އީދު (ނަމާދުގައި) ތިމަން ބައިވެރިވީމެވެ. ޢުމަރު ވިދާޅުވިއެވެ: މި ދެ (އީދު) ދުވަހުގައި ރޯދަ ހިފުން ރަސޫލު ﷲ ﷺ ނަހީ ކުރެއްވިއެވެ. (އެއް ދުވަހަކީ) ތިޔަބައިމީހުން (ރަމަޟާން މަހުގެ) ރޯދަހިފުން ނިންމާލައި ދުވަހެވެ. އަދި (އަނެއް ދުވަހަކީ) ތިޔަބައިމީހުންގެ އުޟްހިޔާތަކުން ތިޔަބައިމީހުން ކާ ދުވަހެވެ.","[البخاري 1990 واللفظ له، ومسلم 1137]"],["#209","كتاب الصيام","ރޯދައިގެ ފޮތް","باب أفضل الصيام وغيره","އެންމެ ހެޔޮވެގެންވާ ރޯދައާއި އެހެނިހެން ކަންކަތައްތަކުގެ ބާބު","عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: «نَهَى رَسُولُ اللَّهِ ﷺ عَنْ صَوْمِ يَوْمَيْنِ: الْفِطْرِ وَالنَّحْرِ. وَعَنْ الصَّمَّاءِ، وَأَنْ يَحْتَبِيَ الرَّجُلُ فِي الثَّوْبِ الْوَاحِدِ، وَعَنْ الصَّلاةِ بَعْدَ الصُّبْحِ وَالْعَصْرِ» أَخْرَجَهُ مُسْلِمٌ بِتَمَامِهِ.\n\nوَأَخْرَجَ الْبُخَارِيُّ الصَّوْمَ فَقَطْ.","އަބޫ ސަޢީދު އަލްޚުދްރީ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ދެ ދުވަހެއްގައި ރޯދަ ހިފުން ރަސޫލު ﷲ ﷺ ނަހީ ކުރެއްވިއެވެ. އެއީ ފިތުރު އީދު ދުވަހާއި، އަޟްހާ އީދު ދުވަހެވެ. އަދި ޞައްމާއު ވެސް ނަހީ ކުރެއްވިއެވެ. (އެބަހީ: ދެ އަތް ނުނެރެވޭ ގޮތަށް ބަންދުކޮށް، އެއް ފޭރާމަކުން މުޅި ހަށިގަނޑުގައި އޮޅުން މަނާ ކުރެއްވިއެވެ.) އަދި އެއް ފޭރާން ލައިގެން ހުއްޓައި ދެ ފައިގެ ކަށިމަތި ކޮޅަށް ޖަހައިގެން އުޑަފައިންއިނުން ނަހީ ކުރެއްވިއެވެ. އަދި ފަތިސް ނަމާދާއި އަސުރު ނަމާދު ފަހުން ނަމާދު ކުރުން ނަހީ ކުރެއްވިއެވެ. (އެބަހީ: ފަތިސް ނަމާދަށް ފަހު އިރު އަރަންދެން، އަދި އަސުރު ނަމާދަށް ފަހު އިރު އޮއްސެންދެން، ސުންނަތް ނަމާދު ކުރުން ނަހީ ކުރެއްވިއެވެ.) (މުއައްލިފު ވިދާޅުވިއެވެ:) މުސްލިމު މި ޙަދީޘް މި އިން ގޮތަށް ނެރުއްވިއެވެ.\n\nއަދި ބުޚާރީ ނެރުއްވި ޙަދީޘްގައި ވަނީ ހަމައެކަނި ރޯދަ (ހިފުމުގެ ވާހަކައެވެ.)","[مسلم 827، أخرجه مقتصرا على الصوم فقط\n\nالبخاري 1991، 1992، أجرجه بتمامه، وليس العكس]"],["#210","كتاب الصيام","ރޯދައިގެ ފޮތް","باب أفضل الصيام وغيره","އެންމެ ހެޔޮވެގެންވާ ރޯދައާއި އެހެނިހެން ކަންކަތައްތަކުގެ ބާބު","عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: «مَنْ صَامَ يَوْماً فِي سَبِيلِ اللَّهِ بَعَّدَ اللَّهُ وَجْهَهُ عَنْ النَّارِ سَبْعِينَ خَرِيفاً»","އަބޫ ސަޢީދު އަލްޚުދްރީ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲގެ މަގުގައި އެންމެ ދުވަހަކު ރޯދަ ހިފައިފި މީހާ، ފަހެ ﷲ އޭނާގެ މޫނު ހަތްދިހަ އަހަރުގެ ރާސްތާއަށް ނަރަކައިން ދުރު ކުރައްވާނެއެވެ.“","[البخاري 2840 واللفظ له، ومسلم 1153]"],["#211","كتاب الصيام","ރޯދައިގެ ފޮތް","باب ليلة القدر","ލައިލަތުލް ޤަދްރީގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُما: «أَنَّ رِجَالاً مِنْ أَصْحَابِ النَّبِيِّ ﷺ أُرُوا لَيْلَةَ الْقَدْرِ فِي الْمَنَامِ فِي السَّبْعِ الأَوَاخِرِ. فَقَالَ النَّبِيُّ ﷺ: أَرَى رُؤْيَاكُمْ قَدْ تَوَاطَأَتْ فِي السَّبْعِ الأَوَاخِرِ. فَمَنْ كَانَ مُتَحَرِّيهَا فَلْيَتَحَرَّهَا فِي السَّبْعِ الأَوَاخِرِ»","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺގެ ސަހާބީންގެ ތެރެއިން ފިރިހެން ބޭކަލުންތަކަކަށް، ހުވަފެނުގައި ލައިލަތުލް ގަދްރި ވިލޭރޭ ވަނީ ފަހު ހަތް ރޭގައި ކަމަށް ދެއްކެވުނެވެ. ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ތިޔަބައިމީހުންގެ ހުވަފެންތައް، ފަހު ހަތް ރޭގައި އިއްތިފާގުވެފައިވާތަން ތިމަން ނަބިއްޔާ ދެކެވަޑައިގަންނަމެވެ. ފަހެ އެ ރޭ ހޯދަން މަސައްކަތް ކުރާ މީހާ، ފަހު ހަތް ރޭތަކުގައި ހޯދުމަށް ގަދައަޅައި މަސައްކަތް ކުރާށެވެ.“","[البخاري 2015، ومسلم 1165]"],["#212","كتاب الصيام","ރޯދައިގެ ފޮތް","باب ليلة القدر","ލައިލަތުލް ޤަދްރީގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: «تَحَرَّوْا لَيْلَةَ الْقَدْرِ فِي الْوِتْرِ مِنْ الْعَشْرِ الأَوَاخِرِ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ފަހު ދިހައިގެ އޮނަހިރި ރޭތަކުގައި ލައިލަތުލް ގަދްރި ވިލޭރޭ ހޯދުމަށް ތިޔަބައިމީހުން ގަދައަޅައި މަސައްކަތް ކުރާށެވެ.“","[البخاري 2017]"],["#213","كتاب الصيام","ރޯދައިގެ ފޮތް","باب ليلة القدر","ލައިލަތުލް ޤަދްރީގެ ބާބު","عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ - رَضِيَ اللَّهُ عَنْهُ -: «أَنَّ رَسُولَ اللَّهِ ﷺ كَانَ يَعْتَكِفُ فِي الْعَشْرِ الأَوْسَطِ مِنْ رَمَضَانَ. فَاعْتَكَفَ عَاماً، حَتَّى إذَا كَانَتْ لَيْلَةُ إحْدَى وَعِشْرِينَ - وَهِيَ اللَّيْلَةُ الَّتِي يَخْرُجُ مِنْ صَبِيحَتِهَا مِنْ اعْتِكَافِهِ - قَالَ: مَنْ اعْتَكَفَ مَعِي فَلْيَعْتَكِفْ الْعَشْرَ الأَوَاخِرَ فَقَدْ أُرِيتُ هَذِهِ اللَّيْلَةَ. ثُمَّ أُنْسِيتُهَا، وَقَدْ رَأَيْتُنِي أَسْجُدُ فِي مَاءٍ وَطِينٍ مِنْ صَبِيحَتِهَا. فَالْتَمِسُوهَا فِي الْعَشْرِ الأَوَاخِرِ. وَالْتَمِسُوهَا فِي كُلِّ وِتْرٍ. فَمَطَرَتِ السَّمَاءُ تِلْكَ اللَّيْلَةِ. وَكَانَ الْمَسْجِدُ عَلَى عَرِيشٍ. فَوَكَفَ الْمَسْجِدُ، فَأَبْصَرَتْ عَيْنَايَ رَسُولَ اللَّهِ ﷺ وَعَلَى جَبْهَتِهِ أَثَرُ الْمَاءِ وَالطِّينِ مِنْ صُبْحِ إحْدَى وَعِشْرِينَ»","އަބޫ ސަޢީދު އަލްޚުދްރީ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން (ކުރީގައި) ރަސޫލު ﷲ ﷺ އިއުތިކާފުއަށް އިންނަވަނީ ރަމަޟާން މަހުގެ މެދު ދިހައިގައެވެ. ފަހެ އެއް އަހަރަކު އެކާވީސް ވަނަ ރޭވަންދެން ނަބިއްޔާ އިއުތިކާފަށް އިންނެވިއެވެ. އަދި އެ ރެއަކީ (ކުރީގައި އާދައިގެ މަތިން) އޭގެ ފަތިހު ނަބިއްޔާ އިއުތިކާފު (ނިންމަވައި) ނުކުމެވަޑައިގަންނަވާ ރެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާއާއެކު (މެދު ދިހައިގައި) އިއުތިކާފަށް އިން ބަޔަކު، ފަހު ދިހައިގައި ވެސް އިއުތިކާފަށް އިންނާށެވެ. އެހެނީ ހަމަކަށަވަރުން މިރޭ (އެބަހީ: ލައިލަތުލް ގަދްރި ވިލޭރޭ) ތިމަން ނަބިއްޔާއަށް ދެއްކެވުނެވެ. އެއަށްފަހު ތިމަން ނަބިއްޔާއަށް އެ ރޭ ހަނދުމަފުޅު ނެތިކުރެއްވުނެވެ. އަދި (ހުވަފެންފުޅެއްގައި) އެ ރޭގެ ފަތިހު ތިމަން ނަބިއްޔާ ފެނާއި މަށީގައި ސަޖިދަ ކުރައްވާ ތަން ދެކެވަޑައިގަތީމެވެ. ވީމާ ފަހު ދިހައިގައި އެ ރޭ ހޯދުމަށް ތިޔަބައިމީހުން މަސައްކަތް ކުރާށެވެ. އަދި (އޭގެން ވެސް) ކޮންމެ އޮނަހިރި ރެއެއްގައި އެ ރޭ ހޯދަން މަސައްކަތް ކުރާށެވެ.“ ދެން ފަހެ އެ ރޭގައި އުޑުން ވާރޭ ވެހުނެވެ. އަދި (އެ ދުވަސްވަރު) މިސްކިތް ވަނީ ކަދުރު ރުކުގެ ފަނުން ހިޔާ ކުރެވިފައެވެ. ވީމާ މިސްކިތުގެ ފުރާޅުން ފެން ފޭބިއެވެ. ފަހެ ތިމަންނަގެ ދެ ލޮލުން ރަސޫލު ﷲ ﷺ ދުށީމެވެ. އަދި ނަބިއްޔާގެ ނިއްކުރިފުޅުގައި ވަނީ ފެނާއި މަށީގެ އަސަރެވެ. އެއީ އެކާވީސްވަނަ ރޭގެ ފަތިހެވެ.","[البخاري 2027 واللفظ له، ومسلم 1167]"],["#214","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الاعتكاف","އިއުތިކާފުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا: «أَنَّ رَسُولَ اللَّهِ ﷺ كَانَ يَعْتَكِفُ فِي الْعَشْرِ الأَوَاخِرِ مِنْ رَمَضَانَ، حَتَّى تَوَفَّاهُ اللَّهُ عَزَّ وَجَلَّ. ثُمَّ اعْتَكَفَ أَزْوَاجُهُ بَعْدَهُ»\n\nوَفِي لَفْظٍ «كَانَ رَسُولُ اللَّهِ ﷺ يَعْتَكِفُ فِي كُلِّ رَمَضَانَ. فَإِذَا صَلَّى الْغَدَاةَ جَاءَ مَكَانَهُ الَّذِي اعْتَكَفَ فِيهِ»","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ﷲ ޢައްޒަ ވަޖައްލަ، ރަސޫލު ﷲ ﷺގެ ފުރާނަފުޅު ހިއްޕަވައިފުމަށް ދާންދެން، (ކޮންމެ) ރަމަޟާން މަހެއްގެ ފަހު ދިހައިގައި ނަބިއްޔާ އިއުތިކާފަށް އިންނަވައެވެ. ދެން ނަބިއްޔާ އަވަހާރަވުމަށް ފަހުގައި ވެސް އެކަލޭގެފާނުގެ އަނބިކަނބަލުން އިއުތިކާފަށް އިންނަވައެވެ.\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ކޮންމެ ރަމަޟާން މަހަކު ރަސޫލު ﷲ ﷺ އިއުތިކާފަށް އިންނަވައެވެ. އަދި ނަބިއްޔާ ފަތިސް ނަމާދު ކުރެއްވުމަށް ފަހު އިއުތިކާފަށް އިންނެވި ތަނަށް އެނބުރި ވަޑައިގަންނަވައެވެ.","[البخاري 2026، ومسلم 1172\n\nالبخاري 2041]"],["#215","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الاعتكاف","އިއުތިކާފުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا: «أَنَّهَا كَانَتْ تُرَجِّلُ النَّبِيَّ ﷺ وَهِيَ حَائِضٌ، وَهُوَ مُعْتَكِفٌ فِي الْمَسْجِدِ. وَهِيَ فِي حُجْرَتِهَا: يُنَاوِلُهَا رَأْسَهُ»\n\nوَفِي رِوَايَةٍ: «وَكَانَ لا يَدْخُلُ الْبَيْتَ إلاَّ لِحَاجَةِ الإِنْسَانِ»\n\nوَفِي رِوَايَةٍ أَنَّ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا قَالَتْ «إنْ كُنْتُ لأَدْخُلُ الْبَيْتَ لِلْحَاجَةِ وَالْمَرِيضُ فِيهِ. فَمَا أَسْأَلُ عَنْهُ إلاَّ وَأَنَا مَارَّةٌ».","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺ މިސްކިތުގައި އިއުތިކާފަށް އިންނަވަނިކޮށް، އެ ކަމަނާ ހައިޟުވެވަޑައިގެންވާ ހާލުގައި އެކަލޭގެފާނުގެ އިސްތަށިފުޅުގައި ފުނާ އަޅުއްވައެވެ. (އެ ގޮތުން އާއިޝާ) އެ ކަމަނާގެ ކޮޓަރިކޮޅުގައި އިންނަވަނިކޮށް، ނަބިއްޔާގެ އިސްތަށިފުޅު އެ ކަމަނާއަށް ދިއްކޮށްލައްވައެވެ. (ނޯޓު: އޭރު މިސްކިތާއި ކޮޓަރިކޮޅު ވަނީ ޖެހިޖެހިގެން، ދޮރުކޮޅަކުން ގުޅިފައެވެ.)\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: އަދި އިންސާނީ ހާޖަތެއް ފުއްދުމަށް ޓަކައި މެނުވީ ނަބިއްޔާ ގެކޮޅަށް ވަދެވަޑައި ނުގަންނަވައެވެ.\n\nއަދި އެހެން ރިވާޔަތެއްގައި ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިއެވެ: ތިމަން ކަމަނާ (އިއުތިކާފަށް އިންނަވަނިކޮށް) ހާޖަތެއް ޖެހިގެން ގެކޮޅަށް ވަންނަ ހިނދު، އެ ތަނުގައި ބަލިމީހަކު ވާނަމަ، ތިމަން ކަމަނާ އެ ދާ މަގުމަތީ ހިނގަހިނގައި ހުއްޓައި މެނުވީ އޭނާގެ ހާލާ ބެހޭ ގޮތުން ނާހަމެވެ. (އެބަހީ: މިސްކިތަށް ދިޔުމުގައި ނުވަތަ ގެއަށް އެނބުރި އައުމުގައި އެއްވެސް ތަނަކު މަޑެއް ނުކުރައްވައެވެ.)","[البخاري 2046 واللفظ له، ومسلم 297\n\nمسلم 297\n\nمسلم 297]"],["#216","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الاعتكاف","އިއުތިކާފުގެ ބާބު","عَنْ عُمَرَ بْنِ الْخَطَّابِ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قُلْتُ: «يَا رَسُولَ اللَّهِ، إنِّي كُنْتُ نَذَرْتُ فِي الْجَاهِلِيَّةِ أَنْ أَعْتَكِفَ لَيْلَةً» - وَفِي رِوَايَةٍ: «يَوْماً - فِي الْمَسْجِدِ الْحَرَامِ. قَالَ فَأَوْفِ بِنَذْرِكَ»\nوَلَمْ يَذْكُرْ بَعْضُ الرُّوَاةِ يَوْماً ولا لَيْلَةً.","ޢުމަރު ބިން އަލްޚައްޠާބު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ތިމަން ދެންނެވީމެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން މަސްޖިދުލް ހަރާމުގައި އެއް ރޭ -އަދި އެހެން ރިވާޔަތެއްގައި އެއް ދުވަސް- ތިމަންނަ އިއުތިކާފަށް އިނުމަށް، ޖާހިލިއްޔަ ޒަމާނުގައި ނަދުރެއް ބުނެފައިވާނެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ފަހެ ތިބާގެ ނަދުރު ފުއްދާށެވެ.“\n\nއަދި ބައެއް ރާވީން، އެއް ރޭ ނުވަތަ އެއް ދުވަސް (އެބައިމީހުންގެ ރިވާޔަތްތަކުގައި) ޒިކުރެއް ނުކުރައްވައެވެ.","[البخاري 2032، ومسلم 1656]"],["#217","كتاب الصيام","ރޯދައިގެ ފޮތް","باب الاعتكاف","އިއުތިކާފުގެ ބާބު","عَنْ صَفِيَّةَ بِنْتِ حُيَيٍّ رَضِيَ اللَّهُ عَنْهَا قَالَتْ: «كَانَ النَّبِيُّ ﷺ مُعْتَكِفًا. فَأَتَيْتُهُ أَزُورُهُ لَيْلاً. فَحَدَّثْتُهُ، ثُمَّ قُمْتُ لأَنْقَلِبَ، فَقَامَ مَعِي لِيَقْلِبَنِي - وَكَانَ مَسْكَنُهَا فِي دَارِ أُسَامَةَ بْنِ زَيْدٍ - فَمَرَّ رَجُلانِ مِنْ الأَنْصَارِ فَلَمَّا رَأَيَا رَسُولَ اللَّهِ ﷺ أَسْرَعَا. فَقَالَ النَّبِيُّ ﷺ: عَلَى رِسْلِكُمَا. إنَّهَا صَفِيَّةُ بِنْتُ حُيَيٍّ. فَقَالا: سُبْحَانَ اللَّهِ يَا رَسُولَ اللَّهِ. فَقَالَ: إنَّ الشَّيْطَانَ يَجْرِي مِنْ ابْنِ آدَمَ مَجْرَى الدَّمِ. وَإِنِّي خَشِيتُ أَنْ يَقْذِفَ فِي قُلُوبِكُمَا شَرّاً- أَوْ قَالَ شَيْئاً»\n\nوَفِي رِوَايَةٍ «أَنَّهَا جَاءَتْ تَزُورُهُ فِي اعْتِكَافِهِ فِي الْمَسْجِدِ فِي الْعَشْرِ الأَوَاخِرِ مِنْ رَمَضَانَ. فَتَحَدَّثَتْ عِنْدَهُ سَاعَةً. ثُمَّ قَامَتْ تَنْقَلِبُ. فَقَامَ النَّبِيُّ ﷺ مَعَهَا يَقْلِبُهَا، حَتَّى إذَا بَلَغَتْ بَابَ الْمَسْجِدِ عِنْدَ بَابِ أُمِّ سَلَمَةَ» ثُمَّ ذَكَرَهُ بِمَعْنَاهُ.","ޞަފިއްޔާ ބިންތި ޙުޔައްޔު رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ އިއުތިކާފަށް އިންނަވަނިކޮށް، އެކަލޭގެފާނަށް ޒިޔާރަތް ކުރެއްވުމަށް އެއް ރެއަކު ތިމަން ކަމަނާ ދިޔައީމެވެ. ދެން ނަބިއްޔާއާ ވާހަކަ ދެއްކުމަށްފަހު، އެނބުރި ދިޔުމަށް ތިމަން ކަމަނާ ތެދުވީމެވެ. ފަހެ ތިމަން ކަމަނާ ގެއަށް ލައިދެއްވުމަށް ނަބިއްޔާ ވެސް ތެދުވެވަޑައިގަތެވެ. -އަދި އެ ކަމަނާގެ ގެކޮޅު ވަނީ މިހާރު އުސާމާ ބިން ޒައިދުގެ ގެކޮޅު ހުންނަ ތަނުގައެވެ- ފަހެ އަންސާރުންގެ ދެ ފިރިހެނުން ބޭކަލުން ހިނގާފައި ދަނިކޮށް، ރަސޫލު ﷲ ﷺ ފެނުމުން އެ ދެ ބޭކަލުންގެ ހިނގުން އަވަސްކޮށްލެއްވިއެވެ. ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަ ދެ ބޭކަލުން ހަމަޖެހިލައިގެން ހިނގާށެވެ. އެހެނީ ހަމަކަށަވަރުން މިއީ ޞަފިއްޔާ ބިންތި ޙުޔައްޔެވެ.“ އެ ދެ ބޭކަލުން ދެންނެވިއެވެ: سُبْحَانَ اللَّهِ އެވެ. އޭ ﷲގެ ރަސޫލާއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން އާދަމުގެ ދަރިޔާގެ ގައިގައި، ލޭ ދައުރުވާ ފަދައިން ޝައިތާނާ ދައުރުވެއެވެ. އަދި ހަމަކަށަވަރުން ޝައިތާނާ ތިޔަ ދެބޭކަލުންގެ ހިތްތަކަށް ނުބައި އެއްޗެއް އެއްލާލައިފާނެކަމަށް ތިމަން ނަބިއްޔާ ބިރުފުޅުގަތީއެވެ.“\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ރަމަޟާން މަހުގެ ފަހު ދިހައިގައި ނަބިއްޔާ މިސްކިތުގައި އިއުތިކާފަށް އިންނަވަނިކޮށް، އެކަލޭގެފާނަށް ޒިޔާރަތް ކުރެއްވުމަށް އެ ކަމަނާ ވަޑައިގަތެވެ. އަދި ގަޑިއެއްގެ އިރު އެ ކަމަނާ ނަބިއްޔާއާ ވާހަކަ ދެއްކެވުމަށްފަހު، އެނބުރި ދިޔުމަށް ތެދުވެވަޑައިގަތެވެ. ފަހެ އެ ކަމަނާ ގެއަށް ލައިދެއްވުމަށް ނަބިއްޔާ ﷺ ވެސް ތެދުވެވަޑައިގެން، މިސްކިތުގައިވާ އުންމު ސަލަމާގެ ދޮރުކޮޅާ ހިސާބަށް އެ ކަމަނާ ގެންދެވިއެވެ. -އެއަށްފަހު ކުރީގެ ރިވާޔަތާ އެއްފަދަ މާނައެއްގައި އެ ޙަދީޘް ޒިކުރު ކުރެވުނެވެ-","[البخاري 3281، ومسلم 2175\n\nالبخاري 2035 واللفظ له، ومسلم 2175]"],["#218","كتاب الحج","ހައްޖުގެ ފޮތް","باب المواقيت","މީގާތުތަކުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُمَا: (أَنَّ رَسُولَ اللهِ ﷺ وَقَّتَ لأَهْلِ الْمَدِينَةِ: ذَا الْحُلَيْفَةِ. وَلأَهْلِ الشَّامِ: الْجُحْفَةَ. وَلأَهْلِ نَجْدٍ: قَرْنَ الْمَنَازِلِ. وَلأَهْلِ الْيَمَنِ: يَلَمْلَمَ. هُنَّ لَهُمْ وَلِمَنْ أَتَى عَلَيْهِنَّ مِنْ غَيْرِ أَهْلِهِنَّ، مِمَّنْ أَرَادَ الْحَجَّ أَوْ الْعُمْرَةَ. وَمَنْ كَانَ دُونَ ذَلِكَ: فَمِنْ حَيْثُ أَنْشَأَ، حَتَّى أَهْلُ مَكَّةَ مِنْ مَكَّةَ).","ޢަބްދުﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ މީގާތު ގޮތުގައި ލެއްވީ، މަދީނާގެ އަހުލުވެރިންނަށް ޛުލް ޙުލައިފާއެވެ. އަދި ޝާމުގެ އަހުލުވެރިންނަށް ޖުޙްފާއެވެ. އަދި ނަޖުދުގެ އަހުލުވެރިންނަށް ގަރްނުލް މަނާޒިލެވެ. އަދި ޔަމަނުގެ އަހުލުވެރިންނަށް ޔަލަމްލަމެވެ. އަދި ނަބިއްޔާ ޙަދީޘްކުރެއްވިއެވެ. ”އެ މީގާތުތައް ވަނީ ހައްޖު ނުވަތަ އުމްރާ ކުރަން އެދޭ، އެ ތަންތަނުގެ އަހުލުވެރިންނަށާއި، އަދި (އޭގެ އިތުރުން) އެ ތަންތަނުގެ އަހުލުވެރިން ކަމުގައި ނުވާ އެކަމަކު އެ ތަންތަނުންލާފައި އަންނަ އެހެން މީހުންނަށެވެ. އަދި އެ (މީގާތުތަކަށް ވުރެ) އެތެރޭގައި އުޅޭ މީހުން (އިހްރާމް ބަންނާނީ) ހަމަ އެ ބަޔަކު އުޅޭ ހިސާބުން ފެށިގެންނެވެ. އެ ގޮތުން މައްކާގެ އަހުލުވެރިން (އިހްރާމް ބަންނާނީ) މައްކާއިންނެވެ.“","[البخاري 1524 واللفظ له، ومسلم 1181]"],["#219","كتاب الحج","ހައްޖުގެ ފޮތް","باب المواقيت","މީގާތުތަކުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: (يُهِلُّ أَهْلُ الْمَدِينَةِ مِنْ ذِي الْحُلَيْفَةِ، وَأَهْلُ الشَّامِ مِنْ الْجُحْفَةِ، وَأَهْلُ نَجْدٍ مِنْ قَرْنٍ). قَالَ: وَبَلَغَنِي أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: (وَيُهِلُّ أَهْلُ الْيَمَنِ مِنْ يَلَمْلَمَ).","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”މަދީނާގެ އަހުލުވެރިން އިހްރާމް ބަންނާނީ ޛުލް ޙުލައިފާ އިންނެވެ. އަދި ޝާމުގެ އަހުލުވެރިން ޖުޙްފާ އިންނެވެ. އަދި ނަޖުދުގެ އަހުލުވެރިން ގަރްނުލް (މަނާޒިލުންނެވެ.)“ ޢަބްދު ﷲ ވިދާޅުވިއެވެ. އަދި ރަސޫލު ﷺ ޙަދީޘް ކުރެއްވި ކަމަށް ތިމަންނައަށް ފޯރާފައިވެއެވެ: ”އަދި ޔަމަނުގެ އަހުލުވެރިން އިހުރާމް ބަންނާނީ ޔަލަމްލަމް އިންނެވެ.“","[البخاري 1525 واللفظ له، ومسلم 1182]"],["#220","كتاب الحج","ހައްޖުގެ ފޮތް","باب ما يلبس المحرم من الثياب","އިހްރާމް ބަންނަ މީހާ އަޅާ ހެދުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا: (أَنَّ رَجُلاً قَالَ يَا رَسُولَ اللَّهِ، مَا يَلْبَسُ الْمُحْرِمُ مِنْ الثِّيَابِ؟ قَالَ رَسُولُ اللَّهِ ﷺ: لايَلْبَسُ الْقَمِيصَ، وَلا الْعَمَائِمَ، وَلا السَّرَاوِيلاتِ، وَلا الْبَرَانِسَ، وَلا الْخِفَافَ، إلاَّ أَحَدٌ لا يَجِدُ نَعْلَيْنِ فَلْيَلْبَسْ خُفَّيْنِ وَلْيَقْطَعْهُمَا أَسْفَلَ مِنْ الْكَعْبَيْنِ، وَلا يَلْبَسْ مِنْ الثِّيَابِ شَيْئاً مَسَّهُ زَعْفَرَانٌ أَوْ وَرْسٌ). \n\nوَلِلْبُخَارِيِّ: (وَلا تَنْتَقِبِ الْمَرْأَةُ. وَلا تَلْبَسِ الْقُفَّازَيْنِ).","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން މީހަކު ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. އިހްރާމް ބަންނަ މީހާ އަޅާނީ ކޮން ހެދުމެއްތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭނާ ގަމީސް ނުލާށެވެ. އަދި ފަގުޑި ނާޅާށެވެ. އަދި ހަރުވާޅު ނުލާށެވެ. އަދި ބޮލާއި އެއްކޮށް ނިވާ ކުރެވޭ ގޮތަށް ހުންނަ ހެދުން ނުލާށެވެ. އަދި ޚުއްފަށް ނާރާށެވެ. އެއީ އަރާނެ ފައިވާނެއް ނުލިބުނު މީހާ މެނުވީއެވެ. ފަހެ އޭނާ ޚުއްފަށް އެރިދާނެއެވެ. އެކަމަކު ދެ ތަނބިކަށްޓަށް ވުރެ ތިރިން (އެބަހީ: ދެ ތަނބިކަށި ފެންނަ ގޮތަށް) އެ ޚުއްފު ބުރިކޮށްލާށެވެ. އަދި ސެފްރޮން ނުވަތަ ވަރްސު (ފަދަ ހުވަނދު) ޖެހިފައިވާ ހެދުން ނުލާށެވެ.“\n\nއަދި ބުޚާރީގެ ރިވާޔަތެއްގައި ވެއެވެ: ”އަދި އަންހެނާ (އިހްރާމުގައިވާ ހާލު) ނިގާބު (އެބަހީ: މޫނު ބުރުގާ) ނާޅާށެވެ. އަދި އެ ކަނބުލޭގެ އަންގި ނުލައްވާށެވެ.“","[البخاري 1542 واللفظ له، ومسلم 1177\n\nالبخاري 1838]"],["#221","كتاب الحج","ހައްޖުގެ ފޮތް","باب ما يلبس المحرم من الثياب","އިހްރާމް ބަންދެގެން ހުރި މީހާ އަޅާ ހެދުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (سَمِعْتُ رَسُولَ اللَّهِ ﷺ يَخْطُبُ بِعَرَفَاتٍ: مَنْ لَمْ يَجِدْ نَعْلَيْنِ فَلْيَلْبَسِ الْخُفَّيْنِ، وَمَنْ لَمْ يَجِدْ إزَاراً فَلْيَلْبَسْ السَّرَاوِيلَ لِلْمُحْرِمِ).","ޢަބްދު ﷲ ބިން ޢައްބާސް ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޢަރަފާތުގައި ޚުތުބާ ދެއްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ”އިހްރާމް ބަންނަ މީހަކަށް އަރާނެ ފައިވާނެއް ނުލިބުނު މީހާ ފަހެ ޚުއްފަށް އަރާށެވެ. އަދި ލާނެ އިޒާރެއް ނުލިބުނު މީހާ ފަހެ ހަރުވާޅު ލާށެވެ.“","[البخاري 1841 واللفظ له، ومسلم 1178]"],["#222","كتاب الحج","ހައްޖުގެ ފޮތް","باب ما يلبس المحرم من الثياب","އިހްރާމް ބަންދެގެން ހުރި މީހާ އަޅާ ހެދުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا: (أَنَّ تَلْبِيَةَ رَسُولِ اللَّهِ ﷺ: لَبَّيْكَ اللَّهُمَّ لَبَّيْكَ، لَبَّيْكَ لا شَرِيكَ لَكَ لَبَّيْكَ، إنَّ الْحَمْدَ وَالنِّعْمَةَ لَكَ وَالْمُلْكَ، لا شَرِيكَ لَكَ).\n\nقَالَ: وَكَانَ عَبْدُ اللَّهِ بْنُ عُمَرَ يَزِيدُ فِيهَا «لَبَّيْكَ لَبَّيْكَ، وَسَعْدَيْكَ، وَالْخَيْرُ بِيَدَيْكَ، وَالرَّغْبَاءُ إلَيْكَ وَالْعَمَلُ».","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺގެ ތަލްބިޔާއަކީ: ”لَبَّيْكَ اللَّهُمَّ لَبَّيْكَ، لَبَّيْكَ لا شَرِيكَ لَكَ لَبَّيْكَ. إنَّ الْحَمْدَ وَالنِّعْمَةَ لَكَ وَالْمُلْكَ، لا شَرِيكَ لَكَ“ އެވެ. (މާނައީ: އިބަ އިލާހަށް މި އަޅާ އިޖާބަ ދެމެވެ. ޔާ ﷲ! އިބަ އިލާހަށް މި އަޅާ އިޖާބަ ދެމެވެ. ހަމަކަށަވަރުން އެންމެހައި ހަމްދަކާއި ނިއުމަތަކާއި ވެރިކަމެއް ވަނީ އިބަ އިލާހަށެވެ. އިބަ އިލާހަށް އެއްވެސް ޝަރީކަކު ނުވެއެވެ.)\n\nއަދި އިބްނު ޢުމަރު ތަލްބިޔާއަށް (މިފަދައިން ވިދާޅުވެ) އިތުރު ކުރައްވައެވެ: لَبَّيْكَ لَبَّيْكَ وَسَعْدَيْكَ، وَالْخَيْرُ بِيَدَيْكَ، لبيك، وَالرَّغْبَاءُ إلَيْكَ وَالْعَمَلُ. (މާނައީ: އިބަ އިލާހަށް މި އަޅާ އިޖާބަ ދެމެވެ. އިބަ އިލާހަށް މި އަޅާ އިޖާބަ ދެމެވެ. އަދި އިބަ އިލާހުގެ އެހީތެރިކަމަށް އެދެމެވެ. އަދި އެންމެހައި ހެވެއް ވަނީ އިބަ އިލާހުގެ ދެ އަތްޕުޅުގައެވެ. އިބަ އިލާހަށް މި އަޅާ އިޖާބަ ދެމެވެ. އަދި އެދޭ ކަންކަމުގައި ދެންނެވުން ވަނީ އިބަ އިލާހަށެވެ. އަދި އަމަލުތައް ވަނީ ވެސް އިބަ އިލާހަށެވެ.)","[البخاري 1549، ومسلم 1184 واللفظ له]"],["#223","كتاب الحج","ހައްޖުގެ ފޮތް","باب ما يلبس المحرم من الثياب","އިހްރާމް ބަންދެގެން ހުރި މީހާ އަޅާ ހެދުމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: (لا يَحِلُّ لامْرَأَةٍ تُؤْمِنُ بِاَللَّهِ وَالْيَوْمِ الآخِرِ أَنْ تُسَافِرَ مَسِيرَةَ يَوْمٍ وَلَيْلَةٍ إلاَّ وَمَعَهَا حُرْمَةٌ).\n\nوَفِي لَفْظِ الْبُخَارِيِّ: (لا تُسَافِرُ مَسِيرَةَ يَوْمٍ إلاَّ مَعَ ذِي مَحْرَمٍ)","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲ އާއި އާޚިރަތް ދުވަހަށް އީމާންވާ އަންހެނަކަށް، އެއް ރެއާއި ދުވާލުގެ ދުރުމިނަށް މަޙްރަމަކާ ނުލާ ދަތުރު ކުރުން ހުއްދަވެގެން ނުވެއެވެ.“\n\nއަދި ބުޚާރީގެ ރިވާޔަތެއްގައި ވެއެވެ: ”އެންމެ ދުވަހެއްވެސް އަދި ރެއެއްވެސް މަޙްރަމަކާ ލައިގެން މެނުވީ އެ ކަނބުލޭގެ ދަތުރުކޮށްގެން ނުވާނެއެވެ.“","[البخاري 1088 واللفظ له، ومسلم 1339\n\nمسلم 1339، وليس للبخاري]"],["#224","كتاب الحج","ހައްޖުގެ ފޮތް","باب الفدية","ފިދުޔައިގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ مَعْقِلٍ قَالَ: (جَلَسْتُ إلَى كَعْبِ بْنِ عُجْرَةَ. فَسَأَلَتْهُ عَنِ الْفِدْيَةِ؟ فَقَالَ: نَزَلَتْ فِي خَاصَّةً. وَهِيَ لَكُمْ عَامَّةً. حُمِلْتُ إلَى رَسُولِ اللَّهِ ﷺ وَالْقَمْلُ يَتَنَاثَرُ عَلَى وَجْهِي. فَقَالَ: مَا كُنْتُ أُرَى الْوَجَعَ بَلَغَ بِكَ مَا أَرَى - أَوْ مَا كُنْتُ أُرَى الْجَهْدَ بَلَغَ بِكَ مَا أَرَى - أَتَجِدُ شَاةً؟ فَقُلْتُ: لا. فَقَالَ: صُمْ ثَلاثَةَ أَيَّامٍ، أَوْ أَطْعِمْ سِتَّةَ مَسَاكِينَ، لِكُلِّ مِسْكِينٍ نِصْفَ صَاعٍ).\n\nوَفِي رِوَايَةٍ: (فَأَمَرَهُ رَسُولُ اللَّهِ ﷺ أَنْ يُطْعِمَ فَرَقاً بَيْنَ سِتَّةٍ، أَوْ يُهْدِيَ شَاةً، أَوْ يَصُومَ ثَلاثَةَ أَيَّامٍ).","ޢަބްދު ﷲ ބިން މަޢުގިލް ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ކައުބް ބިން ޢުޖްރާގެ އަރިހު ތިމަން އިށީނދެ، ފިދުޔައާ ބެހޭ ގޮތުން ސުވާލު ކުރީމެވެ. ކައުބް ދެންނެވިއެވެ: އެކަމާ ގުޅޭ (އާޔަތް) ބާވައިލެއްވުނީ ތިމަންނައާ މެދުގައި ޚާއްސަކޮށެވެ. އަދި ތިޔަބައިމީހުންނަށް އެ އާޔަތް ވަނީ އާންމުކޮށެވެ. (ހައްޖުގައި) ނަބިއްޔާގެ އަރިހަށް ތިމަން އުފުލައިގެން ގެންދެވުނެވެ. އޭރު ތިމަންނަގެ މޫނު މައްޗަށް އުކުނު ފައިބަފައިބަ ހުއްޓެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާއަށް ޖެހިފައިވާ ވަރަށް އުނދަގޫ ޖެހޭނެ ކަމަކަށް ތިމަން ނަބިއްޔާ ހީފުޅު ނުކުރައްވަމެވެ. ތިބާއަށް ކަންބަޅިއެއް ހޯދިދާނެތޯއެވެ؟“ (އެބަހީ: ބޯ ބޭލުމުގެ ފިދުޔައަށްޓަކައި ކަތިލާނެ ކަނބަޅިއެއް ލިބިދާނެތޯއެވެ.) ތިމަން ދެންނެވީމެވެ: ނޫނެކެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ފަހެ ތިން ދުވަސް ވަންދެން ރޯދަ ހިފާށެވެ. ނޫނީ ހަ މިސްކީނުންނަށް ކާންދޭށެވެ. ކޮންމެ މިސްކީނަކަށް ޞާޢެއްގެ ދެބައިކުޅަ އެއްބައެވެ.“\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”ފަހެ ރަސޫލު ﷲ ﷺ ކައުބަށް އަމުރު ކުރެއްވީ، ހަ މިސްކީނުންނަށް ފަރަގެއްގެ މިންވަރު (އެބަހީ: ޞާޢެއްގެ ތިންބައި ކުޅަ އެއްބައި) ކާންދެއްވުމަށެވެ. ނޫނީ ކަންބަޅިއެއް ހަދްޔުގެ ގޮތުގައި (ކަތިލުމަށެވެ.) ނޫނީ ތިން ދުވަސް ވަންދެއް ރޯދަ ހިފުމަށެވެ.","[البخاري 1816 واللفظ له، ومسلم 1201\n\nالبخاري 1817 واللفظ له، ومسلم 1201]"],["#225","كتاب الحج","ހައްޖުގެ ފޮތް","باب حرمة مكة","މައްކާގެ ހުރުމަތްތެރިކަމުގެ ބާބު","عَنْ أَبِي شُرَيْحٍ - خُوَيْلِدِ بْنِ عَمْرٍو - الْخُزَاعِيِّ الْعَدَوِيِّ رَضِيَ اللَّهُ عَنْهُ: أَنَّهُ قَالَ لِعَمْرِو بْنِ سَعِيدِ بْنِ الْعَاصِ - وَهُوَ يَبْعَثُ الْبُعُوثَ إلَى مَكَّةَ - ائْذَنْ لِي أَيُّهَا الأَمِيرُ أَنْ أُحَدِّثَكَ قَوْلاً قَامَ بِهِ رَسُولُ اللَّهِ ﷺ الْغَدَ مِنْ يَوْمِ الْفَتْحِ. فَسَمِعَتْهُ أُذُنَايَ، وَوَعَاهُ قَلْبِي، وَأَبْصَرَتْهُ عَيْنَايَ، حِينَ تَكَلَّمَ بِهِ: (أَنَّهُ حَمِدَ اللَّهَ وَأَثْنَى عَلَيْهِ. ثُمَّ قَالَ: إنَّ مَكَّةَ حَرَّمَهَا اللَّهُ تَعَالَى، وَلَمْ يُحَرِّمْهَا النَّاسُ. فَلا يَحِلُّ لامْرِئٍ يُؤْمِنُ بِاَللَّهِ وَالْيَوْمِ الآخِرِ: أَنْ يَسْفِكَ بِهَا دَماً، وَلا يَعْضِدَ بِهَا شَجَرَةً. فَإِنْ أَحَدٌ تَرَخَّصَ بِقِتَالِ رَسُولِ اللَّهِ ﷺ فَقُولُوا: إنَّ اللَّهَ قَدْ أَذِنَ لِرَسُولِهِ، وَلَمْ يَأْذَنْ لَكُمْ. وَإِنَّمَا أُذِنَ لِي سَاعَةً مِنْ نَهَارٍ. وَقَدْ عَادَتْ حُرْمَتُهَا الْيَوْمَ كَحُرْمَتِهَا بِالأَمْسِ. فَلْيُبَلِّغْ الشَّاهِدُ الْغَائِبَ. فَقِيلَ لأَبِي شُرَيْحٍ: مَا قَالَ لَكَ؟ قَالَ: أَنَا أَعْلَمُ بِذَلِكَ مِنْكَ يَا أَبَا شُرَيْحٍ، إنَّ الْحَرَمَ لا يُعِيذُ عَاصِياً، وَلا فَارَّاً بِدَمٍ وَلا فَارَّاً بِخَرْبَةٍ).","އަބޫ ޝުރައިޙު، ޚުވައިލިދު ބިން ޢަމްރު އަލްޚުޒާޢީ އަލްޢަދަވީ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ޢަމްރު ބިން ސަޢީދު ބިން އަލްޢާޞް، ލަޝްކަރުތައް މައްކާއަށް ފޮނުވި އިރު ތިމަން ދެންނެވީމެވެ: އޭ އަމީރެވެ. ވާހަކަ ދެއްކުމަށް ތިމަންނައަށް ހުއްދަ ދެއްވާށެވެ. އެއީ ނަބިއްޔާ ﷺ މައްކާ ފަތަހަ ކުރެއްވި ދުވަހާ ޖެހިގެން އައި ދުވަހު ޙަދީޘް ކުރެއްވި ވާހަކަފުޅެކެވެ. އެ ދެއްކެވި ވާހަކަފުޅު ތިމަންނަގެ ދެ ކަންފަތުން އަޑުއަހައި، ތިމަންނަގެ ހިތުން ރަނގަޅަށް ދެނެގަނެ، އަދި ތިމަންނަގެ ދެ ލޮލުން ނަބިއްޔާ ވާހަކަފުޅު ދައްކަވަނިކޮށް ދުށީމެވެ. ﷲއަށް ހަމްދު ސަނާ ދެންނެވުމަށް ފަހު ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން މައްކާ ހުރުމަތްތެރި ކުރެއްވީ ﷲ ތަޢާލާއެވެ. އަދި އެ ތަން ހުރުމަތްތެރި ކުރީކީ މީސްތަކުންނެއް ނޫނެވެ. ވީމާ ﷲއާއި އާޚިރަތް ދުވަހަށް އީމާންވާ މީހަކަށް އެ ތަނުގައި ލޭ އޮހޮރުވުން ހުއްދަވެގެނެއް ނުވެއެވެ. އަދި އެ ތަނުގައި ގަސް ކެނޑުން ވެސް ހުއްދައެއް ނުވެއެވެ. އަދި ރަސޫލު ﷲ ﷺ އެ ތަނުގައި ހަނގުރާމަ ކުރެއްވީތީވެ އެކަން ކުރުން ހުއްދަވާނެ ކަމަށް މީހަކު ބުނާ ނަމަ، ފަހެ ތިޔަބައިމީހުން ބުނާށެވެ: ހަމަކަށަވަރުން ﷲ ވަނީ އެ އިލާހުގެ ރަސޫލާއަށް ހުއްދަ ދެއްވާފައެވެ. އަދި އެ އިލާހު ތިޔަބައިމީހުންނަކަށް ހުއްދައެއް ނުދެއްވައެވެ. އަދި އެ އިލާހުގެ ރަސޫލާއަށް ހުއްދަ ދެއްވީ ވެސް ހަމައެކަނި ދުވާލުގެ ވަގުތުކޮޅަކަށެވެ. އެއަށްފަހު ހަމަކަށަވަރުން އޭގެ ކުރީ ކުރީ ދުވަހެކޭ އެއްފަދައިން އެ ތަނުގެ ހުރުމަތްތެރިކަން މިއަދު އިއާދަވެއްޖެއެވެ. ފަހެ ހާޒިރުގައި ހުރި މީހާ، ހާޒިރުގައި ނެތް މީހާއަށް (މި ބަސްފުޅު) ފޯރުކޮށްދޭށެވެ.“ އަބޫ ޝޫރައިޙަށް ދެންނެވުނެވެ: ޢަމްރު ތިބާއަށް ދެންނެވީ ކީކޭތޯއެވެ؟ އަބޫ ޝުރައިޙު ދެންނެވިއެވެ: ޢަމްރު ދެންނެވީ: އޭ އަބޫ ޝުރައިޙެވެ. ތިބާއަށް ވުރެ ބޮޑަށް ތިމަންނަ ތިކަންތައް ދެނަހުރީމެވެ. އޭ އަބޫ ޝުރައިޙެވެ. ހަމަކަށަވަރުން އުރެދޭ މީހަކަށް، ނުވަތަ ލޭއޮހޮރުވައިގެން ފިލާ މީހަކަށް، ނުވަތަ ޖިޒްޔަ ނުދޭންވެގެން ފިލާ މީހަކަށް (މައްކާގެ) ހަރަމުގެ ހިމާޔަތެއް ނުވެއެވެ.","[البخاري 1832، ومسلم 1354]"],["#226","كتاب الحج","ހައްޖުގެ ފޮތް","باب حرمة مكة","މައްކާގެ ހުރުމަތްތެރިކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ يَوْمَ فَتْحِ مَكَّةَ - (لا هِجْرَةَ بَعْدَ الفَتْحِ، وَلَكِنْ جِهَادٌ وَنِيَّةٌ. وَإِذَا اُسْتُنْفِرْتُمْ فَانْفِرُوا.\n\nوَقَالَ: يَوْمَ فَتْحِ مَكَّةَ: إنَّ هَذَا الْبَلَدَ حَرَّمَهُ اللَّهُ يَوْمَ خَلَقَ اللَّهُ السَّمَوَاتِ وَالأَرْضَ. فَهُوَ حَرَامٌ بِحُرْمَةِ اللَّهِ إلَى يَوْمِ الْقِيَامَةِ. وَإِنَّهُ لَمْ يَحِلَّ الْقِتَالُ فِيهِ لأَحَدٍ قَبْلِي، وَلَمْ يَحِلَّ لِي إلاَّ سَاعَةً مِنْ نَهَارٍ فَهُوَ حَرَامٌ بِحُرْمَةِ اللَّهِ إلَى يَوْمِ الْقِيَامَةِ. لا يُعْضَدُ شَوْكُهُ، وَلا يُنَفَّرُ صَيْدُهُ، وَلا يَلْتَقِطُ لُقْطَتَهُ إلاَّ مَنْ عَرَّفَهَا. وَلا يُخْتَلَى خَلاهُ. فَقَالَ الْعَبَّاسُ: يَا رَسُولَ اللَّهِ، إلاَّ الإِذْخِرَ. فَإِنَّهُ لِقَيْنِهِمْ وَبُيُوتِهِمْ. فَقَالَ: إلاَّ الإِذْخِرَ).","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ މައްކާ ފަތަހަ ކުރެއްވި ދުވަހު ޙަދީޘް ކުރެއްވިއެވެ: ފަތަހަ ކުރެއްވުމަށް ފަހު (މައްކާއިން) ހިޖުރަ ކުރުމެއް ނުވެއެވެ. އެހެނެއްކަމަކު އޮތީ ޖިހާދު ކުރުމާއި ނިޔަތްގަތުމެވެ. އެ ގޮތުން ތިޔަބައިމީހުން (ޖިހާދަށް) ނުކުތުމަށް ގޮވާލެވޭ ހިނދު، ފަހެ ތިޔަބައިމީހުން ނުކުންނާށެވެ.\n\nއަދިވެސް މައްކާ ފަތަހަ ކުރެއްވި ދުވަހު ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ހަމަކަށަވަރުން ﷲ އުޑުތަކާއި ބިން ޚަލްގު ކުރެއްވި ދުވަހު، މި ރަށް (އެބަހީ: މައްކާ) ހުރުމަތްތެރި ކުރެއްވިއެވެ. ފަހެ ﷲގެ ހުރުމަތްތެރި ކުރެއްވުމައިގެން ގިޔާމަތަށް ދާންދެން މި ބިން ހަރަމެއް ކަމުގައި ވެއެވެ. އަދި ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާގެ ކުރިން އެކަކަށް ވެސް މައްކާގައި ހަނގުރާމަ ކުރުން ހުއްދަ ކުރެވިފައެއް ނުވެއެވެ. ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާއަށް (މައްކާގައި ހަނގުރާމަ ކުރެއްވުން) ހުއްދަ ކުރެއްވުނީ ހަމައެކަނި ދުވާލުގެ ވަގުތުކޮޅަކަށެވެ. އެ މި ވަގުތުކޮޅެވެ. ފަހެ (އަނެއްކާވެސް) ﷲގެ ހުރުމަތްތެރި ކުރެއްވުމައިގެން ގިޔާމަތަށް ދާންދެން މި ބިން ހަރަމެއް ކަމުގައި ވެއެވެ. މައްކާގެ ކަށިގަސްތައް އުފުރުމެއް ނުވެއެވެ. އަދި (އާންމުކޮށް އެހެން ތަންތަނުގައި) ޝިކާރަ ކުރެވޭ ޖަނަވާރުތައް އެ ތަނުގެ ފަހާ ދުވުމެއްވެސް ނުވެއެވެ. އަދި އެ ތަނުގައި ވެއްޓިގެން (ގެއްލޭ) އެއްޗެއް، އޭގެ ވެރިމީހާ ހޯދުމުގެ ގޮތުން އިއުލާން ކުރުމަށް މެނުވީ ހޮވައިގެން ނުވެއެވެ. އަދި މައްކާގެ ވިނަގަސްތައް ކެނޑުމެއް ނުވެއެވެ.“ ދެން އަލްޢައްބާސް ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. އިޛްޚިރު (އެބަހީ: ގަނދަކޯޅި) ފިޔަވައެވެ. އެހެނީ ހަމަކަށަވަރުން ތިމަންމެންގެ ދަގަނޑު މަސައްކަތް ކުރާ ތަންތަނާއި ގެތަކުގައި (މީރުވަސް ދުއްވަން) އޭތި ބަހައްޓަންށެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އިޛްޚިރު ފިޔަވައެވެ.“","[البخاري 1834 واللفظ له، ومسلم 1353]"],["#227","كتاب الحج","ހައްޖުގެ ފޮތް","باب ما يجوز قتله","މެރުން ހުއްދަ ތަކެތީގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: (خَمْسٌ مِنْ الدَّوَابِّ كُلُّهُنَّ فَاسِقٌ، يُقْتَلْنَ فِي الْحَرَمِ: الْغُرَابُ، وَالْحِدَأَةُ، وَالْعَقْرَبُ، وَالْفَأْرَةُ، وَالْكَلْبُ الْعَقُورُ).\n\nوَلِمُسْلِمٍ: (يُقْتَلُ خَمْسٌ فَوَاسِقُ فِي الْحِلِّ وَالْحَرَمِ)","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ޖަނަވާރުންގެ ތެރޭގައި ފަހެއްޗެއް ވެއެވެ. އެއިން ކޮންމެ އެއްޗަކީ ފަސާދަކޮށް (ގެއްލުން ދެނިވި) ތަކެއްޗެވެ. އަދި އެއިން ކޮންމެ އެއްޗެއް ހަރަމުގައި ވެސް މެރުން ހުއްޓެވެ. (އެއީ) ކާޅާއި، އެހަނޑައާއި، ނަގުލަންދާއްޓާއި، މީދާއާއި، (އަނިޔާދީ ގަތުލުކޮށް ޝިކާރަ ކުރާ) ކުއްތާ (ނުވަތަ ސިންގާ، މިނިކާވަގު، ވަލު ކުއްތާ، ލެޕަޑު ފަދަ މަސްކާ ޖަނަވާރެވެ.)\n\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ”ފަސާދަ ކުރާ ފަސް (ޖަނަވާރެއް) ހަރަމުގައާއި އެ ނޫން ތަންނަނުގައި ވެސް މެރުން ހުއްޓެވެ.","[البخاري 1829 واللفظ له، ومسلم 1198\n\nمسلم 1198]"],["#228","كتاب الحج","ހައްޖުގެ ފޮތް","باب دخول مكة وغيره","މައްކާއަށް ވަނުމާއި، އެ ނޫން ވެސް ކަންކަމުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ -: (أَنَّ رَسُولَ اللَّهِ ﷺ دَخَلَ مَكَّةَ عَامَ الْفَتْحِ، وَعَلَى رَأْسِهِ الْمِغْفَرُ، فَلَمَّا نَزَعَهُ جَاءَهُ رَجُلٌ فَقَالَ: ابْنُ خَطَلٍ مُتَعَلِّقٌ بِأَسْتَارِ الْكَعْبَةِ فَقَالَ: اُقْتُلُوهُ).","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން މައްކާ ފަތަހަ ކުރެއްވި އަހަރު، ރަސޫލު ﷲ ﷺ މައްކާއަށް ވަދެވަޑައިގަތީ އިސްތަށިފުޅުގައި ދަގަނޑު ތާކިހާއެއް ތުރުކުރައްވައިގެންނެވެ. ދެން ނަބިއްޔާ އެ ބޭލުމުން، މީހަކު އައިސް ދެންނެވިއެވެ: ކައުބާގެ އުރަކޮޅުގައި އިބްނު ޚަޠަލު އެލިގަނެގެން (ހިމާޔަތަށް އެދި) އެބައޮތެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭނާ ގަތުލު ކުރާށެވެ.“ (ނޯޓު: އިބްނު ޚަޠަލަކީ، ކުރިން އިސްލާމްވެގެން ހިޖުރަ ކުރި މީހެކެވެ. އެއް ދުވަހަކު އިބްނު ޚަޠަލު ޒަކާތް އެއްކުރުމަށް ރަސޫލާ ފޮނުއްވެވިއެވެ. އަދި އޭނާއާއެކު ޚާދިމު މީހެއް ވެސް އަންސާރުންގެ ޚުޒާޢާ ވަންހައިގެ ތެރެއިން ފޮނުއްވިއެވެ. އެ ގޮތުން ތަނެއްގައި މަޑު ކުރުމަށް ފަހު، ކާން ތައްޔާރު ކުރަން އެ ޚާދީމު މީހާ ގާތު އިބްނު ޚަޠަލު އެނގުމަށް ފަހު، ދުވާލުގެ ދެބައިކުޅަ އެއްބައި ނިދައިފިއެވެ. ދެން ހޭލެވުނު އިރު، ކާން ނަހަދާ އެ ޚާދިމު މީހާ ހުރީ ނިދާފައެވެ. ދެން ރުޅިއައިސްގެން އޭނާ ގައިގައި ތެޅިތެޅީނުންގޮސް މަރާލައިފިއެވެ. އޭނާ ހިތަށް އަރައިފިއެވެ. ތިމަންނަ މުޙައްމަދުގެ އަރިހަށް ގޮސްފިނަމަ، ބަދަލުގެ ގޮތުގައި ތިމަންނަ މަރާނެއެވެ. ދެން އޭނާ މުރުތައްދުވެ، ޒަކާތުގެ މުދާކޮޅު އަތުލައިގެން މައްކާއަށް ފިލާ ރައްކާވެއްޖެއެވެ. އަދި މުޝްރިކުންނާއެކު އެބައިމީހުންގެ ދީން މަތީ އުޅުނެވެ. އަދި މީގެ އިތުރަށް، އޭނާ ބުނެގެން ނަބިއްޔާއަށް ހަޖޫ ޖަހާއި ފުރައްސާރަކޮށް ލަވަ ކިޔާހަދައި ދެ އަންހެން އަޅުން އޭނާ ގެންގުޅުނެވެ. ދެން މައްކާ ފަތަހަ ކުރެއްވި ދުވަހު، މުސްލިމުން އަންނަކަން އެނގިގެން، އިބްނު ޚަޠަލު ދަގަނޑު ދަހަނާ އަޅައިގެން، ހަތިޔާރު ހިފައިގެން އަސްމަތީ ނުކުތެވެ. އެކަމަކު މުސްލިމުންގެ ލަޝްކަރު ފެނިގެން ބިރުންގޮސް އެނބުރި ކައުބާގެ އުރަކޮޅުތަކުތެރެއަށް ވަނެވެ.)","[البخاري 1846، ومسلم 1357 واللفظ له]"],["#229","كتاب الحج","ހައްޖުގެ ފޮތް","باب دخول مكة وغيره","މައްކާއަށް ވަނުމާއި، އެ ނޫން ވެސް ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا: (أَنَّ رَسُولَ اللَّهِ ﷺ دَخَلَ مَكَّةَ مِنْ كَدَاءٍ، مِنْ الثَّنِيَّةِ الْعُلْيَا الَّتِي بِالْبَطْحَاءِ، وَخَرَجَ مِنْ الثَّنِيَّةِ السُّفْلَى).","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ މައްކާއަށް ވަދެވަޑައިގެންނެވީ ބަޠުޙާގައިވާ ކަދާއޭ ކިޔުނު، ޘަނިއްޔަތުލް ޢުލްޔާގެ އުސް މަގުންނެވެ. (މިހާރު މައްކާގެ ގަބުރުސްތާނުގެ މެދުންލާފައި މި މަގު ހިނގައެވެ.) އަދި ނަބިއްޔާ ނުކުމެވަޑައިގަތީ (ކުދާއޭ ކިޔުނު) ޘަނިއްޔަތުއް ސުފްލާއޭ ކިޔުނު އުސް މަގުންނެވެ.","[البخاري 1576 واللفظ له، ومسلم 1257]"],["#230","كتاب الحج","ހައްޖުގެ ފޮތް","باب دخول مكة وغيره","މައްކާއަށް ވަނުމާއި، އެ ނޫން ވެސް ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (دَخَلَ رَسُولُ اللَّهِ ﷺ الْبَيْتَ، وَأُسَامَةُ بْنُ زَيْدٍ وَبِلالٌ وَعُثْمَانُ بْنُ طَلْحَةَ، فَأَغْلَقُوا عَلَيْهِمْ الْبَابَ فَلَمَّا فَتَحُوا: كُنْتُ أَوَّلَ مَنْ وَلَجَ. فَلَقِيتُ بِلالاً، فَسَأَلَتْهُ: هَلْ صَلَّى فِيهِ رَسُولُ اللَّهِ ﷺ؟ قَالَ: نَعَمْ، بَيْنَ الْعَمُودَيْنِ الْيَمَانِيَيْنِ).","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ އާއި، އުސާމާ ބިން ޒައިދާއި، ބިލާލާއި، ޢުޘްމާން ބިން ޠަލްޙާ ގެފުޅަށް ވަދެވަޑައިގަތެވެ. އަދި އެ ބޭކަލުންގެ ފަހަތުން ދޮރުކޮޅު ލައްޕައިފިއެވެ. ފަހެ އެބޭކަލުން ދޮރުކޮޅު ހުޅުވާލެއްވުމުން، ދެން އެންމެ ފުރަތަމަ ވަނީ ތިމަންނައެވެ. އަދި ބިލާލާ ބައްދަލުވުމުން ތިމަން ސުވާލު ކުރީމެވެ: ގެފުޅު ތެރޭގައި ރަސޫލު ﷲ ﷺ ނަމާދު ކުރެއްވިތޯއެވެ؟ ބިލާލު ދެންނެވިއެވެ: އާދޭހެވެ. ކުރެއްވީ ޔަމާނީ ދެ ތަނބު ދެމެދުގައެވެ. (އެބަހީ: ހަޖަރުލް އަސްވަދުގެ ތަނބާއި ރުކުނުލް ޔަމާނީގެ ތަނބެވެ.)","[البخاري 1598 واللفظ له، ومسلم 1329]"],["#231","كتاب الحج","ހައްޖުގެ ފޮތް","باب دخول مكة وغيره","މައްކާއަށް ވަނުމާއި، އެ ނޫން ވެސް ކަންކަމުގެ ބާބު","عَنْ عُمَرَ - رَضِيَ اللَّهُ عَنْهُ - (أَنَّهُ جَاءَ إلَى الْحَجَرِ الأَسْوَدِ، فَقَبَّلَهُ. وَقَالَ: إنِّي لأَعْلَمُ أَنَّك حَجَرٌ، لا تَضُرُّ وَلا تَنْفَعُ، وَلَوْلا أَنِّي رَأَيْتُ النَّبِيَّ ﷺ يُقَبِّلُكَ مَا قَبَّلْتُكَ).","ޢުމަރު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން އެކަލޭގެފާނު ހަޖަރުލް އަސްވަދަށް އައިސް ބޮސްދެއްވިއެވެ. އަދި ދެންނެވިއެވެ: ހަމަކަށަވަރުން ތިޔައީ ގެއްލުމެއް ވެސް އަދި ފައިދާއެއް ވެސް ނުދެވޭ ހިލައެއްކަން ތިމަން ދެނަހުރީމެވެ. އަދި ނަބިއްޔާ ﷺ ތިޔަށް ބޮސްދެއްވިތަން ތިމަންނައަށް ފެނިފައި ނުވާ ނަމަ، ތިޔަކަށް ބޮހެއް ނުދިނީމުހެވެ.","[البخاري 1597 واللفظ له، ومسلم 1270]"],["#232","كتاب الحج","ހައްޖުގެ ފޮތް","باب دخول مكة وغيره","މައްކާއަށް ވަނުމާއި، އެ ނޫން ވެސް ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (لَمَّا قَدِمَ رَسُولُ اللَّهِ ﷺ وَأَصْحَابُهُ مَكَّةَ. فَقَالَ الْمُشْرِكُونَ: إنَّهُ يَقْدَمُ عَلَيْكُمْ قَوْمٌ وَهَنَتْهُمْ حُمَّى يَثْرِبَ. فَأَمَرَهُمْ النَّبِيُّ ﷺ أَنْ يَرْمُلُوا الأَشْوَاطَ الثَّلاثَةَ، وَأَنْ يَمْشُوا مَا بَيْنَ الرُّكْنَيْنِ، وَلَمْ يَمْنَعْهُمْ أَنْ يَرْمُلُوا الأَشْوَاطَ كُلَّهَا: إلاَّ الإِبْقَاءُ عَلَيْهِمْ).","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ އާއި އެކަލޭގެފާނުގެ ސަހާބީން މައްކާއަށް ވަޑައިގަތެވެ. ފަހެ މުޝްރިކުން ބުންޏެވެ: ހަމަކަށަވަރުން ޔަޘްރިބު (އެބަހީ: މަދީނާގެ) ހުމުގެ ސަބަބުން ބަލިކަށިވެފައިވާ ބަޔަކު ތިޔަބައިމީހުންގެ ގާތަށް އެބަ އާދެއެވެ. ދެން ނަބިއްޔާ ﷺ ސަހާބީންގެ (ފިރިހެނުންގެ) ގާތު (ތަވާފުގެ) ފުރަތަމަ ތިން ބުރުގައި ބާރަށް ހިނގަން އެންގެވިއެވެ. އަދި (އެ ކަމުގެ ތެރޭގައި، ހަޖަރުލް އަސްވަދާއި ރުކުނުލް ޔަމާނީގެ) ދެ ރުކުން ދެމެދު (އާދައިގެ މަތިން) ހިނގާފައި ދިޔުމަށް އެންގެވިއެވެ. އަދި މުޅި (ހަތް) ބުރުގައި ބާރަށް ހިނގާފައި (ތަވާފު ކުރަން) ނޭނގެވީ އެބައިމީހުންނަށް ރަހުމް ކުރެއްވުމުގެ ސަބަބުންނެވެ.","[البخاري 1602 واللفظ له، ومسلم 1266]"],["#233","كتاب الحج","ހައްޖުގެ ފޮތް","باب دخول مكة وغيره","މައްކާއަށް ވަނުމާއި، އެ ނޫން ވެސް ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (رَأَيْتُ رَسُولَ اللَّهِ ﷺ حِينَ يَقْدَمُ مَكَّةَ إذَا اسْتَلَمَ الرُّكْنَ الأَسْوَدَ - أَوَّلَ مَا يَطُوفُ - يَخُبُّ ثَلاثَةَ أَشْوَاطٍ).","ޢަބްދު ﷲ ބިން ޢަމްރު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ މައްކާއަށް ވަދެވަޑައިގެންނެވި ހިނދު ތިމަން ދުށީމެވެ. ނަބިއްޔާ ތަވާފު ކުރައްވަން ފެއްޓެވީ ހަޖަރުލް އަސްވަދުގައި ބީއްސަވައިގެންނެވެ. އަދި (ތަވާފުގެ) ފުރަތަމަ ތިން ބުރުގައި ނަބިއްޔާ ބާރަށް ހިނގައިވަޑައިގަތެވެ.","[البخاري 1603، ومسلم 1261]"],["#234","كتاب الحج","ހައްޖުގެ ފޮތް","باب دخول مكة وغيره","މައްކާއަށް ވަނުމާއި، އެ ނޫން ވެސް ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (طَافَ النَّبِيُّ ﷺ فِي حَجَّةِ الْوَدَاعِ عَلَى بَعِيرٍ، يَسْتَلِمُ الرُّكْنَ بِمِحْجَنٍ).","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ހައްޖަތުލް ވަދާއުގައި ނަބިއްޔާ ﷺ ތަވާފު ކުރެއްވީ ޖަމަލުކޮޅު މަތީގައެވެ. އަދި ހަޖަރުލް އަސްވަދުގައި ނަބިއްޔާ ބީއްސެވީ އަސާކޮޅަކުންނެވެ.","[البخاري 1607 واللفظ له، ومسلم 1272]"],["#235","كتاب الحج","ހައްޖުގެ ފޮތް","باب دخول مكة وغيره","މައްކާއަށް ވަނުމާއި، އެ ނޫން ވެސް ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (لَمْ أَرَ النَّبيَّ ﷺ يَسْتَلِمُ مِنْ الْبَيْتِ إلاَّ الرُّكْنَيْنِ الْيَمَانِيَيْنِ).","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ޔަމާނީ ދެ ތަނބުގައި (އެބަހީ: ހަޖަރުލް އަސްވަދާއި ރުކުނުލް ޔަމާނީ) ފިޔަވައި ގެފުޅުގެ އެހެން ތަނެއްގައި ނަބިއްޔާ ﷺ ބީއްސެވި ތަނެއް ތިމަން ނުދެކެމެވެ.","[البخاري 1609 واللفظ له، ومسلم 1267]"],["#236","كتاب الحج","ހައްޖުގެ ފޮތް","باب التمتع","ތަމައްތުޢުގެ ބާބު","عَنْ أَبِي جَمْرَةَ نَصْرِ بْنِ عِمْرَانَ الضُّبَعِيِّ، قَالَ: (سَأَلْتُ ابْنَ عَبَّاسٍ عَنْ الْمُتْعَةِ؟ فَأَمَرَنِي بِهَا، وَسَأَلَتْهُ عَنْ الْهَدْيِ؟ فَقَالَ: فِيهِ جَزُورٌ، أَوْ بَقَرَةٌ، أَوْ شَاةٌ، أَوْ شِرْكٌ فِي دَمٍ قَالَ: وَكَانَ نَاسٌ كَرِهُوهَا، فَنِمْتُ. فَرَأَيْتُ فِي الْمَنَامِ: كَأَنَّ إنْسَاناً يُنَادِي: حَجٌّ مَبْرُورٌ، وَمُتْعَةٌ مُتَقَبَّلَةٌ. فَأَتَيْتُ ابْنَ عَبَّاسٍ فَحَدَّثَتْهُ. فَقَالَ: اللَّهُ أَكْبَرُ سُنَّةُ أَبِي الْقَاسِمِ ﷺ).","އަބޫ ޖަމަރާ، ނަޞްރު ބިން ޢިމްރާން އައްޟުބަޢީ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: (ތިމަންނަ ތަމައްތުޢު ކުރުމުން ބަޔަކު މީހުން އެކަން ނުކުރުމަށް ތިމަން ގާތު ދެންނެވިއެވެ. ދެން) އިބްނު ޢައްބާސް ގާތު ތަމަތުޢު ކުރުމާމެދު ތިމަން ސުވާލު ކުރީމެވެ. ފަހެ ތަމައްތުޢު ކުރުމަށް އިބްނު ޢައްބާސް ތިމަންނައަށް އެންގެވިއެވެ. ދެން ހަދްޔު ކަތިލުމާމެދު ތިމަން ސުވާލު ކުރީމެވެ. އިބްނު ޢައްބާސް ދެންނެވިއެވެ: އޭގައި ކަތިލަން ޖެހޭނީ ޖަމަލެއް، ނުވަތަ ގެރިއެއް، ނުވަތަ ކަންބަޅިއެކެވެ. ނުވަތަ އެހެން ބަޔަކާއެކު ބައިވެރިވާ ގޮތަށް ހަދްޔު ކަތިލެވިދާނެއެވެ. އަބޫ ޖަމަރާ ދެންނެވިއެވެ: ތަމައްތުޢު ކުރުމާމެދު ބަޔަކު މީހުން ނުރުހޭ ފަދައެވެ. ދެން ތިމަންނަ ނިދައިފީމެވެ. ފަހެ ނިދީގައި އިންސާނަކު (މިފަދައިން) ގޮވާލަނިކޮށް ތިމަންނައަށް ފެނުނެވެ: މަބްރޫރު ހައްޖެކެވެ. އަދި ގަބޫލު ވެވިގެންވާ ތަމައްތުޢެކެވެ. ދެން އިބްނު ޢައްބާސް رَضِيَ اللَّهُ عَنْهُ އަރިހަށް އައިސް އެ ވާހަކަ ދެންނެވީމެވެ. އިބްނު ޢައްބާސް ދެންނެވިއެވެ: ﷲ އަކްބަރެވެ. އަބުލް ގާސިމް (އެބަހީ: ރަސޫލު ﷲ) ﷺގެ ސުންނަތެވެ.","[البخاري 1688 واللفظ له، ومسلم 1242]"],["#237","كتاب الحج","ހައްޖުގެ ފޮތް","باب التمتع","ތަމައްތުޢުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (تَمَتَّعَ رَسُولُ اللَّهِ ﷺ فِي حَجَّةِ الْوَدَاعِ بِالْعُمْرَةِ إلَى الْحَجِّ وَأَهْدَى. فَسَاقَ مَعَهُ الْهَدْيَ مِنْ ذِي الْحُلَيْفَةِ. وَبَدَأَ رَسُولُ اللَّهِ ﷺ وَأَهَلَّ بِالْعُمْرَةِ، ثُمَّ أَهَلَّ بِالْحَجِّ، فَتَمَتَّعَ النَّاسُ مَعَ رَسُولِ اللَّهِ ﷺ فَأَهَلَّ بِالْعُمْرَةِ إلَى الْحَجِّ، فَكَانَ مِنْ النَّاسِ مَنْ أَهْدَى، فَسَاقَ الْهَدْيَ مِنْ الْحُلَيْفَةِ. وَمِنْهُمْ مَنْ لَمْ يُهْدِ فَلَمَّا قَدِمَ رَسُولُ اللَّهِ ﷺ قَالَ لِلنَّاسِ: مَنْ كَانَ مِنْكُمْ أَهْدَى، فَإِنَّهُ لا يَحِلُّ مِنْ شَيْءٍ حَرُمَ مِنْهُ حَتَّى يَقْضِيَ حَجَّهُ. وَمَنْ لَمْ يَكُنْ أَهْدَى فَلْيَطُفْ بِالْبَيْتِ وَبِالصَّفَا وَالْمَرْوَةِ، وَلْيُقَصِّرْ وَلْيَحْلِلْ، ثُمَّ لِيُهِلَّ بِالْحَجِّ وَلْيُهْدِ، فَمَنْ لَمْ يَجِدْ هَدْياً فَلْيَصُمْ ثَلاثَةَ أَيَّامٍ فِي الْحَجِّ وَسَبْعَةً إذَا رَجَعَ إلَى أَهْلِهِ فَطَافَ رَسُولُ اللَّهِ ﷺ حِينَ قَدِمَ مَكَّةَ. وَاسْتَلَمَ الرُّكْنَ أَوَّلَ شَيْءٍ، ثُمَّ خَبَّ ثَلاثَةَ أَطْوَافٍ مِنْ السَّبْعِ، وَمَشَى أَرْبَعَةً، وَرَكَعَ حِينَ قَضَى طَوَافَهُ بِالْبَيْتِ عِنْدَ الْمَقَامِ رَكْعَتَيْنِ، ثُمَّ انْصَرَفَ فَأَتَى الصَّفَا، وَطَافَ بِالصَّفَا وَالْمَرْوَةِ سَبْعَةَ أَطْوَافٍ، ثُمَّ لَمْ يَحْلِلْ مِنْ شَيْءٍ حَرُمَ مِنْهُ حَتَّى قَضَى حَجَّهُ، وَنَحَرَ هَدْيَهُ يَوْمَ النَّحْرِ. وَأَفَاضَ فَطَافَ بِالْبَيْتِ، ثُمَّ حَلَّ مِنْ كُلِّ شَيْءٍ حَرُمَ مِنْهُ، وَفَعَلَ مِثْلَ مَا فَعَلَ رَسُولُ اللَّهِ ﷺ: مَنْ أَهْدَى وَسَاقَ الْهَدْيَ مِنْ النَّاسِ).","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ހައްޖަތުލް ވަދާއުގައި، ތަމައްތުޢުކޮށް އުމްރާއަށް ފަހު ހައްޖު ކުރެއްވުމަށް ރަސޫލު ﷲ ﷺ (އަމުރު ކުރެއްވިއެވެ.) (ނަބިއްޔާ ހައްޖު ކުރެއްވީ ގީރާންކޮށެވެ.) އަދި ހަދްޔު ކަތިލައްވަން (މަދީނާގެ މީގާތުކަމަށްވާ) ޛުލް ޙުލައިފާއިން ނަބިއްޔާއާއެކު ހަދްޔު ގެންނެވިއެވެ. އަދި ނަބިއްޔާ އިހްރާމް ބަނެ އުމްރާއާއި ހައްޖަށް ތަލްބިޔާ ވިދާޅުވިއެވެ. (އެބަހީ: ލައްބައިކަ ބިޢުމްރަތިން ވަހައްޖިން، މިފަދައިން ވިދާޅުވިއެވެ.) އަދި ނަބިއްޔާގެ އަރިހުގައި ތިއްބެވި މީސްތަކުން (އާޚިރުގައި ހައްޖު ކުރީ) ތަމައްތުޢުކޮށެވެ. (އެބައިމީހުން ކުރެ ގިނަ މީހުން ކުރިން ފެއްޓީ އިފްރާދުކޮށެވެ.) އެ ގޮތުން މީސްތަކުންގެ ތެރޭގައި ހަދްޔު ކަތިލަން ޛުލް ޙުލައިފާއިން ހަދްޔު ގެނައި ބަޔަކު ތިއްބެވެ. އަދި ހަދްޔު ނުގެންނަ ބަޔަކުވެސް ތިއްބެވެ. ދެން ނަބިއްޔާ ﷺ މައްކާއަށް ވަޑައިގަތް ހިނދު މީސްތަކުންނަށް ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ ތެރެއިން ހަދްޔު ގެނައި މީހަކު، ފަހެ ހަމަކަށަވަރުން އޭނާގެ ހައްޖު އަދާކޮށް ނިމެންދެން (މުހްރިމަކަށް) ހަރާމްވާ އެއްވެސް ކަމަކުން އިހްރާމް ނުކަނޑާ ހުންނާށެވެ. އަދި ތިޔަބައިމީހުންގެ ތެރެއިން ހަދްޔު ނުގެންނަ މީހަކު، ފަހެ ގެފުޅު ވަށައި ތަވާފުކޮށް، ސަފާ މަރްވާ ދެމެދު ސައުޔުކޮށް، ބޯކޮށައި އިހްރާމް ކަނޑާށެވެ. ދެން އެ މީހަކު އަނެއްކާ ހައްޖަށް އިހްރާމް ބަނެ ތަލްބިޔާ ކިޔާށެވެ. (އެބަހީ: ލައްބައިކަ ހައްޖަން ކިޔާށެވެ.) އެއަށްފަހު ހަދްޔު ކަތިލާށެވެ. އަދި ހަދްޔު ކަތިލުމަށް ޖަނަވާރެއް ނުލިބުނު މީހާ، ފަހެ ހައްޖުގައި ތިން ދުވަހު ރޯދަ ހިފާށެވެ. އަދި އެނބުރި އާއިލާ ކައިރިއަށް ދެވުމުން (އެބަހީ: ގައުމަށް ދެވުމުން) ހަތް ދުވަހު ރޯދާ ހިފާށެވެ.“ ފަހެ ރަސޫލު ﷲ ﷺ މައްކާއަށް ވަދެވަޑައިގަތް ހިނދު ތަވާފު ކުރެއްވިއެވެ. އެ ގޮތުން ފުރަތަމަ ހަޖަރުލް އަސްވަދުގައި ބީއްސެވިއެވެ. އަދި ތަވާފުގެ ހަތް ބުރުން ކުރެ ފުރަތަމަ ތިން ބުރުގައި ބާރަށް ހިނގައިވަޑައިގަތެވެ. އަދި އަނެއް ހަތަރު ބުރުގައި (އާދައިގެ މަތިން) ހިނގައިވަޑައިގަތެވެ. ދެން ތަވާފުން އަވަދިވުމުން، މަގާމު އިބްރާހީމްގެ (ފަހަތުގައި) ދެ ރަކުއަތް ކުރެއްވިއެވެ. ދެން ސަލާމް ދެއްވުމަށް ފަހު ސަފާއަށް ވަޑައިގަތެވެ. އަދި ސަފާ މަރްވާ ދެމެދު ހަތް ބުރު ޖެއްސެވިއެވެ. އަދި ހައްޖު އަދާކޮށް ނިމެންދެން (މުހްރިމަކަށް) ހަރާމްވާ އެއްވެސް ކަމަކުން އިހްރާމް ނުކަނޑުވައެވެ. ދެން ނަޙްރުގެ ދުވަހު (އެބަހީ: އަޟްހާ އީދު ދުވަހު) ނަބިއްޔާގެ ހަދްޔު ކަތިލެއްވިއެވެ. އެއަށްފަހު (ގެފުޅަށް) ވަޑައިގެން ތަވާފުލް އިފާޟާ ކުރެއްވިއެވެ. ދެން އިހްރާމް ކެނޑުއްވިއެވެ. އަދި މީސްތަކުންގެ ތެރެއިން ހަދްޔު ގެނައި ބަޔަކު ވެސް ކަންތައް ކުރީ، ރަސޫލު ﷲ ﷺ ކަންތައް ކުރެއްވި ގޮތަށެވެ.","[البخاري 1691، ومسلم 1227 واللفظ له]"],["#238","كتاب الحج","ހައްޖުގެ ފޮތް","باب التمتع","ތަމައްތުޢުގެ ބާބު","عَنْ حَفْصَةَ زَوْجِ النَّبِيِّ ﷺ أَنَّهَا قَالَتْ: (يَا رَسُولَ اللَّهِ، مَا شَأْنُ النَّاسِ حَلُّوا مِنْ الْعُمْرَةِ وَلَمْ تَحِلَّ أَنْتَ مِنْ عُمْرَتِكَ فَقَالَ: إنِّي لَبَّدْتُ رَأْسِي، وَقَلَّدْتُ هَدْيِي، فَلا أَحِلُّ حَتَّى أَنْحَرَ).","ހަމަކަށަވަރުން ނަބިއްޔާގެ އަނބިކަނބަލުން ޙަފްޞާ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އެ ކަމަނާ ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. އިބަ ނަބިއްޔާ އުމްރާއިން އިހްރާމް ނުކަނޑުއްވާ ހާލު، މީސްތަކުން އުމްރާއިން އިހްރާމް ކެނޑީ ކޮންކަމަކާތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާގެ އިސްތަށިފުޅު ތަލްބީދު ކުރެއްވީމެވެ. (އެބަހީ: އެއްޗެއް ބޭނުންކޮށްގެން އިސްތަށިތައް ތަތްކޮށް ހަރުކުރެއްވީމެވެ. މިއީ ބޯހޭވިޔަ އުކުނު އެޅުނަ ނުދިނުމަށްޓަކައި ދިގު ދަތުރުތަކުގައި މީހުން ކޮށްއުޅޭ ކަމެކެވެ.) އަދި ތިމަން ނަބިއްޔާގެ ހަދްޔުގެ ކަރުގައި ގަލާއިދު ފަށް އަޅުއްވައިފީމެވެ. (މިއީ ހަދްޔުކަން އަނގައިދޭ ފާހަގައެކެވެ.) ފަހެ ތިމަން ނަބިއްޔާ ހަދްޔު ކަތިލައިފުމަށް ދާންދެން އިހްރާމް ނުކަނޑުއްވާނަމެވެ. (ނޯޓު: ހަދްޔު ނުގެންނަ މީހުންނަށް ނަބިއްޔާ އެންގެވީ ތަމައްތުޢު ކުރެއްވުމަށެވެ. އެ ގޮތުން އެބައިމީހުން އިހްރާމް ކެނޑުމަށްފަހު ދެވަނަ ފަހަރަށް އިހްރާމް ބަނެވެ. އަދި ނަބިއްޔާއާއި ހަދްޔު ގެންނެވި އެހެން މީހުން އިހްރާމް ނުކަނޑާ ހުންނެވިއެވެ.)","[البخاري 1566 واللفظ له، ومسلم 1229]"],["#239","كتاب الحج","ހައްޖުގެ ފޮތް","باب التمتع","ތަމައްތުޢުގެ ބާބު",'عَنْ عِمْرَانَ بْنِ حُصَيْنٍ قَالَ: (أُنْزِلَتْ آيَةُ الْمُتْعَةِ فِي كِتَابِ اللَّهِ تَعَالَى. فَفَعَلْنَاهَا مَعَ رَسُولِ اللَّهِ ﷺ وَلَمْ يَنْزِلْ قُرْآنٌ يُحَرِّمُهَا، وَلَمْ يَنْهَ عَنْهَا حَتَّى مَاتَ. قَالَ رَجُلٌ بِرَأْيِهِ مَا شَاءَ) قَالَ الْبُخَارِيُّ " يُقَالُ: «إنَّهُ عُمَرُ».\n\nوَلِمُسْلِمٍ (نَزَلَتْ آيَةُ الْمُتْعَةِ - يَعْنِي مُتْعَةَ الْحَجِّ - وَأَمَرَنَا بِهَا رَسُولُ اللَّهِ ﷺ ثُمَّ لَمْ تَنْزِلْ آيَةٌ تَنْسَخُ آيَةَ مُتْعَةِ الْحَجِّ وَلَمْ يَنْهَ عَنْهَا رَسُولُ اللَّهِ ﷺ حَتَّى مَاتَ)\n\nوَلَهُمَا بِمَعْنَاهُ.',"ޢިމްރާން ބިން ޙުޞައިން رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ﷲގެ ފޮތުގައި ތަމައްތުޢު ކުރުމުގެ އާޔަތް ބާވައިލެއްވުނެވެ. ފަހެ ރަސޫލު ﷲ ﷺގެ ހުންނަވަނިކޮށް ތިމަންމެން އެކަން ކުރީމެވެ. އަދި އެކަން މަނާ ކުރާ (އާޔަތެއް) ޤުރްއާނުން ބާވައިނުލައްވައެވެ. އަދި ނަބިއްޔާ އަވަހާރަފުޅުވުމަށް ދާންދެން ވެސް އެކަން ނަހީ ނުކުރައްވައެވެ. އަދި (ނަހީ ކުރެއްވި) ބޭކަލަކު، ފަހެ އެ ބޭކަލަކަށް ފެނުނު ރައުޔެއް ދެންނެވީއެވެ. އިމާމު ބުޚާރީ ވިދާޅުވިއެވެ: އެ ފަދައިން ދެންނެވި ބޭކަލަކީ ޢުމަރު ކަމަށް ބުނެވުނެވެ.\n\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: (ހައްޖުގައި) ތަމައްތުޢު ކުރުމުގެ އާޔަތް ބާވައިލެއްވުނެވެ. އަދި ރަސޫލު ﷲ ﷺ އެކަން ކުރަން އަމުރު ކުރެއްވިއެވެ. އެއަށްފަހު ހައްޖުގައި ތަމައްތުޢު ކުރުން މަންސޫޚަ ކުރުވާ އެހެން އާޔަތެއް ބާވައިނުލައްވައެވެ. އަދި ނަބިއްޔާ އަވަހާރަފުޅުވުމަށް ދާންދެން ވެސް އެކަން ނަހީ ނުކުރައްވައެވެ.\n\n(މުއައްލިފު ވިދާޅުވިއެވެ:) އަދި ބުޚާރީއާއި މުސްލިމްގައި މި މާނައިގައި އެހެން ރިވާޔަތްތައް އައިސްފައިވެއެވެ.","[البخاري 4518\n\nمسلم 1226\n\nالبخاري 1571، ومسلم 1226]"],["#240","كتاب الحج","ހައްޖުގެ ފޮތް","باب الهدي","ހަދްޔުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: (فَتَلْتُ قَلائِدَ هَدْيِ رَسُولِ اللَّهِ ﷺ ثُمَّ أَشْعَرْتُهَا وَقَلَّدَهَا - أَوْ قَلَّدْتُهَا - ثُمَّ بَعَثَ بِهَا إلَى الْبَيْتِ. وَأَقَامَ بِالْمَدِينَةِ، فَمَا حَرُمَ عَلَيْهِ شَيْءٌ كَانَ لَهُ حِلاًّ).","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާގެ ހަދްޔުތަކުގައި އަޅުއްވަން ތިމަން ކަމަނާ ގަލާއިދުގެ ހެއްދެވީމެވެ. ދެން ނަބިއްޔާ އެ (ޖަމަލު) ހަދްޔުތަކުގެ ގޮންގަނޑުތައް (ކަނާތް ފަޅިން) ކަނޑުއްވާ ލޭ ފޭބިޔަ ދިނެވެ. އަދި (ކަންބަޅި ބަކަރި) ހަދްޔުތަކުގެ ކަރުގައި ޤަލާއިދު ފަށް އެޅުއްވިއެވެ. (މިއީ ހަދްޔުކަން ފާހަގަ ކުރަން ކުރާ ކަމެކެވެ.) ދެން (އަބޫ ބަކުރާއެކު) ގެފުޅަށް ހަދްޔުތައް ފޮނުއްވުމަށް ފަހު، މަދީނާގައި ނަބިއްޔާ ހުންނެވި އިރު، (މުހްރިމަކަށް) ހަރާމްވާ އެއްވެސް ކަމެއް ނަބިއްޔާއަށް ހަރާމެއް ނުވެއެވެ. (އެއީ ހަދްޔުތައް ކަތިލެވުމަށް ދާންދެން ވެސް މެއެވެ.) (ނޯޓު: މިއީ ނަބިއްޔާ ހައްޖު ކުރެއްވި ފަހަރެއް ނޫނެވެ. އެހެނަސް މިއީ ހިޖުރައިގެ ނުވަވަނަ އަހަރު މީސްތަކުންނައިގެން އަބޫ ބަކުރު ހިޖުރަ ކުރެއްވި ފަހަރެވެ.)","[البخاري 1699 واللفظ له، ومسلم 1321]"],["#241","كتاب الحج","ހައްޖުގެ ފޮތް","باب الهدي","ހަދްޔުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: (أَهْدَى رَسُولُ اللَّهِ ﷺ مَرَّةً غَنَماً).","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ އެއްފަހަރު ކަންބަޅިއެއް ހަދްޔުގެ ގޮތުގައި ފޮނުއްވިއެވެ. (އަދި އޭގެ ކަރުގައި ގަލާއިދު ފަށް އެޅުއްވިއެވެ.)","[البخاري 1701 واللفظ له، ومسلم 1321]"],["#242","كتاب الحج","ހައްޖުގެ ފޮތް","باب الهدي","ހަދްޔުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ -: (أَنَّ نَبِيَّ اللَّهِ ﷺ رَأَى رَجُلاً يَسُوقُ بَدَنَةً، فَقَالَ: ارْكَبْهَا. قَالَ: إنَّهَا بَدَنَةٌ. قَالَ ارْكَبْهَا. فَرَأَيْتُهُ رَاكِبَهَا، يُسَايِرُ النَّبِيَّ ﷺ).\n\nوَفِي لَفْظٍ قَالَ فِي الثَّانِيَةِ، أَوْ الثَّالِثَةِ: (ارْكَبْهَا. وَيْلَكَ، أَوْ وَيْحَكَ).","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން (ހަދްޔުގެ) ޖަމަލެއް މީހަކު ހިނގުވާފައި ގެންދަނިކޮށް ނަބިއްޔާ ﷺ އަށް ފެނިވަޑައިގަތުމުން ޙަދީޘް ކުރެއްވިއެވެ: ”އެއަށް ސަވާރުވާށެވެ.“ އޭނާ ދެންނެވިއެވެ: ހަމަކަށަވަރުން އެއީ (ހަދްޔުގެ) ޖަމަލެކެވެ. ނަބިއްޔާ އަނެއްކާވެސް ޙަދީޘް ކުރެއްވިއެވެ: ”އެއަށް ސަވާރުވާށެވެ.“ ދެން ނަބިއްޔާ އެނގެވި ގޮތަށް އެ މީހަކު އެއަށް ސަވާރުވިތަން ތިމަން ދުށީމެވެ.\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ދެވަނަ ނުވަތަ ތިން ވަނަ ފަހަރު (އެއަށް ސަވާރުވާން އަންގަން ޖެހުމުން) ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެއަށް ސަވާރުވާށެވެ. ތިބާއަށް ހަލާކު ހުއްޓެވެ.“ (ނޯޓު: މިއީ އަރަބީންގެ ދޫމަތިން ބުނެ އުޅޭ އިބާރާތެކެވެ. ބަދު ދުއާ ކުރުމުގެ މާނައިގައި ބުނާ ބަހެއް ނޫނެވެ. އެ ގޮތުން ނަބިއްޔާއަށް ވަގުތުން ތަބާނުވީމާ އޭނާ އަދަބުވެރި ކުރުވުމުގެ ގޮތުން ވިދާޅުވި ބަސްފުޅެކެވެ. އެހެނީ ޖާހިލިއްޔަ ޒަމާނުގައި މީހުން ގަބޫލު ކުރީ ހަދްޔުތަކަށް ސަވާރުވެ ނޫޅެވޭނެ ކަމަށެވެ.)","[البخاري 1706، ومسلم 1322\n\nالبخاري 1689، 2755، ومسلم 1322]"],["#243","كتاب الحج","ހައްޖުގެ ފޮތް","باب الهدي","ހަދްޔުގެ ބާބު","عَنْ عَلِيِّ بْنِ أَبِي طَالِبٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (أَمَرَنِي رَسُولُ اللَّهِ ﷺ أَنْ أَقُومَ عَلَى بُدْنِهِ، وَأَنْ أَتَصَدَّقَ بِلَحْمِهَا وَجُلُودِهَا وَأَجِلَّتِهَا، وَأَنْ لا أُعْطِيَ الْجَزَّارَ مِنْهَا شَيْئًا). وَقَالَ: نَحْنُ نُعْطِيهِ مِنْ عِنْدِنَا.","ޢަލީ ބިން އަބީ ޠާލިބު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺގެ (ހަދްޔު) ޖަމަލުތައް ބަލަހައްޓަން ތިމަންނައަށް އެނގެވިއެވެ. އަދި އެ ތަކެތީގެ މަހާއި، ހަމާއި އަދި ބުރަކަށީގައި އަޅުވާ ފޮތިގަނޑު ވެސް ސަދަގާތް ކުރުމަށް ތިމަންނައަށް އެނގެވިއެވެ. އަދި (ކަތިލުމަށް ދެވޭ ބަދަލެއްގެ ގޮތުން) ކަތިލާމީހާއަށް އެއްވެސް އެއްޗެއް އެއިން ނުދިނުމަށް އެނގެވިއެވެ. އަދި ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ކަތިލާމީހާއަށް (އަގު) ދޭނީ ތިމަންމެންގެ އަމިއްލަ މުދަލުންނެވެ.“","[البخاري 1707، ومسلم 1317 واللفظ له]"],["#244","كتاب الحج","ހައްޖުގެ ފޮތް","باب الهدي","ހަދްޔުގެ ބާބު","عَنْ زِيَادِ بْنِ جُبَيْرٍ قَالَ: (رَأَيْتُ ابْنَ عُمَرَ أَتَى عَلَى رَجُلٍ قَدْ أَنَاخَ بَدَنَتَهُ، فَنَحَرَهَا. فَقَالَ ابْعَثْهَا قِيَاماً مُقَيَّدَةً سُنَّةَ مُحَمَّدٍ ﷺ).","ޒިޔާދު ބިން ޖުބައިރު ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: މީހެއްގެ (ހަދްޔު) ޖަމަލު ކަތިލުމަށްޓަކައި އެ ޖަމަލު އަރިއަކަށް ބާއްވައިގެން އެ މީހަކު އިށީންދެ އިންދާ، އިބްނު ޢުމަރު އަތުވިތަން ދުށީމެވެ. އިބްނު ޢުމަރު ދެންނެވިއެވެ: އެ ޖަމަލު ކޮއްޅަށް ނަގާފައި (އޭގެ ކުރިމަތީ ވާތުފައި) އައްސައިގެން (ތިބާ ކޮއްޅަށް ހުރެ އޭތި ނަޙުރުކޮށް) ކަތިލާށެވެ. މުޙައްމަދު ﷺގެ ސުންނަތަކީ އެއީއެވެ.","[البخاري 1713 واللفظ له، ومسلم 1320]"],["#245","كتاب الحج","ހައްޖުގެ ފޮތް","باب الغسل للمحرم","މުހްރިމަކު ފެންވެރުމުގެ ބާބު",'عَنْ عَبْدِ اللَّهِ بْنِ حُنَيْنٍ: (أَنَّ عَبْدَ اللَّهِ بْنَ عَبَّاسٍ وَالْمِسْوَرَ بْنَ مَخْرَمَةَ اخْتَلَفَا بِالأَبْوَاءِ. فَقَالَ ابْنُ عَبَّاسٍ: يَغْسِلُ الْمُحْرِمُ رَأْسَهُ. وَقَالَ الْمِسْوَرُ: لا يَغْسِلُ رَأْسَهُ. قَالَ: فَأَرْسَلَنِي ابْنُ عَبَّاسٍ إلَى أَبِي أَيُّوبَ الْأَنْصَارِيِّ - رَضِيَ اللَّهُ عَنْهُ -. فَوَجَدْتُهُ يَغْتَسِلُ بَيْنَ الْقَرْنَيْنِ، وَهُوَ يُسْتَرُ بِثَوْبٍ. فَسَلَّمْت عَلَيْهِ. فَقَالَ: مَنْ هَذَا؟ فَقُلْت: أَنَا عَبْدُ اللَّهِ بْنُ حُنَيْنٍ، أَرْسَلَنِي إلَيْكَ ابْنُ عَبَّاسٍ، يَسْأَلُكَ: كَيْفَ كَانَ رَسُولُ اللَّهِ ﷺ يَغْسِلُ رَأْسَهُ وَهُوَ مُحْرِمٌ؟ فَوَضَعَ أَبُو أَيُّوبَ يَدَهُ عَلَى الثَّوْبِ، فَطَأْطَأَهُ، حَتَّى بَدَا لِي رَأْسُهُ. ثُمَّ قَالَ لإِنْسَانٍ يَصُبُّ عَلَيْهِ الْمَاءَ: اُصْبُبْ، فَصَبَّ عَلَى رَأْسِهِ. ثُمَّ حَرَّكَ رَأْسَهُ بِيَدَيْهِ، فَأَقْبَلَ بِهِمَا وَأَدْبَرَ. ثُمَّ قَالَ: هَكَذَا رَأَيْتُهُ ﷺ يَغْتَسِلُ).\n\nوَفِي رِوَايَةٍ " فَقَالَ الْمِسْوَرُ لابْنِ عَبَّاسٍ: لا أُمَارِيكَ أَبَداً ".',"ޢަބްދު ﷲ ބިން ޙުނައިނުގެ އަރިހުން ރިވާވެގެންވެއެވެ: (މައްކާއާއި މަދީނާ ދެމެދުވާ ތަނެއް ކަމުގައިވާ) އަލްއަބްވާގައި (މަޑުކުރައްވައިގެން އިންނަވަނިކޮށް) ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُ އާއި އަލްމިސްވަރު ބިން މަޚްރަމާ رَضِيَ اللَّهُ عَنْهُ އާ ދެބޭކަލުން (ކަމަކާމެދު) އިޚްތިލާފުވިއެވެ. އިބްނު ޢައްބާސް ދެންނެވިއެވެ: މުހްރިމަކަށް އޭނާގެ ބޯ ދޮވިދާނެއެވެ. އަދި އަލްމިސްވަރު ދެންނެވިއެވެ: މުހްރިމަކަށް އޭނާގެ ބޮލެއް ނުދޮވޭނެއެވެ. ދެން ފަހެ އިބްނު ޢައްބާސް، އަބޫ އައްޔޫބް އަލްއަންޞާރީ رَضِيَ اللَّهُ عَنْهُގެ އަރިހަށް ތިމަން ފޮނުއްވިއެވެ. އެކަލޭގެފާނާ ދިމާވި ހިނދު އިންނެވީ، ވަޅެއްގެ ދެ މުޑި ދެމެދު ފޭރާމެއްގެ ފަހަތުގައި ނިވާވެ ފެންވަރާށެވެ. ދެން ތިމަންނަ ސަލާމް ދެންނެވުމުން އެކަލޭގެފާނު ވިދާޅުވިއެވެ: ތިއީ ކާކުތޯއެވެ؟ ތިމަން ދެންނެވީމެވެ: ތިމަންނައީ ޢަބްދު ﷲ ބިން ޙުނައިނެވެ. ތިބާގެ ގާތަށް ތިމަން ފޮނުއްވީ ޢިބްނު ޢައްބާސެވެ. ރަސޫލު ﷲ މުހްރިމަކަށް ވެވަޑައިގެން ހުންނަވިކޮށް އިސްތަށިފުޅު ދޮންނެވީ ކިހިނަކުންތޯ އިބްނު ޢައްބާސް އައްސަވައެވެ. ދެން އަބޫ އައްޔޫބު އެ ފޭރާން މަތީ އަތް އަޅުއްވާފައި، އެކަލޭގެފާނުގެ ބޯ ތިމަންނައަށް ދެއްކޭވަރަށް އެ ފަރުދާ ތިރިކުރެއްވިއެވެ. އަދި އެކަލޭގެފާނުގެ ބޮލަށް ފެން އަޅުއްވަން މީހަކު ގާތު ދެންނެވުމުން، އެކަން ކުރެވުނެވެ. ދެން އެކަލޭގެފާނުގެ ދެ އަތުން ބޮލުގެ ކުރިމަތިން ފަހަތަށާއި، ފަހަތުން ކުރިމައްޗަށް އަތް އުނގުޅާލެއްވިއެވެ. އެއަށްފަހު ދެންނެވިއެވެ: ތިމަންނައަށް ފެނުނީ ނަބިއްޔާ ﷺ މިފަދައިން ކަން ކުރައްވަނިކޮށެވެ.\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ދެން އިބްނު ޢައްބާސް ގާތު އަލްމިސްވަރު ދެންނެވިއެވެ: ދުވަހަކުވެސް ތިބާއާ ޖަދަލެއް ނުކުރާނަމެވެ.","[البخاري 1840، ومسلم 1205\n\nمسلم 1205]"],["#246","كتاب الحج","ހައްޖުގެ ފޮތް","باب فسخ الحج إلى العمرة","ހައްޖު ފަސްޚުކޮށް އުމްރާއަކަށް ބަދަލު ކުރުމުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رَضِيَ اللَّهُ عَنْهُمَا قَالَ (أَهَلَّ النَّبِيُّ ﷺ وَأَصْحَابُهُ بِالْحَجِّ وَلَيْسَ مَعَ أَحَدٍ مِنْهُمْ هَدْيٌ غَيْرَ النَّبِيِّ ﷺ وَطَلْحَةَ، وَقَدِمَ عَلِيُّ - رَضِيَ اللَّهُ عَنْهُ - مِنْ الْيَمَنِ. فَقَالَ: أَهْلَلْتُ بِمَا أَهَلَّ بِهِ النَّبِيُّ ﷺ فَأَمَرِ النَّبِيُّ ﷺ أَصْحَابَهُ: أَنْ يَجْعَلُوهَا عُمْرَةً، فَيَطُوفُوا ثُمَّ يُقَصِّرُوا وَيَحِلُّوا، إلاَّ مَنْ كَانَ مَعَهُ الْهَدْيُ فَقَالُوا: نَنْطَلِقُ إلَى «مِنىً» وَذَكَرُ أَحَدِنَا يَقْطُرُ؟ فَبَلَغَ ذَلِكَ النَّبِيَّ ﷺ فَقَالَ: لَوْ اسْتَقْبَلْتُ مِنْ أَمْرِي مَا اسْتَدْبَرْتُ مَا أَهْدَيْتُ، وَلَوْلا أَنَّ مَعِي الْهَدْيَ لأَحْلَلْتُ. وَحَاضَتْ عَائِشَةُ. فَنَسَكَتِ الْمَنَاسِكَ كُلَّهَا، غَيْرَ أَنَّهَا لَمْ تَطُفْ بِالْبَيْتِ. فَلَمَّا طَهُرَتْ وَطَافَتْ بِالْبَيْتِ قَالَتْ: يَا رَسُولَ اللَّهِ، يَنْطَلِقُونَ بِحَجٍّ وَعُمْرَةٍ، وَأَنْطَلِقُ بِحَجٍّ فَأَمَرَ عَبْدَ الرَّحْمَنِ بْنَ أَبِي بَكْرٍ: أَنْ يَخْرُجَ مَعَهَا إلَى التَّنْعِيمِ فَاعْتَمَرَتْ بَعْدَ الْحَجِّ).","ޖާބިރު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: (ޛުލް ޙުލައިފާގައި) ނަބިއްޔާ ﷺ އާއި އެކަލޭގެފާނުގެ ސަހާބީން އިހްރާމް ބަންނަވާ ހައްޖަށް ތަލްބިޔާ ވިދާޅުވިއެވެ. (އެބަހީ: ލައްބައިކަ ހައްޖަން ވިދާޅުވިއެވެ.) އަދި ނަބިއްޔާ ﷺ އާއި ޠަލްޙާ ފިޔަވައި އެއިން އެއްވެސް ބޭކަލަކު (ރަށުން މައްކާއާ ހަމައަށް) ހަދްޔު ނުގެންނަވައެވެ. އަދި ޢަލީ ޔަމަނުން (މައްކާއަށް ހަދްޔުއާއެކު) ވަޑައިގަންނަވައިގެން ދެންނެވިއެވެ: ތިމަންނަ އިހްރާމް ބެންނެވީ، ނަބިއްޔާ ﷺ އިހްރާމް ބެންނެވި ކަމަކަށެވެ. ދެން ނަބިއްޔާގެ ސަހާބީންގެ ހައްޖު އުމްރާއަކަށް ބަދަލު ކުރަން ނަބިއްޔާ އެންގެވިއެވެ. އަދި އެބައިމީހުން ތަވާފު ކުރުމަށާއި، އެއަށްފަހު ބޯކޮށައި އިހްރާމް ކެނޑުމަށް އެނގެވިއެވެ. އެއީ އެ މީހަކާއެކު ހަދްޔު ގެނައި މީހަކު މެނުވީއެވެ. މީސްތަކުން (އެބައިމީހުންގެ މެދުގައި) ދެންނެވިއެވެ: ތިމަންމެންގެ ލަދުވެތި ގުނަވަނުން ތިކިޖަހާ ހާލު (އެބަހީ: އަންހެނުންނާއެކު ޖިމާއުވުމަށް ފަހު) މިނާއަށް ދާނީ (ފަހެ ކިހިނަކުންހެއްޔެވެ؟) (ނޯޓު: އުމްރާއަށް ފަހު އިހްރާމް ކެނޑުމުން ޖިމާއުވުމާއި އެހެނިހެން ކަންކަން ހުއްދަވާނެއެވެ. ދެން ހައްޖު މަހުގެ އަށްވަނަ ދުވަހު ހައްޖަށް އިހްރާމް ބަނދެގެން ދާނީ މިނާއަށެވެ. އެ ގޮތުން ސަހާބީން އެފަދައިން ދެންނެވީ، މި ދެ ކަންކަން ދެމެދު މުއްދަތު ކުރުކަމުން އެކަމާމެދު އަޖައިބުވެގެންނެވެ. އަދި ޖާހިލިއްޔަ ޒަމާނުގައި މީހުން ގަބޫލު ކުރި ގޮތުގައި ހައްޖު މަހުގައި އުމްރާ ކުރުމަކީ ބޮޑުވެގެންވާ ފާފައެކެވެ.) ދެން ނަބިއްޔާގެ އަރިހަށް އެ ވާހަކަ ފޯރުމުން ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާއަށް މީހާރު އެނގިވަޑައިގަންނަވާ ކަންތައް، ކުރިން އެނގިވަޑައިގެންނެވި ނަމަ، ހަދްޔު ނުގެންނެވީހެވެ. އަދި ތިމަން ނަބިއްޔާއާއެކު ހަދްޔު ނުވާނަމަ ތިމަން ނަބިއްޔާވެސް އިހްރާމް ކެނޑުއްވީސްކަން ކަށަވަރެވެ.“ އަދި ޢާއިޝާ ހައިޟުވެ ވަޑައިގެންނެވިއެވެ. ވީމާ ގެފުޅު ވަށާ ތަވާފު ކުރުން ފިޔަވައި ދެން ހުރި ހުރިހާ އަޅުކަމެއް އެ ކަމަނާ އަދާ ކުރެއްވިއެވެ. އަދި އެ ކަމަނާ ހައިޟުން ތާހިރުވެ ވަޑައިގަތުމުން ގެފުޅު ވަށާ ތަވާފު ވެސް ކުރެއްވިއެވެ. އެ ކަމަނާ ވިދާޅުވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. މީސްތަކުން އެ ދަނީ ހައްޖާއި އުމްރާ އަދާކޮށްގެންނެވެ. އެކަމަކު ތިމަން ކަމަނާ މި ވަޑައިގަންނަވަން ޖެހެނީ (ހަމައެކަނި) ހައްޖު އަދާކުރުމުންނެވެ. ދެން ޢަބްދުއްރަޙްމާން ބިން އަބޫ ބަކުރު ގާތު ނަބިއްޔާ އެންގެވީ، އެ ކަމަނާ ގޮވައިގެން (މައްކާގެ ރަށްބޭރުގައިވާ) ތަންޢީމަށް ވަޑައިގަންނަވަންށެވެ. (އެއީ މައްކާގައި ހުރި މީހަކަށް އުމްރާ ކުރެވޭނީ މައްކާއިން ބޭރަށް ނުކުމެ އިހްރާމް ބަންދެގެންނެވެ.) ދެން ފަހެ އެ ކަމަނާގެ ހައްޖަށް ފަހު އުމްރާ ވެސް އަދާ ކުރެއްވިއެވެ.","[البخاري 1651 واللفظ له، ومسلم 1216]"],["#247","كتاب الحج","ހައްޖުގެ ފޮތް","باب فسخ الحج إلى العمرة","ހައްޖު ފަސްޚުކޮށް އުމްރާއަކަށް ބަދަލު ކުރުމުގެ ބާބު","عَنْ جَابِرٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (قَدِمْنَا مَعَ رَسُولِ اللَّهِ ﷺ وَنَحْنُ نَقُولُ: لَبَّيْكَ بِالْحَجِّ. فَأَمَرَنَا رَسُولُ اللَّهِ ﷺ فَجَعَلْنَاهَا عُمْرَةً).","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺގެ އަރިހުގައި ތިމަންމެން (މައްކާއަށް ހައްޖަށް) ވަޑައިގަތީ ’ލައްބައިކަ ބިލްހައްޖި‘ މިފަދައިން ވިދާޅުވެގެންނެވެ. (އެބަހީ: ހައްޖަށް އިހްރާމް ބަނދެގެންނެވެ.) ދެން ރަސޫލު ﷲ ﷺ އެންގެވުމުން ތިމަންމެންގެ ހައްޖު އުމްރާއަކަށް ބަދަލުކުރީމެވެ.","[البخاري 1570، ومسلم 1216]"],["#248","كتاب الحج","ހައްޖުގެ ފޮތް","باب فسخ الحج إلى العمرة","ހައްޖު ފަސްޚުކޮށް އުމްރާއަކަށް ބަދަލު ކުރުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (قَدِمَ رَسُولُ اللَّهِ ﷺ وَأَصْحَابُهُ صَبِيحَةَ رَابِعَةٍ. فَأَمَرَهُمْ أَنْ يَجْعَلُوهَا عُمْرَةً. فَقَالُوا: يَا رَسُولَ اللَّهِ، أَيُّ الْحِلِّ؟ قَالَ: الْحِلُّ كُلُّهُ).","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ޛުލް ޙިއްޖާ މަހުގެ ހަތަރުވަނަ ދުވަހުގެ ހެނދު، ރަސޫލު ﷲ ﷺ އާއި އެކަލޭގެފާނުގެ ސަހާބީން (މައްކާއަށް) ވަޑައިގަތީ ހައްޖަށް އިހްރާމް ބަނދެގެންނެވެ. ދެން އެބައިމީހުން (އިހްރާމް ކަނޑާ) ހައްޖު އުމްރާއަކަށް ބަދަލު ކުރަން ނަބިއްޔާ އެންގެވިއެވެ. (އެއާމެދު ކަންބޮޑުވެގެން) އެބައިމީހުން ދެންނެވިއެވެ. އޭ ﷲގެ ރަސޫލާއެވެ. ކޮން އިހްރާމް ކެނޑުމެއްތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ހުރިހާ ކަމަކުންވެސް އިހްރާމް ކަނޑާށެވެ.“","[البخاري 1564، ومسلم 1240]"],["#249","كتاب الحج","ހައްޖުގެ ފޮތް","باب فسخ الحج إلى العمرة","ހައްޖު ފަސްޚުކޮށް އުމްރާއަކަށް ބަދަލު ކުރުމުގެ ބާބު","عَنْ عُرْوَةَ بْنِ الزُّبَيْرِ قَالَ: (سُئِلَ أُسَامَةُ بْنُ زَيْدٍ - وَأَنَا جَالِسٌ - كَيْفَ كَانَ رَسُولُ اللَّهِ ﷺ يَسِيرُ حِينَ دَفَعَ؟ قَالَ: كَانَ يَسِيرُ الْعَنَقَ. فَإِذَا وَجَدَ فَجْوَةً نَصَّ).","ޢުރްވާ ބިން ޒުބައިރު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ތިމަން އިށީނދެ ވަނިކޮށް އުސާމާ ބިން ޒައިދު ގާތު އެއްސެވުނެވެ: ހައްޖަތުލް ވަދާއުގައި ރަސޫލު ﷲ ﷺ (ޢަރަފާތުން މުޒްދަލިފާއަށް ޖަމަލުކޮޅުގައި) ވަޑައިގަތީ ކިހިނަކުންތޯއެވެ؟ އުސާމާ ދެންނެވިއެވެ: ނަބިއްޔާ ވަޑައިގަތީ މެދުމިނެއްގެ ދުވެއްޔެއްގައެވެ. ދެން ޖާގައެއް ފެނިވަޑައިގަންނަވާ ނަމަ ދުވެލި ބާރުކުރައްވައެވެ.","[البخاري 1666 واللفظ له، ومسلم 1286]"],["#250","كتاب الحج","ހައްޖުގެ ފޮތް","باب فسخ الحج إلى العمرة","ހައްޖު ފަސްޚުކޮށް އުމްރާއަކަށް ބަދަލު ކުރުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا: (أَنَّ رَسُولَ اللَّهِ ﷺ وَقَفَ فِي حَجَّةِ الْوَدَاعِ. فَجَعَلُوا يَسْأَلُونَهُ. فَقَالَ: رَجُلٌ لَمْ أَشْعُرْ، فَحَلَقْتُ قَبْلَ أَنْ أَذْبَحَ؟ قَالَ. اذْبَحْ وَلا حَرَجَ. وَجَاءَ آخَرُ، فَقَالَ: لَمْ أَشْعُرْ، فَنَحَرْتُ قَبْلَ أَنْ أَرْمِيَ؟ قَالَ: ارْمِ وَلا حَرَجَ. فَمَا سُئِلَ يَوْمَئِذٍ عَنْ شَيْءٍ قُدِّمَ وَلا أُخِّرَ إلاَّ قَالَ: افْعَلْ وَلا حَرَجَ).","ޢަބްދު ﷲ ބިން ޢަމްރު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ހައްޖަތުލް ވަދާއުގައި މީނާގައި މަޑު ކުރެއްވިއެވެ. ފަހެ މީސްތަކުން އައިސް ނަބިއްޔާ އަރިހު ސުވާލު ކުރަމުން ދިޔައެވެ. އެ ގޮތުން މީހަކު ދެންނެވިއެވެ: ތިމަންނައަށް ނޭނގި ހުއްޓާ، ހަދްޔު ކަތިލުމުގެ ކުރިން ތިމަންނައަށް ބޯ ބޭލިއްޖެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”(މިހާރު ގޮސް ހަދްޔު) ކަތިލާށެވެ. މައްސަލައެއް ނެތެވެ.“ ދެން އެހެން މީހަކު އައިސް ދެންނެވިއެވެ: ތިމަންނައަށް ނޭނގި ހުއްޓާ، ޖަމްރާއަށް ހިލަ އުކުމުގެ ކުރިން ތިމަންނައަށް ޖަމަލު ކަތިލެވިއްޖެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”(މިހާރު ގޮސް ޖަމްރާއަށް) ހިލަ އުކާށެވެ. މައްސަލައެއް ނެތެވެ.“ ފަހެ (މީހަކު ހައްޖުގެ) ކަމެއް އިސްކޮށް ނުވަތަ ފަސްކޮށް ކުރެވިގެން އެ ދުވަހު ނަބިއްޔާ ﷺގެ އަރިހު ސުވާލު ކުރެވިއްޖެ ނަމަ، މިފަދައިން މެނުވީ ޖަވާބު ނުދެއްވައެވެ: ކުރާށެވެ. މައްސަލައެއް ނެތެވެ.","[البخاري 1736 واللفظ له، ومسلم 1306]"],["#251","كتاب الحج","ހައްޖުގެ ފޮތް","باب فسخ الحج إلى العمرة","ހައްޖު ފަސްޚުކޮށް އުމްރާއަކަށް ބަދަލު ކުރުމުގެ ބާބު","عنْ عَبْدِ الرَّحْمَنِ بْنِ يَزِيدَ النَّخَعِيِّ: (أَنَّهُ حَجَّ مَعَ ابْنِ مَسْعُودٍ. فَرَآهُ رَمَى الْجَمْرَةَ الْكُبْرَى بِسَبْعِ حَصَيَاتٍ فَجَعَلَ الْبَيْتَ عَنْ يَسَارِهِ، وَمِنًى عَنْ يَمِينِهِ. ثُمَّ قَالَ: هَذَا مَقَامُ الَّذِي أُنْزِلَتْ عَلَيْهِ سُورَةُ الْبَقَرَةِ ﷺ).","ޢަބްދުއްރަޙްމާން ބިން ޔަޒީދު އައްނަޚަޢީގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން އެކަލޭގެފާނު އިބްނު މަސްޢޫދު رَضِيَ اللَّهُ عَنْهُ އާއެކު ހައްޖު ކުރެއްވިއެވެ. އަދި އިބްނި މަސްޢޫދު ބޮޑު ޖަމަރާއަށް ހަތް ހިލަކޮޅު އުއްކެވިތަން އެކަލޭގެފާނަށް ފެނުނެވެ. ފަހެ އިބްނު މަސްޢޫދުގެ ވާތް ފަރާތުގައި ވަނީ ގެފުޅެވެ. އަދި ކަނާތް ފަރާތުގައި ވަނީ މިނާއެވެ. ދެން އިބްނު މަސްޢޫދު ވިދާޅުވިއެވެ: މިއީ ނަބިއްޔާ ﷺގެ މައްޗަށް ސޫރަތުލް ބަގަރާ ބާވައިލެއްވުނު ތަނެވެ.","[البخاري 1749 واللفظ له، ومسلم 1296]"],["#252","كتاب الحج","ހައްޖުގެ ފޮތް","باب فسخ الحج إلى العمرة","ހައްޖު ފަސްޚުކޮށް އުމްރާއަކަށް ބަދަލު ކުރުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: (اللَّهُمَّ ارْحَمْ الْمُحَلِّقِينَ. قَالُوا: وَالْمُقَصِّرِينَ يَا رَسُولَ اللَّهِ. قَالَ: اللَّهُمَّ ارْحَمْ الْمُحَلِّقِينَ. قَالُوا وَالْمُقَصِّرِينَ يَا رَسُولَ اللَّهِ. قَالَ: وَالْمُقَصِّرِينَ).","ޢަބްދު ﷲ ބިން ޢަމްރު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”اللَّهُمَّ ارْحَمْ الْمُحَلِّقِينَ (ޔާ ﷲ! ބޯ ބާލާ މީހުންނަށް ރަހުމަތް ލައްވާށިއެވެ.)“ މީސްތަކުން ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ބޯ ކޮށާ މީހުންނޭ ވެސް (ވިދާޅުވާށެވެ.) ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”اللَّهُمَّ ارْحَمْ الْمُحَلِّقِينَ (ޔާ ﷲ! ބޯ ބާލާ މީހުންނަށް ރަހުމަތް ލައްވާށިއެވެ.)“ މީސްތަކުން ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ބޯ ކޮށާ މީހުންނޭ ވެސް (ވިދާޅުވާށެވެ.) ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”اللَّهُمَّ ارْحَمْ الْمُحَلِّقِينَ (ޔާ ﷲ! ބޯ ބާލާ މީހުންނަށް ރަހުމަތް ލައްވާށިއެވެ.)“ މީސްތަކުން ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ބޯ ކޮށާ މީހުންނޭ ވެސް (ވިދާޅުވާށެވެ.) ނަބިއްޔާ ދެން ޙަދީޘް ކުރެއްވިއެވެ: ”އަދި ބޯ ކޮށާ މީހުންވެސް މެއެވެ.“","[البخاري 1727، ومسلم 1301 واللفظ له]"],["#253","كتاب الحج","ހައްޖުގެ ފޮތް","باب فسخ الحج إلى العمرة","ހައްޖު ފަސްޚުކޮށް އުމްރާއަކަށް ބަދަލު ކުރުމުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: (حَجَجْنَا مَعَ النَّبِيِّ ﷺ فَأَفَضْنَا يَوْمَ النَّحْرِ. فَحَاضَتْ صَفِيَّةُ. فَأَرَادَ النَّبِيُّ ﷺ مِنْهَا مَا يُرِيدُ الرَّجُلُ مِنْ أَهْلِهِ. فَقُلْتُ: يَا رَسُولَ اللَّهِ، إنَّهَا حَائِضٌ. قَالَ: أَحَابِسَتُنَا هِيَ؟ قَالُوا: يَا رَسُولَ اللَّهِ، إنَّهَا قَدْ أَفَاضَتْ يَوْمَ النَّحْرِ قَالَ: اُخْرُجُوا).\n\nوَفِي لَفْظٍ: قَالَ النَّبِيُّ ﷺ: (عَقْرَى، حَلْقَى. أَطَافَتْ يَوْمَ النَّحْرِ؟ قِيلَ: نَعَمْ. قَالَ: فَانْفِرِي).","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺގެ އަރިހުގައި ތިމަންމެން ހައްޖު ކުރެއްވީމެވެ. ފަހެ ނަޙްރުގެ ދުވަހު (އެބަހީ: އަޟްހާ އީދު ދުވަހު) ތިމަންމެން ތަވާފުލް އިފާޟާ ކުރައްވާ (ހައްޖުވެ ނިމުނީމެވެ.) ފަހެ ޞަފިއްޔާ ހައިޟުވެ ވަޑައިގެންނެވިއެވެ. އަދި (ނަބިއްޔާއަށް އެކަން އެނގިވަޑައި ނުގަންނަވާ ހާލު) މީހަކު، އެ މީހެއްގެ އަނބިމީހާ ގާތުން އެދޭ ކަންތައް (އެބަހީ: ޖިމާއުވުން) އެ ކަމަނާގެ ކިބައިން ނަބިއްޔާ އެދިވަޑައިގެންނެވިއެވެ. (ނަބިއްޔާ ހީފުޅު ކުރެއްވީ އެ ކަމަނާ ތަވާފުލް އިފާޟާ ކުރައްވާ ހައްޖުވެ ނިމުނީކަމަށެވެ.) ތިމަން ކަމަނާ ދެންނެވީމެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ހަމަކަށަވަރު އެ ކަމަނާ ހައިޟުވެ ވަޑައިގެންފިއެވެ. (ދެން ނަބިއްޔާ ހީފުޅު ކުރެއްވީ ހައިޟުގެ ސަބަބުން އެ ކަމަނާ އަދި ތަވާފުލް އިފާޟާ ނުކުރައްވާ ކަމަށެވެ.) ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ ކަމަނާ (ހައިޟުން ތާހިރުވެ، ތަވާފުލް އިފާޟާކޮށް ނިމެންދެން) ތިމަންމެން (މަދީނާއަށް އެނބުރި ދިޔަނުދީ، މައްކާގައި) މަޑުކުރަން ޖެހެނީތޯއެވެ؟“ (ނަބިއްޔާގެ އަނބިކަނބަލުން) ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން ނަޙްރުގެ ދުވަހުގައި އެ ކަމަނާ ތަވާފުލް އިފާޟާކޮށް ނިންމައިފިއެވެ. (އެބަހީ: ހައިޟުވެވަޑައިގަތީ އޭގެ ފަހުންނެވެ.) ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެހެންވިއްޔާ ތިޔަބައިމީހުން (މަދީނާއަށް ފުރަން) ނުކުންނަވާށެވެ.“ \n\nއަދި އެހެން ރިވާޔަތެއްގައި ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ޢަގްރަން ޚަލްގަންއެވެ. (ނޯޓު: މިއީ ކަމަކާމެދު އަޖައިބުވުމުން އަރަބީންގެ ދޫމަތިން ބުނެ އުޅޭ އިބާރާތެކެވެ. ބަދު ދުއާ ކުރުމުގެ މާނައިގައި ބުނާ ބަހެއް ނޫނެވެ. ޢަގްރަކީ ޒަހަމްވުން ނުވަތަ ދަރިމައި ނުވުމެވެ. އަދި ޙަލްގަކީ ބޯބޭލުން ނުވަތަ ކަރުގައި ރިއްސުމެވެ.) ނަޙްރުގެ ދުވަހުގައި އެ ކަމަނާ ތަވާފު ކުރެއްވިތޯއެވެ؟“ (ނަބިއްޔާގެ އަނބިކަނބަލުން) ދެންނެވިއެވެ: އާދޭހެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެހެންވިއްޔާ (މަދީނާއަށް ފުރަން) ކަމަނާ ނުކުންނަވާށެވެ.“","[البخاري 1733 واللفظ له، ومسلم 1211\n\nالبخاري 1771 واللفظ له، ومسلم 1211]"],["#254","كتاب الحج","ހައްޖުގެ ފޮތް","باب فسخ الحج إلى العمرة","ހައްޖު ފަސްޚުކޮށް އުމްރާއަކަށް ބަދަލު ކުރުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (أُمِرَ النَّاسُ أَنْ يَكُونَ آخِرُ عَهْدِهِمْ بِالْبَيْتِ، إلاَّ أَنَّهُ خُفِّفَ عَنْ الْمَرْأَةِ الْحَائِضِ).","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: މީސްތަކުން (މައްކާ ދޫކޮށް ދިޔުމުގެ ކުރިން)، އެބައިމީހުންގެ ހައްޖުގައި ކުރާ އެންމެ ފަހު ކަމަކަށް ގެފުޅުގައި (ތަވާފުލް ވަދާއު ކުރައްވަން) އެބައިމީހުންނަށް އެންގެވުނެވެ. އެހެނަސް ހައިޟުވެފައިވާ އަންހެނަކަށް ލުއިކުރުމުގެ ގޮތުން އެ ކަނބުލޭގެ މައްޗަށް އެކަންތައް ކުރުމެއް ނުވެއެވެ.","[البخاري 1755، ومسلم 1211 واللفظ له]"],["#255","كتاب الحج","ހައްޖުގެ ފޮތް","باب فسخ الحج إلى العمرة","ހައްޖު ފަސްޚުކޮށް އުމްރާއަކަށް ބަދަލު ކުރުމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (اسْتَأْذَنَ الْعَبَّاسُ بْنُ عَبْدِ الْمُطَّلِبِ رَسُولَ اللَّهِ ﷺ: أَنْ يَبِيتَ بِمَكَّةَ لَيَالِيَ مِنىً، مِنْ أَجْلِ سِقَايَتِهِ فَأَذِنَ لَهُ).","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އަލްޢައްބާސް ބިން ޢަބްދުލް މުއްޠަލިބު (ހައްޖުވެރިންނަށް) ފެން ދިނުމަށްޓަކައި، މިނާގެ ރޭތަކުގައި އެކަލޭގެފާނު މައްކާގައި މަޑުކުރެއްވުމަށް، ރަސޫލު ﷲ ﷺގެ އަރިހުން ހުއްދައަށް އެދިވަޑައިގަތެވެ. އަދި ނަބިއްޔާ އެކަލޭގެފާނަށް ހުއްދަ ދެއްވިއެވެ.","[البخاري 1643 واللفظ له، ومسلم 1315]"],["#256","كتاب الحج","ހައްޖުގެ ފޮތް","باب فسخ الحج إلى العمرة","ހައްޖު ފަސްޚުކޮށް އުމްރާއަކަށް ބަދަލު ކުރުމުގެ ބާބު","وَعَنْهُ - أَيْ عَنْ ابْنِ عُمَرَ - قَالَ: (جَمَعَ النَّبِيُّ ﷺ بَيْنَ الْمَغْرِبِ وَالْعِشَاءِ «بِجَمْعٍ»، لِكُلِّ وَاحِدَةٍ مِنْهُمَا إقَامَةٌ. وَلَمْ يُسَبِّحْ بَيْنَهُمَا، وَلا عَلَى إثْرِ وَاحِدَةٍ مِنْهُمَا).","އަދިވެސް ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: މުޒްދަލިފާގައި ނަބިއްޔާ ﷺ މަޣްރިބާއި އިޝާ ނަމާދު ކުރެއްވީ ޖަމްއުކޮށެވެ. އެއިން ކޮންމެ ނަމާދެއްގައިވެސް ގަމަތް ދެއްވިއެވެ. އަދި އެ ދެ ނަމާދު ދެމެދުގައި ސުންނަތް ނަމާދެއް ނުކުރައްވައެވެ. އަދި އެ ދެ ނަމާދަށް ފަހު ވެސް ސުންނަތް ނަމާދެއް ނުކުރައްވައެވެ.","[البخاري 1673، ومسلم 1288]"],["#257","كتاب الحج","ހައްޖުގެ ފޮތް","باب المحرم يأكل من صيد الحلال","ހަލާލު ޝިކާރައަކުން މުހްރިމަކު ކެއުމުގެ ބާބު","عَنْ أَبِي قَتَادَةَ الأَنْصَارِيِّ: (أَنَّ رَسُولَ اللَّهِ ﷺ خَرَجَ حَاجَّاً. فَخَرَجُوا مَعَهُ. فَصَرَفَ طَائِفَةً مِنْهُمْ - فِيهِمْ أَبُو قَتَادَةَ - وَقَالَ: خُذُوا سَاحِلَ الْبَحْرِ، حَتَّى نَلْتَقِيَ. فَأَخَذُوا سَاحِلَ الْبَحْرِ فَلَمَّا انْصَرَفُوا أَحْرَمُوا كُلُّهُمْ، إلاَّ أَبَا قَتَادَةَ، فَلَمْ يُحْرِمْ. فَبَيْنَمَا هُمْ يَسِيرُونَ إذْ رَأَوْا حُمُرَ وَحْشٍ. فَحَمَلَ أَبُو قَتَادَةَ عَلَى الْحُمُرِ. فَعَقَرَ مِنْهَا أَتَانَاً. فَنَزَلْنَا فَأَكَلْنَا مِنْ لَحْمِهَا. ثُمَّ قُلْنَا: أَنَأْكُلُ لَحْمَ صَيْدٍ، وَنَحْنُ مُحْرِمُونَ؟ فَحَمَلْنَا مَا بَقِيَ مِنْ لَحْمِهَا فَأَدْرَكْنَا رَسُولَ اللَّهِ ﷺ. فَسَأَلْنَاهُ عَنْ ذَلِكَ؟ فَقَالَ: مِنْكُمْ أَحَدٌ أَمَرَهُ أَنْ يَحْمِلَ عَلَيْهَا، أَوْ أَشَارَ إلَيْهَا؟ قَالُوا: لا. قَالَ: فَكُلُوا مَا بَقِيَ مِنْ لَحْمِهَا).\n\nوَفِي رِوَايَةٍ: (قَالَ: هَلْ مَعَكُمْ مِنْهُ شَيْءٌ؟ فَقُلْت: نَعَمْ. فَنَاوَلْتُهُ الْعَضُدَ، فَأَكَلَ مِنْهَا).","އަބޫ ޤަތާދާ އަލްއަންޞާރީ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ (ގެފުޅަށް ޒިޔާރާތް ކުރެއްވުމަށް) އުމްރާއަށް ނުކުމެވަޑައިގެންނެވިއެވެ. އަދި މީސްތަކުންވެސް ނަބިއްޔާގެ އަރިހު ނުކުތެވެ. (ނޯޓު: މިއީ ޞުލްޙުލް ޙުދައިބިއްޔާގެ ފަހަރެވެ. އަދި މައްކާގެ މުޝްރިކުން ހަނގުރާމައަށް ނުކުމެފާނެކަމުގެ ބިރު އޮތެވެ.) އެ ގޮތުން އެބައިމީހުންގެ ތެރެއިން އެއްބަކު ވަކިކޮށް އެހެން މަގަކުން ފޮނުއްވިއެވެ. މިބައިމީހުންގެ ތެރޭގައި އަބޫ ޤަތާދާ ހިމެނުނެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަންމެން ބައްދަލުވަންދެން، ކަނޑުގެ އައްސޭރިފަށުންލާފައި ތިޔަބައިމީހުން ދަތުރު ކުރާށެވެ.“ ދެން އެބައިމީހުން އައްސޭރިފަށުންލާފައި ހިނގައްޖެއެވެ. އެ ގޮތުން މިބައިމީހުން ނުކުންނެވި އިރު، އަބޫ ޠަލްޙާ ފިޔަވައި އެބައިމީހުން އެންމެން އިހްރާމް ބަނދެފިއެވެ. އެކަލޭގެފާނު އިހްރާމް ނުބަންނަވައެވެ. ދެން އެބައިމީހުން އެހެން ދަނިކޮށް، ވަލުހިމާރެއް (އެބަހީ: އޮނެޖާއެއް) އެބައިމީހުންނަށް ފެނިއްޖެއެވެ. ފަހެ އަބޫ ޤަޠާދާ ހިމާރަކަށް ސަވާރުވެގެން އެތަނުން އަންހެން ވަލުހިމާރަކަށް ހަމަލާދީ ޝިކާރަކޮށްފިއެވެ. ދެން ތިމަންމެން އެ ތަނުގައި މަޑުކޮށް އެ ވަލުހިމާރުގެ މަސް ކެއީމެވެ. އެއަށްފަހު ތިމަންމެން ދެންނެވީމެވެ: ތިމަންމެން އިހްރާމް ބަނދެގެން ހުރެ، ޝިކާރަކުޅަ މަސް ކެވޭނެހެއްޔެވެ؟ (އެހެނީ: މުހްރިމުންނަށް ޝިކާރަ ކުރުން ހަރާމްވެގެން ވެއެވެ.) ދެން އޭގެ މަހުގެ ބާކީ އޮތް ބައި ހިފައިގެން ގޮސް، ރަސޫލު ﷲ ﷺ އާ އަރާހަމަ ކުރީމެވެ. އަދި އެކަމާ ބެހޭ ގޮތުން ސުވާލު ކުރީމެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ކުރެ އެކަކުވެސް އަބޫ ގަތާދާ ގާތު އެ ޝިކާރަ ކުރަން އެންގެވިންތޯއެވެ؟ ނޫނީ އެއަށް އިޝާރާތް ކުރެއްވިންތޯއެވެ؟ އެބައިމީހުން ދެންނެވިއެވެ: ނޫނެކެވެ. ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އެހެންވިއްޔާ އެ މަހުގެ ބާކީ އޮތް އެއްޗަކުން ކާށެވެ.“\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”އެ މަހުގެ އެއްވެސް އެތިކޮޅެއް ތިޔަބައިމީހުންނާއެކު ވޭތޯއެވެ؟“ ތިމަން ދެންނެވީމެވެ: އާދޭހެވެ. ދެން ތިމަންމެން އޭގެ ފައި ދެއްވުމުން ނަބިއްޔާ ފަރީއްކުޅުވިއެވެ.","[البخاري 1824، ومسلم 1196\n\nالبخاري 2570 واللفظ له، ومسلم 1196]"],["#258","كتاب الحج","ހައްޖުގެ ފޮތް","باب المحرم يأكل من صيد الحلال","ހަލާލު ޝިކާރައަކުން މުހްރިމަކު ކެއުމުގެ ބާބު","عَنْ الصَّعْبِ بْنِ جَثَّامَةَ اللَّيْثِيِّ - رَضِيَ اللَّهُ عَنْهُ - (أَنَّهُ أَهْدَى إلَى النَّبِيِّ ﷺ حِمَاراً وَحْشِيَّاً، وَهُوَ بِالأَبْوَاءِ أَوْ بِوَدَّانَ - فَرَدَّهُ عَلَيْهِ. فَلَمَّا رَأَى مَا فِي وَجْهِي، قَالَ: إنَّا لَمْ نَرُدَّهُ عَلَيْكَ إلاَّ أَنَّا حُرُمٌ).\n\nوَفِي لَفْظٍ لِمُسْلِمٍ «رِجْلَ حِمَارٍ» وَفِي لَفْظٍ «شِقَّ حِمَارٍ» وَفِي لَفْظٍ «عَجُزَ حِمَارٍ».","އައްޞަޢުބް ބިން ޖައްޘާމާ އައްލައިޘީ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން އެކަލޭގެފާނު، ވަލުހިމާރު (އެބަހީ: އޮނެޖާއެއްގެ) މަސްކޮޅެއް ނަބިއްޔާ ﷺ އަށް ހަދިޔާ ކުރެއްވިއެވެ. އޭރު ނަބިއްޔާ ވަނީ (މައްކާއާއި މަދީނާއާ ދެމެދުގައިވާ) އަބްވާ ނުވަތަ ވައްދާނުގައެވެ. ފަހެ އައްޞަޢުބުގެ ހަދިޔާ ނަބިއްޔާ އަނބުރާ ރައްދު ކުރެއްވިއެވެ. ދެން އައްޞަޢުބުގެ މޫނުމަތިން (ދެރަވެފައި ހުރިކަން) ފެނިވަޑައިގަތުމުން ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަންމެން އިހްރާމް ބަނދެގެން ހުރިކަމަށްޓަކައި ނޫނީވިއްޔާ، އެ ހަދިޔާ އަނބުރާ ތިބާއަށް ރައްދު ނުކުރެއްވީހެވެ.“\n\nއަދި މުސްލިމުގެ ރިވާޔަތުގައި ވަނީ: ވަލުހިމާރެއްގެ ފަޔެކެވެ. އަދި އެހެން ރިވާޔަތެއްގައި ވަނީ: ވަލުހިމާރެއްގެ ބައެކެވެ. އަދި އެހެން ރިވާޔަތެއްގައި ވަނީ: ވަލުހިމާރެއްގެ ފަހަތު ފަޔެވެ.\n\n(ނޯޓު: ނަބިއްޔާ މި ހަދިޔާ ގަބޫލު ނުކުރެއްވި ސަބަބަކީ، އައްޞަޢުބް ޝިކާރަ ކުރީ ހަމަ ސީދާ ނަބިއްޔާއަށް އެ މަސް ދޭންވެގެން ކަމުގައި ވާތީއެވެ. އަދި މުހްރިމަކު އަމިއްލައަށް ޝިކާރަ ކުރުމާއި، ޝިކާރަ ކުރުމުގައި އެހީވުމާއި އިޝާރާތް ކުރުން ހަރާމްވުމުގެ އިތުރަށް، ސީދާ މުހްރިމަކަށްޓަކައި ނުވަތަ މުހްރިމަކަށް ދޭންވެގެން ވެސް ޝިކާރަ ކުރުން ހަރާމްވެގެން ވެއެވެ. އަދި އަބޫ ގްތާދާ ޝިކާރަ ކުރެއްވީ މުހްރިމަކަށް ނޫނެވެ. އެހެނަސް އެކަލޭގެފާނު މުހްރިމަކަށް ނުވާ ހާލު ޝިކާރަ ކުރެއްވީ އަމިއްލަ ނަފްސަށްޓަކާއެވެ. އެއަށްފަހު ކެއުމުގައި މުހްރިމުންނަށް މަސް ހަދިޔާ ކުރެއްވީއެވެ. ހުރިހާވެސް ތަފާތަކީ ނިޔަތެވެ.)\n\n(ނޯޓު: އޭގެ މަސް ކެއުން ހުއްދަވާ ވަލުހިމާރަކީ އޮނެޖާއެވެ. މިއީ އާދައިގެ ހިމާރާ މުޅިން ތަފާތު ޖަނަވާރެކެވެ. ވަލުހިމާރާ އާދައިގެ ހިމާރު ވައްތަރު ގޮތެއް ހުއްޓެއް ކަމަކު، ކުލައާއި ސައިޒުގައި ތަފާތުކަމެއް ވެއެވެ. އަދި ޒިބްރާ ނުވަތަ ކަފިހިއަހަކީ ވަލުހިމާރުގެ ބާވަތެކެވެ. އިންސާނުން ގެންގުޅޭ އާދައިގެ ހިމާރު، ފިލައިގެން ވަލުތެރޭގައި އުޅެ އޮޅުކަން ފިލައިދިޔަސް، އެއީ ވަލުހިމާރެއް ކަމުގައި ނުވާނެއެވެ. އަދި އޭގެ މަސް ކެއުންވެސް ހުއްދައެއް ނުވާނެއެވެ.)","[البخاري 1825 واللفظ له، ومسلم 1193\n\nمسلم 1193]"],["#259","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا عَنْ رَسُولِ اللَّهِ ﷺ أَنَّهُ قَالَ: (إذَا تَبَايَعَ الرَّجُلانِ، فَكُلُّ وَاحِدٍ مِنْهُمَا بِالْخِيَارِ مَا لَمْ يَتَفَرَّقَا وَكَانَا جَمِيعاً، أَوْ يُخَيِّرُ أَحَدُهُمَا الآخَرَ. فَتَبَايَعَا عَلَى ذَلِكَ. فَقَدْ وَجَبَ الْبَيْعُ). وَمَا فِي مَعْنَاهُ مِنْ حَدِيثِ","މާނައީ: ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ދެ މީހަކު ގަނެވިއްކާނަމަ، އެ ދެމީހުން ވަކިނުވާހައި ހިނދަކު، އެއިން ކޮންމެ މީހަކު ވެސް ވަނީ (އެ ވިޔަފާރީގެ މުއާމަލާތް ދެމެހެއްޓުން، ނުވަތަ އެ މުއާމަލާތް ބާތިލު ކުރުމަށްފަހު އެ ތަކެތި ރުޖޫއަ ކުރުމުގެ) އިޚްތިޔާރު ލިބިގެންނެވެ. އެއީ އެ ދެބައިމީހުން އެއްކޮށް ހުންނަހައި ހިނދަކުއެވެ. ނޫނީ އެއިން އެކަކު (އޭނާގެ އިޚްތިޔާރު ކުރުމުގެ ހައްގު ދޫކޮށް)، (ހަމައެކަނި) އަނެކަކަށް އިޚްތިޔާރު ކުރުމުގެ ހައްގު ދީފި ނަމައެވެ. އަދި (އެ ގޮތަށް) އަނެކަކަށް އިޚްތިޔާރު ކުރުމުގެ ހައްގު ދިނުމަށް ފަހު، ދެ މީހުން ވެސް އެ ގޮތުގެ މަތިން ވިޔަފާރި ކޮށްފިނަމަ، ފަހެ އެ މުއާމަލާތް ލާޒިމްވެގެން ދާނެއެވެ. އަދި އެ ދެމީހުން ވިޔަފާރިއެއް ކުރުމަށް ފަހު، އެއިން އެކަކުވެސް އެ މުއާމަލާތް ބާތިލުނުކޮށް ދެން ވަކިވެއްޖެ ނަމަ، ފަހެ އެ މުއާމަލާތް ލާޒިމްވެގެން ދާނެއެވެ.","[البخاري 2112، ومسلم 1531]"],["#260","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","","","عَنْ حَكِيمِ بْنِ حِزَامٍ رَضِيَ اللَّهُ عَنْهُ قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: (الْبَيِّعَانِ بِالْخِيَارِ مَا لَمْ يَتَفَرَّقَا - أَوْ قَالَ: حَتَّى يَتَفَرَّقَا - فَإِنْ صَدَقَا وَبَيَّنَا بُورِكَ لَهُمَا فِي بَيْعِهِمَا. وَإِنْ كَتَمَا وَكَذَبَا مُحِقَتْ بَرَكَةُ بَيْعِهِمَا).","ޙަކީމް ބިން ޙިޒާމް رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ގަނެވިއްކާ ދެ މީހުން ވަކިވުމަށް ދާންދެން، އެއިން ކޮންމެ މީހަކު ވެސް ވަނީ (އެ ވިޔަފާރީގެ މުއާމަލާތް ދެމެހެއްޓުން، ނުވަތަ އެ މުއާމަލާތް ބާތިލު ކުރުމަށްފަހު އެ ތަކެތި ރުޖޫއަ ކުރުމުގެ) އިޚްތިޔާރު ލިބިގެންނެވެ. ދެން އެ ދެމީހުން ތެދުވެރިވެ، (ރަނގަޅަށް ކަން އޮތް) ބަޔާންކޮށްދީފި ނަމަ، އެ ދެމީހުންގެ ވިޔަފާރީގައި ބަރަކާތް ލައްވާނެއެވެ. އަދި އެ ދެބައިމީހުން (ކަން އޮތް ގޮތް) ވަންހަނާކޮށް، ދޮގުހަދައިފިނަމަ، އެ ދެމީހުންގެ ވިޔަފާރީގެ ބަރަކާތް ނެތިގެންދާނެއެވެ.“","[البخاري 2079 واللفظ له، ومسلم 1532]"],["#261","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب ما نهي عنه من البيوع","ގަނެވިއްކުމުގައި މަނާ ކުރެވިގެންވާ ކަންކަމުގެ ބާބު","عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ - رَضِيَ اللَّهُ عَنْهُ -: (أَنَّ رَسُولَ اللَّهِ ﷺ نَهَى عَنْ الْمُنَابَذَةِ - وَهِيَ طَرْحُ الرَّجُلِ ثَوْبَهُ بِالْبَيْعِ إلَى الرَّجُلِ قَبْلَ أَنْ يُقَلِّبَهُ، أَوْ يَنْظُرَ إلَيْهِ - وَنَهَى عَنْ الْمُلامَسَةِ. وَالْمُلامَسَةُ: لَمْسُ الثَّوْبِ وَلا يُنْظَرُ إلَيْهِ).","އަބޫ ސަޢީދު އަލްޚުދްރީ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺ މުނާބަޛާގެ މުއާމަލާތް ކުރުން މަނާ ކުރެއްވިއެވެ. މިއީ މީހާކު އޭނާގެ ހެދުމެއް އެހެން މީހަކަށް އުކައިގެން، އެ މީހަކު ލައްވާ އެ ގަންނަން ޖެއްސުމެވެ. އެއީ އަނެކާ އެ ހެދުން ހުޅުވައި ބަލާ ނެހެދެނީހެވެ. އަދި ނަބިއްޔާ މުލާމަސާގެ މުއާމަލާތް ކުރުންވެސް މަނާ ކުރެއްވިއެވެ. އަދި މުލާމަސާއަކީ މީހަކު ހެދުމެއް ނުބަލައި (ހަމައެކަނި އޭގައި) އަތްލުމުން (އެ ގަންނަން ޖެއްސުމެވެ.)","[البخاري 2144 واللفظ له، ومسلم 1512]"],["#262","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب ما نهي عنه من البيوع","ގަނެވިއްކުމުގައި މަނާ ކުރެވިގެންވާ ކަންކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: (لا تَلَقَّوْا الرُّكْبَانَ، وَلا يَبِعْ بَعْضُكُمْ عَلَى بَيْعِ بَعْضٍ. وَلا تَنَاجَشُوا. وَلا يَبِعْ حَاضِرٌ لِبَادٍ. وَلا تُصَرُّوا الْغَنَمَ. وَمَنْ ابْتَاعَهَا فَهُوَ بِخَيْرِ النَّظَرَيْنِ، بَعْدَ أَنْ يَحْلُبَهَا. وَإِنْ رَضِيَهَا أَمْسَكَهَا، وَإِنْ سَخِطَهَا رَدَّهَا وَصَاعاً مِنْ تَمْرٍ).\n\nوَفِي لَفْظٍ: (هُوَ بِالْخِيَارِ ثَلاثَاً).","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”(ބާޒާރަށް މުދާ ގެނައުމުގެ ކުރިން، ކުރީބައިގައި އެ އުފުލާ މީހުންގެ ކިބައިން ވިޔަފާރި ކުރުމަށް) ތިޔަބައިމީހުން ވިޔަފާރި ޣާފިލާތައް ދުއްވާ މީހުންނާ ބައްދަލު ނުކުރާށެވެ. އަދި އެއްބަޔަކު އަނެއްބަޔެއްގެ ވިޔަފާރީގެ މައްޗަށް ވިޔަފާރި ނުކުރާށެވެ. އަދި އެއްބަޔަކު އަނެއްބަޔަކަށް (ލަނޑުދިނުމުގެ ގޮތުން ރާވައިގެން، އެއްޗެއް ގަތުމުގެ ގަސްދަކާ ނުލައި އޭގެ) އަގު ބޮޑުކޮށް ނުހަދާށެވެ. އަދި ރަށުގައި އުޅޭ މީހަކު، (ރަށްފުށު) ބާދިޔާގައި އުޅޭ މީހަކަށް ޓަކައި (ވަކީލެއްގެ ގޮތުން) އެއްޗެހި ނުވިއްކާށެވެ. (އެހެނީ: އޭޖެންޓަކަށް ވެގެން ހުރެ ވިއްކައިފި ނަމަ، އެއަށް އިތުރު ފައިދާލާފަ މީސްތަކުންނަށް އަގު ބޮޑުކުރާނެއެވެ. އޭގެ ބަދަލުގައި ރަށްފުށު މީހާ ބާޒާރަށް ގޮސް އަމިއްލައަށް ވިއްކައިފިނަމަ އެންމެންނަށް ތަކެތި އަގުހެޔޮވާނެއެވެ.) އަދި ޖަމަލާއި ކަނބަޅީގެ (ކިރު ގިނަކަން މީސްތަކުންނަށް ދެއްކުމަށް ޓަކައި، އެ ތަކެތީގެ) ކިރު ނުފެލާ (ގިނަ ދުވަސް ވަންދެން) ނުބަހައްޓާށެވެ. އަދި އެ ގޮތަށް ހުރި އެއްޗެއް މީހަކު ގަނެ ކިރު ފެލައިފި ނަމަ، ފަހެ އޭނާއަށް ދެ އިޚްތިޔާރެއް ލިބިގެންވެއެވެ. އެއަށް ރުހޭ ނަމަ އޭނާގެ އަތުގައި އޭތި ބަހައްޓަންވީއެވެ. އަދި އެއަށް ނުރުހޭ ނަމަ، ކަދުރުގެ ޞާޢަކާއެކު އޭތި އަނބުރާ ރައްދު ކުރަންވީއެވެ.“\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”ފަހެ އޭނާއަށް އިޚްތިޔާރު ލިބިގެންވެއެވެ.“ (މިފަދައިން) ތިން ފަހަރު ޙަދީޘް ކުރެއްވިއެވެ.","[البخاري 2150 واللفظ له، ومسلم 1515\n\nالبخاري 2148 واللفظ له، ومسلم 1524\n]"],["#263","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب ما نهي عنه من البيوع","ގަނެވިއްކުމުގައި މަނާ ކުރެވިގެންވާ ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا (أَنَّ رَسُولَ اللَّهِ ﷺ نَهَى عَنْ بَيْعِ حَبَلِ الْحَبَلَةِ. وَكَانَ بَيْعاً يَتَبَايَعُهُ أَهْلُ الْجَاهِلِيَّةِ، وَكَانَ الرَّجُلُ يَبْتَاعُ الْجَزُورَ إلَى أَنْ تُنْتَجَ النَّاقَةُ. ثُمَّ تُنْتَجَ الَّتِي فِي بَطْنِهَا.)\n\nقِيلَ: إنَّهُ كَانَ يَبِيعُ الشَّارِفَ - وَهِيَ الْكَبِيرَةُ الْمُسِنَّةُ - بِنِتَاجِ الْجَنِينِ الَّذِي فِي بَطْنِ نَاقَتِهِ.","އަދި ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ޖަމަލެއްގެ ބަނޑުގައިވާ ދަރީގެ ދަރި ވިހުމާ ހަމައަށް، އެއްޗެއް ވިއްކައިގެން އޭގެ އަގު ދެއްކުން ފަސް ކުރުން (ނުވަތަ މާނައީ: ޖަމަލެއްގެ ބަނޑުގައިވާ ދަރީގެ ދަރި ވިއްކުން) ރަސޫލު ﷲ ﷺ މަނާ ކުރެއްވިއެވެ. އެއީ ޖާހިލިއްޔަ (ޒަމާނުގައި) މީހުން ކޮށްއުޅުނު ވިޔަފާރިއެކެވެ. ޖަމަލެއްގެ ބަނޑުގައިވާ ދަރިއެއް ވިހައިގެން، ދެން އެ ދަރި (ބޮޑުވުމަށް ފަހު އެއަށް އަނެއްކާ) އިތުރު ދަރިއެއް ލިބެންދެން (ފައިސާ ދެއްކުން ފަސް ކުރާ ގޮތަށް، އެހެން) ޖަމަލެއް (ނޫނީ ޖަމަލު މަސް) މީހުން ގަނެ ހަދާ އުޅުނެވެ.\n\nއަދި ބުނެވޭ ގޮތުގައި: ބޮޑު މުސްކުޅި އަންހެން ޖަމަލެއް ވިއްކައި، އޭގެ އަގަކީ އެހެން ޖަމަލެއްގެ ބަނޑުގައިވާ ދަރީގެ ދަރި ކަމަށް ހެދުމެވެ. (ނޯޓު: މި ވިޔަފާރިތައް މަނާ ކުރެވުނީ ގޮތާއި މުއްދަތު ނޭންގޭނެތީވެއެވެ.)","[البخاري 2143 واللفظ له، ومسلم 1514\n]"],["#264","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب ما نهي عنه من البيوع","ގަނެވިއްކުމުގައި މަނާ ކުރެވިގެންވާ ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا: (أَنَّ رَسُولَ اللَّهِ ﷺ نَهَى عَنْ بَيْعِ الثَّمَرَةِ حَتَّى يَبْدُوَ صَلاحُهَا. نَهَى الْبَائِعَ وَالْمُشْتَرِيَ).","އަދިވެސް ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން (ގަހުގައި އަޅާ) މޭވާ ރަނގަޅުކަން ފާޅުވުމުގެ ކުރިން އެ ތަކެތި ވިއްކުން ރަސޫލު ﷲ ﷺ މަނާ ކުރެއްވިއެވެ. ވިއްކާ މީހާއާއި ގަންނަ މީހާއަށް ވެސް މަނާ ކުރެއްވިއެވެ.","[البخاري 2194، ومسلم 1535، 1534، وأبو داود 3367 واللفظ له]"],["#265","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب ما نهي عنه من البيوع","ގަނެވިއްކުމުގައި މަނާ ކުރެވިގެންވާ ކަންކަމުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ -: (أَنَّ رَسُولَ اللَّهِ ﷺ نَهَى عَنْ بَيْعِ الثِّمَارِ حَتَّى تُزْهِيَ قِيلَ: وَمَا تُزْهِي؟ قَالَ: حَتَّى تَحْمَرَّ. قَالَ: أَرَأَيْتَ إنْ مَنَعَ اللَّهُ الثَّمَرَةَ، بِمَ يَسْتَحِلُّ أَحَدُكُمْ مَالَ أَخِيهِ؟).","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން (ގަހުގައި އަޅާ) މޭވާ ފައްކާވުމުގެ ކުރިން އެ ތަކެތި ވިއްކުން ރަސޫލު ﷲ ﷺ މަނާ ކުރެއްވިއެވެ. މީހަކު ދެންނެވިއެވެ: ދޮންވުމަކީ ކޮބައިތޯއެވެ؟ ވިދާޅުވިއެވެ: އެ ތަކެތި ދޮންވުމެވެ. އަދިވެސް ވިދާޅުވިއެވެ: ތިބާ ދެކެމުހެއްޔެވެ؟ ﷲ އެ މޭވާތައް ހަލާކު ކުރައްވައިފި ނަމަ، ތިޔަބައިމީހުންގެ ތެރެއިން މީހަކު (ކުރީބައިގައި ލާރި ނެގުމަށް ފަހު، ގަތް މީހާއަށް ބަދަލު ދިނުމަކާ ނުލައި) އޭނާގެ އަޚާގެ މުދާ ހަލާލު ވާނީ ކިހިނަކުންހެއްޔެވެ؟","[البخاري 2198 واللفظ له، ومسلم 1555]"],["#266","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب ما نهي عنه من البيوع","ގަނެވިއްކުމުގައި މަނާ ކުރެވިގެންވާ ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (نَهَى رَسُولُ اللَّهِ ﷺ أَنْ تُتَلَقَّى الرُّكْبَانُ، وَأَنْ يَبِيعَ حَاضِرٌ لِبَادٍ، قَالَ: فَقُلْتُ لابْنِ عَبَّاسٍ: مَا قَوْلُهُ حَاضِرٌ لِبَادٍ؟ قَالَ: لا يَكُونُ لَهُ سِمْسَاراً).","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: (ބާޒާރަށް މުދާ ގެނައުމުގެ ކުރިން، ކުރީބައިގައި އެ އުފުލާ މީހުންގެ ކިބައިން ވިޔަފާރި ކުރުމަށް) އެ ވިޔަފާރި ޣާފިލާތައް ދުއްވާ މީހުންނާ ބައްދަލު ކުރުން ރަސޫލު ﷲ ﷺ މަނާ ކުރެއްވިއެވެ. އަދި ރަށުގައި އުޅޭ މީހަކު، (ރަށްފުށު) ބާދިޔާގައި އުޅޭ މީހަކަށް ޓަކައި އެއްޗެހި ވިއްކުން ނަބިއްޔާ މަނާ ކުރެއްވިއެވެ. (ޙަދީޘްގެ ރާވީއެއް ކަމަށްވާ ޠާވޫސް،) އިބްނު ޢައްބާސް އަރިހު ދެންނެވިއެވެ: ރަށުގައި އުޅޭ މީހަކު، ބާދިޔާގައި އުޅޭ މީހަކަށް (ނުވިއްކާށޭ) ވިދާޅުވުމުގެ މާނައަކީ ކޮބައިތޯއެވެ؟ އިބްނު ޢައްބާސް ވިދާޅުވިއެވެ: އެ ބާދިޔާގައި އުޅޭ މީހާއަށް، ރަށުގައި އުޅޭ މީހާ (އަގަށް) ވަކީލެއް ކަމުގައި ނުވާށެވެ.","[البخاري 2274، ومسلم 1521 واللفظ له]"],["#267","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب ما نهي عنه من البيوع","ގަނެވިއްކުމުގައި މަނާ ކުރެވިގެންވާ ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (نَهَى رَسُولُ اللَّهِ ﷺ عَنْ الْمُزَابَنَةِ: أَنْ يَبِيعَ ثَمَرَ حَائِطِهِ، إنْ كَانَ نَخْلاً: بِتَمْرٍ كَيْلاً. وَإِنْ كَانَ كَرْماً: أَنْ يَبِيعَهُ بِزَبِيبٍ كَيْلاً، أَوْ كَانَ زَرْعاً: أَنْ يَبِيعَهُ بِكَيْلِ طَعَامٍ. نَهَى عَنْ ذَلِكَ كُلِّهِ).","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ މުޒާބަނާގެ މުއާމަލާތް ކުރުން މަނާ ކުރެއްވިއެވެ. އެއީ މީހަކު އޭނާގެ ދަނޑުގައިވާ މޭވާ، އެ މޭވާއަކީ ރުކުގައި (އަޅާފައިވާ) ރޯކަދުރު ނަމަ، އެ ރޯކަދުރުގެ ބަދަލަކަށް ހިކި ކަދުރު ހަމަ އެ މިންވަރަށް ވިއްކުމެވެ. އަދި އެއީ (ގަހުގައި އަޅާފައިވާ) މޭބިސްކަދުރު ނަމަ، އެ މޭބިސްކަދުރުގެ ބަދަލަކަށް ހިކި މޭބިސްކަދުރު ހަމަ އެ މިންވަރަށް ވިއްކުމެވެ. އަދި (ކުރާގަނޑުގައި އަޅާފައިވާ) ގޮވާން ކަމުގައި ވާނަމަ، އޭގެ ބަދަލަކަށް (ކަނޑާފައިވާ) ގޮވާން ހަމަ އެ މިންވަރަށް ވިއްކުމެވެ. އެއިން ކޮންމެ ކަމެއް ނަބިއްޔާ މަނާ ކުރެއްވިއެވެ. (ނޯޓު: މިއީ ގޮތް ނޭނގުމާއި މިންވަރު ނޭނގުމުގެ ސަބަބުން މަނާ ކުރެވިފައިވާ ވިޔަފާރިއެކެވެ. ނަމަވެސް ޢަރާޔާގެ މުއާމަލާތަކީ މީގެ އިސްތިސްނާއެކެވެ. އެއީ ފަސްދޮޅަސް ޞާޢަށް ވުރެ ގިނަ ނުވާ ގޮތަށް، ރުކުގައި އަޅާފައިވާ ރޯކަދުރުގެ ލާފާ ކުރެވޭ މިންވަރެއްގެ ބަދަލުގައި، އެއާ އެއްވަރަށް ހިކިކަދުރު ދިނުމެވެ. މިއީ ފައިސާ ނެތިގެން، ކެއުމަށް ޓަކައި ރޯކަދުރަށް ބޭނުން ޖެހިފައިވާތީ މީސްތަކުންނަށް ލުއި ކުރުމުގެ ގޮތުން ދެވުނު ހުއްދައެކެވެ.)","[البخاري 2205 واللفظ له، ومسلم 1542]"],["#268","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب ما نهي عنه من البيوع","ގަނެވިއްކުމުގައި މަނާ ކުރެވިގެންވާ ކަންކަމުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (نَهَى النَّبِيُّ ﷺ عَنْ الْمُخَابَرَةِ وَالْمُحَاقَلَةِ، وَعَنْ الْمُزَابَنَةِ وَعَنْ بَيْعِ الثَّمَرَةِ حَتَّى يَبْدُوَ صَلاحُهَا، وَأَنْ لا تُبَاعَ إلاَّ بِالدِّينَارِ وَالدِّرْهَمِ، إلاَّ الْعَرَايَا)","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ މުޚާބަރާއާއި، މުޙާގާލާއާއި، މުޒާބަނާގެ މުއާމަލާތްތައް ކުރުން މަނާ ކުރެއްވިއެވެ. (މުޚާބަރާއަކީ ދަނޑުވެރިކަން ކުރުމަށް ބިން ދޫކުރުމުގެ ބަދަލަކަށް، އަގުގެ ގޮތުގައި އޭގައި ހައްދާ ތަކެތީގެ ބައެއް ނެގުމެވެ. އަދި މުޙާގާލާއަކީ ކުރާގަނޑުގައި އަޅާފައިވާ ގޮވާމުގެ ބަދަލަކަށް، އަގުގެ ގޮތުގައި ކަނޑާފައިވާ ގޮވާން ދިނުމެވެ. އަދި މުޒާބަނާއަކީ ރުކުގައި އަޅާފައިވާ ރޯކަދުރުގެ ބަދަލަކަށް، އަގުގެ ގޮތުގައި ހިކި ކަދުރު ދިނުން، ނުވަތަ ގަހުގައި އަޅާފައިވާ މޭބިސްކަދުރުގެ ބަދަލަކަށް، ހިކި މޭބިސްކަދުރު ދިނުމެވެ.) އަދި (ގަހުގައި އަޅާ) މޭވާ ރަނގަޅުކަން ފާޅުވުމުގެ ކުރިން އެ ތަކެތި ވިއްކުން ނަބިއްޔާ މަނާ ކުރެއްވިއެވެ. އަދި ދީނާރާއި ދިރުހަމަށް (އެބަހީ: ފައިސާއަށް) ފިޔަވައި މޭވާ ވިއްކުން މަނާ ކުރެއްވިއެވެ. އެއީ ޢަރާޔާގެ މުއާމަލާތުގައި މެނުވީއެވެ. (އެބަހީ: ފަސްދޮޅަސް ޞާޢަށް ވުރެ ގިނަ ނުވާ ގޮތަށް، ރުކުގައި އަޅާފައިވާ ރޯކަދުރުގެ ލާފާ ކުރެވޭ މިންވަރެއްގެ ބަދަލުގައި، އެއާ އެއްވަރަށް ހިކިކަދުރު ދިނުމެވެ.)","[البخاري 2381، ومسلم 1536]"],["#269","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب ما نهي عنه من البيوع","ގަނެވިއްކުމުގައި މަނާ ކުރެވިގެންވާ ކަންކަމުގެ ބާބު","عَنْ أَبِي مَسْعُودٍ الأَنْصَارِيِّ - رَضِيَ اللَّهُ عَنْهُ - (أَنَّ رَسُولَ اللَّهِ ﷺ نَهَى عَنْ ثَمَنِ الْكَلْبِ، وَمَهْرِ الْبَغِيِّ، وَحُلْوَانِ الْكَاهِنِ).","އަބޫ މަސްޢޫދު އަލްއަންޞާރީ ﷲ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ކުއްތާ (ގަނެވިއްކުމުގެ) އަގާއި، ހަށިވިއްކާ މީހާގެ އަގާއި، ކާހިނުގެ އަގު، ރަސޫލު ﷲ ﷺ މަނާ ކުރެއްވިއެވެ.","[البخاري 2237، ومسلم 1567]"],["#270","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب ما نهي عنه من البيوع","ގަނެވިއްކުމުގައި މަނާ ކުރެވިގެންވާ ކަންކަމުގެ ބާބު","عَنْ رَافِعِ بْنِ خَدِيجٍ - رَضِيَ اللَّهُ عَنْهُ - أَنْ رَسُولَ اللَّهِ ﷺ قَالَ: (ثَمَنُ الْكَلْبِ خَبِيثٌ. وَمَهْرُ الْبَغِيِّ خَبِيثٌ، وَكَسْبُ الْحَجَّامِ خَبِيثٌ).","ރާފިޢު ބިން ޚަދީޖު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ކުއްތާ (ގަނެވިއްކުމުގެ) އަގަކީ ހަޑި މުޑުދާރު އެއްޗެކެވެ. އަދި ހަށިވިއްކާ މީހާގެ އަގަކީ ހަޑި މުޑުދާރު އެއްޗެކެވެ. އަދި ހިޖާމާ ޖަހާ މީހާ ނަގާ އަގަކީ ހަޑި މުޑުދާރު އެއްޗެކެވެ.","[مسلم 1568]"],["#271","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب العرايا وغير ذلك","ޢަރާޔާއާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ زَيْدِ بْنِ ثَابِتٍ - رَضِيَ اللَّهُ عَنْهُ - (أَنَّ رَسُولَ اللَّهِ ﷺ رَخَّصَ لِصَاحِبِ الْعَرِيَّةِ: أَنْ يَبِيعَهَا بِخَرْصِهَا)\n\nوَلِمُسْلِمٍ: (بِخَرْصِهَا تَمْراً، يَأْكُلُونَهَا رُطَباً).","ޒައިދު ބިން ޘާބިތު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ (މީސްތަކުންނަށް) ލުއި ކުރެއްވުމުގެ ގޮތުން، އެ މީހެއްގެ ރުއްތަކުގައި ރޯކަދުރު އަޅާފައި ހުރި މީހަކަށް، އެ ރޯކަދުރުގެ ބަދަލުގައި (ހިކިކަދުރު) ހަމަ އެ މިންވަރަށް ވިއްކުން ނަބިއްޔާ ހުއްދަ ކުރެއްވިއެވެ.\n\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: (ލަފާކޮށްފައި) އެ ރޯކަދުރުގެ ބަދަލުގައި ހަމަ އެ މިންވަރަށް ހިކިކަދުރު ވިއްކާ ގޮތަށް، އަދި (ގަންނަ މީހާ) ރޯކަދުރު ކާ ގޮތަށް (މުއާމަލާތް ކުރުމުގެ ހުއްދަ ދެއްވިއެވެ.) (މިއީ ރިބާގެ މުއާމަލާތްތަކުގައި ވާ އިސްތިސްނާއެކެވެ. ފަސްދޮޅަސް ޞާޢަށް ވުރެ ގިނަ ނުވާ ގޮތަށް މި ގޮތަށް ދެ ވައްތަރެއްގެ ކަދުރު ބަދަލު ކުރުން ހުއްދަވެގެން ވެއެވެ.)","[البخاري 2188، ومسلم 1539\n\nمسلم 1539]"],["#272","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب العرايا وغير ذلك","ޢަރާޔާއާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ -: (أَنَّ رَسُولَ اللَّهِ ﷺ رَخَّصَ فِي بَيْعِ الْعَرَايَا فِي خَمْسَةِ أَوْسُقٍ أَوْ دُونَ خَمْسَةِ أَوْسُقٍ).","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷲ ﷺ (މީސްތަކުންނަށް) ލުއި ކުރެއްވުމުގެ ގޮތުން، (ލަފާކޮށްފައި) ރުއްތަކުގައި އަޅާފައި ރޯކަދުރުގެ ބަދަލަކަށް، އަގުގެ ގޮތުން (ހިކިކަދުރު) ހަމަ އެ މިންވަރަށް ވިއްކުން ނަބިއްޔާ ހުއްދަ ކުރެއްވިއެވެ. އެއީ ފަސް ވަސްގު (ނުވަތަ ފަސްދޮޅަސް ޞާޢުގެ) މިންވަރަށެވެ. ނޫނީ ފަސް ވަސްގަށް ވުރެ މަދުވާ މިންވަރަށެވެ.","[البخاري 2190 واللفظ له، ومسلم 1541]"],["#273","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب العرايا وغير ذلك","ޢަރާޔާއާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا أَنَّ رَسُولَ اللَّهِ - ﷺ قَالَ: (مَنْ بَاعَ نَخْلاً قَدْ أُبِّرَتْ فَثَمَرُهَا لِلْبَائِعِ، إلاَّ أَنْ يَشْتَرِطَ الْمُبْتَاعُ).\n\nوَلِمُسْلِمٍ (وَمَنْ ابْتَاعَ عَبْداً فَمَالُهُ لِلَّذِي بَاعَهُ إلاَّ أَنْ يُشْتَرَطَ الْمُبْتَاعُ).","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ކަދުރު ރުކެއް ޖޯޑު (އެބަހީ: ޕޮލިނޭޓް) ކުރެވުމަށް ފަހު މީހަކު އެ ވިއްކައިފި ނަމަ، ފަހެ އޭގެ މޭވާ ލިބިގެން ވަނީ ވިއްކި މީހާއަށެވެ. އެއީ (އެ ނޫން ގޮތަކަށް) ގަންނަ މީހާ ޝަރުތު ކުރާ ނަމަ މެނުވީއެވެ.“\n\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ”މީހަކު އަޅެއް ވިއްކައިފި ނަމަ، ފަހެ އެ އަޅެއްގެ މުދާ ލިބިގެން ވަނީ އޭނާ ވިއްކި މީހާއަށެވެ. އެއީ (އެ ނޫން ގޮތަކަށް) ގަންނަ މީހާ ޝަރުތު ކުރާ ނަމަ މެނުވީއެވެ.“","[البخاري 2716، ومسلم 1543\n\nمسلم 1543]"],["#274","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب العرايا وغير ذلك","ޢަރާޔާއާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: (مَنْ ابْتَاعَ طَعَامًا فَلا يَبِعْهُ حَتَّى يَسْتَوْفِيَهُ)\n\nوَفِي لَفْظٍ: (حَتَّى يَقْبِضَهُ).\n\nوَعَنْ ابْنِ عَبَّاسٍ مِثْلُهُ.","އަދިވެސް ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: މީހަކު ގޮދަން (ނުވަތަ ކާތަކެތި) ގަނެފި ނަމަ، އޭތި އެއްކޮށް ލިބެންދެން (އެހެން މީހަކަށް) ވިއްކައިގެން ނުވާނެއެވެ.\n\nއަދި އެހެން ރިވާތެއްގައި ވެއެވެ: އޭތި އަތަށް ނަގަންދެން (އެހެން މީހަކަށް ވިއްކައިގެން ނުވާނެއެވެ.)\n\nއަދި އިބްނު ޢައްބާސްގެ އަރިހުން ވެސް ހަމަ މިފަދައިން ރިވާވެފައި ވެއެވެ.","[البخاري 2126، ومسلم 1526\n\nالبخاري 2133، ومسلم 1526\n\nالبخاري 2132، ومسلم 1525]"],["#275","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب العرايا وغير ذلك","ޢަރާޔާއާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رَضِيَ اللَّهُ عَنْهُمَا أَنَّهُ سَمِعَ رَسُولَ اللَّهِ ﷺ يَقُولُ عَامَ الْفَتْحِ: (إنَّ اللَّهَ وَرَسُولَهُ حَرَّمَ بَيْعَ الْخَمْرِ وَالْمَيْتَةِ وَالْخِنْزِيرِ وَالأَصْنَامِ. فَقِيلَ: يَا رَسُولَ اللَّهِ، أَرَأَيْتَ شُحُومَ الْمَيْتَةِ؟ فَإِنَّهُ يُطْلَى بِهَا السُّفُنُ، وَيُدْهَنُ بِهَا الْجُلُودُ. وَيَسْتَصْبِحُ بِهَا النَّاسُ. فَقَالَ: لا، هُوَ حَرَامٌ. ثُمَّ قَالَ رَسُولُ اللَّهِ ﷺ عِنْدَ ذَلِكَ: قَاتَلَ اللَّهُ الْيَهُودَ. إنَّ اللَّهَ لَمَّا حَرَّمَ عَلَيْهِمْ شُحُومَهَا، جَمَلُوهُ ثُمَّ بَاعُوهُ فَأَكَلُوا ثَمَنَهُ).","ޖާބިރު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން މައްކާ ފަތަހަ ކުރެއްވި އަހަރު، ރަސޫލު ﷲ ﷺ އެ ބިމުގައި ހުންނަވައިގެން ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ : ”ހަމަކަށަވަރުން ﷲ އާއި އެ އިލާހުގެ ރަސޫލާ ވަނީ ރަލާއި، މުޅަ ތަކެއްޗާއި، އޫރު މަހާއި، ބުދުތަކާ މި ތަކެތީގެ ވިޔަފާރި ކުރުން ހަރާމް ކުރައްވާފައެވެ.“ ދެން ނަބިއްޔާއަށް ދެންނެވުނެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. މުޅަ ތަކެތީގެ ސަރުބީއާމެދު ދެކިލައްވަނީ ކިހިނަކުންތޯއެވެ؟ އެހެނީ ހަމަކަށަވަރުން އޮޑިދޯނިފަހަރުގައި ( ތެޔޮ ހާކައިގެން ފެން ވަނަ ނުދިނުމަށް) ކަޑިތާރު ލާން، އެ މުޅަ ތަކެތީގެ ސަރުބީގެ ތެޔޮ ބޭނުން ކުރެވެއެވެ. އަދި ދިބާޣުކުޅަހަންތަކުގައި ތެޔޮ ހޭކުމަށް ވެސް އޭގެ ބޭނުން ކުރެއެވެ. އަދި ވޮށް ދިއްލައި އަލި ކުރުމަށް ވެސް މީސްތަކުން އޭގެ ބޭނުން ކުރެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ނޫނެކެވެ. އެ ހަރާމްވެގެން ވެއެވެ.“ ދެން އެއަށްފަހު ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”قَاتَلَ اللَّهُ الْيَهُودَ (ﷲ ޔަހޫދީން ގަތުލު ކުރައްވާށިއެވެ.) (އެބަހީ: އެބައިމީހުންނަށް ލަޢުނަތް ލައްވާށިއެވެ.) ހަމަކަށަވަރުން ﷲ އެބައިމީހުންނަށް ސަރުބީ ހަރާމް ކުރެއްވުމުން، އެބައިމީހުން އެ ސަރުބީ ވިރުވައި ތެލަށް ހަދައިގެން، ދެން އެ ވިއްކައިގެން އޭގެ އަގު ކައި އުޅުނެވެ.“ (އެބަހީ: އޭގެން ލިބުނު ފައިސާ ބޭނުން ކުރިއެވެ.)","[البخاري 2236، ومسلم 1581]"],["#276","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب السلم","ސަލަމް (ކުރިއާލާ އަގު ދެއްކުމުގެ) ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (قَدِمَ رَسُولُ اللَّهِ ﷺ الْمَدِينَةَ، وَهُمْ يُسْلِفُونَ فِي الثِّمَارِ: السَّنَةَ وَالسَّنَتَيْنِ وَالثَّلاثَ. فَقَالَ: مَنْ أَسْلَفَ فِي شَيْءٍ فَلْيُسْلِفْ فِي كَيْلٍ مَعْلُومٍ، وَوَزْنٍ مَعْلُومٍ، إلَى أَجَلٍ مَعْلُومٍ).","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ މަދީނާއަށް ވަޑައިގެންނެވި ހިނދު، މީސްތަކުން ކުރިއާލާ އަހަރެއް، ދެ އަހަރު، އަދި ތިން އަހަރު މޭވާއަށް އަގު ދައްކާ ހަދާ އުޅުނެވެ. ދެން ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”މީހަކު އެއްޗަކަށް ކުރިއާލާ އަގު ދައްކާ ނަމަ، ފަހެ ކަނޑައެޅިގެންވާ ވަކި މިންވަރަކަށް، އަދި ވަކި ވަޒަނަކަށް، އަދި ވަކި މުއްދަކާ ހަމައަށް ދައްކާށެވެ.“","[البخاري 2239، 2240، ومسلم 1604]"],["#277","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الشروط في البيع","ވިޔަފާރީގައި ޝަރުތު ކުރުމުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: (جَاءَتْنِي بَرِيرَةُ: فَقَالَتْ: كَاتَبْتُ أَهْلِي عَلَى تِسْعِ أَوَاقٍ، فِي كُلِّ عَامٍ أُوقِيَّةٌ. فَأَعِينِينِي. فَقُلْتُ: إنْ أَحَبَّ أَهْلُكِ أَنْ أَعُدَّهَا لَهُمْ، وَوَلاؤُكِ لِي فَعَلْتُ. فَذَهَبَتْ بَرِيرَةُ إلَى أَهْلِهَا، فَقَالَتْ: لَهُمْ. فَأَبَوْا عَلَيْهَا. فَجَاءَتْ مِنْ عِنْدِهِمْ وَرَسُولُ اللَّهِ ﷺ جَالِسٌ. فَقَالَتْ: إنِّي عَرَضْتُ ذَلِكَ عَلَى أَهْلِي، فَأَبَوْا إلاَّ أَنْ يَكُونَ لَهُمُ الْوَلاءُ. فَأَخْبَرَتْ عَائِشَةُ النَّبِيَّ ﷺ. فَقَالَ: خُذِيهَا وَاشْتَرِطِي لَهُمُ الْوَلاءَ. فَإِنَّمَا الْوَلاءُ لِمَنْ أَعْتَقَ. فَفَعَلَتْ عَائِشَةُ. ثُمَّ قَامَ رَسُولُ اللَّهِ ﷺ فِي النَّاسِ، فَحَمِدَ اللَّهَ وَأَثْنَى عَلَيْهِ. ثُمَّ قَالَ: أَمَّا بَعْدُ. فَمَا بَالُ رِجَالٍ يَشْتَرِطُونَ شُرُوطاً لَيْسَتْ فِي كِتَابِ اللَّهِ؟ كُلُّ شَرْطٍ لَيْسَ فِي كِتَابِ اللَّهِ فَهُوَ بَاطِلٌ وَإِنْ كَانَ مِائَةَ شَرْطٍ. قَضَاءُ اللَّهِ أَحَقُّ. وَشَرْطُ اللَّهِ أَوْثَقُ. وَإِنَّمَا الْوَلاءُ لِمَنْ أَعْتَقَ).","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: (އޭރު އަޅު އަންހެނެއް ކަމުގައިވާ) ބަރީރާ ތިމަން ކަމަނާގެ ގާތަށް އައިސް ދެންނެވިއެވެ: ތިމަންނަގެ ސާހިބާއިންނަށް ކޮންމެ އަހަރަކު (ރަނުގެ) އޫގިޔާއެއް ދީ، (ޖުމްލަ) ނުވަ އޫގޔާ ހަމަކޮށްގެން ތިމަން މިނިވަންވާ ގޮތަށް އެބައިމީހުންނާ އެއްބަސްވުމެއް ހަދައިފީމެވެ. ވީމާ ކަމަނާ ތިމަންނައަށް އެހީވެދެއްވާށެވެ. ޢާއިޝާ ވިދާޅުވިއެވެ: ތިބާގެ ސާހިބައިން ރުހޭ ނަމަ، ތިބާގެ ވަލާވެރިކަން ތިމަން ކަމަނާއަށް ލިބޭ ގޮތުގެ މަތިން، ތިމަން ކަމަނާ (އެއް ފަހަރާ) އެ އަދަދު ދައްކާ ޚަލާސްކޮށްދީފާނަމެވެ. (ނޯޓު: ވަލާވެރިކަމަކީ މީހަކު އަޅެއް މިނިވަން ކުރުމުގެ ސަބަބުން، އެ މީހަކަށް އެ އަޅެއްގެ ކިބައިން ވާރުތަވުމުގެ ހައްގު ލިބުމެވެ.) ދެން ބަރީރާ އޭނާގެ ސާހިބައިން ގާތަށް ދިޔުމުން، އެބައިމީހުން އެ ކަމަށް އެއްބަހެއް ނުވިއެވެ. އެއަށްފަހު ބަރީރާ އެނބުރި ޢާއިޝާގެ އަރިހަށް އަތުވިއެވެ. އެ ހިނދު ރަސޫލު ﷲ ﷺ އެތާ އިށީނދެވަޑައިގެން އިންނެވިއެވެ. ދެން ބަރީރާ ދެންނެވިއެވެ: އެބައިމީހުންނަށް އެ ކަން ހުށާހެޅީމެވެ. ނަމަވެސް އެއްބަހެއް ނުވިއެވެ. އެއީ އެބައިމީހުންނަށް ވަލާވެރިކަން ލިބޭ ނަމަ މެނުވީއެވެ. ފަހެ ނަބިއްޔާ ﷺ އަށް (އެ ވާހަކަ) އިވިވަޑައިގަތެވެ. އަދި ޢާއިޝާ، ނަބިއްޔާ ﷺ އަށް (ކަންތައް ވީ ގޮތުގެ) ޚަބަރު ކިޔައިދެއްވިއެވެ. ދެން ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ކަމަނާ ބަރީރާ (ގަންނަވާ) ނަންގަވާށެވެ. އަދި (ކަމަނާއަށް ވަލާވެރިކަން ލިބޭގޮތަށް) އެބައިމީހުންނަށް ޝަރުތު ކުރާށެވެ. އެހެނީ ވަލާވެރިކަން ލިބުން ވަނީ ހަމައެކަނި މިނިވަން ކުރާ މީހާއަށެވެ.“ ފަހެ ޢާއިޝާ އެ ފަދައިން ކަންތައް ކުރެއްވިއެވެ. ދެން ނަބިއްޔާ މީސްތަކުންގެ މެދުގައި ތެދުވެވަޑައިގެން، ﷲ އަށް ހަމްދު ސަނާ ދަންނަވާފައި ޙަދީޘް ކުރެއްވިއެވެ: ”އެއަށްފަހު ދަންނާށެވެ. ﷲގެ ފޮތުގައި ނުވާ ޝަރުތުތަކެއް ބަޔަކު މީހުން ކަނޑައަޅަނީ ކިހިނެއްވެގެންހެއްޔެވެ؟ ﷲގެ ފޮތުގައި ނުވާ ކޮންމެ ޝަރުތަކީ ބާތިލު އެއްޗެކެވެ. އެއީ ސަތޭކަ ޝަރުތު ކަމުގައިވިޔަސް ވެސްމެއެވެ. ﷲގެ ނިޔާ މާ ހައްގުވެގެން ވެއެވެ. އަދި ﷲގެ ޝަރުތު މާ ވަރުގަދަވެގެން ވެއެވެ. އަދި ވަލާވެރިކަން ލިބުން ވަނީ ހަމައެކަނި މިނިވަން ކުރާ މީހާއަށެވެ.“","[البخاري 2168 واللفظ له، ومسلم 1504]"],["#278","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الشروط في البيع","ވިޔަފާރީގައި ޝަރުތު ކުރުމުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رَضِيَ اللَّهُ عَنْهُمَا (أَنَّهُ كَانَ يَسِيرُ عَلَى جَمَلٍ فَأَعْيَا، فَأَرَادَ أَنْ يُسَيِّبَهُ. فَلَحِقَنِي النَّبِيُّ ﷺ فَدَعَا لِي، وَضَرَبَهُ. فَسَارَ سَيْراً لَمْ يَسِرْ مِثْلَهُ. ثُمَّ قَالَ: بِعْنِيهِ بِوُقِيَّةٍ. قُلْتُ: لا. ثُمَّ قَالَ: بِعْنِيهِ. فَبِعْتُهُ بِأُوقِيَّةٍ. وَاسْتَثْنَيْتُ حِمْلانَهُ إلَى أَهْلِي. فَلَمَّا بَلَغْتُ: أَتَيْتُهُ بِالْجَمَلِ. فَنَقَدَنِي ثَمَنَهُ. ثُمَّ رَجَعْتُ. فَأَرْسَلَ فِي إثْرِي. فَقَالَ: أَتُرَانِي مَاكَسْتُكَ لآخُذَ جَمَلَكَ؟ خُذْ جَمَلَكَ وَدَرَاهِمَكَ. فَهُوَ لَكَ)","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ހިނގުން ބަލިކަށިވެފައިވާ ޖަމަލެއްގެ މަތީގައި ޖާބިރު ދަތުރު ކުރިއެވެ. ޖާބިރު ބޭނުންވީ އެ ޖަމަލު (ލަސްކަމުން ސަހަރާ ތެރެއަށް) ދޫކޮށްލާށެވެ. ޖާބިރު ވިދާޅުވިއެވެ: (އެ ދަތުރު މަތީގައި) ނަބިއްޔާ ﷺ ތިމަން ގާތަށް ވަޑައިގެން ގޮވާލެއްވިއެވެ. އަދި އެ ޖަމަލުގައި ޖައްސަވާލެއްވިއެވެ. އެއަށްފަހު އެ ޖަމަލު ކުރިން ދުވަހަކުވެސް ނުދާހާ ބާރުމިނަކަށް ހިނގައިގަތެވެ. ދެން ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”(ރަނުގެ) އޫގިޔާއަކަށް އެ ޖަމަލު ތިމަން ނަބިއްޔާއަށް ވިއްކާށެވެ.“ ތިމަން ދެންނެވީމެވެ: ނޫނެކެވެ. (ނޯތު: އެއީ ޖާބިރުގެ ހުރި ހަމައެކަނި ޖަމަލެވެ. އަދި ޖާބިރު ވަނީ ޝަހީދުވި ބައްޕާފުޅުގެ ޔަތީމު ކުޑަކުޑަ އަންހެން ދަރިންތަކެއް ދަތި ހާލުގައި ބަލަން ޖެހިފައެވެ.) ދެންވެސް ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ ޖަމަލު ތިމަން ނަބިއްޔާއަށް ވިއްކާށެވެ.“ ދެން އެ ޖަމަލު މަތީގައި (ފުރަތަމަ) ތިމަން ގެއަށް ފޯރަންދެން ދަތުރު ކުރާ ގޮތަށް، އޫގިޔާއަކަށް ނަބިއްޔާއަށް އެ ޖަމަލު ވިއްކީމެވެ. (ގެއާ ހިސާބަށް) ދެވުމުން، ޖަމަލު ހިފައިގެން ނަބިއްޔާގެ އަރިހަށް ތިމަން ދިޔައިމެވެ. އަދި އޭގެ އަގު ނަބިއްޔާ ތިމަންނައަށް ދެއްވިއެވެ. އެއަށްފަހު ތިމަން އެނބުރި ދާން ހިނގައިގަތީމެވެ. ނަބިއްޔާ ތިމަން ފަހަތުން މީހަކު ފޮނުއްވައި (ތިމަން އަނބުރާ ގެނެއްސިއެވެ.) ދެން ޙަދީޘް ކުރެއްވިއެވެ. ”ތިބާއަށް އަގު ކުޑަކޮށް ތިމަން ނަބިއްޔާ ވިދާޅުވީ ތިބާގެ ޖަމަލު އަތުލެއްވުމަށް ކަމަށް ހީކުރީތޯއެވެ؟ ތިބާގެ ޖަމަލާއި އަދި ދިރުހަމްތައް ވެސް ނަގާށެވެ. އެއީ ތިބާއަށްވާ ތަކެއްޗެވެ.“","[البخاري 2718، ومسلم 1221 واللفظ له]"],["#279","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الشروط في البيع","ވިޔަފާރީގައި ޝަރުތު ކުރުމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (نَهَى رَسُولُ اللَّهِ - ﷺ أَنْ يَبِيعَ حَاضِرٌ لِبَادٍ. وَلا تَنَاجَشُوا وَلا يَبِعِ الرَّجُلُ عَلَى بَيْعِ أَخِيهِ. وَلا يَخْطُبْ عَلَى خِطْبَتِهِ. وَلا تَسْأَلِ الْمَرْأَةُ طَلاقَ أُخْتِهَا لِتَكْفِئَ مَا فِي صَحْفَتِهَا)","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަށުގައި އުޅޭ މީހަކު، (ރަށްފުށު) ބާދިޔާގައި އުޅޭ މީހަކަށް ޓަކައި (ވަކީލެއްގެ ގޮތުން) އެއްޗެހި ވިއްކުން ރަސޫލު ﷲ ﷺ މަނާ ކުރެއްވިއެވެ. އަދި އެއްބަޔަކު އަނެއްބަޔަކަށް (ލަނޑުދިނުމުގެ ގޮތުން ރާވައިގެން، އެއްޗެއް ގަތުމުގެ ގަސްދަކާ ނުލައި އޭގެ) އަގު ބޮޑުކޮށް ނަހަދާން ނަބިއްޔާ އެންގެވިއެވެ. އަދި މީހަކު، އޭނާގެ އަޚާގެ ވިޔަފާރީގެ މައްޗަށް ވިޔަފާރި ނުކުރާން އެންގެވިއެވެ. އަދި މީހަކު، އޭނާގެ އަޚާގެ ޚިތުބާގެ މައްޗަށް ޚިތުބާ ނުވާން އެންގެވިއެވެ. އަދި އަންހެނަކު، އޭނާގެ އުޚްތާއަށް ލިބިފައިވާ (ކައިވެނީގެ ހެޔޮކަން) އަތުލައިގަތުމުގެ ގޮތުން، އެ އުޚްތާ ވަރިކޮށްދިނުން އެދި ނާހާން ނަބިއްޔާ އެންގެވިއެވެ.","[البخاري 2140 واللفظ له، ومسلم 413]"],["#280","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الربا والصرف","ރިބާއާއި މާރު ކުރުމުގެ ބާބު","عَنْ عُمَرَ بْنِ الْخَطَّابِ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: (الذَّهَبُ بِالْوَرِقِ رِبًا، إلاَّ هَاءَ وَهَاءَ، وَالْبُرُّ بِالْبُرِّ رِباً، إلاَّ هَاءَ وَهَاءَ. وَالشَّعِيرُ بِالشَّعِيرِ رِباً، إلاَّ هَاءَ وَهَاءَ).","ޢުމަރު ބިން އަލްޚާއްޠާބު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ރަނަށް ރަން ބަދަލު ކުރުމަކީ ރިބާއެވެ. އެއީ އަތުން އަތަށް (އެއް ވަގުތެއްގައި، އެއްވަރަކަށް) ބަދަލު ކުރެވޭނަމަ މެނުވީއެވެ. އަދި ރިއްސަށް ރިހި ބަދަލު ކުރުމަކީ ރިބާއެވެ. އެއީ އަތުން އަތަށް ބަދަލު ކުރެވޭނަމަ މެނުވީއެވެ. އަދި ގޮދަނަށް ގޮދަން ބަދަލު ކުރުމަކީ ރިބާއެވެ. އެއީ އަތުން އަތަށް އެއްވަރަކަށް ބަދަލު ކުރެވޭނަމަ މެނުވީއެވެ. އަދި ހިމަގޮދަނަށް ހިމަގޮދަން ބަދަލު ކުރުމަކީ ރިބާއެވެ. އެއީ އަތުން އަތަށް ބަދަލު ކުރެވޭނަމަ މެނުވީއެވެ.“\n\n[ނޯޓު: ބަހަވީ ގޮތުން ރިބާއަކީ އިތުރުވުމެވެ. ޝަރުއީ ގޮތުން ރިބާއަކީ އެކަކު އަނެކަކަށް މުދާ ބަދަލު ކުރުމުގައި، ވަކި މުދާތަކެއްގައި އުނިއިތުރުކަން ގެނައުމުން، ނުވަތަ ވަކި މުދާތަކެއް ދިނުމުގައި ލަސް ކުރުމުން ހަރާމްވެގެންދާ މުއާމަލާތެކެވެ. މިއީހަލާކު ކުރަނިވި ބޮޑުވެގެންވާ ފާފައެކެވެ. ރިބާގައި އުނިއިތުރުކަން ގެނައުމަށް ކިޔަނީ ފަޟްލެވެ. އަދި ލަސް ކުރުމަށް ކިޔަނީ ނަސީއާއެވެ. ރިބާ ހިނގަނީ ދަރަންޏާއި ވިޔަފާރީ، މި ދެ ކަމުގައެވެ. \n\nރިބާގެ ފުރަތަމަ ވައްތަރު ކަމުގައިވާ ދަރަނީގެ ރިބާ (އެބަހީ: ދައިނުގެ ރިބާ) އަކީ، ދަރަންޏަށް ނަގާ މުދަލެއް ނުވަތަ އެހެން ވެސް އެއްޗެއްގެ އިތުރަށް، އަނބުރާ ދޭންޖެހޭ ކޮންމެ މަންފާއެކެވެ. އެ މަންފާއަކީ އެއްޗެއް ނުވަތަ ކަމެއް ކަމުގައި ވިޔަސް މެއެވެ. އެ ގޮތުން ދަރަންޏާ ގުޅިގެން އަންނަ އިންޓަރެސްޓާއި، ފީއާއި، ޖޫރިމަނާ އަދި ފައިދާ މީގެ ތެރެއަށް ވަންނާނެއެވެ. އަދި ދަރަންޏަށް ނަގާ މުދަލެއްގެ އިތުރަށް ކަމެއް ކޮށްދިނުމާއި، ކަމެއްގައި އެހީވުމާއި، މެދުވެރިއަކަށް ވުމާއި، ނުފޫޒު ފޯރުވައި ދިނުމާއި، ޚިދުމަތެއް ދިނުމާ އަދި މަސައްކަތު ބޮންޑެއް އަދާ ކުރަން ޖެހުން ޝަރުތު ކުރުން ވެސް މީގެ ތެރެއަށް ވަންނާނެއެވެ. އެހެނަސް ކުރީބައިގައި ޝަރުތު ކުރުމަކާ ނުލާ، ނަގާ މީހާ އަމިއްލައަށް ދަރަނީގެ އިތުރަށް އަނބުރާ އެއްޗެއް ދޭނަމަ ރިބާއަކަށް ނުވާނެއެވެ. \n \nރިބާގެ ދެވަނަ ވައްތަރު ކަމުގައިވާ ވިޔަފާރީގެ ރިބާ (އެބަހީ: ބުޔޫޢުގެ ރިބާ) ބެހިގެން ދަނީ ތިން ބަޔަކަށެވެ. ފުރަތަމަ ބަޔަކީ، އެއް ސަބަބެއް އެކުލެވޭ އެއް ޖިންސެއްގެ ރިބާ ހިނގައިފާނެ މުދާ ބަދަލު ކުރުމެވެ. މީގައި މިންވަރު ތަފާތުވުން އަދި ފަހުން ދެއްކުންވެސް ހަރާމްވެގެން ވެއެވެ. ދެވަނަ ބަޔަކީ، އެއް ސަބަބެއް އެކުލެވޭ ތަފާތު ދެ ޖިންސެއްގެ ރިބާ ހިނގައިފާނެ މުދާ ބަދަލު ކުރުމެވެ. މީގައި މިންވަރު ތަފާތުވުން ހުއްދަވެފައި، ފަހުން ދެއްކުން ހަރާމްވެގެން ވެއެވެ. ތިންވަނަ ބަޔަކީ، ތަފާތު ދެ ސަބަބެއް އެކުލެވޭ ތަފާތު ދެ ޖިންސެއްގެ ރިބާ ހިނގައިފާނެ މުދާ ބަދަލު ކުރުމެވެ. މީގައި މިންވަރު ތަފާތުވުން ވެސް އަދި ފަހުން ދެއްކުންވެސް ހުއްދަވެގެން ވެއެވެ. \n\nވިޔަފާރި މުދަލުގައި ރިބަވީ ޢިއްލަތަށް (އެބަހީ: ސަބަބަށް) ބަލާއިރު، ރިބާ ހިނގައިފާނެ މުދާތަކާއި ރިބާ ނުހިނގާނެ މުދާތަކެއް ވެއެވެ. ރިބާ ހިނގައިފާނެ (އެބަހީ: ރިބަވީ) މުދަލަކަށް ވާން ދެ ސަބަބެއް އޮވެއެވެ. އެއް ސަބަބަކީ ފައިސާއަކަށް ވުމެވެ. އަދި އަނެއް ސަބަބަކީ އާންމުކޮށް ކައިއުޅޭ ތަކެތީގެ ތެރެއިން ގިނަ ދުވަހަށް ރައްކާ ކުރެވޭ ތަކެއްޗަށް ވުމެވެ. މިނޫން މުދަލެއްގައި ރިބާ ނުހިނގާނެއެވެ. އެ ގޮތުން ބިމާއި، ގެއާއި، ކާރާއި، ޖަނަވާރު ފަދަ ތަކެތީގައި ރިބާ ނުހިނގާނެއެވެ. \n\nވިޔަފާރީގައި ފައިސާގެ މިސާލަކީ ރަނާއި ރިއްސެވެ. އަދި މީގެ ތެރޭގައި ޑޮލަރާއި ރުފިޔާ ފަދަ ކޮންމެ ގައުމެއްގެ ޚާއްސަ ފައިސާވެސް ހިމެނޭނެއެވެ. މިއިން ކޮންމެ އެއްޗަކީ މުދަލުގެ ވަކި ޖިންސެކެވެ. ދެން އާންމުކޮށް ކައިއުޅޭ ތަކެތީގެ ތެރެއިން ގިނަ ދުވަހަށް ރައްކާ ކުރެވޭ ތަކެތީގެ މިސާލަކީ ގޮދަނާއި، ހިމަގޮދަނާއި، ކަދުރާއި، ލޮނެވެ. މީގެ ތެރޭގައި ހަނޑުލާއި، ހަކުރާއި، ފުށްވެސް ހިމެނޭނެއެވެ. މިއިން ކޮންމެ އެއްޗަކީ ވެސް މުދަލުގެ ވަކި ޖިންސެކެވެ. \n\nވިޔަފާރީގައި މުދާ ބަދަލު ކުރާއިރު މިންވަރު ތަފާތުވުމުގެ ތެރޭގައި ބަރުދަނާއި، އަދަދާއި، ފެންވަރު ފަދަ ކަންކަން ތަފާތުވުން ހިމެނެއެވެ. އަދި އެއް ޖިންސެއްގެ ރިބަވީ މުދާ ބަދަލު ކުރާއިރު މިންވަރު ތަފާތުވުމުގެ މިސާލަކި، ސައްވީސް ކެރެޓު ރަނަށް އަށާރަ ރަން، ނުވަތަ އާ ރިއްސަށް ބާ ރިހި، ނުވަތަ އެއް ޑޮލަރަށް ދެ ޑޮލަރު، ނުވަތަ މޭބިސްކަދުރަށް ހިކިމޭބިސްކަދުރު، ނުވަތަ މޮޅު ކަދުރަށް ދެރަ ކަދުރު، ނުވަތަ މިންވަރު އެނގޭ ގޮދަނަށް މިންވަރު ނޭނގޭ ގޮދަން، ނުވަތަ އަޖްވާ ކަދުރަށް ސުއްކަރީ ކަދުރު، ނުވަތަ ދެ އަޖްވާ ކަދުރަށް އެއް އަޖްވާ ކަދުރު ބަދަލު ކުރުމެވެ. މިއީ ފަޟްލުގެ ރިބާއެވެ. \n\nވިޔަފާރީގައި މިންވަރު ނޭނގޭ އެއްޗަކަށް މިންވަރު އެނގޭ އެއްޗެއް ބަދަލު ކުރުމަކީ ހަރާމް ކަމެކެވެ. އެއީ ޢަރާޔާގެ މުއާމަލާތުގައި މެނުވީއެވެ. އެއީ ފަސްދޮޅަސް ޞާޢަށް ވުރެ ގިނަ ނުވާ ގޮތުގެ މަތިން، ރުކުގައި އަޅާފައިވާ ރޯކަދުރުގެ ލަފާ ކުރެވޭ މިންވަރެއްގެ ބަދަލުގައި، އެއާ އެއްވަރަށް ހިކިކަދުރު ދިނުމެވެ. މިއީ ފައިސާ ނެތިގެން، ކެއުމަށް ޓަކައި ރޯކަދުރަށް ބޭނުން ޖެހިފައިވާތީ މީސްތަކުންނަށް ލުއި ކުރުމުގެ ގޮތުން ދެވުނު ހުއްދައެކެވެ. މިއީ ރިބާގެ މުއާމަލާތް ތަކުގައި ދެވިފައިވާ އިސްތިސްނާއެކެވެ.\n\nވިޔަފާރީގައި މުދާ ބަދަލު ކުރާއިރު ފަހުން ދެއްކުމަކީ، އެއް މުދާ ދިނުމުން، ފަހުން އަނެއް މުދާ ނުވަތަ އެ މުދަލުގެ ބަޔެއްވެސް ބަދަލުގައި ލިބުމެވެ. އެބަހީ ވަގުތުންވަގުތަށް އެކީއެކައްޗަކަށް ބަދަލު ނުވާ ދެ މުދަލެކެވެ. މީގެ މިސާލަކީ ރުފިޔާ ދިނުމުން، އެއް ގަޑިއިރު ފަހުން ޑޮލަރު ބަދަލުގައި ލިބުމެވެ. މިއީ ނަސީއާގެ ރިބާއެވެ. \n\nވިޔަފާރީގައި އެއް ސަބަބެއް އެކުލެވޭ ތަފާތު ދެ ޖިންސެއްގެ ރިބާ ހިނގައިފާނެ މުދާ ބަދަލު ކުރާއިރު، ފަހުން ދެއްކުން ހަރާމްވެފައި، މިންވަރު ތަފާތުވުން ހުއްދަވެގެން ވެއެވެ. މީގެ މިސާލަކީ ފަނަރަ ރުފިޔާ ދިނުމުން، ލަސްވުމެއް ނެތި އެއް ޑޮލަރު ބަދަލުގައި ލިބުމެވެ. ނުވަތަ އެއް ގްރާމް ރަން ދިނުމުން، ވަގުތުންވަގުތަށް ފަސްދޮޅަސް ގްރާމް ރިހި ބަދަލުގައި ލިބުމެވެ. މިތާ ބަދަލު ކުރާ ތަކެތީގެ މިންވަރު ތަފާތު ކަމުގައި ވިޔަސް، އެއް ވަގުތެއްގައި ދޭތި ބަދަލު ކުރެވޭތީ މުއާމަލާތް ހުއްދަވެގެން ދެއެވެ. \n\nވިޔަފާރީގައި ތަފާތު ދެ ސަބަބެއް އެކުލެވޭ ތަފާތު ދެ ޖިންސެއްގެ ރިބާ ހިނގައިފާނެ މުދާ ބަދަލު ކުރާއިރު، ފަހުން ދެއްކުމާ އަދި ހަމައެހެންމެ މިންވަރު ތަފާތުވުން ވެސް ހުއްދަވާނެއެވެ. އެބަހީ، އާންމުކޮށް ކައިއުޅޭ ތަކެތީގެ ތެރެއިން ގިނަ ދުވަހަށް ރައްކާ ކުރެވޭ އެއްޗަކަށް ފައިސާ ބަދަލު ކުރުމެވެ. މީގެ މިސާލަކީ ފަހުން ދައްކާ ގޮތަށް، އެއް ރުފިޔާއަށް ތިން ކަދުރު ބަދަލު ކުރުމެވެ. މީގައި އަދަދު ތަފާތު ވިޔަސް އަދި ވަގުތުންވަގުތަށް ބަދަލު ނުކުރިޔަސް މައްސަލައެއް ނެތެވެ. \n\n…\n\nރިބާ ޝަރުތު ކުރެވޭ ކޮންމެ އެއްބަސްވުމެއް ހެދުމަކި ހަރާމް ކަމެކެވެ. އަދި އެއީ ބާތިލު އެއްޗެކެވެ. ދޭމީހާއަށް ވެސް ނަގާމީހާއަށް ވެސް އެ އަރައިގަނެވެނީ ބޮޑުވެގެންވާ ފާފައަކަށެވެ. އަދި ރިބާ ޗާޖު ކުރެވުމުގެ ކުރިން، މުއްދަތުގެ ތެރޭ ދައްކާ ޚަލާސް ކުރެވުނަސް އެވާނީ ހަދަން ހުއްދަ ނޫން ހަރާމް އެއްބަސްވުމަކަށެވެ. ރިބާގެ މުއާމަލާތެއްގައި މީހަކު ބައިވެރިވެއްޖެ ނަމަ، ތައުބާވާން ޖެހޭނެއެވެ. އެއީ އޭގެ ފަހުން ރިބާގެ މުއާމަލާތެއްގައި ބައިވެރި ނުވާނެ ގޮތުގެ މަތިންނެވެ. އަދި ތައުބާގެ އިތުރަށް ކުރަން ޖެހޭނެ ކަންކަން ވެއެވެ. އެ ގޮތުން ރިބާގެ ގޮތުގައި އިތުރަށް އަރާ މިންވަރު ދޭން ޖެހޭ ޖެހުން ބާތިލު ކުރަން ޖެހޭނެއެވެ. އެ ދެވިގެން ނުވާނެއެވެ. ދެވުނު ނަމަ، އަނބުރާ ރައްދު ކުރެވެން ޖެހޭނެއެވެ. ބާތިލު ކުރެވެން ނުވަތަ ރައްދު ކުރެވެން ނެތް ނަމަ، ރިބާގެ ގޮތުގައި އަރާ އިތުރު މިންވަރު ސަދަގާތުގެ ގޮތުގައި ދީ ހުސް ކުރަން ޖެހޭނެއެވެ. މިއީ ހެޔޮ އަމަލެއް ކުރުމުގެ ގޮތުން ދޭ ސަދަގާތެއް ނޫނެވެ. އެހެނީ އެހެން ހެދޭނީ ހަލާލު މުދަލުންނެވެ. އެހެނެއްކަމަކު މިއީ ހަރާމް ރިބާ ނައްތާލުމަށް ޓަކައި ކުރާ ކަމެކެވެ. އެއްގޮތަކަށް ވެސް މި ކަންތައްތައް ކުރެވެން ނެތް ނަމަ، ތައުބާއިން އެކަނި ފުދޭނެއެވެ. \n\nވިޔަފާރި ކުރުމަށް ބަޔަކަށް ކެޕިޓަލްގެ ގޮތުގައި ފައިސާ ދޭ ނަމަ، އެ ވިޔަފާރީގެ ފައިދާ ނުވަތަ ގެއްލުން ހާސިލު ވުމުގެ ކުރިން، އެ ދިން ކެޕިޓަލް ނުވަތަ ޑިޕޮސިޓްގެ ނިސްބަތަށް ބިނާކޮށް، ކަނޑައެޅިގެން ވަކި މިންވަރެއް ނުވަތަ އިންސައްތައެއް ފައިދާގެ ގޮތުގައި ދޭން ކުރީބައިގައި އެއްބަސްވުމަކީ ހަރާމް ކަމެކެވެ. މީގެ މިސާލަކީ ބޭންކެއްގައި ފައިސާ ބެހެއްޓުމުން، ވިޔަފާރީގެ ފައިދާ ހާސިލު ވުމުގެ ކުރިން، އެ ބެހެއްޓި ފައިސާގެ މިންވަރަށް ބިނާކޮށް، ކޮންމެ އަހަރަކު ވަކި ނިސްބަތެއް ނުވަތަ އަދަދެއް އެ ބެހެއްޓި މީހާއަށް ދޭން އެއްބަސްވުމެވެ. ބޭންކުން މީހުންނަށް ދެނީ ލޯނެވެ. އަދި މީހުން ބޭންކަށް ދެނީ ޑިޕޮސިޓެވެ. ލޯނެއް ނޫނެވެ. އެ ޑިޕޮސިޓުގައި، ނުކުރެވޭ ވިޔަފާރިއަކުން ލިބިގެންދާނެ ފައިދާއެއް ގެއްލުމެއް ނޭނގެނީސް ވަކި މިންވަރެއް އިންޓަރެސްޓުގެ ގޮތުގައި ކުރީބައިގައި ދޭން އެއްބަސްވާތީ، މި ޖަހާލަތުގެ ސަބަބުން މުއާމަލާތް ރިބާއަށްވެ ހަރާމް ވެގެންދެއެވެ.\n\nނަމަވެސް ފައިދާއާއި ގެއްލުން ދެކޮޅަށް ހިއްސާވާ ގޮތަށް، އެ ވިޔަފާރީގެ ނަތީޖާ ހަގީގަތުގައިވެސް ހާސިލުވުމަށް ފަހު ބަހާ ނަމަ، ލިބޭ ފައިދާއާއި ގެއްލުމުގެ ނިސްބަތަށް ބިނާކޮށް އިންސައްތައެއް ދޭން އެއްބަސްވުމަކީ ހުއްދަ ކަމެކެވެ. މިއީ އިސްލާމީ ބޭންކުތަކުގައި ހަދާ ގޮތެވެ. \n\nއަދި ބަޔަކަށް ފައިސާ ދިނުމުން، އޭގެން ރިބާގެ މުއާމަލާތް ކުރާނެކަން އެނގޭ ނަމަ، އެ ބަޔަކަށް ފައިސާ ދިނުން ހަރާމް ވާނެއެވެ. މީގެ މިސާލަކީ ބޭންކެއްގައި ފައިސާ ބެހެއްޓުމުން، އެ ފައިސާ އެހެން މީހަކަށް ރިބަވީ ލޯނެއްގެ ގޮތުގައި ދޫކުރުމެވެ. އަދި ރިބަވީ ލޯނެއް ނެގި މީހަކަށް، އެ ލޯނު ދައްކާ ޚަލާސް ކުރުމަށް މާލީ އެހީ ދިނުމަކީ ވެސް ހަރާމް ކަމެކެވެ. އެއީ ދެން އެކަން ނުކުރާ ގޮތަށް ތައުބާ ވާނަމަ މެނުވީއެވެ.]","[البخاري 2134 واللفظ له، ومسلم 1586]"],["#281","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الربا والصرف","ރިބާއާއި މާރު ކުރުމުގެ ބާބު","عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ - رَضِيَ اللَّهُ عَنْهُ - أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: (لا تَبِيعُوا الذَّهَبَ بِالذَّهَبِ إلاَّ مِثْلاً بِمِثْلٍ. وَلا تُشِفُّوا بَعْضَهَا عَلَى بَعْضٍ. وَلا تَبِيعُوا الْوَرِقَ بِالْوَرِقِ إلاَّ مِثْلاًّ بِمِثْلٍ. وَلا تُشِفُّوا بَعْضَهَا عَلَى بَعْضٍ. وَلا تَبِيعُوا مِنْهَا غَائِباً بِنَاجِزٍ).\n\nوَفِي لَفْظٍ (إلاَّ يَداً بِيَدٍ).\n\nوَفِي لَفْظٍ (إلاَّ وَزْناً بِوَزْنٍ، مِثْلاً بِمِثْلٍ سَوَاءً بِسَوَاءٍ).","އަބޫ ސަޢީދު އަލްޚުދްރީ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ރަނަށް ރަން ނުވިއްކާށެވެ. އެއީ އެއްވަރަކަށް ވާނަމަ މެނުވީއެވެ. އަދި އެއިން މިންވަރެއް، އުނިއިތުރު މިންވަރަކަށް ބަދަލު ނުކުރާށެވެ. އަދި ތިޔަބައިމީހުން ރިއްސަށް ރިހި ނުވިއްކާށެވެ. އެއީ އެއްވަރަކަށް ވާނަމަ މެނުވީއެވެ. އަދި އެއިން މިންވަރެއް، އުނިއިތުރު މިންވަރަކަށް ބަދަލު ނުކުރާށެވެ. އަދި އެއިން އެއްޗެއް، ފަހުން އަގު ދޭ ގޮތަށް ނުވިއްކާށެވެ.“\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”އަތުން އަތަށް (ވަގުތުން ވަގުތައް) ވިއްކާ ނަމަ މެނުވީއެވެ.“\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”އެއް ބަރުދަނަކަށް، އެއްވަރަކަށް، އެކައްޗަކަށް ވިއްކާ ނަމަ މެނުވީއެވެ.“","[البخاري 2177، ومسلم 1584\n\nمسلم 1584\n\nمسلم 1584]"],["#282","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الربا والصرف","ރިބާއާއި މާރު ކުރުމުގެ ބާބު","عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (جَاءَ بِلالٌ إلَى رَسُولِ اللَّهِ ﷺ بِتَمْرٍ بَرْنِيِّ فَقَالَ لَهُ النَّبِيُّ ﷺ: مِنْ أَيْنَ لَكَ هَذَا؟ قَالَ بِلالٌ: كَانَ عِنْدَنَا تَمْرٌ رَدِيءٌ، فَبِعْتُ مِنْهُ صَاعَيْنِ بِصَاعٍ لِيَطْعَمَ النَّبِيُّ ﷺ. فَقَالَ النَّبِيُّ ﷺ عِنْدَ ذَلِكَ: أَوَّهْ، أَوَّهْ، عَيْنُ الرِّبَا، عَيْنُ الرِّبَا، لا تَفْعَلْ. وَلَكِنْ إذَا أَرَدْتَ أَنْ تَشْتَرِيَ فَبِعْ التَّمْرَ بِبَيْعٍ آخَرَ. ثُمَّ اشْتَرِ بِهِ).","އަބޫ ސަޢީދު އަލްޚުދްރީ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: (މޮޅު) ބަރްނީ ކަދުރު ހިފައިގެން، ނަބިއްޔާ ﷺގެ އަރިހަށް ބިލާލު އަތުވިއެވެ. ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ މި ގެނައި ކޮންތާކުންތޯއެވެ؟“ ބިލާލު ދެންނެވިއެވެ: ތިމަންނަ އަތުގައި ދެރަ ކަދުރު އޮތެވެ. ދެން އެ ދެރަ ކަދުރުގެ ދެ ޞާޢަށް، (މި މޮޅު ކަދުރުގެ) އެއް ޞާޢު ތިމަން ވިއްކީމެވެ. އެއީ ނަބިއްޔާ ﷺ ފަރީކުޅުވުމަށެވެ. ދެން ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ހުރި ހިތާމައެއްގެ ބޮޑުކަމާއެވެ. ހުރި ހިތާމައެއްގެ ބޮޑުކަމާއެވެ. ތިއީ ހަމަ ސީދާ ރިބާއެވެ. ތިފަދައިން ކަންތައް ނުކުރާށެވެ. އެހެނެއްކަމަކު ތިބާ ގަންނަން ބޭނުން ނަމަ، ކަދުރު (ފުރަތަމަ ފައިސާއަށް) ވިއްކުމަށް ފަހު ދެން އޭގެން ގަންނާށެވެ.","[البخاري 2312، ومسلم 1594]"],["#283","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الربا والصرف","ރިބާއާއި މާރު ކުރުމުގެ ބާބު","عَنْ أَبِي الْمِنْهَالِ قَالَ: (سَأَلْتُ الْبَرَاءَ بْنَ عَازِبٍ وَزَيْدَ بْنَ أَرْقَمَ، عَنْ الصَّرْفِ؟ فَكُلُّ وَاحِدٍ يَقُولُ: هَذَا خَيْرٌ مِنِّي. وَكِلاهُمَا يَقُولُ: نَهَى رَسُولُ اللَّهِ ﷺ عَنْ بَيْعِ الذَّهَبِ بِالْوَرِقِ دَيْناً).","އަބޫ މިންހާލު ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އަލްބަރާއު ބިން ޢާޒިބާއި، ޒައިދު ބިން އަރްޤަމު رَضِيَ اللَّهُ عَنْهُمَا ގާތު މާރުކުރުމާ ބެހޭ ގޮތުން ތިމަން ސުވާލު ކުރީމެވެ. އަދި އެއިން ކޮންމެ ބޭކަލެއްވެސް ވިދާޅުވީ ’ތިމަންނައަށް ވުރެ (އަނެކާއާ ސުވާލު ކުރުން) ހެޔޮވެގެން ވެއެވެ.‘ އަދި އެ ދެބޭކަލުން ވެސް ވިދާޅުވިއެވެ: ފަހުން ދައްކާ ގޮތަށް، ރަނަށް ރިހި ބަދަކު ކުރުން ރަސޫލު ﷲ ﷺ މަނާ ކުރެއްވިއެވެ.","[البخاري 2180، 2181 واللفظ له، ومسلم 1589]"],["#284","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الربا والصرف","ރިބާއާއި މާރު ކުރުމުގެ ބާބު","عَنْ أَبِي بَكْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (نَهَى رَسُولُ اللَّهِ ﷺ عَنْ الْفِضَّةِ بِالْفِضَّةِ، وَالذَّهَبِ بِالذَّهَبِ، إلاَّ سَوَاءً بِسَوَاءٍ، وَأَمَرَنَا: أَنْ نَشْتَرِيَ الْفِضَّةَ بِالذَّهَبِ، كَيْفَ شِئْنَا. وَنَشْتَرِيَ الذَّهَبَ بِالْفِضَّةِ كَيْفَ شِئْنَا. قَالَ: فَسَأَلَهُ رَجُلٌ فَقَالَ: يَدًا بِيَدٍ؟ فَقَالَ: هَكَذَا سَمِعْتُ).","އަބޫ ބަކްރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރިއްސަށް ރިހި، އަދި ރަނަށް ރަން ބަދަލު ކުރުން ނަބިއްޔާ ﷺ މަނާ ކުރެއްވިއެވެ. އެއީ އެއްވަރަކަށް ވާނަމަ މެނުވީއެވެ. އަދި ރަނަށް ރިހި ބޭނުން ގޮތަކަށް ގަންނަން ތިމަންމެންނަށް އެންގެވިއެވެ. އަދި ރިއްސަށް ރަން ބޭނުން ގޮތަކަށް ގަންނަން ތިމަންމެންނަށް އެންގެވިއެވެ. މީހަކު، އަބޫ ބަކްރާ ގާތު އެއްސެވިއެވެ: އަތުން އަތަށްތޯއެވެ؟ އަބޫ ބަކްރާ ދެންނެވިއެވެ: ތިމަން އަޑުއެހީ (ނަބިއްޔާ) މި ފަދައިން ޙަދީޘް ކުރައްވަނިކޮށެވެ.","[البخاري 2182، ومسلم 1590 واللفظ له]"],["#285","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا (أَنَّ رَسُولَ اللَّهِ ﷺ اشْتَرَى مِنْ يَهُودِيٍّ طَعَاماً، وَرَهَنَهُ دِرْعاً مِنْ حَدِيدٍ).","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ (ފަހުން ދައްކަވާ ގޮތަށް) ޔަހޫދީއެއްގެ އަތުން ގޮދަން ބައްލަވައިގަތެވެ. އަދި ނަބިއްޔާ ދަގަނޑު ދަހަނާކޮޅެއް ރަހުނެއްގެ ގޮތުގައި އެ މީހާ އަތުގައި ބެހެއްޓެވިއެވެ.","[البخاري 2068، ومسلم 1603 واللفظ له]"],["#286","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: (مَطْلُ الْغَنِيِّ ظُلْمٌ. فَإِذَا أُتْبِعَ أَحَدُكُمْ عَلَى مَلِيءٍ فَلْيَتْبَعْ).","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”(ދަރަނި އަދާ ކުރުމަށް ކުޅަދާނަވެގެންވާ) ފުދުންތެރި މީހަކު (އެ އަދާނުކޮށް) ލަސް ކުރުމަކީ އަނިޔާއެކެވެ. އަދި ތިޔަބައިމީހުން ކުރެ މީހަކު ދަރަންޏެއް ދޫކޮށްފައިވާ ވާނަމަ، އެ ދަރަނި އަދާ ކުރަން ޖެހޭ ފަރާތަށް އެހެން ފުދުންތެރި މީހަކު ދަރާފައިވާ ނަމަ، އެ (ތިން ވަނަ ފުދުންތެރި) މީހާގެ އަތުން އެ ދަރަނި ނަގާ ގޮތަށް ތިބާ ގާތު އެދިއްޖެއްޔާ އެކަމަށް ރުހޭށެވެ.“","[البخاري 2287، ومسلم 1564]"],["#287","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ أَوْ قَالَ: سَمِعْتُ النَّبِيَّ ﷺ يَقُولُ -: (مَنْ أَدْرَكَ مَالَهُ بِعَيْنِهِ عِنْدَ رَجُلٍ - أَوْ إنْسَانٍ - قَدْ أَفْلَسَ فَهُوَ أَحَقُّ بِهِ مِنْ غَيْرِهِ).","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”(މީހަކު، އެހެން މީހަކަށް މުދަލެއް ބަހައްޓަން ދީފައި، ނުވަތަ އެހެން މީހަކަށް މުދަލެއް ވިއްކާފައި އޭގެ އަގު ލިބުމުގެ ކުރިން، އެ ނެގި މީހަކީ ބަނގުރޫޓު ވެފައިވާ މީހެއްކަން އެނގިއްޖެއްޔާ) އެ ބަނގުރޫޓު ވެފައިވާ މީހާގެ އަތުގައި، އޭނާގެ މުދާ (ބަދަލެއް ނައިސް) އިންގޮތަށް ފެނިއްޖެ ނަމަ، ފަހެ އެހެން ބަޔަކަށް ވުރެ އެ މުދާ ލިބުން ހައްގުވަނީ އެ (ދިން) މީހާއަށެވެ.“","[البخاري 2402، ومسلم 1559]"],["#288","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رَضِيَ اللَّهُ عَنْهُمَا قَالَ (جَعَلَ - وَفِي لَفْظٍ: (قَضَى النَّبِيُّ ﷺ بِالشُّفْعَةِ فِي كُلِّ مَا لَمْ يُقْسَمْ. فَإِذَا وَقَعَتِ الْحُدُودُ، وَصُرِّفَتِ الطُّرُقُ: فَلا شُفْعَةَ).","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ކޮންމެ (ބައެއްގެ މެދުގައި) ގޯތިގެދޮރެއް ބަހައި ނިންމުމުގެ ކުރިން، އޭގެން ހިއްސާވެރިއަކު އޭނާގެ ބައި ވިއްކާ ނަމަ، އެހެން ފަރާތަކަށް ވުރެ ކުރިން ދެން ތިބި ހިއްސާވެރިންނަށް އެ ހިއްސާ ގަތުމުގެ ހައްގު، ރަސޫލު ﷲ ﷺ ދެއްވިއެވެ. ނަމަވެސް އެ ގޯތިގެދޮރު ބަހައި، ހައްދުތައް ވަކިކުރެވި، މަގުތައް ކަނޑައެޅުމަށް ފަހު ދެން އެ ހައްގެއް ނޯންނާނެއެވެ.","[البخاري 2213، ومسلم 1647]"],["#289","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (قَدْ أَصَابَ عُمَرُ أَرْضاً بِخَيْبَرَ. فَأَتَى النَّبِيَّ ﷺ يَسْتَأْمِرُهُ فِيهَا. فَقَالَ: يَا رَسُولَ اللَّهِ، إنِّي أَصَبْتُ أَرْضاً بِخَيْبَرَ، لَمْ أُصِبْ مَالاً قَطُّ هُوَ أَنْفَسُ عِنْدِي مِنْهُ، فَمَا تَأْمُرُنِي بِهِ؟ فَقَالَ: إنْ شِئْتَ حَبَسْتَ أَصْلَهَا، وَتَصَدَّقْتَ بِهَا. قَالَ: فَتَصَدَّقَ بِهَا. غَيْرَ أَنَّهُ لا يُبَاعُ أَصْلُهَا، وَلا يُوهَبُ، وَلا يُورَثُ. قَالَ: فَتَصَدَّقَ عُمَرُ فِي الْفُقَرَاءِ، وَفِي الْقُرْبَى، وَفِي الرِّقَابِ، وَفِي سَبِيلِ اللَّهِ، وَابْنِ السَّبِيلِ، وَالضَّيْفِ. لا جُنَاحَ عَلَى مَنْ وَلِيَهَا: أَنْ يَأْكُلَ مِنْهَا بِالْمَعْرُوفِ، أَوْ يُطْعِمَ صَدِيقاً، غَيْرَ مُتَمَوِّلٍ فِيهِ).\n\nوَفِي لَفْظٍ: (غَيْرَ مُتَأَثِّلٍ).","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ޚައިބަރުގައި ޢުމަރުގެފާނަށް ބިމެއް ލިބިވަޑައިގަތެވެ. ދެން އެއާމެދު ކަން ކުރަންވީ ގޮތް އައްސަވަން ރަސޫލު ﷲ ﷺގެ އަރިހަށް ވަޑައިގެން ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން ޚައިބަރުން ބިމެއް ތިމަންނައަށް ލިބިއްޖެއެވެ. އެއަށްވުރެ ލޮބުވެތިކަން ބޮޑު މުދަލެއް ތިމަންނައަށް މީގެ ކުރިން ލިބިފައެއް ނުވެއެވެ. ފަހެ އެއާމެދު އިބަ ނަބިއްޔާ ތިމަންނައަށް އަމުރު ކުރައްވަނީ ކިހިނަކުންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ ބޭނުން ނަމަ، އެ ބިމުގެ އަސްލު ތިބާ އަތުގައި ބަހައްޓަވާފައި، އޭގެން (ލިބޭ ތަކެތީން) ސަދަގާތް ކުރާށެވެ.“ (އެބަހީ: ވަގްފު ކުރާށެވެ.) ދެން ޢުމަރުގެފާނު އެ ބިން (ވަގްފު ކުރެއްވިއެވެ.) އެއީ އެ ބިމުގެ އަސްލު ނުވިއްކާ، ވާރުތަ ނުވާ އަދި ހިބައަށް ނުދެވޭ ގޮތަށެވެ. އެ ގޮތުން ފަގީރުންނާއި، ގާތް ތިމާގެ މީހުންނާއި، އަޅުން މިނިވަން ކުރުމަށާއި، ﷲގެ މަގުގައި ޖިހާދު ކުރުމަށާއި، ހާލުގައި ޖެހިފައިވާ ދަތުރުވެރިންނަށާއި، މެހެމާނުންނަށް އެ ތަން ވަގްފު ކުރެއްވިއެވެ. އަދި އެ ގޯތި ބެލުމަށް ހަވާލު ކުރެވުނު ބަޔަކު އޭގެން ހެޔޮ ގޮތުގައި ކެއުމަށާއި، ރަހުމަތްތެރިއަކަށް ކާންދިނުމުގައި މައްސަލައެއް ނެތެވެ. އެއީ އޭގެން އޭނާ މުދާ ގިނަ ކުރުމުގެ ބޭނުމުގައި ނުނަގާ ގޮތުގެ މަތިންނެވެ. \n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: އެއީ އޭގެން އޭނާއަށް މުދާ މިލްކު ނުވާ ގޮތުގެ މަތިންނެވެ.","[البخاري 2737، ومسلم 1632 واللفظ له\n\nمسلم 1632]"],["#290","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ عُمَرَ- رَضِيَ اللَّهُ عَنْهُ - قَالَ: (حَمَلْتُ عَلَى فَرَسٍ فِي سَبِيلِ اللَّهِ، فَأَضَاعَهُ الَّذِي كَانَ عِنْدَهُ، فَأَرَدْتُ أَنْ أَشْتَرِيَهُ، فَظَنَنْتُ أَنَّهُ يَبِيعُهُ بِرُخْصٍ. فَسَأَلْتُ النَّبِيَّ ﷺ؟ فَقَالَ: لا تَشْتَرِهِ. وَلا تَعُدْ فِي صَدَقَتِكَ، وَإِنْ أَعْطَاكَهُ بِدِرْهَمٍ. فَإِنَّ الْعَائِدَ فِي هِبَتِهِ كَالْعَائِدِ فِي قَيْئِهِ).","ޢުމަރު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ޖިހާދު ކުރުމަށް ތިމަން އަހެއް ސަދަގާތް ކުރީމެވެ. ފަހެ އެ އަސް ގެންގުޅުނު މީހާ އެއަށް ފަރުވާ ކުޑަކުރިއެވެ. ވީމާ އެ އަސް އަނބުރާ ގަންނަން ތިމަން ބޭނުން ވިއެވެ. އަދި އެ މީހާ އަގު ހެޔޮކޮށް އެ ވިއްކާނެ ކަމަށް ތިމަން ހީކުރީމެވެ. ދެން (އެކަމާ މެދު) ނަބިއްޔާ ﷺ އަރިހު ތިމަން ސުވާލު ކުރީމެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ ނުގަންނާށެވެ. އަދި ތިބާ ދިން ސަދަގާތް އަނބުރާ ނުނަގާށެވެ. އެއީ އެންމެ ދިރުހަމަކަށް އޭނާ އޭތި ވިއްކި ކަމުގައި ވިޔަސް މެއެވެ. އެހެނީ ހަމަކަށަވަރުން މީހަކު އެއްޗެއް ހިބަކޮށް ދީފައި އަނބުރާ ނެގުމަކީ، މީހަކު ހޮޑުލާފައި އެ ހޮޑު އަނބުރާ ނަގާ ކެއުން ފަދަ ކަމެކެވެ.“","[البخاري 1490 واللفظ له، ومسلم 1620]"],["#291","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","وَعَن ابنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُمَا: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: (العَائِدُ فِي هِبَتِهِ، كَالعَائِدُ فِي قَيئِهِ).\n\nوَفِي لَفظٍ: (فَإِنَّ الَّذِى يَعُودُ فِي صَدَقَتِهِ كَالكَلبُ [يَقَئُ ثُمَّ] يَعُودُ فِي قَيئِهِ).","އިބްނު ޢައްބާސް رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”މީހަކު އެއްޗެއް ހިބަކޮށް ދީފައި އަނބުރާ ނެގުމަކީ، މީހަކު ހޮޑުލާފައި އެ ހޮޑު އަނބުރާ ނަގާ ކެއުން ފަދަ ކަމެކެވެ.“ \n\n”އަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: މީހަކު ސަދަގާތެއް ދީފައި އަނބުރާ ނެގުމަކީ، ކުއްތާއެއް ހޮޑުލާފައި އެ ހޮޑު އަނބުރާ ކެއުން ފަދަ ކަމެކެވެ.“","[البخاري 2621، ومسلم 1622\n\nالبخاري 2623، ومسلم 1620 واللفظ له]"],["#292","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنِ النُّعْمَانِ بْنِ بَشِيرٍ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (تَصَدَّقَ عَلَيَّ أَبِي بِبَعْضِ مَالِهِ. فَقَالَتْ أُمِّي عَمْرَةُ بِنْتُ رَوَاحَةَ: لا أَرْضَى حَتَّى تُشْهِدَ رَسُولَ اللَّهِ ﷺ. فَانْطَلَقَ أَبِي إلَى رَسُولِ اللَّهِ ﷺ لِيُشْهِدَ عَلَى صَدَقَتِي فَقَالَ لَهُ رَسُولُ اللَّهِ ﷺ: أَفَعَلْتَ هَذَا بِوَلَدِكَ كُلِّهِمْ؟ قَالَ: لا. قَالَ: اتَّقُوا اللَّهَ وَاعْدِلُوا فِي أَوْلادِكُمْ، فَرَجَعَ أَبِي، فَرَدَّ تِلْكَ الصَّدَقَةَ).\n\nوَفِي لَفْظٍ: (فَلا تُشْهِدْنِي إذاً. فَإِنِّي لا أَشْهَدُ عَلَى جَوْرٍ)\n\nوَفِي لَفْظٍ: (فَأَشْهِدْ عَلَى هَذَا غَيْرِي).","ނުޢުމާން ބިން ބަޝީރު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ތިމަންނަގެ ބައްޕަ އެކަލޭގެފާނުގެ މުދަލުގެ ބަޔަކުން، ތިމަންނައަށް ސަދަގާތް ކުރެއްވިއެވެ. ފަހެ ތިމަންނަގެ މަންމަ ޢަމްރާ ބިންތި ރަވާޙާ ދެންނެވިއެވެ: (ތިކަމުގެ މައްޗަށް) ރަސޫލު ﷲ ﷺ ހެކި ނުކުރައްވަނީސް ތިމަން ކަމަނާ ނުރުހޭނަމެވެ. ދެން ތިމަންނައަށް ދެއްވި ސަދަގާތުގެ މައްޗަށް ހެކި ކުރެއްވުމަށް ޓަކައި، ނަބިއްޔާގެ އަރިހަށް ތިމަންނަގެ ބައްޕަ ނުކުންނެވިއެވެ. ފަހެ ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާގެ ހުރިހާ ދަރިންނަށް ތިފަދައިން ކަންތައް ކުރެއްވިންތޯއެވެ؟“ ބައްޕަ ދެންނެވިއެވެ: ނޫނެކެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲ އަށް ބިރުވެތިވާށެވެ. އަދި ތިބާގެ ދަރިންގެ މެދުގައި އަދުލުވެރިވާށެވެ.“ ދެން ތިމަންނަގެ ބައްޕަ އެނބުރި ގެއަށް ދުރުވެ، އެ ސަދަގާތް އަނބުރާ ނެންގެވިއެވެ. \n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”އެހެން ކަމުގައި ވަނީ ނަމަ، ތިމަން ނަބިއްޔާ ހެކި ނުކުރާށެވެ. އެހެނީ ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާ ބޭއިންސާފަކަށް ހެކިވެރިއެއް ނުވާނަމެވެ.“ \n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”ތިކަމުގެ މައްޗަށް ތިމަން ނަބިއްޔާ ނޫން މީހަކު ހެކި ކުރާށެވެ.“","[البخاري 2587، ومسلم 1623 واللفظ له\n\nمسلم 1623\n\nمسلم 1623]"],["#293","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا (أَنَّ النَّبِيَّ ﷺ عَامَلَ أَهْلَ خَيْبَرَ بِشَطْرِ مَا يَخْرُجُ مِنْهَا مِنْ تَمْرٍ أَوْ زَرْعٍ).","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ޚައިބަރު (ފަތަހަ ކުރެވި އެ ބިންތައް މުސްލިމުންނަށް ލިބުމަށް ފަހު، އެ ތަނުގެ ޔަހޫދީ) އަހުލުވެރިން، އެބައިމީހުން ދަނޑުވެރިކަން ކޮށްގެން ނެރޭ ކަދުރު ނުވަތަ ގޮވާމުގެ ދެބައިކުޅަ އެއްބައި (ޚަރާޖުގެ ގޮތުގައި) ދޭ ގޮތަށް، ނަބިއްޔާ ﷺ އެބައިމީހުންނަށް އެ ދަނޑުބިންތަކުގައި މަސައްކަތްކޮށް އުޅުމުގެ ހުއްދަ ދެއްވިއެވެ.","[البخاري 2329، ومسلم 1551]"],["#294","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ رَافِعِ بْنِ خَدِيجٍ قَالَ: (كُنَّا أَكْثَرَ الأنْصَارِ حَقْلاً. وَكُنَّا نُكْرِي الأرْضَ، عَلَى أَنَّ لَنَا هَذِهِ، وَلَهُمْ هَذِهِ فَرُبَّمَا أَخْرَجَتْ هَذِهِ، وَلَمْ تُخْرِجْ هَذِهِ فَنَهَانَا عَنْ ذَلِكَ. فَأَمَّا بِالْوَرِقِ: فَلَمْ يَنْهَنَا).","ރާފިޢު ބިން ޚަދީޖު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ތިމަންމެންވީ އަންސާރުންގެ ތެރެއިން އެންމެ ދަނޑުބިން ގިނަ މީހުންނަށެވެ. އަދި ތިމަންމެން ދަނޑުބިން ކުއްޔަށް ދޫކޮށް އުޅުނީމެވެ. އެއީ މި ގޮތުގެ މަތިންނެވެ: ބިމުގެ އެއް ބައިން ހެދޭ ތަކެތި ވާނީ ތިމަންމެންނަށެވެ. އަދި އަނެއް ބައިން ހެދޭ ތަކެތި ވާނީ އެ ކުއްޔަށް ދޫކުރި މީހުންނަށެވެ. ދެން ފަހަރެއްގައި އެއް ބައިން ތަކެތި ހެދިދާނެއެވެ. އަދި އަނެއް ބައިން އެއްޗެއް ނެހެދިދާނެއެވެ. ވީމާ އެކަން ކުރުން ނަބިއްޔާ ތިމަންމެންނަށް މަނާ ކުރެއްވިއެވެ. އެހެނެއްކަމަކު (ކުލީގެ އަގު) ރިހިން ދެއްކުން މަނަލެއް ނުކުރައްވައެވެ.","[البخاري 2327، ومسلم 1547 واللفظ له]"],["#295","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","وَلِمُسْلِمٍ عَنْ حَنْظَلَةَ بْنِ قَيْسٍ قَالَ: (سَأَلْتُ رَافِعَ بْنَ خَدِيجٍ عَنْ كِرَاءِ الأَرْضِ بِالذَّهَبِ وَالْوَرِقِ؟ فَقَالَ: لا بَأْسَ بِهِ. إنَّمَا كَانَ النَّاسُ يُؤَاجِرُونَ عَلَى عَهْدِ رَسُولِ اللَّهِ ﷺ بِمَا عَلَى الْمَاذِيَانَاتِ، وَأَقْبَالِ الْجَدَاوِلِ، وَأَشْيَاءَ مِنْ الزَّرْعِ فَيَهْلِكُ هَذَا، وَيَسْلَمُ هَذَا وَلَمْ يَكُنْ لِلنَّاسِ كِرَاءٌ إلاَّ هَذَا. وَلِذَلِكَ زَجَرَ عَنْهُ. فَأَمَّا شَيْءٌ مَعْلُومٌ مَضْمُونٌ: فَلا بَأْسَ بِهِ).","އަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ޙަންޠަލާ ބިން ގައިސް ރިވާ ކުރެއްވިއެވެ: ރަނާއި ރިހި ދޭގޮތަށް ބިން ކުއްޔަށް ދޫކުރުމާމެދު ރާފިޢު ބިން ޚަދީޖު ގާތު ތިމަން އެހީމެވެ. ރާފިޢު ވިދާޅުވިއެވެ: އެ ކަމުގައި މައްސަލައެއް ނެތެވެ. ރަސޫލު ﷲ ﷺގެ ޒަމާނުގައި މީސްތަކުން (ބިން) ކުއްޔަށް ދޫކޮށް އުޅުނީ، ހަމައެކަނި ބޮޑެތި ކޯރުތައް ކައިރީގައި ހެދޭ ތަކެއްޗާއި، ކުދި ކޯރުތައް ފެށޭ ހިސާބުގައި ހެދޭ ތަކެއްޗާއި، ދަނޑުބިންތަކުގެ ވަކިވަކި ބައިބައިތަކުގައި ހެދޭ ތަކެއްޗެވެ. ދެން ފަހަރެއްގައި އެއް ބައިން ހެދޭ ތަކެތި ހަލާކުވެދާނެއެވެ. އަދި އަނެއް ބައިން ހެދޭ ތަކެތި ސަލާމަތުން ހުރެދާނެއެވެ. އޭރު މި ނޫން ގޮތަކަށް މީސްތަކުން ބިން ކުއްޔަށް ދޫކޮށެއް ނޫޅެއެވެ. އެހެން ކަމުން އެ ގޮތަށް ކަންކުރުން ނަބިއްޔާ މަނާ ކުރެއްވިއެވެ. އެހެނެއްކަމަކު (ކުލީގެ އަގަކީ) ކަނޑައެޅިގެން އެނގޭ ޔަގީން އެއްޗެއް ނަމަ، މައްސަލައެއް ނެތެވެ.","[مسلم 1547]"],["#296","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (قَضَى رَسُولُ اللَّهِ ﷺ بِالْعُمْرَى لِمَنْ وُهِبَتْ لَهُ).\n\nوَفِي لَفْظٍ: (مَنْ أُعْمِرَ عُمْرَى لَهُ وَلِعَقِبِهِ. فَإِنَّهَا لِلَّذِي أُعْطِيَهَا. لا تَرْجِعُ إلَى الَّذِي أَعْطَاهَا ; لأَنَّهُ أَعْطَى عَطَاءً وَقَعَتْ فِيهِ الْمَوَارِيثُ). وَقَالَ جَابِرٌ: (إنَّمَا الْعُمْرَى الَّتِي أَجَازَهَا رَسُولُ اللَّهِ ﷺ، أَنْ يَقُولَ: هِيَ لَكَ وَلِعَقِبِكَ، فَأَمَّا إذَا قَالَ: هِيَ لَك مَا عِشْتَ: فَإِنَّهَا تَرْجِعُ إلَى صَاحِبِهَا).\n\nوَفِي لَفْظٍ لِمُسْلِمٍ: (أَمْسِكُوا عَلَيْكُمْ أَمْوَالَكُمْ وَلا تُفْسِدُوهَا، فَإِنَّهُ مَنْ أَعْمَرَ عُمْرَى فَهِيَ لِلَّذِي أُعْمِرَهَا: حَيّاً، وَمَيِّتاً، وَلِعَقِبِهِ).","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އުމުރު ދުވަހަށް (އެބަހީ: މަރުވަންދެން) އެއްޗެއް ހިބަކޮށް ދީފި ނަމަ، އެ އެއްޗެއް (އެއްކޮށް) ލިބިގެން ވަނީ އެ ދެވުނު މީހާއަށް ކަމަށް ނަބިއްޔާ ﷺ ނިޔާ ކުރެއްވިއެވެ. (އެބަހީ: އެ ދިން މީހާއަށް އަނބުރައެއް ނުލިބޭނެއެވެ.)\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: އުމުރު ދުވަހަށް އެއްޗެއް ހިބަކޮށް ލިބިއްޖެ މީހަކަށް، ފަހެ އެ އެއްޗެއް ވަނީ އޭނާއަށާއި އޭނާގެ ދަރިފަސްކޮޅަށެވެ. އެހެނީ ހަމަކަށަވަރުން އެ އެއްޗެއް ވަނީ އެ ލިބުނު ފަރާތަކަށެވެ. އެ ދިން ފަރާތަކަށް އަނބުރައެއް ނުލިބޭނެއެވެ. ސަބަބަކީ އެ އެއްޗަކީ ވާރުތައަށް ވަންނާނެ އެއްޗެކެވެ.\n\nއަދި ޖާބިރު ވިދާޅުވިއެވެ: ހިބައިގެ ގޮތުގައި އުމުރު ދުވަހަށް ދެވޭ ދިނުން ނަބިއްޔާ ﷺ ހުއްދަ ކުރެއްވި ހަމައެކަނި ގޮތަކީ، މިއީ ތިބާއަށާއި ތިބާގެ ދަރިފަސްކޮޅަށް ދެވޭ އެއްޗެކޭ، މިފަދައިން ބުނުމެވެ. (އެބަހީ: މިލްކު ވާނެއެވެ.) އެހެނެއްކަމަކު، މިއީ ތިބާ ދިރިހުރިހާ ދުވަހެއް ވަންދެން (ތިބާ ގެންގުޅެން) ދޭ އެއްޗެކޭ، މިފަދައިން ބުނެފިނަމަ، ފަހެ ހަމަކަށަވަރުން އޭތި ދިން މީހަކަށް އަނބުރާ ލިބޭނެއެވެ. (އެހެނީ: އެ ދެނީ މިލްކުވާ ގޮތަކަށެއް ނޫނެވެ.)\n\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ތިޔަބައިމީހުންގެ މުދާތަކުގައި ހިފާށެވެ. އަދި އެ ތަކެތި ނަގައިނުލާށެވެ. އެހެނީ ހަމަކަށަވަރުން އުމުރު ދުވަހަށް ހިބައިގެ ގޮތުގައި މީހަކަށް އެއްޗެއް ދެވިއްޖެ ނަމަ، ފަހެ އެއީ ދިރިހުރުމައި މަރުވުމުގައިވެސް އެ ލިބުމު މީހާއާއި، އޭނާގެ ދަރިފަސްކޮޅަށް ލިބިގެންވާ އެއްޗެކެވެ.","[البخاري 2625 واللفظ له، ومسلم 1625\n\nمسلم 1625\n\nمسلم 1625]"],["#297","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ أَبِي هُرَيْرَةَ رَضِيَ اللَّهُ عَنْهُ أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: (لا يَمْنَعَنَّ جَارٌ جَارَهُ: أَنْ يَغْرِزَ خَشَبَةً فِي جِدَارِهِ ثُمَّ يَقُولُ أَبُو هُرَيْرَةَ: مَا لِي أَرَاكُمْ عَنْهَا مُعْرِضِينَ؟ وَاَللَّهِ لأَرْمِيَنَّ بِها بَيْنَ أَكْتَافِكُمْ).","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އެއް އަވައްޓެރިޔަކު، އޭނާގެ ފާރުގައި އަނެއް އަވައްޓެރިޔާ ލަކުޑިބުރި ޖެހުން މަނާ ނުކުރާށެވެ.“ (އެބަހީ: ބޭނުމެއް ޖެހިގެން، މާ ބޮޑު ގެއްލުމެއް ނުވާ ގޮތަށެވެ.) އެއަށްފަހު އަބޫ ހުރައިރާ ވިދާޅުވިއެވެ: ތިޔަބައިމީހުން އެކަމަށް ދެކޮޅުވެރިވާތަން ތިމަންނައަށް ފެންނަނީ ކީއްވެހެއްޔެވެ؟ ﷲ ގަންދީ ބުނަމެވެ. ހަމަކަށަވަރުން (ތިޔަބައިމީހުން ނުރުހުނަސް) ތިޔަބައިމީހުންގެ މެދުގައި މި (ހުކުމް އިއްވާނަމެވެ.)","[البخاري 2463، ومسلم 1609]"],["#298","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الرهن وغيره","ރަހުނާއި އެ ނޫން ކަންކަމުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: (مَنْ ظَلَمَ قِيدَ شِبْرٍ مِنْ الأَرْضِ: طُوِّقَهُ مِنْ سَبْعِ أَرَضِينَ).","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ބިމެއްގެ ކައިވަތެއްގެ މިންވަރު ވެސް ނުހައްގުން މީހަކު އަތުލައިފި ނަމަ، (ގިޔާމަތް ދުވަހު) އެ މީހެއްގެ ކަރުގައި ހަތް ބިން އެޅުވޭނެއެވެ.“","[البخاري 2453، ومسلم 1612]"],["#299","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب اللقطة","ގެއްލިފައި ވަނިކޮށް ފެންނަ ތަކެތީގެ ބާބު","عَنْ زَيْدِ بْنِ خَالِدٍ الْجُهَنِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (سُئِلَ رَسُولُ اللَّهِ ﷺ عَنْ لُقَطَةِ الذَّهَبِ، أَوْ الْوَرِقِ؟ فَقَالَ: اعْرِفْ وِكَاءَهَا وَعِفَاصَهَا، ثُمَّ عَرِّفْهَا سَنَةً فَإِنْ لَمْ تُعْرَفْ فَاسْتَنْفِقْهَا وَلْتَكُنْ وَدِيعَةً عِنْدَكَ فَإِنْ جَاءَ طَالِبُهَا يَوْماً مِنْ الدَّهْرِ: فَأَدِّهَا إلَيْهِ، وَسَأَلَهُ عَنْ ضَالَّةِ الإِبِلِ؟ فَقَالَ: مَا لَك وَلَهَا؟ دَعْهَا فَإِنَّ مَعَهَا حِذَاءَهَا وَسِقَاءَهَا، تَرِدُ الْمَاءَ وَتَأْكُلُ الشَّجَرَ، حَتَّى يَجِدَهَا رَبُّهَا. وَسَأَلَهُ عَنْ الشَّاةِ؟ فَقَالَ: خُذْهَا فَإِنَّمَا هِيَ لَكَ، أَوْ لأَخِيك، أَوْ لِلذِّئْبِ).","ޒައިދު ބިން ޚާލިދު އަލްޖުހަނީ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ގެއްލިފައި ވަނިކޮށް ފެންނަ ރަނާއި ރިއްސާމެދު ރަސޫލު ﷲ ﷺ އަރިހުގައި ސުވާލު ކުރެއްވުނެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ (ފައިސާ) އަޅާފައިވާ އެއްޗަކާއި، އެ ބަނދު ކުރަން އައްސާފައިވާ ވާކޮޅު (ރަނގަޅަށް) ދެނެގަންނާށެވެ. އެއަށްފަހު އެއް އަހަރުދުވަސް ވަންދެން (އޭގެ ވެރިޔާ ހޯދަން) އިއުލާން ކުރާށެވެ. ދެން އޭތި ބަލައިގަންނަ މީހަކު ނުވެއްޖެ ނަމަ، އެ ހޭދަ ކުރާށެވެ. އެއީ ތިބާގެ އަތުގައި ބަހައްޓާފައިވާ (އަނބުރާ ދޭން ޖެހޭ އެއްޗެއް) ގޮތުގައެވެ.“ ދެން ދުވަހަކުވެސް އޭގެ ވެރިޔާ އޭތި ހޯދަން ތިބާ ގާތަށް އައިސްފި ނަމަ، (އެ މިންވަރު) އަނބުރާ ހަވާލު ކުރާށެވެ. ދެން ގެއްލިފައިވަނިކޮށް (ފެންނަ) ޖަމަލާމެދު ސުވާލު ކުރެއްވުނެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެއާއި ތިބާއާ ކޮންކަމެއްހެއްޔެވެ؟ އޭތި ދޫކޮށްލާށެވެ. އެހެނީ ހަމަކަށަވަރުން އެއާއެކު އޭގެ ފަޔާއި ފެން ކަންވާރު ވެއެވެ. އޭގެ ވެރިޔާ އޭތި ފެންނަން ދެން އޭތި ފެން ބޮއެފައި ގަސްތަކުން ކައިހަދާނެއެވެ.“ ދެން (ގެއްލިފައި ވަނިކޮށް ފެންނަ) ކަންބަޅިއާމެދު ސުވާލު ކުރެއްވުނެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭތި ނަގާށެވެ. އެހެނީ ހަމަކަށަވަރުން އެ ވަނީ ތިބާއަށެވެ. ނޫނީ ތިބާގެ އަޚަކަށެވެ. ނޫނީ ވަލުކުއްތާއަށެވެ.","[البخاري 91، ومسلم 1722 واللفظ له]"],["#300","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الوصايا","ވަސިއްޔަތުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: (مَا حَقُّ امْرِئٍ مُسْلِمٍ، لَهُ شَيْءٌ يُوصِي فِيهِ، يَبِيتُ لَيْلَتَيْنِ إلاَّ وَوَصِيَّتُهُ مَكْتُوبَةٌ عِنْدَهُ).\n\nزَادَ مُسْلِمٌ: قَالَ ابْنُ عُمَرَ: (مَا مَرَّتْ عَلَيَّ لَيْلَةٌ مُنْذُ سَمِعْتُ رَسُولَ اللَّهِ ﷺ يَقُولُ ذَلِكَ، إلاَّ وَعِنْدِي وَصِيَّتِي).","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ވަސިއްޔަތެއް ކުރާނެ އެއްވެސް އެއްޗެއް އޮތް މުސްލިމު މީހަކު، އެ ވަސިއްޔަތް އޭނާގެ އަތުގައި ލިޔެފައިވާ ހާލު މެނުވީ އެންމެ ރެއެއް ނުވަތަ ދެރޭވެސް އޭނާ ވޭތުކުރުން ހައްގުވެގެން ނުވެއެވެ.“\n\nއަދި މުސްލިމުގެ ރިވާޔަތުގައި ވެއެވެ: އިބްނު ޢުމަރު ވިދާޅުވިއެވެ: ފަހެ ﷲ ގަންދީ ބުނަމެވެ. ރަސޫލު ﷲ ﷺ އެފަދައިން ޙަދީޘް ކުރެއްވި އަޑު އެހުމަށް ފަހު، ތިމަންނަގެ ވަސިއްޔަތް ތިމަންނަގެ އަތުގައި ނެތީސް އެންމެ ރެއެއް ވޭތުވެ ނުދެއެވެ.","[البخاري 2738، ومسلم 1627\n\nمسلم 1627]"],["#301","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الوصايا","ވަސިއްޔަތުގެ ބާބު","عَنْ سَعْدِ بْنِ أَبِي وَقَّاصٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (جَاءَني رَسُولُ اللَّهِ ﷺ يَعُودُنِي عَامَ حَجَّةِ الْوَدَاعِ مِنْ وَجَعٍ اشْتَدَّ بِي فَقُلْتُ: يَا رَسُولَ اللَّهِ، قَدْ بَلَغَ بِي مِنْ الْوَجَعِ مَا تَرَى، وَأَنَا ذُو مَالٍ، وَلا يَرِثُنِي إلاَّ ابْنَةٌ أَفَأَتَصَدَّقُ بِثُلُثَيْ مَالِي؟ قَالَ: لا، قُلْتُ: فَالشَّطْرُ يَا رَسُولَ اللَّهِ؟ قَالَ: لا، قُلْت: فَالثُّلُثُ قَالَ: الثُّلُثُ، وَالثُّلُثُ كَثِيرٌ، إِنَّكَ أَنْ تَذَرَ ورَثَتَكَ أَغنياءَ خيرٌ منْ أَنْ تَذَرَهمْ عالَةً يَتَكَفَّفونَ النَّاسَ، وإنَّكَ لَنْ تُنْفِقَ نَفَقَةً تَبْتَغِي بِهَا وَجْهَ اللَّهِ إلاَّ أُجِرْتَ بِهَا، حَتَّى مَا تَجْعَلُ فِي فِي امْرَأَتِكَ. قَالَ: قُلْتُ: يَا رَسُولَ اللَّهِ أُخَلَّفُ بَعْدَ أَصْحَابِي؟ قَالَ: إنَّكَ لَنْ تُخَلَّفَ فَتَعْمَلَ عَمَلاً تَبْتَغِي بِهِ وَجْهَ اللَّهِ إلاَّ ازْدَدْت بِهِ دَرَجَةً وَرِفْعَةً، وَلَعَلَّكَ أَنْ تُخَلَّفَ حَتَّى يَنْتَفِعَ بِكَ أَقْوَامٌ، وَيُضَرُّ بِكَ آخَرُونَ. اللَّهُمَّ أَمْضِ لأَصْحَابِي هِجْرَتَهُمْ، وَلا تَرُدَّهُمْ عَلَى أَعْقَابِهِمْ، لَكِنِ الْبَائِسُ سَعْدُ بْنُ خَوْلَةَ يَرْثِي لَهُ رَسُولُ اللَّهِ ﷺ أَنْ مَاتَ بِمَكَّةَ)","ސަޢުދު ބިން އަބީ ވައްގާޞް رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ހައްޖަތުލް ވަދާއުގެ އަހަރުގައި ތިމަންނަ ވަރަށް ބޮޑަށް އާލާސްކަމުގައި އިނުމުން، ރަސޫލު ﷲ ﷺ ތިމަންނައަށް ޒިޔާރާތް ކުރެއްވިއެވެ. ތިމަން ދެންނެވީމެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. އިބަ ނަބިއްޔާއަށް ފެނިވަޑައިގަންނަވާ ފަދައިން މި އާލާސްކަމުގައި (ތިމަންނަގެ ހާލު ވަރަށް ދެރަވެއްޖެއެވެ.) އަދި ތިމަންނައީ މުދާވެރިއަކަށްވެފައި، އަންހެން ދަރިއަކު މެނުވީ ތިމަންނަގެ ފަރާތުން ވާރުތަ ނުކުރާނެއެވެ. ވީމާ ތިމަންނަގެ މުދަލުގެ ތިންބައި ކުޅަ އެއްބައިން ސަދަގާތް ކުރަންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ނޫނެކެވެ.“ ތިމަން ދެންނެވީމެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ދެބައި ކުޅަ އެއްބައި ކުރަންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ނޫނެކެވެ.“ ތިމަން ދެންނެވީމެވެ: ތިންބައި ކުޅަ އެއްބައި ކުރަންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިންބައި ކުޅަ އެއްބައި (ކުރެވިދާނެއެވެ.) އަދި ތިންބައި ކުޅަ އެއްބަޔަކީ ވެސް ގިނައެވެ. ހަމަކަށަވަރުން ތިބާގެ ވާރުތަވެރިން ފަގީރުކަންމަތީ އެހެން މީހުންގެ ކިބައިން ސަލާންޖަހަން ދޫކޮށްލުމަށް ވުރެ، ތަނަވަސްކަންމަތީ ދޫކުރުން މާ ހެޔޮވެގެންވެއެވެ. އަދި ހަމަކަށަވަރުން ﷲގެ ވަޖްހުފުޅު އަދައިގެން ތިބާ ހޭދަ ކުރާ އެއްވެސް އެއްޗަކަށް ހެޔޮ ޖަޒާ ލިބިގެން މެނުވީ ނުދާނެއެވެ. އެއީ ތިބާގެ އަނބިމީހާގެ އަނގައަށް ލަވާ ކާއެތިކޮޅަށް ދާންދެން ވެސްމެއެވެ.“ ތިމަން ދެންނެވީމެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ތިމަންނައާއެކު އުޅުނު މީހުން (މަދީނާއަށް އެނބުރި ދާއިރު، ތިމަންނަގެ އާލާސްކަމުގެ ސަބަބުން، އެ ބިމަކުން ހިޖުރަ މި ކުރި މައްކާގައި) ފަހަތުގައި ތިމަން މަޑުކުރަން ޖެހެނީތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ ފަހަތުގައި މަޑުކުރަން (ޖެހުނުކަމުގައި ވިޔަސް) ހެޔޮ އަމަލެއްކޮށްފި ކަމުގައި ވާނަމަ، އެކަމުގެ ސަބަބުން ތިބާގެ ދަރަޖައާއި މަތިވެރިކަން އިތުރުވެގެން މެނުވީ ނުދާނެއެވެ. އަދި ފަހަރެއްގައި ތިބާގެ ސަބަބުން ބަޔަކަށް ފައިދާ ކުރެވިއްޖައުމަށް ދާންދެން، އަދި އަނެއް ބަޔަކަށް ގެއްލުން ލިބިއްޖައުމަށް ދާންދެން، ތިބާ ދިރިދަންމަވަފާނެއެވެ. اللَّهُمَّ أَمْضِ لأَصْحَابِي هِجْرَتَهُمْ، وَلا تَرُدَّهُمْ عَلَى أَعْقَابِهِمْ (ޔާ ﷲ! ތިމަން ނަބިއްޔާގެ ސަހާބީންގެ ހިޖުރަ ފުރިހަމަ ކުރައްވާށިއެވެ. (އެބަހީ: އެކަމުގައި ސާބިތު ކުރައްވާށިއެވެ.) އަދި އެބައިމީހުން (ކުރި ހިޖުރަ ދޫކޮށް) އެނބުރި ރައްދު ނުކުރައްވާށިއެވެ.) އެހެނަސް ސަޢުދު ބިން ޚައުލާއަށް ވީގޮތް ހާދަ ދެރައެވެ.“ އެކަލޭގެފާނު މައްކާގައި އަވަހާރަވީތީ ރަސޫލު ﷲ ﷺ ސަޢުދު ބިން ޚައުލާއަށް ޓަކައި ދެރަފުޅުވިއެވެ.","[البخاري 1295 واللفظ له، ومسلم 1628]"],["#302","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الوصايا","ވަސިއްޔަތުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (لَوْ أَنَّ النَّاسَ غَضُّوا مِنْ الثُّلُثِ إلَى الرُّبْعِ؟ فَإِنَّ رَسُولَ اللَّهِ ﷺ قَالَ: الثُّلُثُ، وَالثُّلُثُ كَثِيرٌ).","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: (މީސްތަކުން އެބައިމީހުންގެ މުދާ ސަދަގާތް ކުރުމަށް ވަސިއްޔަތް ކުރާއިރު، އެ މުދަލުގެ) ތިންބައި ކުޅަ އެއްބައިން ހަތަރުބައި ކުޅަ އެއްބަޔަށް މަދު ކުރާނަމަ ކިހާ ރަނގަޅުހެއްޔެވެ؟ އެހެނީ ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިންބައި ކުޅަ އެއްބައި (ސަދަގާތުގައި ދިނުމަށް ވަސިއްޔަތް ކުރެވިދާނެއެވެ.) އަދި ތިންބައި ކުޅަ އެއްބައިވެސް ގިނައެވެ.“","[البخاري 2743، ومسلم 1629 واللفظ له]"],["#303","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الفرائض","ތަރިކަ މުދަލުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُمَا عَنْ النَّبِيِّ ﷺ قَالَ: (أَلْحِقُوا الْفَرَائِضَ بِأَهْلِهَا، فَمَا بَقِيَ فَهُوَ لأَوْلَى رَجُلٍ ذَكَرٍ).\n\nوَفِي رِوَايَةٍ: (اقْسِمُوا الْمَالَ بَيْنَ أَهْلِ الْفَرَائِضِ عَلَى كِتَابِ اللَّهِ فَمَا تَرَكَتْ: فَلأَوْلَى رَجُلٍ ذَكَرٍ).","އިބްނު ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތަރިކަ މުދަލުން ފަރުޟު ކަނޑައެޅިފައިވާ ބައިތައް، އޭގެ ހައްގުވެރިންނަށް ބަހާށެވެ. އެއަށްފަހު ބާކީ ހުރި އެއްޗެއް ލިބުން ވަނީ، ޢާއިލީ ގޮތުން (މައްޔިތާއާ) އެންމެ ގާތް ފިރިހެނަކަށެވެ.“\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”ﷲގެ ފޮތުގައިވާ ގޮތުގެ މަތިން، ތަރިކަ މުދަލުން ފަރުޟު ކަނޑައެޅިފައިވާ ބައިތައް އޭގެ ހައްގުވެރިންގެ މެދުގައި ބަހާށެވެ. ދެން އޭގެން ބާކީ ހުރި އެއްޗެއް ލިބުން ވަނީ، ޢާއިލީ ގޮތުން (މައްޔިތާއާ) އެންމެ ގާތް ފިރިހެނަކަށެވެ.“","[البخاري 6732، ومسلم 1615\n\nمسلم 1615]"],["#304","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الفرائض","ތަރިކަ މުދަލުގެ ބާބު","عَنْ أُسَامَةَ بْنِ زَيْدٍ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (قُلْتُ يَا رَسُولَ اللَّهِ، أَتَنْزِلُ غَداً فِي دَارِكَ بِمَكَّةَ؟ قَالَ: وَهَلْ تَرَكَ لَنَا عَقِيلٌ مِنْ رِبَاعٍ؟\n\nثُمَّ قَالَ: لا يَرِثُ الْكَافِرُ الْمُسْلِمَ وَلا الْمُسْلِمُ الْكَافِرَ).","އުސާމާ ބިން ޒައިދު رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: (މައްކާއަށް ވަޑައިގަންނަވަމުން) ރަސޫލު ﷲ ﷺގެ އަރިހުގައި ތިމަން ދެންނެވީމެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. މައްކާގައިވާ އިބަ ނަބިއްޔާ (ކުރިން އުޅުއްވި އަބޫ ޠާލިބުގެ) ގެކޮޅުގައި މާދަމާ މަޑު ކުރައްވާނަމުތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ޢުގައިލު (ބިން އަބީ ޠާލިބު) ތިމަންމެންނަށް އެއްވެސް ގެއެއް ގޯއްޗެއް ދޫކުރިހެއްޔެވެ؟“\n\nއެއަށްފަހު ޙަދީޘް ކުރެއްވިއެވެ: ”މުސްލިމަކު ކާފަރެއްގެ ކިބައިން ވާރުތަ ނުކުރާނެއެވެ. އަދި ކާފަރަކު މުސްލިމެއްގެ ކިބައިން ވާރުތަ ނުކުރާނެއެވެ.“ (ނޯޓު: އަބޫ ޠާލިބު ނިޔާވީ ކާފަރެއްގެ ގޮތުގައެވެ. އަދި ނަބިއްޔާ އެކަލޭގެފާނު އުފަންވެވަޑައިގަތް ގެކޮޅު ދޫކޮށްލައްވާފައި މަދީނާއަށް ހިޖުރަ ކުރެއްވިއެވެ. އަބޫ ޠާލިބުގެ ހަތަރު ފިރިހެން ދަރިން ތިއްބެވެ. އޭގެ ތެރެއިން ޖަޢުފަރާއި ޢަލީ އިސްލާމްވުމުން، އެ ދެ ބޭކަލުންނަކަށް އަބޫ ޠާލިބުގެ ފަރާތުން ވާރުތައެއް ނުވެއެވެ. އެއީ ދީން ތަފާތުވުމުގެ ސަބަބުން ވާރުތަ ނުވާތީއެވެ. ދެން ވާރުތަވާން އޮތީ ކާފަރުކަންމަތީ ތިބި ދެ ދަރިން ކަމުގައިވާ ޠާލިބާއި ޢުގައިލަށެވެ. އެކަމަކު ޠާލިބު ނިޔާވިއެވެ. ދެން މުޅި ހުރިހައި ގެއަކާއި ގޯއްޗެއް ޢުގައިލަށް ވާރުތަވުމުން، އެ ތަކެތި ވިއްކާލިއެވެ. ފަހުން ޢުގައިލު އިސްލާމްވިއެވެ.)","[البخاري 1588، 3058، ومسلم 1351 واللفظ له\n\nالبخاري 6764 واللفظ له، ومسلم 1614]"],["#305","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الفرائض","ތަރިކަ މުދަލުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا (أَنَّ النَّبِيَّ ﷺ نَهَى عَنْ بَيْعِ الْوَلاءِ وَعَنْ هِبَتِهِ).","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ރިވާ ކުރެއްވިއެވެ: ވަލާވެރިކަން ގަނެވިއްކުމާއި ހިބަކޮށްދިނުން ނަބިއްޔާ މަނާ ކުރެއްވިއެވެ. (ނޯޓު: ވަލާއަކީ މީހަކު އަޅެއް މިނިވަން ކުރުމުން، އެ މިނިވަން ކުރާ މީހައަށް އެ އަޅެއްގެ ފަރާތުން ވާރުތަވުމުގެ ހައްގު ލިބިގެންދިއުމެވެ.)","[البخاري 2535، ومسلم 1506]"],["#306","كتاب البيوع","ގަނެވިއްކުމުގެ ފޮތް","باب الفرائض","ތަރިކަ މުދަލުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: (كَانَتْ فِي بَرِيرَةَ ثَلاثُ سُنَنٍ: خُيِّرَتْ عَلَى زَوْجِهَا حِينَ عَتَقَتْ، وَأُهْدِيَ لَهَا لَحْمٌ، فَدَخَلَ عَلَيَّ رَسُولُ اللَّهِ ﷺ وَالْبُرْمَةُ عَلَى النَّارِ، فَدَعَا بِطَعَامٍ فَأُتِيَ بِخُبْزٍ وَأُدْمٍ مِنْ أُدْمِ الْبَيْتِ فَقَالَ: أَلَمْ أَرَ الْبُرْمَةَ عَلَى النَّارِ فِيهَا لَحْمٌ؟ قَالُوا: بَلَى، يَا رَسُولَ اللَّهِ. ذَلِكَ لَحْمٌ تُصُدِّقَ بِهِ عَلَى بَرِيرَةَ فَكَرِهْنَا أَنْ نُطْعِمَكَ مِنْهُ فَقَالَ: هُوَ عَلَيْهَا صَدَقَةٌ، وَهُوَ مِنْهَا لَنَا هَدِيَّةٌ، وَقَالَ النَّبِيُّ ﷺ فِيهَا: إنَّمَا الْوَلاءُ لِمَنْ أَعْتَقَ).","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: (އެކަމަނާ މިނިވަން ކުރެއްވި އަންހެން އަޅެއް ކަމުގައިވާ) ބަރީރާގެ (ސަބަބުން) ތިން ސުންނަތެއް (އެބަހީ: ޝަރުއީ ހުކުމެއް) އައިސްފައިވެއެވެ. (ފުރަތަމަ ކަމަކީ) ބަރީރާ މިނިވަންވުމުން، އޭނާގެ ފިރިމީހާއާއެކު ކައިވެނީގައި ދެމިތިބުމަށް ނުވަތަ ވަކިވަށް އިޚްތިޔާރު (ނަބިއްޔާ) ދެއްވިއެވެ. (ނޯޓު: ފިރިމީހާއަކީ މިނިވަން ނުވާ އަޅެކެވެ.) (ބަރީރާއާ ގުޅޭގޮތުން އައިސްފައިވާ ދެވަނަ ކަމަކީ) ބަރީރާއަށް މަސްކޮޅެއް ހަދީޔާގެ ގޮތުގައި ލިބުނެވެ. ރަސޫލު ﷲ ﷺ ތިމަން ކަމަނާގެ ގެކޮޅަށް ވަދެވަޑައިގަތް ހިނދު، މަސްތެލި ހުރީ ކައްކަން އުނދާފައެވެ. ދެން ނަބިއްޔާއަށް ކާން ގެންނަވަން އެންގެވިއެވެ. ފަހެ ރޮށިކޮޅަކާއި ގޭގައި (އެއާއެކު އާންމުކޮށް ކާން ގެންގުޅޭ ދިޔަފަދަ އެއްޗެއް) ނަބިއްޔާގެ އަރިހަށް ގެނެވުނެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތެއްޔެއްގައި މަސް އުނދާފައިވަނިކޮށް ތިމަން ނަބިއްޔާ ފެނިވަޑައި ނުގަންނަވަމުތޯއެވެ؟“ ނަބިއްޔާއަށް ދެންނެވުނެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. އާދޭހެވެ. އެހެނަސް އެއީ ބަރީރާއަށް ސަދަގާތުގެ ގޮތުގައި ދެވުނު މަސްކޮޅެކެވެ. ވީމާ އޭގެން އިބަ ނަބިއްޔާއަށް ފަރީއްކުޅުވާން އަރުވަން ތިމަންމެން ނުރުހުނީއެވެ. (ނޯޓު: ސަދަގާތުގެ ގޮތުގައި ދެވޭ ތަކެތި ނަބިއްޔާ ފަރީކުޅުވުން ހުއްދަވެގެން ނުވެއެވެ.) ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެއީ ބަރީރާއަށް ވާ ސަދަގާތެކެވެ. އަދި އެއީ އޭނާގެ ފަރާތުން ތިމަންމެންނަށް ވާ ހަދިޔާއެކެވެ.“ (ބަރީރާއާ ގުޅޭގޮތުން އައިސްފައިވާ ތިންވަނަ ކަމަކީ) ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ވަލާވެރިކަން ލިބުން ވަނީ ހަމައެކަނި މިނިވަން ކުރާ މީހާއަށެވެ.“","[البخاري 5097، ومسلم 1504 واللفظ له]"],["#307","كتاب النكاح","ކައިވެނީގެ ފޮތް","","ތަރިކަ މުދަލުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ مَسْعُودٍ قَالَ: قَالَ لَنَا رَسُولُ اللَّهِ ﷺ: (يَا مَعْشَرَ الشَّبَابِ، مَنْ اسْتَطَاعَ مِنْكُمْ الْبَاءَة فَلْيَتَزَوَّجْ فَإِنَّهُ أَغَضُّ لِلْبَصَرِ، وَأَحْصَنُ لِلْفَرْجِ، وَمَنْ لَمْ يَسْتَطِعْ فَعَلَيْهِ بِالصَّوْمِ فَإِنَّهُ لَهُ وِجَاءٌ).","ޢަބްދު ﷲ ބިން މަސްޢޫދު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ ޒުވާނުންގެ ޖަމާއަތުގެ މީހުންނޭވެ. ތިޔަބައިމީހުން ކުރެ ކައިވެނި ކުރުމުގެ ކުޅަދާނަކަން ލިބިފައިވާ މީހުން ފަހެ ކައިވެނި ކުރާށެވެ. އެހެނީ ހަމަކަށަވަރުން އެއީ ބެލުން ތިރި ކުރުމާއި، ލަދުވެތި ގުނަވަން ރައްކާތެރި ކުރުމަށް އޮތް އެންމެ ރަނގަޅު ގޮތެވެ. އަދި އެކަމަށް ކުޅަދާނަކަން ލިބިފައިނުވާ މީހާ ފަހެ އޭނާގެ މައްޗަށް އޮތީ ރޯދަހިފުމެވެ. އެހެނީ ހަމަކަށަވަރުން އެއީ އެ މީހަކަށްވާ ހޯޖާކުރުމެކެވެ.“ (އެބަހީ: އޭނާގެ އެދުންވެރިކަން ކުޑަކޮށްދޭނެ ކަމެކެވެ.)","[البخاري 1905، ومسلم 1400 واللفظ له]"],["#308","كتاب النكاح","ކައިވެނީގެ ފޮތް","","","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ -: (أَنَّ نَفَراً مِنْ أَصْحَابِ النَّبِيِّ ﷺ سَأَلُوا أَزْوَاجَ النَّبِيِّ ﷺ عَنْ عَمَلِهِ فِي السِّرِّ؟ فَقَالَ بَعْضُهُمْ: لا أَتَزَوَّجُ النِّسَاءَ. وَقَالَ بَعْضُهُمْ: لا آكُلُ اللَّحْمَ. وَقَالَ بَعْضُهُمْ: لا أَنَامُ عَلَى فِرَاشٍ. فَبَلَغَ ذَلِكَ النَّبِيَّ ﷺ فَحَمِدَ اللَّهَ وَأَثْنَى عَلَيْهِ وَقَالَ: مَا بَالُ أَقْوَامٍ قَالُوا كَذَا؟ لَكِنِّي أُصَلِّي وَأَنَامُ وَأَصُومُ وَأُفْطِرُ، وَأَتَزَوَّجُ النِّسَاءَ فَمَنْ رَغِبَ عَنْ سُنَّتِي فَلَيْسَ مِنِّي).","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺގެ ސަހާބީންގެ ތެރެއިން ބަޔަކު، އެކަލޭގެފާނު ސިއްރުގައި ކުރައްވާ އަޅުކަންކަމާ ބެހޭ ގޮތުން އެކަލޭގެފާނުގެ އަނބިއަނބިކަނބަލުންގެ އަރިހުގައި ސުވާލު ކުރިއެވެ. އެއަށްފަހު އޭގެ ތެރެއިން އެއް ބަޔަކު ބުންޏެވެ: ތިމަންނަ އަންހެނުންނާ ކައިވެންޏެއް ނުކުރާނަމެވެ. އަދި އަނެއް ބަޔަކު ބުންޏެވެ: ތިމަންނަ (އެއްގަމު) މަހެއް ނުކާނަމެވެ. އަދި އަނެއް ބަޔަކު ބުންޏެވެ: ތިމަންނަ ތަންމައްޗެއްގައި ނުނިދާނަމެވެ. ފަހެ ނަބިއްޔާ ﷺ އަށް އެ ވާހަކަތައް ފޯރުމުން، އެކަލޭގެފާނު (ނުކުމެވަޑައިގެން) ﷲ އަށް ހަމްދު ސަނާ ދެންނެވުމަށް ފަހު ޙަދީޘް ކުރެއްވިއެވެ: ”މިވެނި އެވެނި ގޮތަކަށް ބުނި ބަޔަކަށް ވެފައިވަނީ ކިހިނެއްހެއްޔެވެ؟ އެހެންނަކުން ނޫނެވެ. ތިމަން ނަބިއްޔާ ނަމާދުކޮށް އަވަހާރަފުޅު ލައްޕަވަމެވެ. އަދި ރޯދަ ހިއްޕަވައި، އަދި ރޯދަ ނުހިއްޕަވައި ވެސް ހުންނަވަމެވެ. އަދި އަންހެނުންނާ ކައިވެނިބައްލަވަމުއެވެ. ވީމާ ތިމަން ނަބިއްޔާގެ ސުންނަތުން އެއްކިބާވެއްޖެ މީހާ، ފަހެ އޭނާއީ ތިމަން ނަބިއްޔާގެ (މީސްތަކުންގެ) ތެރެއިންވާ މީހެއް ނޫނެވެ.“","[البخاري 5063، ومسلم 1401 واللفظ له]"],["#309","كتاب النكاح","ކައިވެނީގެ ފޮތް","","","عَنْ سَعْدِ بْنِ أَبِي وَقَّاصٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (رَدَّ رَسُولُ اللَّهِ ﷺ عَلَى عُثْمَانَ بْنِ مَظْعُونٍ التَّبَتُّلَ وَلَوْ أَذِنَ لَهُ لاخْتَصَيْنَا).","ސަޢުދު ބިން އަބީ ވައްގާޞް رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އަންހެނުންނާ މުޅިން ދުރުވެ އަޅުކަމަށް ހުސްވުން ޢުޘްމާން ބިން މަޡްޢޫނަށް ރަސޫލު ﷲ ﷺ މަނާ ކުރެއްވިއެވެ. އަދި ޢުޘްމާން ބިން މަޡްޢޫނަށް އެކަމަށް ހުއްދަ ދެއްވި ނަމަ ތިމަންމެން ހޯޖާކުރީމުސްކަން ކަށަވަރެވެ.","[البخاري 5073، ومسلم 1402]"],["#310","كتاب النكاح","ކައިވެނީގެ ފޮތް","","","عَنْ أُمِّ حَبِيبَةَ بِنْتِ أَبِي سُفْيَانَ رَضِيَ اللَّهُ عَنْهُا أَنَّهَا قَالَتْ: (يَا رَسُولَ اللَّهِ، انْكِحْ أُخْتِي ابْنَةَ أَبِي سُفْيَانَ قَالَ: أَوَتُحِبِّينَ ذَلِكَ؟ فَقُلْتُ: نَعَمْ، لَسْتُ لَكَ بِمُخْلِيَةٍ، وَأَحَبُّ مَنْ شَارَكَنِي فِي خَيْرٍ أُخْتِي: فَقَالَ رَسُولُ اللَّهِ ﷺ: إنَّ ذَلِكَ لا يَحِلُّ لِي. قَالَتْ: إنَّا نُحَدَّثُ أَنَّك تُرِيدُ أَنْ تَنْكِحَ بِنْتَ أَبِي سَلَمَةَ قَالَ: بِنْتَ أُمِّ سَلَمَةَ؟ قَالَتْ: قُلْت: نَعَمْ، قَالَ: إنَّهَا لَوْ لَمْ تَكُنْ رَبِيبَتِي فِي حَجْرِي، مَا حَلَّتْ لِي إنَّهَا لابْنَةُ أَخِي مِنْ الرَّضَاعَةِ، أَرْضَعَتْنِي وَأَبَا سَلَمَةَ ثُوَيْبَةُ فَلا تَعْرِضْنَ عَلِيَّ بَنَاتِكُنَّ وَلا أَخَوَاتِكُنَّ.\n\n قَالَ عُرْوَةُ وَثُوَيْبَةُ: مَوْلاةٌ لأَبِي لَهَبٍ أَعْتَقَهَا، فَأَرْضَعَتْ النَّبِيَّ ﷺ فَلَمَّا مَاتَ أَبُو لَهَبٍ رَآهُ بَعْضُ أَهْلِهِ بِشَرِّ حِيبَةٍ فَقَالَ لَهُ: مَاذَا لَقِيتَ؟ قَالَ أَبُو لَهَبٍ: لَمْ أَلْقَ بَعْدَكُمْ خَيْراً، غَيْرَ أَنِّي سُقِيتُ فِي هَذِهِ بِعَتَاقَتِي ثُوَيْبَةَ).","ހަމަކަށަވަރުން އުންމު ޙަބީބާ ބިންތި އަބީ ސުފްޔާން رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ތިމަން ކަމަނާގެ އުޚްތު، އަބޫ ސުފްޔާންގެ ދަރިކަނބަލުންނާއި ކައިވެނި ބައްލަވައިގަންނަވާށެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ. ”ކަމަނާއަށް އެ ގޮތް ކަމުދާނެތޯއެވެ؟“ ތިމަން ކަމަނާ ދެންނެވީމެވެ. އާދޭހެވެ. ތިމަންކަމަނާއީ (މިހާރު ވެސް) އިބަ ނަބިއްޔާގެ ހުރި ހަމައެކަނި އަނބިކަނބަލުންނެއް ނޫނެވެ. އަދި ތިމަން ކަމަނާއާއެކު ހެޔޮ ކަމެއްގައި ބައިވެރިވުމަށް އެންމެ ލޯބި ކުރައްވަނީ ތިމަން ކަމަނާގެ އުޚްތެވެ. ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާއަށް ތިކަމެއް ހުއްދަވެގެނެއް ނުވެއެވެ.“ އެ ކަމަނާ ދެންނެވިއެވެ: ފަހެ ހަމަކަށަވަރުން އަބޫ ސަލަމާގެ ދަރިކަނބަލުންނާއި ކައިވެނިފުޅު ބައްލަވައިގަތުމަށް އިބަ ނަބިއްޔާ ބޭނުންފުޅުވެލައްވާ ކަމަށް ތިމަން ކަނބަލުންގެ މެދުގައި ވާހަކަދެކެވުނެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ. ”އުންމު ސަލަމާގެ ދަރިކަނބަލުންތޯއެވެ؟“ ތިމަން ކަމަނާ ދެންނެވީމެވެ: އާދޭހެވެ. ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަވަރުން އެ ކަމަނާއީ ތިމަން ނަބިއްޔާގެ ގެކޮޅުގައިވާ ދޮންދަރިކަނބަލަކަށް ނުވިކަމުގައި ވިޔަސް، ތިމަން ނަބިއްޔާއަށް އެ ކަމަނާއާ ކައިވެނި ބައްލަވައިގަތުން ހުއްދައެއް ނުވާނެއެވެ. (އެހެނީ) ހަމަކަށަވަރުން އެ ކަމަނާއަކީ ކިރުގެ ގޮތުން ތިމަން ނަބިއްޔާގެ އަޚުގެ ދަރިކަނބަލުންނެވެ. ތިމަން ނަބިއްޔާއާއި އަބޫ ސަލަމާއާ (ދެ ބޭކަލުންނަށް ވެސް) ޘުވައިބާ ކިރުފޮދު އެރުވިއެވެ. ފަހެ ތިޔަކަނބަލުންގެ އަންހެން ދަރިދަރިކަނބަލުންނާއި އުޚްތުންނާ ކައިވެނި ބެއްލެވުމަށް ތިމަން ނަބިއްޔާއަށް ހުށަނާޅުއްވާށެވެ.“\n\nޢުރްވާ ރިވާ ކުރެއްވިއެވެ. އަދި ޘުވައިބާއަކީ އަބޫ ލަހަބު މިނިވަންކުރި އަޅެކެވެ. މިނިވަން ކުރުމުން، އެ ކަނބުލޭގެ ނަބިއްޔާ ﷺ އަށް ކިރުފޮދު އެރުވިއެވެ. ފަހެ އަބޫ ލަހަބު މަރުވުމުން، އޭނާ ވަރަށް ނުބައި ހާލެއްގައި ވަނިކޮށް އޭނާގެ ބައެއް ތިމާގެ މީހުންނަށް ހުވަފެނެއްގައި ފެނުނެވެ. ދެންނެވުނެވެ: ތިބާއާ ބައްދަލުވީ ކޮން ކަމަކާހެއްޔެވެ؟ އަބޫ ލަހަބު ބުންޏެވެ. ތިޔަބައިމީހުންނާ ވަކިވުމަށް ފަހު އަރާމެއް ނުލިބެއެވެ. އެއީ ޘުވައިބާ މިނިވަންކުރުމުގެ ސަބަބުން ތިމަންނާއަށް މި ވަރަށް ފެން ބޯން ލިބުން ފިޔަވައެވެ. (އަދި އަތުގެ ބޮޑު އިނގިލީގެ ބަދައަށް އިޝާރަތް ކުރިއެވެ.)","[البخاري 5101 واللفظ له، ومسلم 1449]"],["#311","كتاب النكاح","ކައިވެނީގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: (لا يُجْمَعُ بَيْنَ الْمَرْأَةِ وَعَمَّتِهَا، وَلا بَيْنَ الْمَرْأَةِ وَخَالَتِهَا).","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ފިރިހެނަކަށް އަންހެނަކާ އޭނާގެ ބައްޕަ ފަރާތު ބޮޑުދައިތައާ ދެ މީހުންނާ އެއް ވަގުތެއްގައި ކައިވެނިކޮށްގެން އުޅުމެއް ނުވެއެވެ. އަދި އަންހެނަކާ އޭނާގެ މަންމަ ފަރާތު ބޮޑުދައިތައާ ދެ މީހުންނާވެސް މެއެވެ.“","[البخاري 5109، ومسلم 1408]"],["#312","كتاب النكاح","ކައިވެނީގެ ފޮތް","","","عَنْ عُقْبَةَ بْنِ عَامِرٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: (إنَّ أَحَقَّ الشُّرُوطِ أَنْ تُوفُوا بِهِ: مَا اسْتَحْلَلْتُمْ بِهِ الْفُرُوجَ).","އުޤްބާ ބިން ޢާމިރު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ފުއްދަން ޖެހޭ އެންމެ ހައްގުވެގެންވާ ޝަރުތަކީ، ތިޔަބައިމީހުންނަށް ފަރުޖުތައް ހަލާލުކުރުވާ ކަންތަކެވެ.“ (އެބަހީ: ކައިވެނި ކުރުމުގައި ކުރެވޭ ޝަރުތުތަކެވެ.)","[البخاري 2721 واللفظ له، ومسلم 1418]"],["#313","كتاب النكاح","ކައިވެނީގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا: (أَنَّ رَسُولَ اللَّهِ ﷺ نَهَى عَنْ الشِّغَارِ). وَالشِّغَارُ: أَنْ يُزَوِّجَ الرَّجُلُ ابْنَتَهُ عَلَى أَنْ يُزَوِّجَهُ ابْنَتَهُ، وَلَيْسَ بَيْنَهُمَا الصَّدَاقُ.","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ޝިޣާރުކުރުން ރަސޫލު ﷲ ﷺ މަނާ ކުރެއްވިއެވެ. (ނޯޓު: ޝިޣާރަކީ، ފިރިހެނަކު އޭނާގެ އަންހެން ދަރި މީހަކާ ދެވާއިރު، އެ މީހާގެ އަންހެން ދަރި އޭނައާ ދެވާ މަތިން ކައިވެނިކޮށްދިނުމެވެ. އެއީ ދެމަފިރިންގެ ދެމެދުގައި ކައިވެނީގެ ރަން ބަދަލުކުރުމަކާ ނުލައެވެ.)","[البخاري 5112، ومسلم 1415 واللفظ له]"],["#314","كتاب النكاح","ކައިވެނީގެ ފޮތް","","","عَنْ عَلِيِّ بْنِ أَبِي طَالِبٍ - رَضِيَ اللَّهُ عَنْهُ -: (أَنَّ النَّبِيَّ ﷺ نَهَى عَنْ نِكَاحِ الْمُتْعَةِ يَوْمَ خَيْبَرَ، وَعَنْ لُحُومِ الْحُمُرِ الأَهْلِيَّةِ).","ޢަލީ ބިން އަބީ ޠާލިބު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ޚައިބަރުގެ ހަނގުރާމައިގެ ދުވަހު، ނަބިއްޔާ ﷺ މުތުޢާ ކައިވެނި ކުރުމާއި ހިމާރު މަސް ކެއުން މަނާ ކުރެއްވިއެވެ.","[البخاري 5115، ومسلم 1407 واللفظ له]"],["#315","كتاب النكاح","ކައިވެނީގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: (لا تُنْكَحُ الأَيِّمُ حَتَّى تُسْتَأْمَرَ، وَلا تُنْكَحُ الْبِكْرُ حَتَّى تُسْتَأْذَنَ. قَالُوا: يَا رَسُولَ اللَّهِ، فَكَيْفَ إذْنُهَا قَالَ: أَنْ تَسْكُتَ).","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ކުރިން ކައިވެންޏެއް ކޮށްފައިވާ އަންހެނަކު ގޮތެއް ނާންގަނީސް އެ އަންހެނާއާ ކައިވެނި ކުރުމެއް ނުވެއެވެ. (އެބަހީ: ސަރީހަކޮށް ކައިވެނިކުރަން ބޭނުންވާ ކަމަށް ބުނުމުންނެވެ.) އަދި ބިކުރުވެރި އަންހެނެއްގެ އިޒުނަ ނުހޯދަނީސް އޭނާއާ ކައިވެނި ކުރުމެއް ނުވެއެވެ.“ މީސްތަކުން ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. އެ ކަނބުލޭގެ އިޒުނަ (އެނގޭނީ) ކިހިނަކުންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ ކަނބުލޭގެ ހަނުހުރުމުންނެވެ.“","[البخاري 5136، ومسلم 1419]"],["#316","كتاب النكاح","ކައިވެނީގެ ފޮތް","","","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: (جَاءَتْ امْرَأَةُ رِفَاعَةَ الْقُرَظِيِّ إلَى النَّبِيِّ ﷺ فَقَالَتْ: كُنْتُ عِنْدَ رِفَاعَةَ الْقُرَظِيِّ فَطَلَّقَنِي فَبَتَّ طَلاقِي. فَتَزَوَّجْتُ بَعْدَهُ عَبْدَ الرَّحْمَنِ بْنَ الزُّبَيْرِ وَإِنَّمَا مَعَهُ مِثْلُ هُدْبَةِ الثَّوْبِ. فَتَبَسَّمَ رَسُولُ اللَّهِ ﷺ وَقَالَ: أَتُرِيدِينَ أَنْ تَرْجِعِي إلَى رِفَاعَةَ؟ لا، حَتَّى تَذُوقِي عُسَيْلَتَهُ، وَيَذُوقَ عُسَيْلَتَكِ، قَالَتْ: وَأَبُو بَكْرٍ عِنْدَهُ، وَخَالِدُ بْنُ سَعِيدٍ بِالْبَابِ يَنْتَظِرُ أَنْ يُؤْذَنَ لَهُ، فَنَادَى أَبَا بَكْرٍ: أَلا تَسْمَعُ إلَى هَذِهِ: مَا تَجْهَرُ بِهِ عِنْدَ رَسُولِ اللَّهِ ﷺ).","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރިފާޢާ އަލްގުރަޡީގެ އަނބިކަނބަލުން ރަސޫލު ﷲ ﷺގެ އަރިހަށް އައިސް ދެންނެވިއެވެ: ތިމަންނާވީ ރިފާޢާ އަލްގުރަޡީގެ އަނބިކަނބަލުން ކަމުގައެވެ. ދެން ތިމަންނަ ވަރިކޮށްފިއެވެ. އެއީ ތިންވަނަ ފަހަރަށެވެ. އޭނާއަށް ފަހު ތިމަން ކައިވެނި ކުރީ ޢަބްދުއްރަހުމާން ބިން ޒުބައިރާއެވެ. ފަހެ އޭނައިގެ ކިބައިގައި ވަނީ ފޮތިފަށެއްފަދަ އެއްޗެކެވެ. (އެބަހީ: އޭނާގެ ޖިންސީ ބާރުވެރިކަމެއް ނެތެވެ.) ދެން ރަސޫލު ﷲ ﷺ ހިނިތުންވެވަޑައިގެން ޙަދީޘް ކުރެއްވިއެވެ: ”ކަނބުލޭގެ ބޭނުން ވަނީ އެނބުރި ރިފާޢާގެ ގާތަށް ރުޖޫއަވާންތޯއެވެ؟ ނޫނެކެވެ. (އެހެނަކުން ނުވާނެއެވެ.) އެއީ ތިބާ އޭނާގެ ކިބައިން، އަދި އޭނާ ތިބާގެ ކިބައިން ފޮނިކަން ހާސިލުކޮށްފުމަށް ދާންދެނެވެ.“ (އެބަހީ: ޖިމާއުވުމަށް ފަހު ވަރިވެއްޖެ ނަމަ މެނުވީ އަނބުރާ ކުރީ ފިރިމީހާއާ ކައިވެންޏެއް ނުކުރެވޭނެއެވެ.) ދެން ޢާއިޝާ ވިދާޅުވިއެވެ: އޭރު ނަބިއްޔާގެ އަރިހުގައި އަބޫ ބަކުރު ހުންނެވިއެވެ. އަދި ޚާލިދު ބިން ސަޢީދު ވަނުމަށް އިޒުނަ ދެވެންދެން ދޮރުކައިރީގައި މަޑުކޮށްގެން ހުއްޓެވެ. ދެން ޚާލިދު ދެންނެވިއެވެ: އޭ އަބޫ ބަކުރެވެ. ރަސޫލު ﷲ ﷺގެ އަރިހުގައި މި ކަނބުލޭގެ އެ އަޑުއުފުލާ ކަންތައް އިވިވަޑައިގަންނަވާތޯއެވެ؟","[البخاري 2639، ومسلم 1433 واللفظ له]"],["#317","كتاب النكاح","ކައިވެނީގެ ފޮތް","","","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (مِنْ السُّنَّةِ إذَا تَزَوَّجَ الْبِكْرَ عَلَى الثَّيِّبِ: أَقَامَ عِنْدَهَا سَبْعاً ثُمَّ قَسَمَ. وَإِذَا تَزَوَّجَ الثَّيِّبَ: أَقَامَ عِنْدَهَا ثَلاثاً ثُمَّ قَسَمَ).\n\nقَالَ أَبُو قِلابَةَ: وَلَوْ شِئْتُ لَقُلْتُ: إنَّ أَنَساً رَفَعَهُ إلَى النَّبِيِّ ﷺ).","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ސުންނަތުގެ ތެރޭގައި ހިމެނިގެންވެއެވެ: ދެވަނަ އަނބަކަށް ބިކުރުވެރިއަކާ ކައިވެނި ކުރާ ނަމަ، ފަހެ އެ ކަމަނާ ގާތުގައި ހަތް ދުވަސް ވަންދެން (ފުރަތަމަ) މަޑުކުރުމަށް ފަހު ދެން (ދެ އަނބިންގެ މެދުގައި ދުވަސްތައް) ބަހާލާނީއެވެ. އަދި ކުރިނެ މީހަކާ އިނދެފައިވާ އަންހެނަކާ ކައިވެނި ކުރާ ނަމަ، ފަހެ އެ ކަމަނާ ގާތުގައި ތިން ދުވަސް މަޑު ކުރުމަށް ފަހު (ދުވަސްތައް) ބަހާލާނީއެވެ.\n\nއަބޫ ގިލާބާ ވިދާޅުވިއެވެ: އަދި ތިމަން ބޭނުންވި ނަމަ ވިދާޅުވީމުހެވެ: ހަމަކަށަވަރުން އަނަސް ރިވާ ކުރެއްވީ ނަބިއްޔާ ﷺގެ އަރިހުންނެވެ.","[البخاري 5214 واللفظ له، ومسلم 1461]"],["#318","كتاب النكاح","ކައިވެނީގެ ފޮތް","","","عَنْ ابْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ (لَوْ أَنَّ أَحَدَهُمْ: إذَا أَرَادَ أَنْ يَأْتِيَ أَهْلَهُ قَالَ: بِسْمِ اللَّهِ اللَّهُمَّ جَنِّبْنَا الشَّيْطَانَ، وَجَنِّبْ الشَّيْطَانَ مَا رَزَقْتَنَا فَإِنَّهُ إنْ يُقَدَّرْ بَيْنَهُمَا وَلَدٌ فِي ذَلِكَ، لَمْ يَضُرَّهُ الشَّيْطَانُ أَبَداً).","އިބްނު އައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ކުރެ މީހަކު، އޭނާގެ އަންހެނުން ކައިރިއަށް (ރޭކުރުމަށް) އަންނަން އެދޭ ނަމަ، ފަހެ މިފަދައިން އޭނާ ކިޔައިފި ނަމަ: بِسْمِ اللَّهِ اللَّهُمَّ جَنِّبْنَا الشَّيْطَانَ، وَجَنِّبْ الشَّيْطَانَ مَا رَزَقْتَنَا فَإِنَّهُ إنْ يُقَدَّرْ بَيْنَهُمَا وَلَدٌ فِي ذَلِكَ، لَمْ يَضُرَّهُ الشَّيْطَانُ أَبَداً (ﷲގެ އިސްމުފުޅުން ފަށައިގަންނަމެވެ. ޔާ ﷲ! ޝައިތާނާގެ ކިބައިން އަޅަމެން ދުރުކުރައްވާށިއެވެ. އަދި އިބަ އިލާހު އަޅަމެންނަށް ދެއްވާ (ދަރިންގެ) ކިބައިން ވެސް ޝައިތާނާ ދުރުކުރައްވާށިއެވެ.) ހަމަކަށަވަރުން އެދެކަނބަލުންނަށް ދަރިއަކު ލިބެން މިންވަރު ކުރެވިއްޖެ ނަމަ، އެ ދަރިއަކަށް ދުވަހަކުވެސް ގެއްލުމެއް ނުދެވޭނެއެވެ.“","[البخاري 6388، ومسلم 1434]"],["#319","كتاب النكاح","ކައިވެނީގެ ފޮތް","","",'عَنْ عُقْبَةَ بْنِ عَامِرٍ - رَضِيَ اللَّهُ عَنْهُ - أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: (إيَّاكُمْ وَالدُّخُولَ عَلَى النِّسَاءِ. فَقَالَ رَجُلٌ مِنْ الأَنْصَارِ: يَا رَسُولَ اللَّهِ، أَرَأَيْتَ الْحَمْوَ؟ قَالَ: الْحَمْوُ الْمَوْتُ).\n\nولِمُسْلِمٍ: عنْ أَبي الطَّاهِرِ عنِ ابنِ وَهْبٍ قالَ: " سَمِعْتُ اللَّيثَ يقولُ: الحَمْوُ: أَخو الزَّوْجِ ومَاأَشْبَهَهُ منْ أَقاربِ الزَّوْجِ، ابنِ عَمٍّ ونَحْوِهِ.',"ޢުޤްބާ ބިން ޢާމިރު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން ކޮންމެސް ކޮންމެސް (ހިލޭ) އަންހެނުންގެ ކައިރިއަށް ނުވަންނާށެވެ.“ އަންސާރުންގެ ތެރެއިން ފިރިހެނަކު ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ފިރިމީހާގެ ގާތްތިމާގެ ފިރިހެނުންނާ މެދު ދެކިލައްވަނީ ކިހިނަކުންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ފިރިމީހާގެ ގާތްތިމާގެ ފިރިހެނުންނަކީ މަރެވެ.“ (އެބަހީ ހަލާކެވެ.)\n\nއަދި މުސްލިމުގެ ރިވާޔަތުގައި އިބްނު ވަހްބުގެ އަރިހުން، އަބުއް ޠާހިރު ރިވާ ކުރެއްވިއެވެ: އައްލައިޘު ވިދާޅުވިކަމަށް ތިމަން އަޑުއެހީމެވެ: ޙަމްވުއަކީ ފިރިމީހާގެ އަޚެވެ. އަދި އެފަދަ ފިރިމީހާގެ ތިމާގެ މީހުންވެސްމެއެވެ. އެ ގޮތުން ބޮޑުބޭބެގެ ދަރިއާއި އެ ނޫން މީހުންނެވެ.","[البخاري 5232، ومسلم 2172\n\nمسلم 2172]"],["#320","كتاب النكاح","ކައިވެނީގެ ފޮތް","باب الصداق","ކައިވެނީގެ ރަނުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - (أَنَّ رَسُولَ اللَّهِ ﷺ أَعْتَقَ صَفِيَّةَ، وَجَعَلَ عِتْقَهَا صَدَاقَهَا).","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޞަފިއްޔާ މިނިވަން ކުރެއްވިއެވެ. އަދި އެ ކަމަނާގެ މިނިވަން ކުރެއްވުން ހެއްދެވީ އެ ކަމަނާގެ ރަން ކަމުގައެވެ.","[البخاري 5086، ومسلم 1045، 1365]"],["#321","كتاب النكاح","ކައިވެނީގެ ފޮތް","باب الصداق","ކައިވެނީގެ ރަނުގެ ބާބު","عَنْ سَهْلِ بْنِ سَعْدٍ السَّاعِدِيِّ - رَضِيَ اللَّهُ عَنْهُ - (أَنَّ رَسُولَ اللَّهِ ﷺ جَاءَتْهُ امْرَأَةٌ فَقَالَتْ: إنِّي وَهَبْتُ نَفْسِي لَكَ: فَقَامَتْ طَوِيلاً، فَقَالَ رَجُلٌ: يَا رَسُولَ اللَّهِ، زَوِّجْنِيهَا، إنْ لَمْ يَكُنْ لَكَ بِهَا حَاجَةٌ. فَقَالَ: هَلْ عِنْدَكَ مِنْ شَيْءٍ تُصْدِقُهَا؟ فَقَالَ: مَا عِنْدِي إلا إزَارِي هَذَا. فَقَالَ رَسُولُ اللَّهِ ﷺ: إزَارَكَ إنْ أَعْطَيْتَهَا جَلَسْتَ وَلا إزَارَ لَكَ، فَالْتَمِسْ شَيْئاً قَالَ: مَا أَجِدْ. قَالَ: الْتَمِسْ وَلَوْ خَاتَماً مِنْ حَدِيدٍ، فَالْتَمَسَ فَلَمْ يَجِدْ شَيْئاً، فَقَالَ رَسُولُ اللَّهِ ﷺ هَلْ مَعَكَ شَيْءٌ مِنْ الْقُرْآنِ؟ قَالَ: نَعَمْ، فَقَالَ رَسُولُ اللَّهِ ﷺ: زَوَّجْتُكَهَا بِمَا مَعَك مِنْ الْقُرْآنِ).","ސަހްލު ބިން ސަޢުދު އައްސާޢިދީ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺގެ އަރިހަށް އަންހެނަކު އައިސް ދެންނެވިއެވެ: ތިމަންނަގެ ނަފްސު އިބަ ނަބިއްޔާއަށް ހިބަކުރަމެވެ. (އެބަހީ: ރަނަކާނުލާއި ކައިވެންޏަށް ހުށަހަޅައިފީމެވެ.) ދެން ގިނަ އިރުތަކެއް ވަންދެން އޭނާ ކޮޅަށް ހުއްޓެވެ. ފަހެ ފިރިހެނަކު ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. އިބަ ނަބިއްޔާ އެ ކަނބުލޭގެއަށް ބޭނުމެއް ނުވާނަމަ، އެ ކަނބުލޭގެ ތިމަންނާއާ ކައިވެނި ކޮށްދެއްވާށެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ރަނުގެ ގޮތުގައި އެ ކަނބުލޭގެއަށް ދޭނެ އެއްޗެއް ތިބާގެ އަތުގައި ވޭތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ: ތިމަންނަގެ އިޒާރު (އެބަހީ: ތިރިން ބަނދެގެން މިހުރި ފޮތިގަނޑު) ފިޔަވައި އެހެން އެއްޗެއް ނެތެވެ. ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާގެ އިޒާރު އޭނާއަށް ދީފި ނަމަ، ތިބާ އިށީންނަން ޖެހޭނީ އިޒާރަކާ ނުލައެވެ. ވީމާ ތި ނޫން އެއްޗެއް ހޯދާށެވެ.“ ދެން އޭނާ ދެންނެވިއެވެ: އެހެން އެއްޗެއް ނުފެނުނެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ދަގަނޑު އަނގޮށްޓިއެއް ނަމަވެސް ހޯދާށެވެ.“ ދެން އޭނާ ހޯދަން ގޮސްވެސް އެއްވެސް އެއްޗެއް ނުފެނުނެވެ. ފަހެ ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާއަށް ޤުރްއާނުން އެއްވެސް މިންވަރެއް ދަސްވެފައި ވޭތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ: އާދޭހެވެ. ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާއަށް ޤުރްއާނުން ދަސްވެފައިވާ މިންވަރު (އެ ކަނބުލޭގެއަށް ދަސްކޮށްދޭ) މަތިން ތިބާ އެ ކަނބުލޭގެއާ ކައިވެނި ކޮށްދެއްވައިފީމެވެ.“","[رواه الترمذي 1114 بهذا اللفظ، ورواه البخاري 2310 ومسلم 1425 بنحوه]"],["#322","كتاب النكاح","ކައިވެނީގެ ފޮތް","باب الصداق","ކައިވެނީގެ ރަނުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ -: (أَنَّ رَسُولَ اللَّهِ ﷺ رَأَى عَبْدَ الرَّحْمَنِ بْنَ عَوْفٍ، وَعَلَيْهِ رَدْعُ زَعْفَرَانٍ. فَقَالَ النَّبِيُّ ﷺ: مَهْيَمْ؟ فَقَالَ: يَا رَسُولَ اللَّهِ تَزَوَّجْتُ امْرَأَةً، فَقَالَ: مَا أَصْدَقْتَهَا؟ قَالَ: وَزْنَ نَوَاةٍ مِنْ ذَهَبٍ قَالَ: فَبَارَكَ اللَّهُ لَكَ، أَوْلِمْ وَلَوْ بِشَاةٍ).","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ޢަބްދުއްރަހުމާން ބިން ޢައުފުގެ ގައިގައި ޒަޢުފަރާން (ހުވަނދުގެ) އަސަރުވާ ހާލު، ރަސޫލު ﷲ ﷺ އަށް ފެނިވަޑައިގަތެވެ. ދެން ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާއަށްވީ ކިހިނެއްތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ތިމަން އަންހެނަކާ ކައިވެނި ކުރީއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ ކަނބުލޭގެއަށް ރަނުގެ ގޮތުގައި ދިނީ ކޯއްޗެއްތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ: ނަވާއެއްގެ ބަރުދަނަށް ރަނެވެ. (އެބަހީ: ފަސް ދިރުހަމުގެ މިންވަރެވެ.) ދެން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”فَبَارَكَ اللَّهُ لَكَ (ފަހެ ތިބާއަށް ﷲ ބަރަކާތް ލައްވާށިއެވެ.) އެންމެ ބަކަރި (ކަތިލައިގެން) ނަމަވެސް ވަލީމާ ބާއްވާށެވެ.“",'[رواه أبو داود 2109 بهذا اللفظ إلا قوله: "فبارك الله فيك"، ورواه البخاري 2049 ومسلم 1427 بنحوه]'],["#323","كتاب الطلاق","ވަރީގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا، (أَنَّهُ طَلَّقَ امْرَأَتَهُ وَهِيَ حَائِضٌ، فَذَكَرَ ذَلِكَ عُمَرُ لِرَسُولِ اللَّهِ ﷺ، فَتَغَيَّظَ مِنْهُ رَسُولُ اللَّهِ ﷺ، ثُمَّ قَالَ: لِيُرَاجِعْهَا، ثُمَّ لِيُمْسِكْهَا حَتَّى تَطْهُرَ، ثُمَّ تَحِيضَ فَتَطْهُرَ، فَإِنْ بَدَا لَهُ أَنْ يُطَلِّقَهَا فَلْيُطَلِّقْهَا قَبْلَ أَنْ يَمَسَّهَا، فَتِلْكَ الْعِدَّةُ، كَمَا أَمَرَ اللَّهُ عَزَّ وَجَلَّ).\n\nوَفِي لَفْظٍ: (حَتَّى تَحِيضَ حَيْضَةً مُسْتَقْبَلَةً، سِوَى حَيْضَتِهَا الَّتِي طَلَّقَهَا فِيهَا). \n\nوَفِي لَفْظٍ (فَحُسِبَتْ مِنْ طَلاقِهَا، وَرَاجَعَهَا عَبْدُ اللَّهِ كَمَا أَمَرَهُ رَسُولُ اللَّهِ ﷺ).","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން އެކަލޭގެފާނުގެ އަނބިކަނބަލުން ހައިޟުގައިވަނިކޮށް ވަރި ކުރެއްވިއެވެ. ދެން ޢުމަރުގެފާނު އެ ވާހަކަ ރަސޫލު ﷲ ﷺ ކިޔައިދެއްވިއެވެ. ފަހެ ރަސޫލު ﷲ ﷺ އިބުނު ޢުމަރާ މެދު ކޯފާވެވަޑައިގަތެވެ. (އެހެނީ: ހައިޟުގެ ދުވަސްވަރު ވަރިކުރުމަކީ ހަރާމް ކަމެކެވެ.) އަދި ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެކަލޭގެފާނު އެ ކަނބުލޭގެ އަލުން ރުޖޫއަކުރާށެވެ. އެއަށްފަހު އެ ކަނބުލޭގެ ހައިޟުން ތާހިރުވަންދެން ގެންގުޅޭށެވެ. އަދި އަނެއްކާވެސް ހައިޟުވުމަށް ފަހު ތާހިރުވަންދެން (ބަހައްޓާށެވެ.) ދެން އެއަށްފަހު އެކަލޭގެފާނު އެ ކަނބުލޭގެ ވަރިކުރުމަށް ފެންނަ ނަމަ، ފަހެ ހައިޟުން ތާހިރުވެގެންވާ ހާލު ޖިމާއު ވުމުގެ ކުރިން އެ ކަނބުލޭގެ ވަރިކުރާށެވެ. އެހެނީ އެއީ ﷲ އަމުރު ކުރެއްވި ފަދައިންވާ އިއްދައެވެ.“\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: އަދި އެ ކަނބުލޭގެ ވަރިކުރި ހައިޟު ފިޔަވައި، ކުރިއަށް އޮތްތާ އެހެން ހައިޟެއްވަންދެން (ބަހައްޓާށެވެ.)\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ފަހެ އެއީ އެ ކަނބުލޭގެ ވަރިއެއްގެ ގޮތުގައި ގުނިގެން ދިޔައެވެ. އަދި ރަސޫލު ﷲ ﷺ އަމުރު ކުރެއްވި ފަދައިން ޢަބްދު ﷲ ބިން ޢުމަރު އެ ކަނބުލޭގެ އަނބުރާ ރުޖޫއަ ކުރެއްވިއެވެ.","[البخاري 4908 واللفظ له، ومسلم 1471\n\nمسلم 1471\n\nمسلم 1471]"],["#324","كتاب الطلاق","ވަރީގެ ފޮތް","","","عَنْ فَاطِمَةَ بِنْتِ قَيْسٍ (أَنَّ أَبَا عَمْرِو بْنَ حَفْصٍ طَلَّقَهَا الْبَتَّةَ، وَهُوَ غَائِبٌ).\n\nوَفِي رِوَايَةٍ: (طَلَّقَهَا ثَلاثاً - فَأَرْسَلَ إلَيْهَا وَكِيلَهُ بِشَعِيرٍ، فَسَخِطَتْهُ. فَقَالَ: وَاَللَّهِ مَا لَكِ عَلَيْنَا مِنْ شَيْءٍ: فَجَاءَتْ رَسُولَ اللَّهِ ﷺ فَذَكَرَتْ ذَلِكَ لَهُ، فَقَالَ: لَيْسَ لَكِ عَلَيْهِ نَفَقَةٌ)\n\nوَفِي لَفْظٍ: (وَلا سُكْنَى - فَأَمَرَهَا أَنْ تَعْتَدَّ فِي بَيْتِ أُمِّ شَرِيكٍ، ثُمَّ قَالَ: تِلْكَ امْرَأَةٌ يَغْشَاهَا أَصْحَابِي، اعْتَدِّي عِنْدَ ابْنِ أُمِّ مَكْتُومٍ. فَإِنَّهُ رَجُلٌ أَعْمَى، تَضَعِينَ ثِيَابَكَ، فَإِذَا حَلَلْتِ فَآذِنِينِي. قَالَتْ: فَلَمَّا حَلَلْتُ ذَكَرْتُ لَهُ: أَنَّ مُعَاوِيَةَ بْنَ أَبِي سُفْيَانَ وَأَبَا جَهْمٍ خَطَبَانِي، فَقَالَ رَسُولُ اللَّهِ ﷺ: أَمَّا أَبُو جَهْمٍ: فَلا يَضَعُ عَصَاهُ عَنْ عَاتِقِهِ. وَأَمَّا مُعَاوِيَةُ: فَصُعْلُوكٌ لا مَالَ لَهُ، انْكِحِي أُسَامَةَ بْنَ زَيْدٍ، فَكَرِهَتْهُ ثُمَّ قَالَ: انْكِحِي أُسَامَةَ بْنَ زَيْدٍ، فَنَكَحَتْهُ. فَجَعَلَ اللَّهُ فِيهِ خَيْراً، وَاغْتَبَطَتْ بِهِ).","ފާތިމަތު ބިންތި ގައިސް رَضِيَ اللَّهُ عَنْهَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން އަބޫ އަމްރު ބިން ޙަފްޞު ހާޒިރުގައި ނުވާ ހާލު އެ ކަމަނާ ފަހު ފަހަރަށް ވަރި ކުރެއްވިއެވެ. \n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: އެ ކަމަނާ ތިން ފަހަރަށް ވަރި ކުރެއްވިއެވެ. އަދި އެ ކަމަނާއަށް ހިމަގޮދަންތަކެއް ދެއްވުމަށް އެކަލޭގެފާނުގެ ވަކީލު ފޮނުއްވިއެވެ. ދެން އެ ފޮނުއްވި ތަކެއްޗާމެދު އެ ކަމަނާއަށް ކަމަކު ނުދިޔައެވެ. ފަހެ އަބޫ އަމްރު ދެންނެވިއެވެ: ﷲ ގަންދީ ބުނަމެވެ. ތިމަންމެން ކަމަނާއަށް އަދާކުރަން ޖެހޭ އެއްވެސް ހައްގެއް ނުވެއެވެ. ދެން އެ ކަމަނާ ރަސޫލު ﷲ ﷺގެ އަރިހަށް ވަޑައިގެން އެ ވާހަކަ ދެންނެވުމުން ޙަދީޘް ކުރެއްވިއެވެ: ”އެކަލޭގެފާނު ކަމަނާއަށް ޚަރަދުކުރާކަށް ނުޖެހޭނެއެވެ.“\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”އަދި (އިއްދައިގައި ކަމަނާ އިންނާނެ) ގެއެއް ވެސް ދޭކަށް ނުޖެހޭނެއެވެ.“ ދެން އުންމު ޝަރީކުގެ ގޭގައި އެ ކަމަނާގެ އިއްދަ ހަމަވަންދެން އިނުމަށް ނަބިއްޔާ އެންގެވިއެވެ. އެއަށްފަހު ޙަދީޘް ކުރެއްވިއެވެ: އެ ކަމަނާގެ (ގެއަކީ) ތިމަން ނަބިއްޔާގެ ސަހާބީން ވަރަށް ގިނައިން (މެހެމާނުންގެ ގޮތުގައި) ދާ ގެއެކެވެ. (ވީމާ) އިބުނު އުންމު މަކްތޫމުގެ ގޭގައި ކަމަނާ އިއްދަ ހަމަވަންދެން އިންނަވާށެވެ. އެހެނީ ހަމަކަށަވަރުން އެކަލޭގެފާނަކީ ލޮލަށް ފެނިވަޑައި ނުގަންނަވާ ބޭފުޅެކެވެ. އެކަލޭގެފާނުގެ އަރިހުގައި ކަމަނާ ހިޖާބުކުރައްވާކަށް ނުޖެހޭނެއެވެ. ދެން އިއްދަ ހަމަވުމުން ތިމަން ނަބިއްޔާއަށް ޚަބަރު ދެއްވާށެވެ.“ އެކަމަނާ ވިދާޅުވިއެވެ: ދެން ތިމަން ކަމަނާ އިއްދަ ހަމަވުމުން، މުޢާވިޔާ ބިން އަބީ ސުފްޔާނާއި އަބޫ ޖަހްމު ތިމަން ކަމަނާއާ ކައިވެނި ހުށައެޅުއްވި ވާހަކަ ނަބިއްޔާގެ އަރިހުގައި ދެންނެވީމެވެ. ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އަބޫ ޖަހްމަށް ބަލާއިރު، އެކަލޭގެފާނުގެ ކޮނޑުމަތިން އަސާ ނުނަންގަވައެވެ. (އެބަހީ: އަންހެނުން ގައިގައި ވަރަށް ގިނައިން ޖައްސަވައެވެ.) އަދި މުޢާވިޔާއަށް ބަލާއިރު، އެކަލޭގެފާނަކީ ފަގީރު މުދަލެއް ނުވާ ބޭކަލެކެވެ. (އެހެނެއްކަމަކު) ކަމަނާ އުސާމާ ބިން ޒައިދާ ކައިވެނި ކުރައްވާށެވެ.“ ފަހެ ތިމަން ކަމަނާ އުސާމާއާ ކައިވެނި ކުރެއްވުމަށް ނުރުހުންތެރިވީމެވެ. (ނޯޓު: އުސާމާ ބިން ޒައިދަކީ މިނަވަން ކުރެއްވުނު ކުރީގެ އަޅެއްގެ ދަރި މިނިވަން ކުރެއްވުނު ކުރީގެ އަޅެކެވެ. އަދި އޭގެ އިތުރުން އެކަލޭގެފާނަކީ ވަރަށް ކަޅު ބޭކަލެކެވެ. އަދި މި ކަމަނާއަކީ ހަސަބާއި ރީތިކަމުގައި ވެސް އަރާހުންނެވި ގުރައިޝު ވަންހައިގެ ކަނބަލެކެވެ.) އެއަށްފަހު އަނެއްކާވެސް ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ކަމަނާ އުސާމާ ބިން ޒައިދާ ކައިވެނި ކުރައްވާށެވެ.“ ދެން ތިމަންކަމަނާ އެކަލޭގެފާނާ ކައިވެނި ކުރެއްވީމެވެ. ފަހެ އެކަލޭގެފާނުގެ ކިބައިގައި ﷲ ހެޔޮކަން ލެއްވިއެވެ. އަދި ތިމަން ކަމަނާއަށް މި ލިބުނު ފަދަ ހެޔޮކަމެއް ލިބޭނެ ނަމައޭ މީސްތަކުން އުންމީދު ކުރިއެވެ.","[مسلم 1480\n\nمسلم 1480]"],["#325","كتاب الطلاق","ވަރީގެ ފޮތް","باب العدة","އިއްދައިގެ ބާބު","عَنْ سُبَيْعَةَ الأَسْلَمِيَّةِ أَنَّهَا كَانَتْ تَحْتَ سَعْدِ بْنِ خَوْلَةَ - وَهُوَ مِنْ بَنِي عَامِرِ بْنِ لُؤَيٍّ، وَكَانَ مِمَّنْ شَهِدَ بَدْراً - فَتُوُفِّيَ عَنْهَا فِي حَجَّةِ الْوَدَاعِ، وَهِيَ حَامِلٌ. فَلَمْ تَنْشَبْ أَنْ وَضَعَتْ حَمْلَهَا بَعْدَ وَفَاتِهِ، فَلَمَّا تَعَلَّتْ مِنْ نِفَاسِهَا: تَجَمَّلَتْ لِلْخُطَّابِ، فَدَخَلَ عَلَيْهَا أَبُو السَّنَابِلِ بْنُ بَعْكَكٍ - رَجُلٌ مِنْ بَنِي عَبْدِ الدَّارِ - فَقَالَ لَهَا: مَا لِي أَرَاك مُتَجَمِّلَةً؟ لَعَلَّكِ تُرَجِّينَ لِلنِّكَاحِ، وَاَللَّهِ مَا أَنْتِ بِنَاكِحٍ حَتَّى يَمُرَّ عَلَيْك أَرْبَعَةُ أَشْهُرٍ وَعَشْرٌ. قَالَتْ سُبَيْعَةُ: فَلَمَّا قَالَ لِي ذَلِكَ: جَمَعْتُ عَلَيَّ ثِيَابِي حِينَ أَمْسَيْتُ، فَأَتَيْتُ رَسُولَ ﷺ فَسَأَلْتُهُ عَنْ ذَلِكَ؟ فَأَفْتَانِي بِأَنِّي قَدْ حَلَلْتُ حِينَ وَضَعْتُ حَمْلِي، وَأَمَرَنِي بِالتَّزْوِيجِ إنْ بَدَا لِي).\n\nوقالَ ابنُ شِهابٍ: ولاأَرى بَأْساً أَنْ تَتَزَوَّجَ حينَ وضَعَتْ، وإِنْ كَانَتْ في دَمِها، غَيْرَ أَنَّهُ لايَقْرَبُها زَوْجُها حتَّى تَطْهُرَ.","ސުބައިޢާ އަލްއަސްލަމިއްޔާގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން އެ ކަމަނާވީ ސަޢުދު ބިން ޚައުލާގެ އަނބިކަނބަލެއް ކަމުގައެވެ. – އަދި އެކަލޭގެފާނަކީ ބަނީ ޢާމިރު ބިން ލުއައްޔު ވަންހައިގެ ބޭކަލެކެވެ. އަދި ބަދުރު ހަނގުރާމައިގައި ބައިވެރިވެލެއްވި ބޭކަލުންގެ ތެރޭގައި އެކަލޭގެފާނު ހިމެނިވަޑައިގަތެވެ. – ފަހެ އެ ކަމަނާގެ ފިރިކަލުން ހައްޖަތުލް ވަދާއުގައި (މައްކާގައި) އަވަހާރަވި ހިނދު އެ ކަމަނާ އިންނެވީ އާލާސްކަންފުޅުގައެވެ. ދެން އެކަލޭގެފާނު އަވަހާރަވުމަށް ފަހު މާ ގިނަ ވަގުތަކެއް ހޭދަ ނުވެ އެ ކަމަނާގެ އާލާސްކަންފުޅުން ރައްކާވެއްޖެއެވެ. ދެން ނިފާސްކަމުގެ ލޭކެނޑުމުން އެ ކަމަނާއަށް ކައިވެނި ހުށައެޅުއްވުމަށް ޓަކައި ޒީނަތްތެރިވެ ވަޑައިގަތެވެ. ފަހެ ބަނީ ޢަބްދުއް ދާރު ވަންހައިގެ ބޭކަލެއް ކަމަށްވާ އަބުއް ސަނާބިލު ބިން ބަޢުކަކު އެ ކަމަނާގެ އަރިހަށް ވަދެވަޑައިގެން ދެންނެވިއެވެ: ކަމަނާ ޒީނަތްތެރިވެ ނަލަވެފައި ވަނިކޮށް މިފެންނަނީ ކީއްވެތޯ؟ ފަހަރެއްގައި ކަމަނާ ކައިވެންޏަށް އުންމީދު ކުރައްވަނީތާއެވެ. ﷲ ގަންދީ ބުނަމެވެ. ހަތަރު މަހާއި ދިހަ ދުވަސް ހޭދަ ނުކުރަނީސް ކަމަނާއަށް ކައިވެނި ނުކުރެވޭނެއެވެ. ސުބައިޢާ ވިދާޅުވިއެވެ: އެކަލޭގެފާނު ތިމަން ކަމަނާއަށް އެހެން ދެންނެވުމުން، ހަވީރުވުމުން ތިމަން ކަމަނާގެ ހެދުންތައް ތުރުކުރައްވައިގެން، ރަސޫލު ﷲ ﷺގެ އަރިހަށް ގޮސް އެ ކަމާ ބެހޭ ގޮތުން ސުވާލު ކުރެއްވީމެވެ. ދެން ނަބިއްޔާ ފަތުވާ ދެއްވީ ތިމަން ކަމަނާ ވިހެއި ހިސާބުން އިއްދަ ހަމަވީ ކަމަށެވެ. އަދި ތިމަން ކަމަނާ ބޭނުން ނަމަ އަލުން ކައިވެނިކުރުމަށް އެންގެވިއެވެ.\n\nއަދި އިބްނު ޝިހާބު (އައްޒުހްރީ) ވިދާޅުވިއެވެ: ވިހެއުމަށް ފަހު އަލުން ކައިވެނިކުރުމަކީ މައްސަލައެއް ކަމަކަށް ތިމަން ނުދެކެމެވެ. އެއީ ނިފާސްކަމުގެ ލޭ ނުކެނޑުނު ކަމުގައިވިއަސް މެއެވެ. ނަމަވެސް ލޭކެނޑި (ތާހިރުވަންދެން) އެ ކަނބުލޭގެއާ އެއްދާންކޮށްގެން ނުވާނެއެވެ.","[البخاري 5319، 5320، ومسلم 1484 واللفظ له]"],["#326","كتاب الطلاق","ވަރީގެ ފޮތް","باب العدة","އިއްދައިގެ ބާބު","عَنْ زَيْنَبَ بِنْتِ أُمِّ سَلَمَةَ قَالَتْ: (تُوُفِّيَ حَمِيمٌ لأُمِّ حَبِيبَةَ، فَدَعَتْ بِصُفْرَةٍ، فَمَسَحَتْ بِذِرَاعَيْهَا، فَقَالَتْ: إنَّمَا أَصْنَعُ هَذَا ; لأَنِّي سَمِعْتُ رَسُولَ اللَّهِ ﷺ يَقُولُ: لا يَحِلُّ لامْرَأَةٍ تُؤْمِنُ بِاَللَّهِ وَالْيَوْمِ الآخِرِ أَنْ تُحِدَّ عَلَى مَيِّتٍ فَوْقَ ثَلاثٍ، إلاَّ عَلَى زَوْجٍ: أَرْبَعَةَ أَشْهُرٍ وَعَشْراً).","ޒައިނަބު ބިންތު އުންމު ސަލަމާ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އުންމު ޙަބީބާގެ ގާތްތިމާގެ މީހަކު (އެބަހީ: ބައްޕާފުޅު އަބޫ ސުފްޔާނު) އަވަހާރަވިއެވެ. ދެން (ތިން ދުވަސް ފަހުން) އުންމު ޙަބީބާ ރީނދޫ ހުވަނދެއް ގެނައުމަށް އެންގެވިއެވެ. އަދި އޭގައި އެ ކަމަނާގެ ދެ އަތްޕުޅުން ބީއްސެވިއެވެ. އެއަށްފަހު ދެންނެވިއެވެ: ތިމަން ކަމަނާ މިފަދައިން ކަންކުރެއްވި ހަމައެކަނި ސަބަބަކީ، ރަސޫލު ﷲ ﷺ މިފަދައިން ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން ކަމަނާ އަޑުއެހީމައެވެ: ”ﷲ އާއި ގިޔާމަތް ދުވަހަށް އީމާންވާ އަންހެނަކު، ތިން ދުވަހަށް ވުރެ ގިނަ ދުވަހު މައްޔިތެއްގެ މައްޗަށް އިޙްދާދުގައި އިނދެގެން ނުވާނެއެވެ. އެއީ ފިރިމީހާ މަރުވުމުން ހަތަރު މަސްދުވަހާއި ދިހަ ދުވަސް މެނުވީއެވެ.“ (ނޯޓު: އިޙްދާދަކީ އަންހެނެއްގެ ގާތްތިމާގެ މީހަކު ނުވަތަ ފިރިމީހާ ނިޔާވުމުން، އެ އަންހެނާ ވަކި މުއްދަތެއް ވަންދެން ގޭގައި މަޑުކޮށްގެން ހުރެ އަދި އެހެން މީހަކާ ނީންދެ ހުރެ، ރީތި ފޭރާން ލުމާއި ގަހަނާ ލުމާއި މޭކްއަޕް ކުރުމާއި މީރުވަސް ދުވާ އެއްޗެހި ބޭނުން ކުރުމުން މަނާވެގަތުމެވެ. ފިރިމީހާ ނިޔާވުމުން ހަތަރު މަސްދުވަހާއި ދިހަ ދުވަސްވަންދެން އިޙްދާދުގައި އިނުން އަންހެނުންނަށް ވާޖިބެވެ. ނޫނީ ބަނޑުބޮޑު ނަމަ ވިހުމަށް ދާންދެން އިޙްދާދުގައި އިނުން ވާޖިބެވެ. އަދި ފިރިމީހާ ނޫން މިހަކު ނިޔާވެއްޖެ ނަމަ އަންހެނަކު އިޙްދާދުގައި އިނުން ވާޖިބެއް ނޫނެވެ. އެހެނަސް އެ އަންހެނާ އިޙްދާދުގައި އިންނަ ނަމަ ތިން ދުވަހަށް ވުރެ ގިނަދުވަހު އިނދެގެން ނުވާނެވެ.)","[البخاري 1280، ومسلم 1486 واللفظ له]"],["#327","كتاب الطلاق","ވަރީގެ ފޮތް","باب العدة","އިއްދައިގެ ބާބު","عَنْ أُمِّ عَطِيَّةَ رَضِيَ اللَّهُ عَنْهُا: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: (لا تُحِدُّ امْرَأَةٌ عَلَى الْمَيِّتِ فَوْقَ ثَلاثٍ، إلا عَلَى زَوْجٍ: أَرْبَعَةَ أَشْهُرٍ وَعَشْراً، وَلا تَلْبَسُ ثَوْباً مَصْبُوغاً إلاَّ ثَوْبَ عَصْبٍ. وَلا تَكْتَحِلُ. وَلا تَمَسُّ طِيباً، إلاَّ إذَا طَهُرَتْ: نُبْذَةً مِنْ قُسْطٍ أَوْ أَظْفَارٍ).","އުންމު ޢަޠިއްޔާ رَضِيَ اللَّهُ عَنْهَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”މީހަކު މަރުވުމުން، ތިން ދުވަހަށް ވުރެ ގިނަ ދުވަހު އަންހެނަކު އިޙްދާދުގައި އިނދެގެން ނުވާނެއެވެ. އެއީ ފިރިމީހާ މަރުވުމުން ހަތަރު މަސްދުވަހާއި ދިހަ ދުވަސް މެނުވީއެވެ. އަދި ޢަޞަބުގެ (އެބަހީ: ޒީނަތްތެރި ކަމެއް ނުވާ ކުލަކުލައިގެ ޔާނުން ފަހާފައިވާ) ހެދުމެއް ފިޔަވައި ކުލަޖައްސާފައިވާ އެއްވެސް ހެދުމެއް އެ ކަނބުލޭގެ ލައިގެން ނުވާނެއެވެ. އަދި އަނދުން އަޅައިގެން ނުވާނެއެވެ. އަދި ހުވަނދުގައި އަތްލައިގެން ނުވާނެއެވެ. އެއީ އެ ކަނބުލޭގެ ހައިޟުވުމުން ތާހިރުވެގަންނަ ހިނދުގައި މެނުވީއެވެ. ފަހެ އެ ހިނދު ގުސްޠާއި އަޡްފަރުން އެތިކޮޅެއް ބޭނުން ކުރެވިދާނެއެވެ.“","[البخاري 5342، 5343، ومسلم 1127، 938 واللفظ له]"],["#328","كتاب الطلاق","ވަރީގެ ފޮތް","باب العدة","އިއްދައިގެ ބާބު","عَنْ أُمِّ سَلَمَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: (جَاءَتْ امْرَأَةٌ إلَى رَسُولِ اللَّهِ ﷺ فَقَالَتْ: يَا رَسُولَ اللَّهِ، إنَّ ابْنَتِي تُوُفِّيَ عَنْهَا زَوْجُهَا، وَقَدْ اشْتَكَتْ عَيْنَهَا أَفَنُكَحِّلُهَا؟ فَقَالَ رَسُولُ اللَّهِ ﷺ: لا - مَرَّتَيْنِ، أَوْ ثَلاثَاً - ثُمَّ قَالَ: إنَّمَا هِيَ أَرْبَعَةُ أَشْهُرٍ وَعَشْرٌ. وَقَدْ كَانَتْ إحْدَاكُنَّ فِي الْجَاهِلِيَّةِ تَرْمِي بِالْبَعْرَةِ عَلَى رَأْسِ الْحَوْلِ).\n\nفَقَالَتْ زَيْنَبُ: كَانَتْ الْمَرْأَةُ إذَا تُوُفِّيَ عَنْهَا زَوْجُهَا: دَخَلَتْ حِفْشاً، وَلَبِسَتْ شَرَّ ثِيَابِهَا، وَلَمْ تَمَسَّ طِيباً وَلا شَيْئاً حَتَّى تَمُرَّ بِهَا سَنَةٌ، ثُمَّ تُؤْتَى بِدَابَّةٍ - حِمَارٍ أَوْ طَيْرٍ أَوْ شَاةٍ - فَتَفْتَضَّ بِهِ. فَقَلَّمَا تَفْتَضُّ بِشَيْءٍ إلاَّ مَاتَ. ثُمَّ تَخْرُجُ فَتُعْطَى بَعْرَةً، فَتَرْمِي بِهَا ثُمَّ تُرَاجِعُ بَعْدُ مَا شَاءَتْ مِنْ طِيبٍ أَوْ غَيْرِهِ).","އުއްމު ސަލަމާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ:  އަންހެނަކު (ކަމަކާމެދު ސުވާލު ކުރެއްވުމަށް) ރަސޫލު ﷲ ﷺގެ އަރިހަށް އައިސް ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ތިމަން ކަމަނާގެ އަންހެން ދަރިކަނބަލުންގެ ފިރިކަލުން ނިޔާވެއްޖެއެވެ. އަދި ހަމަކަށަވަރުން (އެ ކަމަނާ އިޙްދާދުގައި ވަނިކޮށް) ލޮލުގައި ރިއްސަން ފަށައިފިއެވެ. އެ ކަމަނާ އަނދުން އަޅުވަންވީތޯއެވެ؟ ދެން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ނޫނެކެވެ.“ ދެ ފަހަރު ނުވަތަ ތިން ފަހަރު އެފަދައިން ޙަދީޘް ކުރެއްވިއެވެ. އެއަށްފަހު ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެއީ (އެބަހީ: އިޙްދާދުގައި އިންތިޒާރު ކުރަންޖެހެނީ) ހަމައެކަނި ހަތަރު މަހާއި ދިހަ ދުވަހެވެ. އަދި ހަމަކަށަވަރުން ޖާހިލިއްޔަ ޒަމާނުގައި ތިޔަ އަންހެނުންގެ ތެރެއިން އަންހެނަކު، އަހަރުދުވަސް ވަންދެން (އިޙްދާދުގައި އިންތިޒާރު ކުރުމަށްފަހު އެކަން ނިންމާލަނީ) ޖަމަލެއްގެ ބޮޑުކަމުދާ ތަކެތި ނަގައި އުކާލައިގެންނެވެ.“\n\nއަދި ޒައިނަބު (މި ޙަދީޘް ކިޔައިދެއްވުމަށް ފަހު) ވިދާޅުވިއެވެ: (ޖާހިލިއްޔަ ޒަމާނުގައި) އަންހެނަކު އޭނާގެ ފިރިމީހާ މަރުވެއްޖެނަމަ ކުޑަ އަނދިރި ގެއަކަށް (އެކަހެރިވާން) ވަދެއެވެ. އަދި އެންމެ ހަޑި ނިކަމެތި ހެދުން ލައެވެ. އަދި އެއްވެސް ހުވަނދެއް އެއްޗެއްގައި އަތްނުލައެވެ. އެއީ އޭނާއަށް އަހަރުދުވަހެއް އެއްކޮށް ފާއިތުވަންދެނެވެ. އެއަށްފަހު އޭނާ ޖަނަވާރެއްގައި ބީހުމަށް ޓަކައި –ހިމާރެއް ނުވަތަ ދޫންޏެއް ނުވަތަ ބަކަރިއެއް– އެ އަންހެނާއަށް ގެނެވެއެވެ. އަދި އޭގައި އެ އަންހެނާގެ ގައިގައި ހުރި ހަޑިތައް އުނގުޅާލައެވެ. އަދި މަދު ފަހަރަކު މެނުވީ ޖަނަވާރެއްގައި މިފަދައިން އުނގުޅާލުމުން އޭތި މަރުވެގެން ނޫނީ ނުދެއެވެ. އެއަށްފަހު އޭނާ އެ (ގެއިން) ނުކުމެ ޖަމަލެއްގެ ބޮޑުކަމުދާ ތަކެތި އޭނާއަށް ދެވެއެވެ. ދެން އޭތި އުކާލުމަށް ފަހު (އިޙްދާދު ނިންމާލައެވެ.) އަދި ހުވަނދާއި އެ ނޫން ތަކެތިން ބޭނުން އެއްޗެއް އަނެއްކާވެސް އޭނާ ބޭނުން ކުރެއެވެ.\n","[البخاري 5336، ومسلم 1488\n\nالبخاري 5337، ومسلم 1489 واللفظ له]"],["#329","كتاب اللعان","ލިޢާނުގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا (أَنَّ فُلانَ بْنَ فُلانٍ قَالَ: يَا رَسُولَ اللَّهِ، أَرَأَيْتَ أَنْ لَوْ وَجَدَ أَحَدُنَا امْرَأَتَهُ عَلَى فَاحِشَةٍ، كَيْفَ يَصْنَعُ؟ إنْ تَكَلَّمَ تَكَلَّمَ بِأَمْرٍ عَظِيمٍ، وَإِنْ سَكَتَ سَكَتَ عَلَى مِثْلِ ذَلِكَ. قَالَ: فَسَكَتَ النَّبِيُّ ﷺ فَلَمْ يُجِبْهُ. فَلَمَّا كَانَ بَعْدَ ذَلِكَ أَتَاهُ فَقَالَ: إنَّ الَّذِي سَأَلْتُكَ عَنْهُ قَدْ اُبْتُلِيتُ بِهِ. فَأَنْزَلَ اللَّهُ عَزَّ وَجَلَّ هَؤُلاءِ الآيَاتِ فِي سُورَةِ النُّورِ (وَاَلَّذِينَ يَرْمُونَ أَزْوَاجَهُمْ) فَتَلاهُنَّ عَلَيْهِ وَوَعَظَهُ وَذَكَّرَهُ. وَأَخْبَرَهُ أَنَّ عَذَابَ الدُّنْيَا أَهْوَنُ مِنْ عَذَابِ الآخِرَةِ. فَقَالَ: لا، وَاَلَّذِي بَعَثَكَ بِالْحَقِّ، مَا كَذَبْتُ عَلَيْهَا. ثُمَّ دَعَاهَا، فَوَعَظَهَا، وَأَخْبَرَهَا: أَنَّ عَذَابَ الدُّنْيَا أَهْوَنُ مِنْ عَذَابِ الآخِرَةِ. فَقَالَتْ: لا، وَاَلَّذِي بَعَثَكَ بِالْحَقِّ، إنَّهُ لَكَاذِبٌ. فَبَدَأَ بِالرَّجُلِ فَشَهِدَ أَرْبَعَ شَهَادَاتٍ بِاَللَّهِ: إنَّهُ لَمِنْ الصَّادِقِينَ. وَالْخَامِسَةَ: أَنَّ لَعْنَةَ اللَّهِ عَلَيْهِ إنْ كَانَ مِنْ الْكَاذِبِينَ. ثُمَّ ثَنَّى بِالْمَرْأَةِ. فَشَهِدَتْ أَرْبَعَ شَهَادَاتٍ بِاَللَّهِ: إنَّهُ لَمِنْ الْكَاذِبِينَ، وَالْخَامِسَةَ: أَنَّ غَضَبَ اللَّهِ عَلَيْهَا إنْ كَانَ مِنْ الصَّادِقِينَ. ثُمَّ فَرَّقَ بَيْنَهُمَا.\n\nثُمَّ قَالَ: إنَّ اللَّهَ يَعْلَمُ أَنَّ أَحَدَكُمَا كَاذِبٌ فَهَلْ مِنْكُمَا تَائِبٌ؟) ثَلاثاً.\n\nوَفِي لَفْظٍ (لا سَبِيلَ لَكَ عَلَيْهَا قَالَ: يَا رَسُولَ اللَّهِ، مَالِي؟ قَالَ: لا مَالَ لَكَ. إنْ كُنْتَ صَدَقْتَ عَلَيْهَا فَهُوَ بِمَا اسْتَحْلَلْتَ مِنْ فَرْجِهَا وَإِنْ كُنْتَ كَذَبْتَ فَهُوَ أَبْعَدُ لَكَ مِنْهَا).","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން އެވެނި މީހެއްގެ ދަރި މިވެނި މީހަކު ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ތިމަންމެގެ ތެރެއިން މީހަކު، އޭނާގެ އަނބިމީހާ ޒިނޭކުރަނިކޮށް ފެނިއްޖެނަމަ، ކަންތައްކުރާނީ ކޮންފަދައަކުންތޯއެވެ؟ އޭނާ ވާހަކަދައްކަން ވެއްޖެނަމަ، އެ ދައްކަނީ ބޮޑުވެގެންވާ ކަމެއްގެ މައްޗަށެވެ. އަދި އޭނާ ހަނުހުރި ކަމުގައި ވިޔަސް، އެ ހަނު ހުރެވެނީ ހަމަ އެހާ (ބޮޑު) ކަމެއްގެ މައްޗަށެވެ. ފަހެ ނަބިއްޔާ ސުކޫތުވެ ހުންނަވައި، އޭނާއަށް ޖަވާބެއް ނުދެއްވައެވެ. ދެން ފަހުން އެ މީހާ ނަބިއްޔާގެ އަރިހަށް (އެނބުރި) އަތުވެގެން ދެންނެވިއެވެ: ހަމަކަށަވަރުން (ކުރިން) އިބަ ނަބިއްޔާގެ އަރިހުގައި ސުވާލުކުރި ކަންތައް ތިމަންނައާ ޖެހިއްޖެއެވެ. ދެން ނޫރު ސޫރަތުގެ މި އާޔަތްތައް ﷲ ޢައްޒަ ވަޖައްލަ ބާވައިލެއްވިއެވެ. (އެބަހީ:) ’އަދި (އެބައިމީހުންގެ އަމިއްލަ ނަފްސުތައް މެނުވީ ހެކިދޭ ބަޔަކު އެބައިމީހުންނަށް ލިބިގެން ނުވާ ހާލު) އެބައިމީހުންގެ އަނބީންނަށް ގަޒްފުކުރާ މީހުން (ދަންނާށެވެ.)...‘ ފަހެ ނަބިއްޔާ އެ އާޔަތްތައް އޭނާއަށް ކިޔަވައި ވިދާޅުވިއެވެ. އަދި އޭނާއަށް ވައުޒުދެއްވައި ހަނދުމަކޮށްދެއްވިއެވެ. އަދި އާޚިރަތުގެ އަޒާބަށް ވުރެ ދުނިޔޭގެ އަޒާބު ލުއިވާނެކަން އޭނާއަށް ޚަބަރު ދެއްވިއެވެ. ދެން އޭނާ ދެންނެވިއެވެ: ނޫނެކެވެ. އިބަ ނަބިއްޔާ ހައްގައިގެން ނަބިއްޔެއްގެ ގޮތުގައި ފޮނުއްވެވި ފަރާތް ގަންދެއްވަމެވެ. އެ ކަނބުލޭގެ މައްޗަށް ތިމަން ދޮގެއް ނަހަދަމެވެ. ދެން އެ ކަނބުލޭގެ އަތުވުމަށް ނަބިއްޔާ އެންގެވިއެވެ. އަދި އެ ކަނބުލޭގެއަށް ވައުޒުދެއްވައި ހަނދުމަކޮށްދެއްވިއެވެ. އަދި އާޚިރަތުގެ އަޒާބަށް ވުރެ ދުނިޔޭގެ އަޒާބު ލުއިވާނެކަން އޭނާއަށް ޚަބަރު ދެއްވިއެވެ. ދެން އެ ކަނބުލޭގެ ދެންނެވިއެވެ: ނޫނެކެވެ. އިބަ ނަބިއްޔާ ހައްގައިގެން ފޮނުއްވެވި ފަރާތް ގަންދެއްވަމެވެ. ހަމަކަށަވަރުން އޭނާއަކީ ދޮގުވެރިއެކެވެ. ދެން (ލިޢާނުކުރުމުގައި) ފެއްޓެވީ ފިރިހެން މީހާގެ ކިބައިންނެވެ. ފަހެ ހަމަކަށަވަރުން އޭނާއަކީ ތެދުވެރިއެއްކަމަށް ހަތަރު ފަހަރު ﷲ ގަންދީ ހުވާ ކުރިއެވެ. އަދި ފަސްވަނައަށް، އޭނާއަކީ ދޮގުވެރިއެއް ނަމަ ﷲގެ ލަޢުނަތް އޭނާގެ މައްޗަށް ލެއްވުން އެދި ދުއާކުރިއެވެ. އެއަށްފަހު (ލިޢާނުކުރުމުގައި) ދެވަނައަށް ޖެއްސެވީ އަންހެން މީހާއަށެވެ. ފަހެ ހަމަކަށަވަރުން އެ ފިރިހެން މީހާއަކީ ދޮގުވެރިއެއްކަމަށް ހަތަރު ފަހަރު ﷲ ގަންދީ ހުވާ ކުރިއެވެ. އަދި ފަސްވަނައަށް، އެ ފިރިހެން މީހާއަކީ ތެދުވެރިއެއް ނަމަ ﷲގެ ކޯފާ އެ ކަނބުލޭގެ މައްޗަށް ލެއްވުން އެދި ދުއާކުރިއެވެ. އެއަށްފަހު (ދާއިމަށް) އެދެމަފިރިން ވަކިކުރެއްވިއެވެ. އަދި ތިން ފަހަރު ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަ ދެމީހުން ކުރެ އެކަކަކީ ދޮގުވެރިއެއްކަން ﷲ ދެނެވޮޑިގެންވެއެވެ. ފަހެ ތިޔަ ދެމީހުންގެ ތެރޭގައި ތައުބާވާނެ މީހަކުވޭހެއްޔެވެ؟“\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: (ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ:) ”އެކަނބުލޭގެ މައްޗަށް ތިބާއަށް އެއްވެސް ހައްގެއް ނުވެއެވެ.“ އޭނާ ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. (ކައިވެނީގެ ރަނުގެ ގޮތުގައި ދެވުނު) ތިމަންނަގެ މުދާ ކިހިނަކުންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާއަކަށް ލިބޭނެ މުދަލެއް ނުވެއެވެ. އެ ކަނބުލޭގެ މައްޗަށް ތިބާ ހެދީ ތެދުކަމުގައި ވާނަމަ، ފަހެ އެވާނީ އެ ކަނބުލޭގެއާ އެއްދާންކުރުން ހުއްދަވެގެން ދިޔުމުގެ މައްޗަށްވި ބަދަލަކަށެވެ. އަދި އެ ކަނބުލޭގެ މައްޗަށް ތިބާ ހެދީ ދޮގުކަމުގައި ވާނަމަ، ފަހެ އެ ކަނބުލޭގެއަށް ވުރެ ތިބާ (ހައްގެއް ގޮތުގައިވުން) މާ ދުރުވެގެން ވެއެވެ.“","[البخاري 5311، 5312، 5349، 5350، ومسلم 1493\n\nالبخاري 5312، ومسلم 1493\n\nالبخاري 5350، ومسلم 1493]"],["#330","كتاب اللعان","ލިޢާނުގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا (أَنَّ رَجُلاً رَمَى امْرَأَتَهُ، وَانْتَفَى مِنْ وَلَدِهَا فِي زَمَنِ رَسُولِ اللَّهِ ﷺ فَأَمَرَهُمَا رَسُولُ اللَّهِ ﷺ فَتَلاعَنَا، كَمَا قَالَ اللَّه تَعَالَى، ثُمَّ قَضَى بِالْوَلَدِ لِلْمَرْأَةِ، وَفَرَّقَ بَيْنَ الْمُتَلاعِنَيْنِ).","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺގެ ޒަމާނުގައި، މީހާކު އޭނާގެ އަންހެނުން ޒިނޭކުރި ކަމަށް ތުހުމަތުކޮށް އެ އަންހެނާގެ ދަރިއަކީ އޭނާގެ ދަރިއެއް ނޫން ކަމަށް ބުންޏެވެ. ދެން ރަސޫލު ﷲ ﷺ އެ ދެމަފިރިން ލިޢާނު ކުރުމަށް އެނގެވެމުން އެކަން ކުރިއެވެ. އެއީ ﷲ ތަޢާލާ ވަހީ ކުރެއްވި ގޮތަށެވެ. ދެން ދަރި ވަނީ ހަމައެކަނި މަންމައަށް ކަމަށް ނަބިއްޔާ ނިޔާ ކުރައްވައި، (ކައިވެނި ރޫޅާލައްވައި) ލިޢާނު ކުރި ދެމަފިރިން ވަކި ކުރެއްވިއެވެ.","[البخاري 4748 واللفظ له، ومسلم 1494]"],["#331","كتاب اللعان","ލިޢާނުގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ (جَاءَ رَجُلٌ مِنْ بَنِي فَزَارَةَ إلَى النَّبِيِّ ﷺ فَقَالَ: إنَّ امْرَأَتِي وَلَدَتْ غُلاماً أَسْوَدَ. فَقَالَ النَّبِيُّ ﷺ هَلْ لَك إبِلٌ؟ قَالَ: نَعَمْ. قَالَ: فَمَا أَلْوَانُهَا؟ قَالَ: حُمْرٌ. قَالَ: فَهَلْ يَكُونُ فِيهَا مِنْ أَوْرَقَ؟ قَالَ: إنَّ فِيهَا لَوُرْقاً. قَالَ: فَأَنَّى أَتَاهَا ذَلِكَ؟ قَالَ: عَسَى أَنْ يَكُونَ نَزَعَهُ عِرْقٌ. قَالَ: وَهَذَا عَسَى أَنْ يَكُونَ نَزَعَهُ عِرْقٌ).","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ފަޒާރާ ގަބީލާގެ މީހަކު ނަބިއްޔާ ﷺގެ އަރިހަށް އައިސް ދެންނެވިއެވެ: ތިމަންނަގެ އަނބިމީހާ ކަޅު ފިރިހެން ކުއްޖެއް ވިހައިފިއެވެ. ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ ޖަމަލު ގެންގުޅެންތޯވެ؟“ އޭނާ ދެންނެވިއެވެ: އާދޭހެވެ.ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭގެ ކުލަތަކަކީ ކޮބައިތޯއެވެ؟ އޭނާ ދެންނެވިއެވެ. ރަތްކުލައެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭގެ ތެރޭގައި މަޑު ކަޅު ކުލައިގެ ޖަމަލުވޭތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ: ހަމަކަށަވަރުން އޭގެ ތެރޭގައި މަޑު ކަޅު ކުލައިގެ ޖަމަލުވެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެހެންވިއްޔާ އެ ޖަމަލުތަކަށް އެ ކުލަ އައީ ކޮންތާކުންތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ. ފަހަރެއްގައި އެއީ ވާރުތަވެފައިވާ ކުލައަކަށް ވެދާނެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އަދި މި ކުއްޖާއަކީވެސް، ފަހަރެއްގައި (ކުރީގެ ކާބަފައިންގެ ފަރާތުން އެ ކުލަ) ވާރުތަވެފައިވާ ކުއްޖަކަށް ވެދާނެއެވެ.“","[البخاري 5305، ومسلم 1500 واللفظ له]"],["#332","كتاب اللعان","ލިޢާނުގެ ފޮތް","","","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: (اخْتَصَمَ سَعْدُ بْنُ أَبِي وَقَّاصٍ وَعَبْدُ بْنُ زَمْعَةَ فِي غُلامٍ. فَقَالَ سَعْدٌ: يَا رَسُولَ اللَّهِ هَذَا ابْنُ أَخِي عُتْبَةَ بْنِ أَبِي وَقَّاصٍ، عَهِدَ إلَيَّ أَنَّهُ ابْنُهُ، اُنْظُرْ إلَى شَبَهِهِ. وَقَالَ عَبْدُ بْنُ زَمْعَةَ: هَذَا أَخِي يَا رَسُولَ اللَّهِ، وُلِدَ عَلَى فِرَاشِ أَبِي مِنْ وَلِيدَتِهِ، فَنَظَرَ رَسُولُ اللَّهِ ﷺ إلَى شَبَهِهِ، فَرَأَى شَبَهًا بَيِّنًا بِعُتْبَةَ فَقَالَ: هُوَ لَك يَا عَبْدُ بْنَ زَمْعَةَ، الْوَلَدُ لِلْفِرَاشِ وَلِلْعَاهِرِ الْحَجَرُ. وَاحْتَجِبِي مِنْهُ يَا سَوْدَةُ، فَلَمْ يَرَ سَوْدَةَ قَطُّ).","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ސަޢުދު ބިން އަބީ ވައްޤާޞާއި ޢަބްދު ބިން ޒަމްޢާ، ފިރިހެން ކުއްޖެއްގެ (ނަސްލާމެދު) އަރައިރުންވިއެވެ. (ނަބިއްޔާގެ އަރިހުގައި) ސަޢުދު ދެންނެވިއެވެ: މި ކުއްޖާއަކީ ތިމަންނަގެ އަޚް ޢުތުބާ ބިން އަބީ ވައްގާޞްގެ ދަރިއެކެވެ. އެއީ އޭނާގެ ދަރިއެއްކަމަށް ޢުތުބާ ވަނީ ތިމަންނައަށް ވަސިއްޔަތްކޮށްފައެވެ. އޭނާއާ ވައްތަރުކަން ނިކަން ބައްލަވާށެވެ. ދެން ޢަބްދު ބިން ޒަމްޢާ ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. މި ކުއްޖާއަކީ ތިމަންނަގެ (އެއްބަފާ) އަޚެކެވެ. މި ކުއްޖާ އުފަންވީ ތިމަންނަގެ ބައްޕަގެ ތަންމަތީގައި ބައްޕަގެ އަޅުއަންހެނަކަށެވެ. (އެބަހީ: ސައްހަ ގޮތުގައި ބައްޕަ ރޭކުރެއްވި އަންހެނަކަށް ލިބުނު ދަރިއެކެވެ.) ދެން ރަސޫލު ﷲ ﷺ އެ ކުއްޖާގެ ވައްތަރުކަމަށް ނަޒަރު ހިނގަވާލެއްވިއެވެ. އަދި ފާޅުކަން ބޮޑު ގޮތެއްގައި އެ ކުއްޖާ ޢުތުބާއާ ވައްތަރުކަމަށް ފެނިވަޑައިގަތެވެ. (ނަމަވެސް) ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ ޢަބްދު ބިން ޒަމްޢާއެވެ. އެ ކުއްޖާ ވަނީ ތިބާއަށެވެ. (އެބަހީ: ތިބާގެ އަޚެއް ކަމުގައެވެ.) (އަދި ޙަދީޘް ކުރެއްވިއެވެ: އުފަންވާ) ކުއްޖާ ނިސްބަތްވާނީ ތަންމައްޗަށެވެ. (އެބަހީ: ތަންމަތީގެ ވެރިޔާއަށެވެ. އެ ގޮތުން ވިހާ ދަރި ނަސްލުގެ ގޮތުން ގުޅުވޭނީ މަންމައާ ސައްހަ ގޮތުގައި އެއްދާން ކުރާ ފިރިހެން މީހާއަށެވެ.) އަދި ޒިނޭކުރާ މީހާއަށް ކުއްޖާ ނިސްބަތްކުރުމެއް ނުވެއެވެ. (އަދި ކައިވެނިގެ ރަހަދެކެފައިވާނަމަ އޮތީ ރަޖަމުކުރުމެވެ.) (ދެން ނަބިއްޔާގެ އަނބިކަނބަލުންނަށް ޙަދީޘް ކުރެއްވިއެވެ:) އަދި އޭ ސަޢުދާ (ބިންތި ޒަމްޢާއެވެ.) އެ ކުއްޖާގެ ފަރާތުން ހިޖާބުވެލައްވާށެވެ.“ ފަހެ އޭގެ ފަހުން އެ ކުއްޖާ ސައުދާއެއް ނުދެކެވެ. (ނޯޓު: އެފަދައިން ކަންކުރެއްވީ، ސަޢުދާގެފާނަކީ ޢަބްދު ބިން ޒަމްޢާގެ އުޚްތަކަށް ވާތީއެވެ. ޢަބްދު ބިން ޒަމްޢާއަށް ކުއްޖާދޭން ޝަރުއީ ގޮތުން ހުކުމް ކުރެއްވިއެއް ކަމަކު، އެ ކުއްޖާ ޢުތުބާއާ ވައްތަރުކަމުން ރައްކާތެރިކަމުގެ ގޮތުން ނިވާވާން އެންގެވިއެވެ. އަދި އެ ޒީނޭގެ ހާދިސާއަކީ ޖާހިލިއްޔަ ޒަމާނުގައި ހިނގައިދިޔަ ހާދިސާއެކެވެ.)","[البخاري 2218، ومسلم 1457 واللفظ له]"],["#333","كتاب اللعان","ލިޢާނުގެ ފޮތް","","","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا أَنَّهَا قَالَتْ: (إنَّ رَسُولَ اللَّهِ ﷺ دَخَلَ عَلَيَّ مَسْرُوراً، تَبْرُقُ أَسَارِيرُ وَجْهِهِ. فَقَالَ: أَلَمْ تَرَيْ أَنَّ مُجَزِّزاً نَظَرَ آنِفاً إلَى زَيْدِ بْنِ حَارِثَةَ وَأُسَامَةَ بْنِ زَيْدٍ، فَقَالَ: إنَّ بَعْضَ هَذِهِ الأَقْدَامِ لَمِنْ بَعْضٍ).\n\nوَفِي لَفْظٍ: (كَانَ مُجَزِّزٌ قَائِفاً).","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ވަރަށް އުފާފުޅުވެ ވަޑައިގެން ރަސޫލު ﷲ ﷺ ތިމަން ކަމަނާގެ ގާތަށް ވަދެވަޑައިގަތެވެ. ނަބިއްޔާގެ މޫނުފުޅުން އުފާވެރިކަން ދިއްލިގެންވެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ޒައިދު ބިން ޙާރިޘާއި، (އެކަލޭގެފާނުގެ ދަރިފުޅު) އުސާމާ ބިން ޒައިދާ ދިމާލަށް މުޖައްޒިޒު (އަލްމުދްލިޖީ ކިޔުނު ގާއިފު) ދެންމެ ބަލާލައިފާ މިފަދައިން ވިދާޅުވި ތަން ކަމަނާ ދެކެވަޑައި ނުގަންނަވަމުތޯއެވެ: ހަމަކަށަވަރުން މިހިރަ ފައިތިލަތަކަކީ އެކެއްގެ ފަރާތުން އަނެކަކަށް އައިސްފައިވާ ފައިތަކެކެވެ.“\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: މުޖައްޒިޒު (އަލްމުދްލިޖީ) އަކީ ގާއިފެކެވެ. (އެބަހީ: ވައްތަރުކަމާއި އަސަރުތަކަށް ބަލާފައި ނަސްބު ގުޅުވަން ދަންނަ މީހެކެވެ.) (ނޯޓު: މި ހާދިސާގައި ވީގޮތަކީ: އުސާމާގެ ނަސަބާ މެދު މީހުން ޝައްކުކޮށް އުޅުނެވެ. އެއީ އުސާމާ ވަރަށް ކަޅުވެފައި ބައްޕާފުޅު ވަރަށް ދޮންވީމައެވެ. ދެން އެއް ދުވަހަކު އުސާމާއާއި އެކަލޭގެފާނުގެ ބައްޕާފުޅެ ދެބޭކަލުން ގައިކޮޅުގައި ރަޖަލެއް އަޅުއްވައިގެން އޮށޯވެ އޮންނެވިއެވެ. އަދި އެ ދެބޭކަލުންގެ ބޯތައް ނިވާވެފައި، ފައިތިލަތައް ފެންނާން ހުއްޓެވެ. އެ ހިނދު ގާއިފެއް އައިސް އެ ދެބޭކަލުންގެ ފައިތިލައަށް ބަލާފައި ނަސަބު ސާބިތުކޮށްދިނެވެ. އޭގެ ސަބަބުން މީހުން ޝައްކުކޮށް އުޅުޅު އުޅުން ބާތިލުވެ، ނަބިއްޔާ އުފާފުޅުވެވަޑައިގަތެވެ.)","[البخاري 6770، ومسلم 1459 واللفظ له\n\nمسلم 1459\n]"],["#334","كتاب اللعان","ލިޢާނުގެ ފޮތް","","","عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (ذُكِرَ الْعَزْلُ لِرَسُولِ اللَّهِ ﷺ. فَقَالَ: وَلِمَ يَفْعَلُ ذَلِكَ أَحَدُكُمْ؟ - وَلَمْ يَقُلْ: فَلا يَفْعَلْ ذَلِكَ أَحَدُكُمْ، فَإِنَّهُ لَيْسَتْ نَفْسٌ مَخْلُوقَةٌ إلاَّ اللَّهُ خَالِقُهَا).","އަބޫ ސަޢީދު އަލްޚުދްރީ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺގެ އަރިހުގައި ޢަޒުލުގެ ވާހަކަ ނަންގަނެވުނެވެ. (ޢަޒުލަކީ، ޖިމާއުގެ ތެރޭގައި މަނި ބޭރު ވުމުގެ ކުރިން އަންހެނުންގެ ފަރުޖުން ފިރިހެން މީހާގެ ގުނަވަން ނެރުމެވެ.) ފަހެ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ ތެރެއިން މީހަކު އެފަދައިން ކަންކުރައްވަނީ ކީއްވެގެންތޯއެވެ؟“ -އަދި ތިޔަބައިމީހުން އެހެން ނަހަދާށެކޭ ޙަދީޘެއް ނުކުރައްވައެވެ.- (ނަބިއްޔާ ދެން ޙަދީޘް ކުރެއްވިއެވެ:) ”އެހެނީ ހަމަކަށަވަރުން ﷲ އެ ފުރާނައެއް އުފައްދަވާފައި ނުވާ އެއްވެސް އުފެއްދެވުނު ފުރާނައެއް ނުވެއެވެ.“","[البخاري 2229، ومسلم 1438 واللفظ له]"],["#335","كتاب اللعان","ލިޢާނުގެ ފޮތް","","","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (كُنَّا نَعْزِلُ وَالْقُرْآنُ يَنْزِلُ، لَوْ كَانَ شَيْئاً يُنْهَى عَنْهُ لَنَهَانَا عَنْهُ الْقُرْآنُ).","ޖާބިރު ބިން ޢަބްދު ﷲ رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ޤުރްއާން ބާވައިލެއްވެމުން ދިޔަ ދުވަސްވަރުގައި ތިމަންމެން ޢަޒުލުކޮށް އުޅޫނީމެވެ. އެއީ ނަހީވެގެންވާ ކަމަކަށް ވިނަމަ، ޤުރްއާނުން އެކަންތައް ނަހީ ކުރެއްވުނީސްކަން ކަށަވަރެވެ.","[البخاري 5208، ومسلم 1440]"],["#336","كتاب اللعان","ލިޢާނުގެ ފޮތް","","","عَنْ أَبِي ذَرٍّ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّهُ سَمِعَ رَسُولَ اللَّهِ ﷺ يَقُولُ (لَيْسَ مِنْ رَجُلٍ ادَّعَى لِغَيْرِ أَبِيهِ - وَهُوَ يَعْلَمُهُ - إلاَّ كَفَرَ. وَمَنْ ادَّعَى مَا لَيْسَ لَهُ: فَلَيْسَ مِنَّا، وَلْيَتَبَوَّأْ مَقْعَدَهُ مِنْ النَّارِ وَمَنْ دَعَا رَجُلاً بِالْكُفْرِ، أَوْ قَالَ: عَدُوَّ اللَّهِ، وَلَيْسَ كَذَلِكَ، إلاَّ حَارَ عَلَيْهِ).","އަބޫ ޛައްރު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ”މީހަކަށް އެނގި ހުއްޓައި އޭނާގެ (އަސްލު) ބައްޕަ ނޫން މީހަކަށް އޭނާ ނިސްބަތްވާކަމަށް ދައުވާކޮށްފި ނަމަ، ކާފަރުވާނެއެވެ. އަދި މީހަކަށް ލިބިފައި ނުވާ އެއްޗަކަށް ދައުވާކޮށްފި ނަމަ، އެ މީހަކީ ތިމަންމެންގެ ތެރެއިންވާ މީހެއް ނޫނެކެވެ. އަދި ނަރަކައިގައި އޭނާ އިށީންނާނެ ތަނެއް ނަގާށެވެ. އަދި މީހަކާ ދިމާލަށް ކާފަރު ކަމަށް ގޮވާލައިފި، ނުވަތަ ﷲގެ އަދުއްވެއް ކަމަށް ބުނެފި މީހާ، ފަހެ އެ (ދައުވާ ލިބުނު) މީހަކީ (ހަގީގަތުގައި ވެސް) އެފަދަ މީހަކަށް ނުވާނަމަ، އެ ބުނި ބުނުން އެ ބުނި މީހަކަށް އެނބުރި އަންނާނެއެވެ.“","[البخاري 5208، ومسلم 61]"],["#337","كتاب الرضاع","ގާތުން ކިރު ދިނުމުގެ ފޮތް","","","عَنْ ابْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (قَالَ رَسُولُ اللَّهِ ﷺ فِي بِنْتِ حَمْزَةَ: لاتَحِلُّ لِي، يَحْرُمُ مِنْ الرَّضَاعِ مَا يَحْرُمُ مِنْ النَّسَبِ، وَهِيَ ابْنَةُ أَخِي مِنْ الرَّضَاعَةِ).","އިބްނު ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺގެ (ބޮޑުބޭބޭފުޅު) ޙަމްޒާގެ އަންހެން ދަރިކަނބަލުންނާ އެ ނަބިއްޔާ ކައިވެނި ބެއްލެވުމާއި ގުޅޭ ގޮތުން ޙަދީޘްކ ކުރެއްވިއެވެ: ތިމަން ނަބިއްޔާއަކަށް އެ ކަމަނާ ހަލާލުވެގެން ނުވެއެވެ. ”ވިހެއުމުގެ ސަބަބުން ހަރާމްވާ ކަންތައް، ގާތުން ކިރު ދިނުމުގެ ސަބަބުން ވެސް ހަރާމްވެގެން ދެއެވެ. އަދި އެ ކަމަނާއީ ތިމަން ނަބިއްޔާގެ ކިރުގެގޮތުން އަޚެއްގެ އަންހެން ދަރިއެކެވެ.“ (ނޯޓު: ޘައުބާގެ ގާތުން، ނަބިއްޔާއާއި ބޮޑުބޭބޭފުޅު ޙަމްޒާއާ ދެބޭކަލުން ގާތުން ކިރު ހިއްޕެވިއެވެ.)","[البخاري 2645 واللفظ له، ومسلم 1447]"],["#338","كتاب الرضاع","ގާތުން ކިރު ދިނުމުގެ ފޮތް","","","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: قَالَ رَسُولُ اللَّهِ ﷺ: (إنَّ الرَّضَاعَةَ تُحَرِّمُ مَا يَحْرُمُ مِنْ الْوِلادَةِ).","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ވިހެއުމުގެ ސަބަބުން ހަރާމްވާ ކަންތައް، ގާތުން ކިރު ދިނުމުގެ ސަބަބުން ވެސް ހަރާމްވެގެން ދެއެވެ.“ (އެބަހީ: ނަސަބުގެ ގާތްކަމުގެ ސަބަބުން މީހުންނާ ކާވެނި ކުރުން ހަރާމްވާ ފަދައިން، މައިމީހާ ނޫން އެހެން މީހެއްގެ ފަރާތުން ކިރު ދިނުމުން، އެ ކިރު ދިން މީހާގެ ނަސަބުގެ ބައެއް މީހުންނާ ކާވެނި ކުރުން ވެސް ހަރާމްވެގެން ދެއެވެ.)","[البخاري 5099، ومسلم 1444]"],["#339","كتاب الرضاع","ގާތުން ކިރު ދިނުމުގެ ފޮތް","","",'وَعَنْهَا قَالَتْ: (إنَّ أَفْلَحَ - أَخَا أَبِي الْقُعَيْسِ - اسْتَأْذَنَ عَلَيَّ بَعْدَمَا أُنْزِلَ الْحِجَابُ؟ فَقُلْت: وَاَللَّهِ لا آذَنُ لَهُ، حَتَّى أَسْتَأْذِنَ النَّبِيَّ ﷺ فَإِنَّ أَخَا أَبِي الْقُعَيْسِ: لَيْسَ هُوَ أَرْضَعَنِي، وَلَكِنْ أَرْضَعَتْنِي امْرَأَةُ أَبِي الْقُعَيْسِ، فَدَخَلَ عَلَيَّ رَسُولُ اللَّهِ ﷺ فَقُلْت: يَا رَسُولَ اللَّهِ: إنَّ الرَّجُلَ لَيْسَ هُوَ أَرْضَعَنِي، وَلَكِنْ أَرْضَعَتْنِي امْرَأَتُهُ. فَقَالَ: ائْذَنِي لَهُ، فَإِنَّهُ عَمُّك، تَرِبَتْ يَمِينُك). قَالَ عُرْوَةُ " فَبِذَلِكَ كَانَتْ عَائِشَةُ تَقُولُ: «حَرِّمُوا مِنْ الرَّضَاعَةِ مَا يَحْرُمُ مِنْ النَّسَبِ»\n\nوَفِي لَفْظٍ (اسْتَأْذَنَ عَلَيَّ أَفْلَحُ، فَلَمْ آذَنْ لَهُ. فَقَالَ: أَتَحْتَجِبِينَ مِنِّي، وَأَنَا عَمُّك؟ فَقُلْت: كَيْفَ ذَلِكَ؟ قَالَ: أَرْضَعَتْك امْرَأَةُ أَخِي بِلَبَنِ أَخِي، قَالَتْ: فَسَأَلْت رَسُولَ اللَّهِ ﷺ فَقَالَ: صَدَقَ أَفْلَحُ، ائْذَنِي لَهُ، تَرِبَتْ يَمِينُك) أَيْ افْتَقَرَتْ، وَالْعَرَبُ تَدْعُو عَلَى الرَّجُلِ، وَلا تُرِيدُ وُقُوعَ الأَمْرِ بِهِ.',"އަދިވެސް (ޢާއިޝާ) ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ހިޖާބުގެ ހުކުމް ބާވައިލެއްވުމަށް ފަހު، އަބުލް ގުޢައިސުގެ އަޚް އަފްލަޙު، ތިމަން ކަމަނާގެ ގެކޮޅަށް ވަނުމަށް ހުއްދައަށް އެދުނެވެ. (ނޯޓު: އަފްލަޙަކީ ކިރުގެ ގޮތުން ޢާއިޝާގެ ބޮޑުބޭބެއެވެ.) ފަހެ ތިމަން ކަމަނާ ދެންނެވީމެވެ: ﷲ ގަންދީ ބުނަމެވެ. ނަބިއްޔާގެ އަރިހުން ތިމަން ކަމަނާ (މި ކަމަށް) އިޒުނަ ހޯދައިފުމަށް ދާންދެން އޭނާއަށް ހުއްދައެއް ނުދޭނަމެވެ. އެހެނީ ހަމަކަށަވަރުން ތިމަން ކަމަނާއަށް ކިރުދެއްވީ އަބުލް ގުއައިސްގެ އަޚާ (އަފްލަޙެއް) ނޫނެވެ. އެހެނެއްކަމަކު ތިމަން ކަމަނާއަށް ކިރު ދެއްވީ އަބުލް ގުއިސުގެ އަންހެނުންނެވެ. ދެން ރަސޫލު ﷲ ﷺ ތިމަން ކަމަނާގެ ގާތަށް ވަދެވަޑައިގަތުމުން ތިމަން ކަމަނާ ދެންނެވީމެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން ތިމަން ކަމަނާއަށް ކިރު ދެއްވީ ފިރިހެން މީހާއެއް ނޫނެވެ. އެހެނެއްކަމަކު ތިމަން ކަމަނާއަށް ކިރު ދެއްވީ އޭނާގެ އަންހެނުންނެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭނާއަށް ހުއްދަ ދެއްވާށެވެ. އެހެނީ ހަމަކަށަވަރުން އޭނާއަކީ (ކިރުގެ ގޮތުން) ކަމަނާގެ ބޮޑުބޭބެއެވެ. ކަމަނާގެ ކަނާތް ވެއްޔާ މޮޑެވިގެން ދާހުއްޓެވެ.“ (ނޯޓު: މިއީ އަރަބި ބަހުގައި ދޫމަތިން ބުނެ އުޅޭ ބަހެކެވެ. ބަދު ދުއާއެއް ނޫނެވެ.) (ޙަދީޘްގެ ރާވީއެއް ކަމަށްވާ) ޢުރްވާ ބިން އައްޒުބައިރު ވިދާޅުވިއެވެ: ފަހެ އޭގެ ސަބަބުން ޢާއިޝާ ވިދާޅުވެ އުޅުއްވިއެވެ: ވިހެއުމުގެ ސަބަބުން ހަރާމްވާ ކަންތައް، ގާތުން ކިރު ދިނުމުގެ ސަބަބުން ވެސް ހަރާމްވާ ކަމަށް ތިޔަބައިމީހުން ބަލާށެވެ.“ (އެބަހީ: ނަސަބުގެ ގާތްކަމުގެ ސަބަބުން މީހުންނާ ކާވެނިކުރުން ހަރާމްވާ ފަދައިން، މައިމީހާ ނޫން އެހެން މީހެއްގެ ފަރާތުން ކިރު ދިނުމުން، އެ ކިރު ދިން މީހާގެ ނަސަބުގެ ބައެއް މީހުންނާ ކާވެނިކުރުން ވެސް ހަރާމްވާ ކަމަށް ބަލާށެވެ.)\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ތިމަން ކަމަނާގެ ގެކޮޅަށް ވަނުމަށް އަފްލަޙު ހުއްދައެއް އެދުނެވެ. ފަހެ ތިމަން ކަމަނާ އޭނާއަށް ހުއްދައެއް ނުދެއްވަމެވެ. ދެން އޭނާ ދެންނެވިއެވެ: ތިމަންނައީ ކަމަނާގެ ބޮޑުބޭބެ ކަމަށްވާއިރު، ތިމަންނަގެ ކިބައިން ކަމަނާ ހިޖާބުވެލައްވަނީތޯއެވެ؟ ތިމަން ކަމަނާ ދެންނެވީމެވެ: (ބޮޑު ބޭބެއަށް) ވަނީ ކިހިނަކުންތޯއެވެ؟ އޭނާ ދެންނެވިއެވެ: ތިމަންނަގެ އަޚާގެ އަންހެނުން ކަމަނާއަށް ކިރުދެއްވިއެވެ. އެއީ ތިމަންނަގެ އަޚާއަށް (ނިސްބަތްވާ) ކިރެވެ. ދެން ރަސޫލު ﷲ ﷺގެ އަރިހު (އެކަމާ ބެހޭ ގޮތުން) ސުވާލު ކުރެއްވީމެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އަފްލަޙު ތެދު ހަދައިދިއެވެ. އޭނާއަށް ހުއްދަ ދެއްވާށެވެ. ކަމަނާގެ ބޮޑުބޭބެއެވެ. ކަމަނާގެ ކަނާތް ވެއްޔާ މޮޑެވިގެން ދާހުއްޓެވެ.“","[البخاري 4796، ومسلم 1445\n\nالبخاري 2644 واللفظ له، ومسلم 1445\n]"],["#340","كتاب الرضاع","ގާތުން ކިރު ދިނުމުގެ ފޮތް","","","وَعَنْهَا رَضِيَ اللَّهُ عَنْهُا قَالَتْ (دَخَلَ عَلَيَّ رَسُولُ اللَّهِ ﷺ وَعِنْدِي رَجُلٌ، فَقَالَ: يَا عَائِشَةُ، مَنْ هَذَا؟ قُلْت: أَخِي مِنْ الرَّضَاعَةِ. فَقَالَ: يَا عَائِشَةُ: اُنْظُرْنَ مَنْ إخْوَانُكُنَّ؟ فَإِنَّمَا الرَّضَاعَةُ مِنْ الْمَجَاعَةِ).","އަދިވެސް ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ފިރިހެނަކު ތިމަން ކަމަނާގެ ގާތުގައި ހުއްޓައި، ރަސޫލު ﷲ ﷺ ތިމަން ކަމަނާގެ ގާތައް ވަދެވަދައިގެންނެވިއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ ޢާއިޝާއެވެ. މިއީ ކާކުތޯއެވެ؟“ ތިމަން ކަމަނާ ދެންނެވީމެވެ: ކިރުގެ ގޮތުން ތިމަން ކަމަނާގެ އަޚެކެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ ޢާއިޝާއެވެ. ތިޔަ ކަނބަލުންގެ އަޚުންނަކީ ކޮންބައެއްތޯ ރަނގަޅަށް ބަލާށެވެ. އެހެނީ ކިރުގެ ގޮތުން (މަޙްރަމްކަން ސާބިތުވަނީ) ހަމައެކަނި ބަނޑުހައިވެގެން ކުއްޖާ ކިރު ބުއިކަމުގައި ވާނަމައެވެ.“ (ނޯޓު: އަދި މިކަން ސާބިތުވަނީ ކުއްޖާ ވިހާތާ ދެ އަހަރު ތެރޭގައި ކިރު ނޫން އެއްޗެއް ނުބޯ ހާލު، މަންމަ ނޫން އަންހެނެއްގެ ގާތުން ފަސް ފަހަރު ކިރު ބުއިމުންނެވެ.)","[البخاري 2647 واللفظ له، ومسلم 1455]"],["#341","كتاب الرضاع","ގާތުން ކިރު ދިނުމުގެ ފޮތް","","","عَنْ عُقْبَةَ بْنِ الْحَارِثِ - رَضِيَ اللَّهُ عَنْهُ - (أَنَّهُ تَزَوَّجَ أُمَّ يَحْيَى بِنْتَ أَبِي إهَابٍ، فَجَاءَتْ أَمَةٌ سَوْدَاءُ، فَقَالَتْ: قَدْ أَرْضَعْتُكُمَا، فَذَكَرْتُ ذَلِكَ لِلنَّبِيِّ ﷺ قَالَ: فَأَعْرَضَ عَنِّي. قَالَ: فَتَنَحَّيْت، فَذَكَرْت ذَلِكَ لَهُ. قَالَ: كَيْفَ؟ وَقَدْ زَعَمَتْ أَنْ قَدْ أَرْضَعَتْكُمَا).","ޢުގްބާ ބިން އަލްޙާރިޘް رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން އަބޫ އިހާބުގެ ދަރިކަނބަލުން އުންމު ޔަޙްޔާއާ ތިމަން ކައިވެނި ބެއްލެވީމެވެ. ދެން ކަޅު އަންހެން އަޅަކު އައިސް ދެންނެވިއެވެ: ހަމަކަށަވަރުން ތިޔަ ދެކަނބަލުންނަށް ތިމަން ކިރު ދިނީމެވެ. ފަހެ ނަބިއްޔާ ﷺގެ އަރިހުގައި އެ ވާހަކަ ތިމަން ދެންނެވީމެވެ. ނަބިއްޔާ މޫނުފުޅު އަނބުރައިލެއްވިއެވެ. ދެން ނަބިއްޔާއާ ކުރިމަތި ލައްވައިގެން އަނެއްކާ އެ ވާހަކަ ތިމަން ދެންނެވީމެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ ކަމަނާ ތިޔަ ދެކަނބަލުންނަށް ކިރު ދިންކަން އަނގަވާފައިވުމާއެކު (ތިބާ ތިޔަ ކައިވެނީގައި ދެމިހުންނާނީ) ކިހިނަކުންތޯއެވެ؟“","[البخاري 2659]"],["#342","كتاب الرضاع","ގާތުން ކިރު ދިނުމުގެ ފޮތް","","","عَنْ الْبَرَاءِ بْنِ عَازِبٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (خَرَجَ رَسُولُ اللَّهِ ﷺ يَعْنِي مِنْ مَكَّةَ - فَتَبِعَتْهُمْ ابْنَةُ حَمْزَةَ، تُنَادِي: يَا عَمُّ، فَتَنَاوَلَهَا عَلِيٌّ فَأَخَذَ بِيَدِهَا، وَقَالَ لِفَاطِمَةَ: دُونَكِ ابْنَةَ عَمِّك، فَاحْتَمَلْتُهَا. فَاخْتَصَمَ فِيهَا عَلِيٌّ وَجَعْفَرٌ وَزَيْدٌ فَقَالَ عَلِيٌّ: أَنَا أَحَقُّ بِهَا، وَهِيَ ابْنَةُ عَمِّي وَقَالَ جَعْفَرٌ: ابْنَةُ عَمِّي، وَخَالَتُهَا تَحْتِي. وَقَالَ زَيْدٌ: ابْنَةُ أَخِي. فَقَضَى بِهَا رَسُولُ اللَّهِ ﷺ لِخَالَتِهَا، وَقَالَ: الْخَالَةُ بِمَنْزِلَةِ الأُمِّ. وَقَالَ لِعَلِيٍّ: أَنْتَ مِنِّي، وَأَنَا مِنْك. وَقَالَ لِجَعْفَرٍ: أَشْبَهَتْ خَلْقِي وَخُلُقِي وَقَالَ لِزَيْدٍ: أَنْتَ أَخُونَا وَمَوْلانَا).","އަލްބަރާއު ބިން ޢާޒިބު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ (މުސްލިމުންގެ ބަޔަކާއެކު އުމްރާ ކުރެއްވުމަށް ފަހު) މައްކާއިން ނުކުމެވަޑައިގަންނަވަނިކޮށް، ޙަމްޒާގެ ދަރިކަނބަލުން އެ ބޭކަލުންގެ ފަހަތުން، އޭ ބޮޑުބޭބޭފުޅޭ، އޭ ބޮޑުބޭބޭފުޅޭ، މިފަދައިން ދަންނަވަމުން އަތުވިއެވެ. ދެން ޢަލީ އެ ކަމަނާ ބައްލަވައިގެން، އެ ކަމަނާގެ އަތްޕުޅުގައި ހިއްޕަވައިގެން ފާތިމާގެފާނަށް ދެންނެވިއެވެ: ކަމަނާގެ ބޮޑުބޭބޭގެ ދަރިކަލުން ނަންގަވާށެވެ. ދެން ފާތިމާގެފާނު އެ ކުއްޖާ އުރުއްވައިފިއެވެ. އެއަށްފަހު އެ ކުއްޖާގެ (ބަލަދުވެރިކަން ހޯދުމާ ގުޅޭ ގޮތުން) ޢަލީއާއި، ޒައިދާއި، ޖަޢުފަރު ދެބަސްވެލެއްވިއެވެ. އެ ގޮތުން ޢަލީ ދެންނެވިއެވެ: އެ ކަމަނާ އެންމެ ހައްގުވަނީ ތިމަންނައަށެވެ. އެއީ ތިމަންނަގެ ބޮޑުބޭބެގެ ދަރިކަލުންނެވެ. ދެން ޖަޢުފަރު ވިދާޅުވިއެވެ. އެއީ ތިމަންނަގެ ބޮޑުބޭބެގެ ދަރިކަލުންނެވެ. އަދި އެ ކަމަނާގެ މަންމަ ފަރާތު ބޮޑުދައިތައަކީ ތިމަންނަގެ އަނބިކަނބަލުންނެވެ. ދެން ޒައިދު ދެންނެވިއެވެ: އެ ކަމަނާއަކީ ތިމަންނަގެ އަޚާގެ ދަރިކަނބަލުންނެވެ. ދެން ރަސޫލު ﷲ ﷺ އެ ކަމަނާގެ ބަލަދުވެރިކަން ދެއްވީ އެ ކަމަނާގެ (މަންމަ ފަރާތު) ބޮޑުދައިތައަށެވެ. އަދި ޙަދީޘް ކުރެއްވިއެވެ: ”މަންމަ ފަރާތު ބޮޑު ދައިތަ ވަނީ މަންމަގެ މަގާމުގައެވެ.“ އަދި ނަބިއްޔާ ޢަލީއަށް ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ ވަނީ ތިމަން ނަބިއްޔާގެ ކިބައިންނެވެ. އަދި ތިމަން ނަބިއްޔާ ވަނީ ތިބާގެ ކިބައިންނެވެ.“ ދެން ނަބިއްޔާ ޖަޢުފަރަށް ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާގެ ޚަލްގުފުޅާއި ޚުލްގުފުޅުގައި ތިބާ ވައްތަރުވެގެންވެއެވެ. ދެން ނަބިއްޔާ ޒައިދަށް ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާއަކީ ތިމަންމެންގެ އަޚެވެ. އަދި ތިމަންމެން މިނިވަން ކުރެއްވި އަޅެވެ.“ (އެފަދައިން ޙަދީޘް ކުރެއްވީ ހުރިހާ ބޭކަލުންވެސް ހިތްހެޔޮ ކުރެއްވުމުގެ ގޮތުންނެވެ.)","[البخاري 2699، 4251]"],["#343","كتاب القصاص","ޤިޞާޞްގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ مَسْعُودٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ (لا يَحِلُّ دَمُ امْرِئٍ مُسْلِمٍ يَشْهَدُ أَنْ لا إلَهَ إِلاَّ اللَّهُ وَأَنِّي رَسُولُ اللَّه إلاَّ بِإِحْدَى ثَلاثٍ: الثَّيِّبُ الزَّانِي، وَالنَّفْسُ بِالنَّفْسِ، وَالتَّارِكُ لِدِينِهِ الْمُفَارِقُ لِلْجَمَاعَةِ).","ޢަބްދު ﷲ ބިން މަސްޢޫދު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲ ފިޔަވައި (އަޅުކަންކުރުން ހައްގުވާ) އިލާހަކު ނުވާ ކަމަށާއި، ތިމަން ނަބިއްޔާއީ ﷲގެ ރަސޫލާ ކަމަށް ހެކިވާ މުސްލިމެއްގެ ލޭ ތިން ކަމަކުން މެނުވީ ހުއްދަ ވެގެނެއް ނުވެއެވެ. އެއީ ކާވެންޏަކަށް ފަހު ޒިނޭ ކުރުމާއި، (ހައްގަކާ ނުލައި ގަސްދުގައި މީހަކު މެރުމުން) ނަފްސަކަށް ނަފްސެއްގެ ބަދަލު ހިފުމާ، އަދި އެ މީހެއްގެ ދީން ދޫކޮށް (މުސްލިމުންގެ) ޖަމާއަތާ ވަކިވެގަތުމެވެ.“","[البخاري 6878، ومسلم 1676]"],["#344","كتاب القصاص","ޤިޞާޞްގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ مَسْعُودٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: (أَوَّلُ مَا يُقْضَى بَيْنَ النَّاسِ يَوْمَ الْقِيَامَةِ فِي الدِّمَاءِ).","ޢަބްދު ﷲ ބިން މަސްޢޫދު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ގިޔާމަތް ދުވަހު މީސްތަކުންގެ މެދުގައި އެންމެ ފުރަތަމަ ނިޔާ ކުރެވޭނީ ލޭ އޮހޮރުވުމުގެ މައްސަލަތަކުގައެވެ.“","[البخاري 6533، ومسلم 1678 واللفظ له]"],["#345","كتاب القصاص","ޤިޞާޞްގެ ފޮތް","","","عَنْ سَهْلِ بْنِ أَبِي حَثْمَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (انْطَلَقَ عَبْدُ اللَّهِ بْنُ سَهْلٍ وَمُحَيِّصَةُ بْنُ مَسْعُودٍ إلَى خَيْبَرَ، وَهِيَ يَوْمَئِذٍ صُلْحٌ، فَتَفَرَّقَا، فَأَتَى مُحَيِّصَةُ إلَى عَبْدِ اللَّهِ بْنِ سَهْلٍ - وَهُوَ يَتَشَحَّطُ فِي دَمِهِ قَتِيلاً - فَدَفْنه، ثُمَّ قَدِمَ الْمَدِينَةَ، فَانْطَلَقَ عَبْدُ الرَّحْمَنِ بْنُ سَهْلٍ وَمُحَيِّصَةُ وَحُوَيِّصَةُ ابْنَا مَسْعُودٍ إلَى النَّبِيِّ ﷺ فَذَهَبَ عَبْدُ الرَّحْمَنِ يَتَكَلَّمُ، فَقَالَ النَّبِيُّ ﷺ: كَبِّرْ، كَبِّرْ - وَهُوَ أَحْدَثُ الْقَوْمِ - فَسَكَتَ، فَتَكَلَّمَا، فَقَالَ: أَتَحْلِفُونَ وَتَسْتَحِقُّونَ قَاتِلَكُمْ، أَوْ صَاحِبَكُمْ؟ قَالُوا: وَكَيْفَ نَحْلِفُ، وَلَمْ نَشْهَدْ، وَلَمْ نَرَ؟ قَالَ: فَتُبْرِئُكُمْ يَهُودُ بِخَمْسِينَ يَمِيناً قَالُوا: كَيْفَ بِأَيْمَانِ قَوْمِ كُفَّارٍ؟ فَعَقَلَهُ النَّبِيُّ ﷺ مِنْ عِنْدِهِ).\n\nوَفِي حَدِيثِ حَمَّادِ بْنِ زَيْدٍ: (فَقَالَ رَسُولُ اللَّهِ ﷺ: يُقْسِمُ خَمْسُونَ مِنْكُمْ عَلَى رَجُلٍ مِنْهُمْ، فَيُدْفَعُ بِرُمَّتِهِ، قَالُوا: أَمْرٌ لَمْ نَشْهَدْهُ كَيْفَ نَحْلِفُ؟ قَالُوا: فَتُبْرِئُكُمْ يَهُودُ بِأَيْمَانِ خَمْسِينَ مِنْهُمْ؟ قَالُوا: يَا رَسُولَ اللَّهِ، قَوْمٌ كُفَّارٌ).\n\nوَفِي حَدِيثِ سَعِيدِ بْنِ عُبَيْدٍ: (فَكَرِهَ رَسُولُ اللَّهِ ﷺ أَنْ يُبْطِلَ دَمَهُ، فَوَدَاهُ بِمِائَةٍ مِنْ إبِلِ الصَّدَقَةِ).","ސަހުލު ބިން އަބީ ޙަޘްމާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ޢަބްދު ﷲ ބިން ސަހްލާއި މުޙައްޔިޞާ ބިން މަސްޢޫދު ޚައިބަރަށް ދިޔައެވެ. –އަދި އެ ދުވަސްވަރު މުސްލިމުންނާއި ޔަހޫދީންނާ ދެމެދުގައި ސުލްހައިގެ އެއްބަސްވުމެއް އޮތެވެ- ދެން އެ ދެމީހުން ވަކިވިއެވެ. އޭގެ ފަހުން ޢަބްދު ﷲ ބިން ސަހްލުގެ ގާތަށް މުޙައްޔިޞާ އާދެވުނު އިރު، ޢަބްދު ﷲ އޮތީ ގަތުލުކުރެވިފައިވާ ހާލުގައި ލެއިން ތަތްތެޅި މަރުގެ ސަކަރާތުގައެވެ. އެއަށްފަހު ޢަބްދު ﷲ ފަސްދާނުލާފައި މުޙައްޔިޞާ މަދީނާއަށް ދިޔައެވެ. އަދި ޢަބްދުއްރަހުމާން ބިން ސަހްލާއި، މަސްޢޫދުގެ ދެ ދަރިން ކަމުގައިވާ މުޙައްޔިޞާ އަދި ޙުވައްޔިޞާ، ނަބިއްޔާ ﷺގެ އަރިހަށް އަތުވިއެވެ. ދެން ޢަބްދުއްރަހުމާން (މި މައްސަލައިގައި) ވާހަކަ ދައްކަން އުޅުނެވެ. ނަމަވެސް ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ. ”ބޮޑެތި މީހުން އިސްވެ ވާހަކަދައްކާ ހުއްޓެވެ. ބޮޑެތި މީހުން އިސްވެ ވާހަކަދައްކާ ހުއްޓެވެ.“ –އަދި ޢަބްދުއްރަޙްމާނުވީ އެތާ ހުރި އެންމެ ހަގު މީހާ ކަމުގައެވެ- ފަހެ ޢަބްދުއްރަޙްމާން ހަނުހުރެއްޖެއެވެ. އަދި އަނެއް ދެ މީހުން (އެ މައްސަލައިގައި) ވާހަކަ ދެއްކިއެވެ. ދެން ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”(ގާތިލުގެ މައްޗަށް) ތިޔަބައިމީހުން ހުވާ ކުރަން (ތައްޔާރު) ތޯއެވެ؟ އޭރުން ތިޔަބައިމީހުންގެ ގަތުލު ކުރެވުނު އެކުވެރިޔާގެ (މަރުގެ ދިޔަ) ނެގޭނެއެވެ.“ އެބައިމީހުން ދެންނެވިއެވެ: ތިމަންމެން (އެ ގަތުލަށް) ހެކިނުވެ އަދި ފެނިފައި ނުވާ ހާލު، ހުވާ ކުރާނީ ދެން ކިހިނަކުންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”(ތިޔަބައިމީހުން ހުވާކުރުމަށް ފަހު) ޔަހޫދީންގެ ފަންސާން މީހުން (އެކަން ނުކުރާކަމަށް) ހުވާކޮށްފިނަމަ، ތިޔަބައިމީހުންގެ (ދައުވާގެ ކިބައިން) އެ މީހުންނަށް ބަރީއަ ވެވޭނެއެވެ.“ ދެން އެބައިމީހުން ދެންނެވިއެވެ: ކާފަރުންތަކެއް ކުރާ ހުވާ ތިމަންމެން ގަބޫލުކުރާނީ ކިހިނަކުންތޯއެވެ؟ ދެން ނަބިއްޔާ ﷺ އަމިއްލަފުޅަށް ޢަބްދު ﷲގެ މަރުގެ ދިޔަ ދެއްކެވިއެވެ. (ނޯޓު: އެކަން ކުރެއްވީ މުސްލިމު ވެރިޔާގެ ހައިސިއްޔަތުންނެވެ. އެހެނީ މުސްލިމުންގެ ކަންތައްތައް ބެލެހެއްޓެވީ ނަބިއްޔާއެވެ.)\n\nއަދި ޙައްމާދު ބިން ޒައިދުގެ ޙަދީޘްގައި ރިވާވެގެންވެއެވެ: ދެން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ ތެރެއިން ފަންސާސް މީހުންވެގެން، (ޔަހޫދީންގެ) ތެރެއިން މީހެއް (އޭނާ ގަތުލުކުރިކަމަށް) ހުވާކުރުމުން، އެ މީހެއްގެ (ކަރުގައި އައްސާފައިވާ) ވާފަށާއެކީ ތިޔަބައިމީހުންނާ ހަވާލުކުރެވޭނެއެވެ.“ އެބައިމީހުން ދެންނެވިއެވެ: ތިމަންމެން އެ ހާދިސާއެއް ނުދެކެމެވެ. ވީމާ ހުވާކުރާނީ ކިހިނަކުންތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”(ތިޔަބައިމީހުން ހުވާކުރުމަށް ފަހު) ޔަހޫދީންގެ ފަންސާން މީހުން (އެކަން ނުކުރާކަމަށް) ހުވާކޮށްފިނަމަ، ތިޔަބައިމީހުންގެ (ދައުވާގެ ކިބައިން) އެ މީހުންނަށް ބަރީއަ ވެވޭނެއެވެ.“ ދެން އެބައިމީހުން ދެންނެވިއެވެ: ކާފަރުންތަކެއް (ކުރާ ހުވާ ތިމަންމެން ގަބޫލުކުރާނީ ކިހިނަކުންތޯއެވެ؟) ދެން ނަބިއްޔާ ﷺ އަމިއްލަފުޅަށް ޢަބްދު ﷲގެ މަރުގެ ދިޔަ ދެއްކެވިއެވެ.\n\nއަދި ސަޢީދު ބިން ޢުބައިދުގެގެ ޙަދީޘްގައި ރިވާވެގެންވެއެވެ: ފަހެ ޢަބްދު ﷲގެ މަރުގެ ދިޔަ ގެއްލެނިވެގެން ދިއުމަށް ރަސޫލު ﷲ ﷺ ނުރުހުންތެރިވެ ވަޑައިގަތެވެ. ވީމާ ޒަކާތުގެ ޖަމަލުތަކުގެ ތެރެއިން ސަތޭކަ ޖަމަލު މަރުގެ ދިޔައިގެ ގޮތުގައި ނަބިއްޔާ ދެއްކެވިއެވެ.","[البخاري 3173 واللفظ له، ومسلم 1669\n\nالبخاري 6142، 6143، ومسلم 1669 واللفظ له\n\nالبخاري 6898، ومسلم 1669 واللفظ له]"],["#346","كتاب القصاص","ޤިޞާޞްގެ ފޮތް","","","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ -: (أَنَّ جَارِيَةً وُجِدَ رَأْسُهَا مَرْضُوضاً بَيْنَ حَجَرَيْنِ، فَقِيلَ مَنْ فَعَلَ هَذَا بِك: فُلانٌ، فُلانٌ؟ حَتَّى ذُكِرَ يَهُودِيٌّ، فَأَوْمَأَتْ بِرَأْسِهَا، فَأُخِذَ الْيَهُودِيُّ فَاعْتَرَفَ، فَأَمَرَ النَّبِيُّ ﷺ أَنْ يُرَضَّ رَأْسَهُ بَيْنَ حَجَرَيْنِ).","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ޅަ އަންހެން ކުއްޖަކު، އެ ކަނބުލޭގެ ބޯ ދެ ގާ ދޭތެރޭ ފިސްފެވައި ވަނިކޮށް ފެނުނެވެ. އެ ކަނބުލޭގެއަށް ދެންނެވުނެވެ: ތިބާއަށް މިހެން ހެދީ ކާކުތޯއެވެ؟ މިވެނި މީހެއް އެވެނި މީހެއްތޯއެވެ؟ ދެން އެހެން ކިޔަމުން ގޮސް ޔަހޫދީއެއްގެ ނަންގަނެވުނެވެ. ފަހެ އެ ކަނބުލޭގެ (މަރުވުމުގެ ކުރިން) ބޮލުން އާދޭހޭ އިޝާރާތް ކުރެއްވިއެވެ. ދެން އެ ޔަހޫދީ ކަލޭގެ ހިފައި (ސުވާލު ކުރުމުން) އޭނާ އިއުތިރާފުވެއްޖެއެވެ. އެއަށްފަހު އޭނާގެ ބޯ ދެ ގާ ދޭތެރޭ ފިސްކުރުމަށް ނަބިއްޔާ ﷺ އަމުރު ކުރެއްވިއެވެ.","[البخاري 2413، ومسلم 1672]"],["#347","كتاب القصاص","ޤިޞާޞްގެ ފޮތް","","","وَلِمُسْلِمٍ وَالنَّسَائِيَّ عَنْ أَنَسٍ - رَضِيَ اللَّهُ عَنْهُ - (أَنَّ يَهُودِيّاً قَتَلَ جَارِيَةً عَلَى أَوْضَاحٍ، فَأَقَادَهُ رَسُولُ اللَّهِ ﷺ).","އަދި މުސްލިމާއި ނަސާއިގެ ރިވާޔަތުގައި، އަނަސް رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ޅަ އަންހެން ކުއްޖަކު އަޅައިގެން އިން ރިހި ގަހަނާކޮޅެއް ހޯދުމަށް، ޔަހޫދީއަކު އެ ކަނބުލޭގެ މަރާލިއެވެ. ފަހެ ރަސޫލު ﷲ ﷺ ބަދަލުގައި އެ ޔަހޫދީ މީހާ މަރާލެއްވިއެވެ.",'[رواه النسائي 4740 بهذا اللفظ، ولفظ البخاري 6878 ومسلم 1672 هو "فقتله رسول الله ﷺ بين حجرين".]'],["#348","كتاب القصاص","ޤިޞާޞްގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (لَمَّا فَتَحَ اللَّهُ - تَعَالَى - عَلَى رَسُولِهِ ﷺ مَكَّةَ قَتَلَتْ هُذَيْلٌ رَجُلاً مِنْ بَنِي لَيْثٍ بِقَتِيلٍ كَانَ لَهُمْ فِي الْجَاهِلِيَّةِ، فَقَامَ رَسُولُ اللَّهِ ﷺ فَقَالَ: إنَّ اللَّهَ عَزَّ وَجَلَّ قَدْ حَبَسَ عَنْ مَكَّةَ الْفِيلَ، وَسَلَّطَ عَلَيْهَا رَسُولَهُ وَالْمُؤْمِنِينَ، وَإِنَّهَا لَمْ تَحِلَّ لأَحَدٍ كَانَ قَبْلِي، وَلا تَحِلُّ لأَحَدٍ بَعْدِي، وَإِنَّمَا أُحِلَّتْ لِي سَاعَةً مِنْ نَهَارٍ، وَإِنَّهَا سَاعَتِي هَذِهِ: حَرَامٌ، لا يُعْضَدُ شَجَرُهَا، وَلا يُخْتَلَى خَلاهَا، وَلا يُعْضَدُ شَوْكُهَا، وَلا تُلْتَقَطُ سَاقِطَتُهَا إلاَّ لِمُنْشِدٍ. وَمَنْ قُتِلَ لَهُ قَتِيلٌ: فَهُوَ بِخَيْرِ النَّظَرَيْنِ: إمَّا أَنْ يَقْتُلَ، وَإِمَّا أَنْ يُودِيَ، فَقَامَ رَجُلٌ مِنْ أَهْلِ الْيَمَنِ - يُقَالُ لَهُ: أَبُو شَاهٍ فَقَالَ: يَا رَسُولَ اللَّهِ، اُكْتُبُوا لِي فَقَالَ رَسُولُ اللَّهِ ﷺ: اُكْتُبُوا لأَبِي شَاهٍ، ثُمَّ قَامَ الْعَبَّاسُ فَقَالَ: يَا رَسُولَ اللَّهِ، إلاَّ الاَّذْخِرَ، فَإِنَّا نَجْعَلُهُ فِي بُيُوتِنَا وَقُبُورِنَا. فَقَالَ رَسُولُ اللَّهِ ﷺ: إلاَّ الإِذْخِرَ).","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ﷲ ތަޢާލާ އެ އިލާހުގެ ރަސޫލާގެ މައްޗަށް މައްކާ ފަތަހަކޮށްދެއްވި (އަހަރު)، ޚުޒާޢާ ގަބީލާގެ މީހުން، ބަނޫ ލައިޘްގެ ތެރެއިން ފިރިހެނަކު މަރާލިއެވެ. އެއީ އެ މީހުންގެ ތެރެއިން ކުރިން ޖާހިލިއްޔަ ޒަމާނުގައި މަރާލެވިފައިވާ މީހަކަށް ބަދަލުހިފުމުގެ ގޮތުންނެވެ. ދެން ނަބިއްޔާ ﷺ ތެދުވެވަޑައިގެން ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ﷲ ޢައްޒަ ވަޖައްލަ ވަނީ އެތް މައްކާއިން މަނާކޮށް ހިފަހައްޓަވާފައެވެ. އަދި މައްކާގެ މައްޗަށް އެ އިލާހުގެ ރަސޫލާއާއި މުއުމިނުން ބާރުވެރި ކުރައްވާފައެވެ. ދަންނާށެވެ. ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާގެ ކުރިން އެކަކަށް ވެސް މައްކާގައި (ހަނގުރާމަކުރުން) ހުއްދަ ކުރެވިފައެއް ނުވެއެވެ. އަދި ތިމަން ނަބިއްޔާއަށް ފަހު އެކަކަށް ވެސް މައްކާގައި (ހަނގުރާމަކުރުން) ހުއްދަވެގެނެއް ނުވާނެއެވެ. ދަންނާށެވެ. ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާއަށް މައްކާގައި (ހަނގުރާމަ ކުރެއްވުން) ހުއްދަ ކުރެއްވުނީ ދުވާލުގެ ވަގުތުކޮޅަކަށެވެ. ދަންނާށެވެ. ހަމަކަށަވަރުން މިހާރު މި ވަގުތުގައި (ކުރިއެކޭ އެއްފަދައިން) މައްކާ ހުރުމަތްތެރިވެގެން ވެއެވެ. އަދި މައްކާގެ ކަށިހުރި ކުދި ގަސްތައް އުފުރުމެއް ނުވެއެވެ. އަދި އެ ތަނުގައި ވެއްޓިގެން (ގެއްލޭ) އެއްޗެއް، އޭގެ ވެރިމީހާ ހޯދުމުގެ ގޮތުން އިއުލާން ކުރުމަށް މެނުވީ ހޮވައިގެން ނުވެއެވެ. އަދި މީހަކު މަރައިލެވިއްޖެ ނަމަ، އޭނާގެ ވާރުތަވެރިންނަށް ދެ ގޮތުން ކުރެ އެއް ގޮތް އިޚްތިޔާރު ކުރުމުގެ ހައްގު ލިބިގެންވެއެވެ. އެއް ގޮތަކީ ގަތުލުކުރި މީހާ (ކިބައިން ގިސާސް ހިފައި) ގަތުލު ކުރުމެވެ. އަދި އަނެއް ގޮތަކީ މަރުގެ ދިޔަ ނެގުމެވެ.“ ދެން އަބޫ ޝާހު ކިޔުނު ޔަމަނުގެ މީހަކު ތެދުވެފައި ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. (ތިޔަ ވިދާޅުވި ވާހަކަފުޅު) ތިމަންނައަށް ލިޔެދެއްވާށެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އަބޫ ޝާހަށް ލިޔެދޭށެވެ.“ އެއަށްފަހު އަލްއައްބާސް ތެދުވެވެ ވަޑައިގެން ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. އިޛްޚިރު (އެބަހީ: ގަނދަކޯޅި) ފިޔަވައެވެ. އެހެނީ ހަމަކަށަވަރުން ތިމަންމެންގެ ގެތަކާއި ކަށްވަޅުތަކުގައި (މީރުވަސް ދުއްވަން) އޭތި ބަހައްޓަމެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އިޛްޚިރު ފިޔަވައެވެ.“","[البخاري 112، 2434، 6880 ومسلم 1355 واللفظ له]"],["#349","كتاب القصاص","ޤިޞާޞްގެ ފޮތް","","","عَنْ عُمَرَ بْنِ الْخَطَّابِ - رَضِيَ اللَّهُ عَنْهُ - أَنَّهُ اسْتَشَارَ النَّاسَ فِي إمْلاصِ الْمَرْأَةِ، فَقَالَ الْمُغِيرَةُ بْنُ شُعْبَةَ: (شَهِدْت النَّبِيَّ ﷺ قَضَى فِيهِ بِغُرَّةٍ - عَبْدٍ أَوْ أَمَةٍ - فَقَالَ: لَتَأْتِيَنَّ بِمَنْ يَشْهَدُ مَعَك، فَشَهِدَ مَعَهُ مُحَمَّدُ بْنُ مَسْلَمَةَ)","ޢުމަރު ބިން އަލްޚައްޠާބު رَضِيَ اللَّهُ عَنْهُ ރިވާ ކުރެއްވިއެވެ: (އެހެން މީހަކު ކުރި ކަމެއްގެ ސަބަބުން) އަންހެނެއްގެ ދަރިވެއްޓިގެން ދިއުމާ ބެހޭ ގޮތުން މީސްތަކުންގެ ގާތު (ޚަލީފާ) ޢުމަރު މަޝްވަރާ ކުރެއްވިއެވެ. ފަހެ އަލްމުޣީރާ ބިން ޝުޢުބާ ދެންނެވިއެވެ: ތިކަމުގައި ﷺ ފިރިހެން ނުވަތަ އަންހެން އަޅަކު (ދިޔައިގެ ގޮތުގައި) ދިނުމަށް ނަބިއްޔާ ނިޔާ ކުރެއްވިކަމަށް ތިމަން ހެކިވަމެވެ. ޢުމަރު ދެންނެވިއެވެ: ހަމަކަށަވަރުން ތިބާއާއެކު ހެކިވާނެ އެހެން މީހަކު ގެންނަންވާނެއެވެ. ދެން އެކަލޭގެފާނާއެކު މުހައްމަދު ބިން މަސްލަމާ ހެކިވިއެވެ.","[البخاري 6905، 6906، ومسلم 1683 واللفظ له]"],["#350","كتاب القصاص","ޤިޞާޞްގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (اقْتَتَلَتْ امْرَأَتَانِ مِنْ هُذَيْلٍ. فَرَمَتْ إحْدَاهُمَا الأُخْرَى بِحَجَرٍ، فَقَتَلَتْهَا وَمَا فِي بَطْنِهَا فَاخْتَصَمُوا إلَى النَّبِيِّ ﷺ فَقَضَى رَسُولُ اللَّهِ ﷺ: أَنَّ دِيَةَ جَنِينِهَا غُرَّةٌ - عَبْدٌ، أَوْ وَلِيدَةٌ - وَقَضَى بِدِيَةِ الْمَرْأَةِ عَلَى عَاقِلَتِهَا، وَوَرَّثَهَا وَلَدَهَا وَمَنْ مَعَهُمْ، فَقَامَ حَمَلُ بْنُ النَّابِغَةِ الْهُذَلِيُّ، فَقَالَ: يَا رَسُولَ اللَّهِ، كَيْفَ أَغْرَمُ مَنْ لاشَرِبَ وَلا أَكَلَ، وَلا نَطَقَ وَلا اسْتَهَلَّ، فَمِثْلُ ذَلِكَ يُطَلُّ؟ فَقَالَ رَسُولُ اللَّهِ ﷺ: إنَّمَا هُوَ مِنْ إخْوَانِ الْكُهَّانِ) مِنْ أَجْلِ سَجْعِهِ الَّذِي سَجَعَ.","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ހުޛައިލު ގަބީލާގެ ދެ އަންހެނަކު ތަޅާފޮޅިއެވެ. އެ ތަނުން އެކަކު ގާބުރިއެއް އަނެކަކާ ދިމާއަށް އެއްލައިފިއެވެ. ފަހެ އެ އަންހެނާއާ އޭނާގެ ބަނޑުގައިވާ ދަރިފުޅު މަރުވެއްޖެއެވެ. ދެން ރަސޫލު ﷲ ﷺގެ އަރިހަށް މައްސަލަ ހުށަހެޅުނެވެ. އަދި އަންހެނާގެ ބަނޑުގައިވާ ދަރިފުޅުގެ މަރުގެ ދިޔައަކީ ފިރިހެން ނުވަތަ އަންހެން އަޅެއް ކަމުގައި ނަބިއްޔާ ނިޔާ ކުރެއްވިއެވެ. އަދި ގަތުލުކުރި އަންހެނާގެ ބައްޕަފަރާތު ގާތްތިމާގެ ފިރިހެނުންގެ މައްޗަށް ދިޔަދެއްކުން ނަބިއްޔާ ނިޔާ ކުރެއްވިއެވެ. އަދި ގަތުލު ކުރެވުނު އަންހެނާގެ ކިބައިން އޭނާގެ ދަރިންނާއި އެފަދަ އެހެން ވާރުތަވެރިންނަށް ތަރިކަ މުދާ ދޭން ނަބިއްޔާ ނިޔާކުރެއްވިއެވެ. ދެން (ގަތުލު ކުރި އަންހެނާގެ ބައްޕަ ކަމުގައިވާ) ޙަމަލު ބިން އައްނާބިޣަތުލް ހުޛަލީ ތެދުވެފައި ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ނުކައި، ނުބޮއެ، ވާހަކަވެސް ނުދައްކާ، (ދިރިހުރިކަމުގެ) އަޑެއްވެސް ލައްވާފައި ނުވާ ކުއްޖެއްގެ މައްޗަށް މަރުގެ ދިޔަ ދައްކާނީ ކިހިނަކުންތޯއެވެ؟ އެފަދަ ދިޔައެއް ޖެހެނީ ބާތިލުކުރާކަށް ނޫންތޯއެވެ؟ ދެން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”މީނާއަކީ ކާހިނުންގެ އަޚެއް ކަމުގައި މެނުވީ ނުވެއެވެ.“ (ނަބިއްޔާ އެފަދައިން ޙަދީޘް ކުރެއްވީ) ޅެންވެރިން ކިޔާ ގޮތަށް އޭނާ ވާހަކަދެއްކުމުގެ ސަބަބުންނެވެ. (ނޯޓު: މިއީ ބަހުގެ ފަސާހަތްތެރިކަމާއި ބުއްދީގެ ނަޒަރު ބޭނުންކޮށްގެން ޝަރުއީ ހުކުމާ ދެކޮޅަށް ގެނެވުނު އަމިއްލަ ބާތިލު ރައުޔުއެކެވެ. އެހެންވެ ކާހިނުންގެ ބާތިލު އަމަލުތަކާ އެއްގޮތް ކުރެވުނީއެވެ.)","[البخاري 5758، ومسلم 1681 واللفظ له]"],["#351","كتاب القصاص","ޤިޞާޞްގެ ފޮތް","","","عَنْ عِمْرَانَ بْنِ حُصَيْنٍ - رَضِيَ اللَّهُ عَنْهُ - (أَنَّ رَجُلاً عَضَّ يَدَ رَجُلٍ، فَنَزَعَ يَدَهُ مِنْ فِيهِ، فَوَقَعَتْ ثَنِيَّتُهُ، فَاخْتَصَمَا إلَى النَّبِيِّ ﷺ فَقَالَ: يَعَضُّ أَحَدُكُمْ أَخَاهُ كَمَا يَعَضُّ الْفَحْلُ، لا دِيَةَ لَك).","ޢިމްރާން ބިން ޙުޞައިން رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން މީހަކު އެހެން މީހެއްގެ އަތުގައި ދަތް އެޅިއެވެ. ދަތް އެޅި މިހާގެ އަނގައިން އަތް ދަމާލުމުން އެ މީހާގެ ކުރިމަތީ ދަތް އުފުރިގެން ދިޔައެވެ. ދެން ދެ މީހުންވެގެން ނަބިއްޔާ ﷺ އަރިހަށް މައްސަލަ ހުށަހަޅަން ދިޔައެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ފިރިހެން ޖަމަލެއް ދަތް އަޅާ ފަދައިން، ތިޔަބައިމީހުން ކުރެ މީހަކު އޭނާގެ އަޚާ ގައިގައި ދަތް އަޅަނީ (ހެއްޔެވެ؟) ތިބާއަކަށް އެއްވެސް ދިޔައެއް ނުވެއެވެ.“","[البخاري 6892 واللفظ له، ومسلم 1673]"],["#352","كتاب القصاص","ޤިޞާޞްގެ ފޮތް","","","عَنْ الْحَسَنِ بْنِ أَبِي الْحَسَنِ الْبَصْرِيِّ - رحمه الله تعالى - قَالَ: حَدَّثَنَا جُنْدُبٌ فِي هَذَا الْمَسْجِدِ، وَمَا نَسِينَا مِنْهُ حَدِيثاً، وَمَا نَخْشَى أَنْ يَكُونَ جُنْدُبٌ كَذَبَ عَلَى رَسُولِ اللَّهِ ﷺ قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: (كَانَ فِيمَنْ كَانَ قَبْلَكُمْ رَجُلٌ بِهِ جُرْحٌ فَجَزِعَ، فَأَخَذَ سِكِّيناً فَحَزَّ بِهَا يَدَهُ، فَمَا رَقَأَ الدَّمُ حَتَّى مَاتَ. قَالَ اللَّهُ عَزَّ وَجَلَّ: عَبْدِي بَادَرَنِي بِنَفْسِهِ، حَرَّمْت عَلَيْهِ الْجَنَّةَ).","އަލްޙަސަން ބިން އަބިލް ޙަސަން އަލްބަޞްރީ ރަޙިމަހު ﷲ ތަޢާލާގެ އަރިހުން ރިވާވެގެންވެއެވެ: މި މިސްކިތުގައި ޖުންދުބް ތިމަންމެންނަށް ވިދާޅުވިއެވެ. -އަދި އެކަލޭގެފާނުގެ ކިބައިން ޙަދީޘެއް ތިމަންމެން ހަނދާނެއް ނުނެތެވެ. އަދި ރަސޫލު ﷲ ﷺ އަށް ނިސްބަތްކޮށް ޖުންދުބް ދޮގު ހަދާފާނެކަމަށް ތިމަންމެން ބިރެއް ނުގަންނަމެވެ- އެކަލޭގެފާނު ވިދާޅުވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމިހުންގެ ކުރީގެ މީސްތަކުން ތެރޭގައި، ޒަހަމެއް ލިބުނު ފިރިހެނަކު ވިއެވެ. ދެން ތަދަށް ކެތް ނުކުރެވިގެން ވަޅިއެއް ނަގާ އޭނާގެ އަތް ކަފާލިއެވެ. އެންމެފަހުން ލޭ އޮހޮރުން ހުއްޓުނީ އޭނާ މަރުވުމުންނެވެ. ﷲ ޢައްޒަ ވަޖައްލަ ބަސް ވަހީ ކުރެއްވިއެވެ: ތިމަން އިލާހުގެ އަޅާގެ އޭނާގެ އަމިއްލަ ފުރާނަ (މަރުގަންނުވަން) އަވަސްވެގަތީއެވެ. ތިމަން އިލާހު އޭނާގެ މައްޗަށް ސުވަރުގެ ހަރާމްކޮށްފީމެވެ.“","[البخاري 3463 واللفظ له، ومسلم 113]"],["#353","كتاب الحدود","ހައްދުތަކުގެ ފޮތް","","","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (قَدِمَ نَاسٌ مِنْ عُكْلٍ - أَوْ عُرَيْنَةَ - فَاجْتَوَوُا الْمَدِينَةَ، فَأَمَرَ لَهُمْ النَّبِيُّ ﷺ بِلِقَاحٍ، وَأَمَرَهُمْ أَنْ يَشْرَبُوا مِنْ أَبْوَالِهَا وَأَلْبَانِهَا فَانْطَلَقُوا. فَلَمَّا صَحُّوا قَتَلُوا رَاعِيَ النَّبِيِّ ﷺ وَاسْتَاقُوا النَّعَمَ، فَجَاءَ الْخَبَرُ فِي أَوَّلِ النَّهَارِ، فَبَعَثَ فِي آثَارِهِمْ. فَلَمَّا ارْتَفَعَ النَّهَارُ جِيءَ بِهِمْ، فَأَمَرَ بِهِمْ: فَقُطِّعَتْ أَيْدِيهمْ وَأَرْجُلُهُمْ مِنْ خِلافٍ، وَسُمِرَتْ أَعْيُنُهُمْ، وَتُرِكُوا فِي الْحَرَّةِ يَسْتَسْقُونَ، فَلا يُسْقَوْنَ. قَالَ أَبُو قِلابَةَ: فَهَؤُلاءِ سَرَقُوا وَقَتَلُوا وَكَفَرُوا بَعْدَ إيمَانِهِمْ، وَحَارَبُوا اللَّهَ وَرَسُولَهُ). أَخْرَجَهُ الْجَمَاعَةُ.","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُގެ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ޢުކްލު ނުވަތަ ޢުރައިނާ ގަބީލާއިން ބަޔަކު (މަދީނާއަށް) އަތުވިއެވެ. ދެން މަދީނާގެ (މޫސުން އެބައިމީހުންނާ ނުގުޅުމުގެ ސަބަބުން) އެބައިމީހުން ބަލިވެގެން ދިޔައެވެ. ފަހެ ނަބިއްޔާ ﷺ (އެކަލޭގެފާނުގެ) ކިރުދޭ އަންހެން ޖަމަލުތައް ހުއިހައްޕާ ތަނަކަށް ދިޔުމަށް އެބައިމީހުންނަށް އެންގެވިއެވެ. އަދި އެ ޖަމަލުތަކުގެ ކިރާ ކުޑަކަމުދާ ތަކެތި (ބޭހެއްގެ ގޮތުގައި) އެބައިމީހުން ބުއިމަށް އެންގެވިއެވެ. ދެން އެބައިމީހުން (އެތަނަށް) ނުކުމެ ހިނގައްޖެއެވެ. ދެން އެބައިމީހުން ބަލިން ރަނގަޅުވުމުން، ނަބިއްޔާ ﷺގެ ޖަމަލު ހުއިހައްޕާ މީހާގެ (ލޮލަށް ދޮންދަގަނޑު ކޮށްޕައިގެން) މަރާލުމަށް ފަހު، ޖަމަލުތަކައިގެން އެބައިމީހުން ފިލައިފިއެވެ. ދެން ހެނދުނު އެކަމުގެ ޚަބަރު ފޯރުމުން، އެބައިމީހުން ފަހަތުން ނަބިއްޔާ ބަޔަކު ފޮނުއްވިއެވެ. ފަހެ މެންދުރުވީ އިރު، އެބައިމީހުން އަތުލައިގަނެގެން ގެނެވުނެވެ. އަދި ނަބިއްޔާ އަމުރު ކުރެއްވުމުން އެބައިމީހުންގެ އަތާއި ފައި ދެ ފަރާތުން ބުރިކޮށްލެވުނެވެ. އަދި ދޮންދަގަނޑު އެބައިމީހުންގެ ލޮލުގައި ޖެއްސުނެވެ. އަދި އަލްޙައްރާ އޭ ކިޔުނު ތަނެއްގައި އެބައިމީހުން ދޫކޮށްލާފައި (މަރުވަންދެން) ބޭއްވުނެވެ. ފެނަށް އެދުމުން، އެބައިމީހުންނަކަށް ފެން ނުދެވެއެވެ.\n\n(ޙަދީޘްގެ ރާވީއެއް ކަމަށްވާ) އަބޫ ގިލާބާ ވިދާޅުވިއެވެ: ފަހެ މިބައިމީހުން ވައްކަންކޮށް، ގަތުލުކޮށް އަދި އީމާންވުމަށް ފަހު ކާފަރުވެ، ﷲ އާއި އެ އިލާހުގެ ރަސޫލާއާ ހަނގުރާމަކުރިއެވެ.\n\nމި ޙަދީޘް ނެރުއްވީ ޖަމާއަތެވެ. (އެބަހީ: އެންމެ މަޝްހޫރު ހަ ޙަދީޘް ފޮތުގައި ރިވާވެފައިވެއެވެ.)","[البخاري 233 واللفظ له، ومسلم 1671، وأبو دلود 4364، والترمذي 72، والنسائي 4025، وابن ماجه 2578]"],["#354","كتاب الحدود","ހައްދުތަކުގެ ފޮތް","","","عَنْ عُبَيْدِ اللَّهِ بْنِ عَبْدِ اللَّهِ بْنِ عُتْبَةَ بْنِ مَسْعُودٍ عَنْ أَبِي هُرَيْرَةَ وَزَيْدِ بْنِ خَالِدٍ الْجُهَنِيِّ رَضِيَ اللَّهُ عَنْهُمَا، أَنَّهُمَا قَالا: (إنَّ رَجُلاً مِنَ الأَعْرَابِ أَتَى رَسُولَ ﷺ فَقَالَ: يَا رَسُولَ اللَّهِ، أَنْشُدُك اللَّهَ إلاَّ قَضَيْتَ بَيْنَنَا بِكِتَابِ اللَّهِ. فَقَالَ الْخَصْمُ الآخَرُ - وَهُوَ أَفْقَهُ مِنْهُ - نَعَمْ، فَاقْضِ بَيْنَنَا بِكِتَابِ اللَّهِ، وَأْذَنْ لِي. فَقَالَ النَّبِيُّ ﷺ: قُلْ، فَقَالَ: إنَّ ابْنِي كَانَ عَسِيفاً عَلَى هَذَا، فَزَنَى بِامْرَأَتِهِ، وَإِنِّي أُخْبِرْت أَنَّ عَلَى ابْنِي الرَّجْمَ، فَافْتَدَيْت مِنْهُ بِمِائَةِ شَاةٍ وَوَلِيدَةٍ، فَسَأَلْتُ أَهْلَ الْعِلْمِ فَأَخْبَرُونِي أَنَّمَا عَلَى ابْنِي جَلْدُ مِائَةٍ وَتَغْرِيبُ عَامٍ، وَأَنَّ عَلَى امْرَأَةِ هَذَا الرَّجْمَ. فَقَالَ رَسُولُ اللَّهِ ﷺ: وَاَلَّذِي نَفْسِي بِيَدِهِ لأَقْضِيَنَّ بَيْنَكُمَا بِكِتَابِ اللَّهِ، الْوَلِيدَةُ وَالْغَنَمُ رَدٌّ عَلَيْك وَعَلَى ابْنِك جَلْدُ مِائَةٍ وَتَغْرِيبُ عَامٍ. وَاغْدُ يَا أُنَيْسُ - لِرَجُلٍ مِنْ أَسْلَمَ - عَلَى امْرَأَةِ هَذَا، فَإِنْ اعْتَرَفَتْ فَارْجُمْهَا، فَغَدَا عَلَيْهَا، فَاعْتَرَفَتْ، فَأَمَرَ بِهَا رَسُولُ اللَّهِ ﷺ فَرُجِمَتْ).","ޢުބައިދު ﷲ ބިން ޢަބްދު ﷲ ބިން ޢުތުބާ ބިން މަސްޢޫދު، އަބޫ ހުރައިރާ އަދި ޒައިދު ބިން ޚާލިދު އަލްޖުހަނީ رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާ ކުރެއްވިއެވެ: ހަމަކަށަވަރުން އައުރާބީންގެ ތެރެއިން މީހަކު ނަބިއްޔާ ﷺގެ އަރިހަށް އައިސް ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ﷲ ގަންދީ ދަންނަވަމެވެ. ﷲގެ ފޮތުގައި ވާގޮތަށް ތިމަންމެންގެ މެދުގައި ހުކުމް ކުރައްވާށެވެ. ދެން އެ މައްސަލައިގައި ހިމެނުނު އަނެއް މީހާ ދަންނަވައިފިއެވެ. -އަދި މީނާއަކީ ފުރަތަމަ މީހާއަށްވުރެ ވިސްނޭ މީހެކެވެ-: އާދޭހެވެ. ފަހެ ﷲގެ ފޮތުގައި ވާގޮތަށް ތިމަންމެންގެ މެދުގައި ހުކުމް ކޮށްދެއްވާށެވެ. އަދި ތިމަންނައަށް (ފުރަތަމަ ވާހަކަ ދެއްކުމުގެ) ހުއްދަ ދެއްވާށެވެ. ދެން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ވިދާޅުވާށެވެ.“ އޭނާ ދެންނެވިއެވެ: ތިމަންނަގެ ފިރިހެން ދަރިވީ މި މީހާއަށް އުޖޫރައަށް މަސައްކަތްކޮށްދޭން ދިޔަ ކުއްޖަކަށެވެ. ދެން އެ ކުއްޖާ އޭނާގެ އަންހެނުންނާ ޒިނޭކޮށްފިއެވެ. އަދި ތިމަންނައަށް ޚަބަރު ލިބިފައިވާ ފަދައިން، ތިމަންނަގެ ދަރިއަށް އޮތީ ރަޖަމު ކުރުމެވެ. ވީމާ އެ ކުއްޖާ (މިކަމުން ސަލާމަތް ކުރުމަށްޓަކައި) ސަތޭކަ ބަކަރިއާއި އަންހެން އަޅެއް ދީގެން ފިދްޔަ ދައްކައިފީމެވެ. އެއަށްފަހު ޢިލްމުގެ އަހުލުވެރިން ގާތު ތިމަން ސުވާލު ކުރުމުން ޖަވާބު ދިނީ، ތިމަންނަގެ ދަރިފުޅަށް އޮތީ ސަތޭކަ އެތިފަހަރުން ހައްދުޖަހައި އެއް އަހަރަށް އަރުވާލުން ކަމަށެވެ. އަދި ރަޖަމް ކުރުން އޮތީ މި މީހާގެ އަންހެނުންގެ މައްޗަށް ކަމެވެ. ދެން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާގެ ފުރާނަފުޅު އެ ފަރާތެއްގެ އަތްޕުޅުގައިވާ ފަރާތް ގަންދީ ހުވާކުރަމެވެ. ﷲގެ ފޮތުގައި ވާގޮތަށް ތިޔަ ދެ މީހުންގެ މެދުގައި ތިމަން ނަބިއްޔާ ހުކުމް ކުރައްވާނެކަން ކަށަވަރެވެ. އަޅު އަންހެނާއާއި ބަކަރިތައް ވަނީ އަނބުރާ ރައްދުކުރުމެވެ. އަދި ތިބާގެ ދަރިފުޅަށް ސަތޭކަ އެތިފަހަރުން ހައްދު ޖަހައި އެއް އަހަރަށް އަރުވާލުމެވެ.“ (ދެން އަސްލަމް ވަންހައިގެ މީހަކަށް ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ:) ”އޭ އުނައިސެވެ. މީނާގެ އަންހެނުންގެ ގާތަށް ދާށެވެ. އަދި އެ ކަނބުލޭގެ (ކުށަށް) އިއުތިރާފުވެއްޖެ ނަމަ ރަޖަމު ކުރާށެވެ.“ ދެންފަހެ އެ ކަނބުލޭގެ ގާތަށް ދިޔުމުން އިއުތިރާފު ވެއްޖެއެވެ. އަދި ރަސޫލު ﷲ ﷺ އެންގެވުމުން އެ ކަނބުލޭގެ ގެންގޮސް ރަޖަމު ކުރެވުނެވެ.","[البخاري 2695، 2696، ومسلم 1697، 1698 واللفظ له]"],["#355","كتاب الحدود","ހައްދުތަކުގެ ފޮތް","","","عَنْ عُبَيْدِ اللَّهِ بْنِ عُقْبَةَ بْنِ مَسْعُودٍ عَنْ أَبِي هُرَيْرَةَ وَزَيْدِ بْنِ خَالِدٍ الْجُهَنِيِّ رَضِيَ اللَّهُ عَنْهُمَا قَالا:\n(سُئِلَ النَّبِيُّ ﷺ عَنِ الأَمَةِ إذَا زَنَتْ وَلَمْ تُحْصَنْ؟ قَالَ: إنْ زَنَتْ فَاجْلِدُوهَا، ثُمَّ إنْ زَنَتْ فَاجْلِدُوهَا، ثُمَّ إنْ زَنَتْ فَاجْلِدُوهَا، ثُمَّ بِيعُوهَا وَلَوْ بِضَفِيرٍ).\n\nقالَ ابنُ شِهابٍ: «ولاأَدري، أَبَعْدَ الثَّالِثَةِ أَوِ الرَّابِعةِ». والضَّفيرُ: الحَبْلُ.","ޢުބައިދު ﷲ ބިން ޢަބްދު ﷲ ބިން ޢުތުބާ ބިން މަސްޢޫދު، އަބޫ ހުރައިރާ އަދި ޒައިދު ބިން ޚާލިދު އަލްޖުހަނީ رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާ ކުރެއްވިއެވެ: އެ ދެބޭކަލުން ވިދާޅުވިއެވެ: އަންހެން އަޅަކު ކައިވެނިނުކޮށް ހުރެ ޒިނޭ ކުރުމާ ބެހޭ ގޮތުން ނަބިއްޔާ ﷺ އަރިހުގައި ސުވާލު ކުރެވުނެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ ކަނބުލޭގެ ޒިނޭކޮށްފި ނަމަ އެތިފަހަރުން ހައްދު ޖަހާށެވެ. ދެންވެސް އޭނާ ޒިނޭކޮށްފި ނަމަ އަނެއްކާވެސް އެތިފަހަރުން ހައްދު ޖަހާށެވެ. ދެންވެސް އޭނާ ޒިނޭކޮށްފި ނަމަ އަނެއްކާވެސް އެތިފަހަރުން ހައްދު ޖަހުމަށް ފަހު ވާކޮޅަކަށް ނަމަވެސް އޭނާ ވިއްކާލާށެވެ.“","[البخاري 6837، 6838 واللفظ له، ومسلم 1704]"],["#356","كتاب الحدود","ހައްދުތަކުގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ رَضِيَ اللَّهُ عَنْهُ أَنَّهُ قَالَ: (أَتَى رَجُلٌ مِنْ الْمُسْلِمِينَ رَسُولَ اللَّهِ ﷺ وَهُوَ فِي الْمَسْجِدِ - فَنَادَاهُ: يَا رَسُولَ اللَّهِ، إنِّي زَنَيْتُ، فَأَعْرَضَ عَنْهُ. فَتَنَحَّى تِلْقَاءَ وَجْهِهِ فَقَالَ: يَا رَسُولَ اللَّهِ، إنِّي زَنَيْت، فَأَعْرَضَ عَنْهُ، حَتَّى ثَنَّى ذَلِكَ عَلَيْهِ أَرْبَعَ مَرَّاتٍ. فَلَمَّا شَهِدَ عَلَى نَفْسِهِ أَرْبَعَ شَهَادَاتٍ: دَعَاهُ رَسُولُ اللَّهِ ﷺ، فَقَالَ: أَبِكَ جُنُونٌ؟ قَالَ: لا. قَالَ: فَهَلْ أُحْصِنْت؟ قَالَ: نَعَمْ. فَقَالَ رَسُولُ اللَّه ﷺ: اذْهَبُوا بِهِ فَارْجُمُوهُ).\nقَالَ ابْنُ شِهَابٍ: فَأَخْبَرَنِي أَبُو سَلَمَةَ بْنُ عَبْدِ الرَّحْمَنِ. سَمِعَ جَابِرَ بْنَ عَبْدِ اللَّهِ يَقُولُ «كُنْت فِيمَنْ رَجَمَهُ، فَرَجَمْنَاهُ بِالْمُصَلَّيْ، فَلَمَّا أَذْلَقَتْهُ الْحِجَارَةُ هَرَبَ، فَأَدْرَكْنَاهُ بِالْحَرَّةِ، فَرَجَمْنَاهُ».\n\nالرَّجُلُ هو ماعزُ بنُ مالِكٍ. ورَوَى قِصَّتَهُ جابرُ بنُ عبدِ اللهِ وعَبْدُ اللهِ بنُ عَباسٍ وأَبو سعيدٍ الخُدْرِيُّ وبُرَيْدَةُ بنُ الحُصَيْبِ الأَسْلَمِيُّ.","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ މިސްކިތުގައި ވަނިކޮށް، މުސްލިމުންގެ ތެރެއިން ފިރިހެނަކު ނަބިއްޔާގެ އަރިހަށް އައިސް ގޮވާލެއްވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން ތިމަން ޒިނޭކޮށްފީމެވެ. ފަހެ އޭނާގެ ކިބައިން ނަބިއްޔާ މޫނުފުޅު އަނބުރާލެއްވިއެވެ. ދެން އެ މީހާ ގޮސް ނަބިއްޔާގެ މޫނުފުޅާ ކުރިމަތިލާފައި ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން ތިމަން ޒިނޭކޮށްފީމެވެ. އަނެއްކާވެސް އޭނާގެ ކިބައިން ނަބިއްޔާ މޫނުފުޅު އަނބުރާލެއްވިއެވެ. އެހެން ގޮސް އެ މީހާ ހަތަރު ފަހަރު އެކަން ތަކުރާރު ކުރިއެވެ. ދެން އެ މީހާގެ އަމިއްލަ ނަފްސުގެ މައްޗަށް ހަތަރު ފަހަރު ހެކިވުމުން، ރަސޫލު ﷲ ﷺ އޭނާއަށް ގޮވާލައްވާފައި ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ މޮޔަތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ: ނޫނެކެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާއަށް ކައިވެނީގެ ރަހަ ދެކިފައި ވޭތޯއެވެ؟“ އޭނާ ދެންނެވިއެވެ: އާދޭހެވެ ނަބިއްޔާ (މީސްތަކުންނަށް) ޙަދީޘް ކުރެއްވިއެވެ: ”އޭނާ ގެންގޮސް ރަޖަމް ކުރާށެވެ.“\n\nއިބްނު ޝިހާބު ވިދާޅުވިއެވެ: އަޞު ސަލަމާ ބިން ޢަބްދުއްރަޙްމާން ތިމަންނައަށް ޚަބަރު ދެއްވިއެވެ: އެކަލޭގެފާނު ޖާބިރު ބިން ޢަބްދު ﷲ ވިދާޅުވަނިކޮށް އަޑުއެހިއެވެ: އޭނާ ރަޖަމު ކުރި މީހުންގެ ތެރޭގައި ތިމަންވީމެވެ. ތިމަންމެން އޭނާ ރަޖަމު ކުރީ (ސަހަރާގެ) ނަމާދު ކުރާ ތަން ކައިރީގައެވެ. ފަހެ އެ މީހާ ގައިގައި އޮށްގާތަށް ޖެހި (ވޭން ލިބުމުން)، ރައްކާވުމަށް އޭނާ ފިލައިފިއެވެ. ދެން ޙައްރާއޭ ކިޔުނުތާ ކައިރިން ތިމަންމެން އޭނާ ހިފައި ރަޖަމުކޮށްފީމެވެ.","[البخاري 5271، 5272، ومسلم 1691\n\r\nالبخاري 6824، ومسلم 1693]"],["#357","كتاب الحدود","ހައްދުތަކުގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا أَنَّهُ قَالَ: (إنَّ الْيَهُودَ جَاءُوا إلَى رَسُولِ اللَّهِ ﷺ فَذَكَرُوا لَهُ: أَنَّ امْرَأَةً مِنْهُمْ وَرَجُلاً زَنَيَا. فَقَالَ لَهُمْ رَسُولُ اللَّهِ ﷺ: مَا تَجِدُونَ فِي التَّوْرَاةِ، فِي شَأْنِ الرَّجْمِ؟ فَقَالُوا: نَفْضَحُهُمْ وَيُجْلَدُونَ. قَالَ عَبْدُ اللَّهِ بْنُ سَلامٍ: كَذَبْتُمْ، فِيهَا آيَةُ الرَّجْمِ، فَأَتَوْا بِالتَّوْرَاةِ فَنَشَرُوهَا، فَوَضَعَ أَحَدُهُمْ يَدَهُ عَلَى آيَةِ الرَّجْمِ فَقَرَأَ مَا قَبْلَهَا وَمَا بَعْدَهَا. فَقَالَ لَهُ عَبْدُ اللَّهِ بْنُ سَلامٍ: ارْفَعْ يَدَك. فَرَفَعَ يَدَهُ، فَإِذَا فِيهَا آيَةُ الرَّجْمِ، فَقَالَ: صَدَقَ يَا مُحَمَّدُ، فَأَمَرَ بِهِمَا النَّبِيُّ ﷺ فَرُجِمَا. قَالَ: فَرَأَيْت الرَّجُلَ: يَجْنَأُ عَلَى الْمَرْأَةِ يَقِيهَا الْحِجَارَةَ). \nالرجلُ الذي وضع يدَهُ على آيةِ الرَّجْمِ عبدُ اللهِ بنُ صُوريا.","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺގެ އަރިހަށް ޔަހޫދީން އައިސް، އެބައިމީހުންގެ ތެރެއިން ފިރިހެނަކާއި އަންހެނަކު ޒިނޭކުރި ވާހަކަ ދެންނެވިއެވެ. ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ރަޖަމު ކުރުމުގެ (ހައްދާ) ގުޅޭ ގޮތުން ތައުރާތުގައި ތިޔަބައިމީހުންނަށް ފެންނަނީ ކޮންކަމެއްތޯއެވެ؟“ އެބައިމީހުން ދެންނެވިއެވެ: އެ ޒިނޭ ކުރި މީހުން ފަޟީހަތްކޮށް އެބައިމީހުންގެ ގައިގައި އެތިފަހަރުން ތެޅުމެވެ. ޢަބްދު ﷲ ބިން ސަލާމް ދެންނެވިއެވެ: ތިޔަބައިމީހުން ތިޔަ ހެދީ ދޮގެވެ. އެ (ތައުރާތުގައި) ރަޖަމު ކުރުމުގެ އާޔަތްވެއެވެ. ދެން އެބައިމީހުން ތައުރާތު ގެނެސް ހުޅުވާލިއެވެ. އަދި އެބައިމީހުންގެ ތެރެއިން މީހަކު، ރަޖަމު ކުރުމުގެ އާޔަތް މަތީ އަތް އަޅާފައި، އެ އާޔަތުގެ ކުރިންނާއި ފަހުން އައި އާޔަތްތައް ކިޔައިފިއެވެ. ފަހެ ޢަބްދު ﷲ ބިން ސަލާމް އޭނާއަށް ދެންނެވިއެވެ: ތިބާގެ އަތް އުފުލާށެވެ. ދެން އެ މީހާ އޭނާގެ އަތް އުފުލިއެވެ. ފަހެ އެތާ ވަނީ ރަޖަމު ކުރުމުގެ އާޔަތެވެ. އެބައިމީހުން ދެންނެވިއެވެ. އޭ މުޙައްމަދުގެފާނެއެވެ. ކަލޭގެފާނު ވިދާޅުވީ ތެދުފުޅެވެ. ދެން އެ (ޒިނޭ ކުރި) ދެމީހުން ރަޖަމު ކުރުމަށް ނަބިއްޔާ އަމުރު ކުރެއްވުމުން އެކަން ކުރެވުނެވެ. ޢަބްދު ﷲ ބިން ޢުމަރު ވިދާޅުވިއެވެ: (ރަޖަމު ކުރުމުގެ ތެރޭގައި) އެ (ޒިނޭ ކުރި) ފިރިހެނާ ތިމަން ދުށީމެވެ. އޭނާގެ (ހަށިގަނޑުން) އުޅުނީ އޮށްގާތަކުން އެ (ޒިނޭ ކުރި) އަންހެނާ ހިމާޔަތް ކުރެވޭތޯއެވެ.","[البخاري 3635 واللفظ له، ومسلم 1699]"],["#358","كتاب الحدود","ހައްދުތަކުގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: (لَوْ أَنَّ رَجُلاً - أَوْ قَالَ: امْرَأً - اطَّلَعَ عَلَيْكَ بِغَيْرِ إذْنِكَ، فَحَذَفْتَهُ بِحَصَاةٍ، فَفَقَأْتَ عَيْنَهُ: مَا كَانَ عَلَيْك جُنَاحٌ).","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހުއްދަ ނެތި ތިބާގެ ގޭތެރެއަށް މީހަކު ބެލުމުން، ތިބާ އޭނާގެ ލޮލަށް ހިލަކޮޅެއް އެއްލައިގެން އޭނާގެ ލޮލަށް ގެއްލުން ވެއްޖެ ނަމަ، ތިބާގެ މައްޗަކަށް އެއްވެސް ކުށެއް ނުވާނެއެވެ.“","[البخاري 6902، ومسلم 2158]"],["#359","كتاب الحدود","ހައްދުތަކުގެ ފޮތް","باب حد السرقة","ވައްކަމުގެ ހައްދުގެ ފޮތް","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رضي الله تعالى عنهما: (أَنَّ النَّبِيَّ ﷺ قَطَعَ فِي مِجَنٍّ قِيمَتُهُ).\n\nوَفِي لَفْظٍ: (ثَمَنُهُ - ثَلاثَةُ دَرَاهِمَ).","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ تَعَالَى عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން އައްޑަނައެއްގެ އަގަށް ވާވަރުގެ އެއްޗެއް (ވައްކަން ކުރުމުން) ނަބިއްޔާ ﷺ (އަތް) ކެނޑުއްވިއެވެ.\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: އޭގެ އަގަކީ ތިން ދިރުހަމެވެ.","[البخاري 6796 واللفظ له، ومسلم 1686]"],["#360","كتاب الحدود","ހައްދުތަކުގެ ފޮތް","باب حد السرقة","ވައްކަމުގެ ހައްދުގެ ފޮތް","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا: أَنَّهَا سَمِعَتْ رَسُولَ اللَّهِ ﷺ يَقُولُ: (تُقْطَعُ الْيَدُ فِي رُبْعِ دِينَارٍ فَصَاعِداً).","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން ކަމަނާ އަޑުއެހީމެވެ: ”ދީނާރެއްގެ ހަތަރުބައި ކުޅަ އެއްބައި ނުވަތަ އެއަށް ވުރެ އަގު ބޮޑު (އެއްޗެއް ވައްކަން ކުރުމުން) އަތް ކެނޑޭނެއެވެ.“","[البخاري 6789 واللفظ له، ومسلم 1684]"],["#361","كتاب الحدود","ހައްދުތަކުގެ ފޮތް","باب حد السرقة","ވައްކަމުގެ ހައްދުގެ ފޮތް","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا (أَنَّ قُرَيْشاً أَهَمَّهُمْ شَأْنُ الْمَخْزُومِيَّةِ الَّتِي سَرَقَتْ، فَقَالُوا: مَنْ يُكَلِّمُ فِيهَا رَسُولَ اللَّهِ ﷺ؟ فَقَالُوا: وَمَنْ يَجْتَرِئُ عَلَيْهِ إلاَّ أُسَامَةُ بْنُ زَيْدٍ حِبُّ رَسُولِ اللَّهِ ﷺ فَكَلَّمَهُ أُسَامَةُ، فَقَالَ: أَتَشْفَعُ فِي حَدٍّ مِنْ حُدُودِ اللَّهِ؟ ثُمَّ قَامَ فَاخْتَطَبَ، فَقَالَ: إنَّمَا أَهْلَكَ الَّذِينَ مِنْ قَبْلِكُمْ أَنَّهُمْ كَانُوا إذَا سَرَقَ فِيهِمْ الشَّرِيفُ تَرَكُوهُ، وَإِذَا سَرَقَ فِيهِمْ الضَّعِيفُ أَقَامُوا عَلَيْهِ الْحَدَّ، وَأَيْمُ اللَّهِ: لَوْ أَنَّ فَاطِمَةَ بِنْتَ مُحَمَّدٍ سَرَقَتْ لَقَطَعْتُ يَدَهَا).\n\nوَفِي لَفْظٍ (كَانَتْ امْرَأَةٌ تَسْتَعِيرُ الْمَتَاعَ وَتَجْحَدُهُ، فَأَمَرَ النَّبِيُّ ﷺ بِقَطْعِ يَدِهَا).","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން (އިއްޒަތްތެރި) މަޚްޒޫމީ އަންހެންކަނބަލަކު ވައްކަންކޮށްގެން ގުރައިޝުވަންހައިގެ މީހުންނަށް އެކަންތައް ވަރަށް ބޮޑުކަމަކަށްވިއެވެ. އެބައިމީހުން ދެންނެވިއެވެ: އެ ކަނބުލޭގެ މައްޗަށް (ހައްދު ގާއިމު ނުކުރުމަށް) ރަސޫލު ﷲ ﷺގެ އަރިހުގައި ވާހަކަ ދައްކާނީ ފަހެ ކާކުހެއްޔެވެ؟ ދެން އެބައިމީހުން ދެންނެވިއެވެ: އެކަން ކުރަން ކެރޭނީ ރަސޫލު ﷲ ﷺ ލޯބިފުޅުވެވަޑައިގަންނަވާ އުސާމާ ބިން ޒައިދު ފިޔަވައި ދެން ކާކުހެއްޔެވެ؟ ދެން (އެބައިމިހުން އެދިގެން އެ ކަމުގެ) ވާހަކަ ނަބިއްޔާގެ އަރިހުގައި އުސާމާ ދެންނެވިއެވެ. ފަހެ ނަބިއްޔާ (ކޯފާވެވަޑައިގެން) ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲ ހައްދުތަކުގެ ތެރެއިން ހައްދެއްގައި ތިބާ ޝަފާއަތްތެރިކަމަށް އެދެނީހެއްޔެވެ؟“ (އެބަހީ: އިސްލާމް ދީނުގައި ކަނޑައެޅިފައިވާ ހުކުމެއް ގާއިމު ނުކުރުމަށް މެދުވެރިއެއްގެ ގޮތުގައި އެދެނީހެއްޔެވެ؟) ދެން ނަބިއްޔާ ތެދުވެވަޑައިގެން (މީސްތަކުންނާ) މުޚާތަބު ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ ކުރީގެ މީސްތަކުން ހަލާކު ކުރުވި ހަމައެކަނި ކަމަކީ، އެބައިމީހުންގެ ތެރެއިން އިއްޒަތްތެރި ޝަރަފުވެރިއަކު ވައްކަންކޮށްފި ކަމުގައި ވާނަމަ، އެބައިމީހުން އޭނާ ދޫކޮށްލާކަމެވެ. އަދި އެބައިމީހުންގެ ތެރެއިން ބަލިކަށި ނިކަމެއްޗަކު ވައްކަންކޮށްފި ކަމުގައި ވާނަމަ، އެބައިމީހުން އޭނާގެ މައްޗަށް ހައްދު ގާއިމު ކުރާކަމެވެ. ﷲ ގަންދީ ބުނަމެވެ. މުޙައްމަދުގެފާނުގެ ދަރިކަނބަލުން ފާތިމާ ވައްކަން ކުރެއްވި ކަމުގައި ވިޔަސް، ތިމަން ނަބިއްޔާ އެ ކަމަނާގެ އަތްޕުޅު ކަނޑުއްވާނެކަން ކަށަވަރެވެ.“\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: އެ (މަޚްޒޫމީ) ކަނބުލޭގެ (މީހުން އަތުން) ތަކެތި އަނބުރާ ދޭގޮތަށް ނެގުމަށްފަހު، އެ ނެގިކަމަށް އިންކާރު ކުރިއެވެ. ދެން ނަބިއްޔާ ﷺ އެ ކަނބުލޭގެ އަތް ކެނޑުމަށް އަމުރު ކުރެއްވިއެވެ.","[البخاري 3475، ومسلم 1688\n\r\nمسلم 1688]"],["#362","كتاب الحدود","ހައްދުތަކުގެ ފޮތް","باب حد الخمر","ރާ ބުއިމުގެ ހައްދުގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - (أَنَّ النَّبِيَّ ﷺ أُتِيَ بِرَجُلٍ قَدْ شَرِبَ الْخَمْرَ, فَجَلَدَهُ بِجَرِيدَةٍ نَحوَ أَرْبَعِينَ)","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ރާބުއި މީހަކު ނަބިއްޔާ ﷺގެ އަރިހަށް ގެނެވުނެވެ. ފަހެ ސާޅީހަކަށް އެތިފަރުން އެ މީހާ ގައިގައި ހައްދު ޖެހުނެވެ.","[البخاري 6773، ومسلم 1706 واللفظ له]"],["#363","كتاب الحدود","ހައްދުތަކުގެ ފޮތް","باب حد الخمر","ރާ ބުއިމުގެ ހައްދުގެ ބާބު","عَنْ أَبِي بُرْدَةَ هَانِئِ بْنِ نِيَارٍ الْبَلَوِيِّ - رَضِيَ اللَّهُ عَنْهُ -: أَنَّهُ سَمِعَ رَسُولَ اللَّهِ ﷺ يَقُولُ: (لا يُجْلَدُ فَوْقَ عَشَرَةِ أَسْوَاطٍ إلاَّ فِي حَدٍّ مِنْ حُدُودِ اللَّهِ).","އަބީ ބުރްދާ ހާނީ ބިން ނިޔާރު އަލްބަލަވީ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް އެކަލޭގެފާނަށް އަޑުއިވުނެވެ: ”ﷲގެ ހައްދުތަކުން ކުރެ ހައްދެއްގައި މެނުވީ ދިހަ އެތިފަހަރަށް ވުރެ ގިނައިން ޖަހައިގެން ނުވާނެއެވެ.“ (އެބަހީ: ދީނުގެ ހައްދު ކަނޑައެޅިގެންވާ ކަންތައް ފިޔަވައި ދެން ހުރި ކުށްތަކުގައި އަދަބު ދޭއިރު، އެންމެ ގިނައިން އެތިފަހަރު ޖެހޭނީ ދިހަ ފަހަރަށެވެ.)","[البخاري 6848، ومسلم 1708 واللفظ له]"],["#364","كتاب الأيمان والنذور","ހުވަޔާއި ނަދުރުގެ ފޮތް","","","عَنْ عَبْدِ الرَّحْمَنِ بْنِ سَمُرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: (يَا عَبْدَ الرَّحْمَنِ بْنَ سَمُرَةَ، لا تَسْأَلْ الإِمَارَةَ، فَإِنَّكَ إنْ أُعْطِيتَهَا عَنْ مَسْأَلَةٍ وُكِّلْتَ إلَيْهَا، وَإِنْ أُعْطِيتَهَا عَنْ غَيْرِ مَسْأَلَةٍ أُعِنْتَ عَلَيْهَا، وَإِذَا حَلَفْتَ عَلَى يَمِينٍ فَرَأَيْتَ غَيْرَهَا خَيْراً مِنْهَا، فَكَفِّرْ عَنْ يَمِينِكَ، وَأْتِ الَّذِي هُوَ خَيْرٌ).","ޢަބްދުއްރަޙްމާން ބިން ސަމުރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭ ޢަބްދުއްރަޙްމާން ބިން ސަމުރާއެވެ. ވެރިކަން ހޯދަން ނޫޅުއްވާށެވެ. އެހެނީ ހަމަކަށަވަރުން އެ ހޯދަން އުޅުމުން ތިބާއަށް ވެރިކަން ދެވިއްޖެ ނަމަ، ތިބާއަށް އެ ކަމުގެ ޒިންމާ އުފުލަން ޖެހި، (ﷲގެ އެހީތެރިކަމެއް ނެތި) އެއާ ހަވާލުވާން ޖެހޭނެއެވެ. އަދި އެ ހޯދުމަކާ ނުލާ ވެރިކަން ލިބިއްޖެ ނަމަ، އެކަމުގެ މައްޗަށް (ﷲގެ) އެހިތެރިކަން ލިބޭނެއެވެ. އަދި ތިބާ ހުވައެއް ކުރުމަށްފަހު، އެއަށްވުރެ ރަނގަޅު ގޮތެއް ފެނިއްޖެ ނަމަ، ފަހެ ތިބާގެ ހުވާއަށް ކައްފާރާ ދޭށެވެ. އަދި އެއަށްވުރެ ރަނގަޅުވި ކަންތަކައިގެން އުޅޭށެވެ.“","[البخاري 6622، ومسلم 1652]"],["#365","كتاب الأيمان والنذور","ހުވަޔާއި ނަދުރުގެ ފޮތް","","","عَنْ أَبِي مُوسَى - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: (إنِّي وَاَللَّهِ - إنْ شَاءَ اللَّهُ - لا أَحْلِفُ عَلَى يَمِينٍ، فَأَرَى غَيْرَهَا خَيْراً مِنْهَا إلاَّ أَتَيْتُ الَّذِي هُوَ خَيْرٌ، وَتَحَلَّلْتُهَا).","އަބޫ މޫސާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲ އިރާދަ ކުރެއްވިއްޔާ، ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާއަށް ހުވައެއް ކުރެވުމުން، އެއަށްވުރެ ރަނގަޅު ގޮތެއް ފެނިވަޑައިގެންފި ނަމަ، ފަހެ ރަނގަޅު ގޮތް ޚިޔާރު ކުރައްވައިގެން މެނުވީ ނުދާނަމެވެ. އަދި (ކުރިން ކުރެވުނު) ހުވާއައް ކައްފާރާ ދެއްވާނަމެވެ.“","[البخاري 3133 واللفظ له، ومسلم 1649]"],["#366","كتاب الأيمان والنذور","ހުވަޔާއި ނަދުރުގެ ފޮތް","","","عَنْ عُمَرَ بْنِ الْخَطَّابِ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: (إنَّ اللَّهَ يَنْهَاكُمْ أَنْ تَحْلِفُوا بِآبَائِكُمْ).\n\nوَلِمُسْلِمٍ: (فَمَنْ كَانَ حَالِفاً فَلْيَحْلِفْ بِاَللَّهِ أَوْ لِيَصْمُت).\n\nوَفِي رِوَايَةٍ قَالَ عُمَرُ «فَوَاَللَّهِ مَا حَلَفْتُ بِهَا مُنْذُ سَمِعْتُ رَسُولَ اللَّهِ ﷺ يَنْهَى عَنْهَا، ذَاكِراً وَلا آثِراً»\n\n(آثِراً: يعني: حاكياً عن غيري أَنه حلفَ بها).","ޢުމަރު ބިން އަލްޚައްޠާބު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ތިޔަބައިމީހުންގެ ބައްޕަމެން ގަންދީ ތިޔަބައިމީހުން ހުވާ ކުރުން ﷲ ވަނީ ނަހީ ކުރައްވާފައެވެ.“\n\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ”މީހަކު ހުވާ ކުރާ ނަމަ، ފަހެ ﷲ ގަންދީ ހުވާ ކުރާށެވެ. ނޫން ނަމަ ހަނު ހުންނާށެވެ.“\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ޢުމަރު ވިދާޅުވިއެވެ: ﷲ ގަންދީ ބުނަމެވެ. ރަސޫލު ﷲ ﷺ އެކަން ނަހީ ކުރެއްވިކަމަށް ތިމަން އަޑުއެހީއްސުރެ، އެފަދަ ހުވައެއް ތިމަން ކޮށްފައެއް ނުވާނެއެވެ. ގަސްދުގައިވެސް އަދި އެހެން މީހަކު އެފަދައިން ހުވާ ކުރި ވާހަކައެއް ބުނުމުގައިވެސް މެއެވެ.","[البخاري 6647، ومسلم 1646\n\nالبخاري 1646، ومسلم 6108\n\nالبخاري 6647، ومسلم 1646]"],["#367","كتاب الأيمان والنذور","ހުވަޔާއި ނަދުރުގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - عَنْ النَّبِيِّ ﷺ قَالَ: (قَالَ سُلَيْمَانُ بْنُ دَاوُد عليهما السلام: لأَطُوفَنَّ اللَّيْلَةَ عَلَى سَبْعِينَ امْرَأَةً، تَلِدُ كُلُّ امْرَأَةٍ مِنْهُنَّ غُلامًا يُقَاتِلُ فِي سَبِيلِ اللَّهِ، فَقِيلَ لَهُ: قُلْ: إنْ شَاءَ اللَّهُ، فَلَمْ يَقُلْ، فَطَافَ بِهِنَّ، فَلَمْ تَلِدْ مِنْهُنَّ إلاَّ امْرَأَةٌ وَاحِدَةٌ: نِصْفَ إنْسَانٍ. قَالَ: فَقَالَ رَسُولُ اللَّهِ ﷺ: لَوْ قَالَ إنْ شَاءَ اللَّهُ لَمْ يَحْنَثْ، وَكَانَ دَرَكاً لِحَاجَتِهِ). قولُهُ: (فَقِيلَ لَهُ: قُلْ: إنْ شَاءَ اللَّهُ) يعني قالَ له المَلَكُ.","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ސުލައިމާން ބިން ދާވޫދު ޢަލައިހިމައް ސަލާމް ވިދާޅުވިއެވެ: ހަމަކަށަވަރުން މިރޭ ހަތްދިހަ (އަނބި) ކަނބަލުންގެ ގާތަށް ވަޑައިގެން ތިމަން (ރޭކުރައްވާނަމެވެ.) ﷲގެ މަގުގައި ޖިހާދު ކުރާނެ ފިރިހެން ކުއްޖަކު އެއިން ކޮންމެ ކަނބަލަކު ވިހާނެއެވެ. ފަހެ (މަލާއިކަތަކު) ސުލައިމާންގެފާނަށް ދެންނެވުނެވެ: ﷲ އިރާދަ ކުރެއްވިއްޔާއޭ ވިދާޅުވާށެވެ. އެހެނަސް ސުލައިމާންގެފާނު (ހަނދުމަނެތިވަޑައިގެން) އެފަދައިން ވިދާޅެއް ނުވެއެވެ. ދެން ސުލައިމާންގެފާނު އެ ކަނބަލުންގެ ގާތަށް ވަޑައިގެން (ރޭ ކުރެއްވިއެވެ.) ފަހެ އެންމެ ކަނބަލަކު ފިޔަވައި އެއިން އެއްވެސް ކަނބަލަކު ނުވިހައެވެ. އެ ކަނބުލޭގެ ވިހީ އިންސާނެއްގެ ދެބައިކުޅަ އެއްބައެވެ. ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ސުލައިމާންގެފާނު ﷲ އިރާދަކުރެއްވިއްޔާއޭ ވިދާޅުވި ނަމަ އެކަލޭގެފާނުގެ ވައުދާ ޚިލާފުވެވަޑައިގެންނެވިކަމަށް ނުވާނެއެވެ. އަދި (އެފަދައިން ވިދާޅުވި ނަމަ) އެކަން ވާނީ އެކަލޭގެފާނު އެދިލެއްވި ކަންކަން ހާސިލުކޮށްދޭ ކަމަކަށެވެ.","[البخاري 5242، ومسلم 1654 واللفظ له]"],["#368","كتاب الأيمان والنذور","ހުވަޔާއި ނަދުރުގެ ފޮތް","","",'عَنْ عَبْدِ اللَّهِ بْنِ مَسْعُودٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: (مَنْ حَلَفَ عَلَى يَمِينِ صَبْرٍ يَقْتَطِعُ بِهَا مَالَ امْرِئٍ مُسْلِمٍ، هُوَ فِيهَا فَاجِرٌ، لَقِيَ اللَّهَ وَهُوَ عَلَيْهِ غَضْبَانُ). وَنَزَلَتْ: (إنَّ الَّذِينَ يَشْتَرُونَ بِعَهْدِ اللَّهِ وَأَيْمَانِهِمْ ثَمَناً قَلِيلاً) إلَى آخِرِ الآيَةِ ".',"ޢަބްދު ﷲ ބިން މަސްޢޫދު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ:”(ގާޒީއަކު ޝަރީއަތުގައި) މީހެއް ގާތު ހުވާ ކުރަން އެނގުމުން، އެ މީހާ އެހެން މުސްލިމެއްގެ މުދާ ނުހައްގުން އަތުލައިގަތުމަށް ދޮގު ހުވާކޮށްފި ނަމަ، އޭނާ ﷲ އާ ބައްދަލުވާނީ އޭނާގެ މައްޗަށް ﷲ ކޯފާވެވޮޑިގެންވާ ހާލުގައެވެ.“ ދެން ﷲ ވަހީ ބާވައިލެއްވިއެވެ: ’ހަމަކަށަވަރުން ﷲގެ އަހުދާއި އެބައިމީހުންގެ ހުވާތަކަށް ކުޑަ އަގެއް ގަނެ އިޅޭ މީހުންނީ އާޚިރަތުގައި އެބައިމީހުންނަށް އެއްވެސް ނަސީބެއް ނެތް ބަޔެކެވެ. އަދި ގިޔާމަތް ދުވަހު ﷲ އެބައިމިހުންނަކަށް ބަސް ވަހީއެއް ނުކުރައްވާނެއެވެ. އަދި އެބައިމީހުންނަކަށް ބައްލަވާވޮޑިއެއްވެސް ނުގަންނަވާނެއެވެ. އަދި (ފާފައިން) އެބައިމީހުން ތާހިރެއްވެސް ނުކުރައްވާނެއެވެ. އަދި އެބައިމީހުންނަށް ވޭންދެނިވި އަޒާބު ދެވޭނެއެވެ.‘","[البخاري 2356، ومسلم 138]"],["#369","كتاب الأيمان والنذور","ހުވަޔާއި ނަދުރުގެ ފޮތް","","","عَنْ الأَشْعَثِ بْنِ قَيْسٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (كَانَ بَيْنِي وَبَيْنَ رَجُلٍ خُصُومَةٌ فِي بِئْرٍ فَاخْتَصَمْنَا إلَى رَسُولِ اللَّهِ ﷺ، فَقَالَ رَسُولُ اللَّهِ ﷺ: شَاهِدَاكَ أَوْ يَمِينُهُ، قُلْت: إذاً يَحْلِفُ وَلا يُبَالِي. فَقَالَ رَسُولُ اللَّهِ ﷺ: مَنْ حَلَفَ عَلَى يَمِينِ صَبْرٍ يَقْتَطِعُ بِهَا مَالَ امْرِئٍ مُسْلِمٍ، هُوَ فِيهَا فَاجِرٌ، لَقِيَ اللَّهَ عَزَّ وَجَلَّ وَهُوَ عَلَيْهِ غَضْبَانُ).","އަލްއަޝްޢަޘު ބިން ގައިސް رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ވަޅަކާ ގުޅިގެން ތިމަންނައާއި އެހެން މީހަކާ ދެމެދު މައްސަލަ ޖެހުނެވެ. ދެން ރަސޫލު ﷲ ﷺގެ އަރިހަށް އެ މައްސަލަ ތިމަންމެން ހުށަހެޅީމެވެ. ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާގެ (ދައުވާގެ ތެދުކަން ސާބިތު ކުރުމަށް) ދެ ހެކިން ހުށަހަޅާށެވެ. ނޫންނަމަ (އެ ދައުވާ ރައްދުވާ މީހާއަށް އޮތީ އިންކާރު ކުރުމަށް ﷲ ގަންދީ) ހުވާ ކުރުމެވެ.“ ތިމަން ދެންނެވީމެވެ: އޭރުން އެ މީހާ (ދޮގު) ހުވާކޮށްފައި އެކަމާ އަޅައެއް ނުލާނެއެވެ. ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”(ގާޒީއަކު ޝަރީއަތުގައި) މީހެއް ގާތު ހުވާ ކުރަން އެނގުމުން، އެ މީހާ އެހެން މުސްލިމެއްގެ މުދާ ނުހައްގުން އަތުލައިގަތުމަށް ދޮގު ހުވާކޮށްފި ނަމަ، އޭނާ ﷲ ޢައްޒަ ވަޖައްލައާ ބައްދަލުވާނީ އޭނާގެ މައްޗަށް ﷲ ކޯފާވެވޮޑިގެންވާ ހާލުގައެވެ.“","[البخاري 2669، 2670، ومسلم 138 واللفظ له]"],["#370","كتاب الأيمان والنذور","ހުވަޔާއި ނަދުރުގެ ފޮތް","","","عَنْ ثَابِتِ بْنِ الضَّحَّاكِ الأَنْصَارِيِّ - رَضِيَ اللَّهُ عَنْهُ - أَنَّهُ بَايَعَ رَسُولَ اللَّهِ ﷺ تَحْتَ الشَّجَرَةِ، وَأَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: (مَنْ حَلَفَ عَلَى يَمِينٍ بِمِلَّةٍ غَيْرِ الإِسْلامِ، كَاذِباً مُتَعَمِّداً، فَهُوَ كَمَا قَالَ، وَمَنْ قَتَلَ نَفْسَهُ بِشَيْءٍ عُذِّبَ بِهِ يَوْمَ الْقِيَامَةِ، وَلَيْسَ عَلَى رَجُلٍ نَذْرٌ فِيمَا لا يَمْلِكُ)\n\nوَفِي رِوَايَةٍ: (وَلَعْنُ الْمُؤْمِنِ كَقَتْلِهِ).\n\nوَفِي رِوَايَةٍ: (مَنِ ادَّعَى دَعْوَى كَاذِبَةً لِيَتَكَثَّرَ بِهَا، لَمْ يَزِدْهُ اللَّهُ عَزَّ وَجَلَّ إلاَّ قِلَّةً).","ޘާބިތު ބިން އައްޟައްޙާކު އަލްއަންޞާރީ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން އެކަލޭގެފާނު ރަސޫލު ﷲ ﷺ އަށް (ބައިއަތުލް ރިޟްވާނުގައި) ގަސްދޮށުގައި ބައިއަތު ހިއްޕެވިއެވެ. އަދި ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އިސްލާމް ދީން ނޫން ދީނެއްގެ މަތީވުމަށް (އެބަހީ: މިވެނި ކަމެއް ވެއްޖެ ނަމަ ނުވަތަ ނުވެއްޖެ ނަމަ ތިމަންނައަކީ ޔަހޫދީއެކޭ ނުވަތަ ނަސްރާނީއެކޭ ނުވަތަ ކާފަރެކޭ ބުނެ) މީހަކު އެނގިހުރެ ދޮގު ހުވައެއްކޮށްފި ނަމަ، ފަހެ އެ މީހަކު ވާނީ ހަމަ އެ ބުނި ފަދަ މީހަކަށެވެ. އަދި އެއްޗެއް ބޭނުންކޮށްގެން މީހަކު އަމިއްލައަށް މަރުވެއްޖެ ނަމަ ފަހެ ގިޔާމަތް ދުވަހު ހަމަ އެ އެއްޗަކުން އޭނާއަށް އަޒާބު ދެވޭނެއެވެ. އަދި މީހަކަށް މިލްކުވެރިކަމެއް ނުވާ އެއްޗެއްގައި ނަދުރު ބުނުމެއް ނުވެއެވެ.“\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”އަދި މުއުމިނަކަށް ލައްނަތް ލެއްވުމަކީ އޭނާ މެރުން ކަހަލަ ކަމެކެވެ.“\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”(މުދާ) ގިނަ ކުރަން މީހަކު ދޮގު ދައުވާއެއްކޮށްފި ނަމަ، ފަހެ ﷲ ޢައްޒަ ވަޖައްލަ އޭނާގެ ކިބައިން އުނިކުރެއްވުމުގައި މެނުވީ އިތުރެއް ނުކުރައްވާނެއެވެ.“","[البخاري 6047، ومسلم 110 واللفظ له\n\nالبخاري 6105، ومسلم 110\n\nمسلم 110]"],["#371","كتاب الأيمان والنذور","ހުވަޔާއި ނަދުރުގެ ފޮތް","باب النذر","ނަދުރުގެ ބާބު","عَنْ عُمَرَ بْنِ الْخَطَّابِ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (قُلْتُ: يَا رَسُولَ اللَّهِ، إنِّي كُنْتُ نَذَرْتُ فِي الْجَاهِلِيَّةِ أَنْ أَعْتَكِفَ لَيْلَةً - وَفِي رِوَايَةٍ: يَوْماً - فِي الْمَسْجِدِ الْحَرَامِ؟ قَالَ: فَأَوْفِ بِنَذْرِكَ).","ޢުމަރު ބިން އަލްޚައްޠާބު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ތިމަން ދެންނެވީމެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން މަސްޖިދުލް ހަރާމުގައި އެއް ރޭ (އެހެން ރިވާޔަތެއްގައި އެއް ދުވަސް) އިއުތިކާފަށް އިނުމަށް، ޖާހިލިއްޔަ ޒަމާނުގައި ތިމަންނަ ނަދުރެއް ބުނެފައިވާނެއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ފަހެ ތިބާގެ ނަދުރު ފުއްދާށެވެ.“","[البخاري 2032، ومسلم 1656]"],["#372","كتاب الأيمان والنذور","ހުވަޔާއި ނަދުރުގެ ފޮތް","باب النذر","ނަދުރުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا عَنْ النَّبِيِّ ﷺ (أَنَّهُ نَهَى عَنْ النَّذْرِ، وَقَالَ: إنَّ النَّذْرَ لا يَأْتِي بِخَيْرٍ. وَإِنَّمَا يُسْتَخْرَجُ بِهِ مِنْ الْبَخِيلِ).","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަދުރުބުނުން ނަބިއްޔާ ﷺ ނަހީކުރެއްވިއެވެ. އަދި ޙަދީޘްކުރެއްވިއެވެ ”ހަމަކަށަވަރުން ނަދުރުން ހެޔޮކަމެއް ނާދެއެވެ. އެހެނެއްކަމަކު އެއީ އޭގެ ސަބަބުން ދަހި މީހެއްގެ އަތުން (އެއްޗެއް) ނެރޭ ކަމެއް ކަމުގައި މެނުވީ ނުވެއެވެ.“","[البخاري 6608، ومسلم 1639 واللفظ له]"],["#373","كتاب الأيمان والنذور","ހުވަޔާއި ނަދުރުގެ ފޮތް","باب النذر","ނަދުރުގެ ބާބު","عَنْ عُقْبَةَ بْنِ عَامِرٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (نَذَرَتْ أُخْتِي أَنْ تَمْشِيَ إلَى بَيْتِ اللَّهِ الْحَرَامِ حَافِيَةً فَأَمَرَتْنِي أَنْ أَسْتَفْتِيَ لَهَا رَسُولَ اللَّهِ ﷺ فَاسْتَفْتَيْتُهُ فَقَالَ: لِتَمْشِ وَلْتَرْكَبْ).","ޢުގްބާ ބިން ޢާމިރު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ބައިތު ﷲ އަލްހަރާމަށް ހުސްފަޔާ ހިނގައިގެން ދިޔުމަށް ތިމަންނަގެ އުޚްތު ނަދުރު ބުންޏެވެ. ދެން އެ ކަމަނާއަށް ޓަކައި ރަސުލު ﷲ ﷺގެ އަރިހުގައި އެ ކަމާ ގުޅޭ ގޮތުން އެހުމަށް ތިމަންނައަށް އެނގެވިއެވެ. ދެން އެ ކަމާ ގުޅޭ ގޮތުން އެހުމުން ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ ކަމަނާ ހިނގަވާށެވެ. އަދި (ސަވާރިއަކަށް) އަރާށެވެ.“","[البخاري 1866، ومسلم 1644 واللفظ له]"],["#374","كتاب الأيمان والنذور","ހުވަޔާއި ނަދުރުގެ ފޮތް","باب النذر","ނަދުރުގެ ބާބު","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُمَا أَنَّهُ قَالَ: (اسْتَفْتَى سَعْدُ بْنُ عُبَادَةَ رَسُولَ اللَّهِ ﷺ فِي نَذْرٍ كَانَ عَلَى أُمِّهِ، تُوُفِّيَتْ قَبْلَ أَنْ تَقْضِيَهُ، قَالَ رَسُولُ اللَّهِ ﷺ: فَاقْضِهِ عَنْهَا).","ހަމަކަށަވަރުން ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ސަޢުދު ބިން ޢުބާދާ، އެކަލޭގެފާނުގެ މަންމާފުޅުގެ މައްޗަށް އޮތް ނަދުރެއް ނުފުއްދި ވަނިކޮށް އަވަހާރަވުމުން، އެކަމާ ބެހޭ ގޮތުން ރަސޫލު ﷲ ﷺ ގާތު ސަޢުދު އެއްސެވިއެވެ. ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ފަހެ އެ ކަމަނާގެ މަތިން އެ ނަދުރު ފުއްދާށެވެ.“","[البخاري 2761، ومسلم 1638 واللفظ له]"],["#375","كتاب الأيمان والنذور","ހުވަޔާއި ނަދުރުގެ ފޮތް","باب النذر","ނަދުރުގެ ބާބު","عَنْ كَعْبِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (قُلْتُ: يَا رَسُولَ اللَّهِ، إنَّ مِنْ تَوْبَتِي: أَنْ أَنْخَلِعَ مِنْ مَالِي، صَدَقَةً إلَى اللَّهِ وَإِلَى رَسُولِهِ فَقَالَ رَسُولُ اللَّهِ ﷺ: أَمْسِكْ عَلَيْكَ بَعْضَ مَالِكَ فَهُوَ خَيْرٌ لَكَ).","ކައުބް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ތިމަން ދެންނެވީމެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން ތިމަން (ތަބޫކު ހަނގުރާމައިގައި ބައިވެރި ނުވިކަމުގެ) ތައުބާގެ ގޮތުން، ތިމަންނަގެ މުޅި ހުރިހައި މުދަލެއް ހުސްކުރާނަމެވެ. ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާގެ މުދަލުން ބައެއް ތިބާގެ އަތުގައި ބަހައްޓާށެވެ. އެ ގޮތް ތިބާއަށް މާ ހެޔޮވެގެން ވެއެވެ.“","[البخاري 6690 واللفظ له، ومسلم 2769]"],["#376","كتاب الأيمان والنذور","ހުވަޔާއި ނަދުރުގެ ފޮތް","باب القضاء","ހުކުމްކުރުމުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: قَالَ رَسُولُ اللَّهِ ﷺ: (مَنْ أَحْدَثَ فِي أَمْرِنَا هَذَا مَا لَيْسَ مِنْهُ فَهُوَ رَدٌّ).\n\nوَفِي لَفْظٍ (مَنْ عَمِلَ عَمَلاٍ لَيْسَ عَلَيْهِ أَمْرُنَا فَهُوَ رَدٌّ).","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އަހަރެމެންގެ މި ދީނުގައި ނުވާ އާ ކަމެއް އުފައްދައި (ބިދުއައެއް ގެނެސްގެންފި) މީހާ (ދަންނާށެވެ!) ފަހެ، އެ ކަމެއް ވަނީ (ބާތިލުވެ) ރައްދު ކުރެވިގެންނެވެ.“\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”އަހަރެމެން ކުރުމަށް ނާންގަވާ އަމަލެއްކޮށްފި މީހާ (ދަންނާށެވެ!) ފަހެ، އެ ކަމެއް ވަނީ (ބާތިލުވެ) ރައްދު ކުރެވިގެންނެވެ.“","[البخاري 2697، ومسلم 1718\n\nمسلم 1718]"],["#377","كتاب الأيمان والنذور","ހުވަޔާއި ނަދުރުގެ ފޮތް","باب القضاء","ހުކުމްކުރުމުގެ ބާބު","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا قَالَتْ: (دَخَلَتْ هِنْدُ بِنْتُ عُتْبَةَ - امْرَأَةُ أَبِي سُفْيَانَ - عَلَى رَسُولِ اللَّهِ ﷺ فَقَالَتْ: يَا رَسُولَ اللَّهِ، إنَّ أَبَا سُفْيَانَ رَجُلٌ شَحِيحٌ، لا يُعْطِينِي مِنْ النَّفَقَةِ مَا يَكْفِينِي وَيَكْفِي بَنِيَّ، إلاَّ مَا أَخَذْتُ مِنْ مَالِهِ بِغَيْرِ عِلْمِهِ. فَهَلْ عَلَيَّ فِي ذَلِكَ مِنْ جُنَاحٍ؟ فَقَالَ رَسُولُ اللَّهِ ﷺ: خُذِي مِنْ مَالِهِ بِالْمَعْرُوفِ مَا يَكْفِيكِ وَيَكْفِي بَنِيكِ).","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އަބޫ ސުފިޔާނުގެ އަނބިކަނބަލުން ހިނދު ބިންތި ޢުތުބާ، ރަސޫލު ﷲ ﷺގެ އަރިހަށް ވަދެ ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން އަބޫ ސުފްޔާނަކީ ދަހި ބޭކަލެކެވެ. ތިމަން ކަމަނާއާއި ތިމަން ކަމަނާގެ ދަރިންނަށް ވާވަރަށް ހަރަދެއް ނުދެއެވެ. އެހެނެއްކަމަކު އަބޫ ސުފްޔާނަށް ނޭނގޭ ގޮތަށް އެކަލޭގެފާނުގެ މުދަލުން ތިމަން ކަމަނާ ނަގަމެވެ. ފަހެ އެކަމުގެ ސަބަބުން ތިމަން ކަމަނާއަށް ކުށެއް ވާނެތޯއެވެ؟ ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ރަނގަޅު ގޮތުގައި ކަމަނާއަށާއި ކަމަނާގެ ދަރިންނަށް ވާވަރަށް އެކަލޭގެފާނުގެ މުދަލުން ނަގާށެވެ.“","[البخاري 2211، ومسلم 1714 واللفظ له]"],["#378","كتاب الأيمان والنذور","ހުވަޔާއި ނަދުރުގެ ފޮތް","باب القضاء","ހުކުމްކުރުމުގެ ބާބު","عَنْ أُمِّ سَلَمَةَ رَضِيَ اللَّهُ عَنْهُا (أَنَّ رَسُولَ اللَّهِ ﷺ سَمِعَ جَلَبَةَ خَصْمٍ بِبَابِ حُجْرَتِهِ، فَخَرَجَ إلَيْهِمْ، فَقَالَ: أَلا إنَّمَا أَنَا بَشَرٌ، وَإِنَّمَا يَأْتِينِي الْخَصْمُ، فَلَعَلَّ بَعْضَكُمْ أَنْ يَكُونَ أَبْلَغَ مِنْ بَعْضٍ، فَأَحْسِبُ أَنَّهُ صَادِقٌ، فَأَقْضِي لَهُ. فَمَنْ قَضَيْتُ لَهُ بِحَقِّ مُسْلِمٍ فَإِنَّمَا هِيَ قِطْعَةٌ مِنْ نَارٍ، فَلْيَحْمِلْهَا أَوْ يَذَرْهَا).","އުންމު ސަލަމާ رَضِيَ اللَّهُ عَنْهَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާގެ ގެކޮޅުގައި މުއްސަލަ ޖެހިގެން އުޅުނު ބަޔެއްގެ އަޑުގަދަވާތަން ރަސޫލު ﷲ ﷺ އަށް އިވިވަޑައިގަތެވެ. ދެން އެ މީހުންގެ ގާތަށް ނުކުމެވަޑައިގެން ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަން ނަބިއްޔާއީ ހަމަ އިންސާނެއް ކަމުގައި މެނުވީ ނުވެއެވެ. އަދި ތިމަން ނަބިއްޔާގެ އަރިހަށް މައްސަލަ ހުށަހަޅަން މީހުން އަންނަ ހިނދު، ތިޔަބައިމީހުން ކުރެ އެއްބަޔަކު އަނެއް ބަޔަކަށް ވުރެ ބަސްމޮށުންތެރިކަމުގައި މޮޅުވެދާނެއެވެ. ދެން އޭނާ އެ ހެދީ ތެދުކަމުގައި ތިމަން ނަބިއްޔާ ބައްލަވާފައި، މައްސަލައިގައި އޭނާއާ އެއްކޮޅަށް ތިމަން ނަބިއްޔާ ނިޔާކޮށްފާނެއެވެ. ފަހެ މުސްލިމެއްގެ ހައްގަކައިގެން އެހެން މީހަކަށް ތިމަން ނަބިއްޔާ ނިޔާކޮށްދީފި ނަމަ، އެއީ އޭނައަށްވާ ނަރަކައިގެ މިންވަރެއް ކަމުގައި މެނުވީ ވެގެން ނުދާނެއެވެ. ވީމާ އެ މީހަކު އޭތި ތަޙައްމަލު ކުރާށެވެ. ނޫނީ ދޫކޮށްލާށެވެ.“","[البخاري 2458، ومسلم 1713 واللفظ له]"],["#379","كتاب الأيمان والنذور","ހުވަޔާއި ނަދުރުގެ ފޮތް","باب القضاء","ހުކުމްކުރުމުގެ ބާބު","عَنْ عَبْدِ الرَّحْمَنِ بْنِ أَبِي بَكْرَةَ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (كَتَبَ أَبِي - أَوْ كَتَبْتُ لَهُ - إلَى ابْنِهِ عَبْدِ اللَّهِ بْنِ أَبِي بَكْرَةَ وَهُوَ قَاضٍ بِسِجِسْتَانَ: أَنْ لا تَحْكُمْ بَيْنَ اثْنَيْنِ وَأَنْتَ غَضْبَانُ. فَإِنِّي سَمِعْتُ رَسُولَ اللَّهِ ﷺ يَقُولُ: لايَحْكُمْ أَحَدٌ بَيْنَ اثْنَيْنِ وَهُوَ غَضْبَانُ).\n\nوَفِي رِوَايَةٍ: (لا يَقْضِيَنَّ حَاكِمٌ بَيْنَ اثْنَيْنِ وَهُوَ غَضْبَانُ).","ޢަބްދުއްރަޙްމާން ބިން އަބޫ ބަކްރާ رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ބައްޕާފުޅުގެ ދަރިކަލުން ޢަބްދު ﷲ ބިން އަބޫ ބަކުރަށް އެކަލޭގެފާނު ސިޓީއެއް ލިޔުއްވިއެވެ. –(ނޫނީ ޢަބްދުއްރަޙްމާން ވިދާޅުވިއެވެ:) ބައްޕާފުޅަށް ޓަކައި ތިމަން ސިޓީއެއް ލިޔުނީމެވެ. ޢަބްދު ﷲ ހުންނެވީ ސިޖިސްތާނުގެ ގާޟީއެއްކަމުގައެވެ. (އެ ސިޓީގައި ވަނީ މިފަދައިންނެވެ:) ތިބާ ރުޅިއައިސްގެން ހުރެ ދެ މީހެއްގެ ދެމެދުގައި ހުކުމް ނުކުރާށެވެ. އެހެނީ ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ”ތިޔަބައިމީހުން ކުރެ މީހަކު ރުޅިއައިސްގެން ހުރެ ދެ މީހެއްގެ ދެމެދުގައި ހުކުމް ނުކުރާށެވެ.“\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ”ގާޟީއަކު ރުޅިއައިސްގެން ހުރެ ދެ މީހެއްގެ ދެމެދުގައި ހުކުމްކޮށްގެން ނުވާނެއެވެ“","[مسلم 1717\n\nالبخاري 7158]"],["#380","كتاب الأيمان والنذور","ހުވަޔާއި ނަދުރުގެ ފޮތް","باب القضاء","ހުކުމްކުރުމުގެ ބާބު","عَنْ أَبِي بَكْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: (أَلا أُنَبِّئُكُمْ بِأَكْبَرِ الْكَبَائِرِ؟ - ثَلاثَاً - قُلْنَا: بَلَى يَا رَسُولَ اللَّهِ، قَالَ: الإِشْرَاكُ بِاَللَّهِ وَعُقُوقُ الْوَالِدَيْنِ، وَكَانَ مُتَّكِئاً فَجَلَسَ، وَقَالَ: أَلا وَقَوْلُ الزُّورِ، وَشَهَادَةُ الزُّورِ، فَمَا زَالَ يُكَرِّرُهَا حَتَّى قُلْنَا: لَيْتَهُ سَكَتَ).","އަބޫ ބަކްރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ތިން ފަހަރު ޙަދީޘް ކުރެއްވިއެވެ: ”ބޮޑެތި ފާފަތަކުގެ ތެރެއިން އެންމެ ބޮޑެތި ފާފަތަކުގެ ޚަބަރު ތިޔަބައިމީހުންނަށް ދެއްވަންދޯއެވެ؟“ ތިމަންމެން ދެންނެވީމެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. އާދޭހެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲ އަށް ޝަރީކު ކުރުމާއި، މައިންބަފައިންނަށް ކަމޭނުހިތުމެވެ.“ –ދެން ނަބިއްޔާ ލެނގިލައްވައިގެން އޮންނަވަނިކޮށް ތެދުވެ އިށީނދެވަޑައިގަތެވެ.- ”ދަންނާށެވެ! އަދި ދޮގުބަސް ބުނުމާއި، ދޮގުހެކި ދިނުމެވެ.“ ދެން ނަބިއްޔާ އެފަދައިން ވިދާޅުވުން ހުއްޓަވާނެ ނަމައޭ ތިމަންމެން ބުނުމަށް ދާންދެން، ނަބިއްޔާ އެފަދައިން ތަކުރާރުކޮށް ވިދާޅުވުން މަތީ ދެމި ހުންނެވިއެވެ.","[البخاري 2654، ومسلم 87]"],["#381","كتاب الأيمان والنذور","ހުވަޔާއި ނަދުރުގެ ފޮތް","باب القضاء","ހުކުމްކުރުމުގެ ބާބު","عَنْ ابْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُمَا: أَنَّ النَّبِيَّ ﷺ قَالَ: (لَوْ يُعْطَى النَّاسُ بِدَعْوَاهُمْ لادَّعَى نَاسٌ دِمَاءَ رِجَالٍ وَأَمْوَالَهُمْ، وَلَكِنْ الْيَمِينُ عَلَى الْمُدَّعَى عَلَيْهِ).","އިބްނު ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމައެކަނި މީސްތަކުން ކޮށްލާ ދައުވާއަކުން، (ޝަރީޢަތުން) އެބައިމީހުންނަށް ކަންކަން ނިންމައިދެވޭ ނަމަ، (ﷲ އަށް ބިރުވެތި ނުވާ) މީހުން، އެހެން މީހުންގެ މުދަލާއި (މަރުގެ) ދިޔައަށް ދައުވާ ކުރީސްކަން ކަށަވަރެވެ. އެހެނެއްކަމަކު، ދައުވާ ކުރާ މީހާގެ މައްޗަށް ވަނީ (ދައުވާގެ ތެދުކަން ސާބިތު ކުރުމަށް) ހެކި ހުށަހެޅުމެވެ. އަދި (ދައުވާ ލިބި، އެއަށް) އިންކާރު ކުރާ މީހާގެ މައްޗަށް ވަނީ (އެ ދައުވާގެ ދޮގުކަން ބަޔާންކޮށް، ﷲ ގަންދީ) ހުވާ ކުރުމެވެ.“ (އެބަހީ: މީހެއްގެ މައްޗަށް ކުރެވޭ ދައުވާ ސާބިތު ކުރެވިއްޖައުމަށް ދާންދެން، އޭނާ ވާނީ އެ ކަމަކުން ބަރީއަ ވެގެންނެވެ.)","[البخاري 4552، ومسلم 1711 واللفظ له]"],["#382","كتاب الأطعمة","ކާތަކެތީގެ ފޮތް","","","عَنْ النُّعْمَانِ بْنِ بَشِيرٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: سَمِعْتُ رَسُولَ اللَّهِ ﷺ يَقُولُ - وَأَهْوَى النُّعْمَانُ بِإِصْبَعَيْهِ إلَى أُذُنَيْهِ -: (إنَّ الْحَلالَ بَيِّنٌ، وَالْحَرَامَ بَيِّنٌ وَبَيْنَهُمَا مُشْتَبِهَاتٌ، لا يَعْلَمُهُنَّ كَثِيرٌ مِنْ النَّاسِ، فَمَنْ اتَّقَى الشُّبُهَاتِ: اسْتَبْرَأَ لِدِينِهِ وَعِرْضِهِ، وَمَنْ وَقَعَ فِي الشُّبُهَاتِ: وَقَعَ فِي الْحَرَامِ، كَالرَّاعِي يَرْعَى حَوْلَ الْحِمَى يُوشِكُ أَنْ يَرْتَعَ فِيهِ، أَلا وَإِنَّ لِكُلِّ مَلِكٍ حِمًى، أَلا وَإِنَّ حِمَى اللَّهِ مَحَارِمُهُ، أَلا وَإِنَّ فِي الْجَسَدِ مُضْغَةً إذَا صَلَحَتْ صَلَحَ الْجَسَدُ كُلُّهُ وَإِذَا فَسَدَتْ فَسَدَ الْجَسَدُ كُلُّهُ. أَلا وَهِيَ الْقَلْبُ)","ނުޢުމާން ބިން ބަޝީރު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: –އަދި ނުޢުމާން އެކަލޭގެފާނުގެ ދެ އިނގިލި ދެ ކަންފަތާ ދިމާލަށް އިޝާރާތް ކުރެއްވިއެވެ- ”ހަމަކަށަވަރުން ހަލާލު ކަންތައް ވަނީ ހާމަޔަށް ސާފުކޮށް ބަޔާން ވެގެންނެވެ. އަދި ހަމަކަށަވަރުން ހަރާމް ކަންތައް ވެސް ވަނީ ހާމަޔަށް ސާފުކޮށް ބަޔާން ވެގެންނެވެ. އަދި އެ ދެމެދުގައި (ހަރާމް ހަލާލާ މެދު ޝައްކު އުފެދޭ) ޝުބުހަތަކެއް (އެބަހީ: އޮޅުން އަރާ އެއްގޮތް ކަންތައްތަކެއް) ވެއެވެ. މީސްތަކުންގެ ތެރެއިން ގިނަ ބަޔަކަށް އެ (ހަލާލު ކަމެއް ނުވަތަ ހަރާމް ކަމެއް) ނޭނގޭނެއެވެ. ފަހެ އެ ޝުބުހަތަކުން ރައްކާތެރިވެގެންފި މީހާ، އޭނާގެ ދީނާއި އަބުރަށް ޓަކައި (އެ ކަންތައްތަކުން) ބަރީއަވެ (ސަލާމަތް) ވެއްޖެއެވެ. އަދި އެ ޝުބުހާތަކަށް އަރައިގެންފި މީހާ، އޭނާ ހަރާމް ކަންތަކަށް އަރައިގެންފިއެވެ. (އޭގެ މިސާލަކީ) ހިމާޔަތް ކުރެވިފައިވާ އިމެއްގެ ވަށައިގެން ބަކަރި ހުއިހައްޕާ މީހެއްގެ މިސާލެވެ. ފަހެ، އެ ބަކަރިތައް އެ އިމަށް ވަދެ ހުއިކެއުމަކީ ގާތުގައިވާ ކަމެކެވެ. ދަންނާށެވެ! ހަމަކަށަވަރުން ކޮންމެ ރަސްކަލަކަށް ވެސް ހިމާޔަތް ކުރެވިފައިވާ އިމެއް ވެއެވެ. އަދި ދަންނާށެވެ! ހަމަކަށަވަރުން ﷲގެ އިމަކީ، އެ އިލާހު ހަރާމް ކުރެއްވި ކަންތައްތަކެވެ. އަދި ދަންނާށެވެ! ހަމަކަށަވަރުން (އިންސާނާގެ) ހަށިގަނޑުގައި މަސްކޮޅެއް ވެއެވެ. އެ މަސްކޮޅު އިސްލާހު ވެއްޖެ ނަމަ، މުޅި ހަށިގަނޑު އިސްލާހު ވެދެއެވެ. އަދި އެ މަސްކޮޅު ފާސިދު ވެއްޖެ ނަމަ، މުޅި ހަށިގަނޑު ފާސިދު ވެދެއެވެ. ދަންނާށެވެ! އެއީ ހިތެވެ.“","[البخاري 52، ومسلم 1599 واللفظ له]"],["#383","كتاب الأطعمة","ކާތަކެތީގެ ފޮތް","","","عَنْ أَنَسِ بْنِ مَالِكٍ رَضِيَ اللَّهُ عَنْهُ قَالَ: (أَنْفَجْنَا أَرْنَباً بِمَرِّ الظَّهْرَانِ فَسَعَى الْقَوْمُ فَلَغَبُوا، وَأَدْرَكْتُهَا فَأَخَذْتُهَا فَأَتَيْتُ بِهَا أَبَا طَلْحَةَ، فَذَبَحَهَا وَبَعَثَ إلَى رَسُولِ اللَّهِ ﷺ بِوَرِكِهَا وَفَخِذَيْهَا. فَقَبِلَهُ)","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: މައްރުއް ޡަހްރާނޭ ކިޔުނު ތަނެއްގައި ތިމަންމެން މުސަޅެއް ފެނިގެން ފަހާ ދުވީމެވެ. ދެން މީހުންތައް ދުވެ ވަރުބަލިވެއްޖެއެވެ. އެކަމަކު ތިމަންނަ އެ މުސަޅާ އަރާ ހަމަވެ އަތުލައިގެނެވުނެވެ. އަދި އެ މުސަޅު ހިފައިގެން (ދޮން ބައްޕާފުޅު) އަބޫ ތަލްޙާއަށް ދިނުމުން އެކަލޭގެފާނު އެ ކަތިލައިފިއެވެ. ދެން ރަސޫލު ﷲ ﷺ އަށް އޭގެން ފަޔެއް ފޮނުވުމުން ނަބިއްޔާ އެ ގަބޫލު ކުރެއްވިއެވެ.“","[البخاري 2572 واللفظ له، ومسلم 1953]"],["#384","كتاب الأطعمة","ކާތަކެތީގެ ފޮތް","","","عَنْ أَسْمَاءَ بِنْتِ أَبِي بَكْرٍ رَضِيَ اللَّهُ عَنْهُ قَالَتْ: (نَحَرْنَا عَلَى عَهْدِ رَسُولِ اللَّهِ ﷺ فَرَسًا فَأَكَلْنَاهُ).\n\nوَفِي رِوَايَةٍ (وَنَحْنُ بِالْمَدِينَةِ).","އަސްމާ ބިންތި އަބޫ ބަކުރު رَضِيَ اللَّهُ عَنْهَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺގެ ޒަމާނުގައި ތިމަންމެން އަހެއް ކަތިލައިގެން ކެއީމެވެ.\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: އަދި ތިމަންމެން މަދީނާގައި ވަނިކޮށެވެ.","[البخاري 5512 واللفظ له، ومسلم 1942\n\nالبخاري 5511]"],["#385","كتاب الأطعمة","ކާތަކެތީގެ ފޮތް","","","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رَضِيَ اللَّهُ عَنْهُمَا: (أَنَّ النَّبِيَّ ﷺ نَهَى عَنْ لُحُومِ الْحُمُرِ الأَهْلِيَّةِ، وَأَذِنَ فِي لُحُومِ الْخَيْلِ).\n\nوَلِمُسْلِمٍ وَحْدَهُ قَالَ (أَكَلْنَا زَمَنَ خَيْبَرَ الْخَيْلَ وَحُمُرَ الْوَحْشِ، وَنَهَى النَّبِيُّ ﷺ عَنْ الْحِمَارِ الأَهْلِيِّ).","ޖާބިރު ބިން ޢަބްދުﷲ رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺ (މީސްތަކުން ގެންގުޅޭ) ހިމާރުގެ މަސް ކެއުން މަނާ ކުރެއްވިއެވެ. އަދި އަހުގެ މަސް ކެއުން ހުއްދަ ކުރެއްވިއެވެ.\n\nއަދި މުސްލިމުގައި އެކަނިވާ ރިވާޔަތުގައިވެއެވެ: ޚައިބަރު (ފަތަހަކު ރެވުނު) ޒަމާނުގައި އަސް އަދި ވަލުހިމާރު (އޮނޭޖާ) ތިމަންމެން ކެއީމެވެ. އަދި (މީސްތަކުން ގެންގުޅޭ) ހިމާރު ކެއުން ނަބިއްޔާ ﷺ މަނާ ކުރެއްވިއެވެ.\n\n(ނޯޓު: އޭގެ މަސް ކެއުން ހުއްދަވާ ވަލުހިމާރަކީ އޮނެޖާއެވެ. މިއީ އާދައިގެ ހިމާރާ މުޅިން ތަފާތު ޖަނަވާރެކެވެ. ވަލުހިމާރާ އާދައިގެ ހިމާރު ވައްތަރު ގޮތެއް ހުއްޓެއް ކަމަކު، ކުލައާއި ސައިޒުގައި ތަފާތުކަމެއް ވެއެވެ. އަދި ޒިބްރާ ނުވަތަ ކަފިހިއަހަކީ ވަލުހިމާރުގެ ބާވަތެކެވެ. އިންސާނުން ގެންގުޅޭ އާދައިގެ ހިމާރު، ފިލައިގެން ވަލުތެރޭގައި އުޅެ އޮޅުކަން ފިލައިދިޔަސް، އެއީ ވަލުހިމާރެއް ކަމުގައި ނުވާނެއެވެ. އަދި އޭގެ މަސް ކެއުންވެސް ހުއްދައެއް ނުވާނެއެވެ.)","[البخاري 5524، ومسلم 1941 واللفظ له\n\nمسلم 1941]"],["#386","كتاب الأطعمة","ކާތަކެތީގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ أَبِي أَوْفَى - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (أَصَابَتْنَا مَجَاعَةٌ لَيَالِيَ خَيْبَرَ، فَلَمَّا كَانَ يَوْمُ خَيْبَرَ: وَقَعْنَا فِي الْحُمُرِ الأَهْلِيَّةِ، فَانْتَحَرْنَاهَا فَلَمَّا غَلَتْ بِهَا الْقُدُورُ: نَادَى مُنَادِي رَسُولِ اللَّهِ ﷺ أَنْ أَكْفِئُوا الْقُدُورَ، وَرُبَّمَا قَالَ: وَلا تَأْكُلُوا مِنْ لُحُومِ الْحُمُرِ شَيْئاً).","ޢަބްދުﷲ ބިން އަބޫ އައުފާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ޚައިބަރުގެ ހަނގުރާމައިގެ ރޭތަކުގައި ތިމަންމެން ބަނޑުހައިހޫނުކަމުގައި ޖެހުނެވެ. ދެން ޚައިބަރު (ފަތަހަ ކުރެއްވި) ދުވަހު ތިމަންމެންނަށް (ޣަނީމާގެ ގޮތުގައި މީސްތަކުން ގެންގުޅޭ އާދައިގެ) ހިމާރުތަކެއް ލިބުނެވެ. ފަހެ ތިމަންމެން އެ ހިމާރުތައް ކަތިލައިގަނެފީމެވެ. ދެން އޭގެ މަސްތައް ތެލިތައް ކެކިގަތް ހިނދު، ރަސޫލު ﷲ ﷺގެ ފަރާތުން ގޮވާލައްވަން ފޮނުއްވި މީހަކު އިއްވައިފިއެވެ: ތިޔަބައިމީހުން ތެލިތައް ބަނޑުން ޖަހާށެވެ. އަދި (އާދައިގެ) ހިމާރު މަހުން އެއްޗެއް ނުކާށެވެ.","[البخاري 3155 واللفظ له، ومسلم 1937]"],["#387","كتاب الأطعمة","ކާތަކެތީގެ ފޮތް","","","عَنْ أَبِي ثَعْلَبَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (حَرَّمَ رَسُولُ اللَّهِ ﷺ لُحُومَ الْحُمُرِ الأَهْلِيَّةِ).","އަބޫ ޘަޢުލަބާ އަލްޚުޝަނީ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: (މީސްތަކުން ގެންގުޅޭ) ހިމާރުގެ މަސް ކެއުން ރަސޫލު ﷲ ﷺ ހަރާމް ކުރެއްވިއެވެ.","[البخاري 5527، ومسلم 1936]"],["#388","كتاب الأطعمة","ކާތަކެތީގެ ފޮތް","","","عَنْ ابْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (دَخَلْتُ أَنَا وَخَالِدُ بْنُ الْوَلِيدِ مَعَ رَسُولِ اللَّهِ ﷺ بَيْتَ مَيْمُونَةَ، فَأُتِيَ بِضَبٍّ مَحْنُوذٍ فَأَهْوَى إلَيْهِ رَسُولُ اللَّهِ ﷺ بِيَدِهِ، فَقَالَ بَعْضُ النِّسْوَةِ اللاَّتِي فِي بَيْتِ مَيْمُونَةَ: أَخْبِرُوا رَسُولَ اللَّهِ بِمَا يُرِيدُ أَنْ يَأْكُلَ فَرَفَعَ رَسُولُ اللَّهِ ﷺ يَدَهُ، فَقُلْتُ: أَحَرَامٌ هُوَ يَا رَسُولَ اللَّهِ؟ قَالَ: لا، وَلَكِنَّهُ لَمْ يَكُنْ بِأَرْضِ قَوْمِي، فَأَجِدُنِي أَعَافُهُ، قَالَ خَالِدٌ: فَاجْتَرَرْتُهُ، فَأَكَلْتُهُ. وَالنَّبِيُّ ﷺ يَنْظُرُ).","އިބުނު ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ތިމަންނައާއި ޚާލިދު ބިން ވަލީދު، ރަސޫލު ﷲ ﷺގެ އަރިހުގައި މައިމޫނާގެ ގެކޮޅަށް ވަދެވަޑައިގަތެވެ. ފަހެ ފިހެފައިވާ ޟައްބެއް ގެނެވުނެވެ. (ނޯޓު: އެއީ ނަޖްދުގެ ސަހަރާގައި އުޅޭ ބޮނޑެއް ފަދަ ޖަނަވާރެކެވެ.) ދެން ރަސޫލު ﷲ ﷺގެ އަތްޕުޅު އެއާ ދިމާލަށް ދިއްކުރެއްވުމުން، މައިމޫނާގެ ގެކޮޅުގައި ތިއްބެވި ބައެއް ބޭކަނބަލުން ދަންނަވައިފިއެވެ: ރަސޫލު ﷲ ﷺ އެ ފަރީއްކުޅުވަން އުޅުއްވަނީ ކޮން އެއްޗެއްކަން ތިޔަބައިމީހުން އެ ނަބިއްޔާއަށް ކިޔައިދެއްވާށެވެ. އެބައިމިހުން ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. އެއީ ޟައްބެކެވެ. ދެން ރަސޫލު ﷲ ﷺ އަތްޕުޅު އަނބުރާ ނަންގަވައިފިއެވެ. އިބްނު ޢައްބާސް ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. އެ ހަރާމްވަނީތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ނޫނެކެވެ. އެހެނެއްކަމަކު ތިމަން ނަބިއްޔާގެ ގައުމުގެ ބިމުގައި (ދަންނަ) އެއްޗެއް ނޫނެކެވެ. އެހެންކަމުން ތިމަން ނަބިއްޔާއަށް ކަމުނުދިޔައީއެވެ.“ ޚާލިދު ވިދާޅުވިއެވެ: ދެން ތިމަންނަ އެ ޟައްބު މަސް ނަގައިގެން ކެއީމެވެ. އެއީ ނަބިއްޔާ ﷺ (ތިމަންނައަށް) ބައްލަވާ ހާލުގައެވެ.","[البخاري 5537، ومسلم 1945 واللفظ له]"],["#389","كتاب الأطعمة","ކާތަކެތީގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ أَبِي أَوْفَى - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (غَزَوْنَا مَعَ رَسُولِ اللَّهِ ﷺ سَبْعَ غَزَوَاتٍ، نَأْكُلُ الْجَرَادَ)","ޢަބްދު ﷲ ބިން އަބޫ އައުފާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺގެ އަރިހުގައި ތިމަންމެން ހަތް ފަހަރު ހަނގުރާމައަށް ނުކުތީމެވެ. (އެ ދަތުރު ތަކުގައި) ތިމަންމެން ކެއީ ގޮއްފުޅަނގިއެވެ.","[البخاري 5459، ومسلم 1952 واللفظ له]"],["#390","كتاب الأطعمة","ކާތަކެތީގެ ފޮތް","","","عَنْ زَهْدَمِ بْنِ مُضَرِّبٍ الْجَرْمِيِّ قَالَ: (كُنَّا عِنْدَ أَبِي مُوسَى الأَشْعَرِيِّ. فَدَعَا بِمَائِدَةٍ، وَعَلَيْهَا لَحْمُ دَجَاجٍ، فَدَخَلَ رَجُلٌ مِنْ بَنِي تَيْمِ اللَّهِ، أَحْمَرُ، شَبِيهٌ بِالْمَوَالِي فَقَالَ: هَلُمَّ، فَتَلَكَّأَ فَقَالَ: هَلُمَّ، فَإِنِّي رَأَيْتُ رَسُولَ اللَّهِ ﷺ يَأْكُلُ مِنْهُ).","ޒަހްދަމް ބިން މުޟައްރިބު އަލްޖަރްމީ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އަބޫ މޫސާ އަލްއަޝްޢަރީ رَضِيَ اللَّهُ عَنْهُ އަރިހުގައި ތިމަންމެން ވަނިކޮށް، ޖާފަތެއް ގެންނަން އެކަލޭގެފާނު އެންގެވިއެވެ. އޭގައި ވަނީ ކުކުޅުމަހެވެ. ދެން ބަނީ ތައިމު ﷲ ވަންހައިގެ މީހަކު އެ ތަނަށް ވަދެއްޖެއެވެ. އޭނާ ދޮންވެފައި ވައްތަރީ މިނިވަންކޮށްފައިވާ އަޅަކާއެވެ. ދެން އަބޫ މޫސާ އޭނާއަށް ވިދާޅުވިއެވެ: (ކާން) އަންނާށެވެ. އެކަމަކު އެ މީހާ ފަސްޖެހުނެވެ. ދެންވެސް ވިދާޅުވިއެވެ: (ކާން) އަންނާށެވެ. އެހެނީ ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ އޭގެން ފަރީއްކުޅުވަނިކޮށް ތިމަން ދުށީމެވެ.","[البخاري 6721، ومسلم 1649 واللفظ له]"],["#391","كتاب الأطعمة","ކާތަކެތީގެ ފޮތް","","","عَنْ ابْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُمَا أَنَّ النَّبِيَّ ﷺ قَالَ: (إذَا أَكَلَ أَحَدُكُمْ طَعَامًا فَلا يَمْسَحْ يَدَهُ حَتَّى يَلْعَقَهَا، أَوْ يُلْعِقَهَا).","އިބުނު ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުންގެ ތެރެއިން މީހަކު ކެއުމެއް ކައިފިނަމަ، އޭނާގެ އަތް ފޮހެ (ސާފު ކުރުމުގެ ކުރިން) އިނގިލިތައް ބޯލާށެވެ. ނުވަތަ އެހެން މީހަކު ލައްވާ ބޯލުއްވާށެވެ.“","[البخاري 5456، ومسلم 2031 واللفظ له]"],["#392","كتاب الأطعمة","ކާތަކެތީގެ ފޮތް","باب الصيد","ޝިކާރަކުރުމުގެ ބާބު","عَنْ أَبِي ثَعْلَبَةَ الْخُشَنِيِّ رَضِيَ اللَّهُ عَنْهُ قَالَ (أَتَيْتُ رَسُولَ اللَّهِ ﷺ فَقُلْتُ: يَا رَسُولَ اللَّهِ، إنَّا بِأَرْضِ قَوْمٍ أَهْلِ كِتَابٍ، أَفَنَأْكُلُ فِي آنِيَتِهِمْ؟ وَفِي أَرْضِ صَيْدٍ، أَصِيدُ بِقَوْسِي وَبِكَلْبِي الَّذِي لَيْسَ بِمُعَلَّمٍ، وَبِكَلْبِي الْمُعَلَّمِ. فَمَا يَصْلُحُ لِي؟ قَالَ: أَمَّا مَا ذَكَرْتَ - يَعْنِي مِنْ آنِيَةِ أَهْلِ الْكِتَابِ -: فَإِنْ وَجَدْتُمْ غَيْرَهَا فَلا تَأْكُلُوا فِيهَا، وَإِنْ لَمْ تَجِدُوا فَاغْسِلُوهَا، وَكُلُوا فِيهَا. وَمَا صِدْتَ بِقَوْسِكَ، فَذَكَرْتَ اسْمَ اللَّهِ عَلَيْهِ فَكُلْ، وَمَا صِدْتَ بِكَلْبِكَ الْمُعَلَّمِ، فَذَكَرْتَ اسْمَ اللَّهِ عَلَيْهِ فَكُلْ، وَمَا صِدْتَ بِكَلْبِكَ غَيْرِ الْمُعَلَّمِ فَأَدْرَكْتَ ذَكَاتَهُ فَكُلْ).","އަބޫ ޘަޢުލަބާ އަލްޚުޝަނީ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺގެ އަރިހަށް ތިމަން އައިސް ދެންނެވީމެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން ތިމަންމެންނަކީ އަހުލު ކިތާބީންގެ ބިމެއްގައި އުޅޭ ބަޔަކީމެވެ. ފަހެ އެބައިމީހުންގެ ކަންވާރުތަކުގައި ކެވިދާނެތޯއެވެ؟ އަދި އެ ބިމުގައި ޝިކާރަ ކުރާއިރު، ތިމަން ޝިކާރަ ކުރަނީ ދުނިދަނޑީގެ އިތުރުން، ތިމަންނަގެ ތަމްރީނު ކުރެވިފައިވާ ކުއްތާއާއި ތަމްރީނު ނުކުރެވި ހުންނަ ކުއްތާވެސް ބޭނުން ކޮށްގެންނެވެ. ވީމާ ތިމަންނައަށް (ޝިކާރަ ކުރަން) ރަނގަޅީ ކޮން ގޮތެއްތޯއެވެ؟ ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތި ދެންނެވި ކަންތައްތަކުގައި، އެބަހީ އަހުލު ކިތާބީންގެ ކަންވާރުތަކާ ގުޅޭ ގޮތުން، ފަހެ އެ ނޫން އެއްޗެއް ތިޔަބައިމީހުންނަށް ފެނިއްޖެނަމަ، އެ ކަންވާރުތަކުގައި ތިޔަބައިމީހުން ނުކާށެވެ. އަދި އެ ނޫން އެއްޗެއް ނުފެންނަ ކަމުގައި ވަނީނަމަ، ފަހެ އެ ކަންވާރުތައް ދޮވުމަށްފަހު ދެން އޭގެން ކާށެވެ. އަދި ﷲގެ އިސްމުފުޅު ކިޔުމަށް ފަހު ތިބާގެ ދުނިދަނޑިން ޝިކާރަ ކުރެވުނެ އެއްޗެއް ނަމަ، ފަހެ އޭގެން ކާށެވެ. އަދި ﷲގެ އިސްމުފުޅު ކިޔުމަށް ފަހު ތިބާގެ ތަމްރީނު ކުރެވިފައިވާ ކުއްތާ ބޭނުންކޮށްގެން ޝިކާރަ ކުރެވުނެ އެއްޗެއް ނަމަ، ފަހެ އޭގެން ވެސް ކާށެވެ. އަދި ތިބާގެ ތަމްރީނު ކުރެވިފައިވާ ނުވާ ކުއްތާ ބޭނުންކޮށްގެން ޝިކާރަ ކުރެވުނެ އެއްޗެއް، (އޭތި ދިރިހުއްޓައި) ތިބާއަށް އެ ޝިކާރަ ކަތިލެވިއްޖެ ނަމަ، ފަހެ އޭގެން ވެސް ކާށެވެ.","[البخاري 5496، ومسلم 1930]"],["#393","كتاب الأطعمة","ކާތަކެތީގެ ފޮތް","باب الصيد","ޝިކާރަކުރުމުގެ ބާބު","عَنْ هَمَّامِ بْنِ الْحَارِثِ عَنْ عَدِيِّ بْنِ حَاتِمٍ قَالَ: (قُلْتُ: يَا رَسُولَ اللَّهِ، إنِّي أُرْسِلُ الْكِلابَ الْمُعَلَّمَةَ، فَيُمْسِكْنَ عَلَيَّ، وَأَذْكُرُ اسْمَ اللَّهِ؟ فَقَالَ: إذَا أَرْسَلْتَ كَلْبَكَ الْمُعَلَّمَ وَذَكَرْتَ اسْمَ اللَّهِ، فَكُلْ مَا أَمْسَكَ عَلَيْكَ. قُلْتُ: وَإِنْ قَتَلْنَ؟ قَالَ: وَإِنْ قَتَلْنَ، مَا لَمْ يَشْرَكْهَا كَلْبٌ لَيْسَ مِنْهَا. قُلْتُ: فَإِنِّي أَرْمِي بِالْمِعْرَاضِ الصَّيْدَ، فَأُصِيبُ؟ فَقَالَ: إذَا رَمَيْتَ بِالْمِعْرَاضِ فَخَزَقَ، فَكُلْهُ وَإِنْ أَصَابَهُ بِعَرْضِهِ فَلا تَأْكُلْهُ).","ހައްމާމް ބިން އަލްޙާރިޘް، ޢަދިއްޔު ބިން ޙާތިމް رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާ ކުރެއްވިއެވެ: ތިމަން ދެންނެވީމެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. ހަމަކަށަވަރުން ﷲގެ އިސްމުފުޅު ކިޔައިގެން، ތިމަންނަގެ ތަމްރީނު ކުރެވިފައިވާ ކުއްތާ ފޮނުވުމުން، އެ ސޮރު ތިމަންނައަށް ޓަކައި ޝިކާރަ ހިފައިދެއެވެ. ދެން ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲގެ އިސްމުފުޅު ކިޔުމަށް ފަހު، ތިބާގެ ތަމްރީނު ކުރެވިފައިވާ ކުއްތާ ފޮނުވައިފި ނަމަ، ފަހެ ތިބާއަށް ޓަކައި އެ ސޮރު ހިފައިދިން ޝިކާރައެއް ތިބާ ކާށެވެ.“ ތިމަން ދެންނެވީމެވެ: ދެން ކުއްތާ އެ ޝިކާރަ މަރާލައިފި ނަމަ ކިހިނެއްތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އަދި ކުއްތާ އެ ޝިކާރަ މަރާލައިފި ނަމަ ވެސްމެއެވެ. އެއީ އެ ނޫން ކުއްތާއެއް އެ ޝިކާރައިގައި ބައިވެރި ނުވާހައި ހިނދަކުއެވެ.“ ތިމަން ދެންނެވީމެވެ: އަދި ހަމަކަށަވަރުން މިޢުރާޟޭ ކިޔުނު ޝިކާރަ ކުރާ ހަތިޔާރެއް (އެބަހީ: ލޮންސި ވައްތަރު ދިގު ދަނޑިއެއް) އުކައިގެން ޝިކާރައިގައި ޖެހިއްޖެޔާ ކިހިނެއްތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިބާ މިޢުރާޟު އުކައިގެން (އޭގެ ތޫނުކޮޅުން) ޝިކާރައިގެ ތެރެއަށް ހެރިގެން (އޭތި މަރުވެއްޖެ ނަމަ) ފަހެ އޭގެން ކާށެވެ. އަދި އޭގެ (ތޫނު ނޫން ބައިން ޖެހިގެން އޭތި މަރުވެއްޖެ ނަމަ)، ފަހެ އޭގެން ނުކާށެވެ.“","[البخاري 5477، 7397، ومسلم 1929 واللفظ له]"],["#394","كتاب الأطعمة","ކާތަކެތީގެ ފޮތް","باب الصيد","ޝިކާރަކުރުމުގެ ބާބު","وَحَدِيثُ الشَّعْبِيِّ عَنْ عَدِيٍّ نَحْوُهُ، وَفِيهِ: (إلاَّ أَنْ يَأْكُلَ الْكَلْبُ، فَإِنْ أَكَلَ فَلا تَأْكُلْ فَإِنِّي أَخَافُ أَنْ يَكُونَ إنَّمَا أَمْسَكَ عَلَى نَفْسِهِ. وَإِنْ خَالَطَهَا كِلابٌ مِنْ غَيْرِهَا فَلا تَأْكُلْ، فَإِنَّمَا سَمَّيْتَ عَلَى كَلْبِكَ، وَلَمْ تُسَمِّ عَلَى غَيْرِهِ).\n\nوَفِيهِ (إذَا أَرْسَلْتَ كَلْبَكَ الْمُكَلَّبَ فَاذْكُرْ اسْمَ اللَّهِ عَلَيْهِ، فَإِنْ أَمْسَكَ عَلَيْكَ فَأَدْرَكْتَهُ حَيّاً فَاذْبَحْهُ، وَإِنْ أَدْرَكْتَهُ قَدْ قَتَلَ وَلَمْ يَأْكُلْ مِنْهُ فَكُلْهُ، فَإِنَّ أَخْذَ الْكَلْبِ ذَكَاتُهُ).\n\nوَفِيهِ أَيْضًا (إذَا رَمَيْتَ بِسَهْمِكَ فَاذْكُرْ اسْمَ اللَّهِ عَلَيْهِ)\n\nوَفِيهِ (وَإِنْ غَابَ عَنْكَ يَوْماً أَوْ يَوْمَيْنِ). وَفِي رِوَايَةٍ (الْيَوْمَيْنِ وَالثَّلاثَةَ فَلَمْ تَجِدْ فِيهِ إلاَّ أَثَرَ سَهْمِكَ فَكُلْ إنْ شِئْتَ، فَإِنْ وَجَدْتَهُ غَرِيقاً فِي الْمَاءِ فَلا تَأْكُلْ، فَإِنَّكَ لا تَدْرِي: الْمَاءُ قَتَلَهُ، أَوْ سَهْمُكَ؟).","އަދި އައްޝަޢުބީ، ޢަދިއްޔުގެ އަރިހުން ރިވާ ކުރެއްވި ޙަދީޘްގައި އެފަދައިން އައިސްފައިވެއެވެ. އަދި އޭގައި ވަނީ (މިފަދައިންނެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ:) ”އެއީ ކުއްތާ އޭގެން ކައިފިއްޔާ މެނުވީއެވެ. ފަހެ އެ ސޮރު ކައިފި ނަމަ، ތިބާ ދެން ނުކާށެވެ. އެހެނީ ހަމަކަށަވަރުން އެ ސޮރު ޝިކާރަ ކުރީ އޭގެ އަމިއްލަ ނަފްސަށްޓަކައި ވެދާނެކަމަށް ތިމަން ނަބިއްޔާ ބިރުފުޅުގަންނަމެވެ. އަދި އެނޫން އެހެން ކުއްތާއެއް އެއާއެކު ޝިކާރަ ކުރުމުގައި ބައިވެރިވެއްޖެ ނަމަ، އޭރުން ވެސް ތިބާ ނުކާށެވެ. އެހެނީ ހަމަކަށަވަރުން ﷲގެ އިސްމުފުޅުން ތިބާ ކިޔާފައިވަނީ ތިބާގެ އަމިއްލަ ކުއްތާގެ މައްޗަށެވެ. އަދި އެނޫން އެއްޗަކަށް ތިބާ އިސްމުފުޅު ކިޔާފައެއް ނުވެއެވެ.“\n\nއަދި އެ ރިވަޔަތުގައި އައިސްފައިވެއެވެ: (ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ:) ”ﷲގެ އިސްމުފުޅު ކިޔުމަށް ފަހު، ތިބާގެ ތަމްރީނު ކުރެވުނު ކުއްތާ ޝިކާރަ ކުރަން ފޮނުވައިގެން، ތިބާއަށް ޓަކައި ޝިކާރަ ހިފައިދިން އިރު، ތިބާއަށް އެ ޝިކާރައާ ހަމައަށް ދެވުނީ އޭތި ދިރިހުއްޓައި ކަމުގައި ވަނީ ނަމަ، ފަހެ އޭތި ކަތިލާށެވެ. އަދި ތިބާ އެ ޝިކާރައާ ހަމައަށް ދެވުނު އިރު ކުއްތާ އޭތި މަރައިލައިގެން އޭގެން ކައިފައި ނަމަ، ފަހެ ތިބާ އޭތި ކާށެވެ. އެހެނީ ހަމަކަށަވަރުން ކުއްތާ އޭތި ހިފި ހިފުން ވާނީ އޭގެ ކަތިލުމަށެވެ.“\n\nއަދިވެސް އެ ރިވަޔަތުގައި އައިސްފައިވެއެވެ: (ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ:) ”އަދި ތިބާ ތީރު ޖަހާ ހިނދު ﷲގެ އިސްމުފުޅު ކިޔާށެވެ.“\n\nއަދި އެ ރިވަޔަތުގައި އައިސްފައިވެއެވެ: (ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ:) ”އަދި ޝިކާރަ ކުރެވުނު ޖަނަވާރު އެއް ދުވަހު ދެ ދުވަހަށް -އަދި އެހެން ރިވާޔަތެއްގައި- ދެ ދުވަހު ތިން ދުވަހަށް ނުފެނިގޮސް، (ދެން އެއަށްފަހު ފެނުމުން) އަދި އޭގައި ތިބާގެ ތީރުގެ އަސަރު ނޫން އެއްޗެއް ފެންނަން ނެތް ނަމަ، ދެން ތިބާ ބޭނުން ނަމަ އޭގެން ކާށެވެ. އެހެނެއްކަމަކު އެ ޝިކާރަ ކުރަން އުޅުނު ޖަނަވާރު ފެނުނީ ފެންގަނޑެއްގައި ގެންބިފައިވަނިކޮށް ނަމަ، ދެން އޭގެން ނުކާށެވެ. އެހެނީ ހަމަކަށަވަރުން އެ މަރާލީ ފެން ކަމެއް ނުވަތަ ތިބާގެ ތީރުކަމެއް ނޭނގޭނެއެވެ.“","[البخاري 5483، 5486، 5487، ومسلم 1929\n\nالبخاري 5475، ومسلم 1924، 1929\n\nمسلم 1929\n\nمسلم 1929]"],["#395","كتاب الأطعمة","ކާތަކެތީގެ ފޮތް","باب الصيد","ޝިކާރަކުރުމުގެ ބާބު","عَنْ سَالِمِ بْنِ عَبْدِ اللَّهِ بْنِ عُمَرَ عَنْ أَبِيهِ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: سَمِعْتُ رَسُولَ اللَّهِ ﷺ يَقُولُ (مَنْ اقْتَنَى كَلْباً - إلاَّ كَلْبَ صَيْدٍ، أَوْ مَاشِيَةٍ - فَإِنَّهُ يَنْقُصُ مِنْ أَجْرِهِ كُلَّ يَوْمٍ قِيرَاطَانِ).\n\nقَالَ سَالِمٌ: وَكَانَ أَبُو هُرَيْرَةَ يَقُولُ: (أَوْ كَلْبَ حَرْثٍ) ، وَكَانَ صَاحِبَ حَرْثٍ.","ސާލިމް ބިން ޢަބްދު ﷲ ބިން ޢުމަރު، އެކަލޭގެފާނުގެ ބައްޕާފުޅު ވިދާޅުވިކަމަށް ރިވާ ކުރެއްވިއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ޝިކާރަ ކުރުމަށް ނުވަތަ ބަކަރި ހުއިހައްޕަން ނޫން ގޮތަކަށް ކުއްތާއެއް ގެންގުޅެއްޖެ މީހާ، ފަހެ ހަމަކަށަވަރުން އެ މީހެއްގެ ސަވާބުން ދެ ޤީރާޠުގެ މިންވަރު މަދުވެގެން ދާނެއެވެ.“ (އެބަހީ: އުހުދު ފަދަ ބޮޑު ދެ ފަރުބަދައެވެ.) (ޙަދީޘްގެ ރާވީއެއް ކަމަށްވާ) ސާލިމް ވިދާޅުވިއެވެ: އަދި އަބޫ ހުރައިރާ ވިދާޅުވެ އުޅުއްވިއެވެ: ނުވަތަ ދަނޑު ބަލަހައްޓާ ކުއްތާ ފިޔަވައެވެ. އަދި އަބޫ ހުރައިރާއަކީ ދަނޑު ގެންގުޅުއްވާ ބޭކަލެކެވެ.“\n\nއަދި ސާލިމް ވިދާޅުވިއެވެ: އަބޫ ހުރައިރާ ވިދާޅުވެ އުޅުއްވިއެވެ: ’ނުވަތަ ދަނޑު ބަލަހައްޓަން ގެންގުޅޭ ކުއްތާ ފިޔަވައެވެ.‘ އަދި އެކަލޭގެފާނަކީ ދަނޑުހައްދަވާ ބޭފުޅެކެވެ.","[البخاري 5481، ومسلم 1574 واللفظ له\n\nمسلم 1574]"],["#396","كتاب الأطعمة","ކާތަކެތީގެ ފޮތް","باب الصيد","ޝިކާރަކުރުމުގެ ބާބު","عَنْ رَافِعِ بْنِ خَدِيجٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (كُنَّا مَعَ رَسُولِ اللَّهِ ﷺ بِذِي الْحُلَيْفَةِ مِنْ تِهَامَةَ، فَأَصَابَ النَّاسَ جُوعٌ فَأَصَابُوا إبِلاً وَغَنَماً، وَكَانَ النَّبِيُّ ﷺ فِي أُخْرَيَاتِ الْقَوْمِ، فَعَجِلُوا وَذَبَحُوا وَنَصَبُوا الْقُدُورَ فَأَمَرَ النَّبِيُّ ﷺ بِالْقُدُورِ فَأُكْفِئَتْ، ثُمَّ قَسَمَ فَعَدَلَ عَشَرَةً مِنْ الْغَنَمِ بِبَعِيرٍ، فَنَدَّ مِنْهَا بَعِيرٌ فَطَلَبُوهُ فَأَعْيَاهُمْ، وَكَانَ فِي الْقَوْمِ خَيْلٌ يَسِيرَةٌ فَأَهْوَى رَجُلٌ مِنْهُمْ بِسَهْمٍ، فَحَبَسَهُ اللَّهُ. فَقَالَ: إنَّ لِهَذِهِ الْبَهَائِمِ أَوَابِدَ كَأَوَابِدِ الْوَحْشِ، فَمَا نَدَّ عَلَيْكُمْ مِنْهَا فَاصْنَعُوا بِهِ هَكَذَا، قُلْتُ: يَا رَسُولُ اللَّهِ، إنَّا لاقُو الْعَدُوِّ غَداً، وَلَيْسَ مَعَنَا مُدىً. أَفَنَذْبَحُ بِالْقَصَبِ؟ قَالَ: مَا أَنْهَرَ الدَّمَ، وَذُكِرَ اسْمُ اللَّهِ عَلَيْهِ، فَكُلُوهُ، لَيْسَ السِّنَّ وَالظُّفْرَ، وَسَأُحَدِّثُكُمْ عَنْ ذَلِكَ، أَمَّا السِّنُّ: فَعَظْمٌ، وَأَمَّا الظُّفْرُ: فَمُدَى الْحَبَشَةِ).","ރާފިޢު ބިން ޚަދީޖު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ތިހާމާގެ ޛުލް ޙުލައިފާގައި ރަސޫލު ﷲ ﷺގެ އަރިހު ތިމަންމެން ވީމެވެ. އަދި މީސްތަކުންނަށް ބަނޑުހައި ހޫނުކަން ޖެހުނެވެ. އެއަށްފަހު (ޣަނީމާ މުދަލުގެ ގޮތުގައި) ޖަމަލާއި ބަކަރި ލިބުނެވެ. އޭރު ނަބިއްޔާ ﷺ ވަނީ ލަޝްކަރުގެ ފަހަތުގައެވެ. ދެން މީސްތަކިން އަވަސްވެގަނެގެން (ޣަނީމާ މުދާ ބެހުމުގެ ކުރިން އެ ޖަނަވާރުތައް) ކަތިލައި (ކެއްކުމަށް) ތެލިތަކަށް އަޅައިފިއެވެ. އެކަމަކު ނަބިއްޔާ ﷺ އަމުރު ކުރެއްވުމުން އެ ތެލިތަށް ބަނޑުން ޖަހައިފިއެވެ. އެއަށްފަހު (ޣަނީމާ މުދާ) ބަހާލެއްވިއެވެ. އަދި ދިހަ ބަކަރި އެއް ޖަމަލާ އެއްވަރުކަމަށް ބެއްލެވިއެވެ. ދެން އެތަނުން ޖަމެއް ފިލާ ރައްކާވެއްޖެއެވެ. އެބައިމީހުން އޭތި ފަހާ ދުވުމުންވެސް އަތުލައިގަނެވޭ ގޮތެއް ނުވިއެވެ. އޭރު މީސްތަކުން ގެންގުޅުނީ މަދު އަސްތަކެކެވެ. ދެން އޭގެ ތެރެއިން މީހަކު ތީރެއް އެއްލުމުން، ﷲ އެ ޖަމަލު ހުއްޓުވައިފިއެވެ. ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން މި ނައަމްސޫފިތަކު ތެރޭގައި، ވަލުޖަނަވާރު ފަދައިން ވަލުކަމެއް ހުންނަ އެއްޗެހި ވެއެވެ. ފަހެ އޭގެ ތެރެއިން ތިޔަބައިމީހުންގެ ކިބައިން ފިލައި ރައްކާވާ އެއްޗަކަށް މިފަދައިން ކަންތައް ކުރާށެވެ.“ ރާފިޢު ދެންނެވިއެވެ: އޭ ﷲގެ ރަސޫލާއެވެ. މާދަމާ ތިމަންމެން އަދުއްވުންނާ ކުރިމަތިލާނެކަން ކަށަވަރެވެ. އަދި ތިމަންމެން އަތުގައި ވަޅިއެއް ނެތެވެ. ފަހެ އޮނު (ދަނޑީގެ ތުނުކޮޅުން ޖަނަވާރު) ކަތިލެވިދާނެތޯއެވެ؟ ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ އެއްޗަކުން ލޭ އޮހޮރުވާލާ، އަދި އޭގެ މައްޗަށް ﷲގެ އިސްމުފުޅު ކިޔެވިފައިވާ ކޮންމެ އެއްޗެއް ވެސް ތިޔަބައިމީހުން ކާށެވެ. އެއީ (ޖަނަވާރުގެ) ދަތްތަކާއި ނިޔަފަތިތައް ފިޔަވައެވެ. އެ ތަކެއްޗާ ބެހޭ ގޮތުން ތިޔަބައިމީހުންނަށް ކިޔައިދެއްވާނަމެވެ. ދަތަކީ ކައްޓެވެ. އަދި ނިޔަފައްޗަކީ ހަބަޝާގެ މީހުން ވަޅީގެ ގޮތުގައި ބޭނުން ކުރާ އެއްޗެވެ.“ (އެބަހީ: ކައްޓަކީ ކަތިލަން ކަމުދާ އެއްޗެއް ނޫނެވެ. އަދި ނިޔަފައްޗަކީ ކާފަރުންގެ ބަޔަކު ބޭނުން ކުރާ އެއްޗެކެވެ.)","[البخاري 2488، ومسلم 1968]"],["#397","كتاب الأطعمة","ކާތަކެތީގެ ފޮތް","باب الأضاحي","އުޟްހިޔާގެ ބާބު","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (ضَحَّى النَّبِيُّ ﷺ بِكَبْشَيْنِ أَمْلَحَيْنِ أَقَرْنَيْنِ ذَبَحَهُمَا بِيَدِهِ، وَسَمَّى وَكَبَّرَ وَوَضَعَ رِجْلَهُ عَلَى صِفَاحِهِمَا).\r\nالأَمْلَح: الأَغبَرُ وهو الذي فيهِ سوادٌ وبياضٌ.","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ދަޅު ފަޅާފައި ހުރި، ހުދާ ކަޅު ކުލައިގެ ދެ ކަންބަޅި ނަބިއްޔާ ﷺ އުޟްހިޔާގެ ގޮތުގައި ގުރުބާން ކުރެއްވިއެވެ. ނަބިއްޔާ އެ ކަތިލެއްވީ އަމިއްލަ އަތްޕުޅުންނެވެ. އަދި ﷲގެ އިސްމުފުޅާއި ތަކްބީރު ކިޔުއްވިއެވެ. އަދި ނަބިއްޔާގެ ފައިންޕުޅު އޭގެ ކަރުގެ އަރިމަތީގައި އެޅުއްވިއެވެ.","[البخاري 5565، ومسلم 1966]"],["#398","كتاب الأشربة","ބޯތަކެތީގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا (أَنَّ عُمَرَ قَالَ - عَلَى مِنْبَرِ رَسُولِ اللَّهِ ﷺ أَمَّا بَعْدُ، أَيُّهَا النَّاسُ، إنَّهُ نَزَلَ تَحْرِيمُ الْخَمْرِ وَهِيَ مِنْ خَمْسَةٍ: مِنْ الْعِنَبِ، وَالتَّمْرِ، وَالْعَسَلِ، وَالْحِنْطَةِ، وَالشَّعِيرِ. وَالْخَمْرُ: مَا خَامَرَ الْعَقْلَ ثَلاثٌ وَدِدْتُ أَنَّ رَسُولَ اللَّهِ ﷺ كَانَ عَهِدَ إلَيْنَا فِيهَا عَهْداً نَنْتَهِي إلَيْهِ: الْجَدُّ، وَالْكَلالَةُ، وَأَبْوَابٌ مِنْ الرِّبَا).","ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺގެ މިންބަރުކޮޅުގައި ޢުމަރު ވިދާޅުވިއެވެ: އެއަށްފަހު ދަންނާށެވެ. އޭ މީސްތަކުންނޭވެ. ހަމަކަށަވަރުން ރާ ހަރާމް ކުރެއްވިކަމުގެ (އާޔަތްތައް) ބާވައިލެވުނު އިރު، ރާ ހަދައިއުޅުނީ ފަސް އެއްޗަކުންނެވެ. އެއީ މޭބިސްކަދުރާއި، ކަދުރާއި، މާމުޔާއި، ގޮދަނާއި، ހިމަގޮދަނެވެ. އެހެނަސް ރަލަކީ ބުއްދި ފިލުވާލާ ކޮންމެ އެއްޗެކެވެ. އަދި ތިން ކަމަކާމެދު ރަސޫލު ﷲ ﷺ ތިމަންމެންނަށް އިތުރަށް ބަޔާން ކުރެއްވިނަމައޭ ތިމަންނަ ހިތްޕުޅަށް އަރައިވަޑައިގަންނަވައެވެ. އެއީ ކާފައަކަށް ވާރުތަވުމުގެ މައްސަލައާއި، ދަރިއަކު ނެތި މަރުވާމީހާގެ ވާރުތަ މުދަލުގެ މައްސަލައާއި، ރިބާގެ ބައެއް މައްސަލަތަކެވެ. (ނޯޓު: އެއީ ސަހާބީންގެ މެދުގައި ޚިލާފު އުފެދުނު ބައެއް ފަރުޢީ މައްސަލަތަކެކެވެ.)","[البخاري 5588، ومسلم 3032 واللفظ له]"],["#399","كتاب الأشربة","ބޯތަކެތީގެ ފޮތް","","","عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهُا: (أَنَّ رَسُولَ اللَّهِ ﷺ سُئِلَ عَنْ الْبِتْعِ؟ فَقَالَ: كُلُّ شَرَابٍ أَسْكَرَ فَهُوَ حَرَامٌ).","ޢާއިޝާ رَضِيَ اللَّهُ عَنْهَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ބިތުޢުއޭ ކިޔުނު ބުއިމަކާ ބެހޭ ގޮތުން ނަބިއްޔާ ﷺގެ އަރިހުގައި ސުވާލު ކުރެވުނެވެ. (ނޯޓު: ބިތުޢުއަކީ ފެނަށް މާމުއި އަޅައިގެން ހަދާ ރަލެކެވެ.) ނަބިއްޔާ ޙަދީޘް ކުރެއްވިއެވެ: ”މަސްތު ކުރުވާ ކޮންމެ ބޯއެއްޗެއް ހަރާމްވެގެން ވެއެވެ.“","[البخاري 5585، ومسلم 2001]"],["#400","كتاب الأشربة","ބޯތަކެތީގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عَبَّاسٍ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (بَلَغَ عُمَرَ: أَنَّ فُلاناً بَاعَ خَمْراً فَقَالَ: قَاتَلَ اللَّهُ فُلاناً، أَلَمْ يَعْلَمْ أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: (قَاتَلَ اللَّهُ الْيَهُودَ، حُرِّمَتْ عَلَيْهِمْ الشُّحُومُ، فَجَمَلُوهَا فَبَاعُوهَا؟).","ޢަބްދު ﷲ ބިން ޢައްބާސް رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: މިވެނި މީހަކު ރާ ވިއްކިކަމަށް، ޢުމަރުގެފާނާ ހަމައަށް ޚަބަރު ލިބުނެވެ. ދެން ޢުމަރުގެފާނު ވިދާޅުވިއެވެ: ﷲ މިވެނި މީހަކު ގަތުލު ކުރައްވާށިއެވެ. ރަސޫލު ﷲ ﷺ މިފަދައިން ޙަދީޘް ކުރެއްވިކަން އޭނާއަށް ހަމަ ނޭނގެނީހެއްޔެވެ: ”قَاتَلَ اللَّهُ الْيَهُودَ (ﷲ ޔަހޫދީން ގަތުލު ކުރައްވާށިއެވެ.) ހަމަކަށަވަރުން ﷲ އެބައިމީހުންނަށް ސަރުބީ ހަރާމް ކުރެއްވުމުން، އެބައިމީހުން އެ ސަރުބީ ވިރުވައި ތެލަށް ހަދައިގެން ވިއްކިއެވެ.“ (އެބަހީ: އޭގެން ލިބުނު ފައިސާ ބޭނުން ކުރިއެވެ.)","[البخاري 2223 واللفظ له، ومسلم 1582]"],["#401","كتاب اللباس","ފޭރާމުގެ ފޮތް","","","عَنْ عُمَرَ بْنِ الْخَطَّابِ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: (لا تَلْبَسُوا الْحَرِيرَ، فَإِنَّهُ مَنْ لَبِسَهُ فِي الدُّنْيَا لَمْ يَلْبَسْهُ فِي الآخِرَةِ).","ޢުމަރު ބިން އަލްޚައްތާބު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިޔަބައިމީހުން (އެބަހީ: ފިރިހެނުން) ފަށުވިފޭރާން ނުލާށެވެ. އެހެނީ ހަމަކަށަވަރުން ދުނިޔެ މަތީ އެ ލާ މީހަކު، އާޚިރަތުގައި އެޔެއް ނުލެވޭނެއެވެ.“","[البخاري 5843، ومسلم 2069 واللفظ له]"],["#402","كتاب اللباس","ފޭރާމުގެ ފޮތް","","","عَنْ حُذَيْفَةَ بْنِ الْيَمَانِ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: سَمِعْتُ رَسُولَ اللَّهِ ﷺ يَقُولُ: (لا تَلْبَسُوا الْحَرِيرَ وَلا الدِّيبَاجَ، وَلا تَشْرَبُوا فِي آنِيَةِ الذَّهَبِ وَالْفِضَّةِ وَلا تَأْكُلُوا فِي صِحَافِهِمَا، فَإِنَّهَا لَهُمْ فِي الدُّنْيَا وَلَكُمْ فِي الآخِرَةِ).","ޙުޒައިފާ ބިން އަލްޔަމާން رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރައްވަނިކޮށް ތިމަން އަޑުއެހީމެވެ: ”ތިޔަބައިމީހުން ފަށުވިފޭރާން ނުލާށެވެ. އަދި ދީބާޖު (ކިޔުނު ފަށުވިފޭރާން) ވެސް ނުލާށެވެ. އަދި ރަނާ ރިހީގެ ކަންވާރުތަކުން ނުބޯށެވެ. އަދި އޭގެ ތަށިތަކުން ނުކާށެވެ. އެހެނީ ހަމަކަށަވަރުން ދުނިޔެ މަތީގައި އެ ތަކެތި ވަނީ އެބައިމީހުން (ކާފަރުންނާށެވެ.) އަދި އާޚިރަތުގައި (އެ ތަކެތި ވާނީ) ތިޔަބައިމީހުންނަށެވެ.“","[البخاري 5426، 5638، ومسلم 2067]"],["#403","كتاب اللباس","ފޭރާމުގެ ފޮތް","","","عَنْ الْبَرَاءِ بْنِ عَازِبٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (مَا رَأَيْتُ مِنْ ذِي لِمَّةٍ فِي حُلَّةٍ حَمْرَاءَ أَحْسَنَ مِنْ رَسُولِ اللَّهِ ﷺ، لَهُ شَعَرٌ يَضْرِبُ مَنْكِبَيْهِ، بَعِيدُ مَا بَيْنَ الْمَنْكِبَيْنِ، لَيْسَ بِالْقَصِيرِ وَلا بِالطَّوِيلِ).","އަލްބަރާއު ބިން ޢާޒިބު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އިސްތަށިކޮޅު ދިގު، ރަތް ކުލައިގެ ހެދުންކޮޅެއް ތުރުކުރެއްވި، ރަސޫލު ﷲ ﷺ ހާ ރިވެތި ބޭކަލެއް ތިމަން ނުދެކެމެވެ. ނަބިއްޔާގެ ދެ ކޮނޑުފުޅާ ހަމައަށް އިސްތަށިކޮޅު ފައިބާފައި ހުއްޓެވެ. އަދި ނަބިއްޔާ ބައިމަތިފުޅާވެގެން ވެއެވެ. އަދި ނަބިއްޔާވީ މާ ދިގު އަދި ކުރުވެސް ބޭކަލަކަށް ނޫނެވެ. (އެބަހީ: އިސްކޮޅުގައި މެދުމިން ބޭކަލަކަށެވެ.)","[الترمذي 1724 واللفظ له، ورواه البخاري 3551 ومسلم 2337 بنحوه]"],["#404","كتاب اللباس","ފޭރާމުގެ ފޮތް","","","عَنْ الْبَرَاءِ بْنِ عَازِبٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (أَمَرْنَا رَسُولُ اللَّهِ ﷺ بِسَبْعٍ، وَنَهَانَا عَنْ سَبْعٍ: أَمَرْنَا بِعِيَادَةِ الْمَرِيضِ، وَاتِّبَاعِ الْجِنَازَةِ، وَتَشْمِيتِ الْعَاطِسِ، وَإِبْرَارِ الْقَسَمِ (أَوْ الْمُقْسِمِ)، وَنَصْرِ الْمَظْلُومِ، وَإِجَابَةِ الدَّاعِي، وَإِفْشَاءِ السَّلامِ. وَنَهَانَا عَنْ خَوَاتِيمَ - أَوْ عَنْ تَخَتُّمٍ - بِالذَّهَبِ، وَعَنْ الشُّرْبِ بِالْفِضَّةِ، وَعَنْ الْمَيَاثِرِ، وَعَنْ الْقَسِّيِّ، وَعَنْ لُبْسِ الْحَرِيرِ، وَالإِسْتَبْرَقِ، وَالدِّيبَاجِ).","އަލްބަރާއު ބިން ޢާޒިބު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ހަތް ކަމެއް ކުރުމަށް ރަސޫލު ﷲ ﷺ ތިމަންމެންނަށް އަމުރު ކުރެއްވިއެވެ. އަދި ހަތް ކަމެއް ކުރުމުން ތިމަންމެންނަށް މަނާ ކުރެއްވިއެވެ. ތިމަންމެން ކުރަން އަމުރު ކުރެއްވި ކަންތަކަކީ: ބަލި މީހާއަށް ޒިޔާރަތް ކުރުމާއި، ޖަނާޒާގައި ބައިވެރިވުމާއި، (އެހެން މީހަކު) ކިނބިއްސެއް އެޅުމާއެކީ ހަމްދުކޮށްފި ނަމަ އޭނާއަށް ރަހުމަތް ލެއްވުން އެދި ދުއާ ކުރުމާއި، ހުވާ ފުއްދުމާއި، އަނިޔާ ލިބިގެންވާ މީހާއަށް ނަސްރު ދިނުމާއި، (މީހަކު) ދައުވަތެއް ދީފި ނަމަ އެއަށް އިޖާބަ ދިނުމާއި، ސަލާމް ފެތުރުމެވެ. އަދި ތިމަންމެން ކުރުން މަނާ ކުރެއްވި ކަންތަކަކީ: ރަން އަނގޮށްޓި އެޅުމާއި، ރިހި ކަންވާރުތަކުން ބުއިމާއި، ފަށުވިން ހަދާފައިވާ އަސްދާނުގޮނޑި ބޭނުން ކުރުމާއި، ގިއްސީއޭ ކިޔުނު ފަށުވި ބޭނުން ކުރުމާއި، ފަށުވި ފޭރާން ލުމާއި، އިސްތަބްރަގާއި ދީބާޖޭ ކިޔުނު ފަށުވި ބޭނުން ކުރުމެވެ.","[البخاري 1239، ومسلم 2066 واللفظ له]"],["#405","كتاب اللباس","ފޭރާމުގެ ފޮތް","","","عَنْ ابْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا (أَنَّ رَسُولَ اللَّهِ ﷺ اصْطَنَعَ خَاتَماً مِنْ ذَهَبٍ، فَكَانَ يَجْعَلُ فَصَّهُ فِي بَاطِنِ كَفِّهِ إذَا لَبِسَهُ، فَصَنَعَ النَّاسُ كَذَلِكَ، ثُمَّ إنَّهُ جَلَسَ عَلَى الْمِنْبَرِ فَنَزَعَهُ فَقَالَ: إنِّي كُنْتُ أَلْبَسُ هَذَا الْخَاتَمَ، وَأَجْعَلُ فَصَّهُ مِنْ دَاخِلٍ، فَرَمَى بِهِ ثُمَّ قَالَ: وَاَللَّهِ لا أَلْبَسُهُ أَبَداً فَنَبَذَ النَّاسُ خَوَاتِيمَهُمْ).\n\nوَفِي لَفْظٍ (جَعَلَهُ فِي يَدِهِ الْيُمْنَى).","އަދި ޢަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ އަށް ރަނުން އަނގޮޓިކޮޅެއް ހެދުމަށް އެންގެވިއެވެ. އަދި އެ ތުރުކުރައްވާ ހިނދު، އޭގެ އެސް ބެހެއްޓެވީ އަތްޕުޅުގެ އެތެރެއަށްވާ ގޮތަށެވެ. ދެން މިސްތަކުންވެސް ހަމަ އެފަދައިން ކަންތައް ކުރެއްވިއެވެ. އެއަށްފަހު ހަމަކަށަވަރުން މިންބަރުކޮޅު މަތީ އިށީނދެވަޑައިގެން އެ އަނގޮޓިކޮޅު (އަތްޕުޅުން) ނަންގަވައިފިއެވެ. އަދި ޙަދީޘް ކުރެއްވިއެވެ: ”ހަމަކަށަވަރުން ތިމަން ނަބިއްޔާ މި އަނގޮޓިކޮޅު ތުރުކުރައްވައި އުޅުއްވީމެވެ. އަދި އޭގެ އެސް ބެހެއްޓެވީ (އަތުގެ) އެތެރެއަށްވާ ގޮތަށެވެ.“ ދެން އެ (ރަން) އަނގޮޓިކޮޅު އުކާލައްވާފައި ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲ ގަންދީ ބުނަމެވެ. ދުވަހަކުވެސް އެޔެއް ނާޅުއްވާނަމެވެ.“ އެއަށްފަހު މީސްތަކުން ވެސް އެބައިމީހުންގެ (ރަން) އަނގޮޓިތައް އުކާލައިފިއެވެ.\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ނަބިއްޔާ އަނގޮޓިކޮޅު އެޅުއްވީ ކަނާތުގައެވެ.","[البخاري 6651، ومسلم 2091 واللفظ له\n\nالبخاري 5876، ومسلم 2091]"],["#406","كتاب اللباس","ފޭރާމުގެ ފޮތް","","","عَنْ عُمَرَ بْنِ الْخَطَّابِ - رَضِيَ اللَّهُ عَنْهُ - أَنَّ رَسُولَ اللَّهِ ﷺ (نَهَى عَنْ لُبُوسِ الْحَرِيرِ إلاَّ هَكَذَا، وَرَفَعَ لَنَا رَسُولُ اللَّهِ ﷺ أُصْبُعَيْهِ: السَّبَّابَةَ، وَالْوُسْطَى). \n\nوَلِمُسْلِمٍ (نَهَى رَسُولُ اللَّهِ ﷺ عَنْ لُبْسِ الْحَرِيرِ إلاَّ مَوْضِعَ أُصْبُعَيْنِ، أَوْ ثَلاثٍ، أَوْ أَرْبَعٍ).","ޢުމަރު ބިން އަލްޚައްޠާބު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ފަށުވި ފޭރާން ލުން ރަސޫލު ﷲ ﷺ މަނާ ކުރެއްވިއެވެ. އެއީ މިވަރު މެނުވީއެވެ. އަދި ރަސޫލު ﷲ ﷺގެ ޝަހާދަތު އިނގިލިފުޅާއި މެދު އިނގިލިފުޅު، ތިމަންމެންނަށް (ދެއްކެވުމަށް) އުފުއްލެވިއެވެ. (އެބަހީ: ހެދުމުގައި އެ އިނގިލިތަކުގެ ޖާގައަށް ވާވަރަށް ފަށުވި ބޭނުން ކުރުން ހުއްދައެވެ.)\n\nއަދި މުސްލިމުގެ ރިވާޔަތެއްގައި ވެއެވެ: ފަށުވި ފޭރާން ލުން ރަސޫލު ﷲ ﷺ މަނާ ކުރެއްވިއެވެ. އެއީ ދެއެއް، ނުވަތަ ތިނެއް، ނުވަތަ ހަތަރު އިނގިލީގެ މިންވަރަށް މެނުވީއެވެ.","[البخاري 5828، 5829، ومسلم 2069\n\nومسلم 2069]"],["#407","كتاب الجهاد","ޖިހާދުގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ أَبِي أَوْفَى - رَضِيَ اللَّهُ عَنْهُ -: أَنَّ رَسُولَ اللَّهِ ﷺ فِي بَعْضِ أَيَّامِهِ الَّتِي لَقِيَ فِيهَا الْعَدُوَّ - انْتَظَرَ، حَتَّى إذَا مَالَتِ الشَّمْسُ قَامَ فِيهِمْ، فَقَالَ: (أَيُّهَا النَّاسُ، لا تَتَمَنَّوْا لِقَاءَ الْعَدُوِّ، وَاسْأَلُوا اللَّهَ الْعَافِيَةَ فَإِذَا لَقِيتُمُوهُمْ فَاصْبِرُوا، وَاعْلَمُوا أَنَّ الْجَنَّةَ تَحْتَ ظِلالِ السُّيُوفِ ثُمَّ قَالَ النَّبِيُّ ﷺ: اللَّهُمَّ مُنْزِلَ الْكِتَابِ، وَمُجْرِيَ السَّحَابِ، وَهَازِمَ الأَحْزَابِ: اهْزِمْهُمْ، وَانْصُرْنَا عَلَيْهِمْ).","ޢަބްދު ﷲ ބިން އަބީ އައުފާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން އަދުއްވުންނާ ބައްދަލުވި ދުވަހަކު، އިރު މެދުން ފިލައްޖެއުމަށް ދާންދެން ރަސޫލު ﷲ ﷺ މަޑުކުރައްވައެވެ. އެއަށްފަހު ޙަދީޘް ކުރައްވައެވެ: ”އޭ މީސްތަކުންނެވެ. އަދުއްވުންނާ ކުރިމަތިލާން ތިޔަބައިމީހުން ނޭދޭށެވެ. އަދި ސަލާމަތްކަން ދެއްވުން އެދި ﷲއަށް ދުއާ ދަންނަވާށެވެ. އަދި އެބައިމީހުންނާ ދިމާވެއްޖެ ނަމަ ފަހެ ތިޔަބައިމީހުން ކެތްތެރިވާށެވެ. އަދި ސުވަރުގެ ވަނީ ކަނޑިތަކުގެ ހިޔަލުގައިކަން ތިޔަބައިމީހުން ދަންނާށެވެ.“ އެއަށްފަހު ނަބިއްޔާ ވިދާޅުވިއެވެ: ”اللَّهُمَّ مُنْزِلَ الْكِتَابِ، وَمُجْرِيَ السَّحَابِ، وَهَازِمَ الأَحْزَابِ: اهْزِمْهُمْ، وَانْصُرْنَا عَلَيْهِمْ (ޔާ ﷲ! އިބަ އިލާހީ ފޮތް ބާވައިލެއްވި އިލާހެވެ. އަދި ވިލާ ހިންގަވާ އިލާހެވެ. އަދި ބައިގަނޑުތައް ބަލި ކުރައްވާ އިލާހެވެ. އިބަ އިލާހު އެބައިމީހުން ބަލި ކުރައްވާށިއެވެ. އަދި އެބައިމީހުންގެ މައްޗަށް އަޅަމެންނަށް ނަސްރު ދެއްވާށިއެވެ.)“","[البخاري 2965، 2966، ومسلم 1742]"],["#408","كتاب الجهاد","ޖިހާދުގެ ފޮތް","","","عَنْ سَهْلِ بْنِ سَعْدٍ - رَضِيَ اللَّهُ عَنْهُ - أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: (رِبَاطُ يَوْمٍ فِي سَبِيلِ اللَّهِ خَيْرٌ مِنْ الدُّنْيَا وَمَا عَلَيْهَا، وَمَوْضِعُ سَوْطِ أَحَدِكُمْ فِي الْجَنَّةِ خَيْرٌ مِنْ الدُّنْيَا وَمَا عَلَيْهَا، وَالرَّوْحَةُ يَرُوحُهَا الْعَبْدُ فِي سَبِيلِ اللَّهِ وَالْغَدْوَةُ خَيْرٌ مِنْ الدُّنْيَا وَمَا فِيهَا).","ސަހްލު ބިން ސަޢުދު އައްސާޢިދީ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ﷲގެ މަގުގައި އެއް ދުވަހު ރިބާޠުކުރުން މި ދުނިޔެއާއި އޭގައި ވާހައި އެއްޗަކަށް ވުރެ ހެޔޮވެގެންވެއެވެ. (ނޯޓު: ރިބާޠަކީ މުސްލިމުންނާ ކާފަރުންގެ ދެމެދުގައިވާ ބޯޑަރު ބެލެހެއްޓުމެވެ.) އަދި ސުވަރުގޭގައި ތިޔަބައިމީހުންގެ ތެރެއިން މީހެއްގެ ޗާބުކެއްގެ މިންވަރު ވެސް، ދުނިޔެއާއި އޭގައި ވާހައި އެއްޗަކަށް ވުރެ ހެޔޮވެގެންވެއެވެ. އަދި ﷲގެ މަގުގައި ހެނދުނުން މެނދުރަށް، ނުވަތަ މެނދުރުން ރޭގަނޑަށް އަޅަކު (ޖިހާދު ކުރުމުގައި) ހޭދާކޮށްލާ ވަގުތުކޮޅު ވެސް، ދުނިޔެއާއި އޭގައި ވާހައި އެއްޗަކަށް ވުރެ ހެޔޮވެގެންވެއެވެ.","[البخاري 2892 واللفظ له، ومسلم 1881]"],["#409","كتاب الجهاد","ޖިހާދުގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - عَنْ النَّبِيِّ ﷺ قَالَ: (انْتَدَبَ اللَّهُ (وَلِمُسْلِمٍ: تَضَمُّنَ اللَّهُ) لِمَنْ خَرَجَ فِي سَبِيلِهِ، لا يُخْرِجُهُ إلاَّ جِهَادٌ فِي سَبِيلِي، وَإِيمَانٌ بِي، وَتَصْدِيقٌ بِرُسُلِي فَهُوَ عَلِيّ ضَامِنٌ: أَنْ أُدْخِلَهُ الْجَنَّةَ، أَوْ أُرْجِعَهُ إلَى مَسْكَنِهِ الَّذِي خَرَجَ مِنْهُ، نَائِلاً مَا نَالَ مِنْ أَجْرٍ أَوْ غَنِيمَةٍ).","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲގެ މަގުގައި ނުކުންނަ މީހަކަށް އެ އިލާހު ޖާމިނުވެ ވޮޑިގެންނެވިއެވެ: އޭނާ ނެރުނު ހަމައެކަނި ކަމަކީ ތިމަން އިލާހުގެ މަގުގައި ޖިހާދު ކުރުމާއި، ތިމަން އިލާހަށް އީމާންވުމާއި، އަދި ތިމަން އިލާހުގެ ރަސޫލުން ތެދުކުރުން ކަމުގައި ވަނީނަމަ، ފަހެ ތިމަން އިލާހު އޭނާ ސުވަރުގެ ވައްދަވާނަމެވެ. ނޫނީ އޭނާ އެ ހޯދައިގަތް އަޖުރު ނުވަތަ ޣަނީމާ މުދަލަކައިގެން އެނބުރި އެނާ ނުކުތް ގެއަށް ފޮނުއްވާނަމެވެ.“","[البخاري 36، ومسلم 1876 واللفظ له]"],["#410","كتاب الجهاد","ޖިހާދުގެ ފޮތް","","","وَلِمُسْلِمٍ: (مَثَلُ الْمُجَاهِدِ فِي سَبِيلِ اللَّهِ -، وَاَللَّهُ أَعْلَمُ بِمَنْ جَاهَدَ فِي سَبِيلِهِ - كَمَثَلِ الصَّائِمِ الْقَائِمِ، وَتَوَكَّلَ اللَّهُ لِلْمُجَاهِدِ فِي سَبِيلِهِ إنْ تَوَفَّاهُ: أَنْ يُدْخِلَهُ الْجَنَّةَ، أَوْ يُرْجِعَهُ سَالِماً مَعَ أَجْرٍ أَوْ غَنِيمَةٍ).","އަދި މުސްލިމުގެ ރިވާޔަތުގައި ވެއެވެ: ”ﷲގެ މަގުގައި ޖިހާދު ކުރާ މީހާގެ މިސާލަކީ، -އަދި އެ އިލާހުގެ މަގުގައި ޖިހާދު ކުރަނީ ކާކުކަން ﷲ މޮޅަށް ދެނެވޮޑިގެންވެއެވެ- (ހުއްޓުމެއް ނެތި) ނަމާދުކޮށް ރޯދަހިފާ އުޅޭ މީހާގެ މިސާލެވެ. އަދި ﷲގެ މަގުގައި ޖިހާދު ކުރާ މީހަކަށް، އޭނާ ސުވަރުގެ ވައްދަވާނެކަމަށް، ނޫނީ ސަލާމަތްކަންމަތީ އަޖުރު ނުވަތަ ޣަނީމާ މުދަލައިގެން އެނބުރި ގެންނަވާނެކަމަށް ﷲ ވަނީ ޖާމިނުވެ ވޮޑިގަންނަވާފައެވެ.“","[البخاري 2787]"],["#411","كتاب الجهاد","ޖިހާދުގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: (مَا مِنْ مَكْلُومٍ يُكْلَمُ فِي سَبِيلِ اللَّهِ، إلاَّ جَاءَ يَوْمَ الْقِيَامَةِ، وَكَلْمُهُ يَدْمَى: اللَّوْنُ لَوْنُ الدَّمِ، وَالرِّيحُ رِيحُ الْمِسْكِ).","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲގެ މަގުގައި (ޖިހާދު ކުރުމުގައި) މީހަކަށް ޒަހަމުވެއްޖެ ނަމަ، ގިޔާމަތް ދުވަހު އެ ޒަހަމުން ލޭ އޮހޮރި، އޭގެ ކުލައަކީ ހަމަ ލޭގެ ކުލައަށް ވެފައި، އަދި އޭގެ ވަހަކީ ގޮމަކަސްތޫރި ވަސް ކަމުގައިވާ ހާލު ނޫން ގޮތަކަށް އެ މީހަކު ނާންނާނެއެވެ.“","[البخاري 5533 واللفظ له، ومسلم 1876]"],["#412","كتاب الجهاد","ޖިހާދުގެ ފޮތް","","","عَنْ أَبِي أَيُّوبَ الأَنْصَارِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: (غَدْوَةٌ فِي سَبِيلِ اللَّهِ، أَوْ رَوْحَةٌ: خَيْرٌ مِمَّا طَلَعَتْ عَلَيْهِ الشَّمْسُ وَغَرَبَتْ) أَخْرَجَهُ مُسْلِمٌ.","އަބޫ އައްޔޫބު އަލްއަންޞާރީ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲގެ މަގުގައި ހެނދުނުން މެނދުރަށް، ނުވަތަ މެނދުރުން ރޭގަނޑަށް (ޖިހާދު ކުރުމުގައި) ހޭދާކޮށްލާ ވަގުތުކޮޅު ވެސް، އިރު އަރައި އޮއްސުނު ހުރިހާ އެއްޗަކަށް ވުރެ ހެޔޮވެގެންވެއެވެ.“","[مسلم 1883]"],["#413","كتاب الجهاد","ޖިހާދުގެ ފޮތް","","","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: قَالَ رَسُولُ اللَّهِ ﷺ: (غَدْوَةٌ فِي سَبِيلِ اللَّهِ، أَوْ رَوْحَةٌ: خَيْرٌ مِنْ الدُّنْيَا وَمَا فِيهَا) أَخْرَجَهُ الْبُخَارِيُّ.","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲގެ މަގުގައި ހެނދުނުން މެނދުރަށް، ނުވަތަ މެނދުރުން ރޭގަނޑަށް (ޖިހާދު ކުރުމުގައި) ހޭދާކޮށްލާ ވަގުތުކޮޅު ވެސް، ދުނިޔެއާއި އޭގައި ވާހައި އެއްޗަކަށް ވުރެ ހެޔޮވެގެންވެއެވެ.“","[البخاري 6568، ومسلم 1880]"],["#414","كتاب الجهاد","ޖިހާދުގެ ފޮތް","","","عَنْ أَبِي قَتَادَةَ الأَنْصَارِيِّ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (خَرَجْنَا مَعَ رَسُول اللَّه ﷺ إلَى حُنَيْنٍ - وَذَكَرَ قِصَّةً - فَقَالَ رَسُولُ اللَّهِ ﷺ: مَنْ قَتَلَ قَتِيلاً لَهُ عَلَيْهِ بَيِّنَةٌ فَلَهُ سَلَبُهُ) قَالَهَا ثَلاثاً.","އަބޫ ގަތާދާ އަލްއަންޞާރީ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺގެ އަރިހުގައި ތިމަންމެން ޙުނައިނަށް ނުކުތީމެވެ. –އަދި އެ ވާހަކަ ކިޔައިދިނެވެ- ފަހެ ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”މީހަކު (ހަނގުރާމައިގައި ކާފަރު) މީހަކު ގަތުލުކޮށްފި ނަމަ، އަދި އޭނާއަށް އެކަމުގެ މައްޗަށް ހެކިވާ ނަމަ، ފަހެ އެ ގަތުލު ކުރެވުނު މީހާގެ އަތުގައި ހުރި އެއްޗެއް ލިބިގެންވަނީ އޭނާ ގަތުލު ކުރި މީހާއަށެވެ.“ އެފަދައިން ތިން ފަހަރު ޙަދީޘް ކުރެއްވިއެވެ.","[البخاري 3142، ومسلم 1751]"],["#415","كتاب الجهاد","ޖިހާދުގެ ފޮތް","","","عَنْ سَلَمَةَ بْنِ الأَكْوَعِ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (أَتَى النَّبِيَّ ﷺ عَيْنٌ مِنْ الْمُشْرِكِينَ، وَهُوَ فِي سَفَرِهِ، فَجَلَسَ عِنْدَ أَصْحَابِهِ يَتَحَدَّثُ، ثُمَّ انْفَتَلَ، فَقَالَ النَّبِيُّ ﷺ: اُطْلُبُوهُ وَاقْتُلُوهُ فَقَتَلْتُهُ، فَنَفَّلَنِي سَلَبَهُ). \n\nفِي رِوَايَةٍ (فَقَالَ: مَنْ قَتَلَ الرَّجُلَ؟ فَقَالُوا: ابْنُ الأَكْوَعِ فَقَالَ: لَهُ سَلَبُهُ أَجْمَعُ).","ސަލަމާ ބިން އަލްއަކުވަޢު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ ދަތުރުފުޅެއްގައި ވަނިކޮށް، މުޝްރިކުންގެ ތެރެއިން ޖާސޫސަކު ނަބިއްޔާގެ އަރިހަށް އައެވެ. ދެން އޭނާ ނަބިއްޔާގެ ސަހާބީންނާއެކު އިށީނދެ ވާހަކަ ދައްކައި ހެދުމަށް ފަހު ހިނގައްޖެއެވެ. އެއަށްފަހު ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އޭނާ ހޯދައިގެން ގަތުލު ކުރާށެވެ.“ ފަހެ ތިމަންނަ ގޮސް އޭނާ ގަތުލުކޮށްފީމެވެ. އަދި އޭނާގެ އަތުގައި ހުރި އެއްޗެއް ނަބިއްޔާ ތިމަންނައަށް ދެއްވައިފިއެވެ.\n\nއަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އެ މީހާ ގަތުލު ކުރީ ކާކުތޯއެވެ؟“ މީސްތަކުން ދެންނެވިއެވެ: އިބްނުލް އަކްވަޢުއެވެ. ދެން ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”އެކަލޭގެފާނަށް އޭނާގެ އަތުގައި ހުރި ހުރިހާ އެއްޗެއް ލިބިގެންވެއެވެ.“","[البخاري 3051\n\nمسلم 1754]"],["#416","كتاب الجهاد","ޖިހާދުގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (بَعَثَ رَسُولُ اللَّهِ ﷺ سَرِيَّةً إلَى نَجْدٍ فَخَرَجَ فِيهَا، فَأَصَبْنَا إبِلاً وَغَنَماً، فَبَلَغَتْ سُهْمَانُنَا اثْنَيْ عَشَرَ بَعِيراً، وَنَفَّلَنَا رَسُولُ اللَّهِ ﷺ بَعِيراً بَعِيراً).","އަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ނަޖުދުކަރައަށް ލަޝްކަރެއް ފޮނުއްވިއެވެ. އަދި ތިމަން ވެސް އޭގައި ނުކުތީމެވެ. ދެން ތިމަންމެންނަށް ޖަމަލާއި ބަކަރި ޣަނީމާ މުދަލުގެ ގޮތުގައި ލިބުނެވެ. އެ ގޮތުން ތިމަންމެން ކުރެ ކޮންމެ މީހަކަށް ލިބުނު ބައިގައި ބާރަ ޖަމަލު ހަމަވިއެވެ. އަދި އޭގެ އިތުރަށް ވެސް ރަސޫލު ﷲ ﷺ ތިމަންމެން ކުރެ ކޮންމެ މީހަކަށް ޖަމަލެއް އިތުރު ކުރެއްވިއެވެ.","[البخاري 4338، ومسلم 1749 واللفظ له]"],["#417","كتاب الجهاد","ޖިހާދުގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا عَنْ النَّبِيِّ ﷺ قَالَ: (إذَا جَمَعَ اللَّهُ عَزَّ وَجَلَّ الأَوَّلِينَ وَالآخَرِينَ: يُرْفَعُ لِكُلِّ غَادِرٍ لِوَاءٌ، فَيُقَالُ: هَذِهِ غَدْرَةُ فُلانِ بْنِ فُلانٍ).","އަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”(ގިޔާމަތް ދުވަހު) އިސްވެ ދިޔަ މީހުންނާއި ފަހުން އަންނާނޭ މީހުން ﷲ އެއްކުރައްވާ ހިނދު، ޣައްދާރުވާ ކޮންމެ މީމަކަށް (އަލާމާތެއްގެ ގޮތުގައި) ދިދައެއް އުފުލެވިގެން ދާނެއެވެ. އަދި ބުނެވޭނެއެވެ: މިވެނި މީހެއްގެ ދަރި މިވެނި މީހާގެ ޣައްދާރުވުމަކީ މިއީއެވެ.“","[البخاري 6188، ومسلم 1735 واللفظ له]"],["#418","كتاب الجهاد","ޖިހާދުގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا (أَنَّ امْرَأَةً وُجِدَتْ فِي بَعْضِ مَغَازِي النَّبِيِّ ﷺ مَقْتُولَةً، فَأَنْكَرَ النَّبِيُّ ﷺ قَتْلَ النِّسَاءِ، وَالصِّبْيَانِ).","އަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާގެ ހަނގުރާމައެއްގައި، އަންހެނަކު ގަތުލު ކުރެވިފައިވަނިކޮށް ފެނިގެންދިޔައެވެ. ދެން އަންހެނުންނާއި ކުޑަކުދިން ގަތުލުކުރުން ނަބިއްޔާ ﷺ މަނާ ކުރެއްވިއެވެ.","[البخاري 3014، ومسلم 1744]"],["#419","كتاب الجهاد","ޖިހާދުގެ ފޮތް","","","عَنْ أَنَسِ بْنِ مَالِكٍ - رَضِيَ اللَّهُ عَنْهُ - (أَنَّ عَبْدَ الرَّحْمَنِ بْنَ عَوْفٍ، وَالزُّبَيْرَ بْنَ الْعَوَّامِ، شَكَوَا الْقَمْلَ إلَى رَسُولِ اللَّهِ ﷺ فِي غَزَاةٍ لَهُمَا فَرَخَّصَ لَهُمَا فِي قَمِيصِ الْحَرِيرِ وَرَأَيْته عَلَيْهِمَا).","އަނަސް ބިން މާލިކު رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ޢަބްދުއްރަޙްމާން ބިން ޢައުފާއި އައްޒުބައިރު ބިން އަލްއައްވާމް، އެ ދެ ބޭކަލުން ބައިވެރިވެވަޑައިގަތް ހަނގުރާމައެއްގައި، އުކުނުގެ އުނދަގޫވާތީ ރަސޫލު ﷲ ﷺގެ އަރިހުގައި ޝަކުވާ ދެންނެވިއެވެ. އެހެންކަމުން ނަބިއްޔާ އެ ދެ ބޭކަލުންނަށް ފަށުވި ގަމީސް ލުމަށް ހުއްދަ ދެއްވިއެވެ. އަދި އެ ދެ ބޭކަލުން އެ ތުރުކުރައްވައިގެން ތިއްބާ ތިމަން ދުށީމެވެ.","[رواه الترمذي 1722 بهذا اللفظ، ورواه البخاري 2920 ومسلم 2076 بنحوه]"],["#420","كتاب الجهاد","ޖިހާދުގެ ފޮތް","","","عَنْ عُمَرَ بْنِ الْخَطَّابِ - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (كَانَتْ أَمْوَالُ بَنِي النَّضِيرِ: مِمَّا أَفَاءَ اللَّهُ عَلَى رَسُولِهِ ﷺ مِمَّا لَمْ يُوجِفْ الْمُسْلِمُونَ عَلَيْهِ بِخَيْلٍ وَلا رِكَابٍ وَكَانَتْ لِرَسُولِ اللَّهِ ﷺ خَالِصاً، فَكَانَ رَسُولُ اللَّهِ ﷺ يَعْزِلُ نَفَقَةَ أَهْلِهِ سَنَةً، ثُمَّ يَجْعَلُ مَا بَقِيَ فِي الْكُرَاعِ، وَالسِّلاحِ عُدَّةً فِي سَبِيلِ اللَّهِ عَزَّ وَجَلَّ).","ޢުމަރު ބިން އަލްޚައްޠާބު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ބަނޫ ނަޟީރު ވަންހައިގެ ގަބީލާގެ މުދާތައް ވެގެން ދިޔައީ، ﷲ އެ އިލާހުގެ ރަސޫލާ ﷺ އަށް ޓަކައި ފައިއު މުދަލުގެ ގޮތުގައި ދެއްވި އެއްޗަކަށެވެ. (ނޯޓު: ފައިއު މުދަލަކީ ކާފަރުން ހަނގުރާމަކުރުން ދޫކޮށް އެ ހުރިތަނަކުން ދިޔުމުން ނުވަތަ ފިލުމުން، އެބައިމީހުންގެ ފަހަތުގައި ދޫކޮށްފަދާ މުދަލެވެ.) އަދި އެ ލިބިގަތުމަށް ޓަކައި މުސްލިމުން އަހެއް ޖަމަލެއް އެއްޗެއްވެސް ދުއްވާކަށް ނުޖެހުނެވެ. (އެބަހީ: ހަނގުރާމަ ކުރާކަށް ނުޖެހުނެވެ.) އަދި އެ ވެގެން ދިޔައީ (އެހެން މީހަކަށް ލިބުމަކާ ނުލާ) ރަސޫލު ﷲ ﷺ އަށްޓަކައި ޚާއްސަ ކުރެވުނު އެއްޗަކަށެވެ. ދެން އޭގެން އެއް އަހަރު ދުވަހަށް ވާވަރަށް ނަބިއްޔާގެ އާއިލާއަށް ހަރަދު ނެންގެވުމަށް ފަހު، ބާކީ ވި ހުރިހާ އެއްޗެއް ހޭދަ ކުރެއްވީ، ﷲ ޢައްޒަ ވަޖައްލަގެ މަގުގައި ޖިހާދަށް ތައްޔާރު ވުމުގެ ގޮތުން އަސްތަކާއި ހަތިޔާރުތައް ގަތުމަށެވެ.","[رواه الترمذي 1719 بهذا اللفظ، ورواه البخاري 2904، 4885 ومسلم 1757 بنحوه]"],["#421","كتاب الجهاد","ޖިހާދުގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (أَجْرَى النَّبِيُّ ﷺ مَا ضُمِّرَ مِنْ الْخَيْلِ: مِنْ الْحَفْيَاءِ إلَى ثَنِيَّةِ الْوَدَاعِ، وَأَجْرَى مَا لَمْ يُضَمَّرْ: مِنْ الثَّنِيَّةِ إلَى مَسْجِدِ بَنِي زُرَيْقٍ. قَالَ ابْنُ عُمَرَ: وَكُنْتُ فِيمَنْ أَجْرَى.\n\nقَالَ سُفْيَانُ: مِنَ الْحَفْيَاءِ إلَى ثَنِيَّةِ الْوَدَاعِ: خَمْسَةُ أَمْيَالٍ، أَوْ سِتَّةٌ، وَمَنْ ثَنِيَّةِ الْوَدَاعِ إلَى مَسْجِدِ بَنِي زُرَيْقٍ: مِيلٌ).","އަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އަލްޙަފްޔާއިން ފެށިގެން، ޘަނިއްޔަތުލް ވަދާއާ ހިސާބަށް ތަމްރީނު ކުރެވިފައިވާ އަސްތައް ދުއްވުމުގެ މުބާރާތް ނަބިއްޔާ ﷺ ބޭއްވިއެވެ. އަދި ތަމްރީނު ކުރެއްވިފައި ނުވާ އަސްތައް ދުއްވުމުގެ މުބާރާތް ބޭއްވީ އެ ޘަނިއްޔާއިން ފެށިގެން ބަނޫ ޒުރައިގު މިސްކިތާ ހިސާބަށެވެ. އަދި އިބްނު ޢުމަރު ވިދާޅުވިއެވެ: އަދި މުބާރާތުގައި ބައިވެރިވި މީހުންތަކު ތެރޭ ތިމަންނަ ހިމެނެއެވެ.\n\n(ޙަދީޘްގެ ރާވީއެއް ކަމަށްވާ) ސުފްޔާން ވިދާޅުވިއެވެ: އަލްޙަފްޔާއިން ފެށިގެން ޘަނިއްޔަތުލް ވަދާއާ ދެމެދު ވަނީ ފަހެއް ނުވަތަ ހަ މޭލެވެ. އަދި އެ ޘަނިއްޔާއިން ބަނޫ ޒުރައިގު މިސްކިތާ ދެމެދު ވަނީ އެއް މޭލެވެ.","[البخاري 2868 واللفظ له، ومسلم 1870]"],["#422","كتاب الجهاد","ޖިހާދުގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (عُرِضْتُ عَلَى رَسُولِ اللَّهِ ﷺ يَوْمَ أُحُدٍ، وَأَنَا ابْنُ أَرْبَعَ عَشْرَةَ، فَلَمْ يُجِزْنِي، وَعُرِضْتُ عَلَيْهِ يَوْمَ الْخَنْدَقِ، وَأَنَا ابْنُ خَمْسَ عَشْرَةَ، فَأَجَازَنِي).","އަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَا ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އުހުދު ހަނގުރާމައިގެ ދުވަހު (އޭގައި ބައިވެރިވުމަށް) ރަސޫލު ﷲ ﷺގެ އަރިހު ތިމަން ހުށަހެޅުނެވެ. އޭރު ތިމަންނައަށް ވާނީ ސާދަ އަހަރެވެ. ނަމަވެސް ހަނގުރާމައަށް ދިޔުމަށް ތިމަންނައަށް ހުއްދައެއް ނުދެއްވިއެވެ. އެއަށްފަހު ޚަންދަގު ހަނގުރާމައިގެ ދުވަހު ނަބިއްޔާއަށް ތިމަން ހުށަހެޅުނެވެ. އޭރު ތިމަންނައަށް ވާނީ ފަނަރަ އަހަރެވެ. ދެން ތިމަންނައަށް ހުއްދަ ދެއްވިއެވެ.","[رواه ابن ماجه 2543، ورواه البخاري 4097 ومسلم 1868 بنحوه]"],["#423","كتاب الجهاد","ޖިހާދުގެ ފޮތް","","","وَعَنْهُ (أَنَّ رَسُولَ اللَّهِ ﷺ قَسَمَ فِي النَّفَلِ: لِلْفَرَسِ سَهْمَيْنِ، وَلِلرَّجُلِ سَهْماً).","އަދިވެސް (އަބްދު ﷲ ބިން ޢުމަރު)ގެ އަރިހުން ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޣަނީމާ މުދާ ބެއްސެވި އިރު، އަހަކަށް ދެއްވީ ދެ ބައެވެ. އަދި މީހަކަށް އެއް ބައެވެ. (އެބަހީ: ކޮންމެ އަސްވާރަކަށް ތިން ބައެވެ. އަދި ފައިމަގުގައި އުޅޭ މީހަކަށް އެއް ބައެވެ.)","[البخاري 2863، 4228، ومسلم 1762 واللفظ له]"],["#424","كتاب الجهاد","ޖިހާދުގެ ފޮތް","","","وَعَنْهُ (أَنَّ رَسُولَ اللَّهِ ﷺ: كَانَ يُنَفِّلُ بَعْضَ مَنْ يَبْعَثُ فِي السَّرَايَا لأَنْفُسِهِمْ خَاصَّةً سِوَى قَسْمِ عَامَّةِ الْجَيْشِ).","އަދިވެސް (އަބްދު ﷲ ބިން ޢުމަރު)ގެ އަރިހުން ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺ ޣަނީމާ މުދާ ބެއްސެވި އިރު، ލަޝްކަރުގެ ބައެއް މީހުންނަށް ވަކިން ޚާއްސަ ބައެއް ދެއްވައެވެ. އެއީ އާންމުކޮށް ލަޝްކަރުގެ އެންމެންނަށް ދެއްވާ ބައިގެ އިތުރުންނެވެ.","[البخاري 3135 واللفظ له، ومسلم 1750]"],["#425","كتاب الجهاد","ޖިހާދުގެ ފޮތް","","","عَنْ أَبِي مُوسَى - عَبْدِ اللَّهِ بْنِ قَيْسٍ - عَنْ النَّبِيِّ ﷺ قَالَ: (مَنْ حَمَلَ عَلَيْنَا السِّلاحَ فَلَيْسَ مِنَّا).","އަބޫ މޫސާ ޢަބްދު ﷲ ބިން ގައިސް އަލްއަޝްޢަރީ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ތިމަންމެންނާ ދެކޮޅަށް ހަތިޔާރު އުފުލައިފި މީހާކީ ތިމަންމެންގެ ތެރެއިންވާ މީހެއް ނޫނެވެ.“","[البخاري 7071، ومسلم 100]"],["#426","كتاب الجهاد","ޖިހާދުގެ ފޮތް","","","عَنْ أَبِي مُوسَى - رَضِيَ اللَّهُ عَنْهُ - قَالَ: (سُئِلَ رَسُولُ اللَّهِ ﷺ عَنْ الرَّجُلِ: يُقَاتِلُ شَجَاعَةً، وَيُقَاتِلُ حَمِيَّةً، وَيُقَاتِلُ رِيَاءً. أَيُّ ذَلِكَ فِي سَبِيلِ اللَّهِ؟ فَقَالَ رَسُولُ اللَّهِ ﷺ: مَنْ قَاتَلَ لِتَكُونَ كَلِمَةُ اللَّهِ هِيَ الْعُلْيَا، فَهُوَ فِي سَبِيلِ اللَّهِ).","އަބޫ މޫސާ رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: ރަސޫލު ﷲ ﷺގެ އަރިހުގައި ސުވާލު ކުރެއްވުނެވެ. ހިއްވަރު ގަދަކަން ދައްކަންވެގެން ހަނގުރާމަ ކުރާ މީހަކާއި، އަމިއްލަ މީހުންނަށް ޓަކައި ހަނގުރާމަ ކުރާ މީހަކާއި، ކަންދެއްކުމަށް ހަނގުރާމަ ކުރާ މީހެއްގެ ތެރެއިން، ﷲގެ މަގުގައި (ހަގީގަތުގައި ވެސް) ޖިހާދު ކުރަނީ ކާކުތޯއެވެ؟ ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”ﷲގެ މަގުގައި ޖިހާދު ކުރާ މީހަކީ، ﷲގެ ބަސްފުޅު އެއީ އެންމެ މަތިވެރިވެގެންވާ ބަސްފުޅު ކަމަށް ހެދުމަށް ހަނގުރާމަ ކުރާ މީހާއެވެ.“","[البخاري 7458، ومسلم 1904 واللفظ له]"],["#427","كتاب العتق","އަޅުން މިނިވަންކުރުމުގެ ފޮތް","","","عَنْ عَبْدِ اللَّهِ بْنِ عُمَرَ رَضِيَ اللَّهُ عَنْهُمَا: أَنَّ رَسُولَ اللَّهِ ﷺ قَالَ: (مَنْ أَعْتَقَ شِرْكاً لَهُ فِي عَبْدٍ، فَكَانَ لَهُ مَالٌ يَبْلُغُ ثَمَنَ الْعَبْدِ: قُوِّمَ عَلَيْهِ قِيمَةَ عَدْلٍ، فَأَعْطَى شُرَكَاءَهُ حِصَصَهُمْ، وَعَتَقَ عَلَيْهِ الْعَبْدُ، وَإِلَاّ فَقَدْ عَتَقَ مِنْهُ مَا عَتَقَ).","އަބްދު ﷲ ބިން ޢުމަރު رَضِيَ اللَّهُ عَنْهُمَاގެ އަރިހުން ރިވާވެގެންވެއެވެ: ހަމަކަށަވަރުން ރަސޫލު ﷲ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”(އެކި މީހުންގެ ހިއްސާވާ) އަޅެއްގެ ކިބައިން، މީހަކު އޭނާގެ ހިއްސާ މިނިވަންކޮށްފި ނަމަ، އަދި އެ އަޅާ އެއްކޮށް މިނިވަން ކުރުމަށް އޭނާގެ އަތުގައި ވާވަރަށް މުދާހުރި ނަމަ، (އޭނާ ކުރަންވީ ކަމަކީ) އިންސާފުވެރި މީހަކު ލައްވާ (އެ އަޅާގެ ބާކީ) ހުރި އަގު އަންދާޒާކޮށް، އެ އަގު ދެން ހުރި ހިއްސާވެރިންނަށް ދީ ހަމަކޮށް (އެއްކޮށް) އެ އަޅާ މިނިވަން ކުރަންވީއެވެ. (އެހެން ނެހެދިއްޖެއްޔާ) އޭނާ އެ މިނިވަން ކުރެވުނީ އެ އަޅާގެ ކިބައިން އޭނާއަށް ލިބިގެންވާ ހިއްސާ އެކަންޏެވެ.“","[البخاري 2522، ومسلم 1501 واللفظ له]"],["#428","كتاب العتق","އަޅުން މިނިވަންކުރުމުގެ ފޮތް","","","عَنْ أَبِي هُرَيْرَةَ - رَضِيَ اللَّهُ عَنْهُ - عَنْ النَّبِيِّ ﷺ قَالَ: (مَنْ أَعْتَقَ شِقْصَاً مِنْ مَمْلُوكٍ، فَعَلَيْهِ خَلاصُهُ كُلُّهُ فِي مَالِهِ، فَإِنْ لَمْ يَكُنْ لَهُ مَالٌ قُوِّمَ الْمَمْلُوكُ قِيمَةَ عَدْلٍ، ثُمَّ اُسْتُسْعِيَ الْعَبْدُ، غَيْرَ مَشْقُوقٍ عَلَيْهِ).","އަބޫ ހުރައިރާ رَضِيَ اللَّهُ عَنْهُގެ އަރިހުން ރިވާވެގެންވެއެވެ: ނަބިއްޔާ ﷺ ޙަދީޘް ކުރެއްވިއެވެ: ”(އެކި މީހުންގެ ހިއްސާވާ) އަޅެއްގެ ކިބައިން، މީހަކު އޭނާގެ ހިއްސާ މިނިވަންކޮށްފި ނަމަ، ފަހެ އޭނާގެ މައްޗަށް ވަނީ އޭނާގެ މުދަލުން އެ އަޅާ އެއްކޮށް މިނިވަންކޮށް ޚަލާސްކުރުމެވެ. އެކަމަކު އޭނާގެ އަތުގައި މުދާ ނެތްނަމަ، (އޭނާ ކުރަންވީ ކަމަކީ އެ އަޅާގެ ބާކީ) ހުރި އަގު އިންސާފުވެރިކަމާއެކީ އަންދާޒާކޮށް، އެ އަޅާ ލައްވާ (އެ އަގު ހަމަވަންދެން) މަސައްކަތް ކުރުވާނިއެވެ. އެއީ އެ އަޅާއަށް މާ ބުރަބޮޑު ނުވާ ގޮތަށެވެ.“","[البخاري 2492 واللفظ له، ومسلم 1503]"],["#429","كتاب العتق","އަޅުން މިނިވަންކުރުމުގެ ފޮތް","باب بيع المدبر","ސާހިބާ މަރުވުމުން މިނިވަންވުމަށް ޝަރުތުކޮށްފައިވާ އަޅަކު ވިއްކުމުގެ ބާބު","عَنْ جَابِرِ بْنِ عَبْدِ اللَّهِ رَضِيَ اللَّهُ عَنْهُمَا قَالَ: (دَبَّرَ رَجُلٌ مِنْ الأَنْصَارِ غُلاماً لَهُ -،","ޖާބިރު رَضِيَ اللَّهُ عَنْهُ ވިދާޅުވިކަމަށް ރިވާވެގެންވެއެވެ: އަންސާރުންގެ މީހަކު، އޭނާ މަރުވުމުން އޭނާ ގެންގުޅޭ ފިރިހެން އަޅަކު މިނިވަންވާނެ ކަމަށް ކަނޑައެޅުއްވިއެވެ.","[مسلم 997]"],["#430","كتاب العتق","އަޅުން މިނިވަންކުރުމުގެ ފޮތް","باب بيع المدبر","ސާހިބާ މަރުވުމުން މިނިވަންވުމަށް ޝަރުތުކޮށްފައިވާ އަޅަކު ވިއްކުމުގެ ބާބު","وَفِي لَفْظٍ: (بَلَغَ النَّبِيَّ ﷺ: أَنَّ رَجُلاً مِنْ أَصْحَابِهِ أَعْتَقَ غُلاماً لَهُ عنْ دُبُرٍ - لَمْ يَكُنْ لَهُ مَالٌ غَيْرُهُ فَبَاعَهُ رَسُولُ اللَّهِ ﷺ بِثَمَانِمِائَةِ دِرْهَمٍ، ثُمَّ أَرْسَلَ ثَمَنَهُ إلَيْهِ).","އަދި އެހެން ރިވާޔަތެއްގައި ވެއެވެ: ހަމަކަށަވަރުން ނަބިއްޔާގެ ސަހާބީންގެ ތެރެއިން މީހަކު، އޭނާ މަރުވުމުން އޭނާ ގެންގުޅޭ ފިރިހެން އަޅަކު މިނިވަންވާނެ ކަމަށް ކަނޑައެޅުއްވިއެވެ. އޭނާގެ އަތުގައި އެ އަޅާ ނޫން މުދަލެއް ނުވެއެވެ. ނަބިއްޔާ ﷺ އަށް އެ ވާހަކަ ފޯރުމުން، އަށް ސަތޭކަ ދިރުހަމަށް އެކަލޭގެފާނު އެ އަޅާ ވިއްކާލެއްވިއެވެ. އަދި އެ އަގު އެ މީހާއަށް ފޮނުއްވިއެވެ.","[البخاري 7186 واللفظ له، ومسلم 998]"]];